INSERT INTO `Ayetler` (`id`, `AyetNo`, `SureNo`, `SureAdi`, `Metin`, `Mana`, `Cuzu`, `Sayfasi`, `AyetSayisi`, `HarfSayisi`, `AdAnlami`, `IndigiYer`, `InisSirasi`) VALUES
(1, 1, 1, 'Fatiha', 'بِسْمِ اللّٰهِ الرَّحْمٰنِ الرَّح۪يمِ', 'Rahmân ve rahîm olan Allah´ın adıyla.', 1, 0, 7, 131, 'Açılış, başlangıç', 'Mekke', 5),
(2, 2, 1, 'Fatiha', 'اَلْحَمْدُ لِلّٰهِ رَبِّ الْعَالَم۪ينَۙ', 'Hamd (övme ve övülme), âlemlerin Rabbi Allah´a mahsustur.', 1, 0, 7, 131, 'Açılış, başlangıç', 'Mekke', 5),
(3, 3, 1, 'Fatiha', 'اَلرَّحْمٰنِ الرَّح۪يمِۙ', 'O, rahmândır ve rahîmdir.', 1, 0, 7, 131, 'Açılış, başlangıç', 'Mekke', 5),
(4, 4, 1, 'Fatiha', 'مَالِكِ يَوْمِ الدّ۪ينِۜ', 'Ceza gününün mâlikidir.', 1, 0, 7, 131, 'Açılış, başlangıç', 'Mekke', 5),
(5, 5, 1, 'Fatiha', 'اِيَّاكَ نَعْبُدُ وَاِيَّاكَ نَسْتَع۪ينُۜ', '(Rabbimiz!) Ancak sana kulluk ederiz ve yalnız senden medet umarız.', 1, 0, 7, 131, 'Açılış, başlangıç', 'Mekke', 5),
(6, 6, 1, 'Fatiha', 'اِهْدِنَا الصِّرَاطَ الْمُسْتَق۪يمَۙ', 'Bize doğru yolu göster.', 1, 0, 7, 131, 'Açılış, başlangıç', 'Mekke', 5),
(7, 7, 1, 'Fatiha', 'صِرَاطَ الَّذ۪ينَ اَنْعَمْتَ عَلَيْهِمْۙ غَيْرِ الْمَغْضُوبِ عَلَيْهِمْ وَلَا الضَّٓالّ۪ينَ', 'Kendilerine lütuf ve ikramda bulunduğun kimselerin yolunu; gazaba uğramışların ve sapmışların yolunu değil!', 1, 0, 7, 131, 'Açılış, başlangıç', 'Mekke', 5),
(8, 1, 2, 'Bakara', 'الٓمٓ ۚ', 'Elif. Lâm. Mîm.', 1, 1, 286, 25613, 'Sığır', 'Medine', 87),
(9, 2, 2, 'Bakara', 'ذٰلِكَ الْكِتَابُ لَا رَيْبَۚۛ ف۪يهِۚۛ هُدًى لِلْمُتَّق۪ينَۙ', 'O kitap (Kur´an); onda asla şüphe yoktur. O, müttakîler (sakınanlar ve arınmak isteyenler) için bir yol göstericidir.', 1, 1, 286, 25613, 'Sığır', 'Medine', 87),
(10, 3, 2, 'Bakara', 'اَلَّذ۪ينَ يُؤْمِنُونَ بِالْغَيْبِ وَيُق۪يمُونَ الصَّلٰوةَ وَمِمَّا رَزَقْنَاهُمْ يُنْفِقُونَۙ', 'Onlar gayba inanırlar, namaz kılarlar, kendilerine verdiğimiz mallardan Allah yolunda harcarlar.', 1, 1, 286, 25613, 'Sığır', 'Medine', 87),
(11, 4, 2, 'Bakara', 'وَالَّذ۪ينَ يُؤْمِنُونَ بِمَٓا اُنْزِلَ اِلَيْكَ وَمَٓا اُنْزِلَ مِنْ قَبْلِكَۚ وَبِالْاٰخِرَةِ هُمْ يُوقِنُونَۜ', 'Yine onlar, sana indirilene ve senden önce indirilene iman ederler; ahiret gününe de kesinkes inanırlar.', 1, 1, 286, 25613, 'Sığır', 'Medine', 87),
(12, 5, 2, 'Bakara', 'اُو۬لٰٓئِكَ عَلٰى هُدًى مِنْ رَبِّهِمْ وَاُو۬لٰٓئِكَ هُمُ الْمُفْلِحُونَ', 'İşte onlar, Rablerinden gelen bir hidayet üzeredirler ve kurtuluşa erenler de ancak onlardır.', 1, 1, 286, 25613, 'Sığır', 'Medine', 87),
(13, 6, 2, 'Bakara', 'اِنَّ الَّذ۪ينَ كَفَرُوا سَوَٓاءٌ عَلَيْهِمْ ءَاَنْذَرْتَهُمْ اَمْ لَمْ تُنْذِرْهُمْ لَا يُؤْمِنُونَ', 'Gerçek şu ki, kâfir olanları (azap ile) korkutsan da korkutmasan da onlar için birdir; iman etmezler.', 1, 2, 286, 25613, 'Sığır', 'Medine', 87),
(14, 7, 2, 'Bakara', 'خَتَمَ اللّٰهُ عَلٰى قُلُوبِهِمْ وَعَلٰى سَمْعِهِمْۜ وَعَلٰٓى اَبْصَارِهِمْ غِشَاوَةٌۘ وَلَهُمْ عَذَابٌ عَظ۪يمٌ۟', 'Allah onların kalplerini ve kulaklarını mühürlemiştir. Onların gözlerine de bir çeşit perde gerilmiştir ve onlar için (dünya ve ahirette) büyük bir azap vardır.', 1, 2, 286, 25613, 'Sığır', 'Medine', 87),
(15, 8, 2, 'Bakara', 'وَمِنَ النَّاسِ مَنْ يَقُولُ اٰمَنَّا بِاللّٰهِ وَبِالْيَوْمِ الْاٰخِرِ وَمَا هُمْ بِمُؤْمِن۪ينَۢ', 'İnsanlardan bazıları da vardır ki, inanmadıkları halde «Allah´a ve ahiret gününe inandık» derler.', 1, 2, 286, 25613, 'Sığır', 'Medine', 87),
(16, 9, 2, 'Bakara', 'يُخَادِعُونَ اللّٰهَ وَالَّذ۪ينَ اٰمَنُواۚ وَمَا يَخْدَعُونَ اِلَّٓا اَنْفُسَهُمْ وَمَا يَشْعُرُونَۜ', 'Onlar (kendi akıllarınca) güya Allah´ı ve müminleri aldatırlar. Halbuki onlar ancak kendilerini aldatırlar ve bunun farkında değillerdir.', 1, 2, 286, 25613, 'Sığır', 'Medine', 87),
(17, 10, 2, 'Bakara', 'ف۪ي قُلُوبِهِمْ مَرَضٌۙ فَزَادَهُمُ اللّٰهُ مَرَضاًۚ وَلَهُمْ عَذَابٌ اَل۪يمٌۙ بِمَا كَانُوا يَكْذِبُونَ', 'Onların kalblerinde bir hastalık vardır. Allah da onların hastalığını çoğaltmıştır. Söylemekte oldukları yalanlar sebebiyle de onlar için elîm bir azap vardır.', 1, 2, 286, 25613, 'Sığır', 'Medine', 87),
(18, 11, 2, 'Bakara', 'وَاِذَا ق۪يلَ لَهُمْ لَا تُفْسِدُوا فِي الْاَرْضِۙ قَالُٓوا اِنَّمَا نَحْنُ مُصْلِحُونَ', 'Onlara: Yeryüzünde fesat çıkarmayın, denildiği zaman, «Biz ancak ıslah edicileriz» derler.', 1, 2, 286, 25613, 'Sığır', 'Medine', 87),
(19, 12, 2, 'Bakara', 'اَلَٓا اِنَّهُمْ هُمُ الْمُفْسِدُونَ وَلٰكِنْ لَا يَشْعُرُونَ', 'Şunu bilin ki, onlar bozguncuların ta kendileridir, lâkin anlamazlar.', 1, 2, 286, 25613, 'Sığır', 'Medine', 87),
(20, 13, 2, 'Bakara', 'وَاِذَا ق۪يلَ لَهُمْ اٰمِنُوا كَمَٓا اٰمَنَ النَّاسُ قَالُٓوا اَنُؤْمِنُ كَمَٓا اٰمَنَ السُّفَـهَٓاءُۜ اَلَٓا اِنَّهُمْ هُمُ السُّفَـهَٓاءُ وَلٰكِنْ لَا يَعْلَمُونَ', 'Onlara: İnsanların iman ettiği gibi siz de iman edin, denildiği vakit «Biz hiç, sefihlerin (akılsız ve ahmak kişilerin) iman ettikleri gibi iman eder miyiz!» derler. Biliniz ki, sefihler ancak kendileridir, fakat bunu bilmezler (veya bilmezlikten gelirler).', 1, 2, 286, 25613, 'Sığır', 'Medine', 87),
(21, 14, 2, 'Bakara', 'وَاِذَا لَقُوا الَّذ۪ينَ اٰمَنُوا قَالُٓوا اٰمَنَّاۚ وَاِذَا خَلَوْا اِلٰى شَيَاط۪ينِهِمْۙ قَالُٓوا اِنَّا مَعَكُمْۙ اِنَّمَا نَحْنُ مُسْتَهْزِؤُ۫نَ', '(Bu münafıklar) müminlerle karşılaştıkları vakit «(Biz de) iman ettik» derler. (Kendilerini saptıran) şeytanları ile başbaşa kaldıklarında ise: Biz sizinle beraberiz, biz onlarla (müminlerle) sadece alay ediyoruz, derler.', 1, 2, 286, 25613, 'Sığır', 'Medine', 87),
(22, 15, 2, 'Bakara', 'اَللّٰهُ يَسْتَهْزِئُ بِهِمْ وَيَمُدُّهُمْ ف۪ي طُغْيَانِهِمْ يَعْمَهُونَ', 'Gerçekte, Allah onlarla istihza (alay) eder de azgınlıklarında onlara fırsat verir, bu yüzden onlar bir müddet başıboş dolaşırlar.', 1, 2, 286, 25613, 'Sığır', 'Medine', 87),
(23, 16, 2, 'Bakara', 'اُو۬لٰٓئِكَ الَّذ۪ينَ اشْتَرَوُا الضَّلَالَةَ بِالْهُدٰىۖ فَمَا رَبِحَتْ تِجَارَتُهُمْ وَمَا كَانُوا مُهْتَد۪ينَ', 'İşte onlar, hidayete karşılık dalâleti satın alanlardır. Ancak onların bu ticareti kazançlı olmamış ve kendileri de doğru yola girememişlerdir.', 1, 2, 286, 25613, 'Sığır', 'Medine', 87),
(24, 17, 2, 'Bakara', 'مَثَلُهُمْ كَمَثَلِ الَّذِي اسْتَوْقَدَ نَاراًۚ فَلَمَّٓا اَضَٓاءَتْ مَا حَوْلَهُ ذَهَبَ اللّٰهُ بِنُورِهِمْ وَتَرَكَهُمْ ف۪ي ظُلُمَاتٍ لَا يُبْصِرُونَ', 'Onların (münafıkların) durumu, (karanlık gecede) bir ateş yakan kimse misalidir. O ateş yanıp da etrafını aydınlattığı anda Allah, hemen onların aydınlığını giderir ve onları karanlıklar içinde bırakır; (artık hiçbir şeyi) görmezler.', 1, 3, 286, 25613, 'Sığır', 'Medine', 87),
(25, 18, 2, 'Bakara', 'صُمٌّ بُكْمٌ عُمْيٌ فَهُمْ لَا يَرْجِعُونَۙ', 'Onlar sağırlar, dilsizler ve körlerdir. Bu sebeple onlar geri dönemezler.', 1, 3, 286, 25613, 'Sığır', 'Medine', 87),
(26, 19, 2, 'Bakara', 'اَوْ كَصَيِّبٍ مِنَ السَّمَٓاءِ ف۪يهِ ظُلُمَاتٌ وَرَعْدٌ وَبَرْقٌۚ يَجْعَلُونَ اَصَابِعَهُمْ ف۪ٓي اٰذَانِهِمْ مِنَ الصَّوَاعِقِ حَذَرَ الْمَوْتِۜ وَاللّٰهُ مُح۪يطٌ بِالْكَافِر۪ينَ', 'Yahut (onların durumu), gökten sağanak halinde boşanan, içinde yoğun karanlıklar, gürültü ve şimşek bulunan yağmur(a tutulmuş kimselerin durumu) gibidir. O münafıklar yıldırımlardan gelecek ölüm korkusuyla parmaklarını kulaklarına tıkarlar. Halbuki Allah, kâfirleri çepeçevre kuşatmıştır.', 1, 3, 286, 25613, 'Sığır', 'Medine', 87),
(27, 20, 2, 'Bakara', 'يَكَادُ الْبَرْقُ يَخْطَفُ اَبْصَارَهُمْۜ كُلَّمَٓا اَضَٓاءَ لَهُمْ مَشَوْا ف۪يهِۙ وَاِذَٓا اَظْلَمَ عَلَيْهِمْ قَامُواۜ وَلَوْ شَٓاءَ اللّٰهُ لَذَهَبَ بِسَمْعِهِمْ وَاَبْصَارِهِمْۜ اِنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌ۟', '(O esnada) şimşek sanki gözlerini çıkaracakmış gibi çakar, onlar için etrafı aydınlatınca orada birazcık yürürler, karanlık üzerlerine çökünce de oldukları yerde kalırlar. Allah dileseydi elbette onların kulaklarını sağır, gözlerini kör ederdi. Allah şüphesiz her şeye kadirdir.', 1, 3, 286, 25613, 'Sığır', 'Medine', 87),
(28, 21, 2, 'Bakara', 'يَٓا اَيُّهَا النَّاسُ اعْبُدُوا رَبَّكُمُ الَّذ۪ي خَلَقَكُمْ وَالَّذ۪ينَ مِنْ قَبْلِكُمْ لَعَلَّكُمْ تَتَّقُونَۙ', 'Ey insanlar! Sizi ve sizden öncekileri yaratan Rabbinize kulluk ediniz. Umulur ki, böylece korunmuş (Allah´ın azabından kendinizi kurtarmış) olursunuz.', 1, 3, 286, 25613, 'Sığır', 'Medine', 87),
(29, 22, 2, 'Bakara', 'اَلَّذ۪ي جَعَلَ لَكُمُ الْاَرْضَ فِرَاشاً وَالسَّمَٓاءَ بِنَٓاءًۖ وَاَنْزَلَ مِنَ السَّمَٓاءِ مَٓاءً فَاَخْرَجَ بِه۪ مِنَ الثَّمَرَاتِ رِزْقاً لَكُمْۚ فَلَا تَجْعَلُوا لِلّٰهِ اَنْدَاداً وَاَنْتُمْ تَعْلَمُونَ', 'O Rab ki, yeri sizin için bir döşek, göğü de (kubbemsi) bir tavan yaptı. Gökten su indirerek onunla, size besin olsun diye (yerden) çeşitli ürünler çıkardı. Artık bunu bile bile Allah´a şirk koşmayın.', 1, 3, 286, 25613, 'Sığır', 'Medine', 87),
(30, 23, 2, 'Bakara', 'وَاِنْ كُنْتُمْ ف۪ي رَيْبٍ مِمَّا نَزَّلْنَا عَلٰى عَبْدِنَا فَأْتُوا بِسُورَةٍ مِنْ مِثْلِه۪ۖ وَادْعُوا شُهَدَٓاءَكُمْ مِنْ دُونِ اللّٰهِ اِنْ كُنْتُمْ صَادِق۪ينَ', 'Eğer kulumuza indirdiklerimizden herhangi bir şüpheye düşüyorsanız, haydi onun benzeri bir sûre getirin, eğer iddianızda doğru iseniz Allah´tan gayri şahitlerinizi (yardımcılarınızı) da çağırın.', 1, 3, 286, 25613, 'Sığır', 'Medine', 87),
(31, 24, 2, 'Bakara', 'فَاِنْ لَمْ تَفْعَلُوا وَلَنْ تَفْعَلُوا فَاتَّقُوا النَّارَ الَّت۪ي وَقُودُهَا النَّاسُ وَالْحِجَارَةُۚ اُعِدَّتْ لِلْكَافِر۪ينَ', 'Bunu yapamazsanız -ki elbette yapamayacaksınız- yakıtı, insan ve taş olan cehennem ateşinden sakının. Çünkü o ateş kâfirler için hazırlanmıştır.', 1, 3, 286, 25613, 'Sığır', 'Medine', 87),
(32, 25, 2, 'Bakara', 'وَبَشِّرِ الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ اَنَّ لَهُمْ جَنَّاتٍ تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُۜ كُلَّمَا رُزِقُوا مِنْهَا مِنْ ثَمَرَةٍ رِزْقاًۙ قَالُوا هٰذَا الَّذ۪ي رُزِقْنَا مِنْ قَبْلُ وَاُتُوا بِه۪ مُتَشَابِهاًۜ وَلَهُمْ ف۪يهَٓا اَزْوَاجٌ مُطَهَّرَةٌ وَهُمْ ف۪يهَا خَالِدُونَ', 'İman edip iyi davranışlarda bulunanlara, içinden ırmaklar akan cennetler olduğunu müjdele! O cennetlerdeki bir meyveden kendilerine rızık olarak yedirildikçe: Bundan önce dünyada bize verilenlerdendir bu, derler. Bu rızıklar onlara (bazı yönlerden dünyadakine) benzer olarak verilmiştir. Onlar için cennette tertemiz eşler de vardır. Ve onlar orada ebedî kalıcılardır.', 1, 4, 286, 25613, 'Sığır', 'Medine', 87),
(33, 26, 2, 'Bakara', 'اِنَّ اللّٰهَ لَا يَسْتَحْـي۪ٓ اَنْ يَضْرِبَ مَثَلاً مَا بَعُوضَةً فَمَا فَوْقَهَاۜ فَاَمَّا الَّذ۪ينَ اٰمَنُوا فَيَعْلَمُونَ اَنَّهُ الْحَقُّ مِنْ رَبِّهِمْۚ وَاَمَّا الَّذ۪ينَ كَفَرُوا فَيَقُولُونَ مَاذَٓا اَرَادَ اللّٰهُ بِهٰذَا مَثَلاًۢ يُضِلُّ بِه۪ كَث۪يراً وَيَهْد۪ي بِه۪ كَث۪يراًۜ وَمَا يُضِلُّ بِه۪ٓ اِلَّا الْفَاسِق۪ينَۙ', 'Şüphesiz Allah (hakkı açıklamak için) sivrisinek ve onun da ötesinde bir varlığı misal getirmekten çekinmez. İman etmişlere gelince, onlar böyle misallerin Rablerinden gelen hak ve gerçek olduğunu bilirler. Kâfir olanlara gelince: Allah böyle misal vermekle ne murat eder? derler. Allah onunla birçok kimseyi saptırır, birçoklarını da doğru yola yöneltir. Verdiği misallerle Allah ancak fâsıkları saptırır (çünkü bunlar birer imtihandır).', 1, 4, 286, 25613, 'Sığır', 'Medine', 87),
(34, 27, 2, 'Bakara', 'اَلَّذ۪ينَ يَنْقُضُونَ عَهْدَ اللّٰهِ مِنْ بَعْدِ م۪يثَاقِه۪ۖ وَيَقْطَعُونَ مَٓا اَمَرَ اللّٰهُ بِه۪ٓ اَنْ يُوصَلَ وَيُفْسِدُونَ فِي الْاَرْضِۜ اُو۬لٰٓئِكَ هُمُ الْخَاسِرُونَ', 'Onlar öyle (fâsıklar) ki, Allah´a kesin söz verdikten sonra sözlerinden dönerler. Allah´ın, ziyaret edilip hal ve hatırının sorulmasını istediği kimseleri ziyaretten vazgeçerler ve yeryüzünde fitne ve fesat çıkarırlar. İşte onlar gerçekten zarara uğrayanlardır.', 1, 4, 286, 25613, 'Sığır', 'Medine', 87),
(35, 28, 2, 'Bakara', 'كَيْفَ تَكْفُرُونَ بِاللّٰهِ وَكُنْتُمْ اَمْوَاتاً فَاَحْيَاكُمْۚ ثُمَّ يُم۪يتُكُمْ ثُمَّ يُحْي۪يكُمْ ثُمَّ اِلَيْهِ تُرْجَعُونَ', 'Siz cansız iken size can veren Allah´ı nasıl inkâr edersiniz? Sonra sizi öldürecek, tekrar sizi diriltecek ve sonunda O´na döndürüleceksiniz.', 1, 4, 286, 25613, 'Sığır', 'Medine', 87),
(36, 29, 2, 'Bakara', 'هُوَ الَّذ۪ي خَلَقَ لَكُمْ مَا فِي الْاَرْضِ جَم۪يعاً ثُمَّ اسْتَوٰٓى اِلَى السَّمَٓاءِ فَسَوّٰيهُنَّ سَبْعَ سَمٰوَاتٍۜ وَهُوَ بِكُلِّ شَيْءٍ عَل۪يمٌ۟', 'O, yerde ne varsa hepsini sizin için yarattı. Sonra (kendine has bir şekilde) semaya yöneldi, onu yedi gök olarak yaratıp düzenledi (tanzim etti). O, her şeyi hakkıyla bilendir.', 1, 4, 286, 25613, 'Sığır', 'Medine', 87),
(37, 30, 2, 'Bakara', 'وَاِذْ قَالَ رَبُّكَ لِلْمَلٰٓئِكَةِ اِنّ۪ي جَاعِلٌ فِي الْاَرْضِ خَل۪يفَةًۜ قَالُٓوا اَتَجْعَلُ ف۪يهَا مَنْ يُفْسِدُ ف۪يهَا وَيَسْفِكُ الدِّمَٓاءَۚ وَنَحْنُ نُسَبِّحُ بِحَمْدِكَ وَنُقَدِّسُ لَكَۜ قَالَ اِنّ۪ٓي اَعْلَمُ مَا لَا تَعْلَمُونَ', 'Hatırla ki Rabbin meleklere: Ben yeryüzünde bir halife yaratacağım, dedi. Onlar: Bizler hamdinle seni tesbih ve seni takdis edip dururken, yeryüzünde fesat çıkaracak, orada kan dökecek birini mi yaratacaksın? dediler. Allah da onlara: Sizin bilemiyeceğinizi herhalde ben bilirim, dedi.', 1, 5, 286, 25613, 'Sığır', 'Medine', 87),
(38, 31, 2, 'Bakara', 'وَعَلَّمَ اٰدَمَ الْاَسْمَٓاءَ كُلَّهَا ثُمَّ عَرَضَهُمْ عَلَى الْمَلٰٓئِكَةِ فَقَالَ اَنْبِؤُ۫ن۪ي بِاَسْمَٓاءِ هٰٓؤُ۬لَٓاءِ اِنْ كُنْتُمْ صَادِق۪ينَ', 'Allah Âdem´e bütün isimleri, öğretti. Sonra onları önce meleklere arzedip: Eğer siz sözünüzde sadık iseniz, şunların isimlerini bana bildirin, dedi.', 1, 5, 286, 25613, 'Sığır', 'Medine', 87),
(39, 32, 2, 'Bakara', 'قَالُوا سُبْحَانَكَ لَا عِلْمَ لَنَٓا اِلَّا مَا عَلَّمْتَنَاۜ اِنَّكَ اَنْتَ الْعَل۪يمُ الْحَك۪يمُ', 'Melekler: Yâ Rab! Seni noksan sıfatlardan tenzih ederiz, senin bize öğrettiklerinden başka bizim bilgimiz yoktur. Şüphesiz alîm ve hakîm olan ancak sensin, dediler.', 1, 5, 286, 25613, 'Sığır', 'Medine', 87),
(40, 33, 2, 'Bakara', 'قَالَ يَٓا اٰدَمُ اَنْبِئْهُمْ بِاَسْمَٓائِهِمْۚ فَلَمَّٓا اَنْبَاَهُمْ بِاَسْمَٓائِهِمْۙ قَالَ اَلَمْ اَقُلْ لَكُمْ اِنّ۪ٓي اَعْلَمُ غَيْبَ السَّمٰوَاتِ وَالْاَرْضِ وَاَعْلَمُ مَا تُبْدُونَ وَمَا كُنْتُمْ تَكْتُمُونَ', '(Bunun üzerine:) Ey Âdem! Eşyanın isimlerini meleklere anlat, dedi. Âdem onların isimlerini onlara anlatınca: Ben size, muhakkak semâvat ve arzda görülmeyenleri (oralardaki sırları) bilirim. Bundan da öte, gizli ve açık yapmakta olduklarınızı da bilirim, dememiş miydim? dedi.', 1, 5, 286, 25613, 'Sığır', 'Medine', 87),
(41, 34, 2, 'Bakara', 'وَاِذْ قُلْنَا لِلْمَلٰٓئِكَةِ اسْجُدُوا لِاٰدَمَ فَسَجَدُٓوا اِلَّٓا اِبْل۪يسَۜ اَبٰى وَاسْتَكْبَرَ وَكَانَ مِنَ الْكَافِر۪ينَ', 'Hani biz meleklere (ve cinlere): Âdem´e secde edin, demiştik. İblis hariç hepsi secde ettiler. O yüz çevirdi ve büyüklük tasladı, böylece kâfirlerden oldu.', 1, 5, 286, 25613, 'Sığır', 'Medine', 87),
(42, 35, 2, 'Bakara', 'وَقُلْنَا يَٓا اٰدَمُ اسْكُنْ اَنْتَ وَزَوْجُكَ الْجَنَّةَ وَكُلَا مِنْهَا رَغَداً حَيْثُ شِئْتُمَاۖ وَلَا تَقْرَبَا هٰذِهِ الشَّجَرَةَ فَتَكُونَا مِنَ الظَّالِم۪ينَ', 'Biz: Ey Âdem! Sen ve eşin (Havva) beraberce cennete yerleşin; orada kolaylıkla istediğiniz zaman her yerde cennet nimetlerinden yeyin; sadece şu ağaca yaklaşmayın. Eğer bu ağaçtan yerseniz her ikiniz de kendine kötülük eden zalimlerden olursunuz, dedik.', 1, 5, 286, 25613, 'Sığır', 'Medine', 87),
(43, 36, 2, 'Bakara', 'فَاَزَلَّهُمَا الشَّيْطَانُ عَنْهَا فَاَخْرَجَهُمَا مِمَّا كَانَا ف۪يهِۖ وَقُلْنَا اهْبِطُوا بَعْضُكُمْ لِبَعْضٍ عَدُوٌّۚ وَلَكُمْ فِي الْاَرْضِ مُسْتَقَرٌّ وَمَتَاعٌ اِلٰى ح۪ينٍ', 'Şeytan onların ayaklarını kaydırıp haddi tecavüz ettirdi ve içinde bulundukları (cennetten) onları çıkardı. Bunun üzerine: Bir kısmınız diğerine düşman olarak ininiz, sizin için yeryüzünde barınak ve belli bir zamana dek yaşamak vardır, dedik.', 1, 5, 286, 25613, 'Sığır', 'Medine', 87),
(44, 37, 2, 'Bakara', 'فَتَلَقّٰٓى اٰدَمُ مِنْ رَبِّه۪ كَلِمَاتٍ فَتَابَ عَلَيْهِۜ اِنَّهُ هُوَ التَّوَّابُ الرَّح۪يمُ', 'Bu durum devam ederken Âdem, Rabbinden bir takım ilhamlar aldı ve derhal tevbe etti. Çünkü Allah tevbeleri kabul eden ve merhameti bol olandır.', 1, 5, 286, 25613, 'Sığır', 'Medine', 87),
(45, 38, 2, 'Bakara', 'قُلْنَا اهْبِطُوا مِنْهَا جَم۪يعاًۚ فَاِمَّا يَأْتِيَنَّكُمْ مِنّ۪ي هُدًى فَمَنْ تَبِعَ هُدَايَ فَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ', 'Dedik ki: Hepiniz cennetten inin! Eğer benden size bir hidayet gelir de her kim hidayetime tâbi olursa onlar için herhangi bir korku yoktur ve onlar üzüntü çekmezler.', 1, 6, 286, 25613, 'Sığır', 'Medine', 87),
(46, 39, 2, 'Bakara', 'وَالَّذ۪ينَ كَفَرُوا وَكَذَّبُوا بِاٰيَاتِنَٓا اُو۬لٰٓئِكَ اَصْحَابُ النَّارِۚ هُمْ ف۪يهَا خَالِدُونَ۟', 'İnkâr edip âyetlerimizi yalanlayanlara gelince, onlar cehennemliktir, onlar orada ebedî kalırlar.', 1, 6, 286, 25613, 'Sığır', 'Medine', 87),
(47, 40, 2, 'Bakara', 'يَا بَن۪ٓي اِسْرَٓائ۪لَ اذْكُرُوا نِعْمَتِيَ الَّت۪ٓي اَنْعَمْتُ عَلَيْكُمْ وَاَوْفُوا بِعَهْد۪ٓي اُو۫فِ بِعَهْدِكُمْ وَاِيَّايَ فَارْهَبُونِ', 'Ey İsrailoğulları! Size verdiğim nimetlerimi hatırlayın, bana verdiğiniz sözü yerine getirin ki, ben de size vâdettiklerimi vereyim. Yalnızca benden korkun.', 1, 6, 286, 25613, 'Sığır', 'Medine', 87),
(48, 41, 2, 'Bakara', 'وَاٰمِنُوا بِمَٓا اَنْزَلْتُ مُصَدِّقاً لِمَا مَعَكُمْ وَلَا تَكُونُٓوا اَوَّلَ كَافِرٍ بِه۪ۖ وَلَا تَشْتَرُوا بِاٰيَات۪ي ثَمَناً قَل۪يلاًۘ وَاِيَّايَ فَاتَّقُونِ', 'Elinizdekini (Tevrat´ın aslını) tasdik edici olarak indirdiğime (Kur´an´a) iman edin. Sakın onu inkâr edenlerin ilki olmayın! Âyetlerimi az bir karşılık ile satmayın, yalnız benden (benim azabımdan) korkun.', 1, 6, 286, 25613, 'Sığır', 'Medine', 87),
(49, 42, 2, 'Bakara', 'وَلَا تَلْبِسُوا الْحَقَّ بِالْبَاطِلِ وَتَكْتُمُوا الْحَقَّ وَاَنْتُمْ تَعْلَمُونَ', 'Bilerek hakkı bâtıl ile karıştırmayın, hakkı gizlemeyin.', 1, 6, 286, 25613, 'Sığır', 'Medine', 87),
(50, 43, 2, 'Bakara', 'وَاَق۪يمُوا الصَّلٰوةَ وَاٰتُوا الزَّكٰوةَ وَارْكَعُوا مَعَ الرَّاكِع۪ينَ', 'Namazı tam kılın, zekâtı hakkıyla verin, rükû edenlerle beraber rükû edin.', 1, 6, 286, 25613, 'Sığır', 'Medine', 87),
(51, 44, 2, 'Bakara', 'اَتَأْمُرُونَ النَّاسَ بِالْبِرِّ وَتَنْسَوْنَ اَنْفُسَكُمْ وَاَنْتُمْ تَتْلُونَ الْكِتَابَۜ اَفَلَا تَعْقِلُونَ', '(Ey bilginler!) Sizler Kitab´ı (Tevrat´ı) okuduğunuz (gerçekleri bildiğiniz) halde, insanlara iyiliği emredip kendinizi unutuyor musunuz? Aklınızı kullanmıyor musunuz?', 1, 6, 286, 25613, 'Sığır', 'Medine', 87),
(52, 45, 2, 'Bakara', 'وَاسْتَع۪ينُوا بِالصَّبْرِ وَالصَّلٰوةِۜ وَاِنَّهَا لَكَب۪يرَةٌ اِلَّا عَلَى الْخَاشِع۪ينَۙ', 'Sabır ve namaz ile Allah´tan yardım isteyin. Şüphesiz o (sabır ve namaz), Allah´a saygıdan kalbi ürperenler dışında herkese zor ve ağır gelen bir görevdir.', 1, 6, 286, 25613, 'Sığır', 'Medine', 87),
(53, 46, 2, 'Bakara', 'اَلَّذ۪ينَ يَظُنُّونَ اَنَّهُمْ مُلَاقُوا رَبِّهِمْ وَاَنَّهُمْ اِلَيْهِ رَاجِعُونَ۟', 'Onlar, kesinlikle Rablerine kavuşacaklarını ve O´na döneceklerini düşünen ve bunu kabullenen kimselerdir.', 1, 6, 286, 25613, 'Sığır', 'Medine', 87),
(54, 47, 2, 'Bakara', 'يَا بَن۪ٓي اِسْرَٓائ۪لَ اذْكُرُوا نِعْمَتِيَ الَّت۪ٓي اَنْعَمْتُ عَلَيْكُمْ وَاَنّ۪ي فَضَّلْتُكُمْ عَلَى الْعَالَم۪ينَ', 'Ey İsrailoğulları! Size verdiğim nimetimi ve sizi (bir zamanlar) cümle âleme üstün kıldığımı hatırlayın.', 1, 6, 286, 25613, 'Sığır', 'Medine', 87),
(55, 48, 2, 'Bakara', 'وَاتَّقُوا يَوْماً لَا تَجْز۪ي نَفْسٌ عَنْ نَفْسٍ شَيْـٔاً وَلَا يُقْبَلُ مِنْهَا شَفَاعَةٌ وَلَا يُؤْخَذُ مِنْهَا عَدْلٌ وَلَا هُمْ يُنْصَرُونَ', 'Öyle bir günden korkun ki, o günde hiç kimse başkası için herhangi bir ödemede bulunamaz; hiç kimseden (Allah izin vermedikçe) şefaat kabul olunmaz, fidye alınmaz; onlara asla yardım da yapılmaz.', 1, 6, 286, 25613, 'Sığır', 'Medine', 87),
(56, 49, 2, 'Bakara', 'وَاِذْ نَجَّيْنَاكُمْ مِنْ اٰلِ فِرْعَوْنَ يَسُومُونَكُمْ سُٓوءَ الْعَذَابِ يُذَبِّحُونَ اَبْنَٓاءَكُمْ وَيَسْتَحْيُونَ نِسَٓاءَكُمْۜ وَف۪ي ذٰلِكُمْ بَلَٓاءٌ مِنْ رَبِّكُمْ عَظ۪يمٌ', 'Hatırlayın ki, sizi, Firavun taraftarlarından kurtardık. Çünkü onlar size azabın en kötüsünü reva görüyorlar, yeni doğan erkek çocuklarınızı kesiyorlar, (fenalık için) kızlarınızı hayatta bırakıyorlardı. Aslında o size reva görülenlerde Rabbinizden büyük bir imtihan vardı.', 1, 7, 286, 25613, 'Sığır', 'Medine', 87),
(57, 50, 2, 'Bakara', 'وَاِذْ فَرَقْنَا بِكُمُ الْبَحْرَ فَاَنْجَيْنَاكُمْ وَاَغْرَقْـنَٓا اٰلَ فِرْعَوْنَ وَاَنْتُمْ تَنْظُرُونَ', 'Bir zamanlar biz sizin için denizi yardık, sizi kurtardık, Firavun´un taraftarlarını da, siz bakıp dururken denizde boğduk.', 1, 7, 286, 25613, 'Sığır', 'Medine', 87),
(58, 51, 2, 'Bakara', 'وَاِذْ وٰعَدْنَا مُوسٰٓى اَرْبَع۪ينَ لَيْلَةً ثُمَّ اتَّخَذْتُمُ الْعِجْلَ مِنْ بَعْدِه۪ وَاَنْتُمْ ظَالِمُونَ', 'Musa´ya kırk gece (vahyetmek üzere) söz vermiştik. Sonra haksızlık ederek buzağıyı (tanrı) edindiniz.', 1, 7, 286, 25613, 'Sığır', 'Medine', 87),
(59, 52, 2, 'Bakara', 'ثُمَّ عَفَوْنَا عَنْكُمْ مِنْ بَعْدِ ذٰلِكَ لَعَلَّكُمْ تَشْكُرُونَ', 'O davranışlarınızdan sonra (akıllanıp) şükredersiniz diye sizi affettik.', 1, 7, 286, 25613, 'Sığır', 'Medine', 87),
(60, 53, 2, 'Bakara', 'وَاِذْ اٰتَيْنَا مُوسَى الْكِتَابَ وَالْفُرْقَانَ لَعَلَّكُمْ تَهْتَدُونَ', 'Doğru yolu bulasınız diye Musa´ya Kitab´ı ve hak ile bâtılı ayıran hükümleri verdik.', 1, 7, 286, 25613, 'Sığır', 'Medine', 87),
(61, 54, 2, 'Bakara', 'وَاِذْ قَالَ مُوسٰى لِقَوْمِه۪ يَا قَوْمِ اِنَّكُمْ ظَلَمْتُمْ اَنْفُسَكُمْ بِاتِّخَاذِكُمُ الْعِجْلَ فَتُوبُٓوا اِلٰى بَارِئِكُمْ فَاقْتُلُٓوا اَنْفُسَكُمْۜ ذٰلِكُمْ خَيْرٌ لَكُمْ عِنْدَ بَارِئِكُمْۜ فَتَابَ عَلَيْكُمْۜ اِنَّهُ هُوَ التَّوَّابُ الرَّح۪يمُ', 'Musa kavmine demişti ki: Ey kavmim! Şüphesiz siz, buzağıyı (tanrı) edinmekle kendinize kötülük ettiniz. Onun için Yaradanınıza tevbe edin de nefislerinizi (kötü duygularınızı) öldürün. Öyle yapmanız Yaratıcınızın katında sizin için daha iyidir. Böylece Allah tevbenizi kabul etmiş olur. Çünkü acıyıp tevbeleri kabul eden ancak O´dur.', 1, 7, 286, 25613, 'Sığır', 'Medine', 87),
(62, 55, 2, 'Bakara', 'وَاِذْ قُلْتُمْ يَا مُوسٰى لَنْ نُؤْمِنَ لَكَ حَتّٰى نَرَى اللّٰهَ جَهْرَةً فَاَخَذَتْكُمُ الصَّاعِقَةُ وَاَنْتُمْ تَنْظُرُونَ', 'Bir zamanlar: Ey Musa! Biz Allah´ı açıkça görmedikçe asla sana inanmayız, demiştiniz de bakıp durur olduğunuz halde hemen sizi yıldırım çarpmıştı.', 1, 7, 286, 25613, 'Sığır', 'Medine', 87),
(63, 56, 2, 'Bakara', 'ثُمَّ بَعَثْنَاكُمْ مِنْ بَعْدِ مَوْتِكُمْ لَعَلَّكُمْ تَشْكُرُونَ', 'Sonra ölümünüzün ardından sizi dirilttik ki şükredesiniz.', 1, 7, 286, 25613, 'Sığır', 'Medine', 87),
(64, 57, 2, 'Bakara', 'وَظَلَّلْنَا عَلَيْكُمُ الْغَمَامَ وَاَنْزَلْنَا عَلَيْكُمُ الْمَنَّ وَالسَّلْوٰىۜ كُلُوا مِنْ طَيِّبَاتِ مَا رَزَقْنَاكُمْۜ وَمَا ظَلَمُونَا وَلٰكِنْ كَانُٓوا اَنْفُسَهُمْ يَظْلِمُونَ', 'Ve sizi bulutla gölgeledik, size kudret helvası ve bıldırcın gönderdik ve «Verdiğimiz güzel nimetlerden yeyiniz» (dedik). Hakikatta onlar bize değil sadece kendilerine kötülük ediyorlardı.', 1, 7, 286, 25613, 'Sığır', 'Medine', 87),
(65, 58, 2, 'Bakara', 'وَاِذْ قُلْنَا ادْخُلُوا هٰذِهِ الْقَرْيَةَ فَكُلُوا مِنْهَا حَيْثُ شِئْتُمْ رَغَداً وَادْخُلُوا الْبَابَ سُجَّداً وَقُولُوا حِطَّةٌ نَغْفِرْ لَكُمْ خَطَايَاكُمْۜ وَسَنَز۪يدُ الْمُحْسِن۪ينَ', '(İsrailoğullarına:) Bu kasabaya girin, orada bulunanlardan dilediğiniz şekilde bol bol yeyin, kapısından eğilerek girin, (girerken) «Hıtta!» (Yâ Rabbi bizi affet) deyin ki, sizin hatalarınızı bağışlayalım; zira biz, iyi davrananlara (karşılığını) fazlasıyla vereceğiz, demiştik.', 1, 8, 286, 25613, 'Sığır', 'Medine', 87),
(66, 59, 2, 'Bakara', 'فَبَدَّلَ الَّذ۪ينَ ظَلَمُوا قَوْلاً غَيْرَ الَّذ۪ي ق۪يلَ لَهُمْ فَاَنْزَلْنَا عَلَى الَّذ۪ينَ ظَلَمُوا رِجْزاً مِنَ السَّمَٓاءِ بِمَا كَانُوا يَفْسُقُونَ۟', 'Fakat zalimler, kendilerine söylenenleri başka sözlerle değiştirdiler. Bunun üzerine biz, yapmakta oldukları kötülükler sebebiyle zalimlerin üzerine gökten acı bir azap indirdik.', 1, 8, 286, 25613, 'Sığır', 'Medine', 87),
(67, 60, 2, 'Bakara', 'وَاِذِ اسْتَسْقٰى مُوسٰى لِقَوْمِه۪ فَقُلْنَا اضْرِبْ بِعَصَاكَ الْحَجَرَۜ فَانْفَجَرَتْ مِنْهُ اثْنَتَا عَشْرَةَ عَيْناًۜ قَدْ عَلِمَ كُلُّ اُنَاسٍ مَشْرَبَهُمْۜ كُلُوا وَاشْرَبُوا مِنْ رِزْقِ اللّٰهِ وَلَا تَعْثَوْا فِي الْاَرْضِ مُفْسِد۪ينَ', 'Musa (çölde) kavmi için su istemişti de biz ona: Değneğinle taşa vur! demiştik. Derhal (taştan) oniki kaynak fışkırdı. Her bölük, içeceği kaynağı bildi. (Onlara:) Allah´ın rızkından yeyin, için, sakın yeryüzünde bozgunculuk etmeyin, dedik.', 1, 8, 286, 25613, 'Sığır', 'Medine', 87),
(68, 61, 2, 'Bakara', 'وَاِذْ قُلْتُمْ يَا مُوسٰى لَنْ نَصْبِرَ عَلٰى طَعَامٍ وَاحِدٍ فَادْعُ لَنَا رَبَّكَ يُخْرِجْ لَنَا مِمَّا تُنْبِتُ الْاَرْضُ مِنْ بَقْلِهَا وَقِثَّٓائِهَا وَفُومِهَا وَعَدَسِهَا وَبَصَلِهَاۜ قَالَ اَتَسْتَبْدِلُونَ الَّذ۪ي هُوَ اَدْنٰى بِالَّذ۪ي هُوَ خَيْرٌۜ اِهْبِطُوا مِصْراً فَاِنَّ لَكُمْ مَا سَاَلْتُمْۜ وَضُرِبَتْ عَلَيْهِمُ الذِّلَّةُ وَالْمَسْكَنَةُ وَبَٓاؤُ۫ بِغَضَبٍ مِنَ اللّٰهِۜ ذٰلِكَ بِاَنَّهُمْ كَانُوا يَكْفُرُونَ بِاٰيَاتِ اللّٰهِ وَيَقْتُلُونَ النَّبِيّ۪نَ بِغَيْرِ الْحَقِّۜ ذٰلِكَ بِمَا عَصَوْا وَكَانُوا يَعْتَدُونَ۟', 'Hani siz (verilen nimetlere karşılık): Ey Musa! Bir tek yemekle yetinemeyiz; bizim için Rabbine dua et de yerin bitirdiği şeylerden; sebzesinden, hıyarından, sarımsağından, mercimeğinden, soğanından bize çıkarsın, dediniz. Musa ise: Daha iyiyi daha kötü ile değiştirmek mi istiyorsunuz? O halde şehre inin. Zira istedikleriniz sizin için orada var, dedi. İşte (bu hadiseden sonra) üzerlerine aşağılık ve yoksulluk damgası vuruldu. Allah´ın gazabına uğradılar. Bu musibetler (onların başına), Allah´ın âyetlerini inkâra devam etmeleri, haksız olarak peygamberleri öldürmeleri sebebiyle geldi. Bunların hepsi, sadece isyanları ve taşkınlıkları sebebiyledir.', 1, 8, 286, 25613, 'Sığır', 'Medine', 87),
(69, 62, 2, 'Bakara', 'اِنَّ الَّذ۪ينَ اٰمَنُوا وَالَّذ۪ينَ هَادُوا وَالنَّصَارٰى وَالصَّابِـ۪ٔينَ مَنْ اٰمَنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَعَمِلَ صَالِحاً فَلَهُمْ اَجْرُهُمْ عِنْدَ رَبِّهِمْۖ وَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ', 'Şüphesiz iman edenler; yahudilerden, hıristiyanlardan ve sâbiîlerden de Allah´a ve ahiret gününe inanıp sâlih amel işleyenler için Rableri katında mükâfatlar vardır. Onlar için herhangi bir korku yoktur onlar üzüntü çekmeyeceklerdir.', 1, 9, 286, 25613, 'Sığır', 'Medine', 87),
(70, 63, 2, 'Bakara', 'وَاِذْ اَخَذْنَا م۪يثَاقَكُمْ وَرَفَعْنَا فَوْقَكُمُ الطُّورَۜ خُذُوا مَٓا اٰتَيْنَاكُمْ بِقُوَّةٍ وَاذْكُرُوا مَا ف۪يهِ لَعَلَّكُمْ تَتَّقُونَ', 'Sizden sağlam bir söz almış, Tûr dağının altında, size verdiğimizi kuvvetle tutun, onda bulunanları daima hatırlayın, umulur ki, korunursunuz (demiştik de);', 1, 9, 286, 25613, 'Sığır', 'Medine', 87),
(71, 64, 2, 'Bakara', 'ثُمَّ تَوَلَّيْتُمْ مِنْ بَعْدِ ذٰلِكَۚ فَلَوْلَا فَضْلُ اللّٰهِ عَلَيْكُمْ وَرَحْمَتُهُ لَكُنْتُمْ مِنَ الْخَاسِر۪ينَ', 'Ondan sonra sözünüzden dönmüştünüz. Eğer sizin üzerinizde Allah´ın ihsanı ve rahmeti olmasaydı, muhakkak zarara uğrayanlardan olurdunuz.', 1, 9, 286, 25613, 'Sığır', 'Medine', 87),
(72, 65, 2, 'Bakara', 'وَلَقَدْ عَلِمْتُمُ الَّذ۪ينَ اعْتَدَوْا مِنْكُمْ فِي السَّبْتِ فَقُلْنَا لَهُمْ كُونُوا قِرَدَةً خَاسِـ۪ٔينَۚ', 'İçinizden cumartesi günü azgınlık edip de, bu yüzden kendilerine: Aşağılık maymunlar olun! dediklerimizi elbette bilmektesiniz.', 1, 9, 286, 25613, 'Sığır', 'Medine', 87),
(73, 66, 2, 'Bakara', 'فَجَعَلْنَاهَا نَكَالاً لِمَا بَيْنَ يَدَيْهَا وَمَا خَلْفَهَا وَمَوْعِظَةً لِلْمُتَّق۪ينَ', 'Biz bunu (maymunlaşmış insanları), hadiseyi bizzat görenlere ve sonradan gelenlere bir ibret dersi, müttakîler için de bir öğüt vesilesi kıldık.', 1, 9, 286, 25613, 'Sığır', 'Medine', 87),
(74, 67, 2, 'Bakara', 'وَاِذْ قَالَ مُوسٰى لِقَوْمِه۪ٓ اِنَّ اللّٰهَ يَأْمُرُكُمْ اَنْ تَذْبَحُوا بَقَرَةًۜ قَالُٓوا اَتَتَّخِذُنَا هُزُواًۜ قَالَ اَعُوذُ بِاللّٰهِ اَنْ اَكُونَ مِنَ الْجَاهِل۪ينَ', 'Musa, kavmine: Allah bir sığır kesmenizi emrediyor, demişti de: Bizimle alay mı ediyorsun? demişlerdi. O da: Cahillerden olmaktan Allah´a sığınırım, demişti.', 1, 9, 286, 25613, 'Sığır', 'Medine', 87),
(75, 68, 2, 'Bakara', 'قَالُوا ادْعُ لَنَا رَبَّكَ يُبَيِّنْ لَنَا مَا هِيَۜ قَالَ اِنَّهُ يَقُولُ اِنَّهَا بَقَرَةٌ لَا فَارِضٌ وَلَا بِكْرٌۜ عَوَانٌ بَيْنَ ذٰلِكَۜ فَافْعَلُوا مَا تُؤْمَرُونَ', '«Bizim adımıza Rabbine dua et, bize onun ne olduğunu açıklasın» dediler. Musa: Allah diyor ki: «O, ne yaşlı ne de körpe; ikisi arasında bir inek.» Size emredileni hemen yapın, dedi.', 1, 9, 286, 25613, 'Sığır', 'Medine', 87),
(76, 69, 2, 'Bakara', 'قَالُوا ادْعُ لَنَا رَبَّكَ يُبَيِّنْ لَنَا مَا لَوْنُهَاۜ قَالَ اِنَّهُ يَقُولُ اِنَّهَا بَقَرَةٌ صَفْرَٓاءُۙ فَاقِعٌ لَوْنُهَا تَسُرُّ النَّاظِر۪ينَ', 'Bu defa: Bizim için Rabbine dua et, bize onun rengini açıklasın, dediler. «O diyor ki: Sarı renkli, parlak tüylü, bakanların içini açan bir inektir» dedi.', 1, 9, 286, 25613, 'Sığır', 'Medine', 87),
(77, 70, 2, 'Bakara', 'قَالُوا ادْعُ لَنَا رَبَّكَ يُبَيِّنْ لَنَا مَا هِيَۙ اِنَّ الْبَقَرَ تَشَابَهَ عَلَيْنَاۜ وَاِنَّٓا اِنْ شَٓاءَ اللّٰهُ لَمُهْتَدُونَ', '«(Ey Musa!) Bizim için, Rabbine dua et de onun nasıl bir sığır olduğunu bize açıklasın, nasıl bir inek keseceğimizi anlayamadık. Biz, inşaallah emredileni yapma yolunu buluruz» dediler.', 1, 10, 286, 25613, 'Sığır', 'Medine', 87),
(78, 71, 2, 'Bakara', 'قَالَ اِنَّهُ يَقُولُ اِنَّهَا بَقَرَةٌ لَا ذَلُولٌ تُث۪يرُ الْاَرْضَ وَلَا تَسْقِي الْحَرْثَۚ مُسَلَّمَةٌ لَا شِيَةَ ف۪يهَاۜ قَالُوا الْـٰٔنَ جِئْتَ بِالْحَقِّۜ فَذَبَحُوهَا وَمَا كَادُوا يَفْعَلُونَ۟', '(Musa) dedi ki: Allah şöyle buyuruyor: O, henüz boyunduruk altına alınmayan, yer sürmeyen, ekin sulamayan, serbest dolaşan (salma), renginde hiç alacası bulunmayan bir inektir. «İşte şimdi gerçeği anlattın» dediler ve bunun üzerine (onu bulup) kestiler, ama az kalsın kesmeyeceklerdi.', 1, 10, 286, 25613, 'Sığır', 'Medine', 87),
(79, 72, 2, 'Bakara', 'وَاِذْ قَتَلْتُمْ نَفْساً فَادّٰرَءْتُمْ ف۪يهَاۜ وَاللّٰهُ مُخْرِجٌ مَا كُنْتُمْ تَكْتُمُونَۚ', 'Hani siz bir adam öldürmüştünüz de onun hakkında birbirinizle atışmıştınız. Halbuki Allah gizlemekte olduğunuzu ortaya çıkaracaktır.', 1, 10, 286, 25613, 'Sığır', 'Medine', 87),
(80, 73, 2, 'Bakara', 'فَقُلْنَا اضْرِبُوهُ بِبَعْضِهَاۜ كَذٰلِكَ يُحْـيِ اللّٰهُ الْمَوْتٰى وَيُر۪يكُمْ اٰيَاتِه۪ لَعَلَّكُمْ تَعْقِلُونَ', '«Haydi, şimdi (öldürülen) adama, (kesilen ineğin) bir parçasıyla vurun» dedik. Böylece Allah ölüleri diriltir ve düşünesiniz diye size âyetlerini (Peygamberine verdiği mucizelerini) gösterir.', 1, 10, 286, 25613, 'Sığır', 'Medine', 87),
(81, 74, 2, 'Bakara', 'ثُمَّ قَسَتْ قُلُوبُكُمْ مِنْ بَعْدِ ذٰلِكَ فَهِيَ كَالْحِجَارَةِ اَوْ اَشَدُّ قَسْوَةًۜ وَاِنَّ مِنَ الْحِجَارَةِ لَمَا يَتَفَجَّرُ مِنْهُ الْاَنْهَارُۜ وَاِنَّ مِنْهَا لَمَا يَشَّقَّقُ فَيَخْرُجُ مِنْهُ الْمَٓاءُۜ وَاِنَّ مِنْهَا لَمَا يَهْبِطُ مِنْ خَشْيَةِ اللّٰهِۜ وَمَا اللّٰهُ بِغَافِلٍ عَمَّا تَعْمَلُونَ', '(Ne var ki) bunlardan sonra yine kalpleriniz katılaştı. Artık kalpleriniz taş gibi yahut daha da katıdır. Çünkü taşlardan öylesi var ki, içinden ırmaklar kaynar. Öylesi de var ki, çatlar da ondan su fışkırır. Taşlardan bir kısmı da Allah korkusuyla yukardan aşağı yuvarlanır. Allah yapmakta olduklarınızdan gafil değildir.', 1, 10, 286, 25613, 'Sığır', 'Medine', 87),
(82, 75, 2, 'Bakara', 'اَفَتَطْمَعُونَ اَنْ يُؤْمِنُوا لَكُمْ وَقَدْ كَانَ فَر۪يقٌ مِنْهُمْ يَسْمَعُونَ كَلَامَ اللّٰهِ ثُمَّ يُحَرِّفُونَهُ مِنْ بَعْدِ مَا عَقَلُوهُ وَهُمْ يَعْلَمُونَ', 'Şimdi (ey müminler!) onların size inanacaklarını mı umuyorsunuz? Oysa ki, onlardan bir zümre, Allah´ın kelâmını işitirler de iyice anladıktan sonra, bile bile onu tahrif ederlerdi.', 1, 10, 286, 25613, 'Sığır', 'Medine', 87),
(83, 76, 2, 'Bakara', 'وَاِذَا لَقُوا الَّذ۪ينَ اٰمَنُوا قَالُٓوا اٰمَنَّاۚ وَاِذَا خَلَا بَعْضُهُمْ اِلٰى بَعْضٍ قَالُٓوا اَتُحَدِّثُونَهُمْ بِمَا فَتَحَ اللّٰهُ عَلَيْكُمْ لِيُحَٓاجُّوكُمْ بِه۪ عِنْدَ رَبِّكُمْۜ اَفَلَا تَعْقِلُونَ', '(Münafıklar) inananlarla karşılaştıklarında «İman ettik» derler. Birbirleriyle başbaşa kaldıkları vakit ise: Allah´ın size açtıklarını (Tevrat´taki bilgileri), Rabbiniz katında sizin aleyhinize hüccet getirmeleri için mi onlara anlatıyorsunuz; bunları düşünemiyor musunuz? derler.', 1, 10, 286, 25613, 'Sığır', 'Medine', 87),
(84, 77, 2, 'Bakara', 'اَوَلَا يَعْلَمُونَ اَنَّ اللّٰهَ يَعْلَمُ مَا يُسِرُّونَ وَمَا يُعْلِنُونَ', 'Onlar bilmezler mi ki, gizlediklerini de açıkça yaptıklarını da Allah bilmektedir.', 1, 11, 286, 25613, 'Sığır', 'Medine', 87),
(85, 78, 2, 'Bakara', 'وَمِنْهُمْ اُمِّيُّونَ لَا يَعْلَمُونَ الْكِتَابَ اِلَّٓا اَمَانِيَّ وَاِنْ هُمْ اِلَّا يَظُنُّونَ', 'İçlerinde bir takım ümmîler vardır ki, Kitab´ı (Tevrat´ı) bilmezler. Bütün bildikleri kulaktan dolma şeylerdir. Onlar sadece zan ve tahminde bulunuyorlar.', 1, 11, 286, 25613, 'Sığır', 'Medine', 87),
(86, 79, 2, 'Bakara', 'فَوَيْلٌ لِلَّذ۪ينَ يَكْتُبُونَ الْكِتَابَ بِاَيْد۪يهِمْ ثُمَّ يَقُولُونَ هٰذَا مِنْ عِنْدِ اللّٰهِ لِيَشْتَرُوا بِه۪ ثَمَناً قَل۪يلاًۜ فَوَيْلٌ لَهُمْ مِمَّا كَتَبَتْ اَيْد۪يهِمْ وَوَيْلٌ لَهُمْ مِمَّا يَكْسِبُونَ', 'Elleriyle (bir) Kitap yazıp sonra onu az bir bedel karşılığında satmak için «Bu Allah katındandır» diyenlere yazıklar olsun! Elleriyle yazdıklarından ötürü vay haline onların! Ve kazandıklarından ötürü vay haline onların!', 1, 11, 286, 25613, 'Sığır', 'Medine', 87),
(87, 80, 2, 'Bakara', 'وَقَالُوا لَنْ تَمَسَّنَا النَّارُ اِلَّٓا اَيَّاماً مَعْدُودَةًۜ قُلْ اَتَّخَذْتُمْ عِنْدَ اللّٰهِ عَهْداً فَلَنْ يُخْلِفَ اللّٰهُ عَهْدَهُٓ اَمْ تَقُولُونَ عَلَى اللّٰهِ مَا لَا تَعْلَمُونَ', 'İsrailoğulları: Sayılı birkaç gün müstesna, bize ateş dokunmayacaktır, dediler. De ki (onlara): Siz Allah katından bir söz mü aldınız -ki Allah sözünden caymaz-, yoksa Allah hakkında bilmediğiniz şeyleri mi söylüyorsunuz?', 1, 11, 286, 25613, 'Sığır', 'Medine', 87),
(88, 81, 2, 'Bakara', 'بَلٰى مَنْ كَسَبَ سَيِّئَةً وَاَحَاطَتْ بِه۪ خَط۪ٓيـَٔتُهُ فَاُو۬لٰٓئِكَ اَصْحَابُ النَّارِۚ هُمْ ف۪يهَا خَالِدُونَ', 'Hayır! Kim bir kötülük eder de kötülüğü kendisini çepeçevre kuşatırsa işte o kimseler cehennemliktirler. Onlar orada devamlı kalırlar.', 1, 11, 286, 25613, 'Sığır', 'Medine', 87),
(89, 82, 2, 'Bakara', 'وَالَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ اُو۬لٰٓئِكَ اَصْحَابُ الْجَنَّةِۚ هُمْ ف۪يهَا خَالِدُونَ۟', 'İman edip yararlı iş yapanlara gelince onlar da cennetliktirler. Onlar orada devamlı kalırlar.', 1, 11, 286, 25613, 'Sığır', 'Medine', 87),
(90, 83, 2, 'Bakara', 'وَاِذْ اَخَذْنَا م۪يثَاقَ بَن۪ٓي اِسْرَٓائ۪لَ لَا تَعْبُدُونَ اِلَّا اللّٰهَ وَبِالْوَالِدَيْنِ اِحْسَاناً وَذِي الْقُرْبٰى وَالْيَتَامٰى وَالْمَسَاك۪ينِ وَقُولُوا لِلنَّاسِ حُسْناً وَاَق۪يمُوا الصَّلٰوةَ وَاٰتُوا الزَّكٰوةَۜ ثُمَّ تَوَلَّيْتُمْ اِلَّا قَل۪يلاً مِنْكُمْ وَاَنْتُمْ مُعْرِضُونَ', 'Vaktiyle biz, İsrailoğullarından: Yalnızca Allah´a kulluk edeceksiniz, ana-babaya, yakın akrabaya, yetimlere, yoksullara iyilik edeceksiniz diye söz almış ve «İnsanlara güzel söz söyleyin, namazı kılın, zekâtı verin» diye de emretmiştik. Sonunda azınız müstesna, yüz çevirerek dönüp gittiniz.', 1, 11, 286, 25613, 'Sığır', 'Medine', 87),
(91, 84, 2, 'Bakara', 'وَاِذْ اَخَذْنَا م۪يثَاقَكُمْ لَا تَسْفِكُونَ دِمَٓاءَكُمْ وَلَا تُخْرِجُونَ اَنْفُسَكُمْ مِنْ دِيَارِكُمْ ثُمَّ اَقْرَرْتُمْ وَاَنْتُمْ تَشْهَدُونَ', '(Ey İsrailoğulları!) Birbirinizin kanını dökmeyeceğinize, birbirinizi yurtlarınızdan çıkarmayacağınıza dair sizden söz almıştık. Her şeyi görerek sonunda bunları kabul etmiştiniz.', 1, 12, 286, 25613, 'Sığır', 'Medine', 87),
(92, 85, 2, 'Bakara', 'ثُمَّ اَنْتُمْ هٰٓؤُ۬لَٓاءِ تَقْتُلُونَ اَنْفُسَكُمْ وَتُخْرِجُونَ فَر۪يقاً مِنْكُمْ مِنْ دِيَارِهِمْۘ تَظَاهَرُونَ عَلَيْهِمْ بِالْاِثْمِ وَالْعُدْوَانِۜ وَاِنْ يَأْتُوكُمْ اُسَارٰى تُفَادُوهُمْ وَهُوَ مُحَرَّمٌ عَلَيْكُمْ اِخْرَاجُهُمْۜ اَفَتُؤْمِنُونَ بِبَعْضِ الْكِتَابِ وَتَكْفُرُونَ بِبَعْضٍۚ فَمَا جَزَٓاءُ مَنْ يَفْعَلُ ذٰلِكَ مِنْكُمْ اِلَّا خِزْيٌ فِي الْحَيٰوةِ الدُّنْيَاۚ وَيَوْمَ الْقِيٰمَةِ يُرَدُّونَ اِلٰٓى اَشَدِّ الْعَذَابِۜ وَمَا اللّٰهُ بِغَافِلٍ عَمَّا تَعْمَلُونَ', 'Bu misakı kabul eden sizler, (verdiğiniz sözün tersine) birbirinizi öldürüyor, aranızdan bir zümreyi yurtlarından çıkarıyor, kötülük ve düşmanlıkta onlara karşı birleşiyorsunuz. Onları yurtlarından çıkarmak size haram olduğu halde (hem çıkarıyor hem de) size esirler olarak geldiklerinde fidye verip onları kurtarıyorsunuz. Yoksa siz Kitab´ın bir kısmına inanıp bir kısmını inkâr mı ediyorsunuz? Sizden öyle davrananların cezası dünya hayatında ancak rüsvaylık; kıyamet gününde ise en şiddetli azaba itilmektir. Allah sizin yapmakta olduklarınızdan asla gafil değildir.', 1, 12, 286, 25613, 'Sığır', 'Medine', 87),
(93, 86, 2, 'Bakara', 'اُو۬لٰٓئِكَ الَّذ۪ينَ اشْتَرَوُا الْحَيٰوةَ الدُّنْيَا بِالْاٰخِرَةِۘ فَلَا يُخَفَّفُ عَنْهُمُ الْعَذَابُ وَلَا هُمْ يُنْصَرُونَ۟', 'İşte onlar, ahirete karşılık dünya hayatını satın alan kimselerdir. Bu yüzden ne azapları hafifletilecek ne de kendilerine yardım edilecektir.', 1, 12, 286, 25613, 'Sığır', 'Medine', 87),
(94, 87, 2, 'Bakara', 'وَلَقَدْ اٰتَيْنَا مُوسَى الْكِتَابَ وَقَفَّيْنَا مِنْ بَعْدِه۪ بِالرُّسُلِ وَاٰتَيْنَا ع۪يسَى ابْنَ مَرْيَمَ الْبَيِّنَاتِ وَاَيَّدْنَاهُ بِرُوحِ الْقُدُسِۜ اَفَكُلَّمَا جَٓاءَكُمْ رَسُولٌ بِمَا لَا تَهْوٰٓى اَنْفُسُكُمُ اسْتَكْبَرْتُمْۚ فَفَر۪يقاً كَذَّبْتُمْۘ وَفَر۪يقاً تَقْتُلُونَ', 'Andolsun biz Musa´ya Kitab´ı verdik. Ondan sonra ardarda peygamberler gönderdik. Meryem oğlu İsa´ya da deliller verdik. Ve onu, Rûhu´l-Kudüs (Cebrail) ile destekledik. Ama ne zaman size bir peygamber nefislerinizin hoşlanmadığı bir şey getirdiyse büyüklük taslayarak kimini yalanladığınız kimini de öldürdüğünüz doğru değil mi!', 1, 12, 286, 25613, 'Sığır', 'Medine', 87),
(95, 88, 2, 'Bakara', 'وَقَالُوا قُلُوبُنَا غُلْفٌۜ بَلْ لَعَنَهُمُ اللّٰهُ بِكُفْرِهِمْ فَقَل۪يلاً مَا يُؤْمِنُونَ', '(Yahudiler peygamberlerle alay ederek) «Kalplerimiz perdelidir» dediler. Hayır; küfür ve isyanları sebebiyle Allah onlara lânet etmiştir. O yüzden çok az inanırlar.', 1, 12, 286, 25613, 'Sığır', 'Medine', 87),
(96, 89, 2, 'Bakara', 'وَلَمَّا جَٓاءَهُمْ كِتَابٌ مِنْ عِنْدِ اللّٰهِ مُصَدِّقٌ لِمَا مَعَهُمْۙ وَكَانُوا مِنْ قَبْلُ يَسْتَفْتِحُونَ عَلَى الَّذ۪ينَ كَفَرُواۚ فَلَمَّا جَٓاءَهُمْ مَا عَرَفُوا كَفَرُوا بِه۪ۘ فَلَعْنَةُ اللّٰهِ عَلَى الْكَافِر۪ينَ', 'Daha önce kâfirlere karşı zafer isterlerken kendilerine Allah katından ellerindeki (Tevrat´ı) doğrulayan bir kitap gelip de (Tevrat´tan) bilip öğrendikleri gerçekler karşılarına dikilince onu inkâr ettiler. İşte Allah´ın lâneti böyle inkârcılaradır.', 1, 13, 286, 25613, 'Sığır', 'Medine', 87),
(97, 90, 2, 'Bakara', 'بِئْسَمَا اشْتَرَوْا بِه۪ٓ اَنْفُسَهُمْ اَنْ يَكْفُرُوا بِمَٓا اَنْزَلَ اللّٰهُ بَغْياً اَنْ يُنَزِّلَ اللّٰهُ مِنْ فَضْلِه۪ عَلٰى مَنْ يَشَٓاءُ مِنْ عِبَادِه۪ۚ فَبَٓاؤُ۫ بِغَضَبٍ عَلٰى غَضَبٍۜ وَلِلْكَافِر۪ينَ عَذَابٌ مُه۪ينٌ', 'Allah´ın kullarından dilediğine peygamberlik ihsan etmesini kıskandıkları için Allah´ın indirdiğini (Kur´an´ı) inkâr ederek kendilerini harcamaları ne kötü bir şeydir! Böylece onlar, gazap üstüne gazaba uğradılar. Ayrıca kâfirler için alçaltıcı bir azap vardır.', 1, 13, 286, 25613, 'Sığır', 'Medine', 87),
(98, 91, 2, 'Bakara', 'وَاِذَا ق۪يلَ لَهُمْ اٰمِنُوا بِمَٓا اَنْزَلَ اللّٰهُ قَالُوا نُؤْمِنُ بِمَٓا اُنْزِلَ عَلَيْنَا وَيَكْفُرُونَ بِمَا وَرَٓاءَهُ وَهُوَ الْحَقُّ مُصَدِّقاً لِمَا مَعَهُمْۜ قُلْ فَلِمَ تَقْتُلُونَ اَنْبِيَٓاءَ اللّٰهِ مِنْ قَبْلُ اِنْ كُنْتُمْ مُؤْمِن۪ينَ', 'Kendilerine: Allah´ın indirdiğine iman edin, denilince: Biz sadece bize indirilene (Tevrat´a) inanırız, derler ve ondan başkasını inkâr ederler. Halbuki o Kur´an, kendi ellerinde bulunan Tevrat´ı doğrulayıcı olarak gelmiş hak kitaptır. (Ey Muhammed!) Onlara: Şayet siz gerçekten inanıyor idiyseniz daha önce Allah´ın peygamberlerini neden öldürüyordunuz? deyiver.', 1, 13, 286, 25613, 'Sığır', 'Medine', 87),
(99, 92, 2, 'Bakara', 'وَلَقَدْ جَٓاءَكُمْ مُوسٰى بِالْبَيِّنَاتِ ثُمَّ اتَّخَذْتُمُ الْعِجْلَ مِنْ بَعْدِه۪ وَاَنْتُمْ ظَالِمُونَ', 'Andolsun Musa size apaçık mucizeler getirmişti. Sonra onun ardından, zalimler olarak buzağıyı (tanrı) edindiniz.', 1, 13, 286, 25613, 'Sığır', 'Medine', 87),
(100, 93, 2, 'Bakara', 'وَاِذْ اَخَذْنَا م۪يثَاقَكُمْ وَرَفَعْنَا فَوْقَكُمُ الطُّورَۜ خُذُوا مَٓا اٰتَيْنَاكُمْ بِقُوَّةٍ وَاسْمَعُواۜ قَالُوا سَمِعْنَا وَعَصَيْنَا وَاُشْرِبُوا ف۪ي قُلُوبِهِمُ الْعِجْلَ بِكُفْرِهِمْۜ قُلْ بِئْسَمَا يَأْمُرُكُمْ بِه۪ٓ ا۪يمَانُكُمْ اِنْ كُنْتُمْ مُؤْمِن۪ينَ', 'Hatırlayın ki, Tûr dağının altında sizden söz almış: Size verdiklerimizi kuvvetlice tutun, söylenenleri anlayın, demiştik. Onlar: İşittik ve isyan ettik, dediler. İnkârları sebebiyle kalplerine buzağı sevgisi dolduruldu. De ki: Eğer inanıyorsanız, imanınız size ne kötü şeyler emrediyor!', 1, 13, 286, 25613, 'Sığır', 'Medine', 87),
(101, 94, 2, 'Bakara', 'قُلْ اِنْ كَانَتْ لَكُمُ الدَّارُ الْاٰخِرَةُ عِنْدَ اللّٰهِ خَالِصَةً مِنْ دُونِ النَّاسِ فَتَمَنَّوُا الْمَوْتَ اِنْ كُنْتُمْ صَادِق۪ينَ', '(Ey Muhammed, onlara:) Şayet (iddia ettiğiniz gibi) ahiret yurdu Allah katında diğer insanlara değil de yalnızca size aitse ve bu iddianızda doğru iseniz haydi ölümü temenni edin (bakalım), de.', 1, 14, 286, 25613, 'Sığır', 'Medine', 87),
(102, 95, 2, 'Bakara', 'وَلَنْ يَتَمَنَّوْهُ اَبَداً بِمَا قَدَّمَتْ اَيْد۪يهِمْۜ وَاللّٰهُ عَل۪يمٌ بِالظَّالِم۪ينَ', 'Onlar, kendi elleriyle önceden yaptıkları işler (günah ve isyanları) sebebiyle hiç bir zaman ölümü temenni etmeyeceklerdir. Allah zalimleri iyi bilir.', 1, 14, 286, 25613, 'Sığır', 'Medine', 87),
(103, 96, 2, 'Bakara', 'وَلَتَجِدَنَّهُمْ اَحْرَصَ النَّاسِ عَلٰى حَيٰوةٍۚ وَمِنَ الَّذ۪ينَ اَشْرَكُوا يَوَدُّ اَحَدُهُمْ لَوْ يُعَمَّرُ اَلْفَ سَنَةٍۚ وَمَا هُوَ بِمُزَحْزِحِه۪ مِنَ الْعَذَابِ اَنْ يُعَمَّرَۜ وَاللّٰهُ بَص۪يرٌ بِمَا يَعْمَلُونَ۟', 'Yemin olsun ki, sen onları yaşamaya karşı insanların en düşkünü olarak bulursun. Putperestlerden her biri de arzular ki, bin sene yaşasın. Oysa yaşatılması onu azaptan uzaklaştırmaz. Allah onların yapmakta olduklarını eksiksiz görür.', 1, 14, 286, 25613, 'Sığır', 'Medine', 87),
(104, 97, 2, 'Bakara', 'قُلْ مَنْ كَانَ عَدُواًّ لِجِبْر۪يلَ فَاِنَّهُ نَزَّلَهُ عَلٰى قَلْبِكَ بِاِذْنِ اللّٰهِ مُصَدِّقاً لِمَا بَيْنَ يَدَيْهِ وَهُدًى وَبُشْرٰى لِلْمُؤْمِن۪ينَ', 'De ki: Cebrail´e kim düşman ise şunu iyi bilsin ki Allah´ın izniyle Kur´an´ı senin kalbine bir hidayet rehberi, önce gelen kitapları doğrulayıcı ve müminler için de müjdeci olarak o indirmiştir.', 1, 14, 286, 25613, 'Sığır', 'Medine', 87),
(105, 98, 2, 'Bakara', 'مَنْ كَانَ عَدُواًّ لِلّٰهِ وَمَلٰٓئِكَتِه۪ وَرُسُلِه۪ وَجِبْر۪يلَ وَم۪يكَالَ فَاِنَّ اللّٰهَ عَدُوٌّ لِلْكَافِر۪ينَ', 'Kim, Allah´a, meleklerine, peygamberlerine, Cebrail´e ve Mikâil´e düşman olursa bilsin ki Allah da inkârcı kâfirlerin düşmanıdır.', 1, 14, 286, 25613, 'Sığır', 'Medine', 87),
(106, 99, 2, 'Bakara', 'وَلَقَدْ اَنْزَلْـنَٓا اِلَيْكَ اٰيَاتٍ بَيِّنَاتٍۚ وَمَا يَكْفُرُ بِهَٓا اِلَّا الْفَاسِقُونَ', 'Andolsun ki sana apaçık âyetler indirdik. (Ey Muhammed!) Onları ancak fasıklar inkâr eder.', 1, 14, 286, 25613, 'Sığır', 'Medine', 87),
(107, 100, 2, 'Bakara', 'اَوَكُلَّمَا عَاهَدُوا عَهْداً نَبَذَهُ فَر۪يقٌ مِنْهُمْۜ بَلْ اَكْثَرُهُمْ لَا يُؤْمِنُونَ', 'Ne zaman onlar bir antlaşma yaptılarsa, yine kendilerinden bir grup onu bozmadı mı? Zaten onların çoğu iman etmez.', 1, 14, 286, 25613, 'Sığır', 'Medine', 87),
(108, 101, 2, 'Bakara', 'وَلَمَّا جَٓاءَهُمْ رَسُولٌ مِنْ عِنْدِ اللّٰهِ مُصَدِّقٌ لِمَا مَعَهُمْ نَبَذَ فَر۪يقٌ مِنَ الَّذ۪ينَ اُو۫تُوا الْكِتَابَۗ كِتَابَ اللّٰهِ وَرَٓاءَ ظُهُورِهِمْ كَاَنَّهُمْ لَا يَعْلَمُونَ', 'Allah tarafından kendilerine, yanlarında bulunanı tasdik edici bir elçi gelince ehl-i kitaptan bir gurup, sanki Allah´ın kitabını bilmiyormuş gibi onu arkalarına atıp terkettiler.', 1, 14, 286, 25613, 'Sığır', 'Medine', 87),
(109, 102, 2, 'Bakara', 'وَاتَّـبَعُوا مَا تَتْلُوا الشَّيَاط۪ينُ عَلٰى مُلْكِ سُلَيْمٰنَۚ وَمَا كَفَرَ سُلَيْمٰنُ وَلٰكِنَّ الشَّيَاط۪ينَ كَفَرُوا يُعَلِّمُونَ النَّاسَ السِّحْرَۗ وَمَٓا اُنْزِلَ عَلَى الْمَلَكَيْنِ بِبَابِلَ هَارُوتَ وَمَارُوتَۜ وَمَا يُعَلِّمَانِ مِنْ اَحَدٍ حَتّٰى يَقُولَٓا اِنَّمَا نَحْنُ فِتْنَةٌ فَلَا تَكْفُرْۜ فَيَتَعَلَّمُونَ مِنْهُمَا مَا يُفَرِّقُونَ بِه۪ بَيْنَ الْمَرْءِ وَزَوْجِه۪ۜ وَمَا هُمْ بِضَٓارّ۪ينَ بِه۪ مِنْ اَحَدٍ اِلَّا بِاِذْنِ اللّٰهِۜ وَيَتَعَلَّمُونَ مَا يَضُرُّهُمْ وَلَا يَنْفَعُهُمْۜ وَلَقَدْ عَلِمُوا لَمَنِ اشْتَرٰيهُ مَا لَهُ فِي الْاٰخِرَةِ مِنْ خَلَاقٍ۠ وَلَبِئْسَ مَا شَرَوْا بِه۪ٓ اَنْفُسَهُمْۜ لَوْ كَانُوا يَعْلَمُونَ', 'Süleyman´ın hükümranlığı hakkında onlar, şeytanların uydurup söylediklerine tâbi oldular. Halbuki Süleyman büyü yapıp kâfir olmadı. Lâkin şeytanlar kâfir oldular. Çünkü insanlara sihri ve Babil´de Hârut ile Mârut isimli iki meleğe indirileni öğretiyorlardı. Halbuki o iki melek, herkese: Biz ancak imtihan için gönderildik, sakın yanlış inanıp da kâfir olmayasınız, demeden hiç kimseye (sihir ilmini) öğretmezlerdi. Onlar, o iki melekden, karı ile koca arasını açacak şeyleri öğreniyorlardı. Oysa büyücüler, Allah´ın izni olmadan hiç kimseye zarar veremezler. Onlar, kendilerine fayda vereni değil de zarar vereni öğrenirler. Sihri satın alanların (ona inanıp para verenlerin) ahiretten nasibi olmadığını çok iyi bilmektedirler. Karşılığında kendilerini sattıkları şey ne kötüdür! Keşke bunu anlasalardı!', 1, 15, 286, 25613, 'Sığır', 'Medine', 87),
(110, 103, 2, 'Bakara', 'وَلَوْ اَنَّهُمْ اٰمَنُوا وَاتَّقَوْا لَمَثُوبَةٌ مِنْ عِنْدِ اللّٰهِ خَيْرٌۜ لَوْ كَانُوا يَعْلَمُونَ۟', 'Eğer iman edip kendilerini kötülükten korusalardı, şüphesiz, Allah tarafından verilecek sevap daha hayırlı olacaktı. Keşke bunları anlasalardı!', 1, 15, 286, 25613, 'Sığır', 'Medine', 87),
(111, 104, 2, 'Bakara', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا لَا تَقُولُوا رَاعِنَا وَقُولُوا انْظُرْنَا وَاسْمَعُواۜ وَلِلْكَافِر۪ينَ عَذَابٌ اَل۪يمٌ', 'Ey iman edenler! «Râinâ» demeyin, «unzurnâ» deyin. (Söylenenleri) dinleyin. Kâfirler için elem verici bir azap vardır.', 1, 15, 286, 25613, 'Sığır', 'Medine', 87),
(112, 105, 2, 'Bakara', 'مَا يَوَدُّ الَّذ۪ينَ كَفَرُوا مِنْ اَهْلِ الْكِتَابِ وَلَا الْمُشْرِك۪ينَ اَنْ يُنَزَّلَ عَلَيْكُمْ مِنْ خَيْرٍ مِنْ رَبِّكُمْۜ وَاللّٰهُ يَخْتَصُّ بِرَحْمَتِه۪ مَنْ يَشَٓاءُۜ وَاللّٰهُ ذُوالْفَضْلِ الْعَظ۪يمِ', '(Ey müminler!) Ehl-i Kitaptan kâfirler ve putperestler de Rabbinizden size bir hayır indirilmesini istemezler. Halbuki Allah rahmetini dilediğine verir. Allah büyük lütuf sahibidir.', 1, 15, 286, 25613, 'Sığır', 'Medine', 87),
(113, 106, 2, 'Bakara', 'مَا نَنْسَخْ مِنْ اٰيَةٍ اَوْ نُنْسِهَا نَأْتِ بِخَيْرٍ مِنْهَٓا اَوْ مِثْلِهَاۜ اَلَمْ تَعْلَمْ اَنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌ', 'Biz, bir âyetin hükmünü yürürlükten kaldırır veya onu unutturursak (ertelersek) mutlaka daha iyisini veya benzerini getiririz. Bilmez misin ki Allah her şeye kadirdir.', 1, 16, 286, 25613, 'Sığır', 'Medine', 87),
(114, 107, 2, 'Bakara', 'اَلَمْ تَعْلَمْ اَنَّ اللّٰهَ لَهُ مُلْكُ السَّمٰوَاتِ وَالْاَرْضِۜ وَمَا لَكُمْ مِنْ دُونِ اللّٰهِ مِنْ وَلِيٍّ وَلَا نَص۪يرٍ', '(Yine) bilmez misin, göklerin ve yerin mülkiyet ve hükümranlığı yalnızca Allah´ındır? Sizin için Allah´tan başka ne bir dost ne de bir yardımcı vardır.', 1, 16, 286, 25613, 'Sığır', 'Medine', 87),
(115, 108, 2, 'Bakara', 'اَمْ تُر۪يدُونَ اَنْ تَسْـَٔلُوا رَسُولَكُمْ كَمَا سُئِلَ مُوسٰى مِنْ قَبْلُۜ وَمَنْ يَتَبَدَّلِ الْكُفْرَ بِالْا۪يمَانِ فَقَدْ ضَلَّ سَوَٓاءَ السَّب۪يلِ', 'Yoksa siz de (ey müslümanlar), daha önce Musa´ya sorulduğu gibi peygamberinize sorular sormak mı istiyorsunuz? Kim imanı küfre değişirse, şüphesiz dosdoğru yoldan sapmış olur.', 1, 16, 286, 25613, 'Sığır', 'Medine', 87),
(116, 109, 2, 'Bakara', 'وَدَّ كَث۪يرٌ مِنْ اَهْلِ الْكِتَابِ لَوْ يَرُدُّونَكُمْ مِنْ بَعْدِ ا۪يمَانِكُمْ كُفَّاراًۚ حَسَداً مِنْ عِنْدِ اَنْفُسِهِمْ مِنْ بَعْدِ مَا تَبَيَّنَ لَهُمُ الْحَقُّۚ فَاعْفُوا وَاصْفَحُوا حَتّٰى يَأْتِيَ اللّٰهُ بِاَمْرِه۪ۜ اِنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌ', 'Ehl-i kitaptan çoğu, hakikat kendilerine apaçık belli olduktan sonra, sırf içlerindeki kıskançlıktan ötürü, sizi imanınızdan vazgeçirip küfre döndürmek istediler. Yine de siz, Allah onlar hakkındaki emrini getirinceye kadar affedip bağışlayın. Şüphesiz Allah her şeye kadirdir.', 1, 16, 286, 25613, 'Sığır', 'Medine', 87),
(117, 110, 2, 'Bakara', 'وَاَق۪يمُوا الصَّلٰوةَ وَاٰتُوا الزَّكٰوةَۜ وَمَا تُقَدِّمُوا لِاَنْفُسِكُمْ مِنْ خَيْرٍ تَجِدُوهُ عِنْدَ اللّٰهِۜ اِنَّ اللّٰهَ بِمَا تَعْمَلُونَ بَص۪يرٌ', 'Namazı kılın, zekâtı verin, önceden kendiniz için yaptığınız her iyiliği Allah´ın katında bulacaksınız. Şüphesiz Allah, yapmakta olduklarınızı noksansız görür.', 1, 16, 286, 25613, 'Sığır', 'Medine', 87),
(118, 111, 2, 'Bakara', 'وَقَالُوا لَنْ يَدْخُلَ الْجَنَّةَ اِلَّا مَنْ كَانَ هُوداً اَوْ نَصَارٰىۜ تِلْكَ اَمَانِيُّهُمْۜ قُلْ هَاتُوا بُرْهَانَكُمْ اِنْ كُنْتُمْ صَادِق۪ينَ', '(Ehl-i kitap:) Yahudiler yahut hıristiyanlar hariç hiç kimse cennete giremeyecek, dediler. Bu onların kuruntusudur. Sen de onlara: Eğer sahiden doğru söylüyorsanız delilinizi getirin, de.', 1, 16, 286, 25613, 'Sığır', 'Medine', 87),
(119, 112, 2, 'Bakara', 'بَلٰى مَنْ اَسْلَمَ وَجْهَهُ لِلّٰهِ وَهُوَ مُحْسِنٌ فَلَهُٓ اَجْرُهُ عِنْدَ رَبِّه۪ۖ وَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ۟', 'Bilâkis, kim muhsin olarak yüzünü Allah´a döndürürse (Allah´a hakkıyla kulluk ederse) onun ecri Rabbi katındadır. Öyleleri için ne bir korku vardır, ne de üzüntü çekerler.', 1, 16, 286, 25613, 'Sığır', 'Medine', 87),
(120, 113, 2, 'Bakara', 'وَقَالَتِ الْيَهُودُ لَيْسَتِ النَّصَارٰى عَلٰى شَيْءٍۖ وَقَالَتِ النَّصَارٰى لَيْسَتِ الْيَهُودُ عَلٰى شَيْءٍۙ وَهُمْ يَتْلُونَ الْكِتَابَۜ كَذٰلِكَ قَالَ الَّذ۪ينَ لَا يَعْلَمُونَ مِثْلَ قَوْلِهِمْۚ فَاللّٰهُ يَحْكُمُ بَيْنَهُمْ يَوْمَ الْقِيٰمَةِ ف۪يمَا كَانُوا ف۪يهِ يَخْتَلِفُونَ', 'Hepsi de kitabı (Tevrat ve İncil´i) okumakta oldukları halde Yahudiler: Hıristiyanlar doğru yolda değillerdir, dediler. Hıristiyanlar da: Yahudiler doğru yolda değillerdir, dediler. (Kitabı) bilmeyenler de birbirleri hakkında tıpkı onların söylediklerini söylediler. Allah, ihtilâfa düştükleri hususlarda kıyamet günü onlar hakkında hükmünü verecektir.', 1, 17, 286, 25613, 'Sığır', 'Medine', 87),
(121, 114, 2, 'Bakara', 'وَمَنْ اَظْلَمُ مِمَّنْ مَنَعَ مَسَاجِدَ اللّٰهِ اَنْ يُذْكَرَ ف۪يهَا اسْمُهُ وَسَعٰى ف۪ي خَرَابِهَاۜ اُو۬لٰٓئِكَ مَا كَانَ لَهُمْ اَنْ يَدْخُلُوهَٓا اِلَّا خَٓائِف۪ينَۜ لَهُمْ فِي الدُّنْيَا خِزْيٌ وَلَهُمْ فِي الْاٰخِرَةِ عَذَابٌ عَظ۪يمٌ', 'Allah´ın mescidlerinde O´nun adının anılmasına engel olan ve onların harap olmasına çalışandan daha zalim kim vardır! Aslında bunların oralara ancak korkarak girmeleri gerekir. (Başka türlü girmeye hakları yoktur.) Bunlar için dünyada rezillik, ahirette de büyük azap vardır.', 1, 17, 286, 25613, 'Sığır', 'Medine', 87),
(122, 115, 2, 'Bakara', 'وَلِلّٰهِ الْمَشْرِقُ وَالْمَغْرِبُ فَاَيْنَمَا تُوَلُّوا فَثَمَّ وَجْهُ اللّٰهِۜ اِنَّ اللّٰهَ وَاسِعٌ عَل۪يمٌ', 'Doğu da Allah´ındır batı da. Nereye dönerseniz Allah´ın yüzü (zatı) oradadır. Şüphesiz Allah´(ın rahmeti ve nimeti) geniştir, O her şeyi bilendir.', 1, 17, 286, 25613, 'Sığır', 'Medine', 87);
INSERT INTO `Ayetler` (`id`, `AyetNo`, `SureNo`, `SureAdi`, `Metin`, `Mana`, `Cuzu`, `Sayfasi`, `AyetSayisi`, `HarfSayisi`, `AdAnlami`, `IndigiYer`, `InisSirasi`) VALUES
(123, 116, 2, 'Bakara', 'وَقَالُوا اتَّخَذَ اللّٰهُ وَلَداًۙ سُبْحَانَهُۜ بَلْ لَهُ مَا فِي السَّمٰوَاتِ وَالْاَرْضِۜ كُلٌّ لَهُ قَانِتُونَ', '«Allah çocuk edindi» dediler. Hâşâ! O, bundan münezzehtir. Göklerde ve yerde olanların hepsi O´nundur, hepsi O´na boyun eğmiştir.', 1, 17, 286, 25613, 'Sığır', 'Medine', 87),
(124, 117, 2, 'Bakara', 'بَد۪يعُ السَّمٰوَاتِ وَالْاَرْضِۜ وَاِذَا قَضٰٓى اَمْراً فَاِنَّمَا يَقُولُ لَهُ كُنْ فَيَكُونُ', '(O), göklerin ve yerin eşsiz yaratıcısıdır. Bir şeyi dilediğinde ona sadece «Ol!» der, o da hemen oluverir.', 1, 17, 286, 25613, 'Sığır', 'Medine', 87),
(125, 118, 2, 'Bakara', 'وَقَالَ الَّذ۪ينَ لَا يَعْلَمُونَ لَوْلَا يُكَلِّمُنَا اللّٰهُ اَوْ تَأْت۪ينَٓا اٰيَةٌۜ كَذٰلِكَ قَالَ الَّذ۪ينَ مِنْ قَبْلِهِمْ مِثْلَ قَوْلِهِمْۜ تَشَابَهَتْ قُلُوبُهُمْۜ قَدْ بَيَّنَّا الْاٰيَاتِ لِقَوْمٍ يُوقِنُونَ', 'Bilmeyenler dediler ki: Allah bizimle konuşmalı ya da bize bir âyet (mucize) gelmeli değil miydi? Onlardan öncekiler de işte tıpkı onların dediklerini demişlerdi. Kalpleri (akılları) nasıl da birbirine benzedi? Gerçekleri iyice bilmek isteyenlere âyetleri apaçık gösterdik.', 1, 17, 286, 25613, 'Sığır', 'Medine', 87),
(126, 119, 2, 'Bakara', 'اِنَّٓا اَرْسَلْنَاكَ بِالْحَقِّ بَش۪يراً وَنَذ۪يراًۙ وَلَا تُسْـَٔلُ عَنْ اَصْحَابِ الْجَح۪يمِ', 'Doğrusu biz seni Hak (Kur´an) ile müjdeleyici ve uyarıcı olarak gönderdik. Sen cehennemliklerden sorumlu değilsin.', 1, 17, 286, 25613, 'Sığır', 'Medine', 87),
(127, 120, 2, 'Bakara', 'وَلَنْ تَرْضٰى عَنْكَ الْيَهُودُ وَلَا النَّصَارٰى حَتّٰى تَتَّبِعَ مِلَّتَهُمْۜ قُلْ اِنَّ هُدَى اللّٰهِ هُوَ الْهُدٰىۜ وَلَئِنِ اتَّبَعْتَ اَهْوَٓاءَهُمْ بَعْدَ الَّذ۪ي جَٓاءَكَ مِنَ الْعِلْمِۙ مَا لَكَ مِنَ اللّٰهِ مِنْ وَلِيٍّ وَلَا نَص۪يرٍ', 'Dinlerine uymadıkça yahudiler de hıristiyanlar da asla senden razı olmayacaklardır. De ki: Doğru yol, ancak Allah´ın yoludur. Sana gelen ilimden sonra onların arzularına uyacak olursan, andolsun ki, Allah´tan sana ne bir dost ne de bir yardımcı vardır.', 1, 18, 286, 25613, 'Sığır', 'Medine', 87),
(128, 121, 2, 'Bakara', 'اَلَّذ۪ينَ اٰتَيْنَاهُمُ الْكِتَابَ يَتْلُونَهُ حَقَّ تِلَاوَتِه۪ۜ اُو۬لٰٓئِكَ يُؤْمِنُونَ بِه۪ۜ وَمَنْ يَكْفُرْ بِه۪ فَاُو۬لٰٓئِكَ هُمُ الْخَاسِرُونَ۟', 'Kendilerine kitap verdiğimiz kimseler (den bazısı) onu, hakkını gözeterek okurlar. Çünkü onlar, ona iman ederler. Ama her kim onu inkâr ederse, işte gerçekten zarara uğrayanlar onlardır.', 1, 18, 286, 25613, 'Sığır', 'Medine', 87),
(129, 122, 2, 'Bakara', 'يَا بَن۪ٓي اِسْرَٓائ۪لَ اذْكُرُوا نِعْمَتِيَ الَّت۪ٓي اَنْعَمْتُ عَلَيْكُمْ وَاَنّ۪ي فَضَّلْتُكُمْ عَلَى الْعَالَم۪ينَ', 'Ey İsrailoğulları! Size verdiğim nimetimi ve sizi (bir zamanlar) cümle âleme üstün kılmış olduğumu hatırlayın.', 1, 18, 286, 25613, 'Sığır', 'Medine', 87),
(130, 123, 2, 'Bakara', 'وَاتَّقُوا يَوْماً لَا تَجْز۪ي نَفْسٌ عَنْ نَفْسٍ شَيْـٔاً وَلَا يُقْبَلُ مِنْهَا عَدْلٌ وَلَا تَنْفَعُهَا شَفَاعَةٌ وَلَا هُمْ يُنْصَرُونَ', 'Ve bir günden sakının ki, o günde hiç kimse başkası namına bir şey ödeyemez, kimseden fidye kabul edilmez, hiç kimseye şefaat fayda vermez. Onlar hiçbir yardım da görmezler.', 1, 18, 286, 25613, 'Sığır', 'Medine', 87),
(131, 124, 2, 'Bakara', 'وَاِذِ ابْتَلٰٓى اِبْرٰه۪يمَ رَبُّهُ بِكَلِمَاتٍ فَاَتَمَّهُنَّۜ قَالَ اِنّ۪ي جَاعِلُكَ لِلنَّاسِ اِمَاماًۜ قَالَ وَمِنْ ذُرِّيَّت۪يۜ قَالَ لَا يَنَالُ عَهْدِي الظَّالِم۪ينَ', 'Bir zamanlar Rabbi İbrahim´i bir takım kelimelerle sınamış, onları tam olarak yerine getirince: Ben seni insanlara önder yapacağım, demişti. «Soyumdan da (önderler yap, yâ Rabbi!) » dedi. Allah: Ahdim zalimlere ermez (onlar için söz vermem) buyurdu.', 1, 18, 286, 25613, 'Sığır', 'Medine', 87),
(132, 125, 2, 'Bakara', 'وَاِذْ جَعَلْنَا الْبَيْتَ مَثَابَةً لِلنَّاسِ وَاَمْناًۜ وَاتَّخِذُوا مِنْ مَقَامِ اِبْرٰه۪يمَ مُصَلًّىۜ وَعَهِدْنَٓا اِلٰٓى اِبْرٰه۪يمَ وَاِسْمٰع۪يلَ اَنْ طَهِّرَا بَيْتِيَ لِلطَّٓائِف۪ينَ وَالْعَاكِف۪ينَ وَالرُّكَّعِ السُّجُودِ', 'Biz, Beyt´i (Kâbe´yi) insanlara toplanma mahalli ve güvenli bir yer kıldık. Siz de İbrahim´in makamından bir namaz yeri edinin (orada namaz kılın). İbrahim ve İsmail´e: Tavaf edenler, ibadete kapananlar, rükû ve secde edenler için Evim´i temiz tutun, diye emretmiştik.', 1, 18, 286, 25613, 'Sığır', 'Medine', 87),
(133, 126, 2, 'Bakara', 'وَاِذْ قَالَ اِبْرٰه۪يمُ رَبِّ اجْعَلْ هٰذَا بَلَداً اٰمِناً وَارْزُقْ اَهْلَهُ مِنَ الثَّمَرَاتِ مَنْ اٰمَنَ مِنْهُمْ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِۜ قَالَ وَمَنْ كَفَرَ فَاُمَتِّعُهُ قَل۪يلاً ثُمَّ اَضْطَرُّهُٓ اِلٰى عَذَابِ النَّارِۜ وَبِئْسَ الْمَص۪يرُ', 'İbrahim de demişti ki: Ey Rabbim! Burayı emin bir şehir yap, halkından Allah´a ve ahiret gününe inananları çeşitli meyvelerle besle. Allah buyurdu ki: Kim inkâr ederse onu az bir süre faydalandırır, sonra onu cehennem azabına sürüklerim. Ne kötü varılacak yerdir orası!', 1, 18, 286, 25613, 'Sığır', 'Medine', 87),
(134, 127, 2, 'Bakara', 'وَاِذْ يَرْفَعُ اِبْرٰه۪يمُ الْقَوَاعِدَ مِنَ الْبَيْتِ وَاِسْمٰع۪يلُۜ رَبَّنَا تَقَبَّلْ مِنَّاۜ اِنَّكَ اَنْتَ السَّم۪يعُ الْعَل۪يمُ', 'Bir zamanlar İbrahim, İsmail ile beraber Beytullah´ın temellerini yükseltiyor, (şöyle diyorlardı:) Ey Rabbimiz! Bizden bunu kabul buyur; şüphesiz sen işitensin, bilensin.', 1, 19, 286, 25613, 'Sığır', 'Medine', 87),
(135, 128, 2, 'Bakara', 'رَبَّنَا وَاجْعَلْنَا مُسْلِمَيْنِ لَكَ وَمِنْ ذُرِّيَّتِنَٓا اُمَّةً مُسْلِمَةً لَكَۖ وَاَرِنَا مَنَاسِكَنَا وَتُبْ عَلَيْنَاۚ اِنَّكَ اَنْتَ التَّوَّابُ الرَّح۪يمُ', 'Ey Rabbimiz! Bizi sana boyun eğenlerden kıl, neslimizden de sana itaat eden bir ümmet çıkar, bize ibadet usullerimizi göster, tevbemizi kabul et; zira, tevbeleri çokça kabul eden, çok merhametli olan ancak sensin.', 1, 19, 286, 25613, 'Sığır', 'Medine', 87),
(136, 129, 2, 'Bakara', 'رَبَّنَا وَابْعَثْ ف۪يهِمْ رَسُولاً مِنْهُمْ يَتْلُوا عَلَيْهِمْ اٰيَاتِكَ وَيُعَلِّمُهُمُ الْكِتَابَ وَالْحِكْمَةَ وَيُزَكّ۪يهِمْۜ اِنَّكَ اَنْتَ الْعَز۪يزُ الْحَك۪يمُ۟', 'Ey Rabbimiz! Onlara, içlerinden senin âyetlerini kendilerine okuyacak, onlara kitap ve hikmeti öğretecek, onları temizleyecek bir peygamber gönder. Çünkü üstün gelen, her şeyi yerli yerince yapan yalnız sensin.', 1, 19, 286, 25613, 'Sığır', 'Medine', 87),
(137, 130, 2, 'Bakara', 'وَمَنْ يَرْغَبُ عَنْ مِلَّةِ اِبْرٰه۪يمَ اِلَّا مَنْ سَفِهَ نَفْسَهُۜ وَلَقَدِ اصْطَفَيْنَاهُ فِي الدُّنْيَاۚ وَاِنَّهُ فِي الْاٰخِرَةِ لَمِنَ الصَّالِح۪ينَ', 'İbrahim´in dininden kendini bilmezlerden başka kim yüz çevirir? Andolsun ki, biz onu dünyada (elçi) seçtik, şüphesiz o ahirette de iyilerdendir.', 1, 19, 286, 25613, 'Sığır', 'Medine', 87),
(138, 131, 2, 'Bakara', 'اِذْ قَالَ لَهُ رَبُّهُٓ اَسْلِمْۙ قَالَ اَسْلَمْتُ لِرَبِّ الْعَالَم۪ينَ', 'Çünkü Rabbi ona: Müslüman ol, demiş, o da: Âlemlerin Rabbine boyun eğdim, demişti.', 1, 19, 286, 25613, 'Sığır', 'Medine', 87),
(139, 132, 2, 'Bakara', 'وَوَصّٰى بِهَٓا اِبْرٰه۪يمُ بَن۪يهِ وَيَعْقُوبُۜ يَا بَنِيَّ اِنَّ اللّٰهَ اصْطَفٰى لَكُمُ الدّ۪ينَ فَلَا تَمُوتُنَّ اِلَّا وَاَنْتُمْ مُسْلِمُونَۜ', 'Bunu İbrahim de kendi oğullarına vasiyet etti, Ya´kub da, «Oğullarım! Allah sizin için bu dini (İslâm´ı) seçti. O halde sadece müslümanlar olarak ölünüz» (dediler).', 1, 19, 286, 25613, 'Sığır', 'Medine', 87),
(140, 133, 2, 'Bakara', 'اَمْ كُنْتُمْ شُهَدَٓاءَ اِذْ حَضَرَ يَعْقُوبَ الْمَوْتُۙ اِذْ قَالَ لِبَن۪يهِ مَا تَعْبُدُونَ مِنْ بَعْد۪يۜ قَالُوا نَعْبُدُ اِلٰهَكَ وَاِلٰهَ اٰبَٓائِكَ اِبْرٰه۪يمَ وَاِسْمٰع۪يلَ وَاِسْحٰقَ اِلٰهاً وَاحِداًۚ وَنَحْنُ لَهُ مُسْلِمُونَ', 'Yoksa Ya´kub´a ölüm geldiği zaman siz orada mı idiniz? O zaman (Ya´kub) oğullarına: Benden sonra kime kulluk edeceksiniz? demişti. Onlar: Senin ve ataların İbrahim, İsmail ve İshak´ın ilâhı olan tek Allah´a kulluk edeceğiz; biz ancak O´na teslim olmuşuzdur, dediler.', 1, 19, 286, 25613, 'Sığır', 'Medine', 87),
(141, 134, 2, 'Bakara', 'تِلْكَ اُمَّةٌ قَدْ خَلَتْۚ لَهَا مَا كَسَبَتْ وَلَكُمْ مَا كَسَبْتُمْۚ وَلَا تُسْـَٔلُونَ عَمَّا كَانُوا يَعْمَلُونَ', 'Onlar bir ümmetti, gelip geçti. Onların kazandıkları kendilerinin, sizin kazandıklarınız sizindir. Siz onların yaptıklarından sorguya çekilmezsiniz.', 1, 19, 286, 25613, 'Sığır', 'Medine', 87),
(142, 135, 2, 'Bakara', 'وَقَالُوا كُونُوا هُوداً اَوْ نَصَارٰى تَهْتَدُواۜ قُلْ بَلْ مِلَّةَ اِبْرٰه۪يمَ حَن۪يفاًۜ وَمَا كَانَ مِنَ الْمُشْرِك۪ينَ', '(Yahudiler ve hıristiyanlar müslümanlara:) Yahudi ya da hıristiyan olun ki, doğru yolu bulasınız, dediler. De ki: Hayır! Biz, hanîf olan İbrahim´in dinine uyarız. O, müşriklerden değildi.', 1, 20, 286, 25613, 'Sığır', 'Medine', 87),
(143, 136, 2, 'Bakara', 'قُولُٓوا اٰمَنَّا بِاللّٰهِ وَمَٓا اُنْزِلَ اِلَيْنَا وَمَٓا اُنْزِلَ اِلٰٓى اِبْرٰه۪يمَ وَاِسْمٰع۪يلَ وَاِسْحٰقَ وَيَعْقُوبَ وَالْاَسْبَاطِ وَمَٓا اُو۫تِيَ مُوسٰى وَع۪يسٰى وَمَٓا اُو۫تِيَ النَّبِيُّونَ مِنْ رَبِّهِمْۚ لَا نُفَرِّقُ بَيْنَ اَحَدٍ مِنْهُمْۘ وَنَحْنُ لَهُ مُسْلِمُونَ', '«Biz, Allah´a ve bize indirilene; İbrahim, İsmail, İshak, Ya´kub ve esbâta indirilene, Musa ve İsa´ya verilenlerle Rableri tarafından diğer peygamberlere verilenlere, onlardan hiçbiri arasında fark gözetmeksizin inandık ve biz sadece Allah´a teslim olduk» deyin.', 1, 20, 286, 25613, 'Sığır', 'Medine', 87),
(144, 137, 2, 'Bakara', 'فَاِنْ اٰمَنُوا بِمِثْلِ مَٓا اٰمَنْتُمْ بِه۪ فَقَدِ اهْتَدَوْاۚ وَاِنْ تَوَلَّوْا فَاِنَّمَا هُمْ ف۪ي شِقَاقٍۚ فَسَيَكْف۪يكَهُمُ اللّٰهُۚ وَهُوَ السَّم۪يعُ الْعَل۪يمُۜ', 'Eğer onlar da sizin inandığınız gibi inanırlarsa doğru yolu bulmuş olurlar; dönerlerse mutlaka anlaşmazlık içine düşmüş olurlar. Onlara karşı Allah sana yeter. O işitendir, bilendir.', 1, 20, 286, 25613, 'Sığır', 'Medine', 87),
(145, 138, 2, 'Bakara', 'صِبْغَةَ اللّٰهِۚ وَمَنْ اَحْسَنُ مِنَ اللّٰهِ صِبْغَةًۘ وَنَحْنُ لَهُ عَابِدُونَ', 'Allah´ın (verdiği) rengiyle boyandık. Allah´tan daha güzel rengi kim verebilir? Biz ancak O´na kulluk ederiz (deyin).', 1, 20, 286, 25613, 'Sığır', 'Medine', 87),
(146, 139, 2, 'Bakara', 'قُلْ اَتُحَٓاجُّونَنَا فِي اللّٰهِ وَهُوَ رَبُّنَا وَرَبُّكُمْۚ وَلَـنَٓا اَعْمَالُنَا وَلَكُمْ اَعْمَالُكُمْۚ وَنَحْنُ لَهُ مُخْلِصُونَۙ', 'De ki: Allah bizim de Rabbimiz, sizin de Rabbiniz olduğu halde, O´nun hakkında bizimle tartışmaya mı girişiyorsunuz? Bizim yaptıklarımız bize, sizin yaptıklarınız da size aittir. Biz O´na gönülden bağlananlarız.', 1, 20, 286, 25613, 'Sığır', 'Medine', 87),
(147, 140, 2, 'Bakara', 'اَمْ تَقُولُونَ اِنَّ اِبْرٰه۪يمَ وَاِسْمٰع۪يلَ وَاِسْحٰقَ وَيَعْقُوبَ وَالْاَسْبَاطَ كَانُوا هُوداً اَوْ نَصَارٰىۜ قُلْ ءَاَنْتُمْ اَعْلَمُ اَمِ اللّٰهُۜ وَمَنْ اَظْلَمُ مِمَّنْ كَتَمَ شَهَادَةً عِنْدَهُ مِنَ اللّٰهِۜ وَمَا اللّٰهُ بِغَافِلٍ عَمَّا تَعْمَلُونَ', 'Yoksa siz, İbrahim, İsmail, İshak, Ya´kub ve esbâtın yahudi, yahut hıristiyan olduklarını mı söylüyorsunuz? De ki: Siz mi daha iyi bilirsiniz, yoksa Allah mı? Allah tarafından kendisine (bildirilmiş) bir şahitliği gizleyenden daha zalim kim olabilir? Allah yaptıklarınızdan gafil değildir.', 1, 20, 286, 25613, 'Sığır', 'Medine', 87),
(148, 141, 2, 'Bakara', 'تِلْكَ اُمَّةٌ قَدْ خَلَتْۚ لَهَا مَا كَسَبَتْ وَلَكُمْ مَا كَسَبْتُمْۚ وَلَا تُسْـَٔلُونَ عَمَّا كَانُوا يَعْمَلُونَ۟', 'Onlar bir ümmetti; gelip geçti. Onların kazandıkları kendilerine, sizin kazandıklarınız da size aittir. Siz onların yaptıklarından sorguya çekilmezsiniz.', 1, 20, 286, 25613, 'Sığır', 'Medine', 87),
(149, 142, 2, 'Bakara', 'سَيَقُولُ السُّفَـهَٓاءُ مِنَ النَّاسِ مَا وَلّٰيهُمْ عَنْ قِبْلَتِهِمُ الَّت۪ي كَانُوا عَلَيْهَاۜ قُلْ لِلّٰهِ الْمَشْرِقُ وَالْمَغْرِبُۜ يَهْد۪ي مَنْ يَشَٓاءُ اِلٰى صِرَاطٍ مُسْتَق۪يمٍ', 'İnsanlardan bir kısım beyinsizler: Yönelmekte oldukları kıblelerinden onları çeviren nedir? diyecekler. De ki: Doğu da batı da Allah´ındır. O dilediğini doğru yola iletir.', 2, 21, 286, 25613, 'Sığır', 'Medine', 87),
(150, 143, 2, 'Bakara', 'وَكَذٰلِكَ جَعَلْنَاكُمْ اُمَّةً وَسَطاً لِتَكُونُوا شُهَدَٓاءَ عَلَى النَّاسِ وَيَكُونَ الرَّسُولُ عَلَيْكُمْ شَه۪يداًۜ وَمَا جَعَلْنَا الْقِبْلَةَ الَّت۪ي كُنْتَ عَلَيْهَٓا اِلَّا لِنَعْلَمَ مَنْ يَتَّبِـعُ الرَّسُولَ مِمَّنْ يَنْقَلِبُ عَلٰى عَقِبَيْهِۜ وَاِنْ كَانَتْ لَكَب۪يرَةً اِلَّا عَلَى الَّذ۪ينَ هَدَى اللّٰهُۜ وَمَا كَانَ اللّٰهُ لِيُض۪يعَ ا۪يمَانَكُمْۜ اِنَّ اللّٰهَ بِالنَّاسِ لَرَؤُ۫فٌ رَح۪يمٌ', 'İşte böylece sizin insanlığa şahitler olmanız, Resûl´ün de size şahit olması için sizi mutedil bir millet kıldık. Senin yöneldiğin yeri (Kâbe´yi) biz ancak Peygamber´e uyanı, ökçeleri üzerinde geri dönenden ayırdetmemiz için kıble yaptık. Bu, Allah´ın hidayet verdiği kimselerden başkasına elbette ağır gelir. Allah sizin imanınızı asla zayi edecek değildir. Zira Allah insanlara karşı şefkatli ve merhametlidir.', 2, 21, 286, 25613, 'Sığır', 'Medine', 87),
(151, 144, 2, 'Bakara', 'قَدْ نَرٰى تَقَلُّبَ وَجْهِكَ فِي السَّمَٓاءِۚ فَلَنُوَلِّيَنَّكَ قِبْلَةً تَرْضٰيهَاۖ فَوَلِّ وَجْهَكَ شَطْرَ الْمَسْجِدِ الْحَرَامِۜ وَحَيْثُ مَا كُنْتُمْ فَوَلُّوا وُجُوهَكُمْ شَطْرَهُۜ وَاِنَّ الَّذ۪ينَ اُو۫تُوا الْكِتَابَ لَيَعْلَمُونَ اَنَّهُ الْحَقُّ مِنْ رَبِّهِمْۜ وَمَا اللّٰهُ بِغَافِلٍ عَمَّا يَعْمَلُونَ', '(Ey Muhammed!) Biz senin yüzünün göğe doğru çevrilmekte olduğunu (yücelerden haber beklediğini) görüyoruz. İşte şimdi, seni memnun olacağın bir kıbleye döndürüyoruz. Artık yüzünü Mescid-i Haram tarafına çevir. (Ey müslümanlar!) Siz de nerede olursanız olun, (namazda) yüzlerinizi o tarafa çevirin. Şüphe yok ki, ehl-i kitap, onun Rablerinden gelen gerçek olduğunu çok iyi bilirler. Allah onların yapmakta olduklarından habersiz değildir.', 2, 21, 286, 25613, 'Sığır', 'Medine', 87),
(152, 145, 2, 'Bakara', 'وَلَئِنْ اَتَيْتَ الَّذ۪ينَ اُو۫تُوا الْكِتَابَ بِكُلِّ اٰيَةٍ مَا تَبِعُوا قِبْلَتَكَۚ وَمَٓا اَنْتَ بِتَابِـعٍ قِبْلَتَهُمْۚ وَمَا بَعْضُهُمْ بِتَابِـعٍ قِبْلَةَ بَعْضٍۜ وَلَئِنِ اتَّبَعْتَ اَهْوَٓاءَهُمْ مِنْ بَعْدِ مَا جَٓاءَكَ مِنَ الْعِلْمِۙ اِنَّكَ اِذاً لَمِنَ الظَّالِم۪ينَۢ', 'Yemin olsun ki (habibim!) sen ehl-i kitaba her türlü âyeti (mucizeyi) getirsen yine de onlar senin kıblene dönmezler. Sen de onların kıblesine dönecek değilsin. Onlar da birbirlerinin kıblesine dönmezler. Sana gelen ilimden sonra eğer onların arzularına uyacak olursan, işte o zaman sen hakkı çiğneyenlerden olursun.', 2, 21, 286, 25613, 'Sığır', 'Medine', 87),
(153, 146, 2, 'Bakara', 'اَلَّذ۪ينَ اٰتَيْنَاهُمُ الْكِتَابَ يَعْرِفُونَهُ كَمَا يَعْرِفُونَ اَبْنَٓاءَهُمْۜ وَاِنَّ فَر۪يقاً مِنْهُمْ لَيَكْتُمُونَ الْحَقَّ وَهُمْ يَعْلَمُونَ', 'Kendilerine kitap verdiklerimiz onu (o kitaptaki peygamberi), öz oğullarını tanıdıkları gibi tanırlar. Buna rağmen onlardan bir gurup bile bile gerçeği gizler.', 2, 22, 286, 25613, 'Sığır', 'Medine', 87),
(154, 147, 2, 'Bakara', 'اَلْحَقُّ مِنْ رَبِّكَ فَلَا تَكُونَنَّ مِنَ الْمُمْتَر۪ينَ۟', 'Gerçek olan, Rabbinden gelendir. O halde kuşkulananlardan olma!', 2, 22, 286, 25613, 'Sığır', 'Medine', 87),
(155, 148, 2, 'Bakara', 'وَلِكُلٍّ وِجْهَةٌ هُوَ مُوَلّ۪يهَا فَاسْتَبِقُوا الْخَيْرَاتِۜ اَيْنَ مَا تَكُونُوا يَأْتِ بِكُمُ اللّٰهُ جَم۪يعاًۜ اِنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌ', 'Herkesin yöneldiği bir kıblesi vardır. (Ey müminler!) Siz hayır işlerinde yarışın. Nerede olursanız olun sonunda Allah hepinizi bir araya getirir. Şüphesiz Allah her şeye kadirdir.', 2, 22, 286, 25613, 'Sığır', 'Medine', 87),
(156, 149, 2, 'Bakara', 'وَمِنْ حَيْثُ خَرَجْتَ فَوَلِّ وَجْهَكَ شَطْرَ الْمَسْجِدِ الْحَرَامِۜ وَاِنَّهُ لَلْحَقُّ مِنْ رَبِّكَۜ وَمَا اللّٰهُ بِغَافِلٍ عَمَّا تَعْمَلُونَ', 'Nereden yola çıkarsan çık (namazda) yüzünü Mescid-i Haram tarafına çevir. Bu emir Rabbinden sana gelen gerçektir. (Biliniz ki) Allah yaptıklarınızdan habersiz değildir.', 2, 22, 286, 25613, 'Sığır', 'Medine', 87),
(157, 150, 2, 'Bakara', 'وَمِنْ حَيْثُ خَرَجْتَ فَوَلِّ وَجْهَكَ شَطْرَ الْمَسْجِدِ الْحَرَامِۜ وَحَيْثُ مَا كُنْتُمْ فَوَلُّوا وُجُوهَكُمْ شَطْرَهُۙ لِئَلَّا يَكُونَ لِلنَّاسِ عَلَيْكُمْ حُجَّةٌۗ اِلَّا الَّذ۪ينَ ظَلَمُوا مِنْهُمْ فَلَا تَخْشَوْهُمْ وَاخْشَوْن۪ي وَلِاُتِمَّ نِعْمَت۪ي عَلَيْكُمْ وَلَعَلَّكُمْ تَهْتَدُونَۙ', '(Evet Resûlüm!) Nereden yola çıkarsan çık (namazda) yüzünü Mescid-i Haram´a doğru çevir. Nerede olursanız olunuz, yüzünüzü o yana çevirin ki, aralarından haksızlık edenler (kuru inatçılar) müstesna, insanların aleyhinizde (kullanabilecekleri) bir delili bulunmasın. Sakın onlardan korkmayın! Yalnız benden korkun. Böylece size olan nimetimi tamamlayayım da doğru yolu bulasınız.', 2, 22, 286, 25613, 'Sığır', 'Medine', 87),
(158, 151, 2, 'Bakara', 'كَمَٓا اَرْسَلْنَا ف۪يكُمْ رَسُولاً مِنْكُمْ يَتْلُوا عَلَيْكُمْ اٰيَاتِنَا وَيُزَكّ۪يكُمْ وَيُعَلِّمُكُمُ الْكِتَابَ وَالْحِكْمَةَ وَيُعَلِّمُكُمْ مَا لَمْ تَكُونُوا تَعْلَمُونَۜ', 'Nitekim kendi içinizden size âyetlerimizi okuyan, sizi kötülüklerden arındıran, size Kitab´ı ve hikmeti talim edip bilmediklerinizi size öğreten bir Resûl gönderdik.', 2, 22, 286, 25613, 'Sığır', 'Medine', 87),
(159, 152, 2, 'Bakara', 'فَاذْكُرُون۪ٓي اَذْكُرْكُمْ وَاشْكُرُوا ل۪ي وَلَا تَكْفُرُونِ۟', 'Öyle ise siz beni (ibadetle) anın ki ben de sizi anayım. Bana şükredin; sakın bana nankörlük etmeyin!', 2, 22, 286, 25613, 'Sığır', 'Medine', 87),
(160, 153, 2, 'Bakara', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا اسْتَع۪ينُوا بِالصَّبْرِ وَالصَّلٰوةِۜ اِنَّ اللّٰهَ مَعَ الصَّابِر۪ينَ', 'Ey iman edenler! Sabır ve namaz ile Allah´tan yardım isteyin. Çünkü Allah muhakkak sabredenlerle beraberdir.', 2, 22, 286, 25613, 'Sığır', 'Medine', 87),
(161, 154, 2, 'Bakara', 'وَلَا تَقُولُوا لِمَنْ يُقْتَلُ ف۪ي سَب۪يلِ اللّٰهِ اَمْوَاتٌۜ بَلْ اَحْيَٓاءٌ وَلٰكِنْ لَا تَشْعُرُونَ', 'Allah yolunda öldürülenlere «ölüler» demeyin. Bilakis onlar diridirler, lâkin siz anlayamazsınız.', 2, 23, 286, 25613, 'Sığır', 'Medine', 87),
(162, 155, 2, 'Bakara', 'وَلَنَبْلُوَنَّكُمْ بِشَيْءٍ مِنَ الْخَوْفِ وَالْجُوعِ وَنَقْصٍ مِنَ الْاَمْوَالِ وَالْاَنْفُسِ وَالثَّمَرَاتِۜ وَبَشِّرِ الصَّابِر۪ينَۙ', 'Andolsun ki sizi biraz korku ve açlık; mallardan, canlardan ve ürünlerden biraz azaltma (fakirlik) ile deneriz. (Ey Peygamber!) Sabredenleri müjdele!', 2, 23, 286, 25613, 'Sığır', 'Medine', 87),
(163, 156, 2, 'Bakara', 'اَلَّذ۪ينَ اِذَٓا اَصَابَتْهُمْ مُص۪يبَةٌۙ قَالُٓوا اِنَّا لِلّٰهِ وَاِنَّٓا اِلَيْهِ رَاجِعُونَۜ', 'O sabredenler, kendilerine bir belâ geldiği zaman: Biz Allah´ın kullarıyız ve biz O´na döneceğiz, derler.', 2, 23, 286, 25613, 'Sığır', 'Medine', 87),
(164, 157, 2, 'Bakara', 'اُو۬لٰٓئِكَ عَلَيْهِمْ صَلَوَاتٌ مِنْ رَبِّهِمْ وَرَحْمَةٌ وَاُو۬لٰٓئِكَ هُمُ الْمُهْتَدُونَ', 'İşte Rablerinden bağışlamalar ve rahmet hep onlaradır. Ve doğru yolu bulanlar da onlardır.', 2, 23, 286, 25613, 'Sığır', 'Medine', 87),
(165, 158, 2, 'Bakara', 'اِنَّ الصَّفَا وَالْمَرْوَةَ مِنْ شَعَٓائِرِ اللّٰهِۚ فَمَنْ حَجَّ الْبَيْتَ اَوِ اعْتَمَرَ فَلَا جُنَاحَ عَلَيْهِ اَنْ يَطَّوَّفَ بِهِمَاۜ وَمَنْ تَطَوَّعَ خَيْراًۙ فَاِنَّ اللّٰهَ شَاكِرٌ عَل۪يمٌ', 'Şüphe yok ki, Safa ile Merve Allah´ın koyduğu nişanlardandır. Her kim Beytullah´ı ziyaret eder veya umre yaparsa onları tavaf etmesinde kendisine bir günah yoktur. Her kim gönüllü olarak bir iyilik yaparsa şüphesiz Allah kabul eder ve (yapılanı) hakkıyla bilir.', 2, 23, 286, 25613, 'Sığır', 'Medine', 87),
(166, 159, 2, 'Bakara', 'اِنَّ الَّذ۪ينَ يَكْتُمُونَ مَٓا اَنْزَلْنَا مِنَ الْبَيِّنَاتِ وَالْهُدٰى مِنْ بَعْدِ مَا بَيَّنَّاهُ لِلنَّاسِ فِي الْكِتَابِۙ اُو۬لٰٓئِكَ يَلْعَنُهُمُ اللّٰهُ وَيَلْعَنُهُمُ اللَّاعِنُونَۙ', 'İndirdiğimiz açık delilleri ve hidâyet yolunu -kitapta onu insanlara apaçık göstermemizden sonra- gizleyenler yok mu, işte onlara hem Allah hem de bütün lânet ediciler lânet eder.', 2, 23, 286, 25613, 'Sığır', 'Medine', 87),
(167, 160, 2, 'Bakara', 'اِلَّا الَّذ۪ينَ تَابُوا وَاَصْلَحُوا وَبَيَّنُوا فَاُو۬لٰٓئِكَ اَتُوبُ عَلَيْهِمْۚ وَاَنَا التَّوَّابُ الرَّح۪يمُ', 'Ancak tevbe edip durumlarını düzeltenler ve gerçeği açıkça ortaya koyanlar başkadır. Zira ben onların tevbelerini kabul ederim. Ben tevbeyi çokça kabul eden ve çokça esirgeyenim.', 2, 23, 286, 25613, 'Sığır', 'Medine', 87),
(168, 161, 2, 'Bakara', 'اِنَّ الَّذ۪ينَ كَفَرُوا وَمَاتُوا وَهُمْ كُفَّارٌ اُو۬لٰٓئِكَ عَلَيْهِمْ لَعْنَةُ اللّٰهِ وَالْمَلٰٓئِكَةِ وَالنَّاسِ اَجْمَع۪ينَۙ', '(Âyetlerimizi) inkâr etmiş ve kâfir olarak ölmüşlere gelince, işte Allah´ın, meleklerin ve tüm insanların lâneti onların üzerinedir.', 2, 23, 286, 25613, 'Sığır', 'Medine', 87),
(169, 162, 2, 'Bakara', 'خَالِد۪ينَ ف۪يهَاۚ لَا يُخَفَّفُ عَنْهُمُ الْعَذَابُ وَلَا هُمْ يُنْظَرُونَ', 'Onlar ebediyen lânet içinde kalırlar. Artık ne azapları hafifletilir ne de onların yüzlerine bakılır.', 2, 23, 286, 25613, 'Sığır', 'Medine', 87),
(170, 163, 2, 'Bakara', 'وَاِلٰهُكُمْ اِلٰهٌ وَاحِدٌۚ لَٓا اِلٰهَ اِلَّا هُوَ الرَّحْمٰنُ الرَّح۪يمُ۟', 'İlâhınız bir tek Allah´tır. O´ndan başka ilâh yoktur. O, rahmândır, rahîmdir.', 2, 23, 286, 25613, 'Sığır', 'Medine', 87),
(171, 164, 2, 'Bakara', 'اِنَّ ف۪ي خَلْقِ السَّمٰوَاتِ وَالْاَرْضِ وَاخْتِلَافِ الَّيْلِ وَالنَّهَارِ وَالْفُلْكِ الَّت۪ي تَجْر۪ي فِي الْبَحْرِ بِمَا يَنْفَعُ النَّاسَ وَمَٓا اَنْزَلَ اللّٰهُ مِنَ السَّمَٓاءِ مِنْ مَٓاءٍ فَاَحْيَا بِهِ الْاَرْضَ بَعْدَ مَوْتِهَا وَبَثَّ ف۪يهَا مِنْ كُلِّ دَٓابَّةٍۖ وَتَصْر۪يفِ الرِّيَاحِ وَالسَّحَابِ الْمُسَخَّرِ بَيْنَ السَّمَٓاءِ وَالْاَرْضِ لَاٰيَاتٍ لِقَوْمٍ يَعْقِلُونَ', 'Şüphesiz göklerin ve yerin yaratılmasında, gece ile gündüzün birbiri peşinden gelmesinde, insanlara fayda veren şeylerle yüklü olarak denizde yüzüp giden gemilerde, Allah´ın gökten indirip de ölü haldeki toprağı canlandırdığı suda, yeryüzünde her çeşit canlıyı yaymasında, rüzgârları ve yer ile gök arasında emre hazır bekleyen bulutları yönlendirmesinde düşünen bir toplum için (Allah´ın varlığını ve birliğini isbatlayan) birçok deliller vardır.', 2, 24, 286, 25613, 'Sığır', 'Medine', 87),
(172, 165, 2, 'Bakara', 'وَمِنَ النَّاسِ مَنْ يَتَّخِذُ مِنْ دُونِ اللّٰهِ اَنْدَاداً يُحِبُّونَهُمْ كَحُبِّ اللّٰهِۜ وَالَّذ۪ينَ اٰمَنُٓوا اَشَدُّ حُباًّ لِلّٰهِۜ وَلَوْ يَرَى الَّذ۪ينَ ظَلَمُٓوا اِذْ يَرَوْنَ الْعَذَابَۙ اَنَّ الْقُوَّةَ لِلّٰهِ جَم۪يعاًۙ وَاَنَّ اللّٰهَ شَد۪يدُ الْعَذَابِ', 'İnsanlardan bazıları Allah´tan başkasını Allah´a denk tanrılar edinir de onları Allah´ı sever gibi severler. İman edenlerin Allah´a olan sevgileri ise (onlarınkinden) çok daha fazladır. Keşke zalimler azabı gördükleri zaman (anlayacakları gibi) bütün kuvvetin Allah´a ait olduğunu ve Allah´ın azabının çok şiddetli olduğunu önceden anlayabilselerdi.', 2, 24, 286, 25613, 'Sığır', 'Medine', 87),
(173, 166, 2, 'Bakara', 'اِذْ تَبَرَّاَ الَّذ۪ينَ اتُّبِعُوا مِنَ الَّذ۪ينَ اتَّبَعُوا وَرَاَوُا الْعَذَابَ وَتَقَطَّعَتْ بِهِمُ الْاَسْبَابُ', 'İşte o zaman (görecekler ki) kendilerine uyulup arkalarından gidilenler, uyanlardan hızla uzaklaşırlar ve (o anda her iki taraf da) azabı görmüş, nihayet aralarındaki bağlar kopup parçalanmıştır.', 2, 24, 286, 25613, 'Sığır', 'Medine', 87),
(174, 167, 2, 'Bakara', 'وَقَالَ الَّذ۪ينَ اتَّبَعُوا لَوْ اَنَّ لَنَا كَرَّةً فَنَتَبَرَّاَ مِنْهُمْ كَمَا تَبَرَّؤُ۫ا مِنَّاۜ كَذٰلِكَ يُر۪يهِمُ اللّٰهُ اَعْمَالَهُمْ حَسَرَاتٍ عَلَيْهِمْۜ وَمَا هُمْ بِخَارِج۪ينَ مِنَ النَّارِ۟', '(Kötülere) uyanlar şöyle derler: Ah, keşke bir daha dünyaya geri gitmemiz mümkün olsaydı da, şimdi onların bizden uzaklaştıkları gibi biz de onlardan uzaklaşsaydık! Böylece Allah onlara, işlerini, pişmanlık ve üzüntü kaynağı olarak gösterir ve onlar artık ateşten çıkamazlar.', 2, 24, 286, 25613, 'Sığır', 'Medine', 87),
(175, 168, 2, 'Bakara', 'يَٓا اَيُّهَا النَّاسُ كُلُوا مِمَّا فِي الْاَرْضِ حَـلَالاً طَـيِّباًۘ وَلَا تَتَّبِعُوا خُطُوَاتِ الشَّيْطَانِۜ اِنَّهُ لَكُمْ عَدُوٌّ مُب۪ينٌ', 'Ey insanlar! Yeryüzünde bulunanların helâl ve temiz olanlarından yeyin, şeytanın peşine düşmeyin; zira şeytan sizin açık bir düşmanınızdır.', 2, 24, 286, 25613, 'Sığır', 'Medine', 87),
(176, 169, 2, 'Bakara', 'اِنَّمَا يَأْمُرُكُمْ بِالسُّٓوءِ وَالْفَحْشَٓاءِ وَاَنْ تَقُولُوا عَلَى اللّٰهِ مَا لَا تَعْلَمُونَ', 'O size ancak kötülüğü, çirkini ve Allah hakkında bilmediğiniz şeyleri söylemenizi emreder.', 2, 24, 286, 25613, 'Sığır', 'Medine', 87),
(177, 170, 2, 'Bakara', 'وَاِذَا ق۪يلَ لَهُمُ اتَّبِعُوا مَٓا اَنْزَلَ اللّٰهُ قَالُوا بَلْ نَـتَّبِـعُ مَٓا اَلْفَيْنَا عَلَيْهِ اٰبَٓاءَنَاۜ اَوَلَوْ كَانَ اٰبَٓاؤُ۬هُمْ لَا يَعْقِلُونَ شَيْـٔاً وَلَا يَهْتَدُونَ', 'Onlara (müşriklere): Allah´ın indirdiğine uyun, denildiği zaman onlar, «Hayır! Biz atalarımızı üzerinde bulduğumuz yola uyarız» dediler. Ya ataları bir şey anlamamış, doğruyu da bulamamış idiyseler?', 2, 25, 286, 25613, 'Sığır', 'Medine', 87),
(178, 171, 2, 'Bakara', 'وَمَثَلُ الَّذ۪ينَ كَفَرُوا كَمَثَلِ الَّذ۪ي يَنْعِقُ بِمَا لَا يَسْمَعُ اِلَّا دُعَٓاءً وَنِدَٓاءًۜ صُمٌّ بُكْمٌ عُمْيٌ فَهُمْ لَا يَعْقِلُونَ', '(Hidayet çağrısına kulak vermeyen) kâfirlerin durumu, sadece çobanın bağırıp çağırmasını işiten hayvanların durumuna benzer. Çünkü onlar sağırlar, dilsizler ve körlerdir. Bu sebeple düşünmezler.', 2, 25, 286, 25613, 'Sığır', 'Medine', 87),
(179, 172, 2, 'Bakara', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا كُلُوا مِنْ طَيِّبَاتِ مَا رَزَقْنَاكُمْ وَاشْكُرُوا لِلّٰهِ اِنْ كُنْتُمْ اِيَّاهُ تَعْبُدُونَ', 'Ey iman edenler! Size verdiğimiz rızıkların temiz olanlarından yeyin, eğer siz yalnız Allah´a kulluk ediyorsanız O´na şükredin.', 2, 25, 286, 25613, 'Sığır', 'Medine', 87),
(180, 173, 2, 'Bakara', 'اِنَّمَا حَرَّمَ عَلَيْكُمُ الْمَيْتَةَ وَالدَّمَ وَلَحْمَ الْخِنْز۪يرِ وَمَٓا اُهِلَّ بِه۪ لِغَيْرِ اللّٰهِۚ فَمَنِ اضْطُرَّ غَيْرَ بَاغٍ وَلَا عَادٍ فَلَٓا اِثْمَ عَلَيْهِۜ اِنَّ اللّٰهَ غَفُورٌ رَح۪يمٌ', 'Allah size ancak ölüyü (leşi), kanı, domuz etini ve Allah´tan başkası adına kesileni haram kıldı. Her kim bunlardan yemeye mecbur kalırsa, başkasının hakkına saldırmadan ve haddi aşmadan bir miktar yemesinde günah yoktur. Şüphe yok ki Allah çokça bağışlayan çokça esirgeyendir', 2, 25, 286, 25613, 'Sığır', 'Medine', 87),
(181, 174, 2, 'Bakara', 'اِنَّ الَّذ۪ينَ يَكْتُمُونَ مَٓا اَنْزَلَ اللّٰهُ مِنَ الْكِتَابِ وَيَشْتَرُونَ بِه۪ ثَمَناً قَل۪يلاًۙ اُو۬لٰٓئِكَ مَا يَأْكُلُونَ ف۪ي بُطُونِهِمْ اِلَّا النَّارَ وَلَا يُكَلِّمُهُمُ اللّٰهُ يَوْمَ الْقِيٰمَةِ وَلَا يُزَكّ۪يهِمْۚ وَلَهُمْ عَذَابٌ اَل۪يمٌ', 'Allah´ın indirdiği kitaptan bir şeyi (âhir zaman Peygamberinin vasıflarını) gizleyip onu az bir paha ile değişenler yok mu, işte onların yeyip de karınlarına doldurdukları, ateşten başka bir şey değildir. Kıyamet günü Allah ne kendileriyle konuşur ve ne de onları temize çıkarır. Orada onlar için can yakıcı bir azap vardır.', 2, 25, 286, 25613, 'Sığır', 'Medine', 87),
(182, 175, 2, 'Bakara', 'اُو۬لٰٓئِكَ الَّذ۪ينَ اشْتَرَوُا الضَّلَالَةَ بِالْهُدٰى وَالْعَذَابَ بِالْمَغْفِرَةِۚ فَمَٓا اَصْبَرَهُمْ عَلَى النَّارِ', 'Onlar doğru yol karşılığında sapıklığı, mağfirete bedel olarak da azabı satın almış kimselerdir. Onlar ateşe karşı ne kadar dayanıklıdırlar!', 2, 25, 286, 25613, 'Sığır', 'Medine', 87),
(183, 176, 2, 'Bakara', 'ذٰلِكَ بِاَنَّ اللّٰهَ نَزَّلَ الْكِتَابَ بِالْحَقِّۜ وَاِنَّ الَّذ۪ينَ اخْتَلَفُوا فِي الْكِتَابِ لَف۪ي شِقَاقٍ بَع۪يدٍ۟', 'O azabın sebebi, Allah´ın, kitabı hak olarak indirmiş olmasıdır. (Buna rağmen farklı yorum yapıp) kitapta ayrılığa düşenler, elbette derin bir anlaşmazlığın içine düşmüşlerdir.', 2, 25, 286, 25613, 'Sığır', 'Medine', 87),
(184, 177, 2, 'Bakara', 'لَيْسَ الْبِرَّ اَنْ تُوَلُّوا وُجُوهَكُمْ قِبَلَ الْمَشْرِقِ وَالْمَغْرِبِ وَلٰكِنَّ الْبِرَّ مَنْ اٰمَنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَالْمَلٰٓئِكَةِ وَالْكِتَابِ وَالنَّبِيّ۪نَۚ وَاٰتَى الْمَالَ عَلٰى حُبِّه۪ ذَوِي الْقُرْبٰى وَالْيَتَامٰى وَالْمَسَاك۪ينَ وَابْنَ السَّب۪يلِ وَالسَّٓائِل۪ينَ وَفِي الرِّقَابِۚ وَاَقَامَ الصَّلٰوةَ وَاٰتَى الزَّكٰوةَۚ وَالْمُوفُونَ بِعَهْدِهِمْ اِذَا عَاهَدُواۚ وَالصَّابِر۪ينَ فِي الْبَأْسَٓاءِ وَالضَّرَّٓاءِ وَح۪ينَ الْبَأْسِۜ اُو۬لٰٓئِكَ الَّذ۪ينَ صَدَقُواۜ وَاُو۬لٰٓئِكَ هُمُ الْمُتَّقُونَ', 'İyilik, yüzlerinizi doğu ve batı tarafına çevirmeniz değildir. Asıl iyilik, o kimsenin yaptığıdır ki, Allah´a, ahiret gününe, meleklere, kitaplara, peygamberlere inanır. (Allah´ın rızasını gözeterek) yakınlara, yetimlere, yoksullara, yolda kalmışlara, dilenenlere ve kölelere sevdiği maldan harcar, namaz kılar, zekât verir. Antlaşma yaptığı zaman sözlerini yerine getirir. Sıkıntı, hastalık ve savaş zamanlarında sabreder. İşte doğru olanlar, bu vasıfları taşıyanlardır. Müttakîler ancak onlardır!', 2, 26, 286, 25613, 'Sığır', 'Medine', 87),
(185, 178, 2, 'Bakara', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا كُتِبَ عَلَيْكُمُ الْقِصَاصُ فِي الْقَتْلٰىۜ اَلْحُرُّ بِالْحُرِّ وَالْعَبْدُ بِالْعَبْدِ وَالْاُنْثٰى بِالْاُنْثٰىۜ فَمَنْ عُفِيَ لَهُ مِنْ اَخ۪يهِ شَيْءٌ فَاتِّبَاعٌ بِالْمَعْرُوفِ وَاَدَٓاءٌ اِلَيْهِ بِاِحْسَانٍۜ ذٰلِكَ تَخْف۪يفٌ مِنْ رَبِّكُمْ وَرَحْمَةٌۜ فَمَنِ اعْتَدٰى بَعْدَ ذٰلِكَ فَلَهُ عَذَابٌ اَل۪يمٌ', 'Ey iman edenler! Öldürülenler hakkında size kısas farz kılındı. Hüre hür, köleye köle, kadına kadın (öldürülür). Ancak her kimin cezası, kardeşi (öldürülenin velisi) tarafından bir miktar bağışlanırsa artık (taraflar) hakkaniyete uymalı ve (öldüren) ona (gereken diyeti) güzellikle ödemelidir. Bu söylenenler, Rabbinizden bir hafifletme ve rahmettir. Her kim bundan sonra haddi aşarsa muhakkak onun için elem verici bir azap vardır.', 2, 26, 286, 25613, 'Sığır', 'Medine', 87),
(186, 179, 2, 'Bakara', 'وَلَكُمْ فِي الْقِصَاصِ حَيٰوةٌ يَٓا اُو۬لِي الْاَلْبَابِ لَعَلَّكُمْ تَتَّقُونَ', 'Ey akıl sahipleri! Kısasta sizin için hayat vardır. Umulur ki suç işlemekten sakınırsınız.', 2, 26, 286, 25613, 'Sığır', 'Medine', 87),
(187, 180, 2, 'Bakara', 'كُتِبَ عَلَيْكُمْ اِذَا حَضَرَ اَحَدَكُمُ الْمَوْتُ اِنْ تَرَكَ خَيْراًۚ اَلْوَصِيَّةُ لِلْوَالِدَيْنِ وَالْاَقْرَب۪ينَ بِالْمَعْرُوفِۚ حَقاًّ عَلَى الْمُتَّق۪ينَۜ', 'Birinize ölüm geldiği zaman, eğer bir mal bırakacaksa anaya, babaya, yakınlara uygun bir biçimde vasiyet etmek Allah´tan korkanlar üzerine bir borçtur.', 2, 26, 286, 25613, 'Sığır', 'Medine', 87),
(188, 181, 2, 'Bakara', 'فَمَنْ بَدَّلَهُ بَعْدَ مَا سَمِعَهُ فَاِنَّمَٓا اِثْمُهُ عَلَى الَّذ۪ينَ يُبَدِّلُونَهُۜ اِنَّ اللّٰهَ سَم۪يعٌ عَل۪يمٌۜ', 'Her kim bunu işittikten ve kabullendikten sonra vasiyeti değiştirirse, günahı onu değiştirenleredir. Şüphesiz Allah (her şeyi) işitir ve (her şeyi) bilir.', 2, 26, 286, 25613, 'Sığır', 'Medine', 87),
(189, 182, 2, 'Bakara', 'فَمَنْ خَافَ مِنْ مُوصٍ جَنَفاً اَوْ اِثْماً فَاَصْلَحَ بَيْنَهُمْ فَلَٓا اِثْمَ عَلَيْهِۜ اِنَّ اللّٰهَ غَفُورٌ رَح۪يمٌ۟', 'Her kim, vasiyet edenin haksızlığa yahut günaha meyletmesinden endişe eder de (alâkalıların) aralarını bulursa kendisine günah yoktur. Şüphesiz Allah çok bağışlayan hem de esirgeyendir.', 2, 27, 286, 25613, 'Sığır', 'Medine', 87),
(190, 183, 2, 'Bakara', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا كُتِبَ عَلَيْكُمُ الصِّيَامُ كَمَا كُتِبَ عَلَى الَّذ۪ينَ مِنْ قَبْلِكُمْ لَعَلَّكُمْ تَتَّقُونَۙ', 'Ey iman edenler! Oruç sizden önce gelip geçmiş ümmetlere farz kılındığı gibi size de farz kılındı. Umulur ki korunursunuz.', 2, 27, 286, 25613, 'Sığır', 'Medine', 87),
(191, 184, 2, 'Bakara', 'اَيَّاماً مَعْدُودَاتٍۜ فَمَنْ كَانَ مِنْكُمْ مَر۪يضاً اَوْ عَلٰى سَفَرٍ فَعِدَّةٌ مِنْ اَيَّامٍ اُخَرَۜ وَعَلَى الَّذ۪ينَ يُط۪يقُونَهُ فِدْيَةٌ طَعَامُ مِسْك۪ينٍۜ فَمَنْ تَطَوَّعَ خَيْراً فَهُوَ خَيْرٌ لَهُۜ وَاَنْ تَصُومُوا خَيْرٌ لَكُمْ اِنْ كُنْتُمْ تَعْلَمُونَ', 'Sayılı günlerde olmak üzere (oruç size farz kılındı). Sizden her kim hasta yahut yolcu olursa (tutamadığı günler kadar) diğer günlerde kaza eder. (İhtiyarlık veya şifa umudu kalmamış hastalık gibi devamlı mazereti olup da) oruç tutmaya güçleri yetmeyenlere bir fakiri doyuracak fidye gerekir. Bununla beraber kim gönüllü olarak hayır yaparsa, bu kendisi için daha iyidir. Eğer bilirseniz (güçlüğüne rağmen) oruç tutmanız sizin için daha hayırlıdır.', 2, 27, 286, 25613, 'Sığır', 'Medine', 87),
(192, 185, 2, 'Bakara', 'شَهْرُ رَمَضَانَ الَّذ۪ٓي اُنْزِلَ ف۪يهِ الْقُرْاٰنُ هُدًى لِلنَّاسِ وَبَيِّنَاتٍ مِنَ الْهُدٰى وَالْفُرْقَانِۚ فَمَنْ شَهِدَ مِنْكُمُ الشَّهْرَ فَلْيَصُمْهُۜ وَمَنْ كَانَ مَر۪يضاً اَوْ عَلٰى سَفَرٍ فَعِدَّةٌ مِنْ اَيَّامٍ اُخَرَۜ يُر۪يدُ اللّٰهُ بِكُمُ الْيُسْرَ وَلَا يُر۪يدُ بِكُمُ الْعُسْرَۘ وَلِتُكْمِلُوا الْعِدَّةَ وَلِتُكَبِّرُوا اللّٰهَ عَلٰى مَا هَدٰيكُمْ وَلَعَلَّكُمْ تَشْكُرُونَ', 'Ramazan ayı, insanlara yol gösterici, doğrunun ve doğruyu eğriden ayırmanın açık delilleri olarak Kur´an´ın indirildiği aydır. Öyle ise sizden ramazan ayını idrak edenler onda oruç tutsun. Kim o anda hasta veya yolcu olursa (tutamadığı günler sayısınca) başka günlerde kaza etsin. Allah sizin için kolaylık ister, zorluk istemez. Bütün bunlar, sayıyı tamamlamanız ve size doğru yolu göstermesine karşılık, Allah´ı tazim etmeniz, şükretmeniz içindir.', 2, 27, 286, 25613, 'Sığır', 'Medine', 87),
(193, 186, 2, 'Bakara', 'وَاِذَا سَاَلَكَ عِبَاد۪ي عَنّ۪ي فَاِنّ۪ي قَر۪يبٌۜ اُج۪يبُ دَعْوَةَ الدَّاعِ اِذَا دَعَانِۙ فَلْيَسْتَج۪يبُوا ل۪ي وَلْيُؤْمِنُوا ب۪ي لَعَلَّهُمْ يَرْشُدُونَ', 'Kullarım sana, beni sorduğunda (söyle onlara): Ben çok yakınım. Bana dua ettiği vakit dua edenin dileğine karşılık veririm. O halde (kullarım da) benim davetime uysunlar ve bana inansınlar ki doğru yolu bulalar.', 2, 27, 286, 25613, 'Sığır', 'Medine', 87),
(194, 187, 2, 'Bakara', 'اُحِلَّ لَكُمْ لَيْلَةَ الصِّيَامِ الرَّفَثُ اِلٰى نِسَٓائِكُمْۜ هُنَّ لِبَاسٌ لَكُمْ وَاَنْتُمْ لِبَاسٌ لَهُنَّۜ عَلِمَ اللّٰهُ اَنَّكُمْ كُنْتُمْ تَخْتَانُونَ اَنْفُسَكُمْ فَتَابَ عَلَيْكُمْ وَعَفَا عَنْكُمْۚ فَالْـٰٔنَ بَاشِرُوهُنَّ وَابْتَغُوا مَا كَتَبَ اللّٰهُ لَكُمْۖ وَكُلُوا وَاشْرَبُوا حَتّٰى يَتَبَيَّنَ لَكُمُ الْخَيْطُ الْاَبْيَضُ مِنَ الْخَيْطِ الْاَسْوَدِ مِنَ الْفَجْرِۖ ثُمَّ اَتِمُّوا الصِّيَامَ اِلَى الَّيْلِۚ وَلَا تُبَاشِرُوهُنَّ وَاَنْتُمْ عَاكِفُونَۙ فِي الْمَسَاجِدِۜ تِلْكَ حُدُودُ اللّٰهِ فَلَا تَقْرَبُوهَاۜ كَذٰلِكَ يُبَيِّنُ اللّٰهُ اٰيَاتِه۪ لِلنَّاسِ لَعَلَّهُمْ يَتَّقُونَ', 'Oruç gecesinde kadınlarınıza yaklaşmak size helâl kılındı. Onlar sizin için birer elbise, siz de onlar için birer elbisesiniz. Allah sizin kendinize kötülük ettiğinizi bildi ve tevbenizi kabul edip sizi bağışladı. Artık (ramazan gecelerinde) onlara yaklaşın ve Allah´ın sizin için takdir ettiklerini isteyin. Sabahın beyaz ipliği (aydınlığı), siyah ipliğinden (karanlığından) ayırt edilinceye kadar yeyin, için, sonra akşama kadar orucu tamamlayın. Mescitlerde ibadete çekilmiş olduğunuz zamanlarda kadınlarla birleşmeyin. Bunlar Allah´ın koyduğu sınırlardır. Sakın bu sınırlara yaklaşmayın. İşte böylece Allah âyetlerini insanlara açıklar. Umulur ki korunurlar.', 2, 28, 286, 25613, 'Sığır', 'Medine', 87),
(195, 188, 2, 'Bakara', 'وَلَا تَأْكُلُٓوا اَمْوَالَكُمْ بَيْنَكُمْ بِالْبَاطِلِ وَتُدْلُوا بِهَٓا اِلَى الْحُكَّامِ لِتَأْكُلُوا فَر۪يقاً مِنْ اَمْوَالِ النَّاسِ بِالْاِثْمِ وَاَنْتُمْ تَعْلَمُونَ۟', 'Mallarınızı aranızda haksız sebeplerle yemeyin. Kendiniz bilip dururken, insanların mallarından bir kısmını haram yollardan yemeniz için o malları hakimlere (idarecilere veya mahkeme hakimlerine) vermeyin.', 2, 28, 286, 25613, 'Sığır', 'Medine', 87),
(196, 189, 2, 'Bakara', 'يَسْـَٔلُونَكَ عَنِ الْاَهِلَّةِۜ قُلْ هِيَ مَوَاق۪يتُ لِلنَّاسِ وَالْحَجِّۜ وَلَيْسَ الْبِرُّ بِاَنْ تَأْتُوا الْبُيُوتَ مِنْ ظُهُورِهَا وَلٰكِنَّ الْبِرَّ مَنِ اتَّقٰىۚ وَأْتُوا الْبُيُوتَ مِنْ اَبْوَابِهَاۖ وَاتَّقُوا اللّٰهَ لَعَلَّكُمْ تُفْلِحُونَ', 'Sana, hilâl şeklinde yeni doğan ayları sorarlar. De ki: Onlar, insanlar ve özellikle hac için vakit ölçüleridir. İyi davranış, asla evlere arkalarından gelip girmeniz değildir. Lâkin iyi davranış, korunan (ve ölçülü giden) kimsenin davranışıdır. Evlere kapılarından girin, Allah´tan korkun, umulur ki kurtuluşa erersiniz.', 2, 28, 286, 25613, 'Sığır', 'Medine', 87),
(197, 190, 2, 'Bakara', 'وَقَاتِلُوا ف۪ي سَب۪يلِ اللّٰهِ الَّذ۪ينَ يُقَاتِلُونَكُمْ وَلَا تَعْتَدُواۜ اِنَّ اللّٰهَ لَا يُحِبُّ الْمُعْتَد۪ينَ', 'Size karşı savaş açanlara, siz de Allah yolunda savaş açın. Sakın aşırı gitmeyin, çünkü Allah aşırıları sevmez.', 2, 28, 286, 25613, 'Sığır', 'Medine', 87),
(198, 191, 2, 'Bakara', 'وَاقْتُلُوهُمْ حَيْثُ ثَقِفْتُمُوهُمْ وَاَخْرِجُوهُمْ مِنْ حَيْثُ اَخْرَجُوكُمْ وَالْفِتْنَةُ اَشَدُّ مِنَ الْقَتْلِۚ وَلَا تُقَاتِلُوهُمْ عِنْدَ الْمَسْجِدِ الْحَرَامِ حَتّٰى يُقَاتِلُوكُمْ ف۪يهِۚ فَاِنْ قَاتَلُوكُمْ فَاقْتُلُوهُمْۜ كَذٰلِكَ جَزَٓاءُ الْكَافِر۪ينَ', 'Onları (size karşı savaşanları) yakaladığınız yerde öldürün. Sizi çıkardıkları yerden siz de onları çıkarın. Fitne, adam öldürmekten daha kötüdür. Mescid-i Haram´da onlar sizinle savaşmadıkça, siz de onlarla savaşmayın. Eğer onlar size karşı savaş açarlarsa siz de onları öldürün. İşte kâfirlerin cezası böyledir.', 2, 29, 286, 25613, 'Sığır', 'Medine', 87),
(199, 192, 2, 'Bakara', 'فَاِنِ انْتَهَوْا فَاِنَّ اللّٰهَ غَفُورٌ رَح۪يمٌ', 'Eğer onlar (savaştan) vazgeçerlerse, (şunu iyi bilin ki) Allah gafûr ve rahîmdir.', 2, 29, 286, 25613, 'Sığır', 'Medine', 87),
(200, 193, 2, 'Bakara', 'وَقَاتِلُوهُمْ حَتّٰى لَا تَكُونَ فِتْنَةٌ وَيَكُونَ الدّ۪ينُ لِلّٰهِۜ فَاِنِ انْتَهَوْا فَلَا عُدْوَانَ اِلَّا عَلَى الظَّالِم۪ينَ', 'Fitne tamamen yok edilinceye ve din (kulluk) de yalnız Allah için oluncaya kadar onlarla savaşın. Şayet vazgeçerlerse zalimlerden başkasına düşmanlık ve saldırı yoktur.', 2, 29, 286, 25613, 'Sığır', 'Medine', 87),
(201, 194, 2, 'Bakara', 'اَلشَّهْرُ الْحَرَامُ بِالشَّهْرِ الْحَرَامِ وَالْحُرُمَاتُ قِصَاصٌۜ فَمَنِ اعْتَدٰى عَلَيْكُمْ فَاعْتَدُوا عَلَيْهِ بِمِثْلِ مَا اعْتَدٰى عَلَيْكُمْۖ وَاتَّقُوا اللّٰهَ وَاعْلَمُٓوا اَنَّ اللّٰهَ مَعَ الْمُتَّق۪ينَ', 'Haram ay haram aya karşılıktır. Hürmetler (dokunulmazlıklar) karşılıklıdır. Kim size saldırırsa siz de ona misilleme olacak kadar saldırın. Allah´tan korkun ve bilin ki Allah müttakîlerle beraberdir.', 2, 29, 286, 25613, 'Sığır', 'Medine', 87),
(202, 195, 2, 'Bakara', 'وَاَنْفِقُوا ف۪ي سَب۪يلِ اللّٰهِ وَلَا تُلْقُوا بِاَيْد۪يكُمْ اِلَى التَّهْلُكَةِۚۛ وَاَحْسِنُواۚۛ اِنَّ اللّٰهَ يُحِبُّ الْمُحْسِن۪ينَ', 'Allah yolunda harcayın. Kendi ellerinizle kendinizi tehlikeye atmayın. Yaptığınızı güzel yapın; Allah güzel yapanları sever.', 2, 29, 286, 25613, 'Sığır', 'Medine', 87),
(203, 196, 2, 'Bakara', 'وَاَتِمُّوا الْحَجَّ وَالْعُمْرَةَ لِلّٰهِۜ فَاِنْ اُحْصِرْتُمْ فَمَا اسْتَيْسَرَ مِنَ الْهَدْيِۚ وَلَا تَحْلِقُوا رُؤُ۫سَكُمْ حَتّٰى يَبْلُغَ الْهَدْيُ مَحِلَّهُۜ فَمَنْ كَانَ مِنْكُمْ مَر۪يضاً اَوْ بِه۪ٓ اَذًى مِنْ رَأْسِه۪ فَفِدْيَةٌ مِنْ صِيَامٍ اَوْ صَدَقَةٍ اَوْ نُسُكٍۚ فَاِذَٓا اَمِنْتُمْ۠ فَمَنْ تَمَتَّعَ بِالْعُمْرَةِ اِلَى الْحَجِّ فَمَا اسْتَيْسَرَ مِنَ الْهَدْيِۚ فَمَنْ لَمْ يَجِدْ فَصِيَامُ ثَلٰثَةِ اَيَّامٍ فِي الْحَجِّ وَسَبْعَةٍ اِذَا رَجَعْتُمْۜ تِلْكَ عَشَرَةٌ كَامِلَةٌۜ ذٰلِكَ لِمَنْ لَمْ يَكُنْ اَهْلُهُ حَاضِرِي الْمَسْجِدِ الْحَرَامِۜ وَاتَّقُوا اللّٰهَ وَاعْلَمُٓوا اَنَّ اللّٰهَ شَد۪يدُ الْعِقَابِ۟', 'Haccı ve umreyi Allah için tam yapın. Eğer (bunlardan) alıkonursanız kolayınıza gelen kurbanı gönderin. Kurban, yerine varıncaya kadar başlarınızı tıraş etmeyin. Sizden her kim hasta olursa yahut başından bir rahatsızlığı varsa, oruç veya sadaka veya kurban olmak üzere fidye gerekir. (Hac yolculuğu için) emin olduğunuz vakit kim hac günlerine kadar umre ile faydalanmak isterse, kolayına gelen bir kurban kesmek gerekir. Kurban kesmeyen kimse hac günlerinde üç, memleketine döndüğü zaman yedi olmak üzere oruç tutar ki, hepsi tam on gündür. Bu söylenenler, ailesi Mescid-i Haram civarında oturmayanlar içindir. Allah´tan korkun. Biliniz ki Allah´ın vereceği ceza ağırdır.', 2, 29, 286, 25613, 'Sığır', 'Medine', 87),
(204, 197, 2, 'Bakara', 'اَلْحَجُّ اَشْهُرٌ مَعْلُومَاتٌۚ فَمَنْ فَرَضَ ف۪يهِنَّ الْحَجَّ فَلَا رَفَثَ وَلَا فُسُوقَ وَلَا جِدَالَ فِي الْحَجِّۜ وَمَا تَفْعَلُوا مِنْ خَيْرٍ يَعْلَمْهُ اللّٰهُۜ وَتَزَوَّدُوا فَاِنَّ خَيْرَ الزَّادِ التَّقْوٰىۘ وَاتَّقُونِ يَٓا اُو۬لِي الْاَلْبَابِ', 'Hac, bilinen aylardadır. Kim o aylarda hacca niyet ederse (ihramını giyerse), hac esnasında kadına yaklaşmak, günah sayılan davranışlara yönelmek, kavga etmek yoktur. Ne hayır işlerseniz Allah onu bilir. (Ey müminler! Ahiret için) azık edinin. Bilin ki azığın en hayırlısı takvâdır. Ey akıl sahipleri! Benden (emirlerime muhalefetten) sakının.', 2, 30, 286, 25613, 'Sığır', 'Medine', 87),
(205, 198, 2, 'Bakara', 'لَيْسَ عَلَيْكُمْ جُنَاحٌ اَنْ تَبْتَغُوا فَضْلاً مِنْ رَبِّكُمْۜ فَاِذَٓا اَفَضْتُمْ مِنْ عَرَفَاتٍ فَاذْكُرُوا اللّٰهَ عِنْدَ الْمَشْعَرِ الْحَرَامِۖ وَاذْكُرُوهُ كَمَا هَدٰيكُمْۚ وَاِنْ كُنْتُمْ مِنْ قَبْلِه۪ لَمِنَ الضَّٓالّ۪ينَ', '(Hac mevsiminde ticaret yaparak) Rabbinizden gelecek bir lütfu (kazancı) aramanızda size herhangi bir günah yoktur. Arafat´tan ayrılıp akın ettiğinizde Meş´ar-i Haram´da Allah´ı zikredin ve O´nu size gösterdiği şekilde anın. Şüphesiz siz daha önce yanlış gidenlerden idiniz', 2, 30, 286, 25613, 'Sığır', 'Medine', 87),
(206, 199, 2, 'Bakara', 'ثُمَّ اَف۪يضُوا مِنْ حَيْثُ اَفَاضَ النَّاسُ وَاسْتَغْفِرُوا اللّٰهَۜ اِنَّ اللّٰهَ غَفُورٌ رَح۪يمٌ', 'Sonra insanların (sel gibi) aktığı yerden siz de akın. Allah´tan mağfiret isteyin. Çünkü Allah affedici ve esirgeyicidir.', 2, 30, 286, 25613, 'Sığır', 'Medine', 87),
(207, 200, 2, 'Bakara', 'فَاِذَا قَضَيْتُمْ مَنَاسِكَكُمْ فَاذْكُرُوا اللّٰهَ كَذِكْرِكُمْ اٰبَٓاءَكُمْ اَوْ اَشَدَّ ذِكْراًۜ فَمِنَ النَّاسِ مَنْ يَقُولُ رَبَّنَٓا اٰتِنَا فِي الدُّنْيَا وَمَا لَهُ فِي الْاٰخِرَةِ مِنْ خَلَاقٍ', 'Hac ibadetlerinizi bitirince, babalarınızı andığınız gibi, hatta ondan daha kuvvetli bir şekilde Allah´ı anın. İnsanlardan öyleleri var ki: Ey Rabbimiz! Bize dünyada ver, derler. Böyle kimselerin ahiretten hiç nasibi yoktur.', 2, 30, 286, 25613, 'Sığır', 'Medine', 87),
(208, 201, 2, 'Bakara', 'وَمِنْهُمْ مَنْ يَقُولُ رَبَّنَٓا اٰتِنَا فِي الدُّنْيَا حَسَنَةً وَفِي الْاٰخِرَةِ حَسَنَةً وَقِنَا عَذَابَ النَّارِ', 'Onlardan bir kısmı da: Ey Rabbimiz! Bize dünyada da iyilik ver, ahirette de iyilik ver. Bizi cehennem azabından koru! derler.', 2, 30, 286, 25613, 'Sığır', 'Medine', 87),
(209, 202, 2, 'Bakara', 'اُو۬لٰٓئِكَ لَهُمْ نَص۪يبٌ مِمَّا كَسَبُواۜ وَاللّٰهُ سَر۪يعُ الْحِسَابِ', 'İşte onlar için, kazandıklarından büyük bir nasip vardır. (Şüphesiz) Allah´ın hesabı çok süratlidir.', 2, 30, 286, 25613, 'Sığır', 'Medine', 87),
(210, 203, 2, 'Bakara', 'وَاذْكُرُوا اللّٰهَ ف۪ٓي اَيَّامٍ مَعْدُودَاتٍۜ فَمَنْ تَعَجَّلَ ف۪ي يَوْمَيْنِ فَلَٓا اِثْمَ عَلَيْهِۚ وَمَنْ تَاَخَّرَ فَلَٓا اِثْمَ عَلَيْهِۙ لِمَنِ اتَّقٰىۜ وَاتَّقُوا اللّٰهَ وَاعْلَمُٓوا اَنَّكُمْ اِلَيْهِ تُحْشَرُونَ', 'Sayılı günlerde (eyyam-ı teşrikte telbiye ve tekbir getirerek) Allah´ı anın. Kim iki gün içinde acele edip (Mina´dan Mekke´ye) dönmek isterse, ona günah yoktur. Kim geri kalırsa ona da günah yoktur. Bunlar günahtan sakınanlar içindir. Allah´tan korkun ve bilin ki hepiniz O´nun huzurunda toplanacaksınız.', 2, 31, 286, 25613, 'Sığır', 'Medine', 87),
(211, 204, 2, 'Bakara', 'وَمِنَ النَّاسِ مَنْ يُعْجِبُكَ قَوْلُهُ فِي الْحَيٰوةِ الدُّنْيَا وَيُشْهِدُ اللّٰهَ عَلٰى مَا ف۪ي قَلْبِه۪ۙ وَهُوَ اَلَدُّ الْخِصَامِ', 'İnsanlardan öyleleri vardır ki, dünya hayatı hakkında söyledikleri senin hoşuna gider. Hatta böylesi kalbinde olana (samimi olduğuna) Allah´ı şahit tutar. Halbuki o, hasımların en yamanıdır.', 2, 31, 286, 25613, 'Sığır', 'Medine', 87),
(212, 205, 2, 'Bakara', 'وَاِذَا تَوَلّٰى سَعٰى فِي الْاَرْضِ لِيُفْسِدَ ف۪يهَا وَيُهْلِكَ الْحَرْثَ وَالنَّسْلَۜ وَاللّٰهُ لَا يُحِبُّ الْفَسَادَ', 'O, dönüp gitti mi (yahut bir iş başına geçti mi) yeryüzünde ortalığı fesada vermek, ekinleri tahrip edip nesilleri bozmak için çalışır. Allah bozgunculuğu sevmez.', 2, 31, 286, 25613, 'Sığır', 'Medine', 87),
(213, 206, 2, 'Bakara', 'وَاِذَا ق۪يلَ لَهُ اتَّقِ اللّٰهَ اَخَذَتْهُ الْعِزَّةُ بِالْاِثْمِ فَحَسْبُهُ جَهَنَّمُۜ وَلَبِئْسَ الْمِهَادُ', 'Böylesine «Allah´tan kork!» denilince benlik ve gurur kendisini günaha sevkeder. (Ceza ve azap olarak) ona cehennem yeter. O ne kötü yerdir!', 2, 31, 286, 25613, 'Sığır', 'Medine', 87),
(214, 207, 2, 'Bakara', 'وَمِنَ النَّاسِ مَنْ يَشْر۪ي نَفْسَهُ ابْتِغَٓاءَ مَرْضَاتِ اللّٰهِۜ وَاللّٰهُ رَؤُ۫فٌ بِالْعِبَادِ', 'İnsanlardan öyleleri de var ki, Allah´ın rızasını almak için kendini feda eder. Allah da kullarına şefkatlidir.', 2, 31, 286, 25613, 'Sığır', 'Medine', 87),
(215, 208, 2, 'Bakara', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا ادْخُلُوا فِي السِّلْمِ كَٓافَّةًۖ وَلَا تَتَّبِعُوا خُطُوَاتِ الشَّيْطَانِۜ اِنَّهُ لَكُمْ عَدُوٌّ مُب۪ينٌ', 'Ey iman edenler! Hep birden barışa girin. Sakın şeytanın peşinden gitmeyin. Çünkü o, apaçık düşmanınızdır.', 2, 31, 286, 25613, 'Sığır', 'Medine', 87),
(216, 209, 2, 'Bakara', 'فَاِنْ زَلَلْتُمْ مِنْ بَعْدِ مَا جَٓاءَتْكُمُ الْبَيِّنَاتُ فَاعْلَمُٓوا اَنَّ اللّٰهَ عَز۪يزٌ حَك۪يمٌ', 'Size apaçık deliller geldikten sonra, yine de kayarsanız, şunu iyi bilin ki Allah azîzdir, hakîmdir.', 2, 31, 286, 25613, 'Sığır', 'Medine', 87),
(217, 210, 2, 'Bakara', 'هَلْ يَنْظُرُونَ اِلَّٓا اَنْ يَأْتِيَهُمُ اللّٰهُ ف۪ي ظُلَلٍ مِنَ الْغَمَامِ وَالْمَلٰٓئِكَةُ وَقُضِيَ الْاَمْرُۜ وَاِلَى اللّٰهِ تُرْجَعُ الْاُمُورُ۟', 'Onlar, ille de buluttan gölgeler içinde Allah´ın ve meleklerinin gelmesini mi beklerler? Halbuki iş bitirilmiştir. (Allah nizamı artık değişmez.) Bütün işler yalnızca Allah´a döndürülür.', 2, 31, 286, 25613, 'Sığır', 'Medine', 87),
(218, 211, 2, 'Bakara', 'سَلْ بَن۪ٓي اِسْرَٓائ۪لَ كَمْ اٰتَيْنَاهُمْ مِنْ اٰيَةٍ بَيِّنَةٍۜ وَمَنْ يُبَدِّلْ نِعْمَةَ اللّٰهِ مِنْ بَعْدِ مَا جَٓاءَتْهُ فَاِنَّ اللّٰهَ شَد۪يدُ الْعِقَابِ', 'İsrailoğullarına sor ki kendilerine nice apaçık mucizeler verdik. Kim mucizeler kendisine geldikten sonra Allah´ın nimetini (âyetlerini) değiştirirse bilsin ki Allah´ın azabı şiddetlidir.', 2, 32, 286, 25613, 'Sığır', 'Medine', 87),
(219, 212, 2, 'Bakara', 'زُيِّنَ لِلَّذ۪ينَ كَفَرُوا الْحَيٰوةُ الدُّنْيَا وَيَسْخَرُونَ مِنَ الَّذ۪ينَ اٰمَنُواۢ وَالَّذ۪ينَ اتَّقَوْا فَوْقَهُمْ يَوْمَ الْقِيٰمَةِۜ وَاللّٰهُ يَرْزُقُ مَنْ يَشَٓاءُ بِغَيْرِ حِسَابٍ', 'Kâfir olanlar için dünya hayatı câzip kılındı. (Bu yüzden) onlar, iman edenler ile alay ederler. Oysa ki, (iman edip) inkârdan sakınanlar kıyamet gününde onların üstündedir. Allah dilediğine hesapsız lutufta bulunur.', 2, 32, 286, 25613, 'Sığır', 'Medine', 87),
(220, 213, 2, 'Bakara', 'كَانَ النَّاسُ اُمَّةً وَاحِدَةً فَبَعَثَ اللّٰهُ النَّبِيّ۪نَ مُبَشِّر۪ينَ وَمُنْذِر۪ينَۖ وَاَنْزَلَ مَعَهُمُ الْكِتَابَ بِالْحَقِّ لِيَحْكُمَ بَيْنَ النَّاسِ ف۪يمَا اخْتَلَفُوا ف۪يهِۜ وَمَا اخْتَلَفَ ف۪يهِ اِلَّا الَّذ۪ينَ اُو۫تُوهُ مِنْ بَعْدِ مَا جَٓاءَتْهُمُ الْبَيِّنَاتُ بَغْياً بَيْنَهُمْۚ فَهَدَى اللّٰهُ الَّذ۪ينَ اٰمَنُوا لِمَا اخْتَلَفُوا ف۪يهِ مِنَ الْحَقِّ بِاِذْنِه۪ۜ وَاللّٰهُ يَهْد۪ي مَنْ يَشَٓاءُ اِلٰى صِرَاطٍ مُسْتَق۪يمٍ', 'İnsanlar bir tek ümmet idi. Sonra Allah, müjdeleyici ve uyarıcı olarak peygamberleri gönderdi. İnsanlar arasında, anlaşmazlığa düştükleri hususlarda hüküm vermeleri için, onlarla beraber hak yolu gösteren kitapları da gönderdi. Ancak kendilerine kitap verilenler, apaçık deliller geldikten sonra, aralarındaki kıskançlıktan ötürü dinde anlaşmazlığa düştüler. Bunun üzerine Allah iman edenlere, üzerinde ihtilafa düştükleri gerçeği izniyle gösterdi. Allah dilediğini doğru yola iletir.', 2, 32, 286, 25613, 'Sığır', 'Medine', 87),
(221, 214, 2, 'Bakara', 'اَمْ حَسِبْتُمْ اَنْ تَدْخُلُوا الْجَنَّةَ وَلَمَّا يَأْتِكُمْ مَثَلُ الَّذ۪ينَ خَلَوْا مِنْ قَبْلِكُمْۜ مَسَّتْهُمُ الْبَأْسَٓاءُ وَالضَّرَّٓاءُ وَزُلْزِلُوا حَتّٰى يَقُولَ الرَّسُولُ وَالَّذ۪ينَ اٰمَنُوا مَعَهُ مَتٰى نَصْرُ اللّٰهِۜ اَلَٓا اِنَّ نَصْرَ اللّٰهِ قَر۪يبٌ', '(Ey müminler!) Yoksa siz, sizden önce gelip geçenlerin başına gelenler size de gelmeden cennete gireceğinizi mi sandınız? Yoksulluk ve sıkıntı onlara öylesine dokunmuş ve öyle sarsılmışlardı ki, nihayet Peygamber ve beraberindeki müminler: Allah´ın yardımı ne zaman! dediler. Bilesiniz ki Allah´ın yardımı yakındır.', 2, 32, 286, 25613, 'Sığır', 'Medine', 87),
(222, 215, 2, 'Bakara', 'يَسْـَٔلُونَكَ مَاذَا يُنْفِقُونَۜ قُلْ مَٓا اَنْفَقْتُمْ مِنْ خَيْرٍ فَلِلْوَالِدَيْنِ وَالْاَقْرَب۪ينَ وَالْيَتَامٰى وَالْمَسَاك۪ينِ وَابْنِ السَّب۪يلِۜ وَمَا تَفْعَلُوا مِنْ خَيْرٍ فَاِنَّ اللّٰهَ بِه۪ عَل۪يمٌ', 'Sana (Allah yolunda) ne harcayacaklarını soruyorlar. De ki: Maldan harcadığınız şey, ebeveyn, yakınlar, yetimler, fakirler ve yolcular için olmalıdır. Şüphesiz Allah yapacağınız her hayrı bilir.', 2, 32, 286, 25613, 'Sığır', 'Medine', 87),
(223, 216, 2, 'Bakara', 'كُتِبَ عَلَيْكُمُ الْقِتَالُ وَهُوَ كُرْهٌ لَكُمْۚ وَعَسٰٓى اَنْ تَكْرَهُوا شَيْـٔاً وَهُوَ خَيْرٌ لَكُمْۚ وَعَسٰٓى اَنْ تُحِبُّوا شَيْـٔاً وَهُوَ شَرٌّ لَكُمْۜ وَاللّٰهُ يَعْلَمُ وَاَنْتُمْ لَا تَعْلَمُونَ۟', 'Hoşunuza gitmediği halde savaş size farz kılındı. Sizin için daha hayırlı olduğu halde bir şeyi sevmemeniz mümkündür. Sizin için daha kötü olduğu halde bir şeyi sevmeniz de mümkündür. Allah bilir, siz bilmezsiniz.', 2, 33, 286, 25613, 'Sığır', 'Medine', 87),
(224, 217, 2, 'Bakara', 'يَسْـَٔلُونَكَ عَنِ الشَّهْرِ الْحَرَامِ قِتَالٍ ف۪يهِۜ قُلْ قِتَالٌ ف۪يهِ كَب۪يرٌۜ وَصَدٌّ عَنْ سَب۪يلِ اللّٰهِ وَكُفْرٌ بِه۪ وَالْمَسْجِدِ الْحَرَامِ وَاِخْرَاجُ اَهْلِه۪ مِنْهُ اَكْبَرُ عِنْدَ اللّٰهِۚ وَالْفِتْنَةُ اَكْبَرُ مِنَ الْقَتْلِۜ وَلَا يَزَالُونَ يُقَاتِلُونَكُمْ حَتّٰى يَرُدُّوكُمْ عَنْ د۪ينِكُمْ اِنِ اسْتَطَاعُواۜ وَمَنْ يَرْتَدِدْ مِنْكُمْ عَنْ د۪ينِه۪ فَيَمُتْ وَهُوَ كَافِرٌ فَاُو۬لٰٓئِكَ حَبِطَتْ اَعْمَالُهُمْ فِي الدُّنْيَا وَالْاٰخِرَةِۚ وَاُو۬لٰٓئِكَ اَصْحَابُ النَّارِۚ هُمْ ف۪يهَا خَـالِدُونَ', 'Sana haram ayı, yani onda savaşmayı soruyorlar. De ki: O ayda savaşmak büyük bir günahtır. (İnsanları) Allah yolundan çevirmek, Allah´ı inkâr etmek, Mescid-i Haram´ın ziyaretine mâni olmak ve halkını oradan çıkarmak ise Allah katında daha büyük günahtır. Fitne de adam öldürmekten daha büyük bir günahtır. Onlar eğer güçleri yeterse, sizi dininizden döndürünceye kadar size karşı savaşa devam ederler. Sizden kim, dininden döner ve kâfir olarak ölürse, onların yaptıkları işler dünyada da ahirette de boşa gider. Onlar cehennemliktirler ve orada devamlı kalırlar.', 2, 33, 286, 25613, 'Sığır', 'Medine', 87),
(225, 218, 2, 'Bakara', 'اِنَّ الَّذ۪ينَ اٰمَنُوا وَالَّذ۪ينَ هَاجَرُوا وَجَاهَدُوا ف۪ي سَب۪يلِ اللّٰهِۙ اُو۬لٰٓئِكَ يَرْجُونَ رَحْمَتَ اللّٰهِۜ وَاللّٰهُ غَفُورٌ رَح۪يمٌ', 'İman edenler ve hicret edip Allah yolunda cihad edenler var ya, işte bunlar, Allah´ın rahmetini umabilirler. Allah, gafûr ve rahîmdir.', 2, 33, 286, 25613, 'Sığır', 'Medine', 87),
(226, 219, 2, 'Bakara', 'يَسْـَٔلُونَكَ عَنِ الْخَمْرِ وَالْمَيْسِرِۜ قُلْ ف۪يهِمَٓا اِثْمٌ كَب۪يرٌ وَمَنَافِـعُ لِلنَّاسِۘ وَاِثْمُهُمَٓا اَكْبَرُ مِنْ نَفْعِهِمَاۜ وَيَسْـَٔلُونَكَ مَاذَا يُنْفِقُونَۜ قُلِ الْعَفْوَۜ كَذٰلِكَ يُبَيِّنُ اللّٰهُ لَكُمُ الْاٰيَاتِ لَعَلَّكُمْ تَتَفَكَّرُونَۙ', 'Sana, şarap ve kumar hakkında soru sorarlar. De ki: Her ikisinde de büyük bir günah ve insanlar için bir takım faydalar vardır. Ancak her ikisinin de günahı faydasından daha büyüktür. Yine sana iyilik yolunda ne harcayacaklarını sorarlar. «İhtiyaç fazlasını» de. Allah size âyetleri böyle açıklar ki düşünesiniz.', 2, 33, 286, 25613, 'Sığır', 'Medine', 87);
INSERT INTO `Ayetler` (`id`, `AyetNo`, `SureNo`, `SureAdi`, `Metin`, `Mana`, `Cuzu`, `Sayfasi`, `AyetSayisi`, `HarfSayisi`, `AdAnlami`, `IndigiYer`, `InisSirasi`) VALUES
(227, 220, 2, 'Bakara', 'فِي الدُّنْيَا وَالْاٰخِرَةِۜ وَيَسْـَٔلُونَكَ عَنِ الْيَتَامٰىۜ قُلْ اِصْلَاحٌ لَهُمْ خَيْرٌۜ وَاِنْ تُخَالِطُوهُمْ فَاِخْوَانُكُمْۜ وَاللّٰهُ يَعْلَمُ الْمُفْسِدَ مِنَ الْمُصْلِحِۜ وَلَوْ شَٓاءَ اللّٰهُ لَاَعْنَتَكُمْۜ اِنَّ اللّٰهَ عَز۪يزٌ حَك۪يمٌ', 'Dünya ve ahiret hakkında (lehinize olan davranışları düşünün ve ona göre hareket edin). Sana yetimler hakkında soruyorlar. De ki: Onları iyi yetiştirmek (yüz üstü bırakmaktan) daha hayırlıdır. Eğer onlarla birlikte yaşarsanız, (unutmayın ki) onlar sizin kardeşlerinizdir. Allah, işleri bozanla düzelteni bilir. Eğer Allah dileseydi, sizi de zahmet ve meşakkate sokardı. Çünkü Allah güçlüdür, hakîmdir.', 2, 34, 286, 25613, 'Sığır', 'Medine', 87),
(228, 221, 2, 'Bakara', 'وَلَا تَنْكِحُوا الْمُشْرِكَاتِ حَتّٰى يُؤْمِنَّۜ وَلَاَمَةٌ مُؤْمِنَةٌ خَيْرٌ مِنْ مُشْرِكَةٍ وَلَوْ اَعْجَبَتْكُمْۚ وَلَا تُنْكِحُوا الْمُشْرِك۪ينَ حَتّٰى يُؤْمِنُواۜ وَلَعَبْدٌ مُؤْمِنٌ خَيْرٌ مِنْ مُشْرِكٍ وَلَوْ اَعْجَبَكُمْۜ اُو۬لٰٓئِكَ يَدْعُونَ اِلَى النَّارِۚ وَاللّٰهُ يَدْعُٓوا اِلَى الْجَنَّةِ وَالْمَغْفِرَةِ بِاِذْنِه۪ۚ وَيُبَيِّنُ اٰيَاتِه۪ لِلنَّاسِ لَعَلَّهُمْ يَتَذَكَّرُونَ۟', 'İman etmedikçe putperest kadınlarla evlenmeyin. Beğenseniz bile, putperest bir kadından, imanlı bir câriye kesinlikle daha iyidir. İman etmedikçe putperest erkekleri de (kızlarınızla) evlendirmeyin. Beğenseniz bile, putperest bir kişiden inanmış bir köle kesinlikle daha iyidir. Onlar (müşrikler) cehenneme çağırır. Allah ise, izni (ve yardımı) ile cennete ve mağfirete çağırır. Allah, düşünüp anlasınlar diye âyetlerini insanlara açıklar.', 2, 34, 286, 25613, 'Sığır', 'Medine', 87),
(229, 222, 2, 'Bakara', 'وَيَسْـَٔلُونَكَ عَنِ الْمَح۪يضِۜ قُلْ هُوَ اَذًىۙ فَاعْتَزِلُوا النِّسَٓاءَ فِي الْمَح۪يضِۙ وَلَا تَقْرَبُوهُنَّ حَتّٰى يَطْهُرْنَۚ فَاِذَا تَطَهَّرْنَ فَأْتُوهُنَّ مِنْ حَيْثُ اَمَرَكُمُ اللّٰهُۜ اِنَّ اللّٰهَ يُحِبُّ التَّوَّاب۪ينَ وَيُحِبُّ الْمُتَطَهِّر۪ينَ', 'Sana kadınların ay halini sorarlar. De ki: O, bir rahatsızlıktır. Bu sebeple ay halinde olan kadınlardan uzak durun. Temizleninceye kadar onlara yaklaşmayın. Temizlendikleri vakit, Allah´ın size emrettiği yerden onlara yaklaşın. Şunu iyi bilin ki, Allah tevbe edenleri de sever, temizlenenleri de sever.', 2, 34, 286, 25613, 'Sığır', 'Medine', 87),
(230, 223, 2, 'Bakara', 'نِسَٓاؤُ۬كُمْ حَرْثٌ لَكُمْۖ فَأْتُوا حَرْثَكُمْ اَنّٰى شِئْتُمْۘ وَقَدِّمُوا لِاَنْفُسِكُمْۜ وَاتَّقُوا اللّٰهَ وَاعْلَمُٓوا اَنَّكُمْ مُلَاقُوهُۜ وَبَشِّرِ الْمُؤْمِن۪ينَ', 'Kadınlarınız sizin için bir tarladır. Tarlanıza nasıl dilerseniz öyle varın. Kendiniz için önceden (uygun davranışlarla) hazırlık yapın. Allah´tan korkun, biliniz ki siz O´na kavuşacaksınız. (Yâ Muhammed!) müminleri müjdele!', 2, 34, 286, 25613, 'Sığır', 'Medine', 87),
(231, 224, 2, 'Bakara', 'وَلَا تَجْعَلُوا اللّٰهَ عُرْضَةً لِاَيْمَانِكُمْ اَنْ تَبَرُّوا وَتَتَّقُوا وَتُصْلِحُوا بَيْنَ النَّاسِۜ وَاللّٰهُ سَم۪يعٌ عَل۪يمٌ', 'Yeminlerinizden dolayı Allah´ı (O´nun adını), iyilik etmenize, O´ndan sakınmanıza ve insanların arasını düzeltmenize engel kılmayın. Allah işitir ve bilir.', 2, 34, 286, 25613, 'Sığır', 'Medine', 87),
(232, 225, 2, 'Bakara', 'لَا يُؤَاخِذُكُمُ اللّٰهُ بِاللَّغْوِ ف۪ٓي اَيْمَانِكُمْ وَلٰكِنْ يُؤَاخِذُكُمْ بِمَا كَسَبَتْ قُلُوبُكُمْۜ وَاللّٰهُ غَفُورٌ حَل۪يمٌ', 'Allah sizi kasıtsız yeminlerinizden sorumlu tutmaz. Lâkin kasıtlı yaptığınız yeminlerinizden dolayı sizi sorumlu tutar. Allah gafûrdur, halîmdir.', 2, 35, 286, 25613, 'Sığır', 'Medine', 87),
(233, 226, 2, 'Bakara', 'لِلَّذ۪ينَ يُؤْلُونَ مِنْ نِسَٓائِهِمْ تَرَبُّصُ اَرْبَعَةِ اَشْهُرٍۚ فَاِنْ فَٓاؤُ۫ فَاِنَّ اللّٰهَ غَفُورٌ رَح۪يمٌ', 'Kadınlarına yaklaşmamaya yemin edenler dört ay beklerler. Eğer (bu müddet içinde) kadınlarına dönerlerse, şüphesiz Allah çokça bağışlayan ve esirgeyendir.', 2, 35, 286, 25613, 'Sığır', 'Medine', 87),
(234, 227, 2, 'Bakara', 'وَاِنْ عَزَمُوا الطَّـلَاقَ فَاِنَّ اللّٰهَ سَم۪يعٌ عَل۪يمٌ', 'Eğer (müddeti içinde dönmeyip kadınlarını) boşamaya karar verirlerse (ayrılırlar). Biliniz ki, Allah işitir ve bilir.', 2, 35, 286, 25613, 'Sığır', 'Medine', 87),
(235, 228, 2, 'Bakara', 'وَالْمُطَلَّقَاتُ يَتَرَبَّصْنَ بِاَنْفُسِهِنَّ ثَلٰثَةَ قُرُٓوءٍۜ وَلَا يَحِلُّ لَهُنَّ اَنْ يَكْتُمْنَ مَا خَلَقَ اللّٰهُ ف۪ٓي اَرْحَامِهِنَّ اِنْ كُنَّ يُؤْمِنَّ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِۜ وَبُعُولَتُهُنَّ اَحَقُّ بِرَدِّهِنَّ ف۪ي ذٰلِكَ اِنْ اَرَادُٓوا اِصْلَاحاًۜ وَلَهُنَّ مِثْلُ الَّذ۪ي عَلَيْهِنَّ بِالْمَعْرُوفِۖ وَلِلرِّجَالِ عَلَيْهِنَّ دَرَجَةٌۜ وَاللّٰهُ عَز۪يزٌ حَك۪يمٌ۟', 'Boşanmış kadınlar, kendi başlarına (evlenmeden) üç ay hali (hayız veya temizlik müddeti) beklerler. Eğer onlar Allah´a ve ahiret gününe gerçekten inanmışlarsa, rahimlerinde Allah´ın yarattığını gizlemeleri kendilerine helâl olmaz. Eğer kocalar barışmak isterlerse, bu durumda boşadıkları kadınları geri almaya daha fazla hak sahibidirler. Kadınların da ödevlerine denk belli hakları vardır. Ancak erkekler, kadınlara göre bir derece üstünlüğe sahiptirler. Allah azîzdir, hakîmdir.', 2, 35, 286, 25613, 'Sığır', 'Medine', 87),
(236, 229, 2, 'Bakara', 'اَلطَّـلَاقُ مَرَّتَانِۖ فَاِمْسَاكٌ بِمَعْرُوفٍ اَوْ تَسْر۪يحٌ بِاِحْسَانٍۜ وَلَا يَحِلُّ لَكُمْ اَنْ تَأْخُذُوا مِمَّٓا اٰتَيْتُمُوهُنَّ شَيْـٔاً اِلَّٓا اَنْ يَخَافَٓا اَلَّا يُق۪يمَا حُدُودَ اللّٰهِۜ فَاِنْ خِفْتُمْ اَلَّا يُق۪يمَا حُدُودَ اللّٰهِۙ فَلَا جُنَاحَ عَلَيْهِمَا ف۪يمَا افْتَدَتْ بِه۪ۜ تِلْكَ حُدُودُ اللّٰهِ فَلَا تَعْتَدُوهَاۚ وَمَنْ يَتَعَدَّ حُدُودَ اللّٰهِ فَاُو۬لٰٓئِكَ هُمُ الظَّالِمُونَ', 'Boşama iki defadır. Bundan sonrası ya iyilikle tutmak ya da güzellikle salıvermektir. Kadınlara verdiklerinizden (boşanma esnasında) bir şey almanız size helâl olmaz. Ancak erkek ve kadın Allah´ın sınırlarında kalıp evlilik haklarını tam tatbik edememekten korkarlarsa bu durum müstesna. (Ey müminler!) Siz de karı ile kocanın, Allah´ın sınırlarını, hakkıyla muhafaza etmelerinden kuşkuya düşerseniz, kadının (erkeğe) fidye vermesinde her iki taraf için de sakınca yoktur. Bu söylenenler Allah´ın koyduğu sınırlardır. Sakın onları aşmayın. Kim Allah´ın sınırlarını aşarsa işte onlar zalimlerdir.', 2, 35, 286, 25613, 'Sığır', 'Medine', 87),
(237, 230, 2, 'Bakara', 'فَاِنْ طَلَّقَهَا فَلَا تَحِلُّ لَهُ مِنْ بَعْدُ حَتّٰى تَنْكِحَ زَوْجاً غَيْرَهُۜ فَاِنْ طَلَّقَهَا فَلَا جُنَاحَ عَلَيْهِمَٓا اَنْ يَتَرَاجَعَٓا اِنْ ظَـنَّٓا اَنْ يُق۪يمَا حُدُودَ اللّٰهِۜ وَتِلْكَ حُدُودُ اللّٰهِ يُبَيِّنُهَا لِقَوْمٍ يَعْلَمُونَ', 'Eğer erkek kadını (üçüncü defa) boşarsa, ondan sonra kadın bir başka erkekle evlenmedikçe onu alması kendisine helâl olmaz. Eğer bu kişi de onu boşarsa, (her iki taraf da) Allah´ın sınırlarını muhafaza edeceklerine inandıkları takdirde, yeniden evlenmelerinde beis yoktur. Bunlar Allah´ın sınırlarıdır. Allah bunları bilmek, öğrenmek isteyenler için açıklar.', 2, 35, 286, 25613, 'Sığır', 'Medine', 87),
(238, 231, 2, 'Bakara', 'وَاِذَا طَلَّقْتُمُ النِّسَٓاءَ فَبَلَغْنَ اَجَلَهُنَّ فَاَمْسِكُوهُنَّ بِمَعْرُوفٍ اَوْ سَرِّحُوهُنَّ بِمَعْرُوفٍۖ وَلَا تُمْسِكُوهُنَّ ضِرَاراً لِتَعْتَدُواۚ وَمَنْ يَفْعَلْ ذٰلِكَ فَقَدْ ظَلَمَ نَفْسَهُۜ وَلَا تَتَّخِذُٓوا اٰيَاتِ اللّٰهِ هُزُواًۘ وَاذْكُرُوا نِعْمَتَ اللّٰهِ عَلَيْكُمْ وَمَٓا اَنْزَلَ عَلَيْكُمْ مِنَ الْكِتَابِ وَالْحِكْمَةِ يَعِظُـكُمْ بِه۪ۜ وَاتَّقُوا اللّٰهَ وَاعْلَمُٓوا اَنَّ اللّٰهَ بِكُلِّ شَيْءٍ عَل۪يمٌ۟', 'Kadınları boşadığınız ve onlar da bekleme müddetlerini bitirdikleri vakit ya onları iyilikle tutun yahut iyilikle bırakın. Fakat haksızlık ederek ve zarar vermek için onları nikâh altında tutmayın. Kim bunu yaparsa muhakkak kendine kötülük etmiş olur. Allah´ın âyetlerini eğlenceye almayın. Allah´ın sizin üzerinizdeki nimetini, (size verdiği hidayeti), size öğüt vermek üzere indirdiği Kitab´ı ve hikmeti hatırlayın. Allah´tan korkun. Bilesiniz ki Allah, her şeyi bilir.', 2, 36, 286, 25613, 'Sığır', 'Medine', 87),
(239, 232, 2, 'Bakara', 'وَاِذَا طَلَّقْتُمُ النِّسَٓاءَ فَبَلَغْنَ اَجَلَهُنَّ فَلَا تَعْضُلُوهُنَّ اَنْ يَنْكِحْنَ اَزْوَاجَهُنَّ اِذَا تَرَاضَوْا بَيْنَهُمْ بِالْمَعْرُوفِۜ ذٰلِكَ يُوعَظُ بِه۪ مَنْ كَانَ مِنْكُمْ يُؤْمِنُ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِۜ ذٰلِكُمْ اَزْكٰى لَكُمْ وَاَطْهَرُۜ وَاللّٰهُ يَعْلَمُ وَاَنْتُمْ لَا تَعْلَمُونَ', 'Kadınları boşadığınız ve onlar da bekleme müddetlerini bitirdikleri vakit, aralarında iyilikle anlaştıkları takdirde, onların (eski) kocalarıyla evlenmelerine engel olmayın. İşte bununla içinizden Allah´a ve ahiret gününe inanan kimselere öğüt verilmektedir. Bu öğüdü tutmanız kendiniz için en iyisi ve en temizidir. Allah bilir, siz bilmezsiniz.', 2, 36, 286, 25613, 'Sığır', 'Medine', 87),
(240, 233, 2, 'Bakara', 'وَالْوَالِدَاتُ يُرْضِعْنَ اَوْلَادَهُنَّ حَوْلَيْنِ كَامِلَيْنِ لِمَنْ اَرَادَ اَنْ يُـتِمَّ الرَّضَاعَةَۜ وَعَلَى الْمَوْلُودِ لَهُ رِزْقُهُنَّ وَكِسْوَتُهُنَّ بِالْمَعْرُوفِۜ لَا تُكَلَّفُ نَفْسٌ اِلَّا وُسْعَهَاۚ لَا تُضَٓارَّ وَالِدَةٌ بِوَلَدِهَا وَلَا مَوْلُودٌ لَهُ بِوَلَدِه۪ وَعَلَى الْوَارِثِ مِثْلُ ذٰلِكَۚ فَاِنْ اَرَادَا فِصَالاً عَنْ تَرَاضٍ مِنْهُمَا وَتَشَاوُرٍ فَلَا جُنَاحَ عَلَيْهِمَاۜ وَاِنْ اَرَدْتُمْ اَنْ تَسْتَرْضِعُٓوا اَوْلَادَكُمْ فَلَا جُنَاحَ عَلَيْكُمْ اِذَا سَلَّمْتُمْ مَٓا اٰتَيْتُمْ بِالْمَعْرُوفِۜ وَاتَّقُوا اللّٰهَ وَاعْلَمُٓوا اَنَّ اللّٰهَ بِمَا تَعْمَلُونَ بَص۪يرٌ', 'Emzirmeyi tamamlatmak isteyen (baba) için, anneler çocuklarını iki tam yıl emzirirler. Onların örfe uygun olarak beslenmesi ve giyimi baba tarafına aittir. Bir insan ancak gücü yettiğinden sorumlu tutulur. Hiçbir anne, çocuğu sebebiyle, hiçbir baba da çocuğu yüzünden zarara uğratılmamalıdır. Onun benzeri (nafaka temini) vâris üzerine de gerekir. Eğer ana ve baba birbiriyle görüşerek ve karşılıklı anlaşarak çocuğu memeden kesmek isterlerse, kendilerine günah yoktur. Çocuklarınızı (süt anne tutup) emzirtmek istediğiniz takdirde, süt anneye vermekte olduğunuzu iyilikle teslim etmeniz şartıyla, üzerinize günah yoktur. Allah´tan korkun. Bilin ki Allah, yapmakta olduklarınızı görür.', 2, 36, 286, 25613, 'Sığır', 'Medine', 87),
(241, 234, 2, 'Bakara', 'وَالَّذ۪ينَ يُتَوَفَّوْنَ مِنْكُمْ وَيَذَرُونَ اَزْوَاجاً يَتَرَبَّصْنَ بِاَنْفُسِهِنَّ اَرْبَعَةَ اَشْهُرٍ وَعَشْراًۚ فَاِذَا بَلَغْنَ اَجَلَهُنَّ فَلَا جُنَاحَ عَلَيْكُمْ ف۪يمَا فَعَلْنَ ف۪ٓي اَنْفُسِهِنَّ بِالْمَعْرُوفِۜ وَاللّٰهُ بِمَا تَعْمَلُونَ خَب۪يرٌ', 'Sizden ölenlerin, geride bıraktıkları eşleri, kendi başlarına (evlenmeden) dört ay on gün beklerler. Bekleme müddetlerini bitirdikleri vakit, kendileri hakkında yaptıkları meşru işlerde size bir günah yoktur. Allah yapmakta olduklarınızı bilir.', 2, 37, 286, 25613, 'Sığır', 'Medine', 87),
(242, 235, 2, 'Bakara', 'وَلَا جُنَاحَ عَلَيْكُمْ ف۪يمَا عَرَّضْتُمْ بِه۪ مِنْ خِطْبَةِ النِّسَٓاءِ اَوْ اَكْنَنْتُمْ ف۪ٓي اَنْفُسِكُمْۜ عَلِمَ اللّٰهُ اَنَّكُمْ سَتَذْكُرُونَهُنَّ وَلٰكِنْ لَا تُوَاعِدُوهُنَّ سِراًّ اِلَّٓا اَنْ تَقُولُوا قَوْلاً مَعْرُوفاًۜ وَلَا تَعْزِمُوا عُقْدَةَ النِّكَاحِ حَتّٰى يَبْلُغَ الْكِتَابُ اَجَلَهُۜ وَاعْلَمُٓوا اَنَّ اللّٰهَ يَعْلَمُ مَا ف۪ٓي اَنْفُسِكُمْ فَاحْذَرُوهُۚ وَاعْلَمُٓوا اَنَّ اللّٰهَ غَفُورٌ حَل۪يمٌ۟', '(İddet beklemekte olan) kadınlarla evlenme hususundaki düşüncelerinizi üstü kapalı biçimde anlatmanızda veya onu içinizde gizli tutmanızda size günah yoktur. Allah bilir ki siz onları anacaksınız. Lâkin, meşru sözler söylemeniz müstesna, sakın onlara gizlice buluşma sözü vermeyin. Farz olan bekleme müddeti dolmadan, nikâh kıymaya kalkışmayın. Bilin ki Allah, gönlünüzdekileri bilir. Bu sebeple Allah´tan sakının. Şunu iyi bilin ki Allah gafûrdur, halîmdir.', 2, 37, 286, 25613, 'Sığır', 'Medine', 87),
(243, 236, 2, 'Bakara', 'لَا جُنَاحَ عَلَيْكُمْ اِنْ طَلَّقْتُمُ النِّسَٓاءَ مَا لَمْ تَمَسُّوهُنَّ اَوْ تَفْرِضُوا لَهُنَّ فَر۪يضَةًۚ وَمَتِّعُوهُنَّۚ عَلَى الْمُوسِعِ قَدَرُهُ وَعَلَى الْمُقْتِرِ قَدَرُهُۚ مَتَـاعاً بِالْمَعْرُوفِۚ حَقاًّ عَلَى الْمُحْسِن۪ينَ', 'Nikâhtan sonra henüz dokunmadan veya onlar için belli bir mehir tayin etmeden kadınları boşarsanız bunda size mehir zorunluğu yoktur. Bu durumda onlara müt´a (hediye cinsinden bir şeyler) verin. Zengin olan durumuna göre, fakir de durumuna göre vermelidir. Münasip bir müt´a vermek iyiler için bir borçtur.', 2, 37, 286, 25613, 'Sığır', 'Medine', 87),
(244, 237, 2, 'Bakara', 'وَاِنْ طَلَّقْتُمُوهُنَّ مِنْ قَبْلِ اَنْ تَمَسُّوهُنَّ وَقَدْ فَرَضْتُمْ لَهُنَّ فَر۪يضَةً فَنِصْفُ مَا فَرَضْتُمْ اِلَّٓا اَنْ يَعْفُونَ اَوْ يَعْفُوَا الَّذ۪ي بِيَدِه۪ عُقْدَةُ النِّكَاحِۜ وَاَنْ تَعْفُٓوا اَقْرَبُ لِلتَّقْوٰىۜ وَلَا تَنْسَوُا الْفَضْلَ بَيْنَكُمْۜ اِنَّ اللّٰهَ بِمَا تَعْمَلُونَ بَص۪يرٌ', 'Kendilerine mehir tayin ederek evlendiğiniz kadınları, temas etmeden boşarsanız, tayin ettiğiniz mehrin yarısı onların hakkıdır. Ancak kadınların vazgeçmesi veya nikâh bağı elinde bulunanın (velinin) vazgeçmesi hali müstesna, affetmeniz (mehirden vazgeçmeniz), takvâya daha uygundur. Aranızda iyilik ve ihsanı unutmayın. Şüphesiz Allah yapmakta olduklarınızı hakkıyla görür.', 2, 37, 286, 25613, 'Sığır', 'Medine', 87),
(245, 238, 2, 'Bakara', 'حَافِظُوا عَلَى الصَّلَوَاتِ وَالصَّلٰوةِ الْوُسْطٰى وَقُومُوا لِلّٰهِ قَانِت۪ينَ', 'Namazlara ve orta namaza devam edin. Allah´a saygı ve bağlılık içinde namaz kılın.', 2, 38, 286, 25613, 'Sığır', 'Medine', 87),
(246, 239, 2, 'Bakara', 'فَاِنْ خِفْتُمْ فَرِجَـالاً اَوْ رُكْبَـاناًۚ فَاِذَٓا اَمِنْتُمْ فَاذْكُرُوا اللّٰهَ كَمَا عَلَّمَكُمْ مَا لَمْ تَكُونُوا تَعْلَمُونَ', 'Eğer (herhangi bir şeyden) korkarsanız (namazlarınızı) yürüyerek yahut binmiş olarak (kılın). Güvene kavuştuğunuz zaman, siz bilmezken Allah´ın size öğrettiği şekilde O´nu anın (namaz kılın).', 2, 38, 286, 25613, 'Sığır', 'Medine', 87),
(247, 240, 2, 'Bakara', 'وَالَّذ۪ينَ يُتَوَفَّوْنَ مِنْكُمْ وَيَذَرُونَ اَزْوَاجاًۚ وَصِيَّةً لِاَزْوَاجِهِمْ مَتَاعاً اِلَى الْحَوْلِ غَيْرَ اِخْرَاجٍۚ فَاِنْ خَرَجْنَ فَلَا جُنَاحَ عَلَيْكُمْ ف۪ي مَا فَعَلْنَ ف۪ٓي اَنْفُسِهِنَّ مِنْ مَعْرُوفٍۜ وَاللّٰهُ عَز۪يزٌ حَك۪يمٌ', 'Sizden ölüp de (dul) eşler bırakan kimseler, zevcelerinin, evlerinden çıkarılmadan, bir yıla kadar bıraktıkları maldan faydalanmaları hususunda (sağlıklarında) vasiyet etsinler. Eğer o kadınlar, (kendiliklerinden) çıkıp giderlerse, kendileri hakkında yaptıkları meşru şeylerden size bir günah yoktur. Allah azîzdir, hakîmdir.', 2, 38, 286, 25613, 'Sığır', 'Medine', 87),
(248, 241, 2, 'Bakara', 'وَلِلْمُطَلَّقَاتِ مَتَاعٌ بِالْمَعْرُوفِۜ حَقاًّ عَلَى الْمُتَّق۪ينَ', 'Boşanmış kadınların, hakkaniyet ölçülerinde (kocalarından) menfaat sağlamak haklarıdır; bu, Allah korkusu taşıyanlar üzerine bir borçtur.', 2, 38, 286, 25613, 'Sığır', 'Medine', 87),
(249, 242, 2, 'Bakara', 'كَذٰلِكَ يُبَيِّنُ اللّٰهُ لَكُمْ اٰيَاتِه۪ لَعَلَّكُمْ تَعْقِلُونَ۟', 'Allah size işte böylece âyetlerini açıklar ki düşünüp hakikati anlayasınız.', 2, 38, 286, 25613, 'Sığır', 'Medine', 87),
(250, 243, 2, 'Bakara', 'اَلَمْ تَرَ اِلَى الَّذ۪ينَ خَرَجُوا مِنْ دِيَارِهِمْ وَهُمْ اُلُوفٌ حَذَرَ الْمَوْتِۖ فَقَالَ لَهُمُ اللّٰهُ مُوتُوا ثُمَّ اَحْيَاهُمْۜ اِنَّ اللّٰهَ لَذُو فَضْلٍ عَلَى النَّاسِ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَشْكُرُونَ', 'Binlerce oldukları halde, ölüm korkusundan dolayı yurtlarından çıkıp gidenleri görmedin mi? Allah onlara «Ölün!» dedi (öldüler). Sonra onları diriltti. Şüphesiz Allah insanlara karşı lütufkârdır. Lâkin insanların çoğu şükretmez.', 2, 38, 286, 25613, 'Sığır', 'Medine', 87),
(251, 244, 2, 'Bakara', 'وَقَاتِلُوا ف۪ي سَب۪يلِ اللّٰهِ وَاعْلَمُٓوا اَنَّ اللّٰهَ سَم۪يعٌ عَل۪يمٌ', 'Allah yolunda savaşın ve bilin ki Allah, her şeyi işitir ve bilir.', 2, 38, 286, 25613, 'Sığır', 'Medine', 87),
(252, 245, 2, 'Bakara', 'مَنْ ذَا الَّذ۪ي يُقْرِضُ اللّٰهَ قَرْضاً حَسَناً فَيُضَاعِفَهُ لَهُٓ اَضْعَافاً كَـث۪يرَةًۜ وَاللّٰهُ يَقْبِضُ وَيَبْصُۣطُۖ وَاِلَيْهِ تُرْجَعُونَ', 'Verdiğinin kat kat fazlasını kendisine ödemesi için Allah´a güzel bir borç (isteyene faizsiz ödünç) verecek yok mu? Darlık veren de bolluk veren de Allah´tır. Sadece O´na döndürüleceksiniz.', 2, 38, 286, 25613, 'Sığır', 'Medine', 87),
(253, 246, 2, 'Bakara', 'اَلَمْ تَرَ اِلَى الْمَلَأِ مِنْ بَن۪ٓي اِسْرَٓائ۪لَ مِنْ بَعْدِ مُوسٰىۢ اِذْ قَالُوا لِنَبِيٍّ لَهُمُ ابْعَثْ لَنَا مَلِكاً نُقَاتِلْ ف۪ي سَب۪يلِ اللّٰهِۜ قَالَ هَلْ عَسَيْتُمْ اِنْ كُتِبَ عَلَيْكُمُ الْقِتَالُ اَلَّا تُقَاتِلُواۜ قَالُوا وَمَا لَـنَٓا اَلَّا نُقَاتِلَ ف۪ي سَب۪يلِ اللّٰهِ وَقَدْ اُخْرِجْنَا مِنْ دِيَارِنَا وَاَبْنَٓائِنَاۜ فَلَمَّا كُتِبَ عَلَيْهِمُ الْقِتَالُ تَوَلَّوْا اِلَّا قَل۪يلاً مِنْهُمْۜ وَاللّٰهُ عَل۪يمٌ بِالظَّالِم۪ينَ', 'Musa´dan sonra, Benî İsrail´den ileri gelen kimseleri görmedin mi? Kendilerine gönderilmiş bir peygambere: «Bize bir hükümdar gönder ki (onun komutasında) Allah yolunda savaşalım» demişlerdi. «Ya size savaş yazılır da savaşmazsanız?» dedi. «Yurtlarımızdan çıkarılmış, çocuklarımızdan uzaklaştırılmış olduğumuz halde Allah yolunda neden savaşmayalım?» dediler. Kendilerine savaş yazılınca, içlerinden pek azı hariç, geri dönüp kaçtılar. Allah zalimleri iyi bilir.', 2, 39, 286, 25613, 'Sığır', 'Medine', 87),
(254, 247, 2, 'Bakara', 'وَقَالَ لَهُمْ نَبِيُّهُمْ اِنَّ اللّٰهَ قَدْ بَعَثَ لَكُمْ طَالُوتَ مَلِكاًۜ قَالُٓوا اَنّٰى يَكُونُ لَهُ الْمُلْكُ عَلَيْنَا وَنَحْنُ اَحَقُّ بِالْمُلْكِ مِنْهُ وَلَمْ يُؤْتَ سَعَةً مِنَ الْمَالِۜ قَالَ اِنَّ اللّٰهَ اصْطَفٰيهُ عَلَيْكُمْ وَزَادَهُ بَسْطَةً فِي الْعِلْمِ وَالْجِسْمِۜ وَاللّٰهُ يُؤْت۪ي مُلْكَهُ مَنْ يَشَٓاءُۜ وَاللّٰهُ وَاسِعٌ عَل۪يمٌ', 'Peygamberleri onlara: Bilin ki Allah, Tâlût´u size hükümdar olarak gönderdi, dedi. Bunun üzerine: Biz, hükümdarlığa daha lâyık olduğumuz halde, kendisine servet ve zenginlik yönünden geniş imkânlar verilmemişken o bize nasıl hükümdar olur? dediler. «Allah sizin üzerinize onu seçti, ilimde ve bedende ona üstünlük verdi. Allah mülkünü dilediğine verir. Allah her şeyi ihata eden ve her şeyi bilendir» dedi.', 2, 39, 286, 25613, 'Sığır', 'Medine', 87),
(255, 248, 2, 'Bakara', 'وَقَالَ لَهُمْ نَبِيُّهُمْ اِنَّ اٰيَةَ مُلْكِه۪ٓ اَنْ يَأْتِيَكُمُ التَّابُوتُ ف۪يهِ سَك۪ينَةٌ مِنْ رَبِّكُمْ وَبَقِيَّةٌ مِمَّا تَرَكَ اٰلُ مُوسٰى وَاٰلُ هٰرُونَ تَحْمِلُهُ الْمَلٰٓئِكَةُۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَةً لَكُمْ اِنْ كُنْتُمْ مُؤْمِن۪ينَ۟', 'Peygamberleri onlara: Onun hükümdarlığının alâmeti, Tabut´un size gelmesidir. Meleklerin taşıdığı o Tabut´un içinde Rabbinizden size bir ferahlık ve sükûnet, Musa ve Harun hanedanlarının bıraktıklarından bir kalıntı vardır. Eğer inanmış kimseler iseniz sizin için bunda şüphesiz bir alâmet vardır, dedi.', 2, 39, 286, 25613, 'Sığır', 'Medine', 87),
(256, 249, 2, 'Bakara', 'فَلَمَّا فَصَلَ طَالُوتُ بِالْجُنُودِۙ قَالَ اِنَّ اللّٰهَ مُبْتَل۪يكُمْ بِنَهَرٍۚ فَمَنْ شَرِبَ مِنْهُ فَلَيْسَ مِنّ۪يۚ وَمَنْ لَمْ يَطْعَمْهُ فَاِنَّهُ مِنّ۪ٓي اِلَّا مَنِ اغْتَرَفَ غُرْفَةً بِيَدِه۪ۚ فَشَرِبُوا مِنْهُ اِلَّا قَل۪يلاً مِنْهُمْۜ فَلَمَّا جَاوَزَهُ هُوَ وَالَّذ۪ينَ اٰمَنُوا مَعَهُۙ قَالُوا لَا طَاقَةَ لَنَا الْيَوْمَ بِجَالُوتَ وَجُنُودِه۪ۜ قَالَ الَّذ۪ينَ يَظُنُّونَ اَنَّهُمْ مُلَاقُوا اللّٰهِۙ كَمْ مِنْ فِئَةٍ قَل۪يلَةٍ غَلَبَتْ فِئَةً كَث۪يرَةً بِاِذْنِ اللّٰهِۜ وَاللّٰهُ مَعَ الصَّابِر۪ينَ', 'Tâlût askerlerle beraber (cihad için) ayrılınca: Biliniz ki Allah sizi bir ırmakla imtihan edecek. Kim ondan içerse benden değildir. Eliyle bir avuç içen müstesna kim ondan içmezse bendendir, dedi. İçlerinden pek azı müstesna hepsi ırmaktan içtiler. Tâlût ve iman edenler beraberce ırmağı geçince: Bugün bizim Câlût´a ve askerlerine karşı koyacak hiç gücümüz yoktur, dediler. Allah´ın huzuruna varacaklarına inananlar: Nice az sayıda bir birlik Allah´ın izniyle çok sayıdaki birliği yenmiştir. Allah sabredenlerle beraberdir, dediler.', 2, 40, 286, 25613, 'Sığır', 'Medine', 87),
(257, 250, 2, 'Bakara', 'وَلَمَّا بَرَزُوا لِجَالُوتَ وَجُنُودِه۪ قَالُوا رَبَّنَٓا اَفْرِغْ عَلَيْنَا صَبْراً وَثَبِّتْ اَقْدَامَنَا وَانْصُرْنَا عَلَى الْقَوْمِ الْكَافِر۪ينَۜ', 'Câlût ve askerleriyle savaşa tutuştuklarında: Ey Rabbimiz! Yüreğimizi sabırla doldur; bize direnme gücü ver; kâfir kavme karşı bize yardım et, dediler.', 2, 40, 286, 25613, 'Sığır', 'Medine', 87),
(258, 251, 2, 'Bakara', 'فَهَزَمُوهُمْ بِاِذْنِ اللّٰهِۙ وَقَتَلَ دَاوُ۫دُ جَالُوتَ وَاٰتٰيهُ اللّٰهُ الْمُلْكَ وَالْحِكْمَةَ وَعَلَّمَهُ مِمَّا يَشَٓاءُۜ وَلَوْلَا دَفْعُ اللّٰهِ النَّاسَ بَعْضَهُمْ بِبَعْضٍ لَفَسَدَتِ الْاَرْضُ وَلٰكِنَّ اللّٰهَ ذُوفَضْلٍ عَلَى الْعَالَم۪ينَ', 'Sonunda Allah´ın izniyle onları yendiler. Davud da Câlût´u öldürdü. Allah ona (Davud´a) hükümdarlık ve hikmet verdi, dilediği ilimlerden ona öğretti. Eğer Allah´ın insanlardan bir kısmını diğerleriyle savması olmasaydı elbette yeryüzü altüst olurdu. Lâkin Allah bütün insanlığa karşı lütuf ve kerem sahibidir.', 2, 40, 286, 25613, 'Sığır', 'Medine', 87),
(259, 252, 2, 'Bakara', 'تِلْكَ اٰيَاتُ اللّٰهِ نَتْلُوهَا عَلَيْكَ بِالْحَقِّۜ وَاِنَّكَ لَمِنَ الْمُرْسَل۪ينَ', 'İşte bunlar Allah´ın âyetleridir. Biz onları sana doğru olarak anlatıyoruz. Şüphesiz sen, Allah tarafından gönderilmiş peygamberlerdensin.', 2, 40, 286, 25613, 'Sığır', 'Medine', 87),
(260, 253, 2, 'Bakara', 'تِلْكَ الرُّسُلُ فَضَّلْنَا بَعْضَهُمْ عَلٰى بَعْضٍۢ مِنْهُمْ مَنْ كَلَّمَ اللّٰهُ وَرَفَعَ بَعْضَهُمْ دَرَجَاتٍۜ وَاٰتَيْنَا ع۪يسَى ابْنَ مَرْيَمَ الْبَيِّنَاتِ وَاَيَّدْنَاهُ بِرُوحِ الْقُدُسِۜ وَلَوْ شَٓاءَ اللّٰهُ مَا اقْتَتَلَ الَّذ۪ينَ مِنْ بَعْدِهِمْ مِنْ بَعْدِ مَا جَٓاءَتْهُمُ الْبَيِّنَاتُ وَلٰكِنِ اخْتَلَفُوا فَمِنْهُمْ مَنْ اٰمَنَ وَمِنْهُمْ مَنْ كَفَرَۜ وَلَوْ شَٓاءَ اللّٰهُ مَا اقْتَتَلُوا وَلٰكِنَّ اللّٰهَ يَفْعَلُ مَا يُر۪يدُ۟', 'O peygamberlerin bir kısmını diğerlerinden üstün kıldık. Allah onlardan bir kısmı ile konuşmuş, bazılarını da derece derece yükseltmiştir. Meryem oğlu İsa´ya açık mucizeler verdik ve onu Rûhu´l-Kudüs ile güçlendirdik. Allah dileseydi o peygamberlerden sonra gelen milletler, kendilerine açık deliller geldikten sonra birbirleriyle savaşmazlardı. Fakat onlar ihtilafa düştüler de içlerinden kimi iman etti, kimi de inkâr etti. Allah dileseydi onlar savaşmazlardı; lâkin Allah dilediğini yapar.', 3, 41, 286, 25613, 'Sığır', 'Medine', 87),
(261, 254, 2, 'Bakara', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُٓوا اَنْفِقُوا مِمَّا رَزَقْنَاكُمْ مِنْ قَبْلِ اَنْ يَأْتِيَ يَوْمٌ لَا بَيْعٌ ف۪يهِ وَلَا خُلَّةٌ وَلَا شَفَاعَةٌۜ وَالْكَافِرُونَ هُمُ الظَّالِمُونَ', 'Ey iman edenler! Kendisinde artık alış-veriş, dostluk ve kayırma bulunmayan gün (kıyamet) gelmeden önce, size verdiğimiz rızıktan hayır yolunda harcayın. Gerçekleri inkâr edenler elbette zalimlerdir.', 3, 41, 286, 25613, 'Sığır', 'Medine', 87),
(262, 255, 2, 'Bakara', 'اَللّٰهُ لَٓا اِلٰهَ اِلَّا هُوَۚ اَلْحَيُّ الْقَيُّومُۚ لَا تَأْخُذُهُ سِنَةٌ وَلَا نَوْمٌۜ لَهُ مَا فِي السَّمٰوَاتِ وَمَا فِي الْاَرْضِۜ مَنْ ذَا الَّذ۪ي يَشْفَعُ عِنْدَهُٓ اِلَّا بِاِذْنِه۪ۜ يَعْلَمُ مَا بَيْنَ اَيْد۪يهِمْ وَمَا خَلْفَهُمْۚ وَلَا يُح۪يطُونَ بِشَيْءٍ مِنْ عِلْمِه۪ٓ اِلَّا بِمَا شَٓاءَۚ وَسِعَ كُرْسِيُّهُ السَّمٰوَاتِ وَالْاَرْضَۚ وَلَا يَؤُ۫دُهُ حِفْظُهُمَاۚ وَهُوَ الْعَلِيُّ الْعَظ۪يمُ', 'Allah, O´ndan başka tanrı yoktur; O, hayydir, kayyûmdur. Kendisine ne uyku gelir ne de uyuklama. Göklerde ve yerdekilerin hepsi O´nundur. İzni olmadan O´nun katında kim şefaat edebilir? O, kullarının yaptıklarını ve yapacaklarını bilir. (O´na hiçbir şey gizli kalmaz.) O´nun bildirdiklerinin dışında insanlar O´nun ilminden hiçbir şeyi tam olarak bilemezler. O´nun kürsüsü gökleri ve yeri içine alır, onları koruyup gözetmek kendisine zor gelmez. O, yücedir, büyüktür.', 3, 41, 286, 25613, 'Sığır', 'Medine', 87),
(263, 256, 2, 'Bakara', 'لَٓا اِكْرَاهَ فِي الدّ۪ينِ قَدْ تَبَيَّنَ الرُّشْدُ مِنَ الْغَيِّۚ فَمَنْ يَكْفُرْ بِالطَّاغُوتِ وَيُؤْمِنْ بِاللّٰهِ فَقَدِ اسْتَمْسَكَ بِالْعُرْوَةِ الْوُثْقٰىۗ لَا انْفِصَامَ لَهَاۜ وَاللّٰهُ سَم۪يعٌ عَل۪يمٌ', 'Dinde zorlama yoktur. Artık doğrulukla eğrilik birbirinden ayrılmıştır. O halde kim tâğutu reddedip Allah´a inanırsa, kopmayan sağlam kulpa yapışmıştır. Allah işitir ve bilir.', 3, 41, 286, 25613, 'Sığır', 'Medine', 87),
(264, 257, 2, 'Bakara', 'اَللّٰهُ وَلِيُّ الَّذ۪ينَ اٰمَنُواۙ يُخْرِجُهُمْ مِنَ الظُّلُمَاتِ اِلَى النُّورِۜ وَالَّذ۪ينَ كَفَرُٓوا اَوْلِيَٓاؤُ۬هُمُ الطَّاغُوتُۙ يُخْرِجُونَهُمْ مِنَ النُّورِ اِلَى الظُّلُمَاتِۜ اُو۬لٰٓئِكَ اَصْحَابُ النَّارِۚ هُمْ ف۪يهَا خَالِدُونَ۟', 'Allah, inananların dostudur, onları karanlıklardan aydınlığa çıkarır. İnkâr edenlere gelince, onların dostları da tâğuttur, onları aydınlıktan alıp karanlığa götürürler. İşte bunlar cehennemliklerdir. Onlar orada devamlı kalırlar.', 3, 42, 286, 25613, 'Sığır', 'Medine', 87),
(265, 258, 2, 'Bakara', 'اَلَمْ تَرَ اِلَى الَّذ۪ي حَٓاجَّ اِبْرٰه۪يمَ ف۪ي رَبِّه۪ٓ اَنْ اٰتٰيهُ اللّٰهُ الْمُلْكَۢ اِذْ قَالَ اِبْرٰه۪يمُ رَبِّيَ الَّذ۪ي يُحْـي۪ وَيُم۪يتُۙ قَالَ اَنَا۬ اُحْـي۪ وَاُم۪يتُۜ قَالَ اِبْرٰه۪يمُ فَاِنَّ اللّٰهَ يَأْت۪ي بِالشَّمْسِ مِنَ الْمَشْرِقِ فَأْتِ بِهَا مِنَ الْمَغْرِبِ فَبُهِتَ الَّذ۪ي كَفَرَۜ وَاللّٰهُ لَا يَهْدِي الْقَوْمَ الظَّالِم۪ينَۚ', 'Allah kendisine mülk (hükümdarlık ve zenginlik) verdiği için şımararak Rabbi hakkında İbrahim ile tartışmaya gireni (Nemrut´u) görmedin mi! İşte o zaman İbrahim: Rabbim hayat veren ve öldürendir, demişti. O da: Hayat veren ve öldüren benim, demişti. İbrahim: Allah güneşi doğudan getirmektedir; haydi sen de onu batıdan getir, dedi. Bunun üzerine kâfir apışıp kaldı. Allah zalim kimseleri hidayete erdirmez.', 3, 42, 286, 25613, 'Sığır', 'Medine', 87),
(266, 259, 2, 'Bakara', 'اَوْ كَالَّذ۪ي مَرَّ عَلٰى قَرْيَةٍ وَهِيَ خَاوِيَةٌ عَلٰى عُرُوشِهَاۚ قَالَ اَنّٰى يُحْـي۪ هٰذِهِ اللّٰهُ بَعْدَ مَوْتِهَاۚ فَاَمَاتَهُ اللّٰهُ مِائَةَ عَامٍ ثُمَّ بَعَثَهُۜ قَالَ كَمْ لَبِثْتَۜ قَالَ لَبِثْتُ يَوْماً اَوْ بَعْضَ يَوْمٍۜ قَالَ بَلْ لَبِثْتَ مِائَةَ عَامٍ فَانْظُرْ اِلٰى طَعَامِكَ وَشَرَابِكَ لَمْ يَتَسَنَّهْۚ وَانْظُرْ اِلٰى حِمَارِكَ وَلِنَجْعَلَكَ اٰيَةً لِلنَّاسِ وَانْظُرْ اِلَى الْعِظَامِ كَيْفَ نُنْشِزُهَا ثُمَّ نَكْسُوهَا لَحْماًۜ فَلَمَّا تَبَيَّنَ لَهُۙ قَالَ اَعْلَمُ اَنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌ', 'Yahut görmedin mi o kimseyi ki, evlerinin duvarları çatıları üzerine çökmüş (alt üst olmuş) bir kasabaya uğradı; «Ölümünden sonra Allah bunları nasıl diriltir acaba!» dedi. Bunun üzerine Allah onu öldürüp yüz sene bıraktı; sonra tekrar diriltti. Ne kadar kaldın? dedi. «Bir gün yahut daha az» dedi. Allah ona: Hayır, yüz sene kaldın. Yiyeceğine ve içeceğine bak, henüz bozulmamıştır. Eşeğine de bak. Seni insanlara bir ibret kılalım diye (yüz sene ölü tuttuk, sonra tekrar dirilttik). Şimdi sen kemiklere bak, onları nasıl düzenliyor, sonra ona nasıl et giydiriyoruz, dedi. Durum kendisince anlaşılınca: Şimdi iyice biliyorum ki, Allah her şeye kadirdir, dedi.', 3, 42, 286, 25613, 'Sığır', 'Medine', 87),
(267, 260, 2, 'Bakara', 'وَاِذْ قَالَ اِبْرٰه۪يمُ رَبِّ اَرِن۪ي كَيْفَ تُحْـيِ الْمَوْتٰىۜ قَالَ اَوَلَمْ تُؤْمِنْۜ قَالَ بَلٰى وَلٰكِنْ لِيَطْمَئِنَّ قَلْب۪يۜ قَالَ فَخُذْ اَرْبَعَةً مِنَ الطَّيْرِ فَصُرْهُنَّ اِلَيْكَ ثُمَّ اجْعَلْ عَلٰى كُلِّ جَبَلٍ مِنْهُنَّ جُزْءاً ثُمَّ ادْعُهُنَّ يَأْت۪ينَكَ سَعْياًۜ وَاعْلَمْ اَنَّ اللّٰهَ عَز۪يزٌ حَك۪يمٌ۟', 'İbrahim Rabbine: Ey Rabbim! Ölüyü nasıl dirilttiğini bana göster, demişti. Rabbi ona: Yoksa inanmadın mı? dedi. İbrahim: Hayır! İnandım, fakat kalbimin mutmain olması için (görmek istedim), dedi. Bunun üzerine Allah: Öyleyse dört tane kuş yakala, onları yanına al, sonra (kesip parçala), her dağın başına onlardan bir parça koy. Sonra da onları kendine çağır; koşarak sana gelirler. Bil ki Allah azîzdir, hakîmdir, buyurdu.', 3, 43, 286, 25613, 'Sığır', 'Medine', 87),
(268, 261, 2, 'Bakara', 'مَثَلُ الَّذ۪ينَ يُنْفِقُونَ اَمْوَالَهُمْ ف۪ي سَب۪يلِ اللّٰهِ كَمَثَلِ حَبَّةٍ اَنْبَتَتْ سَبْعَ سَنَابِلَ ف۪ي كُلِّ سُنْبُلَةٍ مِائَةُ حَبَّةٍۜ وَاللّٰهُ يُضَاعِفُ لِمَنْ يَشَٓاءُۜ وَاللّٰهُ وَاسِعٌ عَل۪يمٌ', 'Allah yolunda mallarını harcayanların örneği, yedi başak bitiren bir dane gibidir ki, her başakta yüz dane vardır. Allah dilediğine kat kat fazlasını verir. Allah´ın lütfu geniştir, O herşeyi bilir.', 3, 43, 286, 25613, 'Sığır', 'Medine', 87),
(269, 262, 2, 'Bakara', 'اَلَّذ۪ينَ يُنْفِقُونَ اَمْوَالَهُمْ ف۪ي سَب۪يلِ اللّٰهِ ثُمَّ لَا يُتْبِعُونَ مَٓا اَنْفَقُوا مَناًّ وَلَٓا اَذًۙى لَهُمْ اَجْرُهُمْ عِنْدَ رَبِّهِمْۚ وَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ', 'Mallarını Allah yolunda harcayıp da arkasından başa kakmayan, fakirlerin gönlünü kırmayan kimseler var ya, onların Allah katında has mükâfatları vardır. Onlar için korku yoktur, üzüntü de çekmeyeceklerdir.', 3, 43, 286, 25613, 'Sığır', 'Medine', 87),
(270, 263, 2, 'Bakara', 'قَوْلٌ مَعْرُوفٌ وَمَغْفِرَةٌ خَيْرٌ مِنْ صَدَقَةٍ يَتْبَعُهَٓا اَذًىۜ وَاللّٰهُ غَنِيٌّ حَل۪يمٌ', 'Güzel söz ve bağışlama, arkasından incitme gelen sadakadan daha iyidir. Allah zengindir, acelesi de yoktur.', 3, 43, 286, 25613, 'Sığır', 'Medine', 87),
(271, 264, 2, 'Bakara', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا لَا تُبْطِلُوا صَدَقَاتِكُمْ بِالْمَنِّ وَالْاَذٰىۙ كَالَّذ۪ي يُنْفِقُ مَالَهُ رِئَٓاءَ النَّاسِ وَلَا يُؤْمِنُ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِۜ فَمَثَلُهُ كَمَثَلِ صَفْوَانٍ عَلَيْهِ تُرَابٌ فَاَصَابَهُ وَابِلٌ فَتَرَكَهُ صَلْداًۜ لَا يَقْدِرُونَ عَلٰى شَيْءٍ مِمَّا كَسَبُواۜ وَاللّٰهُ لَا يَهْدِي الْقَوْمَ الْكَافِر۪ينَ', 'Ey iman edenler! Allah´a ve ahiret gününe inanmadığı halde malını gösteriş için harcayan kimse gibi, başa kakmak ve incitmek suretiyle, yaptığınız hayırlarınızı boşa çıkarmayın. Böylesinin durumu, üzerinde biraz toprak bulunan düz kayaya benzer ki, sağanak bir yağmur isabet etmiş de onu çıplak pürüzsüz kaya haline getirivermiştir. Bunlar kazandıklarından hiçbir şeye sahip olamazlar. Allah, kâfirleri doğru yola iletmez.', 3, 43, 286, 25613, 'Sığır', 'Medine', 87),
(272, 265, 2, 'Bakara', 'وَمَثَلُ الَّذ۪ينَ يُنْفِقُونَ اَمْوَالَهُمُ ابْتِغَٓاءَ مَرْضَاتِ اللّٰهِ وَتَثْب۪يتاً مِنْ اَنْفُسِهِمْ كَمَثَلِ جَنَّةٍ بِرَبْوَةٍ اَصَابَهَا وَابِلٌ فَاٰتَتْ اُكُلَهَا ضِعْفَيْنِۚ فَاِنْ لَمْ يُصِبْهَا وَابِلٌ فَطَلٌّۜ وَاللّٰهُ بِمَا تَعْمَلُونَ بَص۪يرٌ', 'Allah´ın rızasını kazanmak ve ruhlarındaki cömertliği kuvvetlendirmek için mallarını hayra sarfedenlerin durumu, bir tepede kurulmuş güzel bir bahçeye benzer ki, üzerine bol yağmur yağmış da iki kat ürün vermiştir. Bol yağmur yağmasa bile bir çisinti düşer (de yine ürün verir). Allah, yaptıklarınızı görmektedir.', 3, 44, 286, 25613, 'Sığır', 'Medine', 87),
(273, 266, 2, 'Bakara', 'اَيَوَدُّ اَحَدُكُمْ اَنْ تَكُونَ لَهُ جَنَّةٌ مِنْ نَخ۪يلٍ وَاَعْنَابٍ تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُۙ لَهُ ف۪يهَا مِنْ كُلِّ الثَّمَرَاتِۙ وَاَصَابَهُ الْكِبَرُ وَلَهُ ذُرِّيَّةٌ ضُعَفَٓاءُۖ فَاَصَابَهَٓا اِعْصَارٌ ف۪يهِ نَارٌ فَاحْتَرَقَتْۜ كَذٰلِكَ يُبَيِّنُ اللّٰهُ لَكُمُ الْاٰيَاتِ لَعَلَّكُمْ تَتَفَكَّرُونَ۟', 'Sizden biriniz arzu eder mi ki, hurma ve üzüm ağaçlarıyla dolu, arasından sular akan ve kendisi için orada her çeşit meyveden (bir miktar) bulunan bir bahçesi olsun da, bakıma muhtaç çoluk çocuğu varken kendisine ihtiyarlık gelip çatsın, bahçeye de içinde ateş bulunan bir kasırga isabet ederek yakıp kül etsin! (Elbette bunu kimse arzu etmez.) İşte düşünüp anlayasınız diye Allah size âyetleri açıklar.', 3, 44, 286, 25613, 'Sığır', 'Medine', 87),
(274, 267, 2, 'Bakara', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُٓوا اَنْفِقُوا مِنْ طَيِّبَاتِ مَا كَسَبْتُمْ وَمِمَّٓا اَخْرَجْنَا لَكُمْ مِنَ الْاَرْضِۖ وَلَا تَيَمَّمُوا الْخَب۪يثَ مِنْهُ تُنْفِقُونَ وَلَسْتُمْ بِاٰخِذ۪يهِ اِلَّٓا اَنْ تُغْمِضُوا ف۪يهِۜ وَاعْلَمُٓوا اَنَّ اللّٰهَ غَنِيٌّ حَم۪يدٌ', 'Ey iman edenler! Kazandıklarınızın iyilerinden ve rızık olarak yerden size çıkardıklarımızdan hayra harcayın. Size verilse, gözünüzü yummadan alamayacağınız kötü malı, hayır diye vermeye kalkışmayın. Biliniz ki Allah zengindir, övgüye lâyıktır.', 3, 44, 286, 25613, 'Sığır', 'Medine', 87),
(275, 268, 2, 'Bakara', 'اَلشَّيْطَانُ يَعِدُكُمُ الْفَقْرَ وَيَأْمُرُكُمْ بِالْفَحْشَٓاءِۚ وَاللّٰهُ يَعِدُكُمْ مَغْفِرَةً مِنْهُ وَفَضْلاًۜ وَاللّٰهُ وَاسِعٌ عَل۪يمٌۚ', 'Şeytan sizi fakirlikle korkutur ve size cimriliği telkin eder. Allah ise size katından bir mağfiret ve bir lütuf vâdeder. Allah herşeyi ihata eden ve herşeyi bilendir.', 3, 44, 286, 25613, 'Sığır', 'Medine', 87),
(276, 269, 2, 'Bakara', 'يُؤْتِي الْحِكْمَةَ مَنْ يَشَٓاءُۚ وَمَنْ يُؤْتَ الْحِكْمَةَ فَقَدْ اُو۫تِيَ خَيْراً كَث۪يراًۜ وَمَا يَذَّكَّرُ اِلَّٓا اُو۬لُوا الْاَلْبَابِ', 'Allah hikmeti dilediğine verir. Kime hikmet verilirse, ona pek çok hayır verilmiş demektir. Ancak akıl sahipleri düşünüp ibret alırlar.', 3, 44, 286, 25613, 'Sığır', 'Medine', 87),
(277, 270, 2, 'Bakara', 'وَمَٓا اَنْفَقْتُمْ مِنْ نَفَقَةٍ اَوْ نَذَرْتُمْ مِنْ نَذْرٍ فَاِنَّ اللّٰهَ يَعْلَمُهُۜ وَمَا لِلظَّالِم۪ينَ مِنْ اَنْصَارٍ', 'Yaptığınız her harcamayı ve adadığınız her adağı muhakkak Allah bilir. Zalimler için hiç yardımcı yoktur.', 3, 45, 286, 25613, 'Sığır', 'Medine', 87),
(278, 271, 2, 'Bakara', 'اِنْ تُبْدُوا الصَّدَقَاتِ فَنِعِمَّا هِيَۚ وَاِنْ تُخْفُوهَا وَتُؤْتُوهَا الْفُقَـرَٓاءَ فَهُوَ خَيْرٌ لَكُمْۜ وَيُكَفِّرُ عَنْكُمْ مِنْ سَيِّـَٔاتِكُمْۜ وَاللّٰهُ بِمَا تَعْمَلُونَ خَب۪يرٌ', 'Eğer sadakaları (zekât ve benzeri hayırları) açıktan verirseniz ne âlâ! Eğer onu fakirlere gizlice verirseniz, işte bu sizin için daha hayırlıdır. Allah da bu sebeple sizin günahlarınızı örter. Allah, yapmakta olduklarınızı bilir.', 3, 45, 286, 25613, 'Sığır', 'Medine', 87),
(279, 272, 2, 'Bakara', 'لَيْسَ عَلَيْكَ هُدٰيهُمْ وَلٰكِنَّ اللّٰهَ يَهْد۪ي مَنْ يَشَٓاءُۜ وَمَا تُنْفِقُوا مِنْ خَيْرٍ فَلِاَنْفُسِكُمْۜ وَمَا تُنْفِقُونَ اِلَّا ابْتِغَٓاءَ وَجْهِ اللّٰهِۜ وَمَا تُنْفِقُوا مِنْ خَيْرٍ يُوَفَّ اِلَيْكُمْ وَاَنْتُمْ لَا تُظْلَمُونَ', '(Ya Muhammed!) Onları doğru yola iletmek sana ait değildir. Lâkin Allah dilediğini doğru yola iletir. Hayır olarak harcadıklarınız kendi iyiliğiniz içindir. Yapacağınız hayırları ancak Allah´ın rızasını kazanmak için yapmalısınız. Hayır olarak verdiğiniz ne varsa, karşılığı size tam olarak verilir ve asla haksızlığa uğratılmazsınız.', 3, 45, 286, 25613, 'Sığır', 'Medine', 87),
(280, 273, 2, 'Bakara', 'لِلْفُقَـرَٓاءِ الَّذ۪ينَ اُحْصِرُوا ف۪ي سَب۪يلِ اللّٰهِ لَا يَسْتَط۪يعُونَ ضَـرْباً فِي الْاَرْضِۘ يَحْسَبُهُمُ الْجَاهِلُ اَغْنِيَٓاءَ مِنَ التَّعَفُّفِۚ تَعْرِفُهُمْ بِس۪يمٰيهُمْۚ لَا يَسْـَٔلُونَ النَّاسَ اِلْحَافاًۜ وَمَا تُنْفِقُوا مِنْ خَيْرٍ فَاِنَّ اللّٰهَ بِه۪ عَل۪يمٌ۟', '(Yapacağınız hayırlar,) kendilerini Allah yoluna adamış, bu sebeple yeryüzünde kazanç için dolaşamayan fakirler için olsun. Bilmeyen kimseler, iffetlerinden dolayı onları zengin zanneder. Sen onları simalarından tanırsın. Çünkü onlar yüzsüzlük ederek istemezler. Yaptığınız her hayrı muhakkak Allah bilir.', 3, 45, 286, 25613, 'Sığır', 'Medine', 87),
(281, 274, 2, 'Bakara', 'اَلَّذ۪ينَ يُنْفِقُونَ اَمْوَالَهُمْ بِالَّيْلِ وَالنَّهَارِ سِراًّ وَعَلَانِيَةً فَلَهُمْ اَجْرُهُمْ عِنْدَ رَبِّهِمْۚ وَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ', 'Mallarını gece ve gündüz, gizli ve açık hayra sarfedenler var ya, onların mükâfatları Allah katındadır. Onlara korku yoktur, üzüntü de çekmezler.', 3, 45, 286, 25613, 'Sığır', 'Medine', 87),
(282, 275, 2, 'Bakara', 'اَلَّذ۪ينَ يَأْكُلُونَ الرِّبٰوا لَا يَقُومُونَ اِلَّا كَمَا يَقُومُ الَّذ۪ي يَتَخَبَّطُهُ الشَّيْطَانُ مِنَ الْمَسِّۜ ذٰلِكَ بِاَنَّهُمْ قَالُٓوا اِنَّمَا الْبَيْعُ مِثْلُ الرِّبٰواۢ وَاَحَلَّ اللّٰهُ الْبَيْعَ وَحَرَّمَ الرِّبٰواۜ فَمَنْ جَٓاءَهُ مَوْعِظَةٌ مِنْ رَبِّه۪ فَانْتَهٰى فَلَهُ مَا سَلَفَۜ وَاَمْرُهُٓ اِلَى اللّٰهِۜ وَمَنْ عَادَ فَاُو۬لٰٓئِكَ اَصْحَابُ النَّارِۚ هُمْ ف۪يهَا خَالِدُونَ', 'Faiz yiyenler (kabirlerinden), şeytan çarpmış kimselerin cinnet nöbetinden kalktığı gibi kalkarlar. Bu hal onların «Alım-satım tıpkı faiz gibidir» demeleri yüzündendir. Halbuki Allah, alım-satımı helâl, faizi haram kılmıştır. Bundan sonra kime Rabbinden bir öğüt gelir de faizden vazgeçerse, geçmişte olan kendisinindir ve artık onun işi Allah´a kalmıştır. Kim tekrar faize dönerse, işte onlar cehennemliktir, orada devamlı kalırlar.', 3, 46, 286, 25613, 'Sığır', 'Medine', 87),
(283, 276, 2, 'Bakara', 'يَمْحَقُ اللّٰهُ الرِّبٰوا وَيُرْبِي الصَّدَقَاتِۜ وَاللّٰهُ لَا يُحِبُّ كُلَّ كَفَّارٍ اَث۪يمٍ', 'Allah faizi tüketir (Faiz karışan malın bereketini giderir), sadakaları ise bereketlendirir. Allah küfürde ve günahta ısrar eden hiç kimseyi sevmez.', 3, 46, 286, 25613, 'Sığır', 'Medine', 87),
(284, 277, 2, 'Bakara', 'اِنَّ الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ وَاَقَامُوا الصَّلٰوةَ وَاٰتَوُا الزَّكٰوةَ لَهُمْ اَجْرُهُمْ عِنْدَ رَبِّهِمْۚ وَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ', 'İman edip iyi işler yapan, namaz kılan ve zekât verenler var ya, onların mükâfatları Rableri katındadır. Onlara korku yoktur, onlar üzüntü de çekmezler.', 3, 46, 286, 25613, 'Sığır', 'Medine', 87),
(285, 278, 2, 'Bakara', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا اتَّقُوا اللّٰهَ وَذَرُوا مَا بَـقِيَ مِنَ الرِّبٰٓوا اِنْ كُنْتُمْ مُؤْمِن۪ينَ', 'Ey iman edenler! Allah´tan korkun. Eğer gerçekten inanıyorsanız mevcut faiz alacaklarınızı terkedin.', 3, 46, 286, 25613, 'Sığır', 'Medine', 87),
(286, 279, 2, 'Bakara', 'فَاِنْ لَمْ تَفْعَلُوا فَأْذَنُوا بِحَرْبٍ مِنَ اللّٰهِ وَرَسُولِه۪ۚ وَاِنْ تُبْتُمْ فَلَكُمْ رُؤُ۫سُ اَمْوَالِكُمْۚ لَا تَظْلِمُونَ وَلَا تُظْلَمُونَ', 'Şayet (faiz hakkında söylenenleri) yapmazsanız, Allah ve Resûlü tarafından (faizcilere karşı) açılan savaştan haberiniz olsun. Eğer tevbe edip vazgeçerseniz, sermayeniz sizindir; ne haksızlık etmiş ne de haksızlığa uğramış olursunuz.', 3, 46, 286, 25613, 'Sığır', 'Medine', 87),
(287, 280, 2, 'Bakara', 'وَاِنْ كَانَ ذُوعُسْرَةٍ فَنَظِرَةٌ اِلٰى مَيْسَرَةٍۜ وَاَنْ تَصَدَّقُوا خَيْرٌ لَكُمْ اِنْ كُنْتُمْ تَعْلَمُونَ', 'Eğer (borçlu) darlık içinde ise, eli genişleyinceye kadar ona mühlet vermek (gerekir). Eğer (gerçekleri) anlarsanız bunu sadakaya (veya zekâta) saymak sizin için daha hayırlıdır.', 3, 46, 286, 25613, 'Sığır', 'Medine', 87),
(288, 281, 2, 'Bakara', 'وَاتَّقُوا يَوْماً تُرْجَعُونَ ف۪يهِ اِلَى اللّٰهِ ثُمَّ تُوَفّٰى كُلُّ نَفْسٍ مَا كَسَبَتْ وَهُمْ لَا يُظْلَمُونَ۟', 'Allah´a döndürüleceğiniz, sonra da herkese hak ettiğinin eksiksiz verileceği ve kimsenin haksızlığa uğratılmayacağı bir günden sakının.', 3, 46, 286, 25613, 'Sığır', 'Medine', 87),
(289, 282, 2, 'Bakara', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُٓوا اِذَا تَدَايَنْتُمْ بِدَيْنٍ اِلٰٓى اَجَلٍ مُسَمًّى فَاكْتُبُوهُۜ وَلْيَكْتُبْ بَيْنَكُمْ كَاتِبٌ بِالْعَدْلِۖ وَلَا يَأْبَ كَاتِبٌ اَنْ يَكْتُبَ كَمَا عَلَّمَهُ اللّٰهُ فَلْيَكْتُبْۚ وَلْيُمْلِلِ الَّذ۪ي عَلَيْهِ الْحَقُّ وَلْيَتَّقِ اللّٰهَ رَبَّهُ وَلَا يَبْخَسْ مِنْهُ شَيْـٔاًۜ فَاِنْ كَانَ الَّذ۪ي عَلَيْهِ الْحَقُّ سَف۪يهاً اَوْ ضَع۪يفاً اَوْ لَا يَسْتَط۪يعُ اَنْ يُمِلَّ هُوَ فَلْيُمْلِلْ وَلِيُّهُ بِالْعَدْلِۜ وَاسْتَشْهِدُوا شَه۪يدَيْنِ مِنْ رِجَالِكُمْۚ فَاِنْ لَمْ يَكُونَا رَجُلَيْنِ فَرَجُلٌ وَامْرَاَتَانِ مِمَّنْ تَرْضَوْنَ مِنَ الشُّهَدَٓاءِ اَنْ تَضِلَّ اِحْدٰيهُمَا فَتُذَكِّرَ اِحْدٰيهُمَا الْاُخْرٰىۜ وَلَا يَأْبَ الشُّهَدَٓاءُ اِذَا مَا دُعُواۜ وَلَا تَسْـَٔمُٓوا اَنْ تَكْتُبُوهُ صَغ۪يراً اَوْ كَب۪يراً اِلٰٓى اَجَلِه۪ۜ ذٰلِكُمْ اَقْسَطُ عِنْدَ اللّٰهِ وَاَقْوَمُ لِلشَّهَادَةِ وَاَدْنٰٓى اَلَّا تَرْتَابُٓوا اِلَّٓا اَنْ تَكُونَ تِجَارَةً حَاضِرَةً تُد۪يرُونَهَا بَيْنَكُمْ فَلَيْسَ عَلَيْكُمْ جُنَاحٌ اَلَّا تَكْتُبُوهَاۜ وَاَشْهِدُٓوا اِذَا تَبَايَعْتُمْۖ وَلَا يُضَٓارَّ كَاتِبٌ وَلَا شَه۪يدٌۜ وَاِنْ تَفْعَلُوا فَاِنَّهُ فُسُوقٌ بِكُمْۜ وَاتَّقُوا اللّٰهَۜ وَيُعَلِّمُكُمُ اللّٰهُۜ وَاللّٰهُ بِكُلِّ شَيْءٍ عَل۪يمٌ', 'Ey iman edenler! Belirlenmiş bir süre için birbirinize borçlandığınız vakit onu yazın. Bir kâtip onu aranızda adaletle yazsın. Hiçbir kâtip Allah´ın kendisine öğrettiği gibi yazmaktan geri durmasın; (her şeyi olduğu gibi) yazsın. Üzerinde hak olan kimse (borçlu) da yazdırsın, Rabbinden korksun ve borcunu asla eksik yazdırmasın. Şayet borçlu sefih veya aklı zayıf veya kendisi söyleyip yazdıramayacak durumda ise, velisi adaletle yazdırsın. Erkeklerinizden iki de şahit bulundurun. Eğer iki erkek bulunamazsa rıza göstereceğiniz şahitlerden bir erkek ile -biri yanılırsa diğerinin ona hatırlatması için- iki kadın (olsun). Çağırıldıkları vakit şahitler gelmemezlik etmesin. Büyük veya küçük, vâdesine kadar hiçbir şeyi yazmaktan sakın üşenmeyin. Böyle yapmanız Allah nezdinde daha adaletli, şehadet için daha sağlam, şüpheye düşmemeniz için daha uygundur. Ancak aranızda yapıp bitirdiğiniz peşin bir ticaret olursa, bu durum farklıdır. Bu durumda onu yazmamanızda sizin için bir sakınca yoktur. (Genellikle) alış-veriş yaptığınızda şahit tutun. Ne yazan, ne de şahit zarara uğratılsın. Eğer bunu yaparsanız (zarar verirseniz) şüphe yok ki bu, sizin yoldan çıkmanız demektir. Allah´tan korkun. Allah size gerekli olanı öğretiyor. Allah her şeyi bilmektedir.', 3, 47, 286, 25613, 'Sığır', 'Medine', 87),
(290, 283, 2, 'Bakara', 'وَاِنْ كُنْتُمْ عَلٰى سَفَرٍ وَلَمْ تَجِدُوا كَاتِباً فَرِهَانٌ مَقْبُوضَةٌۜ فَاِنْ اَمِنَ بَعْضُكُمْ بَعْضاً فَلْيُؤَدِّ الَّذِي اؤْتُمِنَ اَمَانَتَهُ وَلْيَتَّقِ اللّٰهَ رَبَّهُۜ وَلَا تَكْتُمُوا الشَّهَادَةَۜ وَمَنْ يَكْتُمْهَا فَاِنَّهُٓ اٰثِمٌ قَلْبُهُۜ وَاللّٰهُ بِمَا تَعْمَلُونَ عَل۪يمٌ۟', 'Yolculukta olur da, yazacak kimse bulamazsanız (borca karşılık) alınmış bir rehin de yeterlidir. Birbirinize bir emanet bırakırsanız, emanet bırakılan kimse emaneti sahibine versin ve (bu hususta) Rabbi olan Allah´tan korksun. Şahitliği, bildiklerinizi gizlemeyin. Kim onu gizlerse, bilsin ki onun kalbi günahkârdır. Allah yapmakta olduklarınızı bilir.', 3, 48, 286, 25613, 'Sığır', 'Medine', 87),
(291, 284, 2, 'Bakara', 'لِلّٰهِ مَا فِي السَّمٰوَاتِ وَمَا فِي الْاَرْضِۜ وَاِنْ تُبْدُوا مَا ف۪ٓي اَنْفُسِكُمْ اَوْ تُخْفُوهُ يُحَاسِبْكُمْ بِهِ اللّٰهُۜ فَيَغْفِرُ لِمَنْ يَشَٓاءُ وَيُعَذِّبُ مَنْ يَشَٓاءُۜ وَاللّٰهُ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌ', 'Göklerde ve yerdekilerin hepsi Allah´ındır. İçinizdekileri açığa vursanız da gizleseniz de Allah ondan dolayı sizi hesaba çekecektir, sonra dilediğini affeder, dilediğine de azap eder. Allah her şeye kadirdir.', 3, 48, 286, 25613, 'Sığır', 'Medine', 87),
(292, 285, 2, 'Bakara', 'اٰمَنَ الرَّسُولُ بِمَٓا اُنْزِلَ اِلَيْهِ مِنْ رَبِّه۪ وَالْمُؤْمِنُونَۜ كُلٌّ اٰمَنَ بِاللّٰهِ وَمَلٰٓئِكَتِه۪ وَكُتُبِه۪ وَرُسُلِه۪ۜ لَا نُفَرِّقُ بَيْنَ اَحَدٍ مِنْ رُسُلِه۪۠ وَقَالُوا سَمِعْنَا وَاَطَعْنَا غُفْرَانَكَ رَبَّنَا وَاِلَيْكَ الْمَص۪يرُ', 'Peygamber, Rabbi tarafından kendisine indirilene iman etti, müminler de (iman ettiler). Her biri Allah´a, meleklerine, kitaplarına, peygamberlerine iman ettiler. «Allah´ın peygamberlerinden hiçbiri arasında ayırım yapmayız. İşittik, itaat ettik. Ey Rabbimiz, affına sığındık! Dönüş sanadır» dediler.', 3, 48, 286, 25613, 'Sığır', 'Medine', 87),
(293, 286, 2, 'Bakara', 'لَا يُكَلِّفُ اللّٰهُ نَفْساً اِلَّا وُسْعَهَاۜ لَهَا مَا كَسَبَتْ وَعَلَيْهَا مَا اكْتَسَبَتْۜ رَبَّنَا لَا تُؤَاخِذْنَٓا اِنْ نَس۪ينَٓا اَوْ اَخْطَأْنَاۚ رَبَّنَا وَلَا تَحْمِلْ عَلَيْنَٓا اِصْراً كَمَا حَمَلْتَهُ عَلَى الَّذ۪ينَ مِنْ قَبْلِنَاۚ رَبَّنَا وَلَا تُحَمِّلْنَا مَا لَا طَاقَةَ لَنَا بِه۪ۚ وَاعْفُ عَنَّا۠ وَاغْفِرْ لَنَا۠ وَارْحَمْنَا۠ اَنْتَ مَوْلٰينَا فَانْصُرْنَا عَلَى الْقَوْمِ الْكَافِر۪ينَ', 'Allah her şahsı, ancak gücünün yettiği ölçüde mükellef kılar. Herkesin kazandığı (hayır) kendine, yapacağı (şer) de kendinedir. Rabbimiz! Unutursak veya hataya düşersek bizi sorumlu tutma. Ey Rabbimiz! Bizden öncekilere yüklediğin gibi bize de ağır bir yük yükleme. Ey Rabbimiz! Bize gücümüzün yetmediği işler de yükleme! Bizi affet! Bizi bağışla! Bize acı! Sen bizim mevlâmızsın. Kâfirler topluluğuna karşı bize yardım et!', 3, 48, 286, 25613, 'Sığır', 'Medine', 87),
(294, 1, 3, 'Âl-i İmrân', 'الٓمٓۚ', 'Elif. Lâm. Mîm.', 3, 49, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(295, 2, 3, 'Âl-i İmrân', 'اَللّٰهُ لَٓا اِلٰهَ اِلَّا هُوَۙ الْحَيُّ الْقَيُّومُۜ', 'Hayy ve kayyûm olan Allah´tan başka ilâh yoktur.', 3, 49, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(296, 3, 3, 'Âl-i İmrân', 'نَزَّلَ عَلَيْكَ الْكِتَابَ بِالْحَقِّ مُصَدِّقاً لِمَا بَيْنَ يَدَيْهِ وَاَنْزَلَ التَّوْرٰيةَ وَالْاِنْج۪يلَۙ', '(3-4) (Resûlüm!) O, sana Kitab´ı hak ve önceki kitapları tasdik edici olarak tedricen indirmiş; daha önce de, insanlara doğru yolu göstermek üzere Tevrat ile İncil´i indirmişti. Furkan´ı da indirdi. Bilinmeli ki, Allah´ın âyetlerini inkâr edenler için şiddetli bir azap vardır. Allah, suçlunun hakkından gelen mutlak güç sahibidir.', 3, 49, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(297, 4, 3, 'Âl-i İmrân', 'مِنْ قَبْلُ هُدًى لِلنَّاسِ وَاَنْزَلَ الْفُرْقَانَۜ اِنَّ الَّذ۪ينَ كَفَرُوا بِاٰيَاتِ اللّٰهِ لَهُمْ عَذَابٌ شَد۪يدٌۜ وَاللّٰهُ عَز۪يزٌ ذُوانْتِقَامٍ', '(3-4) (Resûlüm!) O, sana Kitab´ı hak ve önceki kitapları tasdik edici olarak tedricen indirmiş; daha önce de, insanlara doğru yolu göstermek üzere Tevrat ile İncil´i indirmişti. Furkan´ı da indirdi. Bilinmeli ki, Allah´ın âyetlerini inkâr edenler için şiddetli bir azap vardır. Allah, suçlunun hakkından gelen mutlak güç sahibidir.', 3, 49, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(298, 5, 3, 'Âl-i İmrân', 'اِنَّ اللّٰهَ لَا يَخْفٰى عَلَيْهِ شَيْءٌ فِي الْاَرْضِ وَلَا فِي السَّمَٓاءِۜ', 'Şüphesiz ki ne yerde ne de gökte hiçbir şey Allah´a gizli kalmaz.', 3, 49, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(299, 6, 3, 'Âl-i İmrân', 'هُوَ الَّذ۪ي يُصَوِّرُكُمْ فِي الْاَرْحَامِ كَيْفَ يَشَٓاءُۜ لَٓا اِلٰهَ اِلَّا هُوَ الْعَز۪يزُ الْحَك۪يمُ', 'Rahimlerde sizi dilediği gibi şekillendiren O´dur. O´ndan başka ilâh yoktur. O mutlak güç ve hikmet sahibidir.', 3, 49, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(300, 7, 3, 'Âl-i İmrân', 'هُوَ الَّـذ۪ٓي اَنْزَلَ عَلَيْكَ الْكِتَابَ مِنْهُ اٰيَاتٌ مُحْكَمَاتٌ هُنَّ اُمُّ الْكِتَابِ وَاُخَرُ مُتَشَابِهَاتٌۜ فَاَمَّا الَّذ۪ينَ ف۪ي قُلُوبِهِمْ زَيْغٌ فَيَتَّبِعُونَ مَا تَشَابَهَ مِنْهُ ابْتِغَٓاءَ الْفِتْنَةِ وَابْتِغَٓاءَ تَأْو۪يلِه۪ۚ وَمَا يَعْلَمُ تَأْو۪يلَهُٓ اِلَّا اللّٰهُۢ وَالرَّاسِخُونَ فِي الْعِلْمِ يَقُولُونَ اٰمَنَّا بِه۪ۙ كُلٌّ مِنْ عِنْدِ رَبِّنَاۚ وَمَا يَذَّكَّرُ اِلَّٓا اُو۬لُوا الْاَلْبَابِ', 'Sana Kitab´ı indiren O´dur. Onun (Kur´an´ın) bazı âyetleri muhkemdir ki, bunlar Kitab´ın esasıdır. Diğerleri de müteşâbihtir. Kalplerinde eğrilik olanlar, fitne çıkarmak ve onu tevil etmek için ondaki müteşâbih âyetlerin peşine düşerler. Halbuki Onun tevilini ancak Allah bilir. İlimde yüksek pâyeye erişenler ise: Ona inandık; hepsi Rabbimiz tarafındandır, derler. (Bu inceliği) ancak aklıselim sahipleri düşünüp anlar.', 3, 49, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(301, 8, 3, 'Âl-i İmrân', 'رَبَّنَا لَا تُزِغْ قُلُوبَنَا بَعْدَ اِذْ هَدَيْتَنَا وَهَبْ لَنَا مِنْ لَدُنْكَ رَحْمَةًۚ اِنَّكَ اَنْتَ الْوَهَّابُ', '(Onlar şöyle yakarırlar:) Rabbimiz! Bizi doğru yola ilettikten sonra kalplerimizi eğriltme. Bize tarafından rahmet bağışla. Lütfu en bol olan sensin.', 3, 49, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(302, 9, 3, 'Âl-i İmrân', 'رَبَّنَٓا اِنَّكَ جَامِعُ النَّاسِ لِيَوْمٍ لَا رَيْبَ ف۪يهِۜ اِنَّ اللّٰهَ لَا يُخْلِفُ الْم۪يعَادَ۟', 'Rabbimiz! Gelmesinde şüphe edilmeyen bir günde, insanları mutlaka toplayacak olan sensin. Allah asla sözünden dönmez.', 3, 49, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(303, 10, 3, 'Âl-i İmrân', 'اِنَّ الَّذ۪ينَ كَفَرُوا لَنْ تُغْنِيَ عَنْهُمْ اَمْوَالُهُمْ وَلَٓا اَوْلَادُهُمْ مِنَ اللّٰهِ شَيْـٔاًۜ وَاُو۬لٰٓئِكَ هُمْ وَقُودُ النَّارِۙ', 'Bilinmelidir ki inkâr edenlerin ne malları ne de evlâtları Allah huzurunda kendilerine bir fayda sağlayacaktır. İşte onlar cehennemin yakıtıdır.', 3, 50, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(304, 11, 3, 'Âl-i İmrân', 'كَدَأْبِ اٰلِ فِرْعَوْنَۙ وَالَّذ۪ينَ مِنْ قَبْلِهِمْۜ كَذَّبُوا بِاٰيَاتِنَاۚ فَاَخَذَهُمُ اللّٰهُ بِذُنُوبِهِمْۜ وَاللّٰهُ شَد۪يدُ الْعِقَابِ', '(Onların yolu) Firavun hanedanının ve onlardan öncekilerin tuttuğu yola benzer. Onlar bizim âyetlerimizi yalanladılar, Allah da kendilerini günahları yüzünden yakalayıverdi. Allah´ın cezası çok şiddetlidir.', 3, 50, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(305, 12, 3, 'Âl-i İmrân', 'قُلْ لِلَّذ۪ينَ كَفَرُوا سَتُغْلَبُونَ وَتُحْشَرُونَ اِلٰى جَهَنَّمَۜ وَبِئْسَ الْمِهَادُ', '(Resûlüm!) İnkâr edenlere de ki: Yakında mağlup olacaksınız ve cehenneme sürüleceksiniz. Orası kalınacak ne kötü bir yerdir!', 3, 50, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(306, 13, 3, 'Âl-i İmrân', 'قَدْ كَانَ لَكُمْ اٰيَةٌ ف۪ي فِئَتَيْنِ الْتَقَتَاۜ فِئَةٌ تُقَاتِلُ ف۪ي سَب۪يلِ اللّٰهِ وَاُخْرٰى كَافِرَةٌ يَرَوْنَهُمْ مِثْلَيْهِمْ رَأْيَ الْعَيْنِۜ وَاللّٰهُ يُؤَيِّدُ بِنَصْرِه۪ مَنْ يَشَٓاءُۜ اِنَّ ف۪ي ذٰلِكَ لَعِبْرَةً لِاُو۬لِي الْاَبْصَارِ', '(Bedir´de) karşı karşıya gelen şu iki gurubun halinde sizin için büyük bir ibret vardır. Biri Allah yolunda çarpışan bir gurup, diğeri ise bunları apaçık kendilerinin iki misli gören kâfir bir gurup. Allah dilediğini yardımı ile destekler. Elbette bunda basiret sahipleri için büyük bir ibret vardır.', 3, 50, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(307, 14, 3, 'Âl-i İmrân', 'زُيِّنَ لِلنَّاسِ حُبُّ الشَّهَوَاتِ مِنَ النِّسَٓاءِ وَالْبَن۪ينَ وَالْقَنَاط۪يرِ الْمُقَنْطَرَةِ مِنَ الذَّهَبِ وَالْفِضَّةِ وَالْخَيْلِ الْمُسَوَّمَةِ وَالْاَنْعَامِ وَالْحَرْثِۜ ذٰلِكَ مَتَاعُ الْحَيٰوةِ الدُّنْيَاۚ وَاللّٰهُ عِنْدَهُ حُسْنُ الْمَاٰبِ', 'Nefsanî arzulara, (özellikle) kadınlara, oğullara, yığın yığın biriktirilmiş altın ve gümüşe, salma atlara, sağmal hayvanlara ve ekinlere karşı düşkünlük insanlara çekici kılındı. Bunlar, dünya hayatının geçici menfaatleridir. Halbuki varılacak güzel yer, Allah´ın katındadır.', 3, 50, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(308, 15, 3, 'Âl-i İmrân', 'قُلْ اَؤُ۬نَبِّئُكُمْ بِخَيْرٍ مِنْ ذٰلِكُمْۜ لِلَّذ۪ينَ اتَّقَوْا عِنْدَ رَبِّهِمْ جَنَّاتٌ تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُ خَالِد۪ينَ ف۪يهَا وَاَزْوَاجٌ مُطَهَّرَةٌ وَرِضْوَانٌ مِنَ اللّٰهِۜ وَاللّٰهُ بَص۪يرٌ بِالْعِبَادِۚ', '(Resûlüm!) De ki: Size bunlardan daha iyisini bildireyim mi? Takvâ sahipleri için Rableri yanında, içinden ırmaklar akan, ebediyyen kalacakları cennetler, tertemiz eşler ve (hepsinin üstünde) Allah´ın hoşnutluğu vardır. Allah kullarını çok iyi görür.', 3, 50, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(309, 16, 3, 'Âl-i İmrân', 'اَلَّذ۪ينَ يَقُولُونَ رَبَّنَٓا اِنَّـنَٓا اٰمَنَّا فَاغْفِرْ لَنَا ذُنُوبَنَا وَقِنَا عَذَابَ النَّارِۚ', '(Bu nimetler) «Ey Rabbimiz! İman ettik; bizim günahlarımızı bağışla, bizi ateş azabından koru!» diyen;', 3, 51, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(310, 17, 3, 'Âl-i İmrân', 'اَلصَّابِر۪ينَ وَالصَّادِق۪ينَ وَالْقَانِت۪ينَ وَالْمُنْفِق۪ينَ وَالْمُسْتَغْفِر۪ينَ بِالْاَسْحَارِ', 'Sabreden, dürüst olan, huzurda boyun büken, hayra harcayan ve seher vaktinde Allah´tan bağış dileyenler (içindir).', 3, 51, 200, 14605, 'İmrân ailesi', 'Medine', 89);
INSERT INTO `Ayetler` (`id`, `AyetNo`, `SureNo`, `SureAdi`, `Metin`, `Mana`, `Cuzu`, `Sayfasi`, `AyetSayisi`, `HarfSayisi`, `AdAnlami`, `IndigiYer`, `InisSirasi`) VALUES
(311, 18, 3, 'Âl-i İmrân', 'شَهِدَ اللّٰهُ اَنَّهُ لَٓا اِلٰهَ اِلَّا هُوَۙ وَالْمَلٰٓئِكَةُ وَاُو۬لُوا الْعِلْمِ قَٓائِماً بِالْقِسْطِۜ لَٓا اِلٰهَ اِلَّا هُوَ الْعَز۪يزُ الْحَك۪يمُۜ', 'Allah, adaleti ayakta tutarak (delilleriyle) şu hususu açıklamıştır ki, kendisinden başka ilâh yoktur. Melekler ve ilim sahipleri de (bunu ikrar etmişlerdir. Evet) mutlak güç ve hikmet sahibi Allah´tan başka ilâh yoktur.', 3, 51, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(312, 19, 3, 'Âl-i İmrân', 'اِنَّ الدّ۪ينَ عِنْدَ اللّٰهِ الْاِسْلَامُ۠ وَمَا اخْتَلَفَ الَّذ۪ينَ اُو۫تُوا الْكِتَابَ اِلَّا مِنْ بَعْدِ مَا جَٓاءَهُمُ الْعِلْمُ بَغْياً بَيْنَهُمْۜ وَمَنْ يَكْفُرْ بِاٰيَاتِ اللّٰهِ فَاِنَّ اللّٰهَ سَر۪يعُ الْحِسَابِ', 'Allah nezdinde hak din İslâm´dır. Kitap verilenler, kendilerine ilim geldikten sonradır ki, aralarındaki kıskançlık yüzünden ayrılığa düştüler. Allah´ın âyetlerini inkâr edenler bilmelidirler ki Allah´ın hesabı çok çabuktur.', 3, 51, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(313, 20, 3, 'Âl-i İmrân', 'فَاِنْ حَٓاجُّوكَ فَقُلْ اَسْلَمْتُ وَجْهِيَ لِلّٰهِ وَمَنِ اتَّـبَعَنِۜ وَقُلْ لِلَّذ۪ينَ اُو۫تُوا الْكِتَابَ وَالْاُمِّيّ۪نَ ءَاَسْلَمْتُمْۜ فَاِنْ اَسْلَمُوا فَقَدِ اهْتَدَوْاۚ وَاِنْ تَوَلَّوْا فَاِنَّمَا عَلَيْكَ الْبَلَاغُۜ وَاللّٰهُ بَص۪يرٌ بِالْعِبَادِ۟', 'Eğer seninle tartışmaya girerlerse de ki: «Bana uyanlarla birlikte ben kendimi Allah´a teslim ettim.» Ehl-i kitaba ve ümmîlere de: «Siz de Allah´a teslim oldunuz mu?» de. Eğer teslim oldularsa doğru yolu buldular demektir. Yok eğer yüz çevirdilerse sana düşen, yalnızca duyurmaktır. Allah kullarını çok iyi görmektedir.', 3, 51, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(314, 21, 3, 'Âl-i İmrân', 'اِنَّ الَّذ۪ينَ يَكْفُرُونَ بِاٰيَاتِ اللّٰهِ وَيَقْتُلُونَ النَّبِيّ۪نَ بِغَيْرِ حَقٍّۙ وَيَقْتُلُونَ الَّذ۪ينَ يَأْمُرُونَ بِالْقِسْطِ مِنَ النَّاسِۙ فَبَشِّرْهُمْ بِعَذَابٍ اَل۪يمٍ', 'Allah´ın âyetlerini inkâr edenler, haksız yere peygamberlerin canlarına kıyanlar ve adaleti emreden insanları öldürenler (yok mu), onlara acı bir azabı müjdele!', 3, 51, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(315, 22, 3, 'Âl-i İmrân', 'اُو۬لٰٓئِكَ الَّذ۪ينَ حَبِطَتْ اَعْمَالُهُمْ فِي الدُّنْيَا وَالْاٰخِرَةِۘ وَمَا لَهُمْ مِنْ نَاصِر۪ينَ', 'İşte bunlar dünyada da ahirette de çabaları boşa giden kimselerdir. Onların hiçbir yardımcısı da yoktur.', 3, 51, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(316, 23, 3, 'Âl-i İmrân', 'اَلَمْ تَرَ اِلَى الَّذ۪ينَ اُو۫تُوا نَص۪يباً مِنَ الْكِتَابِ يُدْعَوْنَ اِلٰى كِتَابِ اللّٰهِ لِيَحْكُمَ بَيْنَهُمْ ثُمَّ يَتَوَلّٰى فَر۪يقٌ مِنْهُمْ وَهُمْ مُعْرِضُونَ', '(Resûlüm!) Kendilerine Kitap´tan bir pay verilenleri (yahudileri) görmez misin ki, aralarında hükmetmesi için Allah´ın Kitab´ına çağırılıyorlar da, sonra içlerinden bir gurup cayarak geri dönüyor.', 3, 52, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(317, 24, 3, 'Âl-i İmrân', 'ذٰلِكَ بِاَنَّهُمْ قَالُوا لَنْ تَمَسَّنَا النَّارُ اِلَّٓا اَيَّاماً مَعْدُودَاتٍۖ وَغَرَّهُمْ ف۪ي د۪ينِهِمْ مَا كَانُوا يَفْتَرُونَ', 'Onların bu tutumları: Bize ateş, sadece sayılı günlerde dokunacaktır, demelerinin bir sonucudur. Onların vaktiyle uydurdukları şeyler de dinleri hakkında kendilerini yanıltmıştır.', 3, 52, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(318, 25, 3, 'Âl-i İmrân', 'فَكَيْفَ اِذَا جَمَعْنَاهُمْ لِيَوْمٍ لَا رَيْبَ ف۪يهِ وَوُفِّيَتْ كُلُّ نَفْسٍ مَا كَسَبَتْ وَهُمْ لَا يُظْلَمُونَ', 'Fakat, onları gelmesinde şüphe edilmeyen bir gün için topladığımız ve hiçbir haksızlığa uğramaksızın herkese kazandığı şeyler tastamam ödendiği zaman halleri nice olur?', 3, 52, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(319, 26, 3, 'Âl-i İmrân', 'قُلِ اللّٰهُمَّ مَالِكَ الْمُلْكِ تُؤْتِي الْمُلْكَ مَنْ تَشَٓاءُ وَتَنْزِعُ الْمُلْكَ مِمَّنْ تَشَٓاءُۘ وَتُعِزُّ مَنْ تَشَٓاءُ وَتُذِلُّ مَنْ تَشَٓاءُۜ بِيَدِكَ الْخَيْرُۜ اِنَّكَ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌ', '(Resûlüm!) De ki: Mülkün gerçek sahibi olan Allah´ım! Sen mülkü dilediğine verirsin ve mülkü dilediğinden geri alırsın. Dilediğini yüceltir, dilediğini de alçaltırsın. Her türlü iyilik senin elindedir. Gerçekten sen her şeye kadirsin.', 3, 52, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(320, 27, 3, 'Âl-i İmrân', 'تُولِجُ الَّيْلَ فِي النَّهَارِ وَتُولِجُ النَّهَارَ فِي الَّيْلِۘ وَتُخْرِجُ الْحَيَّ مِنَ الْمَيِّتِ وَتُخْرِجُ الْمَيِّتَ مِنَ الْحَيِّۘ وَتَرْزُقُ مَنْ تَشَٓاءُ بِغَيْرِ حِسَابٍ', 'Geceyi gündüze katar, gündüzü de geceye katarsın. Ölüden diriyi çıkarır, diriden de ölüyü çıkarırsın. Dilediğine de sayısız rızık verirsin.', 3, 52, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(321, 28, 3, 'Âl-i İmrân', 'لَا يَتَّخِذِ الْمُؤْمِنُونَ الْكَافِر۪ينَ اَوْلِيَٓاءَ مِنْ دُونِ الْمُؤْمِن۪ينَۚ وَمَنْ يَفْعَلْ ذٰلِكَ فَلَيْسَ مِنَ اللّٰهِ ف۪ي شَيْءٍ اِلَّٓا اَنْ تَتَّقُوا مِنْهُمْ تُقٰيةًۜ وَيُحَذِّرُكُمُ اللّٰهُ نَفْسَهُۜ وَاِلَى اللّٰهِ الْمَص۪يرُ', 'Müminler, müminleri bırakıp da kâfirleri dost edinmesin. Kim bunu yaparsa, artık onun Allah nezdinde hiçbir değeri yoktur. Ancak kâfirlerden gelebilecek bir tehlikeden sakınmanız başkadır. Allah, kendisine karşı (gelmekten) sizi sakındırıyor. Dönüş yalnız Allah´adır.', 3, 52, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(322, 29, 3, 'Âl-i İmrân', 'قُلْ اِنْ تُخْفُوا مَا ف۪ي صُدُورِكُمْ اَوْ تُبْدُوهُ يَعْلَمْهُ اللّٰهُۜ وَيَعْلَمُ مَا فِي السَّمٰوَاتِ وَمَا فِي الْاَرْضِۜ وَاللّٰهُ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌ', 'De ki: İçinizdekileri gizleseniz de açığa vursanız da Allah onu bilir. Göklerde ve yerde olanları da bilir. Allah her şeye kadirdir.', 3, 52, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(323, 30, 3, 'Âl-i İmrân', 'يَوْمَ تَجِدُ كُلُّ نَفْسٍ مَا عَمِلَتْ مِنْ خَيْرٍ مُحْضَراًۚۛ وَمَا عَمِلَتْ مِنْ سُٓوءٍۚۛ تَوَدُّ لَوْ اَنَّ بَيْنَهَا وَبَيْنَهُٓ اَمَداً بَع۪يداًۜ وَيُحَذِّرُكُمُ اللّٰهُ نَفْسَهُۜ وَاللّٰهُ رَؤُ۫فٌ بِالْعِبَادِ۟', 'Herkesin, iyilik olarak yaptıklarını da kötülük olarak yaptıklarını da karşısında hazır bulduğu günde (insan) isteyecek ki kötülükleri ile kendisi arasında uzun bir mesafe bulunsun. Allah, kendisine karşı (gelmekten) sizi sakındırıyor. Allah kullarına çok şefkatlidir.', 3, 53, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(324, 31, 3, 'Âl-i İmrân', 'قُلْ اِنْ كُنْتُمْ تُحِبُّونَ اللّٰهَ فَاتَّبِعُون۪ي يُحْبِبْكُمُ اللّٰهُ وَيَغْفِرْ لَكُمْ ذُنُوبَكُمْۜ وَاللّٰهُ غَفُورٌ رَح۪يمٌ', '(Resûlüm!) De ki: Eğer Allah´ı seviyorsanız bana uyunuz ki Allah da sizi sevsin ve günahlarınızı bağışlasın. Allah son derece bağışlayıcı ve esirgeyicidir.', 3, 53, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(325, 32, 3, 'Âl-i İmrân', 'قُلْ اَط۪يعُوا اللّٰهَ وَالرَّسُولَۚ فَاِنْ تَوَلَّوْا فَاِنَّ اللّٰهَ لَا يُحِبُّ الْكَافِر۪ينَ', 'De ki: Allah´a ve Resûlü´ne itaat edin. Eğer yüz çevirirlerse bilsinler ki Allah kâfirleri sevmez.', 3, 53, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(326, 33, 3, 'Âl-i İmrân', 'اِنَّ اللّٰهَ اصْطَفٰٓى اٰدَمَ وَنُوحاً وَاٰلَ اِبْرٰه۪يمَ وَاٰلَ عِمْرٰنَ عَلَى الْعَالَم۪ينَۙ', '(33-34) Allah birbirinden gelme bir nesil olarak Âdem´i, Nuh´u, İbrahim ailesi ile İmrân ailesini seçip âlemlere üstün kıldı. Allah işiten ve bilendir.', 3, 53, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(327, 34, 3, 'Âl-i İmrân', 'ذُرِّيَّةً بَعْضُهَا مِنْ بَعْضٍۜ وَاللّٰهُ سَم۪يعٌ عَل۪يمٌۚ', '(33-34) Allah birbirinden gelme bir nesil olarak Âdem´i, Nuh´u, İbrahim ailesi ile İmrân ailesini seçip âlemlere üstün kıldı. Allah işiten ve bilendir.', 3, 53, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(328, 35, 3, 'Âl-i İmrân', 'اِذْ قَالَتِ امْرَاَتُ عِمْرٰنَ رَبِّ اِنّ۪ي نَذَرْتُ لَكَ مَا ف۪ي بَطْن۪ي مُحَرَّراً فَتَقَبَّلْ مِنّ۪يۚ اِنَّكَ اَنْتَ السَّم۪يعُ الْعَل۪يمُ', 'İmrân´ın karısı şöyle demişti: «Rabbim! Karnımdakini azatlı bir kul olarak sırf sana adadım. Adağımı kabul buyur. Şüphesiz (niyazımı) hakkıyla işiten ve (niyetimi) bilen sensin.»', 3, 53, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(329, 36, 3, 'Âl-i İmrân', 'فَلَمَّا وَضَعَتْهَا قَالَتْ رَبِّ اِنّ۪ي وَضَعْتُهَٓا اُنْثٰىۜ وَاللّٰهُ اَعْلَمُ بِمَا وَضَعَتْۜ وَلَيْسَ الذَّكَرُ كَالْاُنْثٰىۚ وَاِنّ۪ي سَمَّيْتُهَا مَرْيَمَ وَاِنّ۪ٓي اُع۪يذُهَا بِكَ وَذُرِّيَّتَهَا مِنَ الشَّيْطَانِ الرَّج۪يمِ', 'Onu doğurunca, Allah, ne doğurduğunu bilip dururken: Rabbim! Ben onu kız doğurdum. Oysa erkek, kız gibi değildir. Ona Meryem adını verdim. Kovulmuş şeytana karşı onu ve soyunu senin korumanı diliyorum, dedi.', 3, 53, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(330, 37, 3, 'Âl-i İmrân', 'فَتَقَبَّلَهَا رَبُّهَا بِقَبُولٍ حَسَنٍ وَاَنْبَتَهَا نَبَاتاً حَسَناًۙ وَكَفَّلَهَا زَكَرِيَّاۜ كُلَّمَا دَخَلَ عَلَيْهَا زَكَرِيَّا الْمِحْرَابَۙ وَجَدَ عِنْدَهَا رِزْقاًۚ قَالَ يَا مَرْيَمُ اَنّٰى لَكِ هٰذَاۜ قَالَتْ هُوَ مِنْ عِنْدِ اللّٰهِۜ اِنَّ اللّٰهَ يَرْزُقُ مَنْ يَشَٓاءُ بِغَيْرِ حِسَابٍ', 'Rabbi Meryem´e hüsnü kabul gösterdi; onu güzel bir bitki gibi yetiştirdi. Zekeriyya´yı da onun bakımı ile görevlendirdi. Zekeriyya, onun yanına, mâbede her girişinde orada bir rızık bulur ve «Ey Meryem, bu sana nereden geliyor?» der; o da: Bu, Allah tarafındandır. Allah, dilediğine sayısız rızık verir, derdi.', 3, 53, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(331, 38, 3, 'Âl-i İmrân', 'هُنَالِكَ دَعَا زَكَرِيَّا رَبَّهُۚ قَالَ رَبِّ هَبْ ل۪ي مِنْ لَدُنْكَ ذُرِّيَّةً طَيِّبَةًۚ اِنَّكَ سَم۪يعُ الدُّعَٓاءِ', 'Orada Zekeriyya, Rabbine dua etti: Rabbim! Bana tarafından hayırlı bir nesil bağışla. Şüphesiz sen duayı hakkıyla işitensin, dedi.', 3, 54, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(332, 39, 3, 'Âl-i İmrân', 'فَنَادَتْهُ الْمَلٰٓئِكَةُ وَهُوَ قَٓائِمٌ يُصَلّ۪ي فِي الْمِحْرَابِۙ اَنَّ اللّٰهَ يُبَشِّرُكَ بِيَحْيٰى مُصَدِّقاً بِكَلِمَةٍ مِنَ اللّٰهِ وَسَيِّداً وَحَصُوراً وَنَبِياًّ مِنَ الصَّالِح۪ينَ', 'Zekeriyya mâbedde durmuş namaz kılarken melekler ona şöyle nida ettiler: Allah sana, kendisi tarafından gelen bir Kelime´yi tasdik edici, efendi, iffetli ve sâlihlerden bir peygamber olarak Yahya´yı müjdeler.', 3, 54, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(333, 40, 3, 'Âl-i İmrân', 'قَالَ رَبِّ اَنّٰى يَكُونُ ل۪ي غُلَامٌ وَقَدْ بَلَغَنِيَ الْكِبَرُ وَامْرَاَت۪ي عَاقِرٌۜ قَالَ كَذٰلِكَ اللّٰهُ يَفْعَلُ مَا يَشَٓاءُ', 'Zekeriyya: Rabbim! dedi, bana ihtiyarlık gelip çattığına, üstelik karım da kısır olduğuna göre benim nasıl oğlum olabilir? Allah şöyle buyurdu: İşte böyledir; Allah dilediğini yapar.', 3, 54, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(334, 41, 3, 'Âl-i İmrân', 'قَالَ رَبِّ اجْعَلْ ل۪ٓي اٰيَةًۜ قَالَ اٰيَتُكَ اَلَّا تُكَلِّمَ النَّاسَ ثَلٰثَةَ اَيَّامٍ اِلَّا رَمْزاًۜ وَاذْكُرْ رَبَّكَ كَث۪يراً وَسَبِّـحْ بِالْعَشِيِّ وَالْاِبْكَارِ۟', 'Zekeriyya: Rabbim! (Oğlum olacağına dair) bana bir alâmet göster, dedi. Allah buyurdu ki: Senin için alâmet, insanlara, üç gün, işaretten başka söz söylememendir. Ayrıca Rabbini çok an, sabah akşam tesbih et.', 3, 54, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(335, 42, 3, 'Âl-i İmrân', 'وَاِذْ قَالَتِ الْمَلٰٓئِكَةُ يَا مَرْيَمُ اِنَّ اللّٰهَ اصْطَفٰيكِ وَطَهَّرَكِ وَاصْطَفٰيكِ عَلٰى نِسَٓاءِ الْعَالَم۪ينَ', 'Hani melekler demişlerdi: Ey Meryem! Allah seni seçti; seni tertemiz yarattı ve seni bütün dünya kadınlarına tercih etti.', 3, 54, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(336, 43, 3, 'Âl-i İmrân', 'يَا مَرْيَمُ اقْنُت۪ي لِرَبِّكِ وَاسْجُد۪ي وَارْكَع۪ي مَعَ الرَّاكِع۪ينَ', 'Ey Meryem! Rabbine ibadet et; secdeye kapan, (O´nun huzurunda) eğilenlerle beraber sen de eğil.', 3, 54, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(337, 44, 3, 'Âl-i İmrân', 'ذٰلِكَ مِنْ اَنْـبَٓاءِ الْغَيْبِ نُوح۪يهِ اِلَيْكَۜ وَمَا كُنْتَ لَدَيْهِمْ اِذْ يُلْقُونَ اَقْلَامَهُمْ اَيُّهُمْ يَكْفُلُ مَرْيَمَۖ وَمَا كُنْتَ لَدَيْهِمْ اِذْ يَخْتَصِمُونَ', '(Resûlüm!) Bunlar, bizim sana vahiy yoluyla bildirmekte olduğumuz gayb haberlerindendir. İçlerinden hangisi Meryem´i himayesine alacak diye kur´a çekmek üzere kalemlerini atarlarken sen onların yanında değildin; onlar (bu yüzden) çekişirken de yanlarında değildin.', 3, 54, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(338, 45, 3, 'Âl-i İmrân', 'اِذْ قَالَتِ الْمَلٰٓئِكَةُ يَا مَرْيَمُ اِنَّ اللّٰهَ يُبَشِّرُكِ بِكَلِمَةٍ مِنْهُۗ اِسْمُهُ الْمَس۪يحُ ع۪يسَى ابْنُ مَرْيَمَ وَج۪يهاً فِي الدُّنْيَا وَالْاٰخِرَةِ وَمِنَ الْمُقَرَّب۪ينَۙ', 'Melekler demişlerdi ki: Ey Meryem! Allah sana kendisinden bir Kelime´yi müjdeliyor. Adı Meryem oğlu İsa´dır. Mesîh´tir; dünyada da, ahirette de itibarlı ve Allah´ın kendisine yakın kıldıklarındandır.', 3, 54, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(339, 46, 3, 'Âl-i İmrân', 'وَيُكَلِّمُ النَّاسَ فِي الْمَهْدِ وَكَهْلاً وَمِنَ الصَّالِح۪ينَ', 'O, sâlihlerden olarak beşikte iken ve yetişkinlik halinde insanlara (peygamber sözleri ile) konuşacak.', 3, 55, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(340, 47, 3, 'Âl-i İmrân', 'قَالَتْ رَبِّ اَنّٰى يَكُونُ ل۪ي وَلَدٌ وَلَمْ يَمْسَسْن۪ي بَشَرٌۜ قَالَ كَذٰلِكِ اللّٰهُ يَخْلُقُ مَا يَشَٓاءُۜ اِذَا قَضٰٓى اَمْراً فَاِنَّمَا يَقُولُ لَهُ كُنْ فَيَكُونُ', 'Meryem: Rabbim! dedi, bana bir erkek eli değmediği halde nasıl çocuğum olur? Allah şöyle buyurdu: İşte böyledir, Allah dilediğini yaratır. Bir işe hükmedince ona sadece «Ol!» der; o da oluverir.', 3, 55, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(341, 48, 3, 'Âl-i İmrân', 'وَيُعَلِّمُهُ الْكِتَابَ وَالْحِكْمَةَ وَالتَّوْرٰيةَ وَالْاِنْج۪يلَۚ', '(Melekler, Meryem´e hitaben İsa hakkında sözlerine devam ettiler:) Allah ona yazmayı, hikmeti, Tevrat´ı, İncil´i öğretecek.', 3, 55, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(342, 49, 3, 'Âl-i İmrân', 'وَرَسُولاً اِلٰى بَن۪ٓي اِسْرَٓائ۪لَ اَنّ۪ي قَدْ جِئْتُكُمْ بِاٰيَةٍ مِنْ رَبِّكُمْۙ اَنّ۪ٓي اَخْلُقُ لَكُمْ مِنَ الطّ۪ينِ كَهَيْـَٔةِ الطَّيْرِ فَاَنْفُخُ ف۪يهِ فَيَكُونُ طَيْراً بِاِذْنِ اللّٰهِۚ وَاُبْرِئُ الْاَكْمَهَ وَالْاَبْرَصَ وَاُحْـيِ الْمَوْتٰى بِاِذْنِ اللّٰهِۚ وَاُنَبِّئُكُمْ بِمَا تَأْكُلُونَ وَمَا تَدَّخِرُونَۙ ف۪ي بُيُوتِكُمْۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَةً لَكُمْ اِنْ كُنْتُمْ مُؤْمِن۪ينَۚ', 'O, İsrailoğullarına bir elçi olacak (ve onlara şöyle diyecek:) Size Rabbinizden bir mucize getirdim: Size çamurdan bir kuş sureti yapar, ona üflerim ve Allah´ın izni ile o kuş oluverir. Yine Allah´ın izni ile körü ve alacalıyı iyileştirir, ölüleri diriltirim. Ayrıca evlerinizde ne yeyip ne biriktirdiğinizi size haber veririm. Eğer inanan kimseler iseniz, bunda sizin için bir ibret vardır.', 3, 55, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(343, 50, 3, 'Âl-i İmrân', 'وَمُصَدِّقاً لِمَا بَيْنَ يَدَيَّ مِنَ التَّوْرٰيةِ وَلِاُحِلَّ لَكُمْ بَعْضَ الَّذ۪ي حُرِّمَ عَلَيْكُمْ وَجِئْتُكُمْ بِاٰيَةٍ مِنْ رَبِّكُمْ فَاتَّقُوا اللّٰهَ وَاَط۪يعُونِ', 'Benden önce gelen Tevrat´ı doğrulayıcı olarak ve size haram kılınan bazı şeyleri de helâl kılmam için gönderildim. Size Rabbinizden bir mucize getirdim. O halde Allah´tan korkun, bana da itaat edin.', 3, 55, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(344, 51, 3, 'Âl-i İmrân', 'اِنَّ اللّٰهَ رَبّ۪ي وَرَبُّكُمْ فَاعْبُدُوهُۜ هٰذَا صِرَاطٌ مُسْتَق۪يمٌ', 'Allah, benim de Rabbim, sizin de Rabbinizdir. Öyle ise O´na kulluk edin. İşte bu doğru yoldur.', 3, 55, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(345, 52, 3, 'Âl-i İmrân', 'فَلَمَّٓا اَحَسَّ ع۪يسٰى مِنْهُمُ الْكُفْرَ قَالَ مَنْ اَنْصَار۪ٓي اِلَى اللّٰهِۜ قَالَ الْحَوَارِيُّونَ نَحْنُ اَنْصَارُ اللّٰهِۚ اٰمَنَّا بِاللّٰهِۚ وَاشْهَدْ بِاَنَّا مُسْلِمُونَ', 'İsa, onlardaki inkârcılığı sezince: Allah yolunda bana yardımcı olacaklar kimlerdir? dedi. Havârîler: Biz, Allah yolunun yardımcılarıyız; Allah´a inandık, şahit ol ki bizler müslümanlarız, cevabını verdiler.', 3, 55, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(346, 53, 3, 'Âl-i İmrân', 'رَبَّنَٓا اٰمَنَّا بِمَٓا اَنْزَلْتَ وَاتَّبَعْنَا الرَّسُولَ فَاكْتُبْنَا مَعَ الشَّاهِد۪ينَ', '(Havârîler:) Rabbimiz! İndirdiğine inandık ve Peygamber´e uyduk. Şimdi bizi (birliğini ve peygamberlerini tasdik eden) şahitlerden yaz, dediler.', 3, 56, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(347, 54, 3, 'Âl-i İmrân', 'وَمَكَرُوا وَمَكَرَ اللّٰهُۜ وَاللّٰهُ خَيْرُ الْمَاكِر۪ينَ۟', '(Yahudiler) tuzak kurdular; Allah da onların tuzaklarını bozdu. Allah, tuzak kuranların hayırlısıdır.', 3, 56, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(348, 55, 3, 'Âl-i İmrân', 'اِذْ قَالَ اللّٰهُ يَا ع۪يسٰٓى اِنّ۪ي مُتَوَفّ۪يكَ وَرَافِعُكَ اِلَيَّ وَمُطَهِّرُكَ مِنَ الَّذ۪ينَ كَفَرُوا وَجَاعِلُ الَّذ۪ينَ اتَّبَعُوكَ فَوْقَ الَّذ۪ينَ كَفَرُٓوا اِلٰى يَوْمِ الْقِيٰمَةِۚ ثُمَّ اِلَيَّ مَرْجِعُكُمْ فَاَحْكُمُ بَيْنَكُمْ ف۪يمَا كُنْتُمْ ف۪يهِ تَخْتَلِفُونَ', 'Allah buyurmuştu ki: Ey İsa! Seni vefat ettireceğim, seni nezdime yükselteceğim, seni inkâr edenlerden arındıracağım ve sana uyanları kıyamete kadar kâfirlerden üstün kılacağım. Sonra dönüşünüz bana olacak. İşte o zaman ayrılığa düştüğünüz şeyler hakkında aranızda ben hükmedeceğim.', 3, 56, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(349, 56, 3, 'Âl-i İmrân', 'فَاَمَّا الَّذ۪ينَ كَفَرُوا فَاُعَذِّبُهُمْ عَذَاباً شَد۪يداً فِي الدُّنْيَا وَالْاٰخِرَةِۘ وَمَا لَهُمْ مِنْ نَاصِر۪ينَ', 'İnkâr edenler var ya, onları dünya ve ahirette şiddetli bir azaba çarptıracağım; onların hiç yardımcıları da olmayacak.', 3, 56, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(350, 57, 3, 'Âl-i İmrân', 'وَاَمَّا الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ فَيُوَفّ۪يهِمْ اُجُورَهُمْۜ وَاللّٰهُ لَا يُحِبُّ الظَّالِم۪ينَ', 'İman edip iyi davranışlarda bulunanlara gelince, Allah onların mükâfatlarını eksiksiz verecektir. Allah zalimleri sevmez.', 3, 56, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(351, 58, 3, 'Âl-i İmrân', 'ذٰلِكَ نَتْلُوهُ عَلَيْكَ مِنَ الْاٰيَاتِ وَالذِّكْرِ الْحَك۪يمِ', '(Resûlüm!) Bu söylenenleri biz sana âyetlerden ve hikmet dolu Kur´an´dan okuyoruz.', 3, 56, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(352, 59, 3, 'Âl-i İmrân', 'اِنَّ مَثَلَ ع۪يسٰى عِنْدَ اللّٰهِ كَمَثَلِ اٰدَمَۜ خَلَقَهُ مِنْ تُرَابٍ ثُمَّ قَالَ لَهُ كُنْ فَيَكُونُ', 'Allah nezdinde İsa´nın durumu, Âdem´in durumu gibidir. Allah onu topraktan yarattı. Sonra ona «Ol!» dedi ve oluverdi.', 3, 56, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(353, 60, 3, 'Âl-i İmrân', 'اَلْحَقُّ مِنْ رَبِّكَ فَلَا تَكُنْ مِنَ الْمُمْتَر۪ينَ', 'Gerçek, Rabbinden gelendir. Öyle ise şüphecilerden olma.', 3, 56, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(354, 61, 3, 'Âl-i İmrân', 'فَمَنْ حَٓاجَّكَ ف۪يهِ مِنْ بَعْدِ مَا جَٓاءَكَ مِنَ الْعِلْمِ فَقُلْ تَعَالَوْا نَدْعُ اَبْنَٓاءَنَا وَاَبْنَٓاءَكُمْ وَنِسَٓاءَنَا وَنِسَٓاءَكُمْ وَاَنْفُسَنَا وَاَنْفُسَكُمْ ثُمَّ نَبْتَهِلْ فَنَجْعَلْ لَعْنَتَ اللّٰهِ عَلَى الْكَاذِب۪ينَ', 'Sana bu ilim geldikten sonra seninle bu konuda çekişenlere de ki: Geliniz, sizler ve bizler de dahil olmak üzere, siz kendi çocuklarınızı biz de kendi çocuklarımızı, siz kendi kadınlarınızı, biz de kendi kadınlarımızı çağıralım, sonra da dua edelim de Allah´tan yalancılar üzerine lânet dileyelim.', 3, 56, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(355, 62, 3, 'Âl-i İmrân', 'اِنَّ هٰذَا لَهُوَ الْقَصَصُ الْحَقُّۚ وَمَا مِنْ اِلٰهٍ اِلَّا اللّٰهُۜ وَاِنَّ اللّٰهَ لَهُوَ الْعَز۪يزُ الْحَك۪يمُ', 'Şüphesiz bu (İsa hakkında söylenenler), doğru haberlerdir. Allah´tan başka ilâh yoktur. Muhakkak ki Allah, evet O, mutlak güç ve hikmet sahibidir.', 3, 57, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(356, 63, 3, 'Âl-i İmrân', 'فَاِنْ تَوَلَّوْا فَاِنَّ اللّٰهَ عَل۪يمٌ بِالْمُفْسِد۪ينَ۟', 'Eğer yine yüz çevirirlerse, şüphesiz Allah, bozguncuları hakkıyla bilendir.', 3, 57, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(357, 64, 3, 'Âl-i İmrân', 'قُلْ يَٓا اَهْلَ الْكِتَابِ تَعَالَوْا اِلٰى كَلِمَةٍ سَوَٓاءٍ بَيْنَنَا وَبَيْنَكُمْ اَلَّا نَعْبُدَ اِلَّا اللّٰهَ وَلَا نُشْرِكَ بِه۪ شَيْـٔاً وَلَا يَتَّخِذَ بَعْضُنَا بَعْضاً اَرْبَاباً مِنْ دُونِ اللّٰهِۜ فَاِنْ تَوَلَّوْا فَقُولُوا اشْهَدُوا بِاَنَّا مُسْلِمُونَ', '(Resûlüm!) de ki: Ey ehl-i kitap! Sizinle bizim aramızda müşterek olan bir söze geliniz: Allah´tan başkasına tapmayalım; O´na hiçbir şeyi eş tutmayalım ve Allah´ı bırakıp da kimimiz kimimizi ilâhlaştırmasın. Eğer onlar yine yüz çevirirlerse, işte o zaman: Şahit olun ki biz müslümanlarız! deyiniz.', 3, 57, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(358, 65, 3, 'Âl-i İmrân', 'يَٓا اَهْلَ الْكِتَابِ لِمَ تُحَٓاجُّونَ ف۪ٓي اِبْرٰه۪يمَ وَمَٓا اُنْزِلَتِ التَّوْرٰيةُ وَالْاِنْج۪يلُ اِلَّا مِنْ بَعْدِه۪ۜ اَفَلَا تَعْقِلُونَ', 'Ey ehl-i kitap! İbrahim hakkında niçin çekişirsiniz? Halbuki Tevrat ve İncil, kesinlikle ondan sonra indirildi. Siz hiç düşünmez misiniz?', 3, 57, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(359, 66, 3, 'Âl-i İmrân', 'هَٓا اَنْتُمْ هٰٓؤُ۬لَٓاءِ حَاجَجْتُمْ ف۪يمَا لَكُمْ بِه۪ عِلْمٌ فَلِمَ تُحَٓاجُّونَ ف۪يمَا لَيْسَ لَكُمْ بِه۪ عِلْمٌۜ وَاللّٰهُ يَعْلَمُ وَاَنْتُمْ لَا تَعْلَمُونَ', 'İşte siz böyle kimselersiniz! Hadi hakkında bilgi sahibi olduğunuz konuda tartıştınız; fakat bilgi sahibi olmadığınız konuda niçin tartışıyorsunuz! Oysa ki Allah, her şeyi bilir, siz ise bilmezsiniz.', 3, 57, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(360, 67, 3, 'Âl-i İmrân', 'مَا كَانَ اِبْرٰه۪يمُ يَهُودِياًّ وَلَا نَصْرَانِياًّ وَلٰكِنْ كَانَ حَن۪يفاً مُسْلِماًۜ وَمَا كَانَ مِنَ الْمُشْرِك۪ينَ', 'İbrahim, ne yahudi, ne de hıristiyan idi; fakat o, Allah´ı bir tanıyan dosdoğru bir müslüman idi; müşriklerden de değildi.', 3, 57, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(361, 68, 3, 'Âl-i İmrân', 'اِنَّ اَوْلَى النَّاسِ بِاِبْرٰه۪يمَ لَلَّذ۪ينَ اتَّبَعُوهُ وَهٰذَا النَّبِيُّ وَالَّذ۪ينَ اٰمَنُواۜ وَاللّٰهُ وَلِيُّ الْمُؤْمِن۪ينَ', 'İnsanların İbrahim´e en yakın olanı, ona uyanlar, şu Peygamber (Muhammed) ve (ona) iman edenlerdir. Allah müminlerin dostudur.', 3, 57, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(362, 69, 3, 'Âl-i İmrân', 'وَدَّتْ طَٓائِفَةٌ مِنْ اَهْلِ الْكِتَابِ لَوْ يُضِلُّونَكُمْۜ وَمَا يُضِلُّونَ اِلَّٓا اَنْفُسَهُمْ وَمَا يَشْعُرُونَ', 'Ehl-i kitaptan bir kısmı istediler ki, ne yapıp edip sizi saptırabilsinler. Oysa onlar sadece kendilerini saptırırlar da farkına bile varmazlar.', 3, 57, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(363, 70, 3, 'Âl-i İmrân', 'يَٓا اَهْلَ الْكِتَابِ لِمَ تَكْفُرُونَ بِاٰيَاتِ اللّٰهِ وَاَنْتُمْ تَشْهَدُونَ', 'Ey ehl-i kitap! (Gerçeği) görüp bildiğiniz halde niçin Allah´ın âyetlerini inkâr edersiniz?', 3, 57, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(364, 71, 3, 'Âl-i İmrân', 'يَٓا اَهْلَ الْكِتَابِ لِمَ تَلْبِسُونَ الْحَقَّ بِالْبَاطِلِ وَتَكْتُمُونَ الْحَقَّ وَاَنْتُمْ تَعْلَمُونَ۟', 'Ey ehl-i kitap! Neden doğruyu eğriye karıştırıyor ve bile bile gerçeği gizliyorsunuz?', 3, 58, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(365, 72, 3, 'Âl-i İmrân', 'وَقَالَتْ طَٓائِفَةٌ مِنْ اَهْلِ الْكِتَابِ اٰمِنُوا بِالَّـذ۪ٓي اُنْزِلَ عَلَى الَّذ۪ينَ اٰمَنُوا وَجْهَ النَّهَارِ وَاكْفُرُٓوا اٰخِرَهُ لَعَلَّهُمْ يَرْجِعُونَۚ', 'Ehl-i kitaptan bir gurup şöyle dedi: «Müminlere indirilmiş olana sabahleyin (görünüşte) inanıp akşamleyin inkâr edin. Belki onlar (böylece dinlerinden) dönerler.', 3, 58, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(366, 73, 3, 'Âl-i İmrân', 'وَلَا تُؤْمِنُٓوا اِلَّا لِمَنْ تَبِـعَ د۪ينَكُمْۜ قُلْ اِنَّ الْهُدٰى هُدَى اللّٰهِۙ اَنْ يُؤْتٰٓى اَحَدٌ مِثْلَ مَٓا اُو۫ت۪يتُمْ اَوْ يُحَٓاجُّوكُمْ عِنْدَ رَبِّكُمْۜ قُلْ اِنَّ الْفَضْلَ بِيَدِ اللّٰهِۚ يُؤْت۪يهِ مَنْ يَشَٓاءُۜ وَاللّٰهُ وَاسِعٌ عَل۪يمٌۚ', 'Sizin dininize uyanlardan başka hiçbir kimseye inanmayın.» (Resûlüm!) De ki: Doğru yol ancak Allah´ın yoludur. Yine (onlar, kendi aralarında şöyle dediler:) «Size verilenin benzerinin başka herhangi bir kimseye verildiğine, yahut Rabbinizin huzurunda onların size karşı deliller getireceklerine de (inanmayın).» De ki: Lütuf ve ihsan Allah´ın elindedir. Onu dilediğine verir. Allah´ın rahmeti geniştir ve O her şeyi hakkıyla bilir.', 3, 58, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(367, 74, 3, 'Âl-i İmrân', 'يَخْتَصُّ بِرَحْمَتِه۪ مَنْ يَشَٓاءُۜ وَاللّٰهُ ذُوالْفَضْلِ الْعَظ۪يمِ', 'Rahmetini dilediğine ayırır. Allah üstün lütuf sahibidir.', 3, 58, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(368, 75, 3, 'Âl-i İmrân', 'وَمِنْ اَهْلِ الْكِتَابِ مَنْ اِنْ تَأْمَنْهُ بِقِنْطَارٍ يُؤَدِّه۪ٓ اِلَيْكَۚ وَمِنْهُمْ مَنْ اِنْ تَأْمَنْهُ بِد۪ينَارٍ لَا يُؤَدِّه۪ٓ اِلَيْكَ اِلَّا مَا دُمْتَ عَلَيْهِ قَٓائِماًۜ ذٰلِكَ بِاَنَّهُمْ قَالُوا لَيْسَ عَلَيْنَا فِي الْاُمِّيّ۪نَ سَب۪يلٌۚ وَيَقُولُونَ عَلَى اللّٰهِ الْكَذِبَ وَهُمْ يَعْلَمُونَ', 'Ehl-i kitaptan öylesi vardır ki, ona yüklerle mal emanet bıraksan, onu sana noksansız iade eder. Fakat onlardan öylesi de vardır ki, ona bir dinar emanet bıraksan, tepesine dikilip durmazsan onu sana iade etmez. Bu da onların, «Ümmîlere karşı yaptıklarımızdan dolayı bize vebal yoktur» demelerindendir. Allah adına bile bile yalan söylüyorlar.', 3, 58, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(369, 76, 3, 'Âl-i İmrân', 'بَلٰى مَنْ اَوْفٰى بِعَهْدِه۪ وَاتَّقٰى فَاِنَّ اللّٰهَ يُحِبُّ الْمُتَّق۪ينَ', 'Hayır! (Gerçek onların dediği değil.) Her kim sözünü yerine getirir ve kötülükten sakınırsa, bilsin ki Allah sakınanları sever.', 3, 58, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(370, 77, 3, 'Âl-i İmrân', 'اِنَّ الَّذ۪ينَ يَشْتَرُونَ بِعَهْدِ اللّٰهِ وَاَيْمَانِهِمْ ثَمَناً قَل۪يلاً اُو۬لٰٓئِكَ لَا خَلَاقَ لَهُمْ فِي الْاٰخِرَةِ وَلَا يُكَلِّمُهُمُ اللّٰهُ وَلَا يَنْظُرُ اِلَيْهِمْ يَوْمَ الْقِيٰمَةِ وَلَا يُزَكّ۪يهِمْۖ وَلَهُمْ عَذَابٌ اَل۪يمٌ', 'Allah´a karşı verdikleri sözü ve yeminlerini az bir bedelle değiştirenlere gelince, işte bunların ahirette bir payı yoktur. Kıyamet günü Allah onlarla konuşmayacak, onlara bakmayacak ve onları temize çıkarmayacaktır. Onlar için acı bir azap vardır.', 3, 58, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(371, 78, 3, 'Âl-i İmrân', 'وَاِنَّ مِنْهُمْ لَفَر۪يقاً يَلْوُ۫نَ اَلْسِنَتَهُمْ بِالْكِتَابِ لِتَحْسَبُوهُ مِنَ الْكِتَابِ وَمَا هُوَ مِنَ الْكِتَابِۚ وَيَقُولُونَ هُوَ مِنْ عِنْدِ اللّٰهِ وَمَا هُوَ مِنْ عِنْدِ اللّٰهِۚ وَيَقُولُونَ عَلَى اللّٰهِ الْكَذِبَ وَهُمْ يَعْلَمُونَ', 'Ehl-i kitaptan bir gurup, okuduklarını kitaptan sanasınız diye kitabı okurken dillerini eğip bükerler. Halbuki okudukları Kitap´tan değildir. Söyledikleri Allah katından olmadığı halde: Bu Allah katındandır, derler. Onlar bile bile Allah´a iftira ediyorlar.', 3, 59, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(372, 79, 3, 'Âl-i İmrân', 'مَا كَانَ لِبَشَرٍ اَنْ يُؤْتِيَهُ اللّٰهُ الْكِتَابَ وَالْحُكْمَ وَالنُّبُوَّةَ ثُمَّ يَقُولَ لِلنَّاسِ كُونُوا عِبَاداً ل۪ي مِنْ دُونِ اللّٰهِ وَلٰكِنْ كُونُوا رَبَّانِيّ۪نَ بِمَا كُنْتُمْ تُعَلِّمُونَ الْكِتَابَ وَبِمَا كُنْتُمْ تَدْرُسُونَۙ', 'Hiçbir insanın, Allah´ın kendisine Kitap, hikmet ve peygamberlik vermesinden sonra (kalkıp) insanlara: Allah´ı bırakıp bana kul olun! demesi mümkün değildir. Bilakis (şöyle demesi gerekir): Okutmakta ve öğretmekte olduğunuz Kitap uyarınca Rabbe hâlis kullar olunuz.', 3, 59, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(373, 80, 3, 'Âl-i İmrân', 'وَلَا يَأْمُرَكُمْ اَنْ تَتَّخِذُوا الْمَلٰٓئِكَةَ وَالنَّبِيّ۪نَ اَرْبَاباًۜ اَيَأْمُرُكُمْ بِالْكُفْرِ بَعْدَ اِذْ اَنْتُمْ مُسْلِمُونَ۟', 'Ve size: Melekleri ve peygamberleri ilâhlar edinin, diye de emretmez. Siz müslüman olduktan sonra hiç size kâfirliği emreder mi?', 3, 59, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(374, 81, 3, 'Âl-i İmrân', 'وَاِذْ اَخَذَ اللّٰهُ م۪يثَاقَ النَّبِيّ۪نَ لَـمَٓا اٰتَيْتُكُمْ مِنْ كِتَابٍ وَحِكْمَةٍ ثُمَّ جَٓاءَكُمْ رَسُولٌ مُصَدِّقٌ لِمَا مَعَكُمْ لَتُؤْمِنُنَّ بِه۪ وَلَتَنْصُرُنَّهُۜ قَالَ ءَاَقْرَرْتُمْ وَاَخَذْتُمْ عَلٰى ذٰلِكُمْ اِصْر۪يۜ قَالُٓوا اَقْرَرْنَاۜ قَالَ فَاشْهَدُوا وَاَنَا۬ مَعَكُمْ مِنَ الشَّاهِد۪ينَ', 'Hani Allah, peygamberlerden: «Ben size Kitap ve hikmet verdikten sonra nezdinizdekileri tasdik eden bir peygamber geldiğinde ona mutlaka inanıp yardım edeceksiniz» diye söz almış, «Kabul ettiniz ve bu ahdimi yüklendiniz mi?» dediğinde, «Kabul ettik» cevabını vermişler, bunun üzerine Allah: O halde şahit olun; ben de sizinle birlikte şahitlik edenlerdenim, buyurmuştu.', 3, 59, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(375, 82, 3, 'Âl-i İmrân', 'فَمَنْ تَوَلّٰى بَعْدَ ذٰلِكَ فَاُو۬لٰٓئِكَ هُمُ الْفَاسِقُونَ', 'Artık bundan sonra her kim dönerse işte onlar yoldan çıkmışların ta kendileridir.', 3, 59, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(376, 83, 3, 'Âl-i İmrân', 'اَفَغَيْرَ د۪ينِ اللّٰهِ يَبْغُونَ وَلَهُٓ اَسْلَمَ مَنْ فِي السَّمٰوَاتِ وَالْاَرْضِ طَوْعاً وَكَرْهاً وَاِلَيْهِ يُرْجَعُونَ', 'Göklerde ve yerdekiler, ister istemez O´na teslim olduğu halde onlar (ehl-i kitap), Allah´ın dininden başkasını mı arıyorlar? Halbuki O´na döndürüleceklerdir.', 3, 59, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(377, 84, 3, 'Âl-i İmrân', 'قُلْ اٰمَنَّا بِاللّٰهِ وَمَٓا اُنْزِلَ عَلَيْنَا وَمَٓا اُنْزِلَ عَلٰٓى اِبْرٰه۪يمَ وَاِسْمٰع۪يلَ وَاِسْحٰقَ وَيَعْقُوبَ وَالْاَسْبَاطِ وَمَٓا اُو۫تِيَ مُوسٰى وَع۪يسٰى وَالنَّبِيُّونَ مِنْ رَبِّهِمْۖ لَا نُفَرِّقُ بَيْنَ اَحَدٍ مِنْهُمْۘ وَنَحْنُ لَهُ مُسْلِمُونَ', 'De ki: Biz, Allah´a, bize indirilene, İbrahim, İsmail, İshak, Ya´kub ve Ya´kub oğullarına indirilenlere, Musa, İsa ve (diğer) peygamberlere Rableri tarafından verilenlere iman ettik. Onları birbirinden ayırdetmeyiz. Biz ancak O´na teslim oluruz.', 3, 60, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(378, 85, 3, 'Âl-i İmrân', 'وَمَنْ يَبْتَغِ غَيْرَ الْاِسْلَامِ د۪يناً فَلَنْ يُقْبَلَ مِنْهُۚ وَهُوَ فِي الْاٰخِرَةِ مِنَ الْخَاسِر۪ينَ', 'Kim, İslâm´dan başka bir din ararsa, bilsin ki kendisinden (böyle bir din) asla kabul edilmeyecek ve o, ahirette ziyan edenlerden olacaktır.', 3, 60, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(379, 86, 3, 'Âl-i İmrân', 'كَيْفَ يَهْدِي اللّٰهُ قَوْماً كَفَرُوا بَعْدَ ا۪يمَانِهِمْ وَشَهِدُٓوا اَنَّ الرَّسُولَ حَقٌّ وَجَٓاءَهُمُ الْبَيِّنَاتُۜ وَاللّٰهُ لَا يَهْدِي الْقَوْمَ الظَّالِم۪ينَ', 'İman etmelerinden, Resûl´ün hak olduğuna şehadet getirmelerinden ve kendilerine apaçık deliller gelmesinden sonra inkârcılığa sapan bir kavme Allah nasıl hidayet nasip eder? Allah zalimler topluluğunu doğru yola iletmez.', 3, 60, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(380, 87, 3, 'Âl-i İmrân', 'اُو۬لٰٓئِكَ جَزَٓاؤُ۬هُمْ اَنَّ عَلَيْهِمْ لَعْنَةَ اللّٰهِ وَالْمَلٰٓئِكَةِ وَالنَّاسِ اَجْمَع۪ينَۙ', 'İşte onların cezası, Allah´ın, meleklerin ve bütün insanlığın lânetine uğramalarıdır.', 3, 60, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(381, 88, 3, 'Âl-i İmrân', 'خَالِد۪ينَ ف۪يهَاۚ لَا يُخَفَّفُ عَنْهُمُ الْعَذَابُ وَلَا هُمْ يُنْظَرُونَۙ', 'Bu lânete ebedî gömülüp gidecekler. Onların azapları hafifletilmez; yüzlerine de bakılmaz.', 3, 60, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(382, 89, 3, 'Âl-i İmrân', 'اِلَّا الَّذ۪ينَ تَابُوا مِنْ بَعْدِ ذٰلِكَ وَاَصْلَحُوا فَاِنَّ اللّٰهَ غَفُورٌ رَح۪يمٌ', 'Ancak, bundan sonra tevbe edip yola gelenler başka. Çünkü Allah çok bağışlayıcı ve merhametlidir.', 3, 60, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(383, 90, 3, 'Âl-i İmrân', 'اِنَّ الَّذ۪ينَ كَفَرُوا بَعْدَ ا۪يمَانِهِمْ ثُمَّ ازْدَادُوا كُفْراً لَنْ تُقْبَلَ تَوْبَتُهُمْۚ وَاُو۬لٰٓئِكَ هُمُ الضَّٓالُّونَ', 'İnandıktan sonra kâfirliğe sapıp sonra inkârcılıkta daha da ileri gidenlerin tevbeleri asla kabul edilmeyecektir. Ve işte onlar, sapıkların ta kendisidirler.', 3, 60, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(384, 91, 3, 'Âl-i İmrân', 'اِنَّ الَّذ۪ينَ كَفَرُوا وَمَاتُوا وَهُمْ كُفَّارٌ فَلَنْ يُقْبَلَ مِنْ اَحَدِهِمْ مِلْءُ الْاَرْضِ ذَهَباً وَلَوِ افْتَدٰى بِه۪ۜ اُو۬لٰٓئِكَ لَهُمْ عَذَابٌ اَل۪يمٌ وَمَا لَهُمْ مِنْ نَاصِر۪ينَ۟', 'Gerçekten, inkâr edip kâfir olarak ölenler var ya, onların hiçbirinden -fidye olarak dünya dolusu altın verecek olsa dahi- kabul edilmeyecektir. Onlar için acı bir azap vardır; hiç yardımcıları da yoktur.', 3, 60, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(385, 92, 3, 'Âl-i İmrân', 'لَنْ تَنَالُوا الْبِرَّ حَتّٰى تُنْفِقُوا مِمَّا تُحِبُّونَۜ وَمَا تُنْفِقُوا مِنْ شَيْءٍ فَاِنَّ اللّٰهَ بِه۪ عَل۪يمٌ', 'Sevdiğiniz şeylerden (Allah yolunda) harcamadıkça «iyi»ye eremezsiniz. Her ne harcarsanız, Allah onu hakkıyla bilir.', 4, 61, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(386, 93, 3, 'Âl-i İmrân', 'كُلُّ الطَّعَامِ كَانَ حِلاًّ لِبَن۪ٓي اِسْرَٓائ۪لَ اِلَّا مَا حَرَّمَ اِسْرَٓائ۪لُ عَلٰى نَفْسِه۪ مِنْ قَبْلِ اَنْ تُنَزَّلَ التَّوْرٰيةُۜ قُلْ فَأْتُوا بِالتَّوْرٰيةِ فَاتْلُوهَٓا اِنْ كُنْتُمْ صَادِق۪ينَ', 'Tevrat´ın indirilmesinden önce, İsrail´in (Ya´kub´un) kendisine haram kıldıkları dışında, yiyeceğin her türlüsü İsrailoğullarına helâl idi. De ki: Eğer doğru sözlü iseniz, o zaman Tevrat´ı getirip onu okuyun.', 4, 61, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(387, 94, 3, 'Âl-i İmrân', 'فَمَنِ افْتَرٰى عَلَى اللّٰهِ الْكَذِبَ مِنْ بَعْدِ ذٰلِكَ فَاُو۬لٰٓئِكَ هُمُ الظَّالِمُونَ', 'Artık bundan sonra her kim Allah´a karşı yalan uydurursa, işte bunlar, zalimlerin ta kendisidirler.', 4, 61, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(388, 95, 3, 'Âl-i İmrân', 'قُلْ صَدَقَ اللّٰهُ فَاتَّبِعُوا مِلَّةَ اِبْرٰه۪يمَ حَن۪يفاًۜ وَمَا كَانَ مِنَ الْمُشْرِك۪ينَ', 'De ki: Allah doğruyu söylemiştir. Öyle ise, hakka yönelmiş olarak İbrahim´in dinine uyunuz. O, müşriklerden değildi.', 4, 61, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(389, 96, 3, 'Âl-i İmrân', 'اِنَّ اَوَّلَ بَيْتٍ وُضِعَ لِلنَّاسِ لَلَّذ۪ي بِبَكَّةَ مُبَارَكاً وَهُدًى لِلْعَالَم۪ينَۚ', 'Şüphesiz, âlemlere bereket ve hidayet kaynağı olarak insanlar için kurulan ilk ev (mâbet), Mekke´deki (Kâbe)dir.', 4, 61, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(390, 97, 3, 'Âl-i İmrân', 'ف۪يهِ اٰيَاتٌ بَيِّنَاتٌ مَقَامُ اِبْرٰه۪يمَۚ وَمَنْ دَخَلَهُ كَانَ اٰمِناًۜ وَلِلّٰهِ عَلَى النَّاسِ حِجُّ الْبَيْتِ مَنِ اسْتَطَاعَ اِلَيْهِ سَب۪يلاًۜ وَمَنْ كَفَرَ فَاِنَّ اللّٰهَ غَنِيٌّ عَنِ الْعَالَم۪ينَ', 'Orada apaçık nişâneler, (ayrıca) İbrahim´in makamı vardır. Oraya giren emniyette olur. Yoluna gücü yetenlerin o evi haccetmesi, Allah´ın insanlar üzerinde bir hakkıdır. Kim inkâr ederse bilmelidir ki, Allah bütün âlemlerden müstağnîdir.', 4, 61, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(391, 98, 3, 'Âl-i İmrân', 'قُلْ يَٓا اَهْلَ الْكِتَابِ لِمَ تَكْفُرُونَ بِاٰيَاتِ اللّٰهِۗ وَاللّٰهُ شَه۪يدٌ عَلٰى مَا تَعْمَلُونَ', 'De ki: Ey ehl-i kitap! Allah yaptıklarınızı görüp dururken niçin Allah´ın âyetlerini inkâr edersiniz?', 4, 61, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(392, 99, 3, 'Âl-i İmrân', 'قُلْ يَٓا اَهْلَ الْكِتَابِ لِمَ تَصُدُّونَ عَنْ سَب۪يلِ اللّٰهِ مَنْ اٰمَنَ تَبْغُونَهَا عِوَجاً وَاَنْتُمْ شُهَدَٓاءُۜ وَمَا اللّٰهُ بِغَافِلٍ عَمَّا تَعْمَلُونَ', 'De ki: Ey ehl-i kitap! (Gerçeği) görüp bildiğiniz halde niçin Allah´ın yolunu eğri göstermeye yeltenerek müminleri Allah yolundan çevirmeye kalkışıyorsunuz? Allah yaptıklarınızdan habersiz değildir.', 4, 61, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(393, 100, 3, 'Âl-i İmrân', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُٓوا اِنْ تُط۪يعُوا فَر۪يقاً مِنَ الَّذ۪ينَ اُو۫تُوا الْكِتَابَ يَرُدُّوكُمْ بَعْدَ ا۪يمَانِكُمْ كَافِر۪ينَ', 'Ey iman edenler! Kendilerine kitap verilenlerden bir guruba uyarsanız imanınızdan sonra sizi yeniden inkârcılığa sevkederler.', 4, 61, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(394, 101, 3, 'Âl-i İmrân', 'وَكَيْفَ تَكْفُرُونَ وَاَنْتُمْ تُتْلٰى عَلَيْكُمْ اٰيَاتُ اللّٰهِ وَف۪يكُمْ رَسُولُهُۜ وَمَنْ يَعْتَصِمْ بِاللّٰهِ فَقَدْ هُدِيَ اِلٰى صِرَاطٍ مُسْتَق۪يمٍ۟', 'Size Allah´ın âyetleri okunurken, üstelik Allah Resûlü de aranızda iken nasıl inkâra saparsınız? Her kim Allah´a bağlanırsa kesinlikle doğru yola iletilmiştir.', 4, 62, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(395, 102, 3, 'Âl-i İmrân', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا اتَّقُوا اللّٰهَ حَقَّ تُقَاتِه۪ وَلَا تَمُوتُنَّ اِلَّا وَاَنْتُمْ مُسْلِمُونَ', 'Ey iman edenler! Allah´tan, O´na yaraşır şekilde korkun ve ancak müslümanlar olarak can verin.', 4, 62, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(396, 103, 3, 'Âl-i İmrân', 'وَاعْتَصِمُوا بِحَبْلِ اللّٰهِ جَم۪يعاً وَلَا تَفَرَّقُواۖ وَاذْكُرُوا نِعْمَتَ اللّٰهِ عَلَيْكُمْ اِذْ كُنْتُمْ اَعْدَٓاءً فَاَلَّفَ بَيْنَ قُلُوبِكُمْ فَاَصْبَحْتُمْ بِنِعْمَتِه۪ٓ اِخْوَاناًۚ وَكُنْتُمْ عَلٰى شَفَا حُفْرَةٍ مِنَ النَّارِ فَاَنْقَذَكُمْ مِنْهَاۜ كَذٰلِكَ يُبَيِّنُ اللّٰهُ لَكُمْ اٰيَاتِه۪ لَعَلَّكُمْ تَهْتَدُونَ', 'Hep birlikte Allah´ın ipine (İslâm´a) sımsıkı yapışın; parçalanmayın. Allah´ın size olan nimetini hatırlayın: Hani siz birbirinize düşman kişiler idiniz de O, gönüllerinizi birleştirmişti ve O´nun nimeti sayesinde kardeş kimseler olmuştunuz. Yine siz bir ateş çukurunun tam kenarında iken oradan da sizi O kurtarmıştı. İşte Allah size âyetlerini böyle açıklar ki doğru yolu bulasınız.', 4, 62, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(397, 104, 3, 'Âl-i İmrân', 'وَلْتَكُنْ مِنْكُمْ اُمَّةٌ يَدْعُونَ اِلَى الْخَيْرِ وَيَأْمُرُونَ بِالْمَعْرُوفِ وَيَنْهَوْنَ عَنِ الْمُنْكَرِۜ وَاُو۬لٰٓئِكَ هُمُ الْمُفْلِحُونَ', 'Sizden, hayra çağıran, iyiliği emredip kötülüğü meneden bir topluluk bulunsun. İşte onlar kurtuluşa erenlerdir.', 4, 62, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(398, 105, 3, 'Âl-i İmrân', 'وَلَا تَكُونُوا كَالَّذ۪ينَ تَفَرَّقُوا وَاخْتَلَفُوا مِنْ بَعْدِ مَا جَٓاءَهُمُ الْبَيِّنَاتُۜ وَاُو۬لٰٓئِكَ لَهُمْ عَذَابٌ عَظ۪يمٌۙ', 'Kendilerine apaçık deliller geldikten sonra parçalanıp ayrılığa düşenler gibi olmayın. İşte bunlar için büyük bir azap vardır.', 4, 62, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(399, 106, 3, 'Âl-i İmrân', 'يَوْمَ تَبْيَضُّ وُجُوهٌ وَتَسْوَدُّ وُجُوهٌۚ فَاَمَّا الَّذ۪ينَ اسْوَدَّتْ وُجُوهُهُمْ۠ اَكَفَرْتُمْ بَعْدَ ا۪يمَانِكُمْ فَذُوقُوا الْعَذَابَ بِمَا كُنْتُمْ تَكْفُرُونَ', 'Nice yüzlerin ağardığı, nice yüzlerin de karardığı günü (düşünün.) İmdi, yüzleri kararanlara: İnanmanızdan sonra kâfir mi oldunuz? Öyle ise inkâr etmiş olmanız yüzünden tadın azabı! (denilir).', 4, 62, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(400, 107, 3, 'Âl-i İmrân', 'وَاَمَّا الَّذ۪ينَ ابْيَضَّتْ وُجُوهُهُمْ فَف۪ي رَحْمَةِ اللّٰهِۜ هُمْ ف۪يهَا خَالِدُونَ', 'Yüzleri ağaranlara gelince, onlar Allah´ın rahmeti içindedirler; orada ebedî kalacaklardır.', 4, 62, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(401, 108, 3, 'Âl-i İmrân', 'تِلْكَ اٰيَاتُ اللّٰهِ نَتْلُوهَا عَلَيْكَ بِالْحَقِّۜ وَمَا اللّٰهُ يُر۪يدُ ظُلْماً لِلْعَالَم۪ينَ', 'İşte bunlar, Allah´ın, sana hak olarak okuduğumuz âyetleridir. Allah hiçbir kimseye haksızlık etmek istemez.', 4, 62, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(402, 109, 3, 'Âl-i İmrân', 'وَلِلّٰهِ مَا فِي السَّمٰوَاتِ وَمَا فِي الْاَرْضِۜ وَاِلَى اللّٰهِ تُرْجَعُ الْاُمُورُ۟', 'Göklerde ve yerde ne varsa Allah´ındır. İşler, dönüp dolaşıp Allah´a varır.', 4, 63, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(403, 110, 3, 'Âl-i İmrân', 'كُنْتُمْ خَيْرَ اُمَّةٍ اُخْرِجَتْ لِلنَّاسِ تَأْمُرُونَ بِالْمَعْرُوفِ وَتَنْهَوْنَ عَنِ الْمُنْكَرِ وَتُؤْمِنُونَ بِاللّٰهِۜ وَلَوْ اٰمَنَ اَهْلُ الْكِتَابِ لَكَانَ خَيْراً لَهُمْۜ مِنْهُمُ الْمُؤْمِنُونَ وَاَكْثَرُهُمُ الْفَاسِقُونَ', 'Siz, insanların iyiliği için ortaya çıkarılmış en hayırlı ümmetsiniz; iyiliği emreder, kötülükten meneder ve Allah´a inanırsınız. Ehl-i kitap da inansaydı, elbet bu, kendileri için çok iyi olurdu. (Gerçi) içlerinde iman edenler var; (fakat) çoğu yoldan çıkmışlardır.', 4, 63, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(404, 111, 3, 'Âl-i İmrân', 'لَنْ يَضُرُّوكُمْ اِلَّٓا اَذًىۜ وَاِنْ يُقَاتِلُوكُمْ يُوَلُّوكُمُ الْاَدْبَارَ۠ ثُمَّ لَا يُنْصَرُونَ', 'Onlar (ehl-i kitap) size, incitmekten başka bir zarar veremezler. Sizinle savaşa girecek olsalar, size arkalarını dönüp kaçarlar. Sonra kendilerine yardım da edilmez.', 4, 63, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(405, 112, 3, 'Âl-i İmrân', 'ضُرِبَتْ عَلَيْهِمُ الذِّلَّةُ اَيْنَ مَا ثُقِفُٓوا اِلَّا بِحَبْلٍ مِنَ اللّٰهِ وَحَبْلٍ مِنَ النَّاسِ وَبَٓاؤُ۫ بِغَضَبٍ مِنَ اللّٰهِ وَضُرِبَتْ عَلَيْهِمُ الْمَسْكَنَةُۜ ذٰلِكَ بِاَنَّهُمْ كَانُوا يَكْفُرُونَ بِاٰيَاتِ اللّٰهِ وَيَقْتُلُونَ الْاَنْبِيَٓاءَ بِغَيْرِ حَقٍّۜ ذٰلِكَ بِمَا عَصَوْا وَكَانُوا يَعْتَدُونَ۠', 'Onlar (yahudiler) nerede bulunurlarsa bulunsunlar, Allah´ın ahdine ve insanların (müminlerin) himayesine sığınmadıkça kendilerine zillet (damgası) vurulmuştur; Allah´ın hışmına uğramışlar ve miskinliğe mahkum edilmişlerdir. Çünkü onlar, Allah´ın âyetlerini inkâr ediyorlar ve haksız yere peygamberleri öldürüyorlardı. Bu da, onların isyan etmiş ve haddi aşmış bulunmalarındandır.', 4, 63, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(406, 113, 3, 'Âl-i İmrân', 'لَيْسُوا سَوَٓاءًۜ مِنْ اَهْلِ الْكِتَابِ اُمَّةٌ قَٓائِمَةٌ يَتْلُونَ اٰيَاتِ اللّٰهِ اٰنَٓاءَ الَّيْلِ وَهُمْ يَسْجُدُونَ۠', 'Hepsi bir değildir; ehl-i kitap içinde istikamet sahibi bir topluluk vardır ki, gece saatlerinde secdeye kapanarak Allah´ın âyetlerini okurlar.', 4, 63, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(407, 114, 3, 'Âl-i İmrân', 'يُؤْمِنُونَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَيَأْمُرُونَ بِالْمَعْرُوفِ وَيَنْهَوْنَ عَنِ الْمُنْكَرِ وَيُسَارِعُونَ فِي الْخَيْرَاتِۜ وَاُو۬لٰٓئِكَ مِنَ الصَّالِح۪ينَ', 'Onlar, Allah´a ve ahiret gününe inanırlar; iyiliği emreder, kötülükten menederler; hayırlı işlere koşuşurlar. İşte bunlar iyi insanlardandır.', 4, 63, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(408, 115, 3, 'Âl-i İmrân', 'وَمَا يَفْعَلُوا مِنْ خَيْرٍ فَلَنْ يُكْفَرُوهُۜ وَاللّٰهُ عَل۪يمٌ بِالْمُتَّق۪ينَ', 'Onların yaptıkları hiçbir hayır karşılıksız bırakılmayacaktır. Allah, takvâ sahiplerini çok iyi bilir.', 4, 63, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(409, 116, 3, 'Âl-i İmrân', 'اِنَّ الَّذ۪ينَ كَفَرُوا لَنْ تُغْنِيَ عَنْهُمْ اَمْوَالُهُمْ وَلَٓا اَوْلَادُهُمْ مِنَ اللّٰهِ شَيْـٔاًۜ وَاُو۬لٰٓئِكَ اَصْحَابُ النَّارِۚ هُمْ ف۪يهَا خَالِدُونَ', 'İnkâr edenler var ya, onların malları da evlâtları da Allah´a karşı kendilerine hiçbir fayda sağlamayacaktır. İşte onlar, cehennemliklerdir; onlar orada ebedî kalacaklardır.', 4, 64, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(410, 117, 3, 'Âl-i İmrân', 'مَثَلُ مَا يُنْفِقُونَ ف۪ي هٰذِهِ الْحَيٰوةِ الدُّنْيَا كَمَثَلِ ر۪يحٍ ف۪يهَا صِرٌّ اَصَابَتْ حَرْثَ قَوْمٍ ظَلَمُٓوا اَنْفُسَهُمْ فَاَهْلَكَتْهُۜ وَمَا ظَلَمَهُمُ اللّٰهُ وَلٰكِنْ اَنْفُسَهُمْ يَظْلِمُونَ', 'Onların, bu dünya hayatında yapmakta oldukları harcamaların durumu, kendilerine zulmetmiş olan bir kavmin ekinlerini vurup da mahveden kavurucu bir rüzgârın durumu gibidir. Onlara Allah zulmetmedi; fakat onlar kendilerine zulmediyorlar.', 4, 64, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(411, 118, 3, 'Âl-i İmrân', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا لَا تَتَّخِذُوا بِطَانَةً مِنْ دُونِكُمْ لَا يَأْلُونَكُمْ خَبَالاًۜ وَدُّوا مَا عَنِتُّمْۚ قَدْ بَدَتِ الْبَغْضَٓاءُ مِنْ اَفْوَاهِهِمْۚ وَمَا تُخْف۪ي صُدُورُهُمْ اَكْـبَرُۜ قَدْ بَيَّنَّا لَكُمُ الْاٰيَاتِ اِنْ كُنْتُمْ تَعْقِلُونَ', 'Ey iman edenler! Kendi dışınızdakileri sırdaş edinmeyin. Çünkü onlar size fenalık etmekten asla geri durmazlar, hep sıkıntıya düşmenizi isterler. Gerçekten, kin ve düşmanlıkları ağızlarından (dökülen sözlerinden) belli olmaktadır. Kalplerinde sakladıkları (düşmanlıkları) ise daha büyüktür. Eğer düşünüp anlıyorsanız, âyetlerimizi size açıklamış bulunuyoruz.', 4, 64, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(412, 119, 3, 'Âl-i İmrân', 'هَٓا اَنْتُمْ اُو۬لَٓاءِ تُحِبُّونَهُمْ وَلَا يُحِبُّونَكُمْ وَتُؤْمِنُونَ بِالْكِتَابِ كُلِّه۪ۚ وَاِذَا لَقُوكُمْ قَالُٓوا اٰمَنَّاۗ وَاِذَا خَلَوْا عَضُّوا عَلَيْكُمُ الْاَنَامِلَ مِنَ الْغَيْظِۜ قُلْ مُوتُوا بِغَيْظِكُمْۜ اِنَّ اللّٰهَ عَل۪يمٌ بِذَاتِ الصُّدُورِ', 'İşte siz öyle kimselersiniz ki, onlar sizi sevmedikleri halde siz onları seversiniz. Siz, bütün kitaplara inanırsınız; onlar ise, sizinle karşılaştıklarında «İnandık» derler; kendi başlarına kaldıklarında da, size olan kinlerinden dolayı parmaklarının uçlarını ısırırlar. De ki: Kininizden (kahrolup) ölün! Şüphesiz Allah kalplerin içindekini hakkıyla bilmektedir.', 4, 64, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(413, 120, 3, 'Âl-i İmrân', 'اِنْ تَمْسَسْكُمْ حَسَنَةٌ تَسُؤْهُمْۘ وَاِنْ تُصِبْكُمْ سَيِّئَةٌ يَفْرَحُوا بِهَاۜ وَاِنْ تَصْبِرُوا وَتَتَّقُوا لَا يَضُرُّكُمْ كَيْدُهُمْ شَيْـٔاًۜ اِنَّ اللّٰهَ بِمَا يَعْمَلُونَ مُح۪يطٌ۟', 'Size bir iyilik dokunsa, bu onları tasalandırır; başınıza bir musibet gelse, buna da sevinirler. Eğer sabreder ve korunursanız, onların hilesi size hiçbir zarar vermez. Şüphesiz Allah, onların yaptıklarını çepeçevre kuşatmıştır.', 4, 64, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(414, 121, 3, 'Âl-i İmrân', 'وَاِذْ غَدَوْتَ مِنْ اَهْلِكَ تُبَوِّئُ الْمُؤْمِن۪ينَ مَقَاعِدَ لِلْقِتَالِۜ وَاللّٰهُ سَم۪يعٌ عَل۪يمٌۙ', 'Hani sen, sabah erkenden müminleri savaş mevzilerine yerleştirmek için ailenden ayrılmıştın...-Allah, hakkıyle işiten ve bilendir.-', 4, 64, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(415, 122, 3, 'Âl-i İmrân', 'اِذْ هَمَّتْ طَٓائِفَتَانِ مِنْكُمْ اَنْ تَفْشَلَاۙ وَاللّٰهُ وَلِيُّهُمَاۜ وَعَلَى اللّٰهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُونَ', 'O zaman içinizden iki bölük bozulmaya yüz tutmuştu. Halbuki Allah onların yardımcısı idi. Müminler, yalnız Allah´a dayanıp güvensinler.', 4, 65, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(416, 123, 3, 'Âl-i İmrân', 'وَلَقَدْ نَصَرَكُمُ اللّٰهُ بِبَدْرٍ وَاَنْتُمْ اَذِلَّةٌۚ فَاتَّقُوا اللّٰهَ لَعَلَّكُمْ تَشْكُرُونَ', 'Andolsun, sizler güçsüz olduğunuz halde Allah, Bedir´de de size yardım etmişti. Öyle ise, Allah´tan sakının ki O´na şükretmiş olasınız.', 4, 65, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(417, 124, 3, 'Âl-i İmrân', 'اِذْ تَقُولُ لِلْمُؤْمِن۪ينَ اَلَنْ يَكْفِيَكُمْ اَنْ يُمِدَّكُمْ رَبُّكُمْ بِثَلٰثَةِ اٰلَافٍ مِنَ الْمَلٰٓئِكَةِ مُنْزَل۪ينَۜ', 'O zaman sen, müminlere şöyle diyordun: İndirilen üç bin melekle Rabbinizin sizi takviye etmesi, sizin için yeterli değil midir?', 4, 65, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(418, 125, 3, 'Âl-i İmrân', 'بَلٰٓىۙ اِنْ تَصْبِرُوا وَتَتَّقُوا وَيَأْتُوكُمْ مِنْ فَوْرِهِمْ هٰذَا يُمْدِدْكُمْ رَبُّكُمْ بِخَمْسَةِ اٰلَافٍ مِنَ الْمَلٰٓئِكَةِ مُسَوِّم۪ينَ', 'Evet, siz sabır gösterir ve Allah´tan sakınırsanız, onlar (düşmanlarınız) hemen şu anda üzerinize gelseler, Rabbiniz, nişanlı beş bin melekle sizi takviye eder.', 4, 65, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(419, 126, 3, 'Âl-i İmrân', 'وَمَا جَعَلَهُ اللّٰهُ اِلَّا بُشْرٰى لَكُمْ وَلِتَطْمَئِنَّ قُلُوبُكُمْ بِه۪ۜ وَمَا النَّصْرُ اِلَّا مِنْ عِنْدِ اللّٰهِ الْعَز۪يزِ الْحَك۪يمِۙ', 'Allah, bunu size sırf bir müjde olsun ve kalpleriniz bu sayede rahatlasın diye yaptı. Zafer, yalnızca mutlak güç ve hikmet sahibi Allah katındandır.', 4, 65, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(420, 127, 3, 'Âl-i İmrân', 'لِيَقْطَعَ طَرَفاً مِنَ الَّذ۪ينَ كَفَرُٓوا اَوْ يَكْبِتَهُمْ فَيَنْقَلِبُوا خَٓائِب۪ينَ', '(127-128) Allah, kâfirlerden bir kısmının kökünü kessin veya onları perişan etsin, böylece bozulmuş bir halde dönüp gitsinler -ki bu işte senin yapacağın bir şey yoktur- yahut (müslüman olsunlar da) tevbelerini kabul etsin, ya da (ısrar ederlerse) onlara azap etsin diye (Allah Bedir´de size yardım etti). Çünkü onlar zalimdirler.', 4, 65, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(421, 128, 3, 'Âl-i İmrân', 'لَيْسَ لَكَ مِنَ الْاَمْرِ شَيْءٌ اَوْ يَتُوبَ عَلَيْهِمْ اَوْ يُعَذِّبَهُمْ فَاِنَّهُمْ ظَالِمُونَ', '(127-128) Allah, kâfirlerden bir kısmının kökünü kessin veya onları perişan etsin, böylece bozulmuş bir halde dönüp gitsinler -ki bu işte senin yapacağın bir şey yoktur- yahut (müslüman olsunlar da) tevbelerini kabul etsin, ya da (ısrar ederlerse) onlara azap etsin diye (Allah Bedir´de size yardım etti). Çünkü onlar zalimdirler.', 4, 65, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(422, 129, 3, 'Âl-i İmrân', 'وَلِلّٰهِ مَا فِي السَّمٰوَاتِ وَمَا فِي الْاَرْضِۜ يَغْفِرُ لِمَنْ يَشَٓاءُ وَيُعَذِّبُ مَنْ يَشَٓاءُۜ وَاللّٰهُ غَفُورٌ رَح۪يمٌ۟', 'Göklerde ve yerde ne varsa Allah´ındır. Dilediğini bağışlar, dilediğine azap eder. Allah, çok bağışlayıcı ve çok merhametlidir.', 4, 65, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(423, 130, 3, 'Âl-i İmrân', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا لَا تَأْكُلُوا الرِّبٰٓوا اَضْعَافاً مُضَاعَفَةًۖ وَاتَّقُوا اللّٰهَ لَعَلَّكُمْ تُفْلِحُونَۚ', 'Ey iman edenler! Kat kat arttırılmış olarak faiz yemeyin. Allah´tan sakının ki kurtuluşa eresiniz.', 4, 65, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(424, 131, 3, 'Âl-i İmrân', 'وَاتَّقُوا النَّارَ الَّت۪ٓي اُعِدَّتْ لِلْكَافِر۪ينَۚ', 'Kâfirler için hazırlanmış bulunan ateşten sakının!', 4, 65, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(425, 132, 3, 'Âl-i İmrân', 'وَاَط۪يعُوا اللّٰهَ وَالرَّسُولَ لَعَلَّكُمْ تُرْحَمُونَۚ', 'Allah´a ve Resûl´üne itaat edin ki rahmete kavuşturulasınız.', 4, 65, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(426, 133, 3, 'Âl-i İmrân', 'وَسَارِعُٓوا اِلٰى مَغْفِرَةٍ مِنْ رَبِّكُمْ وَجَنَّةٍ عَرْضُهَا السَّمٰوَاتُ وَالْاَرْضُۙ اُعِدَّتْ لِلْمُتَّق۪ينَۙ', 'Rabbinizin bağışına ve takvâ sahipleri için hazırlanmış olup genişliği gökler ve yer kadar olan cennete koşun!', 4, 66, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(427, 134, 3, 'Âl-i İmrân', 'اَلَّذ۪ينَ يُنْفِقُونَ فِي السَّرَّٓاءِ وَالضَّرَّٓاءِ وَالْكَاظِم۪ينَ الْغَيْظَ وَالْعَاف۪ينَ عَنِ النَّاسِۜ وَاللّٰهُ يُحِبُّ الْمُحْسِن۪ينَۚ', 'O takvâ sahipleri ki, bollukta da darlıkta da Allah için harcarlar; öfkelerini yutarlar ve insanları affederler. Allah da güzel davranışta bulunanları sever.', 4, 66, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(428, 135, 3, 'Âl-i İmrân', 'وَالَّذ۪ينَ اِذَا فَعَلُوا فَاحِشَةً اَوْ ظَلَمُٓوا اَنْفُسَهُمْ ذَكَرُوا اللّٰهَ فَاسْتَغْفَرُوا لِذُنُوبِهِمْۖ وَمَنْ يَغْفِرُ الذُّنُوبَ اِلَّا اللّٰهُۖ وَلَمْ يُصِرُّوا عَلٰى مَا فَعَلُوا وَهُمْ يَعْلَمُونَ', 'Yine onlar ki, bir kötülük yaptıklarında, ya da kendilerine zulmettiklerinde Allah´ı hatırlayıp günahlarından dolayı hemen tevbe-istiğfar ederler. Zaten günahları Allah´tan başka kim bağışlayabilir ki! Bir de onlar, işledikleri kötülüklerde, bile bile ısrar etmezler.', 4, 66, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(429, 136, 3, 'Âl-i İmrân', 'اُو۬لٰٓئِكَ جَزَٓاؤُ۬هُمْ مَغْفِرَةٌ مِنْ رَبِّهِمْ وَجَنَّاتٌ تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُ خَالِد۪ينَ ف۪يهَاۜ وَنِعْمَ اَجْرُ الْعَامِل۪ينَۜ', 'İşte onların mükâfatı, Rableri tarafından bağışlanma ve altlarından ırmaklar akan, içinde ebedî kalacakları cennetlerdir. Böyle amel edenlerin mükâfatı ne güzeldir!', 4, 66, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(430, 137, 3, 'Âl-i İmrân', 'قَدْ خَلَتْ مِنْ قَبْلِكُمْ سُنَنٌۙ فَس۪يرُوا فِي الْاَرْضِ فَانْظُرُوا كَيْفَ كَانَ عَاقِبَةُ الْمُكَذِّب۪ينَ', 'Sizden önce nice (milletler hakkında) ilâhî kanunlar gelip geçmiştir. Onun için, yeryüzünde gezin dolaşın da (Allah´ın âyetlerini) yalan sayanların âkıbeti ne olmuş, görün!', 4, 66, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(431, 138, 3, 'Âl-i İmrân', 'هٰذَا بَيَانٌ لِلنَّاسِ وَهُدًى وَمَوْعِظَةٌ لِلْمُتَّق۪ينَ', 'Bu (Kur´an), bütün insanlığa bir açıklamadır; takvâ sahipleri için de bir hidayet ve bir öğüttür.', 4, 66, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(432, 139, 3, 'Âl-i İmrân', 'وَلَا تَهِنُوا وَلَا تَحْزَنُوا وَاَنْتُمُ الْاَعْلَوْنَ اِنْ كُنْتُمْ مُؤْمِن۪ينَ', 'Gevşeklik göstermeyin, üzüntüye kapılmayın. Eğer inanmışsanız, üstün gelecek olan sizsiniz.', 4, 66, 200, 14605, 'İmrân ailesi', 'Medine', 89);
INSERT INTO `Ayetler` (`id`, `AyetNo`, `SureNo`, `SureAdi`, `Metin`, `Mana`, `Cuzu`, `Sayfasi`, `AyetSayisi`, `HarfSayisi`, `AdAnlami`, `IndigiYer`, `InisSirasi`) VALUES
(433, 140, 3, 'Âl-i İmrân', 'اِنْ يَمْسَسْكُمْ قَرْحٌ فَقَدْ مَسَّ الْقَوْمَ قَرْحٌ مِثْلُهُۜ وَتِلْكَ الْاَيَّامُ نُدَاوِلُهَا بَيْنَ النَّاسِۚ وَلِيَعْلَمَ اللّٰهُ الَّذ۪ينَ اٰمَنُوا وَيَتَّخِذَ مِنْكُمْ شُهَدَٓاءَۜ وَاللّٰهُ لَا يُحِبُّ الظَّالِم۪ينَۙ', 'Eğer siz (Uhud´da) bir acıya uğradınızsa, (Bedir´de de düşmanınız olan) o kavim de benzer bir acıya uğramıştır. O günleri biz insanlar arasında döndürür dururuz (zaferi bazen bir topluma bazen öteki topluma nasip ederiz.) Ta ki Allah, iman edenleri ortaya çıkarsın ve aranızdan şahitler edinsin. Allah zalimleri sevmez.', 4, 66, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(434, 141, 3, 'Âl-i İmrân', 'وَلِيُمَحِّصَ اللّٰهُ الَّذ۪ينَ اٰمَنُوا وَيَمْحَقَ الْكَافِر۪ينَ', 'Bir de (böylece) Allah, iman edenleri günahlardan temize çıkarmak, kâfirleri de helâk etmek ister.', 4, 67, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(435, 142, 3, 'Âl-i İmrân', 'اَمْ حَسِبْتُمْ اَنْ تَدْخُلُوا الْجَنَّةَ وَلَمَّا يَعْلَمِ اللّٰهُ الَّذ۪ينَ جَاهَدُوا مِنْكُمْ وَيَعْلَمَ الصَّابِر۪ينَ', 'Yoksa Allah içinizden cihad edenleri belli etmeden, sabredenleri ortaya çıkarmadan cennete gireceğinizi mi sandınız?', 4, 67, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(436, 143, 3, 'Âl-i İmrân', 'وَلَقَدْ كُنْتُمْ تَمَنَّوْنَ الْمَوْتَ مِنْ قَبْلِ اَنْ تَلْقَوْهُۖ فَقَدْ رَاَيْتُمُوهُ وَاَنْتُمْ تَنْظُرُونَ۟', 'Andolsun ki siz, ölümle yüzyüze gelmezden önce onu temenni ederdiniz. İşte şimdi onu karşınızda gördünüz.', 4, 67, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(437, 144, 3, 'Âl-i İmrân', 'وَمَا مُحَمَّدٌ اِلَّا رَسُولٌۚ قَدْ خَلَتْ مِنْ قَبْلِهِ الرُّسُلُۜ اَفَا۬ئِنْ مَاتَ اَوْ قُتِلَ انْقَلَبْتُمْ عَلٰٓى اَعْقَابِكُمْۜ وَمَنْ يَنْقَلِبْ عَلٰى عَقِبَيْهِ فَلَنْ يَضُرَّ اللّٰهَ شَيْـٔاًۜ وَسَيَجْزِي اللّٰهُ الشَّاكِر۪ينَ', 'Muhammed, ancak bir peygamberdir. Ondan önce de peygamberler gelip geçmiştir. Şimdi o ölür ya da öldürülürse, gerisin geriye (eski dininize) mi döneceksiniz? Kim (böyle) geri dönerse, Allah´a hiçbir şekilde zarar vermiş olmayacaktır. Allah, şükredenleri mükâfatlandıracaktır.', 4, 67, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(438, 145, 3, 'Âl-i İmrân', 'وَمَا كَانَ لِنَفْسٍ اَنْ تَمُوتَ اِلَّا بِاِذْنِ اللّٰهِ كِتَاباً مُؤَجَّلاًۜ وَمَنْ يُرِدْ ثَوَابَ الدُّنْيَا نُؤْتِه۪ مِنْهَاۚ وَمَنْ يُرِدْ ثَوَابَ الْاٰخِرَةِ نُؤْتِه۪ مِنْهَاۜ وَسَنَجْزِي الشَّاكِر۪ينَ', 'Hiçbir kimse yok ki, ölümü Allah´ın iznine bağlı olmasın. (Ölüm), belli bir süreye göre yazılmıştır. Her kim, dünya nimetini isterse, kendisine ondan veririz; kim de ahiret sevabını isterse, ona da bundan veririz. Biz şükredenleri mükâfatlandıracağız.', 4, 67, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(439, 146, 3, 'Âl-i İmrân', 'وَكَاَيِّنْ مِنْ نَبِيٍّ قَاتَلَۙ مَعَهُ رِبِّيُّونَ كَث۪يرٌۚ فَمَا وَهَنُوا لِمَٓا اَصَابَهُمْ ف۪ي سَب۪يلِ اللّٰهِ وَمَا ضَعُفُوا وَمَا اسْتَكَانُواۜ وَاللّٰهُ يُحِبُّ الصَّابِر۪ينَ', 'Nice peygamberler vardı ki, beraberinde birçok Allah erleri bulunduğu halde savaştılar da, bunlar, Allah yolunda başlarına gelenlerden dolayı gevşeklik ve zaaf göstermediler, boyun eğmediler. Allah sabredenleri sever.', 4, 67, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(440, 147, 3, 'Âl-i İmrân', 'وَمَا كَانَ قَوْلَهُمْ اِلَّٓا اَنْ قَالُوا رَبَّنَا اغْفِرْ لَنَا ذُنُوبَنَا وَاِسْرَافَنَا ف۪ٓي اَمْرِنَا وَثَبِّتْ اَقْدَامَنَا وَانْصُرْنَا عَلَى الْقَوْمِ الْكَافِر۪ينَ', 'Onların sözleri, sadece şöyle demekten ibaretti: Ey Rabbimiz! Günahlarımızı ve işimizdeki taşkınlığımızı bağışla; ayaklarımızı (yolunda) sabit kıl; kâfirler topluluğuna karşı bizi muzaffer kıl!', 4, 67, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(441, 148, 3, 'Âl-i İmrân', 'فَاٰتٰيهُمُ اللّٰهُ ثَوَابَ الدُّنْيَا وَحُسْنَ ثَوَابِ الْاٰخِرَةِۜ وَاللّٰهُ يُحِبُّ الْمُحْسِن۪ينَ۟', 'Allah da onlara dünya nimetini ve (daha da önemlisi,) ahiret sevabının güzelliğini verdi. Allah, iyi davrananları sever.', 4, 67, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(442, 149, 3, 'Âl-i İmrân', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُٓوا اِنْ تُط۪يعُوا الَّذ۪ينَ كَفَرُوا يَرُدُّوكُمْ عَلٰٓى اَعْقَابِكُمْ فَتَنْقَلِبُوا خَاسِر۪ينَ', 'Ey iman edenler! Eğer kâfirlere uyarsanız, gerisin geriye (eski dininize) döndürürler de, hüsrana uğrayanların durumuna düşersiniz.', 4, 68, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(443, 150, 3, 'Âl-i İmrân', 'بَلِ اللّٰهُ مَوْلٰيكُمْۚ وَهُوَ خَيْرُ النَّاصِر۪ينَ', 'Oysa sizin mevlânız Allah´tır ve O, yardımcıların en hayırlısıdır.', 4, 68, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(444, 151, 3, 'Âl-i İmrân', 'سَنُلْق۪ي ف۪ي قُلُوبِ الَّذ۪ينَ كَفَرُوا الرُّعْبَ بِمَٓا اَشْرَكُوا بِاللّٰهِ مَا لَمْ يُنَزِّلْ بِه۪ سُلْطَاناًۚ وَمَأْوٰيهُمُ النَّارُۜ وَبِئْسَ مَثْوَى الظَّالِم۪ينَ', 'Allah´ın, hakkında hiçbir delil indirmediği şeyleri O´na ortak koşmaları sebebiyle, kâfirlerin kalplerine yakında korku salacağız. Gidecekleri yer de cehennemdir. Zalimlerin varacağı yer ne kötüdür!', 4, 68, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(445, 152, 3, 'Âl-i İmrân', 'وَلَقَدْ صَدَقَكُمُ اللّٰهُ وَعْدَهُٓ اِذْ تَحُسُّونَهُمْ بِاِذْنِه۪ۚ حَتّٰٓى اِذَا فَشِلْتُمْ وَتَنَازَعْتُمْ فِي الْاَمْرِ وَعَصَيْتُمْ مِنْ بَعْدِ مَٓا اَرٰيكُمْ مَا تُحِبُّونَۜ مِنْكُمْ مَنْ يُر۪يدُ الدُّنْيَا وَمِنْكُمْ مَنْ يُر۪يدُ الْاٰخِرَةَۚ ثُمَّ صَرَفَكُمْ عَنْهُمْ لِيَبْتَلِيَكُمْۚ وَلَقَدْ عَفَا عَنْكُمْۜ وَاللّٰهُ ذُوفَضْلٍ عَلَى الْمُؤْمِن۪ينَ', 'Siz Allah´ın izni ile düşmanlarınızı öldürürken, Allah, size olan vâdini yerine getirmiştir. Nihayet, öyle bir an geldi ki, Allah arzuladığınızı (galibiyeti) size gösterdikten sonra zaafa düştünüz; (Peygamberin verdiği) emir konusunda tartışmaya kalkıştınız ve âsi oldunuz. Dünyayı isteyeniniz de vardı, ahireti isteyeniniz de vardı. Sonra Allah, denemek için sizi onlardan (onları mağlup etmekten) alıkoydu. Ve andolsun sizi bağışladı. Zaten Allah, müminlere karşı çok lütufkârdır.', 4, 68, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(446, 153, 3, 'Âl-i İmrân', 'اِذْ تُصْعِدُونَ وَلَا تَلْوُ۫نَ عَلٰٓى اَحَدٍ وَالرَّسُولُ يَدْعُوكُمْ ف۪ٓي اُخْرٰيكُمْ فَاَثَابَكُمْ غَماًّ بِغَمٍّ لِكَيْلَا تَحْزَنُوا عَلٰى مَا فَاتَكُمْ وَلَا مَٓا اَصَابَكُمْۜ وَاللّٰهُ خَب۪يرٌ بِمَا تَعْمَلُونَ', 'O zaman Peygamber arkanızdan sizi çağırdığı halde siz, durmadan (savaş alanından) uzaklaşıyor, hiç kimseye dönüp bakmıyordunuz. (Allah) size keder üstüne keder verdi ki, bundan dolayı gerek elinizden gidene, gerekse başınıza gelenlere üzülmeyesiniz. Allah yaptıklarınızdan haberdardır.', 4, 68, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(447, 154, 3, 'Âl-i İmrân', 'ثُمَّ اَنْزَلَ عَلَيْكُمْ مِنْ بَعْدِ الْغَمِّ اَمَنَةً نُعَاساً يَغْشٰى طَٓائِفَةً مِنْكُمْۙ وَطَٓائِفَةٌ قَدْ اَهَمَّتْهُمْ اَنْفُسُهُمْ يَظُنُّونَ بِاللّٰهِ غَيْرَ الْحَقِّ ظَنَّ الْجَاهِلِيَّةِۜ يَقُولُونَ هَلْ لَنَا مِنَ الْاَمْرِ مِنْ شَيْءٍۜ قُلْ اِنَّ الْاَمْرَ كُلَّهُ لِلّٰهِۜ يُخْفُونَ ف۪ٓي اَنْفُسِهِمْ مَا لَا يُبْدُونَ لَكَۜ يَقُولُونَ لَوْ كَانَ لَنَا مِنَ الْاَمْرِ شَيْءٌ مَا قُتِلْنَا هٰهُنَاۜ قُلْ لَوْ كُنْتُمْ ف۪ي بُيُوتِكُمْ لَبَرَزَ الَّذ۪ينَ كُتِبَ عَلَيْهِمُ الْقَتْلُ اِلٰى مَضَاجِعِهِمْۚ وَلِيَبْتَلِيَ اللّٰهُ مَا ف۪ي صُدُورِكُمْ وَلِيُمَحِّصَ مَا ف۪ي قُلُوبِكُمْۜ وَاللّٰهُ عَل۪يمٌ بِذَاتِ الصُّدُورِ', 'Sonra o kederin arkasından Allah size bir güven indirdi ki, (bu güvenin yol açtığı) uyuklama hali bir kısmınızı kaplıyordu. Kendi canlarının kaygısına düşmüş bir gurup da, Allah´a karşı haksız yere cahiliye devrindekine benzer düşüncelere kapılıyorlar, «Bu işten bize ne!» diyorlardı. De ki: İş (zafer, yardım, herşeyin karar ve buyruğu) tamamen Allah´a aittir. Onlar, sana açıklayamadıklarını içlerinde gizliyorlar. «Bu işten bize bir şey olsaydı, burada öldürülmezdik» diyorlar. Şöyle de: Evlerinizde kalmış olsaydınız bile, öldürülmesi takdir edilmiş olanlar, öldürülüp düşecekleri yerlere kendiliklerinden çıkıp giderlerdi. Allah, içinizdekileri yoklamak ve kalplerinizdekileri temizlemek için (böyle yaptı). Allah içinizde ne varsa hepsini bilir.', 4, 69, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(448, 155, 3, 'Âl-i İmrân', 'اِنَّ الَّذ۪ينَ تَوَلَّوْا مِنْكُمْ يَوْمَ الْتَقَى الْجَمْعَانِۙ اِنَّمَا اسْتَزَلَّهُمُ الشَّيْطَانُ بِبَعْضِ مَا كَسَبُواۚ وَلَقَدْ عَفَا اللّٰهُ عَنْهُمْۜ اِنَّ اللّٰهَ غَفُورٌ حَل۪يمٌ۟', '(Uhud´da) iki ordu karşılaştığı gün, sizi bırakıp gidenleri, sırf işledikleri bazı hatalar yüzünden şeytan (yerlerinden) kaydırmıştı. Yine de Allah onları affetti. Çünkü Allah, çok bağışlayıcıdır, halîmdir.', 4, 69, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(449, 156, 3, 'Âl-i İmrân', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا لَا تَكُونُوا كَالَّذ۪ينَ كَفَرُوا وَقَالُوا لِاِخْوَانِهِمْ اِذَا ضَرَبُوا فِي الْاَرْضِ اَوْ كَانُوا غُزًّى لَوْ كَانُوا عِنْدَنَا مَا مَاتُوا وَمَا قُتِلُواۚ لِيَجْعَلَ اللّٰهُ ذٰلِكَ حَسْرَةً ف۪ي قُلُوبِهِمْۜ وَاللّٰهُ يُحْـي۪ وَيُم۪يتُۜ وَاللّٰهُ بِمَا تَعْمَلُونَ بَص۪يرٌ', 'Ey iman edenler! Sizler, inkâr edenler ve yeryüzünde sefere çıkan veya savaşan kardeşleri hakkında: «Eğer bizim yanımızda kalsalardı ölmezler, öldürülmezlerdi» diyenler gibi olmayın. Allah bu kanaatı onların kalplerine (kaybettikleri yakınları için onulmaz) bir hasret (yarası) olarak koydu. Canı veren de alan da Allah´tır. Allah, yaptıklarınızı hakkıyla görür.', 4, 69, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(450, 157, 3, 'Âl-i İmrân', 'وَلَئِنْ قُتِلْتُمْ ف۪ي سَب۪يلِ اللّٰهِ اَوْ مُتُّمْ لَمَغْفِرَةٌ مِنَ اللّٰهِ وَرَحْمَةٌ خَيْرٌ مِمَّا يَجْمَعُونَ', 'Eğer Allah yolunda öldürülür ya da ölürseniz, şunu bilin ki, Allah´ın mağfireti ve rahmeti onların topladıkları bütün şeylerden daha hayırlıdır.', 4, 69, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(451, 158, 3, 'Âl-i İmrân', 'وَلَئِنْ مُتُّمْ اَوْ قُتِلْتُمْ لَاِلَى اللّٰهِ تُحْشَرُونَ', 'Andolsun, ölseniz de öldürülseniz de Allah´ın huzurunda toplanacaksınız.', 4, 70, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(452, 159, 3, 'Âl-i İmrân', 'فَبِمَا رَحْمَةٍ مِنَ اللّٰهِ لِنْتَ لَهُمْۚ وَلَوْ كُنْتَ فَظًّا غَل۪يظَ الْقَلْبِ لَانْفَضُّوا مِنْ حَوْلِكَۖ فَاعْفُ عَنْهُمْ وَاسْتَغْفِرْ لَهُمْ وَشَاوِرْهُمْ فِي الْاَمْرِۚ فَاِذَا عَزَمْتَ فَتَوَكَّلْ عَلَى اللّٰهِۜ اِنَّ اللّٰهَ يُحِبُّ الْمُتَوَكِّل۪ينَ', 'O vakit Allah´tan bir rahmet ile onlara yumuşak davrandın! Şayet sen kaba, katı yürekli olsaydın, hiç şüphesiz, etrafından dağılıp giderlerdi. Şu halde onları affet; bağışlanmaları için dua et; iş hakkında onlara danış. Kararını verdiğin zaman da artık Allah´a dayanıp güven. Çünkü Allah, kendisine dayanıp güvenenleri sever.', 4, 70, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(453, 160, 3, 'Âl-i İmrân', 'اِنْ يَنْصُرْكُمُ اللّٰهُ فَلَا غَالِبَ لَكُمْۚ وَاِنْ يَخْذُلْكُمْ فَمَنْ ذَا الَّذ۪ي يَنْصُرُكُمْ مِنْ بَعْدِه۪ۜ وَعَلَى اللّٰهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُونَ', 'Allah size yardım ederse, artık size üstün gelecek hiç kimse yoktur. Eğer sizi bırakıverirse, ondan sonra size kim yardım eder? Müminler ancak Allah´a güvenip dayanmalıdırlar.', 4, 70, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(454, 161, 3, 'Âl-i İmrân', 'وَمَا كَانَ لِنَبِيٍّ اَنْ يَغُلَّۜ وَمَنْ يَغْلُلْ يَأْتِ بِمَا غَلَّ يَوْمَ الْقِيٰمَةِۚ ثُمَّ تُوَفّٰى كُلُّ نَفْسٍ مَا كَسَبَتْ وَهُمْ لَا يُظْلَمُونَ', 'Bir peygambere, emanete hıyanet yaraşmaz. Kim emanete (devlet malına) hıyanet ederse, kıyamet günü, hainlik ettiği şeyin günahı boynuna asılı olarak gelir. Sonra herkese -asla haksızlığa uğratılmaksızın-kazandığı tastamam verilir.', 4, 70, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(455, 162, 3, 'Âl-i İmrân', 'اَفَمَنِ اتَّبَعَ رِضْوَانَ اللّٰهِ كَمَنْ بَٓاءَ بِسَخَطٍ مِنَ اللّٰهِ وَمَأْوٰيهُ جَهَنَّمُۜ وَبِئْسَ الْمَص۪يرُ', 'Allah´ın hoşnutluğunu gözetenle Allah´ın hışmına uğrayan bir olur mu hiç? Berikisinin yeri cehennemdir. Cehennem ise ne kötü bir varış noktasıdır.', 4, 70, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(456, 163, 3, 'Âl-i İmrân', 'هُمْ دَرَجَاتٌ عِنْدَ اللّٰهِۜ وَاللّٰهُ بَص۪يرٌ بِمَا يَعْمَلُونَ۟', 'Onlar Allah katında derece derecedirler. Allah onların yaptıklarını görmektedir.', 4, 70, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(457, 164, 3, 'Âl-i İmrân', 'لَقَدْ مَنَّ اللّٰهُ عَلَى الْمُؤْمِن۪ينَ اِذْ بَعَثَ ف۪يهِمْ رَسُولاً مِنْ اَنْفُسِهِمْ يَتْلُوا عَلَيْهِمْ اٰيَاتِه۪ وَيُزَكّ۪يهِمْ وَيُعَلِّمُهُمُ الْكِتَابَ وَالْحِكْمَةَۚ وَاِنْ كَانُوا مِنْ قَبْلُ لَف۪ي ضَلَالٍ مُب۪ينٍ', 'Andolsun ki içlerinden, kendilerine Allah´ın âyetlerini okuyan, (kötülüklerden ve inkârdan) kendilerini temizleyen, kendilerine Kitap ve hikmeti öğreten bir Peygamber göndermekle Allah, müminlere büyük bir lütufta bulunmuştur. Halbuki daha önce onlar apaçık bir sapıklık içinde idiler.', 4, 70, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(458, 165, 3, 'Âl-i İmrân', 'اَوَلَمَّٓا اَصَابَتْكُمْ مُص۪يبَةٌ قَدْ اَصَبْتُمْ مِثْلَيْهَاۙ قُلْتُمْ اَنّٰى هٰذَاۜ قُلْ هُوَ مِنْ عِنْدِ اَنْفُسِكُمْۜ اِنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌ', '(Bedir´de) iki katını (düşmanınızın) başına getirdiğiniz bir musibet, (Uhud´da) kendi başınıza geldiği için mi «Bu nasıl oluyor!» dediniz? De ki: O, kendi kusurunuzdandır. Şüphesiz Allah´ın her şeye gücü yeter.', 4, 70, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(459, 166, 3, 'Âl-i İmrân', 'وَمَٓا اَصَابَكُمْ يَوْمَ الْتَقَى الْجَمْعَانِ فَبِاِذْنِ اللّٰهِ وَلِيَعْلَمَ الْمُؤْمِن۪ينَۙ', '(166-167) İki birliğin karşılaştığı gün sizin başınıza gelenler, ancak Allah´ın dilemesiyle olmuştur ki, bu da, müminleri ayırdetmesi ve münafıkları ortaya çıkarması için idi. Bunlara: «Gelin, Allah yolunda çarpışın; ya da savunma yapın» denildiği zaman, «Harbetmeyi bilseydik, elbette sizin peşinizden gelirdik» dediler. Onlar o gün, imandan çok, kâfirliğe yakın idiler. Ağızlarıyla, kalplerinde olmayanı söylüyorlardı. Halbuki Allah, onların içlerinde gizlediklerini daha iyi bilir.', 4, 71, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(460, 167, 3, 'Âl-i İmrân', 'وَلِيَعْلَمَ الَّذ۪ينَ نَافَقُواۚ وَق۪يلَ لَهُمْ تَعَالَوْا قَاتِلُوا ف۪ي سَب۪يلِ اللّٰهِ اَوِ ادْفَعُواۜ قَالُوا لَوْ نَعْلَمُ قِتَالاً لَاتَّبَعْنَاكُمْۜ هُمْ لِلْكُفْرِ يَوْمَئِذٍ اَقْرَبُ مِنْهُمْ لِلْا۪يمَانِۚ يَقُولُونَ بِاَفْوَاهِهِمْ مَا لَيْسَ ف۪ي قُلُوبِهِمْۜ وَاللّٰهُ اَعْلَمُ بِمَا يَكْتُمُونَۚ', '(166-167) İki birliğin karşılaştığı gün sizin başınıza gelenler, ancak Allah´ın dilemesiyle olmuştur ki, bu da, müminleri ayırdetmesi ve münafıkları ortaya çıkarması için idi. Bunlara: «Gelin, Allah yolunda çarpışın; ya da savunma yapın» denildiği zaman, «Harbetmeyi bilseydik, elbette sizin peşinizden gelirdik» dediler. Onlar o gün, imandan çok, kâfirliğe yakın idiler. Ağızlarıyla, kalplerinde olmayanı söylüyorlardı. Halbuki Allah, onların içlerinde gizlediklerini daha iyi bilir.', 4, 71, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(461, 168, 3, 'Âl-i İmrân', 'اَلَّذ۪ينَ قَالُوا لِاِخْوَانِهِمْ وَقَعَدُوا لَوْ اَطَاعُونَا مَا قُتِلُواۜ قُلْ فَادْرَؤُ۫ا عَنْ اَنْفُسِكُمُ الْمَوْتَ اِنْ كُنْتُمْ صَادِق۪ينَ', '(Evlerinde) oturup da kardeşleri hakkında: «Bize uysalardı öldürülmezlerdi» diyenlere, «Eğer doğru sözlü insanlar iseniz, canlarınızı ölümden kurtarın bakalım!» de.', 4, 71, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(462, 169, 3, 'Âl-i İmrân', 'وَلَا تَحْسَبَنَّ الَّذ۪ينَ قُتِلُوا ف۪ي سَب۪يلِ اللّٰهِ اَمْوَاتاًۜ بَلْ اَحْيَٓاءٌ عِنْدَ رَبِّهِمْ يُرْزَقُونَۙ', '(169-170) Allah yolunda öldürülenleri sakın ölü sanmayın. Bilakis onlar diridirler; Allah´ın, lütuf ve kereminden kendilerine verdikleri ile sevinçli bir halde Rableri yanında rızıklara mazhar olmaktadırlar. Arkalarından gelecek ve henüz kendilerine katılmamış olan şehit kardeşlerine de hiçbir keder ve korku bulunmadığı müjdesinin sevincini duymaktadırlar.', 4, 71, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(463, 170, 3, 'Âl-i İmrân', 'فَرِح۪ينَ بِمَٓا اٰتٰيهُمُ اللّٰهُ مِنْ فَضْلِه۪ۙ وَيَسْتَبْشِرُونَ بِالَّذ۪ينَ لَمْ يَلْحَقُوا بِهِمْ مِنْ خَلْفِهِمْۙ اَلَّا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَۢ', '(169-170) Allah yolunda öldürülenleri sakın ölü sanmayın. Bilakis onlar diridirler; Allah´ın, lütuf ve kereminden kendilerine verdikleri ile sevinçli bir halde Rableri yanında rızıklara mazhar olmaktadırlar. Arkalarından gelecek ve henüz kendilerine katılmamış olan şehit kardeşlerine de hiçbir keder ve korku bulunmadığı müjdesinin sevincini duymaktadırlar.', 4, 71, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(464, 171, 3, 'Âl-i İmrân', 'يَسْتَبْشِرُونَ بِنِعْمَةٍ مِنَ اللّٰهِ وَفَضْلٍۙ وَاَنَّ اللّٰهَ لَا يُض۪يعُ اَجْرَ الْمُؤْمِن۪ينَۚۛ ۟', 'Onlar, Allah´tan gelen nimet ve keremin; Allah´ın, müminlerin ecrini zayi etmeyeceği müjdesinin sevinci içindedirler.', 4, 71, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(465, 172, 3, 'Âl-i İmrân', 'اَلَّذ۪ينَ اسْتَجَابُوا لِلّٰهِ وَالرَّسُولِ مِنْ بَعْدِ مَٓا اَصَابَهُمُ الْقَرْحُۜۛ لِلَّذ۪ينَ اَحْسَنُوا مِنْهُمْ وَاتَّقَوْا اَجْرٌ عَظ۪يمٌۚ', 'Yara aldıktan sonra yine Allah´ın ve Peygamber´in çağrısına uyanlar (özellikle) bunların içlerinden iyilik yapanlar ve takvâ sahibi olanlar için pek büyük bir mükâfat vardır.', 4, 71, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(466, 173, 3, 'Âl-i İmrân', 'اَلَّذ۪ينَ قَالَ لَهُمُ النَّاسُ اِنَّ النَّاسَ قَدْ جَمَعُوا لَكُمْ فَاخْشَوْهُمْ فَزَادَهُمْ ا۪يمَاناًۗ وَقَالُوا حَسْبُنَا اللّٰهُ وَنِعْمَ الْوَك۪يلُ', 'Bir kısım insanlar, müminlere: «Düşmanlarınız olan insanlar, size karşı asker topladılar; aman sakının onlardan!» dediklerinde bu, onların imanlarını bir kat daha arttırdı ve «Allah bize yeter. O ne güzel vekîldir!» dediler.', 4, 71, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(467, 174, 3, 'Âl-i İmrân', 'فَانْقَلَبُوا بِنِعْمَةٍ مِنَ اللّٰهِ وَفَضْلٍ لَمْ يَمْسَسْهُمْ سُٓوءٌۙ وَاتَّبَعُوا رِضْوَانَ اللّٰهِۜ وَاللّٰهُ ذُوفَضْلٍ عَظ۪يمٍ', 'Bunun üzerine, kendilerine hiçbir fenalık dokunmadan, Allah´ın nimet ve keremiyle geri geldiler. Böylece Allah´ın rızasına uymuş oldular. Allah büyük kerem sahibidir.', 4, 72, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(468, 175, 3, 'Âl-i İmrân', 'اِنَّمَا ذٰلِكُمُ الشَّيْطَانُ يُخَوِّفُ اَوْلِيَٓاءَهُۖ فَلَا تَخَافُوهُمْ وَخَافُونِ اِنْ كُنْتُمْ مُؤْمِن۪ينَ', 'İşte o şeytan, ancak kendi dostlarını korkutur. Şu halde, eğer iman etmiş kimseler iseniz onlardan korkmayın, benden korkun.', 4, 72, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(469, 176, 3, 'Âl-i İmrân', 'وَلَا يَحْزُنْكَ الَّذ۪ينَ يُسَارِعُونَ فِي الْكُفْرِۚ اِنَّهُمْ لَنْ يَضُرُّوا اللّٰهَ شَيْـٔاًۜ يُر۪يدُ اللّٰهُ اَلَّا يَجْعَلَ لَهُمْ حَظًّا فِي الْاٰخِرَةِۚ وَلَهُمْ عَذَابٌ عَظ۪يمٌ', '(Resûlüm) İnkârda yarışanlar sana kaygı vermesin. Çünkü onlar, Allah´a hiçbir zarar veremezler. Allah onlara, ahiretten yana bir nasip vermemek istiyor. Onlar için çok büyük bir azap vardır.', 4, 72, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(470, 177, 3, 'Âl-i İmrân', 'اِنَّ الَّذ۪ينَ اشْتَرَوُا الْكُفْرَ بِالْا۪يمَانِ لَنْ يَضُرُّوا اللّٰهَ شَيْـٔاًۚ وَلَهُمْ عَذَابٌ اَل۪يمٌ', 'Şurası muhakkak ki, imanı verip inkârı alanlar, Allah´a hiçbir zarar veremezler. Onlar için elîm bir azap vardır.', 4, 72, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(471, 178, 3, 'Âl-i İmrân', 'وَلَا يَحْسَبَنَّ الَّذ۪ينَ كَفَرُٓوا اَنَّمَا نُمْل۪ي لَهُمْ خَيْرٌ لِاَنْفُسِهِمْۜ اِنَّمَا نُمْل۪ي لَهُمْ لِيَزْدَادُٓوا اِثْماًۚ وَلَهُمْ عَذَابٌ مُه۪ينٌ', 'İnkâr edenler sanmasınlar ki, kendilerine mühlet vermemiz onlar için daha hayırlıdır. Onlara ancak günahlarını arttırmaları için fırsat veriyoruz. Onlar için alçaltıcı bir azap vardır.', 4, 72, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(472, 179, 3, 'Âl-i İmrân', 'مَا كَانَ اللّٰهُ لِيَذَرَ الْمُؤْمِن۪ينَ عَلٰى مَٓا اَنْتُمْ عَلَيْهِ حَتّٰى يَم۪يزَ الْخَب۪يثَ مِنَ الطَّيِّبِۜ وَمَا كَانَ اللّٰهُ لِيُطْلِعَكُمْ عَلَى الْغَيْبِ وَلٰكِنَّ اللّٰهَ يَجْتَب۪ي مِنْ رُسُلِه۪ مَنْ يَشَٓاءُ فَاٰمِنُوا بِاللّٰهِ وَرُسُلِه۪ۚ وَاِنْ تُؤْمِنُوا وَتَتَّقُوا فَلَكُمْ اَجْرٌ عَظ۪يمٌ', 'Allah, müminleri (şu) bulunduğunuz durumda bırakacak değildir; sonunda murdarı temizden ayıracaktır. Bununla beraber Allah, size gaybı da bildirecek değildir. Fakat Allah, elçilerinden dilediğini ayırdeder. O halde Allah´a ve peygamberlerine iman edin. Eğer iman eder, takvâ sahibi olursanız sizin için de çok büyük bir ecir vardır.', 4, 72, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(473, 180, 3, 'Âl-i İmrân', 'وَلَا يَحْسَبَنَّ الَّذ۪ينَ يَبْخَلُونَ بِمَٓا اٰتٰيهُمُ اللّٰهُ مِنْ فَضْلِه۪ هُوَ خَيْراً لَهُمْۜ بَلْ هُوَ شَرٌّ لَهُمْۜ سَيُطَوَّقُونَ مَا بَخِلُوا بِه۪ يَوْمَ الْقِيٰمَةِۜ وَلِلّٰهِ م۪يرَاثُ السَّمٰوَاتِ وَالْاَرْضِۜ وَاللّٰهُ بِمَا تَعْمَلُونَ خَب۪يرٌ۟', 'Allah´ın, kereminden kendilerine verdiklerini (infakta) cimrilik gösterenler, sanmasınlar ki o, kendileri için hayırlıdır; tersine bu onlar için pek fenadır. Cimrilik ettikleri şey de kıyamet gününde boyunlarına dolanacaktır. Göklerin ve yerin mirası Allah´ındır. Allah bütün yaptıklarınızdan haberdardır.', 4, 72, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(474, 181, 3, 'Âl-i İmrân', 'لَقَدْ سَمِـعَ اللّٰهُ قَوْلَ الَّذ۪ينَ قَالُٓوا اِنَّ اللّٰهَ فَق۪يرٌ وَنَحْنُ اَغْنِيَٓاءُۢ سَنَكْتُبُ مَا قَالُوا وَقَتْلَهُمُ الْاَنْبِيَٓاءَ بِغَيْرِ حَقٍّۙ وَنَقُولُ ذُوقُوا عَذَابَ الْحَر۪يقِ', '«Gerçekten Allah fakir, biz ise zenginiz» diyenlerin sözünü andolsun ki Allah işitmiştir. Onların (bu) dediklerini, haksız yere peygamberleri öldürmeleri ile birlikte yazacağız ve diyeceğiz ki: Tadın o yakıcı azabı!', 4, 73, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(475, 182, 3, 'Âl-i İmrân', 'ذٰلِكَ بِمَا قَدَّمَتْ اَيْد۪يكُمْ وَاَنَّ اللّٰهَ لَيْسَ بِظَلَّامٍ لِلْعَب۪يدِۚ', 'Bu, dünyada iken kendi ellerinizle yapmış olduğunuzun karşılığıdır. Yoksa Allah kullarına zulmetmez.', 4, 73, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(476, 183, 3, 'Âl-i İmrân', 'اَلَّذ۪ينَ قَالُٓوا اِنَّ اللّٰهَ عَهِدَ اِلَيْنَٓا اَلَّا نُؤْمِنَ لِرَسُولٍ حَتّٰى يَأْتِيَنَا بِقُرْبَانٍ تَأْكُلُهُ النَّارُۜ قُلْ قَدْ جَٓاءَكُمْ رُسُلٌ مِنْ قَبْل۪ي بِالْبَيِّنَاتِ وَبِالَّذ۪ي قُلْتُمْ فَلِمَ قَتَلْتُمُوهُمْ اِنْ كُنْتُمْ صَادِق۪ينَ', '«Doğrusu Allah bize, (gökten inen) ateşin yiyeceği (yakıp kor edeceği) bir kurban getirmedikçe hiçbir peygambere inanmamamızı emretti» diyenlere şöyle de: Size, benden önce mucizelerle, (özellikle) dediğiniz (mucize) ile nice peygamberler geldi. Eğer doğru insanlar iseniz, ya onları niçin öldürdünüz?', 4, 73, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(477, 184, 3, 'Âl-i İmrân', 'فَاِنْ كَذَّبُوكَ فَقَدْ كُذِّبَ رُسُلٌ مِنْ قَبْلِكَ جَٓاؤُ۫ بِالْبَيِّنَاتِ وَالزُّبُرِ وَالْكِتَابِ الْمُن۪يرِ', '(Resûlüm!) Eğer seni yalancılıkla itham ettilerse (yadırgama); gerçekten, senden önce apaçık mucizeler, sahifeler ve aydınlatıcı kitap getiren nice peygamberler de yalancılıkla itham edildi.', 4, 73, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(478, 185, 3, 'Âl-i İmrân', 'كُلُّ نَفْسٍ ذَٓائِقَةُ الْمَوْتِۜ وَاِنَّمَا تُوَفَّوْنَ اُجُورَكُمْ يَوْمَ الْقِيٰمَةِۜ فَمَنْ زُحْزِحَ عَنِ النَّارِ وَاُدْخِلَ الْجَنَّةَ فَقَدْ فَازَۜ وَمَا الْحَيٰوةُ الدُّنْيَٓا اِلَّا مَتَاعُ الْغُرُورِ', 'Her canlı ölümü tadacaktır. Ve ancak kıyamet günü yaptıklarınızın karşılığı size tastamam verilecektir. Kim cehennemden uzaklaştırılıp cennete konursa o, gerçekten kurtuluşa ermiştir. Bu dünya hayatı ise aldatma metâından başka bir şey değildir.', 4, 73, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(479, 186, 3, 'Âl-i İmrân', 'لَتُبْلَوُنَّ ف۪ٓي اَمْوَالِكُمْ وَاَنْفُسِكُمْ وَلَتَسْمَعُنَّ مِنَ الَّذ۪ينَ اُو۫تُوا الْكِتَابَ مِنْ قَبْلِكُمْ وَمِنَ الَّذ۪ينَ اَشْرَكُٓوا اَذًى كَث۪يراًۜ وَاِنْ تَصْبِرُوا وَتَتَّقُوا فَاِنَّ ذٰلِكَ مِنْ عَزْمِ الْاُمُورِ', 'Andolsun ki, mallarınız ve canlarınız konusunda imtihana çekileceksiniz; sizden önce kendilerine kitap verilenlerden ve müşriklerden birçok üzücü sözler işiteceksiniz. Eğer sabreder ve takvâ gösterirseniz, muhakkak ki bu, (yapılacak) işlerin en değerlisidir.', 4, 73, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(480, 187, 3, 'Âl-i İmrân', 'وَاِذْ اَخَذَ اللّٰهُ م۪يثَاقَ الَّذ۪ينَ اُو۫تُوا الْكِتَابَ لَتُبَيِّنُنَّهُ لِلنَّاسِ وَلَا تَكْتُمُونَهُۘ فَنَبَذُوهُ وَرَٓاءَ ظُهُورِهِمْ وَاشْتَرَوْا بِه۪ ثَمَناً قَل۪يلاًۜ فَبِئْسَ مَا يَشْتَرُونَ', 'Allah, kendilerine kitap verilenlerden, «Onu mutlaka insanlara açıklayacaksınız, onu gizlemeyeceksiniz» diyerek söz almıştı. Onlar ise bunu kulak ardı ettiler, onu az bir dünyalığa değiştiler. Yaptıkları alış-veriş ne kadar kötü!', 4, 74, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(481, 188, 3, 'Âl-i İmrân', 'لَا تَحْسَبَنَّ الَّذ۪ينَ يَفْرَحُونَ بِمَٓا اَتَوْا وَيُحِبُّونَ اَنْ يُحْمَدُوا بِمَا لَمْ يَفْعَلُوا فَلَا تَحْسَبَنَّهُمْ بِمَفَازَةٍ مِنَ الْعَذَابِۚ وَلَهُمْ عَذَابٌ اَل۪يمٌ', 'Sanma ki ettiklerine sevinen, yapmadıkları ile övülmek isteyenler, evet, sanma ki onlar azaptan kurtulacaklardır. Onlar için elem verici bir azap vardır.', 4, 74, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(482, 189, 3, 'Âl-i İmrân', 'وَلِلّٰهِ مُلْكُ السَّمٰوَاتِ وَالْاَرْضِۜ وَاللّٰهُ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌ۟', 'Göklerin ve yerin hükümranlığı Allah´ındır. Allah´ın her şeye gücü yeter.', 4, 74, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(483, 190, 3, 'Âl-i İmrân', 'اِنَّ ف۪ي خَلْقِ السَّمٰوَاتِ وَالْاَرْضِ وَاخْتِلَافِ الَّيْلِ وَالنَّهَارِ لَاٰيَاتٍ لِاُو۬لِي الْاَلْبَابِۚ', 'Göklerin ve yerin yaratılışında, gece ile gündüzün birbiri ardınca gelip gidişinde aklıselim sahipleri için gerçekten açık ibretler vardır.', 4, 74, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(484, 191, 3, 'Âl-i İmrân', 'اَلَّذ۪ينَ يَذْكُرُونَ اللّٰهَ قِيَاماً وَقُعُوداً وَعَلٰى جُنُوبِهِمْ وَيَتَفَكَّرُونَ ف۪ي خَلْقِ السَّمٰوَاتِ وَالْاَرْضِۚ رَبَّنَا مَا خَلَقْتَ هٰذَا بَاطِلاًۚ سُبْحَانَكَ فَقِنَا عَذَابَ النَّارِ', 'Onlar, ayakta dururken, otururken, yanları üzerine yatarken (her vakit) Allah´ı anarlar, göklerin ve yerin yaratılışı hakkında derin derin düşünürler (ve şöyle derler:) Rabbimiz! Sen bunu boşuna yaratmadın. Seni tesbih ederiz. Bizi cehennem azabından koru!', 4, 74, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(485, 192, 3, 'Âl-i İmrân', 'رَبَّنَٓا اِنَّكَ مَنْ تُدْخِلِ النَّارَ فَقَدْ اَخْزَيْتَهُۜ وَمَا لِلظَّالِم۪ينَ مِنْ اَنْصَارٍ', 'Ey Rabbimiz! Doğrusu sen, kimi cehenneme koyarsan, artık onu rüsvay etmişsindir. Zalimlerin hiç yardımcıları yoktur.', 4, 74, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(486, 193, 3, 'Âl-i İmrân', 'رَبَّنَٓا اِنَّـنَا سَمِعْنَا مُنَادِياً يُنَاد۪ي لِلْا۪يمَانِ اَنْ اٰمِنُوا بِرَبِّكُمْ فَاٰمَنَّاۗ رَبَّنَا فَاغْفِرْ لَنَا ذُنُوبَنَا وَكَفِّرْ عَنَّا سَيِّـَٔاتِنَا وَتَوَفَّـنَا مَعَ الْاَبْرَارِۚ', 'Ey Rabbimiz! Gerçek şu ki biz, «Rabbinize inanın!» diye imana çağıran bir davetçiyi (Peygamber´i, Kur´an´ı) işittik, hemen iman ettik. Artık bizim günahlarımızı bağışla, kötülüklerimizi ört, ruhumuzu iyilerle beraber al, ey Rabbimiz!', 4, 74, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(487, 194, 3, 'Âl-i İmrân', 'رَبَّنَا وَاٰتِنَا مَا وَعَدْتَنَا عَلٰى رُسُلِكَ وَلَا تُخْزِنَا يَوْمَ الْقِيٰمَةِۜ اِنَّكَ لَا تُخْلِفُ الْم۪يعَادَ', 'Rabbimiz! Bize, peygamberlerin vasıtasıyla vâdettiklerini de ikram et ve kıyamet gününde bizi rezil-rüsvay etme; şüphesiz sen vâdinden caymazsın!', 4, 74, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(488, 195, 3, 'Âl-i İmrân', 'فَاسْتَجَابَ لَهُمْ رَبُّهُمْ اَنّ۪ي لَٓا اُض۪يعُ عَمَلَ عَامِلٍ مِنْكُمْ مِنْ ذَكَرٍ اَوْ اُنْثٰىۚ بَعْضُكُمْ مِنْ بَعْضٍۚ فَالَّذ۪ينَ هَاجَرُوا وَاُخْرِجُوا مِنْ دِيَارِهِمْ وَاُو۫ذُوا ف۪ي سَب۪يل۪ي وَقَاتَلُوا وَقُتِلُوا لَاُكَفِّرَنَّ عَنْهُمْ سَيِّـَٔاتِهِمْ وَلَاُدْخِلَنَّهُمْ جَنَّاتٍ تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُۚ ثَوَاباً مِنْ عِنْدِ اللّٰهِۜ وَاللّٰهُ عِنْدَهُ حُسْنُ الثَّوَابِ', 'Bunun üzerine Rableri, onların dualarını kabul etti. (Dedi ki:) Ben, erkek olsun kadın olsun -ki hep birbirinizdensiniz- içinizden, çalışan hiçbir kimsenin yaptığını boşa çıkarmayacağım. Onlar ki, hicret ettiler, yurtlarından çıkarıldılar, benim yolumda eziyete uğradılar, çarpıştılar ve öldürüldüler; andolsun, ben de onların kötülüklerini örteceğim ve onları altlarından ırmaklar akan cennetlere koyacağım. Bu mükâfat, Allah tarafındandır. Allah; karşılığın güzeli O´nun katındadır.', 4, 75, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(489, 196, 3, 'Âl-i İmrân', 'لَا يَغُرَّنَّكَ تَقَلُّبُ الَّذ۪ينَ كَفَرُوا فِي الْبِلَادِۜ', 'İnkârcıların (refah içinde) diyar diyar dolaşması, sakın seni aldatmasın!', 4, 75, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(490, 197, 3, 'Âl-i İmrân', 'مَتَاعٌ قَل۪يلٌ ثُمَّ مَأْوٰيهُمْ جَهَنَّمُۜ وَبِئْسَ الْمِهَادُ', 'Azıcık bir menfaattır o. Sonra onların varacakları yer cehennemdir. O ne kötü varış yeridir!', 4, 75, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(491, 198, 3, 'Âl-i İmrân', 'لٰكِنِ الَّذ۪ينَ اتَّقَوْا رَبَّهُمْ لَهُمْ جَنَّاتٌ تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُ خَالِد۪ينَ ف۪يهَا نُزُلاً مِنْ عِنْدِ اللّٰهِۜ وَمَا عِنْدَ اللّٰهِ خَيْرٌ لِلْاَبْرَارِ', 'Fakat Rablerine karşı gelmekten sakınanlar için, Allah tarafından bir ikram olarak, altlarından ırmaklar akan, ebedî olarak kalacakları cennetler vardır. İyi kişiler için Allah katındaki (nimetler) daha hayırlıdır.', 4, 75, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(492, 199, 3, 'Âl-i İmrân', 'وَاِنَّ مِنْ اَهْلِ الْكِتَابِ لَمَنْ يُؤْمِنُ بِاللّٰهِ وَمَٓا اُنْزِلَ اِلَيْكُمْ وَمَٓا اُنْزِلَ اِلَيْهِمْ خَاشِع۪ينَ لِلّٰهِۙ لَا يَشْتَرُونَ بِاٰيَاتِ اللّٰهِ ثَمَناً قَل۪يلاًۜ اُو۬لٰٓئِكَ لَهُمْ اَجْرُهُمْ عِنْدَ رَبِّهِمْۜ اِنَّ اللّٰهَ سَر۪يعُ الْحِسَابِ', 'Ehl-i kitaptan öyleleri var ki, Allah´a, hem size indirilene, hem de kendilerine indirilene tam bir samimiyetle ve Allah´a boyun eğerek iman ederler. Allah´ın âyetlerini az bir paraya satmazlar. İşte onlar için Rableri katında ecirleri vardır. Şüphesiz Allah, hesabı çabuk olandır.', 4, 75, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(493, 200, 3, 'Âl-i İmrân', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا اصْبِرُوا وَصَابِرُوا وَرَابِطُوا وَاتَّقُوا اللّٰهَ لَعَلَّكُمْ تُفْلِحُونَ', 'Ey iman edenler! Sabredin; (düşman karşısında) sebat gösterin; (cihad için) hazırlıklı ve uyanık bulunun ve Allah´tan korkun ki başarıya erişebilesiniz.', 4, 75, 200, 14605, 'İmrân ailesi', 'Medine', 89),
(494, 1, 4, 'Nisa', 'يَٓا اَيُّهَا النَّاسُ اتَّقُوا رَبَّكُمُ الَّذ۪ي خَلَقَكُمْ مِنْ نَفْسٍ وَاحِدَةٍ وَخَلَقَ مِنْهَا زَوْجَهَا وَبَثَّ مِنْهُمَا رِجَالاً كَث۪يراً وَنِسَٓاءًۚ وَاتَّقُوا اللّٰهَ الَّذ۪ي تَسَٓاءَلُونَ بِه۪ وَالْاَرْحَامَۜ اِنَّ اللّٰهَ كَانَ عَلَيْكُمْ رَق۪يباً', 'Ey insanlar! Sizi bir tek nefisten yaratan ve ondan da eşini yaratan ve ikisinden birçok erkekler ve kadınlar üretip yayan Rabbinizden sakının. Adını kullanarak birbirinizden dilekte bulunduğunuz Allah´tan ve akrabalık haklarına riayetsizlikten de sakının. Şüphesiz Allah sizin üzerinizde gözetleyicidir.', 4, 76, 176, 15937, 'Kadınlar', 'Medine', 92),
(495, 2, 4, 'Nisa', 'وَاٰتُوا الْيَتَامٰٓى اَمْوَالَهُمْ وَلَا تَتَبَدَّلُوا الْخَب۪يثَ بِالطَّيِّبِۖ وَلَا تَأْكُلُٓوا اَمْوَالَهُمْ اِلٰٓى اَمْوَالِكُمْۜ اِنَّهُ كَانَ حُوباً كَب۪يراً', 'Yetimlere mallarını verin, temizi pis olanla değişmeyin, onların mallarını kendi mallarınıza katarak (kendi malınızmış gibi) yemeyin; çünkü bu, büyük bir günahtır.', 4, 76, 176, 15937, 'Kadınlar', 'Medine', 92),
(496, 3, 4, 'Nisa', 'وَاِنْ خِفْتُمْ اَلَّا تُـقْسِطُوا فِي الْيَتَامٰى فَانْكِحُوا مَا طَابَ لَكُمْ مِنَ النِّسَٓاءِ مَثْنٰى وَثُلٰثَ وَرُبَاعَۚ فَاِنْ خِفْتُمْ اَلَّا تَعْدِلُوا فَوَاحِدَةً اَوْ مَا مَلَكَتْ اَيْمَانُكُمْۜ ذٰلِكَ اَدْنٰٓى اَلَّا تَعُولُواۜ', 'Eğer (kendileriyle evlendiğiniz takdirde) yetimlerin haklarına riayet edememekten korkarsanız beğendiğiniz (veya size helâl olan) kadınlardan ikişer, üçer, dörder alın. Haksızlık yapmaktan korkarsanız bir tane alın; yahut da sahip olduğunuz (cariyeler) ile yetinin. Bu, adaletten ayrılmamanız için en uygun olanıdır.', 4, 76, 176, 15937, 'Kadınlar', 'Medine', 92),
(497, 4, 4, 'Nisa', 'وَاٰتُوا النِّسَٓاءَ صَدُقَاتِهِنَّ نِحْلَةًۜ فَاِنْ طِبْنَ لَكُمْ عَنْ شَيْءٍ مِنْهُ نَفْساً فَكُلُوهُ هَن۪ٓيـٔاً مَر۪ٓيـٔاً', 'Kadınlara mehirlerini gönül rızası ile (cömertçe) verin; eğer gönül hoşluğu ile o mehrin bir kısmını size bağışlarlarsa onu da afiyetle yeyin.', 4, 76, 176, 15937, 'Kadınlar', 'Medine', 92),
(498, 5, 4, 'Nisa', 'وَلَا تُؤْتُوا السُّفَـهَٓاءَ اَمْوَالَكُمُ الَّت۪ي جَعَلَ اللّٰهُ لَكُمْ قِيَاماً وَارْزُقُوهُمْ ف۪يهَا وَاكْسُوهُمْ وَقُولُوا لَهُمْ قَوْلاً مَعْرُوفاً', 'Allah´ın geçiminize dayanak kıldığı mallarınızı aklı ermezlere (reşit olmayanlara) vermeyin; o mallarla onları besleyin, giydirin ve onlara güzel söz söyleyin.', 4, 76, 176, 15937, 'Kadınlar', 'Medine', 92),
(499, 6, 4, 'Nisa', 'وَابْتَلُوا الْيَتَامٰى حَتّٰٓى اِذَا بَلَغُوا النِّكَاحَۚ فَاِنْ اٰنَسْتُمْ مِنْهُمْ رُشْداً فَادْفَعُٓوا اِلَيْهِمْ اَمْوَالَهُمْۚ وَلَا تَأْكُلُوهَٓا اِسْرَافاً وَبِدَاراً اَنْ يَكْـبَرُواۜ وَمَنْ كَانَ غَنِياًّ فَلْيَسْتَعْفِفْۚ وَمَنْ كَانَ فَق۪يراً فَلْيَأْكُلْ بِالْمَعْرُوفِۜ فَاِذَا دَفَعْتُمْ اِلَيْهِمْ اَمْوَالَهُمْ فَاَشْهِدُوا عَلَيْهِمْۜ وَكَفٰى بِاللّٰهِ حَس۪يباً', 'Evlilik çağına gelinceye kadar yetimleri (gözetip) deneyin, eğer onlarda akılca bir olgunlaşma görürseniz hemen mallarını kendilerine verin. Büyüyecekler (de geri alacaklar) diye o malları israf ile ve tez elden yemeyin. Zengin olan (veli) iffetli olmaya çalışsın, yoksul olan da (ihtiyaç ve emeğine) uygun olarak yesin. Mallarını kendilerine verdiğiniz zaman yanlarında şahit bulundurun. Hesap sorucu olarak da Allah yeter.', 4, 76, 176, 15937, 'Kadınlar', 'Medine', 92),
(500, 7, 4, 'Nisa', 'لِلرِّجَالِ نَص۪يبٌ مِمَّا تَرَكَ الْوَالِدَانِ وَالْاَقْرَبُونَۖ وَلِلنِّسَٓاءِ نَص۪يبٌ مِمَّا تَرَكَ الْوَالِدَانِ وَالْاَقْرَبُونَ مِمَّا قَلَّ مِنْهُ اَوْ كَثُرَۜ نَص۪يباً مَفْرُوضاً', 'Ana-babanın ve yakınların bıraktıklarından erkeklere bir pay vardır; ana-babanın ve yakınların bıraktıklarından kadınlara da bir pay vardır. Gerek azından, gerek çoğundan belli bir hisse ayrılmıştır.', 4, 77, 176, 15937, 'Kadınlar', 'Medine', 92),
(501, 8, 4, 'Nisa', 'وَاِذَا حَضَرَ الْقِسْمَةَ اُو۬لُوا الْقُرْبٰى وَالْيَتَامٰى وَالْمَسَاك۪ينُ فَارْزُقُوهُمْ مِنْهُ وَقُولُوا لَهُمْ قَوْلاً مَعْرُوفاً', '(Mirastan payı olmayan) yakınlar, yetimler ve yoksullar miras taksiminde hazır bulunursa bundan, onları da rızıklandırın ve onlara güzel söz söyleyin.', 4, 77, 176, 15937, 'Kadınlar', 'Medine', 92),
(502, 9, 4, 'Nisa', 'وَلْيَخْشَ الَّذ۪ينَ لَوْ تَرَكُوا مِنْ خَلْفِهِمْ ذُرِّيَّةً ضِعَافاً خَافُوا عَلَيْهِمْۖ فَلْيَتَّقُوا اللّٰهَ وَلْيَقُولُوا قَوْلاً سَد۪يداً', 'Geriye eli ermez, gücü yetmez çocuklar bıraktıkları takdirde (halleri ne olur) diye korkacak olanlar (yetimlere haksızlık etmekten) korkup titresinler; Allah´tan sakınsınlar ve doğru söz söylesinler.', 4, 77, 176, 15937, 'Kadınlar', 'Medine', 92),
(503, 10, 4, 'Nisa', 'اِنَّ الَّذ۪ينَ يَأْكُلُونَ اَمْوَالَ الْيَتَامٰى ظُلْماً اِنَّمَا يَأْكُلُونَ ف۪ي بُطُونِهِمْ نَاراًۜ وَسَيَصْلَوْنَ سَع۪يراً۟', 'Haksızlıkla yetimlerin mallarını yiyenler şüphesiz karınlarına ancak ateş tıkınmış olurlar; zaten onlar alevlenmiş ateşe gireceklerdir.', 4, 77, 176, 15937, 'Kadınlar', 'Medine', 92),
(504, 11, 4, 'Nisa', 'يُوص۪يكُمُ اللّٰهُ ف۪ٓي اَوْلَادِكُمْ لِلذَّكَرِ مِثْلُ حَظِّ الْاُنْثَيَيْنِۚ فَاِنْ كُنَّ نِسَٓاءً فَوْقَ اثْنَتَيْنِ فَلَهُنَّ ثُلُثَا مَا تَرَكَۚ وَاِنْ كَانَتْ وَاحِدَةً فَلَهَا النِّصْفُۜ وَلِاَبَوَيْهِ لِكُلِّ وَاحِدٍ مِنْهُمَا السُّدُسُ مِمَّا تَرَكَ اِنْ كَانَ لَهُ وَلَدٌۚ فَاِنْ لَمْ يَكُنْ لَهُ وَلَدٌ وَوَرِثَهُٓ اَبَوَاهُ فَلِاُمِّهِ الثُّلُثُۚ فَاِنْ كَانَ لَهُٓ اِخْوَةٌ فَلِاُمِّهِ السُّدُسُ مِنْ بَعْدِ وَصِيَّةٍ يُوص۪ي بِهَٓا اَوْ دَيْنٍۜ اٰبَٓاؤُ۬كُمْ وَاَبْنَٓاؤُ۬كُمْۚ لَا تَدْرُونَ اَيُّهُمْ اَقْرَبُ لَكُمْ نَفْعاًۚ فَر۪يضَةً مِنَ اللّٰهِۜ اِنَّ اللّٰهَ كَانَ عَل۪يماً حَك۪يماً', 'Allah size, çocuklarınız hakkında, erkeğe, kadının payının iki misli (miras vermenizi) emreder. (Çocuklar) ikiden fazla kadın iseler, ölünün bıraktığının üçte ikisi onlarındır. Eğer yalnız bir kadınsa yarısı onundur. Ölenin çocuğu varsa, ana-babasından her birinin mirastan altıda bir hissesi vardır. Eğer çocuğu yok da ana-babası ona vâris olmuş ise, anasına üçte bir (düşer). Eğer ölenin kardeşleri varsa, anasına altıda bir (düşer. Bütün bu paylar ölenin) yapacağı vasiyetten ve borçtan sonradır. Babalarınız ve oğullarınızdan hangisinin size, fayda bakımından daha yakın olduğunu bilemezsiniz. Bunlar Allah tarafından konmuş farzlardır (paylardır). Şüphesiz Allah ilim ve hikmet sahibidir.', 4, 77, 176, 15937, 'Kadınlar', 'Medine', 92),
(505, 12, 4, 'Nisa', 'وَلَكُمْ نِصْفُ مَا تَرَكَ اَزْوَاجُكُمْ اِنْ لَمْ يَكُنْ لَهُنَّ وَلَدٌۚ فَاِنْ كَانَ لَهُنَّ وَلَدٌ فَلَكُمُ الرُّبُعُ مِمَّا تَرَكْنَ مِنْ بَعْدِ وَصِيَّةٍ يُوص۪ينَ بِهَٓا اَوْ دَيْنٍۜ وَلَهُنَّ الرُّبُعُ مِمَّا تَرَكْتُمْ اِنْ لَمْ يَكُنْ لَكُمْ وَلَدٌۚ فَاِنْ كَانَ لَكُمْ وَلَدٌ فَلَهُنَّ الثُّمُنُ مِمَّا تَرَكْتُمْ مِنْ بَعْدِ وَصِيَّةٍ تُوصُونَ بِهَٓا اَوْ دَيْنٍۜ وَاِنْ كَانَ رَجُلٌ يُورَثُ كَلَالَةً اَوِ امْرَاَةٌ وَلَهُٓ اَخٌ اَوْ اُخْتٌ فَلِكُلِّ وَاحِدٍ مِنْهُمَا السُّدُسُۚ فَاِنْ كَانُٓوا اَكْثَرَ مِنْ ذٰلِكَ فَهُمْ شُرَكَٓاءُ فِي الثُّلُثِ مِنْ بَعْدِ وَصِيَّةٍ يُوصٰى بِهَٓا اَوْ دَيْنٍۙ غَيْرَ مُضَٓارٍّۚ وَصِيَّةً مِنَ اللّٰهِۜ وَاللّٰهُ عَل۪يمٌ حَل۪يمٌۜ', 'Yapacakları vasiyetten ve borçtan sonra eşlerinizin, eğer çocukları yoksa, bıraktıklarının yarısı sizindir. Çocukları varsa bıraktıklarının dörtte biri sizindir. Çocuğunuz yoksa, sizin de, yapacağınız vasiyetten ve borçtan sonra, bıraktığınızın dörtte biri onlarındır (zevcelerinizindir). Çocuğunuz varsa, bıraktığınızın sekizde biri onlarındır. Eğer bir erkek veya kadının, anababası ve çocukları bulunmadığı halde (kelâle şeklinde) malı mirasçılara kalırsa ve bir erkek yahut bir kızkardeşi varsa, her birine altıda bir düşer. Bundan fazla iseler üçte bire ortaktırlar. (Bu taksim) yapılacak vasiyetten ve borçtan sonra, kimse zarara uğramaksızın (yapılacak)tır. Bunlar Allah´tan size vasiyettir. Allah her şeyi hakkıyle bilendir, halîmdir.', 4, 78, 176, 15937, 'Kadınlar', 'Medine', 92),
(506, 13, 4, 'Nisa', 'تِلْكَ حُدُودُ اللّٰهِۜ وَمَنْ يُطِـعِ اللّٰهَ وَرَسُولَهُ يُدْخِلْهُ جَنَّاتٍ تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُ خَالِد۪ينَ ف۪يهَاۜ وَذٰلِكَ الْفَوْزُ الْعَظ۪يمُ', 'Bunlar, Allah´ın (koyduğu) sınırlardır. Kim Allah´a ve Peygamberine itaat ederse Allah onu, zemininden ırmaklar akan cennetlere koyacaktır; orada devamlı kalıcıdırlar; işte büyük kurtuluş budur.', 4, 78, 176, 15937, 'Kadınlar', 'Medine', 92),
(507, 14, 4, 'Nisa', 'وَمَنْ يَعْصِ اللّٰهَ وَرَسُولَهُ وَيَتَعَدَّ حُدُودَهُ يُدْخِلْهُ نَاراً خَالِداً ف۪يهَاۖ وَلَهُ عَذَابٌ مُه۪ينٌ۟', 'Kim Allah´a ve Peygamberine karşı isyan eder ve sınırlarını aşarsa Allah onu, devamlı kalacağı bir ateşe sokar ve onun için alçaltıcı bir azap vardır.', 4, 78, 176, 15937, 'Kadınlar', 'Medine', 92),
(508, 15, 4, 'Nisa', 'وَالّٰت۪ي يَأْت۪ينَ الْفَاحِشَةَ مِنْ نِسَٓائِكُمْ فَاسْتَشْهِدُوا عَلَيْهِنَّ اَرْبَعَةً مِنْكُمْۚ فَاِنْ شَهِدُوا فَاَمْسِكُوهُنَّ فِي الْبُيُوتِ حَتّٰى يَتَوَفّٰيهُنَّ الْمَوْتُ اَوْ يَجْعَلَ اللّٰهُ لَهُنَّ سَب۪يلاً', 'Kadınlarınızdan fuhuş yapanlara karşı aranızdan dört şahit getirin. Eğer şahitlik ederlerse, o kadınları ölüm alıp götürünceye yahut Allah onlara bir yol açıncaya kadar evlerde hapsedin.', 4, 79, 176, 15937, 'Kadınlar', 'Medine', 92),
(509, 16, 4, 'Nisa', 'وَالَّذَانِ يَأْتِيَانِهَا مِنْكُمْ فَاٰذُوهُمَاۚ فَاِنْ تَابَا وَاَصْلَحَا فَاَعْرِضُوا عَنْهُمَاۜ اِنَّ اللّٰهَ كَانَ تَـوَّاباً رَح۪يماً', 'İçinizden fuhuş yapan her iki tarafa ceza verin; eğer tevbe eder, uslanırlarsa artık onlara ceza verip eziyet etmekten vazgeçin; çünkü Allah tevbeleri çok kabul eden ve çok esirgeyendir.', 4, 79, 176, 15937, 'Kadınlar', 'Medine', 92),
(510, 17, 4, 'Nisa', 'اِنَّمَا التَّوْبَةُ عَلَى اللّٰهِ لِلَّذ۪ينَ يَعْمَلُونَ السُّٓوءَ بِجَهَالَةٍ ثُمَّ يَتُوبُونَ مِنْ قَر۪يبٍ فَاُو۬لٰٓئِكَ يَتُوبُ اللّٰهُ عَلَيْهِمْۜ وَكَانَ اللّٰهُ عَل۪يماً حَك۪يماً', 'Allah´ın kabul edeceği tevbe, ancak bilmeden kötülük edip de sonra tez elden tevbe edenlerin tevbesidir; işte Allah bunların tevbesini kabul eder; Allah her şeyi bilendir, hikmet sahibidir.', 4, 79, 176, 15937, 'Kadınlar', 'Medine', 92),
(511, 18, 4, 'Nisa', 'وَلَيْسَتِ التَّوْبَةُ لِلَّذ۪ينَ يَعْمَلُونَ السَّيِّـَٔاتِۚ حَتّٰٓى اِذَا حَضَرَ اَحَدَهُمُ الْمَوْتُ قَالَ اِنّ۪ي تُبْتُ الْـٰٔنَ وَلَا الَّذ۪ينَ يَمُوتُونَ وَهُمْ كُفَّارٌۜ اُو۬لٰٓئِكَ اَعْتَدْنَا لَهُمْ عَذَاباً اَل۪يماً', 'Yoksa kötülükleri yapıp yapıp da içlerinden birine ölüm gelip çatınca «Ben şimdi tevbe ettim» diyenler ile kâfir olarak ölenler için (kabul edilecek) tevbe yoktur. Onlar için acı bir azap hazırlamışızdır.', 4, 79, 176, 15937, 'Kadınlar', 'Medine', 92),
(512, 19, 4, 'Nisa', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا لَا يَحِلُّ لَكُمْ اَنْ تَرِثُوا النِّسَٓاءَ كَرْهاًۜ وَلَا تَعْضُلُوهُنَّ لِتَذْهَبُوا بِبَعْضِ مَٓا اٰتَيْتُمُوهُنَّ اِلَّٓا اَنْ يَأْت۪ينَ بِفَاحِشَةٍ مُبَيِّنَةٍۚ وَعَاشِرُوهُنَّ بِالْمَعْرُوفِۚ فَاِنْ كَرِهْتُمُوهُنَّ فَعَسٰٓى اَنْ تَكْرَهُوا شَيْـٔاً وَيَجْعَلَ اللّٰهُ ف۪يهِ خَيْراً كَث۪يراً', 'Ey iman edenler! Kadınlara zorla vâris olmanız size helâl değildir. Apaçık bir edepsizlik yapmadıkça, onlara verdiğinizin bir kısmını ele geçirmeniz için de kadınları sıkıştırmayın. Onlarla iyi geçinin. Eğer onlardan hoşlanmazsanız (biliniz ki) Allah´ın hakkınızda çok hayırlı kılacağı bir şeyden de hoşlanmamış olabilirsiniz.', 4, 79, 176, 15937, 'Kadınlar', 'Medine', 92),
(513, 20, 4, 'Nisa', 'وَاِنْ اَرَدْتُمُ اسْتِبْدَالَ زَوْجٍ مَكَانَ زَوْجٍۙ وَاٰتَيْتُمْ اِحْدٰيهُنَّ قِنْطَاراً فَلَا تَأْخُذُوا مِنْهُ شَيْـٔاًۜ اَتَأْخُذُونَهُ بُهْتَاناً وَاِثْماً مُب۪يناً', 'Eğer bir eşi bırakıp da yerine başka bir eş almak isterseniz, onlardan birine yüklerle mehir vermiş olsanız dahi ondan hiçbir şeyi geri almayın. Siz iftira ederek ve apaçık günah işleyerek onu geri alır mısınız?', 4, 80, 176, 15937, 'Kadınlar', 'Medine', 92),
(514, 21, 4, 'Nisa', 'وَكَيْفَ تَأْخُذُونَهُ وَقَدْ اَفْضٰى بَعْضُكُمْ اِلٰى بَعْضٍ وَاَخَذْنَ مِنْكُمْ م۪يثَاقاً غَل۪يظاً', 'Vaktiyle siz birbirinizle haşir-neşir olduğunuz ve onlar sizden sağlam bir teminat almış olduğu halde onu nasıl geri alırsınız!', 4, 80, 176, 15937, 'Kadınlar', 'Medine', 92),
(515, 22, 4, 'Nisa', 'وَلَا تَنْكِحُوا مَا نَكَحَ اٰبَٓاؤُ۬كُمْ مِنَ النِّسَٓاءِ اِلَّا مَا قَدْ سَلَفَۜ اِنَّهُ كَانَ فَاحِشَةً وَمَقْتاًۜ وَسَٓاءَ سَب۪يلاً۟', 'Geçmişte olanlar bir yana, babalarınızın evlendiği kadınlarla evlenmeyin; çünkü bu bir hayasızlıktır, iğrenç bir şeydir ve kötü bir yoldur.', 4, 80, 176, 15937, 'Kadınlar', 'Medine', 92),
(516, 23, 4, 'Nisa', 'حُرِّمَتْ عَلَيْكُمْ اُمَّهَاتُكُمْ وَبَنَاتُكُمْ وَاَخَوَاتُكُمْ وَعَمَّاتُكُمْ وَخَالَاتُكُمْ وَبَنَاتُ الْاَخِ وَبَنَاتُ الْاُخْتِ وَاُمَّهَاتُكُمُ الّٰت۪ٓي اَرْضَعْنَكُمْ وَاَخَوَاتُكُمْ مِنَ الرَّضَاعَةِ وَاُمَّهَاتُ نِسَٓائِكُمْ وَرَبَٓائِبُكُمُ الّٰت۪ي ف۪ي حُجُورِكُمْ مِنْ نِسَٓائِكُمُ الّٰت۪ي دَخَلْتُمْ بِهِنَّۘ فَاِنْ لَمْ تَكُونُوا دَخَلْتُمْ بِهِنَّ فَلَا جُنَاحَ عَلَيْكُمْۘ وَحَلَٓائِلُ اَبْنَٓائِكُمُ الَّذ۪ينَ مِنْ اَصْلَابِكُمْۙ وَاَنْ تَجْمَعُوا بَيْنَ الْاُخْتَيْنِ اِلَّا مَا قَدْ سَلَفَۜ اِنَّ اللّٰهَ كَانَ غَفُوراً رَح۪يماًۙ', 'Analarınız, kızlarınız, kızkardeşleriniz, halalarınız, teyzeleriniz, kardeş kızları, kızkardeş kızları, sizi emziren analarınız, süt bacılarınız, eşlerinizin anaları, kendileriyle birleştiğiniz eşlerinizden olup evlerinizde bulunan üvey kızlarınız size haram kılındı. Eğer onlarla (nikâhlanıp da) henüz birleşmemişseniz kızlarını almanızda size bir mahzur yoktur. Kendi sulbünüzden olan oğullarınızın eşleri ve iki kız kardeşi birden almak da size haram kılındı; ancak geçen geçmiştir. Allah çok bağışlayıcı ve esirgeyicidir.', 4, 80, 176, 15937, 'Kadınlar', 'Medine', 92),
(517, 24, 4, 'Nisa', 'وَالْمُحْصَنَاتُ مِنَ النِّسَٓاءِ اِلَّا مَا مَلَكَتْ اَيْمَانُكُمْۚ كِتَابَ اللّٰهِ عَلَيْكُمْۘ وَاُحِلَّ لَكُمْ مَا وَرَٓاءَ ذٰلِكُمْ اَنْ تَبْتَغُوا بِاَمْوَالِكُمْ مُحْصِن۪ينَ غَيْرَ مُسَافِح۪ينَۜ فَمَا اسْتَمْتَعْتُمْ بِه۪ مِنْهُنَّ فَاٰتُوهُنَّ اُجُورَهُنَّ فَر۪يضَةًۜ وَلَا جُنَاحَ عَلَيْكُمْ ف۪يمَا تَرَاضَيْتُمْ بِه۪ مِنْ بَعْدِ الْفَر۪يضَةِۜ اِنَّ اللّٰهَ كَانَ عَل۪يماً حَك۪يماً', '(Harp esiri olarak) sahip olduğunuz cariyeler müstesna, evli kadınlar da size haram kılındı. Allah´ın size emri budur. Bunlardan başkasını, namuslu olmak ve zina etmemek üzere mallarınızla (mehirlerini vererek) istemeniz size helâl kılındı. Onlardan faydalanmanıza karşılık kararlaştırılmış olan mehirlerini verin. Mehir kesiminden sonra (bir miktar indirim için) karşılıklı anlaşmanızda size günah yoktur. Şüphesiz Allah ilim ve hikmet sahibidir.', 5, 81, 176, 15937, 'Kadınlar', 'Medine', 92),
(518, 25, 4, 'Nisa', 'وَمَنْ لَمْ يَسْتَطِـعْ مِنْكُمْ طَوْلاً اَنْ يَنْكِـحَ الْمُحْصَنَاتِ الْمُؤْمِنَاتِ فَمِنْ مَا مَلَكَتْ اَيْمَانُكُمْ مِنْ فَتَيَاتِكُمُ الْمُؤْمِنَاتِۜ وَاللّٰهُ اَعْلَمُ بِا۪يمَانِكُمْۜ بَعْضُكُمْ مِنْ بَعْضٍۚ فَانْكِحُوهُنَّ بِاِذْنِ اَهْلِهِنَّ وَاٰتُوهُنَّ اُجُورَهُنَّ بِالْمَعْرُوفِ مُحْصَنَاتٍ غَيْرَ مُسَافِحَاتٍ وَلَا مُتَّخِذَاتِ اَخْدَانٍۚ فَاِذَٓا اُحْصِنَّ فَاِنْ اَتَيْنَ بِفَاحِشَةٍ فَعَلَيْهِنَّ نِصْفُ مَا عَلَى الْمُحْصَنَاتِ مِنَ الْعَذَابِۜ ذٰلِكَ لِمَنْ خَشِيَ الْعَنَتَ مِنْكُمْۜ وَاَنْ تَصْبِرُوا خَيْرٌ لَكُمْۜ وَاللّٰهُ غَفُورٌ رَح۪يمٌ۟', 'İçinizden, imanlı hür kadınlarla evlenmeye gücü yetmeyen kimse, ellerinizin altında bulunan imanlı genç kızlarınız (sayılan) cariyelerinizden alsın. Allah sizin imanınızı daha iyi bilmektedir. Hep aynı köktensiniz (insanlık bakımından aranızda fark yoktur). Öyle ise iffetli yaşamaları, zina etmemeleri ve gizli dost da tutmamaları şartı ve sahiplerinin izni ile onları (cariyeleri) nikâhlayıp alın, mehirlerini de normal miktarda verin. Evlendikten sonra bir fuhuş yaparlarsa onlara, hür kadınların cezasının yarısı (uygulanır). Bu (cariye ile evlenme izni), içinizden günaha düşmekten korkanlar içindir. Sabretmeniz ise sizin için daha hayırlıdır. Allah çok bağışlayıcı ve esirgeyicidir.', 5, 81, 176, 15937, 'Kadınlar', 'Medine', 92),
(519, 26, 4, 'Nisa', 'يُر۪يدُ اللّٰهُ لِيُبَيِّنَ لَكُمْ وَيَهْدِيَكُمْ سُنَنَ الَّذ۪ينَ مِنْ قَبْلِكُمْ وَيَتُوبَ عَلَيْكُمْۜ وَاللّٰهُ عَل۪يمٌ حَك۪يمٌ', 'Allah size (bilmediklerinizi) açıklamak ve sizi, sizden önceki (iyi) lerin yollarına iletmek ve sizin günahlarınızı bağışlamak istiyor. Allah hakkıyle bilicidir, yegâne hikmet sahibidir.', 5, 81, 176, 15937, 'Kadınlar', 'Medine', 92),
(520, 27, 4, 'Nisa', 'وَاللّٰهُ يُر۪يدُ اَنْ يَتُوبَ عَلَيْكُمْ وَيُر۪يدُ الَّذ۪ينَ يَتَّبِعُونَ الشَّهَوَاتِ اَنْ تَم۪يلُوا مَيْلاً عَظ۪يماً', 'Allah sizin tevbenizi kabul etmek ister; şehvetlerine uyanlar (kötü arzuların esiri olanlar) ise büsbütün yoldan çıkmanızı isterler.', 5, 82, 176, 15937, 'Kadınlar', 'Medine', 92),
(521, 28, 4, 'Nisa', 'يُر۪يدُ اللّٰهُ اَنْ يُخَفِّفَ عَنْكُمْۚ وَخُلِقَ الْاِنْسَانُ ضَع۪يفاً', 'Allah sizden (yükünüzü) hafifletmek ister; çünkü insan zayıf yaratılmıştır.', 5, 82, 176, 15937, 'Kadınlar', 'Medine', 92),
(522, 29, 4, 'Nisa', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا لَا تَأْكُلُٓوا اَمْوَالَكُمْ بَيْنَكُمْ بِالْبَاطِلِ اِلَّٓا اَنْ تَكُونَ تِجَارَةً عَنْ تَرَاضٍ مِنْكُمْ وَلَا تَقْتُلُٓوا اَنْفُسَكُمْۜ اِنَّ اللّٰهَ كَانَ بِكُمْ رَح۪يماً', 'Ey iman edenler! Karşılıklı rızaya dayanan ticaret olması hali müstesna, mallarınızı, bâtıl (haksız ve haram yollar) ile aranızda (alıp vererek) yemeyin. Ve kendinizi öldürmeyin. Şüphesiz Allah, sizi esirgeyecektir.', 5, 82, 176, 15937, 'Kadınlar', 'Medine', 92),
(523, 30, 4, 'Nisa', 'وَمَنْ يَفْعَلْ ذٰلِكَ عُدْوَاناً وَظُلْماً فَسَوْفَ نُصْل۪يهِ نَاراًۜ وَكَانَ ذٰلِكَ عَلَى اللّٰهِ يَس۪يراً', 'Kim düşmanlık ve haksızlık ile bunu (haram yemeyi veya öldürmeyi) yaparsa (bilsin ki) onu ateşe koyacağız; bu ise Allah´a çok kolaydır.', 5, 82, 176, 15937, 'Kadınlar', 'Medine', 92),
(524, 31, 4, 'Nisa', 'اِنْ تَجْتَنِبُوا كَـبَٓائِرَ مَا تُنْهَوْنَ عَنْهُ نُكَفِّرْ عَنْكُمْ سَيِّـَٔاتِكُمْ وَنُدْخِلْكُمْ مُدْخَلاً كَر۪يماً', 'Eğer yasaklandığınız büyük günahlardan kaçınırsanız, sizin küçük günahlarınızı örteriz ve sizi şerefli bir yere sokarız.', 5, 82, 176, 15937, 'Kadınlar', 'Medine', 92),
(525, 32, 4, 'Nisa', 'وَلَا تَتَمَنَّوْا مَا فَضَّلَ اللّٰهُ بِه۪ بَعْضَكُمْ عَلٰى بَعْضٍۜ لِلرِّجَالِ نَص۪يبٌ مِمَّا اكْتَسَبُوا وَلِلنِّسَٓاءِ نَص۪يبٌ مِمَّا اكْتَسَبْنَۜ وَسْـَٔلُوا اللّٰهَ مِنْ فَضْلِه۪ۜ اِنَّ اللّٰهَ كَانَ بِكُلِّ شَيْءٍ عَل۪يماً', 'Allah´ın sizi, birbirinizden üstün kıldığı şeyleri (başkasında olup da sizde olmayanı) hasretle arzu etmeyin. Erkeklerin de kazandıklarından nasipleri var, kadınların da kazandıklarından nasipleri var. Allah´tan lütfunu isteyin; şüphesiz Allah her şeyi bilmektedir.', 5, 82, 176, 15937, 'Kadınlar', 'Medine', 92),
(526, 33, 4, 'Nisa', 'وَلِكُلٍّ جَعَلْنَا مَوَالِيَ مِمَّا تَرَكَ الْوَالِدَانِ وَالْاَقْرَبُونَۜ وَالَّذ۪ينَ عَقَدَتْ اَيْمَانُكُمْ فَاٰتُوهُمْ نَص۪يبَهُمْۜ اِنَّ اللّٰهَ كَانَ عَلٰى كُلِّ شَيْءٍ شَه۪يداً۟', '(Erkek ve kadından) her biri için, ana, baba ve akrabanın bıraktığından (hisselerini alacak olan) vârisler kıldık. Yeminlerinizin bağladığı kimselere de paylarını verin. Çünkü Allah her şeyi görmektedir.', 5, 82, 176, 15937, 'Kadınlar', 'Medine', 92),
(527, 34, 4, 'Nisa', 'اَلرِّجَالُ قَوَّامُونَ عَلَى النِّسَٓاءِ بِمَا فَضَّلَ اللّٰهُ بَعْضَهُمْ عَلٰى بَعْضٍ وَبِمَٓا اَنْفَقُوا مِنْ اَمْوَالِهِمْۜ فَالصَّالِحَاتُ قَانِتَاتٌ حَافِظَاتٌ لِلْغَيْبِ بِمَا حَفِظَ اللّٰهُۜ وَالّٰت۪ي تَخَافُونَ نُشُوزَهُنَّ فَعِظُوهُنَّ وَاهْجُرُوهُنَّ فِي الْمَضَاجِعِ وَاضْرِبُوهُنَّۚ فَاِنْ اَطَعْنَكُمْ فَلَا تَبْغُوا عَلَيْهِنَّ سَب۪يلاًۜ اِنَّ اللّٰهَ كَانَ عَلِياًّ كَب۪يراً', 'Allah´ın insanlardan bir kısmını diğerlerine üstün kılması sebebiyle ve mallarından harcama yaptıkları için erkekler kadınların yöneticisi ve koruyucusudur. Onun için sâliha kadınlar itaatkârdır. Allah´ın kendilerini korumasına karşılık gizliyi (kimse görmese de namuslarını) koruyucudurlar. Baş kaldırmasından endişe ettiğiniz kadınlara öğüt verin, onları yataklarda yalnız bırakın ve (bunlarla yola gelmezlerse) dövün. Eğer size itaat ederlerse artık onların aleyhine başka bir yol aramayın; çünkü Allah yücedir, büyüktür.', 5, 83, 176, 15937, 'Kadınlar', 'Medine', 92),
(528, 35, 4, 'Nisa', 'وَاِنْ خِفْتُمْ شِقَاقَ بَيْنِهِمَا فَابْعَثُوا حَكَماً مِنْ اَهْلِه۪ وَحَكَماً مِنْ اَهْلِهَاۚ اِنْ يُر۪يدَٓا اِصْلَاحاً يُوَفِّقِ اللّٰهُ بَيْنَهُمَاۜ اِنَّ اللّٰهَ كَانَ عَل۪يماً خَب۪يراً', 'Eğer karı-kocanın aralarının açılmasından korkarsanız, erkeğin ailesinden bir hakem ve kadının ailesinden bir hakem gönderin. Bunlar barıştırmak isterlerse Allah aralarını bulur; şüphesiz Allah her şeyi bilen, her şeyden haberdar olandır.', 5, 83, 176, 15937, 'Kadınlar', 'Medine', 92);
INSERT INTO `Ayetler` (`id`, `AyetNo`, `SureNo`, `SureAdi`, `Metin`, `Mana`, `Cuzu`, `Sayfasi`, `AyetSayisi`, `HarfSayisi`, `AdAnlami`, `IndigiYer`, `InisSirasi`) VALUES
(529, 36, 4, 'Nisa', 'وَاعْبُدُوا اللّٰهَ وَلَا تُشْرِكُوا بِه۪ شَيْـٔاً وَبِالْوَالِدَيْنِ اِحْسَاناً وَبِذِي الْقُرْبٰى وَالْيَتَامٰى وَالْمَسَاك۪ينِ وَالْجَارِ ذِي الْقُرْبٰى وَالْجَارِ الْجُنُبِ وَالصَّاحِبِ بِالْجَنْبِ وَابْنِ السَّب۪يلِۙ وَمَا مَلَكَتْ اَيْمَانُكُمْۜ اِنَّ اللّٰهَ لَا يُحِبُّ مَنْ كَانَ مُخْتَالاً فَخُوراًۙ', 'Allah´a ibadet edin ve O´na hiçbir şeyi ortak koşmayın. Ana-babaya, akrabaya, yetimlere, yoksullara, yakın komşuya, uzak komşuya, yakın arkadaşa, yolcuya, ellerinizin altında bulunanlara (köle, cariye, hizmetçi ve benzerlerine) iyi davranın; Allah kendini beğenen ve daima böbürlenip duran kimseyi sevmez.', 5, 83, 176, 15937, 'Kadınlar', 'Medine', 92),
(530, 37, 4, 'Nisa', 'اَلَّذ۪ينَ يَبْخَلُونَ وَيَأْمُرُونَ النَّاسَ بِالْبُخْلِ وَيَكْتُمُونَ مَٓا اٰتٰيهُمُ اللّٰهُ مِنْ فَضْلِه۪ۜ وَاَعْتَدْنَا لِلْكَافِر۪ينَ عَذَاباً مُه۪يناًۚ', 'Bunlar cimrilik eden ve insanlara da cimriliği tavsiye eden, Allah´ın kendilerine lütfundan verdiğini gizleyen kimselerdir. Biz, kâfirler için alçaltıcı bir azap hazırladık.', 5, 83, 176, 15937, 'Kadınlar', 'Medine', 92),
(531, 38, 4, 'Nisa', 'وَالَّذ۪ينَ يُنْفِقُونَ اَمْوَالَهُمْ رِئَٓاءَ النَّاسِ وَلَا يُؤْمِنُونَ بِاللّٰهِ وَلَا بِالْيَوْمِ الْاٰخِرِۜ وَمَنْ يَكُنِ الشَّيْطَانُ لَهُ قَر۪يناً فَسَٓاءَ قَر۪يناً', 'Allah´a ve ahiret gününe inanmadıkları halde mallarını, insanlara gösteriş için sarfedenler de (ahirette azaba dûçâr olurlar). Şeytan bir kimseye arkadaş olursa, ne kötü bir arkadaştır o!', 5, 84, 176, 15937, 'Kadınlar', 'Medine', 92),
(532, 39, 4, 'Nisa', 'وَمَاذَا عَلَيْهِمْ لَوْ اٰمَنُوا بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَاَنْفَقُوا مِمَّا رَزَقَهُمُ اللّٰهُۜ وَكَانَ اللّٰهُ بِهِمْ عَل۪يماً', 'Allah´a ve ahiret gününe iman edip de Allah´ın kendilerine verdiğinden (O´nun yolunda) harcasalardı ne olurdu sanki! Allah onların durumunu hakkıyle bilmektedir.', 5, 84, 176, 15937, 'Kadınlar', 'Medine', 92),
(533, 40, 4, 'Nisa', 'اِنَّ اللّٰهَ لَا يَظْلِمُ مِثْقَالَ ذَرَّةٍۚ وَاِنْ تَكُ حَسَنَةً يُضَاعِفْهَا وَيُؤْتِ مِنْ لَدُنْهُ اَجْراً عَظ۪يماً', 'Şüphe yok ki Allah zerre kadar haksızlık etmez. (Kulun yaptığı iş, eğer bir kötülük ise, onun cezasını adaletle verir.) İyilik olursa onu katlar (kat kat arttırır), kendinden de büyük mükâfat verir.', 5, 84, 176, 15937, 'Kadınlar', 'Medine', 92),
(534, 41, 4, 'Nisa', 'فَكَيْفَ اِذَا جِئْنَا مِنْ كُلِّ اُمَّةٍ بِشَه۪يدٍ وَجِئْنَا بِكَ عَلٰى هٰٓؤُ۬لَٓاءِ شَه۪يداًۜ', 'Her bir ümmetten bir şahit getirdiğimiz ve seni de onlara şahit olarak gösterdiğimiz zaman halleri nice olacak!', 5, 84, 176, 15937, 'Kadınlar', 'Medine', 92),
(535, 42, 4, 'Nisa', 'يَوْمَئِذٍ يَوَدُّ الَّذ۪ينَ كَفَرُوا وَعَصَوُا الرَّسُولَ لَوْ تُسَوّٰى بِهِمُ الْاَرْضُۜ وَلَا يَكْتُمُونَ اللّٰهَ حَد۪يثاً۟', 'Küfür yoluna sapıp peygamberi dinlemeyenler o gün yerin dibine batırılmayı temenni ederler ve Allah´tan hiçbir haberi gizleyemezler.', 5, 84, 176, 15937, 'Kadınlar', 'Medine', 92),
(536, 43, 4, 'Nisa', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا لَا تَقْرَبُوا الصَّلٰوةَ وَاَنْتُمْ سُكَارٰى حَتّٰى تَعْلَمُوا مَا تَقُولُونَ وَلَا جُنُباً اِلَّا عَابِر۪ي سَب۪يلٍ حَتّٰى تَغْتَسِلُواۜ وَاِنْ كُنْتُمْ مَرْضٰٓى اَوْ عَلٰى سَفَرٍ اَوْ جَٓاءَ اَحَدٌ مِنْكُمْ مِنَ الْغَٓائِطِ اَوْ لٰمَسْتُمُ النِّسَٓاءَ فَلَمْ تَجِدُوا مَٓاءً فَتَيَمَّمُوا صَع۪يداً طَيِّباً فَامْسَحُوا بِوُجُوهِكُمْ وَاَيْد۪يكُمْۜ اِنَّ اللّٰهَ كَانَ عَفُواًّ غَفُوراً', 'Ey iman edenler! Siz sarhoş iken -ne söylediğinizi bilinceye kadar- cünüp iken de -yolcu olan müstesna- gusül edinceye kadar namaza yaklaşmayın. Eğer hasta olur veya bir yolculuk üzerinde bulunursanız, yahut sizden biriniz ayak yolundan gelirse, yahut kadınlara dokunup da (bu durumlarda) su bulamamışsanız o zaman temiz bir toprakla teyemmüm edin: Yüzlerinize ve ellerinize sürün. Şüphesiz Allah çok affedici ve bağışlayıcıdır.', 5, 84, 176, 15937, 'Kadınlar', 'Medine', 92),
(537, 44, 4, 'Nisa', 'اَلَمْ تَرَ اِلَى الَّذ۪ينَ اُو۫تُوا نَص۪يباً مِنَ الْكِتَابِ يَشْتَرُونَ الضَّلَالَةَ وَيُر۪يدُونَ اَنْ تَضِلُّوا السَّب۪يلَۜ', 'Kendilerine Kitap´tan nasip verilenlere baksana! Sapıklığı satın alıyorlar ve sizin de yoldan çıkmanızı istiyorlar!', 5, 84, 176, 15937, 'Kadınlar', 'Medine', 92),
(538, 45, 4, 'Nisa', 'وَاللّٰهُ اَعْلَمُ بِاَعْدَٓائِكُمْۜ وَكَفٰى بِاللّٰهِ وَلِياًّۗ وَكَفٰى بِاللّٰهِ نَص۪يراً', 'Allah düşmanlarınızı sizden daha iyi bilir. Gerçek bir dost olarak Allah yeter, bir yardımcı olarak da Allah kâfidir.', 5, 85, 176, 15937, 'Kadınlar', 'Medine', 92),
(539, 46, 4, 'Nisa', 'مِنَ الَّذ۪ينَ هَادُوا يُحَرِّفُونَ الْكَلِمَ عَنْ مَوَاضِعِه۪ وَيَقُولُونَ سَمِعْنَا وَعَصَيْنَا وَاسْمَعْ غَيْرَ مُسْمَعٍ وَرَاعِنَا لَياًّ بِاَلْسِنَتِهِمْ وَطَعْناً فِي الدّ۪ينِۜ وَلَوْ اَنَّهُمْ قَالُوا سَمِعْنَا وَاَطَعْنَا وَاسْمَعْ وَانْظُرْنَا لَكَانَ خَيْراً لَهُمْ وَاَقْوَمَۙ وَلٰكِنْ لَعَنَهُمُ اللّٰهُ بِكُفْرِهِمْ فَلَا يُؤْمِنُونَ اِلَّا قَل۪يلاً', 'Yahudilerden bir kısmı kelimeleri yerlerinden değiştirirler, dillerini eğerek, bükerek ve dine saldırarak (Peygambere karşı) «İşittik ve karşı geldik», «dinle, dinlemez olası», «râinâ» derler. Eğer onlar «İşittik, itaat ettik, dinle ve bizi gözet» deselerdi şüphesiz kendileri için daha hayırlı ve daha doğru olacaktı; fakat küfürleri (gerçeği kabul etmemeleri) sebebiyle Allah onları lânetlemiştir. Artık pek az inanırlar.', 5, 85, 176, 15937, 'Kadınlar', 'Medine', 92),
(540, 47, 4, 'Nisa', 'يَٓا اَيُّهَا الَّذ۪ينَ اُو۫تُوا الْكِتَابَ اٰمِنُوا بِمَا نَزَّلْنَا مُصَدِّقاً لِمَا مَعَكُمْ مِنْ قَبْلِ اَنْ نَطْمِسَ وُجُوهاً فَنَرُدَّهَا عَلٰٓى اَدْبَارِهَٓا اَوْ نَلْعَنَهُمْ كَمَا لَعَنَّٓا اَصْحَابَ السَّبْتِۜ وَكَانَ اَمْرُ اللّٰهِ مَفْعُولاً', 'Ey ehl-i kitap! Biz, birtakım yüzleri silip dümdüz ederek arkalarına çevirmeden, yahut onları, cumartesi adamları gibi lânetlemeden önce (davranarak), size gelenleri doğrulamak üzere indirdiğimize (Kitab´a) iman edin; Allah´ın emri mutlaka yerine gelecektir.', 5, 85, 176, 15937, 'Kadınlar', 'Medine', 92),
(541, 48, 4, 'Nisa', 'اِنَّ اللّٰهَ لَا يَغْفِرُ اَنْ يُشْرَكَ بِه۪ وَيَغْفِرُ مَا دُونَ ذٰلِكَ لِمَنْ يَشَٓاءُۚ وَمَنْ يُشْرِكْ بِاللّٰهِ فَقَدِ افْتَرٰٓى اِثْماً عَظ۪يماً', 'Allah, kendisine ortak koşulmasını asla bağışlamaz; bundan başkasını, (günahları) dilediği kimse için bağışlar. Allah´a ortak koşan kimse büyük bir günah (ile) iftira etmiş olur.', 5, 85, 176, 15937, 'Kadınlar', 'Medine', 92),
(542, 49, 4, 'Nisa', 'اَلَمْ تَرَ اِلَى الَّذ۪ينَ يُزَكُّونَ اَنْفُسَهُمْۜ بَلِ اللّٰهُ يُزَكّ۪ي مَنْ يَشَٓاءُ وَلَا يُظْلَمُونَ فَت۪يلاً', 'Kendilerini temize çıkaranlara ne dersin! Hayır, Allah dilediğini temize çıkarır ve hiç kimse kıl payı kadar haksızlık görmez.', 5, 85, 176, 15937, 'Kadınlar', 'Medine', 92),
(543, 50, 4, 'Nisa', 'اُنْظُرْ كَيْفَ يَفْتَرُونَ عَلَى اللّٰهِ الْكَذِبَۜ وَكَفٰى بِه۪ٓ اِثْماً مُب۪يناً۟', 'Bak, nasıl da Allah üzerine yalan uyduruyorlar; apaçık bir günah olarak bu (onlara) yeter!', 5, 85, 176, 15937, 'Kadınlar', 'Medine', 92),
(544, 51, 4, 'Nisa', 'اَلَمْ تَرَ اِلَى الَّذ۪ينَ اُو۫تُوا نَص۪يباً مِنَ الْكِتَابِ يُؤْمِنُونَ بِالْجِبْتِ وَالطَّاغُوتِ وَيَقُولُونَ لِلَّذ۪ينَ كَفَرُوا هٰٓؤُ۬لَٓاءِ اَهْدٰى مِنَ الَّذ۪ينَ اٰمَنُوا سَب۪يلاً', 'Kendilerine Kitap´tan nasip verilenleri görmedin mi? Putlara ve bâtıla (tanrılara) iman ediyorlar, sonra da kâfirler için: «Bunlar, Allah´a iman edenlerden daha doğru yoldadır» diyorlar!', 5, 85, 176, 15937, 'Kadınlar', 'Medine', 92),
(545, 52, 4, 'Nisa', 'اُو۬لٰٓئِكَ الَّذ۪ينَ لَعَنَهُمُ اللّٰهُۜ وَمَنْ يَلْعَنِ اللّٰهُ فَلَنْ تَجِدَ لَهُ نَص۪يراًۜ', 'Bunlar, Allah´ın lânetlediği kimselerdir; Allah´ın rahmetinden uzaklaştırdığı (lânetli) kimseye gerçek bir yardımcı bulamazsın.', 5, 86, 176, 15937, 'Kadınlar', 'Medine', 92),
(546, 53, 4, 'Nisa', 'اَمْ لَهُمْ نَص۪يبٌ مِنَ الْمُلْكِ فَاِذاً لَا يُؤْتُونَ النَّاسَ نَق۪يراًۙ', 'Yoksa onların mülkten (hükümranlıktan) bir nasipleri mi var? Öyle olsaydı insanlara çekirdek filizi (kadar bir şey bile) vermezlerdi.', 5, 86, 176, 15937, 'Kadınlar', 'Medine', 92),
(547, 54, 4, 'Nisa', 'اَمْ يَحْسُدُونَ النَّاسَ عَلٰى مَٓا اٰتٰيهُمُ اللّٰهُ مِنْ فَضْلِه۪ۚ فَقَدْ اٰتَيْنَٓا اٰلَ اِبْرٰه۪يمَ الْكِتَابَ وَالْحِكْمَةَ وَاٰتَيْنَاهُمْ مُلْكاً عَظ۪يماً', 'Yoksa onlar, Allah´ın lütfundan verdiği şeyler için insanlara hased mi ediyorlar? Oysa İbrahim soyuna Kitab´ı ve hikmeti verdik ve onlara büyük bir hükümranlık bahşettik.', 5, 86, 176, 15937, 'Kadınlar', 'Medine', 92),
(548, 55, 4, 'Nisa', 'فَمِنْهُمْ مَنْ اٰمَنَ بِه۪ وَمِنْهُمْ مَنْ صَدَّ عَنْهُۜ وَكَفٰى بِجَهَنَّمَ سَع۪يراً', 'Onlardan bir kısmı İbrahim´e inandı, kimi de ondan yüz çevirdi; (onlara) kavurucu bir ateş olarak cehennem yeter.', 5, 86, 176, 15937, 'Kadınlar', 'Medine', 92),
(549, 56, 4, 'Nisa', 'اِنَّ الَّذ۪ينَ كَفَرُوا بِاٰيَاتِنَا سَوْفَ نُصْل۪يهِمْ نَاراًۜ كُلَّمَا نَضِجَتْ جُلُودُهُمْ بَدَّلْنَاهُمْ جُلُوداً غَيْرَهَا لِيَذُوقُوا الْعَذَابَۜ اِنَّ اللّٰهَ كَانَ عَز۪يزاً حَك۪يماً', 'Şüphesiz âyetlerimizi inkâr edenleri gün gelecek bir ateşe sokacağız; onların derileri pişip acı duymaz hale geldikçe, derilerini başka derilerle değiştiririz ki acıyı duysunlar! Allah daima üstün ve hakîmdir.', 5, 86, 176, 15937, 'Kadınlar', 'Medine', 92),
(550, 57, 4, 'Nisa', 'وَالَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ سَنُدْخِلُهُمْ جَنَّاتٍ تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُ خَالِد۪ينَ ف۪يهَٓا اَبَداًۜ لَهُمْ ف۪يهَٓا اَزْوَاجٌ مُطَهَّرَةٌۘ وَنُدْخِلُهُمْ ظِلاًّ ظَل۪يلاً', 'İnanıp, iyi işler yapanları da, içinde ebediyen kalmak üzere girecekleri, zemininden ırmaklar akan cennetlere sokacağız. Orada onlar için tertemiz eşler vardır ve onları koyu (tatlı) bir gölgeye koyarız.', 5, 86, 176, 15937, 'Kadınlar', 'Medine', 92),
(551, 58, 4, 'Nisa', 'اِنَّ اللّٰهَ يَأْمُرُكُمْ اَنْ تُؤَدُّوا الْاَمَانَاتِ اِلٰٓى اَهْلِهَاۙ وَاِذَا حَكَمْتُمْ بَيْنَ النَّاسِ اَنْ تَحْكُمُوا بِالْعَدْلِۜ اِنَّ اللّٰهَ نِعِمَّا يَعِظُـكُمْ بِه۪ۜ اِنَّ اللّٰهَ كَانَ سَم۪يعاً بَص۪يراً', 'Allah size, mutlaka emanetleri ehli olanlara vermenizi ve insanlar arasında hükmettiğiniz zaman adaletle hükmetmenizi emreder. Allah size ne kadar güzel öğütler veriyor! Şüphesiz Allah her şeyi işitici, her şeyi görücüdür.', 5, 86, 176, 15937, 'Kadınlar', 'Medine', 92),
(552, 59, 4, 'Nisa', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُٓوا اَط۪يعُوا اللّٰهَ وَاَط۪يعُوا الرَّسُولَ وَاُو۬لِي الْاَمْرِ مِنْكُمْۚ فَاِنْ تَنَازَعْتُمْ ف۪ي شَيْءٍ فَرُدُّوهُ اِلَى اللّٰهِ وَالرَّسُولِ اِنْ كُنْتُمْ تُؤْمِنُونَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِۜ ذٰلِكَ خَيْرٌ وَاَحْسَنُ تَأْو۪يلاً۟', 'Ey iman edenler! Allah´a itaat edin. Peygamber´e ve sizden olan ülülemre (idarecilere) de itaat edin. Eğer bir hususta anlaşmazlığa düşerseniz -Allah´a ve ahirete gerçekten inanıyorsanız- onu Allah´a ve Resûl´e götürün (onların talimatına göre halledin); bu hem hayırlı, hem de netice bakımından daha güzeldir.', 5, 86, 176, 15937, 'Kadınlar', 'Medine', 92),
(553, 60, 4, 'Nisa', 'اَلَمْ تَرَ اِلَى الَّذ۪ينَ يَزْعُمُونَ اَنَّهُمْ اٰمَنُوا بِمَٓا اُنْزِلَ اِلَيْكَ وَمَٓا اُنْزِلَ مِنْ قَبْلِكَ يُر۪يدُونَ اَنْ يَتَحَاكَمُٓوا اِلَى الطَّاغُوتِ وَقَدْ اُمِرُٓوا اَنْ يَكْفُرُوا بِه۪ۜ وَيُر۪يدُ الشَّيْطَانُ اَنْ يُضِلَّهُمْ ضَلَالاً بَع۪يداً', 'Sana indirilene ve senden önce indirilenlere inandıklarını ileri sürenleri görmedin mi? Tâğut´a inanmamaları kendilerine emrolunduğu halde, Tâğut´un önünde muhakemeleşmek istiyorlar. Halbuki şeytan onları büsbütün saptırmak istiyor.', 5, 87, 176, 15937, 'Kadınlar', 'Medine', 92),
(554, 61, 4, 'Nisa', 'وَاِذَا ق۪يلَ لَهُمْ تَعَالَوْا اِلٰى مَٓا اَنْزَلَ اللّٰهُ وَاِلَى الرَّسُولِ رَاَيْتَ الْمُنَافِق۪ينَ يَصُدُّونَ عَنْكَ صُدُوداًۚ', 'Onlara: Allah´ın indirdiğine (Kitab´a) ve Resûl´e gelin (onlara başvuralım), denildiği zaman, münafıkların senden iyice uzaklaştıklarını görürsün.', 5, 87, 176, 15937, 'Kadınlar', 'Medine', 92),
(555, 62, 4, 'Nisa', 'فَكَيْفَ اِذَٓا اَصَابَتْهُمْ مُص۪يبَةٌ بِمَا قَدَّمَتْ اَيْد۪يهِمْ ثُمَّ جَٓاؤُ۫كَ يَحْلِفُونَ بِاللّٰهِ اِنْ اَرَدْنَٓا اِلَّٓا اِحْسَاناً وَتَوْف۪يقاً', 'Elleriyle yaptıkları yüzünden başlarına bir felâket gelince hemen, biz yalnızca iyilik etmek ve arayı bulmak istedik, diye yemin ederek sana nasıl gelirler!', 5, 87, 176, 15937, 'Kadınlar', 'Medine', 92),
(556, 63, 4, 'Nisa', 'اُو۬لٰٓئِكَ الَّذ۪ينَ يَعْلَمُ اللّٰهُ مَا ف۪ي قُلُوبِهِمْ فَاَعْرِضْ عَنْهُمْ وَعِظْهُمْ وَقُلْ لَهُمْ ف۪ٓي اَنْفُسِهِمْ قَوْلاً بَل۪يغاً', 'Onlar Allah´ın, kalplerindekini bildiği kimselerdir; onlara aldırma, kendilerine öğüt ver ve onlara, kendileri hakkında tesirli söz söyle.', 5, 87, 176, 15937, 'Kadınlar', 'Medine', 92),
(557, 64, 4, 'Nisa', 'وَمَٓا اَرْسَلْنَا مِنْ رَسُولٍ اِلَّا لِيُطَاعَ بِاِذْنِ اللّٰهِۜ وَلَوْ اَنَّهُمْ اِذْ ظَلَمُٓوا اَنْفُسَهُمْ جَٓاؤُ۫كَ فَاسْتَغْفَرُوا اللّٰهَ وَاسْتَغْفَرَ لَهُمُ الرَّسُولُ لَوَجَدُوا اللّٰهَ تَـوَّاباً رَح۪يماً', 'Biz her peygamberi -Allah´ın izniyle- ancak kendisine itaat edilmesi için gönderdik. Eğer onlar kendilerine zulmettikleri zaman sana gelseler de Allah´tan bağışlanmayı dileseler, Resûl de onlar için istiğfar etseydi Allah´ı ziyadesiyle affedici, esirgeyici bulurlardı.', 5, 87, 176, 15937, 'Kadınlar', 'Medine', 92),
(558, 65, 4, 'Nisa', 'فَلَا وَرَبِّكَ لَا يُؤْمِنُونَ حَتّٰى يُحَكِّمُوكَ ف۪يمَا شَجَرَ بَيْنَهُمْۙ ثُمَّ لَا يَجِدُوا ف۪ٓي اَنْفُسِهِمْ حَرَجاً مِمَّا قَضَيْتَ وَيُسَلِّمُوا تَسْل۪يماً', 'Hayır, Rabbine andolsun ki aralarında çıkan anlaşmazlık hususunda seni hakem kılıp sonra da verdiğin hükümden içlerinde hiçbir sıkıntı duymaksızın (onu) tam manasıyla kabullenmedikçe iman etmiş olmazlar.', 5, 87, 176, 15937, 'Kadınlar', 'Medine', 92),
(559, 66, 4, 'Nisa', 'وَلَوْ اَنَّا كَتَبْنَا عَلَيْهِمْ اَنِ اقْتُلُٓوا اَنْفُسَكُمْ اَوِ اخْرُجُوا مِنْ دِيَارِكُمْ مَا فَعَلُوهُ اِلَّا قَل۪يلٌ مِنْهُمْۜ وَلَوْ اَنَّهُمْ فَعَلُوا مَا يُوعَظُونَ بِه۪ لَكَانَ خَيْراً لَهُمْ وَاَشَدَّ تَثْب۪يتاًۙ', 'Eğer onlara, kendinizi öldürün yahut yurtlarınızdan çıkın, diye emretmiş olsaydık, içlerinden pek azı müstesna, bunu yapmazlardı. Eğer kendilerine verilen öğüdü yerine getirselerdi, onlar için hem daha hayırlı hem de (imanlarını) daha pekiştirici olurdu.', 5, 88, 176, 15937, 'Kadınlar', 'Medine', 92),
(560, 67, 4, 'Nisa', 'وَاِذاً لَاٰتَيْنَاهُمْ مِنْ لَدُنَّٓا اَجْراً عَظ۪يـماًۙ', 'O zaman elbette kendilerine nezdimizden büyük mükâfat verirdik.', 5, 88, 176, 15937, 'Kadınlar', 'Medine', 92),
(561, 68, 4, 'Nisa', 'وَلَهَدَيْنَاهُمْ صِرَاطاً مُسْتَق۪يماً', 'Ve onları dosdoğru bir yola iletirdik.', 5, 88, 176, 15937, 'Kadınlar', 'Medine', 92),
(562, 69, 4, 'Nisa', 'وَمَنْ يُطِـعِ اللّٰهَ وَالرَّسُولَ فَاُو۬لٰٓئِكَ مَعَ الَّذ۪ينَ اَنْعَمَ اللّٰهُ عَلَيْهِمْ مِنَ النَّبِيّ۪نَ وَالصِّدّ۪يق۪ينَ وَالشُّهَدَٓاءِ وَالصَّالِح۪ينَۚ وَحَسُنَ اُو۬لٰٓئِكَ رَف۪يقاًۜ', 'Kim Allah´a ve Resûl´e itaat ederse işte onlar, Allah´ın kendilerine lütuflarda bulunduğu peygamberler, sıddîkler, şehidler ve salih kişilerle beraberdir. Bunlar ne güzel arkadaştır!', 5, 88, 176, 15937, 'Kadınlar', 'Medine', 92),
(563, 70, 4, 'Nisa', 'ذٰلِكَ الْفَضْلُ مِنَ اللّٰهِۜ وَكَفٰى بِاللّٰهِ عَل۪يماً۟', 'Bu lütuf Allah´tandır. Bilen olarak Allah yeter.', 5, 88, 176, 15937, 'Kadınlar', 'Medine', 92),
(564, 71, 4, 'Nisa', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا خُذُوا حِذْرَكُمْ فَانْفِرُوا ثُبَاتٍ اَوِ انْفِرُوا جَم۪يعاً', 'Ey iman edenler! Tedbirinizi alın; bölük bölük savaşa çıkın, yahut (gerektiğinde) topyekün savaşın.', 5, 88, 176, 15937, 'Kadınlar', 'Medine', 92),
(565, 72, 4, 'Nisa', 'وَاِنَّ مِنْكُمْ لَمَنْ لَيُبَطِّئَنَّۚ فَاِنْ اَصَابَتْكُمْ مُص۪يبَةٌ قَالَ قَدْ اَنْعَمَ اللّٰهُ عَلَيَّ اِذْ لَمْ اَكُنْ مَعَهُمْ شَه۪يداً', 'İçinizden bazıları vardır ki (cihad konusunda) pek ağırdan alırlar. Eğer size bir felâket erişirse: «Allah bana lütfetti de onlarla beraber bulunmadım» der.', 5, 88, 176, 15937, 'Kadınlar', 'Medine', 92),
(566, 73, 4, 'Nisa', 'وَلَئِنْ اَصَابَكُمْ فَضْلٌ مِنَ اللّٰهِ لَيَقُولَنَّ كَاَنْ لَمْ تَكُنْ بَيْنَكُمْ وَبَيْنَهُ مَوَدَّةٌ يَا لَيْتَن۪ي كُنْتُ مَعَهُمْ فَاَفُوزَ فَوْزاً عَظ۪يماً', 'Eğer Allah´tan size bir lütuf erişirse -sanki sizinle onun arasında (zahirî) bir dostluk yokmuş gibi- «Keşke onlarla beraber olsaydım da ben de büyük bir başarı kazansaydım!» der.', 5, 88, 176, 15937, 'Kadınlar', 'Medine', 92),
(567, 74, 4, 'Nisa', 'فَلْيُقَاتِلْ ف۪ي سَب۪يلِ اللّٰهِ الَّذ۪ينَ يَشْرُونَ الْحَيٰوةَ الدُّنْيَا بِالْاٰخِرَةِۜ وَمَنْ يُقَاتِلْ ف۪ي سَب۪يلِ اللّٰهِ فَيُقْتَلْ اَوْ يَغْلِبْ فَسَوْفَ نُؤْت۪يهِ اَجْراً عَظ۪يماً', 'O halde, dünya hayatını ahiret karşılığında satanlar, Allah yolunda savaşsınlar. Kim Allah yolunda savaşır da öldürülür veya galip gelirse biz ona yakında büyük bir mükâfat vereceğiz.', 5, 88, 176, 15937, 'Kadınlar', 'Medine', 92),
(568, 75, 4, 'Nisa', 'وَمَا لَكُمْ لَا تُقَاتِلُونَ ف۪ي سَب۪يلِ اللّٰهِ وَالْمُسْتَضْعَف۪ينَ مِنَ الرِّجَالِ وَالنِّسَٓاءِ وَالْوِلْدَانِ الَّذ۪ينَ يَقُولُونَ رَبَّنَٓا اَخْرِجْنَا مِنْ هٰذِهِ الْقَرْيَةِ الظَّالِمِ اَهْلُهَاۚ وَاجْعَلْ لَنَا مِنْ لَدُنْكَ وَلِياًّۚ وَاجْعَلْ لَنَا مِنْ لَدُنْكَ نَص۪يراًۜ', 'Size ne oldu da Allah yolunda ve «Rabbimiz! Bizi, halkı zalim olan bu şehirden çıkar, bize tarafından bir sahip gönder, bize katından bir yardımcı yolla!» diyen zavallı erkekler, kadınlar ve çocuklar uğrunda savaşmıyorsunuz!', 5, 89, 176, 15937, 'Kadınlar', 'Medine', 92),
(569, 76, 4, 'Nisa', 'اَلَّذ۪ينَ اٰمَنُوا يُقَاتِلُونَ ف۪ي سَب۪يلِ اللّٰهِۚ وَالَّذ۪ينَ كَفَرُوا يُقَاتِلُونَ ف۪ي سَب۪يلِ الطَّاغُوتِ فَقَاتِلُٓوا اَوْلِيَٓاءَ الشَّيْطَانِۚ اِنَّ كَيْدَ الشَّيْطَانِ كَانَ ضَع۪يفاً۟', 'İman edenler Allah yolunda savaşırlar, inanmayanlar ise tâğut (bâtıl davalar ve şeytan) yolunda savaşırlar. O halde şeytanın dostlarına karşı savaşın; şüphe yok ki şeytanın kurduğu düzen zayıftır.', 5, 89, 176, 15937, 'Kadınlar', 'Medine', 92),
(570, 77, 4, 'Nisa', 'اَلَمْ تَرَ اِلَى الَّذ۪ينَ ق۪يلَ لَهُمْ كُفُّٓوا اَيْدِيَكُمْ وَاَق۪يمُوا الصَّلٰوةَ وَاٰتُوا الزَّكٰوةَۚ فَلَمَّا كُتِبَ عَلَيْهِمُ الْقِتَالُ اِذَا فَر۪يقٌ مِنْهُمْ يَخْشَوْنَ النَّاسَ كَخَشْيَةِ اللّٰهِ اَوْ اَشَدَّ خَشْيَةًۚ وَقَالُوا رَبَّنَا لِمَ كَتَبْتَ عَلَيْنَا الْقِتَالَۚ لَوْلَٓا اَخَّرْتَنَٓا اِلٰٓى اَجَلٍ قَر۪يبٍۜ قُلْ مَتَاعُ الدُّنْيَا قَل۪يلٌۚ وَالْاٰخِرَةُ خَيْرٌ لِمَنِ اتَّقٰى وَلَا تُظْلَمُونَ فَت۪يلاً', 'Kendilerine, ellerinizi savaştan çekin, namazı kılın ve zekâtı verin, denilen kimseleri görmedin mi? Sonra onlara savaş farz kılınınca, içlerinden bir gurup hemen Allah´tan korkar gibi, hatta daha fazla bir korku ile insanlardan korkmaya başladılar da «Rabbimiz! Savaşı bize niçin yazdın! Bizi yakın bir süreye kadar ertelesen (daha bir müddet savaşı farz kılmasan) olmaz mıydı?» dediler. Onlara de ki: «Dünya menfaati önemsizdir, Allah´tan korkanlar için ahiret daha hayırlıdır ve size kıl payı kadar haksızlık edilmez.»', 5, 89, 176, 15937, 'Kadınlar', 'Medine', 92),
(571, 78, 4, 'Nisa', 'اَيْنَ مَا تَكُونُوا يُدْرِكْكُمُ الْمَوْتُ وَلَوْ كُنْتُمْ ف۪ي بُرُوجٍ مُشَيَّدَةٍۜ وَاِنْ تُصِبْهُمْ حَسَنَةٌ يَقُولُوا هٰذِه۪ مِنْ عِنْدِ اللّٰهِۚ وَاِنْ تُصِبْهُمْ سَيِّئَةٌ يَقُولُوا هٰذِه۪ مِنْ عِنْدِكَۜ قُلْ كُلٌّ مِنْ عِنْدِ اللّٰهِۜ فَمَالِ هٰٓؤُ۬لَٓاءِ الْقَوْمِ لَا يَكَادُونَ يَفْقَهُونَ حَد۪يثاً', 'Nerede olursanız olun ölüm size ulaşır; sarp ve sağlam kalelerde olsanız bile! Kendilerine bir iyilik dokunsa «Bu Allah´tan» derler; başlarına bir kötülük gelince de «Bu senden» derler. «Hepsi Allah´tandır» de. Bu adamlara ne oluyor ki bir türlü laf anlamıyorlar!', 5, 89, 176, 15937, 'Kadınlar', 'Medine', 92),
(572, 79, 4, 'Nisa', 'مَٓا اَصَابَكَ مِنْ حَسَنَةٍ فَمِنَ اللّٰهِۘ وَمَٓا اَصَابَكَ مِنْ سَيِّئَةٍ فَمِنْ نَفْسِكَۜ وَاَرْسَلْنَاكَ لِلنَّاسِ رَسُولاًۜ وَكَفٰى بِاللّٰهِ شَه۪يداً', 'Sana gelen iyilik Allah´tandır. Başına gelen kötülük ise nefsindendir. Seni insanlara elçi gönderdik; şahit olarak da Allah yeter.', 5, 89, 176, 15937, 'Kadınlar', 'Medine', 92),
(573, 80, 4, 'Nisa', 'مَنْ يُطِـعِ الرَّسُولَ فَقَدْ اَطَاعَ اللّٰهَۚ وَمَنْ تَوَلّٰى فَمَٓا اَرْسَلْنَاكَ عَلَيْهِمْ حَف۪يظاًۜ', 'Kim Resûl´e itaat ederse Allah´a itaat etmiş olur. Yüz çevirene gelince, seni onların başına bekçi göndermedik!', 5, 90, 176, 15937, 'Kadınlar', 'Medine', 92),
(574, 81, 4, 'Nisa', 'وَيَقُولُونَ طَاعَةٌۘ فَاِذَا بَرَزُوا مِنْ عِنْدِكَ بَيَّتَ طَٓائِفَةٌ مِنْهُمْ غَيْرَ الَّذ۪ي تَقُولُۜ وَاللّٰهُ يَكْتُبُ مَا يُبَيِّتُونَۚ فَاَعْرِضْ عَنْهُمْ وَتَوَكَّلْ عَلَى اللّٰهِۜ وَكَفٰى بِاللّٰهِ وَك۪يلاً', '«Başüstüne» derler, ama yanından ayrılınca onlardan bir kısmı, senin dediğinden başkasını gizlice kurar. Allah da onların gizlice kurduklarını yazar. Sen onlara aldırma ve Allah´a dayan; sana vekil olarak Allah yeter.', 5, 90, 176, 15937, 'Kadınlar', 'Medine', 92),
(575, 82, 4, 'Nisa', 'اَفَلَا يَتَدَبَّرُونَ الْقُرْاٰنَۜ وَلَوْ كَانَ مِنْ عِنْدِ غَيْرِ اللّٰهِ لَوَجَدُوا ف۪يهِ اخْتِلَافاً كَث۪يراً', 'Hâla Kur´an üzerinde gereği gibi düşünmeyecekler mi? Eğer o, Allah´tan başkası tarafından gelmiş olsaydı onda birçok tutarsızlık bulurlardı.', 5, 90, 176, 15937, 'Kadınlar', 'Medine', 92),
(576, 83, 4, 'Nisa', 'وَاِذَا جَٓاءَهُمْ اَمْرٌ مِنَ الْاَمْنِ اَوِ الْخَوْفِ اَذَاعُوا بِه۪ۜ وَلَوْ رَدُّوهُ اِلَى الرَّسُولِ وَاِلٰٓى اُو۬لِي الْاَمْرِ مِنْهُمْ لَعَلِمَهُ الَّذ۪ينَ يَسْتَنْبِطُونَهُ مِنْهُمْۜ وَلَوْلَا فَضْلُ اللّٰهِ عَلَيْكُمْ وَرَحْمَتُهُ لَاتَّبَعْتُمُ الشَّيْطَانَ اِلَّا قَل۪يلاً', 'Onlara güven veya korkuya dair bir haber gelince hemen onu yayarlar; halbuki onu, Resûl´e veya aralarında yetki sahibi kimselere götürselerdi, onların arasından işin içyüzünü anlayanlar, onun ne olduğunu bilirlerdi. Allah´ın size lütuf ve rahmeti olmasaydı, pek azınız müstesna, şeytana uyup giderdiniz.', 5, 90, 176, 15937, 'Kadınlar', 'Medine', 92),
(577, 84, 4, 'Nisa', 'فَقَاتِلْ ف۪ي سَب۪يلِ اللّٰهِۚ لَا تُكَلَّفُ اِلَّا نَفْسَكَ وَحَرِّضِ الْمُؤْمِن۪ينَۚ عَسَى اللّٰهُ اَنْ يَكُفَّ بَأْسَ الَّذ۪ينَ كَفَرُواۜ وَاللّٰهُ اَشَدُّ بَأْساً وَاَشَدُّ تَنْك۪يلاً', 'Artık Allah yolunda savaş. Sen, kendinden başkası (sebebiyle) sorumlu tutulmazsın. Müminleri de teşvik et. Umulur ki Allah kâfirlerin gücünü kırar (güçleriyle size zarar vermelerini önler). Allah´ın gücü daha çetin ve cezası daha şiddetlidir.', 5, 90, 176, 15937, 'Kadınlar', 'Medine', 92),
(578, 85, 4, 'Nisa', 'مَنْ يَشْفَعْ شَفَاعَةً حَسَنَةً يَكُنْ لَهُ نَص۪يبٌ مِنْهَاۚ وَمَنْ يَشْفَعْ شَفَاعَةً سَيِّئَةً يَكُنْ لَهُ كِفْلٌ مِنْهَاۜ وَكَانَ اللّٰهُ عَلٰى كُلِّ شَيْءٍ مُق۪يتاً', 'Kim iyi bir işe aracılık ederse onun da o işten bir nasibi olur. Kim kötü bir işe aracılık ederse onun da ondan bir payı olur. Allah her şeyin karşılığını vericidir.', 5, 90, 176, 15937, 'Kadınlar', 'Medine', 92),
(579, 86, 4, 'Nisa', 'وَاِذَا حُيّ۪يتُمْ بِتَحِيَّةٍ فَحَيُّوا بِاَحْسَنَ مِنْهَٓا اَوْ رُدُّوهَاۜ اِنَّ اللّٰهَ كَانَ عَلٰى كُلِّ شَيْءٍ حَس۪يباً', 'Bir selam ile selamlandığınız zaman siz de ondan daha güzeli ile selamlayın; yahut aynı ile karşılık verin. Şüphesiz Allah, her şeyin hesabını arayandır.', 5, 90, 176, 15937, 'Kadınlar', 'Medine', 92),
(580, 87, 4, 'Nisa', 'اَللّٰهُ لَٓا اِلٰهَ اِلَّا هُوَۜ لَيَجْمَعَنَّكُمْ اِلٰى يَوْمِ الْقِيٰمَةِ لَا رَيْبَ ف۪يهِۜ وَمَنْ اَصْدَقُ مِنَ اللّٰهِ حَد۪يثاً۟', 'Allah -ki ondan başka hiçbir tanrı yoktur- elbette sizi kıyamet günü toplayacaktır, bunda asla şüphe yoktur. Söz bakımından Allah´tan daha doğru kim vardır!', 5, 91, 176, 15937, 'Kadınlar', 'Medine', 92),
(581, 88, 4, 'Nisa', 'فَمَا لَكُمْ فِي الْمُنَافِق۪ينَ فِئَتَيْنِ وَاللّٰهُ اَرْكَسَهُمْ بِمَا كَسَبُواۜ اَتُر۪يدُونَ اَنْ تَهْدُوا مَنْ اَضَلَّ اللّٰهُۜ وَمَنْ يُضْلِلِ اللّٰهُ فَلَنْ تَجِدَ لَهُ سَب۪يلاً', 'Size ne oldu da münafıklar hakkında iki gruba ayrıldınız? Halbuki Allah onları kendi ettikleri yüzünden baş aşağı etmiştir (küfürlerine döndürmüştür). Allah´ın saptırdığını doğru yola getirmek mi istiyorsunuz? Allah´ın saptırdığı kimse için asla (doğruya) yol bulamazsın!', 5, 91, 176, 15937, 'Kadınlar', 'Medine', 92),
(582, 89, 4, 'Nisa', 'وَدُّوا لَوْ تَكْفُرُونَ كَمَا كَفَرُوا فَتَكُونُونَ سَوَٓاءً فَلَا تَتَّخِذُوا مِنْهُمْ اَوْلِيَٓاءَ حَتّٰى يُهَاجِرُوا ف۪ي سَب۪يلِ اللّٰهِۜ فَاِنْ تَوَلَّوْا فَخُذُوهُمْ وَاقْتُلُوهُمْ حَيْثُ وَجَدْتُمُوهُمْۖ وَلَا تَتَّخِذُوا مِنْهُمْ وَلِياًّ وَلَا نَص۪يراًۙ', 'Sizin de kendileri gibi inkâr etmenizi istediler ki onlarla eşit olasınız. O halde Allah yolunda göç edinceye kadar onlardan hiçbirini dost edinmeyin. Eğer yüz çevirirlerse onları yakalayın, bulduğunuz yerde öldürün ve hiçbirini dost ve yardımcı edinmeyin.', 5, 91, 176, 15937, 'Kadınlar', 'Medine', 92),
(583, 90, 4, 'Nisa', 'اِلَّا الَّذ۪ينَ يَصِلُونَ اِلٰى قَوْمٍ بَيْنَكُمْ وَبَيْنَهُمْ م۪يثَاقٌ اَوْ جَٓاؤُ۫كُمْ حَصِرَتْ صُدُورُهُمْ اَنْ يُقَاتِلُوكُمْ اَوْ يُقَاتِلُوا قَوْمَهُمْۜ وَلَوْ شَٓاءَ اللّٰهُ لَسَلَّطَهُمْ عَلَيْكُمْ فَلَقَاتَلُوكُمْۚ فَاِنِ اعْتَزَلُوكُمْ فَلَمْ يُقَاتِلُوكُمْ وَاَلْقَوْا اِلَيْكُمُ السَّلَمَۙ فَمَا جَعَلَ اللّٰهُ لَكُمْ عَلَيْهِمْ سَب۪يلاً', 'Ancak kendileriyle aranızda antlaşma bulunan bir topluma sığınanlar yahut ne sizinle ne de kendi toplumlarıyla savaşmak (istemediklerin) den yürekleri sıkılarak size gelenler müstesna. Allah dileseydi onları başınıza belâ ederdi de sizinle savaşırlardı. Artık onlar sizi bırakıp bir tarafa çekilir de sizinle savaşmazlar ve size barış teklif ederlerse bu durumda Allah size, onların aleyhinde bir yola girme hakkı vermemiştir.', 5, 91, 176, 15937, 'Kadınlar', 'Medine', 92),
(584, 91, 4, 'Nisa', 'سَتَجِدُونَ اٰخَر۪ينَ يُر۪يدُونَ اَنْ يَأْمَنُوكُمْ وَيَأْمَنُوا قَوْمَهُمْۜ كُلَّمَا رُدُّٓوا اِلَى الْفِتْنَةِ اُرْكِسُوا ف۪يهَاۚ فَاِنْ لَمْ يَعْتَزِلُوكُمْ وَيُلْقُٓوا اِلَيْكُمُ السَّلَمَ وَيَكُفُّٓوا اَيْدِيَهُمْ فَخُذُوهُمْ وَاقْتُلُوهُمْ حَيْثُ ثَقِفْتُمُوهُمْۜ وَاُو۬لٰٓئِكُمْ جَعَلْنَا لَكُمْ عَلَيْهِمْ سُلْطَاناً مُب۪يناً۟', 'Hem sizden hem de kendi toplumlarından emin olmak isteyen başkalarını da bulacaksınız. Bunlar her ne zaman fitneye götürülseler ona baş aşağı dalarlar (daldırılırlar). Eğer sizden uzak durmaz, sulh teklif etmez ve ellerini çekmezlerse onları yakalayın, rastladığınız yerde öldürün. İşte onlar üzerine sizin için apaçık yetki verdik.', 5, 91, 176, 15937, 'Kadınlar', 'Medine', 92),
(585, 92, 4, 'Nisa', 'وَمَا كَانَ لِمُؤْمِنٍ اَنْ يَقْتُلَ مُؤْمِناً اِلَّا خَطَـٔاًۚ وَمَنْ قَتَلَ مُـؤْمِناً خَطَــٔاً فَـتَـحْر۪يرُ رَقَبَةٍ مُؤْمِنَةٍ وَدِيَةٌ مُسَلَّمَةٌ اِلٰٓى اَهْلِه۪ٓ اِلَّٓا اَنْ يَصَّدَّقُواۜ فَاِنْ كَانَ مِنْ قَوْمٍ عَدُوٍّ لَكُمْ وَهُوَ مُؤْمِنٌ فَتَحْر۪يرُ رَقَبَةٍ مُؤْمِنَةٍۜ وَاِنْ كَانَ مِنْ قَوْمٍ بَيْنَكُمْ وَبَيْنَهُمْ م۪يثَاقٌ فَدِيَةٌ مُسَلَّمَةٌ اِلٰٓى اَهْلِه۪ وَتَحْر۪يرُ رَقَبَةٍ مُؤْمِنَةٍۚ فَمَنْ لَمْ يَجِدْ فَصِيَامُ شَهْرَيْنِ مُتَتَابِعَيْنِۘ تَوْبَةً مِنَ اللّٰهِۜ وَكَانَ اللّٰهُ عَل۪يماً حَك۪يماً', 'Yanlışlıkla olması dışında bir müminin bir mümini öldürmeye hakkı olamaz. Yanlışlıkla bir mümini öldüren kimsenin, mümin bir köle azat etmesi ve ölenin ailesine teslim edilecek bir diyet vermesi gereklidir. Meğer ki ölünün ailesi o diyeti bağışlamış ola. (Bu takdirde diyet vermez). Eğer öldürülen mümin olduğu halde, size düşman olan bir toplumdan ise mümin bir köle azat etmek lâzımdır. Eğer kendileriyle aranızda antlaşma bulunan bir toplumdan ise ailesine teslim edilecek bir diyet ve bir mümin köleyi azat etmek gerekir. Bunları bulamayan kimsenin, Allah tarafından tevbesinin kabulü için iki ay peşpeşe oruç tutması lâzımdır. Allah her şeyi bilendir, hikmet sahibidir.', 5, 92, 176, 15937, 'Kadınlar', 'Medine', 92),
(586, 93, 4, 'Nisa', 'وَمَنْ يَقْتُلْ مُؤْمِناً مُتَعَمِّداً فَجَزَٓاؤُ۬هُ جَهَنَّمُ خَالِداً ف۪يهَا وَغَضِبَ اللّٰهُ عَلَيْهِ وَلَعَنَهُ وَاَعَدَّ لَهُ عَذَاباً عَظ۪يماً', 'Kim bir mümini kasden öldürürse cezası, içinde ebediyen kalacağı cehennemdir. Allah ona gazap etmiş, onu lânetlemiş ve onun için büyük bir azap hazırlamıştır.', 5, 92, 176, 15937, 'Kadınlar', 'Medine', 92),
(587, 94, 4, 'Nisa', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُٓوا اِذَا ضَرَبْتُمْ ف۪ي سَب۪يلِ اللّٰهِ فَتَبَيَّنُوا وَلَا تَقُولُوا لِمَنْ اَلْقٰٓى اِلَيْكُمُ السَّلَامَ لَسْتَ مُؤْمِناًۚ تَبْتَغُونَ عَرَضَ الْحَيٰوةِ الدُّنْيَاۘ فَعِنْدَ اللّٰهِ مَغَانِمُ كَث۪يرَةٌۜ كَذٰلِكَ كُنْتُمْ مِنْ قَبْلُ فَمَنَّ اللّٰهُ عَلَيْكُمْ فَتَبَيَّنُواۜ اِنَّ اللّٰهَ كَانَ بِمَا تَعْمَلُونَ خَب۪يراً', 'Ey iman edenler! Allah yolunda savaşa çıktığınız zaman iyi anlayıp dinleyin. Size selam verene, dünya hayatının geçici menfaatine göz dikerek «Sen mümin değilsin» demeyin. Çünkü Allah´ın nezdinde sayısız ganimetler vardır. Önceden siz de böyle iken Allah size lütfetti; o halde iyi anlayıp dinleyin. Şüphesiz Allah bütün yaptıklarınızdan haberdardır.', 5, 92, 176, 15937, 'Kadınlar', 'Medine', 92),
(588, 95, 4, 'Nisa', 'لَا يَسْتَوِي الْقَاعِدُونَ مِنَ الْمُؤْمِن۪ينَ غَيْرُ اُو۬لِي الضَّرَرِ وَالْمُجَاهِدُونَ ف۪ي سَب۪يلِ اللّٰهِ بِاَمْوَالِهِمْ وَاَنْفُسِهِمْۜ فَضَّلَ اللّٰهُ الْمُجَاهِد۪ينَ بِاَمْوَالِهِمْ وَاَنْفُسِهِمْ عَلَى الْقَاعِد۪ينَ دَرَجَةًۜ وَكُلاًّ وَعَدَ اللّٰهُ الْحُسْنٰىۜ وَفَضَّلَ اللّٰهُ الْمُجَاهِد۪ينَ عَلَى الْقَاعِد۪ينَ اَجْراً عَظ۪يـماًۙ', 'Müminlerden -özür sahibi olanlar dışında- oturanlarla malları ve canlarıyle Allah yolunda cihad edenler bir olmaz. Allah, malları ve canları ile cihad edenleri, derece bakımından oturanlardan üstün kıldı. Gerçi Allah hepsine de güzellik (cennet) vadetmiştir; ama mücahidleri, oturanlardan çok büyük bir ecirle üstün kılmıştır.', 5, 93, 176, 15937, 'Kadınlar', 'Medine', 92),
(589, 96, 4, 'Nisa', 'دَرَجَاتٍ مِنْهُ وَمَغْفِرَةً وَرَحْمَةًۜ وَكَانَ اللّٰهُ غَفُوراً رَح۪يماً۟', 'Kendinden dereceler, bağışlama ve rahmet vermiştir. Allah çok bağışlayıcı ve esirgeyicidir.', 5, 93, 176, 15937, 'Kadınlar', 'Medine', 92),
(590, 97, 4, 'Nisa', 'اِنَّ الَّذ۪ينَ تَوَفّٰيهُمُ الْمَلٰٓئِكَةُ ظَالِم۪ٓي اَنْفُسِهِمْ قَالُوا ف۪يمَ كُنْتُمْۜ قَالُوا كُنَّا مُسْتَضْعَف۪ينَ فِي الْاَرْضِۜ قَالُٓوا اَلَمْ تَكُنْ اَرْضُ اللّٰهِ وَاسِعَةً فَتُهَاجِرُوا ف۪يهَاۜ فَاُو۬لٰٓئِكَ مَأْوٰيهُمْ جَهَنَّمُۜ وَسَٓاءَتْ مَص۪يراًۙ', 'Kendilerine yazık eden kimselere melekler, canlarını alırken: «Ne işde idiniz!» dediler. Bunlar: «Biz yeryüzünde çaresizdik» diye cevap verdiler. Melekler de: «Allah´ın yeri geniş değil miydi? Hicret etseydiniz ya!» dediler. İşte onların barınağı cehennemdir; orası ne kötü bir gidiş yeridir.', 5, 93, 176, 15937, 'Kadınlar', 'Medine', 92),
(591, 98, 4, 'Nisa', 'اِلَّا الْمُسْتَضْعَف۪ينَ مِنَ الرِّجَالِ وَالنِّسَٓاءِ وَالْوِلْدَانِ لَا يَسْتَط۪يعُونَ ح۪يلَةً وَلَا يَهْتَدُونَ سَب۪يلاً', 'Erkekler, kadınlar ve çocuklardan (gerçekten) âciz olup hiçbir çareye gücü yetmeyenler, hiçbir yol bulamayanlar müstesnadır.', 5, 93, 176, 15937, 'Kadınlar', 'Medine', 92),
(592, 99, 4, 'Nisa', 'فَاُو۬لٰٓئِكَ عَسَى اللّٰهُ اَنْ يَعْفُوَ عَنْهُمْۜ وَكَانَ اللّٰهُ عَفُواًّ غَفُوراً', 'İşte bunları, umulur ki Allah affeder; Allah çok affedicidir, bağışlayıcıdır.', 5, 93, 176, 15937, 'Kadınlar', 'Medine', 92),
(593, 100, 4, 'Nisa', 'وَمَنْ يُهَاجِرْ ف۪ي سَب۪يلِ اللّٰهِ يَجِدْ فِي الْاَرْضِ مُرَاغَماً كَث۪يراً وَسَعَةًۜ وَمَنْ يَخْرُجْ مِنْ بَيْتِه۪ مُهَاجِراً اِلَى اللّٰهِ وَرَسُولِه۪ ثُمَّ يُدْرِكْهُ الْمَوْتُ فَقَدْ وَقَعَ اَجْرُهُ عَلَى اللّٰهِۜ وَكَانَ اللّٰهُ غَفُوراً رَح۪يماً۟', 'Allah yolunda hicret eden kimse yeryüzünde gidecek bir çok güzel yer ve bolluk (imkân) bulur. Kim Allah ve Resûlü uğrunda hicret ederek evinden çıkar da sonra kendisine ölüm yetişirse artık onun mükâfatı Allah´a düşer. Allah da çok bağışlayıcı ve esirgeyicidir.', 5, 93, 176, 15937, 'Kadınlar', 'Medine', 92),
(594, 101, 4, 'Nisa', 'وَاِذَا ضَرَبْتُمْ فِي الْاَرْضِ فَلَيْسَ عَلَيْكُمْ جُنَاحٌ اَنْ تَقْصُرُوا مِنَ الصَّلٰوةِۗ اِنْ خِفْتُمْ اَنْ يَفْتِنَكُمُ الَّذ۪ينَ كَفَرُواۜ اِنَّ الْكَافِر۪ينَ كَانُوا لَكُمْ عَدُواًّ مُب۪يناً', 'Yeryüzünde sefere çıktığınız zaman kâfirlerin size kötülük etmelerinden endişe ederseniz, namazı kısaltmanızda size bir günah yoktur. Şüphesiz kâfirler, sizin apaçık düşmanınızdır.', 5, 93, 176, 15937, 'Kadınlar', 'Medine', 92),
(595, 102, 4, 'Nisa', 'وَاِذَا كُنْتَ ف۪يهِمْ فَاَقَمْتَ لَهُمُ الصَّلٰوةَ فَلْتَقُمْ طَٓائِفَةٌ مِنْهُمْ مَعَكَ وَلْيَأْخُذُٓوا اَسْلِحَتَهُمْ۠ فَاِذَا سَجَدُوا فَلْيَكُونُوا مِنْ وَرَٓائِكُمْۖ وَلْتَأْتِ طَٓائِفَةٌ اُخْرٰى لَمْ يُصَلُّوا فَلْيُصَلُّوا مَعَكَ وَلْيَأْخُذُوا حِذْرَهُمْ وَاَسْلِحَتَهُمْۚ وَدَّ الَّذ۪ينَ كَفَرُوا لَوْ تَغْفُلُونَ عَنْ اَسْلِحَتِكُمْ وَاَمْتِعَتِكُمْ فَيَم۪يلُونَ عَلَيْكُمْ مَيْلَةً وَاحِدَةًۜ وَلَا جُنَاحَ عَلَيْكُمْ اِنْ كَانَ بِكُمْ اَذًى مِنْ مَطَرٍ اَوْ كُنْتُمْ مَرْضٰٓى اَنْ تَضَعُٓوا اَسْلِحَتَكُمْۚ وَخُذُوا حِذْرَكُمْۜ اِنَّ اللّٰهَ اَعَدَّ لِلْكَافِر۪ينَ عَذَاباً مُه۪يناً', 'Sen de içlerinde bulunup onlara namaz kıldırdığın zaman, onlardan bir kısmı seninle beraber namaza dursunlar, silahlarını (yanlarına) alsınlar, böylece (namazı kılıp) secde ettiklerinde (diğerleri) arkanızda olsunlar. Sonra henüz namazını kılmamış olan (bu) diğer gurup gelip seninle beraber namazlarını kılsınlar ve onlar da ihtiyat tedbirlerini ve silahlarını alsınlar. O kâfirler arzu ederler ki siz silahlarınızdan ve eşyanızdan gafil olsanız da üstünüze birden baskın yapsalar. Eğer size yağmurdan bir eziyet olur yahut hasta bulunursanız silahlarınızı bırakmanızda size günah yoktur. Yine de tedbirinizi alın. Şüphesiz Allah, kâfirler için alçaltıcı bir azap hazırlamıştır.', 5, 94, 176, 15937, 'Kadınlar', 'Medine', 92),
(596, 103, 4, 'Nisa', 'فَاِذَا قَضَيْتُمُ الصَّلٰوةَ فَاذْكُرُوا اللّٰهَ قِيَاماً وَقُعُوداً وَعَلٰى جُنُوبِكُمْۚ فَاِذَا اطْمَأْنَنْتُمْ فَاَق۪يمُوا الصَّلٰوةَۚ اِنَّ الصَّلٰوةَ كَانَتْ عَلَى الْمُؤْمِن۪ينَ كِتَاباً مَوْقُوتاً', 'Namazı bitirince de ayakta, otururken ve yanınız üzerinde yatarken (daima) Allah´ı anın. Huzura kavuşunca da namazı dosdoğru kılın; çünkü namaz müminler üzerine vakitleri belli bir farzdır.', 5, 94, 176, 15937, 'Kadınlar', 'Medine', 92),
(597, 104, 4, 'Nisa', 'وَلَا تَهِنُوا فِي ابْتِغَٓاءِ الْقَوْمِۜ اِنْ تَكُونُوا تَأْلَمُونَ فَاِنَّهُمْ يَأْلَمُونَ كَمَا تَأْلَمُونَۚ وَتَرْجُونَ مِنَ اللّٰهِ مَا لَا يَرْجُونَۜ وَكَانَ اللّٰهُ عَل۪يماً حَك۪يماً۟', 'O (düşman) topluluğu takip etmekte gevşeklik göstermeyin. Eğer siz acı çekiyorsanız onlar da, sizin çektiğiniz gibi acı çekmektedirler. Üstelik siz Allah´tan, onların ümit etmedikleri şeyleri umuyorsunuz. Allah ilim ve hikmet sahibidir.', 5, 94, 176, 15937, 'Kadınlar', 'Medine', 92),
(598, 105, 4, 'Nisa', 'اِنَّٓا اَنْزَلْـنَٓا اِلَيْكَ الْكِتَابَ بِالْحَقِّ لِتَحْكُمَ بَيْنَ النَّاسِ بِمَٓا اَرٰيكَ اللّٰهُۜ وَلَا تَكُنْ لِلْخَٓائِن۪ينَ خَص۪يماًۙ', 'Allah´ın sana gösterdiği şekilde insanlar arasında hükmedesin diye sana Kitab´ı hak ile indirdik; hainlerden taraf olma!', 5, 94, 176, 15937, 'Kadınlar', 'Medine', 92),
(599, 106, 4, 'Nisa', 'وَاسْتَغْفِرِ اللّٰهَۜ اِنَّ اللّٰهَ كَانَ غَفُوراً رَح۪يماًۚ', 'Ve Allah´tan mağfiret iste, çünkü Allah, çok yarlığayıcı, ziyadesiyle esirgeyicidir.', 5, 95, 176, 15937, 'Kadınlar', 'Medine', 92),
(600, 107, 4, 'Nisa', 'وَلَا تُجَادِلْ عَنِ الَّذ۪ينَ يَخْتَانُونَ اَنْفُسَهُمْۜ اِنَّ اللّٰهَ لَا يُحِبُّ مَنْ كَانَ خَوَّاناً اَث۪يماًۚ', 'Kendilerine hıyanet edenleri savunma; çünkü Allah hainliği meslek edinmiş günahkârları sevmez.', 5, 95, 176, 15937, 'Kadınlar', 'Medine', 92),
(601, 108, 4, 'Nisa', 'يَسْتَخْفُونَ مِنَ النَّاسِ وَلَا يَسْتَخْفُونَ مِنَ اللّٰهِ وَهُوَ مَعَهُمْ اِذْ يُبَيِّتُونَ مَا لَا يَرْضٰى مِنَ الْقَوْلِۜ وَكَانَ اللّٰهُ بِمَا يَعْمَلُونَ مُح۪يـطاً', 'İnsanlardan gizler de Allah´tan gizlemezler. Halbuki geceleyin, O´nun razı olmadığı sözü düzüp kurarken O, onlarla beraber idi. Allah yaptıklarını kuşatıcıdır (O´nun ilminden hiçbir şeyi gizleyemezler).', 5, 95, 176, 15937, 'Kadınlar', 'Medine', 92),
(602, 109, 4, 'Nisa', 'هَٓا اَنْتُمْ هٰٓؤُ۬لَٓاءِ جَادَلْتُمْ عَنْهُمْ فِي الْحَيٰوةِ الدُّنْيَا فَمَنْ يُجَادِلُ اللّٰهَ عَنْهُمْ يَوْمَ الْقِيٰمَةِ اَمْ مَنْ يَكُونُ عَلَيْهِمْ وَك۪يلاً', 'Haydi siz dünya hayatında onlara taraf çıkıp savundunuz, ya kıyamet günü Allah´a karşı onları kim savunacak yahut onlara kim vekil olacak?', 5, 95, 176, 15937, 'Kadınlar', 'Medine', 92),
(603, 110, 4, 'Nisa', 'وَمَنْ يَعْمَلْ سُٓوءاً اَوْ يَظْلِمْ نَفْسَهُ ثُمَّ يَسْتَغْفِرِ اللّٰهَ يَجِدِ اللّٰهَ غَفُوراً رَح۪يماً', 'Kim bir kötülük yapar yahut nefsine zulmeder de sonra Allah´tan mağfiret dilerse, Allah´ı çok yarlığayıcı ve esirgeyici bulacaktır.', 5, 95, 176, 15937, 'Kadınlar', 'Medine', 92),
(604, 111, 4, 'Nisa', 'وَمَنْ يَكْسِبْ اِثْماً فَاِنَّمَا يَكْسِبُهُ عَلٰى نَفْسِه۪ۜ وَكَانَ اللّٰهُ عَل۪يماً حَك۪يماً', 'Kim bir günah kazanırsa onu ancak kendi aleyhine kazanmış olur. Allah her şeyi bilicidir, büyük hikmet sahibidir.', 5, 95, 176, 15937, 'Kadınlar', 'Medine', 92),
(605, 112, 4, 'Nisa', 'وَمَنْ يَكْسِبْ خَط۪ٓيـَٔةً اَوْ اِثْماً ثُمَّ يَرْمِ بِه۪ بَر۪ٓيـٔاً فَقَدِ احْتَمَلَ بُهْتَـاناً وَاِثْماً مُب۪يناً۟', 'Kim kasıtlı veya kasıtsız bir günah kazanır da sonra onu bir suçsuzun üzerine atarsa, muhakkak ki, büyük bir iftira ve apaçık bir günah yüklenmiş olur.', 5, 95, 176, 15937, 'Kadınlar', 'Medine', 92),
(606, 113, 4, 'Nisa', 'وَلَوْلَا فَضْلُ اللّٰهِ عَلَيْكَ وَرَحْمَتُهُ لَهَمَّتْ طَٓائِفَةٌ مِنْهُمْ اَنْ يُضِلُّوكَۜ وَمَا يُضِلُّونَ اِلَّٓا اَنْفُسَهُمْ وَمَا يَضُرُّونَكَ مِنْ شَيْءٍۜ وَاَنْزَلَ اللّٰهُ عَلَيْكَ الْكِتَابَ وَالْحِكْمَةَ وَعَلَّمَكَ مَا لَمْ تَكُنْ تَعْلَمُۜ وَكَانَ فَضْلُ اللّٰهِ عَلَيْكَ عَظ۪يماً', 'Allah´ın sana lütfu ve esirgemesi olmasaydı, onlardan bir güruh seni saptırmaya yeltenmişti. Onlar yalnızca kendilerini saptırırlar, sana hiçbir zarar veremezler. Allah sana Kitab´ı ve hikmeti indirmiş ve sana bilmediğini öğretmiştir. Allah´ın lütfu sana gerçekten büyük olmuştur.', 5, 95, 176, 15937, 'Kadınlar', 'Medine', 92),
(607, 114, 4, 'Nisa', 'لَا خَيْرَ ف۪ي كَث۪يرٍ مِنْ نَجْوٰيهُمْ اِلَّا مَنْ اَمَرَ بِصَدَقَةٍ اَوْ مَعْرُوفٍ اَوْ اِصْلَاحٍ بَيْنَ النَّاسِۜ وَمَنْ يَفْعَلْ ذٰلِكَ ابْتِغَٓاءَ مَرْضَاتِ اللّٰهِ فَسَوْفَ نُؤْت۪يهِ اَجْراً عَظ۪يماً', 'Onların fısıldaşmalarının birçoğunda hayır yoktur. Ancak bir sadaka yahut bir iyilik yahut da insanların arasını düzeltmeyi isteyen (in fısıldaşması) müstesna. Kim Allah´ın rızasını elde etmek için bunu yaparsa, biz ona yakında büyük bir mükâfat vereceğiz.', 5, 96, 176, 15937, 'Kadınlar', 'Medine', 92),
(608, 115, 4, 'Nisa', 'وَمَنْ يُشَاقِقِ الرَّسُولَ مِنْ بَعْدِ مَا تَبَيَّنَ لَهُ الْهُدٰى وَيَتَّبِـعْ غَيْرَ سَب۪يلِ الْمُؤْمِن۪ينَ نُوَلِّه۪ مَا تَوَلّٰى وَنُصْلِه۪ جَهَنَّمَۜ وَسَٓاءَتْ مَص۪يراً۟', 'Kendisi için doğru yol belli olduktan sonra, kim Peygamber´e karşı çıkar ve müminlerin yolundan başka bir yola giderse, onu o yönde bırakırız ve cehenneme sokarız; o ne kötü bir yerdir.', 5, 96, 176, 15937, 'Kadınlar', 'Medine', 92),
(609, 116, 4, 'Nisa', 'اِنَّ اللّٰهَ لَا يَغْفِرُ اَنْ يُشْرَكَ بِه۪ وَيَغْفِرُ مَا دُونَ ذٰلِكَ لِمَنْ يَشَٓاءُۜ وَمَنْ يُشْرِكْ بِاللّٰهِ فَقَدْ ضَلَّ ضَلَالاً بَع۪يداً', 'Allah, kendisine ortak koşulmasını asla bağışlamaz; ondan başka günahları dilediği kimse için bağışlar. Kim Allah´a ortak koşarsa büsbütün sapıtmıştır.', 5, 96, 176, 15937, 'Kadınlar', 'Medine', 92),
(610, 117, 4, 'Nisa', 'اِنْ يَدْعُونَ مِنْ دُونِه۪ٓ اِلَّٓا اِنَاثاًۚ وَاِنْ يَدْعُونَ اِلَّا شَيْطَاناً مَر۪يداًۙ', 'Onlar (müşrikler) O´nu bırakıp yalnızca bir takım dişilerden (dişi isimli tanrılardan) istiyorlar, ancak inatçı şeytandan dilekte bulunuyorlar.', 5, 96, 176, 15937, 'Kadınlar', 'Medine', 92),
(611, 118, 4, 'Nisa', 'لَعَنَهُ اللّٰهُۢ وَقَالَ لَاَتَّخِذَنَّ مِنْ عِبَادِكَ نَص۪يباً مَفْرُوضاًۙ', 'Allah onu (şeytanı) lânetlemiş; o da: «Yemin ederim ki, kullarından belli bir pay edineceğim» demiştir.', 5, 96, 176, 15937, 'Kadınlar', 'Medine', 92),
(612, 119, 4, 'Nisa', 'وَلَاُضِلَّنَّهُمْ وَلَاُمَنِّيَنَّهُمْ وَلَاٰمُرَنَّهُمْ فَلَيُبَتِّكُنَّ اٰذَانَ الْاَنْعَامِ وَلَاٰمُرَنَّهُمْ فَلَيُغَيِّرُنَّ خَلْقَ اللّٰهِۜ وَمَنْ يَتَّخِذِ الشَّيْطَانَ وَلِياًّ مِنْ دُونِ اللّٰهِ فَقَدْ خَسِرَ خُسْرَاناً مُب۪يناًۜ', '«Onları mutlaka saptıracağım, muhakkak onları boş kuruntulara boğacağım, kesinlikle onlara emredeceğim de hayvanların kulaklarını yaracaklar (putlar için nişanlayacaklar), şüphesiz onlara emredeceğim de Allah´ın yarattığını değiştirecekler» (dedi). Kim Allah´ı bırakır da şeytanı dost edinirse elbette apaçık bir ziyana düşmüştür.', 5, 96, 176, 15937, 'Kadınlar', 'Medine', 92),
(613, 120, 4, 'Nisa', 'يَعِدُهُمْ وَيُمَنّ۪يهِمْۜ وَمَا يَعِدُهُمُ الشَّيْطَانُ اِلَّا غُرُوراً', '(Şeytan) onlara söz verir ve onları ümitlendirir; halbuki şeytanın onlara söz vermesi aldatmacadan başka bir şey değildir.', 5, 96, 176, 15937, 'Kadınlar', 'Medine', 92),
(614, 121, 4, 'Nisa', 'اُو۬لٰٓئِكَ مَأْوٰيهُمْ جَهَنَّمُ وَلَا يَجِدُونَ عَنْهَا مَح۪يصاً', 'İşte onların yeri cehennemdir; ondan kaçıp kurtulacak bir yer de bulamayacaklardır.', 5, 96, 176, 15937, 'Kadınlar', 'Medine', 92),
(615, 122, 4, 'Nisa', 'وَالَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ سَنُدْخِلُهُمْ جَنَّاتٍ تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُ خَالِد۪ينَ ف۪يهَٓا اَبَداًۜ وَعْدَ اللّٰهِ حَقاًّۜ وَمَنْ اَصْدَقُ مِنَ اللّٰهِ ق۪يلاً', 'İman eden ve iyi işler yapanları, içinde ebedî kalmak üzere, zemininden ırmaklar akan cennetlere koyacağız. Allah, (bu söylenenleri) hak bir söz olarak vâdetti. Söz verme ve onu tutma bakımından kim Allah´tan daha doğru olabilir?', 5, 97, 176, 15937, 'Kadınlar', 'Medine', 92),
(616, 123, 4, 'Nisa', 'لَيْسَ بِاَمَانِيِّكُمْ وَلَٓا اَمَانِيِّ اَهْلِ الْكِتَابِۜ مَنْ يَعْمَلْ سُٓوءاً يُجْزَ بِه۪ۙ وَلَا يَجِدْ لَهُ مِنْ دُونِ اللّٰهِ وَلِياًّ وَلَا نَص۪يراً', 'Ne sizin kuruntularınız ne de ehl-i kitabın kuruntuları (gerçektir); kim bir kötülük yaparsa onun cezasını görür ve kendisi için Allah´tan başka dost da, yardımcı da bulamaz.', 5, 97, 176, 15937, 'Kadınlar', 'Medine', 92),
(617, 124, 4, 'Nisa', 'وَمَنْ يَعْمَلْ مِنَ الصَّالِحَاتِ مِنْ ذَكَرٍ اَوْ اُنْثٰى وَهُوَ مُؤْمِنٌ فَاُو۬لٰٓئِكَ يَدْخُلُونَ الْجَنَّةَ وَلَا يُظْلَمُونَ نَق۪يراً', 'Erkek olsun, kadın olsun, her kim de mümin olarak iyi işler yaparsa, işte onlar cennete girerler ve zerre kadar haksızlığa uğratılmazlar.', 5, 97, 176, 15937, 'Kadınlar', 'Medine', 92),
(618, 125, 4, 'Nisa', 'وَمَنْ اَحْسَنُ د۪يناً مِمَّنْ اَسْلَمَ وَجْهَهُ لِلّٰهِ وَهُوَ مُحْسِنٌ وَاتَّبَعَ مِلَّةَ اِبْرٰه۪يمَ حَن۪يفاًۜ وَاتَّخَذَ اللّٰهُ اِبْرٰه۪يمَ خَل۪يلاً', 'İşlerinde doğru olarak kendini Allah´a veren ve İbrahim´in, Allah´ı bir tanıyan dinine tâbi olan kimseden dince daha güzel kim vardır? Allah İbrahim´i dost edinmiştir.', 5, 97, 176, 15937, 'Kadınlar', 'Medine', 92),
(619, 126, 4, 'Nisa', 'وَلِلّٰهِ مَا فِي السَّمٰوَاتِ وَمَا فِي الْاَرْضِۜ وَكَانَ اللّٰهُ بِكُلِّ شَيْءٍ مُح۪يطاً۟', 'Göklerde ve yerde ne varsa hepsi Allah´ındır ve Allah her şeyi kuşatmıştır. (Hiçbir şey O´nun ilim ve kudretinin dışında kalamaz).', 5, 97, 176, 15937, 'Kadınlar', 'Medine', 92),
(620, 127, 4, 'Nisa', 'وَيَسْتَفْتُونَكَ فِي النِّسَٓاءِۜ قُلِ اللّٰهُ يُفْت۪يكُمْ ف۪يهِنَّۙ وَمَا يُتْلٰى عَلَيْكُمْ فِي الْكِتَابِ ف۪ي يَتَامَى النِّسَٓاءِ الّٰت۪ي لَا تُؤْتُونَهُنَّ مَا كُتِبَ لَهُنَّ وَتَرْغَبُونَ اَنْ تَنْكِحُوهُنَّ وَالْمُسْتَضْعَف۪ينَ مِنَ الْوِلْدَانِۙ وَاَنْ تَقُومُوا لِلْيَتَامٰى بِالْقِسْطِۜ وَمَا تَفْعَلُوا مِنْ خَيْرٍ فَاِنَّ اللّٰهَ كَانَ بِه۪ عَل۪يماً', 'Senden kadınlar hakkında fetva istiyorlar. De ki, onlara ait hükmü size Allah açıklıyor: Kitap´ta, kendileri için yazılmışı (mirası) vermeyip nikâhlamak istediğiniz yetim kadınlar, çaresiz çocuklar ve yetimlere karşı âdil davranmanız hakkında size okunan âyetler (Allah´ın hükmünü apaçık ortaya koymaktadır). Hayırdan ne yaparsanız şüphesiz Allah onu bilmektedir.', 5, 97, 176, 15937, 'Kadınlar', 'Medine', 92),
(621, 128, 4, 'Nisa', 'وَاِنِ امْرَاَةٌ خَافَتْ مِنْ بَعْلِهَا نُشُوزاً اَوْ اِعْرَاضاً فَلَا جُنَاحَ عَلَيْهِمَٓا اَنْ يُصْلِحَا بَيْنَهُمَا صُلْحاًۜ وَالصُّلْحُ خَيْرٌۜ وَاُحْضِرَتِ الْاَنْفُسُ الشُّحَّۜ وَاِنْ تُحْسِنُوا وَتَتَّقُوا فَاِنَّ اللّٰهَ كَانَ بِمَا تَعْمَلُونَ خَب۪يراً', 'Eğer bir kadın kocasının geçimsizliğinden yahut kendisinden yüz çevirmesinden endişe ederse, aralarında bir sulh yapmalarında onlara günah yoktur. Sulh (daima) hayırlıdır. Zaten nefisler kıskançlığa hazırdır. Eğer iyi geçinir ve Allah´tan korkarsanız şüphesiz Allah yaptıklarınızdan haberdardır.', 5, 98, 176, 15937, 'Kadınlar', 'Medine', 92),
(622, 129, 4, 'Nisa', 'وَلَنْ تَسْتَط۪يعُٓوا اَنْ تَعْدِلُوا بَيْنَ النِّسَٓاءِ وَلَوْ حَرَصْتُمْ فَلَا تَم۪يلُوا كُلَّ الْمَيْلِ فَتَذَرُوهَا كَالْمُعَلَّقَةِۜ وَاِنْ تُصْلِحُوا وَتَتَّقُوا فَاِنَّ اللّٰهَ كَانَ غَفُوراً رَح۪يماً', 'Üzerine düşüp uğraşsanız da kadınlar arasında âdil davranmaya güç yetiremezsiniz; bâri birisine tamamen kapılıp da diğerini askıya alınmış gibi bırakmayın. Eğer arayı düzeltir, günahtan sakınırsanız Allah şüphesiz çok bağışlayıcı ve esirgeyicidir.', 5, 98, 176, 15937, 'Kadınlar', 'Medine', 92),
(623, 130, 4, 'Nisa', 'وَاِنْ يَتَفَرَّقَا يُغْنِ اللّٰهُ كُلاًّ مِنْ سَعَتِه۪ۜ وَكَانَ اللّٰهُ وَاسِعاً حَك۪يماً', 'Eğer (eşler) birbirinden ayrılırsa Allah, bol nimetinden her birini zenginleştirir (diğerine muhtaç olmaktan kurtarır); Allah´ın lütfu geniş, hikmeti büyüktür.', 5, 98, 176, 15937, 'Kadınlar', 'Medine', 92),
(624, 131, 4, 'Nisa', 'وَلِلّٰهِ مَا فِي السَّمٰوَاتِ وَمَا فِي الْاَرْضِۜ وَلَقَدْ وَصَّيْنَا الَّذ۪ينَ اُو۫تُوا الْكِتَابَ مِنْ قَبْلِكُمْ وَاِيَّاكُمْ اَنِ اتَّقُوا اللّٰهَۜ وَاِنْ تَكْفُرُوا فَاِنَّ لِلّٰهِ مَا فِي السَّمٰوَاتِ وَمَا فِي الْاَرْضِۜ وَكَانَ اللّٰهُ غَنِياًّ حَم۪يداً', 'Göklerde ve yerde ne varsa hepsi Allah´ındır. Sizden önce kendilerine Kitap verilenlere ve size «Allah´tan korkun» diye emrettik. Eğer inkâr ederseniz biliniz ki göklerde ve yerde ne varsa hepsi Allah´ındır. Allah hudutsuz zengindir, ziyadesiyle övgüye lâyıktır.', 5, 98, 176, 15937, 'Kadınlar', 'Medine', 92),
(625, 132, 4, 'Nisa', 'وَلِلّٰهِ مَا فِي السَّمٰوَاتِ وَمَا فِي الْاَرْضِۜ وَكَفٰى بِاللّٰهِ وَك۪يلاً', 'Göklerde ve yerde olanlar Allah´ındır. Vekil olarak Allah yeter.', 5, 98, 176, 15937, 'Kadınlar', 'Medine', 92),
(626, 133, 4, 'Nisa', 'اِنْ يَشَأْ يُذْهِبْكُمْ اَيُّهَا النَّاسُ وَيَأْتِ بِاٰخَر۪ينَۜ وَكَانَ اللّٰهُ عَلٰى ذٰلِكَ قَد۪يراً', 'Ey insanlar! Allah dilerse sizi yokluğa gönderip başkalarını getirir; Allah buna kadirdir.', 5, 98, 176, 15937, 'Kadınlar', 'Medine', 92),
(627, 134, 4, 'Nisa', 'مَنْ كَانَ يُر۪يدُ ثَوَابَ الدُّنْيَا فَعِنْدَ اللّٰهِ ثَوَابُ الدُّنْيَا وَالْاٰخِرَةِۜ وَكَانَ اللّٰهُ سَم۪يعاً بَص۪يراً۟', 'Kim dünya mükâfatını isterse (bilsin ki) dünyanın da ahiretin de mükâfatı Allah katındadır. Allah her şeyi işiten ve her şeyi görendir.', 5, 98, 176, 15937, 'Kadınlar', 'Medine', 92),
(628, 135, 4, 'Nisa', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا كُونُوا قَوَّام۪ينَ بِالْقِسْطِ شُهَدَٓاءَ لِلّٰهِ وَلَوْ عَلٰٓى اَنْفُسِكُمْ اَوِ الْوَالِدَيْنِ وَالْاَقْرَب۪ينَۚ اِنْ يَكُنْ غَنِياًّ اَوْ فَق۪يراً فَاللّٰهُ اَوْلٰى بِهِمَا فَلَا تَتَّبِعُوا الْهَوٰٓى اَنْ تَعْدِلُواۚ وَاِنْ تَلْـوُٓ۫ا اَوْ تُعْرِضُوا فَاِنَّ اللّٰهَ كَانَ بِمَا تَعْمَلُونَ خَب۪يراً', 'Ey iman edenler! Adaleti titizlikle ayakta tutan, kendiniz, ana-babanız ve akrabanız aleyhinde de olsa Allah için şahitlik eden kimseler olun. (Haklarında şahitlik ettikleriniz) zengin olsunlar, fakir olsunlar Allah onlara (sizden) daha yakındır. Hislerinize uyup adaletten sapmayın, (şahitliği) eğer, büker (doğru şahitlik etmez), yahut şâhidlik etmekten kaçınırsanız (biliniz ki) Allah yaptıklarınızdan haberdardır.', 5, 99, 176, 15937, 'Kadınlar', 'Medine', 92),
(629, 136, 4, 'Nisa', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُٓوا اٰمِنُوا بِاللّٰهِ وَرَسُولِه۪ وَالْكِتَابِ الَّذ۪ي نَزَّلَ عَلٰى رَسُولِه۪ وَالْكِتَابِ الَّـذ۪ٓي اَنْزَلَ مِنْ قَبْلُۜ وَمَنْ يَكْفُرْ بِاللّٰهِ وَمَلٰٓئِكَتِه۪ وَكُتُبِه۪ وَرُسُلِه۪ وَالْيَوْمِ الْاٰخِرِ فَقَدْ ضَلَّ ضَلَالاً بَع۪يداً', 'Ey iman edenler! Allah´a, Peygamberine, Peygamberine indirdiği Kitab´a ve daha önce indirdiği kitaba iman (da sebat) ediniz. Kim Allah´ı, meleklerini, kitaplarını, peygamberlerini ve kıyamet gününü inkâr ederse tam manasıyle sapıtmıştır.', 5, 99, 176, 15937, 'Kadınlar', 'Medine', 92),
(630, 137, 4, 'Nisa', 'اِنَّ الَّذ۪ينَ اٰمَنُوا ثُمَّ كَفَرُوا ثُمَّ اٰمَنُوا ثُمَّ كَفَرُوا ثُمَّ ازْدَادُوا كُفْراً لَمْ يَكُنِ اللّٰهُ لِيَغْفِرَ لَهُمْ وَلَا لِيَهْدِيَهُمْ سَب۪يلاًۜ', 'İman edip sonra inkâr edenleri, sonra yine iman edip tekrar inkâr edenleri, sonra da inkârlarını arttıranları Allah ne bağışlayacak, ne de onları doğru yola iletecektir.', 5, 99, 176, 15937, 'Kadınlar', 'Medine', 92),
(631, 138, 4, 'Nisa', 'بَشِّرِ الْمُنَافِق۪ينَ بِاَنَّ لَهُمْ عَذَاباً اَل۪يماًۙ', 'Münafıklara, kendileri için acı bir azap olduğunu müjdele!', 5, 99, 176, 15937, 'Kadınlar', 'Medine', 92),
(632, 139, 4, 'Nisa', 'اَلَّذ۪ينَ يَتَّخِذُونَ الْكَافِر۪ينَ اَوْلِيَٓاءَ مِنْ دُونِ الْمُؤْمِن۪ينَۜ اَيَبْتَغُونَ عِنْدَهُمُ الْعِزَّةَ فَاِنَّ الْعِزَّةَ لِلّٰهِ جَم۪يعاًۜ', 'Müminleri bırakıp da kâfirleri dost edinenler, onların yanında izzet (güç ve şeref) mi arıyorlar? Bilsinler ki bütün izzet yalnızca Allah´a aittir.', 5, 99, 176, 15937, 'Kadınlar', 'Medine', 92),
(633, 140, 4, 'Nisa', 'وَقَدْ نَزَّلَ عَلَيْكُمْ فِي الْكِتَابِ اَنْ اِذَا سَمِعْتُمْ اٰيَاتِ اللّٰهِ يُكْفَرُ بِهَا وَيُسْتَهْزَاُ بِهَا فَلَا تَقْعُدُوا مَعَهُمْ حَتّٰى يَخُوضُوا ف۪ي حَد۪يثٍ غَيْرِه۪ۘ اِنَّكُمْ اِذاً مِثْلُهُمْۜ اِنَّ اللّٰهَ جَامِـعُ الْمُنَافِق۪ينَ وَالْكَافِر۪ينَ ف۪ي جَهَنَّمَ جَم۪يعاًۙ', 'O (Allah), Kitap´ta size şöyle indirmiştir ki: Allah´ın âyetlerinin inkâr edildiğini yahut onlarla alay edildiğini işittiğiniz zaman, onlar bundan başka bir söze dalıncaya (konuya geçinceye) kadar kâfirlerle beraber oturmayın; yoksa siz de onlar gibi olursunuz. Elbette Allah, münafıkları ve kâfirleri cehennemde bir araya getirecektir.', 5, 99, 176, 15937, 'Kadınlar', 'Medine', 92),
(634, 141, 4, 'Nisa', 'اَلَّذ۪ينَ يَتَرَبَّصُونَ بِكُمْۚ فَاِنْ كَانَ لَكُمْ فَتْحٌ مِنَ اللّٰهِ قَالُٓوا اَلَمْ نَكُنْ مَعَكُمْۘ وَاِنْ كَانَ لِلْكَافِر۪ينَ نَص۪يبٌۙ قَالُٓوا اَلَمْ نَسْتَحْوِذْ عَلَيْكُمْ وَنَمْنَعْكُمْ مِنَ الْمُؤْمِن۪ينَۜ فَاللّٰهُ يَحْكُمُ بَيْنَكُمْ يَوْمَ الْقِيٰمَةِۜ وَلَنْ يَجْعَلَ اللّٰهُ لِلْكَافِر۪ينَ عَلَى الْمُؤْمِن۪ينَ سَب۪يلاً۟', 'Sizi gözetleyip duranlar, eğer size Allah´tan bir zafer (nasib) olursa, «Sizinle beraber değil miydik?» derler. Kâfirlerin (zaferden) bir nasipleri olursa (bu sefer de onlara), «Sizi yenip (öldürebileceğimiz halde öldürmeyip) müminlerden korumadık mı?» derler. Artık Allah kıyamet gününde aranızda hükmedecektir ve kâfirler için müminler aleyhine asla bir yol vermeyecektir.', 5, 100, 176, 15937, 'Kadınlar', 'Medine', 92),
(635, 142, 4, 'Nisa', 'اِنَّ الْمُنَافِق۪ينَ يُخَادِعُونَ اللّٰهَ وَهُوَ خَادِعُهُمْۚ وَاِذَا قَامُٓوا اِلَى الصَّلٰوةِ قَامُوا كُسَالٰىۙ يُرَٓاؤُ۫نَ النَّاسَ وَلَا يَذْكُرُونَ اللّٰهَ اِلَّا قَل۪يلاًۘ', 'Şüphesiz münafıklar Allah´a oyun etmeye kalkışıyorlar; halbuki Allah onların oyunlarını başlarına çevirmektedir. Onlar namaza kalktıkları zaman üşenerek kalkarlar, insanlara gösteriş yaparlar, Allah´ı da pek az hatıra getirirler.', 5, 100, 176, 15937, 'Kadınlar', 'Medine', 92);
INSERT INTO `Ayetler` (`id`, `AyetNo`, `SureNo`, `SureAdi`, `Metin`, `Mana`, `Cuzu`, `Sayfasi`, `AyetSayisi`, `HarfSayisi`, `AdAnlami`, `IndigiYer`, `InisSirasi`) VALUES
(636, 143, 4, 'Nisa', 'مُذَبْذَب۪ينَ بَيْنَ ذٰلِكَۗ لَٓا اِلٰى هٰٓؤُ۬لَٓاءِ وَلَٓا اِلٰى هٰٓؤُ۬لَٓاءِۜ وَمَنْ يُضْلِلِ اللّٰهُ فَلَنْ تَجِدَ لَهُ سَب۪يلاً', 'Bunların arasında bocalayıp durmaktalar; ne onlara (bağlanıyorlar) ne bunlara. Allah´ın şaşırttığı kimseye asla bir (çıkar) yol bulamazsın.', 5, 100, 176, 15937, 'Kadınlar', 'Medine', 92),
(637, 144, 4, 'Nisa', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا لَا تَتَّخِذُوا الْكَافِر۪ينَ اَوْلِيَٓاءَ مِنْ دُونِ الْمُؤْمِن۪ينَۜ اَتُر۪يدُونَ اَنْ تَجْعَلُوا لِلّٰهِ عَلَيْكُمْ سُلْطَاناً مُب۪يناً', 'Ey iman edenler! Müminleri bırakıp da kâfirleri dost edinmeyin; (bunu yaparak) Allah´a, aleyhinizde apaçık bir delil mi vermek istiyorsunuz?', 5, 100, 176, 15937, 'Kadınlar', 'Medine', 92),
(638, 145, 4, 'Nisa', 'اِنَّ الْمُنَافِق۪ينَ فِي الدَّرْكِ الْاَسْفَلِ مِنَ النَّارِۚ وَلَنْ تَجِدَ لَهُمْ نَص۪يراًۙ', 'Şüphe yok ki münafıklar cehennemin en alt katındadırlar. Artık onlara asla bir yardımcı bulamazsın.', 5, 100, 176, 15937, 'Kadınlar', 'Medine', 92),
(639, 146, 4, 'Nisa', 'اِلَّا الَّذ۪ينَ تَابُوا وَاَصْلَحُوا وَاعْتَصَمُوا بِاللّٰهِ وَاَخْلَصُوا د۪ينَهُمْ لِلّٰهِ فَاُو۬لٰٓئِكَ مَعَ الْمُؤْمِن۪ينَۜ وَسَوْفَ يُؤْتِ اللّٰهُ الْمُؤْمِن۪ينَ اَجْراً عَظ۪يماً', 'Ancak tevbe edip hallerini düzeltenler, Allah´a sımsıkı sarılıp dinlerini (ibadetlerini) yalnız onun için yapanlar başkadır. İşte bunlar (gerçekte) müminlerle beraberdirler ve Allah müminlere yakında büyük mükâfat verecektir.', 5, 100, 176, 15937, 'Kadınlar', 'Medine', 92),
(640, 147, 4, 'Nisa', 'مَا يَفْعَلُ اللّٰهُ بِعَذَابِكُمْ اِنْ شَكَرْتُمْ وَاٰمَنْتُمْۜ وَكَانَ اللّٰهُ شَاكِراً عَل۪يماً', 'Eğer siz iman eder ve şükrederseniz, Allah size neden azap etsin! Allah şükre karşılık veren ve her şeyi bilendir.', 5, 100, 176, 15937, 'Kadınlar', 'Medine', 92),
(641, 148, 4, 'Nisa', 'لَا يُحِبُّ اللّٰهُ الْجَهْرَ بِالسُّٓوءِ مِنَ الْقَوْلِ اِلَّا مَنْ ظُلِمَۜ وَكَانَ اللّٰهُ سَم۪يعاً عَل۪يماً', 'Allah kötü sözün açıkça söylenmesini sevmez; ancak haksızlığa uğrayan başka. Allah her şeyi işitici ve bilicidir.', 6, 101, 176, 15937, 'Kadınlar', 'Medine', 92),
(642, 149, 4, 'Nisa', 'اِنْ تُبْدُوا خَيْراً اَوْ تُخْفُوهُ اَوْ تَعْفُوا عَنْ سُٓوءٍ فَاِنَّ اللّٰهَ كَانَ عَفُواًّ قَد۪يراً', 'Bir iyiliği açıklar yahut gizlerseniz veya bir kötülüğü (açıklamayıp) affederseniz, şüphesiz Allah da ziyadesiyle affedici ve kadirdir.', 6, 101, 176, 15937, 'Kadınlar', 'Medine', 92),
(643, 150, 4, 'Nisa', 'اِنَّ الَّذ۪ينَ يَكْفُرُونَ بِاللّٰهِ وَرُسُلِه۪ وَيُر۪يدُونَ اَنْ يُفَرِّقُوا بَيْنَ اللّٰهِ وَرُسُلِه۪ وَيَقُولُونَ نُؤْمِنُ بِبَعْضٍ وَنَكْفُرُ بِبَعْضٍۙ وَيُر۪يدُونَ اَنْ يَتَّخِذُوا بَيْنَ ذٰلِكَ سَب۪يلاًۙ', 'Allah´ı ve peygamberlerini inkâr edenler ve (inanma hususunda) Allah ile peygamberlerini birbirinden ayırmak isteyip «Bir kısmına iman ederiz ama bir kısmına inanmayız» diyenler ve bunlar (iman ile küfür) arasında bir yol tutmak isteyenler yok mu;', 6, 101, 176, 15937, 'Kadınlar', 'Medine', 92),
(644, 151, 4, 'Nisa', 'اُو۬لٰٓئِكَ هُمُ الْكَافِرُونَ حَقاًّۚ وَاَعْتَدْنَا لِلْكَافِر۪ينَ عَذَاباً مُه۪يناً', 'İşte gerçekten kâfirler bunlardır. Ve biz kâfirlere alçaltıcı bir azap hazırlamışızdır.', 6, 101, 176, 15937, 'Kadınlar', 'Medine', 92),
(645, 152, 4, 'Nisa', 'وَالَّذ۪ينَ اٰمَنُوا بِاللّٰهِ وَرُسُلِه۪ وَلَمْ يُفَرِّقُوا بَيْنَ اَحَدٍ مِنْهُمْ اُو۬لٰٓئِكَ سَوْفَ يُؤْت۪يهِمْ اُجُورَهُمْۜ وَكَانَ اللّٰهُ غَفُوراً رَح۪يماً۟', 'Allah´a ve peygamberlerine iman eden ve onlardan hiçbirini diğerlerinden ayırmayanlara (gelince) işte Allah onlara bir gün mükâfatlarını verecektir. Allah çok bağışlayıcı ve esirgeyicidir.', 6, 101, 176, 15937, 'Kadınlar', 'Medine', 92),
(646, 153, 4, 'Nisa', 'يَسْـَٔلُكَ اَهْلُ الْكِتَابِ اَنْ تُنَزِّلَ عَلَيْهِمْ كِتَاباً مِنَ السَّمَٓاءِ فَقَدْ سَاَلُوا مُوسٰٓى اَكْبَرَ مِنْ ذٰلِكَ فَقَالُٓوا اَرِنَا اللّٰهَ جَهْرَةً فَاَخَذَتْهُمُ الصَّاعِقَةُ بِظُلْمِهِمْۚ ثُمَّ اتَّخَذُوا الْعِجْلَ مِنْ بَعْدِ مَا جَٓاءَتْهُمُ الْبَيِّنَاتُ فَعَفَوْنَا عَنْ ذٰلِكَۚ وَاٰتَيْنَا مُوسٰى سُلْطَاناً مُب۪يناً', 'Ehl-i kitap senden, kendilerine gökten bir kitap indirmeni istiyor. Onlar Musa´dan, bunun daha büyüğünü istemişler de, «Bize Allah´ı apaçık göster» demişlerdi. Zulümleri sebebiyle hemen onları yıldırım çarptı. Bilâhare kendilerine açık deliller geldikten sonra buzağıyı (tanrı) edindiler. Biz bunu da affettik. Ve Musa´ya apaçık delil (ve yetki) verdik.', 6, 101, 176, 15937, 'Kadınlar', 'Medine', 92),
(647, 154, 4, 'Nisa', 'وَرَفَعْنَا فَوْقَهُمُ الطُّورَ بِم۪يثَاقِهِمْ وَقُلْنَا لَهُمُ ادْخُلُوا الْبَابَ سُجَّداً وَقُلْنَا لَهُمْ لَا تَعْدُوا فِي السَّبْتِ وَاَخَذْنَا مِنْهُمْ م۪يثَاقاً غَل۪يظاً', 'Söz vermeleri (ni takviye) için Tûr´u başlarına diktik de onlara, «Baş eğerek kapıdan girin» dedik, «Cumartesi günü sınırı aşmayın» dedik. Kendilerinden sağlam söz aldık.', 6, 101, 176, 15937, 'Kadınlar', 'Medine', 92),
(648, 155, 4, 'Nisa', 'فَبِمَا نَقْضِهِمْ م۪يثَاقَهُمْ وَكُفْرِهِمْ بِاٰيَاتِ اللّٰهِ وَقَتْلِهِمُ الْاَنْبِيَٓاءَ بِغَيْرِ حَقٍّ وَقَوْلِهِمْ قُلُوبُنَا غُلْفٌۜ بَلْ طَبَعَ اللّٰهُ عَلَيْهَا بِكُفْرِهِمْ فَلَا يُؤْمِنُونَ اِلَّا قَل۪يلاًۖ', 'Sözlerinden dönmeleri, Allah´ın âyetlerini inkâr etmeleri, haksız yere peygamberleri öldürmeleri ve «Kalplerimiz kılıflanmıştır» demeleri sebebiyle (onları lânetledik, türlü belâlar verdik. Onların kalpleri kılıflı değildir;) tam aksine küfürleri sebebiyle Allah o kalpler üzerine mühür vurmuştur; pek azı müstesna artık iman etmezler.', 6, 102, 176, 15937, 'Kadınlar', 'Medine', 92),
(649, 156, 4, 'Nisa', 'وَبِكُفْرِهِمْ وَقَوْلِهِمْ عَلٰى مَرْيَمَ بُهْتَاناً عَظ۪يماًۙ', 'Bir de inkâr etmelerinden ve Meryem´in üzerine büyük bir iftira atmalarından;', 6, 102, 176, 15937, 'Kadınlar', 'Medine', 92),
(650, 157, 4, 'Nisa', 'وَقَوْلِهِمْ اِنَّا قَتَلْنَا الْمَس۪يحَ ع۪يسَى ابْنَ مَرْيَمَ رَسُولَ اللّٰهِۚ وَمَا قَتَلُوهُ وَمَا صَلَبُوهُ وَلٰكِنْ شُبِّهَ لَهُمْۜ وَاِنَّ الَّذ۪ينَ اخْتَلَفُوا ف۪يهِ لَف۪ي شَكٍّ مِنْهُۜ مَا لَهُمْ بِه۪ مِنْ عِلْمٍ اِلَّا اتِّبَاعَ الظَّنِّۚ وَمَا قَتَلُوهُ يَق۪يناًۙ', 'Ve «Allah elçisi Meryem oğlu İsa´yı öldürdük» demeleri yüzünden (onları lânetledik). Halbuki onu ne öldürdüler, ne de astılar; fakat (öldürdükleri) onlara İsa gibi gösterildi. Onun hakkında ihtilâfa düşenler bundan dolayı tam bir kararsızlık içindedirler; bu hususta zanna uymak dışında hiçbir (sağlam) bilgileri yoktur ve kesin olarak onu öldürmediler.', 6, 102, 176, 15937, 'Kadınlar', 'Medine', 92),
(651, 158, 4, 'Nisa', 'بَلْ رَفَعَهُ اللّٰهُ اِلَيْهِۜ وَكَانَ اللّٰهُ عَز۪يزاً حَك۪يماً', 'Bilâkis Allah onu (İsa´yı) kendi nezdine kaldırmıştır. Allah izzet ve hikmet sahibidir.', 6, 102, 176, 15937, 'Kadınlar', 'Medine', 92),
(652, 159, 4, 'Nisa', 'وَاِنْ مِنْ اَهْلِ الْكِتَابِ اِلَّا لَيُؤْمِنَنَّ بِه۪ قَبْلَ مَوْتِه۪ۚ وَيَوْمَ الْقِيٰمَةِ يَكُونُ عَلَيْهِمْ شَه۪يداًۚ', 'Ehl-i kitaptan her biri, ölümünden önce ona muhakkak iman edecektir. Kıyamet gününde de o, onlara şahit olacaktır.', 6, 102, 176, 15937, 'Kadınlar', 'Medine', 92),
(653, 160, 4, 'Nisa', 'فَبِظُلْمٍ مِنَ الَّذ۪ينَ هَادُوا حَرَّمْنَا عَلَيْهِمْ طَيِّبَاتٍ اُحِلَّتْ لَهُمْ وَبِصَدِّهِمْ عَنْ سَب۪يلِ اللّٰهِ كَث۪يراًۙ', '(160-161) Yahudilerin zulmü sebebiyle, bir de çok kimseyi Allah yolundan çevirmeleri, menedildikleri halde faizi almaları ve haksız (yollar) ile insanların mallarını yemeleri yüzünden kendilerine (daha önce) helâl kılınmış bulunan temiz ve iyi şeyleri onlara haram kıldık ve içlerinden inkâra sapanlara acı bir azap hazırladık.', 6, 102, 176, 15937, 'Kadınlar', 'Medine', 92),
(654, 161, 4, 'Nisa', 'وَاَخْذِهِمُ الرِّبٰوا وَقَدْ نُهُوا عَنْهُ وَاَكْلِهِمْ اَمْوَالَ النَّاسِ بِالْبَاطِلِۜ وَاَعْتَدْنَا لِلْكَافِر۪ينَ مِنْهُمْ عَذَاباً اَل۪يماً', '(160-161) Yahudilerin zulmü sebebiyle, bir de çok kimseyi Allah yolundan çevirmeleri, menedildikleri halde faizi almaları ve haksız (yollar) ile insanların mallarını yemeleri yüzünden kendilerine (daha önce) helâl kılınmış bulunan temiz ve iyi şeyleri onlara haram kıldık ve içlerinden inkâra sapanlara acı bir azap hazırladık.', 6, 102, 176, 15937, 'Kadınlar', 'Medine', 92),
(655, 162, 4, 'Nisa', 'لٰكِنِ الرَّاسِخُونَ فِي الْعِلْمِ مِنْهُمْ وَالْمُؤْمِنُونَ يُؤْمِنُونَ بِمَٓا اُنْزِلَ اِلَيْكَ وَمَٓا اُنْزِلَ مِنْ قَبْلِكَ وَالْمُق۪يم۪ينَ الصَّلٰوةَ وَالْمُؤْتُونَ الزَّكٰوةَ وَالْمُؤْمِنُونَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِۜ اُو۬لٰٓئِكَ سَنُؤْت۪يهِمْ اَجْراً عَظ۪يماً۟', 'Fakat içlerinden ilimde derinleşmiş olanlar ve müminler, sana indirilene ve senden önce indirilene iman edenler, namazı kılanlar, zekâtı verenler, Allah´a ve ahiret gününe inananlar var ya; işte onlara pek yakında büyük mükâfat vereceğiz.', 6, 102, 176, 15937, 'Kadınlar', 'Medine', 92),
(656, 163, 4, 'Nisa', 'اِنَّٓا اَوْحَيْنَٓا اِلَيْكَ كَمَٓا اَوْحَيْنَٓا اِلٰى نُوحٍ وَالنَّبِيّ۪نَ مِنْ بَعْدِه۪ۚ وَاَوْحَيْنَٓا اِلٰٓى اِبْرٰه۪يمَ وَاِسْمٰع۪يلَ وَاِسْحٰقَ وَيَعْقُوبَ وَالْاَسْبَاطِ وَع۪يسٰى وَاَيُّوبَ وَيُونُسَ وَهٰرُونَ وَسُلَيْمٰنَۚ وَاٰتَيْنَا دَاوُ۫دَ زَبُوراًۚ', 'Biz Nuh´a ve ondan sonraki peygamberlere vahyettiğimiz gibi sana da vahyettik. Ve (nitekim) İbrahim´e, İsmail´e, İshak´a, Yakub´a, esbâta (torunlara), İsa´ya, Eyyûb´e, Yunus´a, Harun´a ve Süleyman´a vahyettik. Davud´a da Zebûr´u verdik.', 6, 103, 176, 15937, 'Kadınlar', 'Medine', 92),
(657, 164, 4, 'Nisa', 'وَرُسُلاً قَدْ قَصَصْنَاهُمْ عَلَيْكَ مِنْ قَبْلُ وَرُسُلاً لَمْ نَقْصُصْهُمْ عَلَيْكَۜ وَكَلَّمَ اللّٰهُ مُوسٰى تَكْل۪يماًۚ', 'Bir kısım peygamberleri sana daha önce anlattık, bir kısmını ise sana anlatmadık. Ve Allah Musa ile gerçekten konuştu.', 6, 103, 176, 15937, 'Kadınlar', 'Medine', 92),
(658, 165, 4, 'Nisa', 'رُسُلاً مُبَشِّر۪ينَ وَمُنْذِر۪ينَ لِئَلَّا يَكُونَ لِلنَّاسِ عَلَى اللّٰهِ حُجَّةٌ بَعْدَ الرُّسُلِۜ وَكَانَ اللّٰهُ عَز۪يزاً حَك۪يماً', '(Yerine göre) müjdeleyici ve sakındırıcı olarak peygamberler gönderdik ki insanların peygamberlerden sonra Allah´a karşı bir bahaneleri olmasın! Allah izzet ve hikmet sahibidir.', 6, 103, 176, 15937, 'Kadınlar', 'Medine', 92),
(659, 166, 4, 'Nisa', 'لٰكِنِ اللّٰهُ يَشْهَدُ بِمَٓا اَنْزَلَ اِلَيْكَ اَنْزَلَهُ بِعِلْمِه۪ۚ وَالْمَلٰٓئِكَةُ يَشْهَدُونَۜ وَكَفٰى بِاللّٰهِ شَه۪يداً', 'Fakat Allah sana indirdiğine şahitlik eder; onu kendi ilmi ile indirdi. Melekler de (buna) şahitlik ederler. Ve şahit olarak Allah kâfîdir.', 6, 103, 176, 15937, 'Kadınlar', 'Medine', 92),
(660, 167, 4, 'Nisa', 'اِنَّ الَّذ۪ينَ كَفَرُوا وَصَدُّوا عَنْ سَب۪يلِ اللّٰهِ قَدْ ضَلُّوا ضَلَالاً بَع۪يداً', 'İnkâr eden ve (başkalarını da) Allah yolundan alıkoyanlar şüphesiz doğru yoldan çok uzaklaşmışlardır.', 6, 103, 176, 15937, 'Kadınlar', 'Medine', 92),
(661, 168, 4, 'Nisa', 'اِنَّ الَّذ۪ينَ كَفَرُوا وَظَلَمُوا لَمْ يَكُنِ اللّٰهُ لِيَغْفِرَ لَهُمْ وَلَا لِيَهْدِيَهُمْ طَر۪يقاًۙ', 'İnkâr edip zulmedenleri Allah asla bağışlayacak değildir. Onları (başka) bir yola iletecek de değildir.', 6, 103, 176, 15937, 'Kadınlar', 'Medine', 92),
(662, 169, 4, 'Nisa', 'اِلَّا طَر۪يقَ جَهَنَّمَ خَالِد۪ينَ ف۪يهَٓا اَبَداًۜ وَكَانَ ذٰلِكَ عَلَى اللّٰهِ يَس۪يراً', 'Ancak orada ebedî kalmak üzere cehennem onları yoluna (iletecektir). Bu da Allah´a çok kolaydır.', 6, 103, 176, 15937, 'Kadınlar', 'Medine', 92),
(663, 170, 4, 'Nisa', 'يَٓا اَيُّهَا النَّاسُ قَدْ جَٓاءَكُمُ الرَّسُولُ بِالْحَقِّ مِنْ رَبِّكُمْ فَاٰمِنُوا خَيْراً لَكُمْۜ وَاِنْ تَكْفُرُوا فَاِنَّ لِلّٰهِ مَا فِي السَّمٰوَاتِ وَالْاَرْضِۜ وَكَانَ اللّٰهُ عَل۪يماً حَك۪يماً', 'Ey insanlar! Resûl size Rabbinizden gerçeği getirdi (bunda şüphe yoktur), şu halde kendi iyiliğinize olarak (ona) iman edin. Eğer inkâr ederseniz, göklerde ve yerde ne varsa şüphesiz hepsi Allah´ındır. (O´nun sizin inanmanıza ihtiyacı yoktur). Allah geniş ilim ve hikmet sahibidir.', 6, 103, 176, 15937, 'Kadınlar', 'Medine', 92),
(664, 171, 4, 'Nisa', 'يَٓا اَهْلَ الْكِتَابِ لَا تَغْلُوا ف۪ي د۪ينِكُمْ وَلَا تَقُولُوا عَلَى اللّٰهِ اِلَّا الْحَقَّۜ اِنَّمَا الْمَس۪يحُ ع۪يسَى ابْنُ مَرْيَمَ رَسُولُ اللّٰهِ وَكَلِمَتُهُۚ اَلْقٰيهَٓا اِلٰى مَرْيَمَ وَرُوحٌ مِنْهُۘ فَاٰمِنُوا بِاللّٰهِ وَرُسُلِه۪ۚ وَلَا تَقُولُوا ثَلٰثَةٌۜ اِنْتَهُوا خَيْراً لَكُمْۜ اِنَّمَا اللّٰهُ اِلٰهٌ وَاحِدٌۜ سُبْحَانَهُٓ اَنْ يَكُونَ لَهُ وَلَدٌۢ لَهُ مَا فِي السَّمٰوَاتِ وَمَا فِي الْاَرْضِۜ وَكَفٰى بِاللّٰهِ وَك۪يلاً۟', 'Ey ehl-i kitap! Dininizde aşırı gitmeyin ve Allah hakkında, gerçekten başkasını söylemeyin. Meryem oğlu İsa Mesîh, ancak Allah´ın resûlüdür, (o) Allah´ın, Meryem´e ulaştırdığı «kün: Ol» kelimesi(nin eseri)dir, O´ndan bir ruhtur. (O´nun tarafından gönderilmiş, yahut teyit edilmiş, yahut da Cebrail tarafından üfürülmüş bir ruhtur). Şu halde Allah´a ve peygamberlerine iman edin. «(Tanrı) üçtür» demeyin, sizin için hayırlı olmak üzere bundan vazgeçin. Allah ancak bir tek Allah´tır. O, çocuğu olmaktan münezzehtir. Göklerde ve yerde ne varsa hepsi O´nundur. Vekil olarak Allah yeter.', 6, 104, 176, 15937, 'Kadınlar', 'Medine', 92),
(665, 172, 4, 'Nisa', 'لَنْ يَسْتَنْكِفَ الْمَس۪يحُ اَنْ يَكُونَ عَبْداً لِلّٰهِ وَلَا الْمَلٰٓئِكَةُ الْمُقَرَّبُونَۜ وَمَنْ يَسْتَنْكِفْ عَنْ عِبَادَتِه۪ وَيَسْتَكْبِرْ فَسَيَحْشُرُهُمْ اِلَيْهِ جَم۪يعاً', 'Ne Mesîh ve ne de Allah´a yakın melekler, Allah´ın kulu olmaktan geri dururlar. O´na kulluktan geri durup büyüklenen kimselerin hepsini (Allah) yakında huzuruna toplayacaktır.', 6, 104, 176, 15937, 'Kadınlar', 'Medine', 92),
(666, 173, 4, 'Nisa', 'فَاَمَّا الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ فَيُوَفّ۪يهِمْ اُجُورَهُمْ وَيَز۪يدُهُمْ مِنْ فَضْلِه۪ۚ وَاَمَّا الَّذ۪ينَ اسْتَنْكَفُوا وَاسْتَكْبَرُوا فَيُعَذِّبُهُمْ عَذَاباً اَل۪يماًۙ وَلَا يَجِدُونَ لَهُمْ مِنْ دُونِ اللّٰهِ وَلِياًّ وَلَا نَص۪يراً', 'İman edip iyi işler yapanlara (Allah) ecirlerini tam olarak verecek ve onlara lütfundan daha fazlasını da ihsan edecektir. Kulluğundan yüz çeviren ve kibirlenenlere gelince onlara acı bir şekilde azap edecektir. Onlar, kendileri için Allah´tan başka ne bir dost ve ne de bir yardımcı bulurlar. (Kendilerini Allah´ın azabından kurtaracak bir kimse bulamazlar.)', 6, 104, 176, 15937, 'Kadınlar', 'Medine', 92),
(667, 174, 4, 'Nisa', 'يَٓا اَيُّهَا النَّاسُ قَدْ جَٓاءَكُمْ بُرْهَانٌ مِنْ رَبِّكُمْ وَاَنْزَلْـنَٓا اِلَيْكُمْ نُوراً مُب۪يناً', 'Ey insanlar! Şüphesiz size Rabbinizden kesin bir delil geldi ve size apaçık bir nur indirdik.', 6, 104, 176, 15937, 'Kadınlar', 'Medine', 92),
(668, 175, 4, 'Nisa', 'فَاَمَّا الَّذ۪ينَ اٰمَنُوا بِاللّٰهِ وَاعْتَصَمُوا بِه۪ فَسَيُدْخِلُهُمْ ف۪ي رَحْمَةٍ مِنْهُ وَفَضْلٍۙ وَيَهْد۪يهِمْ اِلَيْهِ صِرَاطاً مُسْتَق۪يماًۜ', 'Allah´a iman edip O´na sımsıkı sarılanlara gelince, Allah onları kendinden bir rahmet ve lütuf (deryası) içine daldıracak ve onları kendine doğru (giden) bir yola götürecektir.', 6, 104, 176, 15937, 'Kadınlar', 'Medine', 92),
(669, 176, 4, 'Nisa', 'يَسْتَفْتُونَكَۜ قُلِ اللّٰهُ يُفْت۪يكُمْ فِي الْكَلَالَةِۜ اِنِ امْرُؤٌا هَلَكَ لَيْسَ لَهُ وَلَدٌ وَلَهُٓ اُخْتٌ فَلَهَا نِصْفُ مَا تَرَكَۚ وَهُوَ يَرِثُـهَٓا اِنْ لَمْ يَكُنْ لَهَا وَلَدٌۜ فَاِنْ كَانَتَا اثْنَتَيْنِ فَلَهُمَا الثُّلُثَانِ مِمَّا تَرَكَۜ وَاِنْ كَانُٓوا اِخْوَةً رِجَالاً وَنِسَٓاءً فَلِلذَّكَرِ مِثْلُ حَظِّ الْاُنْثَيَيْنِۜ يُبَيِّنُ اللّٰهُ لَكُمْ اَنْ تَضِلُّواۜ وَاللّٰهُ بِكُلِّ شَيْءٍ عَل۪يمٌ', 'Senden fetva isterler. De ki: «Allah, babası ve çocuğu olmayan kimsenin mirası hakkındaki hükmü şöyle açıklıyor: Eğer çocuğu olmayan bir kimse ölür de onun bir kızkardeşi bulunursa, bıraktığının yarısı bunundur. Kızkardeş ölüp çocuğu olmazsa erkek kardeş de ona vâris olur. Kızkardeşler iki tane olursa (erkek kardeşlerinin) bıraktığının üçte ikisi onlarındır. Eğer erkekli kadınlı daha fazla kardeş mevcut ise erkeğin hakkı, iki kadın payı kadardır. Şaşırmamanız için Allah size açıklama yapıyor. Allah her şeyi bilmektedir.', 6, 105, 176, 15937, 'Kadınlar', 'Medine', 92),
(670, 1, 5, 'Maide', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُٓوا اَوْفُوا بِالْعُقُودِۜ اُحِلَّتْ لَكُمْ بَه۪يمَةُ الْاَنْعَامِ اِلَّا مَا يُتْلٰى عَلَيْكُمْ غَيْرَ مُحِلِّي الصَّيْدِ وَاَنْتُمْ حُرُمٌۜ اِنَّ اللّٰهَ يَحْكُمُ مَا يُر۪يدُ', 'Ey iman edenler! Akitleri(n gereğini) yerine getiriniz. İhramlı iken avlanmayı helal saymamak üzere (aşağıda) size okunacaklar dışında kalan hayvanlar, sizin için helâl kılındı. Allah dilediğine hükmeder.', 6, 105, 120, 11892, 'Sofra', 'Medine', 112),
(671, 2, 5, 'Maide', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا لَا تُحِلُّوا شَعَٓائِرَ اللّٰهِ وَلَا الشَّهْرَ الْحَرَامَ وَلَا الْهَدْيَ وَلَا الْقَلَٓائِدَ وَلَٓا آٰمّ۪ينَ الْبَيْتَ الْحَرَامَ يَبْتَغُونَ فَضْلاً مِنْ رَبِّهِمْ وَرِضْوَاناًۜ وَاِذَا حَلَلْتُمْ فَاصْطَادُواۜ وَلَا يَجْرِمَنَّكُمْ شَنَاٰنُ قَوْمٍ اَنْ صَدُّوكُمْ عَنِ الْمَسْجِدِ الْحَرَامِ اَنْ تَعْتَدُواۢ وَتَعَاوَنُوا عَلَى الْبِرِّ وَالتَّقْوٰىۖ وَلَا تَعَاوَنُوا عَلَى الْاِثْمِ وَالْعُدْوَانِۖ وَاتَّقُوا اللّٰهَۜ اِنَّ اللّٰهَ شَد۪يدُ الْعِقَابِ', 'Ey iman edenler! Allah´ın (koyduğu, dinî) işaretlerine, haram aya, (Allah´a hediye edilmiş) kurbana, (ondaki) gerdanlıklara, Rablerinin lütuf ve rızasını arayarak Beyt-i Haram´a yönelmiş kimselere (tecavüz ve) saygısızlık etmeyin. İhramdan çıkınca avlanabilirsiniz. Mescid-i Haram´a girmenizi önledikleri için bir topluma karşı beslediğiniz kin sizi tecavüze sevketmesin! İyilik ve (Allah´ın yasaklarından) sakınma üzerinde yardımlaşın, günah ve düşmanlık üzerine yardımlaşmayın. Allah´tan korkun; çünkü Allah´ın cezası çetindir.', 6, 105, 120, 11892, 'Sofra', 'Medine', 112),
(672, 3, 5, 'Maide', 'حُرِّمَتْ عَلَيْكُمُ الْمَيْتَةُ وَالدَّمُ وَلَحْمُ الْخِنْز۪يرِ وَمَٓا اُهِلَّ لِغَيْرِ اللّٰهِ بِه۪ وَالْمُنْخَنِقَةُ وَالْمَوْقُوذَةُ وَالْمُتَرَدِّيَةُ وَالنَّط۪يحَةُ وَمَٓا اَكَلَ السَّبُعُ اِلَّا مَا ذَكَّيْتُمْ وَمَا ذُبِحَ عَلَى النُّصُبِ وَاَنْ تَسْتَقْسِمُوا بِالْاَزْلَامِۜ ذٰلِكُمْ فِسْقٌۜ اَلْيَوْمَ يَـئِسَ الَّذ۪ينَ كَفَرُوا مِنْ د۪ينِكُمْ فَلَا تَخْشَوْهُمْ وَاخْشَوْنِۜ اَلْيَوْمَ اَكْمَلْتُ لَكُمْ د۪ينَكُمْ وَاَتْمَمْتُ عَلَيْكُمْ نِعْمَت۪ي وَرَض۪يتُ لَكُمُ الْاِسْلَامَ د۪يناًۜ فَمَنِ اضْطُرَّ ف۪ي مَخْمَصَةٍ غَيْرَ مُتَجَانِفٍ لِاِثْمٍۙ فَاِنَّ اللّٰهَ غَفُورٌ رَح۪يمٌ', 'Leş, kan, domuz eti, Allah´tan başkası adına boğazlanan, boğulmuş, (taş, ağaç vb. ile) vurulup öldürülmüş, yukarıdan yuvarlanıp ölmüş, boynuzlanıp ölmüş (hayvanlar ile) canavarların yediği hayvanlar -ölmeden yetişip kestikleriniz müstesna- dikili taşlar (putlar) üzerine boğazlanmış hayvanlar ve fal oklarıyle kısmet aramanız size haram kılındı. Bunlar yoldan çıkmaktır. Bugün kâfirler, sizin dininizden (onu yok etmekten) ümit kesmişlerdir. Artık onlardan korkmayın, benden korkun. Bugün size dininizi ikmal ettim, üzerinize nimetimi tamamladım ve sizin için din olarak İslâm´ı beğendim. Kim, gönülden günaha yönelmiş olmamak üzere açlık halinde dara düşerse (haram etlerden yiyebilir). Çünkü Allah çok bağışlayıcı ve esirgeyicidir.', 6, 106, 120, 11892, 'Sofra', 'Medine', 112),
(673, 4, 5, 'Maide', 'يَسْـَٔلُونَكَ مَاذَٓا اُحِلَّ لَهُمْۜ قُلْ اُحِلَّ لَكُمُ الطَّيِّبَاتُۙ وَمَا عَلَّمْتُمْ مِنَ الْجَوَارِحِ مُكَلِّب۪ينَ تُعَلِّمُونَهُنَّ مِمَّا عَلَّمَكُمُ اللّٰهُۘ فَكُلُوا مِمَّٓا اَمْسَكْنَ عَلَيْكُمْ وَاذْكُرُوا اسْمَ اللّٰهِ عَلَيْهِۖ وَاتَّقُوا اللّٰهَۜ اِنَّ اللّٰهَ سَر۪يعُ الْحِسَابِ', 'Kendileri için nelerin helâl kılındığını sana soruyorlar; de ki: Bütün iyi ve temiz şeyler size helâl kılınmıştır. Allah´ın size öğrettiğinden öğretip avcı hale getirdiğiniz hayvanların sizin için yakaladıklarından da yeyin ve üzerine Allah´ın adını anın (besmele çekin). Allah´tan korkun. Allah´ın hesabı pek çabuktur.', 6, 106, 120, 11892, 'Sofra', 'Medine', 112),
(674, 5, 5, 'Maide', 'اَلْيَوْمَ اُحِلَّ لَكُمُ الطَّيِّبَاتُۜ وَطَعَامُ الَّذ۪ينَ اُو۫تُوا الْكِتَابَ حِلٌّ لَكُمْۖ وَطَعَامُكُمْ حِلٌّ لَهُمْۘ وَالْمُحْصَنَاتُ مِنَ الْمُؤْمِنَاتِ وَالْمُحْصَنَاتُ مِنَ الَّذ۪ينَ اُو۫تُوا الْكِتَابَ مِنْ قَبْلِكُمْ اِذَٓا اٰتَيْتُمُوهُنَّ اُجُورَهُنَّ مُحْصِن۪ينَ غَيْرَ مُسَافِح۪ينَ وَلَا مُتَّخِذ۪ٓي اَخْدَانٍۜ وَمَنْ يَكْفُرْ بِالْا۪يمَانِ فَقَدْ حَبِطَ عَمَلُهُۘ وَهُوَ فِي الْاٰخِرَةِ مِنَ الْخَاسِر۪ينَ۟', 'Bugün size temiz ve iyi şeyler helâl kılınmıştır. Kendilerine kitap verilenlerin (yahudi, hıristiyan vb. nin) yiyeceği size helâldir, sizin yiyeceğiniz de onlara helâldir. Mümin kadınlardan iffetli olanlar ile daha önce kendilerine kitap verilenlerden iffetli kadınlar da, mehirlerini vermeniz şartıyla, namuslu olmak, zina etmemek ve gizli dost tutmamak üzere size helâldir. Kim (İslâmî hükümlere) inanmayı kabul etmezse onun ameli boşa gitmiştir. O, ahirette de ziyana uğrayanlardandır.', 6, 106, 120, 11892, 'Sofra', 'Medine', 112),
(675, 6, 5, 'Maide', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُٓوا اِذَا قُمْتُمْ اِلَى الصَّلٰوةِ فَاغْسِلُوا وُجُوهَكُمْ وَاَيْدِيَكُمْ اِلَى الْمَرَافِقِ وَامْسَحُوا بِرُؤُ۫سِكُمْ وَاَرْجُلَكُمْ اِلَى الْكَعْبَيْنِۜ وَاِنْ كُنْتُمْ جُنُباً فَاطَّهَّرُواۜ وَاِنْ كُنْتُمْ مَرْضٰٓى اَوْ عَلٰى سَفَرٍ اَوْ جَٓاءَ اَحَدٌ مِنْكُمْ مِنَ الْغَٓائِطِ اَوْ لٰمَسْتُمُ النِّسَٓاءَ فَلَمْ تَجِدُوا مَٓاءً فَتَيَمَّمُوا صَع۪يداً طَيِّباً فَامْسَحُوا بِوُجُوهِكُمْ وَاَيْد۪يكُمْ مِنْهُۜ مَا يُر۪يدُ اللّٰهُ لِيَجْعَلَ عَلَيْكُمْ مِنْ حَرَجٍ وَلٰكِنْ يُر۪يدُ لِيُطَهِّرَكُمْ وَلِيُتِمَّ نِعْمَتَهُ عَلَيْكُمْ لَعَلَّكُمْ تَشْكُرُونَ', 'Ey iman edenler! Namaz kılmaya kalktığınız zaman yüzlerinizi, dirseklerinize kadar ellerinizi yıkayın; başlarınızı meshedip, topuklara kadar ayaklarınızı da (yıkayın). Eğer cünüp oldunuz ise, boy abdesti alın. Hasta, yahut yolculuk halinde bulunursanız, yahut biriniz tuvaletten gelirse, yahut da kadınlara dokunmuşsanız (cinsî birleşme yapmışsanız) ve bu hallerde su bulamamışsanız temiz toprakla teyemmüm edin de yüzünüzü ve (dirseklere kadar) ellerinizi onunla meshedin. Allah size herhangi bir güçlük çıkarmak istemez; fakat sizi tertemiz kılmak ve size (ihsan ettiği) nimetini tamamlamak ister; umulur ki şükredersiniz.', 6, 107, 120, 11892, 'Sofra', 'Medine', 112),
(676, 7, 5, 'Maide', 'وَاذْكُرُوا نِعْمَةَ اللّٰهِ عَلَيْكُمْ وَم۪يثَاقَهُ الَّذ۪ي وَاثَقَكُمْ بِه۪ٓۙ اِذْ قُلْتُمْ سَمِعْنَا وَاَطَعْنَاۘ وَاتَّقُوا اللّٰهَۜ اِنَّ اللّٰهَ عَل۪يمٌ بِذَاتِ الصُّدُورِ', 'Allah´ın size olan nimetini, «Duyduk ve kabul ettik» dediğiniz zaman sizi bununla bağladığı (O´na verdiğiniz) sözü hatırlayın ve Allah´tan korkun. Şüphesiz Allah, kalblerin içindekini bilmektedir.', 6, 107, 120, 11892, 'Sofra', 'Medine', 112),
(677, 8, 5, 'Maide', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا كُونُوا قَوَّام۪ينَ لِلّٰهِ شُهَدَٓاءَ بِالْقِسْطِۘ وَلَا يَجْرِمَنَّكُمْ شَنَاٰنُ قَوْمٍ عَلٰٓى اَلَّا تَعْدِلُواۜ اِعْدِلُوا۠ هُوَ اَقْرَبُ لِلتَّقْوٰىۘ وَاتَّقُوا اللّٰهَۜ اِنَّ اللّٰهَ خَب۪يرٌ بِمَا تَعْمَلُونَ', 'Ey iman edenler! Allah için hakkı ayakta tutan, adaletle şahitlik eden kimseler olun. Bir topluluğa duyduğunuz kin, sizi âdil davranmamaya itmesin. Adaletli olun; bu, Allah korkusuna daha çok yakışan (bir davranış) tır. Allah´a isyandan sakının. Allah yaptıklarınızı hakkıyle bilmektedir.', 6, 107, 120, 11892, 'Sofra', 'Medine', 112),
(678, 9, 5, 'Maide', 'وَعَدَ اللّٰهُ الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِۙ لَهُمْ مَغْفِرَةٌ وَاَجْرٌ عَظ۪يمٌ', 'Allah, iman eden ve iyi şeyler yapanlara söz vermiştir; onlara bağışlama ve büyük mükâfat vardır.', 6, 107, 120, 11892, 'Sofra', 'Medine', 112),
(679, 10, 5, 'Maide', 'وَالَّذ۪ينَ كَفَرُوا وَكَذَّبُوا بِاٰيَاتِنَٓا اُو۬لٰٓئِكَ اَصْحَابُ الْجَح۪يمِ', 'İnkâr eden ve âyetlerimizi yalanlayanlara gelince onlar cehennemliklerdir.', 6, 108, 120, 11892, 'Sofra', 'Medine', 112),
(680, 11, 5, 'Maide', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا اذْكُرُوا نِعْمَتَ اللّٰهِ عَلَيْكُمْ اِذْ هَمَّ قَوْمٌ اَنْ يَبْسُطُٓوا اِلَيْكُمْ اَيْدِيَهُمْ فَكَفَّ اَيْدِيَهُمْ عَنْكُمْۚ وَاتَّقُوا اللّٰهَۜ وَعَلَى اللّٰهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُونَ۟', 'Ey iman edenler! Allah´ın size olan nimetini unutmayın; hani bir topluluk size el uzatmaya yeltenmişti de Allah, onların ellerini sizden çekmişti. Allah´tan korkun ve müminler yalnızca Allah´a güvensinler.', 6, 108, 120, 11892, 'Sofra', 'Medine', 112),
(681, 12, 5, 'Maide', 'وَلَقَدْ اَخَذَ اللّٰهُ م۪يثَاقَ بَن۪ٓي اِسْرَٓائ۪لَۚ وَبَعَثْنَا مِنْهُمُ اثْنَيْ عَشَرَ نَق۪يباًۜ وَقَالَ اللّٰهُ اِنّ۪ي مَعَكُمْۜ لَئِنْ اَقَمْتُمُ الصَّلٰوةَ وَاٰتَيْتُمُ الزَّكٰوةَ وَاٰمَنْتُمْ بِرُسُل۪ي وَعَزَّرْتُمُوهُمْ وَاَقْرَضْتُمُ اللّٰهَ قَرْضاً حَسَناً لَاُكَفِّرَنَّ عَنْكُمْ سَيِّـَٔاتِكُمْ وَلَاُدْخِلَنَّكُمْ جَنَّاتٍ تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُۚ فَمَنْ كَفَرَ بَعْدَ ذٰلِكَ مِنْكُمْ فَقَدْ ضَلَّ سَوَٓاءَ السَّب۪يلِ', 'Andolsun ki Allah, İsrailoğullarından söz almıştı. (Kefil olarak) içlerinden on iki de başkan göndermiştik. Allah onlara şöyle demişti: Ben sizinle beraberim. Eğer namazı dosdoğru kılar, zekâtı verir, peygamberlerime inanır, onları desteklerseniz ve Allah´a güzel borç verirseniz (ihtiyacı olanlara Allah rızası için faizsiz borç verirseniz) andolsun ki sizin günahlarınızı örterim ve sizi, zemininden ırmaklar akan cennetlere sokarım. Bundan sonra sizden kim inkâr yolunu tutarsa doğru yoldan sapmış olur.', 6, 108, 120, 11892, 'Sofra', 'Medine', 112),
(682, 13, 5, 'Maide', 'فَبِمَا نَقْضِهِمْ م۪يثَاقَهُمْ لَعَنَّاهُمْ وَجَعَلْنَا قُلُوبَهُمْ قَاسِيَةًۚ يُحَرِّفُونَ الْكَلِمَ عَنْ مَوَاضِعِه۪ۙ وَنَسُوا حَظاًّ مِمَّا ذُكِّرُوا بِه۪ۚ وَلَا تَزَالُ تَطَّلِعُ عَلٰى خَٓائِنَةٍ مِنْهُمْ اِلَّا قَل۪يلاً مِنْهُمْ فَاعْفُ عَنْهُمْ وَاصْفَحْۜ اِنَّ اللّٰهَ يُحِبُّ الْمُحْسِن۪ينَ', 'Sözlerini bozmaları sebebiyle onları lânetledik ve kalplerini katılaştırdık. Onlar kelimelerin yerlerini değiştirirler (kitaplarını tahrif ederler). Kendilerine öğretilen ahkâmın (Tevrat´ın) önemli bir bölümünü de unuttular. İçlerinden pek azı hariç, onlardan daima bir hainlik görürsün. Yine de sen onları affet ve aldırış etme. Şüphesiz Allah iyilik edenleri sever.', 6, 108, 120, 11892, 'Sofra', 'Medine', 112),
(683, 14, 5, 'Maide', 'وَمِنَ الَّذ۪ينَ قَالُٓوا اِنَّا نَصَارٰٓى اَخَذْنَا م۪يثَاقَهُمْ فَنَسُوا حَظاًّ مِمَّا ذُكِّرُوا بِه۪ۖ فَاَغْرَيْنَا بَيْنَهُمُ الْعَدَاوَةَ وَالْبَغْضَٓاءَ اِلٰى يَوْمِ الْقِيٰمَةِۜ وَسَوْفَ يُنَبِّئُهُمُ اللّٰهُ بِمَا كَانُوا يَصْنَعُونَ', '«Biz hıristiyanlarız» diyenlerden de kesin sözlerini almıştık ama onlar da kendilerine zikredilen (verilen öğütlerin veya Kitab´ın) önemli bir bölümünü unuttular. Bu sebeple kıyamete kadar aralarına düşmanlık ve kin saldık. Yakında Allah onlara yaptıklarını haber verecektir.', 6, 109, 120, 11892, 'Sofra', 'Medine', 112),
(684, 15, 5, 'Maide', 'يَٓا اَهْلَ الْكِتَابِ قَدْ جَٓاءَكُمْ رَسُولُنَا يُبَيِّنُ لَكُمْ كَث۪يراً مِمَّا كُنْتُمْ تُخْفُونَ مِنَ الْكِتَابِ وَيَعْفُوا عَنْ كَث۪يرٍۜ قَدْ جَٓاءَكُمْ مِنَ اللّٰهِ نُورٌ وَكِتَابٌ مُب۪ينٌۙ', 'Ey ehl-i kitap! Resûlümüz size Kitap´tan gizlemekte olduğunuz birçok şeyi açıklamak üzere geldi; birçok (kusurunuzu) da affediyor. Gerçekten size Allah´tan bir nur, apaçık bir kitap geldi.', 6, 109, 120, 11892, 'Sofra', 'Medine', 112),
(685, 16, 5, 'Maide', 'يَهْد۪ي بِهِ اللّٰهُ مَنِ اتَّبَعَ رِضْوَانَهُ سُبُلَ السَّلَامِ وَيُخْرِجُهُمْ مِنَ الظُّلُمَاتِ اِلَى النُّورِ بِاِذْنِه۪ وَيَهْد۪يهِمْ اِلٰى صِرَاطٍ مُسْتَق۪يمٍ', 'Rızasını arayanı Allah onunla kurtuluş yollarına götürür ve onları iradesiyle karanlıklardan aydınlığa çıkarır, dosdoğru bir yola iletir.', 6, 109, 120, 11892, 'Sofra', 'Medine', 112),
(686, 17, 5, 'Maide', 'لَقَدْ كَفَرَ الَّذ۪ينَ قَالُٓوا اِنَّ اللّٰهَ هُوَ الْمَس۪يحُ ابْنُ مَرْيَمَۜ قُلْ فَمَنْ يَمْلِكُ مِنَ اللّٰهِ شَيْـٔاً اِنْ اَرَادَ اَنْ يُهْلِكَ الْمَس۪يحَ ابْنَ مَرْيَمَ وَاُمَّهُ وَمَنْ فِي الْاَرْضِ جَم۪يعاًۜ وَلِلّٰهِ مُلْكُ السَّمٰوَاتِ وَالْاَرْضِ وَمَا بَيْنَهُمَاۜ يَخْلُقُ مَا يَشَٓاءُۜ وَاللّٰهُ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌ', '«Şüphesiz Allah, Meryem oğlu Mesîh´dir» diyenler andolsun ki kâfir olmuşlardır. De ki: Öyleyse Allah, Meryem oğlu Mesîh´i, anasını ve yeryüzündekilerin hepsini imha etmek isterse Allah´a kim bir şey yapabilecektir (O´na kim bir şeyle engel olabilecektir)! Göklerde, yerde ve ikisi arasında ne varsa hepsinin mülkiyeti Allah´a aittir. O dilediğini yaratır ve Allah her şeye tam manasıyle kadirdir.', 6, 109, 120, 11892, 'Sofra', 'Medine', 112),
(687, 18, 5, 'Maide', 'وَقَالَتِ الْيَهُودُ وَالنَّصَارٰى نَحْنُ اَبْنَٓاءُ اللّٰهِ وَاَحِبَّٓاؤُ۬هُۜ قُلْ فَلِمَ يُعَذِّبُكُمْ بِذُنُوبِكُمْۜ بَلْ اَنْتُمْ بَشَرٌ مِمَّنْ خَلَقَۜ يَغْفِرُ لِمَنْ يَشَٓاءُ وَيُعَذِّبُ مَنْ يَشَٓاءُۜ وَلِلّٰهِ مُلْكُ السَّمٰوَاتِ وَالْاَرْضِ وَمَا بَيْنَهُمَاۘ وَاِلَيْهِ الْمَص۪يرُ', 'Yahudiler ve hıristiyanlar «Biz Allah´ın oğulları ve sevgilileriyiz» dediler. De ki: Öyleyse günahlarınızdan dolayı size niçin azap ediyor? Doğrusu siz de O´nun yarattığı insanlardansınız. O, dilediğini bağışlar ve dilediğine azap eder. Göklerde, yerde ve ikisinin arasında ne varsa mülkiyeti Allah´a aittir. Sonunda dönüş de ancak O´nadır.', 6, 110, 120, 11892, 'Sofra', 'Medine', 112),
(688, 19, 5, 'Maide', 'يَٓا اَهْلَ الْكِتَابِ قَدْ جَٓاءَكُمْ رَسُولُنَا يُبَيِّنُ لَكُمْ عَلٰى فَتْرَةٍ مِنَ الرُّسُلِ اَنْ تَقُولُوا مَا جَٓاءَنَا مِنْ بَش۪يرٍ وَلَا نَذ۪يرٍۘ فَقَدْ جَٓاءَكُمْ بَش۪يرٌ وَنَذ۪يرٌۜ وَاللّٰهُ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌ۟', 'Ey ehl-i kitap! Peygamberlerin arası kesildiği bir sırada size elçimiz geldi. Gerçekleri size açıklıyor ki (kıyamette): «Bize bir müjdeleyici ve uyarıcı gelmedi» demiyesiniz. İşte size müjdeleyici ve uyarıcı gelmiştir. Allah her şeye hakkıyle kadirdir.', 6, 110, 120, 11892, 'Sofra', 'Medine', 112),
(689, 20, 5, 'Maide', 'وَاِذْ قَالَ مُوسٰى لِقَوْمِه۪ يَا قَوْمِ اذْكُرُوا نِعْمَةَ اللّٰهِ عَلَيْكُمْ اِذْ جَعَلَ ف۪يكُمْ اَنْبِيَٓاءَ وَجَعَلَكُمْ مُلُوكاًۗ وَاٰتٰيكُمْ مَا لَمْ يُؤْتِ اَحَداً مِنَ الْعَالَم۪ينَ', 'Bir zamanlar Musa, kavmine şöyle demişti: Ey kavmim! Allah´ın size (lütfettiği) nimetini hatırlayın; zira O, içinizden peygamberler çıkardı ve sizi hükümdarlar kıldı. Âlemlerde hiçbir kimseye vermediğini size verdi.', 6, 110, 120, 11892, 'Sofra', 'Medine', 112),
(690, 21, 5, 'Maide', 'يَا قَوْمِ ادْخُلُوا الْاَرْضَ الْمُقَدَّسَةَ الَّت۪ي كَتَبَ اللّٰهُ لَكُمْ وَلَا تَرْتَدُّوا عَلٰٓى اَدْبَارِكُمْ فَتَنْقَلِبُوا خَاسِر۪ينَ', 'Ey kavmim! Allah´ın size (vatan olarak) yazdığı mukaddes toprağa girin ve arkanıza dönmeyin, yoksa kaybederek dönmüş olursunuz.', 6, 110, 120, 11892, 'Sofra', 'Medine', 112),
(691, 22, 5, 'Maide', 'قَالُوا يَا مُوسٰٓى اِنَّ ف۪يهَا قَوْماً جَبَّار۪ينَۗ وَاِنَّا لَنْ نَدْخُلَهَا حَتّٰى يَخْرُجُوا مِنْهَاۚ فَاِنْ يَخْرُجُوا مِنْهَا فَاِنَّا دَاخِلُونَ', 'Onlar şu cevabı verdiler: Yâ Musa! Orada zorba bir toplum var; onlar oradan çıkmadıkça biz oraya asla girmeyeceğiz. Eğer oradan çıkarlarsa biz de hemen gireriz.', 6, 110, 120, 11892, 'Sofra', 'Medine', 112),
(692, 23, 5, 'Maide', 'قَالَ رَجُلَانِ مِنَ الَّذ۪ينَ يَخَافُونَ اَنْعَمَ اللّٰهُ عَلَيْهِمَا ادْخُلُوا عَلَيْهِمُ الْبَابَۚ فَاِذَا دَخَلْتُمُوهُ فَاِنَّكُمْ غَالِبُونَ وَعَلَى اللّٰهِ فَتَوَكَّلُٓوا اِنْ كُنْتُمْ مُؤْمِن۪ينَ', 'Korkanların içinden Allah´ın kendilerine lütufda bulunduğu iki kişi şöyle dedi: Onların üzerine kapıdan girin; oraya bir girdiniz mi artık siz zaferi kazanmışsınızdır. Eğer müminler iseniz ancak Allah´a güvenin.', 6, 110, 120, 11892, 'Sofra', 'Medine', 112),
(693, 24, 5, 'Maide', 'قَالُوا يَا مُوسٰٓى اِنَّا لَنْ نَدْخُلَـهَٓا اَبَداً مَا دَامُوا ف۪يهَا فَاذْهَبْ اَنْتَ وَرَبُّكَ فَقَاتِلَٓا اِنَّا هٰهُنَا قَاعِدُونَ', '«Ey Musa! Onlar orada bulundukları müddetçe biz oraya asla girmeyiz; şu halde sen ve Rabbin gidin savaşın; biz burada oturacağız» dediler.', 6, 111, 120, 11892, 'Sofra', 'Medine', 112),
(694, 25, 5, 'Maide', 'قَالَ رَبِّ اِنّ۪ي لَٓا اَمْلِكُ اِلَّا نَفْس۪ي وَاَخ۪ي فَافْرُقْ بَيْنَنَا وَبَيْنَ الْقَوْمِ الْفَاسِق۪ينَ', 'Musa: «Rabbim! Ben kendimden ve kardeşimden başkasına hakim olamıyorum; bizimle, bu yoldan çıkmış toplumun arasını ayır» dedi.', 6, 111, 120, 11892, 'Sofra', 'Medine', 112),
(695, 26, 5, 'Maide', 'قَالَ فَاِنَّهَا مُحَرَّمَةٌ عَلَيْهِمْ اَرْبَع۪ينَ سَنَةًۚ يَت۪يهُونَ فِي الْاَرْضِ فَلَا تَأْسَ عَلَى الْقَوْمِ الْفَاسِق۪ينَ۟', 'Allah, «Öyleyse orası (arz-ı mukaddes) onlara kırk yıl yasaklanmıştır; (bu müddet içinde) yeryüzünde şaşkın şaşkın dolaşacaklar. Artık sen, yoldan çıkmış toplum için üzülme» dedi.', 6, 111, 120, 11892, 'Sofra', 'Medine', 112),
(696, 27, 5, 'Maide', 'وَاتْلُ عَلَيْهِمْ نَبَاَ ابْنَيْ اٰدَمَ بِالْحَقِّۢ اِذْ قَرَّبَا قُرْبَاناً فَتُقُبِّلَ مِنْ اَحَدِهِمَا وَلَمْ يُتَقَبَّلْ مِنَ الْاٰخَرِۜ قَالَ لَاَقْتُلَنَّكَۜ قَالَ اِنَّمَا يَتَقَبَّلُ اللّٰهُ مِنَ الْمُتَّق۪ينَ', 'Onlara, Âdem´in iki oğlunun haberini gerçek olarak anlat: Hani birer kurban takdim etmişlerdi de birisinden kabul edilmiş, diğerinden ise kabul edilmemişti. (Kurbanı kabul edilmeyen kardeş, kıskançlık yüzünden), «Andolsun seni öldüreceğim» dedi. Diğeri de «Allah ancak takvâ sahiplerinden kabul eder» dedi (ve ekledi:)', 6, 111, 120, 11892, 'Sofra', 'Medine', 112),
(697, 28, 5, 'Maide', 'لَئِنْ بَسَطْتَ اِلَيَّ يَدَكَ لِتَقْتُلَن۪ي مَٓا اَنَا۬ بِبَاسِطٍ يَدِيَ اِلَيْكَ لِاَقْتُلَكَۚ اِنّ۪ٓي اَخَافُ اللّٰهَ رَبَّ الْعَالَم۪ينَ', '«Andolsun ki sen, öldürmek için bana elini uzatsan (bile) ben sana, öldürmek için el uzatacak değilim. Ben, âlemlerin Rabbi olan Allah´tan korkarım.»', 6, 111, 120, 11892, 'Sofra', 'Medine', 112),
(698, 29, 5, 'Maide', 'اِنّ۪ٓي اُر۪يدُ اَنْ تَبُٓوأَ بِاِثْم۪ي وَاِثْمِكَ فَتَكُونَ مِنْ اَصْحَابِ النَّارِۚ وَذٰلِكَ جَزٰٓؤُا الظَّالِم۪ينَۚ', '«Ben istiyorum ki, sen, hem benim günahımı hem de kendi günahını yüklenip ateşe atılacaklardan olasın; zalimlerin cezası işte budur.»', 6, 111, 120, 11892, 'Sofra', 'Medine', 112),
(699, 30, 5, 'Maide', 'فَطَوَّعَتْ لَهُ نَفْسُهُ قَتْلَ اَخ۪يهِ فَقَتَلَهُ فَاَصْبَحَ مِنَ الْخَاسِر۪ينَ', 'Nihayet nefsi onu, kardeşini öldürmeye itti ve onu öldürdü: bu yüzden de kaybedenlerden oldu.', 6, 111, 120, 11892, 'Sofra', 'Medine', 112),
(700, 31, 5, 'Maide', 'فَبَعَثَ اللّٰهُ غُرَاباً يَبْحَثُ فِي الْاَرْضِ لِيُرِيَهُ كَيْفَ يُوَار۪ي سَوْاَةَ اَخ۪يهِۜ قَالَ يَا وَيْلَتٰٓى اَعَجَزْتُ اَنْ اَكُونَ مِثْلَ هٰذَا الْغُرَابِ فَاُوَارِيَ سَوْاَةَ اَخ۪يۚ فَاَصْبَحَ مِنَ النَّادِم۪ينَۚۛ', 'Derken Allah, kardeşinin cesedini nasıl gömeceğini ona göstermek için yeri eşeleyen bir karga gönderdi. (Katil kardeş) «Yazıklar olsun bana! Şu karga kadar da olamadım mı ki, kardeşimin cesedini gömeyim» dedi ve ettiğine yananlardan oldu.', 6, 111, 120, 11892, 'Sofra', 'Medine', 112),
(701, 32, 5, 'Maide', 'مِنْ اَجْلِ ذٰلِكَۚۛ كَتَبْنَا عَلٰى بَن۪ٓي اِسْرَٓائ۪لَ اَنَّهُ مَنْ قَتَلَ نَفْساً بِغَيْرِ نَفْسٍ اَوْ فَسَادٍ فِي الْاَرْضِ فَكَاَنَّمَا قَتَلَ النَّاسَ جَم۪يـعاًۜ وَمَنْ اَحْيَاهَا فَكَاَنَّمَٓا اَحْيَا النَّاسَ جَم۪يعاًۜ وَلَقَدْ جَٓاءَتْهُمْ رُسُلُنَا بِالْبَيِّنَاتِۘ ثُمَّ اِنَّ كَث۪يراً مِنْهُمْ بَعْدَ ذٰلِكَ فِي الْاَرْضِ لَمُسْرِفُونَ', 'İşte bu yüzdendir ki İsrailoğulları´na şöyle yazmıştık: Kim, bir cana veya yeryüzünde bozgunculuk çıkarmaya karşılık olmaksızın (haksız yere) bir cana kıyarsa bütün insanları öldürmüş gibi olur. Her kim bir canı kurtarırsa bütün insanları kurtarmış gibi olur. Peygamberlerimiz onlara apaçık deliller getirdiler; ama bundan sonra da onlardan çoğu yine yeryüzünde aşırı gitmektedirler.', 6, 112, 120, 11892, 'Sofra', 'Medine', 112),
(702, 33, 5, 'Maide', 'اِنَّمَا جَزٰٓؤُا الَّذ۪ينَ يُحَارِبُونَ اللّٰهَ وَرَسُولَهُ وَيَسْعَوْنَ فِي الْاَرْضِ فَسَاداً اَنْ يُقَتَّلُٓوا اَوْ يُصَلَّـبُٓوا اَوْ تُقَطَّعَ اَيْد۪يهِمْ وَاَرْجُلُهُمْ مِنْ خِلَافٍ اَوْ يُنْفَوْا مِنَ الْاَرْضِۜ ذٰلِكَ لَهُمْ خِزْيٌ فِي الدُّنْيَا وَلَهُمْ فِي الْاٰخِرَةِ عَذَابٌ عَظ۪يمٌۙ', 'Allah ve Resûlüne karşı savaşanların ve yeryüzünde (hak) düzeni bozmaya çalışanların cezası ancak ya (acımadan) öldürülmeleri, ya asılmaları, yahut el ve ayaklarının çaprazlama kesilmesi, yahut da bulundukları yerden sürülmeleridir. Bu onların dünyadaki rüsvaylığıdır. Onlar için ahirette de büyük azap vardır.', 6, 112, 120, 11892, 'Sofra', 'Medine', 112),
(703, 34, 5, 'Maide', 'اِلَّا الَّذ۪ينَ تَابُوا مِنْ قَبْلِ اَنْ تَقْدِرُوا عَلَيْهِمْۚ فَاعْلَمُٓوا اَنَّ اللّٰهَ غَفُورٌ رَح۪يمٌ۟', 'Ancak, siz kendilerini yenip ele geçirmeden önce tevbe edenler müstesna; biliniz ki Allah çok bağışlayıcı ve esirgeyicidir.', 6, 112, 120, 11892, 'Sofra', 'Medine', 112),
(704, 35, 5, 'Maide', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا اتَّقُوا اللّٰهَ وَابْتَغُٓوا اِلَيْهِ الْوَس۪يلَةَ وَجَاهِدُوا ف۪ي سَب۪يلِه۪ لَعَلَّكُمْ تُفْلِحُونَ', 'Ey iman edenler! Allah´tan korkun. O´na yaklaşmaya yol arayın ve yolunda cihad edin ki kurtuluşa eresiniz.', 6, 112, 120, 11892, 'Sofra', 'Medine', 112),
(705, 36, 5, 'Maide', 'اِنَّ الَّذ۪ينَ كَفَرُوا لَوْ اَنَّ لَهُمْ مَا فِي الْاَرْضِ جَم۪يعاً وَمِثْلَهُ مَعَهُ لِيَفْتَدُوا بِه۪ مِنْ عَذَابِ يَوْمِ الْقِيٰمَةِ مَا تُقُبِّلَ مِنْهُمْۚ وَلَهُمْ عَذَابٌ اَل۪يمٌ', 'Şüphe yok ki kâfir olanlar, yer yüzündeki her şey ve bunun yanında da bir o kadarı kendilerinin olsa da kıyamet gününün azabından kurtulmak için onu fidye verseler onlardan asla kabul edilmez; onlar için acı bir azap vardır.', 6, 112, 120, 11892, 'Sofra', 'Medine', 112),
(706, 37, 5, 'Maide', 'يُر۪يدُونَ اَنْ يَخْرُجُوا مِنَ النَّارِ وَمَا هُمْ بِخَارِج۪ينَ مِنْهَاۘ وَلَهُمْ عَذَابٌ مُق۪يمٌ', 'Ateşten çıkmak isterler, fakat onlar oradan çıkacak değillerdir. Onlar için devamlı bir azap vardır.', 6, 113, 120, 11892, 'Sofra', 'Medine', 112),
(707, 38, 5, 'Maide', 'وَالسَّارِقُ وَالسَّارِقَةُ فَاقْطَعُٓوا اَيْدِيَهُمَا جَزَٓاءً بِمَا كَسَبَا نَكَالاً مِنَ اللّٰهِۜ وَاللّٰهُ عَز۪يزٌ حَك۪يمٌ', 'Hırsızlık eden erkek ve kadının, yaptıklarına karşılık bir ceza ve Allah´tan bir ibret olmak üzere ellerini kesin. Allah izzet ve hikmet sahibidir.', 6, 113, 120, 11892, 'Sofra', 'Medine', 112),
(708, 39, 5, 'Maide', 'فَمَنْ تَابَ مِنْ بَعْدِ ظُلْمِه۪ وَاَصْلَحَ فَاِنَّ اللّٰهَ يَتُوبُ عَلَيْهِۜ اِنَّ اللّٰهَ غَفُورٌ رَح۪يمٌ', 'Kim (bu) haksız davranışından sonra tevbe eder ve durumunu düzeltirse şüphesiz Allah onun tevbesini kabul eder. Allah çok bağışlayıcı ve esirgeyicidir.', 6, 113, 120, 11892, 'Sofra', 'Medine', 112),
(709, 40, 5, 'Maide', 'اَلَمْ تَعْلَمْ اَنَّ اللّٰهَ لَهُ مُلْكُ السَّمٰوَاتِ وَالْاَرْضِ يُعَذِّبُ مَنْ يَشَٓاءُ وَيَغْفِرُ لِمَنْ يَشَٓاءُۜ وَاللّٰهُ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌ', 'Bilmez misin ki, göklerde ve yerde ne varsa hepsinin mülkiyeti Allah´a aittir; dilediğine azap eder ve dilediğini bağışlar. Allah her şeye hakkıyle kadirdir.', 6, 113, 120, 11892, 'Sofra', 'Medine', 112),
(710, 41, 5, 'Maide', 'يَٓا اَيُّهَا الرَّسُولُ لَا يَحْزُنْكَ الَّذ۪ينَ يُسَارِعُونَ فِي الْكُفْرِ مِنَ الَّذ۪ينَ قَالُٓوا اٰمَنَّا بِاَفْوَاهِهِمْ وَلَمْ تُؤْمِنْ قُلُوبُهُمْۚ وَمِنَ الَّذ۪ينَ هَادُوا سَمَّاعُونَ لِلْكَذِبِ سَمَّاعُونَ لِقَوْمٍ اٰخَر۪ينَۙ لَمْ يَأْتُوكَۜ يُحَرِّفُونَ الْكَلِمَ مِنْ بَعْدِ مَوَاضِعِه۪ۚ يَقُولُونَ اِنْ اُو۫ت۪يتُمْ هٰذَا فَخُذُوهُ وَاِنْ لَمْ تُؤْتَوْهُ فَاحْذَرُواۜ وَمَنْ يُرِدِ اللّٰهُ فِتْنَتَهُ فَلَنْ تَمْلِكَ لَهُ مِنَ اللّٰهِ شَيْـٔاًۜ اُو۬لٰٓئِكَ الَّذ۪ينَ لَمْ يُرِدِ اللّٰهُ اَنْ يُطَهِّرَ قُلُوبَهُمْۜ لَهُمْ فِي الدُّنْيَا خِزْيٌ وَلَهُمْ فِي الْاٰخِرَةِ عَذَابٌ عَظ۪يمٌ', 'Ey Resûl! Kalpleri iman etmediği halde ağızlarıyle «inandık» diyen kimselerden ve yahudilerden küfür içinde koşuşanlar(ın hali) seni üzmesin. Onlar durmadan yalana kulak verirler, ve sana gelmeyen (bazı) kimselere kulak verirler; kelimeleri yerlerinden kaydırıp değiştirirler. «Eğer size şu verilirse hemen alın, o verilmezse sakının!» derler. Allah bir kimseyi şaşkınlığa (fitneye) düşürmek isterse, sen Allah´a karşı, onun lehine hiçbir şey yapamazsın. Onlar, Allah´ın kalplerini temizlemek istemediği kimselerdir. Onlar için dünyada rezillik vardır ve ahirette onlara mahsus büyük bir azap vardır.', 6, 113, 120, 11892, 'Sofra', 'Medine', 112),
(711, 42, 5, 'Maide', 'سَمَّاعُونَ لِلْكَذِبِ اَكَّالُونَ لِلسُّحْتِۜ فَاِنْ جَٓاؤُ۫كَ فَاحْكُمْ بَيْنَهُمْ اَوْ اَعْرِضْ عَنْهُمْۚ وَاِنْ تُعْرِضْ عَنْهُمْ فَلَنْ يَضُرُّوكَ شَيْـٔاًۜ وَاِنْ حَكَمْتَ فَاحْكُمْ بَيْنَهُمْ بِالْقِسْطِۜ اِنَّ اللّٰهَ يُحِبُّ الْمُقْسِط۪ينَ', 'Hep yalana kulak verir, durmadan haram yerler. Sana gelirlerse, ister aralarında hüküm ver, ister onlardan yüz çevir. Eğer onlardan yüz çevirirsen sana hiçbir zarar veremezler. Ve eğer hüküm verirsen, aralarında adaletle hükmet. Allah âdil olanları sever.', 6, 114, 120, 11892, 'Sofra', 'Medine', 112),
(712, 43, 5, 'Maide', 'وَكَيْفَ يُحَكِّمُونَكَ وَعِنْدَهُمُ التَّوْرٰيةُ ف۪يهَا حُكْمُ اللّٰهِ ثُمَّ يَتَوَلَّوْنَ مِنْ بَعْدِ ذٰلِكَۜ وَمَٓا اُو۬لٰٓئِكَ بِالْمُؤْمِن۪ينَ۟', 'İçinde Allah´ın hükmü bulunan Tevrat yanlarında olduğu halde nasıl seni hakem kılıyorlar da sonra, bunun arkasından yüz çevirip gidiyorlar? Onlar inanmış kimseler değildir.', 6, 114, 120, 11892, 'Sofra', 'Medine', 112),
(713, 44, 5, 'Maide', 'اِنَّٓا اَنْزَلْنَا التَّوْرٰيةَ ف۪يهَا هُدًى وَنُورٌۚ يَحْكُمُ بِهَا النَّبِيُّونَ الَّذ۪ينَ اَسْلَمُوا لِلَّذ۪ينَ هَادُوا وَالرَّبَّانِيُّونَ وَالْاَحْبَارُ بِمَا اسْتُحْفِظُوا مِنْ كِتَابِ اللّٰهِ وَكَانُوا عَلَيْهِ شُهَدَٓاءَۚ فَلَا تَخْشَوُا النَّاسَ وَاخْشَوْنِ وَلَا تَشْتَرُوا بِاٰيَات۪ي ثَمَناً قَل۪يلاًۜ وَمَنْ لَمْ يَحْكُمْ بِمَٓا اَنْزَلَ اللّٰهُ فَاُو۬لٰٓئِكَ هُمُ الْكَافِرُونَ', 'Biz, içinde doğruya rehberlik ve nur olduğu halde Tevrat´ı indirdik. Kendilerini (Allah´a) vermiş peygamberler onunla yahudilere hükmederlerdi. Allah´ın Kitab´ını korumaları kendilerinden istendiği için Rablerine teslim olmuş zâhidler ve bilginler de (onunla hükmederlerdi). Hepsi ona (hak olduğuna) şahitlerdi. Şu halde (Ey yahudiler ve hakimler!) İnsanlardan korkmayın, benden korkun. Âyetlerimi az bir bedel karşılığında satmayın. Kim Allah´ın indirdiği (hükümler) ile hükmetmezse işte onlar kâfirlerin ta kendileridir.', 6, 114, 120, 11892, 'Sofra', 'Medine', 112),
(714, 45, 5, 'Maide', 'وَكَتَبْنَا عَلَيْهِمْ ف۪يهَٓا اَنَّ النَّفْسَ بِالنَّفْسِۙ وَالْعَيْنَ بِالْعَيْنِ وَالْاَنْفَ بِالْاَنْفِ وَالْاُذُنَ بِالْاُذُنِ وَالسِّنَّ بِالسِّنِّۙ وَالْجُرُوحَ قِصَاصٌۜ فَمَنْ تَصَدَّقَ بِه۪ فَهُوَ كَفَّارَةٌ لَهُۜ وَمَنْ لَمْ يَحْكُمْ بِمَٓا اَنْزَلَ اللّٰهُ فَاُو۬لٰٓئِكَ هُمُ الظَّالِمُونَ', 'Tevrat´ta onlara şöyle yazdık: Cana can, göze göz, buruna burun, kulağa kulak, dişe diş (karşılık ve cezadır). Yaralar da kısastır (Her yaralama misli ile cezalandırılır). Kim bunu (kısası) bağışlarsa kendisi için o keffâret olur. Kim Allah´ın indirdiği ile hükmetmezse işte onlar zalimlerdir.', 6, 114, 120, 11892, 'Sofra', 'Medine', 112),
(715, 46, 5, 'Maide', 'وَقَفَّيْنَا عَلٰٓى اٰثَارِهِمْ بِع۪يسَى ابْنِ مَرْيَمَ مُصَدِّقاً لِمَا بَيْنَ يَدَيْهِ مِنَ التَّوْرٰيةِۖ وَاٰتَيْنَاهُ الْاِنْج۪يلَ ف۪يهِ هُدًى وَنُورٌۙ وَمُصَدِّقاً لِمَا بَيْنَ يَدَيْهِ مِنَ التَّوْرٰيةِ وَهُدًى وَمَوْعِظَةً لِلْمُتَّق۪ينَ', 'Kendinden önce gelen Tevrat´ı doğrulayıcı olarak peygamberlerin izleri üzerine, Meryem oğlu İsa´yı arkalarından gönderdik. Ve ona, içinde doğruya rehberlik ve nûr bulunmak, önündeki Tevrat´ı tasdik etmek, sakınanlara bir hidayet ve öğüt olmak üzere İncil´i verdik.', 6, 115, 120, 11892, 'Sofra', 'Medine', 112),
(716, 47, 5, 'Maide', 'وَلْيَحْكُمْ اَهْلُ الْاِنْج۪يلِ بِمَٓا اَنْزَلَ اللّٰهُ ف۪يهِۜ وَمَنْ لَمْ يَحْكُمْ بِمَٓا اَنْزَلَ اللّٰهُ فَاُو۬لٰٓئِكَ هُمُ الْفَاسِقُونَ', 'İncil´e inananlar, Allah´ın onda indirdiği (hükümler) ile hükmetsinler. Kim Allah´ın indirdiği ile hükmetmezse işte onlar fâsıklardır.', 6, 115, 120, 11892, 'Sofra', 'Medine', 112),
(717, 48, 5, 'Maide', 'وَاَنْزَلْـنَٓا اِلَيْكَ الْكِتَابَ بِالْحَقِّ مُصَدِّقاً لِمَا بَيْنَ يَدَيْهِ مِنَ الْكِتَابِ وَمُهَيْمِناً عَلَيْهِ فَاحْكُمْ بَيْنَهُمْ بِمَٓا اَنْزَلَ اللّٰهُ وَلَا تَتَّبِعْ اَهْوَٓاءَهُمْ عَمَّا جَٓاءَكَ مِنَ الْحَقِّۜ لِكُلٍّ جَعَلْنَا مِنْكُمْ شِرْعَةً وَمِنْهَاجاًۜ وَلَوْ شَٓاءَ اللّٰهُ لَجَعَلَكُمْ اُمَّةً وَاحِدَةً وَلٰكِنْ لِيَبْلُوَكُمْ ف۪ي مَٓا اٰتٰيكُمْ فَاسْتَبِقُوا الْخَيْرَاتِۜ اِلَى اللّٰهِ مَرْجِعُكُمْ جَم۪يعاً فَيُنَبِّئُكُمْ بِمَا كُنْتُمْ ف۪يهِ تَخْتَلِفُونَۙ', 'Sana da, daha önceki kitabı doğrulamak ve onu korumak üzere hak olarak Kitab´ı (Kur´an´ı) gönderdik. Artık aralarında Allah´ın indirdiği ile hükmet; sana gelen gerçeği bırakıp da onların arzularına uyma. (Ey ümmetler!) Her birinize bir şerîat ve bir yol verdik. Allah dileseydi sizleri bir tek ümmet yapardı; fakat size verdiğinde (yol ve şerîatlerde) sizi denemek için (böyle yaptı). Öyleyse iyi işlerde birbirinizle yarışın. Hepinizin dönüşü Allah´adır. Artık size, üzerinde ayrılığa düştüğünüz şeyleri(n gerçek tarafını) O haber verecektir.', 6, 115, 120, 11892, 'Sofra', 'Medine', 112),
(718, 49, 5, 'Maide', 'وَاَنِ احْكُمْ بَيْنَهُمْ بِمَٓا اَنْزَلَ اللّٰهُ وَلَا تَتَّبِـعْ اَهْوَٓاءَهُمْ وَاحْذَرْهُمْ اَنْ يَفْتِنُوكَ عَنْ بَعْضِ مَٓا اَنْزَلَ اللّٰهُ اِلَيْكَۜ فَاِنْ تَوَلَّوْا فَاعْلَمْ اَنَّمَا يُر۪يدُ اللّٰهُ اَنْ يُص۪يبَهُمْ بِبَعْضِ ذُنُوبِهِمْۜ وَاِنَّ كَث۪يراً مِنَ النَّاسِ لَفَاسِقُونَ', '(Sana şu talîmatı verdik): Aralarında Allah´ın indirdiği ile hükmet ve onların arzularına uyma. Allah´ın sana indirdiği hükümlerin bir kısmından seni saptırmamalarına dikkat et. Eğer (hükümden) yüz çevirirlerse bil ki (bununla) Allah ancak, günahlarının bir kısmını onların başına belâ etmek ister. İnsanların birçoğu da zaten yoldan çıkmışlardır.', 6, 115, 120, 11892, 'Sofra', 'Medine', 112),
(719, 50, 5, 'Maide', 'اَفَحُكْمَ الْجَاهِلِيَّةِ يَبْغُونَۜ وَمَنْ اَحْسَنُ مِنَ اللّٰهِ حُكْماً لِقَوْمٍ يُوقِنُونَ۟', 'Yoksa onlar (İslâm öncesi) cahiliye idaresini mi arıyorlar? İyi anlayan bir topluma göre, hükümranlığı Allah´tan daha güzel kim vardır?', 6, 115, 120, 11892, 'Sofra', 'Medine', 112),
(720, 51, 5, 'Maide', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا لَا تَتَّخِذُوا الْيَهُودَ وَالنَّصَارٰٓى اَوْلِيَٓاءَۢ بَعْضُهُمْ اَوْلِيَٓاءُ بَعْضٍۜ وَمَنْ يَتَوَلَّهُمْ مِنْكُمْ فَاِنَّهُ مِنْهُمْۜ اِنَّ اللّٰهَ لَا يَهْدِي الْقَوْمَ الظَّالِم۪ينَ', 'Ey iman edenler! Yahudileri ve hıristiyanları dost edinmeyin. Zira onlar birbirinin dostudurlar (birbirinin tarafını tutarlar). İçinizden onları dost tutanlar, onlardandır. Şüphesiz Allah, zalimler topluluğuna yol göstermez.', 6, 116, 120, 11892, 'Sofra', 'Medine', 112),
(721, 52, 5, 'Maide', 'فَتَرَى الَّذ۪ينَ ف۪ي قُلُوبِهِمْ مَرَضٌ يُسَارِعُونَ ف۪يهِمْ يَقُولُونَ نَخْشٰٓى اَنْ تُص۪يبَنَا دَٓائِرَةٌۜ فَعَسَى اللّٰهُ اَنْ يَأْتِيَ بِالْفَتْحِ اَوْ اَمْرٍ مِنْ عِنْدِه۪ فَيُصْبِحُوا عَلٰى مَٓا اَسَرُّوا ف۪ٓي اَنْفُسِهِمْ نَادِم۪ينَۜ', 'Kalblerinde hastalık bulunanların: «Başımıza bir felâketin gelmesinden korkuyoruz» diyerek onların arasına koşuştuklarını görürsün. Umulur ki Allah bir fetih, yahut katından bir emir getirecek de onlar, içlerinde gizledikleri şeyden dolayı pişman olacaklardır.', 6, 116, 120, 11892, 'Sofra', 'Medine', 112),
(722, 53, 5, 'Maide', 'وَيَقُولُ الَّذ۪ينَ اٰمَنُٓوا اَهٰٓؤُ۬لَٓاءِ الَّذ۪ينَ اَقْسَمُوا بِاللّٰهِ جَهْدَ اَيْمَانِهِمْۙ اِنَّهُمْ لَمَعَكُمْۜ حَبِطَتْ اَعْمَالُهُمْ فَاَصْبَحُوا خَاسِر۪ينَ', '(O zaman) iman edenler: «Bunlar mıdır sizinle beraber olduklarına bütün güçleriyle yemin edenler?» diyeceklerdir. Onların bütün yaptıkları boşa gitmiştir de kaybedenlerden olmuşlardır.', 6, 116, 120, 11892, 'Sofra', 'Medine', 112),
(723, 54, 5, 'Maide', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا مَنْ يَرْتَدَّ مِنْكُمْ عَنْ د۪ينِه۪ فَسَوْفَ يَأْتِي اللّٰهُ بِقَوْمٍ يُحِبُّهُمْ وَيُحِبُّونَهُٓ اَذِلَّةٍ عَلَى الْمُؤْمِن۪ينَ اَعِزَّةٍ عَلَى الْكَافِر۪ينَۘ يُجَاهِدُونَ ف۪ي سَب۪يلِ اللّٰهِ وَلَا يَخَافُونَ لَوْمَةَ لَٓائِمٍۜ ذٰلِكَ فَضْلُ اللّٰهِ يُؤْت۪يهِ مَنْ يَشَٓاءُۜ وَاللّٰهُ وَاسِعٌ عَل۪يمٌ', 'Ey iman edenler! Sizden kim dininden dönerse (bilsin ki) Allah, sevdiği ve kendisini seven müminlere karşı alçak gönüllü (şefkatli), kâfirlere karşı onurlu ve zorlu bir toplum getirecektir. (Bunlar) Allah yolunda cihad ederler ve hiçbir kınayanın kınamasından korkmazlar (hiçbir kimsenin kınamasına aldırmazlar). Bu, Allah´ın, dilediğine verdiği lütfudur. Allah´ın lütfu ve ilmi geniştir.', 6, 116, 120, 11892, 'Sofra', 'Medine', 112),
(724, 55, 5, 'Maide', 'اِنَّمَا وَلِيُّكُمُ اللّٰهُ وَرَسُولُهُ وَالَّذ۪ينَ اٰمَنُوا الَّذ۪ينَ يُق۪يمُونَ الصَّلٰوةَ وَيُؤْتُونَ الزَّكٰوةَ وَهُمْ رَاكِعُونَ', 'Sizin dostunuz (veliniz) ancak Allah´tır, Resûlüdür, iman edenlerdir; onlar ki Allah´ın emirlerine boyun eğerek namazı kılar, zekâtı verirler.', 6, 116, 120, 11892, 'Sofra', 'Medine', 112),
(725, 56, 5, 'Maide', 'وَمَنْ يَتَوَلَّ اللّٰهَ وَرَسُولَهُ وَالَّذ۪ينَ اٰمَنُوا فَاِنَّ حِزْبَ اللّٰهِ هُمُ الْغَالِبُونَ۟', 'Kim Allah´ı, Resûlünü ve iman edenleri dost edinirse (bilsin ki) üstün gelecek olanlar şüphesiz Allah´ın tarafını tutanlardır.', 6, 116, 120, 11892, 'Sofra', 'Medine', 112),
(726, 57, 5, 'Maide', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا لَا تَتَّخِذُوا الَّذ۪ينَ اتَّخَذُوا د۪ينَكُمْ هُزُواً وَلَعِباً مِنَ الَّذ۪ينَ اُو۫تُوا الْكِتَابَ مِنْ قَبْلِكُمْ وَالْكُفَّارَ اَوْلِيَٓاءَۚ وَاتَّقُوا اللّٰهَ اِنْ كُنْتُمْ مُؤْمِن۪ينَ', 'Ey iman edenler! Sizden önce kendilerine Kitap verilenlerden dininizi alay ve oyun konusu edinenleri ve kâfirleri dost edinmeyin. Allah´tan korkun; eğer müminler iseniz.', 6, 116, 120, 11892, 'Sofra', 'Medine', 112),
(727, 58, 5, 'Maide', 'وَاِذَا نَادَيْتُمْ اِلَى الصَّلٰوةِ اتَّخَذُوهَا هُزُواً وَلَعِباًۜ ذٰلِكَ بِاَنَّهُمْ قَوْمٌ لَا يَعْقِلُونَ', 'Namaza çağırdığınız zaman onu alay ve eğlence konusu yaparlar. Bu davranış, onların düşünemeyen bir toplum olmalarındandır.', 6, 117, 120, 11892, 'Sofra', 'Medine', 112),
(728, 59, 5, 'Maide', 'قُلْ يَٓا اَهْلَ الْكِتَابِ هَلْ تَنْقِمُونَ مِنَّٓا اِلَّٓا اَنْ اٰمَنَّا بِاللّٰهِ وَمَٓا اُنْزِلَ اِلَيْنَا وَمَٓا اُنْزِلَ مِنْ قَبْلُۙ وَاَنَّ اَكْثَرَكُمْ فَاسِقُونَ', '(Onlara) şöyle de: Ey kitap ehli! Yalnızca Allah´a, bize indirilene ve daha önce indirilene inandığımız için mi bizden hoşlanmıyorsunuz? Oysa çoğunuz yoldan çıkmış kimselersiniz.', 6, 117, 120, 11892, 'Sofra', 'Medine', 112),
(729, 60, 5, 'Maide', 'قُلْ هَلْ اُنَبِّئُكُمْ بِشَرٍّ مِنْ ذٰلِكَ مَثُوبَةً عِنْدَ اللّٰهِۜ مَنْ لَعَنَهُ اللّٰهُ وَغَضِبَ عَلَيْهِ وَجَعَلَ مِنْهُمُ الْقِرَدَةَ وَالْخَنَاز۪يرَ وَعَبَدَ الطَّاغُوتَۜ اُو۬لٰٓئِكَ شَرٌّ مَكَاناً وَاَضَلُّ عَنْ سَوَٓاءِ السَّب۪يلِ', 'De ki: Allah katında yeri bundan daha kötü olanı size haber vereyim mi? Allah´ın lânetlediği ve gazap ettiği, aralarından maymunlar, domuzlar ve tâğuta tapanlar çıkardığı kimseler. İşte bunlar, yeri (durumu) daha kötü olan ve doğru yoldan daha ziyade sapmış bulunanlardır.', 6, 117, 120, 11892, 'Sofra', 'Medine', 112),
(730, 61, 5, 'Maide', 'وَاِذَا جَٓاؤُ۫كُمْ قَالُٓوا اٰمَنَّا وَقَدْ دَخَلُوا بِالْـكُفْرِ وَهُمْ قَدْ خَرَجُوا بِه۪ۜ وَاللّٰهُ اَعْلَمُ بِمَا كَانُوا يَكْتُمُونَ', 'Yanınıza inkârla girip yine inkârla çıktıkları halde size geldiklerinde «inandık» derler. Allah gizlediklerini daha iyi bilmektedir.', 6, 117, 120, 11892, 'Sofra', 'Medine', 112),
(731, 62, 5, 'Maide', 'وَتَرٰى كَث۪يراً مِنْهُمْ يُسَارِعُونَ فِي الْاِثْمِ وَالْعُدْوَانِ وَاَكْلِهِمُ السُّحْتَۜ لَبِئْسَ مَا كَانُوا يَعْمَلُونَ', 'Onlardan birçoğunun günah, düşmanlık ve haram yemede yarıştıklarını görürsün. Yaptıkları ne kadar kötüdür!', 6, 117, 120, 11892, 'Sofra', 'Medine', 112);
INSERT INTO `Ayetler` (`id`, `AyetNo`, `SureNo`, `SureAdi`, `Metin`, `Mana`, `Cuzu`, `Sayfasi`, `AyetSayisi`, `HarfSayisi`, `AdAnlami`, `IndigiYer`, `InisSirasi`) VALUES
(732, 63, 5, 'Maide', 'لَوْلَا يَنْهٰيهُمُ الرَّبَّانِيُّونَ وَالْاَحْبَارُ عَنْ قَوْلِهِمُ الْاِثْمَ وَاَكْلِهِمُ السُّحْتَۜ لَبِئْسَ مَا كَانُوا يَصْنَعُونَ', 'Din adamları ve âlimleri onları, günah olan sözleri söylemekten ve haram yemekten menetselerdi ya! İşledikleri (fiiller) ne kötüdür!', 6, 117, 120, 11892, 'Sofra', 'Medine', 112),
(733, 64, 5, 'Maide', 'وَقَالَتِ الْيَهُودُ يَدُ اللّٰهِ مَغْلُولَةٌۜ غُلَّتْ اَيْد۪يهِمْ وَلُعِنُوا بِمَا قَالُواۢ بَلْ يَدَاهُ مَبْسُوطَتَانِۙ يُنْفِقُ كَيْفَ يَشَٓاءُۜ وَلَيَز۪يدَنَّ كَث۪يراً مِنْهُمْ مَٓا اُنْزِلَ اِلَيْكَ مِنْ رَبِّكَ طُغْيَاناً وَكُفْراًۜ وَاَلْقَيْنَا بَيْنَهُمُ الْعَدَاوَةَ وَالْبَغْضَٓاءَ اِلٰى يَوْمِ الْقِيٰمَةِۜ كُلَّمَٓا اَوْقَدُوا نَاراً لِلْحَرْبِ اَطْفَاَهَا اللّٰهُۙ وَيَسْعَوْنَ فِي الْاَرْضِ فَسَاداًۜ وَاللّٰهُ لَا يُحِبُّ الْمُفْسِد۪ينَ', 'Yahudiler, Allah´ın eli bağlıdır (sıkıdır) , dediler. Hay dedikleri yüzünden elleri bağlanası ve lânet olasılar! Bilâkis, Allah´ın elleri açıktır, dilediği gibi verir. Andolsun ki sana Rabbinden indirilen, onlardan çoğunun azgınlığını ve küfrünü arttırır. Aralarına, kıyamete kadar (sürecek) düşmanlık ve kin soktuk. Ne zaman savaş için bir ateş yakmışlarsa (fitneyi uyandırmışlarsa) Allah onu söndürmüştür. Onlar yeryüzünde bozgunculuğa koşarlar; Allah ise bozguncuları sevmez.', 6, 117, 120, 11892, 'Sofra', 'Medine', 112),
(734, 65, 5, 'Maide', 'وَلَوْ اَنَّ اَهْلَ الْكِتَابِ اٰمَنُوا وَاتَّقَوْا لَكَفَّرْنَا عَنْهُمْ سَيِّـَٔاتِهِمْ وَلَاَدْخَلْنَاهُمْ جَنَّاتِ النَّع۪يمِ', 'Eğer ehl-i kitap iman edip (kötülüklerden) sakınsalardı, herhalde (geçmiş) kötülüklerini örter ve onları nimeti bol cennetlere sokardık.', 6, 118, 120, 11892, 'Sofra', 'Medine', 112),
(735, 66, 5, 'Maide', 'وَلَوْ اَنَّهُمْ اَقَامُوا التَّوْرٰيةَ وَالْاِنْج۪يلَ وَمَٓا اُنْزِلَ اِلَيْهِمْ مِنْ رَبِّهِمْ لَاَكَلُوا مِنْ فَوْقِهِمْ وَمِنْ تَحْتِ اَرْجُلِهِمْۜ مِنْهُمْ اُمَّةٌ مُقْتَصِدَةٌۜ وَكَث۪يرٌ مِنْهُمْ سَٓاءَ مَا يَعْمَلُونَ۟', 'Eğer onlar Tevrat´ı, İncil´i ve Rablerinden onlara indirileni (Kur´an´ı) doğru dürüst uygulasalardı, şüphesiz hem üstlerinden, hem de ayaklarının altından yerlerdi (yeraltı ve yerüstü servetlerinden istifade ederek refah içinde yaşarlardı). Onlardan aşırılığa kaçmayan (iktisatlı, mutedil) bir zümre vardır; fakat çoğunun yaptıkları ne kötüdür!', 6, 118, 120, 11892, 'Sofra', 'Medine', 112),
(736, 67, 5, 'Maide', 'يَٓا اَيُّهَا الرَّسُولُ بَلِّـغْ مَٓا اُنْزِلَ اِلَيْكَ مِنْ رَبِّكَۜ وَاِنْ لَمْ تَفْعَلْ فَمَا بَلَّغْتَ رِسَالَتَهُۜ وَاللّٰهُ يَعْصِمُكَ مِنَ النَّاسِۜ اِنَّ اللّٰهَ لَا يَهْدِي الْقَوْمَ الْكَافِر۪ينَ', 'Ey Resûl! Rabbinden sana indirileni tebliğ et. Eğer bunu yapmazsan O´nun elçiliğini yapmamış olursun. Allah seni insanlardan koruyacaktır. Doğrusu Allah, kâfirler topluluğuna rehberlik etmez.', 6, 118, 120, 11892, 'Sofra', 'Medine', 112),
(737, 68, 5, 'Maide', 'قُلْ يَٓا اَهْلَ الْكِتَابِ لَسْتُمْ عَلٰى شَيْءٍ حَتّٰى تُق۪يمُوا التَّوْرٰيةَ وَالْاِنْج۪يلَ وَمَٓا اُنْزِلَ اِلَيْكُمْ مِنْ رَبِّكُمْۜ وَلَيَز۪يدَنَّ كَث۪يراً مِنْهُمْ مَٓا اُنْزِلَ اِلَيْكَ مِنْ رَبِّكَ طُغْيَاناً وَكُفْراًۚ فَلَا تَأْسَ عَلَى الْقَوْمِ الْكَافِر۪ينَ', '«Ey Kitap ehli! Siz, Tevrat´ı, İncil´i ve Rabbinizden size indirileni hakkıyle uygulamadıkça, (doğru) bir şey (yol) üzerinde değilsinizdir» de. Rabbinden sana indirilen, onlardan çoğunun küfür ve azgınlığını elbette artıracaktır. Kâfirler topluluğuna üzülme.', 6, 118, 120, 11892, 'Sofra', 'Medine', 112),
(738, 69, 5, 'Maide', 'اِنَّ الَّذ۪ينَ اٰمَنُوا وَالَّذ۪ينَ هَادُوا وَالصَّابِـؤُ۫نَ وَالنَّصَارٰى مَنْ اٰمَنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَعَمِلَ صَالِحاً فَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ', 'İman edenler ile yahudiler, sâbiîler ve hıristiyanlardan Allah´a ve ahiret gününe (gerçekten) inanıp iyi amel işleyenler üzerine asla korku yoktur; onlar üzülecek de değillerdir.', 6, 118, 120, 11892, 'Sofra', 'Medine', 112),
(739, 70, 5, 'Maide', 'لَقَدْ اَخَذْنَا م۪يثَاقَ بَن۪ٓي اِسْرَٓائ۪لَ وَاَرْسَلْـنَٓا اِلَيْهِمْ رُسُلاًۜ كُلَّمَا جَٓاءَهُمْ رَسُولٌ بِمَا لَا تَهْوٰٓى اَنْفُسُهُمْۙ فَر۪يقاً كَذَّبُوا وَفَر۪يقاً يَقْتُلُونَ', 'Andolsun ki İsrailoğullarının sağlam sözünü aldık ve onlara peygamberler gönderdik. Ne zaman bir peygamber onlara nefislerinin arzu etmediğini (ilâhî hükümleri) getirdi ise bir kısmını yalanladılar, bir kısmını da öldürdüler.', 6, 118, 120, 11892, 'Sofra', 'Medine', 112),
(740, 71, 5, 'Maide', 'وَحَسِبُٓوا اَلَّا تَكُونَ فِتْنَةٌ فَعَمُوا وَصَمُّوا ثُمَّ تَابَ اللّٰهُ عَلَيْهِمْ ثُمَّ عَمُوا وَصَمُّوا كَث۪يرٌ مِنْهُمْۜ وَاللّٰهُ بَص۪يرٌ بِمَا يَعْمَلُونَ', 'Bir belâ olmayacak zannettiler de kör ve sağır kesildiler. Sonra Allah tevbelerini kabul etti. Sonra içlerinden çoğu yine kör ve sağır oldu. Allah onların yaptıklarını görmektedir.', 6, 119, 120, 11892, 'Sofra', 'Medine', 112),
(741, 72, 5, 'Maide', 'لَقَدْ كَفَرَ الَّذ۪ينَ قَالُٓوا اِنَّ اللّٰهَ هُوَ الْمَس۪يحُ ابْنُ مَرْيَمَۜ وَقَالَ الْمَس۪يحُ يَا بَن۪ٓي اِسْرَٓائ۪لَ اعْبُدُوا اللّٰهَ رَبّ۪ي وَرَبَّكُمْۜ اِنَّهُ مَنْ يُشْرِكْ بِاللّٰهِ فَقَدْ حَرَّمَ اللّٰهُ عَلَيْهِ الْجَنَّةَ وَمَأْوٰيهُ النَّارُۜ وَمَا لِلظَّالِم۪ينَ مِنْ اَنْصَارٍ', 'Andolsun ki «Allah, kesinlikle Meryem oğlu Mesîh´tir» diyenler kâfir olmuşlardır. Halbuki Mesîh «Ey İsrailoğulları! Rabbim ve Rabbiniz olan Allah´a kulluk ediniz. Biliniz ki kim Allah´a ortak koşarsa muhakkak Allah ona cenneti haram kılar; artık onun yeri ateştir ve zalimler için yardımcılar yoktur» demişti.', 6, 119, 120, 11892, 'Sofra', 'Medine', 112),
(742, 73, 5, 'Maide', 'لَقَدْ كَفَرَ الَّذ۪ينَ قَالُٓوا اِنَّ اللّٰهَ ثَالِثُ ثَلٰثَةٍۢ وَمَا مِنْ اِلٰهٍ اِلَّٓا اِلٰهٌ وَاحِدٌۜ وَاِنْ لَمْ يَنْتَهُوا عَمَّا يَقُولُونَ لَيَمَسَّنَّ الَّذ۪ينَ كَفَرُوا مِنْهُمْ عَذَابٌ اَل۪يمٌ', 'Andolsun «Allah, üçün üçüncüsüdür» diyenler de kâfir olmuşlardır. Halbuki bir tek Allah´dan başka hiçbir tanrı yoktur. Eğer diye geldiklerinden vazgeçmezlerse, içlerinden kâfir olanlara acı bir azap isabet edecektir.', 6, 119, 120, 11892, 'Sofra', 'Medine', 112),
(743, 74, 5, 'Maide', 'اَفَلَا يَتُوبُونَ اِلَى اللّٰهِ وَيَسْتَغْفِرُونَهُۜ وَاللّٰهُ غَفُورٌ رَح۪يمٌ', 'Hâla Allah´a tevbe edip O´ndan bağışlanmayı dilemiyecekler mi? Allah çok yarlığayıcı, çok esirgeyicidir.', 6, 119, 120, 11892, 'Sofra', 'Medine', 112),
(744, 75, 5, 'Maide', 'مَا الْمَس۪يحُ ابْنُ مَرْيَمَ اِلَّا رَسُولٌۚ قَدْ خَلَتْ مِنْ قَبْلِهِ الرُّسُلُۜ وَاُمُّهُ صِدّ۪يقَةٌۜ كَانَا يَأْكُلَانِ الطَّعَامَۜ اُنْظُرْ كَيْفَ نُبَيِّنُ لَهُمُ الْاٰيَاتِ ثُمَّ انْظُرْ اَنّٰى يُؤْفَكُونَ', 'Meryem oğlu Mesîh ancak bir resûldür. Ondan önce de (birçok) resûller gelip geçmiştir. Anası da çok doğru bir kadındır. Her ikisi de yemek yerlerdi. Bak, onlara delilleri nasıl açıklıyoruz, sonra bak nasıl (haktan) yüz çeviriyorlar.', 6, 119, 120, 11892, 'Sofra', 'Medine', 112),
(745, 76, 5, 'Maide', 'قُلْ اَتَعْبُدُونَ مِنْ دُونِ اللّٰهِ مَا لَا يَمْلِكُ لَكُمْ ضَراًّ وَلَا نَفْعاًۜ وَاللّٰهُ هُوَ السَّم۪يعُ الْعَل۪يمُ', 'De ki: Allah´ı bırakıp da sizin için fayda ve zarara gücü yetmeyen şeylere mi tapıyorsunuz? Hakkıyla işiten ve bilen yalnız Allah´tır.', 6, 119, 120, 11892, 'Sofra', 'Medine', 112),
(746, 77, 5, 'Maide', 'قُلْ يَٓا اَهْلَ الْكِتَابِ لَا تَغْلُوا ف۪ي د۪ينِكُمْ غَيْرَ الْحَقِّ وَلَا تَتَّبِعُٓوا اَهْوَٓاءَ قَوْمٍ قَدْ ضَلُّوا مِنْ قَبْلُ وَاَضَلُّوا كَث۪يراً وَضَلُّوا عَنْ سَوَٓاءِ السَّب۪يلِ۟', 'De ki: Ey Kitap ehli! Dininizde haksız yere haddi aşmayın. Daha önceden sapan, birçoklarını saptıran ve yolun doğrusundan uzaklaşan bir topluma uymayın.', 6, 120, 120, 11892, 'Sofra', 'Medine', 112),
(747, 78, 5, 'Maide', 'لُعِنَ الَّذ۪ينَ كَفَرُوا مِنْ بَن۪ٓي اِسْرَٓائ۪لَ عَلٰى لِسَانِ دَاوُ۫دَ وَع۪يسَى ابْنِ مَرْيَمَۜ ذٰلِكَ بِمَا عَصَوْا وَكَانُوا يَعْتَدُونَ', 'İsrailoğullarından kâfir olanlar, Davud ve Meryem oğlu İsa diliyle lânetlenmişlerdir. Bunun sebebi, söz dinlememeleri ve sınırı aşmalarıdır.', 6, 120, 120, 11892, 'Sofra', 'Medine', 112),
(748, 79, 5, 'Maide', 'كَانُوا لَا يَتَنَاهَوْنَ عَنْ مُنْكَرٍ فَعَلُوهُۜ لَبِئْسَ مَا كَانُوا يَفْعَلُونَ', 'Onlar, işledikleri kötülükten, birbirini vazgeçirmeye çalışmazlardı. Andolsun yaptıkları ne kötüdür!', 6, 120, 120, 11892, 'Sofra', 'Medine', 112),
(749, 80, 5, 'Maide', 'تَرٰى كَث۪يراً مِنْهُمْ يَتَوَلَّوْنَ الَّذ۪ينَ كَفَرُواۜ لَبِئْسَ مَا قَدَّمَتْ لَهُمْ اَنْفُسُهُمْ اَنْ سَخِطَ اللّٰهُ عَلَيْهِمْ وَفِي الْعَذَابِ هُمْ خَالِدُونَ', 'Onlardan çoğunun, inkâr edenlerle dostluk ettiklerini görürsün. Nefislerinin onlar için (ahiret hayatları için) önceden hazırladığı şey ne kötüdür: Allah onlara gazabetmiştir ve onlar azap içinde devamlı kalıcıdırlar!', 6, 120, 120, 11892, 'Sofra', 'Medine', 112),
(750, 81, 5, 'Maide', 'وَلَوْ كَانُوا يُؤْمِنُونَ بِاللّٰهِ وَالنَّبِيِّ وَمَٓا اُنْزِلَ اِلَيْهِ مَا اتَّخَذُوهُمْ اَوْلِيَٓاءَ وَلٰكِنَّ كَث۪يراً مِنْهُمْ فَاسِقُونَ', 'Eğer onlar Allah´a, Peygamber´e ve ona indirilene iman etmiş olsalardı onları (müşrikleri) dost edinmezlerdi; fakat onların çoğu yoldan çıkmışlardır.', 6, 120, 120, 11892, 'Sofra', 'Medine', 112),
(751, 82, 5, 'Maide', 'لَتَجِدَنَّ اَشَدَّ النَّاسِ عَدَاوَةً لِلَّذ۪ينَ اٰمَنُوا الْيَهُودَ وَالَّذ۪ينَ اَشْرَكُواۚ وَلَتَجِدَنَّ اَقْرَبَهُمْ مَوَدَّةً لِلَّذ۪ينَ اٰمَنُوا الَّذ۪ينَ قَالُٓوا اِنَّا نَصَارٰىۜ ذٰلِكَ بِاَنَّ مِنْهُمْ قِسّ۪يس۪ينَ وَرُهْبَاناً وَاَنَّهُمْ لَا يَسْتَكْبِرُونَ', 'İnsanlar içerisinde iman edenlere düşmanlık bakımından en şiddetli olarak yahudiler ile, şirk koşanları bulacaksın. Onlar içinde iman edenlere sevgi bakımından en yakın olarak da «Biz hıristiyanlarız» diyenleri bulacaksın. Çünkü onların içinde keşişler ve râhipler vardır ve onlar büyüklük taslamazlar.', 6, 120, 120, 11892, 'Sofra', 'Medine', 112),
(752, 83, 5, 'Maide', 'وَاِذَا سَمِعُوا مَٓا اُنْزِلَ اِلَى الرَّسُولِ تَرٰٓى اَعْيُنَهُمْ تَف۪يضُ مِنَ الدَّمْعِ مِمَّا عَرَفُوا مِنَ الْحَقِّۚ يَقُولُونَ رَبَّنَٓا اٰمَنَّا فَاكْتُبْنَا مَعَ الشَّاهِد۪ينَ', 'Resûle indirileni duydukları zaman, tanış çıktıkları gerçekten dolayı gözlerinden yaşlar boşandığını görürsün. Derler ki: «Rabbimiz! İman ettik, bizi (hakka) şahit olanlarla beraber yaz.»', 7, 121, 120, 11892, 'Sofra', 'Medine', 112),
(753, 84, 5, 'Maide', 'وَمَا لَنَا لَا نُؤْمِنُ بِاللّٰهِ وَمَا جَٓاءَنَا مِنَ الْحَقِّۙ وَنَطْمَعُ اَنْ يُدْخِلَنَا رَبُّنَا مَعَ الْقَوْمِ الصَّالِح۪ينَ', '«Rabbimizin bizi iyiler arasına katmasını umup dururken niçin Allah´a ve bize gelen gerçeğe iman etmeyelim?»', 7, 121, 120, 11892, 'Sofra', 'Medine', 112),
(754, 85, 5, 'Maide', 'فَاَثَابَهُمُ اللّٰهُ بِمَا قَالُوا جَنَّاتٍ تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُ خَالِد۪ينَ ف۪يهَاۜ وَذٰلِكَ جَزَٓاءُ الْمُحْسِن۪ينَ', 'Söyledikleri (bu) sözden dolayı Allah onlara, içinde devamlı kalmak üzere, zemininden ırmaklar akan cennetleri mükâfat olarak verdi. İyi hareket edenlerin mükâfatı işte budur.', 7, 121, 120, 11892, 'Sofra', 'Medine', 112),
(755, 86, 5, 'Maide', 'وَالَّذ۪ينَ كَفَرُوا وَكَذَّبُوا بِاٰيَاتِنَٓا اُو۬لٰٓئِكَ اَصْحَابُ الْجَح۪يمِ۟', 'İnkâr eden ve âyetlerimizi yalanlayanlara gelince işte onlar cehennemliklerdir.', 7, 121, 120, 11892, 'Sofra', 'Medine', 112),
(756, 87, 5, 'Maide', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا لَا تُحَرِّمُوا طَيِّبَاتِ مَٓا اَحَلَّ اللّٰهُ لَكُمْ وَلَا تَعْتَدُواۜ اِنَّ اللّٰهَ لَا يُحِبُّ الْمُعْتَد۪ينَ', 'Ey iman edenler! Allah´ın size helâl kıldığı iyi ve temiz şeyleri (siz kendinize) haram kılmayın ve sınırı aşmayın. Allah sınırı aşanları sevmez.', 7, 121, 120, 11892, 'Sofra', 'Medine', 112),
(757, 88, 5, 'Maide', 'وَكُلُوا مِمَّا رَزَقَكُمُ اللّٰهُ حَلَالاً طَيِّباًۖ وَاتَّقُوا اللّٰهَ الَّـذ۪ٓي اَنْتُمْ بِه۪ مُؤْمِنُونَ', 'Allah´ın size helâl ve temiz olarak verdiği rızıklardan yeyin ve kendisine iman etmiş olduğunuz Allah´tan korkun.', 7, 121, 120, 11892, 'Sofra', 'Medine', 112),
(758, 89, 5, 'Maide', 'لَا يُؤَاخِذُكُمُ اللّٰهُ بِاللَّغْوِ ف۪ٓي اَيْمَانِكُمْ وَلٰكِنْ يُؤَاخِذُكُمْ بِمَا عَقَّدْتُمُ الْاَيْمَانَۚ فَكَفَّارَتُهُٓ اِطْعَامُ عَشَرَةِ مَسَاك۪ينَ مِنْ اَوْسَطِ مَا تُطْعِمُونَ اَهْل۪يكُمْ اَوْ كِسْوَتُهُمْ اَوْ تَحْر۪يرُ رَقَبَةٍۜ فَمَنْ لَمْ يَجِدْ فَصِيَامُ ثَلٰثَةِ اَيَّامٍۜ ذٰلِكَ كَفَّارَةُ اَيْمَانِكُمْ اِذَا حَلَفْتُمْۜ وَاحْفَظُٓوا اَيْمَانَكُمْۜ كَذٰلِكَ يُبَيِّنُ اللّٰهُ لَكُمْ اٰيَاتِه۪ لَعَلَّكُمْ تَشْكُرُونَ', 'Allah, kasıtsız olarak ağzınızdan çıkıveren yeminlerinizden dolayı sizi sorumlu tutmaz, fakat bilerek yaptığınız yeminlerden dolayı sizi sorumlu tutar. Bunun da keffâreti, ailenize yedirdiğiniz yemeğin orta hallisinden on fakire yedirmek, yahut onları giydirmek, yahut da bir köle azat etmektir. Bunları bulamıyan üç gün oruç tutmalıdır. Yemin ettiğiniz takdirde yeminlerinizin keffâreti işte budur. Yeminlerinizi koruyun (onlara riayet edin). Allah size âyetlerini açıklıyor; umulur ki şükredersiniz!', 7, 121, 120, 11892, 'Sofra', 'Medine', 112),
(759, 90, 5, 'Maide', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُٓوا اِنَّمَا الْخَمْرُ وَالْمَيْسِرُ وَالْاَنْصَابُ وَالْاَزْلَامُ رِجْسٌ مِنْ عَمَلِ الشَّيْطَانِ فَاجْتَنِبُوهُ لَعَلَّكُمْ تُفْلِحُونَ', 'Ey iman edenler! Şarap, kumar, dikili taşlar (putlar), fal ve şans okları birer şeytan işi pisliktir; bunlardan uzak durun ki kurtuluşa eresiniz.', 7, 122, 120, 11892, 'Sofra', 'Medine', 112),
(760, 91, 5, 'Maide', 'اِنَّمَا يُر۪يدُ الشَّيْطَانُ اَنْ يُوقِعَ بَيْنَكُمُ الْعَدَاوَةَ وَالْبَغْضَٓاءَ فِي الْخَمْرِ وَالْمَيْسِرِ وَيَصُدَّكُمْ عَنْ ذِكْرِ اللّٰهِ وَعَنِ الصَّلٰوةِۚ فَهَلْ اَنْتُمْ مُنْتَهُونَ', 'Şeytan içki ve kumar yoluyla ancak aranıza düşmanlık ve kin sokmak; sizi, Allah´ı anmaktan ve namazdan alıkoymak ister. Artık (bunlardan) vazgeçtiniz değil mi?', 7, 122, 120, 11892, 'Sofra', 'Medine', 112),
(761, 92, 5, 'Maide', 'وَاَط۪يعُوا اللّٰهَ وَاَط۪يعُوا الرَّسُولَ وَاحْذَرُواۚ فَاِنْ تَوَلَّيْتُمْ فَاعْلَمُٓوا اَنَّمَا عَلٰى رَسُولِنَا الْبَلَاغُ الْمُب۪ينُ', 'Allah´a itaat edin, Resûle de itaat edin ve (kötülüklerden) sakının. Eğer (itaatten) yüz çevirirseniz bilin ki Resûlümüzün vazifesi apaçık duyurmak ve bildirmektir.', 7, 122, 120, 11892, 'Sofra', 'Medine', 112),
(762, 93, 5, 'Maide', 'لَيْسَ عَلَى الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ جُنَاحٌ ف۪يمَا طَعِمُٓوا اِذَا مَا اتَّقَوْا وَاٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ ثُمَّ اتَّقَوْا وَاٰمَنُوا ثُمَّ اتَّقَوْا وَاَحْسَنُواۜ وَاللّٰهُ يُحِبُّ الْمُحْسِن۪ينَ۟', 'İman eden ve iyi işler yapanlara, hakkıyle sakınıp iman ettikleri ve iyi işler yaptıkları, sonra yine hakkıyle sakınıp iman ettikleri, sonra da hakkıyle sakınıp yaptıklarını, ellerinden geldiğince güzel yaptıkları takdirde (haram kılınmadan önce) tattıklarından dolayı günah yoktur. (Önemli olan inandıktan sonra iman ve iyi amelde sebattır). Allah iyi ve güzel yapanları sever.', 7, 122, 120, 11892, 'Sofra', 'Medine', 112),
(763, 94, 5, 'Maide', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا لَيَبْلُوَنَّكُمُ اللّٰهُ بِشَيْءٍ مِنَ الصَّيْدِ تَنَالُهُٓ اَيْد۪يكُمْ وَرِمَاحُكُمْ لِيَعْلَمَ اللّٰهُ مَنْ يَخَافُهُ بِالْغَيْبِۚ فَمَنِ اعْتَدٰى بَعْدَ ذٰلِكَ فَلَهُ عَذَابٌ اَل۪يمٌ', 'Ey iman edenler! Allah sizi ellerinizin ve mızraklarınızın erişeceği bir avlanma ile (onu yasak ederek) dener ki gizlide (kimsenin görmediği yerde, gerçekten) kendisinden kimin korktuğu ortaya çıksın. Kim bundan sonra sınırı aşarsa onun için acı bir azap vardır.', 7, 122, 120, 11892, 'Sofra', 'Medine', 112),
(764, 95, 5, 'Maide', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا لَا تَقْتُلُوا الصَّيْدَ وَاَنْتُمْ حُرُمٌۜ وَمَنْ قَتَلَهُ مِنْكُمْ مُتَعَمِّداً فَجَزَٓاءٌ مِثْلُ مَا قَتَلَ مِنَ النَّعَمِ يَحْكُمُ بِه۪ ذَوَا عَدْلٍ مِنْكُمْ هَدْياً بَالِغَ الْكَعْبَةِ اَوْ كَفَّارَةٌ طَعَامُ مَسَاك۪ينَ اَوْ عَدْلُ ذٰلِكَ صِيَاماً لِيَذُوقَ وَبَالَ اَمْرِه۪ۜ عَفَا اللّٰهُ عَمَّا سَلَفَۜ وَمَنْ عَادَ فَيَنْتَقِمُ اللّٰهُ مِنْهُۜ وَاللّٰهُ عَز۪يزٌ ذُوانْتِقَامٍ', 'Ey iman edenler! İhramlı iken avı öldürmeyin. İçinizden kim onu kasten öldürürse öldürdüğü hayvanın dengi (ona) cezadır. (Buna) Kâbe´ye varacak bir kurban olmak üzere içinizden adalet sahibi iki kişi hükmeder (öldürülen avın dengini takdir eder). Yahut (avlanmanın cezası), fakirleri doyurmaktan ibaret bir keffârettir, yahut onun dengi oruç tutmaktır. Ta ki (yasak av yapan) işinin cezasını tatmış olsun. Allah geçmişi affetmiştir. Kim bu suçu tekrar işlerse Allah da ondan karşılığını alır. Allah daima galiptir, öç alandır.', 7, 122, 120, 11892, 'Sofra', 'Medine', 112),
(765, 96, 5, 'Maide', 'اُحِلَّ لَكُمْ صَيْدُ الْبَحْرِ وَطَعَامُهُ مَتَاعاً لَكُمْ وَلِلسَّيَّارَةِۚ وَحُرِّمَ عَلَيْكُمْ صَيْدُ الْبَرِّ مَا دُمْتُمْ حُرُماًۜ وَاتَّقُوا اللّٰهَ الَّـذ۪ٓي اِلَيْهِ تُحْشَرُونَ', 'Hem size hem de yolculara fayda olmak üzere (faydalanmanız için) deniz avı yapmak ve onu yemek size helâl kılındı. İhramlı olduğunuz müddetçe kara avı size haram kılındı. Huzuruna toplanacağınız Allah´tan korkun.', 7, 123, 120, 11892, 'Sofra', 'Medine', 112),
(766, 97, 5, 'Maide', 'جَعَلَ اللّٰهُ الْكَعْبَةَ الْبَيْتَ الْحَرَامَ قِيَاماً لِلنَّاسِ وَالشَّهْرَ الْحَرَامَ وَالْهَدْيَ وَالْقَلَٓائِدَۜ ذٰلِكَ لِتَعْلَمُٓوا اَنَّ اللّٰهَ يَعْلَمُ مَا فِي السَّمٰوَاتِ وَمَا فِي الْاَرْضِ وَاَنَّ اللّٰهَ بِكُلِّ شَيْءٍ عَل۪يمٌ', 'Allah, Kâbe´yi, o saygıya lâyık evi, haram ayı, hac kurbanını ve (kurbanın boynuna asılan) gerdanlıkları (maddi ve manevi yönlerden) insanların belini doğrultmaya sebep kıldı. Bu da Allah´ın, göklerde ve yerde ne varsa hepsini bildiğini ve Allah´ın her şeyi bilici olduğunu (sizin de anlayıp) bilmeniz içindir.', 7, 123, 120, 11892, 'Sofra', 'Medine', 112),
(767, 98, 5, 'Maide', 'اِعْلَمُٓوا اَنَّ اللّٰهَ شَد۪يدُ الْعِقَابِ وَاَنَّ اللّٰهَ غَفُورٌ رَح۪يمٌۜ', 'Biliniz ki Allah´ın cezalandırması çetindir ve yine Allah´ın bağışlaması ve esirgemesi sınırsızdır.', 7, 123, 120, 11892, 'Sofra', 'Medine', 112),
(768, 99, 5, 'Maide', 'مَا عَلَى الرَّسُولِ اِلَّا الْبَلَاغُۜ وَاللّٰهُ يَعْلَمُ مَا تُبْدُونَ وَمَا تَكْتُمُونَ', 'Resûle düşen (vazife), ancak duyurmadır. Allah açıkladığınızı da gizlediğinizi de bilir.', 7, 123, 120, 11892, 'Sofra', 'Medine', 112),
(769, 100, 5, 'Maide', 'قُلْ لَا يَسْتَوِي الْخَب۪يثُ وَالطَّيِّبُ وَلَوْ اَعْجَبَكَ كَـثْرَةُ الْخَب۪يثِۚ فَاتَّقُوا اللّٰهَ يَٓا اُو۬لِي الْاَلْبَابِ لَعَلَّكُمْ تُفْلِحُونَ۟', 'De ki: Pis ve kötü ile temiz ve iyi bir değildir; pis ve kötünün çokluğu tuhafına gitse (yahut hoşuna gitse) de (bu böyledir). Öyleyse ey akıl sahipleri! Allah´tan korkunuz ki kurtuluşa eresiniz.', 7, 123, 120, 11892, 'Sofra', 'Medine', 112),
(770, 101, 5, 'Maide', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا لَا تَسْـَٔلُوا عَنْ اَشْيَٓاءَ اِنْ تُبْدَ لَكُمْ تَسُؤْكُمْۚ وَاِنْ تَسْـَٔلُوا عَنْهَا ح۪ينَ يُنَزَّلُ الْقُرْاٰنُ تُبْدَ لَكُمْۜ عَفَا اللّٰهُ عَنْهَاۜ وَاللّٰهُ غَفُورٌ حَل۪يمٌ', 'Ey iman edenler! Açıklanırsa hoşunuza gitmeyecek olan şeyleri sormayın. Eğer Kur´an indirilirken onları sorarsanız size açıklanır. (Açıklanmadığına göre) Allah onları affetmiştir. (Siz sorup da başınıza iş çıkarmayın). Allah çok bağışlayıcıdır, aceleci değildir.', 7, 123, 120, 11892, 'Sofra', 'Medine', 112),
(771, 102, 5, 'Maide', 'قَدْ سَاَلَهَا قَوْمٌ مِنْ قَبْلِكُمْ ثُمَّ اَصْبَحُوا بِهَا كَافِر۪ينَ', 'Sizden önce de bir toplum onları sormuş, sonra da bunları inkâr eder olmuştu.', 7, 123, 120, 11892, 'Sofra', 'Medine', 112),
(772, 103, 5, 'Maide', 'مَا جَعَلَ اللّٰهُ مِنْ بَح۪يرَةٍ وَلَا سَٓائِبَةٍ وَلَا وَص۪يلَةٍ وَلَا حَامٍۙ وَلٰكِنَّ الَّذ۪ينَ كَفَرُوا يَفْتَرُونَ عَلَى اللّٰهِ الْكَذِبَۜ وَاَكْثَرُهُمْ لَا يَعْقِلُونَ', 'Allah bahîra, sâibe, vasîle ve hâm diye bir şey (meşru) kılmamıştır. Fakat kâfirler, yalan yere Allah´a iftira etmektedirler ve onların çoğunun da kafaları çalışmaz.', 7, 123, 120, 11892, 'Sofra', 'Medine', 112),
(773, 104, 5, 'Maide', 'وَاِذَا ق۪يلَ لَهُمْ تَعَالَوْا اِلٰى مَٓا اَنْزَلَ اللّٰهُ وَاِلَى الرَّسُولِ قَالُوا حَسْبُنَا مَا وَجَدْنَا عَلَيْهِ اٰبَٓاءَنَاۜ اَوَلَوْ كَانَ اٰبَٓاؤُ۬هُمْ لَا يَعْلَمُونَ شَيْـٔاً وَلَا يَهْتَدُونَ', 'Onlara, «Allah´ın indirdiğine ve Resûl´e gelin» denildiği vakit, «Babalarımızı üzerinde bulduğumuz (yol) bize yeter» derler. Ataları hiçbir şey bilmiyor ve doğru yol üzerinde bulunmuyor iseler de mi?', 7, 124, 120, 11892, 'Sofra', 'Medine', 112),
(774, 105, 5, 'Maide', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا عَلَيْكُمْ اَنْفُسَكُمْۚ لَا يَضُرُّكُمْ مَنْ ضَلَّ اِذَا اهْتَدَيْتُمْۜ اِلَى اللّٰهِ مَرْجِعُكُمْ جَم۪يعاً فَيُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُونَ', 'Ey iman edenler! Siz kendinize bakın. Siz doğru yolda olunca sapan kimse size zarar veremez. Hepinizin dönüşü Allah´adır. Artık O, size yaptıklarınızı bildirecektir.', 7, 124, 120, 11892, 'Sofra', 'Medine', 112),
(775, 106, 5, 'Maide', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا شَهَادَةُ بَيْنِكُمْ اِذَا حَضَرَ اَحَدَكُمُ الْمَوْتُ ح۪ينَ الْوَصِيَّةِ اثْنَانِ ذَوَا عَدْلٍ مِنْكُمْ اَوْ اٰخَرَانِ مِنْ غَيْرِكُمْ اِنْ اَنْتُمْ ضَرَبْتُمْ فِي الْاَرْضِ فَاَصَابَتْكُمْ مُص۪يبَةُ الْمَوْتِۜ تَحْبِسُونَهُمَا مِنْ بَعْدِ الصَّلٰوةِ فَيُقْسِمَانِ بِاللّٰهِ اِنِ ارْتَبْتُمْ لَا نَشْتَر۪ي بِه۪ ثَمَناً وَلَوْ كَانَ ذَا قُرْبٰىۙ وَلَا نَكْتُمُ شَهَادَةَ اللّٰهِ اِنَّٓا اِذاً لَمِنَ الْاٰثِم۪ينَ', 'Ey iman edenler! Birinize ölüm gelip çatınca vasiyet esnasında içinizden iki adalet sahibi kişi aranızda şahitlik etsin. Yahut seferde iken başınıza ölüm musibeti gelmişse sizden olmayan, başka iki kişi (şahit olsun). Eğer şüpheye düşerseniz o iki şahidi namazdan sonra alıkorsunuz; «Bu vasiyet karşılığında hiçbir şeyi satın almayacağız, akraba (menfaatine) de olsa; Allah (için yaptığımız) şahitliği gizlemiyeceğiz, (aksini yaparsak) bu takdirde biz elbette günahkârlardan oluruz» diye Allah üzerine yemin ederler.', 7, 124, 120, 11892, 'Sofra', 'Medine', 112),
(776, 107, 5, 'Maide', 'فَاِنْ عُثِرَ عَلٰٓى اَنَّهُمَا اسْتَحَقَّٓا اِثْماً فَاٰخَرَانِ يَقُومَانِ مَقَامَهُمَا مِنَ الَّذ۪ينَ اسْتَحَقَّ عَلَيْهِمُ الْاَوْلَيَانِ فَيُقْسِمَانِ بِاللّٰهِ لَشَهَادَتُـنَٓا اَحَقُّ مِنْ شَهَادَتِهِمَا وَمَا اعْتَدَيْنَاۘ اِنَّٓا اِذاً لَمِنَ الظَّالِم۪ينَ', 'Bu şahitlerin (sonradan yalan söyleyerek) bir günah kazandıkları anlaşılırsa, (şahitlerin) haklarına tecavüz ettiği ölüye daha yakın olan (mirasçılardan) iki kişi onların yerini alır ve «Andolsun ki bizim şahitliğimiz onların şahitliğinden daha gerçektir ve biz (kimsenin hakkına) tecavüz etmedik, aksi takdirde biz, elbette zalimlerden oluruz» diye Allah´a yemin ederler.', 7, 124, 120, 11892, 'Sofra', 'Medine', 112),
(777, 108, 5, 'Maide', 'ذٰلِكَ اَدْنٰٓى اَنْ يَأْتُوا بِالشَّهَادَةِ عَلٰى وَجْهِهَٓا اَوْ يَخَافُٓوا اَنْ تُرَدَّ اَيْمَانٌ بَعْدَ اَيْمَانِهِمْۜ وَاتَّقُوا اللّٰهَ وَاسْمَعُواۜ وَاللّٰهُ لَا يَهْدِي الْقَوْمَ الْفَاسِق۪ينَ۟', 'Bu (usul), şahitliği gerektiği şekilde yapmaya, yahut yeminlerinden sonra, yeminlerin (mirasçılar tarafından) reddedilmesinden korkmalarına (çekinmelerine çare olarak) daha uygundur. Allah´tan korkun ve (O´nu) dinleyin. Allah, yoldan çıkmışlar topluluğuna rehberlik etmez.', 7, 124, 120, 11892, 'Sofra', 'Medine', 112),
(778, 109, 5, 'Maide', 'يَوْمَ يَجْمَعُ اللّٰهُ الرُّسُلَ فَيَقُولُ مَاذَٓا اُجِبْتُمْۜ قَالُوا لَا عِلْمَ لَنَاۜ اِنَّكَ اَنْتَ عَلَّامُ الْغُيُوبِ', 'Allah´ın peygamberleri toplayıp da «Size ne cevap verildi» dediği gün, «Bizim hiçbir bilgimiz yok, şüphesiz gizlilikleri hakkıyle bilen ancak sensin» diyeceklerdir.', 7, 125, 120, 11892, 'Sofra', 'Medine', 112),
(779, 110, 5, 'Maide', 'اِذْ قَالَ اللّٰهُ يَا ع۪يسَى ابْنَ مَرْيَمَ اذْكُرْ نِعْمَت۪ي عَلَيْكَ وَعَلٰى وَالِدَتِكَۢ اِذْ اَيَّدْتُكَ بِرُوحِ الْقُدُسِ تُكَلِّمُ النَّاسَ فِي الْمَهْدِ وَكَهْلاًۚ وَاِذْ عَلَّمْتُكَ الْكِتَابَ وَالْحِكْمَةَ وَالتَّوْرٰيةَ وَالْاِنْج۪يلَۚ وَاِذْ تَخْلُقُ مِنَ الطّ۪ينِ كَـهَيْـَٔةِ الطَّيْرِ بِاِذْن۪ي فَتَنْفُخُ ف۪يهَا فَتَكُونُ طَيْراً بِاِذْن۪ي وَتُبْرِئُ الْاَكْمَهَ وَالْاَبْرَصَ بِاِذْن۪يۚ وَاِذْ تُخْرِجُ الْمَوْتٰى بِاِذْن۪يۚ وَاِذْ كَفَفْتُ بَن۪ٓي اِسْرَٓائ۪لَ عَنْكَ اِذْ جِئْتَهُمْ بِالْبَيِّنَاتِ فَقَالَ الَّذ۪ينَ كَفَرُوا مِنْهُمْ اِنْ هٰذَٓا اِلَّا سِحْرٌ مُب۪ينٌ', 'Allah o zaman şöyle diyecek: «Ey Meryem oğlu İsa! Sana ve annene (verdiğim) nimetimi hatırla! Hani seni mukaddes ruh (Cebrail) ile desteklemiştim; (bu sayede) sen beşikte iken de yetişkin çağında da insanlarla konuşuyordun. Sana kitabı (okuyup yazmayı), hikmeti, Tevrat ve İncil´i öğretmiştim. Benim iznimle çamurdan, kuş şeklinde bir şey yapıyordun da ona üflüyordun, hemen benim iznimle o bir kuş oluyordu. Yine benim iznimle anadan doğma körü ve alacalıyı iyileştiriyordun. Ölüleri benim iznimle (hayata) çıkarıyordun. Hani İsrailoğullarını (seni öldürmekten) engellemiştim; kendilerine apaçık deliller (mucizeler) getirdiğin zaman içlerinden inkâr edenler, «Bu, apaçık bir sihirden başka bir şey değildir» demişlerdi.', 7, 125, 120, 11892, 'Sofra', 'Medine', 112),
(780, 111, 5, 'Maide', 'وَاِذْ اَوْحَيْتُ اِلَى الْحَوَارِيّ۪نَ اَنْ اٰمِنُوا ب۪ي وَبِرَسُول۪يۚ قَالُٓوا اٰمَنَّا وَاشْهَدْ بِاَنَّـنَا مُسْلِمُونَ', 'Hani havârîlere, «Bana ve peygamberime iman edin» diye ilham etmiştim. Onlar (da), «İman ettik, bizim Allah´a teslim olmuş kimseler (müslümanlar) olduğumuza sen de şahit ol» demişlerdi.', 7, 125, 120, 11892, 'Sofra', 'Medine', 112),
(781, 112, 5, 'Maide', 'اِذْ قَالَ الْحَوَارِيُّونَ يَا ع۪يسَى ابْنَ مَرْيَمَ هَلْ يَسْتَط۪يعُ رَبُّكَ اَنْ يُنَزِّلَ عَلَيْنَا مَٓائِدَةً مِنَ السَّمَٓاءِۜ قَالَ اتَّقُوا اللّٰهَ اِنْ كُنْتُمْ مُؤْمِن۪ينَ', 'Hani havârîler «Ey Meryem oğlu İsa, Rabbin bize gökten, donatılmış bir sofra indirebilir mi?» demişlerdi. O, «Îman etmiş kimseler iseniz Allah´tan korkun» cevabını vermişti.', 7, 125, 120, 11892, 'Sofra', 'Medine', 112),
(782, 113, 5, 'Maide', 'قَالُوا نُر۪يدُ اَنْ نَأْكُلَ مِنْهَا وَتَطْمَئِنَّ قُلُوبُنَا وَنَعْلَمَ اَنْ قَدْ صَدَقْتَنَا وَنَكُونَ عَلَيْهَا مِنَ الشَّاهِد۪ينَ', 'Onlar «Ondan yiyelim, kalplerimiz mutmain olsun, bize doğru söylediğini (kesin olarak) bilelim ve ona gözleriyle görmüş şahitler olalım istiyoruz» demişlerdi.', 7, 125, 120, 11892, 'Sofra', 'Medine', 112),
(783, 114, 5, 'Maide', 'قَالَ ع۪يسَى ابْنُ مَرْيَمَ اللّٰهُمَّ رَبَّنَٓا اَنْزِلْ عَلَيْنَا مَٓائِدَةً مِنَ السَّمَٓاءِ تَكُونُ لَنَا ع۪يداً لِاَوَّلِنَا وَاٰخِرِنَا وَاٰيَةً مِنْكَۚ وَارْزُقْنَا وَاَنْتَ خَيْرُ الرَّازِق۪ينَ', 'Meryem oğlu İsa şöyle dedi: Ey Rabbimiz! Bize gökten bir sofra indir ki, bizim için, geçmiş ve geleceklerimiz için bayram ve senden bir âyet (mucize) olsun. Bizi rızıklandır; zaten sen, rızık verenlerin en hayırlısısın.', 7, 126, 120, 11892, 'Sofra', 'Medine', 112),
(784, 115, 5, 'Maide', 'قَالَ اللّٰهُ اِنّ۪ي مُنَزِّلُهَا عَلَيْكُمْۚ فَمَنْ يَكْفُرْ بَعْدُ مِنْكُمْ فَاِنّ۪ٓي اُعَذِّبُهُ عَذَاباً لَٓا اُعَذِّبُهُٓ اَحَداً مِنَ الْعَالَم۪ينَ۟', 'Allah da şöyle buyurdu: Ben onu size şüphesiz indireceğim; ama bundan sonra içinizden kim inkâr ederse, kâinatta hiç bir kimseye etmediğim azabı ona edeceğim!', 7, 126, 120, 11892, 'Sofra', 'Medine', 112),
(785, 116, 5, 'Maide', 'وَاِذْ قَالَ اللّٰهُ يَا ع۪يسَى ابْنَ مَرْيَمَ ءَاَنْتَ قُلْتَ لِلنَّاسِ اتَّخِذُون۪ي وَاُمِّيَ اِلٰهَيْنِ مِنْ دُونِ اللّٰهِۜ قَالَ سُبْحَانَكَ مَا يَكُونُ ل۪ٓي اَنْ اَقُولَ مَا لَيْسَ ل۪ي بِحَقٍّۜ اِنْ كُنْتُ قُلْتُهُ فَقَدْ عَلِمْتَهُۜ تَعْلَمُ مَا ف۪ي نَفْس۪ي وَلَٓا اَعْلَمُ مَا ف۪ي نَفْسِكَۜ اِنَّكَ اَنْتَ عَلَّامُ الْغُيُوبِ', 'Allah: Ey Meryem oğlu İsa! İnsanlara, «Beni ve anamı, Allah´tan başka iki tanrı bilin» diye sen mi dedin, buyurduğu zaman o, «Hâşâ! Seni tenzih ederim; hakkım olmayan şeyi söylemek bana yakışmaz. Hem ben söyleseydim sen onu şüphesiz bilirdin. Sen benim içimdekini bilirsin, halbuki ben senin zâtında olanı bilmem. Gizlilikleri eksiksiz bilen yalnızca sensin.', 7, 126, 120, 11892, 'Sofra', 'Medine', 112),
(786, 117, 5, 'Maide', 'مَا قُلْتُ لَهُمْ اِلَّا مَٓا اَمَرْتَن۪ي بِه۪ٓ اَنِ اعْبُدُوا اللّٰهَ رَبّ۪ي وَرَبَّكُمْۚ وَكُنْتُ عَلَيْهِمْ شَه۪يداً مَا دُمْتُ ف۪يهِمْۚ فَلَمَّا تَوَفَّيْتَن۪ي كُنْتَ اَنْتَ الرَّق۪يبَ عَلَيْهِمْۜ وَاَنْتَ عَلٰى كُلِّ شَيْءٍ شَه۪يدٌ', 'Ben onlara, ancak bana emrettiğini söyledim: Benim de Rabbim, sizin de Rabbiniz olan Allah´a kulluk edin, dedim. İçlerinde bulunduğum müddetçe onlar üzerine kontrolcü idim. Beni vefat ettirince artık onlar üzerine gözetleyici yalnız sen oldun. Sen her şeyi hakkıyle görensin.', 7, 126, 120, 11892, 'Sofra', 'Medine', 112),
(787, 118, 5, 'Maide', 'اِنْ تُعَذِّبْهُمْ فَاِنَّهُمْ عِبَادُكَۚ وَاِنْ تَغْفِرْ لَهُمْ فَاِنَّكَ اَنْتَ الْعَز۪يزُ الْحَك۪يمُ', 'Eğer kendilerine azap edersen şüphesiz onlar senin kullarındır (dilediğini yaparsın). Eğer onları bağışlarsan şüphesiz sen izzet ve hikmet sahibisin» dedi.', 7, 126, 120, 11892, 'Sofra', 'Medine', 112),
(788, 119, 5, 'Maide', 'قَالَ اللّٰهُ هٰذَا يَوْمُ يَنْفَعُ الصَّادِق۪ينَ صِدْقُهُمْۜ لَهُمْ جَنَّاتٌ تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُ خَالِد۪ينَ ف۪يهَٓا اَبَداًۜ رَضِيَ اللّٰهُ عَنْهُمْ وَرَضُوا عَنْهُۜ ذٰلِكَ الْفَوْزُ الْعَظ۪يمُ', '(Bu konuşmadan sonra) Allah şöyle buyuracaktır: Bu, doğrulara, doğruluklarının fayda vereceği gündür. Onlara, içinde ebedî kalacakları, zemininden ırmaklar akan cennetler vardır. Allah onlardan razı olmuştur, onlar da O´ndan razı olmuşlardır. İşte büyük kurtuluş ve kazanç budur.', 7, 126, 120, 11892, 'Sofra', 'Medine', 112),
(789, 120, 5, 'Maide', 'لِلّٰهِ مُلْكُ السَّمٰوَاتِ وَالْاَرْضِ وَمَا ف۪يهِنَّۜ وَهُوَ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌ', 'Göklerin, yerin ve içlerindeki her şeyin mülkiyeti Allah´ındır, O, her şeye hakkıyle kadirdir.', 7, 126, 120, 11892, 'Sofra', 'Medine', 112),
(790, 1, 6, 'En’âm', 'اَلْحَمْدُ لِلّٰهِ الَّذ۪ي خَلَقَ السَّمٰوَاتِ وَالْاَرْضَ وَجَعَلَ الظُّلُمَاتِ وَالنُّورَۜ ثُمَّ الَّذ۪ينَ كَفَرُوا بِرَبِّهِمْ يَعْدِلُونَ', 'Hamd, gökleri ve yeri yaratan, karanlıkları ve aydınlığı var eden Allah´a mahsustur. (Bunca âyet ve delillerden) sonra kâfir olanlar (hâla putları) Rab´leri ile denk tutuyorlar.', 7, 127, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(791, 2, 6, 'En’âm', 'هُوَ الَّذ۪ي خَلَقَكُمْ مِنْ ط۪ينٍ ثُمَّ قَضٰٓى اَجَلاًۜ وَاَجَلٌ مُسَمًّى عِنْدَهُ ثُمَّ اَنْتُمْ تَمْتَرُونَ', 'Sizi bir çamurdan yaratan, sonra ölüm zamanını takdir eden ancak O´dur. Bir de O´nun katında muayyen bir ecel (kıyamet günü) vardır. Siz hâla şüphe ediyorsunuz.', 7, 127, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(792, 3, 6, 'En’âm', 'وَهُوَ اللّٰهُ فِي السَّمٰوَاتِ وَفِي الْاَرْضِۜ يَعْلَمُ سِرَّكُمْ وَجَهْرَكُمْ وَيَعْلَمُ مَا تَكْسِبُونَ', 'O, göklerde ve yerde tek Allah´tır. Gizlinizi, açığınızı bilir. (Hayır ve şerden) ne kazanacağınızı da bilir.', 7, 127, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(793, 4, 6, 'En’âm', 'وَمَا تَأْت۪يهِمْ مِنْ اٰيَةٍ مِنْ اٰيَاتِ رَبِّهِمْ اِلَّا كَانُوا عَنْهَا مُعْرِض۪ينَ', 'Rablerinin âyetlerinden onlara (kâfirlere) bir âyet gelmeyedursun, o âyetlerden ille de yüz çevirirler.', 7, 127, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(794, 5, 6, 'En’âm', 'فَقَدْ كَذَّبُوا بِالْحَقِّ لَمَّا جَٓاءَهُمْۜ فَسَوْفَ يَأْت۪يهِمْ اَنْبٰٓـؤُ۬ا مَا كَانُوا بِه۪ يَسْتَهْزِؤُ۫نَ', 'Gerçekten onlar, kendilerine Hak geldiğinde onu yalanlamışlardı. Fakat yakında onlara alay ettikleri şeyin haberleri gelecektir.', 7, 127, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(795, 6, 6, 'En’âm', 'اَلَمْ يَرَوْا كَمْ اَهْلَكْنَا مِنْ قَبْلِهِمْ مِنْ قَرْنٍ مَكَّنَّاهُمْ فِي الْاَرْضِ مَا لَمْ نُمَكِّنْ لَكُمْ وَاَرْسَلْنَا السَّمَٓاءَ عَلَيْهِمْ مِدْرَاراًۖ وَجَعَلْنَا الْاَنْهَارَ تَجْر۪ي مِنْ تَحْتِهِمْ فَاَهْلَكْنَاهُمْ بِذُنُوبِهِمْ وَاَنْشَأْنَا مِنْ بَعْدِهِمْ قَرْناً اٰخَر۪ينَ', 'Görmediler mi ki, onlardan önce yeryüzünde size vermediğimiz bütün imkânları kendilerine verdiğimiz, gökten üzerlerine bol bol yağmurlar indirip evlerinin altından ırmaklar akıttığımız nice nesilleri helâk ettik. Biz onları, günahları sebebiyle helâk ettik ve onların ardından başka nesiller yarattık.', 7, 127, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(796, 7, 6, 'En’âm', 'وَلَوْ نَزَّلْنَا عَلَيْكَ كِتَاباً ف۪ي قِرْطَاسٍ فَلَمَسُوهُ بِاَيْد۪يهِمْ لَقَالَ الَّذ۪ينَ كَفَرُٓوا اِنْ هٰذَٓا اِلَّا سِحْرٌ مُب۪ينٌ', 'Eğer sana kâğıt üzerine yazılmış bir kitap indirseydik de onlar elleriyle onu tutmuş olsalardı, yine de inkâr ediciler: Bu, apaçık büyüden başka bir şey değildir, derlerdi.', 7, 127, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(797, 8, 6, 'En’âm', 'وَقَالُوا لَوْلَٓا اُنْزِلَ عَلَيْهِ مَلَكٌۜ وَلَوْ اَنْزَلْنَا مَلَكاً لَقُضِيَ الْاَمْرُ ثُمَّ لَا يُنْظَرُونَ', 'Muhammed´e (görebileceğimiz) bir melek indirilseydi ya! dediler. Eğer biz öyle bir melek indirseydik elbette iş bitirilmiş olur, artık kendilerine göz bile açtırılmazdı.', 7, 127, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(798, 9, 6, 'En’âm', 'وَلَوْ جَعَلْنَاهُ مَلَكاً لَجَعَلْنَاهُ رَجُلاً وَلَلَبَسْنَا عَلَيْهِمْ مَا يَلْبِسُونَ', 'Eğer peygamberi bir melek kılsaydık muhakkak ki onu insan sûretine sokar onları yine düşmekte oldukları kuşkuya düşürürdük.', 7, 128, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(799, 10, 6, 'En’âm', 'وَلَقَدِ اسْتُهْزِئَ بِرُسُلٍ مِنْ قَبْلِكَ فَحَاقَ بِالَّذ۪ينَ سَخِرُوا مِنْهُمْ مَا كَانُوا بِه۪ يَسْتَهْزِؤُ۫نَ۟', 'Senden önceki peygamberlerle de alay edilmiş, bu yüzden onlarla alay edenleri alay ettikleri şey (azap) kuşatıvermişti.', 7, 128, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(800, 11, 6, 'En’âm', 'قُلْ س۪يرُوا فِي الْاَرْضِ ثُمَّ انْظُرُوا كَيْفَ كَانَ عَاقِبَةُ الْمُكَذِّب۪ينَ', 'De ki: Yeryüzünde dolaşın, sonra (peygamberleri) yalanlayanların sonunun nasıl olduğuna bakın!', 7, 128, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(801, 12, 6, 'En’âm', 'قُلْ لِمَنْ مَا فِي السَّمٰوَاتِ وَالْاَرْضِۜ قُلْ لِلّٰهِۜ كَتَبَ عَلٰى نَفْسِهِ الرَّحْمَةَۜ لَيَجْمَعَنَّكُمْ اِلٰى يَوْمِ الْقِيٰمَةِ لَا رَيْبَ ف۪يهِۜ اَلَّذ۪ينَ خَسِرُٓوا اَنْفُسَهُمْ فَهُمْ لَا يُؤْمِنُونَ', '(Onlara) Göklerde ve yerde olanlar kimindir? diye sor. «Allah´ındır» de. O, merhamet etmeyi kendi zatına farz kıldı. Sizi, varlığında şüphe olmayan kıyamet gününde elbette toplayacaktır. Kendilerini ziyana sokanlar var ya işte onlar inanmazlar.', 7, 128, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(802, 13, 6, 'En’âm', 'وَلَهُ مَا سَكَنَ فِي الَّيْلِ وَالنَّهَارِۜ وَهُوَ السَّم۪يعُ الْعَل۪يمُ', 'Gecede ve gündüzde barınan her şey O´nundur. O her şeyi işitendir, bilendir.', 7, 128, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(803, 14, 6, 'En’âm', 'قُلْ اَغَيْرَ اللّٰهِ اَتَّخِذُ وَلِياًّ فَاطِرِ السَّمٰوَاتِ وَالْاَرْضِ وَهُوَ يُطْعِمُ وَلَا يُطْعَمُۜ قُلْ اِنّ۪ٓي اُمِرْتُ اَنْ اَكُونَ اَوَّلَ مَنْ اَسْلَمَ وَلَا تَكُونَنَّ مِنَ الْمُشْرِك۪ينَ', 'De ki: Gökleri ve yeri yoktan var eden, yedirdiği halde yedirilmeyen Allah´tan başkasını mı dost edineceğim! De ki: Bana müslüman olanların ilki olmam emredildi ve sakın müşriklerden olma! (denildi).', 7, 128, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(804, 15, 6, 'En’âm', 'قُلْ اِنّ۪ٓي اَخَافُ اِنْ عَصَيْتُ رَبّ۪ي عَذَابَ يَوْمٍ عَظ۪يمٍ', 'De ki: Ben, Rabbim´e isyan edersem gerçekten büyük bir günün (kıyametin) azabından korkarım.', 7, 128, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(805, 16, 6, 'En’âm', 'مَنْ يُصْرَفْ عَنْهُ يَوْمَئِذٍ فَقَدْ رَحِمَهُۜ وَذٰلِكَ الْفَوْزُ الْمُب۪ينُ', 'O gün kim azaptan kurtarılırsa, gerçekten Allah onu esirgemiştir. İşte apaçık kurtuluş budur.', 7, 128, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(806, 17, 6, 'En’âm', 'وَاِنْ يَمْسَسْكَ اللّٰهُ بِضُرٍّ فَلَا كَاشِفَ لَهُٓ اِلَّا هُوَۜ وَاِنْ يَمْسَسْكَ بِخَيْرٍ فَهُوَ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌ', 'Eğer Allah seni bir zarara uğratırsa, onu kendisinden başka giderecek yoktur. Ve eğer sana bir hayır verirse, (bunu da geri alacak yoktur). Şüphesiz O herşeye kadirdir.', 7, 128, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(807, 18, 6, 'En’âm', 'وَهُوَ الْقَاهِرُ فَوْقَ عِبَادِه۪ۜ وَهُوَ الْحَك۪يمُ الْخَب۪يرُ', 'O, kullarının üstünde her türlü tasarrufa sahiptir. O, hüküm ve hikmet sahibidir, herşeyden haberdardır.', 7, 128, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(808, 19, 6, 'En’âm', 'قُلْ اَيُّ شَيْءٍ اَكْبَرُ شَهَادَةًۜ قُلِ اللّٰهُ شَه۪يدٌ بَيْن۪ي وَبَيْنَكُمْ وَاُو۫حِيَ اِلَيَّ هٰذَا الْقُرْاٰنُ لِاُنْذِرَكُمْ بِه۪ وَمَنْ بَلَغَۜ اَئِنَّكُمْ لَتَشْهَدُونَ اَنَّ مَعَ اللّٰهِ اٰلِهَةً اُخْرٰىۜ قُلْ لَٓا اَشْهَدُۚ قُلْ اِنَّمَا هُوَ اِلٰهٌ وَاحِدٌ وَاِنَّن۪ي بَر۪ٓيءٌ مِمَّا تُشْرِكُونَۢ', 'De ki: Hangi şey şahadetçe en büyüktür? De ki: (Hak peygamber olduğuma dair) benimle sizin aranızda Allah şahittir. Bu Kur´an bana, kendisiyle sizi ve ulaştığı herkesi uyarmam için vahyolundu. Yoksa siz, Allah ile beraber başka tanrılar olduğuna şahitlik mi ediyorsunuz? De ki: «Ben buna şahitlik etmem.» «O ancak bir tek Allah´tır, ben sizin ortak koştuğunuz şeylerden kesinlikle uzağım» de.', 7, 129, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(809, 20, 6, 'En’âm', 'اَلَّذ۪ينَ اٰتَيْنَاهُمُ الْكِتَابَ يَعْرِفُونَهُ كَمَا يَعْرِفُونَ اَبْنَٓاءَهُمْۢ اَلَّذ۪ينَ خَسِرُٓوا اَنْفُسَهُمْ فَهُمْ لَا يُؤْمِنُونَ۟', 'Kendilerine kitap verdiklerimiz onu (Resûlullah´ı) kendi oğullarını tanıdıkları gibi tanırlar. Kendilerini ziyan edenler var ya, işte onlar inanmazlar.', 7, 129, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(810, 21, 6, 'En’âm', 'وَمَنْ اَظْلَمُ مِمَّنِ افْتَرٰى عَلَى اللّٰهِ كَذِباً اَوْ كَذَّبَ بِاٰيَاتِه۪ۜ اِنَّهُ لَا يُفْلِحُ الظَّالِمُونَ', 'Yalan sözlerle Allah´a iftira edenden veya O´nun âyetlerini yalanlayandan daha zalim kimdir! Şüphe yok ki, zalimler kurtuluşa ermezler!', 7, 129, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(811, 22, 6, 'En’âm', 'وَيَوْمَ نَحْشُرُهُمْ جَم۪يعاً ثُمَّ نَقُولُ لِلَّذ۪ينَ اَشْرَكُٓوا اَيْنَ شُرَكَٓاؤُ۬كُمُ الَّذ۪ينَ كُنْتُمْ تَزْعُمُونَ', 'Unutma o günü ki, onları hep birden toplayacağız; sonra da, Allah´a ortak koşanlara: Nerede boş yere davasını güttüğünüz ortaklarınız? diyeceğiz.', 7, 129, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(812, 23, 6, 'En’âm', 'ثُمَّ لَمْ تَكُنْ فِتْنَتُهُمْ اِلَّٓا اَنْ قَالُوا وَاللّٰهِ رَبِّنَا مَا كُنَّا مُشْرِك۪ينَ', 'Sonra onların mazeretleri, «Rabbimiz Allah hakkı için biz ortak koşanlar olmadık!» demekten başka bir şey olmadı.', 7, 129, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(813, 24, 6, 'En’âm', 'اُنْظُرْ كَيْفَ كَذَبُوا عَلٰٓى اَنْفُسِهِمْ وَضَلَّ عَنْهُمْ مَا كَانُوا يَفْتَرُونَ', 'Gör ki, kendi aleyhlerine nasıl yalan söylediler ve (tanrı diye) uydurdukları şeyler kendilerinden nasıl kaybolup gitti!', 7, 129, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(814, 25, 6, 'En’âm', 'وَمِنْهُمْ مَنْ يَسْتَمِعُ اِلَيْكَۚ وَجَعَلْنَا عَلٰى قُلُوبِهِمْ اَكِنَّةً اَنْ يَفْقَهُوهُ وَف۪ٓي اٰذَانِهِمْ وَقْراًۜ وَاِنْ يَرَوْا كُلَّ اٰيَةٍ لَا يُؤْمِنُوا بِهَاۜ حَتّٰٓى اِذَا جَٓاؤُ۫كَ يُجَادِلُونَكَ يَقُولُ الَّذ۪ينَ كَفَرُٓوا اِنْ هٰذَٓا اِلَّٓا اَسَاط۪يرُ الْاَوَّل۪ينَ', 'Onlardan seni (okuduğun Kur´an´ı) dinleyenler de vardır. Fakat onu anlamalarına engel olmak için kalplerinin üstüne perdeler, kulaklarına da ağırlık verdik. Onlar her türlü mucizeyi görseler bile yine de ona inanmazlar. Hatta o kâfirler sana geldiklerinde: «Bu Kur´an eskilerin masallarından başka bir şey değildir» diyerek seninle tartışırlar.', 7, 129, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(815, 26, 6, 'En’âm', 'وَهُمْ يَنْهَوْنَ عَنْهُ وَيَنْـَٔوْنَ عَنْهُۚ وَاِنْ يُهْلِكُونَ اِلَّٓا اَنْفُسَهُمْ وَمَا يَشْعُرُونَ', 'Onlar, hem insanları Peygamber´e yaklaşmaktan vazgeçirmeye çalışırlar, hem de kendileri ondan uzaklaşırlar. Oysa onlar farkında olmadan ancak kendilerini helak ederler.', 7, 129, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(816, 27, 6, 'En’âm', 'وَلَوْ تَرٰٓى اِذْ وُقِفُوا عَلَى النَّارِ فَقَالُوا يَا لَيْتَنَا نُرَدُّ وَلَا نُكَذِّبَ بِاٰيَاتِ رَبِّنَا وَنَكُونَ مِنَ الْمُؤْمِن۪ينَ', 'Onların ateşin karşısında durdurulup «Ah, keşke dünyaya geri gönderilsek de bir daha Rabbimizin âyetlerini yalanlamasak ve inananlardan olsak!» dediklerini bir görsen!..', 7, 129, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(817, 28, 6, 'En’âm', 'بَلْ بَدَا لَهُمْ مَا كَانُوا يُخْفُونَ مِنْ قَبْلُۜ وَلَوْ رُدُّوا لَعَادُوا لِمَا نُهُوا عَنْهُ وَاِنَّهُمْ لَكَاذِبُونَ', 'Hayır! Daha önce gizlemekte oldukları şeyler (günahlar) kendilerine göründü. Eğer (dünyaya) geri gönderilseler yine kendilerine yasak edilen şeylere döneceklerdir. Zira onlar gerçekten yalancıdırlar.', 7, 130, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(818, 29, 6, 'En’âm', 'وَقَالُٓوا اِنْ هِيَ اِلَّا حَيَاتُنَا الدُّنْيَا وَمَا نَحْنُ بِمَبْعُوث۪ينَ', 'Onlar, hayat ancak bu dünyadaki hayatımızdan ibarettir; biz, bir daha da diriltilecek değiliz, demişlerdi.', 7, 130, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(819, 30, 6, 'En’âm', 'وَلَوْ تَرٰٓى اِذْ وُقِفُوا عَلٰى رَبِّهِمْۜ قَالَ اَلَيْسَ هٰذَا بِالْحَقِّۜ قَالُوا بَلٰى وَرَبِّنَاۜ قَالَ فَذُوقُوا الْعَذَابَ بِمَا كُنْتُمْ تَكْفُرُونَ۟', 'Rablerinin huzuruna getirildikleri zaman sen onları bir görsen! Allah: Bu (yeniden dirilme olayı), hak değil miymiş? diyecek. Onlar da «Rabbimize andolsun ki evet!» diyecekler. Allah da, Öyle ise inkâr ettiğinizden dolayı azabı tadın! diyecek.', 7, 130, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(820, 31, 6, 'En’âm', 'قَدْ خَسِرَ الَّذ۪ينَ كَذَّبُوا بِلِقَٓاءِ اللّٰهِۜ حَتّٰٓى اِذَا جَٓاءَتْهُمُ السَّاعَةُ بَغْتَةً قَالُوا يَا حَسْرَتَنَا عَلٰى مَا فَرَّطْنَا ف۪يهَاۙ وَهُمْ يَحْمِلُونَ اَوْزَارَهُمْ عَلٰى ظُهُورِهِمْۜ اَلَا سَٓاءَ مَا يَزِرُونَ', 'Allah´ın huzuruna çıkmayı yalanlayanlar gerçekten ziyana uğramıştır. Nihayet onlara Kıyamet vakti ansızın gelip çatınca, onlar, günahlarını sırtlarına yüklenerek diyecekler ki: «Dünyada iyi amelleri terketmemizden dolayı vah bize!» Dikkat edin, yüklendikleri şey ne kötüdür!', 7, 130, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(821, 32, 6, 'En’âm', 'وَمَا الْحَيٰوةُ الدُّنْيَٓا اِلَّا لَعِبٌ وَلَهْوٌۜ وَلَلدَّارُ الْاٰخِرَةُ خَيْرٌ لِلَّذ۪ينَ يَتَّقُونَۜ اَفَلَا تَعْقِلُونَ', 'Dünya hayatı bir oyun ve eğlenceden başka bir şey değildir. Müttakî olanlar için ahiret yurdu muhakkak ki daha hayırlıdır. Hâla akıl erdiremiyor musunuz?', 7, 130, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(822, 33, 6, 'En’âm', 'قَدْ نَعْلَمُ اِنَّهُ لَيَحْزُنُكَ الَّذ۪ي يَقُولُونَ فَاِنَّهُمْ لَا يُكَذِّبُونَكَ وَلٰكِنَّ الظَّالِم۪ينَ بِاٰيَاتِ اللّٰهِ يَجْحَدُونَ', 'Onların söylediklerinin hakikaten seni üzmekte olduğunu biliyoruz. Aslında onlar seni yalanlamıyorlar, fakat o zalimler açıkça Allah´ın âyetlerini inkâr ediyorlar.', 7, 130, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(823, 34, 6, 'En’âm', 'وَلَقَدْ كُذِّبَتْ رُسُلٌ مِنْ قَبْلِكَ فَصَبَرُوا عَلٰى مَا كُذِّبُوا وَاُو۫ذُوا حَتّٰٓى اَتٰيهُمْ نَصْرُنَاۚ وَلَا مُبَدِّلَ لِكَلِمَاتِ اللّٰهِۚ وَلَقَدْ جَٓاءَكَ مِنْ نَبَا۬ئِ الْمُرْسَل۪ينَ', 'Andolsun ki senden önceki peygamberler de yalanlanmıştı. Onlar, yalanlanmalarına ve eziyet edilmelerine rağmen sabrettiler, sonunda yardımımız onlara yetişti. Allah´ın kelimelerini (kanunlarını) değiştirebilecek hiçbir kimse yoktur. Muhakkak ki peygamberlerin haberlerinden bazısı sana da geldi.', 7, 130, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(824, 35, 6, 'En’âm', 'وَاِنْ كَانَ كَبُرَ عَلَيْكَ اِعْرَاضُهُمْ فَاِنِ اسْتَطَعْتَ اَنْ تَبْتَغِيَ نَفَقاً فِي الْاَرْضِ اَوْ سُلَّماً فِي السَّمَٓاءِ فَتَأْتِيَهُمْ بِاٰيَةٍۜ وَلَوْ شَٓاءَ اللّٰهُ لَجَمَعَهُمْ عَلَى الْهُدٰى فَلَا تَكُونَنَّ مِنَ الْجَاهِل۪ينَ', 'Eğer onların yüz çevirmesi sana ağır geldi ise, yapabilirsen yerin içine inebileceğin bir tünel ya da göğe çıkabileceğin bir merdiven ara ki onlara bir mucize getiresin! Allah dileseydi, elbette onları hidayet üzerinde toplayıp birleştirirdi, o halde sakın cahillerden olma!', 7, 130, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(825, 36, 6, 'En’âm', 'اِنَّمَا يَسْتَج۪يبُ الَّذ۪ينَ يَسْمَعُونَۜ وَالْمَوْتٰى يَبْعَثُهُمُ اللّٰهُ ثُمَّ اِلَيْهِ يُرْجَعُونَ', 'Ancak (samimiyetle) dinleyenler daveti kabul eder. Ölülere gelince, Allah onları diriltecek, sonra da O´na döndürülecekler.', 7, 131, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(826, 37, 6, 'En’âm', 'وَقَالُوا لَوْلَا نُزِّلَ عَلَيْهِ اٰيَةٌ مِنْ رَبِّه۪ۜ قُلْ اِنَّ اللّٰهَ قَادِرٌ عَلٰٓى اَنْ يُنَزِّلَ اٰيَةً وَلٰكِنَّ اَكْثَرَهُمْ لَا يَعْلَمُونَ', 'O´na Rabbinden bir mucize indirilseydi ya! dediler. De ki: Şüphesiz Allah mucize indirmeye kadirdir. Fakat onların çoğu bilmezler.', 7, 131, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(827, 38, 6, 'En’âm', 'وَمَا مِنْ دَٓابَّةٍ فِي الْاَرْضِ وَلَا طَٓائِرٍ يَط۪يرُ بِجَنَاحَيْهِ اِلَّٓا اُمَمٌ اَمْثَالُكُمْۜ مَا فَرَّطْنَا فِي الْكِتَابِ مِنْ شَيْءٍ ثُمَّ اِلٰى رَبِّهِمْ يُحْشَرُونَ', 'Yeryüzünde yürüyen hayvanlar ve (gökyüzünde) iki kanadıyla uçan kuşlardan ne varsa hepsi ancak sizin gibi topluluklardır. Biz o kitapta hiçbir şeyi eksik bırakmadık. Nihayet (hepsi) toplanıp Rablerinin huzuruna getirilecekler.', 7, 131, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(828, 39, 6, 'En’âm', 'وَالَّذ۪ينَ كَذَّبُوا بِاٰيَاتِنَا صُمٌّ وَبُكْمٌ فِي الظُّلُمَاتِۜ مَنْ يَشَأِ اللّٰهُ يُضْلِلْهُۜ وَمَنْ يَشَأْ يَجْعَلْهُ عَلٰى صِرَاطٍ مُسْتَق۪يمٍ', 'Âyetlerimizi yalanlayanlar karanlıklar içinde kalmış sağır ve dilsizlerdir. Allah kimi dilerse onu şaşırtır, dilediği kimseyi de doğru yola iletir.', 7, 131, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(829, 40, 6, 'En’âm', 'قُلْ اَرَاَيْتَكُمْ اِنْ اَتٰيكُمْ عَذَابُ اللّٰهِ اَوْ اَتَتْكُمُ السَّاعَةُ اَغَيْرَ اللّٰهِ تَدْعُونَۚ اِنْ كُنْتُمْ صَادِق۪ينَ', 'De ki: Ne dersiniz; size Allah´ın azabı gelse veya o kıyamet gelip çatıverse size, Allah´tan başkasına mı yalvarırsınız? Doğru sözlü iseniz (söyleyin bakalım)!', 7, 131, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(830, 41, 6, 'En’âm', 'بَلْ اِيَّاهُ تَدْعُونَ فَيَكْشِفُ مَا تَدْعُونَ اِلَيْهِ اِنْ شَٓاءَ وَتَنْسَوْنَ مَا تُشْرِكُونَ۟', 'Bilâkis yalnız Allah´a yalvarırsınız. O da (kaldırılması için) kendisine yalvardığınız belâyı dilerse kaldırır; ve siz ortak koştuğunuz şeyleri unutursunuz.', 7, 131, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(831, 42, 6, 'En’âm', 'وَلَقَدْ اَرْسَلْـنَٓا اِلٰٓى اُمَمٍ مِنْ قَبْلِكَ فَاَخَذْنَاهُمْ بِالْبَأْسَٓاءِ وَالضَّرَّٓاءِ لَعَلَّهُمْ يَتَضَرَّعُونَ', 'Andolsun ki, senden önceki ümmetlere de elçiler gönderdik. Ardından boyun eğsinler diye onları darlık ve hastalıklara uğrattık.', 7, 131, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(832, 43, 6, 'En’âm', 'فَلَوْلَٓا اِذْ جَٓاءَهُمْ بَأْسُنَا تَضَرَّعُوا وَلٰكِنْ قَسَتْ قُلُوبُهُمْ وَزَيَّنَ لَهُمُ الشَّيْطَانُ مَا كَانُوا يَعْمَلُونَ', 'Hiç olmazsa, onlara bu şekilde azabımız geldiği zaman boyun eğselerdi! Fakat kalpleri iyice katılaştı ve şeytan da onlara yaptıklarını câzip gösterdi.', 7, 131, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(833, 44, 6, 'En’âm', 'فَلَمَّا نَسُوا مَا ذُكِّرُوا بِه۪ فَتَحْنَا عَلَيْهِمْ اَبْوَابَ كُلِّ شَيْءٍۜ حَتّٰٓى اِذَا فَرِحُوا بِمَٓا اُو۫تُٓوا اَخَذْنَاهُمْ بَغْتَةً فَاِذَا هُمْ مُبْلِسُونَ', 'Kendilerine yapılan uyarıları unuttuklarında, (indirmiş olduğumuz sıkıntı ve musibetleri kaldırıp) üzerlerine her şeyin kapılarını açtık. Nihayet kendilerine verilenler yüzünden şımardıkları zaman onları ansızın yakaladık, birdenbire onlar bütün ümitlerini yitirdiler.', 7, 131, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(834, 45, 6, 'En’âm', 'فَقُطِـعَ دَابِرُ الْقَوْمِ الَّذ۪ينَ ظَلَمُواۜ وَالْحَمْدُ لِلّٰهِ رَبِّ الْعَالَم۪ينَ', 'Böylece zulmeden toplumun kökü kesildi. Hamd, âlemlerin Rabbi Allah´a mahsustur.', 7, 132, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(835, 46, 6, 'En’âm', 'قُلْ اَرَاَيْتُمْ اِنْ اَخَذَ اللّٰهُ سَمْعَكُمْ وَاَبْصَارَكُمْ وَخَتَمَ عَلٰى قُلُوبِكُمْ مَنْ اِلٰهٌ غَيْرُ اللّٰهِ يَأْت۪يكُمْ بِهِۜ اُنْظُرْ كَيْفَ نُصَرِّفُ الْاٰيَاتِ ثُمَّ هُمْ يَصْدِفُونَ', 'De ki: Ne dersiniz; eğer Allah kulaklarınızı sağır, gözlerinizi kör eder, kalplerinizi de mühürlerse bunları size Allah´tan başka hangi tanrı geri verebilir! Bak, delilleri nasıl açıklıyoruz. Onlar hâla yüz çeviriyorlar!', 7, 132, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(836, 47, 6, 'En’âm', 'قُلْ اَرَاَيْتَكُمْ اِنْ اَتٰيكُمْ عَذَابُ اللّٰهِ بَغْتَةً اَوْ جَهْرَةً هَلْ يُهْلَكُ اِلَّا الْقَوْمُ الظَّالِمُونَ', 'De ki: Söyler misiniz; size Allah´ın azabı ansızın veya açıkça gelirse, zalim toplumdan başkası mı helâk olur?', 7, 132, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(837, 48, 6, 'En’âm', 'وَمَا نُرْسِلُ الْمُرْسَل۪ينَ اِلَّا مُبَشِّر۪ينَ وَمُنْذِر۪ينَۚ فَمَنْ اٰمَنَ وَاَصْلَحَ فَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ', 'Biz, peygamberleri ancak müjdeleyiciler ve uyarıcılar olarak göndeririz. Kim iman eder ve kendini düzeltirse onlara korku yoktur. Onlar üzüntü de çekmeyecekler.', 7, 132, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(838, 49, 6, 'En’âm', 'وَالَّذ۪ينَ كَذَّبُوا بِاٰيَاتِنَا يَمَسُّهُمُ الْعَذَابُ بِمَا كَانُوا يَفْسُقُونَ', 'Âyetlerimizi yalanlayanlara gelince, yoldan çıkmalarından dolayı onlar azap çekeceklerdir.', 7, 132, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(839, 50, 6, 'En’âm', 'قُلْ لَٓا اَقُولُ لَكُمْ عِنْد۪ي خَزَٓائِنُ اللّٰهِ وَلَٓا اَعْلَمُ الْغَيْبَ وَلَٓا اَقُولُ لَكُمْ اِنّ۪ي مَلَكٌۚ اِنْ اَتَّبِعُ اِلَّا مَا يُوحٰٓى اِلَيَّۜ قُلْ هَلْ يَسْتَوِي الْاَعْمٰى وَالْبَص۪يرُۜ اَفَلَا تَتَفَكَّرُونَ۟', 'De ki: Ben size, Allah´ın hazineleri benim yanımdadır, demiyorum. Ben gaybı da bilmem. Size, ben bir meleğim de demiyorum. Ben, sadece bana vahyolunana uyarım. De ki: Kör ile gören hiç bir olur mu? Hiç düşünmez misiniz?', 7, 132, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(840, 51, 6, 'En’âm', 'وَاَنْذِرْ بِهِ الَّذ۪ينَ يَخَافُونَ اَنْ يُحْشَرُٓوا اِلٰى رَبِّهِمْ لَيْسَ لَهُمْ مِنْ دُونِه۪ وَلِيٌّ وَلَا شَف۪يعٌ لَعَلَّهُمْ يَتَّقُونَ', 'Rablerinin huzurunda toplanacaklarından korkanları onunla (Kur´an ile) uyar. Onlar için Rablerinden başka ne bir dost, ne de bir aracı vardır; belki sakınırlar.', 7, 132, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(841, 52, 6, 'En’âm', 'وَلَا تَطْرُدِ الَّذ۪ينَ يَدْعُونَ رَبَّهُمْ بِالْغَدٰوةِ وَالْعَشِيِّ يُر۪يدُونَ وَجْهَهُۜ مَا عَلَيْكَ مِنْ حِسَابِهِمْ مِنْ شَيْءٍ وَمَا مِنْ حِسَابِكَ عَلَيْهِمْ مِنْ شَيْءٍ فَتَطْرُدَهُمْ فَتَكُونَ مِنَ الظَّالِم۪ينَ', 'Rablerinin rızasını isteyerek sabah akşam O´na yalvaranları kovma! Onların hesabından sana bir sorumluluk; senin hesabından da onlara herhangi bir sorumluluk yoktur ki bunları kovup da zalimlerden olasın!', 7, 132, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(842, 53, 6, 'En’âm', 'وَكَذٰلِكَ فَتَنَّا بَعْضَهُمْ بِبَعْضٍ لِيَقُولُٓوا اَهٰٓؤُ۬لَٓاءِ مَنَّ اللّٰهُ عَلَيْهِمْ مِنْ بَيْنِنَاۜ اَلَيْسَ اللّٰهُ بِاَعْلَمَ بِالشَّاكِر۪ينَ', '«Aramızdan Allah´ın kendilerine lütuf ve ihsanda bulunduğu kimseler de bunlar mı!» demeleri için onların bir kısmını diğerleri ile işte böyle imtihan ettik. Allah şükredenleri daha iyi bilmez mi?', 7, 133, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55);
INSERT INTO `Ayetler` (`id`, `AyetNo`, `SureNo`, `SureAdi`, `Metin`, `Mana`, `Cuzu`, `Sayfasi`, `AyetSayisi`, `HarfSayisi`, `AdAnlami`, `IndigiYer`, `InisSirasi`) VALUES
(843, 54, 6, 'En’âm', 'وَاِذَا جَٓاءَكَ الَّذ۪ينَ يُؤْمِنُونَ بِاٰيَاتِنَا فَقُلْ سَلَامٌ عَلَيْكُمْ كَتَبَ رَبُّكُمْ عَلٰى نَفْسِهِ الرَّحْمَةَۙ اَنَّهُ مَنْ عَمِلَ مِنْكُمْ سُٓوءاً بِجَهَالَةٍ ثُمَّ تَابَ مِنْ بَعْدِه۪ وَاَصْلَحَ فَاَنَّهُ غَفُورٌ رَح۪يمٌ', 'Âyetlerimize inananlar sana geldiğinde onlara de ki: Selâm size! Rabbiniz merhamet etmeyi kendisine yazdı. Gerçek şu ki: Sizden kim, bilmeyerek bir kötülük yapar, sonra ardından tevbe edip de kendini ıslah ederse, bilsin ki Allah çok bağışlayan, çok esirgeyendir.', 7, 133, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(844, 55, 6, 'En’âm', 'وَكَذٰلِكَ نُفَصِّلُ الْاٰيَاتِ وَلِتَسْتَب۪ينَ سَب۪يلُ الْمُجْرِم۪ينَ۟', 'Böylece suçluların yolu belli olsun diye âyetleri iyice açıklıyoruz.', 7, 133, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(845, 56, 6, 'En’âm', 'قُلْ اِنّ۪ي نُه۪يتُ اَنْ اَعْبُدَ الَّذ۪ينَ تَدْعُونَ مِنْ دُونِ اللّٰهِۜ قُلْ لَٓا اَتَّبِـعُ اَهْوَٓاءَكُمْۙ قَدْ ضَلَلْتُ اِذاً وَمَٓا اَنَا۬ مِنَ الْمُهْتَد۪ينَ', 'De ki: Allah´ın dışında taptığınız şeylere tapmak bana yasak edildi. De ki: Ben sizin arzularınıza uymam, aksi halde sapıtırım da hidayete erenlerden olmam.', 7, 133, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(846, 57, 6, 'En’âm', 'قُلْ اِنّ۪ي عَلٰى بَيِّنَةٍ مِنْ رَبّ۪ي وَكَذَّبْتُمْ بِه۪ۜ مَا عِنْد۪ي مَا تَسْتَعْجِلُونَ بِه۪ۜ اِنِ الْحُكْمُ اِلَّا لِلّٰهِۜ يَقُصُّ الْحَقَّ وَهُوَ خَيْرُ الْفَاصِل۪ينَ', 'De ki: Şüphesiz ben Rabbimden gelen apaçık bir delile dayanıyorum. Siz ise onu yalanladınız. Çabucak gelmesini istediğiniz (azap) benim yanımda değildir. Hüküm ancak Allah´ındır. O hakkı anlatır ve O, doğru hüküm verenlerin en hayırlısıdır.', 7, 133, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(847, 58, 6, 'En’âm', 'قُلْ لَوْ اَنَّ عِنْد۪ي مَا تَسْتَعْجِلُونَ بِه۪ لَقُضِيَ الْاَمْرُ بَيْن۪ي وَبَيْنَكُمْۜ وَاللّٰهُ اَعْلَمُ بِالظَّالِم۪ينَ', 'De ki: Acele istediğiniz şey benim elimde olsaydı, elbette benimle sizin aranızda iş bitirilmişti. Allah zalimleri daha iyi bilir.', 7, 133, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(848, 59, 6, 'En’âm', 'وَعِنْدَهُ مَفَاتِـحُ الْغَيْبِ لَا يَعْلَمُهَٓا اِلَّا هُوَۜ وَيَعْلَمُ مَا فِي الْبَرِّ وَالْبَحْرِۜ وَمَا تَسْقُطُ مِنْ وَرَقَةٍ اِلَّا يَعْلَمُهَا وَلَا حَبَّةٍ ف۪ي ظُلُمَاتِ الْاَرْضِ وَلَا رَطْبٍ وَلَا يَابِسٍ اِلَّا ف۪ي كِتَابٍ مُب۪ينٍ', 'Gaybın anahtarları Allah´ın yanındadır; onları O´ndan başkası bilmez. O, karada ve denizde ne varsa bilir; O´nun ilmi dışında bir yaprak bile düşmez. O yerin karanlıkları içindeki tek bir taneyi dahi bilir. Yaş ve kuru ne varsa hepsi apaçık bir kitaptadır.', 7, 133, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(849, 60, 6, 'En’âm', 'وَهُوَ الَّذ۪ي يَتَوَفّٰيكُمْ بِالَّيْلِ وَيَعْلَمُ مَا جَرَحْتُمْ بِالنَّهَارِ ثُمَّ يَبْعَثُـكُمْ ف۪يهِ لِيُقْضٰٓى اَجَلٌ مُسَمًّىۚ ثُمَّ اِلَيْهِ مَرْجِعُكُمْ ثُمَّ يُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُونَ۟', 'Geceleyin sizi öldüren (öldürür gibi uyutan), gündüzün de ne işlediğinizi bilen; sonra belirlenmiş ecel tamamlansın diye gündüzün sizi dirilten (uyandıran) O´dur. Sonra dönüşünüz yine O´nadır. Sonunda O, yaptıklarınızı size haber verecektir.', 7, 134, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(850, 61, 6, 'En’âm', 'وَهُوَ الْقَاهِرُ فَوْقَ عِبَادِه۪ وَيُرْسِلُ عَلَيْكُمْ حَفَظَةًۜ حَتّٰٓى اِذَا جَٓاءَ اَحَدَكُمُ الْمَوْتُ تَوَفَّتْهُ رُسُلُنَا وَهُمْ لَا يُفَرِّطُونَ', 'O, kullarının üstünde yegâne kudret ve tasarruf sahibidir. Size koruyucular gönderir. Nihayet birinize ölüm geldi mi elçilerimiz (görevli melekler) onun canını alırlar. Onlar vazifede kusur etmezler.', 7, 134, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(851, 62, 6, 'En’âm', 'ثُمَّ رُدُّٓوا اِلَى اللّٰهِ مَوْلٰيهُمُ الْحَقِّۜ اَلَا لَهُ الْحُكْمُ وَهُوَ اَسْرَعُ الْحَاسِب۪ينَ', 'Sonra insanlar gerçek sahipleri olan Allah´a döndürülürler. Bilesiniz ki hüküm yalnız O´nundur ve O hesap görenlerin en çabuğudur.', 7, 134, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(852, 63, 6, 'En’âm', 'قُلْ مَنْ يُنَجّ۪يكُمْ مِنْ ظُلُمَاتِ الْبَرِّ وَالْبَحْرِ تَدْعُونَهُ تَضَرُّعاً وَخُفْيَةًۚ لَئِنْ اَنْجٰينَا مِنْ هٰذِه۪ لَنَكُونَنَّ مِنَ الشَّاكِر۪ينَ', 'De ki: Karanın ve denizin karanlıklarından (tehlikelerinden) sizi kim kurtarır ki? (O zaman) O´na gizli gizli yalvararak «Eğer bizi bundan kurtarırsan andolsun şükredenlerden olacağız» diye dua edersiniz.', 7, 134, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(853, 64, 6, 'En’âm', 'قُلِ اللّٰهُ يُنَجّ۪يكُمْ مِنْهَا وَمِنْ كُلِّ كَرْبٍ ثُمَّ اَنْتُمْ تُشْرِكُونَ', 'De ki: Ondan ve bütün sıkıntılardan sizi Allah kurtarır. Sonra siz yine O´na ortak koşarsınız.', 7, 134, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(854, 65, 6, 'En’âm', 'قُلْ هُوَ الْقَادِرُ عَلٰٓى اَنْ يَبْعَثَ عَلَيْكُمْ عَذَاباً مِنْ فَوْقِكُمْ اَوْ مِنْ تَحْتِ اَرْجُلِكُمْ اَوْ يَلْبِسَكُمْ شِيَعاً وَيُذ۪يقَ بَعْضَكُمْ بَأْسَ بَعْضٍۜ اُنْظُرْ كَيْفَ نُصَرِّفُ الْاٰيَاتِ لَعَلَّهُمْ يَفْقَهُونَ', 'De ki: «Allah´ın size üstünüzden (gökten) veya ayaklarınızın altından (yerden) bir azap göndermeğe ya da birbirinize düşürüp kiminize kiminizin hıncını tattırmaya gücü yeter.» Bak, anlasınlar diye âyetlerimizi nasıl açıklıyoruz!', 7, 134, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(855, 66, 6, 'En’âm', 'وَكَذَّبَ بِه۪ قَوْمُكَ وَهُوَ الْحَقُّۜ قُلْ لَسْتُ عَلَيْكُمْ بِوَك۪يلٍۜ', 'Kur´an hak olduğu halde kavmin onu yalanladı. De ki: Ben size vekil (kefil) değilim.', 7, 134, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(856, 67, 6, 'En’âm', 'لِكُلِّ نَبَأٍ مُسْتَقَرٌّۘ وَسَوْفَ تَعْلَمُونَ', 'Her haberin gerçekleşeceği bir zaman vardır. Yakında siz de gerçeği bileceksiniz.', 7, 134, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(857, 68, 6, 'En’âm', 'وَاِذَا رَاَيْتَ الَّذ۪ينَ يَخُوضُونَ ف۪ٓي اٰيَاتِنَا فَاَعْرِضْ عَنْهُمْ حَتّٰى يَخُوضُوا ف۪ي حَد۪يثٍ غَيْرِه۪ۜ وَاِمَّا يُنْسِيَنَّكَ الشَّيْطَانُ فَلَا تَقْعُدْ بَعْدَ الذِّكْرٰى مَعَ الْقَوْمِ الظَّالِم۪ينَ', 'Âyetlerimiz hakkında ileri geri konuşmaya dalanları gördüğünde, onlar başka bir söze geçinceye kadar onlardan uzak dur. Eğer şeytan sana unutturursa, hatırladıktan sonra artık o zalimler topluluğu ile oturma.', 7, 134, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(858, 69, 6, 'En’âm', 'وَمَا عَلَى الَّذ۪ينَ يَتَّقُونَ مِنْ حِسَابِهِمْ مِنْ شَيْءٍ وَلٰكِنْ ذِكْرٰى لَعَلَّهُمْ يَتَّقُونَ', 'Takvâ sahiplerine, inanmayanların hesabından herhangi bir sorumluluk yoktur. Fakat belki korunurlar diye hatırlatmak gerekir.', 7, 135, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(859, 70, 6, 'En’âm', 'وَذَرِ الَّذ۪ينَ اتَّخَذُوا د۪ينَهُمْ لَعِباً وَلَهْواً وَغَرَّتْهُمُ الْحَيٰوةُ الدُّنْيَا وَذَكِّرْ بِه۪ٓ اَنْ تُبْسَلَ نَفْسٌ بِمَا كَسَبَتْۗ لَيْسَ لَهَا مِنْ دُونِ اللّٰهِ وَلِيٌّ وَلَا شَف۪يعٌۚ وَاِنْ تَعْدِلْ كُلَّ عَدْلٍ لَا يُؤْخَذْ مِنْهَاۜ اُو۬لٰٓئِكَ الَّذ۪ينَ اُبْسِلُوا بِمَا كَسَبُواۚ لَهُمْ شَرَابٌ مِنْ حَم۪يمٍ وَعَذَابٌ اَل۪يمٌ بِمَا كَانُوا يَكْفُرُونَ۟', 'Dinlerini bir oyuncak ve bir eğlence edinen ve dünya hayatının aldattığı kimseleri (bir tarafa) bırak! Kazandıkları sebebiyle hiçbir nefsin felâkete dûçar olmaması için Kur´an ile nasihat et. O nefis için Allah´tan başka ne dost vardır, ne de şefaatçı. O, bütün varını fidye olarak verse, yine de ondan kabul edilmez. Onlar kazandıkları (günahlar) yüzünden helâke sürüklenmiş kimselerdir. İnkâr ettiklerinden dolayı onlar için kaynar sudan ibaret bir içecek ve elem verici bir azap vardır.', 7, 135, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(860, 71, 6, 'En’âm', 'قُلْ اَنَدْعُوا مِنْ دُونِ اللّٰهِ مَا لَا يَنْفَعُنَا وَلَا يَضُرُّنَا وَنُرَدُّ عَلٰٓى اَعْقَابِنَا بَعْدَ اِذْ هَدٰينَا اللّٰهُ كَالَّذِي اسْتَهْوَتْهُ الشَّيَاط۪ينُ فِي الْاَرْضِ حَيْرَانَۖ لَهُٓ اَصْحَابٌ يَدْعُونَهُٓ اِلَى الْهُدَى ائْتِنَاۜ قُلْ اِنَّ هُدَى اللّٰهِ هُوَ الْهُدٰىۜ وَاُمِرْنَا لِنُسْلِمَ لِرَبِّ الْعَالَم۪ينَۙ', 'De ki: Allah´ı bırakıp da bize fayda veya zarar veremeyecek olan şeylere mi tapalım? Allah bizi doğru yola ilettikten sonra şeytanların saptırıp şaşkın olarak çöle düşürmek istedikleri, arkadaşlarının ise: «Bize gel!» diye doğru yola çağırdıkları şaşkın kimse gibi gerisin geri (inkârcılığa) mı döndürüleceğiz? De ki: Allah´ın hidayeti doğru yolun ta kendisidir. Bize âlemlerin Rabbine teslim olmamız emredilmiştir.', 7, 135, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(861, 72, 6, 'En’âm', 'وَاَنْ اَق۪يمُوا الصَّلٰوةَ وَاتَّقُوهُۜ وَهُوَ الَّـذ۪ٓي اِلَيْهِ تُحْشَرُونَ', '«Namazı dosdoğru kılın ve Allah´tan korkun» (diye de emredildik). O, huzuruna varıp toplanacağınız Allah´tır.', 7, 135, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(862, 73, 6, 'En’âm', 'وَهُوَ الَّذ۪ي خَلَقَ السَّمٰوَاتِ وَالْاَرْضَ بِالْحَقِّۜ وَيَوْمَ يَقُولُ كُنْ فَيَكُونُۜ قَوْلُهُ الْحَقُّۜ وَلَهُ الْمُلْكُ يَوْمَ يُنْفَخُ فِي الصُّورِۜ عَالِمُ الْغَيْبِ وَالشَّهَادَةِۜ وَهُوَ الْحَك۪يمُ الْخَب۪يرُ', 'O, gökleri ve yeri hak (ve hikmet) ile yaratandır. «Ol!» dediği gün herşey oluverir. O´nun sözü gerçektir. Sûr´a üflendiği gün de hükümranlık O´nundur. Gizliyi ve açığı bilendir ve O, hikmet sahibidir, her şeyden haberdardır.', 7, 135, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(863, 74, 6, 'En’âm', 'وَاِذْ قَالَ اِبْرٰه۪يمُ لِاَب۪يهِ اٰزَرَ اَتَـتَّخِذُ اَصْنَاماً اٰلِهَةًۚ اِنّ۪ٓي اَرٰيكَ وَقَوْمَكَ ف۪ي ضَلَالٍ مُب۪ينٍ', 'İbrahim, babası Âzer´e: Birtakım putları tanrılar mı ediniyorsun? Doğrusu ben seni de kavmini de apaçık bir sapıklık içinde görüyorum, demişti.', 7, 136, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(864, 75, 6, 'En’âm', 'وَكَذٰلِكَ نُر۪ٓي اِبْرٰه۪يمَ مَلَكُوتَ السَّمٰوَاتِ وَالْاَرْضِ وَلِيَكُونَ مِنَ الْمُوقِن۪ينَ', 'Böylece biz, kesin iman edenlerden olması için İbrahim´e göklerin ve yerin melekûtunu gösteriyorduk.', 7, 136, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(865, 76, 6, 'En’âm', 'فَلَمَّا جَنَّ عَلَيْهِ الَّيْلُ رَاٰ كَوْكَباًۚ قَالَ هٰذَا رَبّ۪يۚ فَلَمَّٓا اَفَلَ قَالَ لَٓا اُحِبُّ الْاٰفِل۪ينَ', 'Gecenin karanlığı onu kaplayınca bir yıldız gördü, Rabbim budur, dedi. Yıldız batınca, batanları sevmem, dedi.', 7, 136, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(866, 77, 6, 'En’âm', 'فَلَمَّا رَاَ الْقَمَرَ بَازِغاً قَالَ هٰذَا رَبّ۪يۚ فَلَمَّٓا اَفَلَ قَالَ لَئِنْ لَمْ يَهْدِن۪ي رَبّ۪ي لَاَكُونَنَّ مِنَ الْقَوْمِ الضَّٓالّ۪ينَ', 'Ay´ı doğarken görünce, Rabbim budur, dedi. O da batınca, Rabbim bana doğru yolu göstermezse elbette yoldan sapan topluluklardan olurum, dedi.', 7, 136, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(867, 78, 6, 'En’âm', 'فَلَمَّا رَاَ الشَّمْسَ بَازِغَةً قَالَ هٰذَا رَبّ۪ي هٰذَٓا اَكْبَرُۚ فَلَمَّٓا اَفَلَتْ قَالَ يَا قَوْمِ اِنّ۪ي بَر۪ٓيءٌ مِمَّا تُشْرِكُونَ', 'Güneşi doğarken görünce de, Rabbim budur, zira bu daha büyük, dedi. O da batınca, dedi ki: Ey kavmim! Ben sizin (Allah´a) ortak koştuğunuz şeylerden uzağım.', 7, 136, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(868, 79, 6, 'En’âm', 'اِنّ۪ي وَجَّهْتُ وَجْهِيَ لِلَّذ۪ي فَطَرَ السَّمٰوَاتِ وَالْاَرْضَ حَن۪يفاً وَمَٓا اَنَا۬ مِنَ الْمُشْرِك۪ينَۚ', 'Ben hanîf olarak, yüzümü gökleri ve yeri yoktan yaratan Allah´a çevirdim ve ben müşriklerden değilim.', 7, 136, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(869, 80, 6, 'En’âm', 'وَحَٓاجَّهُ قَوْمُهُۜ قَالَ اَتُحَٓاجُّٓونّ۪ي فِي اللّٰهِ وَقَدْ هَدٰينِۜ وَلَٓا اَخَافُ مَا تُشْرِكُونَ بِه۪ٓ اِلَّٓا اَنْ يَشَٓاءَ رَبّ۪ي شَيْـٔاًۜ وَسِعَ رَبّ۪ي كُلَّ شَيْءٍ عِلْماًۜ اَفَلَا تَتَذَكَّرُونَ', 'Kavmi onunla tartışmaya girişti. Onlara dedi ki: Beni doğru yola iletmişken, Allah hakkında benimle tartışıyor musunuz? Ben sizin O´na ortak koştuğunuz şeylerden korkmam. Ancak, Rabbim´in bir şey dilemesi hariç. Rabbimin ilmi herşeyi kuşatmıştır. Hâla ibret almıyor musunuz?', 7, 136, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(870, 81, 6, 'En’âm', 'وَكَيْفَ اَخَافُ مَٓا اَشْرَكْتُمْ وَلَا تَخَافُونَ اَنَّكُمْ اَشْرَكْتُمْ بِاللّٰهِ مَا لَمْ يُنَزِّلْ بِه۪ عَلَيْكُمْ سُلْطَاناًۜ فَاَيُّ الْفَر۪يقَيْنِ اَحَقُّ بِالْاَمْنِۚ اِنْ كُنْتُمْ تَعْلَمُونَۢ', 'Siz, Allah´ın size haklarında hiçbir hüküm indirmediği şeyleri O´na ortak koşmaktan korkmazken, ben sizin ortak koştuğunuz şeylerden nasıl korkarım! Şimdi biliyorsanız (söyleyin), iki guruptan hangisi güvende olmaya daha lâyıktır?', 7, 136, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(871, 82, 6, 'En’âm', 'اَلَّذ۪ينَ اٰمَنُوا وَلَمْ يَلْبِسُٓوا ا۪يمَانَهُمْ بِظُلْمٍ اُو۬لٰٓئِكَ لَهُمُ الْاَمْنُ وَهُمْ مُهْتَدُونَ۟', 'İnanıp da imanlarına herhangi bir haksızlık bulaştırmayanlar var ya, işte güven onlarındır ve onlar doğru yolu bulanlardır.', 7, 137, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(872, 83, 6, 'En’âm', 'وَتِلْكَ حُجَّتُنَٓا اٰتَيْنَاهَٓا اِبْرٰه۪يمَ عَلٰى قَوْمِه۪ۜ نَرْفَعُ دَرَجَاتٍ مَنْ نَشَٓاءُۜ اِنَّ رَبَّكَ حَك۪يمٌ عَل۪يمٌ', 'İşte bu, kavmine karşı İbrahim´e verdiğimiz delillerimizdir. Biz dilediğimiz kimselerin derecelerini yükseltiriz. Şüphesiz ki senin Rabbin hikmet sahibidir, hakkıyle bilendir.', 7, 137, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(873, 84, 6, 'En’âm', 'وَوَهَبْنَا لَهُٓ اِسْحٰقَ وَيَعْقُوبَۜ كُلاًّ هَدَيْنَاۚ وَنُوحاً هَدَيْنَا مِنْ قَبْلُ وَمِنْ ذُرِّيَّتِه۪ دَاوُ۫دَ وَسُلَيْمٰنَ وَاَيُّوبَ وَيُوسُفَ وَمُوسٰى وَهٰرُونَۜ وَكَذٰلِكَ نَجْزِي الْمُحْسِن۪ينَۙ', 'Biz O´na İshak ve (İshak´ın oğlu) Yakub´u da armağan ettik; hepsini de doğru yola ilettik. Daha önce de Nuh´u ve O´nun soyundan Davud´u, Süleyman´ı, Eyyub´u, Yusuf´u, Musa´yı ve Harun´u doğru yola iletmiştik; Biz iyi davrananları işte böyle mükâfatlandırırız.', 7, 137, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(874, 85, 6, 'En’âm', 'وَزَكَرِيَّا وَيَحْيٰى وَع۪يسٰى وَاِلْيَاسَۜ كُلٌّ مِنَ الصَّالِح۪ينَۙ', 'Zekeriyya, Yahya, İsa ve İlyas´ı da (doğru yola iletmiştik). Hepsi de iyilerden idi.', 7, 137, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(875, 86, 6, 'En’âm', 'وَاِسْمٰع۪يلَ وَالْيَسَعَ وَيُونُسَ وَلُوطاًۜ وَكُلاًّ فَضَّلْنَا عَلَى الْعَالَم۪ينَۙ', 'İsmail, Elyesa´, Yunus ve Lût´u da (hidayete erdirdik). Hepsini âlemlere üstün kıldık.', 7, 137, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(876, 87, 6, 'En’âm', 'وَمِنْ اٰبَٓائِهِمْ وَذُرِّيَّاتِهِمْ وَاِخْوَانِهِمْۚ وَاجْتَبَيْنَاهُمْ وَهَدَيْنَاهُمْ اِلٰى صِرَاطٍ مُسْتَق۪يمٍ', 'Onların babalarından, çocuklarından ve kardeşlerinden bazılarına da (üstün meziyetler verdik). Onları seçkin kıldık ve doğru yola ilettik.', 7, 137, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(877, 88, 6, 'En’âm', 'ذٰلِكَ هُدَى اللّٰهِ يَهْد۪ي بِه۪ مَنْ يَشَٓاءُ مِنْ عِبَادِه۪ۜ وَلَوْ اَشْرَكُوا لَحَبِطَ عَنْهُمْ مَا كَانُوا يَعْمَلُونَ', 'İşte bu, Allah´ın hidayetidir, kullarından dilediğini ona iletir. Eğer onlar da Allah´a ortak koşsalardı yapmakta oldukları amelleri elbette boşa giderdi.', 7, 137, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(878, 89, 6, 'En’âm', 'اُو۬لٰٓئِكَ الَّذ۪ينَ اٰتَيْنَاهُمُ الْكِتَابَ وَالْحُكْمَ وَالنُّبُوَّةَۚ فَاِنْ يَكْفُرْ بِهَا هٰٓؤُ۬لَٓاءِ فَقَدْ وَكَّلْنَا بِهَا قَوْماً لَيْسُوا بِهَا بِكَافِر۪ينَ', 'İşte onlar, kendilerine kitap, hikmet ve peygamberlik verdiğimiz kimselerdir. Eğer onlar (kâfirler) bunları inkâr ederse şüphesiz yerlerine bunları inkâr etmeyecek bir toplum getiririz.', 7, 137, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(879, 90, 6, 'En’âm', 'اُو۬لٰٓئِكَ الَّذ۪ينَ هَدَى اللّٰهُ فَبِهُدٰيهُمُ اقْتَدِهْۜ قُلْ لَٓا اَسْـَٔلُكُمْ عَلَيْهِ اَجْراًۜ اِنْ هُوَ اِلَّا ذِكْرٰى لِلْعَالَم۪ينَ۟', 'İşte o peygamberler Allah´ın hidayet ettiği kimselerdir. Sen de onların yoluna uy. De ki: Ben buna (peygamberlik görevime) karşılık sizden bir ücret istemiyorum. Bu (Kur´an) âlemler için ancak bir öğüttür.', 7, 137, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(880, 91, 6, 'En’âm', 'وَمَا قَدَرُوا اللّٰهَ حَقَّ قَدْرِه۪ٓ اِذْ قَالُوا مَٓا اَنْزَلَ اللّٰهُ عَلٰى بَشَرٍ مِنْ شَيْءٍۜ قُلْ مَنْ اَنْزَلَ الْكِتَابَ الَّذ۪ي جَٓاءَ بِه۪ مُوسٰى نُوراً وَهُدًى لِلنَّاسِ تَجْعَلُونَهُ قَرَاط۪يسَ تُبْدُونَهَا وَتُخْفُونَ كَث۪يراًۚ وَعُلِّمْتُمْ مَا لَمْ تَعْلَمُٓوا اَنْتُمْ وَلَٓا اٰبَٓاؤُ۬كُمْۜ قُلِ اللّٰهُۙ ثُمَّ ذَرْهُمْ ف۪ي خَوْضِهِمْ يَلْعَبُونَ', '(Yahudiler) Allah´ı gereği gibi tanımadılar. Çünkü «Allah hiçbir beşere bir şey indirmedi» dediler. De ki: Öyle ise Musa´nın insanlara bir nûr ve hidayet olarak getirdiği Kitab´ı kim indirdi? Siz onu kâğıtlara yazıp (istediğinizi) açıklıyor, çoğunu da gizliyorsunuz. Sizin de atalarınızın da bilemediği şeyler (Kur´an´da) size öğretilmiştir. (Resûlüm) sen «Allah» de, sonra onları bırak, daldıkları bataklıkta oynayadursunlar!', 7, 138, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(881, 92, 6, 'En’âm', 'وَهٰذَا كِتَابٌ اَنْزَلْنَاهُ مُبَارَكٌ مُصَدِّقُ الَّذ۪ي بَيْنَ يَدَيْهِ وَلِتُنْذِرَ اُمَّ الْقُرٰى وَمَنْ حَوْلَهَاۜ وَالَّذ۪ينَ يُؤْمِنُونَ بِالْاٰخِرَةِ يُؤْمِنُونَ بِه۪ وَهُمْ عَلٰى صَلَاتِهِمْ يُحَافِظُونَ', 'Bu (Kur´an), Ümmü´l-kurâ (Mekke) ve çevresindekileri uyarman için sana indirdiğimiz ve kendinden öncekileri doğrulayıcı mübarek bir kitaptır. Âhirete inananlar buna da inanırlar ve onlar namazlarını hakkıyla kılmaya devam ederler.', 7, 138, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(882, 93, 6, 'En’âm', 'وَمَنْ اَظْلَمُ مِمَّنِ افْتَرٰى عَلَى اللّٰهِ كَذِباً اَوْ قَالَ اُو۫حِيَ اِلَيَّ وَلَمْ يُوحَ اِلَيْهِ شَيْءٌ وَمَنْ قَالَ سَاُنْزِلُ مِثْلَ مَٓا اَنْزَلَ اللّٰهُۜ وَلَوْ تَرٰٓى اِذِ الظَّالِمُونَ ف۪ي غَمَرَاتِ الْمَوْتِ وَالْمَلٰٓئِكَةُ بَاسِطُٓوا اَيْد۪يهِمْۚ اَخْرِجُٓوا اَنْفُسَكُمْۜ اَلْيَوْمَ تُجْزَوْنَ عَذَابَ الْهُونِ بِمَا كُنْتُمْ تَقُولُونَ عَلَى اللّٰهِ غَيْرَ الْحَقِّ وَكُنْتُمْ عَنْ اٰيَاتِه۪ تَسْتَكْبِرُونَ', 'Allah´a karşı yalan uydurandan yahut kendisine hiçbir şey vahyedilmemişken «Bana da vahyolundu» diyenden ve «Ben de Allah´ın indirdiği âyetlerin benzerini indireceğim» diyenden daha zalim kim vardır! O zalimler, ölümün (boğucu) dalgaları içinde, melekler de pençelerini uzatmış, onlara: «Haydi canlarınızı kurtarın! Allah´a karşı gerçek olmayanı söylemenizden ve O´nun âyetlerine karşı kibirlilik taslamış olmanızdan ötürü, bugün alçaklık azabı ile cezalandırılacaksınız!» derken onların halini bir görsen!', 7, 138, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(883, 94, 6, 'En’âm', 'وَلَقَدْ جِئْتُمُونَا فُرَادٰى كَمَا خَلَقْنَاكُمْ اَوَّلَ مَرَّةٍ وَتَرَكْتُمْ مَا خَوَّلْنَاكُمْ وَرَٓاءَ ظُهُورِكُمْۚ وَمَا نَرٰى مَعَكُمْ شُفَعَٓاءَكُمُ الَّذ۪ينَ زَعَمْتُمْ اَنَّهُمْ ف۪يكُمْ شُرَكٰٓؤُ۬اۜ لَقَدْ تَقَطَّعَ بَيْنَكُمْ وَضَلَّ عَنْكُمْ مَا كُنْتُمْ تَزْعُمُونَ۟', 'Andolsun ki, sizi ilk defa yarattığımız gibi teker teker bize geleceksiniz ve (dünyada) size verdiğimiz şeyleri arkanızda bırakacaksınız. Yaratılışınızda ortaklarımız sandığınız şefaatçılarınızı da yanınızda göremeyeceğiz. Andolsun, aranız açılmış ve (tanrı) sandığınız şeyler sizden kaybolup gitmiştir.', 7, 138, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(884, 95, 6, 'En’âm', 'اِنَّ اللّٰهَ فَالِقُ الْحَبِّ وَالنَّوٰىۜ يُخْرِجُ الْحَيَّ مِنَ الْمَيِّتِ وَمُخْرِجُ الْمَيِّتِ مِنَ الْحَيِّۜ ذٰلِكُمُ اللّٰهُ فَاَنّٰى تُؤْفَكُونَ', 'Şüphesiz Allah, tohumu ve çekirdeği çatlatandır, ölüden diriyi çıkaran, diriden de ölüyü çıkarandır. İşte Allah budur. O halde (haktan) nasıl dönersiniz!', 7, 139, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(885, 96, 6, 'En’âm', 'فَالِقُ الْاِصْبَاحِۚ وَجَعَلَ الَّيْلَ سَكَناً وَالشَّمْسَ وَالْقَمَرَ حُسْبَاناًۜ ذٰلِكَ تَقْد۪يرُ الْعَز۪يزِ الْعَل۪يمِ', 'O, sabahı aydınlatandır. O, geceyi dinlenme zamanı, güneş ve ayı (vakitlerin tayini için) birer hesap ölçüsü kılmıştır. İşte bu, azîz olan (ve her şeyi) pek iyi bilen Allah´ın takdiridir.', 7, 139, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(886, 97, 6, 'En’âm', 'وَهُوَ الَّذ۪ي جَعَلَ لَكُمُ النُّجُومَ لِتَهْتَدُوا بِهَا ف۪ي ظُلُمَاتِ الْبَرِّ وَالْبَحْرِۜ قَدْ فَصَّلْنَا الْاٰيَاتِ لِقَوْمٍ يَعْلَمُونَ', 'O, kara ve denizin karanlıklarında kendileri ile yol bulasınız diye sizin için yıldızları yaratandır. Gerçekten biz, bilen bir toplum için âyetleri geniş geniş açıkladık.', 7, 139, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(887, 98, 6, 'En’âm', 'وَهُوَ الَّـذ۪ٓي اَنْشَاَكُمْ مِنْ نَفْسٍ وَاحِدَةٍ فَمُسْتَقَرٌّ وَمُسْتَوْدَعٌۜ قَدْ فَصَّلْنَا الْاٰيَاتِ لِقَوْمٍ يَفْقَـهُونَ', 'O, sizi bir tek nefisten (Âdem´den) yaratandır. (Sizin için) bir kalma yeri, bir de emanet olarak konulacağınız yer vardır. Anlayan bir toplum için âyetleri ayrıntılı bir şekilde açıkladık.', 7, 139, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(888, 99, 6, 'En’âm', 'وَهُوَ الَّـذ۪ٓي اَنْزَلَ مِنَ السَّمَٓاءِ مَٓاءًۚ فَاَخْرَجْنَا بِه۪ نَبَاتَ كُلِّ شَيْءٍ فَاَخْرَجْنَا مِنْهُ خَضِراً نُخْرِجُ مِنْهُ حَباًّ مُتَرَاكِباًۚ وَمِنَ النَّخْلِ مِنْ طَلْعِهَا قِنْوَانٌ دَانِيَةٌ وَجَنَّاتٍ مِنْ اَعْنَابٍ وَالزَّيْتُونَ وَالرُّمَّانَ مُشْتَبِهاً وَغَيْرَ مُتَشَابِهٍۜ اُنْظُـرُٓوا اِلٰى ثَمَرِه۪ٓ اِذَٓا اَثْمَرَ وَيَنْعِه۪ۜ اِنَّ ف۪ي ذٰلِكُمْ لَاٰيَاتٍ لِقَوْمٍ يُؤْمِنُونَ', 'O, gökten su indirendir. İşte biz her çeşit bitkiyi onunla bitirdik. O bitkiden de kendisinde üstüste binmiş taneler bitireceğimiz bir yeşillik; hurmanın tomurcuğundan sarkan salkımlar; üzüm bağları; bir kısmı birbirine benzeyen, bir kısmı da benzemeyen zeytin ve nar bahçeleri meydana getirdik. Meyve verirken ve olgunlaştığı zaman her birinin meyvesine bakın! Kuşkusuz bütün bunlarda inanan bir toplum için ibretler vardır.', 7, 139, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(889, 100, 6, 'En’âm', 'وَجَعَلُوا لِلّٰهِ شُرَكَٓاءَ الْجِنَّ وَخَلَقَهُمْ وَخَرَقُوا لَهُ بَن۪ينَ وَبَنَاتٍ بِغَيْرِ عِلْمٍۜ سُبْحَانَهُ وَتَعَالٰى عَمَّا يَصِفُونَ۟', 'Cinleri Allah´a ortak koştular. Oysa ki onları da Allah yaratmıştı. Bilgisizce O´na oğullar ve kızlar yakıştırdılar. Hâşâ! O, onların ileri sürdüğü vasıflardan uzak ve yücedir.', 7, 139, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(890, 101, 6, 'En’âm', 'بَد۪يعُ السَّمٰوَاتِ وَالْاَرْضِۜ اَنّٰى يَكُونُ لَهُ وَلَدٌ وَلَمْ تَكُنْ لَهُ صَاحِبَةٌۜ وَخَلَقَ كُلَّ شَيْءٍۚ وَهُوَ بِكُلِّ شَيْءٍ عَل۪يمٌ', 'O, göklerin ve yerin eşsiz yaratıcısıdır. O´nun eşi olmadığı halde nasıl çocuğu olabilir! Her şeyi O yaratmıştır ve her şeyi hakkıyla bilen O´dur.', 7, 139, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(891, 102, 6, 'En’âm', 'ذٰلِكُمُ اللّٰهُ رَبُّكُمْۚ لَٓا اِلٰهَ اِلَّا هُوَۚ خَالِقُ كُلِّ شَيْءٍ فَاعْبُدُوهُۚ وَهُوَ عَلٰى كُلِّ شَيْءٍ وَك۪يلٌ', 'İşte Rabbiniz Allah O´dur. O´ndan başka tanrı yoktur. O, her şeyin yaratıcısıdır. Öyle ise O´na kulluk edin, O her şeye vekildir (güvenilip dayanılacak tek varlık O´dur).', 7, 140, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(892, 103, 6, 'En’âm', 'لَا تُدْرِكُهُ الْاَبْصَارُۘ وَهُوَ يُدْرِكُ الْاَبْصَارَۚ وَهُوَ اللَّط۪يفُ الْخَب۪يرُ', 'Gözler O´nu göremez; halbuki O, gözleri görür. O, eşyayı pek iyi bilen, her şeyden haberdar olandır.', 7, 140, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(893, 104, 6, 'En’âm', 'قَدْ جَٓاءَكُمْ بَصَٓائِرُ مِنْ رَبِّكُمْۚ فَمَنْ اَبْصَرَ فَلِنَفْسِه۪ۚ وَمَنْ عَمِيَ فَعَلَيْهَاۜ وَمَٓا اَنَا۬ عَلَيْكُمْ بِحَف۪يظٍ', '(Doğrusu) size Rabbiniz tarafından basiretler (idrak kabiliyeti) verilmiştir. Artık kim hakkı görürse faydası kendisine, kim de kör olursa zararı kendinedir. Ben üzerinize bekçi değilim.', 7, 140, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(894, 105, 6, 'En’âm', 'وَكَذٰلِكَ نُصَرِّفُ الْاٰيَاتِ وَلِيَقُولُوا دَرَسْتَ وَلِنُبَيِّنَهُ لِقَوْمٍ يَعْلَمُونَ', 'Böylece biz âyetleri geniş geniş açıklıyoruz ki, «Sen ders almışsın» desinler de biz de anlayan toplum için Kur´an´ı iyice açıklayalım.', 7, 140, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(895, 106, 6, 'En’âm', 'اِتَّبِعْ مَٓا اُو۫حِيَ اِلَيْكَ مِنْ رَبِّكَۚ لَٓا اِلٰهَ اِلَّا هُوَۚ وَاَعْرِضْ عَنِ الْمُشْرِك۪ينَ', 'Rabbinden sana vahyolunana uy. O´ndan başka tanrı yoktur. Müşriklerden yüz çevir.', 7, 140, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(896, 107, 6, 'En’âm', 'وَلَوْ شَٓاءَ اللّٰهُ مَٓا اَشْرَكُواۜ وَمَا جَعَلْنَاكَ عَلَيْهِمْ حَف۪يظاًۚ وَمَٓا اَنْتَ عَلَيْهِمْ بِوَك۪يلٍ', 'Allah dileseydi, onlar ortak koşmazlardı. Biz seni onların üzerine bir bekçi kılmadık. Sen onların vekili de değilsin.', 7, 140, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(897, 108, 6, 'En’âm', 'وَلَا تَسُبُّوا الَّذ۪ينَ يَدْعُونَ مِنْ دُونِ اللّٰهِ فَيَسُبُّوا اللّٰهَ عَدْواً بِغَيْرِ عِلْمٍۜ كَذٰلِكَ زَيَّنَّا لِكُلِّ اُمَّةٍ عَمَلَهُمْ ثُمَّ اِلٰى رَبِّهِمْ مَرْجِعُهُمْ فَيُنَبِّئُهُمْ بِمَا كَانُوا يَعْمَلُونَ', 'Allah´tan başkasına tapanlara (ve putlarına) sövmeyin; sonra onlar da bilgisizce, düşmanca Allah´a söverler. Böylece biz her ümmete kendi işlerini câzip gösterdik. Sonunda dönüşleri Rablerinedir. Artık O ne yaptıklarını kendilerine bildirecektir.', 7, 140, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(898, 109, 6, 'En’âm', 'وَاَقْسَمُوا بِاللّٰهِ جَهْدَ اَيْمَانِهِمْ لَئِنْ جَٓاءَتْهُمْ اٰيَةٌ لَيُؤْمِنُنَّ بِهَاۜ قُلْ اِنَّمَا الْاٰيَاتُ عِنْدَ اللّٰهِ وَمَا يُشْعِرُكُمْۙ اَنَّـهَٓا اِذَا جَٓاءَتْ لَا يُؤْمِنُونَ', 'Kendilerine bir mucize gelirse ona mutlaka inanacaklarına dair kuvvetli bir şekilde Allah´a and içtiler. De ki: Mucizeler ancak Allah katındandır. Ama mucize geldiğinde de inanmayacaklarının farkında mısınız?', 7, 140, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(899, 110, 6, 'En’âm', 'وَنُقَلِّبُ اَفْـِٔدَتَهُمْ وَاَبْصَارَهُمْ كَمَا لَمْ يُؤْمِنُوا بِه۪ٓ اَوَّلَ مَرَّةٍ وَنَذَرُهُمْ ف۪ي طُغْيَانِهِمْ يَعْمَهُونَ۟', 'Yine O´na iman etmedikleri ilk durumdaki gibi onların gönüllerini ve gözlerini ters çeviririz. Ve onları şaşkın olarak azgınlıkları içerisinde bırakırız.', 7, 140, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(900, 111, 6, 'En’âm', 'وَلَوْ اَنَّـنَا نَزَّلْـنَٓا اِلَيْهِمُ الْمَلٰٓئِكَةَ وَكَلَّمَهُمُ الْمَوْتٰى وَحَشَرْنَا عَلَيْهِمْ كُلَّ شَيْءٍ قُبُلاً مَا كَانُوا لِيُؤْمِنُٓوا اِلَّٓا اَنْ يَشَٓاءَ اللّٰهُ وَلٰكِنَّ اَكْثَرَهُمْ يَجْهَلُونَ', 'Eğer biz onlara melekleri indirseydik, ölüler de onlarla konuşsaydı ve her şeyi toplayıp karşılarına getirseydik, Allah dilemedikçe yine de inanacak değillerdi; fakat çokları bunu bilmezler.', 8, 141, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(901, 112, 6, 'En’âm', 'وَكَذٰلِكَ جَعَلْنَا لِكُلِّ نَبِيٍّ عَدُواًّ شَيَاط۪ينَ الْاِنْسِ وَالْجِنِّ يُوح۪ي بَعْضُهُمْ اِلٰى بَعْضٍ زُخْرُفَ الْقَوْلِ غُرُوراًۜ وَلَوْ شَٓاءَ رَبُّكَ مَا فَعَلُوهُ فَذَرْهُمْ وَمَا يَفْتَرُونَ', 'Böylece biz, her peygambere insan ve cin şeytanlarını düşman kıldık. (Bunlar), aldatmak için birbirlerine yaldızlı sözler fısıldarlar. Rabbin dileseydi onu da yapamazlardı. Artık onları uydurdukları şeylerle başbaşa bırak.', 8, 141, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(902, 113, 6, 'En’âm', 'وَلِتَصْغٰٓى اِلَيْهِ اَفْـِٔدَةُ الَّذ۪ينَ لَا يُؤْمِنُونَ بِالْاٰخِرَةِ وَلِيَرْضَوْهُ وَلِيَقْتَرِفُوا مَا هُمْ مُقْتَرِفُونَ', 'Âhirete inanmayanların kalpleri ona (yaldızlı söze) kansın, ondan hoşlansınlar ve işledikleri suçu işlemeye devam etsinler diye (böyle yaparlar).', 8, 141, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(903, 114, 6, 'En’âm', 'اَفَغَيْرَ اللّٰهِ اَبْتَغ۪ي حَكَماً وَهُوَ الَّـذ۪ٓي اَنْزَلَ اِلَيْكُمُ الْكِتَابَ مُفَصَّلاًۜ وَالَّذ۪ينَ اٰتَيْنَاهُمُ الْكِتَابَ يَعْلَمُونَ اَنَّهُ مُنَزَّلٌ مِنْ رَبِّكَ بِالْحَقِّ فَلَا تَكُونَنَّ مِنَ الْمُمْتَر۪ينَ', '(De ki): Allah´dan başka bir hakem mi arayacağım? Halbuki size Kitab´ı açık olarak indiren O´dur. Kendilerine kitap verdiğimiz kimseler, Kur´an´ın gerçekten Rabbin tarafından indirilmiş olduğunu bilirler. Sakın şüpheye düşenlerden olma!', 8, 141, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(904, 115, 6, 'En’âm', 'وَتَمَّتْ كَلِمَتُ رَبِّكَ صِدْقاً وَعَدْلاًۜ لَا مُبَدِّلَ لِكَلِمَاتِه۪ۚ وَهُوَ السَّم۪يعُ الْعَل۪يمُ', 'Rabbinin sözü, doğruluk ve adalet bakımından tamamlanmıştır. O´nun sözlerini değiştirecek kimse yoktur. O işitendir, bilendir.', 8, 141, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(905, 116, 6, 'En’âm', 'وَاِنْ تُطِـعْ اَكْثَرَ مَنْ فِي الْاَرْضِ يُضِلُّوكَ عَنْ سَب۪يلِ اللّٰهِۜ اِنْ يَتَّبِعُونَ اِلَّا الظَّنَّ وَاِنْ هُمْ اِلَّا يَخْرُصُونَ', 'Yeryüzünde bulunanların çoğuna uyacak olursan, seni Allah´ın yolundan saptırırlar. Onlar zandan başka bir şeye tâbi olmaz, yalandan başka söz de söylemezler.', 8, 141, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(906, 117, 6, 'En’âm', 'اِنَّ رَبَّكَ هُوَ اَعْلَمُ مَنْ يَضِلُّ عَنْ سَب۪يلِه۪ۚ وَهُوَ اَعْلَمُ بِالْمُهْتَد۪ينَ', 'Muhakkak ki senin Rabbin, evet O, kendi yolundan sapanı en iyi bilendir. O, doğru yolda gidenleri de iyi bilendir.', 8, 141, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(907, 118, 6, 'En’âm', 'فَكُلُوا مِمَّا ذُكِرَ اسْمُ اللّٰهِ عَلَيْهِ اِنْ كُنْتُمْ بِاٰيَاتِه۪ مُؤْمِن۪ينَ', 'Allah´ın âyetlerine inanıyorsanız, üzerine O´nun adı anılarak kesilenlerden yeyin.', 8, 141, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(908, 119, 6, 'En’âm', 'وَمَا لَكُمْ اَلَّا تَأْكُلُوا مِمَّا ذُكِرَ اسْمُ اللّٰهِ عَلَيْهِ وَقَدْ فَصَّلَ لَكُمْ مَا حَرَّمَ عَلَيْكُمْ اِلَّا مَا اضْطُرِرْتُمْ اِلَيْهِۜ وَاِنَّ كَث۪يراً لَيُضِلُّونَ بِاَهْوَٓائِهِمْ بِغَيْرِ عِلْمٍۜ اِنَّ رَبَّكَ هُوَ اَعْلَمُ بِالْمُعْتَد۪ينَ', 'Üzerine Allah´ın adı anılıp kesilenden yememenize sebep ne? Oysa Allah, çaresiz yemek zorunda kaldığınız dışında, haram kıldığı şeyleri size açıklamıştır. Doğrusu bir çokları bilgisizce kendi kötü arzularına uyarak saptırıyorlar. Muhakkak ki Rabbin haddi aşanları çok iyi bilir.', 8, 142, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(909, 120, 6, 'En’âm', 'وَذَرُوا ظَاهِرَ الْاِثْمِ وَبَاطِنَهُۜ اِنَّ الَّذ۪ينَ يَكْسِبُونَ الْاِثْمَ سَيُجْزَوْنَ بِمَا كَانُوا يَقْتَرِفُونَ', 'Günahın açığını da gizlisini de bırakın! Çünkü günah işleyenler, yaptıklarının cezasını mutlaka çekeceklerdir.', 8, 142, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(910, 121, 6, 'En’âm', 'وَلَا تَأْكُلُوا مِمَّا لَمْ يُذْكَرِ اسْمُ اللّٰهِ عَلَيْهِ وَاِنَّهُ لَفِسْقٌۜ وَاِنَّ الشَّيَاط۪ينَ لَيُوحُونَ اِلٰٓى اَوْلِيَٓائِهِمْ لِيُجَادِلُوكُمْۚ وَاِنْ اَطَعْتُمُوهُمْ اِنَّكُمْ لَمُشْرِكُونَ۟', 'Üzerine Allah´ın adı anılmadan kesilen hayvanlardan yemeyin. Kuşkusuz bu büyük günahtır. Gerçekten şeytanlar dostlarına, sizinle mücadele etmeleri için telkinde bulunurlar. Eğer onlara uyarsanız şüphesiz siz de Allah´a ortak koşanlar olursunuz.', 8, 142, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(911, 122, 6, 'En’âm', 'اَوَمَنْ كَانَ مَيْتاً فَاَحْيَيْنَاهُ وَجَعَلْنَا لَهُ نُوراً يَمْش۪ي بِه۪ فِي النَّاسِ كَمَنْ مَثَلُهُ فِي الظُّلُمَاتِ لَيْسَ بِخَارِجٍ مِنْهَاۜ كَذٰلِكَ زُيِّنَ لِلْكَافِر۪ينَ مَا كَانُوا يَعْمَلُونَ', 'Ölü iken dirilttiğimiz ve kendisine insanlar arasında yürüyebileceği bir ışık verdiğimiz kimse, karanlıklar içinde kalıp ondan hiç çıkamayacak durumdaki kimse gibi olur mu? İşte kâfirlere yaptıkları böyle süslü gösterilmiştir.', 8, 142, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(912, 123, 6, 'En’âm', 'وَكَذٰلِكَ جَعَلْنَا ف۪ي كُلِّ قَرْيَةٍ اَكَابِرَ مُجْرِم۪يهَا لِيَمْكُرُوا ف۪يهَاۜ وَمَا يَمْكُرُونَ اِلَّا بِاَنْفُسِهِمْ وَمَا يَشْعُرُونَ', 'Böylece biz, her kasabada, oralarda bozgunculuk yapmaları için, günahkârlarını liderler yaptık. Onlar yalnız kendilerini aldatırlar, ama farkında olmazlar.', 8, 142, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(913, 124, 6, 'En’âm', 'وَاِذَا جَٓاءَتْهُمْ اٰيَةٌ قَالُوا لَنْ نُؤْمِنَ حَتّٰى نُؤْتٰى مِثْلَ مَٓا اُو۫تِيَ رُسُلُ اللّٰهِۜ اَللّٰهُ اَعْلَمُ حَيْثُ يَجْعَلُ رِسَالَتَهُۜ سَيُص۪يبُ الَّذ۪ينَ اَجْرَمُوا صَغَارٌ عِنْدَ اللّٰهِ وَعَذَابٌ شَد۪يدٌ بِمَا كَانُوا يَمْكُرُونَ', 'Onlara bir âyet geldiğinde, Allah´ın elçilerine verilenin benzeri bize de verilmedikçe kesinlikle inanmayız, dediler. Allah, peygamberliğini kime vereceğini daha iyi bilir. Suç işleyenlere, yapmakta oldukları hilelere karşılık Allah tarafından aşağılık ve çetin bir azap erişecektir.', 8, 142, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(914, 125, 6, 'En’âm', 'فَمَنْ يُرِدِ اللّٰهُ اَنْ يَهْدِيَهُ يَشْرَحْ صَدْرَهُ لِلْاِسْلَامِۚ وَمَنْ يُرِدْ اَنْ يُضِلَّهُ يَجْعَلْ صَدْرَهُ ضَيِّقاً حَرَجاً كَاَنَّمَا يَصَّعَّدُ فِي السَّمَٓاءِۜ كَذٰلِكَ يَجْعَلُ اللّٰهُ الرِّجْسَ عَلَى الَّذ۪ينَ لَا يُؤْمِنُونَ', 'Allah kimi doğru yola iletmek isterse onun kalbini İslâm´a açar; kimi de saptırmak isterse göğe çıkıyormuş gibi kalbini iyice daraltır. Allah inanmayanların üstüne işte böyle murdarlık verir.', 8, 143, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(915, 126, 6, 'En’âm', 'وَهٰذَا صِرَاطُ رَبِّكَ مُسْتَق۪يماًۜ قَدْ فَصَّلْنَا الْاٰيَاتِ لِقَوْمٍ يَذَّكَّرُونَ', 'Bu (din), Rabbinin dosdoğru yoludur. Biz, öğüt alacak bir kavim için âyetleri ayrıntılı olarak açıkladık.', 8, 143, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(916, 127, 6, 'En’âm', 'لَهُمْ دَارُ السَّلَامِ عِنْدَ رَبِّهِمْ وَهُوَ وَلِيُّهُمْ بِمَا كَانُوا يَعْمَلُونَ', 'Rableri katında onlara esenlik yurdu (cennet) vardır.Ve yapmakta oldukları (güzel) işler sebebiyle Allah onların dostudur.', 8, 143, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(917, 128, 6, 'En’âm', 'وَيَوْمَ يَحْشُرُهُمْ جَم۪يعاًۚ يَا مَعْشَرَ الْجِنِّ قَدِ اسْتَكْثَرْتُمْ مِنَ الْاِنْسِۚ وَقَالَ اَوْلِيَٓاؤُ۬هُمْ مِنَ الْاِنْسِ رَبَّـنَا اسْتَمْتَعَ بَعْضُنَا بِبَعْضٍ وَبَلَغْنَٓا اَجَلَنَا الَّـذ۪ٓي اَجَّلْتَ لَنَاۜ قَالَ النَّارُ مَثْوٰيكُمْ خَالِد۪ينَ ف۪يهَٓا اِلَّا مَا شَٓاءَ اللّٰهُۜ اِنَّ رَبَّكَ حَك۪يمٌ عَل۪يمٌ', 'Allah, onların hepsini bir araya topladığı gün, «Ey cinler (şeytanlar) topluluğu! Siz insanlarla çok uğraştınız» der. Onların, insanlardan olan dostları ise: «Ey Rabbimiz! (Biz) birbirimizden yararlandık ve bize verdiğin sürenin sonuna ulaştık» derler. Allah da buyurur ki: Allah´ın dilediği hariç, içinde ebedî kalacağınız yer ateştir. Şüphesiz Rabbin hikmet sahibidir, bilendir.', 8, 143, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(918, 129, 6, 'En’âm', 'وَكَذٰلِكَ نُوَلّ۪ي بَعْضَ الظَّالِم۪ينَ بَعْضاً بِمَا كَانُوا يَكْسِبُونَ۟', 'İşte böylece işledikleri günahlardan ötürü zalimlerin bir kısmını diğer bir kısmının peşine takarız.', 8, 143, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(919, 130, 6, 'En’âm', 'يَا مَعْشَرَ الْجِنِّ وَالْاِنْسِ اَلَمْ يَأْتِكُمْ رُسُلٌ مِنْكُمْ يَقُصُّونَ عَلَيْكُمْ اٰيَات۪ي وَيُنْذِرُونَكُمْ لِقَٓاءَ يَوْمِكُمْ هٰذَاۜ قَالُوا شَهِدْنَا عَلٰٓى اَنْفُسِنَا وَغَرَّتْهُمُ الْحَيٰوةُ الدُّنْيَا وَشَهِدُوا عَلٰٓى اَنْفُسِهِمْ اَنَّهُمْ كَانُوا كَافِر۪ينَ', 'Ey cin ve insan topluluğu! İçinizden size âyetlerimi anlatan ve bu günle karşılaşacağınıza dair sizi uyaran peygamberler gelmedi mi? Derler ki: «Kendi aleyhimize şahitlik ederiz.» Dünya hayatı onları aldattı ve kâfir olduklarına dair kendi aleyhlerine şahitlik ettiler.', 8, 143, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(920, 131, 6, 'En’âm', 'ذٰلِكَ اَنْ لَمْ يَكُنْ رَبُّكَ مُهْلِكَ الْقُرٰى بِظُلْمٍ وَاَهْلُهَا غَافِلُونَ', 'Gerçek şu ki: Halkı habersizken, Rabbin haksızlık ile ülkeleri helâk edici değildir.', 8, 143, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(921, 132, 6, 'En’âm', 'وَلِكُلٍّ دَرَجَاتٌ مِمَّا عَمِلُواۜ وَمَا رَبُّكَ بِغَافِلٍ عَمَّا يَعْمَلُونَ', 'Herkesin yaptıkları işlere göre dereceleri vardır. Rabbin onların yaptıklarından habersiz değildir.', 8, 144, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(922, 133, 6, 'En’âm', 'وَرَبُّكَ الْغَنِيُّ ذُوالرَّحْمَةِۜ اِنْ يَشَأْ يُذْهِبْكُمْ وَيَسْتَخْلِفْ مِنْ بَعْدِكُمْ مَا يَشَٓاءُ كَمَٓا اَنْشَاَكُمْ مِنْ ذُرِّيَّةِ قَوْمٍ اٰخَر۪ينَۜ', 'Rabbin zengindir, rahmet sahibidir. Dilerse sizi yok eder ve sizi başka bir kavmin zürriyetinden yarattığı gibi sizden sonra yerinize dilediği bir kavmi yaratır.', 8, 144, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(923, 134, 6, 'En’âm', 'اِنَّ مَا تُوعَدُونَ لَاٰتٍۙ وَمَٓا اَنْتُمْ بِمُعْجِز۪ينَ', 'Size vadedilen mutlaka gelecektir; siz bunu önleyemezsiniz.', 8, 144, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(924, 135, 6, 'En’âm', 'قُلْ يَا قَوْمِ اعْمَلُوا عَلٰى مَكَانَتِكُمْ اِنّ۪ي عَامِلٌۚ فَسَوْفَ تَعْلَمُونَۙ مَنْ تَكُونُ لَهُ عَاقِبَةُ الدَّارِۜ اِنَّهُ لَا يُفْلِحُ الظَّالِمُونَ', 'De ki: Ey kavmim! Elinizden geleni yapın! Ben de yapacağım! Yurdun (dünyanın) sonunun kimin lehine olduğunu yakında bileceksiniz. Gerçek şu ki, zalimler iflah olmazlar.', 8, 144, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(925, 136, 6, 'En’âm', 'وَجَعَلُوا لِلّٰهِ مِمَّا ذَرَاَ مِنَ الْحَرْثِ وَالْاَنْعَامِ نَص۪يباً فَقَالُوا هٰذَا لِلّٰهِ بِزَعْمِهِمْ وَهٰذَا لِشُرَكَٓائِنَاۚ فَمَا كَانَ لِشُرَكَٓائِهِمْ فَلَا يَصِلُ اِلَى اللّٰهِۚ وَمَا كَانَ لِلّٰهِ فَهُوَ يَصِلُ اِلٰى شُرَكَٓائِهِمْۜ سَٓاءَ مَا يَحْكُمُونَ', 'Allah´ın yarattığı ekinlerle hayvanlardan Allah´a pay ayırıp zanlarınca, bu Allah´a, bu da ortaklarımıza (putlarımıza) dediler. Ortakları için ayrılan Allah´a ulaşmıyor, fakat Allah için ayrılan ortaklarına ulaşıyor! Ne kötü hüküm veriyorlar?', 8, 144, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(926, 137, 6, 'En’âm', 'وَكَذٰلِكَ زَيَّنَ لِكَث۪يرٍ مِنَ الْمُشْرِك۪ينَ قَتْلَ اَوْلَادِهِمْ شُرَكَٓاؤُ۬هُمْ لِيُرْدُوهُمْ وَلِيَلْبِسُوا عَلَيْهِمْ د۪ينَهُمْۜ وَلَوْ شَٓاءَ اللّٰهُ مَا فَعَلُوهُ فَذَرْهُمْ وَمَا يَفْتَرُونَ', 'Bunun gibi ortakları, müşriklerden çoğuna çocuklarını (kızlarını) öldürmeyi hoş gösterdi ki, hem kendilerini mahvetsinler hem de dinlerini karıştırıp bozsunlar! Allah dileseydi bunu yapamazlardı. Öyle ise onları uydurdukları ile başbaşa bırak!', 8, 144, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(927, 138, 6, 'En’âm', 'وَقَالُوا هٰذِه۪ٓ اَنْعَامٌ وَحَرْثٌ حِجْرٌۘ لَا يَطْعَمُهَٓا اِلَّا مَنْ نَشَٓاءُ بِزَعْمِهِمْ وَاَنْعَامٌ حُرِّمَتْ ظُهُورُهَا وَاَنْعَامٌ لَا يَذْكُرُونَ اسْمَ اللّٰهِ عَلَيْهَا افْتِرَٓاءً عَلَيْهِۜ سَيَجْز۪يهِمْ بِمَا كَانُوا يَفْتَرُونَ', 'Onlar saçma düşüncelerine göre dediler ki: «Bu (tanrılar için ayrılan) hayvanlarla ekinler haramdır. Bunları bizim dilediğimizden başkası yiyemez. Bunlar da binilmesi yasaklanmış hayvanlardır.» Birtakım hayvanlar da vardır ki, (Allah böyle emrediyor diye) O´na iftira ederek üzerlerine Allah´ın adını anmazlar. Yapmakta oldukları iftiraları yüzünden Allah onları cezalandıracaktır.', 8, 145, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(928, 139, 6, 'En’âm', 'وَقَالُوا مَا ف۪ي بُطُونِ هٰذِهِ الْاَنْعَامِ خَالِصَةٌ لِذُكُورِنَا وَمُحَرَّمٌ عَلٰٓى اَزْوَاجِنَاۚ وَاِنْ يَكُنْ مَيْتَةً فَهُمْ ف۪يهِ شُرَكَٓاءُۜ سَيَجْز۪يهِمْ وَصْفَهُمْۜ اِنَّهُ حَك۪يمٌ عَل۪يمٌ', 'Dediler ki: «Şu hayvanların karınlarında olanlar yalnız erkeklerimize aittir, kadınlarımıza ise haram kılınmıştır. Şayet (yavru) ölü doğarsa, o zaman (kadın erkek) hepsi onda ortaktır.» Allah bu değerlendirmelerinin cezasını verecektir. Şüphesiz ki O hikmet sahibidir, hakkıyla bilendir.', 8, 145, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(929, 140, 6, 'En’âm', 'قَدْ خَسِرَ الَّذ۪ينَ قَتَلُٓوا اَوْلَادَهُمْ سَفَهاً بِغَيْرِ عِلْمٍ وَحَرَّمُوا مَا رَزَقَهُمُ اللّٰهُ افْتِرَٓاءً عَلَى اللّٰهِۜ قَدْ ضَلُّوا وَمَا كَانُوا مُهْتَد۪ينَ۟', 'Bilgisizlikleri yüzünden beyinsizce çocuklarını öldürenler ve Allah´ın kendilerine verdiği rızkı, Allah´a iftira ederek (kadınlara) haram kılanlar, muhakkak ki ziyana uğramışlardır. Onlar gerçekten sapmışlardır ve doğru yolu bulacak da değillerdir.', 8, 145, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(930, 141, 6, 'En’âm', 'وَهُوَ الَّـذ۪ٓي اَنْشَاَ جَنَّاتٍ مَعْرُوشَاتٍ وَغَيْرَ مَعْرُوشَاتٍ وَالنَّخْلَ وَالزَّرْعَ مُخْتَلِفاً اُكُلُهُ وَالزَّيْتُونَ وَالرُّمَّانَ مُتَشَابِهاً وَغَيْرَ مُتَشَابِهٍۜ كُلُوا مِنْ ثَمَرِه۪ٓ اِذَٓا اَثْمَرَ وَاٰتُوا حَقَّهُ يَوْمَ حَصَادِه۪ۘ وَلَا تُسْرِفُواۜ اِنَّهُ لَا يُحِبُّ الْمُسْرِف۪ينَۙ', 'Çardaklı ve çardaksız (üzüm) bahçeleri, ürünleri çeşit çeşit hurmaları, ekinleri, birbirine benzer ve benzemez biçimde zeytin ve narları yaratan O´dur. Herbiri meyve verdiği zaman meyvesinden yeyin. Devşirilip toplandığı gün de hakkını (zekât ve sadakasını) verin, fakat israf etmeyin; çünkü Allah israf edenleri sevmez.', 8, 145, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(931, 142, 6, 'En’âm', 'وَمِنَ الْاَنْعَامِ حَمُولَةً وَفَرْشاًۜ كُلُوا مِمَّا رَزَقَكُمُ اللّٰهُ وَلَا تَتَّبِعُوا خُطُوَاتِ الشَّيْطَانِۜ اِنَّهُ لَكُمْ عَدُوٌّ مُب۪ينٌۙ', 'Hayvanlardan yük taşıyanı ve tüyünden döşek yapılanları yaratan O´dur. Allah´ın size verdiği rızıktan yeyin, şeytanın ardına düşmeyin; şüphesiz o sizin için apaçık bir düşmandır.', 8, 145, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(932, 143, 6, 'En’âm', 'ثَمَانِيَةَ اَزْوَاجٍۚ مِنَ الضَّأْنِ اثْنَيْنِ وَمِنَ الْمَعْزِ اثْنَيْنِۜ قُلْ آٰلذَّكَرَيْنِ حَرَّمَ اَمِ الْاُنْثَيَيْنِ اَمَّا اشْتَمَلَتْ عَلَيْهِ اَرْحَامُ الْاُنْثَيَيْنِۜ نَبِّؤُ۫ن۪ي بِعِلْمٍ اِنْ كُنْتُمْ صَادِق۪ينَۙ', '(Dişi ve erkek olarak) sekiz eş yarattı: Koyundan iki, keçiden iki... De ki: O, bunların erkeklerini mi, dişilerini mi, yoksa bu iki dişinin rahimlerinde bulunan yavruları mı haram etti? Eğer doğru iseniz bana ilimle söyleyin.', 8, 146, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(933, 144, 6, 'En’âm', 'وَمِنَ الْاِبِلِ اثْنَيْنِ وَمِنَ الْبَقَرِ اثْنَيْنِۜ قُلْ آٰلذَّكَرَيْنِ حَرَّمَ اَمِ الْاُنْثَيَيْنِ اَمَّا اشْتَمَلَتْ عَلَيْهِ اَرْحَامُ الْاُنْثَيَيْنِۜ اَمْ كُنْتُمْ شُهَدَٓاءَ اِذْ وَصّٰيكُمُ اللّٰهُ بِهٰذَاۚ فَمَنْ اَظْلَمُ مِمَّنِ افْتَرٰى عَلَى اللّٰهِ كَذِباً لِيُضِلَّ النَّاسَ بِغَيْرِ عِلْمٍۜ اِنَّ اللّٰهَ لَا يَهْدِي الْقَوْمَ الظَّالِم۪ينَ۟', 'Deveden de iki, sığırdan da iki (yarattı.) De ki: O bunların erkeklerini mi, dişilerini mi, yoksa bu iki dişinin rahimlerinde bulunan yavruları mı haram kıldı? Yoksa Allah´ın size böyle vasiyet ettiğine şahit mi oldunuz? Bilgisizce insanları saptırmak için Allah´a karşı yalan uydurandan kim daha zalimdir! Şüphesiz Allah o zalimler topluluğunu doğru yola iletmez.', 8, 146, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(934, 145, 6, 'En’âm', 'قُلْ لَٓا اَجِدُ ف۪ي مَٓا اُو۫حِيَ اِلَيَّ مُحَرَّماً عَلٰى طَاعِمٍ يَطْعَمُهُٓ اِلَّٓا اَنْ يَكُونَ مَيْتَةً اَوْ دَماً مَسْفُوحاً اَوْ لَحْمَ خِنْز۪يرٍ فَاِنَّهُ رِجْسٌ اَوْ فِسْقاً اُهِلَّ لِغَيْرِ اللّٰهِ بِه۪ۚ فَمَنِ اضْطُرَّ غَيْرَ بَاغٍ وَلَا عَادٍ فَاِنَّ رَبَّكَ غَفُورٌ رَح۪يمٌ', 'De ki: Bana vahyolunanda, leş veya akıtılmış kan yahut domuz eti -ki pisliğin kendisidir- ya da günah işlenerek Allah´tan başkası adına kesilmiş bir hayvandan başka, yiyecek kimseye haram kılınmış birşey bulamıyorum. Başkasına zarar vermemek ve sınırı aşmamak üzere kim (bunlardan) yemek zorunda kalırsa bilsin ki Rabbin bağışlayan ve esirgeyendir.', 8, 146, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(935, 146, 6, 'En’âm', 'وَعَلَى الَّذ۪ينَ هَادُوا حَرَّمْنَا كُلَّ ذ۪ي ظُفُرٍۚ وَمِنَ الْبَقَرِ وَالْغَنَمِ حَرَّمْنَا عَلَيْهِمْ شُحُومَهُمَٓا اِلَّا مَا حَمَلَتْ ظُهُورُهُمَٓا اَوِ الْحَوَايَٓا اَوْ مَا اخْتَلَطَ بِعَظْمٍۜ ذٰلِكَ جَزَيْنَاهُمْ بِبَغْيِهِمْۘ وَاِنَّا لَصَادِقُونَ', 'Yahudilere bütün tırnaklı hayvanları haram kıldık. Sırtlarında yahut bağırsaklarında taşıdıkları ya da kemiğe karışan yağlar hariç olmak üzere sığır ve koyunun iç yağlarını da onlara haram kıldık. Bu, zulümleri yüzünden onlara verdiğimiz cezâdır. Biz elbette doğru söyleyeniz.', 8, 146, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(936, 147, 6, 'En’âm', 'فَاِنْ كَذَّبُوكَ فَقُلْ رَبُّكُمْ ذُورَحْمَةٍ وَاسِعَةٍۚ وَلَا يُرَدُّ بَأْسُهُ عَنِ الْقَوْمِ الْمُجْرِم۪ينَ', 'Eğer seni yalanlarlarsa de ki: Rabbiniz geniş bir rahmet sahibidir. Bununla beraber O´nun azabı, suçlular topluluğundan uzaklaştırılamaz.', 8, 147, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(937, 148, 6, 'En’âm', 'سَيَقُولُ الَّذ۪ينَ اَشْرَكُوا لَوْ شَٓاءَ اللّٰهُ مَٓا اَشْرَكْنَا وَلَٓا اٰبَٓاؤُ۬نَا وَلَا حَرَّمْنَا مِنْ شَيْءٍۜ كَذٰلِكَ كَذَّبَ الَّذ۪ينَ مِنْ قَبْلِهِمْ حَتّٰى ذَاقُوا بَأْسَنَاۜ قُلْ هَلْ عِنْدَكُمْ مِنْ عِلْمٍ فَتُخْرِجُوهُ لَنَاۜ اِنْ تَتَّبِعُونَ اِلَّا الظَّنَّ وَاِنْ اَنْتُمْ اِلَّا تَخْرُصُونَ', 'Putperestler diyecekler ki: «Allah dileseydi ne biz ortak koşardık ne de atalarımız. Hiçbir şeyi de haram kılmazdık.» Onlardan öncekiler de aynı şekilde (peygamberleri) yalanladılar ve sonunda azabımızı tattılar. De ki: Yanınızda bize açıklayacağınız bir bilgi var mı? Siz zandan başka bir şeye uymuyorsunuz ve siz sadece yalan söylüyorsunuz.', 8, 147, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(938, 149, 6, 'En’âm', 'قُلْ فَلِلّٰهِ الْحُجَّةُ الْبَالِغَةُۚ فَلَوْ شَٓاءَ لَهَدٰيكُمْ اَجْمَع۪ينَ', 'De ki: Kesin delil, ancak Allah´ındır. Allah dileseydi elbette hepinizi doğru yola iletirdi.', 8, 147, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(939, 150, 6, 'En’âm', 'قُلْ هَلُمَّ شُهَدَٓاءَكُمُ الَّذ۪ينَ يَشْهَدُونَ اَنَّ اللّٰهَ حَرَّمَ هٰذَاۚ فَاِنْ شَهِدُوا فَلَا تَشْهَدْ مَعَهُمْۚ وَلَا تَتَّبِعْ اَهْوَٓاءَ الَّذ۪ينَ كَذَّبُوا بِاٰيَاتِنَا وَالَّذ۪ينَ لَا يُؤْمِنُونَ بِالْاٰخِرَةِ وَهُمْ بِرَبِّهِمْ يَعْدِلُونَ۟', 'De ki: Allah şunu yasak etti, diye şehadet edecek şahitlerinizi getirin! Eğer onlar şahitlik ederlerse, sen onlarla beraber şahitlik etme; âyetlerimizi yalanlayanların ve ahiret gününe inanmayanların arzularına uyma. Onlar, Rablerine eş tutuyorlar.', 8, 147, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(940, 151, 6, 'En’âm', 'قُلْ تَعَالَوْا اَتْلُ مَا حَرَّمَ رَبُّكُمْ عَلَيْكُمْ اَلَّا تُشْرِكُوا بِه۪ شَيْـٔاًۜ وَبِالْوَالِدَيْنِ اِحْسَاناًۚ وَلَا تَقْتُلُٓوا اَوْلَادَكُمْ مِنْ اِمْلَاقٍۜ نَحْنُ نَرْزُقُكُمْ وَاِيَّاهُمْۚ وَلَا تَقْرَبُوا الْفَوَاحِشَ مَا ظَهَرَ مِنْهَا وَمَا بَطَنَۚ وَلَا تَقْتُلُوا النَّفْسَ الَّت۪ي حَرَّمَ اللّٰهُ اِلَّا بِالْحَقِّۜ ذٰلِكُمْ وَصّٰيكُمْ بِه۪ لَعَلَّكُمْ تَعْقِلُونَ', 'De ki: Gelin Rabbinizin size neleri haram kıldığını okuyayım: O´na hiçbir şeyi ortak koşmayın, ana-babaya iyilik edin, fakirlik korkusuyla çocuklarınızı öldürmeyin -sizin de onların da rızkını biz veririz-; kötülüklerin açığına da gizlisine de yaklaşmayın ve Allah´ın yasakladığı cana haksız yere kıymayın! İşte bunlar Allah´ın size emrettikleridir. Umulur ki düşünüp anlarsınız.', 8, 147, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(941, 152, 6, 'En’âm', 'وَلَا تَقْرَبُوا مَالَ الْيَت۪يمِ اِلَّا بِالَّت۪ي هِيَ اَحْسَنُ حَتّٰى يَبْلُغَ اَشُدَّهُۚ وَاَوْفُوا الْكَيْلَ وَالْم۪يزَانَ بِالْقِسْطِۚ لَا نُكَلِّفُ نَفْساً اِلَّا وُسْعَهَا وَاِذَا قُلْتُمْ فَاعْدِلُوا وَلَوْ كَانَ ذَا قُرْبٰىۚ وَبِعَهْدِ اللّٰهِ اَوْفُواۜ ذٰلِكُمْ وَصّٰيكُمْ بِه۪ لَعَلَّكُمْ تَذَكَّرُونَۙ', 'Rüşd çağına erişinceye kadar, yetimin malına, sadece en iyi tutumla yaklaşın; ölçü ve tartıyı adaletle yapın. Biz herkese ancak gücünün yettiği kadarını yükleriz. Söz söylediğiniz zaman, yakınlarınız dahi olsa adaletli olun, Allah´a verdiğiniz sözü tutun. İşte Allah size, iyice düşünesiniz diye bunları emretti.', 8, 148, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(942, 153, 6, 'En’âm', 'وَاَنَّ هٰذَا صِرَاط۪ي مُسْتَق۪يماً فَاتَّبِعُوهُۚ وَلَا تَتَّبِعُوا السُّبُلَ فَتَفَرَّقَ بِكُمْ عَنْ سَب۪يلِه۪ۜ ذٰلِكُمْ وَصّٰيكُمْ بِه۪ لَعَلَّكُمْ تَتَّقُونَ', 'Şüphesiz bu, benim dosdoğru yolumdur. Buna uyun. (Başka) yollara uymayın. Zira o yollar sizi Allah´ın yolundan ayırır. İşte sakınmanız için Allah size bunları emretti.', 8, 148, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(943, 154, 6, 'En’âm', 'ثُمَّ اٰتَيْنَا مُوسَى الْكِتَابَ تَمَاماً عَلَى الَّـذ۪ٓي اَحْسَنَ وَتَفْص۪يلاً لِكُلِّ شَيْءٍ وَهُدًى وَرَحْمَةً لَعَلَّهُمْ بِلِقَٓاءِ رَبِّهِمْ يُؤْمِنُونَ۟', 'Sonra iyilik edenlere nimetimizi tamamlamak, her şeyi açıklamak, hidayete erdirmek ve rahmet etmek maksadıyla Musa´ya da Kitab´ı (Tevrat´ı) verdik. Umulur ki, Rablerinin huzuruna varacaklarına iman ederler.', 8, 148, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(944, 155, 6, 'En’âm', 'وَهٰذَا كِتَابٌ اَنْزَلْنَاهُ مُبَارَكٌ فَاتَّبِعُوهُ وَاتَّقُوا لَعَلَّكُمْ تُرْحَمُونَۙ', 'İşte bu (Kur´an), bizim indirdiğimiz mübarek bir kitaptır. Buna uyun ve Allah´tan korkun ki size merhamet edilsin.', 8, 148, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(945, 156, 6, 'En’âm', 'اَنْ تَقُولُٓوا اِنَّـمَٓا اُنْزِلَ الْكِتَابُ عَلٰى طَٓائِفَتَيْنِ مِنْ قَبْلِنَاۖ وَاِنْ كُنَّا عَنْ دِرَاسَتِهِمْ لَغَافِل۪ينَۙ', '«Kitap, yalnız bizden önceki iki topluluğa (hıristiyanlara ve yahudilere) indirildi, biz ise onların okumasından gerçekten habersizdik» demeyesiniz diye;', 8, 148, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(946, 157, 6, 'En’âm', 'اَوْ تَقُولُوا لَوْ اَنَّٓا اُنْزِلَ عَلَيْنَا الْكِتَابُ لَكُنَّٓا اَهْدٰى مِنْهُمْۚ فَقَدْ جَٓاءَكُمْ بَيِّنَةٌ مِنْ رَبِّكُمْ وَهُدًى وَرَحْمَةٌۚ فَمَنْ اَظْلَمُ مِمَّنْ كَذَّبَ بِاٰيَاتِ اللّٰهِ وَصَدَفَ عَنْهَاۜ سَنَجْزِي الَّذ۪ينَ يَصْدِفُونَ عَنْ اٰيَاتِنَا سُٓوءَ الْعَذَابِ بِمَا كَانُوا يَصْدِفُونَ', 'Yahut «Bize de kitap indirilseydi, biz onlardan daha çok doğru yolda olurduk» demeyesiniz diye (Kur´an´ı indirdik). İşte size de Rabbinizden açık bir delil, hidayet ve rahmet geldi. Kim, Allah´ın âyetlerini yalanlayıp onlardan yüz çevirenden daha zalimdir! Âyetlerimizden yüz çevirenleri, yüz çevirmelerinden ötürü azabın en kötüsüyle cezalandıracağız.', 8, 148, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(947, 158, 6, 'En’âm', 'هَلْ يَنْظُرُونَ اِلَّٓا اَنْ تَأْتِيَهُمُ الْمَلٰٓئِكَةُ اَوْ يَأْتِيَ رَبُّكَ اَوْ يَأْتِيَ بَعْضُ اٰيَاتِ رَبِّكَۜ يَوْمَ يَأْت۪ي بَعْضُ اٰيَاتِ رَبِّكَ لَا يَنْفَعُ نَفْساً ا۪يمَانُهَا لَمْ تَكُنْ اٰمَنَتْ مِنْ قَبْلُ اَوْ كَسَبَتْ ف۪ٓي ا۪يمَانِهَا خَيْراًۜ قُلِ انْتَظِرُٓوا اِنَّا مُنْتَظِرُونَ', 'Onlar ancak kendilerine meleklerin gelmesini veya Rabbinin gelmesini yahut Rabbinin bazı alâmetlerinin gelmesini bekliyorlar. Rabbinin bazı alâmetleri geldiği gün, önceden inanmamış ya da imanında bir hayır kazanmamış olan kimseye artık imanı bir fayda sağlamaz. De ki: Bekleyin, şüphesiz biz de beklemekteyiz!', 8, 149, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(948, 159, 6, 'En’âm', 'اِنَّ الَّذ۪ينَ فَرَّقُوا د۪ينَهُمْ وَكَانُوا شِيَعاً لَسْتَ مِنْهُمْ ف۪ي شَيْءٍۜ اِنَّـمَٓا اَمْرُهُمْ اِلَى اللّٰهِ ثُمَّ يُنَبِّئُهُمْ بِمَا كَانُوا يَفْعَلُونَ', 'Dinlerini parça parça edip guruplara ayrılanlar var ya, senin onlarla hiçbir ilişkin yoktur. Onların işi ancak Allah´a kalmıştır. Sonra Allah onlara yaptıklarını bildirecektir.', 8, 149, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(949, 160, 6, 'En’âm', 'مَنْ جَٓاءَ بِالْحَسَنَةِ فَلَهُ عَشْرُ اَمْثَالِهَاۚ وَمَنْ جَٓاءَ بِالسَّيِّئَةِ فَلَا يُجْزٰٓى اِلَّا مِثْلَهَا وَهُمْ لَا يُظْلَمُونَ', 'Kim (Allah huzuruna) iyilikle gelirse ona getirdiğinin on katı vardır. Kim de kötülükle gelirse o sadece getirdiğinin dengiyle cezalandırılır. Onlar haksızlığa uğratılmazlar.', 8, 149, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(950, 161, 6, 'En’âm', 'قُلْ اِنَّن۪ي هَدٰين۪ي رَبّ۪ٓي اِلٰى صِرَاطٍ مُسْتَق۪يمٍۚ د۪يناً قِيَماً مِلَّةَ اِبْرٰه۪يمَ حَن۪يفاًۚ وَمَا كَانَ مِنَ الْمُشْرِك۪ينَ', 'De ki: Şüphesiz Rabbim beni doğru yola, dosdoğru dine, Allah´ı birleyen İbrahim´in dinine iletti. O, ortak koşanlardan değildi.', 8, 149, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(951, 162, 6, 'En’âm', 'قُلْ اِنَّ صَلَات۪ي وَنُسُك۪ي وَمَحْيَايَ وَمَمَات۪ي لِلّٰهِ رَبِّ الْعَالَم۪ينَۙ', 'De ki: Şüphesiz benim namazım, kurbanım, hayatım ve ölümüm hepsi âlemlerin Rabbi Allah içindir.', 8, 149, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55);
INSERT INTO `Ayetler` (`id`, `AyetNo`, `SureNo`, `SureAdi`, `Metin`, `Mana`, `Cuzu`, `Sayfasi`, `AyetSayisi`, `HarfSayisi`, `AdAnlami`, `IndigiYer`, `InisSirasi`) VALUES
(952, 163, 6, 'En’âm', 'لَا شَر۪يكَ لَهُۚ وَبِذٰلِكَ اُمِرْتُ وَاَنَا۬ اَوَّلُ الْمُسْلِم۪ينَ', 'O´nun ortağı yoktur. Bana sadece bu emrolundu ve ben müslümanların ilkiyim.', 8, 149, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(953, 164, 6, 'En’âm', 'قُلْ اَغَيْرَ اللّٰهِ اَبْغ۪ي رَباًّ وَهُوَ رَبُّ كُلِّ شَيْءٍۜ وَلَا تَكْسِبُ كُلُّ نَفْسٍ اِلَّا عَلَيْهَاۚ وَلَا تَزِرُ وَازِرَةٌ وِزْرَ اُخْرٰىۚ ثُمَّ اِلٰى رَبِّكُمْ مَرْجِعُكُمْ فَيُنَبِّئُكُمْ بِمَا كُنْتُمْ ف۪يهِ تَخْتَلِفُونَ', 'De ki: Allah her şeyin Rabbi iken ben ondan başka Rab mı arayacağım? Herkesin kazanacağı yalnız kendisine aittir. Hiçbir suçlu başkasının suçunu yüklenmez. Sonunda dönüşünüz Rabbinizedir. Ve O, uyuşmazlığa düştüğünüz gerçeği size haber verecektir.', 8, 149, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(954, 165, 6, 'En’âm', 'وَهُوَ الَّذ۪ي جَعَلَكُمْ خَلَٓائِفَ الْاَرْضِ وَرَفَعَ بَعْضَكُمْ فَوْقَ بَعْضٍ دَرَجَاتٍ لِيَبْلُوَكُمْ ف۪ي مَٓا اٰتٰيكُمْۜ اِنَّ رَبَّكَ سَر۪يعُ الْعِقَابِۘ وَاِنَّهُ لَغَفُورٌ رَح۪يمٌ', 'Sizi yeryüzünün halifeleri kılan, size verdiği (nimetler) hususunda sizi denemek için kiminizi kiminizden derecelerle üstün kılan O´dur. Şüphesiz Rabbin, cezası çabuk olandır ve gerçekten O, bağışlayan merhamet edendir.', 8, 149, 165, 12418, 'Ehli hayvanlar', 'Mekke', 55),
(955, 1, 7, 'A\'raf', 'الٓمٓصٓۜ', 'Elif. Lâm. Mîm. Sâd.', 8, 150, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(956, 2, 7, 'A\'raf', 'كِتَابٌ اُنْزِلَ اِلَيْكَ فَلَا يَكُنْ ف۪ي صَدْرِكَ حَرَجٌ مِنْهُ لِتُنْذِرَ بِه۪ وَذِكْرٰى لِلْمُؤْمِن۪ينَ', '(Bu), kendisiyle insanları uyarman, inananlara öğüt vermen için sana indirilen bir kitaptır. Artık bu hususta kalbinde bir şüphe olmasın.', 8, 150, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(957, 3, 7, 'A\'raf', 'اِتَّبِعُوا مَٓا اُنْزِلَ اِلَيْكُمْ مِنْ رَبِّكُمْ وَلَا تَتَّبِعُوا مِنْ دُونِه۪ٓ اَوْلِيَٓاءَۜ قَل۪يلاً مَا تَذَكَّرُونَ', 'Rabbinizden size indirilene (Kur´an´a) uyun. O´nu bırakıp da başka dostların peşlerinden gitmeyin. Ne kadar da az öğüt alıyorsunuz!', 8, 150, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(958, 4, 7, 'A\'raf', 'وَكَمْ مِنْ قَرْيَةٍ اَهْلَكْنَاهَا فَجَٓاءَهَا بَأْسُنَا بَيَاتاً اَوْ هُمْ قَٓائِلُونَ', 'Nice memleketler var ki biz onları helâk ettik. Azabımız onlara geceleyin yahut gündüz istirahat ederlerken geldi.', 8, 150, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(959, 5, 7, 'A\'raf', 'فَمَا كَانَ دَعْوٰيهُمْ اِذْ جَٓاءَهُمْ بَأْسُنَٓا اِلَّٓا اَنْ قَالُٓوا اِنَّا كُنَّا ظَالِم۪ينَ', 'Azabımız onlara geldiğinde çağırışları, «Biz gerçekten zalim kişilermişiz» demelerinden başka bir şey olmadı.', 8, 150, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(960, 6, 7, 'A\'raf', 'فَلَنَسْـَٔلَنَّ الَّذ۪ينَ اُرْسِلَ اِلَيْهِمْ وَلَنَسْـَٔلَنَّ الْمُرْسَل۪ينَۙ', 'Elbette kendilerine peygamber gönderilen kimseleri de, gönderilen peygamberleri de mutlaka sorguya çekeceğiz!', 8, 150, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(961, 7, 7, 'A\'raf', 'فَلَنَقُصَّنَّ عَلَيْهِمْ بِعِلْمٍ وَمَا كُنَّا غَٓائِب۪ينَ', 'Ve onlara (olup bitenleri) tam bir bilgi ile mutlaka anlatacağız. Biz, onlardan uzak değiliz.', 8, 150, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(962, 8, 7, 'A\'raf', 'وَالْوَزْنُ يَوْمَئِذٍۨ الْحَقُّۚ فَمَنْ ثَقُلَتْ مَوَاز۪ينُهُ فَاُو۬لٰٓئِكَ هُمُ الْمُفْلِحُونَ', 'O gün tartı haktır. Kimin (sevap) tartıları ağır gelirse, işte onlar kurtuluşa erenlerdir.', 8, 150, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(963, 9, 7, 'A\'raf', 'وَمَنْ خَفَّتْ مَوَاز۪ينُهُ فَاُو۬لٰٓئِكَ الَّذ۪ينَ خَسِرُٓوا اَنْفُسَهُمْ بِمَا كَانُوا بِاٰيَاتِنَا يَظْلِمُونَ', 'Kimin de tartıları hafif gelirse, işte onlar, âyetlerimize karşı haksızlık ettiklerinden dolayı kendilerini ziyana sokanlardır.', 8, 150, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(964, 10, 7, 'A\'raf', 'وَلَقَدْ مَكَّنَّاكُمْ فِي الْاَرْضِ وَجَعَلْنَا لَكُمْ ف۪يهَا مَعَايِشَۜ قَل۪يلاً مَا تَشْكُرُونَ۟', 'Doğrusu biz sizi yeryüzüne yerleştirdik ve orada size geçim vasıtaları verdik. Ne kadar da az şükrediyorsunuz!', 8, 150, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(965, 11, 7, 'A\'raf', 'وَلَقَدْ خَلَقْنَاكُمْ ثُمَّ صَوَّرْنَاكُمْ ثُمَّ قُلْنَا لِلْمَلٰٓئِكَةِ اسْجُدُوا لِاٰدَمَۗ فَسَجَدُٓوا اِلَّٓا اِبْل۪يسَۜ لَمْ يَكُنْ مِنَ السَّاجِد۪ينَ', 'Andolsun sizi yarattık, sonra size şekil verdik, sonra da meleklere, Âdem´e secde edin! diye emrettik. İblis´in dışındakiler secde ettiler. O secde edenlerden olmadı.', 8, 150, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(966, 12, 7, 'A\'raf', 'قَالَ مَا مَنَعَكَ اَلَّا تَسْجُدَ اِذْ اَمَرْتُكَۜ قَالَ اَنَا۬ خَيْرٌ مِنْهُۚ خَلَقْتَن۪ي مِنْ نَارٍ وَخَلَقْتَهُ مِنْ ط۪ينٍ', 'Allah buyurdu: Ben sana emretmişken seni secde etmekten alıkoyan nedir? (İblis): Ben ondan daha üstünüm. Çünkü beni ateşten yarattın, onu çamurdan yarattın, dedi.', 8, 151, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(967, 13, 7, 'A\'raf', 'قَالَ فَاهْبِطْ مِنْهَا فَمَا يَكُونُ لَكَ اَنْ تَتَكَبَّرَ ف۪يهَا فَاخْرُجْ اِنَّكَ مِنَ الصَّاغِر۪ينَ', 'Allah: Öyle ise, «İn oradan!» Orada büyüklük taslamak senin haddin değildir. Çık! çünkü sen aşağılıklardansın! buyurdu.', 8, 151, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(968, 14, 7, 'A\'raf', 'قَالَ اَنْظِرْن۪ٓي اِلٰى يَوْمِ يُبْعَثُونَ', 'İblis: Bana, (insanların) tekrar dirilecekleri güne kadar mühlet ver, dedi.', 8, 151, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(969, 15, 7, 'A\'raf', 'قَالَ اِنَّكَ مِنَ الْمُنْظَر۪ينَ', 'Allah: Haydi, sen mühlet verilenlerdensin, buyurdu.', 8, 151, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(970, 16, 7, 'A\'raf', 'قَالَ فَبِمَٓا اَغْوَيْتَن۪ي لَاَقْعُدَنَّ لَهُمْ صِرَاطَكَ الْمُسْتَق۪يمَۙ', 'İblis dedi ki: Öyle ise beni azdırmana karşılık, and içerim ki, ben de onları saptırmak için senin doğru yolunun üstüne oturacağım.', 8, 151, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(971, 17, 7, 'A\'raf', 'ثُمَّ لَاٰتِيَنَّهُمْ مِنْ بَيْنِ اَيْد۪يهِمْ وَمِنْ خَلْفِهِمْ وَعَنْ اَيْمَانِهِمْ وَعَنْ شَمَٓائِلِهِمْۜ وَلَا تَجِدُ اَكْثَرَهُمْ شَاكِر۪ينَ', '«Sonra elbette onlara önlerinden, arkalarından, sağlarından, sollarından sokulacağım ve sen, onların çoklarını şükredenlerden bulmayacaksın!» dedi.', 8, 151, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(972, 18, 7, 'A\'raf', 'قَالَ اخْرُجْ مِنْهَا مَذْؤُ۫ماً مَدْحُوراًۜ لَمَنْ تَبِعَكَ مِنْهُمْ لَاَمْلَـَٔنَّ جَهَنَّمَ مِنْكُمْ اَجْمَع۪ينَ', 'Allah buyurdu: Haydi, yerilmiş ve kovulmuş olarak oradan çık! Andolsun ki, onlardan kim sana uyarsa, sizin hepinizi cehenneme dolduracağım!', 8, 151, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(973, 19, 7, 'A\'raf', 'وَيَٓا اٰدَمُ اسْكُنْ اَنْتَ وَزَوْجُكَ الْجَنَّةَ فَكُلَا مِنْ حَيْثُ شِئْتُمَا وَلَا تَقْرَبَا هٰذِهِ الشَّجَرَةَ فَتَكُونَا مِنَ الظَّالِم۪ينَ', '(Allah buyurdu ki): Ey Âdem! Sen ve eşin cennette yerleşip dilediğiniz yerden yeyin. Ancak şu ağaca yaklaşmayın! Sonra zalimlerden olursunuz.', 8, 151, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(974, 20, 7, 'A\'raf', 'فَوَسْوَسَ لَهُمَا الشَّيْطَانُ لِيُبْدِيَ لَهُمَا مَا وُ۫رِيَ عَنْهُمَا مِنْ سَوْاٰتِهِمَا وَقَالَ مَا نَهٰيكُمَا رَبُّكُمَا عَنْ هٰذِهِ الشَّجَرَةِ اِلَّٓا اَنْ تَكُونَا مَلَكَيْنِ اَوْ تَكُونَا مِنَ الْخَالِد۪ينَ', 'Derken şeytan, birbirine kapalı ayıp yerlerini kendilerine göstermek için onlara vesvese verdi ve: Rabbiniz size bu ağacı sırf melek olursunuz veya ebedî kalanlardan olursunuz diye yasakladı, dedi.', 8, 151, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(975, 21, 7, 'A\'raf', 'وَقَاسَمَـهُمَٓا اِنّ۪ي لَكُمَا لَمِنَ النَّاصِح۪ينَۙ', 'Ve onlara: Ben gerçekten size öğüt verenlerdenim, diye yemin etti.', 8, 151, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(976, 22, 7, 'A\'raf', 'فَدَلّٰيهُمَا بِغُرُورٍۚ فَلَمَّا ذَاقَا الشَّجَرَةَ بَدَتْ لَهُمَا سَوْاٰتُهُمَا وَطَفِقَا يَخْصِفَانِ عَلَيْهِمَا مِنْ وَرَقِ الْجَنَّةِۜ وَنَادٰيهُمَا رَبُّهُمَٓا اَلَمْ اَنْهَكُمَا عَنْ تِلْكُمَا الشَّجَرَةِ وَاَقُلْ لَكُمَٓا اِنَّ الشَّيْطَانَ لَكُمَا عَدُوٌّ مُب۪ينٌ', 'Böylece onları hile ile aldattı. Ağacın meyvesini tattıklarında ayıp yerleri kendilerine göründü. Ve cennet yapraklarından üzerlerini örtmeye başladılar. Rableri onlara: Ben size o ağacı yasaklamadım mı ve şeytan size apaçık bir düşmandır, demedim mi? diye nidâ etti.', 8, 151, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(977, 23, 7, 'A\'raf', 'قَالَا رَبَّـنَا ظَلَمْنَٓا اَنْفُسَنَا وَاِنْ لَمْ تَغْفِرْ لَنَا وَتَرْحَمْنَا لَنَكُونَنَّ مِنَ الْخَاسِر۪ينَ', '(Âdem ile eşi) dediler ki: Ey Rabbimiz! Biz kendimize zulmettik. Eğer bizi bağışlamaz ve bize acımazsan mutlaka ziyan edenlerden oluruz.', 8, 152, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(978, 24, 7, 'A\'raf', 'قَالَ اهْبِطُوا بَعْضُكُمْ لِبَعْضٍ عَدُوٌّۚ وَلَكُمْ فِي الْاَرْضِ مُسْتَقَرٌّ وَمَتَاعٌ اِلٰى ح۪ينٍ', 'Allah: Birbirinize düşman olarak inin! Sizin için yeryüzünde bir süreye kadar yerleşme ve faydalanma vardır, buyurdu.', 8, 152, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(979, 25, 7, 'A\'raf', 'قَالَ ف۪يهَا تَحْيَوْنَ وَف۪يهَا تَمُوتُونَ وَمِنْهَا تُخْرَجُونَ۟', '«Orada yaşayacaksınız, orada öleceksiniz ve oradan (diriltilip) çıkarılacaksınız» dedi.', 8, 152, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(980, 26, 7, 'A\'raf', 'يَا بَن۪ٓي اٰدَمَ قَدْ اَنْزَلْنَا عَلَيْكُمْ لِبَاساً يُوَار۪ي سَوْاٰتِكُمْ وَر۪يشاً۠ وَلِبَاسُ التَّقْوٰى ذٰلِكَ خَيْرٌۜ ذٰلِكَ مِنْ اٰيَاتِ اللّٰهِ لَعَلَّهُمْ يَذَّكَّرُونَ', 'Ey Âdem oğulları! Size ayıp yerlerinizi örtecek giysi, süslenecek elbise yarattık. Takvâ elbisesi... İşte o daha hayırlıdır. Bunlar Allah´ın âyetlerindendir. Belki düşünüp öğüt alırlar (diye onları indirdi).', 8, 152, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(981, 27, 7, 'A\'raf', 'يَا بَن۪ٓي اٰدَمَ لَا يَفْتِنَنَّكُمُ الشَّيْطَانُ كَمَٓا اَخْرَجَ اَبَوَيْكُمْ مِنَ الْجَنَّةِ يَنْزِعُ عَنْهُمَا لِبَاسَهُمَا لِيُرِيَهُمَا سَوْاٰتِهِمَاۜ اِنَّهُ يَرٰيكُمْ هُوَ وَقَب۪يلُهُ مِنْ حَيْثُ لَا تَرَوْنَهُمْۜ اِنَّا جَعَلْنَا الشَّيَاط۪ينَ اَوْلِيَٓاءَ لِلَّذ۪ينَ لَا يُؤْمِنُونَ', 'Ey Âdem oğulları! Şeytan, ana-babanızı, ayıp yerlerini kendilerine göstermek için elbiselerini soyarak cennetten çıkardığı gibi sizi de aldatmasın. Çünkü o ve yandaşları, sizin onları göremeyeceğiniz yerden sizi görürler. Şüphesiz biz şeytanları, inanmayanların dostları kıldık.', 8, 152, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(982, 28, 7, 'A\'raf', 'وَاِذَا فَعَلُوا فَاحِشَةً قَالُوا وَجَدْنَا عَلَيْهَٓا اٰبَٓاءَنَا وَاللّٰهُ اَمَرَنَا بِهَاۜ قُلْ اِنَّ اللّٰهَ لَا يَأْمُرُ بِالْفَحْشَٓاءِۜ اَتَقُولُونَ عَلَى اللّٰهِ مَا لَا تَعْلَمُونَ', 'Onlar bir kötülük yaptıkları zaman: «Babalarımızı bu yolda bulduk. Allah da bize bunu emretti» derler. De ki: Allah kötülüğü emretmez. Allah´a karşı bilmediğiniz şeyleri mi söylüyorsunuz?', 8, 152, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(983, 29, 7, 'A\'raf', 'قُلْ اَمَرَ رَبّ۪ي بِالْقِسْطِ۠ وَاَق۪يمُوا وُجُوهَكُمْ عِنْدَ كُلِّ مَسْجِدٍ وَادْعُوهُ مُخْلِص۪ينَ لَهُ الدّ۪ينَۜ كَمَا بَدَاَكُمْ تَعُودُونَۜ', 'De ki: Rabbim adaleti emretti. Her secde ettiğinizde yüzlerinizi O´na çevirin ve dini yalnız Allah´a has kılarak O´na yalvarın. İlkin sizi yarattığı gibi (yine O´na) döneceksiniz.', 8, 152, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(984, 30, 7, 'A\'raf', 'فَر۪يقاً هَدٰى وَفَر۪يقاً حَقَّ عَلَيْهِمُ الضَّلَالَةُۜ اِنَّهُمُ اتَّخَذُوا الشَّيَاط۪ينَ اَوْلِيَٓاءَ مِنْ دُونِ اللّٰهِ وَيَحْسَبُونَ اَنَّهُمْ مُهْتَدُونَ', 'O, bir gurubu doğru yola iletti, bir guruba da sapıklık müstehak oldu. Çünkü onlar Allah´ı bırakıp şeytanları kendilerine dost edindiler. Böyle iken kendilerinin doğru yolda olduklarını sanıyorlar.', 8, 152, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(985, 31, 7, 'A\'raf', 'يَا بَن۪ٓي اٰدَمَ خُذُوا ز۪ينَتَكُمْ عِنْدَ كُلِّ مَسْجِدٍ وَكُلُوا وَاشْرَبُوا وَلَا تُسْرِفُواۚ اِنَّهُ لَا يُحِبُّ الْمُسْرِف۪ينَ۟', 'Ey Âdem oğulları! Her secde edişinizde güzel elbiselerinizi giyin; yeyin, için, fakat israf etmeyin; çünkü Allah israf edenleri sevmez.', 8, 153, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(986, 32, 7, 'A\'raf', 'قُلْ مَنْ حَرَّمَ ز۪ينَةَ اللّٰهِ الَّت۪ٓي اَخْرَجَ لِعِبَادِه۪ وَالطَّيِّبَاتِ مِنَ الرِّزْقِۜ قُلْ هِيَ لِلَّذ۪ينَ اٰمَنُوا فِي الْحَيٰوةِ الدُّنْيَا خَالِصَةً يَوْمَ الْقِيٰمَةِۜ كَذٰلِكَ نُفَصِّلُ الْاٰيَاتِ لِقَوْمٍ يَعْلَمُونَ', 'De ki: Allah´ın kulları için yarattığı süsü ve temiz rızıkları kim haram kıldı? De ki: Onlar, dünya hayatında, özellikle kıyamet gününde müminlerindir. İşte bilen bir topluluk için âyetleri böyle açıklıyoruz.', 8, 153, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(987, 33, 7, 'A\'raf', 'قُلْ اِنَّمَا حَرَّمَ رَبِّيَ الْفَوَاحِشَ مَا ظَهَرَ مِنْهَا وَمَا بَطَنَ وَالْاِثْمَ وَالْبَغْيَ بِغَيْرِ الْحَقِّ وَاَنْ تُشْرِكُوا بِاللّٰهِ مَا لَمْ يُنَزِّلْ بِه۪ سُلْطَاناً وَاَنْ تَقُولُوا عَلَى اللّٰهِ مَا لَا تَعْلَمُونَ', 'De ki: Rabbim ancak açık ve gizli kötülükleri, günahı ve haksız yere sınırı aşmayı, hakkında hiçbir delil indirmediği bir şeyi, Allah´a ortak koşmanızı ve Allah hakkında bilmediğiniz şeyleri söylemenizi haram kılmıştır.', 8, 153, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(988, 34, 7, 'A\'raf', 'وَلِكُلِّ اُمَّةٍ اَجَلٌۚ فَاِذَا جَٓاءَ اَجَلُهُمْ لَا يَسْتَأْخِرُونَ سَاعَةً وَلَا يَسْتَقْدِمُونَ', 'Her ümmetin bir eceli vardır. Ecelleri gelince ne bir an geri kalırlar ne de bir an ileri gidebilirler.', 8, 153, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(989, 35, 7, 'A\'raf', 'يَا بَن۪ٓي اٰدَمَ اِمَّا يَأْتِيَنَّكُمْ رُسُلٌ مِنْكُمْ يَقُصُّونَ عَلَيْكُمْ اٰيَات۪يۙ فَمَنِ اتَّقٰى وَاَصْلَحَ فَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ', 'Ey Âdem oğulları! Size kendi içinizden âyetlerimi anlatacak peygamberler gelir de kim (onlara karşı gelmekten) sakınır ve kendini ıslah ederse, onlara korku yoktur ve onlar üzülmeyeceklerdir.', 8, 153, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(990, 36, 7, 'A\'raf', 'وَالَّذ۪ينَ كَذَّبُوا بِاٰيَاتِنَا وَاسْتَكْبَرُوا عَنْهَٓا اُو۬لٰٓئِكَ اَصْحَابُ النَّارِۚ هُمْ ف۪يهَا خَالِدُونَ', 'Âyetlerimizi yalanlayanlar ve büyüklenip onlardan yüz çevirenler var ya, işte onlar ateş ehlidir. Onlar orada ebedî kalacaklardır.', 8, 153, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(991, 37, 7, 'A\'raf', 'فَمَنْ اَظْلَمُ مِمَّنِ افْتَرٰى عَلَى اللّٰهِ كَذِباً اَوْ كَذَّبَ بِاٰيَاتِه۪ۜ اُو۬لٰٓئِكَ يَنَالُهُمْ نَص۪يبُهُمْ مِنَ الْكِتَابِۜ حَتّٰٓى اِذَا جَٓاءَتْهُمْ رُسُلُنَا يَتَوَفَّوْنَهُمْۙ قَالُٓوا اَيْنَ مَا كُنْتُمْ تَدْعُونَ مِنْ دُونِ اللّٰهِۜ قَالُوا ضَلُّوا عَنَّا وَشَهِدُوا عَلٰٓى اَنْفُسِهِمْ اَنَّهُمْ كَانُوا كَافِر۪ينَ', 'Allah´a iftira eden ya da O´nun âyetlerini yalanlayandan daha zalim kimdir! Onların kitaptaki nasipleri kendilerine erişecektir. Sonunda elçilerimiz (melekler) gelip canlarını alırken «Allah´ı bırakıp da tapmakta olduğunuz tanrılar nerede?» derler. (Onlar da) «Bizden sıvışıp gittiler» derler. Ve kâfir olduklarına dair kendi aleyhlerine şahitlik ederler.', 8, 153, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(992, 38, 7, 'A\'raf', 'قَالَ ادْخُلُوا ف۪ٓي اُمَمٍ قَدْ خَلَتْ مِنْ قَبْلِكُمْ مِنَ الْجِنِّ وَالْاِنْسِ فِي النَّارِۜ كُلَّمَا دَخَلَتْ اُمَّةٌ لَعَنَتْ اُخْتَهَاۜ حَتّٰٓى اِذَا ادَّارَكُوا ف۪يهَا جَم۪يعاًۙ قَالَتْ اُخْرٰيهُمْ لِاُو۫لٰيهُمْ رَبَّنَا هٰٓؤُ۬لَٓاءِ اَضَلُّونَا فَاٰتِهِمْ عَذَاباً ضِعْفاً مِنَ النَّارِۜ قَالَ لِكُلٍّ ضِعْفٌ وَلٰكِنْ لَا تَعْلَمُونَ', 'Allah buyuracak ki: «Sizden önce geçmiş cin ve insan toplulukları arasında siz de ateşe girin!» Her ümmet girdikçe yoldaşlarına lânet edecekler. Hepsi birbiri ardından orada (cehennemde) toplanınca, sonrakiler öncekiler için, «Ey Rabbimiz! Bizi işte bunlar saptırdılar! Onun için onlara ateşten bir kat daha fazla azap ver!» diyecekler. Allah da: Zaten herkes için bir kat daha fazla azap vardır, fakat siz bilmezsiniz, diyecektir.', 8, 154, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(993, 39, 7, 'A\'raf', 'وَقَالَتْ اُو۫لٰيهُمْ لِاُخْرٰيهُمْ فَمَا كَانَ لَكُمْ عَلَيْنَا مِنْ فَضْلٍ فَذُوقُوا الْعَذَابَ بِمَا كُنْتُمْ تَكْسِبُونَ۟', 'Öncekiler de sonrakilere derler ki: Sizin bize bir üstünlüğünüz yok. O halde siz de yaptıklarınıza karşılık azabı tadın!', 8, 154, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(994, 40, 7, 'A\'raf', 'اِنَّ الَّذ۪ينَ كَذَّبُوا بِاٰيَاتِنَا وَاسْتَكْبَرُوا عَنْهَا لَا تُفَتَّحُ لَهُمْ اَبْوَابُ السَّمَٓاءِ وَلَا يَدْخُلُونَ الْجَنَّةَ حَتّٰى يَلِجَ الْجَمَلُ ف۪ي سَمِّ الْخِيَاطِۜ وَكَذٰلِكَ نَجْزِي الْمُجْرِم۪ينَ', 'Bizim âyetlerimizi yalanlayıp da onlara karşı kibirlenmek isteyenler var ya, işte onlara gök kapıları açılmayacak ve onlar, deve iğne deliğine girinceye kadar cennete giremiyeceklerdir! Suçluları işte böyle cezalandırırız!', 8, 154, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(995, 41, 7, 'A\'raf', 'لَهُمْ مِنْ جَهَنَّمَ مِهَادٌ وَمِنْ فَوْقِهِمْ غَوَاشٍۜ وَكَذٰلِكَ نَجْزِي الظَّالِم۪ينَ', 'Onlar için cehennem ateşinden döşekler, üstlerine de örtüler vardır. İşte zalimleri böyle cezalandırırız!', 8, 154, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(996, 42, 7, 'A\'raf', 'وَالَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ لَا نُكَلِّفُ نَفْساً اِلَّا وُسْعَهَاۘ اُو۬لٰٓئِكَ اَصْحَابُ الْجَنَّةِۚ هُمْ ف۪يهَا خَالِدُونَ', 'İnanıp da iyi işler yapanlara gelince -ki hiç kimseye gücünün üstünde bir vazife yüklemeyiz- işte onlar, cennet ehlidir. Orada onlar ebedî kalacaklar.', 8, 154, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(997, 43, 7, 'A\'raf', 'وَنَزَعْنَا مَا ف۪ي صُدُورِهِمْ مِنْ غِلٍّ تَجْر۪ي مِنْ تَحْتِهِمُ الْاَنْهَارُۚ وَقَالُوا الْحَمْدُ لِلّٰهِ الَّذ۪ي هَدٰينَا لِهٰذَا وَمَا كُنَّا لِنَهْتَدِيَ لَوْلَٓا اَنْ هَدٰينَا اللّٰهُۚ لَقَدْ جَٓاءَتْ رُسُلُ رَبِّنَا بِالْحَقِّۜ وَنُودُٓوا اَنْ تِلْكُمُ الْجَنَّةُ اُو۫رِثْتُمُوهَا بِمَا كُنْتُمْ تَعْمَلُونَ', '(Cennette) onların altlarından ırmaklar akarken, kalplerinde kinden ne varsa hepsini çıkarıp atarız. Ve onlar derler ki: «Hidayetiyle bizi (bu nimete) kavuşturan Allah´a hamdolsun! Allah bizi doğru yola iletmeseydi kendiliğimizden doğru yolu bulacak değildik. Hakikaten Rabbimizin elçileri gerçeği getirmişler.» Onlara: İşte size cennet; yapmış olduğunuz iyi amellere karşılık ona vâris kılındınız diye seslenilir.', 8, 154, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(998, 44, 7, 'A\'raf', 'وَنَادٰٓى اَصْحَابُ الْجَنَّةِ اَصْحَابَ النَّارِ اَنْ قَدْ وَجَدْنَا مَا وَعَدَنَا رَبُّنَا حَقاًّ فَهَلْ وَجَدْتُمْ مَا وَعَدَ رَبُّكُمْ حَقاًّۜ قَالُوا نَعَمْۚ فَاَذَّنَ مُؤَذِّنٌ بَيْنَهُمْ اَنْ لَعْنَةُ اللّٰهِ عَلَى الظَّالِم۪ينَۙ', 'Cennet ehli cehennem ehline: Biz Rabbimizin bize vadettiğini gerçek bulduk, siz de Rabbinizin size vadettiğini gerçek buldunuz mu? diye seslenir. «Evet!» derler. Ve aralarından bir çağrıcı, Allah´ın lâneti zalimlerin üzerine olsun! diye bağırır.', 8, 155, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(999, 45, 7, 'A\'raf', 'اَلَّذ۪ينَ يَصُدُّونَ عَنْ سَب۪يلِ اللّٰهِ وَيَبْغُونَهَا عِوَجاًۚ وَهُمْ بِالْاٰخِرَةِ كَافِرُونَۜ', 'Onlar, Allah yolundan alıkoyan ve onu eğip bükmek isteyen zalimlerdir. Onlar ahireti de inkâr edenlerdir.', 8, 155, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1000, 46, 7, 'A\'raf', 'وَبَيْنَـهُمَا حِجَابٌۚ وَعَلَى الْاَعْرَافِ رِجَالٌ يَعْرِفُونَ كُلاًّ بِس۪يمٰيهُمْۚ وَنَادَوْا اَصْحَابَ الْجَنَّةِ اَنْ سَلَامٌ عَلَيْكُمْ لَمْ يَدْخُلُوهَا وَهُمْ يَطْمَعُونَ', 'İki taraf (cennetlikler ve cehennemlikler) arasında bir perde ve A´râf üzerinde de herkesi simalarından tanıyan adamlar vardır ki, bunlar henüz cennete giremedikleri halde (girmeyi) umarak cennet ehline: «Selâm size!» diye seslenirler.', 8, 155, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1001, 47, 7, 'A\'raf', 'وَاِذَا صُرِفَتْ اَبْصَارُهُمْ تِلْقَٓاءَ اَصْحَابِ النَّارِۙ قَالُوا رَبَّنَا لَا تَجْعَلْنَا مَعَ الْقَوْمِ الظَّالِم۪ينَ۟', 'Gözleri cehennem ehli tarafına döndürülünce de: Ey Rabbimiz! Bizi zalimler topluluğu ile beraber bulundurma! derler.', 8, 155, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1002, 48, 7, 'A\'raf', 'وَنَادٰٓى اَصْحَابُ الْاَعْرَافِ رِجَالاً يَعْرِفُونَهُمْ بِس۪يمٰيهُمْ قَالُوا مَٓا اَغْنٰى عَنْكُمْ جَمْعُكُمْ وَمَا كُنْتُمْ تَسْتَكْبِرُونَ', '(Yine) A´râf ehli simalarından tanıdıkları birtakım adamlara seslenerek derler ki: «Ne çokluğunuz ne de taslamakta olduğunuz büyüklük size hiçbir yarar sağlamadı.', 8, 155, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1003, 49, 7, 'A\'raf', 'اَهٰٓؤُ۬لَٓاءِ الَّذ۪ينَ اَقْسَمْتُمْ لَا يَنَالُهُمُ اللّٰهُ بِرَحْمَةٍۜ اُدْخُلُوا الْجَنَّةَ لَا خَوْفٌ عَلَيْكُمْ وَلَٓا اَنْتُمْ تَحْزَنُونَ', 'Allah´ın, kendilerini hiçbir rahmete erdirmeyeceğine dair yemin ettiğiniz kimseler bunlar mı?» (ve cennet ehline dönerek): «Girin cennete; artık size korku yoktur ve siz üzülecek de değilsiniz» (derler).', 8, 155, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1004, 50, 7, 'A\'raf', 'وَنَادٰٓى اَصْحَابُ النَّارِ اَصْحَابَ الْجَنَّةِ اَنْ اَف۪يضُوا عَلَيْنَا مِنَ الْمَٓاءِ اَوْ مِمَّا رَزَقَكُمُ اللّٰهُۜ قَالُٓوا اِنَّ اللّٰهَ حَرَّمَهُمَا عَلَى الْكَافِر۪ينَۙ', 'Cehennem ehli, cennet ehline: Suyunuzdan veya Allah´ın size verdiği rızıktan biraz da bize verin! diye seslenirler. Onlar da: Allah bunları kâfirlere haram kılmıştır, derler.', 8, 155, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1005, 51, 7, 'A\'raf', 'اَلَّذ۪ينَ اتَّخَذُوا د۪ينَهُمْ لَهْواً وَلَعِباً وَغَرَّتْهُمُ الْحَيٰوةُ الدُّنْيَاۚ فَالْيَوْمَ نَنْسٰيهُمْ كَمَا نَسُوا لِقَٓاءَ يَوْمِهِمْ هٰذَاۙ وَمَا كَانُوا بِاٰيَاتِنَا يَجْحَدُونَ', 'O kâfirler ki, dinlerini bir eğlence ve oyun edindiler de dünya hayatı onları aldattı. Onlar, bu günleri ile karşılaşacaklarını unuttukları ve âyetlerimizi bile bile inkâr ettikleri gibi biz de bugün onları unuturuz.', 8, 155, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1006, 52, 7, 'A\'raf', 'وَلَقَدْ جِئْنَاهُمْ بِكِتَابٍ فَصَّلْنَاهُ عَلٰى عِلْمٍ هُدًى وَرَحْمَةً لِقَوْمٍ يُؤْمِنُونَ', 'Gerçekten onlara, inanan bir toplum için yol gösterici ve rahmet olarak, ilim üzere açıkladığımız bir kitap getirdik.', 8, 156, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1007, 53, 7, 'A\'raf', 'هَلْ يَنْظُرُونَ اِلَّا تَأْو۪يلَهُۜ يَوْمَ يَأْت۪ي تَأْو۪يلُهُ يَقُولُ الَّذ۪ينَ نَسُوهُ مِنْ قَبْلُ قَدْ جَٓاءَتْ رُسُلُ رَبِّنَا بِالْحَقِّۚ فَهَلْ لَنَا مِنْ شُفَعَٓاءَ فَيَشْفَعُوا لَـنَٓا اَوْ نُرَدُّ فَنَعْمَلَ غَيْرَ الَّذ۪ي كُنَّا نَعْمَلُۜ قَدْ خَسِرُٓوا اَنْفُسَهُمْ وَضَلَّ عَنْهُمْ مَا كَانُوا يَفْتَرُونَ۟', '(Fakat onlar), Onun tevilinden başka bir şey beklemiyorlar. Tevili geldiği (haber verdiği şeyler ortaya çıktığı) gün, önceden onu unutmuş olanlar derler ki: Doğrusu Rabbimizin elçileri gerçeği getirmişler. Şimdi bizim şefaatçılarımız var mı ki bize şefaat etsinler veya (dünyaya) geri döndürülmemiz mümkün mü ki, yapmış olduğumuz amellerden başkasını yapalım? Onlar cidden kendilerine yazık ettiler ve uydurdukları şeyler (putlar) da kendilerinden kaybolup gitti.', 8, 156, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1008, 54, 7, 'A\'raf', 'اِنَّ رَبَّكُمُ اللّٰهُ الَّذ۪ي خَلَقَ السَّمٰوَاتِ وَالْاَرْضَ ف۪ي سِتَّةِ اَيَّامٍ ثُمَّ اسْتَوٰى عَلَى الْعَرْشِ يُغْشِي الَّيْلَ النَّهَارَ يَطْلُبُهُ حَث۪يثاًۙ وَالشَّمْسَ وَالْقَمَرَ وَالنُّجُومَ مُسَخَّرَاتٍ بِاَمْرِه۪ۜ اَلَا لَهُ الْخَلْقُ وَالْاَمْرُۜ تَبَارَكَ اللّٰهُ رَبُّ الْعَالَم۪ينَ', 'Şüphesiz ki Rabbiniz, gökleri ve yeri altı günde yaratan, sonra Arş´a istivâ eden, geceyi, durmadan kendisini kovalayan gündüze bürüyüp örten; güneşi, ayı ve yıldızları emrine boyun eğmiş durumda yaratan Allah´tır. Bilesiniz ki, yaratmak da emretmek de O´na mahsustur. Âlemlerin Rabbi Allah ne yücedir!', 8, 156, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1009, 55, 7, 'A\'raf', 'اُدْعُوا رَبَّكُمْ تَضَرُّعاً وَخُفْيَةًۜ اِنَّهُ لَا يُحِبُّ الْمُعْتَد۪ينَۚ', 'Rabbinize yalvara yakara ve gizlice dua edin. Bilesiniz ki O, haddi aşanları sevmez.', 8, 156, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1010, 56, 7, 'A\'raf', 'وَلَا تُفْسِدُوا فِي الْاَرْضِ بَعْدَ اِصْلَاحِهَا وَادْعُوهُ خَوْفاً وَطَمَعاًۜ اِنَّ رَحْمَتَ اللّٰهِ قَر۪يبٌ مِنَ الْمُحْسِن۪ينَ', 'Islah edilmesinden sonra yeryüzünde bozgunculuk yapmayın. Allah´a korkarak ve (rahmetini) umarak dua edin. Muhakkak ki iyilik edenlere Allah´ın rahmeti çok yakındır.', 8, 156, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1011, 57, 7, 'A\'raf', 'وَهُوَ الَّذ۪ي يُرْسِلُ الرِّيَاحَ بُشْراً بَيْنَ يَدَيْ رَحْمَتِه۪ۜ حَتّٰٓى اِذَٓا اَقَلَّتْ سَحَاباً ثِقَالاً سُقْنَاهُ لِبَلَدٍ مَيِّتٍ فَاَنْزَلْنَا بِهِ الْمَٓاءَ فَاَخْرَجْنَا بِه۪ مِنْ كُلِّ الثَّمَرَاتِۜ كَذٰلِكَ نُخْرِجُ الْمَوْتٰى لَعَلَّكُمْ تَذَكَّرُونَ', 'Rüzgârları rahmetinin önünde müjde olarak gönderen O´dur. Sonunda onlar (o rüzgârlar), ağır bulutları yüklenince onu ölü bir memlekete sevkederiz. Orada suyu indirir ve onunla türlü türlü meyveler çıkarırız. İşte ölüleri de böyle çıkaracağız. Her halde bundan ibret alırsınız.', 8, 156, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1012, 58, 7, 'A\'raf', 'وَالْبَلَدُ الطَّيِّبُ يَخْرُجُ نَبَاتُهُ بِاِذْنِ رَبِّه۪ۚ وَالَّذ۪ي خَبُثَ لَا يَخْرُجُ اِلَّا نَكِداًۜ كَذٰلِكَ نُصَرِّفُ الْاٰيَاتِ لِقَوْمٍ يَشْكُرُونَ۟', 'Rabbinin izniyle güzel memleketin bitkisi (güzel) çıkar; kötü olandan ise faydasız bitkiden başka birşey çıkmaz. İşte biz, şükreden bir kavim için âyetleri böyle açıklıyoruz.', 8, 157, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1013, 59, 7, 'A\'raf', 'لَقَدْ اَرْسَلْنَا نُوحاً اِلٰى قَوْمِه۪ فَقَالَ يَا قَوْمِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِنْ اِلٰهٍ غَيْرُهُۜ اِنّ۪ٓي اَخَافُ عَلَيْكُمْ عَذَابَ يَوْمٍ عَظ۪يمٍ', 'Andolsun ki Nuh´u elçi olarak kavmine gönderdik. Dedi ki: Ey kavmim! Allah´a kulluk edin, sizin ondan başka tanrınız yoktur. Doğrusu ben, üstünüze gelecek büyük bir günün azabından korkuyorum.', 8, 157, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1014, 60, 7, 'A\'raf', 'قَالَ الْمَلَأُ مِنْ قَوْمِه۪ٓ اِنَّا لَنَرٰيكَ ف۪ي ضَلَالٍ مُب۪ينٍ', 'Kavminden ileri gelenler dediler ki: Biz seni gerçekten apaçık bir sapıklık içinde görüyoruz!', 8, 157, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1015, 61, 7, 'A\'raf', 'قَالَ يَا قَوْمِ لَيْسَ ب۪ي ضَلَالَةٌ وَلٰكِنّ۪ي رَسُولٌ مِنْ رَبِّ الْعَالَم۪ينَ', 'Dedi ki: «Ey kavmim! Bende herhangi bir sapıklık yoktur; fakat ben, âlemlerin Rabbi tarafından gönderilmiş bir elçiyim.', 8, 157, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1016, 62, 7, 'A\'raf', 'اُبَلِّغُكُمْ رِسَالَاتِ رَبّ۪ي وَاَنْصَحُ لَكُمْ وَاَعْلَمُ مِنَ اللّٰهِ مَا لَا تَعْلَمُونَ', 'Size Rabbimin vahyettiklerini duyuruyorum, size öğüt veriyorum ve ben sizin bilmediklerinizi Allah´tan (gelen vahiy ile) biliyorum.', 8, 157, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1017, 63, 7, 'A\'raf', 'اَوَعَجِبْتُمْ اَنْ جَٓاءَكُمْ ذِكْرٌ مِنْ رَبِّكُمْ عَلٰى رَجُلٍ مِنْكُمْ لِيُنْذِرَكُمْ وَلِتَتَّقُوا وَلَعَلَّكُمْ تُرْحَمُونَ', '(Allah´ın azabından) sakınıp da rahmete nâil olmanız ümidiyle, içinizden sizi uyaracak bir adam vasıtasıyla size bir zikir (kitap) gelmesine şaştınız mı?»', 8, 157, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1018, 64, 7, 'A\'raf', 'فَكَذَّبُوهُ فَاَنْجَيْنَاهُ وَالَّذ۪ينَ مَعَهُ فِي الْفُلْكِ وَاَغْرَقْنَا الَّذ۪ينَ كَذَّبُوا بِاٰيَاتِنَاۜ اِنَّهُمْ كَانُوا قَوْماً عَم۪ينَ۟', 'Onu yalanladılar, biz de onu ve onunla beraber gemide bulunanları kurtardık, âyetlerimizi yalanlayanları da suda boğduk! Çünkü onlar kör bir kavim idiler.', 8, 157, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1019, 65, 7, 'A\'raf', 'وَاِلٰى عَادٍ اَخَاهُمْ هُوداًۜ قَالَ يَا قَوْمِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِنْ اِلٰهٍ غَيْرُهُۜ اَفَلَا تَتَّقُونَ', 'Âd kavmine de kardeşleri Hûd´u (gönderdik). O dedi ki: «Ey kavmim! Allah´a kulluk edin; sizin O´ndan başka tanrınız yoktur. Hâla sakınmayacak mısınız?»', 8, 157, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1020, 66, 7, 'A\'raf', 'قَالَ الْمَلَأُ الَّذ۪ينَ كَفَرُوا مِنْ قَوْمِه۪ٓ اِنَّا لَنَرٰيكَ ف۪ي سَفَاهَةٍ وَاِنَّا لَنَظُنُّكَ مِنَ الْكَاذِب۪ينَ', 'Kavminden ileri gelen kâfirler dediler ki: Biz seni kesinlikle bir beyinsizlik içinde görüyoruz ve gerçekten seni yalancılardan sanıyoruz.', 8, 157, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1021, 67, 7, 'A\'raf', 'قَالَ يَا قَوْمِ لَيْسَ ب۪ي سَفَاهَةٌ وَلٰكِنّ۪ي رَسُولٌ مِنْ رَبِّ الْعَالَم۪ينَ', '«Ey kavmim! dedi, ben beyinsiz değilim; fakat ben âlemlerin Rabbinin gönderdiği bir elçiyim.', 8, 157, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1022, 68, 7, 'A\'raf', 'اُبَلِّغُكُمْ رِسَالَاتِ رَبّ۪ي وَاَنَا۬ لَكُمْ نَاصِحٌ اَم۪ينٌ', 'Size Rabbimin vahyettiklerini duyuruyorum ve ben sizin için güvenilir bir öğütçüyüm.', 8, 158, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1023, 69, 7, 'A\'raf', 'اَوَعَجِبْتُمْ اَنْ جَٓاءَكُمْ ذِكْرٌ مِنْ رَبِّكُمْ عَلٰى رَجُلٍ مِنْكُمْ لِيُنْذِرَكُمْۜ وَاذْكُرُٓوا اِذْ جَعَلَكُمْ خُلَـفَٓاءَ مِنْ بَعْدِ قَوْمِ نُوحٍ وَزَادَكُمْ فِي الْخَلْقِ بَصْۣـطَةًۚ فَاذْكُرُٓوا اٰلَٓاءَ اللّٰهِ لَعَلَّكُمْ تُفْلِحُونَ', 'Sizi uyarmak için içinizden bir adam vasıtasıyla Rabbinizden size bir zikir (kitap) gelmesine şaştınız mı? Düşünün ki O sizi, Nuh kavminden sonra onların yerine getirdi ve yaratılışta sizi onlardan üstün kıldı. O halde Allah´ın nimetlerini hatırlayın ki kurtuluşa eresiniz.»', 8, 158, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1024, 70, 7, 'A\'raf', 'قَالُٓوا اَجِئْتَنَا لِنَعْبُدَ اللّٰهَ وَحْدَهُ وَنَذَرَ مَا كَانَ يَعْبُدُ اٰبَٓاؤُ۬نَاۚ فَأْتِنَا بِمَا تَعِدُنَٓا اِنْ كُنْتَ مِنَ الصَّادِق۪ينَ', 'Dediler ki: Sen bize tek Allah´a kulluk etmemiz ve atalarımızın tapmakta olduklarını bırakmamız için mi geldin? Eğer doğrulardan isen, bizi tehdit ettiğini (azabı) bize getir.', 8, 158, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1025, 71, 7, 'A\'raf', 'قَالَ قَدْ وَقَعَ عَلَيْكُمْ مِنْ رَبِّكُمْ رِجْسٌ وَغَضَبٌۜ اَتُجَادِلُونَن۪ي ف۪ٓي اَسْمَٓاءٍ سَمَّيْتُمُوهَٓا اَنْتُمْ وَاٰبَٓاؤُ۬كُمْ مَا نَزَّلَ اللّٰهُ بِهَا مِنْ سُلْطَانٍۜ فَانْتَظِرُٓوا اِنّ۪ي مَعَكُمْ مِنَ الْمُنْتَظِر۪ينَ', '(Hûd) dedi ki: «Üzerinize Rabbinizden bir azap ve bir hışım inmiştir. Haklarında Allah´ın hiçbir delil indirmediği, sadece sizin ve atalarınızın taktığı kuru isimler hususunda benimle tartışıyor musunuz? Bekleyin öyleyse, şüphesiz ben de sizinle beraber bekleyenlerdenim!»', 8, 158, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1026, 72, 7, 'A\'raf', 'فَاَنْجَيْنَاهُ وَالَّذ۪ينَ مَعَهُ بِرَحْمَةٍ مِنَّا وَقَطَعْنَا دَابِرَ الَّذ۪ينَ كَذَّبُوا بِاٰيَاتِنَا وَمَا كَانُوا مُؤْمِن۪ينَ۟', 'Onu ve onunla beraber olanları rahmetimizle kurtardık ve âyetlerimizi yalanlayıp da iman etmeyenlerin kökünü kestik.', 8, 158, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1027, 73, 7, 'A\'raf', 'وَاِلٰى ثَمُودَ اَخَاهُمْ صَـالِـحاًۢ قَالَ يَا قَوْمِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِنْ اِلٰهٍ غَيْرُهُۜ قَدْ جَٓاءَتْكُمْ بَيِّنَةٌ مِنْ رَبِّكُمْۜ هٰذِه۪ نَاقَةُ اللّٰهِ لَكُمْ اٰيَةً فَذَرُوهَا تَأْكُلْ ف۪ٓي اَرْضِ اللّٰهِ وَلَا تَمَسُّوهَا بِسُٓوءٍ فَيَأْخُذَكُمْ عَذَابٌ اَل۪يمٌ', 'Semûd kavmine de kardeşleri Salih´i (gönderdik). Dedi ki: Ey kavmim! Allah´a kulluk edin; sizin O´ndan başka tanrınız yoktur. Size Rabbinizden açık bir delil gelmiştir. O da, size bir mucize olarak Allah´ın şu devesidir. Onu bırakın, Allah´ın arzında yesin, (içsin); ona kötülük etmeyin; sonra sizi elem verici bir azap yakalar.', 8, 158, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1028, 74, 7, 'A\'raf', 'وَاذْ‌كُرُٓوا اِذْ جَعَلَكُمْ خُلَـفَٓاءَ مِنْ بَعْدِ عَادٍ وَبَوَّاَكُمْ فِي الْاَرْضِ تَتَّخِذُونَ مِنْ سُهُولِهَا قُصُوراً وَتَنْحِتُونَ الْجِبَالَ بُيُوتاًۚ فَاذْكُـرُٓوا اٰلَٓاءَ اللّٰهِ وَلَا تَعْثَوْا فِي الْاَرْضِ مُفْسِد۪ينَ', 'Düşünün ki, (Allah) Âd kavminden sonra yerlerine sizi getirdi. Ve yeryüzünde sizi yerleştirdi: Onun düzlüklerinde saraylar yapıyorsunuz, dağlarında evler yontuyorsunuz. Artık Allah´ın nimetlerini hatırlayın da yeryüzünde fesatçılar olarak karışıklık çıkarmayın.', 8, 159, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1029, 75, 7, 'A\'raf', 'قَالَ الْمَلَأُ الَّذ۪ينَ اسْتَكْبَرُوا مِنْ قَوْمِه۪ لِلَّذ۪ينَ اسْتُضْعِفُوا لِمَنْ اٰمَنَ مِنْهُمْ اَتَعْلَمُونَ اَنَّ صَالِحاً مُرْسَلٌ مِنْ رَبِّه۪ۜ قَالُٓوا اِنَّا بِمَٓا اُرْسِلَ بِه۪ مُؤْمِنُونَ', 'Kavminin ileri gelenlerinden büyüklük taslayanlar, içlerinden zayıf görülen inananlara dediler ki: Siz Salih´in, Rabbi tarafından gönderildiğini biliyor musunuz? Onlar da Şüphesiz biz onunla ne gönderilmişse ona inananlarız, dediler.', 8, 159, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1030, 76, 7, 'A\'raf', 'قَالَ الَّذ۪ينَ اسْتَكْبَرُٓوا اِنَّا بِالَّـذ۪ٓي اٰمَنْتُمْ بِه۪ كَافِرُونَ', 'Büyüklük taslayanlar dediler ki: «Biz de sizin inandığınızı inkâr edenleriz.»', 8, 159, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1031, 77, 7, 'A\'raf', 'فَعَقَرُوا النَّاقَةَ وَعَتَوْا عَنْ اَمْرِ رَبِّهِمْ وَقَالُوا يَا صَالِحُ ائْتِنَا بِمَا تَعِدُنَٓا اِنْ كُنْتَ مِنَ الْمُرْسَل۪ينَ', 'Derken o dişi deveyi ayaklarını keserek öldürdüler ve Rablerinin emrinden dışarı çıktılar da: Ey Salih! Eğer sen gerçekten peygamberlerdensen bizi tehdit ettiğin azabı bize getir, dediler.', 8, 159, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1032, 78, 7, 'A\'raf', 'فَاَخَذَتْهُمُ الرَّجْفَةُ فَاَصْبَحُوا ف۪ي دَارِهِمْ جَاثِم۪ينَ', 'Bunun üzerine onları o (gürültülü) sarsıntı yakaladı da yurtlarında diz üstü dona kaldılar.', 8, 159, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1033, 79, 7, 'A\'raf', 'فَتَوَلّٰى عَنْهُمْ وَقَالَ يَا قَوْمِ لَقَدْ اَبْلَغْتُكُمْ رِسَالَةَ رَبّ۪ي وَنَصَحْتُ لَـكُمْ وَلٰكِنْ لَا تُحِبُّونَ النَّاصِح۪ينَ', 'Salih o zaman onlardan yüz çevirdi ve şöyle dedi: Ey kavmim! Andolsun ki ben size Rabbimin vahyettiklerini tebliğ ettim ve size öğüt verdim; fakat siz öğüt verenleri sevmiyorsunuz.', 8, 159, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1034, 80, 7, 'A\'raf', 'وَلُوطاً اِذْ قَالَ لِقَوْمِه۪ٓ اَتَأْتُونَ الْفَاحِشَةَ مَا سَبَقَكُمْ بِهَا مِنْ اَحَدٍ مِنَ الْعَالَم۪ينَ', 'Lût´u da (peygamber gönderdik). Kavmine dedi ki: «Sizden önceki milletlerden hiçbirinin yapmadığı fuhuşu mu yapıyorsunuz?', 8, 159, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1035, 81, 7, 'A\'raf', 'اِنَّكُمْ لَتَأْتُونَ الرِّجَالَ شَهْوَةً مِنْ دُونِ النِّسَٓاءِۜ بَلْ اَنْتُمْ قَوْمٌ مُسْرِفُونَ', 'Çünkü siz, şehveti tatmin için kadınları bırakıp da şehvetle erkeklere yanaşıyorsunuz. Doğrusu siz taşkın bir milletsiniz.»', 8, 159, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1036, 82, 7, 'A\'raf', 'وَمَا كَانَ جَوَابَ قَوْمِه۪ٓ اِلَّٓا اَنْ قَالُٓوا اَخْرِجُوهُمْ مِنْ قَرْيَتِكُمْۚ اِنَّهُمْ اُنَاسٌ يَتَطَهَّرُونَ', 'Kavminin cevabı: Onları (Lût´u ve taraftarlarını) memleketinizden çıkarın; çünkü onlar fazla temizlenen insanlarmış! demelerinden başka bir şey olmadı.', 8, 160, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1037, 83, 7, 'A\'raf', 'فَاَنْجَيْنَاهُ وَاَهْلَهُٓ اِلَّا امْرَاَتَهُۘ كَانَتْ مِنَ الْغَابِر۪ينَ', 'Biz de onu ve karısından başka aile efradını kurtardık; çünkü karısı geride kalanlardan (kâfirlerden) idi.', 8, 160, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1038, 84, 7, 'A\'raf', 'وَاَمْطَرْنَا عَلَيْهِمْ مَطَراًۜ فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الْمُجْرِم۪ينَ۟', 'Ve üzerlerine (taş) yağmuru yağdırdık. Bak ki günahkârların sonu nasıl oldu!', 8, 160, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1039, 85, 7, 'A\'raf', 'وَاِلٰى مَدْيَنَ اَخَاهُمْ شُعَيْباًۜ قَالَ يَا قَوْمِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِنْ اِلٰهٍ غَيْرُهُۜ قَدْ جَٓاءَتْكُمْ بَيِّنَةٌ مِنْ رَبِّكُمْ فَاَوْفُوا الْكَيْلَ وَالْم۪يزَانَ وَلَا تَبْخَسُوا النَّاسَ اَشْيَٓاءَهُمْ وَلَا تُفْسِدُوا فِي الْاَرْضِ بَعْدَ اِصْلَاحِهَاۜ ذٰلِكُمْ خَيْرٌ لَكُمْ اِنْ كُنْتُمْ مُؤْمِن۪ينَۚ', 'Medyen´e de kardeşleri Şuayb´ı (gönderdik). Dedi ki: Ey kavmim! Allah´a kulluk edin, sizin ondan başka tanrınız yoktur. Size Rabbinizden açık bir delil gelmiştir; artık ölçüyü, tartıyı tam yapın, insanların eşyalarını eksik vermeyin. Düzeltilmesinden sonra yeryüzünde bozgunculuk yapmayın. Eğer inananlar iseniz bunlar sizin için daha hayırlıdır.', 8, 160, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1040, 86, 7, 'A\'raf', 'وَلَا تَقْعُدُوا بِكُلِّ صِرَاطٍ تُوعِدُونَ وَتَصُدُّونَ عَنْ سَب۪يلِ اللّٰهِ مَنْ اٰمَنَ بِه۪ وَتَبْغُونَهَا عِوَجاًۚ وَاذْكُرُٓوا اِذْ كُنْتُمْ قَل۪يلاً فَكَثَّرَكُمْۖ وَانْظُرُوا كَيْفَ كَانَ عَاقِبَةُ الْمُفْسِد۪ينَ', 'Tehdit ederek, inananları Allah yolundan alıkoyarak ve o yolu eğip bükmek isteyerek öyle her yolun başında oturmayın. Düşünün ki siz az idiniz de O sizi çoğalttı. Bakın ki, bozguncuların sonu nasıl olmuştur!', 8, 160, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1041, 87, 7, 'A\'raf', 'وَاِنْ كَانَ طَٓائِفَةٌ مِنْكُمْ اٰمَنُوا بِالَّـذ۪ٓي اُرْسِلْتُ بِه۪ وَطَٓائِفَةٌ لَمْ يُؤْمِنُوا فَاصْبِرُوا حَتّٰى يَحْكُمَ اللّٰهُ بَيْنَنَاۚ وَهُوَ خَيْرُ الْحَاكِم۪ينَ', 'Eğer içinizden bir gurup benimle gönderilene inanır, bir gurup da inanmazsa, Allah aranızda hükmedinceye kadar bekleyin. O hakimlerin en iyisidir.', 8, 160, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1042, 88, 7, 'A\'raf', 'قَالَ الْمَلَأُ الَّذ۪ينَ اسْتَكْبَرُوا مِنْ قَوْمِه۪ لَنُخْرِجَنَّكَ يَا شُعَيْبُ وَالَّذ۪ينَ اٰمَنُوا مَعَكَ مِنْ قَرْيَتِنَٓا اَوْ لَتَعُودُنَّ ف۪ي مِلَّتِنَاۜ قَالَ اَوَلَوْ كُنَّا كَارِه۪ينَ', 'Kavminden ileri gelen kibirliler dediler ki: «Ey Şuayb! Seni ve seninle beraber inananları memleketimizden kesinlikle çıkaracağız veya dinimize döneceksiniz» (Şuayb): İstemesek de mi? dedi.', 9, 161, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1043, 89, 7, 'A\'raf', 'قَدِ افْتَرَيْنَا عَلَى اللّٰهِ كَذِباً اِنْ عُدْنَا ف۪ي مِلَّتِكُمْ بَعْدَ اِذْ نَجّٰينَا اللّٰهُ مِنْهَاۜ وَمَا يَكُونُ لَـنَٓا اَنْ نَعُودَ ف۪يهَٓا اِلَّٓا اَنْ يَشَٓاءَ اللّٰهُ رَبُّنَاۜ وَسِعَ رَبُّنَا كُلَّ شَيْءٍ عِلْماًۜ عَلَى اللّٰهِ تَوَكَّلْنَاۜ رَبَّـنَا افْتَحْ بَيْنَنَا وَبَيْنَ قَوْمِنَا بِالْحَقِّ وَاَنْتَ خَيْرُ الْفَاتِح۪ينَ', 'Doğrusu Allah bizi ondan kurtardıktan sonra tekrar sizin dininize dönersek Allah´a karşı yalan uydurmuş oluruz. Rabbimiz Allah dilemiş başka, yoksa ona geri dönmemiz bizim için olacak şey değildir. Rabbimizin ilmi her şeyi kuşatmıştır. Biz sadece Allah´a dayanırız. Rabbimiz! Bizimle kavmimiz arasında adaletle hükmet! Sen hükmedenlerin en hayırlısısın.', 9, 161, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1044, 90, 7, 'A\'raf', 'وَقَالَ الْمَلَأُ الَّذ۪ينَ كَفَرُوا مِنْ قَوْمِه۪ لَئِنِ اتَّبَعْتُمْ شُعَيْباً اِنَّكُمْ اِذاً لَخَاسِرُونَ', 'Kavminden ileri gelen kâfirler dediler ki: Eğer Şuayb´e uyarsanız o takdirde siz mutlaka ziyana uğrarsınız.', 9, 161, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1045, 91, 7, 'A\'raf', 'فَاَخَذَتْهُمُ الرَّجْفَةُ فَاَصْبَحُوا ف۪ي دَارِهِمْ جَاثِم۪ينَۚۛ', 'Derken o şiddetli deprem onları yakalayıverdi de yurtlarında diz üstü donakaldılar.', 9, 161, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1046, 92, 7, 'A\'raf', 'اَلَّذ۪ينَ كَذَّبُوا شُعَيْباً كَاَنْ لَمْ يَغْنَوْا ف۪يهَاۚۛ اَلَّذ۪ينَ كَذَّبُوا شُعَيْباً كَانُوا هُمُ الْخَاسِر۪ينَ', 'Şuayb´ı yalanlayanlar sanki yurtlarında hiç oturmamış gibiydiler. Asıl ziyana uğrayanlar Şuayb´ı yalanlayanların kendileridir.', 9, 161, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1047, 93, 7, 'A\'raf', 'فَتَوَلّٰى عَنْهُمْ وَقَالَ يَا قَوْمِ لَقَدْ اَبْلَغْتُكُمْ رِسَالَاتِ رَبّ۪ي وَنَصَحْتُ لَكُمْۚ فَكَيْفَ اٰسٰى عَلٰى قَوْمٍ كَافِر۪ينَ۟', '(Şuayb), onlardan yüz çevirdi ve (içinden) dedi ki: «Ey kavmim! Ben size Rabbimin gönderdiği gerçekleri duyurdum ve size öğüt verdim. Artık kâfir bir kavme nasıl acırım!»', 9, 161, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1048, 94, 7, 'A\'raf', 'وَمَٓا اَرْسَلْنَا ف۪ي قَرْيَةٍ مِنْ نَبِيٍّ اِلَّٓا اَخَذْنَٓا اَهْلَهَا بِالْبَأْسَٓاءِ وَالضَّرَّٓاءِ لَعَلَّهُمْ يَضَّرَّعُونَ', 'Biz hangi ülkeye bir peygamber gönderdiysek, ora halkını, (peygambere baş kaldırdıklarından ötürü bize) yalvarıp yakarsınlar diye mutlaka yoksulluk ve darlıkla sıkmışızdır.', 9, 161, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1049, 95, 7, 'A\'raf', 'ثُمَّ بَدَّلْنَا مَكَانَ السَّيِّئَةِ الْحَسَنَةَ حَتّٰى عَفَوْا وَقَالُوا قَدْ مَسَّ اٰبَٓاءَنَا الضَّرَّٓاءُ وَالسَّرَّٓاءُ فَاَخَذْنَاهُمْ بَغْتَةً وَهُمْ لَا يَشْعُرُونَ', 'Sonra kötülüğü (darlığı) değiştirip yerine iyilik (bolluk) getirdik. Nihayet çoğaldılar ve: «Atalarımız da böyle sıkıntı ve sevinç yaşamışlardı» dediler. Biz de onları, kendileri farkına varmadan ansızın yakaladık.', 9, 161, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1050, 96, 7, 'A\'raf', 'وَلَوْ اَنَّ اَهْلَ الْقُرٰٓى اٰمَنُوا وَاتَّقَوْا لَفَتَحْنَا عَلَيْهِمْ بَرَكَاتٍ مِنَ السَّمَٓاءِ وَالْاَرْضِ وَلٰكِنْ كَذَّبُوا فَاَخَذْنَاهُمْ بِمَا كَانُوا يَكْسِبُونَ', 'O (peygamberlerin gönderildiği) ülkelerin halkı inansalar ve (günahtan) sakınsalardı, elbette onların üstüne gökten ve yerden nice bereket kapıları açardık, fakat yalanladılar, biz de ettikleri yüzünden onları yakalayıverdik.', 9, 162, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1051, 97, 7, 'A\'raf', 'اَفَاَمِنَ اَهْلُ الْقُرٰٓى اَنْ يَأْتِيَهُمْ بَأْسُنَا بَيَاتاً وَهُمْ نَٓائِمُونَۜ', 'Yoksa o ülkelerin halkı geceleyin uyurlarken kendilerine azabımızın gelmeyeceğinden emin mi oldular?', 9, 162, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1052, 98, 7, 'A\'raf', 'اَوَاَمِنَ اَهْلُ الْقُرٰٓى اَنْ يَأْتِيَهُمْ بَأْسُنَا ضُحًى وَهُمْ يَلْعَبُونَ', 'Ya da o ülkelerin halkı kuşluk vakti eğlenirlerken kendilerine azabımızın gelmeyeceğinden emin mi oldular?', 9, 162, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1053, 99, 7, 'A\'raf', 'اَفَاَمِنُوا مَكْرَ اللّٰهِۚ فَلَا يَأْمَنُ مَكْرَ اللّٰهِ اِلَّا الْقَوْمُ الْخَاسِرُونَ۟', 'Allah´ın azabından emin mi oldular? Fakat ziyana uğrayan topluluktan başkası, Allah´ın (böyle) mühlet vermesinden emin olamaz.', 9, 162, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1054, 100, 7, 'A\'raf', 'اَوَلَمْ يَهْدِ لِلَّذ۪ينَ يَرِثُونَ الْاَرْضَ مِنْ بَعْدِ اَهْلِهَٓا اَنْ لَوْ نَشَٓاءُ اَصَبْنَاهُمْ بِذُنُوبِهِمْۚ وَنَطْبَعُ عَلٰى قُلُوبِهِمْ فَهُمْ لَا يَسْمَعُونَ', 'Önceki sahiplerinden sonra yeryüzüne vâris olanlara hâla şu gerçek belli olmadı mı ki: Eğer biz dileseydik onları da günahlarından dolayı musibetlere uğratırdık! Biz onların kalplerini mühürleriz de onlar (gerçekleri) işitmezler.', 9, 162, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1055, 101, 7, 'A\'raf', 'تِلْكَ الْقُرٰى نَقُصُّ عَلَيْكَ مِنْ اَنْـبَٓائِهَاۚ وَلَقَدْ جَٓاءَتْهُمْ رُسُلُهُمْ بِالْبَيِّنَاتِۚ فَمَا كَانُوا لِيُؤْمِنُوا بِمَا كَذَّبُوا مِنْ قَبْلُۜ كَذٰلِكَ يَطْبَعُ اللّٰهُ عَلٰى قُلُوبِ الْكَافِر۪ينَ', 'İşte o ülkeler... Onların haberlerinden bir kısmını sana anlatıyoruz. Andolsun ki, peygamberleri onlara apaçık deliller getirmişlerdi. Fakat önceden yalanladıkları gerçeklere iman edecek değillerdi. İşte kâfirlerin kalplerini Allah böyle mühürler.', 9, 162, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1056, 102, 7, 'A\'raf', 'وَمَا وَجَدْنَا لِاَكْثَرِهِمْ مِنْ عَهْدٍۚ وَاِنْ وَجَدْنَٓا اَكْثَرَهُمْ لَفَاسِق۪ينَ', 'Onların çoğunda, sözünde durma diye bir şey bulamadık. Gerçek şu ki, onların çoğunu yoldan çıkmış bulduk.', 9, 162, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1057, 103, 7, 'A\'raf', 'ثُمَّ بَعَثْنَا مِنْ بَعْدِهِمْ مُوسٰى بِاٰيَاتِنَٓا اِلٰى فِرْعَوْنَ وَمَلَا۬ئِه۪ فَظَلَمُوا بِهَاۚ فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الْمُفْسِد۪ينَ', 'Sonra onların ardından Musa´yı mucizelerimizle Firavun ve kavmine gönderdik de o mucizeleri inkâr ettiler; ama, bak ki, fesatçıların sonu ne oldu!', 9, 162, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1058, 104, 7, 'A\'raf', 'وَقَالَ مُوسٰى يَا فِرْعَوْنُ اِنّ۪ي رَسُولٌ مِنْ رَبِّ الْعَالَم۪ينَۚ', 'Musa dedi ki: «Ey Firavun! Ben âlemlerin Rabbi tarafından gönderilmiş bir peygamberim.', 9, 162, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1059, 105, 7, 'A\'raf', 'حَق۪يقٌ عَلٰٓى اَنْ لَٓا اَقُولَ عَلَى اللّٰهِ اِلَّا الْحَقَّۜ قَدْ جِئْتُكُمْ بِبَيِّنَةٍ مِنْ رَبِّكُمْ فَاَرْسِلْ مَعِيَ بَن۪ٓي اِسْرَٓائ۪لَۜ', 'Allah hakkında gerçekten başkasını söylememek benim üzerime borçtur. Size Rabbinizden açık bir delil getirdim; artık İsrailoğullarını benimle bırak!»', 9, 163, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1060, 106, 7, 'A\'raf', 'قَالَ اِنْ كُنْتَ جِئْتَ بِاٰيَةٍ فَأْتِ بِهَٓا اِنْ كُنْتَ مِنَ الصَّادِق۪ينَ', '(Firavun) dedi ki: Eğer bir mucize getirdiysen ve gerçekten doğru söylüyorsan onu göster bakalım.', 9, 163, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1061, 107, 7, 'A\'raf', 'فَاَلْقٰى عَصَاهُ فَاِذَا هِيَ ثُعْبَانٌ مُب۪ينٌۚ', 'Bunun üzerine Musa asasını yere attı. O hemen apaçık bir ejderha oluverdi!', 9, 163, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1062, 108, 7, 'A\'raf', 'وَنَزَعَ يَدَهُ فَاِذَا هِيَ بَيْضَٓاءُ لِلنَّاظِر۪ينَ۟', 'Ve elini (cebinden) çıkardı. Birdenbire o da seyredenlere bembeyaz görünüverdi.', 9, 163, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1063, 109, 7, 'A\'raf', 'قَالَ الْمَلَأُ مِنْ قَوْمِ فِرْعَوْنَ اِنَّ هٰذَا لَسَاحِرٌ عَل۪يمٌۙ', '(109-110) Firavun´un kavminden ileri gelenler dediler ki: Bu çok bilgili bir sihirbazdır. Sizi yurdunuzdan çıkarmak istiyor. Ne buyurursunuz?', 9, 163, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1064, 110, 7, 'A\'raf', 'يُر۪يدُ اَنْ يُخْرِجَكُمْ مِنْ اَرْضِكُمْۚ فَمَاذَا تَأْمُرُونَ', '(109-110) Firavun´un kavminden ileri gelenler dediler ki: Bu çok bilgili bir sihirbazdır. Sizi yurdunuzdan çıkarmak istiyor. Ne buyurursunuz?', 9, 163, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1065, 111, 7, 'A\'raf', 'قَالُٓوا اَرْجِهْ وَاَخَاهُ وَاَرْسِلْ فِي الْمَدَٓائِنِ حَاشِر۪ينَۙ', '(111-112) Dediler ki: Onu da kardeşini de beklet; şehirlere toplayıcılar (memurlar) yolla. Bütün bilgili sihirbazları sana getirsinler.', 9, 163, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1066, 112, 7, 'A\'raf', 'يَأْتُوكَ بِكُلِّ سَاحِرٍ عَل۪يمٍ', '(111-112) Dediler ki: Onu da kardeşini de beklet; şehirlere toplayıcılar (memurlar) yolla. Bütün bilgili sihirbazları sana getirsinler.', 9, 163, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1067, 113, 7, 'A\'raf', 'وَجَٓاءَ السَّحَرَةُ فِرْعَوْنَ قَالُٓوا اِنَّ لَنَا لَاَجْراً اِنْ كُنَّا نَحْنُ الْغَالِب۪ينَ', 'Sihirbazlar Firavun´a geldi ve: Eğer üstün gelen biz olursak, bize kesin bir mükâfat var mı? dediler.', 9, 163, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1068, 114, 7, 'A\'raf', 'قَالَ نَعَمْ وَاِنَّكُمْ لَمِنَ الْمُقَرَّب۪ينَ', '(Firavun): Evet hem de siz mutlaka yakınlarımdan olacaksınız, dedi.', 9, 163, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1069, 115, 7, 'A\'raf', 'قَالُوا يَا مُوسٰٓى اِمَّٓا اَنْ تُلْقِيَ وَاِمَّٓا اَنْ نَكُونَ نَحْنُ الْمُلْق۪ينَ', '(Sihirbazlar), Ey Musa sen mi (önce) atacaksın, yoksa atanlar biz mi olalım? dediler.', 9, 163, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1070, 116, 7, 'A\'raf', 'قَالَ اَلْقُواۚ فَلَمَّٓا اَلْقَوْا سَحَرُٓوا اَعْيُنَ النَّاسِ وَاسْتَرْهَبُوهُمْ وَجَٓاؤُ۫ بِسِحْرٍ عَظ۪يمٍ', '«Siz atın» dedi. Onlar atınca, insanların gözlerini büyülediler, onları korkuttular ve büyük bir sihir gösterdiler.', 9, 163, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1071, 117, 7, 'A\'raf', 'وَاَوْحَيْنَٓا اِلٰى مُوسٰٓى اَنْ اَلْقِ عَصَاكَۚ فَاِذَا هِيَ تَلْقَفُ مَا يَأْفِكُونَۚ', 'Biz de Musa´ya, «Asanı at!» diye vahyettik. Bir de baktılar ki bu, onların uydurduklarını yakalayıp yutuyor.', 9, 163, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1072, 118, 7, 'A\'raf', 'فَوَقَعَ الْحَقُّ وَبَطَلَ مَا كَانُوا يَعْمَلُونَۚ', 'Böylece gerçek ortaya çıktı ve onların yapmakta oldukları yok olup gitti.', 9, 163, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1073, 119, 7, 'A\'raf', 'فَغُلِبُوا هُنَالِكَ وَانْقَلَبُوا صَاغِر۪ينَۚ', 'İşte Firavun ve kavmi, orada yenildi ve küçük düşerek geri döndüler.', 9, 163, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1074, 120, 7, 'A\'raf', 'وَاُلْقِيَ السَّحَرَةُ سَاجِد۪ينَۚ', 'Sihirbazlar ise secdeye kapandılar.', 9, 163, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1075, 121, 7, 'A\'raf', 'قَالُٓوا اٰمَنَّا بِرَبِّ الْعَالَم۪ينَۙ', '(121-122) «Musa ve Harun´un Rabbi olan âlemlerin Rabbine inandık» dediler.', 9, 164, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1076, 122, 7, 'A\'raf', 'رَبِّ مُوسٰى وَهٰرُونَ', '(121-122) «Musa ve Harun´un Rabbi olan âlemlerin Rabbine inandık» dediler.', 9, 164, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1077, 123, 7, 'A\'raf', 'قَالَ فِرْعَوْنُ اٰمَنْتُمْ بِه۪ قَبْلَ اَنْ اٰذَنَ لَكُمْۚ اِنَّ هٰذَا لَمَكْرٌ مَكَرْتُمُوهُ فِي الْمَد۪ينَةِ لِتُخْرِجُوا مِنْهَٓا اَهْلَهَاۚ فَسَوْفَ تَعْلَمُونَ', 'Firavun dedi ki: «Ben size izin vermeden ona iman mı ettiniz? Bu, hiç şüphesiz şehirde, halkını oradan çıkarmak için kurduğunuz bir tuzaktır. Ama yakında (başınıza gelecekleri) göreceksiniz!', 9, 164, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1078, 124, 7, 'A\'raf', 'لَاُقَطِّعَنَّ اَيْدِيَكُمْ وَاَرْجُلَكُمْ مِنْ خِلَافٍ ثُمَّ لَاُصَلِّبَنَّكُمْ اَجْمَع۪ينَ', 'Mutlaka ellerinizi ve ayaklarınızı çaprazlama keseceğim, sonra da hepinizi asacağım!»', 9, 164, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39);
INSERT INTO `Ayetler` (`id`, `AyetNo`, `SureNo`, `SureAdi`, `Metin`, `Mana`, `Cuzu`, `Sayfasi`, `AyetSayisi`, `HarfSayisi`, `AdAnlami`, `IndigiYer`, `InisSirasi`) VALUES
(1079, 125, 7, 'A\'raf', 'قَالُٓوا اِنَّٓا اِلٰى رَبِّنَا مُنْقَلِبُونَۚ', '(125-126) Onlar: Biz zaten Rabbimize döneceğiz. Sen sadece Rabbimizin âyetleri bize geldiğinde onlara inandığımız için bizden intikam alıyorsun. Ey Rabbimiz! Bize bol bol sabır ver, müslüman olarak canımızı al, dediler.', 9, 164, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1080, 126, 7, 'A\'raf', 'وَمَا تَنْقِمُ مِنَّٓا اِلَّٓا اَنْ اٰمَنَّا بِاٰيَاتِ رَبِّنَا لَمَّا جَٓاءَتْنَاۜ رَبَّـنَٓا اَفْرِغْ عَلَيْنَا صَبْراً وَتَوَفَّـنَا مُسْلِم۪ينَ۟', '(125-126) Onlar: Biz zaten Rabbimize döneceğiz. Sen sadece Rabbimizin âyetleri bize geldiğinde onlara inandığımız için bizden intikam alıyorsun. Ey Rabbimiz! Bize bol bol sabır ver, müslüman olarak canımızı al, dediler.', 9, 164, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1081, 127, 7, 'A\'raf', 'وَقَالَ الْمَلَأُ مِنْ قَوْمِ فِرْعَوْنَ اَتَذَرُ مُوسٰى وَقَوْمَهُ لِيُفْسِدُوا فِي الْاَرْضِ وَيَذَرَكَ وَاٰلِهَتَكَۜ قَالَ سَنُقَتِّلُ اَبْنَٓاءَهُمْ وَنَسْتَحْـي۪ نِسَٓاءَهُمْۚ وَاِنَّا فَوْقَهُمْ قَاهِرُونَ', 'Firavun´un kavminden ileri gelenler dediler ki: Musa´yı ve kavmini, seni ve tanrılarını bırakıp yeryüzünde bozgunculuk çıkarsınlar diye mi bırakacaksın? (Firavun): «Biz onların oğullarını öldürüp, kadınlarını sağ bırakacağız. Elbette biz onları ezecek üstünlükteyiz» dedi.', 9, 164, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1082, 128, 7, 'A\'raf', 'قَالَ مُوسٰى لِقَوْمِهِ اسْتَع۪ينُوا بِاللّٰهِ وَاصْبِرُواۚ اِنَّ الْاَرْضَ لِلّٰهِۚ يُورِثُهَا مَنْ يَشَٓاءُ مِنْ عِبَادِه۪ۜ وَالْعَاقِبَةُ لِلْمُتَّق۪ينَ', 'Musa kavmine dedi ki: «Allah´tan yardım isteyin ve sabredin. Şüphesiz ki yeryüzü Allah´ındır. Kullarından dilediğini ona vâris kılar. Sonuç (Allah´tan korkup günahtan) sakınanlarındır.»', 9, 164, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1083, 129, 7, 'A\'raf', 'قَالُٓوا اُو۫ذ۪ينَا مِنْ قَبْلِ اَنْ تَأْتِيَنَا وَمِنْ بَعْدِ مَا جِئْتَنَاۜ قَالَ عَسٰى رَبُّكُمْ اَنْ يُهْلِكَ عَدُوَّكُمْ وَيَسْتَخْلِفَكُمْ فِي الْاَرْضِ فَيَنْظُرَ كَيْفَ تَعْمَلُونَ۟', 'Onlar da, sen bize (peygamber olarak) gelmeden önce de geldikten sonra da bize işkence edildi, dediler. (Musa), «Umulur ki Rabbiniz düşmanınızı helâk eder ve onların yerine sizi yer yüzüne hakim kılar da nasıl hareket edeceğinize bakar» dedi.', 9, 164, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1084, 130, 7, 'A\'raf', 'وَلَقَدْ اَخَذْنَٓا اٰلَ فِرْعَوْنَ بِالسِّن۪ينَ وَنَقْصٍ مِنَ الثَّمَرَاتِ لَعَلَّهُمْ يَذَّكَّرُونَ', 'Andolsun ki, biz de Firavun´a uyanları ders alsınlar diye yıllarca kuraklık ve mahsül kıtlığı ile cezalandırdık.', 9, 164, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1085, 131, 7, 'A\'raf', 'فَاِذَا جَٓاءَتْهُمُ الْحَسَنَةُ قَالُوا لَنَا هٰذِه۪ۚ وَاِنْ تُصِبْهُمْ سَيِّئَةٌ يَطَّيَّرُوا بِمُوسٰى وَمَنْ مَعَهُۜ اَلَٓا اِنَّمَا طَٓائِرُهُمْ عِنْدَ اللّٰهِ وَلٰكِنَّ اَكْثَرَهُمْ لَا يَعْلَمُونَ', 'Onlara bir iyilik (bolluk) gelince, «Bu bizim hakkımızdır» derler; eğer kendilerine bir fenalık gelirse Musa ve onunla beraber olanları uğursuz sayarlardı. Bilesiniz ki, onlara gelen uğursuzluk Allah katındandır, fakat onların çoğu bunu bilmezler.', 9, 165, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1086, 132, 7, 'A\'raf', 'وَقَالُوا مَهْمَا تَأْتِنَا بِه۪ مِنْ اٰيَةٍ لِتَسْحَرَنَا بِهَاۙ فَمَا نَحْنُ لَكَ بِمُؤْمِن۪ينَ', 'Ve dediler ki: «Bizi sihirlemek için ne mucize getirirsen getir, biz sana inanacak değiliz.»', 9, 165, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1087, 133, 7, 'A\'raf', 'فَاَرْسَلْنَا عَلَيْهِمُ الطُّوفَانَ وَالْجَرَادَ وَالْقُمَّلَ وَالضَّفَادِعَ وَالدَّمَ اٰيَاتٍ مُفَصَّلَاتٍ فَاسْتَكْبَرُوا وَكَانُوا قَوْماً مُجْرِم۪ينَ', 'Biz de ayrı ayrı mucizeler olarak onların üzerine tufan, çekirge, haşere, kurbağalar ve kan gönderdik; yine de büyüklük tasladılar ve günahkâr bir kavim oldular.', 9, 165, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1088, 134, 7, 'A\'raf', 'وَلَمَّا وَقَعَ عَلَيْهِمُ الرِّجْزُ قَالُوا يَا مُوسَى ادْعُ لَنَا رَبَّكَ بِمَا عَهِدَ عِنْدَكَۚ لَئِنْ كَشَفْتَ عَنَّا الرِّجْزَ لَنُؤْمِنَنَّ لَكَ وَلَنُرْسِلَنَّ مَعَكَ بَن۪ٓي اِسْرَٓائ۪لَۚ', 'Azap üzerlerine çökünce, «Ey Musa! sana verdiği söz hürmetine, bizim için Rabbine dua et; eğer bizden azabı kaldırırsan, mutlaka sana inanacağız ve muhakkak İsrailoğullarını seninle göndereceğiz» dediler.', 9, 165, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1089, 135, 7, 'A\'raf', 'فَلَمَّا كَشَفْنَا عَنْهُمُ الرِّجْزَ اِلٰٓى اَجَلٍ هُمْ بَالِغُوهُ اِذَا هُمْ يَنْكُثُونَ', 'Biz, ulaşacakları bir müddete kadar onlardan azabı kaldırınca hemen sözlerinden dönüverdiler.', 9, 165, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1090, 136, 7, 'A\'raf', 'فَانْتَقَمْنَا مِنْهُمْ فَاَغْرَقْنَاهُمْ فِي الْيَمِّ بِاَنَّهُمْ كَذَّبُوا بِاٰيَاتِنَا وَكَانُوا عَنْهَا غَافِل۪ينَ', 'Biz de âyetlerimizi yalanlamaları ve onlardan gafil kalmaları sebebiyle kendilerinden intikam aldık ve onları denizde boğduk.', 9, 165, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1091, 137, 7, 'A\'raf', 'وَاَوْرَثْنَا الْقَوْمَ الَّذ۪ينَ كَانُوا يُسْتَضْعَفُونَ مَشَارِقَ الْاَرْضِ وَمَغَارِبَهَا الَّت۪ي بَارَكْنَا ف۪يهَاۜ وَتَمَّتْ كَلِمَتُ رَبِّكَ الْحُسْنٰى عَلٰى بَن۪ٓي اِسْرَٓائ۪لَ بِمَا صَبَرُواۜ وَدَمَّرْنَا مَا كَانَ يَصْنَعُ فِرْعَوْنُ وَقَوْمُهُ وَمَا كَانُوا يَعْرِشُونَ', 'Hor görülüp ezilmekte olan o kavmi (yahudileri) de, içini bereketle doldurduğumuz yerin doğu taraflarına ve batı taraflarına mirasçı kıldık. Sabırlarına karşılık Rabbinin İsrailoğullarına verdiği güzel söz yerine geldi. Firavun ve kavminin yapmakta olduklarını ve yetiştirdikleri bahçeleri helâk ettik.', 9, 165, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1092, 138, 7, 'A\'raf', 'وَجَاوَزْنَا بِبَن۪ٓي اِسْرَٓائ۪لَ الْبَحْرَ فَاَتَوْا عَلٰى قَوْمٍ يَعْكُفُونَ عَلٰٓى اَصْنَامٍ لَهُمْۚ قَالُوا يَا مُوسَى اجْعَلْ لَـنَٓا اِلٰهاً كَمَا لَهُمْ اٰلِهَةٌۜ قَالَ اِنَّكُمْ قَوْمٌ تَجْهَلُونَ', 'İsrailoğullarını denizden geçirdik, orada kendilerine mahsus birtakım putlara tapan bir kavme rastladılar. Bunun üzerine: Ey Musa! Onların tanrıları olduğu gibi, sen de bizim için bir tanrı yap! dediler. Musa: Gerçekten siz cahil bir toplumsunuz, dedi.', 9, 166, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1093, 139, 7, 'A\'raf', 'اِنَّ هٰٓؤُ۬لَٓاءِ مُتَبَّرٌ مَا هُمْ ف۪يهِ وَبَاطِلٌ مَا كَانُوا يَعْمَلُونَ', 'Şüphesiz bunların içinde bulundukları (din) yıkılmıştır, yapmakta oldukları da bâtıldır.', 9, 166, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1094, 140, 7, 'A\'raf', 'قَالَ اَغَيْرَ اللّٰهِ اَبْغ۪يكُمْ اِلٰهاً وَهُوَ فَضَّلَكُمْ عَلَى الْعَالَم۪ينَ', 'Musa dedi ki: Allah sizi âlemlere üstün kılmışken ben size Allah´tan başka bir tanrı mı arayayım?', 9, 166, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1095, 141, 7, 'A\'raf', 'وَاِذْ اَنْجَيْنَاكُمْ مِنْ اٰلِ فِرْعَوْنَ يَسُومُونَكُمْ سُٓوءَ الْعَذَابِۚ يُقَتِّلُونَ اَبْنَٓاءَكُمْ وَيَسْتَحْيُونَ نِسَٓاءَكُمْۜ وَف۪ي ذٰلِكُمْ بَلَٓاءٌ مِنْ رَبِّكُمْ عَظ۪يمٌ۟', 'Hatırlayın ki, size işkencenin en kötüsünü yapan Firavun´un adamlarından sizi kurtardık. Onlar oğullarınızı öldürüyorlar, kadınlarınızı sağ bırakıyorlardı. İşte bunda size Rabbiniz tarafından büyük bir imtihan vardır.', 9, 166, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1096, 142, 7, 'A\'raf', 'وَوٰعَدْنَا مُوسٰى ثَلٰث۪ينَ لَيْلَةً وَاَتْمَمْنَاهَا بِعَشْرٍ فَتَمَّ م۪يقَاتُ رَبِّه۪ٓ اَرْبَع۪ينَ لَيْلَةًۚ وَقَالَ مُوسٰى لِاَخ۪يهِ هٰرُونَ اخْلُفْن۪ي ف۪ي قَوْم۪ي وَاَصْلِحْ وَلَا تَتَّبِـعْ سَب۪يلَ الْمُفْسِد۪ينَ', '(Bana ibadet etmesi için) Musa´ya otuz gece vade verdik ve ona on gece daha ilâve ettik; böylece Rabbinin tayin ettiği vakit kırk geceyi buldu. Musa, kardeşi Harun´a dedi ki: Kavmimin içinde benim yerime geç, onları ıslah et, bozguncuların yoluna uyma.', 9, 166, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1097, 143, 7, 'A\'raf', 'وَلَمَّا جَٓاءَ مُوسٰى لِم۪يقَاتِنَا وَكَلَّمَهُ رَبُّهُۙ قَالَ رَبِّ اَرِن۪ٓي اَنْظُرْ اِلَيْكَۜ قَالَ لَنْ تَرٰين۪ي وَلٰكِنِ انْظُرْ اِلَى الْجَبَلِ فَاِنِ اسْتَقَرَّ مَكَانَهُ فَسَوْفَ تَرٰين۪يۚ فَلَمَّا تَجَلّٰى رَبُّهُ لِلْجَبَلِ جَعَلَهُ دَكاًّ وَخَرَّ مُوسٰى صَعِقاًۚ فَلَمَّٓا اَفَاقَ قَالَ سُبْحَانَكَ تُبْتُ اِلَيْكَ وَاَنَا۬ اَوَّلُ الْمُؤْمِن۪ينَ', 'Musa tayin ettiğimiz vakitte (Tûr´a) gelip de Rabbi onunla konuşunca «Rabbim! Bana (kendini) göster; seni göreyim!» dedi. (Rabbi): «Sen beni asla göremezsin. Fakat şu dağa bak, eğer o yerinde durabilirse sen de beni göreceksin!» buyurdu. Rabbi o dağa tecelli edince onu paramparça etti, Musa da baygın düştü. Ayılınca dedi ki: Seni noksan sıfatlardan tenzih ederim, sana tevbe ettim. Ben inananların ilkiyim.', 9, 166, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1098, 144, 7, 'A\'raf', 'قَالَ يَا مُوسٰٓى اِنِّي اصْطَفَيْتُكَ عَلَى النَّاسِ بِرِسَالَات۪ي وَبِكَلَام۪يۘ فَخُذْ مَٓا اٰتَيْتُكَ وَكُنْ مِنَ الشَّاكِر۪ينَ', '(Allah) Ey Musa! dedi, ben risaletlerimle (sana verdiğim görevlerle) ve sözlerimle seni insanların başına seçtim. Sana verdiğimi al ve şükredenlerden ol.', 9, 167, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1099, 145, 7, 'A\'raf', 'وَكَتَبْنَا لَهُ فِي الْاَلْوَاحِ مِنْ كُلِّ شَيْءٍ مَوْعِظَةً وَتَفْص۪يلاً لِكُلِّ شَيْءٍۚ فَخُذْهَا بِقُوَّةٍ وَأْمُرْ قَوْمَكَ يَأْخُذُوا بِاَحْسَنِهَاۜ سَاُر۪يكُمْ دَارَ الْفَاسِق۪ينَ', 'Nasihat ve her şeyin açıklamasına dair ne varsa hepsini Musa için levhalarda yazdık. (Ve dedik ki): Bunları kuvvetle tut, kavmine de onun en güzelini almalarını emret. Yakında size, yoldan çıkmışların yurdunu göstereceğim.', 9, 167, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1100, 146, 7, 'A\'raf', 'سَاَصْرِفُ عَنْ اٰيَاتِيَ الَّذ۪ينَ يَتَكَبَّرُونَ فِي الْاَرْضِ بِغَيْرِ الْحَقِّۜ وَاِنْ يَرَوْا كُلَّ اٰيَةٍ لَا يُؤْمِنُوا بِهَاۚ وَاِنْ يَرَوْا سَب۪يلَ الرُّشْدِ لَا يَتَّخِذُوهُ سَب۪يلاًۚ وَاِنْ يَرَوْا سَب۪يلَ الْغَيِّ يَتَّخِذُوهُ سَب۪يلاًۜ ذٰلِكَ بِاَنَّهُمْ كَذَّبُوا بِاٰيَاتِنَا وَكَانُوا عَنْهَا غَافِل۪ينَ', 'Yeryüzünde haksız yere böbürlenenleri âyetlerimden uzaklaştıracağım. Onlar bütün mucizeleri görseler de iman etmezler. Doğru yolu görseler onu yol edinmezler. Fakat azgınlık yolunu görürlerse, hemen ona saparlar. Bu durum, onların âyetlerimizi yalanlamalarından ve onlardan gafil olmalarından ileri gelmektedir.', 9, 167, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1101, 147, 7, 'A\'raf', 'وَالَّذ۪ينَ كَذَّبُوا بِاٰيَاتِنَا وَلِقَٓاءِ الْاٰخِرَةِ حَبِطَتْ اَعْمَالُهُمْۜ هَلْ يُجْزَوْنَ اِلَّا مَا كَانُوا يَعْمَلُونَ۟', 'Halbuki âyetlerimizi ve ahirete kavuşmayı yalanlayanların amelleri boşa çıkmıştır. Onlar, yapmakta oldukları amellerden başka bir şey için mi cezalandırılırlar!', 9, 167, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1102, 148, 7, 'A\'raf', 'وَاتَّخَذَ قَوْمُ مُوسٰى مِنْ بَعْدِه۪ مِنْ حُلِيِّهِمْ عِجْلاً جَسَداً لَهُ خُوَارٌۜ اَلَمْ يَرَوْا اَنَّهُ لَا يُكَلِّمُهُمْ وَلَا يَهْد۪يهِمْ سَب۪يلاًۢ اِتَّخَذُوهُ وَكَانُوا ظَالِم۪ينَ', '(Tûr´a giden) Musa´nın arkasından kavmi, zinet takımlarından, böğürebilen bir buzağı heykelini (tanrı) edindiler. Görmediler mi ki o, onlarla ne konuşuyor ne de onlara yol gösteriyor? Onu (tanrı olarak) benimsediler ve zalimler oldular.', 9, 167, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1103, 149, 7, 'A\'raf', 'وَلَمَّا سُقِطَ ف۪ٓي اَيْد۪يهِمْ وَرَاَوْا اَنَّهُمْ قَدْ ضَلُّواۙ قَالُوا لَئِنْ لَمْ يَرْحَمْنَا رَبُّنَا وَيَغْفِرْ لَنَا لَنَكُونَنَّ مِنَ الْخَاسِر۪ينَ', 'Pişman olup da kendilerinin gerçekten sapmış olduklarını görünce dediler ki: Eğer Rabbimiz bize acımaz ve bizi bağışlamazsa mutlaka ziyana uğrayanlardan olacağız!', 9, 167, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1104, 150, 7, 'A\'raf', 'وَلَمَّا رَجَعَ مُوسٰٓى اِلٰى قَوْمِه۪ غَضْبَانَ اَسِفاًۙ قَالَ بِئْسَمَا خَلَفْتُمُون۪ي مِنْ بَعْد۪يۚ اَعَجِلْتُمْ اَمْرَ رَبِّكُمْۚ وَاَلْقَى الْاَلْوَاحَ وَاَخَذَ بِرَأْسِ اَخ۪يهِ يَجُرُّهُٓ اِلَيْهِۜ قَالَ ابْنَ اُمَّ اِنَّ الْقَوْمَ اسْتَضْعَفُون۪ي وَكَادُوا يَقْتُلُونَن۪يۘ فَلَا تُشْمِتْ بِيَ الْاَعْدَٓاءَ وَلَا تَجْعَلْن۪ي مَعَ الْقَوْمِ الظَّالِم۪ينَ', 'Musa, kızgın ve üzgün bir halde kavmine dönünce: «Benden sonra arkamdan ne kötü işler yapmışsınız! Rabbinizin emrini (beklemeyip) acele mi ettiniz?» dedi. Tevrat levhalarını yere attı ve kardeşinin (Harun´un) başını tutup kendine doğru çekmeye başladı. (Kardeşi): «Anam oğlu! Bu kavim beni cidden zayıf gördüler ve nerede ise beni öldüreceklerdi. Sen de düşmanları bana güldürme ve beni bu zalim kavimle beraber tutma!» dedi.', 9, 168, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1105, 151, 7, 'A\'raf', 'قَالَ رَبِّ اغْفِرْ ل۪ي وَلِاَخ۪ي وَاَدْخِلْنَا ف۪ي رَحْمَتِكَۘ وَاَنْتَ اَرْحَمُ الرَّاحِم۪ينَ۟', '(Musa da) Ey Rabbim, beni ve kardeşimi bağışla, bizi rahmetine kabul et. Zira sen merhametlilerin en merhametlisisin! dedi.', 9, 168, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1106, 152, 7, 'A\'raf', 'اِنَّ الَّذ۪ينَ اتَّخَذُوا الْعِجْلَ سَيَنَالُهُمْ غَضَبٌ مِنْ رَبِّهِمْ وَذِلَّةٌ فِي الْحَيٰوةِ الدُّنْيَاۜ وَكَذٰلِكَ نَجْزِي الْمُفْتَر۪ينَ', 'Buzağıyı (tanrı) edinenler var ya, işte onlara mutlaka Rablerinden bir gazap ve dünya hayatında bir alçaklık erişecektir. Biz iftiracıları böyle cezalandırırız.', 9, 168, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1107, 153, 7, 'A\'raf', 'وَالَّذ۪ينَ عَمِلُوا السَّيِّـَٔاتِ ثُمَّ تَابُوا مِنْ بَعْدِهَا وَاٰمَنُواۘ اِنَّ رَبَّكَ مِنْ بَعْدِهَا لَغَفُورٌ رَح۪يمٌ', 'Kötülükler yaptıktan sonra ardından tevbe edip de iman edenlere gelince, şüphesiz ki o tevbe ve imandan sonra, Rabbin elbette bağışlayan ve esirgeyendir.', 9, 168, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1108, 154, 7, 'A\'raf', 'وَلَمَّا سَكَتَ عَنْ مُوسَى الْغَضَبُ اَخَذَ الْاَلْوَاحَۚ وَف۪ي نُسْخَتِهَا هُدًى وَرَحْمَةٌ لِلَّذ۪ينَ هُمْ لِرَبِّهِمْ يَرْهَبُونَ', 'Musa´nın öfkesi dinince levhaları aldı. Onlardaki yazıda Rablerinden korkanlar için hidayet ve rahmet (haberi) vardı.', 9, 168, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1109, 155, 7, 'A\'raf', 'وَاخْتَارَ مُوسٰى قَوْمَهُ سَبْع۪ينَ رَجُلاً لِم۪يقَاتِنَاۚ فَلَمَّٓا اَخَذَتْهُمُ الرَّجْفَةُ قَالَ رَبِّ لَوْ شِئْتَ اَهْلَكْتَهُمْ مِنْ قَبْلُ وَاِيَّايَۜ اَتُهْلِكُنَا بِمَا فَعَلَ السُّفَـهَٓاءُ مِنَّاۚ اِنْ هِيَ اِلَّا فِتْنَتُكَۜ تُضِلُّ بِهَا مَنْ تَشَٓاءُ وَتَهْد۪ي مَنْ تَشَٓاءُۜ اَنْتَ وَلِيُّنَا فَاغْفِرْ لَنَا وَارْحَمْنَا وَاَنْتَ خَيْرُ الْغَافِر۪ينَ', 'Musa tayin ettiğimiz vakitte kavminden yetmiş adam seçti. Onları o müthiş deprem yakalayınca Musa dedi ki: «Ey Rabbim! Dileseydin onları da beni de daha önce helâk ederdin. İçimizden birtakım beyinsizlerin işlediği (günah) yüzünden hepimizi helâk edecek misin? Bu iş, senin imtihanından başka bir şey değildir. Onunla dilediğini saptırırsın, dilediğini de doğru yola iletirsin. Sen bizim sahibimizsin, bizi bağışla ve bize acı! Sen bağışlayanların en iyisisin!', 9, 168, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1110, 156, 7, 'A\'raf', 'وَاكْتُبْ لَنَا ف۪ي هٰذِهِ الدُّنْيَا حَسَنَةً وَفِي الْاٰخِرَةِ اِنَّا هُدْنَٓا اِلَيْكَۜ قَالَ عَذَاب۪ٓي اُص۪يبُ بِه۪ مَنْ اَشَٓاءُۚ وَرَحْمَت۪ي وَسِعَتْ كُلَّ شَيْءٍۜ فَسَاَكْتُبُهَا لِلَّذ۪ينَ يَتَّقُونَ وَيُؤْتُونَ الزَّكٰوةَ وَالَّذ۪ينَ هُمْ بِاٰيَاتِنَا يُؤْمِنُونَۚ', 'Bize, bu dünyada da iyilik yaz ahirette de. Şüphesiz biz sana döndük.» Allah buyurdu ki: Kimi dilersem onu azabıma uğratırım; rahmetim ise her şeyi kuşatır. Onu, sakınanlara, zekâtı verenlere ve âyetlerimize inananlara yazacağım.', 9, 169, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1111, 157, 7, 'A\'raf', 'اَلَّذ۪ينَ يَتَّبِعُونَ الرَّسُولَ النَّبِيَّ الْاُمِّيَّ الَّذ۪ي يَجِدُونَهُ مَكْتُوباً عِنْدَهُمْ فِي التَّوْرٰيةِ وَالْاِنْج۪يلِۘ يَأْمُرُهُمْ بِالْمَعْرُوفِ وَيَنْهٰيهُمْ عَنِ الْمُنْكَرِ وَيُحِلُّ لَهُمُ الطَّيِّبَاتِ وَيُحَرِّمُ عَلَيْهِمُ الْخَبَٓائِثَ وَيَضَعُ عَنْهُمْ اِصْرَهُمْ وَالْاَغْلَالَ الَّت۪ي كَانَتْ عَلَيْهِمْۜ فَالَّذ۪ينَ اٰمَنُوا بِه۪ وَعَزَّرُوهُ وَنَصَرُوهُ وَاتَّبَعُوا النُّورَ الَّـذ۪ٓي اُنْزِلَ مَعَهُٓۙ اُو۬لٰٓئِكَ هُمُ الْمُفْلِحُونَ۟', 'Yanlarındaki Tevrat ve İncil´de yazılı buldukları o elçiye, o ümmî Peygamber´e uyanlar (var ya), işte o Peygamber onlara iyiliği emreder, onları kötülükten meneder, onlara temiz şeyleri helâl, pis şeyleri haram kılar. Ağırlıklarını ve üzerlerindeki zincirleri indirir. O Peygamber´e inanıp ona saygı gösteren, ona yardım eden ve onunla birlikte gönderilen nûr´a (Kur´an´a) uyanlar var ya, işte kurtuluşa erenler onlardır.', 9, 169, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1112, 158, 7, 'A\'raf', 'قُلْ يَٓا اَيُّهَا النَّاسُ اِنّ۪ي رَسُولُ اللّٰهِ اِلَيْكُمْ جَم۪يعاًۨ الَّذ۪ي لَهُ مُلْكُ السَّمٰوَاتِ وَالْاَرْضِۚ لَٓا اِلٰهَ اِلَّا هُوَ يُحْـي۪ وَيُم۪يتُۖ فَاٰمِنُوا بِاللّٰهِ وَرَسُولِهِ النَّبِيِّ الْاُمِّيِّ الَّذ۪ي يُؤْمِنُ بِاللّٰهِ وَكَلِمَاتِه۪ وَاتَّبِعُوهُ لَعَلَّكُمْ تَهْتَدُونَ', 'De ki: Ey insanlar! Gerçekten ben sizin hepinize, göklerin ve yerin sahibi olan Allah´ın elçisiyim. Ondan başka tanrı yoktur, O diriltir ve öldürür. Öyle ise Allah´a ve ümmî Peygamber olan Resûlüne -ki o, Allah´a ve onun sözlerine inanır- iman edin ve O´na uyun ki doğru yolu bulasınız.', 9, 169, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1113, 159, 7, 'A\'raf', 'وَمِنْ قَوْمِ مُوسٰٓى اُمَّةٌ يَهْدُونَ بِالْحَقِّ وَبِه۪ يَعْدِلُونَ', 'Musa´nın kavminden hak ile doğru yolu bulan ve onun sayesinde âdil davranan bir topluluk vardır.', 9, 169, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1114, 160, 7, 'A\'raf', 'وَقَطَّعْنَاهُمُ اثْنَتَيْ عَشْرَةَ اَسْبَاطاً اُمَماًۜ وَاَوْحَيْنَٓا اِلٰى مُوسٰٓى اِذِ اسْتَسْقٰيهُ قَوْمُهُٓ اَنِ اضْرِبْ بِعَصَاكَ الْحَجَرَۚ فَانْبَجَسَتْ مِنْهُ اثْنَتَا عَشْرَةَ عَيْناًۜ قَدْ عَلِمَ كُلُّ اُنَاسٍ مَشْرَبَهُمْۜ وَظَلَّلْنَا عَلَيْهِمُ الْغَمَامَ وَاَنْزَلْنَا عَلَيْهِمُ الْمَنَّ وَالسَّلْوٰىۜ كُلُوا مِنْ طَيِّبَاتِ مَا رَزَقْنَاكُمْۜ وَمَا ظَلَمُونَا وَلٰكِنْ كَانُٓوا اَنْفُسَهُمْ يَظْلِمُونَ', 'Biz İsrailoğullarını oymaklar halinde oniki kabileye ayırdık. Kavmi kendisinden su isteyince, Musa´ya, «Asanı taşa vur!» diye vahyettik. Derhal ondan oniki pınar fışkırdı. Her kabile içeceği yeri belledi. Sonra üzerlerine bulutla gölge yaptık, onlara kudret helvası ve bıldırcın eti indirdik. (Onlara dedik ki) «Size verdiğimiz rızıkların temizlerinden yeyin.» Ama onlar (emirlerimizi dinlememekle) bize değil kendilerine zulmediyorlardı.', 9, 170, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1115, 161, 7, 'A\'raf', 'وَاِذْ ق۪يلَ لَهُمُ اسْكُنُوا هٰذِهِ الْقَرْيَةَ وَكُلُوا مِنْهَا حَيْثُ شِئْتُمْ وَقُولُوا حِطَّةٌ وَادْخُلُوا الْبَابَ سُجَّداً نَغْفِرْ لَكُمْ خَط۪ٓيـَٔاتِكُمْۜ سَنَز۪يدُ الْمُحْسِن۪ينَ', 'Onlara denildi ki: Şu şehirde (Kudüs´te) yerleşin, ondan (nimetlerinden) dilediğiniz gibi yeyin, «bağışlanmak istiyoruz» deyin ve kapıdan eğilerek girin ki hatalarınızı bağışlayalım. İyilik yapanlara ileride ihsanımızı daha da artıracağız.', 9, 170, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1116, 162, 7, 'A\'raf', 'فَبَدَّلَ الَّذ۪ينَ ظَلَمُوا مِنْهُمْ قَوْلاً غَيْرَ الَّذ۪ي ق۪يلَ لَهُمْ فَاَرْسَلْنَا عَلَيْهِمْ رِجْزاً مِنَ السَّمَٓاءِ بِمَا كَانُوا يَظْلِمُونَ۟', 'Fakat onlardan zalim olanlar, sözü, kendilerine söylenenden başkasıyla değiştirdiler. Biz de zulmetmelerinden ötürü üzerlerine gökten bir azap gönderdik.', 9, 170, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1117, 163, 7, 'A\'raf', 'وَسْـَٔلْهُمْ عَنِ الْقَرْيَةِ الَّت۪ي كَانَتْ حَاضِرَةَ الْبَحْرِۢ اِذْ يَعْدُونَ فِي السَّبْتِ اِذْ تَأْت۪يهِمْ ح۪يتَانُهُمْ يَوْمَ سَبْتِهِمْ شُرَّعاً وَيَوْمَ لَا يَسْبِتُونَۙ لَا تَأْت۪يهِمْۚ كَذٰلِكَ نَبْلُوهُمْ بِمَا كَانُوا يَفْسُقُونَ', 'Onlara, deniz kıyısında bulunan şehir halkının durumunu sor. Hani onlar cumartesi gününe saygısızlık gösterip haddi aşıyorlardı. Çünkü cumartesi tatili yaptıkları gün, balıklar meydana çıkarak akın akın onlara gelirdi, cumartesi tatili yapmadıkları gün de gelmezlerdi. İşte böylece biz, yoldan çıkmalarından dolayı onları imtihan ediyorduk.', 9, 170, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1118, 164, 7, 'A\'raf', 'وَاِذْ قَالَتْ اُمَّةٌ مِنْهُمْ لِمَ تَعِظُونَ قَوْماًۙۨ اللّٰهُ مُهْلِكُهُمْ اَوْ مُعَذِّبُهُمْ عَذَاباً شَد۪يداًۜ قَالُوا مَعْذِرَةً اِلٰى رَبِّكُمْ وَلَعَلَّهُمْ يَتَّقُونَ', 'İçlerinden bir topluluk: «Allah´ın helâk edeceği yahut şiddetli bir şekilde azap edeceği bir kavme ne diye öğüt veriyorsunuz?» dedi. (Öğüt verenler) dediler ki: Rabbinize mazeret beyan edelim diye bir de sakınırlar ümidiyle (öğüt veriyoruz).', 9, 171, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1119, 165, 7, 'A\'raf', 'فَلَمَّا نَسُوا مَا ذُكِّرُوا بِه۪ٓ اَنْجَيْنَا الَّذ۪ينَ يَنْهَوْنَ عَنِ السُّٓوءِ وَاَخَذْنَا الَّذ۪ينَ ظَلَمُوا بِعَذَابٍ بَـ۪ٔيسٍ بِمَا كَانُوا يَفْسُقُونَ', 'Onlar kendilerine yapılan uyarıları unutunca, biz de kötülükten men edenleri kurtardık, zulmedenleri de yapmakta oldukları kötülüklerden ötürü şiddetli bir azap ile yakaladık.', 9, 171, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1120, 166, 7, 'A\'raf', 'فَلَمَّا عَتَوْا عَنْ مَا نُهُوا عَنْهُ قُلْنَا لَهُمْ كُونُوا قِرَدَةً خَاسِـ۪ٔينَ', 'Kibirlenip de kendilerine yasak edilen şeylerden vazgeçmeyince onlara: Aşağılık maymunlar olun! dedik.', 9, 171, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1121, 167, 7, 'A\'raf', 'وَاِذْ تَاَذَّنَ رَبُّكَ لَيَبْعَثَنَّ عَلَيْهِمْ اِلٰى يَوْمِ الْقِيٰمَةِ مَنْ يَسُومُهُمْ سُٓوءَ الْعَذَابِۜ اِنَّ رَبَّكَ لَسَر۪يعُ الْعِقَابِۚ وَاِنَّهُ لَغَفُورٌ رَح۪يمٌ', 'Rabbin, elbette kıyamet gününe kadar onlara en kötü eziyeti yapacak kimseler göndereceğini ilân etti. Şüphesiz Rabbin cezayı çabuk verendir. Ve O çok bağışlayan, pek esirgeyendir.', 9, 171, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1122, 168, 7, 'A\'raf', 'وَقَطَّعْنَاهُمْ فِي الْاَرْضِ اُمَماًۚ مِنْهُمُ الصَّالِحُونَ وَمِنْهُمْ دُونَ ذٰلِكَۘ وَبَلَوْنَاهُمْ بِالْحَسَنَاتِ وَالسَّيِّـَٔاتِ لَعَلَّهُمْ يَرْجِعُونَ', 'Onları (yahudileri) gurup gurup yeryüzüne dağıttık. Onlardan iyi kimseler vardır, yine onlardan bundan aşağıda olanları da vardır. (Kötülüklerinden) belki dönerler diye onları iyilik ve kötülüklerle imtihan ettik.', 9, 171, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1123, 169, 7, 'A\'raf', 'فَخَلَفَ مِنْ بَعْدِهِمْ خَلْفٌ وَرِثُوا الْكِتَابَ يَأْخُذُونَ عَرَضَ هٰذَا الْاَدْنٰى وَيَقُولُونَ سَيُغْفَرُ لَنَاۚ وَاِنْ يَأْتِهِمْ عَرَضٌ مِثْلُهُ يَأْخُذُوهُۜ اَلَمْ يُؤْخَذْ عَلَيْهِمْ م۪يثَاقُ الْكِتَابِ اَنْ لَا يَقُولُوا عَلَى اللّٰهِ اِلَّا الْحَقَّ وَدَرَسُوا مَا ف۪يهِۜ وَالدَّارُ الْاٰخِرَةُ خَيْرٌ لِلَّذ۪ينَ يَتَّقُونَۜ اَفَلَا تَعْقِلُونَ', 'Onların ardından da (âyetleri tahrif karşılığında) şu değersiz dünya malını alıp, nasıl olsa bağışlanacağız, diyerek Kitab´a vâris olan birtakım kötü kimseler geldi. Onlara, ona benzer bir menfaat daha gelse onu da alırlar. Peki, Kitap´ta Allah hakkında gerçekten başka bir şey söylemeyeceklerine dair onlardan söz alınmamış mıydı ve onlar Kitap´takini okumamışlar mıydı? Âhiret yurdu sakınanlar için daha hayırlıdır. Hâla aklınız ermiyor mu?', 9, 171, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1124, 170, 7, 'A\'raf', 'وَالَّذ۪ينَ يُمَسِّكُونَ بِالْكِتَابِ وَاَقَامُوا الصَّلٰوةَۜ اِنَّا لَا نُض۪يعُ اَجْرَ الْمُصْلِح۪ينَ', 'Kitab´a sımsıkı sarılıp namazı dosdoğru kılanlar var ya, işte biz böyle iyiliğe çalışanların ecrini zayi etmeyiz.', 9, 171, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1125, 171, 7, 'A\'raf', 'وَاِذْ نَتَقْنَا الْجَبَلَ فَوْقَهُمْ كَاَنَّهُ ظُلَّةٌ وَظَنُّٓوا اَنَّهُ وَاقِعٌ بِهِمْۚ خُذُوا مَٓا اٰتَيْنَاكُمْ بِقُوَّةٍ وَاذْكُرُوا مَا ف۪يهِ لَعَلَّكُمْ تَتَّقُونَ۟', 'Bir zamanlar dağı İsrailoğullarının üzerine gölge gibi kaldırdık da üstlerine düşecek sandılar. «Size verdiğimi (Kitab´ı) kuvvetle tutun ve içinde olanı hatırlayın ki korunasınız» dedik.', 9, 172, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1126, 172, 7, 'A\'raf', 'وَاِذْ اَخَذَ رَبُّكَ مِنْ بَن۪ٓي اٰدَمَ مِنْ ظُهُورِهِمْ ذُرِّيَّتَهُمْ وَاَشْهَدَهُمْ عَلٰٓى اَنْفُسِهِمْۚ اَلَسْتُ بِرَبِّكُمْۜ قَالُوا بَلٰىۚۛ شَهِدْنَاۚۛ اَنْ تَقُولُوا يَوْمَ الْقِيٰمَةِ اِنَّا كُنَّا عَنْ هٰذَا غَافِل۪ينَۙ', 'Kıyamet gününde, biz bundan habersizdik demeyesiniz diye Rabbin Âdem oğullarından, onların bellerinden zürriyetlerini çıkardı, onları kendilerine şahit tuttu ve dedi ki: Ben sizin Rabbiniz değil miyim? (Onlar da), Evet (buna) şâhit olduk, dediler.', 9, 172, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1127, 173, 7, 'A\'raf', 'اَوْ تَقُولُٓوا اِنَّـمَٓا اَشْرَكَ اٰبَٓاؤُ۬نَا مِنْ قَبْلُ وَكُنَّا ذُرِّيَّةً مِنْ بَعْدِهِمْۚ اَفَتُهْلِكُنَا بِمَا فَعَلَ الْمُبْطِلُونَ', 'Yahut «Daha önce babalarımız Allah´a ortak koştu, biz de onlardan sonra gelen bir nesildik (onların izinden gittik). Bâtıl işleyenlerin yüzünden bizi helâk edecek misin?» dememeniz için (böyle yaptık).', 9, 172, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1128, 174, 7, 'A\'raf', 'وَكَذٰلِكَ نُفَصِّلُ الْاٰيَاتِ وَلَعَلَّهُمْ يَرْجِعُونَ', 'Belki inkârdan dönerler diye âyetleri böyle ayrıntılı bir şekilde açıklıyoruz.', 9, 172, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1129, 175, 7, 'A\'raf', 'وَاتْلُ عَلَيْهِمْ نَبَاَ الَّـذ۪ٓي اٰتَيْنَاهُ اٰيَاتِنَا فَانْسَلَخَ مِنْهَا فَاَتْبَعَهُ الشَّيْطَانُ فَكَانَ مِنَ الْغَاو۪ينَ', 'Onlara (yahudilere), kendisine âyetlerimizden verdiğimiz ve fakat onlardan sıyrılıp çıkan, o yüzden de şeytanın takibine uğrayan ve sonunda azgınlardan olan kimsenin haberini oku.', 9, 172, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1130, 176, 7, 'A\'raf', 'وَلَوْ شِئْنَا لَرَفَعْنَاهُ بِهَا وَلٰكِنَّهُٓ اَخْلَدَ اِلَى الْاَرْضِ وَاتَّـبَعَ هَوٰيهُۚ فَمَثَلُهُ كَمَثَلِ الْكَلْبِۚ اِنْ تَحْمِلْ عَلَيْهِ يَلْهَثْ اَوْ تَتْرُكْهُ يَلْهَثْۜ ذٰلِكَ مَثَلُ الْقَوْمِ الَّذ۪ينَ كَذَّبُوا بِاٰيَاتِنَاۚ فَاقْصُصِ الْقَصَصَ لَعَلَّهُمْ يَتَفَكَّرُونَ', 'Dileseydik elbette onu bu âyetler sayesinde yükseltirdik. Fakat o, dünyaya saplandı ve hevesinin peşine düştü. Onun durumu tıpkı köpeğin durumuna benzer: Üstüne varsan da dilini çıkarıp solur, bıraksan da dilini sarkıtıp solur. İşte âyetlerimizi yalanlayan kavmin durumu böyledir. Kıssayı anlat; belki düşünürler.', 9, 172, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1131, 177, 7, 'A\'raf', 'سَٓاءَ مَثَلاًۨ الْقَوْمُ الَّذ۪ينَ كَذَّبُوا بِاٰيَاتِنَا وَاَنْفُسَهُمْ كَانُوا يَظْلِمُونَ', 'Âyetlerimizi yalanlayan ve kendilerine zulmetmiş olan kavmin durumu ne kötüdür!', 9, 172, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1132, 178, 7, 'A\'raf', 'مَنْ يَهْدِ اللّٰهُ فَهُوَ الْمُهْتَد۪يۚ وَمَنْ يُضْلِلْ فَاُو۬لٰٓئِكَ هُمُ الْخَاسِرُونَ', 'Allah kimi hidayete erdirirse, doğru yolu bulan odur. Kimi de şaşırtırsa, işte asıl ziyana uğrayanlar onlardır.', 9, 172, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1133, 179, 7, 'A\'raf', 'وَلَقَدْ ذَرَأْنَا لِجَهَنَّمَ كَث۪يراً مِنَ الْجِنِّ وَالْاِنْسِۘ لَهُمْ قُلُوبٌ لَا يَفْقَهُونَ بِهَاۘ وَلَهُمْ اَعْيُنٌ لَا يُبْصِرُونَ بِهَاۘ وَلَهُمْ اٰذَانٌ لَا يَسْمَعُونَ بِهَاۜ اُو۬لٰٓئِكَ كَالْاَنْعَامِ بَلْ هُمْ اَضَلُّۜ اُو۬لٰٓئِكَ هُمُ الْغَافِلُونَ', 'Andolsun, biz cinler ve insanlardan birçoğunu cehennem için yaratmışızdır. Onların kalpleri vardır, onlarla kavramazlar; gözleri vardır, onlarla görmezler; kulakları vardır, onlarla işitmezler. İşte onlar hayvanlar gibidir; hatta daha da şaşkındırlar. İşte asıl gafiller onlardır.', 9, 173, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1134, 180, 7, 'A\'raf', 'وَلِلّٰهِ الْاَسْمَٓاءُ الْحُسْنٰى فَادْعُوهُ بِهَاۖ وَذَرُوا الَّذ۪ينَ يُلْحِدُونَ ف۪ٓي اَسْمَٓائِه۪ۜ سَيُجْزَوْنَ مَا كَانُوا يَعْمَلُونَ', 'En güzel isimler (el-esmâü´l-hüsnâ) Allah´ındır. O halde O´na o güzel isimlerle dua edin. Onun isimleri hakkında eğri yola gidenleri bırakın. Onlar yapmakta olduklarının cezasına çarptırılacaklardır.', 9, 173, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1135, 181, 7, 'A\'raf', 'وَمِمَّنْ خَلَقْنَٓا اُمَّةٌ يَهْدُونَ بِالْحَقِّ وَبِه۪ يَعْدِلُونَ۟', 'Yarattıklarımızdan, daima hakka ileten ve adaleti hak ile yerine getiren bir millet bulunur.', 9, 173, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1136, 182, 7, 'A\'raf', 'وَالَّذ۪ينَ كَذَّبُوا بِاٰيَاتِنَا سَنَسْتَدْرِجُهُمْ مِنْ حَيْثُ لَا يَعْلَمُونَۚ', 'Âyetlerimizi yalanlayanları, hiç bilmeyecekleri yerden yavaş yavaş helâke götüreceğiz.', 9, 173, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1137, 183, 7, 'A\'raf', 'وَاُمْل۪ي لَهُمْۜ اِنَّ كَيْد۪ي مَت۪ينٌ', 'Onlara mühlet veririm; (ama) benim cezam çetindir.', 9, 173, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1138, 184, 7, 'A\'raf', 'اَوَلَمْ يَتَفَكَّرُوا مَا بِصَاحِبِهِمْ مِنْ جِنَّةٍۜ اِنْ هُوَ اِلَّا نَذ۪يرٌ مُب۪ينٌ', 'Düşünmediler mi ki, arkadaşlarında (Muhammed´de) delilik yoktur? O, ancak apaçık bir uyarıcıdır.', 9, 173, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1139, 185, 7, 'A\'raf', 'اَوَلَمْ يَنْظُرُوا ف۪ي مَلَكُوتِ السَّمٰوَاتِ وَالْاَرْضِ وَمَا خَلَقَ اللّٰهُ مِنْ شَيْءٍۙ وَاَنْ عَسٰٓى اَنْ يَكُونَ قَدِ اقْتَرَبَ اَجَلُهُمْۚ فَبِاَيِّ حَد۪يثٍ بَعْدَهُ يُؤْمِنُونَ', 'Göklerin ve yerin hükümranlığına, Allah´ın yarattığı her şeye ve ecellerinin yaklaşmış olabileceğine bakmadılar mı? O halde Kur´an´dan sonra hangi söze inanacaklar?', 9, 173, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1140, 186, 7, 'A\'raf', 'مَنْ يُضْلِلِ اللّٰهُ فَلَا هَادِيَ لَهُۜ وَيَذَرُهُمْ ف۪ي طُغْيَانِهِمْ يَعْمَهُونَ', 'Allah kimi şaşırtırsa, artık onun için yol gösteren yoktur. Ve onları azgınlıkları içinde şaşkın olarak bırakır.', 9, 173, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1141, 187, 7, 'A\'raf', 'يَسْـَٔلُونَكَ عَنِ السَّاعَةِ اَيَّانَ مُرْسٰيهَاۜ قُلْ اِنَّمَا عِلْمُهَا عِنْدَ رَبّ۪يۚ لَا يُجَلّ۪يهَا لِوَقْتِهَٓا اِلَّا هُوَۜ ثَقُلَتْ فِي السَّمٰوَاتِ وَالْاَرْضِۜ لَا تَأْت۪يكُمْ اِلَّا بَغْتَةًۜ يَسْـَٔلُونَكَ كَاَنَّكَ حَفِيٌّ عَنْهَاۜ قُلْ اِنَّمَا عِلْمُهَا عِنْدَ اللّٰهِ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُونَ', 'Sana kıyameti, ne zaman gelip çatacağını soruyorlar. De ki: Onun ilmi ancak Rabbimin katındadır. Onun vaktini O´ndan başkası açıklayamaz. O göklere de yere de ağır gelmiştir. O size ansızın gelecektir. Sanki sen onu biliyormuşsun gibi sana soruyorlar. De ki: Onun bilgisi ancak Allah´ın katındadır; ama insanların çoğu bilmezler.', 9, 173, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1142, 188, 7, 'A\'raf', 'قُلْ لَٓا اَمْلِكُ لِنَفْس۪ي نَفْعاً وَلَا ضَراًّ اِلَّا مَا شَٓاءَ اللّٰهُۜ وَلَوْ كُنْتُ اَعْلَمُ الْغَيْبَ لَاسْتَكْثَرْتُ مِنَ الْخَيْرِۚ وَمَا مَسَّنِيَ السُّٓوءُ اِنْ اَنَا۬ اِلَّا نَذ۪يرٌ وَبَش۪يرٌ لِقَوْمٍ يُؤْمِنُونَ۟', 'De ki: «Ben, Allah´ın dilediğinden başka kendime herhangi bir fayda veya zarar verecek güce sahip değilim. Eğer ben gaybı bilseydim elbette daha çok hayır yapmak isterdim ve bana hiçbir fenalık dokunmazdı. Ben sadece inanan bir kavim için bir uyarıcı ve müjdeleyiciyim.»', 9, 174, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1143, 189, 7, 'A\'raf', 'هُوَ الَّذ۪ي خَلَقَكُمْ مِنْ نَفْسٍ وَاحِدَةٍ وَجَعَلَ مِنْهَا زَوْجَهَا لِيَسْكُنَ اِلَيْهَاۚ فَلَمَّا تَغَشّٰيهَا حَمَلَتْ حَمْلاً خَف۪يفاً فَمَرَّتْ بِه۪ۚ فَلَمَّٓا اَثْقَلَتْ دَعَوَا اللّٰهَ رَبَّهُمَا لَئِنْ اٰتَيْتَنَا صَالِحاً لَنَكُونَنَّ مِنَ الشَّاكِر۪ينَ', 'Sizi bir tek candan (Âdem´den) yaratan, ondan da yanında huzur bulsun diye eşini (Havva´yı) yaratan O´dur. Eşi ile (birleşince) eşi hafif bir yük yüklendi (hamile kaldı). Onu bir müddet taşıdı. Hamileliği ağırlaşınca, Rableri Allah´a: Andolsun bize kusursuz bir çocuk verirsen muhakkak şükredenlerden olacağız, diye dua ettiler.', 9, 174, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1144, 190, 7, 'A\'raf', 'فَلَمَّٓا اٰتٰيهُمَا صَالِحاً جَعَلَا لَهُ شُرَكَٓاءَ ف۪يمَٓا اٰتٰيهُمَاۚ فَتَعَالَى اللّٰهُ عَمَّا يُشْرِكُونَ', 'Fakat (Allah) onlara kusursuz bir çocuk verince, kendilerine verdiği bu çocuk hakkında (sonradan insanlar) Allah´a ortak koştular. Allah ise onların ortak koştuğu şeyden yücedir.', 9, 174, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1145, 191, 7, 'A\'raf', 'اَيُشْرِكُونَ مَا لَا يَخْلُقُ شَيْـٔاً وَهُمْ يُخْلَقُونَۘ', 'Kendileri yaratıldığı halde hiçbir şeyi yaratamayan varlıkları (Allah´a) ortak mı koşuyorlar?', 9, 174, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1146, 192, 7, 'A\'raf', 'وَلَا يَسْتَط۪يعُونَ لَهُمْ نَصْراً وَلَٓا اَنْفُسَهُمْ يَنْصُرُونَ', 'Halbuki (putlar) ne onlara bir yardım edebilirler ne de kendilerine bir yardımları olur.', 9, 174, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1147, 193, 7, 'A\'raf', 'وَاِنْ تَدْعُوهُمْ اِلَى الْهُدٰى لَا يَتَّبِعُوكُمْۜ سَوَٓاءٌ عَلَيْكُمْ اَدَعَوْتُمُوهُمْ اَمْ اَنْتُمْ صَامِتُونَ', 'Onları doğru yola çağırırsanız size uymazlar; onları çağırsanız da, sukût etseniz de sizin için birdir.', 9, 174, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1148, 194, 7, 'A\'raf', 'اِنَّ الَّذ۪ينَ تَدْعُونَ مِنْ دُونِ اللّٰهِ عِبَادٌ اَمْثَالُكُمْ فَادْعُوهُمْ فَلْيَسْتَج۪يبُوا لَكُمْ اِنْ كُنْتُمْ صَادِق۪ينَ', '(Ey kâfirler!) Allah´ı bırakıp da taptıklarınız sizler gibi kullardır. (Onların tanrılığı hakkında iddianızda) doğru iseniz, onları çağırın da size cevap versinler!', 9, 174, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1149, 195, 7, 'A\'raf', 'اَلَهُمْ اَرْجُلٌ يَمْشُونَ بِهَاۘ اَمْ لَهُمْ اَيْدٍ يَبْطِشُونَ بِهَاۘ اَمْ لَهُمْ اَعْيُنٌ يُبْصِرُونَ بِهَاۘ اَمْ لَهُمْ اٰذَانٌ يَسْمَعُونَ بِهَاۜ قُلِ ادْعُوا شُرَكَٓاءَكُمْ ثُمَّ ك۪يدُونِ فَلَا تُنْظِرُونِ', 'Onların yürüyecekleri ayakları mı var, yoksa tutacakları elleri mi var veya görecekleri gözleri mi var yahut işitecekleri kulakları mı var (neleri var)? De ki: «Ortaklarınızı çağırın, sonra bana (istediğiniz) tuzağı kurun ve bana göz bile açtırmayın!»', 9, 174, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1150, 196, 7, 'A\'raf', 'اِنَّ وَلِـِّيَ اللّٰهُ الَّذ۪ي نَزَّلَ الْكِتَابَۘ وَهُوَ يَتَوَلَّى الصَّالِح۪ينَ', 'Şüphesiz ki, benim koruyanım Kitab´ı indiren Allah´tır. Ve O bütün salih kullarını görüp gözetir.', 9, 175, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1151, 197, 7, 'A\'raf', 'وَالَّذ۪ينَ تَدْعُونَ مِنْ دُونِه۪ لَا يَسْتَط۪يعُونَ نَصْرَكُمْ وَلَٓا اَنْفُسَهُمْ يَنْصُرُونَ', 'Allah´ın dışında taptıklarınızın ne size yardıma güçleri yeter ne de kendilerine yardım edebilirler.', 9, 175, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1152, 198, 7, 'A\'raf', 'وَاِنْ تَدْعُوهُمْ اِلَى الْهُدٰى لَا يَسْمَعُواۜ وَتَرٰيهُمْ يَنْظُرُونَ اِلَيْكَ وَهُمْ لَا يُبْصِرُونَ', 'Onları doğru yola çağırmış olsanız işitmezler. Ve onları sana bakar görürsün, oysa onlar görmezler.', 9, 175, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1153, 199, 7, 'A\'raf', 'خُذِ الْعَفْوَ وَأْمُرْ بِالْعُرْفِ وَاَعْرِضْ عَنِ الْجَاهِل۪ينَ', '(Resûlüm!) Sen af yolunu tut, iyiliği emret ve cahillerden yüz çevir.', 9, 175, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1154, 200, 7, 'A\'raf', 'وَاِمَّا يَنْزَغَنَّكَ مِنَ الشَّيْطَانِ نَزْغٌ فَاسْتَعِذْ بِاللّٰهِۜ اِنَّهُ سَم۪يعٌ عَل۪يمٌ', 'Eğer şeytanın fitlemesi seni dürterse hemen Allah´a sığın. Çünkü O, işitendir, bilendir.', 9, 175, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1155, 201, 7, 'A\'raf', 'اِنَّ الَّذ۪ينَ اتَّقَوْا اِذَا مَسَّهُمْ طَٓائِفٌ مِنَ الشَّيْطَانِ تَذَكَّرُوا فَاِذَا هُمْ مُبْصِرُونَۚ', 'Takvâya erenler var ya, onlara şeytan tarafından bir vesvese dokunduğunda (Allah´ın emir ve yasaklarını) hatırlayıp hemen gerçeği görürler.', 9, 175, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1156, 202, 7, 'A\'raf', 'وَاِخْوَانُهُمْ يَمُدُّونَهُمْ فِي الْغَيِّ ثُمَّ لَا يُقْصِرُونَ', '(Şeytanların) dostlarına gelince, şeytanlar onları azgınlığa sürüklerler. Sonra da yakalarını bırakmazlar.', 9, 175, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1157, 203, 7, 'A\'raf', 'وَاِذَا لَمْ تَأْتِهِمْ بِاٰيَةٍ قَالُوا لَوْلَا اجْتَبَيْتَهَاۜ قُلْ اِنَّـمَٓا اَتَّبِـعُ مَا يُوحٰٓى اِلَيَّ مِنْ رَبّ۪يۚ هٰذَا بَصَٓائِرُ مِنْ رَبِّكُمْ وَهُدًى وَرَحْمَةٌ لِقَوْمٍ يُؤْمِنُونَ', 'Onlara bir mucize getirmediğin zaman, (ötekiler gibi) onu da derleyip getirseydin ya! derler. De ki: Ben ancak Rabbimden bana vahyolunana uyarım. Bu (Kur´an), Rabbinizden gelen basîretlerdir (kalp gözlerini açan beyanlardır); inanan bir kavim için hidayet ve rahmettir.', 9, 175, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1158, 204, 7, 'A\'raf', 'وَاِذَا قُرِئَ الْقُرْاٰنُ فَاسْتَمِعُوا لَهُ وَاَنْصِتُوا لَعَلَّكُمْ تُرْحَمُونَ', 'Kur´an okunduğu zaman onu dinleyin ve susun ki size merhamet edilsin.', 9, 175, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1159, 205, 7, 'A\'raf', 'وَاذْكُرْ رَبَّكَ ف۪ي نَفْسِكَ تَضَرُّعاً وَخ۪يفَةً وَدُونَ الْجَهْرِ مِنَ الْقَوْلِ بِالْغُدُوِّ وَالْاٰصَالِ وَلَا تَكُنْ مِنَ الْغَافِل۪ينَ', 'Kendi kendine, yalvararak ve ürpererek, yüksek olmayan bir sesle sabah akşam Rabbini an. Gafillerden olma.', 9, 175, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1160, 206, 7, 'A\'raf', 'اِنَّ الَّذ۪ينَ عِنْدَ رَبِّكَ لَا يَسْتَكْبِرُونَ عَنْ عِبَادَتِه۪ وَيُسَبِّحُونَهُ وَلَهُ يَسْجُدُونَ ۩', 'Kuşkusuz Rabbin katındakiler O´na kulluk etmekten kibirlenmezler, O´nu tesbih eder ve yalnız O´na secde ederler.', 9, 175, 206, 14071, 'Yüksek yerler, yüksek mevkiler', 'Mekke', 39),
(1161, 1, 8, 'Enfal', 'يَسْـَٔلُونَكَ عَنِ الْاَنْفَالِۜ قُلِ الْاَنْفَالُ لِلّٰهِ وَالرَّسُولِۚ فَاتَّقُوا اللّٰهَ وَاَصْلِحُوا ذَاتَ بَيْنِكُمْۖ وَاَط۪يعُوا اللّٰهَ وَرَسُولَهُٓ اِنْ كُنْتُمْ مُؤْمِن۪ينَ', 'Sana savaş ganimetlerini soruyorlar. De ki: Ganimetler Allah ve Peygamber´e aittir. O halde siz (gerçek) müminler iseniz Allah´tan korkun, aranızı düzeltin, Allah ve Resûlüne itaat edin.', 9, 176, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1162, 2, 8, 'Enfal', 'اِنَّمَا الْمُؤْمِنُونَ الَّذ۪ينَ اِذَا ذُكِرَ اللّٰهُ وَجِلَتْ قُلُوبُهُمْ وَاِذَا تُلِيَتْ عَلَيْهِمْ اٰيَاتُهُ زَادَتْهُمْ ا۪يمَاناً وَعَلٰى رَبِّهِمْ يَتَوَكَّلُونَۚ', 'Müminler ancak, Allah anıldığı zaman yürekleri titreyen, kendilerine Allah´ın âyetleri okunduğunda imanlarını artıran ve yalnız Rablerine dayanıp güvenen kimselerdir.', 9, 176, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1163, 3, 8, 'Enfal', 'اَلَّذ۪ينَ يُق۪يمُونَ الصَّلٰوةَ وَمِمَّا رَزَقْنَاهُمْ يُنْفِقُونَۜ', 'Onlar namazlarını dosdoğru kılan ve kendilerine rızık olarak verdiğimizden (Allah yolunda) harcayan kimselerdir.', 9, 176, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1164, 4, 8, 'Enfal', 'اُو۬لٰٓئِكَ هُمُ الْمُؤْمِنُونَ حَـقاًّۜ لَهُمْ دَرَجَاتٌ عِنْدَ رَبِّهِمْ وَمَغْفِرَةٌ وَرِزْقٌ كَر۪يمٌۚ', 'İşte onlar gerçek müminlerdir. Onlar için Rableri katında nice dereceler, bağışlanma ve tükenmez bir rızık vardır.', 9, 176, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1165, 5, 8, 'Enfal', 'كَمَٓا اَخْرَجَكَ رَبُّكَ مِنْ بَيْتِكَ بِالْحَقِّۖ وَاِنَّ فَر۪يقاً مِنَ الْمُؤْمِن۪ينَ لَكَارِهُونَۙ', '(Onların bu hali,) müminlerden bir gurup kesinlikle istemediği halde, Rabbinin seni evinden hak uğruna çıkardığı (zamanki halleri) gibidir.', 9, 176, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1166, 6, 8, 'Enfal', 'يُجَادِلُونَكَ فِي الْحَقِّ بَعْدَ مَا تَبَيَّنَ كَاَنَّمَا يُسَاقُونَ اِلَى الْمَوْتِ وَهُمْ يَنْظُرُونَۜ', 'Hak ortaya çıktıktan sonra sanki gözleri göre göre ölüme sürükleniyorlarmış gibi (cihad hususunda) seninle tartışıyorlardı.', 9, 176, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1167, 7, 8, 'Enfal', 'وَاِذْ يَعِدُكُمُ اللّٰهُ اِحْدَى الطَّٓائِفَتَيْنِ اَنَّهَا لَكُمْ وَتَوَدُّونَ اَنَّ غَيْرَ ذَاتِ الشَّوْكَةِ تَكُونُ لَكُمْ وَيُر۪يدُ اللّٰهُ اَنْ يُحِقَّ الْحَقَّ بِكَلِمَاتِه۪ وَيَقْطَعَ دَابِرَ الْكَافِر۪ينَۙ', 'Hatırlayın ki, Allah size, iki taifeden (kervan veya Kureyş ordusundan) birinin sizin olduğunu vadediyordu; siz de kuvvetsiz olanın (kervanın) sizin olmasını istiyordunuz. Oysa Allah, sözleriyle hakkı gerçekleştirmek ve (Kureyş ordusunu yok ederek) kâfirlerin ardını kesmek istiyordu.', 9, 176, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1168, 8, 8, 'Enfal', 'لِيُحِقَّ الْحَقَّ وَيُبْطِلَ الْبَاطِلَ وَلَوْ كَرِهَ الْمُجْرِمُونَۚ', '(Bunlar,) günahkârlar istemese de hakkı gerçekleştirmek ve bâtılı ortadan kaldırmak içindi.', 9, 176, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1169, 9, 8, 'Enfal', 'اِذْ تَسْتَغ۪يثُونَ رَبَّكُمْ فَاسْتَجَابَ لَكُمْ اَنّ۪ي مُمِدُّكُمْ بِاَلْفٍ مِنَ الْمَلٰٓئِكَةِ مُرْدِف۪ينَ', 'Hatırlayın ki, siz Rabbinizden yardım istiyordunuz. O da, ben peşpeşe gelen bin melek ile size yardım edeceğim, diyerek duanızı kabul buyurdu.', 9, 177, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1170, 10, 8, 'Enfal', 'وَمَا جَعَلَهُ اللّٰهُ اِلَّا بُشْرٰى وَلِتَطْمَئِنَّ بِه۪ قُلُوبُكُمْۚ وَمَا النَّصْرُ اِلَّا مِنْ عِنْدِ اللّٰهِۜ اِنَّ اللّٰهَ عَز۪يزٌ حَك۪يمٌ۟', 'Allah bunu (meleklerle yardımı) sadece müjde olsun ve onunla kalbiniz yatışsın diye yapmıştı. Zaten yardım yalnız Allah tarafındandır. Çünkü Allah mutlak galiptir, yegâne hüküm ve hikmet sahibidir.', 9, 177, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1171, 11, 8, 'Enfal', 'اِذْ يُغَشّ۪يكُمُ النُّعَاسَ اَمَنَةً مِنْهُ وَيُنَزِّلُ عَلَيْكُمْ مِنَ السَّمَٓاءِ مَٓاءً لِيُطَهِّرَكُمْ بِه۪ وَيُذْهِبَ عَنْكُمْ رِجْزَ الشَّيْطَانِ وَلِيَرْبِطَ عَلٰى قُلُوبِكُمْ وَيُثَبِّتَ بِهِ الْاَقْدَامَۜ', 'O zaman katından bir güven olmak üzere sizi hafif bir uykuya daldırıyordu; sizi temizlemek, şeytanın pisliğini (verdiği vesveseyi) sizden gidermek, kalplerinizi birbirine bağlamak ve savaşta sebat ettirmek için üzerinize gökten bir su (yağmur) indiriyordu.', 9, 177, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1172, 12, 8, 'Enfal', 'اِذْ يُوح۪ي رَبُّكَ اِلَى الْمَلٰٓئِكَةِ اَنّ۪ي مَعَكُمْ فَثَبِّتُوا الَّذ۪ينَ اٰمَنُواۜ سَاُلْق۪ي ف۪ي قُلُوبِ الَّذ۪ينَ كَفَرُوا الرُّعْبَ فَاضْرِبُوا فَوْقَ الْاَعْنَاقِ وَاضْرِبُوا مِنْهُمْ كُلَّ بَنَانٍۜ', 'Hani Rabbin meleklere: «Muhakkak ben sizinle beraberim; haydi iman edenlere destek olun; Ben kâfirlerin yüreğine korku salacağım; vurun boyunlarına! Vurun onların bütün parmaklarına! diye vahyediyordu.', 9, 177, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1173, 13, 8, 'Enfal', 'ذٰلِكَ بِاَنَّهُمْ شَٓاقُّوا اللّٰهَ وَرَسُولَهُۚ وَمَنْ يُشَاقِقِ اللّٰهَ وَرَسُولَهُ فَاِنَّ اللّٰهَ شَد۪يدُ الْعِقَابِ', 'Bu söylenenler, onların Allah´a ve Resûlüne karşı gelmelerinden ötürüdür. Kim Allah ve Resûlüne karşı gelirse, bilsin ki Allah, azabı şiddetli olandır.', 9, 177, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1174, 14, 8, 'Enfal', 'ذٰلِكُمْ فَذُوقُوهُ وَاَنَّ لِلْكَافِر۪ينَ عَذَابَ النَّارِ', 'İşte bu yenilgi size Allah´ın azabı! Şimdilik onu tadın! Kâfirlere bir de cehennem ateşinin azabı vardır.', 9, 177, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1175, 15, 8, 'Enfal', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُٓوا اِذَا لَق۪يتُمُ الَّذ۪ينَ كَفَرُوا زَحْفاً فَلَا تُوَلُّوهُمُ الْاَدْبَارَۚ', 'Ey müminler! Toplu halde kâfirlerle karşılaştığınız zaman onlara arkanızı dönmeyin. (Korkup kaçmayın).', 9, 177, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1176, 16, 8, 'Enfal', 'وَمَنْ يُوَلِّهِمْ يَوْمَئِذٍ دُبُرَهُٓ اِلَّا مُتَحَرِّفاً لِقِتَالٍ اَوْ مُتَحَيِّزاً اِلٰى فِئَةٍ فَقَدْ بَٓاءَ بِغَضَبٍ مِنَ اللّٰهِ وَمَأْوٰيهُ جَهَنَّمُۜ وَبِئْسَ الْمَص۪يرُ', 'Tekrar savaşmak için bir tarafa çekilme veya diğer bölüğe ulaşıp mevzi tutma durumu dışında, kim öyle bir günde onlara arka çevirirse muhakkak ki o, Allah´ın gazabını hak etmiş olarak döner. Onun yeri de cehennemdir. Orası, varılacak ne kötü yerdir!', 9, 177, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1177, 17, 8, 'Enfal', 'فَلَمْ تَقْتُلُوهُمْ وَلٰكِنَّ اللّٰهَ قَتَلَهُمْۖ وَمَا رَمَيْتَ اِذْ رَمَيْتَ وَلٰكِنَّ اللّٰهَ رَمٰىۚ وَلِيُبْلِيَ الْمُؤْمِن۪ينَ مِنْهُ بَلَٓاءً حَسَناًۜ اِنَّ اللّٰهَ سَم۪يعٌ عَل۪يمٌ', '(Savaşta) onları siz öldürmediniz, fakat Allah öldürdü onları; attığın zaman da sen atmadın, fakat Allah attı (onu). Ve bunu, müminleri güzel bir imtihanla denemek için (yaptı). Şüphesiz Allah işitendir, bilendir.', 9, 178, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1178, 18, 8, 'Enfal', 'ذٰلِكُمْ وَاَنَّ اللّٰهَ مُوهِنُ كَيْدِ الْكَافِر۪ينَ', 'Bu böyledir. Şüphesiz Allah, kâfirlerin tuzağını bozar.', 9, 178, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1179, 19, 8, 'Enfal', 'اِنْ تَسْتَفْتِحُوا فَقَدْ جَٓاءَكُمُ الْفَتْحُۚ وَاِنْ تَنْتَهُوا فَهُوَ خَيْرٌ لَكُمْۚ وَاِنْ تَعُودُوا نَعُدْۚ وَلَنْ تُغْنِيَ عَنْكُمْ فِئَتُكُمْ شَيْـٔاً وَلَوْ كَـثُرَتْۙ وَاَنَّ اللّٰهَ مَعَ الْمُؤْمِن۪ينَ۟', '(Ey kâfirler!) Eğer siz fetih istiyorsanız, işte size fetih geldi! (Yenelim derken yenildiniz.) Ve eğer (inkardan) vazgeçerseniz bu sizin için daha iyidir. Yine (Peygamber´e düşmanlığa) dönerseniz, biz de (ona) yardıma döneriz. Topluluğunuz çok bile olsa, sizden hiçbir şeyi savamaz. Çünkü Allah müminlerle beraberdir.', 9, 178, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1180, 20, 8, 'Enfal', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُٓوا اَط۪يعُوا اللّٰهَ وَرَسُولَهُ وَلَا تَوَلَّوْا عَنْهُ وَاَنْتُمْ تَسْمَعُونَۚ', 'Ey iman edenler! Allah´a ve Resûlüne itaat edin, işittiğiniz halde O´ndan yüz çevirmeyin.', 9, 178, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1181, 21, 8, 'Enfal', 'وَلَا تَكُونُوا كَالَّذ۪ينَ قَالُوا سَمِعْنَا وَهُمْ لَا يَسْمَعُونَ', 'İşitmedikleri halde işittik diyenler gibi olmayın.', 9, 178, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1182, 22, 8, 'Enfal', 'اِنَّ شَرَّ الدَّوَٓابِّ عِنْدَ اللّٰهِ الصُّمُّ الْبُكْمُ الَّذ۪ينَ لَا يَعْقِلُونَ', 'Şüphesiz Allah katında canlıların en kötüsü, düşünmeyen sağırlar ve dilsizlerdir.', 9, 178, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1183, 23, 8, 'Enfal', 'وَلَوْ عَلِمَ اللّٰهُ ف۪يهِمْ خَيْراً لَاَسْمَعَهُمْۜ وَلَوْ اَسْمَعَهُمْ لَتَوَلَّوْا وَهُمْ مُعْرِضُونَ', 'Allah onlarda bir hayır görseydi elbette onlara işittirirdi. Fakat işittirseydi bile yine onlar yüz çevirerek dönerlerdi.', 9, 178, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1184, 24, 8, 'Enfal', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا اسْتَج۪يبُوا لِلّٰهِ وَلِلرَّسُولِ اِذَا دَعَاكُمْ لِمَا يُحْي۪يكُمْۚ وَاعْلَمُٓوا اَنَّ اللّٰهَ يَحُولُ بَيْنَ الْمَرْءِ وَقَلْبِه۪ وَاَنَّـهُٓ اِلَيْهِ تُحْشَرُونَ', 'Ey inananlar! Hayat verecek şeylere sizi çağırdığı zaman, Allah ve Resûlüne uyun. Ve bilin ki, Allah kişi ile onun kalbi arasına girer ve siz mutlaka onun huzurunda toplanacaksınız.', 9, 178, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1185, 25, 8, 'Enfal', 'وَاتَّقُوا فِتْنَةً لَا تُص۪يبَنَّ الَّذ۪ينَ ظَلَمُوا مِنْكُمْ خَٓاصَّةًۚ وَاعْلَمُٓوا اَنَّ اللّٰهَ شَد۪يدُ الْعِقَابِ', 'Bir de öyle bir fitneden sakının ki o, içinizden sadece zulmedenlere erişmekle kalmaz (umuma sirayet ve hepsini perişan eder). Biliniz ki, Allah´ın azabı şiddetlidir.', 9, 178, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1186, 26, 8, 'Enfal', 'وَاذْكُـرُٓوا اِذْ اَنْتُمْ قَل۪يلٌ مُسْتَضْعَفُونَ فِي الْاَرْضِ تَخَافُونَ اَنْ يَتَخَطَّفَكُمُ النَّاسُ فَاٰوٰيكُمْ وَاَيَّدَكُمْ بِنَصْرِه۪ وَرَزَقَكُمْ مِنَ الطَّيِّبَاتِ لَعَلَّكُمْ تَشْكُرُونَ', 'Hatırlayın ki, bir zaman siz yeryüzünde âciz tanınan az (bir toplum) idiniz; insanların sizi kapıp götürmesinden korkuyordunuz da şükredesiniz diye Allah size yer yurt verdi; yardımıyla sizi destekledi ve size temizinden rızıklar verdi.', 9, 179, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1187, 27, 8, 'Enfal', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا لَا تَخُونُوا اللّٰهَ وَالرَّسُولَ وَتَخُونُٓوا اَمَانَاتِكُمْ وَاَنْتُمْ تَعْلَمُونَ', 'Ey iman edenler! Allah´a ve Peygamber´e hainlik etmeyin; (sonra) bile bile kendi emanetlerinize hainlik etmiş olursunuz.', 9, 179, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1188, 28, 8, 'Enfal', 'وَاعْلَمُٓوا اَنَّـمَٓا اَمْوَالُكُمْ وَاَوْلَادُكُمْ فِتْنَةٌۙ وَاَنَّ اللّٰهَ عِنْدَهُٓ اَجْرٌ عَظ۪يمٌ۟', 'Biliniz ki, mallarınız ve çocuklarınız birer imtihan sebebidir ve büyük mükâfat Allah´ın katındadır.', 9, 179, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1189, 29, 8, 'Enfal', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُٓوا اِنْ تَتَّقُوا اللّٰهَ يَجْعَلْ لَكُمْ فُرْقَاناً وَيُكَفِّرْ عَنْكُمْ سَيِّـَٔاتِكُمْ وَيَغْفِرْ لَكُمْۜ وَاللّٰهُ ذُوالْفَضْلِ الْعَظ۪يمِ', 'Ey iman edenler! Eğer Allah´tan korkarsanız O, size iyi ile kötüyü ayırdedecek bir anlayış verir, suçlarınızı örter ve sizi bağışlar. Çünkü Allah büyük lütuf sahibidir.', 9, 179, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1190, 30, 8, 'Enfal', 'وَاِذْ يَمْكُرُ بِكَ الَّذ۪ينَ كَفَرُوا لِيُثْبِتُوكَ اَوْ يَقْتُلُوكَ اَوْ يُخْرِجُوكَۜ وَيَمْكُرُونَ وَيَمْكُرُ اللّٰهُۜ وَاللّٰهُ خَيْرُ الْمَاكِر۪ينَ', 'Hatırla ki, kâfirler seni tutup bağlamaları veya öldürmeleri yahut seni (yurdundan) çıkarmaları için sana tuzak kuruyorlardı. Onlar (sana) tuzak kurarlarken Allah da (onlara) tuzak kuruyordu. Çünkü Allah tuzak kuranların en iyisidir.', 9, 179, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1191, 31, 8, 'Enfal', 'وَاِذَا تُتْلٰى عَلَيْهِمْ اٰيَاتُنَا قَالُوا قَدْ سَمِعْنَا لَوْ نَشَٓاءُ لَقُلْنَا مِثْلَ هٰذَٓاۙ اِنْ هٰذَٓا اِلَّٓا اَسَاط۪يرُ الْاَوَّل۪ينَ', 'Onlara âyetlerimiz okunduğu zaman dediler ki: «(Evet) işittik, istesek biz de bunun benzerini elbette söyleyebiliriz. Bu öncekilerin masallarından başka bir şey değildir.»', 9, 179, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1192, 32, 8, 'Enfal', 'وَاِذْ قَالُوا اللّٰهُمَّ اِنْ كَانَ هٰذَا هُوَ الْحَقَّ مِنْ عِنْدِكَ فَاَمْطِرْ عَلَيْنَا حِجَارَةً مِنَ السَّمَٓاءِ اَوِ ائْتِنَا بِعَذَابٍ اَل۪يمٍ', 'Hani (o kâfirler) bir zaman da: Ey Allah´ım! Eğer bu Kitap senin katından gelmiş bir gerçekse üzerimize gökten taş yağdır, yahut bize elem verici bir azap getir! demişlerdi.', 9, 179, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1193, 33, 8, 'Enfal', 'وَمَا كَانَ اللّٰهُ لِيُعَذِّبَهُمْ وَاَنْتَ ف۪يهِمْۜ وَمَا كَانَ اللّٰهُ مُعَذِّبَهُمْ وَهُمْ يَسْتَغْفِرُونَ', 'Halbuki sen onların içinde iken Allah, onlara azap edecek değildir. Ve onlar mağfiret dilerlerken de Allah onlara azap edici değildir.', 9, 179, 75, 5299, 'Savaş ganimetleri', 'Medine', 88);
INSERT INTO `Ayetler` (`id`, `AyetNo`, `SureNo`, `SureAdi`, `Metin`, `Mana`, `Cuzu`, `Sayfasi`, `AyetSayisi`, `HarfSayisi`, `AdAnlami`, `IndigiYer`, `InisSirasi`) VALUES
(1194, 34, 8, 'Enfal', 'وَمَا لَهُمْ اَلَّا يُعَذِّبَهُمُ اللّٰهُ وَهُمْ يَصُدُّونَ عَنِ الْمَسْجِدِ الْحَرَامِ وَمَا كَانُٓوا اَوْلِيَٓاءَهُۜ اِنْ اَوْلِيَٓاؤُ۬هُٓ اِلَّا الْمُتَّقُونَ وَلٰكِنَّ اَكْثَرَهُمْ لَا يَعْلَمُونَ', 'Onlar Mescid-i Haram´ın mütevellîleri olmadıkları halde (müminleri) oradan geri çevirirlerken Allah onlara ne diye azap etmeyecek? Oranın mütevellîleri takvâ sahiplerinden başkaları değildir. Fakat onların çoğu bunu bilmez.', 9, 180, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1195, 35, 8, 'Enfal', 'وَمَا كَانَ صَلَاتُهُمْ عِنْدَ الْبَيْتِ اِلَّا مُكَٓاءً وَتَصْدِيَةًۜ فَذُوقُوا الْعَذَابَ بِمَا كُنْتُمْ تَكْفُرُونَ', 'Onların Beytullah yanındaki duaları da ıslık çalmak ve el çırpmaktan başka bir şey değildir. (Ey kâfirler!) İnkâr etmekte olduğunuz şeylerden ötürü şimdi azabı tadın!', 9, 180, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1196, 36, 8, 'Enfal', 'اِنَّ الَّذ۪ينَ كَفَرُوا يُنْفِقُونَ اَمْوَالَهُمْ لِيَصُدُّوا عَنْ سَب۪يلِ اللّٰهِۜ فَسَيُنْفِقُونَهَا ثُمَّ تَكُونُ عَلَيْهِمْ حَسْرَةً ثُمَّ يُغْلَبُونَۜ وَالَّذ۪ينَ كَفَرُٓوا اِلٰى جَهَنَّمَ يُحْشَرُونَۙ', 'Şüphesiz ki inkâr edenler mallarını, (insanları) Allah yolundan alıkoymak için harcıyorlar. Daha da harcayacaklar. Ama sonunda bu, onlara yürek acısı olacak ve en sonunda mağlûp olacaklardır. Kâfirlikte ısrar edenler ise cehenneme toplanacaklardır.', 9, 180, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1197, 37, 8, 'Enfal', 'لِيَم۪يزَ اللّٰهُ الْخَب۪يثَ مِنَ الطَّيِّبِ وَيَجْعَلَ الْخَب۪يثَ بَعْضَهُ عَلٰى بَعْضٍ فَيَرْكُمَهُ جَم۪يعاً فَيَجْعَلَهُ ف۪ي جَهَنَّمَۜ اُو۬لٰٓئِكَ هُمُ الْخَاسِرُونَ۟', '(Bu toplama) Allah´ın murdarı temizden ayıklaması (mümini kâfirden ayırması) ve bütün murdarların bir kısmını diğer bir kısmının üstüne koyup hepsini yığarak cehenneme atması içindir. İşte onlar ziyana uğrayanların kendileridir.', 9, 180, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1198, 38, 8, 'Enfal', 'قُلْ لِلَّذ۪ينَ كَفَرُٓوا اِنْ يَنْتَهُوا يُغْفَرْ لَهُمْ مَا قَدْ سَلَفَۚ وَاِنْ يَعُودُوا فَقَدْ مَضَتْ سُنَّتُ الْاَوَّل۪ينَ', 'İnkâr edenlere, (sana düşmanlıktan) vazgeçerlerse, geçmiş günahlarının bağışlanacağını söyle. Yok geri dönerlerse kendilerinden öncekilerin hali gözlerinin önündedir!', 9, 180, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1199, 39, 8, 'Enfal', 'وَقَاتِلُوهُمْ حَتّٰى لَا تَكُونَ فِتْنَةٌ وَيَكُونَ الدّ۪ينُ كُلُّهُ لِلّٰهِۚ فَاِنِ انْتَهَوْا فَاِنَّ اللّٰهَ بِمَا يَعْمَلُونَ بَص۪يرٌ', 'Fitne ortadan kalkıncaya ve din tamamen Allah´ın oluncaya kadar onlarla savaşın! (İnkâra) son verirlerse şüphesiz ki Allah onların yaptıklarını çok iyi görür.', 9, 180, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1200, 40, 8, 'Enfal', 'وَاِنْ تَوَلَّوْا فَاعْلَمُٓوا اَنَّ اللّٰهَ مَوْلٰيكُمْۜ نِعْمَ الْمَوْلٰى وَنِعْمَ النَّص۪يرُ', 'Eğer (imandan) yüz çevirirlerse, bilin ki Allah sizin sahibinizdir. O ne güzel sahip ve ne güzel yardımcıdır!', 9, 180, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1201, 41, 8, 'Enfal', 'وَاعْلَمُٓوا اَنَّمَا غَنِمْتُمْ مِنْ شَيْءٍ فَاَنَّ لِلّٰهِ خُمُسَهُ وَلِلرَّسُولِ وَلِذِي الْقُرْبٰى وَالْيَتَامٰى وَالْمَسَاك۪ينِ وَابْنِ السَّب۪يلِۙ اِنْ كُنْتُمْ اٰمَنْتُمْ بِاللّٰهِ وَمَٓا اَنْزَلْنَا عَلٰى عَبْدِنَا يَوْمَ الْفُرْقَانِ يَوْمَ الْتَقَى الْجَمْعَانِۜ وَاللّٰهُ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌ', 'Eğer Allah´a ve hak ile bâtılın ayrıldığı gün, iki ordunun birbiri ile karşılaştığı gün (Bedir savaşında) kulumuza indirdiğimize inanmışsanız, bilin ki, ganimet olarak aldığınız herhangi bir şeyin beşte biri Allah´a, Resûlüne, onun akrabalarına, yetimlere, yoksullara ve yolcuya aittir. Allah her şeye hakkıyla kadirdir.', 10, 181, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1202, 42, 8, 'Enfal', 'اِذْ اَنْتُمْ بِالْعُدْوَةِ الدُّنْيَا وَهُمْ بِالْعُدْوَةِ الْقُصْوٰى وَالرَّكْبُ اَسْفَلَ مِنْكُمْۜ وَلَوْ تَوَاعَدْتُمْ لَاخْتَلَفْتُمْ فِي الْم۪يعَادِۙ وَلٰكِنْ لِيَقْضِيَ اللّٰهُ اَمْراً كَانَ مَفْعُولاًۙ لِيَهْلِكَ مَنْ هَلَكَ عَنْ بَيِّنَةٍ وَيَحْيٰى مَنْ حَيَّ عَنْ بَيِّنَةٍۜ وَاِنَّ اللّٰهَ لَسَم۪يعٌ عَل۪يمٌۙ', 'Hatırlayın ki, (Bedir savaşında) siz vâdinin yakın kenarında (Medine tarafında) idiniz, onlar da uzak kenarında (Mekke tarafında) idiler. Kervan da sizden daha aşağıda (deniz sahilinde) idi. Eğer (savaş için) sözleşmiş olsaydınız, sözleştiğiniz vakit hususunda ihtilâfa düşerdiniz. Fakat Allah, gerekli olan emri yerine getirmesi, helâk olanın açık bir delille (gözüyle gördükten sonra) helâk olması, yaşayanın da açık bir delille yaşaması için (böyle yaptı). Çünkü Allah hakkıyla işitendir, bilendir.', 10, 181, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1203, 43, 8, 'Enfal', 'اِذْ يُر۪يكَهُمُ اللّٰهُ ف۪ي مَنَامِكَ قَل۪يلاًۜ وَلَوْ اَرٰيكَهُمْ كَث۪يراً لَفَشِلْتُمْ وَلَتَنَازَعْتُمْ فِي الْاَمْرِ وَلٰكِنَّ اللّٰهَ سَلَّمَۜ اِنَّهُ عَل۪يمٌ بِذَاتِ الصُّدُورِ', 'Hatırla ki, Allah, uykunda sana onları az gösterdi. Eğer onları sana çok gösterseydi, elbette çekinecek ve bu iş hakkında münakaşaya girişecektiniz. Fakat Allah (sizi bundan) kurtardı. Şüphesiz O, kalplerin özünü bilir.', 10, 181, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1204, 44, 8, 'Enfal', 'وَاِذْ يُر۪يكُمُوهُمْ اِذِ الْتَقَيْتُمْ ف۪ٓي اَعْيُنِكُمْ قَل۪يلاً وَيُقَلِّلُكُمْ ف۪ٓي اَعْيُنِهِمْ لِيَقْضِيَ اللّٰهُ اَمْراً كَانَ مَفْعُولاًۜ وَاِلَى اللّٰهِ تُرْجَعُ الْاُمُورُ۟', 'Allah, olacak bir işi yerine getirmek için (savaş alanında) karşılaştığınız zaman onları sizin gözlerinizde az gösteriyor, sizi de onların gözlerinde azaltıyordu. Bütün işler Allah´a döner.', 10, 181, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1205, 45, 8, 'Enfal', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُٓوا اِذَا لَق۪يتُمْ فِئَةً فَاثْبُتُوا وَاذْكُرُوا اللّٰهَ كَث۪يراً لَعَلَّكُمْ تُفْلِحُونَۚ', 'Ey iman edenler! Herhangi bir topluluk ile karşılaştığınız zaman sebat edin ve Allah´ı çok anın ki başarıya erişesiniz.', 10, 181, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1206, 46, 8, 'Enfal', 'وَاَط۪يعُوا اللّٰهَ وَرَسُولَهُ وَلَا تَنَازَعُوا فَتَفْشَلُوا وَتَذْهَبَ ر۪يحُكُمْ وَاصْبِرُواۜ اِنَّ اللّٰهَ مَعَ الصَّابِر۪ينَۚ', 'Allah ve Resûlüne itaat edin, birbirinizle çekişmeyin; sonra korkuya kapılırsınız da kuvvetiniz gider. Bir de sabredin. Çünkü Allah sabredenlerle beraberdir.', 10, 182, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1207, 47, 8, 'Enfal', 'وَلَا تَكُونُوا كَالَّذ۪ينَ خَرَجُوا مِنْ دِيَارِهِمْ بَطَراً وَرِئَٓاءَ النَّاسِ وَيَصُدُّونَ عَنْ سَب۪يلِ اللّٰهِۜ وَاللّٰهُ بِمَا يَعْمَلُونَ مُح۪يطٌ', 'Çalım satmak, insanlara gösteriş yapmak ve (insanları) Allah yolundan alıkoymak için yurtlarından çıkanlar (kâfirler) gibi olmayın. Allah onların yaptıklarını çepeçevre kuşatmıştır.', 10, 182, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1208, 48, 8, 'Enfal', 'وَاِذْ زَيَّنَ لَهُمُ الشَّيْطَانُ اَعْمَالَهُمْ وَقَالَ لَا غَالِبَ لَكُمُ الْيَوْمَ مِنَ النَّاسِ وَاِنّ۪ي جَارٌ لَكُمْۚ فَلَمَّا تَرَٓاءَتِ الْفِئَتَانِ نَكَصَ عَلٰى عَقِبَيْهِ وَقَالَ اِنّ۪ي بَر۪ٓيءٌ مِنْكُمْ اِنّ۪ٓي اَرٰى مَا لَا تَرَوْنَ اِنّ۪ٓي اَخَافُ اللّٰهَۜ وَاللّٰهُ شَد۪يدُ الْعِقَابِ۟', 'Hani şeytan onlara yaptıklarını güzel gösterdi de: Bugün insanlardan size galip gelecek kimse yoktur, şüphesiz ben de sizin yardımcınızım, dedi. Fakat iki ordu birbirini görünce ardına döndü ve: Ben sizden uzağım, ben sizin göremediklerinizi (melekleri) görüyorum, ben Allah´tan korkuyorum; Allah´ın azabı şiddetlidir, dedi.', 10, 182, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1209, 49, 8, 'Enfal', 'اِذْ يَقُولُ الْمُنَافِقُونَ وَالَّذ۪ينَ ف۪ي قُلُوبِهِمْ مَرَضٌ غَرَّ هٰٓؤُ۬لَٓاءِ د۪ينُهُمْۜ وَمَنْ يَتَوَكَّلْ عَلَى اللّٰهِ فَاِنَّ اللّٰهَ عَز۪يزٌ حَك۪يمٌ', 'O zaman münafıklarla kalplerinde hastalık bulunanlar, (sizin için), «Bunları, dinleri aldatmış» diyorlardı. Halbuki kim Allah´a dayanırsa, bilsin ki Allah mutlak galiptir, hikmet sahibidir. (Kendisine güveneni üstün ve galip kılacak O´dur. Yoksa orduların sayı ve techizat üstünlüğü değildir).', 10, 182, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1210, 50, 8, 'Enfal', 'وَلَوْ تَرٰٓى اِذْ يَتَوَفَّى الَّذ۪ينَ كَفَرُواۙ الْمَلٰٓئِكَةُ يَضْرِبُونَ وُجُوهَهُمْ وَاَدْبَارَهُمْۚ وَذُوقُوا عَذَابَ الْحَر۪يقِ', 'Melekler yüzlerine ve arkalarına vurarak ve «Tadın yakıcı cehennem azabını» (diyerek) o kâfirlerin canlarını alırken onları bir görseydin!', 10, 182, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1211, 51, 8, 'Enfal', 'ذٰلِكَ بِمَا قَدَّمَتْ اَيْد۪يكُمْ وَاَنَّ اللّٰهَ لَيْسَ بِظَلَّامٍ لِلْعَب۪يدِۙ', 'İşte bu, ellerinizle yaptığınız yüzündendir, yoksa Allah kullara zulmedici değildir.', 10, 182, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1212, 52, 8, 'Enfal', 'كَدَأْبِ اٰلِ فِرْعَوْنَۙ وَالَّذ۪ينَ مِنْ قَبْلِهِمْۜ كَفَرُوا بِاٰيَاتِ اللّٰهِ فَاَخَذَهُمُ اللّٰهُ بِذُنُوبِهِمْۜ اِنَّ اللّٰهَ قَوِيٌّ شَد۪يدُ الْعِقَابِ', '(Bunların gidişatı) tıpkı Firavun ailesi ve onlardan öncekilerin gidişatı gibidir. (Onlar da) Allah´ın âyetlerini inkâr etmişlerdi de Allah onları günahları sebebiyle yakalamıştı. Allah güçlüdür. O´nun cezası şiddetlidir.', 10, 182, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1213, 53, 8, 'Enfal', 'ذٰلِكَ بِاَنَّ اللّٰهَ لَمْ يَكُ مُغَيِّراً نِعْمَةً اَنْعَمَهَا عَلٰى قَوْمٍ حَتّٰى يُغَيِّرُوا مَا بِاَنْفُسِهِمْۙ وَاَنَّ اللّٰهَ سَم۪يعٌ عَل۪يمٌۙ', 'Bu da, bir millet kendilerinde bulunanı (güzel ahlâk ve meziyetleri) değiştirinceye kadar Allah´ın onlara verdiği nimeti değiştirmeyeceğinden dolayıdır. Gerçekten Allah işitendir, bilendir.', 10, 183, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1214, 54, 8, 'Enfal', 'كَدَأْبِ اٰلِ فِرْعَوْنَۙ وَالَّذ۪ينَ مِنْ قَبْلِهِمْۜ كَذَّبُوا بِاٰيَاتِ رَبِّهِمْۚ فَاَهْلَكْنَاهُمْ بِذُنُوبِهِمْ وَاَغْرَقْـنَٓا اٰلَ فِرْعَوْنَۚ وَكُلٌّ كَانُوا ظَالِم۪ينَ', '(Evet bunların durumu), Firavun ailesi ve onlardan öncekilerin durumuna benzer. Onlar Rablerinin âyetlerini yalanlamışlardı; biz de onları günahlarından ötürü helâk etmiştik ve Firavun ailesini (denizde) boğmuştuk. Hepsi de zalimler idiler.', 10, 183, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1215, 55, 8, 'Enfal', 'اِنَّ شَرَّ الدَّوَٓابِّ عِنْدَ اللّٰهِ الَّذ۪ينَ كَفَرُوا فَهُمْ لَا يُؤْمِنُونَۚ', 'Allah katında, canlıların en kötüsü kâfir olanlardır. Çünkü onlar iman etmezler.', 10, 183, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1216, 56, 8, 'Enfal', 'اَلَّذ۪ينَ عَاهَدْتَ مِنْهُمْ ثُمَّ يَنْقُضُونَ عَهْدَهُمْ ف۪ي كُلِّ مَرَّةٍ وَهُمْ لَا يَتَّقُونَ', 'Onlar, kendileriyle antlaşma yaptığın, sonra her defasında hiç çekinmeden ahidlerini bozan kimselerdir.', 10, 183, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1217, 57, 8, 'Enfal', 'فَاِمَّا تَثْقَفَنَّهُمْ فِي الْحَرْبِ فَشَرِّدْ بِهِمْ مَنْ خَلْفَهُمْ لَعَلَّهُمْ يَذَّكَّرُونَ', 'Eğer savaşta onları yakalarsan, ibret almaları için onlar ile (onlara vereceğin ceza ile) arkalarında bulunan kimseleri de dağıt.', 10, 183, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1218, 58, 8, 'Enfal', 'وَاِمَّا تَخَافَنَّ مِنْ قَوْمٍ خِيَانَةً فَانْبِذْ اِلَيْهِمْ عَلٰى سَوَٓاءٍۜ اِنَّ اللّٰهَ لَا يُحِبُّ الْخَٓائِن۪ينَ۟', '(Antlaşma yaptığın) bir kavmin hainlik yapmasından korkarsan, sen de (onlarla yaptığın ahdi) aynı şekilde bozduğunu kendilerine bildir. Çünkü Allah, hainleri sevmez.', 10, 183, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1219, 59, 8, 'Enfal', 'وَلَا يَحْسَبَنَّ الَّذ۪ينَ كَفَرُوا سَبَقُواۜ اِنَّهُمْ لَا يُعْجِزُونَ', 'İnkâr edenler yakayı kurtardıklarını sanmasınlar. Çünkü onlar (bizi) âciz bırakamazlar.', 10, 183, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1220, 60, 8, 'Enfal', 'وَاَعِدُّوا لَهُمْ مَا اسْتَطَعْتُمْ مِنْ قُوَّةٍ وَمِنْ رِبَاطِ الْخَيْلِ تُرْهِبُونَ بِه۪ عَدُوَّ اللّٰهِ وَعَدُوَّكُمْ وَاٰخَر۪ينَ مِنْ دُونِهِمْۚ لَا تَعْلَمُونَهُمْۚ اَللّٰهُ يَعْلَمُهُمْۜ وَمَا تُنْفِقُوا مِنْ شَيْءٍ ف۪ي سَب۪يلِ اللّٰهِ يُوَفَّ اِلَيْكُمْ وَاَنْتُمْ لَا تُظْلَمُونَ', 'Onlara (düşmanlara) karşı gücünüz yettiği kadar kuvvet ve cihad için bağlanıp beslenen atlar hazırlayın, onunla Allah´ın düşmanını, sizin düşmanınızı ve onlardan başka sizin bilmediğiniz, Allah´ın bildiği (düşman) kimseleri korkutursunuz. Allah yolunda ne harcarsanız size eksiksiz ödenir, siz asla haksızlığa uğratılmazsınız.', 10, 183, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1221, 61, 8, 'Enfal', 'وَاِنْ جَنَحُوا لِلسَّلْمِ فَاجْنَحْ لَهَا وَتَوَكَّلْ عَلَى اللّٰهِۜ اِنَّهُ هُوَ السَّم۪يعُ الْعَل۪يمُ', 'Eğer onlar barışa yanaşırlarsa sen de ona yanaş ve Allah´a tevekkül et, çünkü O işitendir, bilendir.', 10, 183, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1222, 62, 8, 'Enfal', 'وَاِنْ يُر۪يدُٓوا اَنْ يَخْدَعُوكَ فَاِنَّ حَسْبَكَ اللّٰهُۜ هُوَ الَّـذ۪ٓي اَيَّدَكَ بِنَصْرِه۪ وَبِالْمُؤْمِن۪ينَۙ', 'Eğer sana hile yapmak isterlerse, şunu bil ki, Allah sana kâfidir. O, seni yardımıyla ve müminlerle destekleyendir.', 10, 184, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1223, 63, 8, 'Enfal', 'وَاَلَّفَ بَيْنَ قُلُوبِهِمْۜ لَوْ اَنْفَقْتَ مَا فِي الْاَرْضِ جَم۪يعاً مَٓا اَلَّفْتَ بَيْنَ قُلُوبِهِمْ وَلٰكِنَّ اللّٰهَ اَلَّفَ بَيْنَهُمْۜ اِنَّهُ عَز۪يزٌ حَك۪يمٌ', 'Ve (Allah), onların kalplerini birleştirmiştir. Sen yeryüzünde bulunan her şeyi verseydin, yine onların gönüllerini birleştiremezdin, fakat Allah onların aralarını bulup kaynaştırdı. Çünkü O, mutlak galiptir, hikmet sahibidir.', 10, 184, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1224, 64, 8, 'Enfal', 'يَٓا اَيُّهَا النَّبِيُّ حَسْبُكَ اللّٰهُ وَمَنِ اتَّبَعَكَ مِنَ الْمُؤْمِن۪ينَ۟', 'Ey Peygamber! Sana ve sana uyan müminlere Allah yeter.', 10, 184, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1225, 65, 8, 'Enfal', 'يَٓا اَيُّهَا النَّبِيُّ حَرِّضِ الْمُؤْمِن۪ينَ عَلَى الْقِتَالِۜ اِنْ يَكُنْ مِنْكُمْ عِشْرُونَ صَابِرُونَ يَغْلِبُوا مِائَتَيْنِۚ وَاِنْ يَكُنْ مِنْكُمْ مِائَةٌ يَغْلِبُٓوا اَلْفاً مِنَ الَّذ۪ينَ كَفَرُوا بِاَنَّهُمْ قَوْمٌ لَا يَفْقَهُونَ', 'Ey Peygamber! Müminleri savaşa teşvik et. Eğer sizden sabırlı yirmi kişi bulunursa, iki yüze (kâfire) galip gelirler. Eğer sizden yüz kişi olursa, kâfir olanlardan bin kişiye galip gelirler. Çünkü onlar anlamayan bir topluluktur.', 10, 184, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1226, 66, 8, 'Enfal', 'اَلْـٰٔنَ خَفَّفَ اللّٰهُ عَنْكُمْ وَعَلِمَ اَنَّ ف۪يكُمْ ضَعْفاًۜ فَاِنْ يَكُنْ مِنْكُمْ مِائَةٌ صَابِرَةٌ يَغْلِبُوا مِائَتَيْنِۚ وَاِنْ يَكُنْ مِنْكُمْ اَلْفٌ يَغْلِبُٓوا اَلْفَيْنِ بِاِذْنِ اللّٰهِۜ وَاللّٰهُ مَعَ الصَّابِر۪ينَ', 'Şimdi Allah, yükünüzü hafifletti; sizde zayıflık olduğunu bildi. O halde sizden sabırlı yüz kişi bulunursa, (onlardan) ikiyüz kişiye galip gelir. Ve eğer sizden bin kişi olursa, Allah´ın izniyle (onlardan) ikibin kişiye galip gelirler. Allah sabredenlerle beraberdir.', 10, 184, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1227, 67, 8, 'Enfal', 'مَا كَانَ لِنَبِيٍّ اَنْ يَكُونَ لَـهُٓ اَسْرٰى حَتّٰى يُثْخِنَ فِي الْاَرْضِۜ تُر۪يدُونَ عَرَضَ الدُّنْيَاۗ وَاللّٰهُ يُر۪يدُ الْاٰخِرَةَۜ وَاللّٰهُ عَز۪يزٌ حَك۪يمٌ', 'Yeryüzünde ağır basıncaya (küfrün belini kırıncaya) kadar, hiçbir peygambere esirleri bulunması yaraşmaz. Siz geçici dünya malını istiyorsunuz, halbuki Allah (sizin için) ahireti istiyor. Allah güçlüdür, hikmet sahibidir.', 10, 184, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1228, 68, 8, 'Enfal', 'لَوْلَا كِتَابٌ مِنَ اللّٰهِ سَبَقَ لَمَسَّكُمْ ف۪يمَٓا اَخَذْتُمْ عَذَابٌ عَظ۪يمٌ', 'Allah tarafından önceden verilmiş bir hüküm olmasaydı, aldığınız fidyeden ötürü size mutlaka büyük bir azap dokunurdu.', 10, 184, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1229, 69, 8, 'Enfal', 'فَكُلُوا مِمَّا غَنِمْتُمْ حَلَالاً طَيِّباًۘ وَاتَّقُوا اللّٰهَۜ اِنَّ اللّٰهَ غَفُورٌ رَح۪يمٌ۟', 'Artık elde ettiğiniz ganimetten helâl ve temiz olarak yeyin. Ve Allah´tan korkun. Şüphesiz ki Allah bağışlayan, merhamet edendir.', 10, 184, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1230, 70, 8, 'Enfal', 'يَٓا اَيُّهَا النَّبِيُّ قُلْ لِمَنْ ف۪ٓي اَيْد۪يكُمْ مِنَ الْاَسْرٰٓىۙ اِنْ يَعْلَمِ اللّٰهُ ف۪ي قُلُوبِكُمْ خَيْراً يُؤْتِكُمْ خَيْراً مِمَّٓا اُخِذَ مِنْكُمْ وَيَغْفِرْ لَكُمْۜ وَاللّٰهُ غَفُورٌ رَح۪يمٌ', 'Ey Peygamber! Elinizdeki esirlere de ki: Eğer Allah kalplerinizde hayır olduğunu bilirse, sizden alınandan (fidyeden) daha hayırlısını size verir ve sizi bağışlar. Çünkü Allah bağışlayandır, esirgeyendir.', 10, 185, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1231, 71, 8, 'Enfal', 'وَاِنْ يُر۪يدُوا خِيَانَتَكَ فَقَدْ خَانُوا اللّٰهَ مِنْ قَبْلُ فَاَمْكَنَ مِنْهُمْۜ وَاللّٰهُ عَل۪يمٌ حَك۪يمٌ', 'Eğer sana hainlik etmek isterlerse (üzülme, çünkü) daha önce Allah´a da hainlik etmişlerdi de Allah onlara karşı sana imkân ve kudret vermişti. Allah bilendir, hikmet sahibidir.', 10, 185, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1232, 72, 8, 'Enfal', 'اِنَّ الَّذ۪ينَ اٰمَنُوا وَهَاجَرُوا وَجَاهَدُوا بِاَمْوَالِهِمْ وَاَنْفُسِهِمْ ف۪ي سَب۪يلِ اللّٰهِ وَالَّذ۪ينَ اٰوَوْا وَنَصَرُٓوا اُو۬لٰٓئِكَ بَعْضُهُمْ اَوْلِيَٓاءُ بَعْضٍۜ وَالَّذ۪ينَ اٰمَنُوا وَلَمْ يُهَاجِرُوا مَا لَكُمْ مِنْ وَلَايَتِهِمْ مِنْ شَيْءٍ حَتّٰى يُهَاجِرُواۚ وَاِنِ اسْتَنْصَرُوكُمْ فِي الدّ۪ينِ فَعَلَيْكُمُ النَّصْرُ اِلَّا عَلٰى قَوْمٍ بَيْنَكُمْ وَبَيْنَهُمْ م۪يثَاقٌۜ وَاللّٰهُ بِمَا تَعْمَلُونَ بَص۪يرٌ', 'İman edip de hicret edenler, Allah yolunda mallarıyla, canlarıyla cihad edenler ve (muhacirleri) barındırıp yardım edenler var ya, işte onların bir kısmı diğer bir kısmının dostlarıdır. İman edip de hicret etmeyenlere gelince, onlar hicret edinceye kadar size onların mirasından hiçbir pay yoktur. Eğer onlar din hususunda sizden yardım isterlerse, sizinle aralarında sözleşme bulunan bir kavim aleyhine olmaksızın (o müslümanlara) yardım etmek üzerinize borçtur. Allah yapacaklarınızı hakkıyla görmektedir.', 10, 185, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1233, 73, 8, 'Enfal', 'وَالَّذ۪ينَ كَفَرُوا بَعْضُهُمْ اَوْلِيَٓاءُ بَعْضٍۜ اِلَّا تَفْعَلُوهُ تَكُنْ فِتْنَةٌ فِي الْاَرْضِ وَفَسَادٌ كَب۪يرٌۜ', 'Kâfir olanlar da birbirlerinin yardımcılarıdır. Eğer siz onu (Allah´ın emirlerini) yerine getirmezseniz yeryüzünde bir fitne ve büyük bir fesat olur.', 10, 185, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1234, 74, 8, 'Enfal', 'وَالَّذ۪ينَ اٰمَنُوا وَهَاجَرُوا وَجَاهَدُوا ف۪ي سَب۪يلِ اللّٰهِ وَالَّذ۪ينَ اٰوَوْا وَنَصَرُٓوا اُو۬لٰٓئِكَ هُمُ الْمُؤْمِنُونَ حَقاًّۜ لَهُمْ مَغْفِرَةٌ وَرِزْقٌ كَر۪يمٌ', 'İman edip de Allah yolunda hicret ve cihad edenler, (muhacirleri) barındıran ve yardım edenler var ya, işte gerçek müminler onlardır. Onlar için mağfiret ve bol rızık vardır.', 10, 185, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1235, 75, 8, 'Enfal', 'وَالَّذ۪ينَ اٰمَنُوا مِنْ بَعْدُ وَهَاجَرُوا وَجَاهَدُوا مَعَكُمْ فَاُو۬لٰٓئِكَ مِنْكُمْۜ وَاُو۬لُوا الْاَرْحَامِ بَعْضُهُمْ اَوْلٰى بِبَعْضٍ ف۪ي كِتَابِ اللّٰهِۜ اِنَّ اللّٰهَ بِكُلِّ شَيْءٍ عَل۪يمٌ', 'Sonradan iman eden ve hicret edip de sizinle beraber cihad edenler de sizdendir. Allah´ın kitabına göre yakın akrabalar birbirlerine (vâris olmağa) daha uygundur. Şüphesiz ki Allah her şeyi bilendir.', 10, 185, 75, 5299, 'Savaş ganimetleri', 'Medine', 88),
(1236, 1, 9, 'Tevbe', 'بَرَٓاءَةٌ مِنَ اللّٰهِ وَرَسُولِه۪ٓ اِلَى الَّذ۪ينَ عَاهَدْتُمْ مِنَ الْمُشْرِك۪ينَۜ', 'Allah ve Resûlünden kendileriyle antlaşma yapmış olduğunuz müşriklere bir ihtar!', 10, 186, 129, 10873, 'Tövbe', 'Medine', 113),
(1237, 2, 9, 'Tevbe', 'فَس۪يحُوا فِي الْاَرْضِ اَرْبَعَةَ اَشْهُرٍ وَاعْلَمُٓوا اَنَّكُمْ غَيْرُ مُعْجِزِي اللّٰهِۙ وَاَنَّ اللّٰهَ مُخْزِي الْكَافِر۪ينَ', '(Ey müşrikler!) Yeryüzünde dört ay daha dolaşın. İyi bilin ki siz Allah´ı âciz bırakacak değilsiniz; Allah ise kâfirleri rezil (ve perişan) edecektir.', 10, 186, 129, 10873, 'Tövbe', 'Medine', 113),
(1238, 3, 9, 'Tevbe', 'وَاَذَانٌ مِنَ اللّٰهِ وَرَسُولِه۪ٓ اِلَى النَّاسِ يَوْمَ الْحَجِّ الْاَكْبَرِ اَنَّ اللّٰهَ بَر۪ٓيءٌ مِنَ الْمُشْرِك۪ينَۙ وَرَسُولُهُۜ فَاِنْ تُبْتُمْ فَهُوَ خَيْرٌ لَكُمْۚ وَاِنْ تَوَلَّيْتُمْ فَاعْلَمُٓوا اَنَّكُمْ غَيْرُ مُعْجِزِي اللّٰهِۜ وَبَشِّرِ الَّذ۪ينَ كَفَرُوا بِعَذَابٍ اَل۪يمٍۙ', 'Hacc-ı ekber (en büyük hac) gününde Allah ve Resûlünden insanlara bir bildiridir: Allah ve Resûlü müşriklerden uzaktır. Eğer tevbe ederseniz, bu sizin için daha hayırlıdır. Ve eğer yüz çevirirseniz bilin ki, siz Allah´ı âciz bırakacak değilsiniz. (Ey Muhammed)! o kâfirlere elem verici bir azabı müjdele!', 10, 186, 129, 10873, 'Tövbe', 'Medine', 113),
(1239, 4, 9, 'Tevbe', 'اِلَّا الَّذ۪ينَ عَاهَدْتُمْ مِنَ الْمُشْرِك۪ينَ ثُمَّ لَمْ يَنْقُصُوكُمْ شَيْـٔاً وَلَمْ يُظَاهِرُوا عَلَيْكُمْ اَحَداً فَاَتِمُّٓوا اِلَيْهِمْ عَهْدَهُمْ اِلٰى مُدَّتِهِمْۜ اِنَّ اللّٰهَ يُحِبُّ الْمُتَّق۪ينَ', 'Ancak kendileriyle antlaşma yaptığınız müşriklerden (antlaşma şartlarına uyan) hiçbir şeyi size eksik bırakmayan ve sizin aleyhinize herhangi bir kimseye arka çıkmayanlar (bu hükmün) dışındadır. Onların antlaşmalarını, süreleri bitinceye kadar tamamlayınız. Allah (haksızlıktan) sakınanları sever.', 10, 186, 129, 10873, 'Tövbe', 'Medine', 113),
(1240, 5, 9, 'Tevbe', 'فَاِذَا انْسَلَخَ الْاَشْهُرُ الْحُرُمُ فَاقْتُلُوا الْمُشْرِك۪ينَ حَيْثُ وَجَدْتُمُوهُمْ وَخُذُوهُمْ وَاحْصُرُوهُمْ وَاقْعُدُوا لَهُمْ كُلَّ مَرْصَدٍۚ فَاِنْ تَابُوا وَاَقَامُوا الصَّلٰوةَ وَاٰتَوُا الزَّكٰوةَ فَخَلُّوا سَب۪يلَهُمْۜ اِنَّ اللّٰهَ غَفُورٌ رَح۪يمٌ', 'Haram aylar çıkınca müşrikleri bulduğunuz yerde öldürün; onları yakalayın, onları hapsedin ve onları her gözetleme yerinde oturup bekleyin. Eğer tevbe eder, namazı dosdoğru kılar, zekâtı da verirlerse artık yollarını serbest bırakın. Allah yarlığayan, esirgeyendir.', 10, 186, 129, 10873, 'Tövbe', 'Medine', 113),
(1241, 6, 9, 'Tevbe', 'وَاِنْ اَحَدٌ مِنَ الْمُشْرِك۪ينَ اسْتَجَارَكَ فَاَجِرْهُ حَتّٰى يَسْمَعَ كَلَامَ اللّٰهِ ثُمَّ اَبْلِغْهُ مَأْمَنَهُۜ ذٰلِكَ بِاَنَّهُمْ قَوْمٌ لَا يَعْلَمُونَ۟', 'Ve eğer müşriklerden biri senden aman dilerse, Allah´ın kelâmını işitip dinleyinceye kadar ona aman ver, sonra (müslüman olmazsa) onu güven içinde bulunacağı bir yere ulaştır. İşte bu (müsamaha), onların, bilmeyen bir kavim olmalarından dolayıdır.', 10, 186, 129, 10873, 'Tövbe', 'Medine', 113),
(1242, 7, 9, 'Tevbe', 'كَيْفَ يَكُونُ لِلْمُشْرِك۪ينَ عَهْدٌ عِنْدَ اللّٰهِ وَعِنْدَ رَسُولِه۪ٓ اِلَّا الَّذ۪ينَ عَاهَدْتُمْ عِنْدَ الْمَسْجِدِ الْحَرَامِۚ فَمَا اسْتَقَامُوا لَكُمْ فَاسْتَق۪يمُوا لَهُمْۜ اِنَّ اللّٰهَ يُحِبُّ الْمُتَّق۪ينَ', 'Mescid-i Haram´ın yanında kendileriyle antlaşma yaptıklarınızın dışında müşriklerin Allah ve Resûlü yanında nasıl (muteber) bir ahdi olabilir? Onlar size karşı dürüst davrandıkları müddetçe siz de onlara dürüst davranın. Çünkü Allah (ahdi bozmaktan) sakınanları sever.', 10, 187, 129, 10873, 'Tövbe', 'Medine', 113),
(1243, 8, 9, 'Tevbe', 'كَيْفَ وَاِنْ يَظْهَرُوا عَلَيْكُمْ لَا يَرْقُبُوا ف۪يكُمْ اِلاًّ وَلَا ذِمَّةًۜ يُرْضُونَكُمْ بِاَفْوَاهِهِمْ وَتَأْبٰى قُلُوبُهُمْۚ وَاَكْثَرُهُمْ فَاسِقُونَۚ', 'Nasıl olabilir ki! Onlar size galip gelselerdi, sizin hakkınızda ne ahit, ne de antlaşma gözetirlerdi. Onlar ağızlarıyla sizi razı ediyorlar, halbuki kalpleri (buna) karşı çıkıyor. Çünkü onların çoğu yoldan çıkmışlardır.', 10, 187, 129, 10873, 'Tövbe', 'Medine', 113),
(1244, 9, 9, 'Tevbe', 'اِشْتَرَوْا بِاٰيَاتِ اللّٰهِ ثَمَناً قَل۪يلاً فَصَدُّوا عَنْ سَب۪يلِه۪ۜ اِنَّهُمْ سَٓاءَ مَا كَانُوا يَعْمَلُونَ', 'Allah´ın âyetlerine karşılık az bir değeri (dünya malını ve nefsânî istekleri) satın aldılar da (insanları) O´nun yolundan alıkoydular. Gerçekten onların yapmakta oldukları şeyler ne kötüdür!', 10, 187, 129, 10873, 'Tövbe', 'Medine', 113),
(1245, 10, 9, 'Tevbe', 'لَا يَرْقُبُونَ ف۪ي مُؤْمِنٍ اِلاًّ وَلَا ذِمَّةًۜ وَاُو۬لٰٓئِكَ هُمُ الْمُعْتَدُونَ', 'Bir mümin hakkında ne ahit tanırlar ne de antlaşma. Çünkü onlar saldırganların kendileridir.', 10, 187, 129, 10873, 'Tövbe', 'Medine', 113),
(1246, 11, 9, 'Tevbe', 'فَاِنْ تَابُوا وَاَقَامُوا الصَّلٰوةَ وَاٰتَوُا الزَّكٰوةَ فَاِخْوَانُكُمْ فِي الدّ۪ينِۜ وَنُفَصِّلُ الْاٰيَاتِ لِقَوْمٍ يَعْلَمُونَ', 'Fakat tevbe eder, namaz kılar ve zekât verirlerse, artık onlar dinde kardeşlerinizdir. Biz, bilen bir kavme âyetlerimizi böyle açıklıyoruz.', 10, 187, 129, 10873, 'Tövbe', 'Medine', 113),
(1247, 12, 9, 'Tevbe', 'وَاِنْ نَكَثُٓوا اَيْمَانَهُمْ مِنْ بَعْدِ عَهْدِهِمْ وَطَعَنُوا ف۪ي د۪ينِكُمْ فَقَاتِلُٓوا اَئِمَّةَ الْكُفْرِۙ اِنَّهُمْ لَٓا اَيْمَانَ لَهُمْ لَعَلَّهُمْ يَنْتَهُونَ', 'Eğer antlaşmalarından sonra yeminlerini bozarlar ve dininize saldırırlarsa, küfrün önderlerine karşı savaşın. Çünkü onlar yeminleri olmayan adamlardır. (Onlara karşı savaşırsanız) umulur ki küfre son verirler.', 10, 187, 129, 10873, 'Tövbe', 'Medine', 113),
(1248, 13, 9, 'Tevbe', 'اَلَا تُقَاتِلُونَ قَوْماً نَكَثُٓوا اَيْمَانَهُمْ وَهَمُّوا بِاِخْرَاجِ الرَّسُولِ وَهُمْ بَدَؤُ۫كُمْ اَوَّلَ مَرَّةٍۜ اَتَخْشَوْنَهُمْۚ فَاللّٰهُ اَحَقُّ اَنْ تَخْشَوْهُ اِنْ كُنْتُمْ مُؤْمِن۪ينَ', '(Ey müminler!) verdikleri sözü bozan, Peygamber´i (yurdundan) çıkarmaya kalkışan ve ilk önce size karşı savaşa başlamış olan bir kavme karşı savaşmayacak mısınız; yoksa onlardan korkuyor musunuz? Eğer (gerçek) müminler iseniz, bilin ki, Allah, kendisinden korkmanıza daha lâyıktır.', 10, 187, 129, 10873, 'Tövbe', 'Medine', 113),
(1249, 14, 9, 'Tevbe', 'قَاتِلُوهُمْ يُعَذِّبْهُمُ اللّٰهُ بِاَيْد۪يكُمْ وَيُخْزِهِمْ وَيَنْصُرْكُمْ عَلَيْهِمْ وَيَشْفِ صُدُورَ قَوْمٍ مُؤْمِن۪ينَۙ', 'Onlarla savaşın ki, Allah sizin ellerinizle onları cezalandırsın; onları rezil etsin; sizi onlara galip kılsın ve mümin toplumun kalplerini ferahlatsın.', 10, 188, 129, 10873, 'Tövbe', 'Medine', 113),
(1250, 15, 9, 'Tevbe', 'وَيُذْهِبْ غَيْظَ قُلُوبِهِمْۜ وَيَتُوبُ اللّٰهُ عَلٰى مَنْ يَشَٓاءُۜ وَاللّٰهُ عَل۪يمٌ حَك۪يمٌ', 'Ve onların (müminlerin) kalplerinden öfkeyi gidersin. Allah dilediğinin tevbesini kabul eder. Allah bilendir, hikmet sahibidir.', 10, 188, 129, 10873, 'Tövbe', 'Medine', 113),
(1251, 16, 9, 'Tevbe', 'اَمْ حَسِبْتُمْ اَنْ تُتْرَكُوا وَلَمَّا يَعْلَمِ اللّٰهُ الَّذ۪ينَ جَاهَدُوا مِنْكُمْ وَلَمْ يَتَّخِذُوا مِنْ دُونِ اللّٰهِ وَلَا رَسُولِه۪ وَلَا الْمُؤْمِن۪ينَ وَل۪يجَةًۜ وَاللّٰهُ خَب۪يرٌ بِمَا تَعْمَلُونَ۟', 'Yoksa, Allah, sizden, cihad edip Allah, peygamber ve müminlerden başkasını kendilerine sırdaş edinmeyenleri ortaya çıkarmadan bırakılacağınızı mı sandınız? Allah yaptıklarınızdan haberdardır.', 10, 188, 129, 10873, 'Tövbe', 'Medine', 113),
(1252, 17, 9, 'Tevbe', 'مَا كَانَ لِلْمُشْرِك۪ينَ اَنْ يَعْمُرُوا مَسَاجِدَ اللّٰهِ شَاهِد۪ينَ عَلٰٓى اَنْفُسِهِمْ بِالْكُفْرِۜ اُو۬لٰٓئِكَ حَبِطَتْ اَعْمَالُهُمْۚ وَفِي النَّارِ هُمْ خَالِدُونَ', 'Allah´a ortak koşanlar, kendi kâfirliklerine bizzat kendileri şahitlik ederken, Allah´ın mescitlerini imar etmeye layık değildirler. Onların bütün işleri boşa gitmiştir. Ve onlar ateşte ebedî kalacaklardır.', 10, 188, 129, 10873, 'Tövbe', 'Medine', 113),
(1253, 18, 9, 'Tevbe', 'اِنَّمَا يَعْمُرُ مَسَاجِدَ اللّٰهِ مَنْ اٰمَنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَاَقَامَ الصَّلٰوةَ وَاٰتَى الزَّكٰوةَ وَلَمْ يَخْشَ اِلَّا اللّٰهَ فَعَسٰٓى اُو۬لٰٓئِكَ اَنْ يَكُونُوا مِنَ الْمُهْتَد۪ينَ', 'Allah´ın mescitlerini ancak Allah´a ve ahiret gününe iman eden, namazı dosdoğru kılan, zekâtı veren ve Allah´tan başkasından korkmayan kimseler imar eder. İşte doğru yola ermişlerden olmaları umulanlar bunlardır.', 10, 188, 129, 10873, 'Tövbe', 'Medine', 113),
(1254, 19, 9, 'Tevbe', 'اَجَعَلْتُمْ سِقَايَةَ الْحَٓاجِّ وَعِمَارَةَ الْمَسْجِدِ الْحَرَامِ كَمَنْ اٰمَنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَجَاهَدَ ف۪ي سَب۪يلِ اللّٰهِۜ لَا يَسْتَوُ۫نَ عِنْدَ اللّٰهِۜ وَاللّٰهُ لَا يَهْدِي الْقَوْمَ الظَّالِم۪ينَۢ', '(Ey müşrikler!) Siz hacılara su vermeyi ve Mescid-i Haram´ı onarmayı, Allah´a ve ahiret gününe iman edip de Allah yolunda cihad edenlerin imanı ile bir mi tutuyorsunuz? Halbuki onlar Allah katında eşit değillerdir. Allah zalimler topluluğunu hidayete erdirmez.', 10, 188, 129, 10873, 'Tövbe', 'Medine', 113),
(1255, 20, 9, 'Tevbe', 'اَلَّذ۪ينَ اٰمَنُوا وَهَاجَرُوا وَجَاهَدُوا ف۪ي سَب۪يلِ اللّٰهِ بِاَمْوَالِهِمْ وَاَنْفُسِهِمْۙ اَعْظَمُ دَرَجَةً عِنْدَ اللّٰهِۜ وَاُو۬لٰٓئِكَ هُمُ الْفَٓائِزُونَ', 'İman edip de hicret edenler ve Allah yolunda mallarıyla, canlarıyla cihad edenler, rütbe bakımından Allah katında daha üstündürler. Kurtuluşa erenler de işte onlardır.', 10, 188, 129, 10873, 'Tövbe', 'Medine', 113),
(1256, 21, 9, 'Tevbe', 'يُبَشِّرُهُمْ رَبُّهُمْ بِرَحْمَةٍ مِنْهُ وَرِضْوَانٍ وَجَنَّاتٍ لَهُمْ ف۪يهَا نَع۪يمٌ مُق۪يمٌۙ', 'Rableri onlara, tarafından bir rahmet ve hoşnutluk ile, kendileri için, içinde tükenmez nimetler bulunan cennetler müjdeler.', 10, 189, 129, 10873, 'Tövbe', 'Medine', 113),
(1257, 22, 9, 'Tevbe', 'خَالِد۪ينَ ف۪يهَٓا اَبَداًۜ اِنَّ اللّٰهَ عِنْدَهُٓ اَجْرٌ عَظ۪يمٌ', 'Onlar orada ebedî kalacaklardır. Şüphesiz ki Allah katında büyük mükâfat vardır.', 10, 189, 129, 10873, 'Tövbe', 'Medine', 113),
(1258, 23, 9, 'Tevbe', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا لَا تَتَّخِذُٓوا اٰبَٓاءَكُمْ وَاِخْوَانَكُمْ اَوْلِيَٓاءَ اِنِ اسْتَحَبُّوا الْكُفْرَ عَلَى الْا۪يمَانِۜ وَمَنْ يَتَوَلَّهُمْ مِنْكُمْ فَاُو۬لٰٓئِكَ هُمُ الظَّالِمُونَ', 'Ey iman edenler! Eğer küfrü imana tercih ediyorlarsa, babalarınızı ve kardeşlerinizi (bile) veli edinmeyin. Sizden kim onları dost edinirse, işte onlar zalimlerin kendileridir.', 10, 189, 129, 10873, 'Tövbe', 'Medine', 113),
(1259, 24, 9, 'Tevbe', 'قُلْ اِنْ كَانَ اٰبَٓاؤُ۬كُمْ وَاَبْنَٓاؤُ۬كُمْ وَاِخْوَانُكُمْ وَاَزْوَاجُكُمْ وَعَش۪يرَتُكُمْ وَاَمْوَالٌۨ اقْتَرَفْتُمُوهَا وَتِجَارَةٌ تَخْشَوْنَ كَسَادَهَا وَمَسَاكِنُ تَرْضَوْنَـهَٓا اَحَبَّ اِلَيْكُمْ مِنَ اللّٰهِ وَرَسُولِه۪ وَجِهَادٍ ف۪ي سَب۪يلِه۪ فَتَرَبَّصُوا حَتّٰى يَأْتِيَ اللّٰهُ بِاَمْرِه۪ۜ وَاللّٰهُ لَا يَهْدِي الْقَوْمَ الْفَاسِق۪ينَ۟', 'De ki: Eğer babalarınız, oğullarınız, kardeşleriniz, eşleriniz, hısım akrabanız kazandığınız mallar, kesada uğramasından korktuğunuz ticaret, hoşlandığınız meskenler size Allah´tan, Resûlünden ve Allah yolunda cihad etmekten daha sevgili ise, artık Allah emrini getirinceye kadar bekleyin. Allah fâsıklar topluluğunu hidayete erdirmez.', 10, 189, 129, 10873, 'Tövbe', 'Medine', 113),
(1260, 25, 9, 'Tevbe', 'لَقَدْ نَصَرَكُمُ اللّٰهُ ف۪ي مَوَاطِنَ كَث۪يرَةٍۙ وَيَوْمَ حُنَيْنٍۙ اِذْ اَعْجَبَتْكُمْ كَـثْرَتُكُمْ فَلَمْ تُغْنِ عَنْكُمْ شَيْـٔاً وَضَاقَتْ عَلَيْكُمُ الْاَرْضُ بِمَا رَحُبَتْ ثُمَّ وَلَّيْتُمْ مُدْبِر۪ينَۚ', 'Andolsun ki Allah, birçok yerde (savaş alanlarında) ve Huneyn savaşında size yardım etmişti. Hani çokluğunuz size kendinizi beğendirmiş, fakat sizi hezimete uğramaktan kurtaramamıştı. Yeryüzü bütün genişliğine rağmen size dar gelmişti, sonunda (bozularak) gerisin geri dönmüştünüz.', 10, 189, 129, 10873, 'Tövbe', 'Medine', 113),
(1261, 26, 9, 'Tevbe', 'ثُمَّ اَنْزَلَ اللّٰهُ سَك۪ينَتَهُ عَلٰى رَسُولِه۪ وَعَلَى الْمُؤْمِن۪ينَ وَاَنْزَلَ جُنُوداً لَمْ تَرَوْهَا وَعَذَّبَ الَّذ۪ينَ كَفَرُواۜ وَذٰلِكَ جَزَٓاءُ الْكَافِر۪ينَ', 'Sonra Allah, Resûl´ü ile müminler üzerine sekînetini (sükûnet ve huzur duygusu) indirdi, sizin görmediğiniz ordular (melekler) indirdi de kâfirlere azap etti. İşte bu, o kâfirlerin cezasıdır.', 10, 189, 129, 10873, 'Tövbe', 'Medine', 113),
(1262, 27, 9, 'Tevbe', 'ثُمَّ يَتُوبُ اللّٰهُ مِنْ بَعْدِ ذٰلِكَ عَلٰى مَنْ يَشَٓاءُۜ وَاللّٰهُ غَفُورٌ رَح۪يمٌ', 'Sonra Allah, bunun ardından yine dilediğinin tevbesini kabul eder. Zira Allah bağışlayan, esirgeyendir.', 10, 190, 129, 10873, 'Tövbe', 'Medine', 113),
(1263, 28, 9, 'Tevbe', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُٓوا اِنَّمَا الْمُشْرِكُونَ نَجَسٌ فَلَا يَقْرَبُوا الْمَسْجِدَ الْحَرَامَ بَعْدَ عَامِهِمْ هٰذَاۚ وَاِنْ خِفْتُمْ عَيْلَةً فَسَوْفَ يُغْن۪يكُمُ اللّٰهُ مِنْ فَضْلِه۪ٓ اِنْ شَٓاءَۜ اِنَّ اللّٰهَ عَل۪يمٌ حَك۪يمٌ', 'Ey iman edenler! Müşrikler ancak bir pisliktir. Onun için bu yıllarından sonra Mescid-i Haram´a yaklaşmasınlar. Eğer yoksulluktan korkarsanız, (biliniz ki) Allah dilerse sizi kendi lütfundan zengin edecektir. Şüphesiz Allah iyi bilendir, hikmet sahibidir.', 10, 190, 129, 10873, 'Tövbe', 'Medine', 113),
(1264, 29, 9, 'Tevbe', 'قَاتِلُوا الَّذ۪ينَ لَا يُؤْمِنُونَ بِاللّٰهِ وَلَا بِالْيَوْمِ الْاٰخِرِ وَلَا يُحَرِّمُونَ مَا حَرَّمَ اللّٰهُ وَرَسُولُهُ وَلَا يَد۪ينُونَ د۪ينَ الْحَقِّ مِنَ الَّذ۪ينَ اُو۫تُوا الْكِتَابَ حَتّٰى يُعْطُوا الْجِزْيَةَ عَنْ يَدٍ وَهُمْ صَاغِرُونَ۟', 'Kendilerine Kitap verilenlerden Allah´a ve ahiret gününe inanmayan, Allah ve Resûlünün haram kıldığını haram saymayan ve hak dini kendine din edinmeyen kimselerle, küçülerek elleriyle cizye verinceye kadar savaşın.', 10, 190, 129, 10873, 'Tövbe', 'Medine', 113),
(1265, 30, 9, 'Tevbe', 'وَقَالَتِ الْيَهُودُ عُزَيْرٌۨ ابْنُ اللّٰهِ وَقَالَتِ النَّصَارَى الْمَس۪يحُ ابْنُ اللّٰهِۜ ذٰلِكَ قَوْلُهُمْ بِاَفْوَاهِهِمْۚ يُضَاهِؤُ۫نَ قَوْلَ الَّذ۪ينَ كَفَرُوا مِنْ قَبْلُۜ قَاتَلَهُمُ اللّٰهُۘ اَنّٰى يُؤْفَكُونَ', 'Yahudiler, Uzeyr Allah´ın oğludur, dediler. Hıristiyanlar da, Mesîh (İsa) Allah´ın oğludur dediler. Bu onların ağızlarıyla geveledikleri sözlerdir. (Sözlerini) daha önce kâfir olmuş kimselerin sözlerine benzetiyorlar. Allah onları kahretsin! Nasıl da (haktan bâtıla) döndürülüyorlar!', 10, 190, 129, 10873, 'Tövbe', 'Medine', 113),
(1266, 31, 9, 'Tevbe', 'اِتَّخَذُٓوا اَحْبَارَهُمْ وَرُهْبَانَهُمْ اَرْبَاباً مِنْ دُونِ اللّٰهِ وَالْمَس۪يحَ ابْنَ مَرْيَمَۚ وَمَٓا اُمِرُٓوا اِلَّا لِيَعْبُدُٓوا اِلٰهاً وَاحِداًۚ لَٓا اِلٰهَ اِلَّا هُوَۜ سُبْحَانَهُ عَمَّا يُشْرِكُونَ', '(Yahudiler) Allah´ı bırakıp bilginlerini (hahamlarını); (hıristiyanlar) da rahiplerini ve Meryem oğlu Mesîh´i (İsa´yı) rabler edindiler. Halbuki onlara ancak tek ilâha kulluk etmeleri emrolundu. O´ndan başka tanrı yoktur. O, bunların ortak koştukları şeylerden uzaktır.', 10, 190, 129, 10873, 'Tövbe', 'Medine', 113),
(1267, 32, 9, 'Tevbe', 'يُر۪يدُونَ اَنْ يُطْفِؤُ۫ا نُورَ اللّٰهِ بِاَفْوَاهِهِمْ وَيَأْبَى اللّٰهُ اِلَّٓا اَنْ يُتِمَّ نُورَهُ وَلَوْ كَرِهَ الْكَافِرُونَ', 'Allah´ın nûrunu ağızlarıyla (üfleyip) söndürmek istiyorlar. Halbuki kâfirler hoşlanmasalar da Allah nûrunu tamamlamaktan asla vazgeçmez.', 10, 191, 129, 10873, 'Tövbe', 'Medine', 113),
(1268, 33, 9, 'Tevbe', 'هُوَ الَّـذ۪ٓي اَرْسَلَ رَسُولَهُ بِالْهُدٰى وَد۪ينِ الْحَقِّ لِيُظْهِرَهُ عَلَى الدّ۪ينِ كُلِّه۪ۙ وَلَوْ كَرِهَ الْمُشْرِكُونَ', 'O (Allah), müşrikler hoşlanmasalar da (kendi) dinini bütün dinlere üstün kılmak için Resûlünü hidayet ve Hak Din ile gönderendir.', 10, 191, 129, 10873, 'Tövbe', 'Medine', 113),
(1269, 34, 9, 'Tevbe', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُٓوا اِنَّ كَث۪يراً مِنَ الْاَحْبَارِ وَالرُّهْبَانِ لَيَأْكُلُونَ اَمْوَالَ النَّاسِ بِالْبَاطِلِ وَيَصُدُّونَ عَنْ سَب۪يلِ اللّٰهِۜ وَالَّذ۪ينَ يَكْنِزُونَ الذَّهَبَ وَالْفِضَّةَ وَلَا يُنْفِقُونَهَا ف۪ي سَب۪يلِ اللّٰهِۙ فَبَشِّرْهُمْ بِعَذَابٍ اَل۪يمٍۙ', 'Ey iman edenler! (Biliniz ki), hahamlardan ve râhiplerden birçoğu insanların mallarını haksız yollardan yerler ve (insanları) Allah yolundan engellerler. Altın ve gümüşü yığıp da onları Allah yolunda harcamayanlar yok mu, işte onlara elem verici bir azabı müjdele!', 10, 191, 129, 10873, 'Tövbe', 'Medine', 113),
(1270, 35, 9, 'Tevbe', 'يَوْمَ يُحْمٰى عَلَيْهَا ف۪ي نَارِ جَهَنَّمَ فَتُكْوٰى بِهَا جِبَاهُهُمْ وَجُنُوبُهُمْ وَظُهُورُهُمْۜ هٰذَا مَا كَنَزْتُمْ لِاَنْفُسِكُمْ فَذُوقُوا مَا كُنْتُمْ تَكْنِزُونَ', '(Bu paralar) cehennem ateşinde kızdırılıp bunlarla onların alınları, yanları ve sırtları dağlanacağı gün (onlara denilir ki): «İşte bu kendiniz için biriktirdiğiniz servettir. Artık yığmakta olduğunuz şeylerin (azabını) tadın!»', 10, 191, 129, 10873, 'Tövbe', 'Medine', 113),
(1271, 36, 9, 'Tevbe', 'اِنَّ عِدَّةَ الشُّهُورِ عِنْدَ اللّٰهِ اثْنَا عَشَرَ شَهْراً ف۪ي كِتَابِ اللّٰهِ يَوْمَ خَلَقَ السَّمٰوَاتِ وَالْاَرْضَ مِنْهَٓا اَرْبَعَةٌ حُرُمٌۜ ذٰلِكَ الدّ۪ينُ الْقَيِّمُ فَلَا تَظْلِمُوا ف۪يهِنَّ اَنْفُسَكُمْ وَقَاتِلُوا الْمُشْرِك۪ينَ كَٓافَّةً كَمَا يُقَاتِلُونَكُمْ كَٓافَّةًۜ وَاعْلَمُٓوا اَنَّ اللّٰهَ مَعَ الْمُتَّق۪ينَ', 'Gökleri ve yeri yarattığı günde Allah´ın yazısına göre Allah katında ayların sayısı on iki olup, bunlardan dördü haram aylarıdır. İşte bu doğru hesaptır. O aylar içinde (Allah´ın koyduğu yasağı çiğneyerek) kendinize zulmetmeyin ve müşrikler nasıl sizinle topyekün savaşıyorlarsa siz de onlara karşı topyekün savaşın ve bilin ki Allah (kötülükten) sakınanlarla beraberdir.', 10, 191, 129, 10873, 'Tövbe', 'Medine', 113),
(1272, 37, 9, 'Tevbe', 'اِنَّمَا النَّس۪ٓيءُ زِيَادَةٌ فِي الْكُفْرِ يُضَلُّ بِهِ الَّذ۪ينَ كَفَرُوا يُحِلُّونَهُ عَاماً وَيُحَرِّمُونَهُ عَاماً لِيُوَاطِؤُ۫ا عِدَّةَ مَا حَرَّمَ اللّٰهُ فَيُحِلُّوا مَا حَرَّمَ اللّٰهُۜ زُيِّنَ لَهُمْ سُٓوءُ اَعْمَالِهِمْۜ وَاللّٰهُ لَا يَهْدِي الْقَوْمَ الْكَافِر۪ينَ۟', '(Haram ayları) ertelemek, sadece kâfirlikte ileri gitmektir. Çünkü onunla, kâfir olanlar saptırılır. Allah´ın haram kıldığının sayısını bozmak ve O´nun haram kıldığını helâl kılmak için (haram ayını) bir yıl helâl sayarlar, bir yıl da haram sayarlar. (Böylece) onların kötü işleri kendilerine güzel gösterilmiştir. Allah kâfirler topluluğunu hidayete erdirmez.', 10, 192, 129, 10873, 'Tövbe', 'Medine', 113),
(1273, 38, 9, 'Tevbe', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا مَا لَكُمْ اِذَا ق۪يلَ لَكُمُ انْفِرُوا ف۪ي سَب۪يلِ اللّٰهِ اثَّاقَلْتُمْ اِلَى الْاَرْضِۜ اَرَض۪يتُمْ بِالْحَيٰوةِ الدُّنْيَا مِنَ الْاٰخِرَةِۚ فَمَا مَتَاعُ الْحَيٰوةِ الدُّنْيَا فِي الْاٰخِرَةِ اِلَّا قَل۪يلٌ', 'Ey iman edenler! Size ne oldu ki, «Allah yolunda savaşa çıkın!» denildiği zaman yere çakılıp kalıyorsunuz? Dünya hayatını ahirete tercih mi ediyorsunuz? Fakat dünya hayatının faydası ahiretin yanında pek azdır.', 10, 192, 129, 10873, 'Tövbe', 'Medine', 113),
(1274, 39, 9, 'Tevbe', 'اِلَّا تَنْفِرُوا يُعَذِّبْكُمْ عَذَاباً اَل۪يماً وَيَسْتَبْدِلْ قَوْماً غَيْرَكُمْ وَلَا تَضُرُّوهُ شَيْـٔاًۜ وَاللّٰهُ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌ', 'Eğer (gerektiğinde savaşa) çıkmazsanız, (Allah) sizi pek elem verici bir azap ile cezalandırır ve yerinize sizden başka bir kavim getirir; siz (savaşa çıkmamakla) O´na hiçbir zarar veremezsiniz. Allah her şeye kadirdir.', 10, 192, 129, 10873, 'Tövbe', 'Medine', 113),
(1275, 40, 9, 'Tevbe', 'اِلَّا تَنْصُرُوهُ فَقَدْ نَصَرَهُ اللّٰهُ اِذْ اَخْرَجَهُ الَّذ۪ينَ كَفَرُوا ثَانِيَ اثْنَيْنِ اِذْ هُمَا فِي الْغَارِ اِذْ يَقُولُ لِصَاحِبِه۪ لَا تَحْزَنْ اِنَّ اللّٰهَ مَعَنَاۚ فَاَنْزَلَ اللّٰهُ سَك۪ينَتَهُ عَلَيْهِ وَاَيَّدَهُ بِجُنُودٍ لَمْ تَرَوْهَا وَجَعَلَ كَلِمَةَ الَّذ۪ينَ كَفَرُوا السُّفْلٰىۜ وَكَلِمَةُ اللّٰهِ هِيَ الْعُلْيَاۜ وَاللّٰهُ عَز۪يزٌ حَك۪يمٌ', 'Eğer siz ona (Resûlullah´a) yardım etmezseniz (bu önemli değil); ona Allah yardım etmiştir: Hani, kâfirler onu, iki kişiden biri olarak (Ebu Bekir ile birlikte Mekke´den) çıkarmışlardı; hani onlar mağaradaydı; o, arkadaşına. Üzülme, çünkü Allah bizimle beraberdir, diyordu. Bunun üzerine Allah ona (sükûnet sağlayan) emniyetini indirdi, onu sizin görmediğiniz bir ordu ile destekledi ve kâfir olanların sözünü alçalttı. Allah´ın sözü ise zaten yücedir. Çünkü Allah üstündür, hikmet sahibidir.', 10, 192, 129, 10873, 'Tövbe', 'Medine', 113),
(1276, 41, 9, 'Tevbe', 'اِنْفِرُوا خِفَافاً وَثِقَالاً وَجَاهِدُوا بِاَمْوَالِكُمْ وَاَنْفُسِكُمْ ف۪ي سَب۪يلِ اللّٰهِۜ ذٰلِكُمْ خَيْرٌ لَكُمْ اِنْ كُنْتُمْ تَعْلَمُونَ', '(Ey müminler!) Gerek hafif, gerek ağır olarak savaşa çıkın, mallarınızla ve canlarınızla Allah yolunda cihad edin. Eğer bilirseniz, bu sizin için daha hayırlıdır.', 10, 193, 129, 10873, 'Tövbe', 'Medine', 113),
(1277, 42, 9, 'Tevbe', 'لَوْ كَانَ عَرَضاً قَر۪يباً وَسَفَراً قَاصِداً لَاتَّـبَعُوكَ وَلٰكِنْ بَعُدَتْ عَلَيْهِمُ الشُّقَّةُۜ وَسَيَحْلِفُونَ بِاللّٰهِ لَوِ اسْتَطَعْنَا لَخَرَجْنَا مَعَكُمْۚ يُهْلِكُونَ اَنْفُسَهُمْۚ وَاللّٰهُ يَعْلَمُ اِنَّهُمْ لَكَاذِبُونَ۟', 'Eğer yakın bir dünya malı ve kolay bir yolculuk olsaydı (o münafıklar) mutlaka sana uyup peşinden gelirlerdi. Fakat meşakkatli yol onlara uzak geldi. Gerçi onlar, «Gücümüz yetseydi mutlaka sizinle beraber çıkardık» diye kendilerini helâk edercesine Allah´a yemin edecekler. Halbuki Allah onların mutlaka yalancı olduklarını biliyor.', 10, 193, 129, 10873, 'Tövbe', 'Medine', 113),
(1278, 43, 9, 'Tevbe', 'عَفَا اللّٰهُ عَنْكَۚ لِمَ اَذِنْتَ لَهُمْ حَتّٰى يَتَبَيَّنَ لَكَ الَّذ۪ينَ صَدَقُوا وَتَعْلَمَ الْكَاذِب۪ينَ', 'Allah seni affetti. Fakat doğru söyleyenler sana iyice belli olup, sen yalancıları bilinceye kadar onlara niçin izin verdin?', 10, 193, 129, 10873, 'Tövbe', 'Medine', 113),
(1279, 44, 9, 'Tevbe', 'لَا يَسْتَأْذِنُكَ الَّذ۪ينَ يُؤْمِنُونَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ اَنْ يُجَاهِدُوا بِاَمْوَالِهِمْ وَاَنْفُسِهِمْۜ وَاللّٰهُ عَل۪يمٌ بِالْمُتَّق۪ينَ', 'Allah´a ve ahiret gününe iman edenler, mallarıyla canlarıyla savaşmaktan (geri kalmak için) senden izin istemezler. Allah takvâ sahiplerini pek iyi bilir.', 10, 193, 129, 10873, 'Tövbe', 'Medine', 113),
(1280, 45, 9, 'Tevbe', 'اِنَّمَا يَسْتَأْذِنُكَ الَّذ۪ينَ لَا يُؤْمِنُونَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَارْتَابَتْ قُلُوبُهُمْ فَهُمْ ف۪ي رَيْبِهِمْ يَتَرَدَّدُونَ', 'Ancak Allah´a ve ahiret gününe inanmayan, kalpleri şüpheye düşüp, kuşkuları içinde bocalayanlar senden izin isterler.', 10, 193, 129, 10873, 'Tövbe', 'Medine', 113),
(1281, 46, 9, 'Tevbe', 'وَلَوْ اَرَادُوا الْخُرُوجَ لَاَعَدُّوا لَهُ عُدَّةً وَلٰكِنْ كَرِهَ اللّٰهُ انْبِعَاثَهُمْ فَثَبَّطَهُمْ وَق۪يلَ اقْعُدُوا مَعَ الْقَاعِد۪ينَ', 'Eğer onlar (savaşa) çıkmak isteselerdi elbette bunun için bir hazırlık yaparlardı. Fakat Allah onların davranışlarını çirkin gördü ve onları geri koydu; onlara «Oturanlarla (kadın ve çocuklarla) beraber oturun!» denildi.', 10, 193, 129, 10873, 'Tövbe', 'Medine', 113),
(1282, 47, 9, 'Tevbe', 'لَوْ خَرَجُوا ف۪يكُمْ مَا زَادُوكُمْ اِلَّا خَبَالاً وَلَا۬اَوْضَعُوا خِلَالَكُمْ يَبْغُونَكُمُ الْفِتْنَةَۚ وَف۪يكُمْ سَمَّاعُونَ لَهُمْۜ وَاللّٰهُ عَل۪يمٌ بِالظَّالِم۪ينَ', 'Eğer içinizde (onlar da savaşa) çıksalardı, size bozgunculuktan başka bir katkıları olmazdı ve mutlaka fitne çıkarmak isteyerek aranızda koşarlardı. İçinizde, onlara iyice kulak verecekler de vardır. Allah zalimleri gayet iyi bilir.', 10, 193, 129, 10873, 'Tövbe', 'Medine', 113),
(1283, 48, 9, 'Tevbe', 'لَقَدِ ابْتَغَوُا الْفِتْنَةَ مِنْ قَبْلُ وَقَلَّبُوا لَكَ الْاُمُورَ حَتّٰى جَٓاءَ الْحَقُّ وَظَهَرَ اَمْرُ اللّٰهِ وَهُمْ كَارِهُونَ', 'Andolsun onlar önceden de fitne çıkarmak istemişler ve sana nice işler çevirmişlerdi. Nihayet hak geldi ve onlar istemedikleri halde Allah´ın emri yerini buldu.', 10, 194, 129, 10873, 'Tövbe', 'Medine', 113),
(1284, 49, 9, 'Tevbe', 'وَمِنْهُمْ مَنْ يَقُولُ ائْذَنْ ل۪ي وَلَا تَفْتِنّ۪يۜ اَلَا فِي الْفِتْنَةِ سَقَطُواۜ وَاِنَّ جَهَنَّمَ لَمُح۪يطَةٌ بِالْكَافِر۪ينَ', 'Onlardan öylesi de var ki: «Bana izin ver, beni fitneye düşürme» der. Bilesiniz ki onlar zaten fitneye düşmüşlerdir. Cehennem, kâfirleri mutlaka kuşatacaktır.', 10, 194, 129, 10873, 'Tövbe', 'Medine', 113),
(1285, 50, 9, 'Tevbe', 'اِنْ تُصِبْكَ حَسَنَةٌ تَسُؤْهُمْۚ وَاِنْ تُصِبْكَ مُص۪يبَةٌ يَقُولُوا قَدْ اَخَذْنَٓا اَمْرَنَا مِنْ قَبْلُ وَيَتَوَلَّوْا وَهُمْ فَرِحُونَ', 'Eğer sana bir iyilik erişirse, bu onları üzer. Ve eğer başına bir musibet gelirse, «İyi ki biz daha önce tedbirimizi almışız» derler ve böbürlenerek dönüp giderler.', 10, 194, 129, 10873, 'Tövbe', 'Medine', 113),
(1286, 51, 9, 'Tevbe', 'قُلْ لَنْ يُص۪يبَنَٓا اِلَّا مَا كَتَبَ اللّٰهُ لَنَاۚ هُوَ مَوْلٰينَاۚ وَعَلَى اللّٰهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُونَ', 'De ki: Allah´ın bizim için yazdığından başkası bize asla erişmez. O bizim mevlâmızdır. Onun için müminler yalnız Allah´a dayanıp güvensinler.', 10, 194, 129, 10873, 'Tövbe', 'Medine', 113),
(1287, 52, 9, 'Tevbe', 'قُلْ هَلْ تَرَبَّصُونَ بِنَٓا اِلَّٓا اِحْدَى الْحُسْنَيَيْنِۜ وَنَحْنُ نَتَرَبَّصُ بِكُمْ اَنْ يُص۪يبَكُمُ اللّٰهُ بِعَذَابٍ مِنْ عِنْدِه۪ٓ اَوْ بِاَيْد۪ينَاۘ فَتَرَبَّصُٓوا اِنَّا مَعَكُمْ مُتَرَبِّصُونَ', 'De ki: Siz bizim için ancak iki iyilikten birini beklemektesiniz. Biz de, Allah´ın, ya kendi katından veya bizim elimizle size bir azap vermesini bekliyoruz. Haydi bekleyin; şüphesiz biz de sizinle beraber beklemekteyiz.', 10, 194, 129, 10873, 'Tövbe', 'Medine', 113),
(1288, 53, 9, 'Tevbe', 'قُلْ اَنْفِقُوا طَوْعاً اَوْ كَرْهاً لَنْ يُتَقَبَّلَ مِنْكُمْۜ اِنَّكُمْ كُنْتُمْ قَوْماً فَاسِق۪ينَ', 'De ki: İster gönüllü verin ister gönülsüz, sizden (sadaka) asla kabul olunmayacaktır. Çünkü siz yoldan çıkan bir topluluk oldunuz.', 10, 194, 129, 10873, 'Tövbe', 'Medine', 113),
(1289, 54, 9, 'Tevbe', 'وَمَا مَنَعَهُمْ اَنْ تُقْبَلَ مِنْهُمْ نَفَقَاتُهُمْ اِلَّٓا اَنَّهُمْ كَفَرُوا بِاللّٰهِ وَبِرَسُولِه۪ وَلَا يَأْتُونَ الصَّلٰوةَ اِلَّا وَهُمْ كُسَالٰى وَلَا يُنْفِقُونَ اِلَّا وَهُمْ كَارِهُونَ', 'Onların harcamalarının kabul edilmesini engelleyen, onların Allah ve Resûlünü inkâr etmeleri, namaza ancak üşenerek gelmeleri ve istemeyerek harcamalarından başka bir şey değildir.', 10, 194, 129, 10873, 'Tövbe', 'Medine', 113),
(1290, 55, 9, 'Tevbe', 'فَلَا تُعْجِبْكَ اَمْوَالُهُمْ وَلَٓا اَوْلَادُهُمْۜ اِنَّمَا يُر۪يدُ اللّٰهُ لِيُعَذِّبَهُمْ بِهَا فِي الْحَيٰوةِ الدُّنْيَا وَتَزْهَقَ اَنْفُسُهُمْ وَهُمْ كَافِرُونَ', '(Ey Muhammed!) Onların malları ve çocukları seni imrendirmesin. Çünkü Allah bunlarla, ancak dünya hayatında onların azaplarını çoğaltmayı ve onların kâfir olarak canlarının çıkmasını istiyor.', 10, 195, 129, 10873, 'Tövbe', 'Medine', 113),
(1291, 56, 9, 'Tevbe', 'وَيَحْلِفُونَ بِاللّٰهِ اِنَّهُمْ لَمِنْكُمْۜ وَمَا هُمْ مِنْكُمْ وَلٰكِنَّهُمْ قَوْمٌ يَفْرَقُونَ', '(O münafıklar) mutlaka sizden olduklarına dair Allah´a yemin ederler. Halbuki onlar sizden değillerdir, fakat onlar (kılıçlarınızdan) korkan bir toplumdur.', 10, 195, 129, 10873, 'Tövbe', 'Medine', 113),
(1292, 57, 9, 'Tevbe', 'لَوْ يَجِدُونَ مَلْجَـٔاً اَوْ مَغَارَاتٍ اَوْ مُدَّخَلاً لَوَلَّوْا اِلَيْهِ وَهُمْ يَجْمَحُونَ', 'Eğer sığınacak bir yer yahut (barınabilecek) mağaralar veya (sokulabilecek) bir delik bulsalardı, koşarak o tarafa yönelip giderlerdi.', 10, 195, 129, 10873, 'Tövbe', 'Medine', 113),
(1293, 58, 9, 'Tevbe', 'وَمِنْهُمْ مَنْ يَلْمِزُكَ فِي الصَّدَقَاتِۚ فَاِنْ اُعْطُوا مِنْهَا رَضُوا وَاِنْ لَمْ يُعْطَوْا مِنْهَٓا اِذَا هُمْ يَسْخَطُونَ', 'Onlardan sadakaların (taksimi) hususunda seni ayıplayanlar da vardır. Sadakalardan onlara da (bir pay) verilirse razı olurlar, şayet onlara sadakalardan verilmezse hemen kızarlar.', 10, 195, 129, 10873, 'Tövbe', 'Medine', 113),
(1294, 59, 9, 'Tevbe', 'وَلَوْ اَنَّهُمْ رَضُوا مَٓا اٰتٰيهُمُ اللّٰهُ وَرَسُولُهُ وَقَالُوا حَسْبُنَا اللّٰهُ سَيُؤْت۪ينَا اللّٰهُ مِنْ فَضْلِه۪ وَرَسُولُهُٓۙ اِنَّٓا اِلَى اللّٰهِ رَاغِبُونَ۟', 'Eğer onlar Allah ve Resûlünün kendilerine verdiğine razı olup, «Allah bize yeter, yakında bize Allah da lütfundan verecek, Resûlü de. Biz yalnız Allah´a rağbet edenleriz» deselerdi (daha iyi olurdu).', 10, 195, 129, 10873, 'Tövbe', 'Medine', 113),
(1295, 60, 9, 'Tevbe', 'اِنَّمَا الصَّدَقَاتُ لِلْفُقَـرَٓاءِ وَالْمَسَاك۪ينِ وَالْعَامِل۪ينَ عَلَيْهَا وَالْمُؤَ۬لَّفَةِ قُلُوبُهُمْ وَفِي الرِّقَابِ وَالْغَارِم۪ينَ وَف۪ي سَب۪يلِ اللّٰهِ وَابْنِ السَّب۪يلِۜ فَر۪يضَةً مِنَ اللّٰهِۜ وَاللّٰهُ عَل۪يمٌ حَك۪يمٌ', 'Sadakalar (zekâtlar) Allah´tan bir farz olarak ancak, yoksullara, düşkünlere, (zekât toplayan) memurlara, gönülleri (İslâm´a) ısındırılacak olanlara, (hürriyetlerini satın almaya çalışan) kölelere, borçlulara, Allah yolunda olana, yolda kalana mahsustur. Allah pek iyi bilendir, hikmet sahibidir.', 10, 195, 129, 10873, 'Tövbe', 'Medine', 113),
(1296, 61, 9, 'Tevbe', 'وَمِنْهُمُ الَّذ۪ينَ يُؤْذُونَ النَّبِيَّ وَيَقُولُونَ هُوَ اُذُنٌۜ قُلْ اُذُنُ خَيْرٍ لَكُمْ يُؤْمِنُ بِاللّٰهِ وَيُؤْمِنُ لِلْمُؤْمِن۪ينَ وَرَحْمَةٌ لِلَّذ۪ينَ اٰمَنُوا مِنْكُمْۜ وَالَّذ۪ينَ يُؤْذُونَ رَسُولَ اللّٰهِ لَهُمْ عَذَابٌ اَل۪يمٌ', '(Yine o münafıklardan:) O (Peygamber, her söyleneni dinleyen) bir kulaktır, diyerek peygamberi incitenler de vardır. De ki: O, sizin için bir hayır kulağıdır. Çünkü o Allah´a inanır, müminlere güvenir ve o, sizden iman edenler için de bir rahmettir. Allah´ın Resûlüne eziyet edenler için mutlaka elem verici bir azap vardır.', 10, 195, 129, 10873, 'Tövbe', 'Medine', 113),
(1297, 62, 9, 'Tevbe', 'يَحْلِفُونَ بِاللّٰهِ لَكُمْ لِيُرْضُوكُمْۚ وَاللّٰهُ وَرَسُولُـهُٓ اَحَقُّ اَنْ يُرْضُوهُ اِنْ كَانُوا مُؤْمِن۪ينَ', 'Rızanızı almak için size (gelip) Allah´a and içerler. Eğer mümin iseler Allah ve Resûlünü razı etmeleri daha doğrudur.', 10, 196, 129, 10873, 'Tövbe', 'Medine', 113),
(1298, 63, 9, 'Tevbe', 'اَلَمْ يَعْلَمُٓوا اَنَّهُ مَنْ يُحَادِدِ اللّٰهَ وَرَسُولَهُ فَاَنَّ لَهُ نَارَ جَهَنَّمَ خَالِداً ف۪يهَاۜ ذٰلِكَ الْخِزْيُ الْعَظ۪يمُ', '(Hâla) bilmediler mi ki, kim Allah ve Resûlüne karşı koyarsa elbette onun için, içinde ebedî kalacağı cehennem ateşi vardır. İşte bu büyük rüsvaylıktır.', 10, 196, 129, 10873, 'Tövbe', 'Medine', 113),
(1299, 64, 9, 'Tevbe', 'يَحْذَرُ الْمُنَافِقُونَ اَنْ تُنَزَّلَ عَلَيْهِمْ سُورَةٌ تُنَبِّئُهُمْ بِمَا ف۪ي قُلُوبِهِمْۜ قُلِ اسْتَهْزِؤُ۫اۚ اِنَّ اللّٰهَ مُخْرِجٌ مَا تَحْذَرُونَ', 'Münafıklar, kalplerinde olanı kendilerine haber verecek bir sûrenin müminlere indirilmesinden çekinirler. De ki: Siz alay edin! Allah o çekindiğiniz şeyi ortaya çıkaracaktır.', 10, 196, 129, 10873, 'Tövbe', 'Medine', 113),
(1300, 65, 9, 'Tevbe', 'وَلَئِنْ سَاَلْتَهُمْ لَيَقُولُنَّ اِنَّمَا كُنَّا نَخُوضُ وَنَلْعَبُۜ قُلْ اَبِاللّٰهِ وَاٰيَاتِه۪ وَرَسُولِه۪ كُنْتُمْ تَسْتَهْزِؤُ۫نَ', 'Eğer onlara, (niçin alay ettiklerini) sorarsan, elbette, biz sadece lafa dalmış şakalaşıyorduk, derler. De ki: Allah ile, O´nun âyetleriyle ve O´nun peygamberi ile mi alay ediyordunuz?', 10, 196, 129, 10873, 'Tövbe', 'Medine', 113),
(1301, 66, 9, 'Tevbe', 'لَا تَعْتَذِرُوا قَدْ كَفَرْتُمْ بَعْدَ ا۪يمَانِكُمْۜ اِنْ نَعْفُ عَنْ طَٓائِفَةٍ مِنْكُمْ نُعَذِّبْ طَٓائِفَةً بِاَنَّهُمْ كَانُوا مُجْرِم۪ينَ۟', '(Boşuna) özür dilemeyin; çünkü siz iman ettikten sonra tekrar kâfir oldunuz. Sizden (tevbe eden) bir gurubu bağışlasak bile, bir guruba da suçlu olduklarından dolayı azap edeceğiz.', 10, 196, 129, 10873, 'Tövbe', 'Medine', 113),
(1302, 67, 9, 'Tevbe', 'اَلْمُنَافِقُونَ وَالْمُنَافِقَاتُ بَعْضُهُمْ مِنْ بَعْضٍۢ يَأْمُرُونَ بِالْمُنْكَرِ وَيَنْهَوْنَ عَنِ الْمَعْرُوفِ وَيَقْبِضُونَ اَيْدِيَهُمْۜ نَسُوا اللّٰهَ فَنَسِيَهُمْۜ اِنَّ الْمُنَافِق۪ينَ هُمُ الْفَاسِقُونَ', 'Münafık erkekler ve münafık kadınlar (sizden değil), birbirlerindendir. Onlar kötülüğü emreder, iyilikten alıkor ve cimrilik ederler. Onlar Allah´ı unuttular. Allah da onları unuttu! Çünkü münafıklar fâsıkların kendileridir.', 10, 196, 129, 10873, 'Tövbe', 'Medine', 113),
(1303, 68, 9, 'Tevbe', 'وَعَدَ اللّٰهُ الْمُنَافِق۪ينَ وَالْمُنَافِقَاتِ وَالْكُفَّارَ نَارَ جَهَنَّمَ خَالِد۪ينَ ف۪يهَاۜ هِيَ حَسْبُهُمْۚ وَلَعَنَهُمُ اللّٰهُۚ وَلَهُمْ عَذَابٌ مُق۪يمٌۙ', 'Allah erkek münafıklara da kadın münafıklara da kâfirlere de içinde ebedî kalacakları cehennem ateşini vâdetti. O, onlara yeter. Allah onlara lânet etmiştir! Onlar için devamlı bir azap vardır.', 10, 196, 129, 10873, 'Tövbe', 'Medine', 113);
INSERT INTO `Ayetler` (`id`, `AyetNo`, `SureNo`, `SureAdi`, `Metin`, `Mana`, `Cuzu`, `Sayfasi`, `AyetSayisi`, `HarfSayisi`, `AdAnlami`, `IndigiYer`, `InisSirasi`) VALUES
(1304, 69, 9, 'Tevbe', 'كَالَّذ۪ينَ مِنْ قَبْلِكُمْ كَانُٓوا اَشَدَّ مِنْكُمْ قُوَّةً وَاَكْثَرَ اَمْوَالاً وَاَوْلَاداًۜ فَاسْتَمْتَعُوا بِخَلَاقِهِمْ فَاسْتَمْتَعْتُمْ بِخَلَاقِكُمْ كَمَا اسْتَمْتَعَ الَّذ۪ينَ مِنْ قَبْلِكُمْ بِخَلَاقِهِمْ وَخُضْتُمْ كَالَّذ۪ي خَاضُواۜ اُو۬لٰٓئِكَ حَبِطَتْ اَعْمَالُهُمْ فِي الدُّنْيَا وَالْاٰخِرَةِۚ وَاُو۬لٰٓئِكَ هُمُ الْخَاسِرُونَ', '(Ey münafıklar! Siz de) sizden öncekiler gibi (yaptınız). Onlar sizden kuvvetçe daha üstün, mal ve evlâtça daha çok idiler. Onlar (dünya malından) paylarına düşenden faydalandılar. İşte sizden öncekiler nasıl paylarına düşenden faydalandıysalar, siz de payınıza düşenden faydalandınız ve (bâtıla) dalanlar gibi siz de daldınız. İşte onların amelleri dünyada da ahirette de boşa gitmiştir. Ve onlar ziyana uğrayanların kendileridir.', 10, 197, 129, 10873, 'Tövbe', 'Medine', 113),
(1305, 70, 9, 'Tevbe', 'اَلَمْ يَأْتِهِمْ نَبَاُ الَّذ۪ينَ مِنْ قَبْلِهِمْ قَوْمِ نُوحٍ وَعَادٍ وَثَمُودَ وَقَوْمِ اِبْرٰه۪يمَ وَاَصْحَابِ مَدْيَنَ وَالْمُؤْتَفِكَاتِۜ اَتَتْهُمْ رُسُلُهُمْ بِالْبَيِّنَاتِۚ فَمَا كَانَ اللّٰهُ لِيَظْلِمَهُمْ وَلٰكِنْ كَانُٓوا اَنْفُسَهُمْ يَظْلِمُونَ', 'Onlara kendilerinden evvelkilerin, Nuh, Âd ve Semûd kavimlerinin, İbrahim kavminin, Medyen halkının ve altüst olan şehirlerin haberi ulaşmadı mı? Peygamberi onlara apaçık mucizeler getirmişti. Demek ki, Allah onlara zulmedecek değildi, fakat onlar kendi kendilerine zulmetmekte idiler.', 10, 197, 129, 10873, 'Tövbe', 'Medine', 113),
(1306, 71, 9, 'Tevbe', 'وَالْمُؤْمِنُونَ وَالْمُؤْمِنَاتُ بَعْضُهُمْ اَوْلِيَٓاءُ بَعْضٍۢ يَأْمُرُونَ بِالْمَعْرُوفِ وَيَنْهَوْنَ عَنِ الْمُنْكَرِ وَيُق۪يمُونَ الصَّلٰوةَ وَيُؤْتُونَ الزَّكٰوةَ وَيُط۪يعُونَ اللّٰهَ وَرَسُولَهُۜ اُو۬لٰٓئِكَ سَيَرْحَمُهُمُ اللّٰهُۜ اِنَّ اللّٰهَ عَز۪يزٌ حَك۪يمٌ', 'Mümin erkeklerle mümin kadınlar da birbirlerinin velileridir. Onlar iyiliği emreder, kötülükten alıkorlar, namazı dosdoğru kılarlar, zekâtı verirler, Allah ve Resûlüne itaat ederler. İşte onlara Allah rahmet edecektir. Şüphesiz Allah azîzdir, hikmet sahibidir.', 10, 197, 129, 10873, 'Tövbe', 'Medine', 113),
(1307, 72, 9, 'Tevbe', 'وَعَدَ اللّٰهُ الْمُؤْمِن۪ينَ وَالْمُؤْمِنَاتِ جَنَّاتٍ تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُ خَالِد۪ينَ ف۪يهَا وَمَسَاكِنَ طَيِّبَةً ف۪ي جَنَّاتِ عَدْنٍۜ وَرِضْوَانٌ مِنَ اللّٰهِ اَكْبَرُۜ ذٰلِكَ هُوَ الْفَوْزُ الْعَظ۪يمُ۟', 'Allah, mümin erkeklere ve mümin kadınlara, içinde ebedî kalmak üzere altından ırmaklar akan cennetler ve Adn cennetlerinde güzel meskenler vâdetti. Allah´ın rızası ise hepsinden büyüktür. İşte büyük kurtuluş da budur.', 10, 197, 129, 10873, 'Tövbe', 'Medine', 113),
(1308, 73, 9, 'Tevbe', 'يَٓا اَيُّهَا النَّبِيُّ جَاهِدِ الْكُفَّارَ وَالْمُنَافِق۪ينَ وَاغْلُظْ عَلَيْهِمْۜ وَمَأْوٰيهُمْ جَهَنَّمُۜ وَبِئْسَ الْمَص۪يرُ', 'Ey Peygamber! Kâfirlere ve münafıklara karşı cihad et, onlara karşı sert davran. Onların varacakları yer cehennemdir. O ne kötü bir varış yeridir!', 10, 198, 129, 10873, 'Tövbe', 'Medine', 113),
(1309, 74, 9, 'Tevbe', 'يَحْلِفُونَ بِاللّٰهِ مَا قَالُواۜ وَلَقَدْ قَالُوا كَلِمَةَ الْكُفْرِ وَكَفَرُوا بَعْدَ اِسْلَامِهِمْ وَهَمُّوا بِمَا لَمْ يَنَالُواۚ وَمَا نَقَمُٓوا اِلَّٓا اَنْ اَغْنٰيهُمُ اللّٰهُ وَرَسُولُهُ مِنْ فَضْلِه۪ۚ فَاِنْ يَتُوبُوا يَكُ خَيْراً لَهُمْۚ وَاِنْ يَتَوَلَّوْا يُعَذِّبْهُمُ اللّٰهُ عَذَاباً اَل۪يماً فِي الدُّنْيَا وَالْاٰخِرَةِۚ وَمَا لَهُمْ فِي الْاَرْضِ مِنْ وَلِيٍّ وَلَا نَص۪يرٍ', '(Ey Muhammed! O sözleri) söylemediklerine dair Allah´a yemin ediyorlar. Halbuki o küfür sözünü elbette söylediler ve müslüman olduktan sonra kâfir oldular. Başaramadıkları bir şeye (Peygambere suikast yapmaya) de yeltendiler. Ve sırf Allah ve Resûlü kendi lütuflarından onları zenginleştirdiği için öç almaya kalkıştılar. Eğer tevbe ederlerse onlar için daha hayırlı olur. Yüz çevirirlerse Allah onları dünyada da, ahirette de elem verici bir azaba çarptıracaktır. Yeryüzünde onların ne dostu ne de yardımcısı vardır.', 10, 198, 129, 10873, 'Tövbe', 'Medine', 113),
(1310, 75, 9, 'Tevbe', 'وَمِنْهُمْ مَنْ عَاهَدَ اللّٰهَ لَئِنْ اٰتٰينَا مِنْ فَضْلِه۪ لَنَصَّدَّقَنَّ وَلَنَكُونَنَّ مِنَ الصَّالِح۪ينَ', 'Onlardan kimi de, eğer Allah lütuf ve kereminden bize verirse, mutlaka sadaka vereceğiz ve elbette biz sâlihlerden olacağız! diye Allah´a and içti.', 10, 198, 129, 10873, 'Tövbe', 'Medine', 113),
(1311, 76, 9, 'Tevbe', 'فَلَمَّٓا اٰتٰيهُمْ مِنْ فَضْلِه۪ بَخِلُوا بِه۪ وَتَوَلَّوْا وَهُمْ مُعْرِضُونَ', 'Fakat Allah lütfundan onlara (zenginlik) verince, onda cimrilik edip (Allah´ın emrinden) yüz çevirerek sözlerinden döndüler.', 10, 198, 129, 10873, 'Tövbe', 'Medine', 113),
(1312, 77, 9, 'Tevbe', 'فَاَعْقَبَهُمْ نِفَاقاً ف۪ي قُلُوبِهِمْ اِلٰى يَوْمِ يَلْقَوْنَهُ بِمَٓا اَخْلَفُوا اللّٰهَ مَا وَعَدُوهُ وَبِمَا كَانُوا يَكْذِبُونَ', 'Nihayet, Allah´a verdikleri sözden döndüklerinden ve yalan söylediklerinden dolayı Allah, kendisiyle karşılaşacakları güne kadar onların kalbine nifak (iki yüzlülük) soktu.', 10, 198, 129, 10873, 'Tövbe', 'Medine', 113),
(1313, 78, 9, 'Tevbe', 'اَلَمْ يَعْلَمُٓوا اَنَّ اللّٰهَ يَعْلَمُ سِرَّهُمْ وَنَجْوٰيهُمْ وَاَنَّ اللّٰهَ عَلَّامُ الْغُيُوبِۚ', '(Münafıklar), Allah´ın, onların sırrını da fısıltılarını da bildiğini ve gaybları (gizli şeyleri) çok iyi bilen olduğunu hâla anlamadılar mı?', 10, 198, 129, 10873, 'Tövbe', 'Medine', 113),
(1314, 79, 9, 'Tevbe', 'اَلَّذ۪ينَ يَلْمِزُونَ الْمُطَّوِّع۪ينَ مِنَ الْمُؤْمِن۪ينَ فِي الصَّدَقَاتِ وَالَّذ۪ينَ لَا يَجِدُونَ اِلَّا جُهْدَهُمْ فَيَسْخَرُونَ مِنْهُمْۜ سَخِرَ اللّٰهُ مِنْهُمْۘ وَلَهُمْ عَذَابٌ اَل۪يمٌ', 'Sadakalar hususunda, müminlerden gönüllü verenleri ve güçlerinin yettiğinden başkasını bulamayanları çekiştirip onlarla alay edenler var ya, Allah işte onları maskaraya çevirmiştir. Ve onlar için elem verici azap vardır.', 10, 198, 129, 10873, 'Tövbe', 'Medine', 113),
(1315, 80, 9, 'Tevbe', 'اِسْتَغْفِرْ لَهُمْ اَوْ لَا تَسْتَغْفِرْ لَهُمْۜ اِنْ تَسْتَغْفِرْ لَهُمْ سَبْع۪ينَ مَرَّةً فَلَنْ يَغْفِرَ اللّٰهُ لَهُمْۜ ذٰلِكَ بِاَنَّهُمْ كَفَرُوا بِاللّٰهِ وَرَسُولِه۪ۜ وَاللّٰهُ لَا يَهْدِي الْقَوْمَ الْفَاسِق۪ينَ۟', '(Ey Muhammed!) Onlar için ister af dile, ister dileme; onlar için yetmiş kez af dilesen de Allah onları asla affetmeyecek. Bu, onların Allah ve Resûlünü inkâr etmelerinden ötürüdür. Allah fâsıklar topluluğunu hidayete erdirmez.', 10, 199, 129, 10873, 'Tövbe', 'Medine', 113),
(1316, 81, 9, 'Tevbe', 'فَرِحَ الْمُخَلَّفُونَ بِمَقْعَدِهِمْ خِلَافَ رَسُولِ اللّٰهِ وَكَرِهُٓوا اَنْ يُجَاهِدُوا بِاَمْوَالِهِمْ وَاَنْفُسِهِمْ ف۪ي سَب۪يلِ اللّٰهِ وَقَالُوا لَا تَنْفِرُوا فِي الْحَرِّۜ قُلْ نَارُ جَهَنَّمَ اَشَدُّ حَراًّۜ لَوْ كَانُوا يَفْقَهُونَ', 'Allah´ın Resûlüne muhalefet etmek için geri kalanlar (sefere çıkmayıp) oturmaları ile sevindiler; mallarıyla, canlarıyla Allah yolunda cihad etmeyi çirkin gördüler; «bu sıcakta sefere çıkmayın» dediler. De ki: «Cehennem ateşi daha sıcaktır!» Keşke anlasalardı!', 10, 199, 129, 10873, 'Tövbe', 'Medine', 113),
(1317, 82, 9, 'Tevbe', 'فَلْيَضْحَكُوا قَل۪يلاً وَلْيَبْكُوا كَث۪يراًۚ جَزَٓاءً بِمَا كَانُوا يَكْسِبُونَ', 'Artık kazanmakta olduklarının cezası olarak az gülsünler, çok ağlasınlar!', 10, 199, 129, 10873, 'Tövbe', 'Medine', 113),
(1318, 83, 9, 'Tevbe', 'فَاِنْ رَجَعَكَ اللّٰهُ اِلٰى طَٓائِفَةٍ مِنْهُمْ فَاسْتَأْذَنُوكَ لِلْخُرُوجِ فَقُلْ لَنْ تَخْرُجُوا مَعِيَ اَبَداً وَلَنْ تُقَاتِلُوا مَعِيَ عَدُواًّۜ اِنَّكُمْ رَض۪يتُمْ بِالْقُعُودِ اَوَّلَ مَرَّةٍ فَاقْعُدُوا مَعَ الْخَالِف۪ينَ', 'Eğer Allah seni onlardan bir gurubun yanına döndürür de (Tebük seferinden Medine´ye döner de başka bir savaşa seninle beraber) çıkmak için senden izin isterlerse, de ki: Benimle beraber asla çıkmayacaksınız ve düşmana karşı benimle beraber asla savaşmayacaksınız! Çünkü siz birinci defa (Tebük seferinde) yerinizde kalmaya razı oldunuz. Şimdi de geri kalanlarla (kadın ve çocuklarla) beraber oturun!', 10, 199, 129, 10873, 'Tövbe', 'Medine', 113),
(1319, 84, 9, 'Tevbe', 'وَلَا تُصَلِّ عَلٰٓى اَحَدٍ مِنْهُمْ مَاتَ اَبَداً وَلَا تَقُمْ عَلٰى قَبْرِه۪ۜ اِنَّهُمْ كَفَرُوا بِاللّٰهِ وَرَسُولِه۪ وَمَاتُوا وَهُمْ فَاسِقُونَ', 'Onlardan ölmüş olan hiçbirine asla namaz kılma; onun kabri başında da durma! Çünkü onlar, Allah ve Resûlünü inkâr ettiler ve fâsık olarak öldüler.', 10, 199, 129, 10873, 'Tövbe', 'Medine', 113),
(1320, 85, 9, 'Tevbe', 'وَلَا تُعْجِبْكَ اَمْوَالُهُمْ وَاَوْلَادُهُمْۜ اِنَّمَا يُر۪يدُ اللّٰهُ اَنْ يُعَذِّبَهُمْ بِهَا فِي الدُّنْيَا وَتَزْهَقَ اَنْفُسُهُمْ وَهُمْ كَافِرُونَ', 'Onların malları ve çocukları seni imrendirmesin. Çünkü Allah, bunlarla ancak dünyada onların azaplarını çoğaltmayı ve onların kâfir olarak canlarının güçlükle çıkmasını istiyor.', 10, 199, 129, 10873, 'Tövbe', 'Medine', 113),
(1321, 86, 9, 'Tevbe', 'وَاِذَٓا اُنْزِلَتْ سُورَةٌ اَنْ اٰمِنُوا بِاللّٰهِ وَجَاهِدُوا مَعَ رَسُولِهِ اسْتَأْذَنَكَ اُو۬لُوا الطَّوْلِ مِنْهُمْ وَقَالُوا ذَرْنَا نَكُنْ مَعَ الْقَاعِد۪ينَ', '«Allah´a inanın, Resûlü ile beraber cihad edin» diye bir sûre indirildiği zaman, onlardan servet sahibi olanlar, senden izin istediler ve: Bizi bırak (evlerinde) oturanlarla beraber olalım, dediler.', 10, 199, 129, 10873, 'Tövbe', 'Medine', 113),
(1322, 87, 9, 'Tevbe', 'رَضُوا بِاَنْ يَكُونُوا مَعَ الْخَوَالِفِ وَطُبِـعَ عَلٰى قُلُوبِهِمْ فَهُمْ لَا يَفْقَهُونَ', 'Geride kalan kadınlarla beraber olmaya razı oldular, onların kalplerine mühür vuruldu. Bu yüzden onlar anlamazlar.', 10, 200, 129, 10873, 'Tövbe', 'Medine', 113),
(1323, 88, 9, 'Tevbe', 'لٰكِنِ الرَّسُولُ وَالَّذ۪ينَ اٰمَنُوا مَعَهُ جَاهَدُوا بِاَمْوَالِهِمْ وَاَنْفُسِهِمْۜ وَاُو۬لٰٓئِكَ لَهُمُ الْخَيْرَاتُۘ وَاُو۬لٰٓئِكَ هُمُ الْمُفْلِحُونَ', 'Fakat Peygamber ve onunla beraber inananlar, mallarıyla, canlarıyla cihad ettiler. İşte bütün hayırlar onlarındır ve onlar kurtuluşa erenlerin kendileridir.', 10, 200, 129, 10873, 'Tövbe', 'Medine', 113),
(1324, 89, 9, 'Tevbe', 'اَعَدَّ اللّٰهُ لَهُمْ جَنَّاتٍ تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُ خَالِد۪ينَ ف۪يهَاۜ ذٰلِكَ الْفَوْزُ الْعَظ۪يمُ۟', 'Allah, onlara içinde ebedî kalacakları ve zemininden ırmaklar akan cennetler hazırlamıştır. İşte büyük kazanç budur.', 10, 200, 129, 10873, 'Tövbe', 'Medine', 113),
(1325, 90, 9, 'Tevbe', 'وَجَٓاءَ الْمُعَذِّرُونَ مِنَ الْاَعْرَابِ لِيُؤْذَنَ لَهُمْ وَقَعَدَ الَّذ۪ينَ كَذَبُوا اللّٰهَ وَرَسُولَهُۜ سَيُص۪يبُ الَّذ۪ينَ كَفَرُوا مِنْهُمْ عَذَابٌ اَل۪يمٌ', 'Bedevîlerden, (mazeretleri olduğunu) iddia edenler, kendilerine izin verilsin diye geldiler. Allah ve Resûlüne yalan söyleyenler de oturup kaldılar. Onlardan kâfir olanlara elem verici bir azap erişecektir.', 10, 200, 129, 10873, 'Tövbe', 'Medine', 113),
(1326, 91, 9, 'Tevbe', 'لَيْسَ عَلَى الضُّعَفَٓاءِ وَلَا عَلَى الْمَرْضٰى وَلَا عَلَى الَّذ۪ينَ لَا يَجِدُونَ مَا يُنْفِقُونَ حَرَجٌ اِذَا نَصَحُوا لِلّٰهِ وَرَسُولِه۪ۜ مَا عَلَى الْمُحْسِن۪ينَ مِنْ سَب۪يلٍۜ وَاللّٰهُ غَفُورٌ رَح۪يمٌۙ', 'Allah ve Resûlü için (insanlara) öğüt verdikleri takdirde, zayıflara, hastalara ve (savaşta) harcayacak bir şey bulamayanlara günah yoktur. Zira iyilik edenlerin aleyhine bir yol (sorumluluk) yoktur. Allah çok bağışlayan ve çok esirgeyendir.', 10, 200, 129, 10873, 'Tövbe', 'Medine', 113),
(1327, 92, 9, 'Tevbe', 'وَلَا عَلَى الَّذ۪ينَ اِذَا مَٓا اَتَوْكَ لِتَحْمِلَهُمْ قُلْتَ لَٓا اَجِدُ مَٓا اَحْمِلُكُمْ عَلَيْهِۖ تَوَلَّوْا وَاَعْيُنُهُمْ تَف۪يضُ مِنَ الدَّمْعِ حَزَناً اَلَّا يَجِدُوا مَا يُنْفِقُونَۜ', 'Kendilerine binek sağlaman için sana geldiklerinde: Sizi bindirecek bir binek bulamıyorum, deyince, harcayacak bir şey bulamadıklarından dolayı üzüntüden gözleri yaş dökerek dönen kimselere de (sorumluluk yoktur).', 10, 200, 129, 10873, 'Tövbe', 'Medine', 113),
(1328, 93, 9, 'Tevbe', 'اِنَّمَا السَّب۪يلُ عَلَى الَّذ۪ينَ يَسْتَأْذِنُونَكَ وَهُمْ اَغْنِيَٓاءُۚ رَضُوا بِاَنْ يَكُونُوا مَعَ الْخَوَالِفِۙ وَطَبَعَ اللّٰهُ عَلٰى قُلُوبِهِمْ فَهُمْ لَا يَعْلَمُونَ', 'Sorumluluk ancak, zengin oldukları halde senden izin isteyenleredir. Çünkü onlar geri kalan kadınlarla beraber olmaya râzı oldular. Allah da onların kalplerini mühürledi, artık onlar (neyin doğru olduğunu) bilmezler.', 10, 200, 129, 10873, 'Tövbe', 'Medine', 113),
(1329, 94, 9, 'Tevbe', 'يَعْتَذِرُونَ اِلَيْكُمْ اِذَا رَجَعْتُمْ اِلَيْهِمْۜ قُلْ لَا تَعْتَذِرُوا لَنْ نُؤْمِنَ لَكُمْ قَدْ نَبَّاَنَا اللّٰهُ مِنْ اَخْبَارِكُمْۜ وَسَيَرَى اللّٰهُ عَمَلَكُمْ وَرَسُولُهُ ثُمَّ تُرَدُّونَ اِلٰى عَالِمِ الْغَيْبِ وَالشَّهَادَةِ فَيُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُونَ', '(Seferden) onlara döndüğünüz zaman size özür beyan edecekler. De ki: (Boşuna) özür dilemeyin! Size asla inanmayız; çünkü Allah, haberlerinizi bize bildirmiştir. (Bundan sonraki) amelinizi Allah da görecektir, Resûlü de. Sonra görüleni ve görülmeyeni bilene döndürüleceksiniz de yapmakta olduklarınızı size haber verecektir.', 11, 201, 129, 10873, 'Tövbe', 'Medine', 113),
(1330, 95, 9, 'Tevbe', 'سَيَحْلِفُونَ بِاللّٰهِ لَكُمْ اِذَا انْقَلَبْتُمْ اِلَيْهِمْ لِتُعْرِضُوا عَنْهُمْۜ فَاَعْرِضُوا عَنْهُمْۜ اِنَّهُمْ رِجْسٌۘ وَمَأْوٰيهُمْ جَهَنَّمُۚ جَزَٓاءً بِمَا كَانُوا يَكْسِبُونَ', 'Onların yanına döndüğünüz zaman size, kendilerinden (onları cezalandırmaktan) vazgeçmeniz için Allah adına and içecekler. Artık onlardan yüz çevirin. Çünkü onlar murdardır. Kazanmakta olduklarına (kötü işlerine) karşılık ceza olarak varacakları yer cehennemdir.', 11, 201, 129, 10873, 'Tövbe', 'Medine', 113),
(1331, 96, 9, 'Tevbe', 'يَحْلِفُونَ لَكُمْ لِتَرْضَوْا عَنْهُمْۚ فَاِنْ تَرْضَوْا عَنْهُمْ فَاِنَّ اللّٰهَ لَا يَرْضٰى عَنِ الْقَوْمِ الْفَاسِق۪ينَ', 'Onlardan razı olasınız diye size yemin edecekler. Fakat siz onlardan razı olsanız bile Allah fâsıklar topluluğundan asla razı olmaz.', 11, 201, 129, 10873, 'Tövbe', 'Medine', 113),
(1332, 97, 9, 'Tevbe', 'اَلْاَعْرَابُ اَشَدُّ كُفْراً وَنِفَاقاً وَاَجْدَرُ اَلَّا يَعْلَمُوا حُدُودَ مَٓا اَنْزَلَ اللّٰهُ عَلٰى رَسُولِه۪ۜ وَاللّٰهُ عَل۪يمٌ حَك۪يمٌ', 'Bedevîler, kâfirlik ve münafıklık bakımından hem daha beter, hem de Allah´ın Resûlüne indirdiği kanunları tanımamaya daha yatkındır. Allah çok iyi bilendir, hikmet sahibidir.', 11, 201, 129, 10873, 'Tövbe', 'Medine', 113),
(1333, 98, 9, 'Tevbe', 'وَمِنَ الْاَعْرَابِ مَنْ يَتَّخِذُ مَا يُنْفِقُ مَغْرَماً وَيَتَرَبَّصُ بِكُمُ الدَّوَٓائِرَۜ عَلَيْهِمْ دَٓائِرَةُ السَّوْءِۜ وَاللّٰهُ سَم۪يعٌ عَل۪يمٌ', 'Bedevîlerden öylesi vardır ki (Allah yolunda) harcayacağını angarya sayar ve sizin başınıza belâlar gelmesini bekler. (Bekledikleri) o kötü belâ kendi başlarına gelmiştir. Allah pek iyi işiten, çok iyi bilendir.', 11, 201, 129, 10873, 'Tövbe', 'Medine', 113),
(1334, 99, 9, 'Tevbe', 'وَمِنَ الْاَعْرَابِ مَنْ يُؤْمِنُ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَيَتَّخِذُ مَا يُنْفِقُ قُرُبَاتٍ عِنْدَ اللّٰهِ وَصَلَوَاتِ الرَّسُولِۜ اَلَٓا اِنَّهَا قُرْبَةٌ لَهُمْۜ سَيُدْخِلُهُمُ اللّٰهُ ف۪ي رَحْمَتِه۪ۜ اِنَّ اللّٰهَ غَفُورٌ رَح۪يمٌ۟', 'Bedevîlerden öylesi de vardır ki, Allah´a ve ahiret gününe inanır, (hayır için) harcayacağını Allah katında yakınlığa ve Peygamber´in dualarını almaya vesile edinir. Bilesiniz ki o (harcadıkları mal, Allah katında) onlar için bir yakınlıktır. Allah onları rahmetine (cennetine) koyacaktır. Şüphesiz Allah bağışlayan, esirgeyendir.', 11, 201, 129, 10873, 'Tövbe', 'Medine', 113),
(1335, 100, 9, 'Tevbe', 'وَالسَّابِقُونَ الْاَوَّلُونَ مِنَ الْمُهَاجِر۪ينَ وَالْاَنْصَارِ وَالَّذ۪ينَ اتَّبَعُوهُمْ بِاِحْسَانٍۙ رَضِيَ اللّٰهُ عَنْهُمْ وَرَضُوا عَنْهُ وَاَعَدَّ لَهُمْ جَنَّاتٍ تَجْر۪ي تَحْتَهَا الْاَنْهَارُ خَالِد۪ينَ ف۪يهَٓا اَبَداًۜ ذٰلِكَ الْفَوْزُ الْعَظ۪يمُ', '(İslâm dinine girme hususunda) öne geçen ilk muhacirler ve ensar ile onlara güzellikle tabi olanlar var ya, işte Allah onlardan razı olmuştur, onlar da Allah´tan razı olmuşlardır. Allah onlara, içinde ebedî kalacakları, zemininden ırmaklar akan cennetler hazırlamıştır. İşte bu büyük kurtuluştur.', 11, 202, 129, 10873, 'Tövbe', 'Medine', 113),
(1336, 101, 9, 'Tevbe', 'وَمِمَّنْ حَوْلَكُمْ مِنَ الْاَعْرَابِ مُنَافِقُونَۜ وَمِنْ اَهْلِ الْمَد۪ينَةِ مَرَدُوا عَلَى النِّفَاقِ لَا تَعْلَمُهُمْۜ نَحْنُ نَعْلَمُهُمْۜ سَنُعَذِّبُهُمْ مَرَّتَيْنِ ثُمَّ يُرَدُّونَ اِلٰى عَذَابٍ عَظ۪يمٍۚ', 'Çevrenizdeki bedevî Araplardan ve Medine halkından birtakım münafıklar vardır ki, münafıklıkta maharet kazanmışlardır. Sen onları bilmezsin, biz biliriz onları. Onlara iki kez azap edeceğiz, sonra da onlar büyük bir azaba itileceklerdir.', 11, 202, 129, 10873, 'Tövbe', 'Medine', 113),
(1337, 102, 9, 'Tevbe', 'وَاٰخَرُونَ اعْتَرَفُوا بِذُنُوبِهِمْ خَلَطُوا عَمَلاً صَالِحاً وَاٰخَرَ سَيِّئاًۜ عَسَى اللّٰهُ اَنْ يَتُوبَ عَلَيْهِمْۜ اِنَّ اللّٰهَ غَفُورٌ رَح۪يمٌ', 'Diğerleri ise günahlarını itiraf ettiler, iyi bir ameli diğer kötü bir amelle karıştırdılar. (Tevbe ederlerse) umulur ki Allah onların tevbesini kabul eder. Çünkü Allah çok bağışlayan, pek esirgeyendir.', 11, 202, 129, 10873, 'Tövbe', 'Medine', 113),
(1338, 103, 9, 'Tevbe', 'خُذْ مِنْ اَمْوَالِهِمْ صَدَقَةً تُطَهِّرُهُمْ وَتُزَكّ۪يهِمْ بِهَا وَصَلِّ عَلَيْهِمْۜ اِنَّ صَلٰوتَكَ سَكَنٌ لَهُمْۜ وَاللّٰهُ سَم۪يعٌ عَل۪يمٌ', 'Onların mallarından sadaka al; bununla onları (günahlardan) temizlersin, onları arıtıp yüceltirsin. Ve onlar için dua et. Çünkü senin duan onlar için sükûnettir (onları yatıştırır). Allah işitendir, bilendir.', 11, 202, 129, 10873, 'Tövbe', 'Medine', 113),
(1339, 104, 9, 'Tevbe', 'اَلَمْ يَعْلَمُٓوا اَنَّ اللّٰهَ هُوَ يَقْبَلُ التَّوْبَةَ عَنْ عِبَادِه۪ وَيَأْخُذُ الصَّدَقَاتِ وَاَنَّ اللّٰهَ هُوَ التَّوَّابُ الرَّح۪يمُ', 'Allah´ın, kullarının tevbesini kabul edeceğini, sadakaları geri çevirmeyeceğini ve Allah´ın tevbeyi çok kabul eden ve pek esirgeyen olduğunu hâla bilmezler mi?', 11, 202, 129, 10873, 'Tövbe', 'Medine', 113),
(1340, 105, 9, 'Tevbe', 'وَقُلِ اعْمَلُوا فَسَيَرَى اللّٰهُ عَمَلَكُمْ وَرَسُولُهُ وَالْمُؤْمِنُونَۜ وَسَتُرَدُّونَ اِلٰى عَالِمِ الْغَيْبِ وَالشَّهَادَةِ فَيُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُونَۚ', 'De ki: (Yapacağınızı) yapın! Amelinizi Allah da Resûlü de müminler de görecektir. Sonra görüleni ve görülmeyeni bilen Allah´a döndürüleceksiniz de O size yapmakta olduklarınızı haber verecektir.', 11, 202, 129, 10873, 'Tövbe', 'Medine', 113),
(1341, 106, 9, 'Tevbe', 'وَاٰخَرُونَ مُرْجَوْنَ لِاَمْرِ اللّٰهِ اِمَّا يُعَذِّبُهُمْ وَاِمَّا يَتُوبُ عَلَيْهِمْۜ وَاللّٰهُ عَل۪يمٌ حَك۪يمٌ', '(Sefere katılmayanlardan) diğer bir gurup da Allah´ın emrine bırakılmışlardır. O, bunlara ya azap eder veya tevbelerini kabul eder. Allah çok bilendir, hikmet sahibidir.', 11, 202, 129, 10873, 'Tövbe', 'Medine', 113),
(1342, 107, 9, 'Tevbe', 'وَالَّذ۪ينَ اتَّخَذُوا مَسْجِداً ضِرَاراً وَكُفْراً وَتَفْر۪يقاً بَيْنَ الْمُؤْمِن۪ينَ وَاِرْصَاداً لِمَنْ حَارَبَ اللّٰهَ وَرَسُولَهُ مِنْ قَبْلُۜ وَلَيَحْلِفُنَّ اِنْ اَرَدْنَٓا اِلَّا الْحُسْنٰىۜ وَاللّٰهُ يَشْهَدُ اِنَّهُمْ لَكَاذِبُونَ', '(Münafıklar arasında) bir de (müminlere) zarar vermek, (hakkı) inkâr etmek, müminlerin arasına ayrılık sokmak ve daha önce Allah ve Resûlüne karşı savaşmış olan adamı beklemek için bir mescid kuranlar ve: (Bununla) iyilikten başka birşey istemedik, diye mutlaka yemin edecek olanlar da vardır. Halbuki Allah onların kesinlikle yalancı olduklarına şahitlik eder.', 11, 203, 129, 10873, 'Tövbe', 'Medine', 113),
(1343, 108, 9, 'Tevbe', 'لَا تَقُمْ ف۪يهِ اَبَداًۜ لَمَسْجِدٌ اُسِّسَ عَلَى التَّقْوٰى مِنْ اَوَّلِ يَوْمٍ اَحَقُّ اَنْ تَقُومَ ف۪يهِۜ ف۪يهِ رِجَالٌ يُحِبُّونَ اَنْ يَتَطَهَّرُواۜ وَاللّٰهُ يُحِبُّ الْمُطَّهِّر۪ينَ', 'Onun içinde asla namaz kılma! İlk günden takvâ üzerine kurulan mescit (Kuba Mescidi) içinde namaz kılman elbette daha doğrudur. Onda temizlenmeyi seven adamlar vardır. Allah da çok temizlenenleri sever.', 11, 203, 129, 10873, 'Tövbe', 'Medine', 113),
(1344, 109, 9, 'Tevbe', 'اَفَمَنْ اَسَّسَ بُنْيَانَهُ عَلٰى تَقْوٰى مِنَ اللّٰهِ وَرِضْوَانٍ خَيْرٌ اَمْ مَنْ اَسَّسَ بُنْيَانَهُ عَلٰى شَفَا جُرُفٍ هَارٍ فَانْهَارَ بِه۪ ف۪ي نَارِ جَهَنَّمَۜ وَاللّٰهُ لَا يَهْدِي الْقَوْمَ الظَّالِم۪ينَ', 'Binasını Allah korkusu ve rızası üzerine kuran kimse mi daha hayırlıdır, yoksa yapısını yıkılacak bir yarın kenarına kurup, onunla beraber kendisi de çöküp cehennem ateşine giden kimse mi? Allah zalimler topluluğunu doğru yola iletmez.', 11, 203, 129, 10873, 'Tövbe', 'Medine', 113),
(1345, 110, 9, 'Tevbe', 'لَا يَزَالُ بُنْيَانُهُمُ الَّذ۪ي بَنَوْا ر۪يبَةً ف۪ي قُلُوبِهِمْ اِلَّٓا اَنْ تَقَطَّعَ قُلُوبُهُمْۜ وَاللّٰهُ عَل۪يمٌ حَك۪يمٌ۟', 'Yaptıkları bina, (ölüp de) kalpleri parçalanıncaya kadar yüreklerine devamlı olarak bir kuşku (sebebi) olacaktır. Allah çok iyi bilendir, hikmet sahibidir.', 11, 203, 129, 10873, 'Tövbe', 'Medine', 113),
(1346, 111, 9, 'Tevbe', 'اِنَّ اللّٰهَ اشْتَرٰى مِنَ الْمُؤْمِن۪ينَ اَنْفُسَهُمْ وَاَمْوَالَهُمْ بِاَنَّ لَهُمُ الْجَنَّةَۜ يُقَاتِلُونَ ف۪ي سَب۪يلِ اللّٰهِ فَيَقْتُلُونَ وَيُقْتَلُونَ وَعْداً عَلَيْهِ حَقاًّ فِي التَّوْرٰيةِ وَالْاِنْج۪يلِ وَالْقُرْاٰنِۜ وَمَنْ اَوْفٰى بِعَهْدِه۪ مِنَ اللّٰهِ فَاسْتَبْشِرُوا بِبَيْعِكُمُ الَّذ۪ي بَايَعْتُمْ بِه۪ۜ وَذٰلِكَ هُوَ الْفَوْزُ الْعَظ۪يمُ', 'Allah müminlerden, mallarını ve canlarını, kendilerine (verilecek) cennet karşılığında satın almıştır. Çünkü onlar Allah yolunda savaşırlar, öldürürler, ölürler. (Bu), Tevrat´ta, İncil´de ve Kur´an´da Allah üzerine hak bir vaaddir. Allah´tan daha çok sözünü yerine getiren kim vardır! O halde O´nunla yapmış olduğunuz bu alışverişinizden dolayı sevinin. İşte bu, (gerçekten) büyük kazançtır.', 11, 203, 129, 10873, 'Tövbe', 'Medine', 113),
(1347, 112, 9, 'Tevbe', 'اَلتَّٓائِبُونَ الْعَابِدُونَ الْحَامِدُونَ السَّٓائِحُونَ الرَّاكِعُونَ السَّاجِدُونَ الْاٰمِرُونَ بِالْمَعْرُوفِ وَالنَّاهُونَ عَنِ الْمُنْكَرِ وَالْحَافِظُونَ لِحُدُودِ اللّٰهِۜ وَبَشِّرِ الْمُؤْمِن۪ينَ', '(Bu alış verişi yapanlar), tevbe edenler, ibadet edenler, hamdedenler, oruç tutanlar, rükû edenler, secde edenler, iyiliği emredip kötülükten alıkoyanlar ve Allah´ın sınırlarını koruyanlardır. O müminleri müjdele!', 11, 204, 129, 10873, 'Tövbe', 'Medine', 113),
(1348, 113, 9, 'Tevbe', 'مَا كَانَ لِلنَّبِيِّ وَالَّذ۪ينَ اٰمَنُٓوا اَنْ يَسْتَغْفِرُوا لِلْمُشْرِك۪ينَ وَلَوْ كَانُٓوا اُو۬ل۪ي قُرْبٰى مِنْ بَعْدِ مَا تَبَيَّنَ لَهُمْ اَنَّهُمْ اَصْحَابُ الْجَح۪يمِ', '(Kâfir olarak ölüp) cehennem ehli oldukları onlara açıkça belli olduktan sonra, akraba dahi olsalar, (Allah´a) ortak koşanlar için af dilemek ne peygambere yaraşır ne de inananlara.', 11, 204, 129, 10873, 'Tövbe', 'Medine', 113),
(1349, 114, 9, 'Tevbe', 'وَمَا كَانَ اسْتِغْفَارُ اِبْرٰه۪يمَ لِاَب۪يهِ اِلَّا عَنْ مَوْعِدَةٍ وَعَدَهَٓا اِيَّاهُۚ فَلَمَّا تَبَيَّنَ لَـهُٓ اَنَّهُ عَدُوٌّ لِلّٰهِ تَبَرَّاَ مِنْهُۜ اِنَّ اِبْرٰه۪يمَ لَاَوَّاهٌ حَل۪يمٌ', 'İbrahim´in babası için af dilemesi, sadece ona verdiği sözden dolayı idi. Ne var ki, onun Allah´ın düşmanı olduğu kendisine belli olunca, ondan uzaklaştı. Şüphesiz ki İbrahim çok yumuşak huylu ve pek sabırlı idi.', 11, 204, 129, 10873, 'Tövbe', 'Medine', 113),
(1350, 115, 9, 'Tevbe', 'وَمَا كَانَ اللّٰهُ لِيُضِلَّ قَوْماً بَعْدَ اِذْ هَدٰيهُمْ حَتّٰى يُبَيِّنَ لَهُمْ مَا يَتَّقُونَۜ اِنَّ اللّٰهَ بِكُلِّ شَيْءٍ عَل۪يمٌ', 'Allah bir topluluğu doğru yola ilettikten sonra, sakınacakları şeyleri kendilerine açıklayıncaya kadar onları saptıracak değildir. Allah her şeyi çok iyi bilendir.', 11, 204, 129, 10873, 'Tövbe', 'Medine', 113),
(1351, 116, 9, 'Tevbe', 'اِنَّ اللّٰهَ لَهُ مُلْكُ السَّمٰوَاتِ وَالْاَرْضِۜ يُحْـي۪ وَيُم۪يتُۜ وَمَا لَكُمْ مِنْ دُونِ اللّٰهِ مِنْ وَلِيٍّ وَلَا نَص۪يرٍ', 'Göklerin ve yerin mülkü yalnız Allah´ındır. O diriltir ve öldürür. Sizin için Allah´tan başka ne bir dost ne de bir yardımcı vardır.', 11, 204, 129, 10873, 'Tövbe', 'Medine', 113),
(1352, 117, 9, 'Tevbe', 'لَقَدْ تَابَ اللّٰهُ عَلَى النَّبِيِّ وَالْمُهَاجِر۪ينَ وَالْاَنْصَارِ الَّذ۪ينَ اتَّبَعُوهُ ف۪ي سَاعَةِ الْعُسْرَةِ مِنْ بَعْدِ مَا كَادَ يَز۪يغُ قُلُوبُ فَر۪يقٍ مِنْهُمْ ثُمَّ تَابَ عَلَيْهِمْۜ اِنَّهُ بِهِمْ رَؤُ۫فٌ رَح۪يمٌۙ', 'Andolsun ki Allah, müslümanlardan bir gurubun kalpleri eğrilmeye yüz tuttuktan sonra, Peygamberi ve güçlük zamanında ona uyan muhacirlerle ensarı affetti. Sonra da onların tevbelerini kabul etti. Çünkü O, onlara karşı çok şefkatli, pek merhametlidir.', 11, 204, 129, 10873, 'Tövbe', 'Medine', 113),
(1353, 118, 9, 'Tevbe', 'وَعَلَى الثَّلٰثَةِ الَّذ۪ينَ خُلِّفُواۜ حَتّٰٓى اِذَا ضَاقَتْ عَلَيْهِمُ الْاَرْضُ بِمَا رَحُبَتْ وَضَاقَتْ عَلَيْهِمْ اَنْفُسُهُمْ وَظَنُّٓوا اَنْ لَا مَلْجَأَ مِنَ اللّٰهِ اِلَّٓا اِلَيْهِۜ ثُمَّ تَابَ عَلَيْهِمْ لِيَتُوبُواۜ اِنَّ اللّٰهَ هُوَ التَّوَّابُ الرَّح۪يمُ۟', 'Ve (seferden) geri bırakılan üç kişinin de (tevbelerini kabul etti). Yeryüzü, genişliğine rağmen onlara dar gelmiş, vicdanları kendilerini sıktıkça sıkmıştı. Nihayet Allah´tan (O´nun azabından) yine Allah´a sığınmaktan başka çare olmadığını anlamışlardı. Sonra (eski hallerine) dönmeleri için Allah onların tevbesini kabul etti. Çünkü Allah tevbeyi çok kabul eden, pek esirgeyendir.', 11, 205, 129, 10873, 'Tövbe', 'Medine', 113),
(1354, 119, 9, 'Tevbe', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا اتَّقُوا اللّٰهَ وَكُونُوا مَعَ الصَّادِق۪ينَ', 'Ey iman edenler! Allah´tan korkun ve doğrularla beraber olun.', 11, 205, 129, 10873, 'Tövbe', 'Medine', 113),
(1355, 120, 9, 'Tevbe', 'مَا كَانَ لِاَهْلِ الْمَد۪ينَةِ وَمَنْ حَوْلَهُمْ مِنَ الْاَعْرَابِ اَنْ يَتَخَلَّفُوا عَنْ رَسُولِ اللّٰهِ وَلَا يَرْغَبُوا بِاَنْفُسِهِمْ عَنْ نَفْسِه۪ۜ ذٰلِكَ بِاَنَّهُمْ لَا يُص۪يبُهُمْ ظَمَاٌ وَلَا نَصَبٌ وَلَا مَخْمَصَةٌ ف۪ي سَب۪يلِ اللّٰهِ وَلَا يَطَؤُ۫نَ مَوْطِئاً يَغ۪يظُ الْكُفَّارَ وَلَا يَنَالُونَ مِنْ عَدُوٍّ نَيْلاً اِلَّا كُتِبَ لَهُمْ بِه۪ عَمَلٌ صَالِحٌۜ اِنَّ اللّٰهَ لَا يُض۪يعُ اَجْرَ الْمُحْسِن۪ينَۙ', 'Medine halkına ve onların çevresinde bulunan bedevî Araplara Allah´ın Resûlünden geri kalmaları ve onun canından önce kendi canlarını düşünmeleri yakışmaz. İşte onların Allah yolunda bir susuzluğa, bir yorgunluğa ve bir açlığa dûçar olmaları, kâfirleri öfkelendirecek bir yere (ayak) basmaları ve düşmana karşı bir başarı kazanmaları, ancak bunların karşılığında kendilerine salih bir amel yazılması içindir. Çünkü Allah iyilik yapanların mükâfatını zayi etmez.', 11, 205, 129, 10873, 'Tövbe', 'Medine', 113),
(1356, 121, 9, 'Tevbe', 'وَلَا يُنْفِقُونَ نَفَقَةً صَغ۪يرَةً وَلَا كَب۪يرَةً وَلَا يَقْطَعُونَ وَادِياً اِلَّا كُتِبَ لَهُمْ لِيَجْزِيَهُمُ اللّٰهُ اَحْسَنَ مَا كَانُوا يَعْمَلُونَ', 'Allah onları, yapmakta olduklarının en güzeli ile mükâfatlandırmak için küçük büyük yaptıkları her masraf, geçtikleri her vâdi mutlaka onların lehine yazılır.', 11, 205, 129, 10873, 'Tövbe', 'Medine', 113),
(1357, 122, 9, 'Tevbe', 'وَمَا كَانَ الْمُؤْمِنُونَ لِيَنْفِرُوا كَٓافَّةًۜ فَلَوْلَا نَفَرَ مِنْ كُلِّ فِرْقَةٍ مِنْهُمْ طَٓائِفَةٌ لِيَتَفَقَّهُوا فِي الدّ۪ينِ وَلِيُنْذِرُوا قَوْمَهُمْ اِذَا رَجَعُٓوا اِلَيْهِمْ لَعَلَّهُمْ يَحْذَرُونَ۟', 'Müminlerin hepsinin toptan sefere çıkmaları doğru değildir. Onların her kesiminde bir gurup dinde (dinî ilimlerde) geniş bilgi elde etmek ve kavimleri (savaştan) döndüklerinde onları ikaz etmek için geride kalmalıdır. Umulur ki sakınırlar.', 11, 205, 129, 10873, 'Tövbe', 'Medine', 113),
(1358, 123, 9, 'Tevbe', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا قَاتِلُوا الَّذ۪ينَ يَلُونَكُمْ مِنَ الْكُفَّارِ وَلْيَجِدُوا ف۪يكُمْ غِلْظَةًۜ وَاعْلَمُٓوا اَنَّ اللّٰهَ مَعَ الْمُتَّق۪ينَ', 'Ey iman edenler! Kâfirlerden yakınınızda olanlara karşı savaşın ve onlar (savaş anında) sizde bir sertlik bulsunlar. Bilin ki, Allah sakınanlarla beraberdir.', 11, 206, 129, 10873, 'Tövbe', 'Medine', 113),
(1359, 124, 9, 'Tevbe', 'وَاِذَا مَٓا اُنْزِلَتْ سُورَةٌ فَمِنْهُمْ مَنْ يَقُولُ اَيُّـكُمْ زَادَتْهُ هٰذِه۪ٓ ا۪يمَاناًۚ فَاَمَّا الَّذ۪ينَ اٰمَنُوا فَزَادَتْهُمْ ا۪يمَاناً وَهُمْ يَسْتَبْشِرُونَ', 'Herhangi bir sûre indirildiği zaman onlardan bir kısmı der ki: «Bu sizin hanginizin imanını artırdı?» İman edenlere gelince (bu sûre) onların imanlarını artırır ve onlar sevinirler.', 11, 206, 129, 10873, 'Tövbe', 'Medine', 113),
(1360, 125, 9, 'Tevbe', 'وَاَمَّا الَّذ۪ينَ ف۪ي قُلُوبِهِمْ مَرَضٌ فَزَادَتْهُمْ رِجْساً اِلٰى رِجْسِهِمْ وَمَاتُوا وَهُمْ كَافِرُونَ', 'Kalplerinde hastalık (kâfirlik ve münafıklık) olanlara gelince, onların da inkârlarını büsbütün artırır ve onlar artık kâfir olarak ölürler.', 11, 206, 129, 10873, 'Tövbe', 'Medine', 113),
(1361, 126, 9, 'Tevbe', 'اَوَلَا يَرَوْنَ اَنَّهُمْ يُفْتَنُونَ ف۪ي كُلِّ عَامٍ مَرَّةً اَوْ مَرَّتَيْنِ ثُمَّ لَا يَتُوبُونَ وَلَا هُمْ يَذَّكَّرُونَ', 'Onlar, her yıl bir veya iki kez (çeşitli belâlarla) imtihan edildiklerini görmüyorlar mı? Sonra da ne tevbe ediyorlar ne de ibret alıyorlar.', 11, 206, 129, 10873, 'Tövbe', 'Medine', 113),
(1362, 127, 9, 'Tevbe', 'وَاِذَا مَٓا اُنْزِلَتْ سُورَةٌ نَظَرَ بَعْضُهُمْ اِلٰى بَعْضٍۜ هَلْ يَرٰيكُمْ مِنْ اَحَدٍ ثُمَّ انْصَرَفُواۜ صَرَفَ اللّٰهُ قُلُوبَهُمْ بِاَنَّهُمْ قَوْمٌ لَا يَفْقَهُونَ', 'Bir sûre indirildiği zaman, (göz kırpıp alay ederek) birbirlerine bakar (ve): (Çevreden) sizi birisi görüyor mu? diye sorarlar, sonra da (sıvışıp) giderler. Anlamayan bir kavim oldukları için Allah onların kalplerini (imandan) çevirmiştir.', 11, 206, 129, 10873, 'Tövbe', 'Medine', 113),
(1363, 128, 9, 'Tevbe', 'لَقَدْ جَٓاءَكُمْ رَسُولٌ مِنْ اَنْفُسِكُمْ عَز۪يزٌۘ عَلَيْهِ مَا عَنِتُّمْ حَر۪يصٌ عَلَيْكُمْ بِالْمُؤْمِن۪ينَ رَؤُ۫فٌ رَح۪يمٌ', 'Andolsun size kendinizden öyle bir Peygamber gelmiştir ki, sizin sıkıntıya uğramanız ona çok ağır gelir. O, size çok düşkün, müminlere karşı çok şefkatlidir, merhametlidir.', 11, 206, 129, 10873, 'Tövbe', 'Medine', 113),
(1364, 129, 9, 'Tevbe', 'فَاِنْ تَوَلَّوْا فَقُلْ حَسْبِيَ اللّٰهُۘ لَٓا اِلٰهَ اِلَّا هُوَۜ عَلَيْهِ تَوَكَّلْتُ وَهُوَ رَبُّ الْعَرْشِ الْعَظ۪يمِ', '(Ey Muhammed!) Yüz çevirirlerse de ki: Allah bana yeter. O´ndan başka ilâh yoktur. Ben sadece O´na güvenip dayanırım. O yüce Arş´ın sahibidir.', 11, 206, 129, 10873, 'Tövbe', 'Medine', 113),
(1365, 1, 10, 'Yunus', 'الٓـرٰ۠ تِلْكَ اٰيَاتُ الْكِتَابِ الْحَك۪يمِ', 'Elif. Lâm. Râ. İşte bunlar hikmet dolu Kitâb’ın âyetleridir.', 11, 207, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1366, 2, 10, 'Yunus', 'اَكَانَ لِلنَّاسِ عَجَباً اَنْ اَوْحَيْنَٓا اِلٰى رَجُلٍ مِنْهُمْ اَنْ اَنْذِرِ النَّاسَ وَبَشِّرِ الَّذ۪ينَ اٰمَنُٓوا اَنَّ لَهُمْ قَدَمَ صِدْقٍ عِنْدَ رَبِّهِمْۜ قَالَ الْكَافِرُونَ اِنَّ هٰذَا لَسَاحِرٌ مُب۪ينٌ', 'İçlerinden bir adama: İnsanları uyar ve iman edenlere, Rableri katında onlar için yüksek bir doğruluk makamı olduğunu müjdele, diye vahyetmemiz, insanlar için şaşılacak bir şey mi oldu ki, o kâfirler: Bu elbette apaçık bir sihirbazdır, dediler?', 11, 207, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1367, 3, 10, 'Yunus', 'اِنَّ رَبَّكُمُ اللّٰهُ الَّذ۪ي خَلَقَ السَّمٰوَاتِ وَالْاَرْضَ ف۪ي سِتَّةِ اَيَّامٍ ثُمَّ اسْتَوٰى عَلَى الْعَرْشِ يُدَبِّرُ الْاَمْرَۜ مَا مِنْ شَف۪يعٍ اِلَّا مِنْ بَعْدِ اِذْنِه۪ۜ ذٰلِكُمُ اللّٰهُ رَبُّكُمْ فَاعْبُدُوهُۜ اَفَلَا تَذَكَّرُونَ', 'Şüphesiz ki Rabbiniz, gökleri ve yeri altı günde yaratan, sonra da işleri yerli yerince idare ederek arşa istiva eden Allah’dır. Onun izni olmadan hiç kimse şefaatçı olamaz. İşte O Rabbiniz Allah’tır. O halde O’na kulluk edin. Hâla düşünmüyor musunuz!', 11, 207, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1368, 4, 10, 'Yunus', 'اِلَيْهِ مَرْجِعُكُمْ جَم۪يعاًۜ وَعْدَ اللّٰهِ حَقاًّۜ اِنَّهُ يَبْدَؤُا الْخَلْقَ ثُمَّ يُع۪يدُهُ لِيَجْزِيَ الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ بِالْقِسْطِۜ وَالَّذ۪ينَ كَفَرُوا لَهُمْ شَرَابٌ مِنْ حَم۪يمٍ وَعَذَابٌ اَل۪يمٌ بِمَا كَانُوا يَكْفُرُونَ', 'Allah’ın gerçek bir vâdi olarak hepinizin dönüşü ancak O’nadır. Çünkü O, mahlûkatı önce (yoktan) yaratır, sonra da iman edip iyi işler yapanlara adaletle mükâfat vermek için (onları huzuruna) geri çevirir. Kâfir olanlara gelince, inkâr etmekte oldukları şeylerden ötürü onlar için kaynar sudan bir içki ve elem verici bir azap vardır.', 11, 207, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1369, 5, 10, 'Yunus', 'هُوَ الَّذ۪ي جَعَلَ الشَّمْسَ ضِيَٓاءً وَالْقَمَرَ نُوراً وَقَدَّرَهُ مَنَازِلَ لِتَعْلَمُوا عَدَدَ السِّن۪ينَ وَالْحِسَابَۜ مَا خَلَقَ اللّٰهُ ذٰلِكَ اِلَّا بِالْحَقِّۜ يُفَصِّلُ الْاٰيَاتِ لِقَوْمٍ يَعْلَمُونَ', 'Güneşi ışıklı, ayı da parlak kılan, yılların sayısını ve hesabı bilmeniz için ona (aya) birtakım menziller takdir eden O’dur. Allah bunları, ancak bir gerçeğe (ve hikmete) binaen yaratmıştır. O, bilen bir kavme âyetlerini açıklamaktadır.', 11, 207, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1370, 6, 10, 'Yunus', 'اِنَّ فِي اخْتِلَافِ الَّيْلِ وَالنَّهَارِ وَمَا خَلَقَ اللّٰهُ فِي السَّمٰوَاتِ وَالْاَرْضِ لَاٰيَاتٍ لِقَوْمٍ يَتَّقُونَ', 'Gece ve gündüzün değişmesinde (uzayıp kısalmasında) Allah’ın göklerde ve yerde yarattığı şeylerde, (Onu inkâr etmekten) sakınan bir kavim için elbette nice deliller vardır!', 11, 207, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1371, 7, 10, 'Yunus', 'اِنَّ الَّذ۪ينَ لَا يَرْجُونَ لِقَٓاءَنَا وَرَضُوا بِالْحَيٰوةِ الدُّنْيَا وَاطْمَاَنُّوا بِهَا وَالَّذ۪ينَ هُمْ عَنْ اٰيَاتِنَا غَافِلُونَۙ', '(7-8) Huzurumuza çıkacaklarını beklemeyenler, dünya hayatına razı olup onunla rahat bulanlar ve âyetlerimizden gafil olanlar yok mu, işte onların, kazanmakta oldukları (günahlar) yüzünden varacakları yer, ateştir!', 11, 208, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1372, 8, 10, 'Yunus', 'اُو۬لٰٓئِكَ مَأْوٰيهُمُ النَّارُ بِمَا كَانُوا يَكْسِبُونَ', '(7-8) Huzurumuza çıkacaklarını beklemeyenler, dünya hayatına razı olup onunla rahat bulanlar ve âyetlerimizden gafil olanlar yok mu, işte onların, kazanmakta oldukları (günahlar) yüzünden varacakları yer, ateştir!', 11, 208, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1373, 9, 10, 'Yunus', 'اِنَّ الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ يَهْد۪يهِمْ رَبُّهُمْ بِا۪يمَانِهِمْۚ تَجْر۪ي مِنْ تَحْتِهِمُ الْاَنْهَارُ ف۪ي جَنَّاتِ النَّع۪يمِ', 'İman edip güzel işler yapanlara gelince, imanları sebebiyle Rableri onları nimet dolu cennetlerde, alt tarafından ırmaklar akan (saraylara) erdirir.', 11, 208, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1374, 10, 10, 'Yunus', 'دَعْوٰيهُمْ ف۪يهَا سُبْحَانَكَ اللّٰهُمَّ وَتَحِيَّتُهُمْ ف۪يهَا سَلَامٌۚ وَاٰخِرُ دَعْوٰيهُمْ اَنِ الْحَمْدُ لِلّٰهِ رَبِّ الْعَالَم۪ينَ۟', 'Onların oradaki duası: «Allah’ım! Seni noksan sıfatlardan tenzih ederiz!» (sözleridir). Orada birbirleriyle karşılaştıkça söyledikleri ise «selâm» dır. Onların dualarının sonu da şudur: Hamd, âlemlerin Rabbi Allah’a mahsustur.', 11, 208, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1375, 11, 10, 'Yunus', 'وَلَوْ يُعَجِّلُ اللّٰهُ لِلنَّاسِ الشَّرَّ اسْتِعْجَالَهُمْ بِالْخَيْرِ لَقُضِيَ اِلَيْهِمْ اَجَلُهُمْۜ فَنَذَرُ الَّذ۪ينَ لَا يَرْجُونَ لِقَٓاءَنَا ف۪ي طُغْيَانِهِمْ يَعْمَهُونَ', 'Eğer Allah insanlara, hayrı çarçabuk istedikleri gibi şerri de acele verseydi, elbette onların ecelleri bitirilmiş olurdu. Fakat bize kavuşmayı beklemeyenleri biz, azgınlıkları içinde bocalar bir halde (kendi başlarına) bırakırız.', 11, 208, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1376, 12, 10, 'Yunus', 'وَاِذَا مَسَّ الْاِنْسَانَ الضُّرُّ دَعَانَا لِجَنْبِه۪ٓ اَوْ قَاعِداً اَوْ قَٓائِماًۚ فَلَمَّا كَشَفْنَا عَنْهُ ضُرَّهُ مَرَّ كَاَنْ لَمْ يَدْعُنَٓا اِلٰى ضُرٍّ مَسَّهُۜ كَذٰلِكَ زُيِّنَ لِلْمُسْرِف۪ينَ مَا كَانُوا يَعْمَلُونَ', 'İnsana bir zarar geldiği zaman, yan yatarak, oturarak veya ayakta durarak (o zararın giderilmesi için) bize dua eder; fakat biz ondan sıkıntısını kaldırınca, sanki kendisine dokunan bir sıkıntıdan ötürü bize dua etmemiş gibi geçip gider. İşte böylece haddi aşanlara yapmakta oldukları şeyler güzel gösterildi.', 11, 208, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1377, 13, 10, 'Yunus', 'وَلَقَدْ اَهْلَكْنَا الْقُرُونَ مِنْ قَبْلِكُمْ لَمَّا ظَلَمُواۙ وَجَٓاءَتْهُمْ رُسُلُهُمْ بِالْبَيِّنَاتِ وَمَا كَانُوا لِيُؤْمِنُواۜ كَذٰلِكَ نَجْزِي الْقَوْمَ الْمُجْرِم۪ينَ', 'Andolsun ki sizden önce, peygamberleri kendilerine mûcizeler getirdiği halde (yalanlayıp) zulmettiklerinden dolayı nice milletleri helâk ettik; zaten onlar iman edecek değillerdi. İşte biz suçlu kavimleri böyle cezalandırırız.', 11, 208, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1378, 14, 10, 'Yunus', 'ثُمَّ جَعَلْنَاكُمْ خَلَٓائِفَ فِي الْاَرْضِ مِنْ بَعْدِهِمْ لِنَنْظُرَ كَيْفَ تَعْمَلُونَ', 'Sonra da, nasıl davranacağınızı görmemiz için onların ardından sizi yeryüzünde halifeler kıldık (Onların yerine sizi getirdik).', 11, 208, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1379, 15, 10, 'Yunus', 'وَاِذَا تُتْلٰى عَلَيْهِمْ اٰيَاتُنَا بَيِّنَاتٍۙ قَالَ الَّذ۪ينَ لَا يَرْجُونَ لِقَٓاءَنَا ائْتِ بِقُرْاٰنٍ غَيْرِ هٰذَٓا اَوْ بَدِّلْهُۜ قُلْ مَا يَكُونُ ل۪ٓي اَنْ اُبَدِّلَهُ مِنْ تِلْقَٓائِ۬ نَفْس۪يۚ اِنْ اَتَّبِعُ اِلَّا مَا يُوحٰٓى اِلَيَّۚ اِنّ۪ٓي اَخَافُ اِنْ عَصَيْتُ رَبّ۪ي عَذَابَ يَوْمٍ عَظ۪يمٍ', 'Onlara âyetlerimiz açık açık okunduğu zaman (öldükten sonra) bize kavuşmayı beklemeyenler: Ya bundan başka bir Kur’an getir veya bunu değiştir! dediler. De ki: Onu kendiliğimden değiştirmem benim için olacak şey değildir. Ben, bana vahyolunandan başkasına uymam. Çünkü Rabbime isyan edersem elbette büyük günün azabından korkarım.', 11, 209, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1380, 16, 10, 'Yunus', 'قُلْ لَوْ شَٓاءَ اللّٰهُ مَا تَلَوْتُهُ عَلَيْكُمْ وَلَٓا اَدْرٰيكُمْ بِه۪ۘ فَقَدْ لَبِثْتُ ف۪يكُمْ عُمُراً مِنْ قَبْلِه۪ۜ اَفَلَا تَعْقِلُونَ', 'De ki: Eğer Allah dileseydi onu size okumazdım, Allah da onu size bildirmezdi. Ben bundan önce bir ömür boyu içinizde durmuştum. Hâla akıl erdiremiyor musunuz?', 11, 209, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1381, 17, 10, 'Yunus', 'فَمَنْ اَظْلَمُ مِمَّنِ افْتَرٰى عَلَى اللّٰهِ كَذِباً اَوْ كَذَّبَ بِاٰيَاتِه۪ۜ اِنَّهُ لَا يُفْلِحُ الْمُجْرِمُونَ', 'Öyleyse kim Allah’a karşı yalan uydurandan veya onun âyetlerini yalanlayandan daha zalimdir! Bilesiniz ki suçlular asla onmazlar!', 11, 209, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1382, 18, 10, 'Yunus', 'وَيَعْبُدُونَ مِنْ دُونِ اللّٰهِ مَا لَا يَضُرُّهُمْ وَلَا يَنْفَعُهُمْ وَيَقُولُونَ هٰٓؤُ۬لَٓاءِ شُفَعَٓاؤُ۬نَا عِنْدَ اللّٰهِۜ قُلْ اَتُنَبِّؤُ۫نَ اللّٰهَ بِمَا لَا يَعْلَمُ فِي السَّمٰوَاتِ وَلَا فِي الْاَرْضِۜ سُبْحَانَهُ وَتَعَالٰى عَمَّا يُشْرِكُونَ', 'Onlar Allah’ı bırakıp kendilerine ne zarar ne de fayda verebilecek şeylere tapıyorlar ve: Bunlar, Allah katında bizim şefaatçılarımızdır, diyorlar. De ki: «Siz Allah’a göklerde ve yerde bilemeyeceği bir şeyi mi haber veriyorsunuz? Hâşâ! O, onların ortak koştuklarından uzak ve yücedir.»', 11, 209, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1383, 19, 10, 'Yunus', 'وَمَا كَانَ النَّاسُ اِلَّٓا اُمَّةً وَاحِدَةً فَاخْتَلَفُواۜ وَلَوْلَا كَلِمَةٌ سَبَقَتْ مِنْ رَبِّكَ لَقُضِيَ بَيْنَهُمْ ف۪يمَا ف۪يهِ يَخْتَلِفُونَ', 'İnsanlar sadece bir tek ümmetti, sonradan ayrılığa düştüler. Eğer (azabın ertelenmesi ile ilgili) Rabbinden bir söz (ezelî bir takdir) geçmemiş olsaydı, ayrılığa düştükleri konuda hemen aralarında hüküm verilirdi (Derhal azap iner ve işleri bitirilirdi).', 11, 209, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1384, 20, 10, 'Yunus', 'وَيَقُولُونَ لَوْلَٓا اُنْزِلَ عَلَيْهِ اٰيَةٌ مِنْ رَبِّه۪ۚ فَقُلْ اِنَّمَا الْغَيْبُ لِلّٰهِ فَانْتَظِرُواۚ اِنّ۪ي مَعَكُمْ مِنَ الْمُنْتَظِر۪ينَ۟', 'Ona (Muhammed’e) Rabbinden bir mucize indirilse ya! diyorlar. De ki: Gayb ancak Allah’ındır. Bekleyin (bakalım) ben de sizinle beraber bekleyenlerdenim.', 11, 209, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1385, 21, 10, 'Yunus', 'وَاِذَٓا اَذَقْنَا النَّاسَ رَحْمَةً مِنْ بَعْدِ ضَرَّٓاءَ مَسَّتْهُمْ اِذَا لَهُمْ مَكْرٌ ف۪ٓي اٰيَاتِنَاۜ قُلِ اللّٰهُ اَسْرَعُ مَكْراًۜ اِنَّ رُسُلَنَا يَكْتُبُونَ مَا تَمْكُرُونَ', 'Kendilerine dokunan (kıtlık ve hastalık gibi) bir sıkıntıdan sonra insanlara bir rahmet (esenlik) tattırdığımız zaman, bir de bakarsın ki âyetlerimiz hakkında onların bir tuzağı vardır. De ki: Allah’ın tuzağı daha süratlidir. Şüphesiz elçilerimiz kurduğunuz tuzakları yazıyorlar.', 11, 210, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1386, 22, 10, 'Yunus', 'هُوَ الَّذ۪ي يُسَيِّرُكُمْ فِي الْبَرِّ وَالْبَحْرِۜ حَتّٰٓى اِذَا كُنْتُمْ فِي الْفُلْكِۚ وَجَرَيْنَ بِهِمْ بِر۪يحٍ طَيِّبَةٍ وَفَرِحُوا بِهَا جَٓاءَتْهَا ر۪يحٌ عَاصِفٌ وَجَٓاءَهُمُ الْمَوْجُ مِنْ كُلِّ مَكَانٍ وَظَنُّٓوا اَنَّهُمْ اُح۪يطَ بِهِمْۙ دَعَوُا اللّٰهَ مُخْلِص۪ينَ لَهُ الدّ۪ينَۚ لَئِنْ اَنْجَيْتَنَا مِنْ هٰذِه۪ لَنَكُونَنَّ مِنَ الشَّاكِر۪ينَ', 'Sizi karada ve denizde gezdiren O’dur. Hatta siz gemilerde bulunduğunuz, o gemiler de içindekileri tatlı bir rüzgârla alıp götürdükleri ve (yolcular) bu yüzden neşelendikleri zaman, o gemiye şiddetli bir fırtına gelip çatar, her yerden onlara dalgalar hücum eder ve onlar çepeçevre kuşatıldıklarını anlarlar da dini yalnız Allah’a halis kılarak: «Andolsun eğer bizi bundan kurtarırsan mutlaka şükredenlerden olacağız» diye Allah’a yalvarırlar.', 11, 210, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1387, 23, 10, 'Yunus', 'فَلَمَّٓا اَنْجٰيهُمْ اِذَا هُمْ يَبْغُونَ فِي الْاَرْضِ بِغَيْرِ الْحَقِّۜ يَٓا اَيُّهَا النَّاسُ اِنَّمَا بَغْيُكُمْ عَلٰٓى اَنْفُسِكُمْۙ مَتَاعَ الْحَيٰوةِ الدُّنْيَا ثُمَّ اِلَيْنَا مَرْجِعُكُمْ فَنُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُونَ', 'Fakat Allah onları kurtarınca bir de bakarsın ki onlar, yine haksız yere taşkınlık ediyorlar. Ey insanlar! Sizin taşkınlığınız ancak kendi aleyhinizedir; (bununla) sadece fâni dünya hayatının menfaatini elde edersiniz; sonunda dönüşünüz yine bizedir. O zaman yapmakta olduklarınızı size haber vereceğiz.', 11, 210, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1388, 24, 10, 'Yunus', 'اِنَّمَا مَثَلُ الْحَيٰوةِ الدُّنْيَا كَمَٓاءٍ اَنْزَلْنَاهُ مِنَ السَّمَٓاءِ فَاخْتَلَطَ بِه۪ نَبَاتُ الْاَرْضِ مِمَّا يَأْكُلُ النَّاسُ وَالْاَنْعَامُۜ حَتّٰٓى اِذَٓا اَخَذَتِ الْاَرْضُ زُخْرُفَهَا وَازَّيَّـنَتْ وَظَنَّ اَهْلُهَٓا اَنَّهُمْ قَادِرُونَ عَلَيْهَٓاۙ اَتٰيهَٓا اَمْرُنَا لَيْلاً اَوْ نَهَاراً فَجَعَلْنَاهَا حَص۪يداً كَاَنْ لَمْ تَغْنَ بِالْاَمْسِۜ كَذٰلِكَ نُفَصِّلُ الْاٰيَاتِ لِقَوْمٍ يَتَفَكَّرُونَ', 'Dünya hayatının durumu, gökten indirdiğimiz bir su gibidir ki, insanların ve hayvanların yiyeceklerinden olan yeryüzü bitkileri o su sayesinde gürleşip birbirine girer. Nihayet yeryüzü zinetini takınıp, (rengârenk) süslendiği ve sahipleri de onun üzerinde kudret sahibi olduklarını sandıkları bir sırada, bir gece veya gündüz ona emrimiz (âfetimiz) gelir de onu sanki dün yerinde yokmuş gibi kökünden koparılarak biçilmiş bir hale getiririz. İşte iyi düşünecek kavimler için âyetlerimizi böyle açıklıyoruz.', 11, 210, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1389, 25, 10, 'Yunus', 'وَاللّٰهُ يَدْعُٓوا اِلٰى دَارِ السَّلَامِۜ وَيَهْد۪ي مَنْ يَشَٓاءُ اِلٰى صِرَاطٍ مُسْتَق۪يمٍ', 'Allah kullarını esenlik yurduna çağırıyor ve O, dilediğini doğru yola iletir.', 11, 210, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1390, 26, 10, 'Yunus', 'لِلَّذ۪ينَ اَحْسَنُوا الْحُسْنٰى وَزِيَادَةٌۜ وَلَا يَرْهَقُ وُجُوهَهُمْ قَتَرٌ وَلَا ذِلَّةٌۜ اُو۬لٰٓئِكَ اَصْحَابُ الْجَنَّةِۚ هُمْ ف۪يهَا خَالِدُونَ', 'Güzel davrananlara daha güzel karşılık, bir de fazlası vardır. Onların yüzlerine ne bir toz (kara leke) bulaşır ne de bir horluk (gelir). İşte onlar cennet ehlidirler. Ve onlar orada ebedî kalacaklardır.', 11, 211, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1391, 27, 10, 'Yunus', 'وَالَّذ۪ينَ كَسَبُوا السَّيِّـَٔاتِ جَزَٓاءُ سَيِّئَةٍ بِمِثْلِهَاۙ وَتَرْهَقُهُمْ ذِلَّةٌۜ مَا لَهُمْ مِنَ اللّٰهِ مِنْ عَاصِمٍۚ كَاَنَّـمَٓا اُغْشِيَتْ وُجُوهُهُمْ قِطَعاً مِنَ الَّيْلِ مُظْلِماًۜ اُو۬لٰٓئِكَ اَصْحَابُ النَّارِۚ هُمْ ف۪يهَا خَالِدُونَ', 'Kötülük yapanlara gelince, kötülüğün cezası misli iledir. Onları zillet kaplayacaktır. Onları Allah’a karşı koruyacak hiç kimse yoktur. Onların yüzleri sanki karanlık geceden bir parçaya bürünmüştür. İşte onlar da cehennem ehlidir. Onlar orada ebedî kalacaklardır.', 11, 211, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1392, 28, 10, 'Yunus', 'وَيَوْمَ نَحْشُرُهُمْ جَم۪يعاً ثُمَّ نَقُولُ لِلَّذ۪ينَ اَشْرَكُوا مَكَانَكُمْ اَنْتُمْ وَشُرَكَٓاؤُ۬كُمْۚ فَزَيَّلْنَا بَيْنَهُمْ وَقَالَ شُرَكَٓاؤُ۬هُمْ مَا كُنْتُمْ اِيَّانَا تَعْبُدُونَ', 'Onların hepsini biraraya toplayacağımız, sonra da Allah’a ortak koşanlara: «Siz ve koştuğunuz ortaklar yerinizde bekleyin» diyeceğimiz gün artık onların (putlarıyla) aralarını tamamen ayırmışızdır. Ve onların ortakları, (putları) derler ki: «Siz, bize ibadet etmiyordunuz.', 11, 211, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1393, 29, 10, 'Yunus', 'فَكَفٰى بِاللّٰهِ شَه۪يداً بَيْنَنَا وَبَيْنَكُمْ اِنْ كُنَّا عَنْ عِبَادَتِكُمْ لَغَافِل۪ينَ', 'Bu yüzden bizimle sizin aranızda şahit olarak Allah yeter. Şüphesiz ki biz sizin (bize) tapmanızdan tamamen habersizdik.»', 11, 211, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1394, 30, 10, 'Yunus', 'هُنَالِكَ تَبْلُوا كُلُّ نَفْسٍ مَٓا اَسْلَفَتْ وَرُدُّٓوا اِلَى اللّٰهِ مَوْلٰيهُمُ الْحَقِّ وَضَلَّ عَنْهُمْ مَا كَانُوا يَفْتَرُونَ۟', 'Orada herkes geçmişte yaptıklarının ne olduğunu anlar. Artık onlar gerçek sahipleri olan Allah’a döndürülmüşlerdir. Uydurmakta oldukları şeyler (bâtıl tanrıları) da onları terkedip kaybolmuştur.', 11, 211, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1395, 31, 10, 'Yunus', 'قُلْ مَنْ يَرْزُقُكُمْ مِنَ السَّمَٓاءِ وَالْاَرْضِ اَمَّنْ يَمْلِكُ السَّمْعَ وَالْاَبْصَارَ وَمَنْ يُخْرِجُ الْحَيَّ مِنَ الْمَيِّتِ وَيُخْرِجُ الْمَيِّتَ مِنَ الْحَيِّ وَمَنْ يُدَبِّرُ الْاَمْرَۜ فَسَيَقُولُونَ اللّٰهُۚ فَقُلْ اَفَلَا تَتَّقُونَ', '(Resûlüm!) De ki: Size gökten ve yerden kim rızık veriyor? Ya da kulaklara ve gözlere kim mâlik (ve hakim) bulunuyor? Ölüden diriyi kim çıkarıyor, diriden ölüyü kim çıkarıyor? (Her türlü) işi kim idare ediyor? «Allah» diyecekler. De ki: Öyle ise (Ona âsi olmaktan) sakınmıyor musunuz?', 11, 211, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1396, 32, 10, 'Yunus', 'فَذٰلِكُمُ اللّٰهُ رَبُّكُمُ الْحَقُّۚ فَمَاذَا بَعْدَ الْحَقِّ اِلَّا الضَّلَالُۚ فَاَنّٰى تُصْرَفُونَ', 'İşte O, sizin gerçek Rabbiniz olan Allah’tır. Artık haktan (ayrıldıktan) sonra sapıklıktan başka ne kalır? O halde nasıl (sapıklığa) döndürülüyorsunuz?', 11, 211, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1397, 33, 10, 'Yunus', 'كَذٰلِكَ حَقَّتْ كَلِمَتُ رَبِّكَ عَلَى الَّذ۪ينَ فَسَقُٓوا اَنَّهُمْ لَا يُؤْمِنُونَ', 'İşte böylece Rabbinin yoldan çıkanlar hakkındaki «Onlar inanmazlar» sözü gerçekleşmiş oldu.', 11, 211, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1398, 34, 10, 'Yunus', 'قُلْ هَلْ مِنْ شُرَكَٓائِكُمْ مَنْ يَبْدَؤُا الْخَلْقَ ثُمَّ يُع۪يدُهُۜ قُلِ اللّٰهُ يَبْدَؤُا الْخَلْقَ ثُمَّ يُع۪يدُهُ فَاَنّٰى تُؤْفَكُونَ', '(Resûlüm!) De ki: (Allah’a) ortak koştuklarınız arasında, (birini yokken) ilk defa yaratacak, arkasından onu (ölümünden sonra hayata) yeniden döndürecek biri var mı? De ki: Allah ilk defa yaratıp (ölümden sonra) onu yeniden (hayata) döndürür. O halde nasıl saptırılırsınız!', 11, 212, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1399, 35, 10, 'Yunus', 'قُلْ هَلْ مِنْ شُرَكَٓائِكُمْ مَنْ يَهْد۪ٓي اِلَى الْحَقِّۜ قُلِ اللّٰهُ يَهْد۪ي لِلْحَقِّۜ اَفَمَنْ يَهْد۪ٓي اِلَى الْحَقِّ اَحَقُّ اَنْ يُتَّبَعَ اَمَّنْ لَا يَهِدّ۪ٓي اِلَّٓا اَنْ يُهْدٰىۚ فَمَا لَكُمْ۠ كَيْفَ تَحْكُمُونَ', 'De ki: Ortak koştuklarınızdan hakka iletecek olan var mı? De ki: «Hakka Allah iletir.» Öyle ise hakka ileten mi uyulmaya daha lâyıktır; yoksa hidayet verilmedikçe kendi kendine doğru yolu bulamayan mı? Size ne oluyor? Nasıl (böyle yanlış) hükmediyorsunuz?', 11, 212, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1400, 36, 10, 'Yunus', 'وَمَا يَتَّبِعُ اَكْثَرُهُمْ اِلَّا ظَناًّۜ اِنَّ الظَّنَّ لَا يُغْن۪ي مِنَ الْحَقِّ شَيْـٔاًۜ اِنَّ اللّٰهَ عَل۪يمٌ بِمَا يَفْعَلُونَ', 'Onların çoğu zandan başka bir şeye uymaz. Şüphesiz zan, haktan (ilimden) hiçbir şeyin yerini tutmaz. Allah onların yapmakta olduklarını pek iyi bilendir.', 11, 212, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1401, 37, 10, 'Yunus', 'وَمَا كَانَ هٰذَا الْقُرْاٰنُ اَنْ يُفْتَرٰى مِنْ دُونِ اللّٰهِ وَلٰكِنْ تَصْد۪يقَ الَّذ۪ي بَيْنَ يَدَيْهِ وَتَفْص۪يلَ الْكِتَابِ لَا رَيْبَ ف۪يهِ مِنْ رَبِّ الْعَالَم۪ينَ۠', 'Bu Kur’an Allah’tan başkası tarafından uydurulmuş bir şey değildir. Ancak kendinden öncekini doğrulayan ve o Kitab’ı açıklayandır. Onda şüphe yoktur, o âlemlerin Rabbindendir.', 11, 212, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1402, 38, 10, 'Yunus', 'اَمْ يَقُولُونَ افْتَرٰيهُۜ قُلْ فَأْتُوا بِسُورَةٍ مِثْلِه۪ وَادْعُوا مَنِ اسْتَطَعْتُمْ مِنْ دُونِ اللّٰهِ اِنْ كُنْتُمْ صَادِق۪ينَ', 'Yoksa, Onu (Muhammed) uydurdu mu diyorlar? De ki: Eğer sizler doğru iseniz Allah’tan başka, gücünüzün yettiklerini çağırın da (hep beraber) onun benzeri bir sûre getirin.', 11, 212, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1403, 39, 10, 'Yunus', 'بَلْ كَذَّبُوا بِمَا لَمْ يُح۪يطُوا بِعِلْمِه۪ وَلَمَّا يَأْتِهِمْ تَأْو۪يلُهُۜ كَذٰلِكَ كَذَّبَ الَّذ۪ينَ مِنْ قَبْلِهِمْ فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الظَّالِم۪ينَ', 'Bilakis, onlar hakkıyla bilmedikleri ve bildirdikleri kendilerine (vakıa olarak) gelmemiş Kur’an’ı yalanladılar. Onlardan öncekiler de böyle yalanlamışlardı. Şimdi bak, zalimlerin sonu nasıl oldu!', 11, 212, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1404, 40, 10, 'Yunus', 'وَمِنْهُمْ مَنْ يُؤْمِنُ بِه۪ وَمِنْهُمْ مَنْ لَا يُؤْمِنُ بِه۪ۜ وَرَبُّكَ اَعْلَمُ بِالْمُفْسِد۪ينَ۟', 'İçlerinden öylesi var ki ona (Kur’an’a) inanır, yine onlardan öylesi de var ki ona inanmaz. Rabbin bozguncuları en iyi bilendir.', 11, 212, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1405, 41, 10, 'Yunus', 'وَاِنْ كَذَّبُوكَ فَقُلْ ل۪ي عَمَل۪ي وَلَكُمْ عَمَلُكُمْۚ اَنْتُمْ بَر۪ٓيؤُ۫نَ مِمَّٓا اَعْمَلُ وَاَنَا۬ بَر۪ٓيءٌ مِمَّا تَعْمَلُونَ', '(Resûlüm!) onlar seni yalanlarlarsa de ki: Benim işim bana, sizin işiniz de size aittir. Siz benim yaptığımdan uzaksınız, ben de sizin yaptığınızdan uzağım.', 11, 212, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1406, 42, 10, 'Yunus', 'وَمِنْهُمْ مَنْ يَسْتَمِعُونَ اِلَيْكَۜ اَفَاَنْتَ تُسْمِـعُ الصُّمَّ وَلَوْ كَانُوا لَا يَعْقِلُونَ', 'Onlardan seni dinleyenler vardır. Fakat sağırlara -üstelik akılları da ermiyorsa- sen mi duyuracaksın?', 11, 212, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1407, 43, 10, 'Yunus', 'وَمِنْهُمْ مَنْ يَنْظُرُ اِلَيْكَۜ اَفَاَنْتَ تَهْدِي الْعُمْيَ وَلَوْ كَانُوا لَا يُبْصِرُونَ', 'Onlardan sana bakan da vardır. Fakat -hele (gerçeği) göremiyorlarsa- körleri sen mi doğru yola ileteceksin?', 11, 213, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1408, 44, 10, 'Yunus', 'اِنَّ اللّٰهَ لَا يَظْلِمُ النَّاسَ شَيْـٔاً وَلٰكِنَّ النَّاسَ اَنْفُسَهُمْ يَظْلِمُونَ', 'Şüphesiz ki Allah insanlara hiçbir şekilde zulmetmez, fakat insanlar kendilerine zulmederler.', 11, 213, 109, 7425, 'Peygamber Yunus', 'Mekke', 51);
INSERT INTO `Ayetler` (`id`, `AyetNo`, `SureNo`, `SureAdi`, `Metin`, `Mana`, `Cuzu`, `Sayfasi`, `AyetSayisi`, `HarfSayisi`, `AdAnlami`, `IndigiYer`, `InisSirasi`) VALUES
(1409, 45, 10, 'Yunus', 'وَيَوْمَ يَحْشُرُهُمْ كَاَنْ لَمْ يَلْبَثُٓوا اِلَّا سَاعَةً مِنَ النَّهَارِ يَتَعَارَفُونَ بَيْنَهُمْۜ قَدْ خَسِرَ الَّذ۪ينَ كَذَّبُوا بِلِقَٓاءِ اللّٰهِ وَمَا كَانُوا مُهْتَد۪ينَ', 'Allah’ın onları, sanki günün ancak bir saati kadar kaldıklarını zanneder vaziyette yeniden diriltip toplayacağı gün aralarında birbirleriyle tanışırlar. Allah’ın huzuruna varmayı yalanlayanlar elbette zarara uğramışlardır. Zira onlar doğru yola gitmemişlerdi.', 11, 213, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1410, 46, 10, 'Yunus', 'وَاِمَّا نُرِيَنَّكَ بَعْضَ الَّذ۪ي نَعِدُهُمْ اَوْ نَتَوَفَّـيَنَّكَ فَاِلَيْنَا مَرْجِعُهُمْ ثُمَّ اللّٰهُ شَه۪يدٌ عَلٰى مَا يَفْعَلُونَ', 'Eğer onları tehdit ettiğimiz (azabın) bir kısmını sana (dünyada iken) gösterirsek (ne âlâ); yok eğer (göstermeden) seni vefat ettirirsek nihayet onların dönüşü de bizedir. (O zaman onlara ne olacağını göreceksin). Sonra, Allah onların yapmakta olduklarına da şahittir.', 11, 213, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1411, 47, 10, 'Yunus', 'وَلِكُلِّ اُمَّةٍ رَسُولٌۚ فَاِذَا جَٓاءَ رَسُولُهُمْ قُضِيَ بَيْنَهُمْ بِالْقِسْطِ وَهُمْ لَا يُظْلَمُونَ', 'Her ümmetin bir peygamberi vardır. Peygamberleri geldiği zaman, aralarında adaletle hükmedilir ve onlara asla zulmedilmez.', 11, 213, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1412, 48, 10, 'Yunus', 'وَيَقُولُونَ مَتٰى هٰذَا الْوَعْدُ اِنْ كُنْتُمْ صَادِق۪ينَ', 'Doğru iseniz bu vaad (azap) ne zamandır? diyorlar.', 11, 213, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1413, 49, 10, 'Yunus', 'قُلْ لَٓا اَمْلِكُ لِنَفْس۪ي ضَراًّ وَلَا نَفْعاً اِلَّا مَا شَٓاءَ اللّٰهُۜ لِكُلِّ اُمَّةٍ اَجَلٌۜ اِذَا جَٓاءَ اَجَلُهُمْ فَلَا يَسْتَأْخِرُونَ سَاعَةً وَلَا يَسْتَقْدِمُونَ', 'De ki: «Ben kendime bile Allah’ın dilediğinden başka ne bir zarar ne de bir menfaat verme gücüne sahibim.» Her ümmetin bir eceli vardır. Ecelleri geldiği zaman artık ne bir saat geri kalırlar ne de ileri giderler.', 11, 213, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1414, 50, 10, 'Yunus', 'قُلْ اَرَاَيْتُمْ اِنْ اَتٰيكُمْ عَذَابُهُ بَيَاتاً اَوْ نَهَاراً مَاذَا يَسْتَعْجِلُ مِنْهُ الْمُجْرِمُونَ', 'De ki: (Ey müşrikler!) Ne dersiniz? Allah’ın azabı size geceleyin veya gündüzün gelirse (ne yaparsınız?). Suçlular ondan hangisini istemekte acele ediyorlar!', 11, 213, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1415, 51, 10, 'Yunus', 'اَثُمَّ اِذَا مَا وَقَعَ اٰمَنْتُمْ بِه۪ۜ آٰلْـٰٔنَ وَقَدْ كُنْتُمْ بِه۪ تَسْتَعْجِلُونَ', 'Olacaklar olduktan sonra mı O’na iman edeceksiniz? Şimdi mi? Halbuki onu (azabın gelmesini) istemekte acele ediyordunuz?', 11, 213, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1416, 52, 10, 'Yunus', 'ثُمَّ ق۪يلَ لِلَّذ۪ينَ ظَلَمُوا ذُوقُوا عَذَابَ الْخُلْدِۚ هَلْ تُجْزَوْنَ اِلَّا بِمَا كُنْتُمْ تَكْسِبُونَ', 'Sonra o (kendilerine) zulmedenlere, «Ebedî azabı tadın!» denilecek. Kazanmakta olduğunuzdan başkasının karşılığını mı bulacaksınız?', 11, 213, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1417, 53, 10, 'Yunus', 'وَيَسْتَنْبِـؤُ۫نَكَ اَحَقٌّ هُوَۜ قُلْ ا۪ي وَرَبّ۪ٓي اِنَّهُ لَحَقٌّ وَمَٓا اَنْتُمْ بِمُعْجِز۪ينَ۟', '«O (azap) bir gerçek midir?» diye senden haber istiyorlar. De ki: Evet, Rabbime andolsun ki o şüphesiz gerçektir ve siz âciz bırakacak değilsiniz.', 11, 213, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1418, 54, 10, 'Yunus', 'وَلَوْ اَنَّ لِكُلِّ نَفْسٍ ظَلَمَتْ مَا فِي الْاَرْضِ لَافْتَدَتْ بِه۪ۜ وَاَسَرُّوا النَّدَامَةَ لَمَّا رَاَوُا الْعَذَابَۚ وَقُضِيَ بَيْنَهُمْ بِالْقِسْطِ وَهُمْ لَا يُظْلَمُونَ', '(O zaman) zulmeden herkes yeryüzündeki bütün servete sahip olsa (azaptan kurtulmak için) elbette onu feda eder. Ve azabı gördükleri zaman için için yanarlar. Aralarında adaletle hükmolunur ve onlara zulmedilmez.', 11, 214, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1419, 55, 10, 'Yunus', 'اَلَٓا اِنَّ لِلّٰهِ مَا فِي السَّمٰوَاتِ وَالْاَرْضِۜ اَلَٓا اِنَّ وَعْدَ اللّٰهِ حَقٌّ وَلٰكِنَّ اَكْثَرَهُمْ لَا يَعْلَمُونَ', 'Bilesiniz ki, göklerde ve yerde olan her şey Allah’ındır. Yine bilesiniz ki, Allah’ın vâdi haktır, fakat onların çoğu bilmez.', 11, 214, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1420, 56, 10, 'Yunus', 'هُوَ يُحْـي۪ وَيُم۪يتُ وَاِلَيْهِ تُرْجَعُونَ', 'O hem diriltir hem de öldürür ve yalnız O’na döndürüleceksiniz.', 11, 214, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1421, 57, 10, 'Yunus', 'يَٓا اَيُّهَا النَّاسُ قَدْ جَٓاءَتْكُمْ مَوْعِظَةٌ مِنْ رَبِّكُمْ وَشِفَٓاءٌ لِمَا فِي الصُّدُورِ وَهُدًى وَرَحْمَةٌ لِلْمُؤْمِن۪ينَ', 'Ey insanlar! Size Rabbinizden bir öğüt, gönüllerdekine bir şifa, müminler için bir hidayet ve rahmet gelmiştir.', 11, 214, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1422, 58, 10, 'Yunus', 'قُلْ بِفَضْلِ اللّٰهِ وَبِرَحْمَتِه۪ فَبِذٰلِكَ فَلْيَفْرَحُواۜ هُوَ خَيْرٌ مِمَّا يَجْمَعُونَ', 'De ki: Ancak Allah’ın lütfu ve rahmetiyle, işte bunlarla sevinsinler. Bu, onların (dünya malı olarak) topladıklarından daha hayırlıdır.', 11, 214, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1423, 59, 10, 'Yunus', 'قُلْ اَرَاَيْتُمْ مَٓا اَنْزَلَ اللّٰهُ لَكُمْ مِنْ رِزْقٍ فَجَعَلْتُمْ مِنْهُ حَرَاماً وَحَلَالاًۜ قُلْ آٰللّٰهُ اَذِنَ لَكُمْ اَمْ عَلَى اللّٰهِ تَفْتَرُونَ', 'De ki: Allah’ın size indirdiği rızıktan bir kısmını helâl, bir kısmını da haram bulmanıza ne dersiniz? De ki: Allah mı size izin verdi, yoksa Allah’a iftira mı ediyorsunuz?', 11, 214, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1424, 60, 10, 'Yunus', 'وَمَا ظَنُّ الَّذ۪ينَ يَفْتَرُونَ عَلَى اللّٰهِ الْكَذِبَ يَوْمَ الْقِيٰمَةِۜ اِنَّ اللّٰهَ لَذُو فَضْلٍ عَلَى النَّاسِ وَلٰكِنَّ اَكْثَرَهُمْ لَا يَشْكُرُونَ۟', 'Allah’a karşı yalan uyduranların kıyamet günü (âkıbetleri) hakkındaki kanaatleri nedir? Şüphesiz Allah insanlara karşı lütuf sahibidir. Fakat onların çoğu şükretmezler.', 11, 214, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1425, 61, 10, 'Yunus', 'وَمَا تَكُونُ ف۪ي شَأْنٍ وَمَا تَتْلُوا مِنْهُ مِنْ قُرْاٰنٍ وَلَا تَعْمَلُونَ مِنْ عَمَلٍ اِلَّا كُنَّا عَلَيْكُمْ شُهُوداً اِذْ تُف۪يضُونَ ف۪يهِۜ وَمَا يَعْزُبُ عَنْ رَبِّكَ مِنْ مِثْقَالِ ذَرَّةٍ فِي الْاَرْضِ وَلَا فِي السَّمَٓاءِ وَلَٓا اَصْغَرَ مِنْ ذٰلِكَ وَلَٓا اَكْبَرَ اِلَّا ف۪ي كِتَابٍ مُب۪ينٍ', 'Ne zaman sen bir işte bulunsan, ne zaman Kur’an’dan bir şey okusan ve siz ne zaman bir iş yaparsanız, o işe daldığınız zaman biz mutlaka üstünüzde şahidizdir. Ne yerde ne gökte zerre ağırlığınca bir şey Rabbinden uzak (ve gizli) kalmaz. Bundan daha küçüğü ve daha büyüğü yoktur ki apaçık kitapta (levh-i mahfuzda) bulunmasın.', 11, 214, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1426, 62, 10, 'Yunus', 'اَلَٓا اِنَّ اَوْلِيَٓاءَ اللّٰهِ لَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَۚ', 'Bilesiniz ki, Allah’ın dostlarına korku yoktur; onlar üzülmeyecekler de.', 11, 215, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1427, 63, 10, 'Yunus', 'اَلَّذ۪ينَ اٰمَنُوا وَكَانُوا يَتَّقُونَۜ', 'Onlar, iman edip de takvâya ermiş olanlardır.', 11, 215, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1428, 64, 10, 'Yunus', 'لَهُمُ الْبُشْرٰى فِي الْحَيٰوةِ الدُّنْيَا وَفِي الْاٰخِرَةِۜ لَا تَبْد۪يلَ لِكَلِمَاتِ اللّٰهِۜ ذٰلِكَ هُوَ الْفَوْزُ الْعَظ۪يمُۜ', 'Dünya hayatında da ahirette de onlara müjde vardır. Allah’ın sözlerinde asla değişme yoktur. İşte bu, büyük kurtuluşun kendisidir.', 11, 215, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1429, 65, 10, 'Yunus', 'وَلَا يَحْزُنْكَ قَوْلُهُمْۢ اِنَّ الْعِزَّةَ لِلّٰهِ جَم۪يعاًۜ هُوَ السَّم۪يعُ الْعَل۪يمُ', '(Resûlüm) Onların (inkârcıların) sözleri seni üzmesin. Çünkü bütün izzet (ve üstünlük) Allah’ındır. O, işitendir, bilendir.', 11, 215, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1430, 66, 10, 'Yunus', 'اَلَٓا اِنَّ لِلّٰهِ مَنْ فِي السَّمٰوَاتِ وَمَنْ فِي الْاَرْضِۜ وَمَا يَتَّبِعُ الَّذ۪ينَ يَدْعُونَ مِنْ دُونِ اللّٰهِ شُرَكَٓاءَۜ اِنْ يَتَّبِعُونَ اِلَّا الظَّنَّ وَاِنْ هُمْ اِلَّا يَخْرُصُونَ', 'İyi bilin ki, göklerde ve yerde ne varsa yalnız Allah’ındır. (O halde) Allah’tan başka ortaklara tapanlar neyin ardına düşüyorlar! Doğrusu onlar, zandan başka bir şeyin ardına düşmüyorlar ve onlar sadece yalan söylüyorlar.', 11, 215, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1431, 67, 10, 'Yunus', 'هُوَ الَّذ۪ي جَعَلَ لَـكُمُ الَّيْلَ لِتَسْكُنُوا ف۪يهِ وَالنَّهَارَ مُبْصِراًۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَاتٍ لِقَوْمٍ يَسْمَعُونَ', 'O (Allah), geceyi içinde dinlenesiniz diye sizin için yaratan, (çalışıp kazanmanız için de) gündüzü aydınlık kılandır. Şüphesiz bunda dinleyen bir toplum için ibretler vardır.', 11, 215, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1432, 68, 10, 'Yunus', 'قَالُوا اتَّخَذَ اللّٰهُ وَلَداً سُبْحَانَهُۜ هُوَ الْغَنِيُّۜ لَهُ مَا فِي السَّمٰوَاتِ وَمَا فِي الْاَرْضِۜ اِنْ عِنْدَ‌كُمْ مِنْ سُلْطَانٍ بِهٰذَاۜ اَتَقُولُونَ عَلَى اللّٰهِ مَا لَا تَعْلَمُونَ', '(Müşrikler:) «Allah çocuk edindi» dediler. Hâşâ! O bundan münezzehtir. O’nun (çocuğa) ihtiyacı yoktur. Göklerde ve yerde ne varsa O’nundur. Bu hususta yanınızda herhangi bir delil yoktur. Allah hakkında bilmediğiniz bir şeyi mi söylüyorsunuz?', 11, 215, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1433, 69, 10, 'Yunus', 'قُلْ اِنَّ الَّذ۪ينَ يَفْتَرُونَ عَلَى اللّٰهِ الْكَذِبَ لَا يُفْلِحُونَۜ', 'De ki: Allah hakkında yalan uyduranlar asla kurtuluşa eremezler.', 11, 215, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1434, 70, 10, 'Yunus', 'مَتَاعٌ فِي الدُّنْيَا ثُمَّ اِلَيْنَا مَرْجِعُهُمْ ثُمَّ نُذ۪يقُهُمُ الْعَذَابَ الشَّد۪يدَ بِمَا كَانُوا يَكْفُرُونَ۟', 'Dünyada bir miktar geçim (sağlarlar), sonra dönüşleri bizedir; sonra da inkâr etmekte oldukları şeylerden ötürü onlara şiddetli azabı tattırırız.', 11, 215, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1435, 71, 10, 'Yunus', 'وَاتْلُ عَلَيْهِمْ نَبَاَ نُوحٍۢ اِذْ قَالَ لِقَوْمِه۪ يَا قَوْمِ اِنْ كَانَ كَبُرَ عَلَيْكُمْ مَقَام۪ي وَتَذْك۪ير۪ي بِاٰيَاتِ اللّٰهِ فَعَلَى اللّٰهِ تَوَكَّلْتُ فَاَجْمِعُٓوا اَمْرَكُمْ وَشُرَكَٓاءَكُمْ ثُمَّ لَا يَكُنْ اَمْرُكُمْ عَلَيْكُمْ غُمَّةً ثُمَّ اقْضُٓوا اِلَيَّ وَلَا تُنْظِرُونِ', 'Onlara Nuh’un haberini oku: Hani o kavmine demişti ki: «Ey kavmim! Eğer benim (aranızda) durmam ve Allah’ın âyetlerini hatırlatmam size ağır geldi ise, ben yalnız Allah’a dayanıp güvenirim. Siz de ortaklarınızla beraber toplanıp yapacağınızı kararlaştırın. Sonra işiniz başınıza dert olmasın. Bundan sonra (vereceğiniz) hükmü, bana uygulayın ve bana mühlet de vermeyin.»', 11, 216, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1436, 72, 10, 'Yunus', 'فَاِنْ تَوَلَّيْتُمْ فَمَا سَاَلْتُكُمْ مِنْ اَجْرٍۜ اِنْ اَجْرِيَ اِلَّا عَلَى اللّٰهِۙ وَاُمِرْتُ اَنْ اَكُونَ مِنَ الْمُسْلِم۪ينَ', '«Eğer yüz çeviriyorsanız, zaten ben sizden bir ücret istemedim. Benim ecrim Allah’tan başkasına ait değildir ve bana müslümanlardan olmam emrolundu.»', 11, 216, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1437, 73, 10, 'Yunus', 'فَكَذَّبُوهُ فَنَجَّيْنَاهُ وَمَنْ مَعَهُ فِي الْفُلْكِ وَجَعَلْنَاهُمْ خَلَٓائِفَ وَاَغْرَقْنَا الَّذ۪ينَ كَذَّبُوا بِاٰيَاتِنَاۚ فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الْمُنْذَر۪ينَ', 'Yine de onu yalanladılar, biz de hem onu hem de onunla beraber gemide bulunanları kurtardık ve onları (yeryüzünde) halifeler kıldık; âyetlerimizi yalanlayanları da (denizde) boğduk. Bak ki uyarılanların (fakat inanmayanların) sonu nasıl oldu!', 11, 216, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1438, 74, 10, 'Yunus', 'ثُمَّ بَعَثْنَا مِنْ بَعْدِه۪ رُسُلاً اِلٰى قَوْمِهِمْ فَجَٓاؤُ۫هُمْ بِالْبَيِّنَاتِ فَمَا كَانُوا لِيُؤْمِنُوا بِمَا كَذَّبُوا بِه۪ مِنْ قَبْلُۜ كَذٰلِكَ نَطْبَعُ عَلٰى قُلُوبِ الْمُعْتَد۪ينَ', 'Sonra onun arkasından birçok peygamberi kendi toplumlarına gönderdik. Onlara mucizeler getirdiler. Fakat onlar daha önce yalanladıkları şeye inanacak değillerdi. İşte haddi aşanların kalplerini biz böyle mühürleriz.', 11, 216, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1439, 75, 10, 'Yunus', 'ثُمَّ بَعَثْنَا مِنْ بَعْدِهِمْ مُوسٰى وَهٰرُونَ اِلٰى فِرْعَوْنَ وَمَلَا۬ئِه۪ بِاٰيَاتِنَا فَاسْتَكْبَرُوا وَكَانُوا قَوْماً مُجْرِم۪ينَ', 'Sonra onların ardından da Firavun ve toplumuna Musa ile Harun’u mucizelerimizle gönderdik, fakat onlar kibirlendiler ve günahkâr bir toplum oldular.', 11, 216, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1440, 76, 10, 'Yunus', 'فَلَمَّا جَٓاءَهُمُ الْحَقُّ مِنْ عِنْدِنَا قَالُٓوا اِنَّ هٰذَا لَسِحْرٌ مُب۪ينٌ', 'Katımızdan onlara hak (mucize) gelince: «Bu elbette apaçık bir sihirdir» dediler.', 11, 216, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1441, 77, 10, 'Yunus', 'قَالَ مُوسٰٓى اَتَقُولُونَ لِلْحَقِّ لَمَّا جَٓاءَكُمْۜ اَسِحْرٌ هٰذَاۜ وَلَا يُفْلِحُ السَّاحِرُونَ', 'Musa: «Size hak geldiğinde onun için (hep böyle) mi dersiniz? Bu bir sihir midir? Halbuki sihirbazlar iflâh olmazlar» dedi.', 11, 216, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1442, 78, 10, 'Yunus', 'قَالُٓوا اَجِئْتَنَا لِتَلْفِتَنَا عَمَّا وَجَدْنَا عَلَيْهِ اٰبَٓاءَنَا وَتَكُونَ لَكُمَا الْكِبْرِيَٓاءُ فِي الْاَرْضِۜ وَمَا نَحْنُ لَكُمَا بِمُؤْمِن۪ينَ', 'Onlar dediler ki: Babalarımızı üzerinde bulduğumuz (dinden) bizi döndüresin ve yeryüzünde ululuk sizin ikinizin olsun diye mi bize geldin? Halbuki biz size inanacak değiliz.', 11, 216, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1443, 79, 10, 'Yunus', 'وَقَالَ فِرْعَوْنُ ائْتُون۪ي بِكُلِّ سَاحِرٍ عَل۪يمٍ', 'Firavun dedi ki: Bilgili bütün sihirbazları bana getirin!', 11, 217, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1444, 80, 10, 'Yunus', 'فَلَمَّا جَٓاءَ السَّحَرَةُ قَالَ لَهُمْ مُوسٰٓى اَلْقُوا مَٓا اَنْتُمْ مُلْقُونَ', 'Sihirbazlar gelince Musa onlara: Atacağınızı atın, dedi.', 11, 217, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1445, 81, 10, 'Yunus', 'فَلَمَّٓا اَلْقَوْا قَالَ مُوسٰى مَا جِئْتُمْ بِهِ السِّحْرُۜ اِنَّ اللّٰهَ سَيُبْطِلُهُۜ اِنَّ اللّٰهَ لَا يُصْلِحُ عَمَلَ الْمُفْسِد۪ينَ', 'Onlar (iplerini) atınca, Musa dedi ki: «Sizin getirdiğiniz sihirdir. Allah onu boşa çıkaracaktır. Çünkü Allah bozguncuların işini düzeltmez.»', 11, 217, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1446, 82, 10, 'Yunus', 'وَيُحِقُّ اللّٰهُ الْحَقَّ بِكَلِمَاتِه۪ وَلَوْ كَرِهَ الْمُجْرِمُونَ۟', '«Suçluların hoşuna gitmese de Allah, sözleriyle gerçeği açığa çıkaracaktır.»', 11, 217, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1447, 83, 10, 'Yunus', 'فَمَٓا اٰمَنَ لِمُوسٰٓى اِلَّا ذُرِّيَّةٌ مِنْ قَوْمِه۪ عَلٰى خَوْفٍ مِنْ فِرْعَوْنَ وَمَلَا۬ئِهِمْ اَنْ يَفْتِنَهُمْۜ وَاِنَّ فِرْعَوْنَ لَعَالٍ فِي الْاَرْضِۚ وَاِنَّهُ لَمِنَ الْمُسْرِف۪ينَ', 'Firavun ve kavminin kendilerine işkence etmesinden korkuya düştükleri için kavminden bir gurup gençten başka kimse Musa’ya iman etmedi. Çünkü Firavun yeryüzünde ululuk taslayan (bir diktatör) ve haddi aşanlardan idi.', 11, 217, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1448, 84, 10, 'Yunus', 'وَقَالَ مُوسٰى يَا قَوْمِ اِنْ كُنْتُمْ اٰمَنْتُمْ بِاللّٰهِ فَعَلَيْهِ تَوَكَّلُٓوا اِنْ كُنْتُمْ مُسْلِم۪ينَ', 'Musa dedi ki: Ey kavmim! Eğer Allah’a inandıysanız ve O’na teslim olduysanız sadece O’na güvenip dayanın.', 11, 217, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1449, 85, 10, 'Yunus', 'فَقَالُوا عَلَى اللّٰهِ تَوَكَّلْنَاۚ رَبَّـنَا لَا تَجْعَلْنَا فِتْنَةً لِلْقَوْمِ الظَّالِم۪ينَۙ', 'Onlar da dediler ki: «Allah’a dayandık. Ey Rabbimiz! Bizi o zalimler topluluğu için deneme konusu kılma!', 11, 217, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1450, 86, 10, 'Yunus', 'وَنَجِّنَا بِرَحْمَتِكَ مِنَ الْقَوْمِ الْكَافِر۪ينَ', 'Ve bizi rahmetinle o kâfirler topluluğundan kurtar!»', 11, 217, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1451, 87, 10, 'Yunus', 'وَاَوْحَيْنَٓا اِلٰى مُوسٰى وَاَخ۪يهِ اَنْ تَبَوَّاٰ لِقَوْمِكُمَا بِمِصْرَ بُيُوتاً وَاجْعَلُوا بُيُوتَكُمْ قِبْلَةً وَاَق۪يمُوا الصَّلٰوةَۜ وَبَشِّرِ الْمُؤْمِن۪ينَ', 'Biz de Musa ve kardeşine: Kavminiz için Mısır’da evler hazırlayın ve evlerinizi namaz kılınacak yerler yapın, namazlarınızı da dosdoğru kılın. (Ey Musa!) Müminleri müjdele! diye vahyettik.', 11, 217, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1452, 88, 10, 'Yunus', 'وَقَالَ مُوسٰى رَبَّـنَٓا اِنَّكَ اٰتَيْتَ فِرْعَوْنَ وَمَلَاَهُ ز۪ينَةً وَاَمْوَالاً فِي الْحَيٰوةِ الدُّنْيَاۙ رَبَّـنَا لِيُضِلُّوا عَنْ سَب۪يلِكَۚ رَبَّـنَا اطْمِسْ عَلٰٓى اَمْوَالِهِمْ وَاشْدُدْ عَلٰى قُلُوبِهِمْ فَلَا يُؤْمِنُوا حَتّٰى يَرَوُا الْعَذَابَ الْاَل۪يمَ', 'Musa dedi ki: Ey Rabbimiz! Gerçekten sen Firavun ve kavmine dünya hayatında zinet ve nice mallar verdin. Ey Rabbimiz! (Onlara bu nimetleri), insanları senin yolundan saptırsınlar ve elem verici cezayı görünceye kadar iman etmesinler, diye mi (verdin)? Ey Rabbimiz! Onların mallarını yok et, kalplerine sıkıntı ver (ki iman etsinler).', 11, 217, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1453, 89, 10, 'Yunus', 'قَالَ قَدْ اُج۪يبَتْ دَعْوَتُكُمَا فَاسْتَق۪يمَا وَلَا تَتَّبِعَٓانِّ سَب۪يلَ الَّذ۪ينَ لَا يَعْلَمُونَ', '(Allah): İkinizin de duası kabul olunmuştur. O halde siz doğruluğa devam edin ve sakın o bilmezlerin yoluna gitmeyin! dedi.', 11, 218, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1454, 90, 10, 'Yunus', 'وَجَاوَزْنَا بِبَن۪ٓي اِسْرَٓائ۪لَ الْبَحْرَ فَاَتْبَعَهُمْ فِرْعَوْنُ وَجُنُودُهُ بَغْياً وَعَدْواًۜ حَتّٰٓى اِذَٓا اَدْرَكَهُ الْغَرَقُۙ قَالَ اٰمَنْتُ اَنَّهُ لَٓا اِلٰهَ اِلَّا الَّـذ۪ٓي اٰمَنَتْ بِه۪ بَنُٓوا اِسْرَٓائ۪لَ وَاَنَا۬ مِنَ الْمُسْلِم۪ينَ', 'Biz, İsrailoğullarını denizden geçirdik. Ama Firavun ve askerleri zulmetmek ve saldırmak üzere onları takip etti. Nihayet (denizde) boğulma haline gelince, (Firavun:) «Gerçekten, İsrailoğullarının inandığı Tanrı’dan başka tanrı olmadığına ben de iman ettim. Ben de müslümanlardanım!» dedi.', 11, 218, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1455, 91, 10, 'Yunus', 'آٰلْـٰٔنَ وَقَدْ عَصَيْتَ قَبْلُ وَكُنْتَ مِنَ الْمُفْسِد۪ينَ', 'Şimdi mi (iman ettin)! Halbuki daha önce isyan etmiş ve bozgunculardan olmuştun.', 11, 218, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1456, 92, 10, 'Yunus', 'فَالْيَوْمَ نُنَجّ۪يكَ بِبَدَنِكَ لِتَكُونَ لِمَنْ خَلْفَكَ اٰيَةًۜ وَاِنَّ كَث۪يراً مِنَ النَّاسِ عَنْ اٰيَاتِنَا لَغَافِلُونَ۟', '(Ey Firavun!) Senden sonra geleceklere ibret olman için, bugün senin bedenini (cansız olarak) kurtaracağız. İşte insanlardan bir çoğu, hakikaten âyetlerimizden gafildirler.', 11, 218, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1457, 93, 10, 'Yunus', 'وَلَقَدْ بَوَّأْنَا بَن۪ٓي اِسْرَٓائ۪لَ مُبَوَّاَ صِدْقٍ وَرَزَقْنَاهُمْ مِنَ الطَّيِّبَاتِۚ فَمَا اخْتَلَفُوا حَتّٰى جَٓاءَهُمُ الْعِلْمُۜ اِنَّ رَبَّكَ يَقْض۪ي بَيْنَهُمْ يَوْمَ الْقِيٰمَةِ ف۪يمَا كَانُوا ف۪يهِ يَخْتَلِفُونَ', 'Andolsun biz İsrailoğullarını güzel bir yurda yerleştirdik ve onlara temiz nimetlerden rızık verdik. Kendilerine ilim gelinceye kadar ayrılığa düşmediler. Şüphesiz ki Rabbin, kıyamet günü onların, aralarında ihtilaf etmekte oldukları şeyler hakkında hükmedecektir.', 11, 218, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1458, 94, 10, 'Yunus', 'فَاِنْ كُنْتَ ف۪ي شَكٍّ مِمَّٓا اَنْزَلْـنَٓا اِلَيْكَ فَسْـَٔلِ الَّذ۪ينَ يَقْرَؤُ۫نَ الْكِتَابَ مِنْ قَبْلِكَۚ لَقَدْ جَٓاءَكَ الْحَقُّ مِنْ رَبِّكَ فَلَا تَكُونَنَّ مِنَ الْمُمْتَر۪ينَۙ', '(Resülüm!) Eğer sana indirdiğimizden (bu anlattığımız olaylardan) kuşkuda isen, senden önce Kitab’ı (Tevrat’ı) okuyanlara sor. Andolsun ki, Rabbinden sana hak gelmiştir. Sakın şüphecilerden olma!', 11, 218, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1459, 95, 10, 'Yunus', 'وَلَا تَكُونَنَّ مِنَ الَّذ۪ينَ كَذَّبُوا بِاٰيَاتِ اللّٰهِ فَتَكُونَ مِنَ الْخَاسِر۪ينَ', 'Allah’ın âyetlerini yalanlayanlardan da olma, sonra ziyana uğrayanlardan olursun.', 11, 218, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1460, 96, 10, 'Yunus', 'اِنَّ الَّذ۪ينَ حَقَّتْ عَلَيْهِمْ كَلِمَتُ رَبِّكَ لَا يُؤْمِنُونَۙ', '(96-97) Gerçekten haklarında Rabbinin sözü (hükmü) sabit olanlar, kendilerine (istedikleri) bütün mucizeler gelmiş olsa bile, elem verici azabı görünceye kadar inanmayacaklardır.', 11, 218, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1461, 97, 10, 'Yunus', 'وَلَوْ جَٓاءَتْهُمْ كُلُّ اٰيَةٍ حَتّٰى يَرَوُا الْعَذَابَ الْاَل۪يمَ', '(96-97) Gerçekten haklarında Rabbinin sözü (hükmü) sabit olanlar, kendilerine (istedikleri) bütün mucizeler gelmiş olsa bile, elem verici azabı görünceye kadar inanmayacaklardır.', 11, 218, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1462, 98, 10, 'Yunus', 'فَلَوْلَا كَانَتْ قَرْيَةٌ اٰمَنَتْ فَنَفَعَهَٓا ا۪يمَانُهَٓا اِلَّا قَوْمَ يُونُسَۜ لَمَّٓا اٰمَنُوا كَشَفْنَا عَنْهُمْ عَذَابَ الْخِزْيِ فِي الْحَيٰوةِ الدُّنْيَا وَمَتَّعْنَاهُمْ اِلٰى ح۪ينٍ', 'Yunus’un kavmi müstesna, (halkını yok ettiğimiz ülkelerden) herhangi bir ülke halkı, keşke (kendilerine azap gelmeden) iman etse de bu imanları kendilerine fayda verseydi! Yunus’un kavmi iman edince, kendilerinden dünya hayatındaki rüsvaylık azabını kaldırdık ve onları bir süre (dünya nimetlerinden) faydalandırdık.', 11, 219, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1463, 99, 10, 'Yunus', 'وَلَوْ شَٓاءَ رَبُّكَ لَاٰمَنَ مَنْ فِي الْاَرْضِ كُلُّهُمْ جَم۪يعاًۜ اَفَاَنْتَ تُكْرِهُ النَّاسَ حَتّٰى يَكُونُوا مُؤْمِن۪ينَ', '(Resûlüm!) Eğer Rabbin dileseydi, yeryüzündekilerin hepsi elbette iman ederlerdi. O halde sen, inanmaları için insanları zorlayacak mısın?', 11, 219, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1464, 100, 10, 'Yunus', 'وَمَا كَانَ لِنَفْسٍ اَنْ تُؤْمِنَ اِلَّا بِاِذْنِ اللّٰهِۜ وَيَجْعَلُ الرِّجْسَ عَلَى الَّذ۪ينَ لَا يَعْقِلُونَ', 'Allah’ın izni olmadan hiç kimse inanamaz. O, akıllarını kullanmayanları murdar (inkârcı) kılar.', 11, 219, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1465, 101, 10, 'Yunus', 'قُلِ انْظُرُوا مَاذَا فِي السَّمٰوَاتِ وَالْاَرْضِۜ وَمَا تُغْنِي الْاٰيَاتُ وَالنُّذُرُ عَنْ قَوْمٍ لَا يُؤْمِنُونَ', 'De ki: «Göklerde ve yerde neler var, bakın (da ibret alın!)» Fakat inanmayan bir topluma deliller ve uyarılar fayda sağlamaz.', 11, 219, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1466, 102, 10, 'Yunus', 'فَهَلْ يَنْتَظِرُونَ اِلَّا مِثْلَ اَيَّامِ الَّذ۪ينَ خَلَوْا مِنْ قَبْلِهِمْۜ قُلْ فَانْتَظِرُٓوا اِنّ۪ي مَعَكُمْ مِنَ الْمُنْتَظِر۪ينَ', 'Onlar, kendilerinden önce gelip geçmiş toplumların (acıklı) günlerinin benzerlerinden başkasını mı bekliyorlar? De ki: Haydi bekleyin! Şüphesiz ben de sizinle beraber bekleyenlerdenim.', 11, 219, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1467, 103, 10, 'Yunus', 'ثُمَّ نُنَجّ۪ي رُسُلَنَا وَالَّذ۪ينَ اٰمَنُوا كَذٰلِكَۚ حَقاًّ عَلَيْنَا نُنْجِ الْمُؤْمِن۪ينَ۟', 'Biz, sonra peygamberlerimizi ve aynı şekilde iman edenleri kurtarırız. İnananları üzerimize bir borç olarak kurtaracağız.', 11, 219, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1468, 104, 10, 'Yunus', 'قُلْ يَٓا اَيُّهَا النَّاسُ اِنْ كُنْتُمْ ف۪ي شَكٍّ مِنْ د۪ين۪ي فَلَٓا اَعْبُدُ الَّذ۪ينَ تَعْبُدُونَ مِنْ دُونِ اللّٰهِ وَلٰكِنْ اَعْبُدُ اللّٰهَ الَّذ۪ي يَتَوَفّٰيكُمْۚ وَاُمِرْتُ اَنْ اَكُونَ مِنَ الْمُؤْمِن۪ينَۙ', 'De ki: «Ey insanlar! Benim dinimden şüphede iseniz, (bilin ki) ben Allah’ı bırakıp da sizin taptıklarınıza tapmam, fakat ancak sizi öldürecek olan Allah’a kulluk ederim. Bana müminlerden olmam emrolundu.»', 11, 219, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1469, 105, 10, 'Yunus', 'وَاَنْ اَقِمْ وَجْهَكَ لِلدّ۪ينِ حَن۪يفاًۚ وَلَا تَكُونَنَّ مِنَ الْمُشْرِك۪ينَ', '«Ve (bana) hanîf (Allah’ın birliğini tanıyıcı) olarak yüzünü dine çevir; sakın müşriklerden olma, diye (emredildi).»', 11, 219, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1470, 106, 10, 'Yunus', 'وَلَا تَدْعُ مِنْ دُونِ اللّٰهِ مَا لَا يَنْفَعُكَ وَلَا يَضُرُّكَۚ فَاِنْ فَعَلْتَ فَاِنَّكَ اِذاً مِنَ الظَّالِم۪ينَ', 'Allah’ı bırakıp da sana fayda veya zarar vermeyecek şeylere tapma. Eğer bunu yaparsan, o takdirde sen mutlaka zalimlerden olursun.', 11, 219, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1471, 107, 10, 'Yunus', 'وَاِنْ يَمْسَسْكَ اللّٰهُ بِضُرٍّ فَلَا كَاشِفَ لَهُٓ اِلَّا هُوَۚ وَاِنْ يُرِدْكَ بِخَيْرٍ فَلَا رَٓادَّ لِفَضْلِه۪ۜ يُص۪يبُ بِه۪ مَنْ يَشَٓاءُ مِنْ عِبَادِه۪ۜ وَهُوَ الْغَفُورُ الرَّح۪يمُ', 'Eğer Allah sana bir zarar dokundurursa, onu yine O’ndan başka giderecek yoktur. Eğer sana bir hayır dilerse, O’nun keremini geri çevirecek de yoktur. O, hayrını kullarından dilediğine eriştirir. Ve O bağışlayandır, esirgeyendir.', 11, 220, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1472, 108, 10, 'Yunus', 'قُلْ يَٓا اَيُّهَا النَّاسُ قَدْ جَٓاءَكُمُ الْحَقُّ مِنْ رَبِّكُمْۚ فَمَنِ اهْتَدٰى فَاِنَّمَا يَهْتَد۪ي لِنَفْسِه۪ۚ وَمَنْ ضَلَّ فَاِنَّمَا يَضِلُّ عَلَيْهَاۚ وَمَٓا اَنَا۬ عَلَيْكُمْ بِوَك۪يلٍۜ', 'De ki: Ey insanlar! Size Rabbinizden Hak (Kur’an) gelmiştir. Artık kim doğru yola gelirse, ancak kendisi için gelecektir. Kim de saparsa, o da ancak kendi aleyhine sapacaktır. Ben sizin üzerinize vekil değilim. (Sadece tebliğ etmekle memurum).', 11, 220, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1473, 109, 10, 'Yunus', 'وَاتَّبِعْ مَا يُوحٰٓى اِلَيْكَ وَاصْبِرْ حَتّٰى يَحْكُمَ اللّٰهُۚ وَهُوَ خَيْرُ الْحَاكِم۪ينَ', '(Resûlüm!) Sen, sana vahyolunana uy ve Allah hükmedinceye kadar sabret. O hakimlerin en hayırlısıdır.', 11, 220, 109, 7425, 'Peygamber Yunus', 'Mekke', 51),
(1474, 1, 11, 'Hud', 'الٓـرٰ۠ كِتَابٌ اُحْكِمَتْ اٰيَاتُهُ ثُمَّ فُصِّلَتْ مِنْ لَدُنْ حَك۪يمٍ خَب۪يرٍۙ', 'Elif. Lâm. Râ. (Bu sana indirilen), hikmet sahibi (ve) her şeyden haberdar olan (Allah) tarafından âyetleri sağlamlaştırılmış, sonra da açıklanmış bir kitaptır.', 11, 220, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1475, 2, 11, 'Hud', 'اَلَّا تَعْبُدُٓوا اِلَّا اللّٰهَۜ اِنَّن۪ي لَكُمْ مِنْهُ نَذ۪يرٌ وَبَش۪يرٌۙ', '(De ki: Bu Kitap) «Allah´tan başkasına ibadet etmemeniz için (indirildi). Şüphesiz ki ben, onun tarafından size (gönderilmiş) bir uyarıcı ve müjdeleyiciyim.', 11, 220, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1476, 3, 11, 'Hud', 'وَاَنِ اسْتَغْفِرُوا رَبَّكُمْ ثُمَّ تُوبُٓوا اِلَيْهِ يُمَتِّعْكُمْ مَتَاعاً حَسَناً اِلٰٓى اَجَلٍ مُسَمًّى وَيُؤْتِ كُلَّ ذ۪ي فَضْلٍ فَضْلَهُۜ وَاِنْ تَوَلَّوْا فَاِنّ۪ٓي اَخَافُ عَلَيْكُمْ عَذَابَ يَوْمٍ كَب۪يرٍ', 'Ve Rabbinizden mağfiret dilemeniz, sonra da ona tevbe etmeniz için (indirildi. Eğer bu emrolunanları yaparsanız), Allah sizi, tayin edilmiş bir süreye kadar güzel bir şekilde yaşatır, fazlasını yapan herkese de iyiliğinin karşılığını verir. Eğer yüz çevirirseniz, ben sizin başınıza gelecek büyük bir günün azabından korkarım.»', 11, 220, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1477, 4, 11, 'Hud', 'اِلَى اللّٰهِ مَرْجِعُكُمْۚ وَهُوَ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌ', 'Dönüşünüz yalnız Allah´adır. O, her şeye kadirdir.', 11, 220, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1478, 5, 11, 'Hud', 'اَلَٓا اِنَّهُمْ يَثْنُونَ صُدُورَهُمْ لِيَسْتَخْفُوا مِنْهُۜ اَلَا ح۪ينَ يَسْتَغْشُونَ ثِيَابَهُمْۙ يَعْلَمُ مَا يُسِرُّونَ وَمَا يُعْلِنُونَۚ اِنَّهُ عَل۪يمٌ بِذَاتِ الصُّدُورِ', 'Bilesiniz ki, onlar Peygamber´den, (düşmanlıklarını) gizlemeleri için göğüslerini çevirirler (gönüllerinden geçeni gizlerler). İyi bilin ki, onlar elbiselerine büründükleri zaman dahi, Allah onların gizlediklerini de, açığa çıkardıklarını da bilir. Çünkü O, kalplerin özünü bilendir.', 11, 220, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1479, 6, 11, 'Hud', 'وَمَا مِنْ دَٓابَّةٍ فِي الْاَرْضِ اِلَّا عَلَى اللّٰهِ رِزْقُهَا وَيَعْلَمُ مُسْتَقَرَّهَا وَمُسْتَوْدَعَهَاۜ كُلٌّ ف۪ي كِتَابٍ مُب۪ينٍ', 'Yeryüzünde yürüyen her canlının rızkı, yalnızca Allah´ın üzerinedir. Allah o canlının durduğu yeri ve sonunda bırakılacağı mekanı bilir. (Bunların) hepsi açık bir kitapta (levh-i mahfuz´da) dır.', 12, 221, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1480, 7, 11, 'Hud', 'وَهُوَ الَّذ۪ي خَلَقَ السَّمٰوَاتِ وَالْاَرْضَ ف۪ي سِتَّةِ اَيَّامٍ وَكَانَ عَرْشُهُ عَلَى الْمَٓاءِ لِيَبْلُوَكُمْ اَيُّكُمْ اَحْسَنُ عَمَلاًۜ وَلَئِنْ قُلْتَ اِنَّكُمْ مَبْعُوثُونَ مِنْ بَعْدِ الْمَوْتِ لَيَقُولَنَّ الَّذ۪ينَ كَفَرُٓوا اِنْ هٰذَٓا اِلَّا سِحْرٌ مُب۪ينٌ', 'O, hanginizin amelinin daha güzel olacağı hususunda sizi imtihan etmek için, Arş´ı su üzerinde iken, gökleri ve yeri altı günde yaratandır. Yemin ederim ki, (Resûlüm!): «Ölümden sonra muhakkak diriltileceksiniz» desen, kâfir olanlar derhal «Bu, açık bir büyüden başka bir şey değildir» derler.', 12, 221, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1481, 8, 11, 'Hud', 'وَلَئِنْ اَخَّرْنَا عَنْهُمُ الْعَذَابَ اِلٰٓى اُمَّةٍ مَعْدُودَةٍ لَيَقُولُنَّ مَا يَحْبِسُهُۜ اَلَا يَوْمَ يَأْت۪يهِمْ لَيْسَ مَصْرُوفاً عَنْهُمْ وَحَاقَ بِهِمْ مَا كَانُوا بِه۪ يَسْتَهْزِؤُ۫نَ۟', 'Andolsun, eğer biz onlardan azabı sayılı bir süreye kadar ertelesek, mutlaka «Onun gelmesini engelleyen nedir?» derler. Bilesiniz ki, kendilerine azap geldiği gün, bir daha onlardan uzaklaştırılacak değildir. Ve alay etmekte oldukları şey, onları çepeçevre kuşatacaktır.', 12, 221, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1482, 9, 11, 'Hud', 'وَلَئِنْ اَذَقْنَا الْاِنْسَانَ مِنَّا رَحْمَةً ثُمَّ نَزَعْنَاهَا مِنْهُۚ اِنَّهُ لَيَؤُ۫سٌ كَفُورٌ', 'Eğer insana tarafımızdan bir rahmet (nimet) tattırır da sonra bunu ondan çekip alırsak, tamamen ümitsiz ve nankör olur.', 12, 221, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1483, 10, 11, 'Hud', 'وَلَئِنْ اَذَقْنَاهُ نَعْمَٓاءَ بَعْدَ ضَرَّٓاءَ مَسَّتْهُ لَيَقُولَنَّ ذَهَبَ السَّيِّـَٔاتُ عَنّ۪يۜ اِنَّهُ لَفَرِحٌ فَخُورٌۙ', 'Eğer kendisine dokunan bir zarardan sonra ona bir nimet tattırırsak, elbette «Kötülükler benden gitti» der. Çünkü o (bunu derken) şımarıktır, kibirlidir.', 12, 221, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1484, 11, 11, 'Hud', 'اِلَّا الَّذ۪ينَ صَبَرُوا وَعَمِلُوا الصَّالِحَاتِۜ اُو۬لٰٓئِكَ لَهُمْ مَغْفِرَةٌ وَاَجْرٌ كَب۪يرٌ', 'Ancak (musibetlere) sabredip güzel iş yapanlar böyle değildir. İşte onlar için bir bağış ve bir büyük mükâfat vardır.', 12, 221, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1485, 12, 11, 'Hud', 'فَلَعَلَّكَ تَارِكٌ بَعْضَ مَا يُوحٰٓى اِلَيْكَ وَضَٓائِقٌ بِه۪ صَدْرُكَ اَنْ يَقُولُوا لَوْلَٓا اُنْزِلَ عَلَيْهِ كَنْزٌ اَوْ جَٓاءَ مَعَهُ مَلَكٌۜ اِنَّـمَٓا اَنْتَ نَذ۪يرٌۜ وَاللّٰهُ عَلٰى كُلِّ شَيْءٍ وَك۪يلٌۜ', 'Belki de sen (müşriklerin:) «Ona (gökten) bir hazine indirilseydi veya onunla beraber bir melek gelseydi!» demelerinden ötürü sana vahyolunan âyetlerin bir kısmını (duyurmayı) terk edeceksin ve bu yüzden ruhun daralacaktır. (İyi bil ki) sen ancak bir uyarıcısın. Allah ise her şeye vekîldir.', 12, 221, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1486, 13, 11, 'Hud', 'اَمْ يَقُولُونَ افْتَرٰيهُۜ قُلْ فَأْتُوا بِعَشْرِ سُوَرٍ مِثْلِه۪ مُفْتَرَيَاتٍ وَادْعُوا مَنِ اسْتَطَعْتُمْ مِنْ دُونِ اللّٰهِ اِنْ كُنْتُمْ صَادِق۪ينَ', 'Yoksa, «Onu (Kur´an´ı) kendisi uydurdu» mu diyorlar? De ki: Eğer doğru iseniz Allah´tan başka çağırabildiklerinizi (yardıma) çağırın da siz de onun gibi uydurulmuş on sûre getirin.', 12, 222, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1487, 14, 11, 'Hud', 'فَاِلَّمْ يَسْتَج۪يبُوا لَكُمْ فَاعْلَمُٓوا اَنَّـمَٓا اُنْزِلَ بِعِلْمِ اللّٰهِ وَاَنْ لَٓا اِلٰهَ اِلَّا هُوَۚ فَهَلْ اَنْتُمْ مُسْلِمُونَ', 'Eğer (onlar) size cevap veremiyorlarsa, bilin ki, o ancak Allah´ın ilmiyle indirilmiştir ve O´ndan başka tanrı yoktur. Artık siz müslüman oluyor musunuz?', 12, 222, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1488, 15, 11, 'Hud', 'مَنْ كَانَ يُر۪يدُ الْحَيٰوةَ الدُّنْيَا وَز۪ينَتَهَا نُوَفِّ اِلَيْهِمْ اَعْمَالَهُمْ ف۪يهَا وَهُمْ ف۪يهَا لَا يُبْخَسُونَ', 'Kim, (yalnız) dünya hayatını ve zinetini istemekte ise, işlerinin karşılığını orada onlara tam olarak veririz ve orada onlar hiçbir zarara uğratılmazlar.', 12, 222, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1489, 16, 11, 'Hud', 'اُو۬لٰٓئِكَ الَّذ۪ينَ لَيْسَ لَهُمْ فِي الْاٰخِرَةِ اِلَّا النَّارُۘ وَحَبِطَ مَا صَنَعُوا ف۪يهَا وَبَاطِلٌ مَا كَانُوا يَعْمَلُونَ', 'İşte onlar, ahirette kendileri için ateşten başka hiçbir şeyleri olmayan kimselerdir; (dünyada) yaptıkları da boşa gitmiştir; yapmakta oldukları şeyler (zaten) bâtıldır.', 12, 222, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1490, 17, 11, 'Hud', 'اَفَمَنْ كَانَ عَلٰى بَيِّنَةٍ مِنْ رَبِّه۪ وَيَتْلُوهُ شَاهِدٌ مِنْهُ وَمِنْ قَبْلِه۪ كِتَابُ مُوسٰٓى اِمَاماً وَرَحْمَةًۜ اُو۬لٰٓئِكَ يُؤْمِنُونَ بِه۪ۜ وَمَنْ يَكْفُرْ بِه۪ مِنَ الْاَحْزَابِ فَالنَّارُ مَوْعِدُهُۚ فَلَا تَكُ ف۪ي مِرْيَةٍ مِنْهُ اِنَّهُ الْحَقُّ مِنْ رَبِّكَ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يُؤْمِنُونَ', 'Rabbin tarafından (gelmiş) açık bir delile dayanan ve kendisini Rabbinden bir şahidin izlediği, ayrıca kendisinden önce, bir önder ve bir rahmet olarak Musa´nın Kitab´ı (elinde) bulunan kimse (inkârcılar gibi) midir? Çünkü bunlar ona (Kur´an´a) inanırlar. Zümrelerden hangisi onu inkâr ederse işte cehennem ateşi onun varacağı yerdir, bundan şüphen olmasın; zira bu, senin Rabbin tarafından bildirilmiş gerçektir; fakat insanların çoğu inanmazlar.', 12, 222, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1491, 18, 11, 'Hud', 'وَمَنْ اَظْلَمُ مِمَّنِ افْتَرٰى عَلَى اللّٰهِ كَذِباًۜ اُو۬لٰٓئِكَ يُعْرَضُونَ عَلٰى رَبِّهِمْ وَيَقُولُ الْاَشْهَادُ هٰٓؤُ۬لَٓاءِ الَّذ۪ينَ كَذَبُوا عَلٰى رَبِّهِمْۚ اَلَا لَعْنَةُ اللّٰهِ عَلَى الظَّالِم۪ينَۙ', 'Kim Allah´a karşı yalan uydurandan daha zalim olabilir? Onlar (kıyamet gününde) Rablerine arz edilecekler, şahitler de: İşte bunlar Rablerine karşı yalan söyleyenlerdir, diyecekler. Bilin ki, Allah´ın lâneti zalimlerin üzerinedir!', 12, 222, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1492, 19, 11, 'Hud', 'اَلَّذ۪ينَ يَصُدُّونَ عَنْ سَب۪يلِ اللّٰهِ وَيَبْغُونَهَا عِوَجاًۜ وَهُمْ بِالْاٰخِرَةِ هُمْ كَافِرُونَ', 'Onlar, (insanları) Allah´ın yolundan alıkoyan ve onu eğri göstermek isteyenlerdir. Ahireti inkâr edenler de onlardır.', 12, 222, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1493, 20, 11, 'Hud', 'اُو۬لٰٓئِكَ لَمْ يَكُونُوا مُعْجِز۪ينَ فِي الْاَرْضِ وَمَا كَانَ لَهُمْ مِنْ دُونِ اللّٰهِ مِنْ اَوْلِيَٓاءَۢ يُضَاعَفُ لَهُمُ الْعَذَابُۜ مَا كَانُوا يَسْتَط۪يعُونَ السَّمْعَ وَمَا كَانُوا يُبْصِرُونَ', 'Onlar yeryüzünde (Allah´ı) âciz bırakacak değillerdir; onların Allah´tan başka (yardım isteyecekleri) dostları da yoktur. Onların azabı kat kat olacaktır. Çünkü onlar (gerçekleri) ne görebiliyorlar ne de kulak veriyorlardı.', 12, 223, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1494, 21, 11, 'Hud', 'اُو۬لٰٓئِكَ الَّذ۪ينَ خَسِرُٓوا اَنْفُسَهُمْ وَضَلَّ عَنْهُمْ مَا كَانُوا يَفْتَرُونَ', 'İşte onlar kendilerini ziyana uğrattılar. Uydurmakta oldukları şeyler de kendilerinden kaybolup gitti.', 12, 223, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1495, 22, 11, 'Hud', 'لَا جَرَمَ اَنَّهُمْ فِي الْاٰخِرَةِ هُمُ الْاَخْسَرُونَ', 'Şüphesiz onlar, ahirette en çok ziyana uğrayanlardır.', 12, 223, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1496, 23, 11, 'Hud', 'اِنَّ الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ وَاَخْبَتُٓوا اِلٰى رَبِّهِمْۙ اُو۬لٰٓئِكَ اَصْحَابُ الْجَنَّةِۚ هُمْ ف۪يهَا خَالِدُونَ', 'İnanıp da güzel işler yapan ve Rablerine gönülden boyun eğenlere gelince, işte onlar cennet ehlidir. Onlar orada ebedî kalırlar.', 12, 223, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1497, 24, 11, 'Hud', 'مَثَلُ الْفَر۪يقَيْنِ كَالْاَعْمٰى وَالْاَصَمِّ وَالْبَص۪يرِ وَالسَّم۪يعِۜ هَلْ يَسْتَوِيَانِ مَثَلاًۜ اَفَلَا تَذَكَّرُونَ۟', 'Bu iki zümrenin (müminlerle kâfirlerin) durumu, kör ve sağır ile gören ve işiten kimseler gibidir. Bunların hali hiç eşit olur mu? Hâla ibret almıyor musunuz?', 12, 223, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1498, 25, 11, 'Hud', 'وَلَقَدْ اَرْسَلْنَا نُوحاً اِلٰى قَوْمِه۪ۘ اِنّ۪ي لَكُمْ نَذ۪يرٌ مُب۪ينٌۙ', 'Andolsun, biz Nuh´u kavmine elçi gönderdik. Onlara: «Ben (dedi), sizin için apaçık bir uyarıcıyım.', 12, 223, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1499, 26, 11, 'Hud', 'اَنْ لَا تَعْبُدُٓوا اِلَّا اللّٰهَۜ اِنّ۪ٓي اَخَافُ عَلَيْكُمْ عَذَابَ يَوْمٍ اَل۪يمٍ', 'Allah´tan başkasına tapmayın! Ben, size (gelecek) elem verici bir günün azabından korkuyorum.»', 12, 223, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1500, 27, 11, 'Hud', 'فَقَالَ الْمَلَأُ الَّذ۪ينَ كَفَرُوا مِنْ قَوْمِه۪ مَا نَرٰيكَ اِلَّا بَشَراً مِثْلَنَا وَمَا نَرٰيكَ اتَّبَعَكَ اِلَّا الَّذ۪ينَ هُمْ اَرَاذِلُنَا بَادِيَ الرَّأْيِۚ وَمَا نَرٰى لَكُمْ عَلَيْنَا مِنْ فَضْلٍ بَلْ نَظُنُّكُمْ كَاذِب۪ينَ', 'Kavminden ileri gelen kâfirler dediler ki: «Biz seni sadece bizim gibi bir insan olarak görüyoruz. Bizden, basit görüşle hareket eden alt tabakamızdan başkasının sana uyduğunu görmüyoruz. Ve sizin bize karşı bir üstünlüğünüzü de görmüyoruz. Bilakis sizin yalancılar olduğunuzu düşünüyoruz.»', 12, 223, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1501, 28, 11, 'Hud', 'قَالَ يَا قَوْمِ اَرَاَيْتُمْ اِنْ كُنْتُ عَلٰى بَيِّنَةٍ مِنْ رَبّ۪ي وَاٰتٰين۪ي رَحْمَةً مِنْ عِنْدِه۪ فَعُمِّيَتْ عَلَيْكُمْۜ اَنُلْزِمُكُمُوهَا وَاَنْتُمْ لَهَا كَارِهُونَ', '(Nuh) dedi ki: Ey kavmim! Eğer ben Rabbim tarafından (bildirilen) açık bir delil üzerinde isem ve O bana kendi katından bir rahmet vermiş de bu size gizli tutulmuşsa, buna ne dersiniz? Siz onu istemediğiniz halde biz sizi ona zorlayacak mıyız?', 12, 223, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1502, 29, 11, 'Hud', 'وَيَا قَوْمِ لَٓا اَسْـَٔلُكُمْ عَلَيْهِ مَالاًۜ اِنْ اَجْرِيَ اِلَّا عَلَى اللّٰهِ وَمَٓا اَنَا۬ بِطَارِدِ الَّذ۪ينَ اٰمَنُواۜ اِنَّهُمْ مُلَاقُوا رَبِّهِمْ وَلٰكِنّ۪ٓي اَرٰيكُمْ قَوْماً تَجْهَلُونَ', 'Ey kavmim! Allah´ın emirlerini bildirmeye karşılık sizden herhangi bir mal istemiyorum. Benim mükâfatım ancak Allah´a aittir. Ben iman edenleri kovacak değilim; çünkü onlar Rablerine kavuşacaklardır. Fakat ben sizi, bilgisizce davranan bir topluluk olarak görüyorum.', 12, 224, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1503, 30, 11, 'Hud', 'وَيَا قَوْمِ مَنْ يَنْصُرُن۪ي مِنَ اللّٰهِ اِنْ طَرَدْتُهُمْۜ اَفَلَا تَذَكَّرُونَ', 'Ey kavmim! Ben onları kovarsam, beni Allah´tan (onun azabından) kim korur? Düşünmüyor musunuz?', 12, 224, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1504, 31, 11, 'Hud', 'وَلَٓا اَقُولُ لَكُمْ عِنْد۪ي خَزَٓائِنُ اللّٰهِ وَلَٓا اَعْلَمُ الْغَيْبَ وَلَٓا اَقُولُ اِنّ۪ي مَلَكٌ وَلَٓا اَقُولُ لِلَّذ۪ينَ تَزْدَر۪ٓي اَعْيُنُكُمْ لَنْ يُؤْتِيَهُمُ اللّٰهُ خَيْراًۜ اَللّٰهُ اَعْلَمُ بِمَا ف۪ٓي اَنْفُسِهِمْۚ اِنّ۪ٓي اِذاً لَمِنَ الظَّالِم۪ينَ', 'Ben size: «Allah´ın hazineleri benim yanımdadır» demiyorum, gaybı da bilmem. «Ben bir meleğim» de demiyorum. Sizin gözlerinizin hor gördüğü kimseler için, «Allah onlara asla bir hayır vermeyecektir» diyemem. Onların kalplerinde olanı, Allah daha iyi bilir. Onları kovduğum takdirde ben gerçekten zalimlerden olurum.»', 12, 224, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1505, 32, 11, 'Hud', 'قَالُوا يَا نُوحُ قَدْ جَادَلْتَنَا فَاَكْثَرْتَ جِدَالَنَا فَأْتِنَا بِمَا تَعِدُنَٓا اِنْ كُنْتَ مِنَ الصَّادِق۪ينَ', 'Dediler ki: Ey Nuh! Bizimle mücadele ettin ve bize karşı mücadelede çok ileri gittin. Eğer doğrulardan isen, kendisiyle bizi tehdit ettiğini (azabı) bize getir!', 12, 224, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1506, 33, 11, 'Hud', 'قَالَ اِنَّمَا يَأْت۪يكُمْ بِهِ اللّٰهُ اِنْ شَٓاءَ وَمَٓا اَنْتُمْ بِمُعْجِز۪ينَ', '(Nuh) dedi ki: «Onu size ancak dilerse Allah getirir. Ve siz (Allah´ı) âciz bırakacak değilsiniz.', 12, 224, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1507, 34, 11, 'Hud', 'وَلَا يَنْفَعُكُمْ نُصْح۪ٓي اِنْ اَرَدْتُ اَنْ اَنْصَحَ لَكُمْ اِنْ كَانَ اللّٰهُ يُر۪يدُ اَنْ يُغْوِيَكُمْۜ هُوَ رَبُّكُمْ وَاِلَيْهِ تُرْجَعُونَۜ', 'Eğer Allah sizi azdırmak istiyorsa, ben size öğüt vermek istesem de, öğüdüm size fayda vermez. (Çünkü) O sizin Rabbinizdir. Ve (nihayet) O´na döndürüleceksiniz.»', 12, 224, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1508, 35, 11, 'Hud', 'اَمْ يَقُولُونَ افْتَرٰيهُۜ قُلْ اِنِ افْتَرَيْتُهُ فَعَلَيَّ اِجْرَام۪ي وَاَنَا۬ بَر۪ٓيءٌ مِمَّا تُجْرِمُونَ۟', '(Resûlüm!) Yoksa, «Bunu uydurdu» mu diyorlar? De ki: «Eğer onu uydurduysam günahım bana aittir. Fakat ben sizin işlediğiniz günahtan uzağım.»', 12, 224, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1509, 36, 11, 'Hud', 'وَاُو۫حِيَ اِلٰى نُوحٍ اَنَّهُ لَنْ يُؤْمِنَ مِنْ قَوْمِكَ اِلَّا مَنْ قَدْ اٰمَنَ فَلَا تَبْتَئِسْ بِمَا كَانُوا يَفْعَلُونَۚ', 'Nuh´a vahyolundu ki: Kavminden iman etmiş olanlardan başkası artık (sana) asla inanmayacak. Öyle ise onların işlemekte olduklarından (günahlardan) dolayı üzülme.', 12, 224, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1510, 37, 11, 'Hud', 'وَاصْنَعِ الْفُلْكَ بِاَعْيُنِنَا وَوَحْيِنَا وَلَا تُخَاطِبْن۪ي فِي الَّذ۪ينَ ظَلَمُواۚ اِنَّهُمْ مُغْرَقُونَ', 'Gözlerimizin önünde ve vahyimiz (emrimiz) uyarınca gemiyi yap ve zulmedenler hakkında bana (bir şey) söyleme! Onlar mutlaka boğulacaklardır!', 12, 224, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1511, 38, 11, 'Hud', 'وَيَصْنَعُ الْفُلْكَ وَكُلَّمَا مَرَّ عَلَيْهِ مَلَاٌ مِنْ قَوْمِه۪ سَخِرُوا مِنْهُۜ قَالَ اِنْ تَسْخَرُوا مِنَّا فَاِنَّا نَسْخَرُ مِنْكُمْ كَمَا تَسْخَرُونَۜ', 'Nuh gemiyi yapıyor, kavminden ileri gelenler ise, yanına her uğradıkça onunla alay ediyorlardı. Dedi ki: «Eğer bizimle alay ediyorsanız, iyi bilin ki siz nasıl alay ediyorsanız biz de sizinle alay edeceğiz!', 12, 225, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1512, 39, 11, 'Hud', 'فَسَوْفَ تَعْلَمُونَۙ مَنْ يَأْت۪يهِ عَذَابٌ يُخْز۪يهِ وَيَحِلُّ عَلَيْهِ عَذَابٌ مُق۪يمٌ', 'Kendisini rezil edecek azabın kime geleceğini ve sürekli bir azabın kimin başına ineceğini yakında bileceksiniz.»', 12, 225, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1513, 40, 11, 'Hud', 'حَتّٰٓى اِذَا جَٓاءَ اَمْرُنَا وَفَارَ التَّنُّورُۙ قُلْنَا احْمِلْ ف۪يهَا مِنْ كُلٍّ زَوْجَيْنِ اثْنَيْنِ وَاَهْلَكَ اِلَّا مَنْ سَبَقَ عَلَيْهِ الْقَوْلُ وَمَنْ اٰمَنَۜ وَمَٓا اٰمَنَ مَعَهُٓ اِلَّا قَل۪يلٌ', 'Nihayet emrimiz gelip de sular coşup yükselmeye başlayınca Nuh´a dedik ki: «(Canlı çeşitlerinin) her birinden birer çift ile -(boğulacağına dair) aleyhinde söz geçmiş olanlar dışında- aileni ve iman edenleri gemiye yükle!» Zaten onunla beraber pek azı iman etmişti.', 12, 225, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1514, 41, 11, 'Hud', 'وَقَالَ ارْكَبُوا ف۪يهَا بِسْمِ اللّٰهِ مَجْرٰۭۙيهَا وَمُرْسٰيهَاۜ اِنَّ رَبّ۪ي لَغَفُورٌ رَح۪يمٌ', '(Nuh) dedi ki: «Gemiye binin! Onun yüzüp gitmesi de, durması da Allah´ın adıyladır. Şüphesiz ki Rabbim çok bağışlayan, pek esirgeyendir.»', 12, 225, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1515, 42, 11, 'Hud', 'وَهِيَ تَجْر۪ي بِهِمْ ف۪ي مَوْجٍ كَالْجِبَالِ وَنَادٰى نُوحٌۨ ابْنَهُ وَكَانَ ف۪ي مَعْزِلٍ يَا بُنَيَّ ارْكَبْۭۗ مَعَنَا وَلَا تَكُنْ مَعَ الْكَافِر۪ينَ', 'Gemi, dağlar gibi dalgalar arasında onları götürüyordu. Nuh, gemiden uzakta bulunan oğluna: Yavrucuğum! (Sen de) bizimle beraber bin, kâfirlerle beraber olma! diye seslendi.', 12, 225, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1516, 43, 11, 'Hud', 'قَالَ سَاٰو۪ٓي اِلٰى جَبَلٍ يَعْصِمُن۪ي مِنَ الْمَٓاءِۜ قَالَ لَا عَاصِمَ الْيَوْمَ مِنْ اَمْرِ اللّٰهِ اِلَّا مَنْ رَحِمَۚ وَحَالَ بَيْنَهُمَا الْمَوْجُ فَكَانَ مِنَ الْمُغْرَق۪ينَ', 'Oğlu: Beni sudan koruyacak bir dağa sığınacağım, dedi. (Nuh): «Bugün Allah´ın emrinden (azabından), merhamet sahibi Allah´tan başka koruyacak kimse yoktur» dedi. Aralarına dalga girdi, böylece o da boğulanlardan oldu.', 12, 225, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1517, 44, 11, 'Hud', 'وَق۪يلَ يَٓا اَرْضُ ابْلَع۪ي مَٓاءَكِ وَيَا سَمَٓاءُ اَقْلِع۪ي وَغ۪يضَ الْمَٓاءُ وَقُضِيَ الْاَمْرُ وَاسْتَوَتْ عَلَى الْجُودِيِّ وَق۪يلَ بُعْداً لِلْقَوْمِ الظَّالِم۪ينَ', '(Nihayet) «Ey yer suyunu yut! Ve ey gök (suyunu) tut!» denildi. Su çekildi; iş bitirildi; (gemi de) Cûdî (dağının) üzerine yerleşti. Ve: «O zalimler topluluğunun canı cehenneme!» denildi.', 12, 225, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1518, 45, 11, 'Hud', 'وَنَادٰى نُوحٌ رَبَّهُ فَقَالَ رَبِّ اِنَّ ابْن۪ي مِنْ اَهْل۪ي وَاِنَّ وَعْدَكَ الْحَقُّ وَاَنْتَ اَحْكَمُ الْحَاكِم۪ينَ', 'Nuh Rabbine dua edip dedi ki: «Ey Rabbim! Şüphesiz oğlum da ailemdendir. Senin vâdin ise elbette haktır. Sen hakimler hakimisin.»', 12, 225, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1519, 46, 11, 'Hud', 'قَالَ يَا نُوحُ اِنَّهُ لَيْسَ مِنْ اَهْلِكَۚ اِنَّهُ عَمَلٌ غَيْرُ صَالِحٍۗ فَلَا تَسْـَٔلْنِ مَا لَيْسَ لَكَ بِه۪ عِلْمٌۜ اِنّ۪ٓي اَعِظُكَ اَنْ تَكُونَ مِنَ الْجَاهِل۪ينَ', 'Allah buyurdu ki: Ey Nuh! O asla senin ailenden değildir. Çünkü onun yaptığı kötü bir iştir. O halde hakkında bilgin olmayan bir şeyi benden isteme! Ben sana cahillerden olmamanı tavsiye ederim.', 12, 226, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1520, 47, 11, 'Hud', 'قَالَ رَبِّ اِنّ۪ٓي اَعُوذُ بِكَ اَنْ اَسْـَٔلَكَ مَا لَيْسَ ل۪ي بِه۪ عِلْمٌۜ وَاِلَّا تَغْفِرْ ل۪ي وَتَرْحَمْن۪ٓي اَكُنْ مِنَ الْخَاسِر۪ينَ', 'Nuh dedi ki: Ey Rabbim! Ben senden hakkında bilgim olmayan şeyi istemekten sana sığınırım. Eğer beni bağışlamaz ve esirgemezsen, ben ziyana uğrayanlardan olurum!', 12, 226, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1521, 48, 11, 'Hud', 'ق۪يلَ يَا نُوحُ اهْبِطْ بِسَلَامٍ مِنَّا وَبَرَكَاتٍ عَلَيْكَ وَعَلٰٓى اُمَمٍ مِمَّنْ مَعَكَۜ وَاُمَمٌ سَنُمَتِّعُهُمْ ثُمَّ يَمَسُّهُمْ مِنَّا عَذَابٌ اَل۪يمٌ', 'Denildi ki: Ey Nuh! Sana ve seninle beraber olan ümmetlere bizden selam ve bereketlerle (gemiden) in! Kendilerini (dünyada) faydalandıracağımız, sonra da bizden kendilerine elem verici bir azabın dokunacağı ümmetler de olacaktır.', 12, 226, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1522, 49, 11, 'Hud', 'تِلْكَ مِنْ اَنْـبَٓاءِ الْغَيْبِ نُوح۪يهَٓا اِلَيْكَۚ مَا كُنْتَ تَعْلَمُهَٓا اَنْتَ وَلَا قَوْمُكَ مِنْ قَبْلِ هٰذَاۜۛ فَاصْبِرْۜۛ اِنَّ الْعَاقِبَةَ لِلْمُتَّق۪ينَ۟', '(Resûlüm!) İşte bunlar sana vahyettiğimiz gayb haberlerindendir. Bundan önce onları ne sen biliyordun ne de kavmin. O halde sabret. Çünkü iyi sonuç (sabredip) sakınanlarındır.', 12, 226, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1523, 50, 11, 'Hud', 'وَاِلٰى عَادٍ اَخَاهُمْ هُوداًۜ قَالَ يَا قَوْمِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِنْ اِلٰهٍ غَيْرُهُۜ اِنْ اَنْتُمْ اِلَّا مُفْتَرُونَ', 'Âd kavmine de kardeşleri Hûd´u (gönderdik). Dedi ki: Ey kavmim! Allah´a kulluk edin. Sizin O´ndan başka tanrınız yoktur. Siz yalan uyduranlardan başkası değilsiniz.', 12, 226, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1524, 51, 11, 'Hud', 'يَا قَوْمِ لَٓا اَسْـَٔلُكُمْ عَلَيْهِ اَجْراًۜ اِنْ اَجْرِيَ اِلَّا عَلَى الَّذ۪ي فَطَرَن۪يۜ اَفَلَا تَعْقِلُونَ', 'Ey kavmim! Ben, ona (peygamberliğe) karşılık sizden bir ücret istemiyorum. Benim ücretim, beni yaratandan başkasına ait değildir. Hâla aklınızı kullanmıyor musunuz?', 12, 226, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1525, 52, 11, 'Hud', 'وَيَا قَوْمِ اسْتَغْفِرُوا رَبَّكُمْ ثُمَّ تُوبُٓوا اِلَيْهِ يُرْسِلِ السَّمَٓاءَ عَلَيْكُمْ مِدْرَاراً وَيَزِدْكُمْ قُوَّةً اِلٰى قُوَّتِكُمْ وَلَا تَتَوَلَّوْا مُجْرِم۪ينَ', 'Ey kavmim! Rabbinizden bağış dileyin; sonra da O´na tevbe edin ki, üzerinize göğü (yağmuru) bol bol göndersin ve kuvvetinize kuvvet katsın. Günah işleyerek (Allah´tan) yüz çevirmeyin.', 12, 226, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1526, 53, 11, 'Hud', 'قَالُوا يَا هُودُ مَا جِئْتَنَا بِبَيِّنَةٍ وَمَا نَحْنُ بِتَارِك۪ٓي اٰلِهَتِنَا عَنْ قَوْلِكَ وَمَا نَحْنُ لَكَ بِمُؤْمِن۪ينَ', 'Dediler ki: Ey Hûd! Sen bize açık bir mucize getirmedin, biz de senin sözünle tanrılarımızı bırakacak değiliz ve biz sana iman edecek de değiliz.', 12, 226, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1527, 54, 11, 'Hud', 'اِنْ نَقُولُ اِلَّا اعْتَرٰيكَ بَعْضُ اٰلِهَتِنَا بِسُٓوءٍۜ قَالَ اِنّ۪ٓي اُشْهِدُ اللّٰهَ وَاشْهَدُٓوا اَنّ۪ي بَر۪ٓيءٌ مِمَّا تُشْرِكُونَۙ', 'Biz «Tanrılarımızdan biri seni fena çarpmış!» demekten başka bir söz söylemeyiz! (Hûd) dedi ki: «Ben Allah´ı şahit tutuyorum; siz de şahit olun ki ben sizin ortak koştuklarınızdan uzağım.»', 12, 227, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1528, 55, 11, 'Hud', 'مِنْ دُونِه۪ فَك۪يدُون۪ي جَم۪يعاً ثُمَّ لَا تُنْظِرُونِ', '«O´ndan başka (taptıklarınızın hepsinden uzağım). Haydi hepiniz bana tuzak kurun; sonra da bana mühlet vermeyin!»', 12, 227, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1529, 56, 11, 'Hud', 'اِنّ۪ي تَوَكَّلْتُ عَلَى اللّٰهِ رَبّ۪ي وَرَبِّكُمْۜ مَا مِنْ دَٓابَّةٍ اِلَّا هُوَ اٰخِذٌ بِنَاصِيَتِهَاۜ اِنَّ رَبّ۪ي عَلٰى صِرَاطٍ مُسْتَق۪يمٍ', '«Ben, benim de Rabbim, sizin de Rabbiniz olan Allah´a dayandım. Çünkü yürüyen hiçbir varlık yoktur ki, O, onun perçeminden tutmuş olmasın. Şüphesiz Rabbim dosdoğru yoldadır.»', 12, 227, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1530, 57, 11, 'Hud', 'فَاِنْ تَوَلَّوْا فَقَدْ اَبْلَغْتُكُمْ مَٓا اُرْسِلْتُ بِه۪ٓ اِلَيْكُمْۜ وَيَسْتَخْلِفُ رَبّ۪ي قَوْماً غَيْرَكُمْۚ وَلَا تَضُرُّونَهُ شَيْـٔاًۜ اِنَّ رَبّ۪ي عَلٰى كُلِّ شَيْءٍ حَف۪يظٌ', '«Eğer yüz çevirirseniz şüphesiz ki benimle size gönderileni size bildirdim. Rabbim (dilerse) sizden başka bir kavmi yerinize getirir de O´na hiçbir zarar veremezsiniz. Çünkü benim Rabbim her şeyi gözetendir.»', 12, 227, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1531, 58, 11, 'Hud', 'وَلَمَّا جَٓاءَ اَمْرُنَا نَجَّيْنَا هُوداً وَالَّذ۪ينَ اٰمَنُوا مَعَهُ بِرَحْمَةٍ مِنَّاۚ وَنَجَّيْنَاهُمْ مِنْ عَذَابٍ غَل۪يظٍ', 'Emrimiz gelince, Hûd´u ve onunla beraber iman edenleri tarafımızdan bir rahmetle kurtardık, onları ağır bir azaptan kurtuluşa erdirdik.', 12, 227, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1532, 59, 11, 'Hud', 'وَتِلْكَ عَادٌ جَحَدُوا بِاٰيَاتِ رَبِّهِمْ وَعَصَوْا رُسُلَهُ وَاتَّبَعُٓوا اَمْرَ كُلِّ جَبَّارٍ عَن۪يدٍ', 'İşte Âd (kavmi). Rablerinin âyetlerini inkâr ettiler; O´nun peygamberlerine âsi oldular ve inatçı her zorbanın emrine uydular.', 12, 227, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1533, 60, 11, 'Hud', 'وَاُتْبِعُوا ف۪ي هٰذِهِ الدُّنْيَا لَعْنَةً وَيَوْمَ الْقِيٰمَةِۜ اَلَٓا اِنَّ عَاداً كَفَرُوا رَبَّهُمْۜ اَلَا بُعْداً لِعَادٍ قَوْمِ هُودٍ۟', 'Onlar hem bu dünyada hem de kıyamet gününde lânete tâbi tutuldular. Biliniz ki, Âd (kavmi) Rablerini inkâr ettiler. (Şunu da) bilin ki Hûd´un kavmi Âd, Allah´ın rahmetinden uzak kılındı.', 12, 227, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1534, 61, 11, 'Hud', 'وَاِلٰى ثَمُودَ اَخَاهُمْ صَالِحاًۢ قَالَ يَا قَوْمِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِنْ اِلٰهٍ غَيْرُهُۜ هُوَ اَنْشَاَكُمْ مِنَ الْاَرْضِ وَاسْتَعْمَرَكُمْ ف۪يهَا فَاسْتَغْفِرُوهُ ثُمَّ تُوبُٓوا اِلَيْهِۜ اِنَّ رَبّ۪ي قَر۪يبٌ مُج۪يبٌ', 'Semûd kavmine de kardeşleri Sâlih´i (gönderdik). Dedi ki: Ey kavmim! Allah´a kulluk edin. Sizin O´ndan başka tanrınız yoktur. O sizi yerden (topraktan) yarattı. Ve sizi orada yaşattı. O halde O´ndan mağfiret isteyin; sonra da O´na tevbe edin. Çünkü Rabbim (kullarına) çok yakındır, (dualarını) kabul edendir.', 12, 227, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1535, 62, 11, 'Hud', 'قَالُوا يَا صَالِحُ قَدْ كُنْتَ ف۪ينَا مَرْجُواًّ قَبْلَ هٰذَٓا اَتَنْهٰينَٓا اَنْ نَعْبُدَ مَا يَعْبُدُ اٰبَٓاؤُ۬نَا وَاِنَّنَا لَف۪ي شَكٍّ مِمَّا تَدْعُونَٓا اِلَيْهِ مُر۪يبٍ', 'Dediler ki: Ey Sâlih! Sen bundan önce içimizde ümit beslenen birisiydin. (Şimdi) babalarımızın taptıklarına tapmaktan bizi engelliyor musun? Doğrusu biz, bizi kendisine (kulluğa) çağırdığın şeyden ciddi bir şüphe içindeyiz.', 12, 227, 123, 7633, 'Peygamber Hud', 'Mekke', 52);
INSERT INTO `Ayetler` (`id`, `AyetNo`, `SureNo`, `SureAdi`, `Metin`, `Mana`, `Cuzu`, `Sayfasi`, `AyetSayisi`, `HarfSayisi`, `AdAnlami`, `IndigiYer`, `InisSirasi`) VALUES
(1536, 63, 11, 'Hud', 'قَالَ يَا قَوْمِ اَرَاَيْتُمْ اِنْ كُنْتُ عَلٰى بَيِّنَةٍ مِنْ رَبّ۪ي وَاٰتٰين۪ي مِنْهُ رَحْمَةً فَمَنْ يَنْصُرُن۪ي مِنَ اللّٰهِ اِنْ عَصَيْتُهُ فَمَا تَز۪يدُونَن۪ي غَيْرَ تَخْس۪يرٍ', '(Sâlih) dedi ki: Ey kavmim! Eğer ben Rabbimden (verilen) apaçık bir delil üzerinde isem ve O bana kendinden bir rahmet (peygamberlik) vermişse, buna ne dersiniz? Bu durum karşısında O´na âsi olursam beni Allah´tan (O´nun azabından) kim korur? O zaman siz de bana ziyan vermekten fazla bir şey yapamazsınız.', 12, 228, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1537, 64, 11, 'Hud', 'وَيَا قَوْمِ هٰذِه۪ نَاقَةُ اللّٰهِ لَكُمْ اٰيَةً فَذَرُوهَا تَأْكُلْ ف۪ٓي اَرْضِ اللّٰهِ وَلَا تَمَسُّوهَا بِسُٓوءٍ فَيَأْخُذَكُمْ عَذَابٌ قَر۪يبٌ', 'Ey kavmim! İşte size mucize olarak Allah´ın devesi. Onu bırakın, Allah´ın arzında yesin (içsin). Ona kötülük dokundurmayın; sonra sizi yakın bir azap yakalar.', 12, 228, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1538, 65, 11, 'Hud', 'فَعَقَرُوهَا فَقَالَ تَمَتَّعُوا ف۪ي دَارِكُمْ ثَلٰثَةَ اَيَّامٍۜ ذٰلِكَ وَعْدٌ غَيْرُ مَكْذُوبٍ', 'Fakat Semûd kavmi o deveyi, ayaklarını keserek öldürdüler. Sâlih dedi ki: «Yurdunuzda üç gün daha yaşayın (sonra helâk olacaksınız)!» Bu söz, yalanlanamayan bir tehdit idi.', 12, 228, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1539, 66, 11, 'Hud', 'فَلَمَّا جَٓاءَ اَمْرُنَا نَجَّيْنَا صَالِحاً وَالَّذ۪ينَ اٰمَنُوا مَعَهُ بِرَحْمَةٍ مِنَّا وَمِنْ خِزْيِ يَوْمِئِذٍۜ اِنَّ رَبَّكَ هُوَ الْقَوِيُّ الْعَز۪يزُ', 'Emrimiz gelince, Sâlih´i ve onunla beraber iman edenleri, bizden bir rahmet olarak (azaptan) ve o günün zilletinden kurtardık. Şüphesiz Rabbin kuvvetlidir, (her şeye) galip gelendir.', 12, 228, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1540, 67, 11, 'Hud', 'وَاَخَذَ الَّذ۪ينَ ظَلَمُوا الصَّيْحَةُ فَاَصْبَحُوا ف۪ي دِيَارِهِمْ جَاثِم۪ينَۙ', 'Zulmedenleri de o korkunç ses yakaladı ve yurtlarında diz üstü çökekaldılar.', 12, 228, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1541, 68, 11, 'Hud', 'كَاَنْ لَمْ يَغْنَوْا ف۪يهَاۜ اَلَٓا اِنَّ ثَمُودَا۬ كَفَرُوا رَبَّهُمْۜ اَلَا بُعْداً لِثَمُودَ۟', 'Sanki orada hiç oturmamışlardı. Biliniz ki, Semûd kavmi gerçekten Rablerini inkâr ettiler. Yine bilesiniz ki, Semûd kavmi (Allah´ın rahmetinden) uzak kılındı.', 12, 228, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1542, 69, 11, 'Hud', 'وَلَقَدْ جَٓاءَتْ رُسُلُنَٓا اِبْرٰه۪يمَ بِالْبُشْرٰى قَالُوا سَلَاماًۜ قَالَ سَلَامٌۚ فَمَا لَبِثَ اَنْ جَٓاءَ بِعِجْلٍ حَن۪يذٍ', 'Andolsun ki elçilerimiz (melekler) İbrahim´e müjde getirdiler ve: «Selam (sana) » dediler. O da: «(Size de)selam» dedi ve hemen kızartılmış bir buzağı getirdi.', 12, 228, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1543, 70, 11, 'Hud', 'فَلَمَّا رَآٰ اَيْدِيَهُمْ لَا تَصِلُ اِلَيْهِ نَكِرَهُمْ وَاَوْجَسَ مِنْهُمْ خ۪يفَةًۜ قَالُوا لَا تَخَفْ اِنَّٓا اُرْسِلْـنَٓا اِلٰى قَوْمِ لُوطٍۜ', 'Ellerini yemeğe uzatmadıklarını görünce, onları yadırgadı ve onlardan dolayı içine bir korku düştü. Dediler ki: Korkma! (biz melekleriz). Lût kavmine gönderildik.', 12, 228, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1544, 71, 11, 'Hud', 'وَامْرَاَتُهُ قَٓائِمَةٌ فَضَحِكَتْ فَبَشَّرْنَاهَا بِاِسْحٰقَۙ وَمِنْ وَرَٓاءِ اِسْحٰقَ يَعْقُوبَ', 'O esnada hanımı ayakta idi ve (bu sözleri duyunca) güldü. Ona da İshak´ı, İshak´ın ardından da Ya´kub´u müjdeledik.', 12, 228, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1545, 72, 11, 'Hud', 'قَالَتْ يَا وَيْلَتٰٓى ءَاَلِدُ وَاَنَا۬ عَجُوزٌ وَهٰذَا بَعْل۪ي شَيْخاًۜ اِنَّ هٰذَا لَشَيْءٌ عَج۪يبٌ', '(İbrahim´in karısı:) Olacak şey değil! Ben bir kocakarı, bu kocam da bir ihtiyar iken çocuk mu doğuracağım? Bu gerçekten şaşılacak bir şey! dedi.', 12, 229, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1546, 73, 11, 'Hud', 'قَالُٓوا اَتَعْجَب۪ينَ مِنْ اَمْرِ اللّٰهِ رَحْمَتُ اللّٰهِ وَبَرَكَاتُهُ عَلَيْكُمْ اَهْلَ الْبَيْتِۜ اِنَّهُ حَم۪يدٌ مَج۪يدٌ', '(Melekler) dediler ki: Allah´ın emrine şaşıyor musun? Ey ev halkı! Allah´ın rahmeti ve bereketleri sizin üzerinizdedir. Şüphesiz ki O, övülmeye lâyıktır, iyiliği boldur.', 12, 229, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1547, 74, 11, 'Hud', 'فَلَمَّا ذَهَبَ عَنْ اِبْرٰه۪يمَ الرَّوْعُ وَجَٓاءَتْهُ الْبُشْرٰى يُجَادِلُنَا ف۪ي قَوْمِ لُوطٍۜ', 'İbrahim´den korku gidip kendisine müjde gelince, Lût kavmi hakkında (adeta) bizimle mücadeleye başladı.', 12, 229, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1548, 75, 11, 'Hud', 'اِنَّ اِبْرٰه۪يمَ لَحَل۪يمٌ اَوَّاهٌ مُن۪يبٌ', 'İbrahim cidden yumuşak huylu, bağrı yanık, kendisini Allah´a vermiş biri idi.', 12, 229, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1549, 76, 11, 'Hud', 'يَٓا اِبْرٰه۪يمُ اَعْرِضْ عَنْ هٰذَاۚ اِنَّهُ قَدْ جَٓاءَ اَمْرُ رَبِّكَۚ وَاِنَّهُمْ اٰت۪يهِمْ عَذَابٌ غَيْرُ مَرْدُودٍ', '(Melekler dediler ki): Ey İbrahim! Bundan vazgeç. Çünkü Rabbinin (azap) emri gelmiştir. Ve onlara, geri çevrilmez bir azap mutlaka gelecektir!', 12, 229, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1550, 77, 11, 'Hud', 'وَلَمَّا جَٓاءَتْ رُسُلُنَا لُوطاً س۪ٓيءَ بِهِمْ وَضَاقَ بِهِمْ ذَرْعاً وَقَالَ هٰذَا يَوْمٌ عَص۪يبٌ', 'Elçilerimiz Lût´a gelince, (Lût) onların yüzünden üzüldü ve onlardan dolayı içi daraldı da «Bu, çetin bir gündür» dedi.', 12, 229, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1551, 78, 11, 'Hud', 'وَجَٓاءَهُ قَوْمُهُ يُهْرَعُونَ اِلَيْهِ وَمِنْ قَبْلُ كَانُوا يَعْمَلُونَ السَّيِّـَٔاتِۜ قَالَ يَا قَوْمِ هٰٓؤُ۬لَٓاءِ بَنَات۪ي هُنَّ اَطْهَرُ لَكُمْ فَاتَّقُوا اللّٰهَ وَلَا تُخْزُونِ ف۪ي ضَيْف۪يۜ اَلَيْسَ مِنْكُمْ رَجُلٌ رَش۪يدٌ', 'Lût´un kavmi, koşarak onun yanına geldiler. Daha önce de o kötü işleri yapmaktaydılar. (Lût): «Ey kavmim! İşte şunlar kızlarımdır (onlarla evlenin); sizin için onlar daha temizdir. Allah´tan korkun ve misafirlerimin önünde beni rezil etmeyin! İçinizde aklı başında bir adam yok mu!» dedi.', 12, 229, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1552, 79, 11, 'Hud', 'قَالُوا لَقَدْ عَلِمْتَ مَا لَنَا ف۪ي بَنَاتِكَ مِنْ حَقٍّۚ وَاِنَّكَ لَتَعْلَمُ مَا نُر۪يدُ', 'Dediler ki: Senin kızlarında bizim bir hakkımız olmadığını biliyorsun. Ve sen bizim ne istediğimizi elbette bilirsin.', 12, 229, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1553, 80, 11, 'Hud', 'قَالَ لَوْ اَنَّ ل۪ي بِكُمْ قُوَّةً اَوْ اٰو۪ٓي اِلٰى رُكْنٍ شَد۪يدٍ', '(Lût:) Keşke benim size karşı (koyacak) bir gücüm olsaydı veya güçlü bir kaleye sığınabilseydim! dedi.', 12, 229, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1554, 81, 11, 'Hud', 'قَالُوا يَا لُوطُ اِنَّا رُسُلُ رَبِّكَ لَنْ يَصِلُٓوا اِلَيْكَ فَاَسْرِ بِاَهْلِكَ بِقِطْعٍ مِنَ الَّيْلِ وَلَا يَلْتَفِتْ مِنْكُمْ اَحَدٌ اِلَّا امْرَاَتَكَۜ اِنَّهُ مُص۪يبُهَا مَٓا اَصَابَهُمْۜ اِنَّ مَوْعِدَهُمُ الصُّبْحُۜ اَلَيْسَ الصُّبْحُ بِقَر۪يبٍ', '(Melekler) dediler ki: Ey Lût! Biz Rabbinin elçileriyiz. Onlar sana asla dokunamazlar. Sen gecenin bir kısmında ailenle (yola çıkıp) yürü. Karından başka sizden hiçbiri geride kalmasın. Çünkü onlara gelecek olan (azap) şüphesiz ona da isabet edecektir. Onlara vâdolunan (helâk) zamanı, sabah vaktidir. Sabah yakın değil mi?', 12, 229, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1555, 82, 11, 'Hud', 'فَلَمَّا جَٓاءَ اَمْرُنَا جَعَلْنَا عَالِيَهَا سَافِلَهَا وَاَمْطَرْنَا عَلَيْهَا حِجَارَةً مِنْ سِجّ۪يلٍۙ مَنْضُودٍۙ', 'Emrimiz gelince, oranın altını üstüne getirdik ve üzerlerine (balçıktan) pişirilip istif edilmiş taşlar yağdırdık.', 12, 230, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1556, 83, 11, 'Hud', 'مُسَوَّمَةً عِنْدَ رَبِّكَۜ وَمَا هِيَ مِنَ الظَّالِم۪ينَ بِبَع۪يدٍ۟', '(O taşlar:) Rabbin katında işaretlenerek (yağdırılmıştır). Onlar zalimlerden uzak değildir.', 12, 230, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1557, 84, 11, 'Hud', 'وَاِلٰى مَدْيَنَ اَخَاهُمْ شُعَيْباًۜ قَالَ يَا قَوْمِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِنْ اِلٰهٍ غَيْرُهُۜ وَلَا تَنْقُصُوا الْمِكْيَالَ وَالْم۪يزَانَ اِنّ۪ٓي اَرٰيكُمْ بِخَيْرٍ وَاِنّ۪ٓي اَخَافُ عَلَيْكُمْ عَذَابَ يَوْمٍ مُح۪يطٍ', 'Medyen´e de kardeşleri Şuayb´ı (gönderdik). Dedi ki: Ey kavmim! Allah´a kulluk edin! Sizin için ondan başka tanrı yoktur. Ölçüyü ve tartıyı eksik yapmayın. Zira ben sizi hayır (ve bolluk) içinde görüyorum. Ve ben, gerçekten sizin için kuşatıcı bir günün azabından korkuyorum.', 12, 230, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1558, 85, 11, 'Hud', 'وَيَا قَوْمِ اَوْفُوا الْمِكْيَالَ وَالْم۪يزَانَ بِالْقِسْطِ وَلَا تَبْخَسُوا النَّاسَ اَشْيَٓاءَهُمْ وَلَا تَعْثَوْا فِي الْاَرْضِ مُفْسِد۪ينَ', 'Ve ey kavmim! Ölçüyü ve tartıyı adaletle yapın; insanlara eşyalarını eksik vermeyin; yeryüzünde bozguncular olarak dolaşmayın.', 12, 230, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1559, 86, 11, 'Hud', 'بَقِيَّتُ اللّٰهِ خَيْرٌ لَكُمْ اِنْ كُنْتُمْ مُؤْمِن۪ينَۚ وَمَٓا اَنَا۬ عَلَيْكُمْ بِحَف۪يظٍ', 'Eğer mümin iseniz Allah´ın (helâlinden) bıraktığı (kâr) sizin için daha hayırlıdır. Ben üzerinize bir bekçi değilim.', 12, 230, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1560, 87, 11, 'Hud', 'قَالُوا يَا شُعَيْبُ اَصَلٰوتُكَ تَأْمُرُكَ اَنْ نَتْرُكَ مَا يَعْبُدُ اٰبَٓاؤُ۬نَٓا اَوْ اَنْ نَفْعَلَ ف۪ٓي اَمْوَالِنَا مَا نَشٰٓؤُ۬اۜ اِنَّكَ لَاَنْتَ الْحَل۪يمُ الرَّش۪يدُ', 'Dediler ki: Ey Şuayb! Babalarımızın taptıklarını (putları), yahut mallarımız hususunda dilediğimizi yapmayı terketmemizi sana namazın mı emrediyor? Oysa sen yumuşak huylu ve çok akıllısın!', 12, 230, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1561, 88, 11, 'Hud', 'قَالَ يَا قَوْمِ اَرَاَيْتُمْ اِنْ كُنْتُ عَلٰى بَيِّنَةٍ مِنْ رَبّ۪ي وَرَزَقَن۪ي مِنْهُ رِزْقاً حَسَناًۜ وَمَٓا اُر۪يدُ اَنْ اُخَالِفَكُمْ اِلٰى مَٓا اَنْهٰيكُمْ عَنْهُۜ اِنْ اُر۪يدُ اِلَّا الْاِصْلَاحَ مَا اسْتَطَعْتُۜ وَمَا تَوْف۪يق۪ٓي اِلَّا بِاللّٰهِۜ عَلَيْهِ تَوَكَّلْتُ وَاِلَيْهِ اُن۪يبُ', 'Dedi ki: Ey kavmim! Eğer benim, Rabbim tarafından (verilmiş) apaçık bir delilim varsa ve O bana tarafından güzel bir rızık vermişse buna ne dersiniz? Size yasak ettiğim şeylerin aksini yaparak size aykırı davranmak istemiyorum. Ben sadece gücümün yettiği kadar ıslah etmek istiyorum. Fakat başarmam ancak Allah´ın yardımı iledir. Yalnız O´na dayandım ve yalnız O´na döneceğim.', 12, 230, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1562, 89, 11, 'Hud', 'وَيَا قَوْمِ لَا يَجْرِمَنَّكُمْ شِقَاق۪ٓي اَنْ يُص۪يبَكُمْ مِثْلُ مَٓا اَصَابَ قَوْمَ نُوحٍ اَوْ قَوْمَ هُودٍ اَوْ قَوْمَ صَالِحٍۜ وَمَا قَوْمُ لُوطٍ مِنْكُمْ بِبَع۪يدٍ', 'Ey kavmim! Sakın bana karşı düşmanlığınız, Nuh kavminin veya Hûd kavminin yahut Sâlih kavminin başlarına gelenler gibi size de bir musibet getirmesin! Lût kavmi de sizden uzak değildir.', 12, 231, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1563, 90, 11, 'Hud', 'وَاسْتَغْفِرُوا رَبَّكُمْ ثُمَّ تُوبُٓوا اِلَيْهِۜ اِنَّ رَبّ۪ي رَح۪يمٌ وَدُودٌ', 'Rabbinizden bağışlanma dileyin; sonra O´na tevbe edin. Muhakkak ki Rabbim çok merhametlidir, (müminleri) çok sever.', 12, 231, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1564, 91, 11, 'Hud', 'قَالُوا يَا شُعَيْبُ مَا نَفْقَهُ۬ كَث۪يراً مِمَّا تَقُولُ وَاِنَّا لَنَرٰيكَ ف۪ينَا ضَع۪يفاًۚ وَلَوْلَا رَهْطُكَ لَرَجَمْنَاكَۘ وَمَٓا اَنْتَ عَلَيْنَا بِعَز۪يزٍ', 'Dediler ki: Ey Şuayb! Söylediklerinin çoğunu anlamıyoruz ve içimizde seni cidden zayıf (âciz) görüyoruz! Eğer kabilen olmasa, seni mutlaka taşlayarak öldürürüz. Sen bizden üstün değilsin.', 12, 231, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1565, 92, 11, 'Hud', 'قَالَ يَا قَوْمِ اَرَهْط۪ٓي اَعَزُّ عَلَيْكُمْ مِنَ اللّٰهِۜ وَاتَّخَذْتُمُوهُ وَرَٓاءَكُمْ ظِهْرِياًّۜ اِنَّ رَبّ۪ي بِمَا تَعْمَلُونَ مُح۪يطٌ', '(Şuayb:) «Ey kavmim dedi, size göre benim kabilem Allah´tan daha mı güçlü ve değerli ki, onu (Allah´ın emirlerini) arkanıza atıp unuttunuz. Şüphesiz ki Rabbim yapmakta olduklarınızı çepeçevre kuşatıcıdır.', 12, 231, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1566, 93, 11, 'Hud', 'وَيَا قَوْمِ اعْمَلُوا عَلٰى مَكَانَتِكُمْ اِنّ۪ي عَامِلٌۜ سَوْفَ تَعْلَمُونَۙ مَنْ يَأْت۪يهِ عَذَابٌ يُخْز۪يهِ وَمَنْ هُوَ كَاذِبٌۜ وَارْتَقِبُٓوا اِنّ۪ي مَعَكُمْ رَق۪يبٌ', 'Ey kavmim! Elinizden geleni yapın! Ben de yapacağım! Kendisini rezil edecek azabın geleceği şahsın ve yalancının kim olduğunu yakında öğreneceksiniz! Bekleyin! Ben de sizinle beraber beklemekteyim.»', 12, 231, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1567, 94, 11, 'Hud', 'وَلَمَّا جَٓاءَ اَمْرُنَا نَجَّيْنَا شُعَيْباً وَالَّذ۪ينَ اٰمَنُوا مَعَهُ بِرَحْمَةٍ مِنَّا وَاَخَذَتِ الَّذ۪ينَ ظَلَمُوا الصَّيْحَةُ فَاَصْبَحُوا ف۪ي دِيَارِهِمْ جَاثِم۪ينَۙ', 'Emrimiz gelince, Şuayb´ı ve onunla beraber iman edenleri tarafımızdan bir rahmetle kurtardık; zulmedenleri ise korkunç bir gürültü yakaladı da yurtlarında diz üstü çökekaldılar.', 12, 231, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1568, 95, 11, 'Hud', 'كَاَنْ لَمْ يَغْنَوْا ف۪يهَاۜ اَلَا بُعْداً لِمَدْيَنَ كَمَا بَعِدَتْ ثَمُودُ۟', 'Sanki orada hiç barınmamışlardı. Biliniz ki, Semûd kavmi (Allah´ın rahmetinden) uzak olduğu gibi Medyen kavmi de uzak oldu.', 12, 231, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1569, 96, 11, 'Hud', 'وَلَقَدْ اَرْسَلْنَا مُوسٰى بِاٰيَاتِنَا وَسُلْطَانٍ مُب۪ينٍۙ', '(96-97) Andolsun ki Musa´yı da mucizelerimizle ve apaçık bir delille Firavun´a ve onun ileri gelenlerine gönderdik. Fakat onlar Firavun´un emrine uydular. Oysa Firavun´un emri doğru değildi.', 12, 231, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1570, 97, 11, 'Hud', 'اِلٰى فِرْعَوْنَ وَمَلَا۬ئِه۪ فَاتَّـبَعُٓوا اَمْرَ فِرْعَوْنَۚ وَمَٓا اَمْرُ فِرْعَوْنَ بِرَش۪يدٍ', '(96-97) Andolsun ki Musa´yı da mucizelerimizle ve apaçık bir delille Firavun´a ve onun ileri gelenlerine gönderdik. Fakat onlar Firavun´un emrine uydular. Oysa Firavun´un emri doğru değildi.', 12, 231, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1571, 98, 11, 'Hud', 'يَقْدُمُ قَوْمَهُ يَوْمَ الْقِيٰمَةِ فَاَوْرَدَهُمُ النَّارَۜ وَبِئْسَ الْوِرْدُ الْمَوْرُودُ', 'Firavun, kıyamet gününde kavminin önüne düşecek ve onları (çekip) ateşe götürecektir. Varacakları yer ne kötü yerdir!', 12, 232, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1572, 99, 11, 'Hud', 'وَاُتْبِعُوا ف۪ي هٰذِه۪ لَعْنَةً وَيَوْمَ الْقِيٰمَةِۜ بِئْسَ الرِّفْدُ الْمَرْفُودُ', 'Onlar burada da, kıyamet gününde de lânete uğratıldılar. (Onlara) verilen bu armağan ne kötü armağandır!', 12, 232, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1573, 100, 11, 'Hud', 'ذٰلِكَ مِنْ اَنْـبَٓاءِ الْقُرٰى نَقُصُّهُ عَلَيْكَ مِنْهَا قَٓائِمٌ وَحَص۪يدٌ', '(Ey Muhammed!) İşte bu, (halkı helâk olmuş) memleketlerin haberlerindendir. Biz onu sana anlatıyoruz; onlardan (bugüne kadar izleri) kalan da vardır, biçilmiş ekin (gibi yok olan) da vardır.', 12, 232, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1574, 101, 11, 'Hud', 'وَمَا ظَلَمْنَاهُمْ وَلٰكِنْ ظَلَمُٓوا اَنْفُسَهُمْ فَـمَٓا اَغْنَتْ عَنْهُمْ اٰلِهَتُهُمُ الَّت۪ي يَدْعُونَ مِنْ دُونِ اللّٰهِ مِنْ شَيْءٍ لَمَّا جَٓاءَ اَمْرُ رَبِّكَۜ وَمَا زَادُوهُمْ غَيْرَ تَتْب۪يبٍ', 'Onlara biz zulmetmedik; fakat, onlar kendilerine zulmettiler. Rabbinin (azap) emri geldiğinde, Allah´ı bırakıp da taptıkları tanrıları, onlara hiçbir şey sağlamadı, ziyanlarını artırmaktan başka bir şeye yaramadı.', 12, 232, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1575, 102, 11, 'Hud', 'وَكَذٰلِكَ اَخْذُ رَبِّكَ اِذَٓا اَخَذَ الْقُرٰى وَهِيَ ظَالِمَةٌۜ اِنَّ اَخْذَهُٓ اَل۪يمٌ شَد۪يدٌ', 'Rabbin, haksızlık eden memleketleri (onların halkını) yakaladığında, onun yakalayışı işte böyle (şiddetlidir). Şüphesiz onun yakalaması pek elem vericidir, pek çetindir!', 12, 232, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1576, 103, 11, 'Hud', 'اِنَّ ف۪ي ذٰلِكَ لَاٰيَةً لِمَنْ خَافَ عَذَابَ الْاٰخِرَةِۜ ذٰلِكَ يَوْمٌ مَجْمُوعٌۙ لَهُ النَّاسُ وَذٰلِكَ يَوْمٌ مَشْهُودٌ', 'İşte bunda, ahiret azabından korkanlar için elbette bir ibret vardır. O gün bütün insanların bir araya toplandığı bir gündür ve o gün (bütün mahlûkatın) hazır bulunduğu bir gündür.', 12, 232, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1577, 104, 11, 'Hud', 'وَمَا نُؤَخِّرُهُٓ اِلَّا لِاَجَلٍ مَعْدُودٍۜ', 'Biz onu (kıyamet gününü) sadece sayılı bir müddete kadar bekletiriz.', 12, 232, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1578, 105, 11, 'Hud', 'يَوْمَ يَأْتِ لَا تَكَلَّمُ نَفْسٌ اِلَّا بِـاِذْنِه۪ۚ فَمِنْهُمْ شَقِيٌّ وَسَع۪يدٌ', 'O geldiği gün Allah´ın izni olmadan hiç kimse konuşamaz. Onlardan kimi bedbahttır, kimi mutlu.', 12, 232, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1579, 106, 11, 'Hud', 'فَاَمَّا الَّذ۪ينَ شَقُوا فَفِي النَّارِ لَهُمْ ف۪يهَا زَف۪يرٌ وَشَه۪يقٌۙ', 'Bedbaht olanlar ateştedirler, orada onların (öyle feci) nefes alıp vermeleri vardır ki.', 12, 232, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1580, 107, 11, 'Hud', 'خَالِد۪ينَ ف۪يهَا مَا دَامَتِ السَّمٰوَاتُ وَالْاَرْضُ اِلَّا مَا شَٓاءَ رَبُّكَۜ اِنَّ رَبَّكَ فَعَّالٌ لِمَا يُر۪يدُ', 'Rabbinin dilediği hariç, (onlar) gökler ve yer durdukça o ateşte ebedî kalacaklardır. Çünkü Rabbin, istediğini hakkıyla yapandır.', 12, 232, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1581, 108, 11, 'Hud', 'وَاَمَّا الَّذ۪ينَ سُعِدُوا فَفِي الْجَنَّةِ خَالِد۪ينَ ف۪يهَا مَا دَامَتِ السَّمٰوَاتُ وَالْاَرْضُ اِلَّا مَا شَٓاءَ رَبُّكَۜ عَطَٓاءً غَيْرَ مَجْذُوذٍ', 'Mutlu olanlara gelince, onlar da cennettedirler. Rabbinin dilediği hariç, gökler ve yer durdukça onlar da orada ebedî kalacaklardır. Bu (nimetler) bitmez, tükenmez bir lütuftur.', 12, 232, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1582, 109, 11, 'Hud', 'فَلَا تَكُ ف۪ي مِرْيَةٍ مِمَّا يَعْبُدُ هٰٓؤُ۬لَٓاءِۜ مَا يَعْبُدُونَ اِلَّا كَمَا يَعْبُدُ اٰبَٓاؤُ۬هُمْ مِنْ قَبْلُۜ وَاِنَّا لَمُوَفُّوهُمْ نَص۪يبَهُمْ غَيْرَ مَنْقُوصٍ۟', 'O halde onların tapmakta oldukları şeylerden (bu şeylerin onları azaba götürdüğünden) şüphen olmasın. Çünkü onlar ancak daha önce babalarının taptığı gibi tapıyorlar. Biz onların (azaptan) nasiplerini mutlaka eksiksiz olarak vereceğiz.', 12, 233, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1583, 110, 11, 'Hud', 'وَلَقَدْ اٰتَيْنَا مُوسَى الْكِتَابَ فَاخْتُلِفَ ف۪يهِۜ وَلَوْلَا كَلِمَةٌ سَبَقَتْ مِنْ رَبِّكَ لَقُضِيَ بَيْنَهُمْۜ وَاِنَّهُمْ لَف۪ي شَكٍّ مِنْهُ مُر۪يبٍ', 'Andolsun biz Musa´ya Kitab´ı verdik; fakat onda ihtilaf edildi. Eğer Rabbinden bir söz geçmemiş olsaydı, elbette onların arasında hüküm verilmişti (ve işleri de bitirilmişti). Şüphesiz ki onlar (Mekkeliler) de Kur´an hakkında derin bir şüphe içindedirler.', 12, 233, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1584, 111, 11, 'Hud', 'وَاِنَّ كُلاًّ لَمَّا لَيُوَفِّيَنَّهُمْ رَبُّكَ اَعْمَالَهُمْۜ اِنَّهُ بِمَا يَعْمَلُونَ خَب۪يرٌ', 'Şüphesiz Rabbin, onların her birinin amellerinin karşılığını onlara tam olarak verecektir. Çünkü Rabbin, onların yapmakta olduklarından haberdardır.', 12, 233, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1585, 112, 11, 'Hud', 'فَاسْتَقِمْ كَمَٓا اُمِرْتَ وَمَنْ تَابَ مَعَكَ وَلَا تَطْغَوْاۜ اِنَّهُ بِمَا تَعْمَلُونَ بَص۪يرٌ', 'O halde seninle beraber tevbe edenlerle birlikte emrolunduğun gibi dosdoğru ol! Aşırı da gitmeyin. Çünkü O, sizin yaptıklarınızı çok iyi görendir.', 12, 233, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1586, 113, 11, 'Hud', 'وَلَا تَرْكَـنُٓوا اِلَى الَّذ۪ينَ ظَلَمُوا فَتَمَسَّكُمُ النَّارُۙ وَمَا لَكُمْ مِنْ دُونِ اللّٰهِ مِنْ اَوْلِيَٓاءَ ثُمَّ لَا تُنْصَرُونَ', 'Zulmedenlere meyletmeyin; sonra size ateş dokunur (cehennemde yanarsınız). Sizin Allah´tan başka dostlarınız yoktur. Sonra (O´ndan da) yardım göremezsiniz!', 12, 233, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1587, 114, 11, 'Hud', 'وَاَقِمِ الصَّلٰوةَ طَرَفَيِ النَّهَارِ وَزُلَفاً مِنَ الَّيْلِۜ اِنَّ الْحَسَنَاتِ يُذْهِبْنَ السَّيِّـَٔاتِۜ ذٰلِكَ ذِكْرٰى لِلذَّاكِر۪ينَۚ', 'Gündüzün iki ucunda, gecenin de ilk saatlerinde namaz kıl. Çünkü iyilikler kötülükleri (günahları) giderir. Bu, öğüt almak isteyenlere bir hatırlatmadır.', 12, 233, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1588, 115, 11, 'Hud', 'وَاصْبِرْ فَاِنَّ اللّٰهَ لَا يُض۪يعُ اَجْرَ الْمُحْسِن۪ينَ', '(Ey Muhammed!) Sabırlı ol, çünkü Allah güzel iş yapanların mükâfatını zayi etmez.', 12, 233, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1589, 116, 11, 'Hud', 'فَلَوْلَا كَانَ مِنَ الْقُرُونِ مِنْ قَبْلِكُمْ اُو۬لُوا بَقِيَّةٍ يَنْهَوْنَ عَنِ الْفَسَادِ فِي الْاَرْضِ اِلَّا قَل۪يلاً مِمَّنْ اَنْجَيْنَا مِنْهُمْۚ وَاتَّبَعَ الَّذ۪ينَ ظَلَمُوا مَٓا اُتْرِفُوا ف۪يهِ وَكَانُوا مُجْرِم۪ينَ', 'Sizden önceki asırlarda yeryüzünde (insanları) bozgunculuktan alıkoyacak faziletli kimseler bulunsaydı ya! Fakat onlardan, kurtuluşa erdirdiğimiz az bir kısmı müstesnadır (bunlar görevlerini yaptılar). Zulmedenler ise, kendilerine verilen refahın peşine düştüler. Zaten günahkâr idiler.', 12, 233, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1590, 117, 11, 'Hud', 'وَمَا كَانَ رَبُّكَ لِيُهْلِكَ الْقُرٰى بِظُلْمٍ وَاَهْلُهَا مُصْلِحُونَ', 'Halkı iyi olduğu halde Rabbin, haksızlıkla memleketleri helâk etmez.', 12, 233, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1591, 118, 11, 'Hud', 'وَلَوْ شَٓاءَ رَبُّكَ لَجَعَلَ النَّاسَ اُمَّةً وَاحِدَةً وَلَا يَزَالُونَ مُخْتَلِف۪ينَۙ', 'Rabbin dileseydi bütün insanları bir tek millet yapardı. (Fakat) onlar ihtilafa düşmeye devam edecekler.', 12, 234, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1592, 119, 11, 'Hud', 'اِلَّا مَنْ رَحِمَ رَبُّكَۜ وَلِذٰلِكَ خَلَقَهُمْۜ وَتَمَّتْ كَلِمَةُ رَبِّكَ لَاَمْلَـَٔنَّ جَهَنَّمَ مِنَ الْجِنَّةِ وَالنَّاسِ اَجْمَع۪ينَ', 'Ancak Rabbinin merhamet ettikleri müstesnadır. Zaten Rabbin onları bunun için yarattı. Rabbinin, «Andolsun ki cehennemi tümüyle insanlar ve cinlerle dolduracağım» sözü yerini buldu.', 12, 234, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1593, 120, 11, 'Hud', 'وَكُلاًّ نَقُصُّ عَلَيْكَ مِنْ اَنْبَٓاءِ الرُّسُلِ مَا نُثَبِّتُ بِه۪ فُؤٰادَكَۚ وَجَٓاءَكَ ف۪ي هٰذِهِ الْحَقُّ وَمَوْعِظَةٌ وَذِكْرٰى لِلْمُؤْمِن۪ينَ', 'Peygamberlerin haberlerinden senin kalbini (tatmin ve) teskin edeceğimiz her haberi sana anlatıyoruz. Bunda sana gerçeğin bilgisi, müminlere de bir öğüt ve bir uyarı gelmiştir.', 12, 234, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1594, 121, 11, 'Hud', 'وَقُلْ لِلَّذ۪ينَ لَا يُؤْمِنُونَ اعْمَلُوا عَلٰى مَكَانَتِكُمْۜ اِنَّا عَامِلُونَۙ', 'İman etmeyenlere de ki: Elinizden geleni yapın! Biz de (gerekeni) yapmaktayız!', 12, 234, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1595, 122, 11, 'Hud', 'وَانْتَظِرُواۚ اِنَّا مُنْتَظِرُونَ', 'Bekleyin! Şüphesiz biz de beklemekteyiz!', 12, 234, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1596, 123, 11, 'Hud', 'وَلِلّٰهِ غَيْبُ السَّمٰوَاتِ وَالْاَرْضِ وَاِلَيْهِ يُرْجَعُ الْاَمْرُ كُلُّهُ فَاعْبُدْهُ وَتَوَكَّلْ عَلَيْهِۜ وَمَا رَبُّكَ بِغَافِلٍ عَمَّا تَعْمَلُونَ', 'Göklerin ve yerin gaybı (sırrı) yalnız Allah´a aittir. Her iş O´na döndürülür. Öyle ise O´na kulluk et ve O´na dayan! Rabbin yaptıklarınızdan gafil değildir.', 12, 234, 123, 7633, 'Peygamber Hud', 'Mekke', 52),
(1597, 1, 12, 'Yusuf', 'الٓـرٰ۠ تِلْكَ اٰيَاتُ الْـكِتَابِ الْمُب۪ينِ۠', 'Elif. Lâm. Râ. Bunlar, apaçık Kitab´ın âyetleridir.', 12, 234, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1598, 2, 12, 'Yusuf', 'اِنَّٓا اَنْزَلْنَاهُ قُرْءٰناً عَرَبِياًّ لَعَلَّـكُمْ تَعْقِلُونَ', 'Anlayasınız diye biz onu Arapça bir Kur´an olarak indirdik.', 12, 234, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1599, 3, 12, 'Yusuf', 'نَحْنُ نَقُصُّ عَلَيْكَ اَحْسَنَ الْقَصَصِ بِمَٓا اَوْحَيْنَٓا اِلَيْكَ هٰذَا الْقُرْاٰنَۗ وَاِنْ كُنْتَ مِنْ قَبْلِه۪ لَمِنَ الْغَافِل۪ينَ', '(Ey Muhammed!) Biz, sana bu Kur´an´ı vahyetmekle geçmiş milletlerin haberlerini sana en güzel bir şekilde anlatıyoruz. Gerçek şu ki, sen bundan önce (bu haberleri) elbette bilmeyenlerden idin.', 12, 234, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1600, 4, 12, 'Yusuf', 'اِذْ قَالَ يُوسُفُ لِاَب۪يهِ يَٓا اَبَتِ اِنّ۪ي رَاَيْتُ اَحَدَ عَشَرَ كَوْكَباً وَالشَّمْسَ وَالْقَمَرَ رَاَيْتُهُمْ ل۪ي سَاجِد۪ينَ', 'Bir zamanlar Yusuf, babasına (Ya´kub´a) demişti ki: Babacığım! Ben (rüyamda) on bir yıldızla güneşi ve ayı gördüm; onları bana secde ederlerken gördüm.', 12, 234, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1601, 5, 12, 'Yusuf', 'قَالَ يَا بُنَيَّ لَا تَقْصُصْ رُءْيَاكَ عَلٰٓى اِخْوَتِكَ فَيَك۪يدُوا لَكَ كَيْداًۜ اِنَّ الشَّيْطَانَ لِلْاِنْسَانِ عَدُوٌّ مُب۪ينٌ', '(Babası:) Yavrucuğum! dedi, rüyanı sakın kardeşlerine anlatma; sonra sana bir tuzak kurarlar! Çünkü şeytan insana apaçık bir düşmandır.', 12, 235, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1602, 6, 12, 'Yusuf', 'وَكَذٰلِكَ يَجْتَب۪يكَ رَبُّكَ وَيُعَلِّمُكَ مِنْ تَأْو۪يلِ الْاَحَاد۪يثِ وَيُتِمُّ نِعْمَتَهُ عَلَيْكَ وَعَلٰٓى اٰلِ يَعْقُوبَ كَمَٓا اَتَمَّهَا عَلٰٓى اَبَوَيْكَ مِنْ قَبْلُ اِبْرٰه۪يمَ وَاِسْحٰقَۜ اِنَّ رَبَّكَ عَل۪يمٌ حَك۪يمٌ۟', 'İşte böylece Rabbin seni seçecek, sana (rüyada görülen) olayların yorumunu öğretecek ve daha önce iki atan İbrahim ve İshak´a nimetini tamamladığı gibi sana ve Ya´kub soyuna da nimetini tamamlayacaktır. Çünkü Rabbin çok iyi bilendir, hikmet sahibidir.', 12, 235, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1603, 7, 12, 'Yusuf', 'لَقَدْ كَانَ ف۪ي يُوسُفَ وَاِخْوَتِه۪ٓ اٰيَاتٌ لِلسَّٓائِل۪ينَ', 'Andolsun ki Yusuf ve kardeşlerinde, (almak) isteyenler için ibretler vardır.', 12, 235, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1604, 8, 12, 'Yusuf', 'اِذْ قَالُوا لَيُوسُفُ وَاَخُوهُ اَحَبُّ اِلٰٓى اَب۪ينَا مِنَّا وَنَحْنُ عُصْبَةٌۜ اِنَّ اَبَانَا لَف۪ي ضَلَالٍ مُب۪ينٍۚ', '(Kardeşleri) dediler ki: Yusuf´la kardeşi (Bünyamin) babamıza bizden daha sevgilidir. Halbuki biz kalabalık bir cemaatiz. Şüphesiz ki babamız apaçık bir yanlışlık içindedir.', 12, 235, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1605, 9, 12, 'Yusuf', 'اُقْتُلُوا يُوسُفَ اَوِ اطْرَحُوهُ اَرْضاً يَخْلُ لَكُمْ وَجْهُ اَب۪يكُمْ وَتَكُونُوا مِنْ بَعْدِه۪ قَوْماً صَالِح۪ينَ', '(Aralarında dediler ki:) Yusuf´u öldürün veya onu (uzak) bir yere atın ki babanızın teveccühü yalnız size kalsın! Ondan sonra da (tevbe ederek) sâlih kimseler olursunuz!', 12, 235, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1606, 10, 12, 'Yusuf', 'قَالَ قَٓائِلٌ مِنْهُمْ لَا تَقْتُلُوا يُوسُفَ وَاَلْقُوهُ ف۪ي غَيَابَتِ الْجُبِّ يَلْتَقِطْهُ بَعْضُ السَّيَّارَةِ اِنْ كُنْتُمْ فَاعِل۪ينَ', 'Onlardan biri: Yusuf´u öldürmeyin, eğer mutlaka yapacaksanız onu kuyunun dibine atın da geçen kervanlardan biri onu alsın (götürsün), dedi.', 12, 235, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1607, 11, 12, 'Yusuf', 'قَالُوا يَٓا اَبَانَا مَا لَكَ لَا تَأْمَنَّۭۖا عَلٰى يُوسُفَ وَاِنَّا لَهُ لَنَاصِحُونَ', 'Dediler ki: «Ey babamız! Sana ne oluyor da Yusuf hakkında bize güvenmiyorsun! Oysa ki biz onun iyiliğini istemekteyiz.', 12, 235, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1608, 12, 12, 'Yusuf', 'اَرْسِلْهُ مَعَنَا غَداً يَرْتَعْ وَيَلْعَبْ وَاِنَّا لَهُ لَحَافِظُونَ', 'Yarın onu bizimle beraber (kıra) gönder de bol bol yesin (içsin), oynasın. Biz onu mutlaka koruruz.»', 12, 235, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1609, 13, 12, 'Yusuf', 'قَالَ اِنّ۪ي لَيَحْزُنُن۪ٓي اَنْ تَذْهَبُوا بِه۪ وَاَخَافُ اَنْ يَأْكُلَهُ الذِّئْبُ وَاَنْتُمْ عَنْهُ غَافِلُونَ', '(Babaları) dedi ki: Onu götürmeniz beni mutlaka üzer. Siz ondan habersizken onu bir kurdun yemesinden korkarım.', 12, 235, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1610, 14, 12, 'Yusuf', 'قَالُوا لَئِنْ اَكَلَهُ الذِّئْبُ وَنَحْنُ عُصْبَةٌ اِنَّٓا اِذاً لَخَاسِرُونَ', 'Dediler ki: Hakikaten biz (kuvvetli) bir topluluk olduğumuz halde, eğer onu kurt yerse, o zaman biz gerçekten âciz kimseler sayılırız.', 12, 235, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1611, 15, 12, 'Yusuf', 'فَلَمَّا ذَهَبُوا بِه۪ وَاَجْمَعُٓوا اَنْ يَجْعَلُوهُ ف۪ي غَيَابَتِ الْجُبِّۚ وَاَوْحَيْنَٓا اِلَيْهِ لَتُنَبِّئَنَّهُمْ بِاَمْرِهِمْ هٰذَا وَهُمْ لَا يَشْعُرُونَ', 'Onu götürüp de kuyunun dibine atmaya ittifakla karar verdikleri zaman, biz Yusuf´a: Andolsun ki sen onların bu işlerini onlar (işin) farkına varmadan, kendilerine haber vereceksin, diye vahyettik.', 12, 236, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1612, 16, 12, 'Yusuf', 'وَجَٓاؤُٓ۫ اَبَاهُمْ عِشَٓاءً يَبْكُونَۜ', 'Akşamleyin ağlayarak babalarına geldiler.', 12, 236, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1613, 17, 12, 'Yusuf', 'قَالُوا يَٓا اَبَانَٓا اِنَّا ذَهَبْنَا نَسْتَبِقُ وَتَرَكْنَا يُوسُفَ عِنْدَ مَتَاعِنَا فَاَكَلَهُ الذِّئْبُۚ وَمَٓا اَنْتَ بِمُؤْمِنٍ لَنَا وَلَوْ كُنَّا صَادِق۪ينَ', 'Ey babamız! dediler, biz yarışmak üzere uzaklaştık; Yusuf´u eşyamızın yanında bırakmıştık. (Ne yazık ki) onu kurt yemiş! Fakat biz doğru söyleyenler olsak da sen bize inanmazsın.', 12, 236, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1614, 18, 12, 'Yusuf', 'وَجَٓاؤُ۫ عَلٰى قَم۪يصِه۪ بِدَمٍ كَذِبٍۜ قَالَ بَلْ سَوَّلَتْ لَكُمْ اَنْفُسُكُمْ اَمْراًۜ فَصَبْرٌ جَم۪يلٌۜ وَاللّٰهُ الْمُسْتَعَانُ عَلٰى مَا تَصِفُونَ', 'Gömleğinin üstünde sahte bir kan ile geldiler. (Ya´kub) dedi ki: Bilakis nefisleriniz size (kötü) bir işi güzel gösterdi. Artık (bana düşen) hakkıyla sabretmektir. Anlattığınız karşısında (bana) yardım edecek olan, ancak Allah´tır.', 12, 236, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1615, 19, 12, 'Yusuf', 'وَجَٓاءَتْ سَيَّارَةٌ فَاَرْسَلُوا وَارِدَهُمْ فَاَدْلٰى دَلْوَهُۜ قَالَ يَا بُشْرٰى هٰذَا غُلَامٌۜ وَاَسَرُّوهُ بِضَاعَةًۜ وَاللّٰهُ عَل۪يمٌ بِمَا يَعْمَلُونَ', 'Bir kervan geldi ve sucularını (kuyuya) gönderdiler, o da (gidip) kovasını saldı, (Yusuf´u görünce) «Müjde! İşte bir oğlan!» dedi. Onu bir ticaret malı olarak sakladılar. Allah onların yaptıklarını çok iyi bilir.', 12, 236, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1616, 20, 12, 'Yusuf', 'وَشَرَوْهُ بِثَمَنٍ بَخْسٍ دَرَاهِمَ مَعْدُودَةٍۚ وَكَانُوا ف۪يهِ مِنَ الزَّاهِد۪ينَ۟', '(Kafile Mısır´a vardığında) onu değersiz bir pahaya, sayılı birkaç dirheme sattılar. Onlar zaten ona değer vermemişlerdi.', 12, 236, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1617, 21, 12, 'Yusuf', 'وَقَالَ الَّذِي اشْتَرٰيهُ مِنْ مِصْرَ لِامْرَاَتِه۪ٓ اَكْرِم۪ي مَثْوٰيهُ عَسٰٓى اَنْ يَنْفَعَنَٓا اَوْ نَتَّخِذَهُ وَلَداًۜ وَكَذٰلِكَ مَكَّنَّا لِيُوسُفَ فِي الْاَرْضِۘ وَلِنُعَلِّمَهُ مِنْ تَأْو۪يلِ الْاَحَاد۪يثِۜ وَاللّٰهُ غَالِبٌ عَلٰٓى اَمْرِه۪ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُونَ', 'Mısır´da onu satın alan adam, karısına dedi ki: «Ona değer ver ve güzel bak! Umulur ki bize faydası olur. Veya onu evlât ediniriz.» İşte böylece (Mısır´da adaletle hükmetmesi) ve kendisine (rüyadaki) olayların yorumunu öğretmemiz için Yusuf´u o yere yerleştirdik. Allah, emrini yerine getirmeye kadirdir. Fakat insanların çoğu (bunu) bilmezler.', 12, 236, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1618, 22, 12, 'Yusuf', 'وَلَمَّا بَلَغَ اَشُدَّهُٓ اٰتَيْنَاهُ حُكْماً وَعِلْماًۜ وَكَذٰلِكَ نَجْزِي الْمُحْسِن۪ينَ', '(Yusuf) erginlik çağına erişince, ona (isabetle) hükmetme (yeteneği) ve ilim verdik. İşte güzel davrananları biz böyle mükâfatlandırırız.', 12, 236, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1619, 23, 12, 'Yusuf', 'وَرَاوَدَتْهُ الَّت۪ي هُوَ ف۪ي بَيْتِهَا عَنْ نَفْسِه۪ وَغَلَّقَتِ الْاَبْوَابَ وَقَالَتْ هَيْتَ لَكَۜ قَالَ مَعَاذَ اللّٰهِ اِنَّهُ رَبّ۪ٓي اَحْسَنَ مَثْوَايَۜ اِنَّهُ لَا يُفْلِحُ الظَّالِمُونَ', 'Evinde bulunduğu kadın, onun nefsinden murat almak istedi, kapıları iyice kapattı ve «Haydi gel!» dedi. O da «(Hâşâ), Allah´a sığınırım! Zira kocanız benim velinimetimdir, bana güzel davrandı. Gerçek şu ki, zalimler iflah olmaz!» dedi.', 12, 237, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1620, 24, 12, 'Yusuf', 'وَلَقَدْ هَمَّتْ بِه۪ۗ وَهَمَّ بِهَاۚ لَوْلَٓا اَنْ رَاٰ بُرْهَانَ رَبِّه۪ۜ كَذٰلِكَ لِنَصْرِفَ عَنْهُ السُّٓوءَ وَالْفَحْشَٓاءَۜ اِنَّهُ مِنْ عِبَادِنَا الْمُخْلَص۪ينَ', 'Andolsun ki, kadın ona meyletti. Eğer Rabbinin işaret ve ikazını görmeseydi o da kadına meyletmişti. İşte böylece biz, kötülük ve fuhşu ondan uzaklaştırmak için (delilimizi gösterdik). Şüphesiz o ihlâslı kullarımızdandı.', 12, 237, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1621, 25, 12, 'Yusuf', 'وَاسْتَبَقَا الْبَابَ وَقَدَّتْ قَم۪يصَهُ مِنْ دُبُرٍ وَاَلْفَيَا سَيِّدَهَا لَدَا الْبَابِۜ قَالَتْ مَا جَزَٓاءُ مَنْ اَرَادَ بِاَهْلِكَ سُٓوءاً اِلَّٓا اَنْ يُسْجَنَ اَوْ عَذَابٌ اَل۪يمٌ', 'İkisi de kapıya doğru koştular. Kadın onun gömleğini arkadan yırttı. Kapının yanında onun kocasına rastladılar. Kadın dedi ki: Senin ailene kötülük etmek isteyenin cezası, zindana atılmaktan veya elem verici bir işkenceden başka ne olabilir!', 12, 237, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1622, 26, 12, 'Yusuf', 'قَالَ هِيَ رَاوَدَتْن۪ي عَنْ نَفْس۪ي وَشَهِدَ شَاهِدٌ مِنْ اَهْلِهَاۚ اِنْ كَانَ قَم۪يصُهُ قُدَّ مِنْ قُبُلٍ فَصَدَقَتْ وَهُوَ مِنَ الْكَاذِب۪ينَ', 'Yusuf: «Asıl kendisi benim nefsimden murat almak istedi» dedi. Kadının akrabasından biri şöyle şahitlik etti: «Eğer gömleği önden yırtılmışsa, kadın doğru söylemiştir, bu ise yalancılardandır.»', 12, 237, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1623, 27, 12, 'Yusuf', 'وَاِنْ كَانَ قَم۪يصُهُ قُدَّ مِنْ دُبُرٍ فَكَذَبَتْ وَهُوَ مِنَ الصَّادِق۪ينَ', '«Eğer gömleği arkadan yırtılmışsa, kadın yalan söylemiştir. Bu ise doğru söyleyenlerdendir.»', 12, 237, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1624, 28, 12, 'Yusuf', 'فَلَمَّا رَاٰ قَم۪يصَهُ قُدَّ مِنْ دُبُرٍ قَالَ اِنَّهُ مِنْ كَيْدِكُنَّۜ اِنَّ كَيْدَكُنَّ عَظ۪يمٌ', '(Kocası, Yusuf´un gömleğinin) arkadan yırtılmış olduğunu görünce, (kadına): «Şüphesiz, dedi; bu, sizin tuzağınızdır. Sizin tuzağınız gerçekten büyüktür.»', 12, 237, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1625, 29, 12, 'Yusuf', 'يُوسُفُ اَعْرِضْ عَنْ هٰذَا وَاسْتَغْفِر۪ي لِذَنْبِكِۚ اِنَّكِ كُنْتِ مِنَ الْخَاطِـ۪ٔينَ۟', '«Ey Yusuf! Sen bundan (olanları söylemekten) vazgeç! (Ey kadın!) Sen de günahının affını dile! Çünkü sen günahkârlardan oldun»', 12, 237, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1626, 30, 12, 'Yusuf', 'وَقَالَ نِسْوَةٌ فِي الْمَد۪ينَةِ امْرَاَتُ الْعَز۪يزِ تُرَاوِدُ فَتٰيهَا عَنْ نَفْسِه۪ۚ قَدْ شَغَفَهَا حُباًّۜ اِنَّا لَنَرٰيهَا ف۪ي ضَلَالٍ مُب۪ينٍ', 'Şehirdeki bazı kadınlar dediler ki: Azizin karısı, delikanlısının nefsinden murat almak istiyormuş; Yusuf´un sevdası onun kalbine işlemiş! Biz onu gerçekten açık bir sapıklık içinde görüyoruz.', 12, 237, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1627, 31, 12, 'Yusuf', 'فَلَمَّا سَمِعَتْ بِمَكْرِهِنَّ اَرْسَلَتْ اِلَيْهِنَّ وَاَعْتَدَتْ لَهُنَّ مُتَّكَـٔاً وَاٰتَتْ كُلَّ وَاحِدَةٍ مِنْهُنَّ سِكّ۪يناً وَقَالَتِ اخْرُجْ عَلَيْهِنَّۚ فَلَمَّا رَاَيْنَهُٓ اَكْبَرْنَهُ وَقَطَّعْنَ اَيْدِيَهُنَّ وَقُلْنَ حَاشَ لِلّٰهِ مَا هٰذَا بَشَراًۜ اِنْ هٰذَٓا اِلَّا مَلَكٌ كَر۪يمٌ', 'Kadın, onların dedikodusunu duyunca, onlara dâvetçi gönderdi; onlar için dayanacak yastıklar hazırladı. Onlardan herbirine bir bıçak verdi. (Kadınlar meyveleri soyarken Yusuf´a): «Çık karşılarına!» dedi. Kadınlar onu görünce, onun büyüklüğünü anladılar. (Şaşkınlıklarından) ellerini kestiler ve dediler ki: Hâşâ Rabbimiz! Bu bir beşer değil... Bu ancak üstün bir melektir!', 12, 238, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1628, 32, 12, 'Yusuf', 'قَالَتْ فَذٰلِكُنَّ الَّذ۪ي لُمْتُنَّن۪ي ف۪يهِۜ وَلَقَدْ رَاوَدْتُهُ عَنْ نَفْسِه۪ فَاسْتَعْصَمَۜ وَلَئِنْ لَمْ يَفْعَلْ مَٓا اٰمُرُهُ لَيُسْجَنَنَّ وَلَيَكُوناً مِنَ الصَّاغِر۪ينَ', 'Kadın dedi ki: İşte hakkında beni kınadığınız şahıs budur. Ben onun nefsinden murat almak istedim. Fakat o, (bundan) şiddetle sakındı. Andolsun, eğer o kendisine emredeceğimi yapmazsa mutlaka zindana atılacak ve elbette sürünenlerden olacaktır!', 12, 238, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1629, 33, 12, 'Yusuf', 'قَالَ رَبِّ السِّجْنُ اَحَبُّ اِلَيَّ مِمَّا يَدْعُونَن۪ٓي اِلَيْهِۚ وَاِلَّا تَصْرِفْ عَنّ۪ي كَيْدَهُنَّ اَصْبُ اِلَيْهِنَّ وَاَكُنْ مِنَ الْجَاهِل۪ينَ', '(Yusuf:) Rabbim! Bana zindan, bunların benden istediklerinden daha iyidir! Eğer onların hilelerini benden çevirmezsen, onlara meyleder ve cahillerden olurum! dedi.', 12, 238, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1630, 34, 12, 'Yusuf', 'فَاسْتَجَابَ لَهُ رَبُّهُ فَصَرَفَ عَنْهُ كَيْدَهُنَّۜ اِنَّهُ هُوَ السَّم۪يعُ الْعَل۪يمُ', 'Rabbi onun duasını kabul etti ve hilelerini ondan uzaklaştırdı. Çünkü O çok iyi işiten, pek iyi bilendir.', 12, 238, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1631, 35, 12, 'Yusuf', 'ثُمَّ بَدَا لَهُمْ مِنْ بَعْدِ مَا رَاَوُا الْاٰيَاتِ لَيَسْجُنُنَّهُ حَتّٰى ح۪ينٍ۟', 'Sonunda (aziz ve arkadaşları) kesin delilleri görmelerine rağmen (halkın dedikodusunu kesmek için yine de) onu bir zamana kadar mutlaka zindana atmaları kendilerine uygun göründü.', 12, 238, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1632, 36, 12, 'Yusuf', 'وَدَخَلَ مَعَهُ السِّجْنَ فَتَيَانِۜ قَالَ اَحَدُهُمَٓا اِنّ۪ٓي اَرٰين۪ٓي اَعْصِرُ خَمْراًۚ وَقَالَ الْاٰخَرُ اِنّ۪ٓي اَرٰين۪ٓي اَحْمِلُ فَوْقَ رَأْس۪ي خُبْزاً تَأْكُلُ الطَّيْرُ مِنْهُۜ نَبِّئْنَا بِتَأْو۪يلِه۪ۚ اِنَّا نَرٰيكَ مِنَ الْمُحْسِن۪ينَ', 'Onunla birlikte zindana iki delikanlı daha girdi. Onlardan biri dedi ki: Ben (rüyada) şarap sıktığımı gördüm. Diğeri de: Ben de başımın üstünde kuşların yemekte olduğu bir ekmek taşıdığımı gördüm. Bunun yorumunu bize haber ver. Çünkü biz seni güzel davrananlardan görüyoruz, dedi.', 12, 238, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1633, 37, 12, 'Yusuf', 'قَالَ لَا يَأْت۪يكُمَا طَعَامٌ تُرْزَقَانِه۪ٓ اِلَّا نَبَّأْتُكُمَا بِتَأْو۪يلِه۪ قَبْلَ اَنْ يَأْتِيَكُمَاۜ ذٰلِكُمَا مِمَّا عَلَّمَن۪ي رَبّ۪يۜ اِنّ۪ي تَرَكْتُ مِلَّةَ قَوْمٍ لَا يُؤْمِنُونَ بِاللّٰهِ وَهُمْ بِالْاٰخِرَةِ هُمْ كَافِرُونَۙ', '(Yusuf) dedi ki: Size yedirilecek yemek gelmeden önce onun yorumunu mutlaka size haber vereceğim. Bu, Rabbimin bana öğrettiklerindendir. Şüphesiz ben Allah´a inanmayan bir kavmin dininden uzaklaştım. Onlar ahireti inkâr edenlerin kendileridir.', 12, 238, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1634, 38, 12, 'Yusuf', 'وَاتَّبَعْتُ مِلَّةَ اٰبَٓاء۪ٓي اِبْرٰه۪يمَ وَاِسْحٰقَ وَيَعْقُوبَۜ مَا كَانَ لَـنَٓا اَنْ نُشْرِكَ بِاللّٰهِ مِنْ شَيْءٍۜ ذٰلِكَ مِنْ فَضْلِ اللّٰهِ عَلَيْنَا وَعَلَى النَّاسِ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَشْكُرُونَ', 'Atalarım İbrahim, İshak ve Ya´kub´un dinine uydum. Allah´a herhangi bir şeyi ortak koşmamız bize yaraşmaz. Bu, Allah´ın bize ve insanlara olan lütfundandır. Fakat insanların çoğu şükretmezler.', 12, 239, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1635, 39, 12, 'Yusuf', 'يَا صَاحِبَيِ السِّجْنِ ءَاَرْبَابٌ مُتَفَرِّقُونَ خَيْرٌ اَمِ اللّٰهُ الْوَاحِدُ الْقَهَّارُۜ', 'Ey zindan arkadaşlarım! Çeşitli tanrılar mı daha iyi, yoksa gücüne karşı durulamaz olan bir tek Allah mı?', 12, 239, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1636, 40, 12, 'Yusuf', 'مَا تَعْبُدُونَ مِنْ دُونِه۪ٓ اِلَّٓا اَسْمَٓاءً سَمَّيْتُمُوهَٓا اَنْتُمْ وَاٰبَٓاؤُ۬كُمْ مَٓا اَنْزَلَ اللّٰهُ بِهَا مِنْ سُلْطَانٍۜ اِنِ الْحُكْمُ اِلَّا لِلّٰهِۜ اَمَرَ اَلَّا تَعْبُدُٓوا اِلَّٓا اِيَّاهُۜ ذٰلِكَ الدّ۪ينُ الْقَيِّمُ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُونَ', 'Allah´ı bırakıp da taptıklarınız, sizin ve atalarınızın taktığı birtakım isimlerden başka bir şey değildir. Allah onlar hakkında herhangi bir delil indirmemiştir. Hüküm sadece Allah´a aittir. O size kendisinden başkasına ibadet etmemenizi emretmiştir. İşte dosdoğru din budur. Fakat insanların çoğu bilmezler.', 12, 239, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1637, 41, 12, 'Yusuf', 'يَا صَاحِبَيِ السِّجْنِ اَمَّٓا اَحَدُكُمَا فَيَسْق۪ي رَبَّهُ خَمْراًۚ وَاَمَّا الْاٰخَرُ فَيُصْلَبُ فَتَأْكُلُ الطَّيْرُ مِنْ رَأْسِه۪ۜ قُضِيَ الْاَمْرُ الَّذ۪ي ف۪يهِ تَسْتَفْتِيَانِۜ', 'Ey zindan arkadaşlarım! (Rüyalarınıza gelince), biriniz (daha önce olduğu gibi) efendisine şarap içirecek; diğeri ise asılacak ve kuşlar onun başından (beynini) yiyecekler. Yorumunu sorduğunuz iş (bu şekilde) kesinleşmiştir.', 12, 239, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1638, 42, 12, 'Yusuf', 'وَقَالَ لِلَّذ۪ي ظَنَّ اَنَّهُ نَاجٍ مِنْهُمَا اذْكُرْن۪ي عِنْدَ رَبِّكَۘ فَاَنْسٰيهُ الشَّيْطَانُ ذِكْرَ رَبِّه۪ فَلَبِثَ فِي السِّجْنِ بِضْعَ سِن۪ينَۜ ۟', 'Onlardan, kurtulacağını bildiği kimseye dedi ki: Beni efendinin yanında an, (umulur ki beni çıkarır). Fakat şeytan ona, efendisine anmayı unutturdu. Dolayısıyla (Yusuf), birkaç sene daha zindanda kaldı.', 12, 239, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1639, 43, 12, 'Yusuf', 'وَقَالَ الْمَلِكُ اِنّ۪ٓي اَرٰى سَبْعَ بَقَرَاتٍ سِمَانٍ يَأْكُلُهُنَّ سَبْعٌ عِجَافٌ وَسَبْعَ سُنْبُلَاتٍ خُضْرٍ وَاُخَرَ يَابِسَاتٍۜ يَٓا اَيُّهَا الْمَلَأُ اَفْتُون۪ي ف۪ي رُءْيَايَ اِنْ كُنْتُمْ لِلرُّءْيَا تَعْبُرُونَ', 'Kral dedi ki: Ben (rüyada) yedi arık ineğin yediği yedi semiz inek gördüm. Ayrıca, yedi yeşil başak ve diğerlerini de kuru gördüm. Ey ileri gelenler! Eğer rüya yorumluyorsanız, benim rüyamı da bana yorumlayınız.', 12, 239, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1640, 44, 12, 'Yusuf', 'قَالُٓوا اَضْغَاثُ اَحْلَامٍۚ وَمَا نَحْنُ بِتَأْو۪يلِ الْاَحْلَامِ بِعَالِم۪ينَ', '(Yorumcular) dediler ki: Bunlar karmakarışık düşlerdir. Biz böyle düşlerin yorumunu bilenlerden değiliz.', 12, 240, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1641, 45, 12, 'Yusuf', 'وَقَالَ الَّذ۪ي نَجَا مِنْهُمَا وَادَّكَرَ بَعْدَ اُمَّةٍ اَنَا۬ اُنَبِّئُكُمْ بِتَأْو۪يلِه۪ فَاَرْسِلُونِ', '(Zindandaki) iki kişiden kurtulmuş olan, uzun bir zaman sonra (Yusuf´u) hatırlayarak dedi ki: Ben size onun yorumunu haber veririm, beni hemen (zindana) gönderin.', 12, 240, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1642, 46, 12, 'Yusuf', 'يُوسُفُ اَيُّهَا الصِّدّ۪يقُ اَفْتِنَا ف۪ي سَبْعِ بَقَرَاتٍ سِمَانٍ يَأْكُلُهُنَّ سَبْعٌ عِجَافٌ وَسَبْعِ سُنْبُلَاتٍ خُضْرٍ وَاُخَرَ يَابِسَاتٍۙ لَعَلّ۪ٓي اَرْجِعُ اِلَى النَّاسِ لَعَلَّهُمْ يَعْلَمُونَ', '(Yusuf´un yanına gelerek dedi ki:) Ey Yusuf, ey doğru sözlü kişi! (Rüyada görülen) yedi arık ineğin yediği yedi semiz inek ile yedi yeşil başak ve diğerleri de kuru olan (başaklar) hakkında bize yorum yap. Ümit ederim ki, insanlara (isabetli yorumunla) dönerim de belki onlar da doğruyu öğrenirler.', 12, 240, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1643, 47, 12, 'Yusuf', 'قَالَ تَزْرَعُونَ سَبْعَ سِن۪ينَ دَاَباًۚ فَمَا حَصَدْتُمْ فَذَرُوهُ ف۪ي سُنْبُلِه۪ٓ اِلَّا قَل۪يلاً مِمَّا تَأْكُلُونَ', 'Yusuf dedi ki: Yedi sene âdetiniz üzere ekin ekersiniz. Sonra da yiyeceklerinizden az bir miktar hariç, biçtiklerinizi başağında (stok edip) bırakınız.', 12, 240, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1644, 48, 12, 'Yusuf', 'ثُمَّ يَأْت۪ي مِنْ بَعْدِ ذٰلِكَ سَبْعٌ شِدَادٌ يَأْكُلْنَ مَا قَدَّمْتُمْ لَهُنَّ اِلَّا قَل۪يلاً مِمَّا تُحْصِنُونَ', 'Sonra bunun ardından, saklayacaklarınızdan az bir miktar (tohumluk) hariç, o yıllar için biriktirdiklerinizi yeyip bitirecek yedi kıtlık yılı gelecektir.', 12, 240, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1645, 49, 12, 'Yusuf', 'ثُمَّ يَأْت۪ي مِنْ بَعْدِ ذٰلِكَ عَامٌ ف۪يهِ يُغَاثُ النَّاسُ وَف۪يهِ يَعْصِرُونَ۟', 'Sonra bunun ardından da bir yıl gelecek ki, o yılda insanlara (Allah tarafından) yardım olunacak ve o yılda (meyvesuyu ve yağ) sıkacaklar.', 12, 240, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1646, 50, 12, 'Yusuf', 'وَقَالَ الْمَلِكُ ائْتُون۪ي بِه۪ۚ فَلَمَّا جَٓاءَهُ الرَّسُولُ قَالَ ارْجِعْ اِلٰى رَبِّكَ فَسْـَٔلْهُ مَا بَالُ النِّسْوَةِ الّٰت۪ي قَطَّعْنَ اَيْدِيَهُنَّۜ اِنَّ رَبّ۪ي بِكَيْدِهِنَّ عَل۪يمٌ', '(Adam bu yorumu getirince) kral dedi ki: «Onu bana getirin!» Elçi, Yusuf´a geldiği zaman, (Yusuf) dedi ki: «Efendine dön de ona: Ellerini kesen o kadınların zoru neydi? diye sor. Şüphesiz benim Rabbim onların hilesini çok iyi bilir.»', 12, 240, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1647, 51, 12, 'Yusuf', 'قَالَ مَا خَطْبُكُنَّ اِذْ رَاوَدْتُنَّ يُوسُفَ عَنْ نَفْسِه۪ۜ قُلْنَ حَاشَ لِلّٰهِ مَا عَلِمْنَا عَلَيْهِ مِنْ سُٓوءٍۜ قَالَتِ امْرَاَتُ الْعَز۪يزِ الْـٰٔنَ حَصْحَصَ الْحَقُّۘ اَنَا۬ رَاوَدْتُهُ عَنْ نَفْسِه۪ وَاِنَّهُ لَمِنَ الصَّادِق۪ينَ', '(Kral kadınlara) dedi ki: Yusuf´un nefsinden murat almak istediğiniz zaman durumunuz neydi? Kadınlar, Hâşâ! Allah için, biz ondan hiçbir kötülük görmedik, dediler. Azizin karısı da dedi ki: «Şimdi gerçek ortaya çıktı. Ben onun nefsinden murat almak istemiştim. Şüphesiz ki o doğru söyleyenlerdendir.»', 12, 240, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1648, 52, 12, 'Yusuf', 'ذٰلِكَ لِيَعْلَمَ اَنّ۪ي لَمْ اَخُنْهُ بِالْغَيْبِ وَاَنَّ اللّٰهَ لَا يَهْد۪ي كَيْدَ الْخَٓائِن۪ينَ', '(Yusuf dedi ki): Bu, azizin yokluğunda ona hainlik etmediğimi ve Allah´ın hainlerin hilesini başarıya ulaştırmayacağını (herkesin) bilmesi içindir.', 12, 240, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1649, 53, 12, 'Yusuf', 'وَمَٓا اُبَرِّئُ نَفْس۪يۚ اِنَّ النَّفْسَ لَاَمَّارَةٌ بِالسُّٓوءِ اِلَّا مَا رَحِمَ رَبّ۪يۜ اِنَّ رَبّ۪ي غَفُورٌ رَح۪يمٌ', '(Bununla beraber) nefsimi temize çıkarmıyorum. Çünkü nefis aşırı şekilde kötülüğü emreder; Rabbim acıyıp korumuş başka. Şüphesiz Rabbim çok bağışlayan, pek esirgeyendir.', 13, 241, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1650, 54, 12, 'Yusuf', 'وَقَالَ الْمَلِكُ ائْتُون۪ي بِه۪ٓ اَسْتَخْلِصْهُ لِنَفْس۪يۚ فَلَمَّا كَلَّمَهُ قَالَ اِنَّكَ الْيَوْمَ لَدَيْنَا مَك۪ينٌ اَم۪ينٌ', 'Kral dedi ki: Onu bana getirin, onu kendime özel danışman edineyim. Onunla konuşunca: Bugün sen yanımızda yüksek makam sahibi ve güvenilir birisin, dedi.', 13, 241, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1651, 55, 12, 'Yusuf', 'قَالَ اجْعَلْن۪ي عَلٰى خَزَٓائِنِ الْاَرْضِۚ اِنّ۪ي حَف۪يظٌ عَل۪يمٌ', '«Beni ülkenin hazinelerine tayin et! Çünkü ben (onları) çok iyi korurum ve bu işi bilirim» dedi.', 13, 241, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1652, 56, 12, 'Yusuf', 'وَكَذٰلِكَ مَكَّنَّا لِيُوسُفَ فِي الْاَرْضِۚ يَتَبَوَّاُ مِنْهَا حَيْثُ يَشَٓاءُۜ نُص۪يبُ بِرَحْمَتِنَا مَنْ نَشَٓاءُ وَلَا نُض۪يعُ اَجْرَ الْمُحْسِن۪ينَ', 'Ve böylece Yusuf´a orada dilediği gibi hareket etmek üzere ülke içinde yetki verdik. Biz dilediğimiz kimseye rahmetimizi eriştiririz. Ve güzel davrananların mükâfatını zayi etmeyiz.', 13, 241, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1653, 57, 12, 'Yusuf', 'وَلَاَجْرُ الْاٰخِرَةِ خَيْرٌ لِلَّذ۪ينَ اٰمَنُوا وَكَانُوا يَتَّقُونَ۟', 'İman edip de (kötülüklerden) sakınanlar için ahiret mükâfatı daha hayırlıdır.', 13, 241, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1654, 58, 12, 'Yusuf', 'وَجَٓاءَ اِخْوَةُ يُوسُفَ فَدَخَلُوا عَلَيْهِ فَعَرَفَهُمْ وَهُمْ لَهُ مُنْكِرُونَ', 'Yusuf´un kardeşleri gelip onun huzuruna girdiler, (Yusuf) onları tanıdı, onlar onu tanımıyorlardı.', 13, 241, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1655, 59, 12, 'Yusuf', 'وَلَمَّا جَهَّزَهُمْ بِجَهَازِهِمْ قَالَ ائْتُون۪ي بِاَخٍ لَكُمْ مِنْ اَب۪يكُمْۚ اَلَا تَرَوْنَ اَنّ۪ٓي اُو۫فِي الْكَيْلَ وَاَنَا۬ خَيْرُ الْمُنْزِل۪ينَ', '(Yusuf) onların yüklerini hazırlayınca dedi ki: «Sizin bababir kardeşinizi de bana getirin. Görmüyor musunuz, ben ölçeği tam dolduruyorum ve ben misafirperverlerin en iyisiyim.', 13, 241, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1656, 60, 12, 'Yusuf', 'فَاِنْ لَمْ تَأْتُون۪ي بِه۪ فَلَا كَيْلَ لَكُمْ عِنْد۪ي وَلَا تَقْرَبُونِ', 'Eğer onu bana getirmezseniz, artık benim yanımda size verilecek bir ölçek (erzak) yoktur, bana hiç yaklaşmayın!»', 13, 241, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1657, 61, 12, 'Yusuf', 'قَالُوا سَنُرَاوِدُ عَنْهُ اَبَاهُ وَاِنَّا لَفَاعِلُونَ', 'Dediler ki: Onu babasından istemeye çalışacağız, kuşkusuz bunu yapacağız.', 13, 241, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1658, 62, 12, 'Yusuf', 'وَقَالَ لِفِتْيَانِهِ اجْعَلُوا بِضَاعَتَهُمْ ف۪ي رِحَالِهِمْ لَعَلَّهُمْ يَعْرِفُونَـهَٓا اِذَا انْقَلَـبُٓوا اِلٰٓى اَهْلِهِمْ لَعَلَّهُمْ يَرْجِعُونَ', '(Yusuf) emrindeki gençlere dedi ki: Sermayelerini yüklerinin içine koyun. Olur ki ailelerine döndüklerinde bunun farkına varırlar da belki geri gelirler.', 13, 241, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1659, 63, 12, 'Yusuf', 'فَلَمَّا رَجَعُٓوا اِلٰٓى اَب۪يهِمْ قَالُوا يَٓا اَبَانَا مُنِـعَ مِنَّا الْكَيْلُ فَاَرْسِلْ مَعَنَٓا اَخَانَا نَكْتَلْ وَاِنَّا لَهُ لَحَافِظُونَ', 'Babalarına döndüklerinde dediler ki: Ey babamız! Erzak bize yasaklandı. Kardeşimizi (Bünyamin´i) bizimle beraber gönder de (onun sayesinde) ölçüp alalım. Biz onu mutlaka koruyacağız.', 13, 241, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1660, 64, 12, 'Yusuf', 'قَالَ هَلْ اٰمَنُكُمْ عَلَيْهِ اِلَّا كَمَٓا اَمِنْتُكُمْ عَلٰٓى اَخ۪يهِ مِنْ قَبْلُۜ فَاللّٰهُ خَيْرٌ حَافِظاًۖ وَهُوَ اَرْحَمُ الرَّاحِم۪ينَ', 'Ya´kub dedi ki: Daha önce kardeşi (Yusuf) hakkında size ne kadar güvendiysem, bunun hakkında da size ancak o kadar güvenirim! (Ben onu sadece Allah´a emanet ediyorum); Allah en hayırlı koruyucudur. O, acıyanların en merhametlisidir.', 13, 242, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1661, 65, 12, 'Yusuf', 'وَلَمَّا فَتَحُوا مَتَاعَهُمْ وَجَدُوا بِضَاعَتَهُمْ رُدَّتْ اِلَيْهِمْۜ قَالُوا يَٓا اَبَانَا مَا نَبْغ۪يۜ هٰذِه۪ بِضَاعَتُنَا رُدَّتْ اِلَيْنَاۚ وَنَم۪يرُ اَهْلَنَا وَنَحْفَظُ اَخَانَا وَنَزْدَادُ كَيْلَ بَع۪يرٍۜ ذٰلِكَ كَيْلٌ يَس۪يرٌ', 'Eşyalarını açtıklarında sermayelerinin kendilerine geri verildiğini gördüler. Dediler ki: Ey babamız! Daha ne istiyoruz. İşte sermâyemiz de bize geri verilmiş. (Onunla yine) ailemize yiyecek getiririz, kardeşimizi koruruz ve bir deve yükü de fazla alırız. Çünkü bu (seferki aldığımız) az bir miktardır.', 13, 242, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1662, 66, 12, 'Yusuf', 'قَالَ لَنْ اُرْسِلَهُ مَعَكُمْ حَتّٰى تُؤْتُونِ مَوْثِقاً مِنَ اللّٰهِ لَتَأْتُنَّن۪ي بِه۪ٓ اِلَّٓا اَنْ يُحَاطَ بِكُمْۚ فَلَمَّٓا اٰتَوْهُ مَوْثِقَهُمْ قَالَ اللّٰهُ عَلٰى مَا نَقُولُ وَك۪يلٌ', '(Ya´kub) dedi ki: Kuşatılmanız (ve çaresiz kalma durumunuz) hariç, onu bana mutlaka getireceğinize dair Allah adına bana sağlam bir söz vermediğiniz takdirde onu sizinle beraber göndermem!» Ona (istediği şekilde) teminatlarını verdiklerinde dedi ki: Söylediklerimize Allah şahittir.', 13, 242, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1663, 67, 12, 'Yusuf', 'وَقَالَ يَا بَنِيَّ لَا تَدْخُلُوا مِنْ بَابٍ وَاحِدٍ وَادْخُلُوا مِنْ اَبْوَابٍ مُتَفَرِّقَةٍۜ وَمَٓا اُغْن۪ي عَنْكُمْ مِنَ اللّٰهِ مِنْ شَيْءٍۜ اِنِ الْحُكْمُ اِلَّا لِلّٰهِۜ عَلَيْهِ تَوَكَّلْتُۚ وَعَلَيْهِ فَلْيَتَوَكَّلِ الْمُتَوَكِّلُونَ', 'Sonra şöyle dedi: Oğullarım! (Şehre) hepiniz bir kapıdan girmeyin, ayrı ayrı kapılardan girin. Ama Allah´tan (gelecek) hiçbir şeyi sizden savamam. Hüküm Allah´tan başkasının değildir. (Onun için) ben yalnız O´na dayandım. Tevekkül edenler yalnız O´na dayansınlar.', 13, 242, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1664, 68, 12, 'Yusuf', 'وَلَمَّا دَخَلُوا مِنْ حَيْثُ اَمَرَهُمْ اَبُوهُمْۜ مَا كَانَ يُغْن۪ي عَنْهُمْ مِنَ اللّٰهِ مِنْ شَيْءٍ اِلَّا حَاجَةً ف۪ي نَفْسِ يَعْقُوبَ قَضٰيهَاۜ وَاِنَّهُ لَذُو عِلْمٍ لِمَا عَلَّمْنَاهُ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُونَ۟', 'Babalarının kendilerine emrettiği yerden (çeşitli kapılardan) girdiklerinde (onun emrini yerine getirdiler. Fakat bu tedbir) Allah´tan gelecek hiçbir şeyi onlardan savamazdı; ancak Ya´kub içindeki bir dileği açığa vurmuş oldu. Şüphesiz o, ilim sahibiydi, çünkü ona biz öğretmiştik. Fakat insanların çoğu bilmezler.', 13, 242, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53);
INSERT INTO `Ayetler` (`id`, `AyetNo`, `SureNo`, `SureAdi`, `Metin`, `Mana`, `Cuzu`, `Sayfasi`, `AyetSayisi`, `HarfSayisi`, `AdAnlami`, `IndigiYer`, `InisSirasi`) VALUES
(1665, 69, 12, 'Yusuf', 'وَلَمَّا دَخَلُوا عَلٰى يُوسُفَ اٰوٰٓى اِلَيْهِ اَخَاهُ قَالَ اِنّ۪ٓي اَنَا۬ اَخُوكَ فَلَا تَبْتَئِسْ بِمَا كَانُوا يَعْمَلُونَ', 'Yusuf´un yanına girdiklerinde öz kardeşini yanına aldı ve «Bilesin ki ben senin kardeşinim, onların yaptıklarına üzülme» dedi.', 13, 242, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1666, 70, 12, 'Yusuf', 'فَلَمَّا جَهَّزَهُمْ بِجَهَازِهِمْ جَعَلَ السِّقَايَةَ ف۪ي رَحْلِ اَخ۪يهِ ثُمَّ اَذَّنَ مُؤَذِّنٌ اَيَّتُهَا الْع۪يرُ اِنَّكُمْ لَسَارِقُونَ', '(Yusuf) onların yükünü hazırladığı zaman maşrabayı kardeşinin yükü içine koydu! (Kafile hareket ettikten) sonra bir tellal: Ey kafile! Siz hırsızsınız! diye seslendi.', 13, 243, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1667, 71, 12, 'Yusuf', 'قَالُوا وَاَقْبَلُوا عَلَيْهِمْ مَاذَا تَفْقِدُونَ', '(Yusuf´un kardeşleri) onlara dönerek: Ne arıyorsunuz? dediler.', 13, 243, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1668, 72, 12, 'Yusuf', 'قَالُوا نَفْقِدُ صُوَاعَ الْمَلِكِ وَلِمَنْ جَٓاءَ بِه۪ حِمْلُ بَع۪يرٍ وَاَنَا۬ بِه۪ زَع۪يمٌ', 'Kralın su kabını arıyoruz; onu getirene bir deve yükü (bahşiş) var dediler. (İçlerinden biri:) Ben buna kefilim, dedi.', 13, 243, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1669, 73, 12, 'Yusuf', 'قَالُوا تَاللّٰهِ لَقَدْ عَلِمْتُمْ مَا جِئْنَا لِنُفْسِدَ فِي الْاَرْضِ وَمَا كُنَّا سَارِق۪ينَ', 'Allah´a andolsun ki, bizim yeryüzünde fesat çıkarmak için gelmediğimizi siz de biliyorsunuz. Biz hırsız da değiliz, dediler.', 13, 243, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1670, 74, 12, 'Yusuf', 'قَالُوا فَمَا جَزَٓاؤُ۬هُٓ اِنْ كُنْتُمْ كَاذِب۪ينَ', '(Yusuf´un adamları) dediler ki: Peki, siz yalancıysanız bunun cezası nedir?', 13, 243, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1671, 75, 12, 'Yusuf', 'قَالُوا جَزَٓاؤُ۬هُ مَنْ وُجِدَ ف۪ي رَحْلِه۪ فَهُوَ جَزَٓاؤُ۬هُۜ كَذٰلِكَ نَجْزِي الظَّالِم۪ينَ', '«Onun cezası, kayıp eşya, kimin yükünde bulunursa işte o (şahsa el koymak) onun cezasıdır. Biz zalimleri böyle cezalandırırız» dediler.', 13, 243, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1672, 76, 12, 'Yusuf', 'فَبَدَاَ بِاَوْعِيَتِهِمْ قَبْلَ وِعَٓاءِ اَخ۪يهِ ثُمَّ اسْتَخْرَجَهَا مِنْ وِعَٓاءِ اَخ۪يهِۜ كَذٰلِكَ كِدْنَا لِيُوسُفَۜ مَا كَانَ لِيَأْخُذَ اَخَاهُ ف۪ي د۪ينِ الْمَلِكِ اِلَّٓا اَنْ يَشَٓاءَ اللّٰهُۜ نَرْفَعُ دَرَجَاتٍ مَنْ نَشَٓاءُۜ وَفَوْقَ كُلِّ ذ۪ي عِلْمٍ عَل۪يمٌ', 'Bunun üzerine Yusuf, kardeşinin yükünden önce onların yüklerini (aramaya) başladı. Sonra da onu, kardeşinin yükünden çıkarttı. İşte biz Yusuf´a böyle bir tedbir öğrettik, yoksa kralın kanununa göre kardeşini tutamayacaktı. Ancak Allah´ın dilemesi hariç. Biz kimi dilersek onu derecelerle yükseltiriz. Zira her ilim sahibinin üstünde daha iyi bilen birisi vardır.', 13, 243, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1673, 77, 12, 'Yusuf', 'قَالُٓوا اِنْ يَسْرِقْ فَقَدْ سَرَقَ اَخٌ لَهُ مِنْ قَبْلُۚ فَاَسَرَّهَا يُوسُفُ ف۪ي نَفْسِه۪ وَلَمْ يُبْدِهَا لَهُمْ قَالَ اَنْتُمْ شَرٌّ مَكَاناًۚ وَاللّٰهُ اَعْلَمُ بِمَا تَصِفُونَ', '(Kardeşleri) dediler ki: «Eğer o çaldıysa, daha önce onun bir kardeşi de çalmıştı.» Yusuf bunu içinde sakladı, onlara açmadı. (Kendi kendine) dedi ki: Siz daha kötü durumdasınız! Allah, sizin anlattığınızı çok iyi bilir.', 13, 243, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1674, 78, 12, 'Yusuf', 'قَالُوا يَٓا اَيُّهَا الْعَز۪يزُ اِنَّ لَـهُٓ اَباً شَيْخاً كَب۪يراً فَخُذْ اَحَدَنَا مَكَانَهُۚ اِنَّا نَرٰيكَ مِنَ الْمُحْسِن۪ينَ', 'Dediler ki: Ey aziz! Gerçekten onun çok yaşlı bir babası var. Onun yerine bizim birimizi alıkoy. Zira biz seni, iyilik edenlerden görüyoruz.', 13, 243, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1675, 79, 12, 'Yusuf', 'قَالَ مَعَاذَ اللّٰهِ اَنْ نَأْخُذَ اِلَّا مَنْ وَجَدْنَا مَتَاعَنَا عِنْدَهُٓۙ اِنَّٓا اِذاً لَظَالِمُونَ۟', 'Dedi ki: Eşyamızı yanında bulduğumuz kimseden başkasını yakalamaktan Allah´a sığınırız, o takdirde biz gerçekten zalimler oluruz!', 13, 244, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1676, 80, 12, 'Yusuf', 'فَلَمَّا اسْتَيْـَٔسُوا مِنْهُ خَلَصُوا نَجِياًّۜ قَالَ كَب۪يرُهُمْ اَلَمْ تَعْلَمُٓوا اَنَّ اَبَاكُمْ قَدْ اَخَذَ عَلَيْكُمْ مَوْثِقاً مِنَ اللّٰهِ وَمِنْ قَبْلُ مَا فَرَّطْتُمْ ف۪ي يُوسُفَۚ فَلَنْ اَبْرَحَ الْاَرْضَ حَتّٰى يَأْذَنَ ل۪ٓي اَب۪ٓي اَوْ يَحْكُمَ اللّٰهُ ل۪يۚ وَهُوَ خَيْرُ الْحَاكِم۪ينَ', 'Ondan ümitlerini kesince, (meseleyi) gizli görüşmek üzere ayrılıp (bir kenara) çekildiler. Büyükleri dedi ki: «Babanızın sizden Allah adına söz aldığını, daha önce de Yusuf hakkında işlediğiniz kusuru bilmiyor musunuz? Babam bana izin verinceye veya benim için Allah hükmedinceye kadar bu yerden asla ayrılmayacağım. O hükmedenlerin en hayırlısıdır.', 13, 244, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1677, 81, 12, 'Yusuf', 'اِرْجِعُٓوا اِلٰٓى اَب۪يكُمْ فَقُولُوا يَٓا اَبَانَٓا اِنَّ ابْنَكَ سَرَقَۚ وَمَا شَهِدْنَٓا اِلَّا بِمَا عَلِمْنَا وَمَا كُنَّا لِلْغَيْبِ حَافِظ۪ينَ', 'Babanıza dönün ve deyin ki: «Ey babamız! Şüphesiz oğlun hırsızlık etti. Biz, bildiğimizden başkasına şahitlik etmedik. Biz gaybın bekçileri değiliz.', 13, 244, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1678, 82, 12, 'Yusuf', 'وَسْـَٔلِ الْقَرْيَةَ الَّت۪ي كُنَّا ف۪يهَا وَالْع۪يرَ الَّت۪ٓي اَقْبَلْنَا ف۪يهَاۜ وَاِنَّا لَصَادِقُونَ', '(İstersen) içinde bulunduğumuz şehire (Mısır halkına) ve aralarında geldiğimiz kafileye de sor. Biz gerçekten doğru söylüyoruz.»', 13, 244, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1679, 83, 12, 'Yusuf', 'قَالَ بَلْ سَوَّلَتْ لَكُمْ اَنْفُسُكُمْ اَمْراًۜ فَصَبْرٌ جَم۪يلٌۜ عَسَى اللّٰهُ اَنْ يَأْتِيَن۪ي بِهِمْ جَم۪يعاًۜ اِنَّهُ هُوَ الْعَل۪يمُ الْحَك۪يمُ', '(Babaları) dedi ki: «Hayır, nefisleriniz sizi (böyle) bir işe sürükledi. (Bana düşen) artık, güzel bir sabırdır. Umulur ki, Allah onların hepsini bana getirir. Çünkü O çok iyi bilendir, hikmet sahibidir.»', 13, 244, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1680, 84, 12, 'Yusuf', 'وَتَوَلّٰى عَنْهُمْ وَقَالَ يَٓا اَسَفٰى عَلٰى يُوسُفَ وَابْيَضَّتْ عَيْنَاهُ مِنَ الْحُزْنِ فَهُوَ كَظ۪يمٌ', 'Onlardan yüz çevirdi, «Ah Yusuf´um ah!» diye sızlandı ve kederini içine gömmesi yüzünden gözlerine boz geldi.', 13, 244, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1681, 85, 12, 'Yusuf', 'قَالُوا تَاللّٰهِ تَفْتَؤُ۬ا تَذْكُرُ يُوسُفَ حَتّٰى تَكُونَ حَرَضاً اَوْ تَكُونَ مِنَ الْهَالِك۪ينَ', '(Oğulları:) «Allah´a andolsun ki sen hâla Yusuf´u anıyorsun. Sonunda ya hasta olacaksın ya da büsbütün helâk olacaksın!» dediler.', 13, 244, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1682, 86, 12, 'Yusuf', 'قَالَ اِنَّـمَٓا اَشْكُوا بَثّ۪ي وَحُزْن۪ٓي اِلَى اللّٰهِ وَاَعْلَمُ مِنَ اللّٰهِ مَا لَا تَعْلَمُونَ', '(Ya´kub:) Ben gam ve kederimi sadece Allah´a arzediyorum. Ve ben sizin bilemiyeceğiniz şeyleri Allah tarafından (vahiy ile) biliyorum, dedi.', 13, 244, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1683, 87, 12, 'Yusuf', 'يَا بَنِيَّ اذْهَبُوا فَتَحَسَّسُوا مِنْ يُوسُفَ وَاَخ۪يهِ وَلَا تَايْـَٔسُوا مِنْ رَوْحِ اللّٰهِۜ اِنَّهُ لَا يَايْـَٔسُ مِنْ رَوْحِ اللّٰهِ اِلَّا الْقَوْمُ الْكَافِرُونَ', 'Ey oğullarım! Gidin de Yusuf´u ve kardeşini iyice araştırın, Allah´ın rahmetinden ümit kesmeyin. Çünkü kâfirler topluluğundan başkası Allah´ın rahmetinden ümit kesmez.', 13, 245, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1684, 88, 12, 'Yusuf', 'فَلَمَّا دَخَلُوا عَلَيْهِ قَالُوا يَٓا اَيُّهَا الْعَز۪يزُ مَسَّنَا وَاَهْلَنَا الضُّرُّ وَجِئْنَا بِبِضَاعَةٍ مُزْجٰيةٍ فَاَوْفِ لَنَا الْكَيْلَ وَتَصَدَّقْ عَلَيْنَاۜ اِنَّ اللّٰهَ يَجْزِي الْمُتَصَدِّق۪ينَ', 'Yusuf´un yanına girdiklerinde dediler ki: Ey aziz! Bizi ve ailemizi kıtlık bastı ve biz değersiz bir sermaye ile geldik. Hakkımızı tam ölçerek ver. Ayrıca bize bağışta da bulun. Şüphesiz Allah sadaka verenleri mükâfatlandırır.', 13, 245, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1685, 89, 12, 'Yusuf', 'قَالَ هَلْ عَلِمْتُمْ مَا فَعَلْتُمْ بِيُوسُفَ وَاَخ۪يهِ اِذْ اَنْتُمْ جَاهِلُونَ', 'Yusuf dedi ki: Siz, cahilliğiniz yüzünden Yusuf ve kardeşine yaptıklarınızı biliyor musunuz?', 13, 245, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1686, 90, 12, 'Yusuf', 'قَالُٓوا ءَاِنَّكَ لَاَنْتَ يُوسُفُۜ قَالَ اَنَا۬ يُوسُفُ وَهٰذَٓا اَخ۪يۘ قَدْ مَنَّ اللّٰهُ عَلَيْنَاۜ اِنَّهُ مَنْ يَتَّقِ وَيَصْبِرْ فَاِنَّ اللّٰهَ لَا يُض۪يعُ اَجْرَ الْمُحْسِن۪ينَ', 'Yoksa sen, gerçekten Yusuf musun? dediler. O da: (Evet) ben Yusuf´um, bu da kardeşim. (Birbirimize kavuşmayı) Allah bize lütfetti. Çünkü kim (Allah´tan) korkar ve sabrederse, şüphesiz Allah güzel davrananların mükâfatını zayi etmez, dedi.', 13, 245, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1687, 91, 12, 'Yusuf', 'قَالُوا تَاللّٰهِ لَقَدْ اٰثَرَكَ اللّٰهُ عَلَيْنَا وَاِنْ كُنَّا لَخَاطِـ۪ٔينَ', '(Kardeşleri) dediler ki: Allah´a andolsun, hakikaten Allah seni bize üstün kılmış. Gerçekten biz hataya düşmüşüz.', 13, 245, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1688, 92, 12, 'Yusuf', 'قَالَ لَا تَثْر۪يبَ عَلَيْكُمُ الْيَوْمَۜ يَغْفِرُ اللّٰهُ لَكُمْۘ وَهُوَ اَرْحَمُ الرَّاحِم۪ينَ', '(Yusuf) dedi ki: «Bugün sizi kınamak yok, Allah sizi affetsin! O, merhametlilerin en merhametlisidir.»', 13, 245, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1689, 93, 12, 'Yusuf', 'اِذْهَبُوا بِقَم۪يص۪ي هٰذَا فَاَلْقُوهُ عَلٰى وَجْهِ اَب۪ي يَأْتِ بَص۪يراًۚ وَأْتُون۪ي بِاَهْلِكُمْ اَجْمَع۪ينَ۟', '«Şu benim gömleğimi götürün de onu babamın yüzüne koyun, (gözleri) görecek duruma gelir. Ve bütün ailenizi bana getirin.»', 13, 245, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1690, 94, 12, 'Yusuf', 'وَلَمَّا فَصَلَتِ الْع۪يرُ قَالَ اَبُوهُمْ اِنّ۪ي لَاَجِدُ ر۪يحَ يُوسُفَ لَوْلَٓا اَنْ تُفَنِّدُونِ', 'Kafile (Mısır´dan) ayrılınca, babaları (yanındakilere): Eğer bana bunamış demezseniz inanın ben Yusuf´un kokusunu alıyorum! dedi.', 13, 245, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1691, 95, 12, 'Yusuf', 'قَالُوا تَاللّٰهِ اِنَّكَ لَف۪ي ضَلَالِكَ الْقَد۪يمِ', '(Onlar da:) Vallahi sen hâla eski şaşkınlığındasın, dediler.', 13, 245, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1692, 96, 12, 'Yusuf', 'فَلَمَّٓا اَنْ جَٓاءَ الْبَش۪يرُ اَلْقٰيهُ عَلٰى وَجْهِه۪ فَارْتَدَّ بَص۪يراًۚ قَالَ اَلَمْ اَقُلْ لَكُمْ اِنّ۪ٓي اَعْلَمُ مِنَ اللّٰهِ مَا لَا تَعْلَمُونَ', 'Müjdeci gelince, gömleği onun yüzüne koyar koymaz (Ya´kub) görür oldu. Ben size: «Allah tarafından (vahiy ile) sizin bilemeyeceğiniz şeyleri bilirim» demedim mi! dedi.', 13, 246, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1693, 97, 12, 'Yusuf', 'قَالُوا يَٓا اَبَانَا اسْتَغْفِرْ لَنَا ذُنُوبَنَٓا اِنَّا كُنَّا خَاطِـ۪ٔينَ', '(Oğulları) dediler ki: Ey babamız! (Allah´tan) bizim günahlarımızın affını dile! Çünkü biz gerçekten günahkârlar idik.', 13, 246, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1694, 98, 12, 'Yusuf', 'قَالَ سَوْفَ اَسْتَغْفِرُ لَكُمْ رَبّ۪يۜ اِنَّهُ هُوَ الْغَفُورُ الرَّح۪يمُ', '(Ya´kub:) Sizin için Rabbimden af dileyeceğim. Çünkü O çok bağışlayan, pek esirgeyendir, dedi.', 13, 246, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1695, 99, 12, 'Yusuf', 'فَلَمَّا دَخَلُوا عَلٰى يُوسُفَ اٰوٰٓى اِلَيْهِ اَبَوَيْهِ وَقَالَ ادْخُلُوا مِصْرَ اِنْ شَٓاءَ اللّٰهُ اٰمِن۪ينَۜ', '(Hep beraber Mısır´a gidip) Yusuf´un yanına girdikleri zaman, ana babasını kucakladı, «Güven içinde Allah´ın iradesiyle Mısır´a girin!» dedi.', 13, 246, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1696, 100, 12, 'Yusuf', 'وَرَفَعَ اَبَوَيْهِ عَلَى الْعَرْشِ وَخَرُّوا لَهُ سُجَّداًۚ وَقَالَ يَٓا اَبَتِ هٰذَا تَأْو۪يلُ رُءْيَايَ مِنْ قَبْلُۘ قَدْ جَعَلَهَا رَبّ۪ي حَقاًّۜ وَقَدْ اَحْسَنَ ب۪ٓي اِذْ اَخْرَجَن۪ي مِنَ السِّجْنِ وَجَٓاءَ بِكُمْ مِنَ الْبَدْوِ مِنْ بَعْدِ اَنْ نَزَغَ الشَّيْطَانُ بَيْن۪ي وَبَيْنَ اِخْوَت۪يۜ اِنَّ رَبّ۪ي لَط۪يفٌ لِمَا يَشَٓاءُۜ اِنَّهُ هُوَ الْعَل۪يمُ الْحَك۪يمُ', 'Ana ve babasını tahtının üstüne çıkartıp oturttu ve hepsi onun için (ona kavuştukları için) secdeye kapandılar. (Yusuf) dedi ki: «Ey babacığım! İşte bu, daha önce (gördüğüm) rüyanın yorumudur. Rabbim onu gerçekleştirdi. Doğrusu Rabbim bana (çok şey) lütfetti. Çünkü beni zindandan çıkardı ve şeytan benimle kardeşlerimin arasını bozduktan sonra sizi çölden getirdi. Şüphesiz ki Rabbim dilediğine lütfedicidir. Kuşkusuz O çok iyi bilendir, hikmet sahibidir.»', 13, 246, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1697, 101, 12, 'Yusuf', 'رَبِّ قَدْ اٰتَيْتَن۪ي مِنَ الْمُلْكِ وَعَلَّمْتَن۪ي مِنْ تَأْو۪يلِ الْاَحَاد۪يثِۚ فَاطِرَ السَّمٰوَاتِ وَالْاَرْضِ اَنْتَ وَلِيّ۪ فِي الدُّنْيَا وَالْاٰخِرَةِۚ تَوَفَّن۪ي مُسْلِماً وَاَلْحِقْن۪ي بِالصَّالِح۪ينَ', '«Ey Rabbim! Mülkten bana (nasibimi) verdin ve bana (rüyada görülen) olayların yorumunu da öğrettin. Ey gökleri ve yeri yaratan! Sen dünyada da ahirette de benim sahibimsin. Beni müslüman olarak öldür ve beni sâlihler arasına kat!»', 13, 246, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1698, 102, 12, 'Yusuf', 'ذٰلِكَ مِنْ اَنْبَٓاءِ الْغَيْبِ نُوح۪يهِ اِلَيْكَۚ وَمَا كُنْتَ لَدَيْهِمْ اِذْ اَجْمَعُٓوا اَمْرَهُمْ وَهُمْ يَمْكُرُونَ', 'İşte bu (Yusuf kıssası) gayb haberlerindendir. Onu sana vahyediyoruz. Onlar hile yaparak işlerine karar verdikleri zaman sen onların yanında değildin (ki bunları bilesin).', 13, 246, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1699, 103, 12, 'Yusuf', 'وَمَٓا اَكْثَرُ النَّاسِ وَلَوْ حَرَصْتَ بِمُؤْمِن۪ينَ', 'Sen ne kadar üstüne düşsen de insanların çoğu iman edecek değillerdir.', 13, 246, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1700, 104, 12, 'Yusuf', 'وَمَا تَسْـَٔلُهُمْ عَلَيْهِ مِنْ اَجْرٍۜ اِنْ هُوَ اِلَّا ذِكْرٌ لِلْعَالَم۪ينَ۟', 'Halbuki sen bunun için (peygamberlik görevini îfa için) onlardan bir ücret istemiyorsun. Kur´an, âlemler için ancak bir öğüttür.', 13, 247, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1701, 105, 12, 'Yusuf', 'وَكَاَيِّنْ مِنْ اٰيَةٍ فِي السَّمٰوَاتِ وَالْاَرْضِ يَمُرُّونَ عَلَيْهَا وَهُمْ عَنْهَا مُعْرِضُونَ', 'Göklerde ve yerde nice deliller vardır ki, onlar bu delillerden yüzlerini çevirip geçerler.', 13, 247, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1702, 106, 12, 'Yusuf', 'وَمَا يُؤْمِنُ اَكْثَرُهُمْ بِاللّٰهِ اِلَّا وَهُمْ مُشْرِكُونَ', 'Onların çoğu, ancak ortak koşarak Allah´a iman ederler.', 13, 247, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1703, 107, 12, 'Yusuf', 'اَفَاَمِنُٓوا اَنْ تَأْتِيَهُمْ غَاشِيَةٌ مِنْ عَذَابِ اللّٰهِ اَوْ تَأْتِيَهُمُ السَّاعَةُ بَغْتَةً وَهُمْ لَا يَشْعُرُونَ', 'Allah tarafından kuşatıcı bir felâket gelmesi veya farkında olmadan kıyametin ansızın kopması karşısında kendilerini emîn mi gördüler?', 13, 247, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1704, 108, 12, 'Yusuf', 'قُلْ هٰذِه۪ سَب۪يل۪ٓي اَدْعُٓوا اِلَى اللّٰهِ عَلٰى بَص۪يرَةٍ اَنَا۬ وَمَنِ اتَّبَعَن۪يۜ وَسُبْحَانَ اللّٰهِ وَمَٓا اَنَا۬ مِنَ الْمُشْرِك۪ينَ', '(Resûlüm!) De ki: «İşte bu, benim yolumdur. Ben Allah´a çağırıyorum, ben ve bana uyanlar aydınlık bir yol üzerindeyiz. Allah´ı (ortaklardan) tenzih ederim! Ve ben ortak koşanlardan değilim.»', 13, 247, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1705, 109, 12, 'Yusuf', 'وَمَٓا اَرْسَلْنَا مِنْ قَبْلِكَ اِلَّا رِجَالاً نُوح۪ٓي اِلَيْهِمْ مِنْ اَهْلِ الْقُرٰىۜ اَفَلَمْ يَس۪يرُوا فِي الْاَرْضِ فَيَنْظُرُوا كَيْفَ كَانَ عَاقِبَةُ الَّذ۪ينَ مِنْ قَبْلِهِمْۜ وَلَدَارُ الْاٰخِرَةِ خَيْرٌ لِلَّذ۪ينَ اتَّقَوْاۜ اَفَلَا تَعْقِلُونَ', 'Senden önce de, şehirler halkından kendilerine vahyettiğimiz erkeklerden başkasını peygamber göndermedik. (Kâfirler) yeryüzünde hiç gezmediler mi ki, kendilerinden öncekilerin sonunun nasıl olduğunu görsünler! Sakınanlar için ahiret yurdu elbette daha iyidir. Hâla aklınızı kullanmıyor musunuz?', 13, 247, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1706, 110, 12, 'Yusuf', 'حَتّٰٓى اِذَا اسْتَيْـَٔسَ الرُّسُلُ وَظَنُّٓوا اَنَّهُمْ قَدْ كُذِبُوا جَٓاءَهُمْ نَصْرُنَاۙ فَنُجِّيَ مَنْ نَشَٓاءُۜ وَلَا يُرَدُّ بَأْسُنَا عَنِ الْقَوْمِ الْمُجْرِم۪ينَ', 'Nihayet peygamberler ümitlerini yitirip de kendilerinin yalana çıkarıldıklarını sandıkları sırada onlara yardımımız gelir ve dilediğimiz kimse kurtuluşa erdirilir. (Fakat) suçlular topluluğundan azabımız asla geri çevrilmez.', 13, 247, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1707, 111, 12, 'Yusuf', 'لَقَدْ كَانَ ف۪ي قَصَصِهِمْ عِبْرَةٌ لِاُو۬لِي الْاَلْبَابِۜ مَا كَانَ حَد۪يثاً يُفْتَرٰى وَلٰكِنْ تَصْد۪يقَ الَّذ۪ي بَيْنَ يَدَيْهِ وَتَفْص۪يلَ كُلِّ شَيْءٍ وَهُدًى وَرَحْمَةً لِقَوْمٍ يُؤْمِنُونَ', 'Andolsun onların (geçmiş peygamberler ve ümmetlerinin) kıssalarında akıl sahipleri için pek çok ibretler vardır. (Bu Kur´an) uydurulabilecek bir söz değildir. Fakat o, kendinden öncekileri tasdik eden, her şeyi açıklayan (bir kitaptır); iman eden toplum için bir rahmet ve bir hidayettir.', 13, 247, 111, 7125, 'Peygamber Yusuf', 'Mekke', 53),
(1708, 1, 13, 'Ra\'d', 'الٓمٓرٰ ۠تِلْكَ اٰيَاتُ الْكِتَابِۜ وَالَّـذ۪ٓي اُنْزِلَ اِلَيْكَ مِنْ رَبِّكَ الْحَقُّ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يُؤْمِنُونَ', 'Elif. Lâm. Mîm. Râ. Bunlar, Kitab´ın âyetleridir. Sana Rabbinden indirilen haktır, fakat insanların çoğu inanmazlar.', 13, 248, 43, 3450, 'Gök gürültüsü', 'Medine', 96),
(1709, 2, 13, 'Ra\'d', 'اَللّٰهُ الَّذ۪ي رَفَعَ السَّمٰوَاتِ بِغَيْرِ عَمَدٍ تَرَوْنَهَا ثُمَّ اسْتَوٰى عَلَى الْعَرْشِ وَسَخَّرَ الشَّمْسَ وَالْقَمَرَۜ كُلٌّ يَجْر۪ي لِاَجَلٍ مُسَمًّىۜ يُدَبِّرُ الْاَمْرَ يُفَصِّلُ الْاٰيَاتِ لَعَلَّكُمْ بِلِقَٓاءِ رَبِّكُمْ تُوقِنُونَ', 'Görmekte olduğunuz gökleri direksiz olarak yükselten, sonra Arş´a istivâ eden, güneşi ve ayı emrine boyun eğdiren Allah´tır. (Bunların) her biri muayyen bir vakte kadar akıp gitmektedir. O, Rabbinize kavuşacağınıza kesin olarak inanmanız için her işi düzenleyip âyetleri açıklamaktadır.', 13, 248, 43, 3450, 'Gök gürültüsü', 'Medine', 96),
(1710, 3, 13, 'Ra\'d', 'وَهُوَ الَّذ۪ي مَدَّ الْاَرْضَ وَجَعَلَ ف۪يهَا رَوَاسِيَ وَاَنْهَاراًۜ وَمِنْ كُلِّ الثَّمَرَاتِ جَعَلَ ف۪يهَا زَوْجَيْنِ اثْنَيْنِ يُغْشِي الَّيْلَ النَّهَارَۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَاتٍ لِقَوْمٍ يَتَفَكَّرُونَ', 'Yeri döşeyen, onda oturaklı dağlar ve ırmaklar yaratan ve orada bütün meyvelerden çifter çifter yaratan O´dur. Geceyi de gündüzün üzerine O örtüyor. Şüphesiz bütün bunlarda düşünen bir toplum için ibretler vardır.', 13, 248, 43, 3450, 'Gök gürültüsü', 'Medine', 96),
(1711, 4, 13, 'Ra\'d', 'وَفِي الْاَرْضِ قِطَعٌ مُتَجَاوِرَاتٌ وَجَنَّاتٌ مِنْ اَعْنَابٍ وَزَرْعٌ وَنَخ۪يلٌ صِنْوَانٌ وَغَيْرُ صِنْوَانٍ يُسْقٰى بِمَٓاءٍ وَاحِدٍ۠ وَنُفَضِّلُ بَعْضَهَا عَلٰى بَعْضٍ فِي الْاُكُلِۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَاتٍ لِقَوْمٍ يَعْقِلُونَ', 'Yeryüzünde birbirine komşu kıtalar, üzüm bağları, ekinler, bir kökten ve çeşitli köklerden dallanmış hurma ağaçları vardır. Bunların hepsi bir su ile sulanır. (Böyle iken) yemişlerinde onların bir kısmını bir kısmına üstün kılarız. İşte bunlarda akıllarını kullanan bir toplum için ibretler vardır.', 13, 248, 43, 3450, 'Gök gürültüsü', 'Medine', 96),
(1712, 5, 13, 'Ra\'d', 'وَاِنْ تَعْجَبْ فَعَجَبٌ قَوْلُهُمْ ءَاِذَا كُنَّا تُرَاباً ءَاِنَّا لَف۪ي خَلْقٍ جَد۪يدٍۜ اُو۬لٰٓئِكَ الَّذ۪ينَ كَفَرُوا بِرَبِّهِمْۚ وَاُو۬لٰٓئِكَ الْاَغْلَالُ ف۪ٓي اَعْنَاقِهِمْۚ وَاُو۬لٰٓئِكَ اَصْحَابُ النَّارِۚ هُمْ ف۪يهَا خَالِدُونَ', '(Resûlüm! Kâfirlerin seni yalanlamalarına) şaşıyorsan, asıl şaşılacak şey onların: «Biz toprak olduğumuz zaman yeniden mi yaratılacağız?» demeleridir. İşte onlar, Rablerini inkâr edenlerdir; işte onlar (kıyamet gününde) boyunlarında tasmalar bulunanlardır. Ve onlar ateş ehlidir. Onlar, orada ebedî kalacaklardır!', 13, 248, 43, 3450, 'Gök gürültüsü', 'Medine', 96),
(1713, 6, 13, 'Ra\'d', 'وَيَسْتَعْجِلُونَكَ بِالسَّيِّئَةِ قَبْلَ الْحَسَنَةِ وَقَدْ خَلَتْ مِنْ قَبْلِهِمُ الْمَثُلَاتُۜ وَاِنَّ رَبَّكَ لَذُو مَغْفِرَةٍ لِلنَّاسِ عَلٰى ظُلْمِهِمْۚ وَاِنَّ رَبَّكَ لَشَد۪يدُ الْعِقَابِ', '(Müşrikler) senden iyilikten önce kötülüğü çabucak istiyorlar. Halbuki onlardan önce ibret alınacak nice azap örnekleri gelip geçmiştir. Doğrusu insanlar kötülük ettikleri halde Rabbin onlar için mağfiret sahibidir. (Bununla beraber) Rabbinin azabı da çok şiddetlidir.', 13, 249, 43, 3450, 'Gök gürültüsü', 'Medine', 96),
(1714, 7, 13, 'Ra\'d', 'وَيَقُولُ الَّذ۪ينَ كَفَرُوا لَوْلَٓا اُنْزِلَ عَلَيْهِ اٰيَةٌ مِنْ رَبِّه۪ۜ اِنَّـمَٓا اَنْتَ مُنْذِرٌ وَلِكُلِّ قَوْمٍ هَادٍ۟', 'Kâfirler diyorlar ki: Ona Rabbinden bir mucize indirilseydi ya! (Halbuki) sen ancak bir uyarıcısın ve her toplumun bir rehberi vardır.', 13, 249, 43, 3450, 'Gök gürültüsü', 'Medine', 96),
(1715, 8, 13, 'Ra\'d', 'اَللّٰهُ يَعْلَمُ مَا تَحْمِلُ كُلُّ اُنْثٰى وَمَا تَغ۪يضُ الْاَرْحَامُ وَمَا تَزْدَادُۜ وَكُلُّ شَيْءٍ عِنْدَهُ بِمِقْدَارٍ', 'Her dişinin neye gebe kalacağını, rahimlerin neyi eksik, neyi ziyade edeceğini Allah bilir. Onun katında her şey ölçü iledir.', 13, 249, 43, 3450, 'Gök gürültüsü', 'Medine', 96),
(1716, 9, 13, 'Ra\'d', 'عَالِمُ الْغَيْبِ وَالشَّهَادَةِ الْكَب۪يرُ الْمُتَعَالِ', 'O, görüleni de görülmeyeni de bilir; çok büyüktür, yücedir.', 13, 249, 43, 3450, 'Gök gürültüsü', 'Medine', 96),
(1717, 10, 13, 'Ra\'d', 'سَوَٓاءٌ مِنْكُمْ مَنْ اَسَرَّ الْقَوْلَ وَمَنْ جَهَرَ بِه۪ وَمَنْ هُوَ مُسْتَخْفٍ بِالَّيْلِ وَسَارِبٌ بِالنَّهَارِ', 'Sizden, sözü gizleyenle onu açığa vuran, geceleyin gizlenenle gündüzün yürüyen (onun ilminde) eşittir.', 13, 249, 43, 3450, 'Gök gürültüsü', 'Medine', 96),
(1718, 11, 13, 'Ra\'d', 'لَهُ مُعَقِّبَاتٌ مِنْ بَيْنِ يَدَيْهِ وَمِنْ خَلْفِه۪ يَحْفَظُونَهُ مِنْ اَمْرِ اللّٰهِۜ اِنَّ اللّٰهَ لَا يُغَيِّرُ مَا بِقَوْمٍ حَتّٰى يُغَيِّرُوا مَا بِاَنْفُسِهِمْۜ وَاِذَٓا اَرَادَ اللّٰهُ بِقَوْمٍ سُٓوءاً فَلَا مَرَدَّ لَهُۚ وَمَا لَهُمْ مِنْ دُونِه۪ مِنْ وَالٍ', 'Onun önünde ve arkasında Allah´ın emriyle onu koruyan takipçiler (melekler) vardır. Bir toplum kendilerindeki özellikleri değiştirinceye kadar Allah, onlarda bulunanı değiştirmez. Allah bir topluma kötülük diledi mi, artık onun için geri çevrilme diye bir şey yoktur. Onların Allah´tan başka yardımcıları da yoktur.', 13, 249, 43, 3450, 'Gök gürültüsü', 'Medine', 96),
(1719, 12, 13, 'Ra\'d', 'هُوَ الَّذ۪ي يُر۪يكُمُ الْبَرْقَ خَوْفاً وَطَمَعاً وَيُنْشِئُ السَّحَابَ الثِّقَالَۚ', 'O, size korku ve ümit içinde şimşeği gösteren ve (yağmur dolu) ağır bulutları meydana getirendir.', 13, 249, 43, 3450, 'Gök gürültüsü', 'Medine', 96),
(1720, 13, 13, 'Ra\'d', 'وَيُسَبِّحُ الرَّعْدُ بِحَمْدِه۪ وَالْمَلٰٓئِكَةُ مِنْ خ۪يفَتِه۪ۚ وَيُرْسِلُ الصَّوَاعِقَ فَيُص۪يبُ بِهَا مَنْ يَشَٓاءُ وَهُمْ يُجَادِلُونَ فِي اللّٰهِۚ وَهُوَ شَد۪يدُ الْمِحَالِۜ', 'Gök gürültüsü Allah´ı hamd ile tesbih eder. Melekler de O´nun heybetinden dolayı tesbih ederler. Onlar, Allah hakkında mücâdele edip dururken O, yıldırımlar gönderip onlarla dilediğini çarpar. Ve O, azabı pek şiddetli olandır.', 13, 249, 43, 3450, 'Gök gürültüsü', 'Medine', 96),
(1721, 14, 13, 'Ra\'d', 'لَهُ دَعْوَةُ الْحَقِّۜ وَالَّذ۪ينَ يَدْعُونَ مِنْ دُونِه۪ لَا يَسْتَج۪يبُونَ لَهُمْ بِشَيْءٍ اِلَّا كَبَاسِطِ كَفَّيْهِ اِلَى الْمَٓاءِ لِيَبْلُغَ فَاهُ وَمَا هُوَ بِبَالِغِه۪ۜ وَمَا دُعَٓاءُ الْكَافِر۪ينَ اِلَّا ف۪ي ضَلَالٍ', 'El açıp yalvarmaya lâyık olan ancak O´dur. O´nun dışında el açıp dua ettikleri onların isteklerini hiçbir şeyle karşılamazlar. Onlar ancak ağzına gelsin diye suya doğru iki avucunu açan kimse gibidir. Halbuki (suyu ağzına götürmedikçe) su onun ağzına girecek değildir. Kâfirlerin duası kuşkusuz hedefini şaşırmıştır.', 13, 250, 43, 3450, 'Gök gürültüsü', 'Medine', 96),
(1722, 15, 13, 'Ra\'d', 'وَلِلّٰهِ يَسْجُدُ مَنْ فِي السَّمٰوَاتِ وَالْاَرْضِ طَوْعاً وَكَرْهاً وَظِلَالُهُمْ بِالْغُدُوِّ وَالْاٰصَالِ ۩', 'Göklerde ve yerde bulunanlar da onların gölgeleri de sabah akşam ister istemez sadece Allah´a secde ederler.', 13, 250, 43, 3450, 'Gök gürültüsü', 'Medine', 96),
(1723, 16, 13, 'Ra\'d', 'قُلْ مَنْ رَبُّ السَّمٰوَاتِ وَالْاَرْضِۜ قُلِ اللّٰهُۜ قُلْ اَفَاتَّخَذْتُمْ مِنْ دُونِه۪ٓ اَوْلِيَٓاءَ لَا يَمْلِكُونَ لِاَنْفُسِهِمْ نَفْعاً وَلَا ضَراًّۜ قُلْ هَلْ يَسْتَوِي الْاَعْمٰى وَالْبَص۪يرُۙ اَمْ هَلْ تَسْتَوِي الظُّلُمَاتُ وَالنُّورُۚ اَمْ جَعَلُوا لِلّٰهِ شُرَكَٓاءَ خَلَقُوا كَخَلْقِه۪ فَتَشَابَهَ الْخَلْقُ عَلَيْهِمْۜ قُلِ اللّٰهُ خَالِقُ كُلِّ شَيْءٍ وَهُوَ الْوَاحِدُ الْقَهَّارُ', '(Resûlüm!) De ki: «Göklerin ve yerin Rabbi kimdir?» De ki: «Allah´tır.» O halde de ki: «O´nu bırakıp da kendilerine fayda ya da zarar verme gücüne sahip olmayan dostlar mı edindiniz?» De ki: «Körle gören bir olur mu hiç? Ya da karanlıklarla aydınlık eşit olur mu?» Yoksa O´nun yarattığı gibi yaratan ortaklar buldular da bu yaratma onlarca birbirine benzer mi göründü? De ki: Allah her şeyi yaratandır. Ve O, birdir, karşı durulamaz güç sahibidir.', 13, 250, 43, 3450, 'Gök gürültüsü', 'Medine', 96),
(1724, 17, 13, 'Ra\'d', 'اَنْزَلَ مِنَ السَّمَٓاءِ مَٓاءً فَسَالَتْ اَوْدِيَةٌ بِقَدَرِهَا فَاحْتَمَلَ السَّيْلُ زَبَداً رَابِياًۜ وَمِمَّا يُوقِدُونَ عَلَيْهِ فِي النَّارِ ابْتِغَٓاءَ حِلْيَةٍ اَوْ مَتَاعٍ زَبَدٌ مِثْلُهُۜ كَذٰلِكَ يَضْرِبُ اللّٰهُ الْحَقَّ وَالْبَاطِلَۜ فَاَمَّا الزَّبَدُ فَيَذْهَبُ جُفَٓاءًۚ وَاَمَّا مَا يَنْفَعُ النَّاسَ فَيَمْكُثُ فِي الْاَرْضِۜ كَذٰلِكَ يَضْرِبُ اللّٰهُ الْاَمْثَالَۜ', 'O, gökten su indirdi de vâdiler kendi hacimlerince sel olup aktı. Bu sel, üste çıkan bir köpüğü yüklenip götürdü. Süs veya (diğer) eşya yapmak isteyerek ateşte erittikleri şeylerden de buna benzer köpük olur. İşte Allah hak ile bâtıla böyle misal verir. Köpük atılıp gider. İnsanlara fayda veren şeye gelince, o yeryüzünde kalır. İşte Allah böyle misaller getirir.', 13, 250, 43, 3450, 'Gök gürültüsü', 'Medine', 96),
(1725, 18, 13, 'Ra\'d', 'لِلَّذ۪ينَ اسْتَجَابُوا لِرَبِّهِمُ الْحُسْنٰىۜ وَالَّذ۪ينَ لَمْ يَسْتَج۪يبُوا لَهُ لَوْ اَنَّ لَهُمْ مَا فِي الْاَرْضِ جَم۪يعاً وَمِثْلَهُ مَعَهُ لَافْتَدَوْا بِه۪ۜ اُو۬لٰٓئِكَ لَهُمْ سُٓوءُ الْحِسَابِۙ وَمَأْوٰيهُمْ جَهَنَّمُۜ وَبِئْسَ الْمِهَادُ۟', 'İşte Rablerinin emrine uyanlar için en güzel (mükâfat) vardır. Ona uymayanlara gelince, eğer yeryüzünde olanların tümü ile bunun yanında bir misli daha kendilerinin olsa, (kurtulmak için) onu mutlaka feda ederler. İşte onlar var ya, hesabın en kötüsü onlaradır. Varacakları yer de cehennemdir. O ne kötü yataktır!', 13, 250, 43, 3450, 'Gök gürültüsü', 'Medine', 96),
(1726, 19, 13, 'Ra\'d', 'اَفَمَنْ يَعْلَمُ اَنَّـمَٓا اُنْزِلَ اِلَيْكَ مِنْ رَبِّكَ الْحَقُّ كَمَنْ هُوَ اَعْمٰىۜ اِنَّمَا يَتَذَكَّرُ اُو۬لُوا الْاَلْبَابِۙ', 'Rabbinden sana indirilenin hak olduğunu bilen kimse, (inkâr eden) kör kimse gibi olur mu? (Fakat bunu) ancak akıl sahipleri anlar.', 13, 251, 43, 3450, 'Gök gürültüsü', 'Medine', 96),
(1727, 20, 13, 'Ra\'d', 'اَلَّذ۪ينَ يُوفُونَ بِعَهْدِ اللّٰهِ وَلَا يَنْقُضُونَ الْم۪يثَاقَۙ', 'Onlar, Allah´ın ahdini yerine getirenler ve verdikleri sözü bozmayanlardır.', 13, 251, 43, 3450, 'Gök gürültüsü', 'Medine', 96),
(1728, 21, 13, 'Ra\'d', 'وَالَّذ۪ينَ يَصِلُونَ مَٓا اَمَرَ اللّٰهُ بِه۪ٓ اَنْ يُوصَلَ وَيَخْشَوْنَ رَبَّهُمْ وَيَخَافُونَ سُٓوءَ الْحِسَابِۜ', 'Onlar Allah´ın gözetilmesini emrettiği şeyleri gözeten, Rablerinden sakınan ve kötü hesaptan korkan kimselerdir.', 13, 251, 43, 3450, 'Gök gürültüsü', 'Medine', 96),
(1729, 22, 13, 'Ra\'d', 'وَالَّذ۪ينَ صَبَرُوا ابْتِغَٓاءَ وَجْهِ رَبِّهِمْ وَاَقَامُوا الصَّلٰوةَ وَاَنْفَقُوا مِمَّا رَزَقْنَاهُمْ سِراًّ وَعَلَانِيَةً وَيَدْرَؤُ۫نَ بِالْحَسَنَةِ السَّيِّئَةَ اُو۬لٰٓئِكَ لَهُمْ عُقْبَى الدَّارِۙ', 'Yine onlar, Rablerinin rızasını isteyerek sabreden, namazı dosdoğru kılan, kendilerine verdiğimiz rızıklardan gizli ve açık olarak (Allah yolunda) harcayan ve kötülüğü iyilikle savan kimselerdir. İşte onlar var ya, dünya yurdunun (güzel) sonu sadece onlarındır.', 13, 251, 43, 3450, 'Gök gürültüsü', 'Medine', 96),
(1730, 23, 13, 'Ra\'d', 'جَنَّاتُ عَدْنٍ يَدْخُلُونَهَا وَمَنْ صَلَحَ مِنْ اٰبَٓائِهِمْ وَاَزْوَاجِهِمْ وَذُرِّيَّاتِهِمْ وَالْمَلٰٓئِكَةُ يَدْخُلُونَ عَلَيْهِمْ مِنْ كُلِّ بَابٍۚ', '(O yurt) Adn cennetleridir; oraya babalarından, eşlerinden ve çocuklarından sâlih olanlarla beraber girecekler, melekler de her kapıdan onların yanına varacaklardır.', 13, 251, 43, 3450, 'Gök gürültüsü', 'Medine', 96),
(1731, 24, 13, 'Ra\'d', 'سَلَامٌ عَلَيْكُمْ بِمَا صَبَرْتُمْ فَنِعْمَ عُقْبَى الدَّارِۜ', '(Melekler:) Sabrettiğinize karşılık size selam olsun! Dünya yurdunun sonu (cennet) ne güzeldir! (derler).', 13, 251, 43, 3450, 'Gök gürültüsü', 'Medine', 96),
(1732, 25, 13, 'Ra\'d', 'وَالَّذ۪ينَ يَنْقُضُونَ عَهْدَ اللّٰهِ مِنْ بَعْدِ م۪يثَاقِه۪ وَيَقْطَعُونَ مَٓا اَمَرَ اللّٰهُ بِه۪ٓ اَنْ يُوصَلَ وَيُفْسِدُونَ فِي الْاَرْضِۙ اُو۬لٰٓئِكَ لَهُمُ اللَّعْنَةُ وَلَهُمْ سُٓوءُ الدَّارِ', 'Allah´a verdikleri sözü kuvvetle pekiştirdikten sonra bozanlar, Allah´ın riayet edilmesini emrettiği şeyleri (akrabalık bağlarını) terk edenler ve yeryüzünde fesat çıkaranlar; işte lânet onlar içindir. Ve kötü yurt (cehennem) onlarındır.', 13, 251, 43, 3450, 'Gök gürültüsü', 'Medine', 96),
(1733, 26, 13, 'Ra\'d', 'اَللّٰهُ يَبْسُطُ الرِّزْقَ لِمَنْ يَشَٓاءُ وَيَقْدِرُۜ وَفَرِحُوا بِالْحَيٰوةِ الدُّنْيَاۜ وَمَا الْحَيٰوةُ الدُّنْيَا فِي الْاٰخِرَةِ اِلَّا مَتَاعٌ۟', 'Allah dilediğine rızkını bollaştırır da daraltır da. Onlar dünya hayatıyla şımardılar. Oysa ahiretin yanında dünya hayatı, geçici bir faydadan başka bir şey değildir.', 13, 251, 43, 3450, 'Gök gürültüsü', 'Medine', 96),
(1734, 27, 13, 'Ra\'d', 'وَيَقُولُ الَّذ۪ينَ كَفَرُوا لَوْلَٓا اُنْزِلَ عَلَيْهِ اٰيَةٌ مِنْ رَبِّه۪ۜ قُلْ اِنَّ اللّٰهَ يُضِلُّ مَنْ يَشَٓاءُ وَيَهْد۪ٓي اِلَيْهِ مَنْ اَنَابَۚ', 'Kâfir olanlar diyorlar ki: Ona Rabbinden bir mucize indirilmeli değil miydi? De ki: Kuşkusuz Allah dilediğini saptırır, kendisine yöneleni de hidayete erdirir.', 13, 251, 43, 3450, 'Gök gürültüsü', 'Medine', 96),
(1735, 28, 13, 'Ra\'d', 'اَلَّذ۪ينَ اٰمَنُوا وَتَطْمَئِنُّ قُلُوبُهُمْ بِذِكْرِ اللّٰهِۜ اَلَا بِذِكْرِ اللّٰهِ تَطْمَئِنُّ الْقُلُوبُۜ', 'Bunlar, iman edenler ve gönülleri Allah´ın zikriyle sükûnete erenlerdir. Bilesiniz ki, kalpler ancak Allah´ı anmakla huzur bulur.', 13, 251, 43, 3450, 'Gök gürültüsü', 'Medine', 96),
(1736, 29, 13, 'Ra\'d', 'اَلَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ طُوبٰى لَهُمْ وَحُسْنُ مَاٰبٍ', 'İman edip iyi işler yapanlara ne mutlu! Varılacak güzel yurt da onlar içindir.', 13, 252, 43, 3450, 'Gök gürültüsü', 'Medine', 96),
(1737, 30, 13, 'Ra\'d', 'كَذٰلِكَ اَرْسَلْنَاكَ ف۪ٓي اُمَّةٍ قَدْ خَلَتْ مِنْ قَبْلِهَٓا اُمَمٌ لِتَتْلُوَ۬ا عَلَيْهِمُ الَّـذ۪ٓي اَوْحَيْنَٓا اِلَيْكَ وَهُمْ يَكْفُرُونَ بِالرَّحْمٰنِۜ قُلْ هُوَ رَبّ۪ي لَٓا اِلٰهَ اِلَّا هُوَۚ عَلَيْهِ تَوَكَّلْتُ وَاِلَيْهِ مَتَابِ', '(Ey Muhammed!) Böylece seni, kendilerinden önce nice ümmetlerin gelip geçtiği bir ümmete gönderdik ki, sana vahyettiğimizi onlara okuyasın. Onlar Rahman´ı inkâr ediyorlar. De ki: O benim Rabbimdir. O´ndan başka tanrı yoktur. Sadece O´na tevekkül ettim ve dönüş sadece O´nadır.', 13, 252, 43, 3450, 'Gök gürültüsü', 'Medine', 96),
(1738, 31, 13, 'Ra\'d', 'وَلَوْ اَنَّ قُرْاٰناً سُيِّرَتْ بِهِ الْجِبَالُ اَوْ قُطِّعَتْ بِهِ الْاَرْضُ اَوْ كُلِّمَ بِهِ الْمَوْتٰىۜ بَلْ لِلّٰهِ الْاَمْرُ جَم۪يعاًۜ اَفَلَمْ يَايْـَٔسِ الَّذ۪ينَ اٰمَنُٓوا اَنْ لَوْ يَشَٓاءُ اللّٰهُ لَهَدَى النَّاسَ جَم۪يعاًۜ وَلَا يَزَالُ الَّذ۪ينَ كَفَرُوا تُص۪يبُهُمْ بِمَا صَنَعُوا قَارِعَةٌ اَوْ تَحُلُّ قَر۪يباً مِنْ دَارِهِمْ حَتّٰى يَأْتِيَ وَعْدُ اللّٰهِۜ اِنَّ اللّٰهَ لَا يُخْلِفُ الْم۪يعَادَ۟', 'Eğer okunan bir Kitapla dağlar yürütülseydi veya onunla yer parçalansaydı, yahut onunla ölüler konuşturulsaydı (o Kitap yine bu Kur´an olacaktı). Fakat bütün işler Allah´a aittir. İman edenler hâla bilmediler mi ki, Allah dileseydi bütün insanları hidayete erdirirdi? Allah´ın vâdi gelinceye kadar inkâr edenlere, yaptıklarından dolayı ya ansızın büyük bir belâ gelmeye devam edecek veya o belâ evlerinin yakınına inecek. Allah, vâdinden asla dönmez.', 13, 252, 43, 3450, 'Gök gürültüsü', 'Medine', 96),
(1739, 32, 13, 'Ra\'d', 'وَلَقَدِ اسْتُهْزِئَ بِرُسُلٍ مِنْ قَبْلِكَ فَاَمْلَيْتُ لِلَّذ۪ينَ كَفَرُوا ثُمَّ اَخَذْتُهُمْ۠ فَكَيْفَ كَانَ عِقَابِ', 'Andolsun, senden önceki peygamberlerle de alay edildi de ben inkâr edenlere mühlet verdim, sonra da onları yakaladım. (Görseydin ki) azabım nasılmış!', 13, 252, 43, 3450, 'Gök gürültüsü', 'Medine', 96),
(1740, 33, 13, 'Ra\'d', 'اَفَمَنْ هُوَ قَٓائِمٌ عَلٰى كُلِّ نَفْسٍ بِمَا كَسَبَتْۚ وَجَعَلُوا لِلّٰهِ شُرَكَٓاءَۜ قُلْ سَمُّوهُمْۜ اَمْ تُنَبِّؤُ۫نَهُ بِمَا لَا يَعْلَمُ فِي الْاَرْضِ اَمْ بِظَاهِرٍ مِنَ الْقَوْلِۜ بَلْ زُيِّنَ لِلَّذ۪ينَ كَفَرُوا مَكْرُهُمْ وَصُدُّوا عَنِ السَّب۪يلِۜ وَمَنْ يُضْلِلِ اللّٰهُ فَمَا لَهُ مِنْ هَادٍ', 'Herkesin kazandığını gözetleyip muhafaza eden, (hiç böyle yapamayan gibi olur mu?). Onlar Allah´a ortaklar koştular. De ki: «Onlara ad verin (onlar necidir?). Yoksa siz Allah´a yeryüzünde bilemeyeceği bir şeyi mi haber veriyorsunuz? Yahut boş laf mı ediyorsunuz?» Doğrusu inkâr edenlere hileleri süslü gösterildi ve onlar doğru yoldan alıkonuldular. Allah kimi saptırırsa artık onu doğru yola iletecek yoktur.', 13, 252, 43, 3450, 'Gök gürültüsü', 'Medine', 96),
(1741, 34, 13, 'Ra\'d', 'لَهُمْ عَذَابٌ فِي الْحَيٰوةِ الدُّنْيَا وَلَعَذَابُ الْاٰخِرَةِ اَشَقُّۚ وَمَا لَهُمْ مِنَ اللّٰهِ مِنْ وَاقٍ', 'Dünya hayatında onlara sadece bir azap vardır. Ahiret azabı ise daha şiddetlidir. Onları Allah´tan (onun azabından) koruyacak kimse de yoktur.', 13, 252, 43, 3450, 'Gök gürültüsü', 'Medine', 96),
(1742, 35, 13, 'Ra\'d', 'مَثَلُ الْجَنَّةِ الَّت۪ي وُعِدَ الْمُتَّقُونَۜ تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُۜ اُكُلُهَا دَٓائِمٌ وَظِلُّهَاۜ تِلْكَ عُقْبَى الَّذ۪ينَ اتَّقَوْاۗ وَعُقْبَى الْكَافِر۪ينَ النَّارُ', 'Takvâ sahiplerine vâdolunan cennetin özelliği (şudur): Onun zemininden ırmaklar akar. Yemişleri ve gölgesi süreklidir. İşte bu, (kötülüklerden) sakınanların (mutlu) sonudur. Kâfirlerin sonu ise ateştir.', 13, 253, 43, 3450, 'Gök gürültüsü', 'Medine', 96),
(1743, 36, 13, 'Ra\'d', 'وَالَّذ۪ينَ اٰتَيْنَاهُمُ الْكِتَابَ يَفْرَحُونَ بِمَٓا اُنْزِلَ اِلَيْكَ وَمِنَ الْاَحْزَابِ مَنْ يُنْكِرُ بَعْضَهُۜ قُلْ اِنَّـمَٓا اُمِرْتُ اَنْ اَعْبُدَ اللّٰهَ وَلَٓا اُشْرِكَ بِه۪ۜ اِلَيْهِ اَدْعُوا وَاِلَيْهِ مَاٰبِ', 'Kendilerine kitap verdiğimiz kimseler, sana indirilene (Kur´an´a) sevinirler. Fakat (senin aleyhinde birleşen) guruplardan onun bir kısmını inkâr eden de vardır. De ki: «Bana, sadece Allah´a kulluk etmem ve O´na ortak koşmamam emrolundu. Ben yalnız O´na çağırıyorum ve dönüş de yalnız O´nadır.', 13, 253, 43, 3450, 'Gök gürültüsü', 'Medine', 96),
(1744, 37, 13, 'Ra\'d', 'وَكَذٰلِكَ اَنْزَلْنَاهُ حُكْماً عَرَبِياًّۜ وَلَئِنِ اتَّبَعْتَ اَهْوَٓاءَهُمْ بَعْدَ مَا جَٓاءَكَ مِنَ الْعِلْمِۙ مَا لَكَ مِنَ اللّٰهِ مِنْ وَلِيٍّ وَلَا وَاقٍ۟', 'Ve böylece biz onu Arapça bir hüküm (hikmetli bir söz) olarak indirdik. Eğer sana gelen bu ilimden sonra, onların arzularına uyarsan, (işte o zaman) Allah tarafından senin ne bir dostun ne de koruyucun vardır.', 13, 253, 43, 3450, 'Gök gürültüsü', 'Medine', 96),
(1745, 38, 13, 'Ra\'d', 'وَلَقَدْ اَرْسَلْنَا رُسُلاً مِنْ قَبْلِكَ وَجَعَلْنَا لَهُمْ اَزْوَاجاً وَذُرِّيَّةًۜ وَمَا كَانَ لِرَسُولٍ اَنْ يَأْتِيَ بِاٰيَةٍ اِلَّا بِاِذْنِ اللّٰهِۜ لِكُلِّ اَجَلٍ كِتَابٌ', 'Andolsun senden önce de peygamberler gönderdik ve onlara da eşler ve çocuklar verdik. Allah´ın izni olmadan hiçbir peygamber için mucize getirme imkânı yoktur. Her müddetin (yazıldığı) bir kitap vardır.', 13, 253, 43, 3450, 'Gök gürültüsü', 'Medine', 96),
(1746, 39, 13, 'Ra\'d', 'يَمْحُوا اللّٰهُ مَا يَشَٓاءُ وَيُثْبِتُۚ وَعِنْدَهُٓ اُمُّ الْكِتَابِ', 'Allah dilediğini siler, (dilediğini de) sabit bırakır. Bütün kitapların aslı onun yanındadır.', 13, 253, 43, 3450, 'Gök gürültüsü', 'Medine', 96),
(1747, 40, 13, 'Ra\'d', 'وَاِنْ مَا نُرِيَنَّكَ بَعْضَ الَّذ۪ي نَعِدُهُمْ اَوْ نَتَوَفَّـيَنَّكَ فَاِنَّمَا عَلَيْكَ الْبَلَاغُ وَعَلَيْنَا الْحِسَابُ', 'Biz, onlara vâdettiğimizin (azabın) bir kısmını sana göstersek de veya (ondan önce) seni öldürürsek de sana ancak (Allah´ın emirlerini) tebliğ etmek düşer. Hesap yalnız bize aittir.', 13, 253, 43, 3450, 'Gök gürültüsü', 'Medine', 96),
(1748, 41, 13, 'Ra\'d', 'اَوَلَمْ يَرَوْا اَنَّا نَأْتِي الْاَرْضَ نَنْقُصُهَا مِنْ اَطْرَافِهَاۜ وَاللّٰهُ يَحْكُمُ لَا مُعَقِّبَ لِحُكْمِه۪ۜ وَهُوَ سَر۪يعُ الْحِسَابِ', 'Bizim, yeryüzüne gelip, onu uçlarından eksilttiğimizi görmediler mi? Allah (dilediği gibi) hükmeder, O´nun hükmünü bozacak kimse yoktur. Ve O hesabı çabuk görendir.', 13, 253, 43, 3450, 'Gök gürültüsü', 'Medine', 96),
(1749, 42, 13, 'Ra\'d', 'وَقَدْ مَكَرَ الَّذ۪ينَ مِنْ قَبْلِهِمْ فَلِلّٰهِ الْمَكْرُ جَم۪يعاًۜ يَعْلَمُ مَا تَكْسِبُ كُلُّ نَفْسٍۜ وَسَيَعْلَمُ الْكُفَّارُ لِمَنْ عُقْبَى الدَّارِ', 'Onlardan öncekiler de (peygamberlerine) tuzak kurmuşlardı; halbuki bütün tuzaklar Allah´a aittir. Çünkü O, herkesin ne kazanacağını bilir. Bu yurdun (dünyanın) sonunun kimin olduğunu yakında kâfirler bileceklerdir!', 13, 253, 43, 3450, 'Gök gürültüsü', 'Medine', 96),
(1750, 43, 13, 'Ra\'d', 'وَيَقُولُ الَّذ۪ينَ كَفَرُوا لَسْتَ مُرْسَلاًۜ قُلْ كَفٰى بِاللّٰهِ شَه۪يداً بَيْن۪ي وَبَيْنَكُمْۙ وَمَنْ عِنْدَهُ عِلْمُ الْكِتَابِ', 'Kâfir olanlar: Sen resûl olarak gönderilmiş bir kimse değilsin, derler. De ki: Benimle sizin aranızda şahit olarak Allah ve yanında Kitab´ın bilgisi olan (Peygamber) yeter.', 13, 254, 43, 3450, 'Gök gürültüsü', 'Medine', 96),
(1751, 1, 14, 'İbrahim', 'الٓـرٰ۠ كِتَابٌ اَنْزَلْنَاهُ اِلَيْكَ لِتُخْرِجَ النَّاسَ مِنَ الظُّلُمَاتِ اِلَى النُّورِ بِـاِذْنِ رَبِّهِمْ اِلٰى صِرَاطِ الْعَز۪يزِ الْحَم۪يدِۙ', 'Elif. Lâm. Râ. (Bu Kur´an), Rablerinin izniyle insanları karanlıklardan aydınlığa, yani her şeye galip (ve) övgüye lâyık olan Allah´ın yoluna çıkarman için sana indirdiğimiz bir kitaptır.', 13, 254, 52, 3461, 'Peygamber İbrahim', 'Mekke', 72),
(1752, 2, 14, 'İbrahim', 'اَللّٰهِ الَّذ۪ي لَهُ مَا فِي السَّمٰوَاتِ وَمَا فِي الْاَرْضِۜ وَوَيْلٌ لِلْكَافِر۪ينَ مِنْ عَذَابٍ شَد۪يدٍۙ', 'O Allah ki, göklerde ve yerde ne varsa hepsi O´nundur. Şiddetli azaptan dolayı kâfirlerin vay haline!', 13, 254, 52, 3461, 'Peygamber İbrahim', 'Mekke', 72),
(1753, 3, 14, 'İbrahim', 'اَلَّذ۪ينَ يَسْتَحِبُّونَ الْحَيٰوةَ الدُّنْيَا عَلَى الْاٰخِرَةِ وَيَصُدُّونَ عَنْ سَب۪يلِ اللّٰهِ وَيَبْغُونَهَا عِوَجاًۜ اُو۬لٰٓئِكَ ف۪ي ضَلَالٍ بَع۪يدٍ', 'Dünya hayatını ahirete tercih edenler, Allah yolundan alıkoyanlar ve onun eğriliğini isteyenler var ya, işte onlar (haktan) uzak bir sapıklık içindedirler.', 13, 254, 52, 3461, 'Peygamber İbrahim', 'Mekke', 72),
(1754, 4, 14, 'İbrahim', 'وَمَٓا اَرْسَلْنَا مِنْ رَسُولٍ اِلَّا بِلِسَانِ قَوْمِه۪ لِيُبَيِّنَ لَهُمْۜ فَيُضِلُّ اللّٰهُ مَنْ يَشَٓاءُ وَيَهْد۪ي مَنْ يَشَٓاءُۜ وَهُوَ الْعَز۪يزُ الْحَك۪يمُ', '(Allah´ın emirlerini) onlara iyice açıklasın diye her peygamberi yalnız kendi kavminin diliyle gönderdik. Artık Allah dilediğini saptırır, dilediğini de doğru yola iletir. Çünkü O, güç ve hikmet sahibidir.', 13, 254, 52, 3461, 'Peygamber İbrahim', 'Mekke', 72),
(1755, 5, 14, 'İbrahim', 'وَلَقَدْ اَرْسَلْنَا مُوسٰى بِاٰيَاتِنَٓا اَنْ اَخْرِجْ قَوْمَكَ مِنَ الظُّلُمَاتِ اِلَى النُّورِ وَذَكِّرْهُمْ بِاَيَّامِ اللّٰهِۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَاتٍ لِكُلِّ صَبَّارٍ شَكُورٍ', 'Andolsun ki Musa´yı da: Kavmini karanlıklardan aydınlığa çıkar ve onlara Allah´ın (geçmiş kavimlerin başına getirdiği felâket) günlerini hatırlat, diye mucizelerimizle gönderdik. Şüphesiz ki bunda çok sabırlı, çok şükreden herkes için ibretler vardır.', 13, 254, 52, 3461, 'Peygamber İbrahim', 'Mekke', 72),
(1756, 6, 14, 'İbrahim', 'وَاِذْ قَالَ مُوسٰى لِقَوْمِهِ اذْكُرُوا نِعْمَةَ اللّٰهِ عَلَيْكُمْ اِذْ اَنْجٰيكُمْ مِنْ اٰلِ فِرْعَوْنَ يَسُومُونَكُمْ سُٓوءَ الْعَذَابِ وَيُذَبِّحُونَ اَبْنَٓاءَكُمْ وَيَسْتَحْيُونَ نِسَٓاءَكُمْۜ وَف۪ي ذٰلِكُمْ بَلَٓاءٌ مِنْ رَبِّكُمْ عَظ۪يمٌ۟', 'Hani Musa kavmine demişti ki: «Allah´ın üzerinizdeki nimetini hatırlayın. Çünkü O, sizi işkencenin en kötüsüne sürmekte ve oğullarınızı kesip, kadınlarınızı (kızlarınızı) bırakmakta olan Firavun ailesinden kurtardı. İşte bu size anlatılanlarda, Rabbinizden büyük bir imtihan vardır.»', 13, 255, 52, 3461, 'Peygamber İbrahim', 'Mekke', 72),
(1757, 7, 14, 'İbrahim', 'وَاِذْ تَاَذَّنَ رَبُّكُمْ لَئِنْ شَكَرْتُمْ لَاَز۪يدَنَّكُمْ وَلَئِنْ كَفَرْتُمْ اِنَّ عَذَاب۪ي لَشَد۪يدٌ', '«Hatırlayın ki Rabbiniz size: Eğer şükrederseniz, elbette size (nimetimi) artıracağım ve eğer nankörlük ederseniz hiç şüphesiz azabım çok şiddetlidir! diye bildirmişti.»', 13, 255, 52, 3461, 'Peygamber İbrahim', 'Mekke', 72),
(1758, 8, 14, 'İbrahim', 'وَقَالَ مُوسٰٓى اِنْ تَكْفُرُٓوا اَنْتُمْ وَمَنْ فِي الْاَرْضِ جَم۪يعاًۙ فَاِنَّ اللّٰهَ لَغَنِيٌّ حَم۪يدٌ', 'Musa dedi ki: «Eğer siz ve yeryüzünde olanların hepsi nankörlük etseniz, bilin ki Allah gerçekten zengindir, hamdedilmeye lâyıktır.»', 13, 255, 52, 3461, 'Peygamber İbrahim', 'Mekke', 72),
(1759, 9, 14, 'İbrahim', 'اَلَمْ يَأْتِكُمْ نَبَؤُا الَّذ۪ينَ مِنْ قَبْلِكُمْ قَوْمِ نُوحٍ وَعَادٍ وَثَمُودَۜۛ وَالَّذ۪ينَ مِنْ بَعْدِهِمْۜۛ لَا يَعْلَمُهُمْ اِلَّا اللّٰهُۜ جَٓاءَتْهُمْ رُسُلُهُمْ بِالْبَيِّنَاتِ فَرَدُّٓوا اَيْدِيَهُمْ ف۪ٓي اَفْوَاهِهِمْ وَقَالُٓوا اِنَّا كَفَرْنَا بِمَٓا اُرْسِلْتُمْ بِه۪ وَاِنَّا لَف۪ي شَكٍّ مِمَّا تَدْعُونَـنَٓا اِلَيْهِ مُر۪يبٍ', 'Sizden öncekilerin, Nuh, Âd ve Semûd kavimlerinin ve onlardan sonrakilerin haberleri size gelmedi mi? Onları Allah´tan başkası bilmez. Peygamberleri kendilerine mucizeler getirdi de onlar, ellerini peygamberlerinin ağızlarına bastılar ve dediler ki: Biz, size gönderileni inkâr ettik ve bizi kendisine çağırdığınız şeye karşı derin bir kuşku içindeyiz.', 13, 255, 52, 3461, 'Peygamber İbrahim', 'Mekke', 72),
(1760, 10, 14, 'İbrahim', 'قَالَتْ رُسُلُهُمْ اَفِي اللّٰهِ شَكٌّ فَاطِرِ السَّمٰوَاتِ وَالْاَرْضِۜ يَدْعُوكُمْ لِيَغْفِرَ لَكُمْ مِنْ ذُنُوبِكُمْ وَيُؤَخِّرَكُمْ اِلٰٓى اَجَلٍ مُسَمًّىۜ قَالُٓوا اِنْ اَنْتُمْ اِلَّا بَشَرٌ مِثْلُنَاۜ تُر۪يدُونَ اَنْ تَصُدُّونَا عَمَّا كَانَ يَعْبُدُ اٰبَٓاؤُ۬نَا فَأْتُونَا بِسُلْطَانٍ مُب۪ينٍ', 'Peygamberleri dedi ki: Gökleri ve yeri yaratan Allah hakkında şüphe mi var? Halbuki O, sizin günahlarınızdan bir kısmını bağışlamak ve sizi muayyen bir vakte kadar yaşatmak için sizi (hak dine) çağırıyor. Onlar dediler ki: Siz de bizim gibi bir insandan başka bir şey değilsiniz. Siz bizi atalarımızın tapmış olduğu şeylerden döndürmek istiyorsunuz. Öyleyse bize, apaçık bir delil getirin!', 13, 255, 52, 3461, 'Peygamber İbrahim', 'Mekke', 72),
(1761, 11, 14, 'İbrahim', 'قَالَتْ لَهُمْ رُسُلُهُمْ اِنْ نَحْنُ اِلَّا بَشَرٌ مِثْلُكُمْ وَلٰكِنَّ اللّٰهَ يَمُنُّ عَلٰى مَنْ يَشَٓاءُ مِنْ عِبَادِه۪ۜ وَمَا كَانَ لَـنَٓا اَنْ نَأْتِيَكُمْ بِسُلْطَانٍ اِلَّا بِاِذْنِ اللّٰهِۜ وَعَلَى اللّٰهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُونَ', 'Peygamberleri onlara dediler ki: «(Evet) biz sizin gibi bir insandan başkası değiliz. Fakat Allah nimetini kullarından dilediğine lütfeder. Allah´ın izni olmadan bizim size bir delil getirmemize imkân yoktur. Müminler ancak Allah´a dayansınlar.»', 13, 256, 52, 3461, 'Peygamber İbrahim', 'Mekke', 72),
(1762, 12, 14, 'İbrahim', 'وَمَا لَـنَٓا اَلَّا نَتَوَكَّلَ عَلَى اللّٰهِ وَقَدْ هَدٰينَا سُبُلَنَاۜ وَلَنَصْبِرَنَّ عَلٰى مَٓا اٰذَيْتُمُونَاۜ وَعَلَى اللّٰهِ فَلْيَتَوَكَّلِ الْمُتَوَكِّلُونَ۟', '«Hem, bize yollarımızı göstermiş olduğu halde ne diye biz, Allah´a dayanıp güvenmeyelim? Sizin bize verdiğiniz eziyete elbette katlanacağız. Tevekkül edenler yalnız Allah´a tevekkülde sebat etsinler.»', 13, 256, 52, 3461, 'Peygamber İbrahim', 'Mekke', 72),
(1763, 13, 14, 'İbrahim', 'وَقَالَ الَّذ۪ينَ كَفَرُوا لِرُسُلِهِمْ لَنُخْرِجَنَّكُمْ مِنْ اَرْضِنَٓا اَوْ لَتَعُودُنَّ ف۪ي مِلَّتِنَاۜ فَاَوْحٰٓى اِلَيْهِمْ رَبُّهُمْ لَنُهْلِكَنَّ الظَّالِم۪ينَۙ', 'Kâfir olanlar peygamberlerine dediler ki: «Elbette sizi ya yurdumuzdan çıkaracağız, ya da mutlaka dinimize döneceksiniz!» Rableri de onlara: «Zalimleri mutlaka helâk edeceğiz!» diye vahyetti.', 13, 256, 52, 3461, 'Peygamber İbrahim', 'Mekke', 72),
(1764, 14, 14, 'İbrahim', 'وَلَنُسْكِنَنَّكُمُ الْاَرْضَ مِنْ بَعْدِهِمْۜ ذٰلِكَ لِمَنْ خَافَ مَقَام۪ي وَخَافَ وَع۪يدِ', 'Ve (ey inananlar!) Onlardan sonra sizi mutlaka o yerde yerleştireceğiz. İşte bu, makamımdan korkan ve tehdidimden sakınan kimselere mahsustur.', 13, 256, 52, 3461, 'Peygamber İbrahim', 'Mekke', 72),
(1765, 15, 14, 'İbrahim', 'وَاسْتَفْتَحُوا وَخَابَ كُلُّ جَبَّارٍ عَن۪يدٍۙ', '(Peygamberler) fetih istediler (Allah da verdi). Her inatçı zorba da hüsrana uğradı.', 13, 256, 52, 3461, 'Peygamber İbrahim', 'Mekke', 72),
(1766, 16, 14, 'İbrahim', 'مِنْ وَرَٓائِه۪ جَهَنَّمُ وَيُسْقٰى مِنْ مَٓاءٍ صَد۪يدٍۙ', 'Ardından da (o inatçı zorbaya) cehennem vardır; kendisine irinli su içirilecektir!', 13, 256, 52, 3461, 'Peygamber İbrahim', 'Mekke', 72),
(1767, 17, 14, 'İbrahim', 'يَتَجَرَّعُهُ وَلَا يَكَادُ يُس۪يغُهُ وَيَأْت۪يهِ الْمَوْتُ مِنْ كُلِّ مَكَانٍ وَمَا هُوَ بِمَيِّتٍۜ وَمِنْ وَرَٓائِه۪ عَذَابٌ غَل۪يظٌ', 'Onu yudumlamaya çalışacak, fakat boğazından geçiremeyecek ve ona her yandan ölüm gelecek, oysa o ölecek değildir (ki azaptan kurtulsun). Bundan ötede şiddetli bir azap da vardır.', 13, 256, 52, 3461, 'Peygamber İbrahim', 'Mekke', 72),
(1768, 18, 14, 'İbrahim', 'مَثَلُ الَّذ۪ينَ كَفَرُوا بِرَبِّهِمْ اَعْمَالُهُمْ كَرَمَادٍۨ اشْتَدَّتْ بِهِ الرّ۪يحُ ف۪ي يَوْمٍ عَاصِفٍۜ لَا يَقْدِرُونَ مِمَّا كَسَبُوا عَلٰى شَيْءٍۜ ذٰلِكَ هُوَ الضَّلَالُ الْبَع۪يدُ', 'Rablerini inkâr edenlerin durumu (şudur): Onların amelleri fırtınalı bir günde rüzgârın şiddetle savurduğu küle benzer. Kazandıklarından hiçbir şeyi elde edemezler. İyiden iyiye sapıtma işte budur.', 13, 256, 52, 3461, 'Peygamber İbrahim', 'Mekke', 72),
(1769, 19, 14, 'İbrahim', 'اَلَمْ تَرَ اَنَّ اللّٰهَ خَلَقَ السَّمٰوَاتِ وَالْاَرْضَ بِالْحَقِّۜ اِنْ يَشَأْ يُذْهِبْكُمْ وَيَأْتِ بِخَلْقٍ جَد۪يدٍۙ', 'Allah´ın gökleri ve yeri hak ile yarattığını görmedin mi? O dilerse sizi ortadan kaldırıp yepyeni bir halk getirir.', 13, 257, 52, 3461, 'Peygamber İbrahim', 'Mekke', 72),
(1770, 20, 14, 'İbrahim', 'وَمَا ذٰلِكَ عَلَى اللّٰهِ بِعَز۪يزٍ', 'Bu, Allah´a güç değildir.', 13, 257, 52, 3461, 'Peygamber İbrahim', 'Mekke', 72),
(1771, 21, 14, 'İbrahim', 'وَبَرَزُوا لِلّٰهِ جَم۪يعاً فَقَالَ الضُّعَفٰٓؤُ۬ا لِلَّذ۪ينَ اسْتَكْـبَرُٓوا اِنَّا كُنَّا لَكُمْ تَبَعاً فَهَلْ اَنْتُمْ مُغْنُونَ عَنَّا مِنْ عَذَابِ اللّٰهِ مِنْ شَيْءٍۜ قَالُوا لَوْ هَدٰينَا اللّٰهُ لَهَدَيْنَاكُمْۜ سَوَٓاءٌ عَلَيْنَٓا اَجَزِعْنَٓا اَمْ صَبَرْنَا مَا لَنَا مِنْ مَح۪يصٍ۟', '(Kıyamet gününde) hepsi Allah´ın huzuruna çıkacak ve zayıflar o büyüklük taslayanlara diyecekler ki: «Biz sizin tâbilerinizdik. Şimdi siz, Allah´ın azabından herhangi bir şeyi bizden savabilir misiniz?» Onlar da diyecekler ki: «(Ne yapalım) Allah bizi hidayete erdirseydi biz de sizi doğru yola iletirdik. Şimdi sızlansak da sabretsek de birdir. Çünkü bizim için sığınacak bir yer yoktur.»', 13, 257, 52, 3461, 'Peygamber İbrahim', 'Mekke', 72),
(1772, 22, 14, 'İbrahim', 'وَقَالَ الشَّيْطَانُ لَمَّا قُضِيَ الْاَمْرُ اِنَّ اللّٰهَ وَعَدَكُمْ وَعْدَ الْحَقِّ وَوَعَدْتُكُمْ فَاَخْلَفْتُكُمْۜ وَمَا كَانَ لِيَ عَلَيْكُمْ مِنْ سُلْطَانٍ اِلَّٓا اَنْ دَعَوْتُكُمْ فَاسْتَجَبْتُمْ ل۪يۚ فَلَا تَلُومُون۪ي وَلُومُٓوا اَنْفُسَكُمْۜ مَٓا اَنَا۬ بِمُصْرِخِكُمْ وَمَٓا اَنْتُمْ بِمُصْرِخِيَّۜ اِنّ۪ي كَفَرْتُ بِمَٓا اَشْرَكْتُمُونِ مِنْ قَبْلُۜ اِنَّ الظَّالِم۪ينَ لَهُمْ عَذَابٌ اَل۪يمٌ', '(Hesapları görülüp) iş bitirilince, şeytan diyecek ki: «Şüphesiz Allah size gerçek olanı vâdetti, ben de size vâdettim ama, size yalancı çıktım. Zaten benim size karşı bir gücüm yoktu. Ben, sadece sizi (inkâra) çağırdım, siz de benim davetime hemen koştunuz. O halde beni yermeyin, kendinizi yerin. Ne ben sizi kurtarabilirim, ne de siz beni kurtarabilirsiniz! Kuşkusuz daha önce ben, beni (Allah´a) ortak koşmanızı reddettim.» Şüphesiz zalimler için elem verici bir azap vardır.', 13, 257, 52, 3461, 'Peygamber İbrahim', 'Mekke', 72),
(1773, 23, 14, 'İbrahim', 'وَاُدْخِلَ الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ جَنَّاتٍ تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُ خَالِد۪ينَ ف۪يهَا بِاِذْنِ رَبِّهِمْۜ تَحِيَّتُهُمْ ف۪يهَا سَلَامٌ', 'İman edip de iyi işler yapanlar, Rablerinin izniyle içinde ebedî kalacakları ve zemininden ırmaklar akan cennetlere sokulacaklardır. Orada (birbirleriyle) karşılaştıkça söyledikleri «selam» dır.', 13, 257, 52, 3461, 'Peygamber İbrahim', 'Mekke', 72),
(1774, 24, 14, 'İbrahim', 'اَلَمْ تَرَ كَيْفَ ضَرَبَ اللّٰهُ مَثَلاً كَلِمَةً طَيِّبَةً كَشَجَرَةٍ طَيِّبَةٍ اَصْلُهَا ثَابِتٌ وَفَرْعُهَا فِي السَّمَٓاءِۙ', 'Görmedin mi Allah nasıl bir misal getirdi: Güzel bir sözü, kökü (yerde) sabit, dalları gökte olan güzel bir ağaca (benzetti).', 13, 257, 52, 3461, 'Peygamber İbrahim', 'Mekke', 72),
(1775, 25, 14, 'İbrahim', 'تُؤْت۪ٓي اُكُلَهَا كُلَّ ح۪ينٍ بِاِذْنِ رَبِّهَاۜ وَيَضْرِبُ اللّٰهُ الْاَمْثَالَ لِلنَّاسِ لَعَلَّهُمْ يَتَذَكَّرُونَ', '(O ağaç), Rabbinin izniyle her zaman yemişini verir. Öğüt alsınlar diye Allah insanlara misaller getirir.', 13, 258, 52, 3461, 'Peygamber İbrahim', 'Mekke', 72),
(1776, 26, 14, 'İbrahim', 'وَمَثَلُ كَلِمَةٍ خَب۪يثَةٍ كَشَجَرَةٍ خَب۪يثَةٍۨ اجْتُثَّتْ مِنْ فَوْقِ الْاَرْضِ مَا لَهَا مِنْ قَرَارٍ', 'Kötü bir sözün misali, gövdesi yerden koparılmış, o yüzden ayakta durma imkânı olmayan (kötü) bir ağaca benzer.', 13, 258, 52, 3461, 'Peygamber İbrahim', 'Mekke', 72),
(1777, 27, 14, 'İbrahim', 'يُثَبِّتُ اللّٰهُ الَّذ۪ينَ اٰمَنُوا بِالْقَوْلِ الثَّابِتِ فِي الْحَيٰوةِ الدُّنْيَا وَفِي الْاٰخِرَةِۚ وَيُضِلُّ اللّٰهُ الظَّالِم۪ينَ وَيَفْعَلُ اللّٰهُ مَا يَشَٓاءُ۟', 'Allah Teâlâ sağlam sözle iman edenleri hem dünya hayatında hem de ahirette sapasağlam tutar. Zalimleri ise Allah saptırır. Allah dilediğini yapar.', 13, 258, 52, 3461, 'Peygamber İbrahim', 'Mekke', 72),
(1778, 28, 14, 'İbrahim', 'اَلَمْ تَرَ اِلَى الَّذ۪ينَ بَدَّلُوا نِعْمَتَ اللّٰهِ كُفْراً وَاَحَلُّوا قَوْمَهُمْ دَارَ الْبَوَارِۙ', 'Allah´ın nimetine nankörlükle karşılık veren ve sonunda kavimlerini helâk yurduna sürükleyenleri görmedin mi?', 13, 258, 52, 3461, 'Peygamber İbrahim', 'Mekke', 72),
(1779, 29, 14, 'İbrahim', 'جَهَنَّمَۚ يَصْلَوْنَهَاۜ وَبِئْسَ الْقَرَارُ', 'Onlar cehenneme girecekler. O ne kötü karargâhtır!', 13, 258, 52, 3461, 'Peygamber İbrahim', 'Mekke', 72),
(1780, 30, 14, 'İbrahim', 'وَجَعَلُوا لِلّٰهِ اَنْدَاداً لِيُضِلُّوا عَنْ سَب۪يلِه۪ۜ قُلْ تَمَتَّعُوا فَاِنَّ مَص۪يرَكُمْ اِلَى النَّارِ', '(İnsanları) Allah yolundan saptırmak için O´na ortaklar koştular. De ki: (İstediğiniz gibi) yaşayın! Çünkü dönüşünüz ateşedir.', 13, 258, 52, 3461, 'Peygamber İbrahim', 'Mekke', 72),
(1781, 31, 14, 'İbrahim', 'قُلْ لِعِبَادِيَ الَّذ۪ينَ اٰمَنُوا يُق۪يمُوا الصَّلٰوةَ وَيُنْفِقُوا مِمَّا رَزَقْنَاهُمْ سِراًّ وَعَلَانِيَةً مِنْ قَبْلِ اَنْ يَأْتِيَ يَوْمٌ لَا بَيْعٌ ف۪يهِ وَلَا خِلَالٌ', 'İman eden kullarıma söyle: Namazlarını dosdoğru kılsınlar, kendisinde ne alışveriş, ne de dostluk bulunan bir gün gelmeden önce, kendilerine verdiğimiz rızıklardan (Allah için) gizli açık harcasınlar.', 13, 258, 52, 3461, 'Peygamber İbrahim', 'Mekke', 72);
INSERT INTO `Ayetler` (`id`, `AyetNo`, `SureNo`, `SureAdi`, `Metin`, `Mana`, `Cuzu`, `Sayfasi`, `AyetSayisi`, `HarfSayisi`, `AdAnlami`, `IndigiYer`, `InisSirasi`) VALUES
(1782, 32, 14, 'İbrahim', 'اَللّٰهُ الَّذ۪ي خَلَقَ السَّمٰوَاتِ وَالْاَرْضَ وَاَنْزَلَ مِنَ السَّمَٓاءِ مَٓاءً فَاَخْرَجَ بِه۪ مِنَ الثَّمَرَاتِ رِزْقاً لَكُمْۚ وَسَخَّرَ لَكُمُ الْفُلْكَ لِتَجْرِيَ فِي الْبَحْرِ بِاَمْرِه۪ۚ وَسَخَّرَ لَكُمُ الْاَنْهَارَۚ', '(O öyle lütufkâr) Allah´tır ki, gökleri ve yeri yarattı, gökten suyu indirip onunla rızık olarak size türlü meyveler çıkardı; izni ile denizde yüzüp gitmeleri için gemileri emrinize verdi; nehirleri de sizin (yararlanmanız) için akıttı.', 13, 258, 52, 3461, 'Peygamber İbrahim', 'Mekke', 72),
(1783, 33, 14, 'İbrahim', 'وَسَخَّرَ لَكُمُ الشَّمْسَ وَالْقَمَرَ دَٓائِبَيْنِۚ وَسَخَّرَ لَكُمُ الَّيْلَ وَالنَّهَارَۚ', 'Düzenli seyreden güneşi ve ayı size faydalı kıldı; geceyi ve gündüzü de istifadenize verdi.', 13, 258, 52, 3461, 'Peygamber İbrahim', 'Mekke', 72),
(1784, 34, 14, 'İbrahim', 'وَاٰتٰيكُمْ مِنْ كُلِّ مَا سَاَلْتُمُوهُۜ وَاِنْ تَعُدُّوا نِعْمَتَ اللّٰهِ لَا تُحْصُوهَاۜ اِنَّ الْاِنْسَانَ لَظَلُومٌ كَفَّارٌ۟', 'O size istediğiniz her şeyden verdi. Allah´ın nimetini sayacak olsanız sayamazsınız. Doğrusu insan çok zalim, çok nankördür!', 13, 259, 52, 3461, 'Peygamber İbrahim', 'Mekke', 72),
(1785, 35, 14, 'İbrahim', 'وَاِذْ قَالَ اِبْرٰه۪يمُ رَبِّ اجْعَلْ هٰذَا الْبَلَدَ اٰمِناً وَاجْنُبْن۪ي وَبَنِيَّ اَنْ نَعْبُدَ الْاَصْنَامَۜ', 'Hatırla ki İbrahim şöyle demişti: «Rabbim! Bu şehri (Mekke´yi) emniyetli kıl, beni ve oğullarımı putlara tapmaktan uzak tut!»', 13, 259, 52, 3461, 'Peygamber İbrahim', 'Mekke', 72),
(1786, 36, 14, 'İbrahim', 'رَبِّ اِنَّهُنَّ اَضْلَلْنَ كَث۪يراً مِنَ النَّاسِۚ فَمَنْ تَبِعَن۪ي فَاِنَّهُ مِنّ۪يۚ وَمَنْ عَصَان۪ي فَاِنَّكَ غَفُورٌ رَح۪يمٌ', '«Çünkü, onlar (putlar), insanlardan birçoğunun sapmasına sebep oldular, Rabbim. Şimdi kim bana uyarsa o bendendir. Kim de bana karşı gelirse, artık sen gerçekten çok bağışlayan, pek esirgeyensin.»', 13, 259, 52, 3461, 'Peygamber İbrahim', 'Mekke', 72),
(1787, 37, 14, 'İbrahim', 'رَبَّـنَٓا اِنّ۪ٓي اَسْكَنْتُ مِنْ ذُرِّيَّت۪ي بِوَادٍ غَيْرِ ذ۪ي زَرْعٍ عِنْدَ بَيْتِكَ الْمُحَرَّمِۙ رَبَّـنَا لِيُق۪يمُوا الصَّلٰوةَ فَاجْعَلْ اَفْـِٔدَةً مِنَ النَّاسِ تَهْو۪ٓي اِلَيْهِمْ وَارْزُقْهُمْ مِنَ الثَّمَرَاتِ لَعَلَّهُمْ يَشْكُرُونَ', '«Ey Rabbimiz! Ey sahibimiz! Namazı dosdoğru kılmaları için ben, neslimden bir kısmını senin Beyt-i Harem´inin (Kâbe´nin) yanında, ziraat yapılmayan bir vâdiye yerleştirdim. Artık sen de insanlardan bir kısmının gönüllerini onlara meyledici kıl ve meyvelerden bunlara rızık ver! Umulur ki bu nimetlere şükrederler.»', 13, 259, 52, 3461, 'Peygamber İbrahim', 'Mekke', 72),
(1788, 38, 14, 'İbrahim', 'رَبَّـنَٓا اِنَّكَ تَعْلَمُ مَا نُخْف۪ي وَمَا نُعْلِنُۜ وَمَا يَخْفٰى عَلَى اللّٰهِ مِنْ شَيْءٍ فِي الْاَرْضِ وَلَا فِي السَّمَٓاءِ', '«Ey Rabbimiz! Şüphesiz ki sen bizim gizleyeceğimizi de açıklayacağımızı da bilirsin. Çünkü ne yerde ne de gökte hiçbir şey Allah´a gizli kalmaz.»', 13, 259, 52, 3461, 'Peygamber İbrahim', 'Mekke', 72),
(1789, 39, 14, 'İbrahim', 'اَلْحَمْدُ لِلّٰهِ الَّذ۪ي وَهَبَ ل۪ي عَلَى الْكِبَرِ اِسْمٰع۪يلَ وَاِسْحٰقَۜ اِنَّ رَبّ۪ي لَسَم۪يعُ الدُّعَٓاءِ', '«İhtiyar halimde bana İsmail´i ve İshak´ı lütfeden Allah´a hamdolsun! Şüphesiz Rabbim duayı işitendir.»', 13, 259, 52, 3461, 'Peygamber İbrahim', 'Mekke', 72),
(1790, 40, 14, 'İbrahim', 'رَبِّ اجْعَلْن۪ي مُق۪يمَ الصَّلٰوةِ وَمِنْ ذُرِّيَّت۪يۗ رَبَّـنَا وَتَقَبَّلْ دُعَٓاءِ', '«Ey Rabbim! Beni ve soyumdan gelecekleri namazı devamlı kılanlardan eyle; ey Rabbimiz! Duamı kabul et!»', 13, 259, 52, 3461, 'Peygamber İbrahim', 'Mekke', 72),
(1791, 41, 14, 'İbrahim', 'رَبَّـنَا اغْفِرْ ل۪ي وَلِوَالِدَيَّ وَلِلْمُؤْمِن۪ينَ يَوْمَ يَقُومُ الْحِسَابُ۟', '«Ey Rabbimiz! (Amellerin) hesap olunacağı gün beni, ana babamı ve müminleri bağışla!»', 13, 259, 52, 3461, 'Peygamber İbrahim', 'Mekke', 72),
(1792, 42, 14, 'İbrahim', 'وَلَا تَحْسَبَنَّ اللّٰهَ غَافِلاً عَمَّا يَعْمَلُ الظَّالِمُونَۜ اِنَّمَا يُؤَخِّرُهُمْ لِيَوْمٍ تَشْخَصُ ف۪يهِ الْاَبْصَارُۙ', '(Resûlüm!) Sakın, Allah´ı zalimlerin yaptıklarından habersiz sanma! Ancak, Allah onları (cezalandırmayı), korkudan gözlerin dışarı fırlayacağı bir güne erteliyor.', 13, 259, 52, 3461, 'Peygamber İbrahim', 'Mekke', 72),
(1793, 43, 14, 'İbrahim', 'مُهْطِع۪ينَ مُقْنِع۪ي رُؤُ۫سِهِمْ لَا يَرْتَدُّ اِلَيْهِمْ طَرْفُهُمْۚ وَاَفْـِٔدَتُهُمْ هَوَٓاءٌۜ', 'Zihinleri bomboş olarak kendilerine bile dönüp bakamaz durumda, gözleri göğe dikilmiş bir vaziyette koşarlar.', 13, 260, 52, 3461, 'Peygamber İbrahim', 'Mekke', 72),
(1794, 44, 14, 'İbrahim', 'وَاَنْذِرِ النَّاسَ يَوْمَ يَأْت۪يهِمُ الْعَذَابُۙ فَيَقُولُ الَّذ۪ينَ ظَلَمُوا رَبَّـنَٓا اَخِّرْنَٓا اِلٰٓى اَجَلٍ قَر۪يبٍۙ نُجِبْ دَعْوَتَكَ وَنَتَّبِـعِ الرُّسُلَۜ اَوَلَمْ تَكُونُٓوا اَقْسَمْتُمْ مِنْ قَبْلُ مَا لَكُمْ مِنْ زَوَالٍۙ', 'Kendilerine azabın geleceği, bu yüzden zalimlerin: «Ey Rabbimiz! Yakın bir müddete kadar bize süre ver de senin davetine uyalım ve peygamberlere tâbi olalım» diyecekleri gün hakkında insanları uyar. (Onlara denilir ki:) «Daha önce, sizin için bir zevâl olmadığına, yemin etmemiş miydiniz?»', 13, 260, 52, 3461, 'Peygamber İbrahim', 'Mekke', 72),
(1795, 45, 14, 'İbrahim', 'وَسَكَنْتُمْ ف۪ي مَسَاكِنِ الَّذ۪ينَ ظَلَمُٓوا اَنْفُسَهُمْ وَتَبَيَّنَ لَكُمْ كَيْفَ فَعَلْنَا بِهِمْ وَضَرَبْنَا لَكُمُ الْاَمْثَالَ', '«(Sizden önce) kendilerine zulmedenlerin yurtlarında oturdunuz. Onlara nasıl muamele ettiğimiz size apaçık belli oldu. Ve size misaller de verdik.»', 13, 260, 52, 3461, 'Peygamber İbrahim', 'Mekke', 72),
(1796, 46, 14, 'İbrahim', 'وَقَدْ مَكَرُوا مَكْرَهُمْ وَعِنْدَ اللّٰهِ مَكْرُهُمْۜ وَاِنْ كَانَ مَكْرُهُمْ لِتَزُولَ مِنْهُ الْجِبَالُ', 'Hilelerinin cezası Allah katında (malum) iken, onlar, tuzaklarını kurmuşlardı. Halbuki onların hileleriyle dağlar yerinden gidecek değildi!', 13, 260, 52, 3461, 'Peygamber İbrahim', 'Mekke', 72),
(1797, 47, 14, 'İbrahim', 'فَلَا تَحْسَبَنَّ اللّٰهَ مُخْلِفَ وَعْدِه۪ رُسُلَهُۜ اِنَّ اللّٰهَ عَز۪يزٌ ذُوانْتِقَامٍۜ', 'O halde, sakın Allah´ın peygamberlerine verdiği sözden cayacağını sanma! Çünkü Allah mutlak üstündür, kimsenin yaptığını yanına bırakmaz.', 13, 260, 52, 3461, 'Peygamber İbrahim', 'Mekke', 72),
(1798, 48, 14, 'İbrahim', 'يَوْمَ تُبَدَّلُ الْاَرْضُ غَيْرَ الْاَرْضِ وَالسَّمٰوَاتُ وَبَرَزُوا لِلّٰهِ الْوَاحِدِ الْقَهَّارِ', 'Yer başka bir yer, gökler de (başka gökler) haline getirildiği, (insanlar) bir ve gücüne karşı durulamaz olan Allah´ın huzuruna çıktıkları gün (Allah bütün zalimlerin cezasını verecektir).', 13, 260, 52, 3461, 'Peygamber İbrahim', 'Mekke', 72),
(1799, 49, 14, 'İbrahim', 'وَتَرَى الْمُجْرِم۪ينَ يَوْمَئِذٍ مُقَرَّن۪ينَ فِي الْاَصْفَادِۚ', 'O gün, günahkârların zincire vurulmuş olduğunu görürsün.', 13, 260, 52, 3461, 'Peygamber İbrahim', 'Mekke', 72),
(1800, 50, 14, 'İbrahim', 'سَرَاب۪يلُهُمْ مِنْ قَطِرَانٍ وَتَغْشٰى وُجُوهَهُمُ النَّارُۙ', 'Onların gömlekleri katrandandır, yüzlerini de ateş bürümektedir.', 13, 260, 52, 3461, 'Peygamber İbrahim', 'Mekke', 72),
(1801, 51, 14, 'İbrahim', 'لِيَجْزِيَ اللّٰهُ كُلَّ نَفْسٍ مَا كَسَبَتْۜ اِنَّ اللّٰهَ سَر۪يعُ الْحِسَابِ', 'Allah herkese kazandığının karşılığını vermek için (onları diriltecektir.) Kuşkusuz Allah, hesabı çabuk görendir.', 13, 260, 52, 3461, 'Peygamber İbrahim', 'Mekke', 72),
(1802, 52, 14, 'İbrahim', 'هٰذَا بَلَاغٌ لِلنَّاسِ وَلِيُنْذَرُوا بِه۪ وَلِيَعْلَمُٓوا اَنَّمَا هُوَ اِلٰهٌ وَاحِدٌ وَلِيَذَّكَّرَ اُو۬لُوا الْاَلْبَابِ', 'İşte bu (Kur´an), kendisiyle uyarılsınlar, Allah´ın ancak bir tek Tanrı olduğunu bilsinler ve akıl sahipleri iyice düşünüp öğüt alsınlar diye insanlara (gönderilmiş) bir bildiridir.', 13, 260, 52, 3461, 'Peygamber İbrahim', 'Mekke', 72),
(1803, 1, 15, 'Hicr', 'الٓـرٰ۠ تِلْكَ اٰيَاتُ الْكِتَابِ وَقُرْاٰنٍ مُب۪ينٍ', 'Elif. Lâm. Râ. Bunlar Kitab´ın ve apaçık bir Kur´an´ın âyetleridir.', 14, 261, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1804, 2, 15, 'Hicr', 'رُبَمَا يَوَدُّ الَّذ۪ينَ كَفَرُوا لَوْ كَانُوا مُسْلِم۪ينَ', 'İnkâr edenler zaman zaman, keşke biz de müslüman olsaydık, diye arzu ederler.', 14, 261, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1805, 3, 15, 'Hicr', 'ذَرْهُمْ يَأْكُلُوا وَيَتَمَتَّعُوا وَيُلْهِهِمُ الْاَمَلُ فَسَوْفَ يَعْلَمُونَ', 'Onları bırak; yesinler, eğlensinler ve boş ümit onları oyalayadursun. (Kötü sonucu) yakında bilecekler!', 14, 261, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1806, 4, 15, 'Hicr', 'وَمَٓا اَهْلَكْنَا مِنْ قَرْيَةٍ اِلَّا وَلَهَا كِتَابٌ مَعْلُومٌ', 'Helâk ettiğimiz hiçbir ülke yoktur ki hakkında (bizce) bilinen bir yazgı olmasın.', 14, 261, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1807, 5, 15, 'Hicr', 'مَا تَسْبِقُ مِنْ اُمَّةٍ اَجَلَهَا وَمَا يَسْتَأْخِرُونَ', 'Hiçbir millet, ecelinin önüne geçemez, ve onu geciktiremez.', 14, 261, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1808, 6, 15, 'Hicr', 'وَقَالُوا يَٓا اَيُّهَا الَّذ۪ي نُزِّلَ عَلَيْهِ الذِّكْرُ اِنَّكَ لَمَجْنُونٌۜ', 'Dediler ki: «Ey kendisine Kur´an indirilen (Muhammed)! Sen mutlaka bir mecnunsun!»', 14, 261, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1809, 7, 15, 'Hicr', 'لَوْ مَا تَأْت۪ينَا بِالْمَلٰٓئِكَةِ اِنْ كُنْتَ مِنَ الصَّادِق۪ينَ', '«Eğer doğru söyleyenlerden idiysen, bize melekleri getirmeliydin.»', 14, 261, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1810, 8, 15, 'Hicr', 'مَا نُنَزِّلُ الْمَلٰٓئِكَةَ اِلَّا بِالْحَقِّ وَمَا كَانُٓوا اِذاً مُنْظَر۪ينَ', 'Biz melekleri ancak hak ile indiririz. O zaman onlara mühlet verilmez.', 14, 261, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1811, 9, 15, 'Hicr', 'اِنَّا نَحْنُ نَزَّلْنَا الذِّكْرَ وَاِنَّا لَهُ لَحَافِظُونَ', 'Kur´an´ı kesinlikle biz indirdik; elbette onu yine biz koruyacağız.', 14, 261, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1812, 10, 15, 'Hicr', 'وَلَقَدْ اَرْسَلْنَا مِنْ قَبْلِكَ ف۪ي شِيَعِ الْاَوَّل۪ينَ', 'Andolsun, senden önceki milletler arasında da elçiler gönderdik.', 14, 261, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1813, 11, 15, 'Hicr', 'وَمَا يَأْت۪يهِمْ مِنْ رَسُولٍ اِلَّا كَانُوا بِه۪ يَسْتَهْزِؤُ۫نَ', 'Onlara bir peygamber gelmeyedursun, hemen onunla alay ederlerdi.', 14, 261, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1814, 12, 15, 'Hicr', 'كَذٰلِكَ نَسْلُكُهُ ف۪ي قُلُوبِ الْمُجْرِم۪ينَۙ', 'İşte böylece biz onu, (inkârcılığı) suçluların kalplerine sokarız.', 14, 261, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1815, 13, 15, 'Hicr', 'لَا يُؤْمِنُونَ بِه۪ وَقَدْ خَلَتْ سُنَّةُ الْاَوَّل۪ينَ', 'Öncekilerin başına gelenlerden ders almaları gerekirken onlar hala buna (Kur´an´a) inanmıyorlar.', 14, 261, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1816, 14, 15, 'Hicr', 'وَلَوْ فَتَحْنَا عَلَيْهِمْ بَاباً مِنَ السَّمَٓاءِ فَظَلُّوا ف۪يهِ يَعْرُجُونَۙ', '(14-15) Onlara gökten bir kapı açsak da oradan yukarı çıksalar, yine de «Gözlerimiz boyandı, daha doğrusu bize büyü yapılmıştır.» derler.', 14, 261, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1817, 15, 15, 'Hicr', 'لَقَالُٓوا اِنَّمَا سُكِّرَتْ اَبْصَارُنَا بَلْ نَحْنُ قَوْمٌ مَسْحُورُونَ۟', '(14-15) Onlara gökten bir kapı açsak da oradan yukarı çıksalar, yine de «Gözlerimiz boyandı, daha doğrusu bize büyü yapılmıştır.» derler.', 14, 261, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1818, 16, 15, 'Hicr', 'وَلَقَدْ جَعَلْنَا فِي السَّمَٓاءِ بُرُوجاً وَزَيَّنَّاهَا لِلنَّاظِر۪ينَۙ', 'Andolsun, biz gökte birtakım burçlar yarattık ve seyr edenler için onu süsledik.', 14, 262, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1819, 17, 15, 'Hicr', 'وَحَفِظْنَاهَا مِنْ كُلِّ شَيْطَانٍ رَج۪يمٍۙ', 'Onları, taşlanmış (kovulmuş) her şeytandan koruduk.', 14, 262, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1820, 18, 15, 'Hicr', 'اِلَّا مَنِ اسْتَرَقَ السَّمْعَ فَاَتْبَعَهُ شِهَابٌ مُب۪ينٌ', 'Ancak kulak hırsızlığı eden müstesna. Onun da peşine açık bir alev sütunu düşmüştür.', 14, 262, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1821, 19, 15, 'Hicr', 'وَالْاَرْضَ مَدَدْنَاهَا وَاَلْقَيْنَا ف۪يهَا رَوَاسِيَ وَاَنْبَتْنَا ف۪يهَا مِنْ كُلِّ شَيْءٍ مَوْزُونٍ', 'Yeri uzatıp yaydık, orada sabit dağlar yerleştirdik, yine orada miktarı ve ölçüsü belirli olan şeyler bitirdik.', 14, 262, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1822, 20, 15, 'Hicr', 'وَجَعَلْنَا لَكُمْ ف۪يهَا مَعَايِشَ وَمَنْ لَسْتُمْ لَهُ بِرَازِق۪ينَ', 'Orada hem sizin için hem de rızıkları size ait olmayanlar için (gerekli) geçim vasıtaları yarattık.', 14, 262, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1823, 21, 15, 'Hicr', 'وَاِنْ مِنْ شَيْءٍ اِلَّا عِنْدَنَا خَزَٓائِنُهُۘ وَمَا نُنَزِّلُـهُٓ اِلَّا بِقَدَرٍ مَعْلُومٍ', 'Her şeyin hazineleri yalnız bizim yanımızdadır. Biz onu ancak belli bir ölçüyle indiririz.', 14, 262, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1824, 22, 15, 'Hicr', 'وَاَرْسَلْنَا الرِّيَاحَ لَوَاقِـحَ فَاَنْزَلْنَا مِنَ السَّمَٓاءِ مَٓاءً فَاَسْقَيْنَاكُمُوهُۚ وَمَٓا اَنْتُمْ لَهُ بِخَازِن۪ينَ', 'Biz, rüzgârları aşılayıcı olarak gönderdik ve gökten bir su indirdik de onunla su ihtiyacınızı karşıladık. (Biz bunları yapmasaydık) siz onu (yeterli) suyu depolayamazdınız.', 14, 262, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1825, 23, 15, 'Hicr', 'وَاِنَّا لَنَحْنُ نُحْـي۪ وَنُم۪يتُ وَنَحْنُ الْوَارِثُونَ', 'Şüphesiz biz diriltir ve biz öldürürüz! Ve her şeye biz vâris oluruz.', 14, 262, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1826, 24, 15, 'Hicr', 'وَلَقَدْ عَلِمْنَا الْمُسْتَقْدِم۪ينَ مِنْكُمْ وَلَقَدْ عَلِمْنَا الْمُسْتَأْخِر۪ينَ', 'Andolsun biz, sizden önce gelip geçenleri de biliriz, geri kalanları da biliriz.', 14, 262, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1827, 25, 15, 'Hicr', 'وَاِنَّ رَبَّكَ هُوَ يَحْشُرُهُمْۜ اِنَّهُ حَك۪يمٌ عَل۪يمٌ۟', 'Şüphesiz Rabbin onları (kıyamette) toplayacaktır. Çünkü O, hakîmdir, alîmdir.', 14, 262, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1828, 26, 15, 'Hicr', 'وَلَقَدْ خَلَقْنَا الْاِنْسَانَ مِنْ صَلْصَالٍ مِنْ حَمَأٍ مَسْنُونٍۚ', 'Andolsun biz insanı, (pişmiş) kuru bir çamurdan, şekillenmiş kara balçıktan yarattık.', 14, 262, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1829, 27, 15, 'Hicr', 'وَالْجَٓانَّ خَلَقْنَاهُ مِنْ قَبْلُ مِنْ نَارِ السَّمُومِ', 'Cinleri de daha önce zehirli ateşten yaratmıştık.', 14, 262, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1830, 28, 15, 'Hicr', 'وَاِذْ قَالَ رَبُّكَ لِلْمَلٰٓئِكَةِ اِنّ۪ي خَالِقٌ بَشَراً مِنْ صَلْصَالٍ مِنْ حَمَأٍ مَسْنُونٍ', 'Hani Rabbin meleklere demişti ki: «Ben kupkuru bir çamurdan, şekillenmiş kara balçıktan bir insan yaratacağım.»', 14, 262, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1831, 29, 15, 'Hicr', 'فَاِذَا سَوَّيْتُهُ وَنَفَخْتُ ف۪يهِ مِنْ رُوح۪ي فَقَعُوا لَهُ سَاجِد۪ينَ', '«Ona şekil verdiğim ve ona ruhumdan üflediğim zaman, siz hemen onun için secdeye kapanın!»', 14, 262, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1832, 30, 15, 'Hicr', 'فَسَجَدَ الْمَلٰٓئِكَةُ كُلُّهُمْ اَجْمَعُونَۙ', 'Meleklerin hepsi de hemen secde ettiler.', 14, 262, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1833, 31, 15, 'Hicr', 'اِلَّٓا اِبْل۪يسَۜ اَبٰٓى اَنْ يَكُونَ مَعَ السَّاجِد۪ينَ', 'Fakat İblis hariç! O, secde edenlerle beraber olmaktan kaçındı.', 14, 262, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1834, 32, 15, 'Hicr', 'قَالَ يَٓا اِبْل۪يسُ مَا لَكَ اَلَّا تَكُونَ مَعَ السَّاجِد۪ينَ', '(Allah:) Ey İblis! Secde edenlerle beraber olmayışının sebebi nedir? dedi.', 14, 263, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1835, 33, 15, 'Hicr', 'قَالَ لَمْ اَكُنْ لِاَسْجُدَ لِبَشَرٍ خَلَقْتَهُ مِنْ صَلْصَالٍ مِنْ حَمَأٍ مَسْنُونٍ', '(İblis:) Ben kuru bir çamurdan, şekillenmiş kara balçıktan yarattığın bir insana secde edecek değilim, dedi.', 14, 263, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1836, 34, 15, 'Hicr', 'قَالَ فَاخْرُجْ مِنْهَا فَاِنَّكَ رَج۪يمٌ', 'Allah şöyle buyurdu: Öyle ise oradan çık! Artık kovuldun!', 14, 263, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1837, 35, 15, 'Hicr', 'وَاِنَّ عَلَيْكَ اللَّعْنَةَ اِلٰى يَوْمِ الدّ۪ينِ', 'Muhakkak ki kıyamet gününe kadar lânet senin üzerine olacaktır!', 14, 263, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1838, 36, 15, 'Hicr', 'قَالَ رَبِّ فَاَنْظِرْن۪ٓي اِلٰى يَوْمِ يُبْعَثُونَ', '(İblis:) Rabbim! Öyle ise, (varlıkların) tekrar dirileceği güne kadar bana mühlet ver, dedi.', 14, 263, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1839, 37, 15, 'Hicr', 'قَالَ فَاِنَّكَ مِنَ الْمُنْظَر۪ينَۙ', '(37-38) Allah: Sen bilinen bir vakte kadar kendilerine mühlet verilenlerdensin, buyurdu.', 14, 263, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1840, 38, 15, 'Hicr', 'اِلٰى يَوْمِ الْوَقْتِ الْمَعْلُومِ', '(37-38) Allah: Sen bilinen bir vakte kadar kendilerine mühlet verilenlerdensin, buyurdu.', 14, 263, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1841, 39, 15, 'Hicr', 'قَالَ رَبِّ بِمَٓا اَغْوَيْتَن۪ي لَاُزَيِّنَنَّ لَهُمْ فِي الْاَرْضِ وَلَاُغْوِيَنَّهُمْ اَجْمَع۪ينَۙ', '(İblis) dedi ki: Rabbim! Beni azdırmana karşılık ben de yeryüzünde onlara (günahları) süsleyeceğim ve onların hepsini mutlaka azdıracağım!', 14, 263, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1842, 40, 15, 'Hicr', 'اِلَّا عِبَادَكَ مِنْهُمُ الْمُخْلَص۪ينَ', 'Ancak onlardan ihlâslı kulların müstesna.', 14, 263, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1843, 41, 15, 'Hicr', 'قَالَ هٰذَا صِرَاطٌ عَلَيَّ مُسْتَق۪يمٌ', '(Allah) şöyle buyurdu: «İşte bana varan dosdoğru yol budur.»', 14, 263, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1844, 42, 15, 'Hicr', 'اِنَّ عِبَاد۪ي لَيْسَ لَكَ عَلَيْهِمْ سُلْطَانٌ اِلَّا مَنِ اتَّبَعَكَ مِنَ الْغَاو۪ينَ', '«Şüphesiz kullarım üzerinde senin bir hakimiyetin yoktur. Ancak azgınlardan sana uyanlar müstesna.»', 14, 263, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1845, 43, 15, 'Hicr', 'وَاِنَّ جَهَنَّمَ لَمَوْعِدُهُمْ اَجْمَع۪ينَۙ', 'Muhakkak cehennem, onların hepsine vâdolunan yerdir.', 14, 263, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1846, 44, 15, 'Hicr', 'لَهَا سَبْعَةُ اَبْوَابٍۜ لِكُلِّ بَابٍ مِنْهُمْ جُزْءٌ مَقْسُومٌ۟', 'Cehennemin yedi kapısı vardır. Onlardan her kapı için birer gurup ayrılmıştır.', 14, 263, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1847, 45, 15, 'Hicr', 'اِنَّ الْمُتَّق۪ينَ ف۪ي جَنَّاتٍ وَعُيُونٍۜ', '(Allah´ın azabından korkup rahmetine sığınan) takvâ sahipleri, mutlaka cennetlerde ve pınar başlarında olacaklar.', 14, 263, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1848, 46, 15, 'Hicr', 'اُدْخُلُوهَا بِسَلَامٍ اٰمِن۪ينَ', '«Oraya emniyet ve selâmetle girin» (denilir, onlara).', 14, 263, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1849, 47, 15, 'Hicr', 'وَنَزَعْنَا مَا ف۪ي صُدُورِهِمْ مِنْ غِلٍّ اِخْوَاناً عَلٰى سُرُرٍ مُتَقَابِل۪ينَ', 'Biz, onların gönüllerindeki kini söküp attık; onlar artık köşkler üzerinde karşı karşıya oturan kardeşler olacaklar.', 14, 263, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1850, 48, 15, 'Hicr', 'لَا يَمَسُّهُمْ ف۪يهَا نَصَبٌ وَمَا هُمْ مِنْهَا بِمُخْرَج۪ينَ', 'Onlara orada hiçbir yorgunluk gelmeyecek ve onlar, oradan çıkarılmayacaklardır.', 14, 263, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1851, 49, 15, 'Hicr', 'نَبِّئْ عِبَاد۪ٓي اَنّ۪ٓي اَنَا الْغَفُورُ الرَّح۪يمُۙ', '(Resûlüm!) Kullarıma, benim, çok bağışlayıcı ve pek esirgeyici olduğumu haber ver.', 14, 263, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1852, 50, 15, 'Hicr', 'وَاَنَّ عَذَاب۪ي هُوَ الْعَذَابُ الْاَل۪يمُ', 'Benim azabımın elem verici bir azap olduğunu da bildir.', 14, 263, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1853, 51, 15, 'Hicr', 'وَنَبِّئْهُمْ عَنْ ضَيْفِ اِبْرٰه۪يمَۢ', 'Onlara İbrahim´in misafirlerinden (meleklerden) de haber ver.', 14, 263, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1854, 52, 15, 'Hicr', 'اِذْ دَخَلُوا عَلَيْهِ فَقَالُوا سَلَاماًۜ قَالَ اِنَّا مِنْكُمْ وَجِلُونَ', 'Onun yanına girdikleri zaman, «selam» dediler. (İbrahim:) Biz sizden çekiniyoruz, dedi.', 14, 264, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1855, 53, 15, 'Hicr', 'قَالُوا لَا تَوْجَلْ اِنَّا نُبَشِّرُكَ بِغُلَامٍ عَل۪يمٍ', 'Dediler ki: Korkma; biz sana bilgin bir oğul müjdeliyoruz.', 14, 264, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1856, 54, 15, 'Hicr', 'قَالَ اَبَشَّرْتُمُون۪ي عَلٰٓى اَنْ مَسَّنِيَ الْكِبَرُ فَبِمَ تُبَشِّرُونَ', '(İbrahim:) Bana ihtiyarlık çökmesine rağmen beni müjdeliyor musunuz? Beni ne ile müjdeliyorsunuz? dedi.', 14, 264, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1857, 55, 15, 'Hicr', 'قَالُوا بَشَّرْنَاكَ بِالْحَقِّ فَلَا تَكُنْ مِنَ الْقَانِط۪ينَ', 'Sana gerçeği müjdeledik, sakın ümitsizliğe düşenlerden olma! dediler.', 14, 264, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1858, 56, 15, 'Hicr', 'قَالَ وَمَنْ يَقْنَطُ مِنْ رَحْمَةِ رَبِّه۪ٓ اِلَّا الضَّٓالُّونَ', '(İbrahim:) dedi ki: Rabbinin rahmetinden, sapıklardan başka kim ümit keser?', 14, 264, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1859, 57, 15, 'Hicr', 'قَالَ فَمَا خَطْبُكُمْ اَيُّهَا الْمُرْسَلُونَ', '«Ey elçiler! (Başka) ne işiniz var?» dedi.', 14, 264, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1860, 58, 15, 'Hicr', 'قَالُٓوا اِنَّٓا اُرْسِلْـنَٓا اِلٰى قَوْمٍ مُجْرِم۪ينَۙ', 'Dediler ki: «Biz, suçlu bir topluma (onları helâk etmeye) gönderildik.»', 14, 264, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1861, 59, 15, 'Hicr', 'اِلَّٓا اٰلَ لُوطٍۜ اِنَّا لَمُنَجُّوهُمْ اَجْمَع۪ينَۙ', '«Ancak Lût ailesi hariç. Onların hepsini kurtaracağız.»', 14, 264, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1862, 60, 15, 'Hicr', 'اِلَّا امْرَاَتَهُ قَدَّرْنَٓاۙ اِنَّهَا لَمِنَ الْغَابِر۪ينَ۟', '«(Fakat Lût´un) karısı müstesna; biz onun geri kalanlardan olmasını takdir ettik.»', 14, 264, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1863, 61, 15, 'Hicr', 'فَلَمَّا جَٓاءَ اٰلَ لُوطٍۨ الْمُرْسَلُونَۙ', '(61-62) Elçiler Lût âilesine gelince, Lût onlara: «Hakikaten siz tanınmayan kimselersiniz» dedi.', 14, 264, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1864, 62, 15, 'Hicr', 'قَالَ اِنَّكُمْ قَوْمٌ مُنْكَرُونَ', '(61-62) Elçiler Lût âilesine gelince, Lût onlara: «Hakikaten siz tanınmayan kimselersiniz» dedi.', 14, 264, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1865, 63, 15, 'Hicr', 'قَالُوا بَلْ جِئْنَاكَ بِمَا كَانُوا ف۪يهِ يَمْتَرُونَ', 'Dediler ki: «Bilakis, biz sana, onların şüphe etmekte oldukları şeyi (azabı ve helâkı) getirdik.', 14, 264, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1866, 64, 15, 'Hicr', 'وَاَتَيْنَاكَ بِالْحَقِّ وَاِنَّا لَصَادِقُونَ', 'Sana gerçeği getirdik; biz, hakikaten doğru söyleyenleriz.', 14, 264, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1867, 65, 15, 'Hicr', 'فَاَسْرِ بِاَهْلِكَ بِقِطْعٍ مِنَ الَّيْلِ وَاتَّبِـعْ اَدْبَارَهُمْ وَلَا يَلْتَفِتْ مِنْكُمْ اَحَدٌ وَامْضُوا حَيْثُ تُؤْمَرُونَ', 'Gecenin bir bölümünde aile fertlerini yola çıkar, sen de arkalarından yürü. Sizden hiç kimse, sakın dönüp de ardına bakmasın, istenen yere gidin.»', 14, 264, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1868, 66, 15, 'Hicr', 'وَقَضَيْنَٓا اِلَيْهِ ذٰلِكَ الْاَمْرَ اَنَّ دَابِرَ هٰٓؤُ۬لَٓاءِ مَقْطُوعٌ مُصْبِح۪ينَ', 'Ona (Lût´a) şu hükmümüzü vahyettik: «Sabaha çıkarlarken mutlaka onların ardı kesilmiş olacaktır.»', 14, 264, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1869, 67, 15, 'Hicr', 'وَجَٓاءَ اَهْلُ الْمَد۪ينَةِ يَسْتَبْشِرُونَ', 'Şehir halkı, birbirlerini kutlayarak, (meleklerin yanına) geldiler.', 14, 264, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1870, 68, 15, 'Hicr', 'قَالَ اِنَّ هٰٓؤُ۬لَٓاءِ ضَيْف۪ي فَلَا تَفْضَحُونِۙ', '(68-69) (Lût) onlara «Bunlar benim misafirimdir. Sakın beni utandırmayın; Allah´tan korkun, beni rezil etmeyin!» dedi.', 14, 264, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1871, 69, 15, 'Hicr', 'وَاتَّقُوا اللّٰهَ وَلَا تُخْزُونِ', '(68-69) (Lût) onlara «Bunlar benim misafirimdir. Sakın beni utandırmayın; Allah´tan korkun, beni rezil etmeyin!» dedi.', 14, 264, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1872, 70, 15, 'Hicr', 'قَالُٓوا اَوَلَمْ نَنْهَكَ عَنِ الْعَالَم۪ينَ', '«Biz seni, elâlemin işine karışmaktan men etmemiş miydik?» dediler.', 14, 264, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1873, 71, 15, 'Hicr', 'قَالَ هٰٓؤُ۬لَٓاءِ بَنَات۪ٓي اِنْ كُنْتُمْ فَاعِل۪ينَۜ', '(Lût:) İşte kızlarım! (Düşündüğünüzü) yapacaksanız (onlarla evlenin), dedi.', 14, 265, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1874, 72, 15, 'Hicr', 'لَعَمْرُكَ اِنَّهُمْ لَف۪ي سَكْرَتِهِمْ يَعْمَهُونَ', '(Resûlüm!) Hayatın hakkı için onlar, sarhoşlukları içinde bocalıyorlardı.', 14, 265, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1875, 73, 15, 'Hicr', 'فَاَخَذَتْهُمُ الصَّيْحَةُ مُشْرِق۪ينَۙ', 'Güneş doğarken onları o korkunç ses yakaladı.', 14, 265, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1876, 74, 15, 'Hicr', 'فَجَعَلْنَا عَالِيَهَا سَافِلَهَا وَاَمْطَرْنَا عَلَيْهِمْ حِجَارَةً مِنْ سِجّ۪يلٍۜ', 'Böylece ülkelerinin üstünü altına getirdik. Üzerlerine de balçıktan pişirilmiş taşlar yağdırdık.', 14, 265, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1877, 75, 15, 'Hicr', 'اِنَّ ف۪ي ذٰلِكَ لَاٰيَاتٍ لِلْمُتَوَسِّم۪ينَ', 'İşte bunda ibret alanlar için işaretler vardır.', 14, 265, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1878, 76, 15, 'Hicr', 'وَاِنَّهَا لَبِسَب۪يلٍ مُق۪يمٍ', 'Onlar hâla gözler önünde duran bir yol üzerindedirler.', 14, 265, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1879, 77, 15, 'Hicr', 'اِنَّ ف۪ي ذٰلِكَ لَاٰيَةً لِلْمُؤْمِن۪ينَۜ', 'Hakikaten bunda iman edenler için bir ibret vardır.', 14, 265, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1880, 78, 15, 'Hicr', 'وَاِنْ كَانَ اَصْحَابُ الْاَيْكَةِ لَظَالِم۪ينَۙ', 'Eyke halkı da gerçekten zalim idiler.', 14, 265, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1881, 79, 15, 'Hicr', 'فَانْتَقَمْنَا مِنْهُمْۢ وَاِنَّهُمَا لَبِاِمَامٍ مُب۪ينٍۜ۟', 'Biz onlardan da intikam aldık. İkisi de (Eyke ve Medyen) açık bir yol üzerindedir.', 14, 265, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1882, 80, 15, 'Hicr', 'وَلَقَدْ كَذَّبَ اَصْحَابُ الْحِجْرِ الْمُرْسَل۪ينَۙ', 'Andolsun, Hicr halkı da peygamberleri yalanlamıştı.', 14, 265, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1883, 81, 15, 'Hicr', 'وَاٰتَيْنَاهُمْ اٰيَاتِنَا فَكَانُوا عَنْهَا مُعْرِض۪ينَۙ', 'Biz onlara mucizelerimizi vermiştik; fakat onlardan yüz çevirmişlerdi.', 14, 265, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1884, 82, 15, 'Hicr', 'وَكَانُوا يَنْحِتُونَ مِنَ الْجِبَالِ بُيُوتاً اٰمِن۪ينَ', 'Onlar, dağlardan emniyet içinde kalacakları evler oyarlardı.', 14, 265, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1885, 83, 15, 'Hicr', 'فَاَخَذَتْهُمُ الصَّيْحَةُ مُصْبِح۪ينَۙ', 'Onları da sabaha çıkarlarken o korkunç ses yakaladı.', 14, 265, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1886, 84, 15, 'Hicr', 'فَمَٓا اَغْنٰى عَنْهُمْ مَا كَانُوا يَكْسِبُونَۜ', 'Kazanmakta oldukları şeyler onlardan hiçbir zararı savmadı.', 14, 265, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1887, 85, 15, 'Hicr', 'وَمَا خَلَقْنَا السَّمٰوَاتِ وَالْاَرْضَ وَمَا بَيْنَهُمَٓا اِلَّا بِالْحَقِّۜ وَاِنَّ السَّاعَةَ لَاٰتِيَةٌ فَاصْفَحِ الصَّفْحَ الْجَم۪يلَ', 'Biz gökleri, yeri ve ikisinin arasındakileri ancak hak ile yarattık. O saat (kıyamet), mutlaka gelecektir. Şimdilik onlara güzel muamele et.', 14, 265, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1888, 86, 15, 'Hicr', 'اِنَّ رَبَّكَ هُوَ الْخَلَّاقُ الْعَل۪يمُ', 'Şüphesiz Rabbin hakkıyla yaratan pek iyi bilendir.', 14, 265, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1889, 87, 15, 'Hicr', 'وَلَقَدْ اٰتَيْنَاكَ سَبْعاً مِنَ الْمَثَان۪ي وَالْقُرْاٰنَ الْعَظ۪يمَ', 'Andolsun ki, biz sana tekrarlanan yedi âyeti ve yüce Kur´an´ı verdik.', 14, 265, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1890, 88, 15, 'Hicr', 'لَا تَمُدَّنَّ عَيْنَيْكَ اِلٰى مَا مَتَّعْنَا بِه۪ٓ اَزْوَاجاً مِنْهُمْ وَلَا تَحْزَنْ عَلَيْهِمْ وَاخْفِضْ جَنَاحَكَ لِلْمُؤْمِن۪ينَ', 'Sakın onlardan bazı sınıflara verdiğimiz dünya malına göz dikme, onlardan dolayı üzülme ve müminlere alçak gönüllü ol.', 14, 265, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1891, 89, 15, 'Hicr', 'وَقُلْ اِنّ۪ٓي اَنَا النَّذ۪يرُ الْمُب۪ينُۚ', 'De ki: Şüphesiz ben apaçık bir uyarıcıyım.', 14, 265, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1892, 90, 15, 'Hicr', 'كَمَٓا اَنْزَلْنَا عَلَى الْمُقْتَسِم۪ينَۙ', 'Nitekim biz, komplo kuranlara (azabı) indirmişizdir.', 14, 265, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1893, 91, 15, 'Hicr', 'اَلَّذ۪ينَ جَعَلُوا الْقُرْاٰنَ عِض۪ينَ', 'Onlar, Kur´an´ı tutarsız parçalar olarak nitelendirenlere gelince,', 14, 266, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1894, 92, 15, 'Hicr', 'فَوَرَبِّكَ لَنَسْـَٔلَنَّهُمْ اَجْمَع۪ينَۙ', '(92-93) Rabbin hakkı için, mutlaka onların hepsini yaptıklarından dolayı sorguya çekeceğiz.', 14, 266, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1895, 93, 15, 'Hicr', 'عَمَّا كَانُوا يَعْمَلُونَ', '(92-93) Rabbin hakkı için, mutlaka onların hepsini yaptıklarından dolayı sorguya çekeceğiz.', 14, 266, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1896, 94, 15, 'Hicr', 'فَاصْدَعْ بِمَا تُؤْمَرُ وَاَعْرِضْ عَنِ الْمُشْرِك۪ينَ', 'Sana emrolunanı açıkça söyle ve ortak koşanlardan yüz çevir!', 14, 266, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1897, 95, 15, 'Hicr', 'اِنَّا كَفَيْنَاكَ الْمُسْتَهْزِء۪ينَۙ', '(Seninle) alay edenlere karşı biz sana yeteriz.', 14, 266, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1898, 96, 15, 'Hicr', 'اَلَّذ۪ينَ يَجْعَلُونَ مَعَ اللّٰهِ اِلٰهاً اٰخَرَۚ فَسَوْفَ يَعْلَمُونَ', 'Onlar Allah ile beraber başka bir tanrı edinenlerdir. (Kimin doğru olduğunu) yakında bilecekler!', 14, 266, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1899, 97, 15, 'Hicr', 'وَلَقَدْ نَعْلَمُ اَنَّكَ يَض۪يقُ صَدْرُكَ بِمَا يَقُولُونَۙ', 'Onların söyledikleri şeyler yüzünden senin canının sıkıldığını andolsun biliyoruz.', 14, 266, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1900, 98, 15, 'Hicr', 'فَسَبِّـحْ بِحَمْدِ رَبِّكَ وَكُنْ مِنَ السَّاجِد۪ينَۙ', 'Sen şimdi Rabbini hamd ile tesbih et ve secde edenlerden ol!', 14, 266, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1901, 99, 15, 'Hicr', 'وَاعْبُدْ رَبَّكَ حَتّٰى يَأْتِيَكَ الْيَق۪ينُ', 'Ve sana yakîn (ölüm) gelinceye kadar Rabbine ibadet et!', 14, 266, 99, 2797, 'Medine’nin kuzeyinde vaktiyle ', 'Mekke', 54),
(1902, 1, 16, 'Nahl', 'اَتٰٓى اَمْرُ اللّٰهِ فَلَا تَسْتَعْجِلُوهُۜ سُبْحَانَهُ وَتَعَالٰى عَمَّا يُشْرِكُونَ', 'Allah´ın emri gelmiştir. Artık onu istemekte acele etmeyin. Allah, onların koştukları ortaklardan uzak ve yücedir.', 14, 266, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1903, 2, 16, 'Nahl', 'يُنَزِّلُ الْمَلٰٓئِكَةَ بِالرُّوحِ مِنْ اَمْرِه۪ عَلٰى مَنْ يَشَٓاءُ مِنْ عِبَادِه۪ٓ اَنْ اَنْذِرُٓوا اَنَّهُ لَٓا اِلٰهَ اِلَّٓا اَنَا۬ فَاتَّقُونِ', 'Allah kendi emriyle melekleri, kullarından dilediği kimseye vahiy ile, «Benden başka tanrı olmadığına dair (kullarımı) uyarın ve benden korkun» diye gönderir.', 14, 266, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1904, 3, 16, 'Nahl', 'خَلَقَ السَّمٰوَاتِ وَالْاَرْضَ بِالْحَقِّۜ تَعَالٰى عَمَّا يُشْرِكُونَ', '(Allah) gökleri ve yeri hak ile yarattı. O, koştukları ortaklardan münezzehtir.', 14, 266, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1905, 4, 16, 'Nahl', 'خَلَقَ الْاِنْسَانَ مِنْ نُطْفَةٍ فَاِذَا هُوَ خَص۪يمٌ مُب۪ينٌ', 'O, insanı bir damla sudan yarattı. Fakat bakarsın ki (insan) Rabbine apaçık bir hasım oluvermiştir.', 14, 266, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1906, 5, 16, 'Nahl', 'وَالْاَنْعَامَ خَلَقَهَاۚ لَكُمْ ف۪يهَا دِفْءٌ وَمَنَافِعُ وَمِنْهَا تَأْكُلُونَۖ', 'Hayvanları da O yarattı. Onlarda sizin için ısıtıcı (şeyler) ve birçok faydalar vardır. Onlardan bir kısmını da yersiniz.', 14, 266, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1907, 6, 16, 'Nahl', 'وَلَكُمْ ف۪يهَا جَمَالٌ ح۪ينَ تُر۪يحُونَ وَح۪ينَ تَسْرَحُونَۖ', 'Sizin için onlardan ayrıca akşamleyin getirirken, sabahleyin salıverirken bir güzellik (bir zevk) vardır.', 14, 266, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1908, 7, 16, 'Nahl', 'وَتَحْمِلُ اَثْقَالَكُمْ اِلٰى بَلَدٍ لَمْ تَكُونُوا بَالِغ۪يهِ اِلَّا بِشِقِّ الْاَنْفُسِۜ اِنَّ رَبَّكُمْ لَرَؤُ۫فٌ رَح۪يمٌۙ', 'Bu hayvanlar sizin ağırlıklarınızı, ancak güçlüklere katlanarak varabileceğiniz bir memlekete taşırlar. Şüphesiz Rabbiniz çok şefkatli, pek merhametlidir.', 14, 267, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1909, 8, 16, 'Nahl', 'وَالْخَيْلَ وَالْبِغَالَ وَالْحَم۪يرَ لِتَرْكَبُوهَا وَز۪ينَةًۜ وَيَخْلُقُ مَا لَا تَعْلَمُونَ', 'Atları, katırları ve eşekleri binmeniz ve (gözlere) zinet olsun diye (yarattı). Allah şu anda bilemeyeceğiniz daha nice (nakil vasıtaları) yaratır.', 14, 267, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1910, 9, 16, 'Nahl', 'وَعَلَى اللّٰهِ قَصْدُ السَّب۪يلِ وَمِنْهَا جَٓائِرٌۜ وَلَوْ شَٓاءَ لَهَدٰيكُمْ اَجْمَع۪ينَ۟', 'Yolun doğrusu Allah´ındır. Yolun eğrisi de vardır. Allah dileseydi hepinizi doğru yola iletirdi.', 14, 267, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1911, 10, 16, 'Nahl', 'هُوَ الَّـذ۪ٓي اَنْزَلَ مِنَ السَّمَٓاءِ مَٓاءً لَكُمْ مِنْهُ شَرَابٌ وَمِنْهُ شَجَرٌ ف۪يهِ تُس۪يمُونَ', 'Gökten suyu indiren O´dur. Ondan hem size içecek vardır, hem de hayvanlarınızı otlatacağınız bitkiler.', 14, 267, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1912, 11, 16, 'Nahl', 'يُنْبِتُ لَكُمْ بِهِ الزَّرْعَ وَالزَّيْتُونَ وَالنَّخ۪يلَ وَالْاَعْنَابَ وَمِنْ كُلِّ الثَّمَرَاتِۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَةً لِقَوْمٍ يَتَفَكَّرُونَ', '(Allah) su sayesinde sizin için ekinler, zeytinler, hurmalar, üzümler ve diğer meyvelerin hepsinden bitirir. İşte bunlarda düşünen bir toplum için büyük bir ibret vardır.', 14, 267, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1913, 12, 16, 'Nahl', 'وَسَخَّرَ لَكُمُ الَّيْلَ وَالنَّهَارَۙ وَالشَّمْسَ وَالْقَمَرَۜ وَالنُّجُومُ مُسَخَّرَاتٌ بِاَمْرِه۪ۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَاتٍ لِقَوْمٍ يَعْقِلُونَۙ', 'O, geceyi, gündüzü, güneşi ve ayı sizin hizmetinize verdi. Yıldızlar da Allah´ın emri ile hareket ederler. Şüphesiz ki bunlarda aklını kullananlar için pek çok deliller vardır.', 14, 267, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1914, 13, 16, 'Nahl', 'وَمَا ذَرَاَ لَكُمْ فِي الْاَرْضِ مُخْتَلِفاً اَلْوَانُهُۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَةً لِقَوْمٍ يَذَّكَّرُونَ', 'Yeryüzünde sizin için rengârenk yarattıklarında da öğüt alan bir toplum için gerçek bir ibret vardır.', 14, 267, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1915, 14, 16, 'Nahl', 'وَهُوَ الَّذ۪ي سَخَّرَ الْبَحْرَ لِتَأْكُلُوا مِنْهُ لَحْماً طَرِياًّ وَتَسْتَخْرِجُوا مِنْهُ حِلْيَةً تَلْبَسُونَهَاۚ وَتَرَى الْفُلْكَ مَوَاخِرَ ف۪يهِ وَلِتَبْتَغُوا مِنْ فَضْلِه۪ وَلَعَلَّكُمْ تَشْكُرُونَ', 'İçinden taze et (balık) yemeniz ve takacağınız bir süs (eşyası) çıkarmanız için denizi emrinize veren O´dur. Gemilerin denizde (suları) yara yara gittiklerini de görüyorsun. (Bütün bunlar) onun lütfunu aramanız ve nimetine şükretmeniz içindir.', 14, 267, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1916, 15, 16, 'Nahl', 'وَاَلْقٰى فِي الْاَرْضِ رَوَاسِيَ اَنْ تَم۪يدَ بِكُمْ وَاَنْهَاراً وَسُبُلاً لَعَلَّكُمْ تَهْتَدُونَۙ', 'Sizi sarsmaması için yeryüzünde sağlam dağları, yolunuzu bulmanız için de ırmakları ve yolları yarattı.', 14, 268, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1917, 16, 16, 'Nahl', 'وَعَلَامَاتٍۜ وَبِالنَّجْمِ هُمْ يَهْتَدُونَ', 'Daha nice alâmetler (yarattı). Onlar, yıldızlarla da yollarını doğrulturlar.', 14, 268, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1918, 17, 16, 'Nahl', 'اَفَمَنْ يَخْلُقُ كَمَنْ لَا يَخْلُقُۜ اَفَلَا تَذَكَّرُونَ', 'O halde, yaratan (Allah), yaratmayan (putlar) gibi olur mu? Hâla düşünmüyor musunuz?', 14, 268, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1919, 18, 16, 'Nahl', 'وَاِنْ تَعُدُّوا نِعْمَةَ اللّٰهِ لَا تُحْصُوهَاۜ اِنَّ اللّٰهَ لَغَفُورٌ رَح۪يمٌ', 'Allah´ın nimetini saymaya kalksanız, onu sayamazsınız. Hakikaten Allah çok bağışlayan, pek esirgeyendir.', 14, 268, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1920, 19, 16, 'Nahl', 'وَاللّٰهُ يَعْلَمُ مَا تُسِرُّونَ وَمَا تُعْلِنُونَ', 'Allah, gizlediğinizi de açıkladığınızı da bilir.', 14, 268, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1921, 20, 16, 'Nahl', 'وَالَّذ۪ينَ يَدْعُونَ مِنْ دُونِ اللّٰهِ لَا يَخْلُقُونَ شَيْـٔاً وَهُمْ يُخْلَقُونَۜ', 'Allah´ı bırakıp da taptıkları (putlar), hiçbir şey yaratamazlar. Çünkü onlar kendileri yaratılmışlardır.', 14, 268, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1922, 21, 16, 'Nahl', 'اَمْوَاتٌ غَيْرُ اَحْيَٓاءٍۚ وَمَا يَشْعُرُونَۙ اَيَّانَ يُبْعَثُونَ۟', 'Onlar diriler değil, ölülerdir. Ne zaman diriltileceklerini de bilmezler.', 14, 268, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1923, 22, 16, 'Nahl', 'اِلٰهُكُمْ اِلٰهٌ وَاحِدٌۚ فَالَّذ۪ينَ لَا يُؤْمِنُونَ بِالْاٰخِرَةِ قُلُوبُهُمْ مُنْكِرَةٌ وَهُمْ مُسْتَكْبِرُونَ', 'İlâhınız bir tek Tanrı´dır. Fakat ahirete inanmayanlar var ya, onların kalpleri inkârcı, kendileri de böbürlenen kimselerdir.', 14, 268, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1924, 23, 16, 'Nahl', 'لَا جَرَمَ اَنَّ اللّٰهَ يَعْلَمُ مَا يُسِرُّونَ وَمَا يُعْلِنُونَۜ اِنَّهُ لَا يُحِبُّ الْمُسْتَكْبِر۪ينَ', 'Hiç şüphesiz Allah, onların gizleyeceklerini de açıklayacaklarını da bilir. O, büyüklük taslayanları asla sevmez.', 14, 268, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1925, 24, 16, 'Nahl', 'وَاِذَا ق۪يلَ لَهُمْ مَاذَٓا اَنْزَلَ رَبُّكُمْۙ قَالُٓوا اَسَاط۪يرُ الْاَوَّل۪ينَۙ', 'Onlara: Rabbiniz ne indirdi? denildiği zaman, «Öncekilerin masallarını» derler.', 14, 268, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1926, 25, 16, 'Nahl', 'لِيَحْمِلُٓوا اَوْزَارَهُمْ كَامِلَةً يَوْمَ الْقِيٰمَةِۙ وَمِنْ اَوْزَارِ الَّذ۪ينَ يُضِلُّونَهُمْ بِغَيْرِ عِلْمٍۜ اَلَا سَٓاءَ مَا يَزِرُونَ۟', 'Kıyamet gününde kendi günahlarını tam olarak taşımaları ve bilgisizce saptırmakta oldukları kimselerin günahlarından da bir kısmını yüklenmeleri için (öyle derler). Bak ki yüklenecekleri şey ne kötüdür!', 14, 268, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1927, 26, 16, 'Nahl', 'قَدْ مَكَرَ الَّذ۪ينَ مِنْ قَبْلِهِمْ فَاَتَى اللّٰهُ بُنْيَانَهُمْ مِنَ الْقَوَاعِدِ فَخَرَّ عَلَيْهِمُ السَّقْفُ مِنْ فَوْقِهِمْ وَاَتٰيهُمُ الْعَذَابُ مِنْ حَيْثُ لَا يَشْعُرُونَ', 'Onlardan öncekiler de (peygamberlere) hile yapmışlardı. Sonunda Allah da onların binalarını temellerinden söktü üstlerindeki tavan da tepelerine çöktü. Bu azap onlara, farkedemedikleri bir yerden gelmişti.', 14, 268, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1928, 27, 16, 'Nahl', 'ثُمَّ يَوْمَ الْقِيٰمَةِ يُخْز۪يهِمْ وَيَقُولُ اَيْنَ شُرَكَٓاءِيَ الَّذ۪ينَ كُنْتُمْ تُشَٓاقُّونَ ف۪يهِمْۜ قَالَ الَّذ۪ينَ اُو۫تُوا الْعِلْمَ اِنَّ الْخِزْيَ الْيَوْمَ وَالسُّٓوءَ عَلَى الْكَافِر۪ينَۙ', 'Sonra kıyamet gününde (Allah), onları rezil eder ve der ki: «Kendileri hakkında (müminlere) düşman kesildiğiniz ortaklarım nerede?» Kendilerine ilim verilmiş olanlar derler ki: «Şüphesiz bugün rezillik ve kötülük kâfirleredir.»', 14, 269, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1929, 28, 16, 'Nahl', 'اَلَّذ۪ينَ تَتَوَفّٰيهُمُ الْمَلٰٓئِكَةُ ظَالِم۪ٓي اَنْفُسِهِمْۖ فَاَلْقَوُا السَّلَمَ مَا كُنَّا نَعْمَلُ مِنْ سُٓوءٍۜ بَلٰٓى اِنَّ اللّٰهَ عَل۪يمٌ بِمَا كُنْتُمْ تَعْمَلُونَ', 'Kendilerine haksızlık ederlerken meleklerin canlarını aldıkları kimseler: Biz hiçbir kötülük yapmıyorduk, diyerek teslim olurlar. (Melekler onlara şöyle der:) «Hayır, Allah, sizin yaptıklarınızı elbette çok iyi bilendir.»', 14, 269, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1930, 29, 16, 'Nahl', 'فَادْخُلُٓوا اَبْوَابَ جَهَنَّمَ خَالِد۪ينَ ف۪يهَاۜ فَلَبِئْسَ مَثْوَى الْمُتَكَبِّر۪ينَ', '«O halde, içinde ebedî kalacağınız cehennemin kapılarından girin! Kibirlenenlerin yeri ne kötüdür!»', 14, 269, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1931, 30, 16, 'Nahl', 'وَق۪يلَ لِلَّذ۪ينَ اتَّقَوْا مَاذَٓا اَنْزَلَ رَبُّكُمْۜ قَالُوا خَيْراًۜ لِلَّذ۪ينَ اَحْسَنُوا ف۪ي هٰذِهِ الدُّنْيَا حَسَنَةٌۜ وَلَدَارُ الْاٰخِرَةِ خَيْرٌۜ وَلَنِعْمَ دَارُ الْمُتَّق۪ينَۙ', '(Kötülüklerden) sakınanlara: Rabbiniz ne indirdi? denildiğinde, «Hayır (indirdi)» derler. Bu dünyada güzel davrananlara, güzel mükâfat vardır. Ahiret yurdu ise daha hayırlıdır. Takvâ sahiplerinin yurdu gerçekten güzeldir!', 14, 269, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1932, 31, 16, 'Nahl', 'جَنَّاتُ عَدْنٍ يَدْخُلُونَهَا تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُ لَهُمْ ف۪يهَا مَا يَشَٓاؤُ۫نَۜ كَذٰلِكَ يَجْزِي اللّٰهُ الْمُتَّق۪ينَۙ', '(O yurt,) girecekleri, zemininden ırmaklar akan Adn cennetleridir. Onlar için orada kendilerine diledikleri her şey vardır. İşte Allah, takvâ sahiplerini böyle mükâfatlandırır.', 14, 269, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1933, 32, 16, 'Nahl', 'اَلَّذ۪ينَ تَتَوَفّٰيهُمُ الْمَلٰٓئِكَةُ طَيِّب۪ينَۙ يَقُولُونَ سَلَامٌ عَلَيْكُمُۙ ادْخُلُوا الْجَنَّةَ بِمَا كُنْتُمْ تَعْمَلُونَ', '(Onlar,) meleklerin, «Size selâm olsun. Yapmış olduğunuz (iyi) işlere karşılık cennete girin» diyerek tertemiz olarak canlarını aldıkları kimselerdir.', 14, 269, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1934, 33, 16, 'Nahl', 'هَلْ يَنْظُرُونَ اِلَّٓا اَنْ تَأْتِيَهُمُ الْمَلٰٓئِكَةُ اَوْ يَأْتِيَ اَمْرُ رَبِّكَۜ كَذٰلِكَ فَعَلَ الَّذ۪ينَ مِنْ قَبْلِهِمْۜ وَمَا ظَلَمَهُمُ اللّٰهُ وَلٰكِنْ كَانُٓوا اَنْفُسَهُمْ يَظْلِمُونَ', '(Kâfirler) kendilerine meleklerin gelmesinden veya Rablerinin emrinin gelmesinden başka bir şey mi bekliyorlar? Onlardan öncekiler de böyle yapmışlardı. Allah onlara zulmetmedi, fakat onlar kendilerine zulmediyorlardı.', 14, 269, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1935, 34, 16, 'Nahl', 'فَاَصَابَهُمْ سَيِّـَٔاتُ مَا عَمِلُوا وَحَاقَ بِهِمْ مَا كَانُوا بِه۪ يَسْتَهْزِؤُ۫نَ۟', 'Sonunda yaptıklarının cezası onlara ulaştı ve alay etmekte oldukları şey onları çepeçevre kuşatıverdi.', 14, 269, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1936, 35, 16, 'Nahl', 'وَقَالَ الَّذ۪ينَ اَشْرَكُوا لَوْ شَٓاءَ اللّٰهُ مَا عَبَدْنَا مِنْ دُونِه۪ مِنْ شَيْءٍ نَحْنُ وَلَٓا اٰبَٓاؤُ۬نَا وَلَا حَرَّمْنَا مِنْ دُونِه۪ مِنْ شَيْءٍۜ كَذٰلِكَ فَعَلَ الَّذ۪ينَ مِنْ قَبْلِهِمْۚ فَهَلْ عَلَى الرُّسُلِ اِلَّا الْبَلَاغُ الْمُب۪ينُ', 'Ortak koşanlar dediler ki: «Allah dileseydi ne biz ne de babalarımız ondan başkasına tapardık. Onun emri olmadan hiçbir şeyi de haram kılmazdık.» Onlardan öncekiler de böyle yapmışlardı. Peygamberlerin üzerine açık seçik tebliğden başka bir şey düşer mi!', 14, 270, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1937, 36, 16, 'Nahl', 'وَلَقَدْ بَعَثْنَا ف۪ي كُلِّ اُمَّةٍ رَسُولاً اَنِ اعْبُدُوا اللّٰهَ وَاجْتَنِبُوا الطَّاغُوتَۚ فَمِنْهُمْ مَنْ هَدَى اللّٰهُ وَمِنْهُمْ مَنْ حَقَّتْ عَلَيْهِ الضَّلَالَةُۜ فَس۪يرُوا فِي الْاَرْضِ فَانْظُرُوا كَيْفَ كَانَ عَاقِبَةُ الْمُكَذِّب۪ينَ', 'Andolsun ki biz, «Allah´a kulluk edin ve Tâğut´tan sakının» diye (emretmeleri için) her ümmete bir peygamber gönderdik. Allah, onlardan bir kısmını doğru yola iletti. Onlardan bir kısmı da sapıklığı hak ettiler. Yeryüzünde gezin de görün, inkâr edenlerin sonu nasıl olmuştur!', 14, 270, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1938, 37, 16, 'Nahl', 'اِنْ تَحْرِصْ عَلٰى هُدٰيهُمْ فَاِنَّ اللّٰهَ لَا يَهْد۪ي مَنْ يُضِلُّ وَمَا لَهُمْ مِنْ نَاصِر۪ينَ', '(Resûlüm!) Sen, onların hidayete ermelerine çok düşkünlük göstersen de bil ki Allah, saptırdığı kimseyi (dilemezse) hidayete erdirmez. Onların yardımcıları da yoktur.', 14, 270, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1939, 38, 16, 'Nahl', 'وَاَقْسَمُوا بِاللّٰهِ جَهْدَ اَيْمَانِهِمْۙ لَا يَبْعَثُ اللّٰهُ مَنْ يَمُوتُۜ بَلٰى وَعْداً عَلَيْهِ حَقاًّ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُونَۙ', 'Onlar: «Allah ölen bir kimseyi diriltmez» diye olanca güçleriyle Allah´a and içtiler. Aksine, bu O´nun bizzat kendisine karşı gerçek bir vâdidir. Fakat insanların çoğu bilmez.', 14, 270, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1940, 39, 16, 'Nahl', 'لِيُبَيِّنَ لَهُمُ الَّذ۪ي يَخْتَلِفُونَ ف۪يهِ وَلِيَعْلَمَ الَّذ۪ينَ كَفَرُٓوا اَنَّهُمْ كَانُوا كَاذِب۪ينَ', 'Hakkında ihtilaf ettikleri şeyi onlara açıklaması ve kâfir olanların da kendilerinin yalancılar olduklarını bilmeleri için (Allah onları diriltecek).', 14, 270, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1941, 40, 16, 'Nahl', 'اِنَّمَا قَوْلُنَا لِشَيْءٍ اِذَٓا اَرَدْنَاهُ اَنْ نَقُولَ لَهُ كُنْ فَيَكُونُ۟', 'Biz, bir şeyin olmasını istediğimiz zaman, ona (söyleyecek) sözümüz sadece «Ol» dememizdir. Hemen oluverir.', 14, 270, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1942, 41, 16, 'Nahl', 'وَالَّذ۪ينَ هَاجَرُوا فِي اللّٰهِ مِنْ بَعْدِ مَا ظُلِمُوا لَنُبَوِّئَنَّهُمْ فِي الدُّنْيَا حَسَنَةًۜ وَلَاَجْرُ الْاٰخِرَةِ اَكْبَرُۢ لَوْ كَانُوا يَعْلَمُونَۙ', 'Zulme uğradıktan sonra Allah yolunda hicret edenlere gelince, onları dünyada güzel bir şekilde yerleştireceğiz. Eğer bilirlerse ahiretin mükâfatı elbette daha büyüktür.', 14, 270, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1943, 42, 16, 'Nahl', 'اَلَّذ۪ينَ صَبَرُوا وَعَلٰى رَبِّهِمْ يَتَوَكَّلُونَ', '(Onlar) sadece Rablerine tevekkül ederek sabredenlerdir.', 14, 270, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1944, 43, 16, 'Nahl', 'وَمَٓا اَرْسَلْنَا مِنْ قَبْلِكَ اِلَّا رِجَالاً نُوح۪ٓي اِلَيْهِمْ فَسْـَٔلُٓوا اَهْلَ الذِّكْرِ اِنْ كُنْتُمْ لَا تَعْلَمُونَۙ', 'Senden önce de, kendilerine vahyettiğimiz kişilerden başkasını peygamber olarak göndermedik. Eğer bilmiyorsanız, bilenlere sorun.', 14, 271, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1945, 44, 16, 'Nahl', 'بِالْبَيِّنَاتِ وَالزُّبُرِۜ وَاَنْزَلْـنَٓا اِلَيْكَ الذِّكْرَ لِتُبَيِّنَ لِلنَّاسِ مَا نُزِّلَ اِلَيْهِمْ وَلَعَلَّهُمْ يَتَفَكَّرُونَ', 'Apaçık mucizeler ve kitaplarla (gönderildiler). İnsanlara, kendilerine indirileni açıklaman için ve düşünüp anlasınlar diye sana da bu Kur´an´ı indirdik.', 14, 271, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1946, 45, 16, 'Nahl', 'اَفَاَمِنَ الَّذ۪ينَ مَكَرُوا السَّيِّـَٔاتِ اَنْ يَخْسِفَ اللّٰهُ بِهِمُ الْاَرْضَ اَوْ يَأْتِيَهُمُ الْعَذَابُ مِنْ حَيْثُ لَا يَشْعُرُونَۙ', '(45-46) Kötülük tuzakları kuranlar, Allah´ın, kendilerini yere geçirmeyeceğinden veya kendilerine bilemeyecekleri bir yerden azabın gelmeyeceğinden veya onlar dönüp dolaşırlarken Allah´ın kendilerini yakalamayacağından emin mi oldular? Onlar (Allah´ı) âciz bırakacak değillerdir.', 14, 271, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1947, 46, 16, 'Nahl', 'اَوْ يَأْخُذَهُمْ ف۪ي تَقَلُّبِهِمْ فَمَا هُمْ بِمُعْجِز۪ينَۙ', '(45-46) Kötülük tuzakları kuranlar, Allah´ın, kendilerini yere geçirmeyeceğinden veya kendilerine bilemeyecekleri bir yerden azabın gelmeyeceğinden veya onlar dönüp dolaşırlarken Allah´ın kendilerini yakalamayacağından emin mi oldular? Onlar (Allah´ı) âciz bırakacak değillerdir.', 14, 271, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1948, 47, 16, 'Nahl', 'اَوْ يَأْخُذَهُمْ عَلٰى تَخَوُّفٍۜ فَاِنَّ رَبَّكُمْ لَرَؤُ۫فٌ رَح۪يمٌ', 'Yoksa Allah´ın kendilerini yavaş yavaş tüketerek cezalandırmayacağından (emin mi oldular)? Kuşkusuz Rabbin çok şefkatli, pek merhametlidir.', 14, 271, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1949, 48, 16, 'Nahl', 'اَوَلَمْ يَرَوْا اِلٰى مَا خَلَقَ اللّٰهُ مِنْ شَيْءٍ يَتَفَيَّؤُ۬ا ظِلَالُهُ عَنِ الْيَم۪ينِ وَالشَّمَٓائِلِ سُجَّداً لِلّٰهِ وَهُمْ دَاخِرُونَ', 'Allah´ın yarattığı herhangi bir şeyi görmediler mi? Onun gölgeleri, küçülerek ve Allah´a secde ederek sağa sola döner.', 14, 271, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1950, 49, 16, 'Nahl', 'وَلِلّٰهِ يَسْجُدُ مَا فِي السَّمٰوَاتِ وَمَا فِي الْاَرْضِ مِنْ دَٓابَّةٍ وَالْمَلٰٓئِكَةُ وَهُمْ لَا يَسْتَكْبِرُونَ ۩', 'Göklerde bulunanlar, yerdeki canlılar ve bütün melekler, büyüklük taslamadan Allah´a secde ederler.', 14, 271, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1951, 50, 16, 'Nahl', 'يَخَافُونَ رَبَّهُمْ مِنْ فَوْقِهِمْ وَيَفْعَلُونَ مَا يُؤْمَرُونَ۟', 'Onlar, üstlerindeki Rablerinden korkarlar ve kendilerine ne emrolunursa onu yaparlar.', 14, 271, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1952, 51, 16, 'Nahl', 'وَقَالَ اللّٰهُ لَا تَتَّخِذُٓوا اِلٰهَيْنِ اثْنَيْنِۚ اِنَّمَا هُوَ اِلٰهٌ وَاحِدٌۚ فَاِيَّايَ فَارْهَبُونِ', 'Allah buyurdu ki: İki tanrı edinmeyin! O ancak bir Tanrı´dır. O halde yalnız benden korkun!', 14, 271, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1953, 52, 16, 'Nahl', 'وَلَهُ مَا فِي السَّمٰوَاتِ وَالْاَرْضِ وَلَهُ الدّ۪ينُ وَاصِباًۜ اَفَغَيْرَ اللّٰهِ تَتَّقُونَ', 'Göklerde ve yerde ne varsa, O´nundur, din de yalnız O´nundur. O halde Allah´tan başkasından mı korkuyorsunuz?', 14, 271, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1954, 53, 16, 'Nahl', 'وَمَا بِكُمْ مِنْ نِعْمَةٍ فَمِنَ اللّٰهِ ثُمَّ اِذَا مَسَّكُمُ الضُّرُّ فَاِلَيْهِ تَجْـَٔرُونَۚ', 'Nimet olarak size ulaşan ne varsa, Allah´tandır. Sonra size bir zarar dokunduğu zaman da yalnız O´na yalvarırsınız.', 14, 271, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1955, 54, 16, 'Nahl', 'ثُمَّ اِذَا كَشَفَ الضُّرَّ عَنْكُمْ اِذَا فَر۪يقٌ مِنْكُمْ بِرَبِّهِمْ يُشْرِكُونَۙ', 'Sonra da sizden o zararı giderdiğinde, içinizden bir zümre, hemen Rablerine ortak koşarlar!', 14, 271, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1956, 55, 16, 'Nahl', 'لِيَكْفُرُوا بِمَٓا اٰتَيْنَاهُمْۜ فَتَمَتَّعُوا۠ فَسَوْفَ تَعْلَمُونَ', 'Kendilerine verdiklerimize karşılık nankörlük etmeleri için (öyle yaparlar). O halde bir süre daha faydalanın; fakat yakında hakikati bileceksiniz!', 14, 272, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1957, 56, 16, 'Nahl', 'وَيَجْعَلُونَ لِمَا لَا يَعْلَمُونَ نَص۪يباً مِمَّا رَزَقْنَاهُمْۜ تَاللّٰهِ لَتُسْـَٔلُنَّ عَمَّا كُنْتُمْ تَفْتَرُونَ', 'Bir de kendilerine rızık olarak verdiklerimizden, mahiyetini bilmedikleri şeylere (putlara) pay ayırıyorlar. Allah´a andolsun ki, iftira etmekte olduğunuz şeylerden mutlaka sorguya çekileceksiniz!', 14, 272, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1958, 57, 16, 'Nahl', 'وَيَجْعَلُونَ لِلّٰهِ الْبَنَاتِ سُبْحَانَهُۙ وَلَهُمْ مَا يَشْتَهُونَ', 'Onlar, kızların Allah´a ait olduğunu iddia ediyorlar. Hâşâ! Allah bundan münezzehtir. Beğendikleri de (erkek çocuklar) kendilerinin oluyor.', 14, 272, 128, 7723, 'Bal arısı', 'Mekke', 70);
INSERT INTO `Ayetler` (`id`, `AyetNo`, `SureNo`, `SureAdi`, `Metin`, `Mana`, `Cuzu`, `Sayfasi`, `AyetSayisi`, `HarfSayisi`, `AdAnlami`, `IndigiYer`, `InisSirasi`) VALUES
(1959, 58, 16, 'Nahl', 'وَاِذَا بُشِّرَ اَحَدُهُمْ بِالْاُنْثٰى ظَلَّ وَجْهُهُ مُسْوَداًّ وَهُوَ كَظ۪يمٌۚ', 'Onlardan birine kız müjdelendiği zaman öfkelenmiş olarak yüzü kapkara kesilir.', 14, 272, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1960, 59, 16, 'Nahl', 'يَتَوَارٰى مِنَ الْقَوْمِ مِنْ سُٓوءِ مَا بُشِّرَ بِه۪ۜ اَيُمْسِكُهُ عَلٰى هُونٍ اَمْ يَدُسُّهُ فِي التُّرَابِۜ اَلَا سَٓاءَ مَا يَحْكُمُونَ', 'Kendisine verilen müjdenin kötülüğünden dolayı kavminden gizlenir. Onu, aşağılık duygusu içinde yanında mı tutsun, yoksa toprağa mı gömsün! Bakın ki, verdikleri hüküm ne kadar kötüdür!', 14, 272, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1961, 60, 16, 'Nahl', 'لِلَّذ۪ينَ لَا يُؤْمِنُونَ بِالْاٰخِرَةِ مَثَلُ السَّوْءِۚ وَلِلّٰهِ الْمَثَلُ الْاَعْلٰىۜ وَهُوَ الْعَز۪يزُ الْحَك۪يمُ۟', 'Kötü sıfat, ahirete inanmayanlar içindir. En yüce sıfatlar ise Allah´a aittir. Çünkü O, her şeyden üstün ve hikmet sahibidir.', 14, 272, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1962, 61, 16, 'Nahl', 'وَلَوْ يُؤَاخِذُ اللّٰهُ النَّاسَ بِظُلْمِهِمْ مَا تَرَكَ عَلَيْهَا مِنْ دَٓابَّةٍ وَلٰكِنْ يُؤَخِّرُهُمْ اِلٰٓى اَجَلٍ مُسَمًّىۚ فَاِذَا جَٓاءَ اَجَلُهُمْ لَا يَسْتَأْخِرُونَ سَاعَةً وَلَا يَسْتَقْدِمُونَ', 'Eğer Allah, insanları zulümleri yüzünden cezalandıracak olsaydı, orada hiçbir canlı bırakmazdı. Fakat onları takdir edilen bir müddete kadar erteliyor. Ecelleri geldiği zaman onlar ne bir saat geri kalabilirler ne de öne geçebilirler.', 14, 272, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1963, 62, 16, 'Nahl', 'وَيَجْعَلُونَ لِلّٰهِ مَا يَكْرَهُونَ وَتَصِفُ اَلْسِنَتُهُمُ الْكَذِبَ اَنَّ لَهُمُ الْحُسْنٰىۜ لَا جَرَمَ اَنَّ لَهُمُ النَّارَ وَاَنَّهُمْ مُفْرَطُونَ', 'Kendilerinin hoşlarına gitmeyen şeyleri Allah´a isnat ediyorlar. En güzel sonucun kendilerinin olduğunu anlatan dilleri de yalanın örneğini veriyor. Hiç şüphesiz onlar için sadece ateş vardır ve onlar, (ateşe) terkolunacaklar.', 14, 272, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1964, 63, 16, 'Nahl', 'تَاللّٰهِ لَقَدْ اَرْسَلْـنَٓا اِلٰٓى اُمَمٍ مِنْ قَبْلِكَ فَزَيَّنَ لَهُمُ الشَّيْطَانُ اَعْمَالَهُمْ فَهُوَ وَلِيُّهُمُ الْيَوْمَ وَلَهُمْ عَذَابٌ اَل۪يمٌ', 'Allah´a andolsun, senden önceki ümmetlere de (peygamberler) göndermişizdir. Fakat şeytan onlara işlerini süslü gösterdi de (iman etmediler). işte o, bugün onların velisidir. Ve onlar için elem verici bir azap vardır.', 14, 272, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1965, 64, 16, 'Nahl', 'وَمَٓا اَنْزَلْنَا عَلَيْكَ الْكِتَابَ اِلَّا لِتُبَيِّنَ لَهُمُ الَّذِي اخْتَلَفُوا ف۪يهِۙ وَهُدًى وَرَحْمَةً لِقَوْمٍ يُؤْمِنُونَ', 'Biz bu Kitab´ı sana sırf hakkında ihtilafa düştükleri şeyi insanlara açıklayasın ve iman eden bir topluma da hidayet ve rahmet olsun diye indirdik.', 14, 272, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1966, 65, 16, 'Nahl', 'وَاللّٰهُ اَنْزَلَ مِنَ السَّمَٓاءِ مَٓاءً فَاَحْيَا بِهِ الْاَرْضَ بَعْدَ مَوْتِهَاۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَةً لِقَوْمٍ يَسْمَعُونَ۟', 'Allah gökten bir su indirdi ve onunla yeryüzünü ölümünden sonra diriltti. Şüphesiz ki bunda dinleyen toplum için bir ibret vardır.', 14, 273, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1967, 66, 16, 'Nahl', 'وَاِنَّ لَكُمْ فِي الْاَنْعَامِ لَعِبْرَةًۜ نُسْق۪يكُمْ مِمَّا ف۪ي بُطُونِه۪ مِنْ بَيْنِ فَرْثٍ وَدَمٍ لَبَناً خَالِصاً سَٓائِغاً لِلشَّارِب۪ينَ', 'Kuşkusuz sizin için hayvanlarda da büyük bir ibret vardır. Zira size, onların karınlarındaki fışkı ile kan arasından (gelen), içenlerin boğazından kolayca geçen hâlis bir süt içiriyoruz.', 14, 273, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1968, 67, 16, 'Nahl', 'وَمِنْ ثَمَرَاتِ النَّخ۪يلِ وَالْاَعْنَابِ تَتَّخِذُونَ مِنْهُ سَكَراً وَرِزْقاً حَسَناًۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَةً لِقَوْمٍ يَعْقِلُونَ', 'Hurma ve üzüm gibi meyvelerden hem içki hem de güzel gıdalar edinirsiniz. İşte bunlarda da aklını kullanan kimseler için büyük bir ibret vardır.', 14, 273, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1969, 68, 16, 'Nahl', 'وَاَوْحٰى رَبُّكَ اِلَى النَّحْلِ اَنِ اتَّخِذ۪ي مِنَ الْجِبَالِ بُيُوتاً وَمِنَ الشَّجَرِ وَمِمَّا يَعْرِشُونَۙ', '(68-69) Rabbin bal arısına: Dağlardan, ağaçlardan ve insanların yaptıkları çardaklardan kendine evler (kovanlar) edin. Sonra meyvelerin her birinden ye ve Rabbinin sana kolaylaştırdığı yaylım yollarına gir, diye ilham etti. Onların karınlarından renkleri çeşitli bir şerbet (bal) çıkar ki, onda insanlar için şifa vardır. Elbette bunda düşünen bir kavim için büyük bir ibret vardır.', 14, 273, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1970, 69, 16, 'Nahl', 'ثُمَّ كُل۪ي مِنْ كُلِّ الثَّمَرَاتِ فَاسْلُك۪ي سُبُلَ رَبِّكِ ذُلُلاًۜ يَخْرُجُ مِنْ بُطُونِهَا شَرَابٌ مُخْتَلِفٌ اَلْوَانُهُ ف۪يهِ شِفَٓاءٌ لِلنَّاسِۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَةً لِقَوْمٍ يَتَفَكَّرُونَ', '(68-69) Rabbin bal arısına: Dağlardan, ağaçlardan ve insanların yaptıkları çardaklardan kendine evler (kovanlar) edin. Sonra meyvelerin her birinden ye ve Rabbinin sana kolaylaştırdığı yaylım yollarına gir, diye ilham etti. Onların karınlarından renkleri çeşitli bir şerbet (bal) çıkar ki, onda insanlar için şifa vardır. Elbette bunda düşünen bir kavim için büyük bir ibret vardır.', 14, 273, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1971, 70, 16, 'Nahl', 'وَاللّٰهُ خَلَقَكُمْ ثُمَّ يَتَوَفّٰيكُمْ وَمِنْكُمْ مَنْ يُرَدُّ اِلٰٓى اَرْذَلِ الْعُمُرِ لِكَيْ لَا يَعْلَمَ بَعْدَ عِلْمٍ شَيْـٔاًۜ اِنَّ اللّٰهَ عَل۪يمٌ قَد۪يرٌ۟', 'Sizi Allah yarattı; sonra sizi vefat ettirecek. Daha önce bilgili iken hiçbir şeyi bilmez hale gelsin diye sizden bazı kimseler ömrün en kötü çağına kadar yaşatılacak Şüphesiz ki Allah bilgilidir, kudretlidir.', 14, 273, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1972, 71, 16, 'Nahl', 'وَاللّٰهُ فَضَّلَ بَعْضَكُمْ عَلٰى بَعْضٍ فِي الرِّزْقِۚ فَمَا الَّذ۪ينَ فُضِّلُوا بِرَٓادّ۪ي رِزْقِهِمْ عَلٰى مَا مَلَكَتْ اَيْمَانُهُمْ فَهُمْ ف۪يهِ سَوَٓاءٌۜ اَفَبِنِعْمَةِ اللّٰهِ يَجْحَدُونَ', 'Allah kiminize kiminizden daha bol rızık verdi. Bol rızık verilenler, rızıklarını ellerinin altındakilere verip de bu hususta kendilerini onlara eşit kılmazlar. Durum böyle iken Allah´ın nimetini inkâr mı ediyorlar?', 14, 273, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1973, 72, 16, 'Nahl', 'وَاللّٰهُ جَعَلَ لَكُمْ مِنْ اَنْفُسِكُمْ اَزْوَاجاً وَجَعَلَ لَكُمْ مِنْ اَزْوَاجِكُمْ بَن۪ينَ وَحَفَدَةً وَرَزَقَكُمْ مِنَ الطَّيِّبَاتِۜ اَفَبِالْبَاطِلِ يُؤْمِنُونَ وَبِنِعْمَتِ اللّٰهِ هُمْ يَكْفُرُونَۙ', 'Allah size kendi nefislerinizden eşler yarattı, eşlerinizden de sizin için oğullar ve torunlar yarattı ve sizi temiz gıdalarla rızıklandırdı. Onlar hâla bâtıla inanıp Allah´ın nimetine nankörlük mü ediyorlar?', 14, 273, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1974, 73, 16, 'Nahl', 'وَيَعْبُدُونَ مِنْ دُونِ اللّٰهِ مَا لَا يَمْلِكُ لَهُمْ رِزْقاً مِنَ السَّمٰوَاتِ وَالْاَرْضِ شَيْـٔاً وَلَا يَسْتَط۪يعُونَۚ', '(Müşrikler) Allah´ı bırakıp da kendilerine göklerde ve yerde olan rızıktan hiçbir şey veremeyen ve buna asla güçleri yetmeyen şeylere (putlara) tapıyorlar.', 14, 274, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1975, 74, 16, 'Nahl', 'فَلَا تَضْرِبُوا لِلّٰهِ الْاَمْثَالَۜ اِنَّ اللّٰهَ يَعْلَمُ وَاَنْتُمْ لَا تَعْلَمُونَ', 'Allah için emsal göstermeyin. Çünkü Allah bilir, siz ise bilmezsiniz.', 14, 274, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1976, 75, 16, 'Nahl', 'ضَرَبَ اللّٰهُ مَثَلاً عَبْداً مَمْلُوكاً لَا يَقْدِرُ عَلٰى شَيْءٍ وَمَنْ رَزَقْنَاهُ مِنَّا رِزْقاً حَسَناً فَهُوَ يُنْفِقُ مِنْهُ سِراًّ وَجَهْراًۜ هَلْ يَسْتَوُ۫نَۜ اَلْحَمْدُ لِلّٰهِۜ بَلْ اَكْثَرُهُمْ لَا يَعْلَمُونَ', 'Allah, hiçbir şeye gücü yetmeyen, başkasının malı olmuş bir köle ile katımızdan kendisine verdiğimiz güzel rızıktan gizli ve açık olarak harcayan (hür) bir kimseyi misal verir. Bunlar hiç eşit olurlar mı? Doğrusu hamd Allah´a mahsustur. Fakat onların çoğu (bunu) bilmezler.', 14, 274, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1977, 76, 16, 'Nahl', 'وَضَرَبَ اللّٰهُ مَثَلاً رَجُلَيْنِ اَحَدُهُمَٓا اَبْكَمُ لَا يَقْدِرُ عَلٰى شَيْءٍ وَهُوَ كَلٌّ عَلٰى مَوْلٰيهُۙ اَيْنَمَا يُوَجِّهْهُ لَا يَأْتِ بِخَيْرٍۜ هَلْ يَسْتَو۪ي هُوَۙ وَمَنْ يَأْمُرُ بِالْعَدْلِۙ وَهُوَ عَلٰى صِرَاطٍ مُسْتَق۪يمٍ۟', 'Allah, şu iki kişiyi de misal verir: Onlardan biri dilsizdir, hiçbir şey beceremez ve efendisinin üstüne bir yüktür. Onu nereye gönderse bir hayır getiremez. Şimdi, bu adamla, doğru yolda yürüyerek adaleti emreden kimse eşit olur mu?', 14, 274, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1978, 77, 16, 'Nahl', 'وَلِلّٰهِ غَيْبُ السَّمٰوَاتِ وَالْاَرْضِۜ وَمَٓا اَمْرُ السَّاعَةِ اِلَّا كَلَمْحِ الْبَصَرِ اَوْ هُوَ اَقْرَبُۜ اِنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌ', 'Göklerin ve yerin gaybı Allah´a aittir. Kıyametin kopması ise, göz açıp kapama gibi veya daha az bir zamandan ibarettir. Şüphesiz Allah, her şeye kadirdir.', 14, 274, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1979, 78, 16, 'Nahl', 'وَاللّٰهُ اَخْرَجَكُمْ مِنْ بُطُونِ اُمَّهَاتِكُمْ لَا تَعْلَمُونَ شَيْـٔاًۙ وَجَعَلَ لَكُمُ السَّمْعَ وَالْاَبْصَارَ وَالْاَفْـِٔدَةَۙ لَعَلَّكُمْ تَشْكُرُونَ', 'Siz, hiçbir şey bilmezken Allah, sizi analarınızın karnından çıkardı; şükredesiniz diye size kulaklar, gözler ve kalpler verdi.', 14, 274, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1980, 79, 16, 'Nahl', 'اَلَمْ يَرَوْا اِلَى الطَّيْرِ مُسَخَّرَاتٍ ف۪ي جَوِّ السَّمَٓاءِۜ مَا يُمْسِكُهُنَّ اِلَّا اللّٰهُۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَاتٍ لِقَوْمٍ يُؤْمِنُونَ', 'Göğün boşluğunda emre boyun eğdirilmiş olarak uçuşan kuşları görmediler mi? Onları orada Allah´tan başkası tutamaz. Kuşkusuz bunda inanan bir toplum için ibretler vardır.', 14, 274, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1981, 80, 16, 'Nahl', 'وَاللّٰهُ جَعَلَ لَكُمْ مِنْ بُيُوتِكُمْ سَكَناً وَجَعَلَ لَكُمْ مِنْ جُلُودِ الْاَنْعَامِ بُيُوتاً تَسْتَخِفُّونَهَا يَوْمَ ظَعْنِكُمْ وَيَوْمَ اِقَامَتِكُمْۙ وَمِنْ اَصْوَافِهَا وَاَوْبَارِهَا وَاَشْعَارِهَٓا اَثَاثاً وَمَتَاعاً اِلٰى ح۪ينٍ', 'Allah, evlerinizi sizin için bir huzur ve sükûn yeri yaptı ve sizin için davar derilerinden gerek göç gününüzde, gerekse konaklama gününüzde, kolayca taşıyacağınız evler; yünlerinden, yapağılarından ve kıllarından bir süreye kadar (faydalanacağınız) bir ev eşyası ve bir ticaret malı meydana getirdi.', 14, 275, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1982, 81, 16, 'Nahl', 'وَاللّٰهُ جَعَلَ لَكُمْ مِمَّا خَلَقَ ظِلَالاً وَجَعَلَ لَكُمْ مِنَ الْجِبَالِ اَكْنَاناً وَجَعَلَ لَكُمْ سَرَاب۪يلَ تَق۪يكُمُ الْحَرَّ وَسَرَاب۪يلَ تَق۪يكُمْ بَأْسَكُمْۜ كَذٰلِكَ يُتِمُّ نِعْمَتَهُ عَلَيْكُمْ لَعَلَّكُمْ تُسْلِمُونَ', 'Allah, yarattıklarından sizin için gölgeler yaptı. Dağlarda da sizin için barınaklar yarattı. Sizi sıcaktan koruyacak elbiseler ve savaşta sizi koruyacak zırhlar yarattı. İşte böylece Allah, müslüman olmanız için üzerinize nimetini tamamlıyor.', 14, 275, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1983, 82, 16, 'Nahl', 'فَاِنْ تَوَلَّوْا فَاِنَّمَا عَلَيْكَ الْبَلَاغُ الْمُب۪ينُ', '(Ey Resûlüm!) Yine de yüz çevirirlerse, artık sana düşen ancak açık bir tebliğden ibarettir.', 14, 275, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1984, 83, 16, 'Nahl', 'يَعْرِفُونَ نِعْمَتَ اللّٰهِ ثُمَّ يُنْكِرُونَهَا وَاَكْثَرُهُمُ الْكَافِرُونَ۟', 'Onlar Allah´ın nimetini bilirler (itiraf ederler). Sonra da onu inkâr ederler. Onların çoğu kâfirdir.', 14, 275, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1985, 84, 16, 'Nahl', 'وَيَوْمَ نَبْعَثُ مِنْ كُلِّ اُمَّةٍ شَه۪يداً ثُمَّ لَا يُؤْذَنُ لِلَّذ۪ينَ كَفَرُوا وَلَا هُمْ يُسْتَعْتَبُونَ', 'Her ümmetten bir şahit göndereceğimiz gün, artık ne kâfir olanlara (özür dilemelerine) izin verilir ne de onların özür dilemeleri istenir.', 14, 275, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1986, 85, 16, 'Nahl', 'وَاِذَا رَاَ الَّذ۪ينَ ظَلَمُوا الْعَذَابَ فَلَا يُخَفَّفُ عَنْهُمْ وَلَا هُمْ يُنْظَرُونَ', 'O zulmedenler azabı gördüklerinde, artık onlardan azap hafifletilmez, onlara mühlet de verilmez.', 14, 275, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1987, 86, 16, 'Nahl', 'وَاِذَا رَاَ الَّذ۪ينَ اَشْرَكُوا شُرَكَٓاءَهُمْ قَالُوا رَبَّـنَا هٰٓؤُ۬لَٓاءِ شُرَكَٓاؤُ۬نَا الَّذ۪ينَ كُنَّا نَدْعُوا مِنْ دُونِكَۚ فَاَلْقَوْا اِلَيْهِمُ الْقَوْلَ اِنَّكُمْ لَكَاذِبُونَۚ', '(Allah´a) ortak koşanlar, ortak koştukları şeyleri gördükleri zaman derler ki: «Rabbimiz! İşte bunlar, seni bırakıp da tapmış olduğumuz ortaklarımızdır.» Onlar da bunlara: «Siz mutlaka yalancılarsınız» diye söz atarlar.', 14, 275, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1988, 87, 16, 'Nahl', 'وَاَلْقَوْا اِلَى اللّٰهِ يَوْمَئِذٍۨ السَّلَمَ وَضَلَّ عَنْهُمْ مَا كَانُوا يَفْتَرُونَ', 'O gün Allah´a teslim (bayrağını) çekerler ve uydurmakta oldukları şeyler onlardan kaybolup gider.', 14, 275, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1989, 88, 16, 'Nahl', 'اَلَّذ۪ينَ كَفَرُوا وَصَدُّوا عَنْ سَب۪يلِ اللّٰهِ زِدْنَاهُمْ عَذَاباً فَوْقَ الْعَذَابِ بِمَا كَانُوا يُفْسِدُونَ', 'İnkâr edip de (insanları) Allah yolundan alıkoyanlar var ya, işte onlara, yapmakta oldukları bozgunculuklar sebebiyle, azaplarını kat kat artıracağız.', 14, 276, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1990, 89, 16, 'Nahl', 'وَيَوْمَ نَبْعَثُ ف۪ي كُلِّ اُمَّةٍ شَه۪يداً عَلَيْهِمْ مِنْ اَنْفُسِهِمْ وَجِئْنَا بِكَ شَه۪يداً عَلٰى هٰٓؤُ۬لَٓاءِۜ وَنَزَّلْنَا عَلَيْكَ الْكِتَابَ تِبْيَاناً لِكُلِّ شَيْءٍ وَهُدًى وَرَحْمَةً وَبُشْرٰى لِلْمُسْلِم۪ينَ۟', 'O gün her ümmetin içinden kendilerine birer şahit göndereceğiz. Seni de hepsinin üzerine şahit olarak getireceğiz. Ayrıca bu Kitab´ı da sana, her şey için bir açıklama, bir hidayet ve rahmet kaynağı ve müslümanlar için bir müjde olarak indirdik.', 14, 276, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1991, 90, 16, 'Nahl', 'اِنَّ اللّٰهَ يَأْمُرُ بِالْعَدْلِ وَالْاِحْسَانِ وَا۪يتَٓائِ ذِي الْقُرْبٰى وَيَنْهٰى عَنِ الْفَحْشَٓاءِ وَالْمُنْكَرِ وَالْبَغْيِۚ يَعِظُكُمْ لَعَلَّكُمْ تَذَكَّرُونَ', 'Muhakkak ki Allah, adaleti, iyiliği, akrabaya yardım etmeyi emreder, çirkin işleri, fenalık ve azgınlığı da yasaklar. O, düşünüp tutasınız diye size öğüt veriyor.', 14, 276, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1992, 91, 16, 'Nahl', 'وَاَوْفُوا بِعَهْدِ اللّٰهِ اِذَا عَاهَدْتُمْ وَلَا تَنْقُضُوا الْاَيْمَانَ بَعْدَ تَوْك۪يدِهَا وَقَدْ جَعَلْتُمُ اللّٰهَ عَلَيْكُمْ كَف۪يلاًۜ اِنَّ اللّٰهَ يَعْلَمُ مَا تَفْعَلُونَ', 'Antlaşma yaptığınız zaman, Allah´ın ahdini yerine getirin ve Allah´ı üzerinize şahit tutarak, pekiştirdikten sonra yeminleri bozmayın. Şüphesiz Allah, yapacağınız şeyleri pek iyi bilir.', 14, 276, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1993, 92, 16, 'Nahl', 'وَلَا تَكُونُوا كَالَّت۪ي نَقَضَتْ غَزْلَهَا مِنْ بَعْدِ قُوَّةٍ اَنْكَاثاًۜ تَتَّخِذُونَ اَيْمَانَكُمْ دَخَلاً بَيْنَكُمْ اَنْ تَكُونَ اُمَّةٌ هِيَ اَرْبٰى مِنْ اُمَّةٍۜ اِنَّمَا يَبْلُوكُمُ اللّٰهُ بِه۪ۜ وَلَيُبَيِّنَنَّ لَكُمْ يَوْمَ الْقِيٰمَةِ مَا كُنْتُمْ ف۪يهِ تَخْتَلِفُونَ', 'Bir toplum diğer bir toplumdan (sayıca ve malca) daha çok olduğu için yeminlerinizi, aranızda bir fesat aracı edinerek ipliğini sağlamca büktükten sonra, çözüp bozan (kadın) gibi olmayın. Allah, bununla sizi imtihan etmektedir. Hakkında ihtilafa düşmekte olduğunuz şeyi kıyamet gününde mutlaka size açıklayacaktır.', 14, 276, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1994, 93, 16, 'Nahl', 'وَلَوْ شَٓاءَ اللّٰهُ لَجَعَلَكُمْ اُمَّةً وَاحِدَةً وَلٰكِنْ يُضِلُّ مَنْ يَشَٓاءُ وَيَهْد۪ي مَنْ يَشَٓاءُۜ وَلَتُسْـَٔلُنَّ عَمَّا كُنْتُمْ تَعْمَلُونَ', 'Allah dileseydi hepinizi bir tek ümmet kılardı; fakat O, dilediğini saptırır, dilediğini de doğru yola iletir. Yaptıklarınızdan mutlaka sorumlu tutulacaksınız.', 14, 276, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1995, 94, 16, 'Nahl', 'وَلَا تَتَّخِذُٓوا اَيْمَانَكُمْ دَخَلاً بَيْنَكُمْ فَتَزِلَّ قَدَمٌ بَعْدَ ثُبُوتِهَا وَتَذُوقُوا السُّٓوءَ بِمَا صَدَدْتُمْ عَنْ سَب۪يلِ اللّٰهِۚ وَلَكُمْ عَذَابٌ عَظ۪يمٌ', 'Yeminlerinizi aranızda fesada araç edinmeyin, aksi halde (İslâm´da) sebat etmişken ayağınız kayar da (insanları) Allah yolundan alıkoymanız sebebiyle (dünyada) kötülüğü tadarsınız. Sizin için (ahirette de) büyük bir azap vardır.', 14, 277, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1996, 95, 16, 'Nahl', 'وَلَا تَشْتَرُوا بِعَهْدِ اللّٰهِ ثَمَناً قَل۪يلاًۜ اِنَّمَا عِنْدَ اللّٰهِ هُوَ خَيْرٌ لَكُمْ اِنْ كُنْتُمْ تَعْلَمُونَ', 'Allah´ın ahdini az bir karşılığa değişmeyin! Şayet anlayan kimseler iseniz, şüphesiz Allah katında olan (sevap) sizin için daha hayırlıdır.', 14, 277, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1997, 96, 16, 'Nahl', 'مَا عِنْدَكُمْ يَنْفَدُ وَمَا عِنْدَ اللّٰهِ بَاقٍۜ وَلَنَجْزِيَنَّ الَّذ۪ينَ صَبَرُٓوا اَجْرَهُمْ بِاَحْسَنِ مَا كَانُوا يَعْمَلُونَ', 'Sizin yanınızdaki (dünya malı) tükenir, Allah katındakiler ise bâkidir. Elbette sabırlı davrananlara yapmakta olduklarının en güzeliyle mükâfatlarını vereceğiz.', 14, 277, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1998, 97, 16, 'Nahl', 'مَنْ عَمِلَ صَالِحاً مِنْ ذَكَرٍ اَوْ اُنْثٰى وَهُوَ مُؤْمِنٌ فَلَنُحْيِيَنَّهُ حَيٰوةً طَيِّبَةًۚ وَلَنَجْزِيَنَّهُمْ اَجْرَهُمْ بِاَحْسَنِ مَا كَانُوا يَعْمَلُونَ', 'Erkek veya kadın, mümin olarak kim iyi amel işlerse, onu mutlaka güzel bir hayat ile yaşatırız. Ve mükâfatlarını, elbette yapmakta olduklarının en güzeli ile veririz.', 14, 277, 128, 7723, 'Bal arısı', 'Mekke', 70),
(1999, 98, 16, 'Nahl', 'فَاِذَا قَرَأْتَ الْقُرْاٰنَ فَاسْتَعِذْ بِاللّٰهِ مِنَ الشَّيْطَانِ الرَّج۪يمِ', 'Kur´an okuduğun zaman o kovulmuş şeytandan Allah´a sığın!', 14, 277, 128, 7723, 'Bal arısı', 'Mekke', 70),
(2000, 99, 16, 'Nahl', 'اِنَّهُ لَيْسَ لَهُ سُلْطَانٌ عَلَى الَّذ۪ينَ اٰمَنُوا وَعَلٰى رَبِّهِمْ يَتَوَكَّلُونَ', 'Gerçek şu ki: İman edip de yalnız Rablerine tevekkül edenler üzerinde onun (şeytanın) bir hakimiyeti yoktur.', 14, 277, 128, 7723, 'Bal arısı', 'Mekke', 70),
(2001, 100, 16, 'Nahl', 'اِنَّمَا سُلْطَانُهُ عَلَى الَّذ۪ينَ يَتَوَلَّوْنَهُ وَالَّذ۪ينَ هُمْ بِه۪ مُشْرِكُونَ۟', 'Onun hakimiyeti, ancak onu dost edinenlere ve onu Allah´a ortak koşanlaradır.', 14, 277, 128, 7723, 'Bal arısı', 'Mekke', 70),
(2002, 101, 16, 'Nahl', 'وَاِذَا بَدَّلْـنَٓا اٰيَةً مَكَانَ اٰيَةٍۙ وَاللّٰهُ اَعْلَمُ بِمَا يُنَزِّلُ قَالُٓوا اِنَّـمَٓا اَنْتَ مُفْتَرٍۜ بَلْ اَكْثَرُهُمْ لَا يَعْلَمُونَ', 'Biz bir âyetin yerine başka bir âyeti getirdiğimiz zaman -ki Allah, neyi indireceğini çok iyi bilir- «Sen ancak bir iftiracısın» dediler. Hayır; onların çoğu bilmezler.', 14, 277, 128, 7723, 'Bal arısı', 'Mekke', 70),
(2003, 102, 16, 'Nahl', 'قُلْ نَزَّلَهُ رُوحُ الْقُدُسِ مِنْ رَبِّكَ بِالْحَقِّ لِيُثَبِّتَ الَّذ۪ينَ اٰمَنُوا وَهُدًى وَبُشْرٰى لِلْمُسْلِم۪ينَ', 'De ki: Onu, Mukaddes Rûh (Cebrail), iman edenlere sebat vermek, müslümanları doğru yola iletmek ve onlara müjde vermek için, Rabbin katından hak olarak indirdi.', 14, 277, 128, 7723, 'Bal arısı', 'Mekke', 70),
(2004, 103, 16, 'Nahl', 'وَلَقَدْ نَعْلَمُ اَنَّهُمْ يَقُولُونَ اِنَّمَا يُعَلِّمُهُ بَشَرٌۜ لِسَانُ الَّذ۪ي يُلْحِدُونَ اِلَيْهِ اَعْجَمِيٌّ وَهٰذَا لِسَانٌ عَرَبِيٌّ مُب۪ينٌ', 'Şüphesiz biz onların: «Kur´an´ı ona ancak bir insan öğretiyor» dediklerini biliyoruz. Kendisine nisbet ettikleri şahsın dili yabancıdır. Halbuki bu (Kur´an) apaçık bir Arapçadır.', 14, 278, 128, 7723, 'Bal arısı', 'Mekke', 70),
(2005, 104, 16, 'Nahl', 'اِنَّ الَّذ۪ينَ لَا يُؤْمِنُونَ بِاٰيَاتِ اللّٰهِۙ لَا يَهْد۪يهِمُ اللّٰهُ وَلَهُمْ عَذَابٌ اَل۪يمٌ', 'Allah´ın âyetlerine inanmayanlar yok mu, kuşkusuz Allah onları doğru yola iletmez ve onlar için elem verici bir azap vardır.', 14, 278, 128, 7723, 'Bal arısı', 'Mekke', 70),
(2006, 105, 16, 'Nahl', 'اِنَّمَا يَفْتَرِي الْكَذِبَ الَّذ۪ينَ لَا يُؤْمِنُونَ بِاٰيَاتِ اللّٰهِۚ وَاُو۬لٰٓئِكَ هُمُ الْكَاذِبُونَ', 'Allah´ın âyetlerine inanmayanlar, ancak yalan uydurur. İşte onlar, yalancıların kendileridir.', 14, 278, 128, 7723, 'Bal arısı', 'Mekke', 70),
(2007, 106, 16, 'Nahl', 'مَنْ كَفَرَ بِاللّٰهِ مِنْ بَعْدِ ا۪يمَانِه۪ٓ اِلَّا مَنْ اُكْرِهَ وَقَلْبُهُ مُطْمَئِنٌّ بِالْا۪يمَانِ وَلٰكِنْ مَنْ شَرَحَ بِالْكُفْرِ صَدْراً فَعَلَيْهِمْ غَضَبٌ مِنَ اللّٰهِۚ وَلَهُمْ عَذَابٌ عَظ۪يمٌ', 'Kim iman ettikten sonra Allah´ı inkâr ederse -kalbi iman ile dolu olduğu halde (inkâra) zorlanan başka- fakat kim kalbini kâfirliğe açarsa, işte Allah´ın gazabı bunlaradır; onlar için büyük bir azap vardır.', 14, 278, 128, 7723, 'Bal arısı', 'Mekke', 70),
(2008, 107, 16, 'Nahl', 'ذٰلِكَ بِاَنَّهُمُ اسْتَحَبُّوا الْحَيٰوةَ الدُّنْيَا عَلَى الْاٰخِرَةِۙ وَاَنَّ اللّٰهَ لَا يَهْدِي الْقَوْمَ الْكَافِر۪ينَ', 'Bu (azap), onların dünya hayatını ahirete tercih etmelerinden ve Allah´ın kâfirler topluluğunu hidayete erdirmemesinden ötürüdür.', 14, 278, 128, 7723, 'Bal arısı', 'Mekke', 70),
(2009, 108, 16, 'Nahl', 'اُو۬لٰٓئِكَ الَّذ۪ينَ طَبَعَ اللّٰهُ عَلٰى قُلُوبِهِمْ وَسَمْعِهِمْ وَاَبْصَارِهِمْۚ وَاُو۬لٰٓئِكَ هُمُ الْغَافِلُونَ', 'İşte onlar Allah´ın, kalplerini, kulaklarını ve gözlerini mühürlediği kimselerdir. Ve onlar gafillerin kendileridir.', 14, 278, 128, 7723, 'Bal arısı', 'Mekke', 70),
(2010, 109, 16, 'Nahl', 'لَا جَرَمَ اَنَّهُمْ فِي الْاٰخِرَةِ هُمُ الْخَاسِرُونَ', 'Hiç şüphesiz onlar ahirette ziyana uğrayanların ta kendileridir.', 14, 278, 128, 7723, 'Bal arısı', 'Mekke', 70),
(2011, 110, 16, 'Nahl', 'ثُمَّ اِنَّ رَبَّكَ لِلَّذ۪ينَ هَاجَرُوا مِنْ بَعْدِ مَا فُتِنُوا ثُمَّ جَاهَدُوا وَصَبَرُٓواۙ اِنَّ رَبَّكَ مِنْ بَعْدِهَا لَغَفُورٌ رَح۪يمٌ۟', 'Sonra şüphesiz Rabbin, eziyet edildikten sonra hicret edip, ardından da sabrederek cihad edenlerin yardımcısıdır. Bütün bunlardan sonra Rabbin elbette çok bağışlayan, pek esirgeyendir.', 14, 278, 128, 7723, 'Bal arısı', 'Mekke', 70),
(2012, 111, 16, 'Nahl', 'يَوْمَ تَأْت۪ي كُلُّ نَفْسٍ تُجَادِلُ عَنْ نَفْسِهَا وَتُوَفّٰى كُلُّ نَفْسٍ مَا عَمِلَتْ وَهُمْ لَا يُظْلَمُونَ', 'O gün, herkes gelip kendi canını kurtarmak için uğraşır ve herkese yaptığının karşılığı eksiksiz ödenir, onlara asla zulmedilmez.', 14, 279, 128, 7723, 'Bal arısı', 'Mekke', 70),
(2013, 112, 16, 'Nahl', 'وَضَرَبَ اللّٰهُ مَثَلاً قَرْيَةً كَانَتْ اٰمِنَةً مُطْمَئِنَّةً يَأْت۪يهَا رِزْقُهَا رَغَداً مِنْ كُلِّ مَكَانٍ فَكَفَرَتْ بِاَنْعُمِ اللّٰهِ فَاَذَاقَهَا اللّٰهُ لِبَاسَ الْجُوعِ وَالْخَوْفِ بِمَا كَانُوا يَصْنَعُونَ', 'Allah, (ibret için) bir ülkeyi örnek verdi: Bu ülke güvenli, huzurlu idi; ona rızkı her yerden bol bol gelirdi. Sonra onlar Allah´ın nimetlerine karşı nankörlük ettiler. Allah da onlara, yaptıklarından ötürü açlık ve korku sıkıntısını tattırdı.', 14, 279, 128, 7723, 'Bal arısı', 'Mekke', 70),
(2014, 113, 16, 'Nahl', 'وَلَقَدْ جَٓاءَهُمْ رَسُولٌ مِنْهُمْ فَكَذَّبُوهُ فَاَخَذَهُمُ الْعَذَابُ وَهُمْ ظَالِمُونَ', 'Andolsun ki, onlara kendilerinden peygamber geldi de onu yalanladılar. Onlar zulmederlerken azap onları yakalayıverdi.', 14, 279, 128, 7723, 'Bal arısı', 'Mekke', 70),
(2015, 114, 16, 'Nahl', 'فَكُلُوا مِمَّا رَزَقَكُمُ اللّٰهُ حَلَالاً طَيِّباًۖ وَاشْكُرُوا نِعْمَتَ اللّٰهِ اِنْ كُنْتُمْ اِيَّاهُ تَعْبُدُونَ', 'Artık, Allah´ın size verdiği rızıktan helâl ve temiz olarak yeyin, eğer (gerçekten) yalnız Allah´a ibadet ediyorsanız, onun nimetine şükredin.', 14, 279, 128, 7723, 'Bal arısı', 'Mekke', 70),
(2016, 115, 16, 'Nahl', 'اِنَّمَا حَرَّمَ عَلَيْكُمُ الْمَيْتَةَ وَالدَّمَ وَلَحْمَ الْخِنْز۪يرِ وَمَٓا اُهِلَّ لِغَيْرِ اللّٰهِ بِه۪ۚ فَمَنِ اضْطُرَّ غَيْرَ بَاغٍ وَلَا عَادٍ فَاِنَّ اللّٰهَ غَفُورٌ رَح۪يمٌ', '(Allah) size, sadece ölü hayvanı, kanı, domuz etini ve Allah´tan başkası adına kesilen hayvanı haram kıldı. Ancak kim mecbur kalırsa (başkalarının haklarına) saldırmaksızın, sınırı da aşmadan (bunlardan yiyebilir). Çünkü Allah çok bağışlayan, pek esirgeyendir.', 14, 279, 128, 7723, 'Bal arısı', 'Mekke', 70),
(2017, 116, 16, 'Nahl', 'وَلَا تَقُولُوا لِمَا تَصِفُ اَلْسِنَتُكُمُ الْكَذِبَ هٰذَا حَلَالٌ وَهٰذَا حَرَامٌ لِتَفْتَرُوا عَلَى اللّٰهِ الْكَذِبَۜ اِنَّ الَّذ۪ينَ يَفْتَرُونَ عَلَى اللّٰهِ الْكَذِبَ لَا يُفْلِحُونَۜ', 'Dillerinizin uydurduğu yalana dayanarak «Bu helâldir, şu da haramdır» demeyin, çünkü Allah´a karşı yalan uydurmuş oluyorsunuz. Kuşkusuz Allah´a karşı yalan uyduranlar kurtuluşa eremezler.', 14, 279, 128, 7723, 'Bal arısı', 'Mekke', 70),
(2018, 117, 16, 'Nahl', 'مَتَاعٌ قَل۪يلٌۖ وَلَهُمْ عَذَابٌ اَل۪يمٌ', '(Kazandıkları) pek az bir menfaattir. Halbuki onlar için elem verici bir azap vardır.', 14, 279, 128, 7723, 'Bal arısı', 'Mekke', 70),
(2019, 118, 16, 'Nahl', 'وَعَلَى الَّذ۪ينَ هَادُوا حَرَّمْنَا مَا قَصَصْنَا عَلَيْكَ مِنْ قَبْلُۚ وَمَا ظَلَمْنَاهُمْ وَلٰكِنْ كَانُٓوا اَنْفُسَهُمْ يَظْلِمُونَ', 'Sana anlattıklarımızı, daha önce, yahudi olanlara da haram kılmıştık. Biz onlara zulmetmedik, fakat, onlar kendilerine haksızlık ediyorlardı.', 14, 279, 128, 7723, 'Bal arısı', 'Mekke', 70),
(2020, 119, 16, 'Nahl', 'ثُمَّ اِنَّ رَبَّكَ لِلَّذ۪ينَ عَمِلُوا السُّٓوءَ بِجَهَالَةٍ ثُمَّ تَابُوا مِنْ بَعْدِ ذٰلِكَ وَاَصْلَحُٓواۙ اِنَّ رَبَّكَ مِنْ بَعْدِهَا لَغَفُورٌ رَح۪يمٌ۟', 'Sonra şüphesiz Rabbin, cahillik sebebiyle kötülük yapan, sonra da bunun ardından tevbe edip durumunu düzeltenleri (bağışlayacaktır). Çünkü onlar tevbe ettikten sonra Rabbin elbet çok bağışlayan, pek esirgeyendir.', 14, 280, 128, 7723, 'Bal arısı', 'Mekke', 70),
(2021, 120, 16, 'Nahl', 'اِنَّ اِبْرٰه۪يمَ كَانَ اُمَّةً قَانِتاً لِلّٰهِ حَن۪يفاًۜ وَلَمْ يَكُ مِنَ الْمُشْرِك۪ينَۙ', 'İbrahim, gerçekten Hakk´a yönelen, Allah´a itaat eden bir önder idi; Allah´a ortak koşanlardan değildi.', 14, 280, 128, 7723, 'Bal arısı', 'Mekke', 70),
(2022, 121, 16, 'Nahl', 'شَاكِراً لِاَنْعُمِهِۜ اِجْتَبٰيهُ وَهَدٰيهُ اِلٰى صِرَاطٍ مُسْتَق۪يمٍ', 'Allah´ın nimetlerine şükrediciydi. Çünkü Allah, onu seçmiş ve doğru yola iletmişti.', 14, 280, 128, 7723, 'Bal arısı', 'Mekke', 70),
(2023, 122, 16, 'Nahl', 'وَاٰتَيْنَاهُ فِي الدُّنْيَا حَسَنَةًۜ وَاِنَّهُ فِي الْاٰخِرَةِ لَمِنَ الصَّالِح۪ينَۜ', 'Ona dünyada güzellik verdik. Muhakkak ki o, ahirette de sâlihlerdendir.', 14, 280, 128, 7723, 'Bal arısı', 'Mekke', 70),
(2024, 123, 16, 'Nahl', 'ثُمَّ اَوْحَيْنَٓا اِلَيْكَ اَنِ اتَّبِـعْ مِلَّةَ اِبْرٰه۪يمَ حَن۪يفاًۜ وَمَا كَانَ مِنَ الْمُشْرِك۪ينَ', 'Sonra da sana: «Doğru yola yönelerek İbrahim´in dinine uy! O müşriklerden değildi» diye vahyettik.', 14, 280, 128, 7723, 'Bal arısı', 'Mekke', 70),
(2025, 124, 16, 'Nahl', 'اِنَّمَا جُعِلَ السَّبْتُ عَلَى الَّذ۪ينَ اخْتَلَفُوا ف۪يهِۜ وَاِنَّ رَبَّكَ لَيَحْكُمُ بَيْنَهُمْ يَوْمَ الْقِيٰمَةِ ف۪يمَا كَانُوا ف۪يهِ يَخْتَلِفُونَ', 'Cumartesi tatili, ancak onda ihtilaf edenlere (farz) kılınmıştı. Kıyamet günü Rabbin, muhakkak onların ihtilafa düştükleri şey hakkında aralarında hüküm verecektir.', 14, 280, 128, 7723, 'Bal arısı', 'Mekke', 70),
(2026, 125, 16, 'Nahl', 'اُدْعُ اِلٰى سَب۪يلِ رَبِّكَ بِالْحِكْمَةِ وَالْمَوْعِظَةِ الْحَسَنَةِ وَجَادِلْهُمْ بِالَّت۪ي هِيَ اَحْسَنُۜ اِنَّ رَبَّكَ هُوَ اَعْلَمُ بِمَنْ ضَلَّ عَنْ سَب۪يلِه۪ وَهُوَ اَعْلَمُ بِالْمُهْتَد۪ينَ', '(Resûlüm!) Sen, Rabbinin yoluna hikmet ve güzel öğütle çağır ve onlarla en güzel şekilde mücadele et! Rabbin, kendi yolundan sapanları en iyi bilendir ve O, hidayete erenleri de çok iyi bilir.', 14, 280, 128, 7723, 'Bal arısı', 'Mekke', 70),
(2027, 126, 16, 'Nahl', 'وَاِنْ عَاقَبْتُمْ فَعَاقِبُوا بِمِثْلِ مَا عُوقِبْتُمْ بِه۪ۜ وَلَئِنْ صَبَرْتُمْ لَهُوَ خَيْرٌ لِلصَّابِر۪ينَ', 'Eğer ceza verecekseniz, size yapılan işkencenin misliyle ceza verin. Ama sabrederseniz, elbette o, sabredenler için daha hayırlıdır.', 14, 280, 128, 7723, 'Bal arısı', 'Mekke', 70),
(2028, 127, 16, 'Nahl', 'وَاصْبِرْ وَمَا صَبْرُكَ اِلَّا بِاللّٰهِ وَلَا تَحْزَنْ عَلَيْهِمْ وَلَا تَكُ ف۪ي ضَيْقٍ مِمَّا يَمْكُرُونَ', 'Sabret! Senin sabrın da ancak Allah´ın yardımı iledir. Onlardan dolayı kederlenme; kurmakta oldukları tuzaktan kaygı duyma!', 14, 280, 128, 7723, 'Bal arısı', 'Mekke', 70),
(2029, 128, 16, 'Nahl', 'اِنَّ اللّٰهَ مَعَ الَّذ۪ينَ اتَّقَوْا وَالَّذ۪ينَ هُمْ مُحْسِنُونَ', 'Çünkü Allah, (kötülükten) sakınanlar ve güzel amel edenlerle beraberdir.', 14, 280, 128, 7723, 'Bal arısı', 'Mekke', 70),
(2030, 1, 17, 'İsra', 'سُبْحَانَ الَّـذ۪ٓي اَسْرٰى بِعَبْدِه۪ لَيْلاً مِنَ الْمَسْجِدِ الْحَرَامِ اِلَى الْمَسْجِدِ الْاَقْصَا الَّذ۪ي بَارَكْنَا حَوْلَهُ لِنُرِيَهُ مِنْ اٰيَاتِنَاۜ اِنَّهُ هُوَ السَّم۪يعُ الْبَص۪يرُ', 'Bir gece, kendisine âyetlerimizden bir kısmını gösterelim diye (Muhammed) kulunu Mescid-i Harâm´dan, çevresini mübarek kıldığımız Mescid-i Aksâ´ya götüren Allah noksan sıfatlardan münezzehtir; O, gerçekten işitendir, görendir.', 15, 281, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2031, 2, 17, 'İsra', 'وَاٰتَيْنَا مُوسَى الْكِتَابَ وَجَعَلْنَاهُ هُدًى لِبَن۪ٓي اِسْرَٓائ۪لَ اَلَّا تَتَّخِذُوا مِنْ دُون۪ي وَك۪يلاًۜ', 'Biz, Musa´ya Kitab´ı verdik ve İsrailoğullarına: «Benden başkasını dayanılıp güvenilen bir rab edinmeyin» diyerek bu Kitab´ı bir hidayet rehberi kıldık.', 15, 281, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2032, 3, 17, 'İsra', 'ذُرِّيَّةَ مَنْ حَمَلْنَا مَعَ نُوحٍۜ اِنَّهُ كَانَ عَبْداً شَكُوراً', '(Ey) Nuh ile birlikte (gemide) taşıdığımız kimselerin nesli! Şunu bilin ki Nuh, çok şükreden bir kul idi.', 15, 281, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2033, 4, 17, 'İsra', 'وَقَضَيْنَٓا اِلٰى بَن۪ٓي اِسْرَٓائ۪لَ فِي الْكِتَابِ لَتُفْسِدُنَّ فِي الْاَرْضِ مَرَّتَيْنِ وَلَتَعْـلُنَّ عُـلُواًّ كَب۪يراً', 'Biz, Kitap´ta İsrailoğullarına: Sizler, yeryüzünde iki defa fesat çıkaracaksınız ve azgınlık derecesinde bir kibre kapılacaksınız, diye bildirdik.', 15, 281, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2034, 5, 17, 'İsra', 'فَاِذَا جَٓاءَ وَعْدُ اُو۫لٰيهُمَا بَعَثْنَا عَلَيْكُمْ عِبَاداً لَنَٓا اُو۬ل۪ي بَأْسٍ شَد۪يدٍ فَجَاسُوا خِلَالَ الدِّيَارِۜ وَكَانَ وَعْداً مَفْعُولاً', 'Bunlardan ilkinin zamanı gelince, üzerinize güçlü kuvvetli kullarımızı gönderdik. Bunlar, evlerin arasında dolaşarak (sizi) aradılar. Bu, yerine getirilmiş bir vaad idi.', 15, 281, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2035, 6, 17, 'İsra', 'ثُمَّ رَدَدْنَا لَكُمُ الْكَرَّةَ عَلَيْهِمْ وَاَمْدَدْنَاكُمْ بِاَمْوَالٍ وَبَن۪ينَ وَجَعَلْنَاكُمْ اَكْثَرَ نَف۪يراً', 'Sonra onlara karşı size tekrar (galibiyet ve zafer) verdik; servet ve oğullarla gücünüzü arttırdık; sayınızı daha da çoğalttık.', 15, 281, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2036, 7, 17, 'İsra', 'اِنْ اَحْسَنْتُمْ اَحْسَنْتُمْ لِاَنْفُسِكُمْ وَاِنْ اَسَأْتُمْ فَلَهَاۜ فَاِذَا جَٓاءَ وَعْدُ الْاٰخِرَةِ لِيَسُٓؤُ۫ا وُجُوهَكُمْ وَلِيَدْخُلُوا الْمَسْجِدَ كَمَا دَخَلُوهُ اَوَّلَ مَرَّةٍ وَلِيُتَبِّرُوا مَا عَلَوْا تَتْب۪يراً', 'Eğer iyilik ederseniz kendinize etmiş, kötülük ederseniz yine kendinize etmiş olursunuz. Artık diğer cezalandırma zamanı gelince, yüzünüzü kara etsinler, daha önce girdikleri gibi yine Mescid´e (Süleyman Mâbedi´ne) girsinler ve ellerine geçirdikleri her şeyi büsbütün tahrip etsinler (diye, başınıza yine düşmanlarınızı musallat kıldık).', 15, 281, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2037, 8, 17, 'İsra', 'عَسٰى رَبُّكُمْ اَنْ يَرْحَمَكُمْۚ وَاِنْ عُدْتُمْ عُدْنَاۢ وَجَعَلْنَا جَهَنَّمَ لِلْكَافِر۪ينَ حَص۪يراً', 'Belki Rabbiniz size merhamet eder; fakat siz eğer yine (fesatçılığa) dönerseniz, biz de sizi yine cezalandırırız. Biz cehennemi kâfirler için bir hapishane yaptık.', 15, 282, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2038, 9, 17, 'İsra', 'اِنَّ هٰذَا الْقُرْاٰنَ يَهْد۪ي لِلَّت۪ي هِيَ اَقْوَمُ وَيُبَشِّرُ الْمُؤْمِن۪ينَ الَّذ۪ينَ يَعْمَلُونَ الصَّالِحَاتِ اَنَّ لَهُمْ اَجْراً كَب۪يراًۙ', 'Şüphesiz ki bu Kur´an en doğru yola iletir; iyi davranışlarda bulunan müminlere, kendileri için büyük bir mükâfat olduğunu müjdeler.', 15, 282, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2039, 10, 17, 'İsra', 'وَاَنَّ الَّذ۪ينَ لَا يُؤْمِنُونَ بِالْاٰخِرَةِ اَعْتَدْنَا لَهُمْ عَذَاباً اَل۪يماً۟', 'Ahirete inanmayanlara gelince, onlar için de elemli bir azap hazırlamışızdır.', 15, 282, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2040, 11, 17, 'İsra', 'وَيَدْعُ الْاِنْسَانُ بِالشَّرِّ دُعَٓاءَهُ بِالْخَيْرِۜ وَكَانَ الْاِنْسَانُ عَجُولاً', 'İnsan hayrı istediği kadar şerri de ister. İnsan pek acelecidir!', 15, 282, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2041, 12, 17, 'İsra', 'وَجَعَلْنَا الَّيْلَ وَالنَّهَارَ اٰيَتَيْنِ فَمَحَوْنَٓا اٰيَةَ الَّيْلِ وَجَعَلْـنَٓا اٰيَةَ النَّهَارِ مُبْصِرَةً لِتَبْتَغُوا فَضْلاً مِنْ رَبِّكُمْ وَلِتَعْلَمُوا عَدَدَ السِّن۪ينَ وَالْحِسَابَۜ وَكُلَّ شَيْءٍ فَصَّلْنَاهُ تَفْص۪يلاً', 'Biz, geceyi ve gündüzü birer âyet (delil) olarak yarattık. Nitekim, Rabbinizin nimetlerini araştırmanız, ayrıca, yılların sayısını ve hesabı bilmeniz için gecenin karanlığını silip (yerine, eşyayı) aydınlatan gündüzün aydınlığını getirdik. İşte biz, her şeyi açık açık anlattık.', 15, 282, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2042, 13, 17, 'İsra', 'وَكُلَّ اِنْسَانٍ اَلْزَمْنَاهُ طَٓائِرَهُ ف۪ي عُنُقِه۪ۜ وَنُخْرِجُ لَهُ يَوْمَ الْقِيٰمَةِ كِتَاباً يَلْقٰيهُ مَنْشُوراً', 'Her insanın amelini (veya kaderini) boynuna bağladık. İnsan için kıyamet gününde, açılmış olarak önüne konacak bir kitap çıkarırız.', 15, 282, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2043, 14, 17, 'İsra', 'اِقْرَأْ كِتَابَكَۜ كَفٰى بِنَفْسِكَ الْيَوْمَ عَلَيْكَ حَس۪يباًۜ', 'Kitabını oku! Bugün sana hesap sorucu olarak kendi nefsin yeter.', 15, 282, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2044, 15, 17, 'İsra', 'مَنِ اهْتَدٰى فَاِنَّمَا يَهْتَد۪ي لِنَفْسِه۪ۚ وَمَنْ ضَلَّ فَاِنَّمَا يَضِلُّ عَلَيْهَاۜ وَلَا تَزِرُ وَازِرَةٌ وِزْرَ اُخْرٰىۜ وَمَا كُنَّا مُعَذِّب۪ينَ حَتّٰى نَبْعَثَ رَسُولاً', 'Kim hidayet yolunu seçerse, bunu ancak kendi iyiliği için seçmiş olur; kim de doğruluktan saparsa, kendi zararına sapmış olur. Hiçbir günahkâr, başkasının günah yükünü üstlenmez. Biz, bir peygamber göndermedikçe (kimseye) azap edecek değiliz.', 15, 282, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2045, 16, 17, 'İsra', 'وَاِذَٓا اَرَدْنَٓا اَنْ نُهْلِكَ قَرْيَةً اَمَرْنَا مُتْرَف۪يهَا فَفَسَقُوا ف۪يهَا فَحَقَّ عَلَيْهَا الْقَوْلُ فَدَمَّرْنَاهَا تَدْم۪يراً', 'Bir ülkeyi helâk etmek istediğimizde, o ülkenin zenginlik sebebiyle şımarmış elebaşılarına (iyilikleri) emrederiz; buna rağmen onlar orada kötülük işlerler. Böylece o ülke, helâke müstahak olur; biz de orayı darmadağın ederiz.', 15, 282, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2046, 17, 17, 'İsra', 'وَكَمْ اَهْلَكْنَا مِنَ الْقُرُونِ مِنْ بَعْدِ نُوحٍۜ وَكَفٰى بِرَبِّكَ بِذُنُوبِ عِبَادِه۪ خَب۪يراً بَص۪يراً', 'Nuh´tan sonraki nesillerden nicelerini helâk ettik. Kullarının günahlarını bilen ve gören olarak Rabbin yeterlidir.', 15, 282, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2047, 18, 17, 'İsra', 'مَنْ كَانَ يُر۪يدُ الْعَاجِلَةَ عَجَّلْنَا لَهُ ف۪يهَا مَا نَشَٓاءُ لِمَنْ نُر۪يدُ ثُمَّ جَعَلْنَا لَهُ جَهَنَّمَۚ يَصْلٰيهَا مَذْمُوماً مَدْحُوراً', 'Her kim bu çarçabuk geçen dünyayı dilerse ona, yani dilediğimiz kimseye dilediğimiz kadarını dünyada hemen verir, sonra da onu, kınanmış ve kovulmuş olarak gireceği cehenneme sokarız.', 15, 283, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2048, 19, 17, 'İsra', 'وَمَنْ اَرَادَ الْاٰخِرَةَ وَسَعٰى لَهَا سَعْيَهَا وَهُوَ مُؤْمِنٌ فَاُو۬لٰٓئِكَ كَانَ سَعْيُهُمْ مَشْكُوراً', 'Kim de ahireti diler ve bir mümin olarak ona yaraşır bir çaba ile çalışırsa, işte bunların çalışmaları makbuldür.', 15, 283, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2049, 20, 17, 'İsra', 'كُلاًّ نُمِدُّ هٰٓؤُ۬لَٓاءِ وَهٰٓؤُ۬لَٓاءِ مِنْ عَطَٓاءِ رَبِّكَۜ وَمَا كَانَ عَطَٓاءُ رَبِّكَ مَحْظُوراً', 'Hepsine, onlara da bunlara da (dünyayı isteyenlere de ahireti isteyenlere de) Rabbinin ihsanından (istediklerini) veririz. Rabbinin ihsanı kısıtlanmış değildir.', 15, 283, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2050, 21, 17, 'İsra', 'اُنْظُرْ كَيْفَ فَضَّلْنَا بَعْضَهُمْ عَلٰى بَعْضٍۜ وَلَلْاٰخِرَةُ اَكْبَرُ دَرَجَاتٍ وَاَكْبَرُ تَفْض۪يلاً', 'Baksana, biz insanların kimini kiminden nasıl üstün kılmışızdır! Elbette ki ahiret, derece ve üstünlük farkları bakımından daha büyüktür.', 15, 283, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2051, 22, 17, 'İsra', 'لَا تَجْعَلْ مَعَ اللّٰهِ اِلٰهاً اٰخَرَ فَتَقْعُدَ مَذْمُوماً مَخْذُولاً۟', 'Allah ile birlikte bir ilâh daha tanıma! Sonra kınanmış ve kendi başına terkedilmiş olarak kalırsın.', 15, 283, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2052, 23, 17, 'İsra', 'وَقَضٰى رَبُّكَ اَلَّا تَعْبُدُٓوا اِلَّٓا اِيَّاهُ وَبِالْوَالِدَيْنِ اِحْسَاناًۜ اِمَّا يَبْلُغَنَّ عِنْدَكَ الْكِبَرَ اَحَدُهُمَٓا اَوْ كِلَاهُمَا فَلَا تَقُلْ لَهُمَٓا اُفٍّ وَلَا تَنْهَرْهُمَا وَقُلْ لَهُمَا قَوْلاً كَر۪يماً', 'Rabbin, sadece kendisine kulluk etmenizi, ana babanıza da iyi davranmanızı kesin bir şekilde emretti. Onlardan biri veya her ikisi senin yanında yaşlanırsa, kendilerine «of!» bile deme; onları azarlama; ikisine de güzel söz söyle.', 15, 283, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2053, 24, 17, 'İsra', 'وَاخْفِضْ لَهُمَا جَنَاحَ الذُّلِّ مِنَ الرَّحْمَةِ وَقُلْ رَبِّ ارْحَمْهُمَا كَمَا رَبَّيَان۪ي صَغ۪يراًۜ', 'Onları esirgeyerek alçakgönüllülükle üzerlerine kanat ger ve: «Rabbim! Küçüklüğümde onlar beni nasıl yetiştirmişlerse, şimdi de sen onlara (öyle) rahmet et!» diyerek dua et.', 15, 283, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2054, 25, 17, 'İsra', 'رَبُّكُمْ اَعْلَمُ بِمَا ف۪ي نُفُوسِكُمْۜ اِنْ تَكُونُوا صَالِح۪ينَ فَاِنَّهُ كَانَ لِلْاَوَّاب۪ينَ غَفُوراً', 'Rabbiniz sizin kalplerinizdekini çok iyi bilir. Eğer siz iyi olursanız, şunu bilin ki Allah, kötülükten yüz çevirerek tevbeye yönelenleri son derece bağışlayıcıdır.', 15, 283, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2055, 26, 17, 'İsra', 'وَاٰتِ ذَا الْقُرْبٰى حَقَّهُ وَالْمِسْك۪ينَ وَابْنَ السَّب۪يلِ وَلَا تُبَذِّرْ تَبْذ۪يراً', 'Bir de akrabaya, yoksula, yolcuya hakkını ver. Gereksiz yere de saçıp savurma.', 15, 283, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2056, 27, 17, 'İsra', 'اِنَّ الْمُبَذِّر۪ينَ كَانُٓوا اِخْوَانَ الشَّيَاط۪ينِۜ وَكَانَ الشَّيْطَانُ لِرَبِّه۪ كَفُوراً', 'Zira böylesine saçıp savuranlar şeytanların dostlarıdırlar. Şeytan ise Rabbine karşı çok nankördür.', 15, 283, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2057, 28, 17, 'İsra', 'وَاِمَّا تُعْرِضَنَّ عَنْهُمُ ابْتِغَٓاءَ رَحْمَةٍ مِنْ رَبِّكَ تَرْجُوهَا فَقُلْ لَهُمْ قَوْلاً مَيْسُوراً', 'Eğer Rabbinden umduğun (beklemek durumunda olduğun) bir rahmet için onların yüzlerine bakamıyorsan, hiç olmazsa kendilerine gönül alıcı bir söz söyle.', 15, 284, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2058, 29, 17, 'İsra', 'وَلَا تَجْعَلْ يَدَكَ مَغْلُولَةً اِلٰى عُنُقِكَ وَلَا تَبْسُطْهَا كُلَّ الْبَسْطِ فَتَقْعُدَ مَلُوماً مَحْسُوراً', 'Eli sıkı olma; büsbütün eli açık da olma. Sonra kınanır, (kaybettiklerinin) hasretini çeker durursun.', 15, 284, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2059, 30, 17, 'İsra', 'اِنَّ رَبَّكَ يَبْسُطُ الرِّزْقَ لِمَنْ يَشَٓاءُ وَيَقْدِرُۜ اِنَّهُ كَانَ بِعِبَادِه۪ خَب۪يراً بَص۪يراً۟', 'Rabbin rızkı dilediğine bol verir, dilediğine daraltır. Şüphesiz ki O, kullarından haberdardır, (onları) çok iyi görür.', 15, 284, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2060, 31, 17, 'İsra', 'وَلَا تَقْتُلُٓوا اَوْلَادَكُمْ خَشْيَةَ اِمْلَاقٍۜ نَحْنُ نَرْزُقُهُمْ وَاِيَّاكُمْۜ اِنَّ قَتْلَهُمْ كَانَ خِطْـٔاً كَب۪يراً', 'Geçim endişesi ile çocuklarınızın canına kıymayın. Biz, onların da sizin de rızkınızı veririz. Onları öldürmek gerçekten büyük bir suçtur.', 15, 284, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2061, 32, 17, 'İsra', 'وَلَا تَقْرَبُوا الزِّنٰٓى اِنَّهُ كَانَ فَاحِشَةًۜ وَسَٓاءَ سَب۪يلاً', 'Zinaya yaklaşmayın. Zira o, bir hayâsızlıktır ve çok kötü bir yoldur.', 15, 284, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2062, 33, 17, 'İsra', 'وَلَا تَقْتُلُوا النَّفْسَ الَّت۪ي حَرَّمَ اللّٰهُ اِلَّا بِالْحَقِّۜ وَمَنْ قُتِلَ مَظْلُوماً فَقَدْ جَعَلْنَا لِوَلِيِّه۪ سُلْطَاناً فَلَا يُسْرِفْ فِي الْقَتْلِۜ اِنَّهُ كَانَ مَنْصُوراً', 'Haklı bir sebep olmadıkça Allah´ın muhterem kıldığı cana kıymayın. Bir kimse zulmen öldürülürse, onun velîsine (hakkını alması için) yetki verdik. Ancak bu velî de kısasta ileri gitmesin. Zaten (kendisine bu yetki verilmekle) o, alacağını almıştır.', 15, 284, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2063, 34, 17, 'İsra', 'وَلَا تَقْرَبُوا مَالَ الْيَت۪يمِ اِلَّا بِالَّت۪ي هِيَ اَحْسَنُ حَتّٰى يَبْلُغَ اَشُدَّهُۖ وَاَوْفُوا بِالْعَهْدِۚ اِنَّ الْعَهْدَ كَانَ مَسْؤُ۫لاً', 'Yetimin malına, rüşdüne erinceye kadar, ancak en güzel bir niyetle yaklaşın. Verdiğiniz sözü de yerine getirin. Çünkü verilen söz, sorumluluğu gerektirir.', 15, 284, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2064, 35, 17, 'İsra', 'وَاَوْفُوا الْكَيْلَ اِذَا كِلْتُمْ وَزِنُوا بِالْقِسْطَاسِ الْمُسْتَق۪يمِۜ ذٰلِكَ خَيْرٌ وَاَحْسَنُ تَأْو۪يلاً', 'Ölçtüğünüz zaman tastamam ölçün ve doğru terazi ile tartın. Bu, hem daha iyidir hem de neticesi bakımından daha güzeldir.', 15, 284, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2065, 36, 17, 'İsra', 'وَلَا تَقْفُ مَا لَيْسَ لَكَ بِه۪ عِلْمٌۜ اِنَّ السَّمْعَ وَالْبَصَرَ وَالْفُؤٰادَ كُلُّ اُو۬لٰٓئِكَ كَانَ عَنْهُ مَسْؤُ۫لاً', 'Hakkında bilgin bulunmayan şeyin ardına düşme. Çünkü kulak, göz ve gönül, bunların hepsi ondan sorumludur.', 15, 284, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2066, 37, 17, 'İsra', 'وَلَا تَمْشِ فِي الْاَرْضِ مَرَحاًۚ اِنَّكَ لَنْ تَخْرِقَ الْاَرْضَ وَلَنْ تَبْلُغَ الْجِبَالَ طُولاً', 'Yeryüzünde böbürlenerek dolaşma. Çünkü sen (ağırlık ve azametinle) ne yeri yarabilir ne de dağlarla ululuk yarışına girebilirsin.', 15, 284, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2067, 38, 17, 'İsra', 'كُلُّ ذٰلِكَ كَانَ سَيِّئُهُ عِنْدَ رَبِّكَ مَكْرُوهاً', 'Bütün bu sayılanların kötü olanları, Rabbinin nezdinde sevimsizdir.', 15, 284, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2068, 39, 17, 'İsra', 'ذٰلِكَ مِمَّٓا اَوْحٰٓى اِلَيْكَ رَبُّكَ مِنَ الْحِكْمَةِۜ وَلَا تَجْعَلْ مَعَ اللّٰهِ اِلٰهاً اٰخَرَ فَتُلْقٰى ف۪ي جَهَنَّمَ مَلُوماً مَدْحُوراً', 'İşte bunlar, Rabbinin sana vahyettiği hikmetlerdir. Allah ile birlikte başka ilâh edinme; sonra kınanmış ve (Allah´ın rahmetinden) uzaklaştırılmış olarak cehenneme atılırsın.', 15, 285, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2069, 40, 17, 'İsra', 'اَفَاَصْفٰيكُمْ رَبُّكُمْ بِالْبَن۪ينَ وَاتَّخَذَ مِنَ الْمَلٰٓئِكَةِ اِنَاثاًۜ اِنَّكُمْ لَتَقُولُونَ قَوْلاً عَظ۪يماً۟', '(Ey müşrikler!) Rabbiniz, erkek çocukları sizin için ayırdı da, kendisi meleklerden kız çocuklar mı edindi! Gerçekten siz, (vebali) çok büyük bir söz söylüyorsunuz.', 15, 285, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2070, 41, 17, 'İsra', 'وَلَقَدْ صَرَّفْنَا ف۪ي هٰذَا الْقُرْاٰنِ لِيَذَّكَّرُواۜ وَمَا يَز۪يدُهُمْ اِلَّا نُفُوراً', 'Biz, onların akıllarını başlarına toplamaları için bu Kur´an´da (çeşitli ikaz ve ihtarları) türlü şekillerde tekrar ettik. Fakat bu, onlara, daha da kaçıp uzaklaşmaktan başka bir şey sağlamıyor.', 15, 285, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2071, 42, 17, 'İsra', 'قُلْ لَوْ كَانَ مَعَهُٓ اٰلِهَةٌ كَمَا يَقُولُونَ اِذاً لَابْتَغَوْا اِلٰى ذِي الْعَرْشِ سَب۪يلاً', 'De ki: Eğer söyledikleri gibi Allah ile birlikte başka ilâhlar da bulunsaydı, o takdirde bu ilâhlar, Arş´ın sahibi olan Allah´a ulaşmak için çareler arayacaklardı.', 15, 285, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2072, 43, 17, 'İsra', 'سُبْحَانَهُ وَتَعَالٰى عَمَّا يَقُولُونَ عُلُواًّ كَب۪يراً', 'Allah, onların söyledikleri şeylerden münezzehtir; son derece yücedir ve uludur.', 15, 285, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2073, 44, 17, 'İsra', 'تُسَبِّحُ لَهُ السَّمٰوَاتُ السَّبْعُ وَالْاَرْضُ وَمَنْ ف۪يهِنَّۜ وَاِنْ مِنْ شَيْءٍ اِلَّا يُسَبِّحُ بِحَمْدِه۪ وَلٰكِنْ لَا تَفْقَهُونَ تَسْب۪يحَهُمْۜ اِنَّهُ كَانَ حَل۪يماً غَفُوراً', 'Yedi gök, yer ve bunlarda bulunan herkes O´nu tesbih eder. O´nu övgü ile tesbih etmeyen hiçbir şey yoktur. Ne var ki siz, onların tesbihini anlamazsınız. O, halîmdir, bağışlayıcıdır.', 15, 285, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2074, 45, 17, 'İsra', 'وَاِذَا قَرَأْتَ الْقُرْاٰنَ جَعَلْنَا بَيْنَكَ وَبَيْنَ الَّذ۪ينَ لَا يُؤْمِنُونَ بِالْاٰخِرَةِ حِجَاباً مَسْتُوراًۙ', 'Biz, Kur´an okuduğun zaman, seninle ahirete inanmayanların arasına gizleyici bir örtü çekeriz.', 15, 285, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2075, 46, 17, 'İsra', 'وَجَعَلْنَا عَلٰى قُلُوبِهِمْ اَكِنَّةً اَنْ يَفْقَهُوهُ وَف۪ٓي اٰذَانِهِمْ وَقْراًۜ وَاِذَا ذَكَرْتَ رَبَّكَ فِي الْقُرْاٰنِ وَحْدَهُ وَلَّوْا عَلٰٓى اَدْبَارِهِمْ نُفُوراً', 'Ayrıca, onu anlamamaları için kalplerine bir kapalılık ve kulaklarına bir ağırlık veririz. Sen, Kur´an´da Rabbinin birliğini yâdettiğinde onlar, canları sıkılmış bir vaziyette, gerisin geri dönüp giderler.', 15, 285, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2076, 47, 17, 'İsra', 'نَحْنُ اَعْلَمُ بِمَا يَسْتَمِعُونَ بِه۪ٓ اِذْ يَسْتَمِعُونَ اِلَيْكَ وَاِذْ هُمْ نَجْوٰٓى اِذْ يَقُولُ الظَّالِمُونَ اِنْ تَتَّبِعُونَ اِلَّا رَجُلاً مَسْحُوراً', 'Biz, onların seni dinlerken ne maksatla dinlediklerini, kendi aralarında fısıldaşırlarken de o zalimlerin: «Siz, büyülenmiş bir adamdan başkasına uymuyorsunuz!» dediklerini çok iyi biliriz.', 15, 285, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2077, 48, 17, 'İsra', 'اُنْظُرْ كَيْفَ ضَرَبُوا لَكَ الْاَمْثَالَ فَضَلُّوا فَلَا يَسْتَط۪يعُونَ سَب۪يلاً', 'Baksana; senin için ne türlü benzetmeler yaptılar! Bu yüzden, (öyle bir) saptılar ki, artık (doğru) yolu bulamayacaklardır.', 15, 285, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2078, 49, 17, 'İsra', 'وَقَالُٓوا ءَاِذَا كُنَّا عِظَاماً وَرُفَاتاً ءَاِنَّا لَمَبْعُوثُونَ خَلْقاً جَد۪يداً', 'Bir de onlar dediler ki: Sahi biz, bir kemik yığını ve kokuşmuş bir toprak olmuş iken, yepyeni bir hilkatte diriltileceğiz, öyle mi!', 15, 285, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2079, 50, 17, 'İsra', 'قُلْ كُونُوا حِجَارَةً اَوْ حَد۪يداًۙ', '(50-51) De ki: İster taş olun, ister demir, isterse gözünüzde büyüyen herhangi bir mahlûk! (Bunlar, Allah´ın sizi yeniden diriltmesini güçleştirmez.) Diyecekler ki: «Bizi tekrar (hayata) kim döndürecek?» De ki: Sizi ilk kez yaratan. Bunun üzerine onlar sana alaylı bir tarzda başlarını sallayacak ve «Ne zamanmış o?» diyecekler. De ki: Yakın olsa gerek!', 15, 286, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2080, 51, 17, 'İsra', 'اَوْ خَلْقاً مِمَّا يَكْبُرُ ف۪ي صُدُورِكُمْۚ فَسَيَقُولُونَ مَنْ يُع۪يدُنَاۜ قُلِ الَّذ۪ي فَطَرَكُمْ اَوَّلَ مَرَّةٍۚ فَسَيُنْغِضُونَ اِلَيْكَ رُؤُ۫سَهُمْ وَيَقُولُونَ مَتٰى هُوَۜ قُلْ عَسٰٓى اَنْ يَكُونَ قَر۪يباً', '(50-51) De ki: İster taş olun, ister demir, isterse gözünüzde büyüyen herhangi bir mahlûk! (Bunlar, Allah´ın sizi yeniden diriltmesini güçleştirmez.) Diyecekler ki: «Bizi tekrar (hayata) kim döndürecek?» De ki: Sizi ilk kez yaratan. Bunun üzerine onlar sana alaylı bir tarzda başlarını sallayacak ve «Ne zamanmış o?» diyecekler. De ki: Yakın olsa gerek!', 15, 286, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2081, 52, 17, 'İsra', 'يَوْمَ يَدْعُوكُمْ فَتَسْتَج۪يبُونَ بِحَمْدِه۪ وَتَظُنُّونَ اِنْ لَبِثْتُمْ اِلَّا قَل۪يلاً۟', 'Allah sizi çağıracağı gün, kendisine hamdederek çağrısına uyarsınız ve (dirilmeden önceki halinizde) çok az kaldığınızı sanırsınız.', 15, 286, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2082, 53, 17, 'İsra', 'وَقُلْ لِعِبَاد۪ي يَقُولُوا الَّت۪ي هِيَ اَحْسَنُۜ اِنَّ الشَّيْطَانَ يَنْزَغُ بَيْنَهُمْۜ اِنَّ الشَّيْطَانَ كَانَ لِلْاِنْسَانِ عَدُواًّ مُب۪يناً', 'Kullarıma söyle, sözün en güzelini söylesinler. Sonra şeytan aralarını bozar. Çünkü şeytan, insanın apaçık düşmanıdır.', 15, 286, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2083, 54, 17, 'İsra', 'رَبُّكُمْ اَعْلَمُ بِكُمْۜ اِنْ يَشَأْ يَرْحَمْكُمْ اَوْ اِنْ يَشَأْ يُعَذِّبْكُمْۜ وَمَٓا اَرْسَلْنَاكَ عَلَيْهِمْ وَك۪يلاً', 'Rabbiniz, sizi en iyi bilendir. Dilerse size merhamet eder; dilerse sizi cezalandırır. Biz, seni onların üstüne bir vekil olarak göndermedik.', 15, 286, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2084, 55, 17, 'İsra', 'وَرَبُّكَ اَعْلَمُ بِمَنْ فِي السَّمٰوَاتِ وَالْاَرْضِۜ وَلَقَدْ فَضَّلْنَا بَعْضَ النَّبِيّ۪نَ عَلٰى بَعْضٍ وَاٰتَيْنَا دَاوُ۫دَ زَبُوراً', 'Rabbin, göklerde ve yerde olan herkesi en iyi bilendir. Gerçekten biz, peygamberlerin kimini kiminden üstün kıldık; Davud´a da Zebur´u verdik.', 15, 286, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2085, 56, 17, 'İsra', 'قُلِ ادْعُوا الَّذ۪ينَ زَعَمْتُمْ مِنْ دُونِه۪ فَلَا يَمْلِكُونَ كَشْفَ الضُّرِّ عَنْكُمْ وَلَا تَحْو۪يلاً', '(Resûlüm!) De ki: Allah´ı bırakıp da (ilâh olduğunu) ileri sürdüklerinize yalvarın. Ne var ki onlar, sizin sıkıntınızı ne uzaklaştırabilir, ne de değiştirebilirler.»', 15, 286, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2086, 57, 17, 'İsra', 'اُو۬لٰٓئِكَ الَّذ۪ينَ يَدْعُونَ يَبْتَغُونَ اِلٰى رَبِّهِمُ الْوَس۪يلَةَ اَيُّهُمْ اَقْرَبُ وَيَرْجُونَ رَحْمَتَهُ وَيَخَافُونَ عَذَابَهُۜ اِنَّ عَذَابَ رَبِّكَ كَانَ مَحْذُوراً', 'Onların yalvardıkları bu varlıklar Rablerine -hangisi daha yakın olacak diye- vesile ararlar; O´nun rahmetini umarlar ve azabından korkarlar. Çünkü Rabbinin azabı, sakınılacak bir azaptır.', 15, 286, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2087, 58, 17, 'İsra', 'وَاِنْ مِنْ قَرْيَةٍ اِلَّا نَحْنُ مُهْلِكُوهَا قَبْلَ يَوْمِ الْقِيٰمَةِ اَوْ مُعَذِّبُوهَا عَذَاباً شَد۪يداًۜ كَانَ ذٰلِكَ فِي الْكِتَابِ مَسْطُوراً', 'Ne kadar ülke varsa hepsini kıyamet gününden önce ya helâk edecek veya en çetin bir şekilde azaplandıracağız. Bu, Kitap´ta (levh-i mahfuz´da) yazılıdır.', 15, 286, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2088, 59, 17, 'İsra', 'وَمَا مَنَعَنَٓا اَنْ نُرْسِلَ بِالْاٰيَاتِ اِلَّٓا اَنْ كَذَّبَ بِهَا الْاَوَّلُونَۜ وَاٰتَيْنَا ثَمُودَ النَّاقَةَ مُبْصِرَةً فَظَلَمُوا بِهَاۜ وَمَا نُرْسِلُ بِالْاٰيَاتِ اِلَّا تَخْو۪يفاً', 'Bizi, âyetler (mucizeler) göndermekten alıkoyan tek şey, öncekilerin bu âyetleri yalanlamış olmasıdır. Nitekim Semûd kavmine, açık bir mucize olmak üzere bir dişi deve vermiştik. Onlar ise, (bu deveyi boğazladılar ve) bu yüzden zalim oldular. Oysa biz âyetleri ancak korkutmak için göndeririz.', 15, 287, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2089, 60, 17, 'İsra', 'وَاِذْ قُلْنَا لَكَ اِنَّ رَبَّكَ اَحَاطَ بِالنَّاسِۜ وَمَا جَعَلْنَا الرُّءْيَا الَّت۪ٓي اَرَيْنَاكَ اِلَّا فِتْنَةً لِلنَّاسِ وَالشَّجَرَةَ الْمَلْعُونَةَ فِي الْقُرْاٰنِۜ وَنُخَوِّفُهُمْۙ فَمَا يَز۪يدُهُمْ اِلَّا طُغْيَاناً كَب۪يراً۟', 'Hani sana: Rabbin, insanları çepeçevre kuşatmıştır, demiştik. Sana gösterdiğimiz o görüntüleri ve Kur´an´da lânetlenen ağacı, ancak insanları sınamak için meydana getirdik. Biz onları korkuturuz da, bu onlara, büyük bir azgınlıktan başka bir şey sağlamaz.', 15, 287, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2090, 61, 17, 'İsra', 'وَاِذْ قُلْنَا لِلْمَلٰٓئِكَةِ اسْجُدُوا لِاٰدَمَ فَسَجَدُٓوا اِلَّٓا اِبْل۪يسَۜ قَالَ ءَاَسْجُدُ لِمَنْ خَلَقْتَ ط۪يناًۚ', 'Meleklere: Âdem´e secde edin! demiştik. İblis´in dışında hepsi secde ettiler. İblis: «Ben, dedi, çamurdan yarattığın bir kimseye secde mi ederim!»', 15, 287, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50);
INSERT INTO `Ayetler` (`id`, `AyetNo`, `SureNo`, `SureAdi`, `Metin`, `Mana`, `Cuzu`, `Sayfasi`, `AyetSayisi`, `HarfSayisi`, `AdAnlami`, `IndigiYer`, `InisSirasi`) VALUES
(2091, 62, 17, 'İsra', 'قَالَ اَرَاَيْتَكَ هٰذَا الَّذ۪ي كَرَّمْتَ عَلَيَّۘ لَئِنْ اَخَّرْتَنِ اِلٰى يَوْمِ الْقِيٰمَةِ لَاَحْتَنِكَنَّ ذُرِّيَّتَهُٓ اِلَّا قَل۪يلاً', 'Dedi ki: «Şu benden üstün kıldığına da bir bak! Yemin ederim ki, eğer beni kıyamete kadar yaşatırsan, pek azı dışında, onun neslini kendime bağlayacağım!»', 15, 287, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2092, 63, 17, 'İsra', 'قَالَ اذْهَبْ فَمَنْ تَبِعَكَ مِنْهُمْ فَاِنَّ جَهَنَّمَ جَزَٓاؤُ۬كُمْ جَزَٓاءً مَوْفُوراً', 'Allah buyurdu: Git! Onlardan kim sana uyarsa, iyi bilin ki hepinizin cezası cehennemdir. Tam bir ceza!', 15, 287, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2093, 64, 17, 'İsra', 'وَاسْتَفْزِزْ مَنِ اسْتَطَعْتَ مِنْهُمْ بِصَوْتِكَ وَاَجْلِبْ عَلَيْهِمْ بِخَيْلِكَ وَرَجِلِكَ وَشَارِكْهُمْ فِي الْاَمْوَالِ وَالْاَوْلَادِ وَعِدْهُمْۜ وَمَا يَعِدُهُمُ الشَّيْطَانُ اِلَّا غُرُوراً', 'Onlardan gücünün yettiği kimseleri dâvetinle şaşırt; süvarilerinle, yayalarınla onları yaygaraya boğ; mallarına, evlâtlarına ortak ol, kendilerine vaadlerde bulun. Şeytan, insanlara, aldatmadan başka bir şey vâdetmez.', 15, 287, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2094, 65, 17, 'İsra', 'اِنَّ عِبَاد۪ي لَيْسَ لَكَ عَلَيْهِمْ سُلْطَانٌۜ وَكَفٰى بِرَبِّكَ وَك۪يلاً', 'Şurası muhakkak ki, benim (ihlâslı) kullarım üzerinde senin hiçbir ağırlığın olmayacaktır. (Onları) koruyucu olarak Rabbin yeter.', 15, 287, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2095, 66, 17, 'İsra', 'رَبُّكُمُ الَّذ۪ي يُزْج۪ي لَكُمُ الْفُلْكَ فِي الْبَحْرِ لِتَبْتَغُوا مِنْ فَضْلِه۪ۜ اِنَّهُ كَانَ بِكُمْ رَح۪يماً', '(Kullarım!) Rabbiniz, lütfuna nâil olmanız için denizde gemileri sizin için yüzdürendir. Doğrusu O, sizin için çok merhametlidir.', 15, 287, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2096, 67, 17, 'İsra', 'وَاِذَا مَسَّكُمُ الضُّرُّ فِي الْبَحْرِ ضَلَّ مَنْ تَدْعُونَ اِلَّٓا اِيَّاهُۚ فَلَمَّا نَجّٰيكُمْ اِلَى الْبَرِّ اَعْرَضْتُمْۜ وَكَانَ الْاِنْسَانُ كَفُوراً', 'Denizde başınıza bir musibet geldiğinde, O´ndan başka bütün yalvardıklarınız kaybolup gider. O sizi kurtarıp karaya çıkardığında, (yine eski halinize) dönersiniz. İnsanoğlu çok nankördür.', 15, 288, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2097, 68, 17, 'İsra', 'اَفَاَمِنْتُمْ اَنْ يَخْسِفَ بِكُمْ جَانِبَ الْبَرِّ اَوْ يُرْسِلَ عَلَيْكُمْ حَاصِباً ثُمَّ لَا تَجِدُوا لَكُمْ وَك۪يلاًۙ', 'O´nun, sizi kara tarafında yerin dibine geçirmeyeceğinden, yahut başınıza taş yağdırmayacağından emin misiniz? Sonra kendinize bir koruyucu da bulamazsınız.', 15, 288, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2098, 69, 17, 'İsra', 'اَمْ اَمِنْتُمْ اَنْ يُع۪يدَكُمْ ف۪يهِ تَارَةً اُخْرٰى فَيُرْسِلَ عَلَيْكُمْ قَاصِفاً مِنَ الرّ۪يحِ فَيُغْرِقَكُمْ بِمَا كَفَرْتُمْۙ ثُمَّ لَا تَجِدُوا لَكُمْ عَلَيْنَا بِه۪ تَب۪يعاً', 'Yahut O´nun, sizi bir kez daha oraya (denize) gönderip üzerinize bir kasırga yollayarak, inkâr etmiş olmanız sebebiyle sizi boğmayacağından emin misiniz? Sonra, bundan dolayı kendinize (intikamınızı almak için) bizi arayıp soracak bir destekçi de bulamazsınız.', 15, 288, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2099, 70, 17, 'İsra', 'وَلَقَدْ كَرَّمْنَا بَن۪ٓي اٰدَمَ وَحَمَلْنَاهُمْ فِي الْبَرِّ وَالْبَحْرِ وَرَزَقْنَاهُمْ مِنَ الطَّيِّبَاتِ وَفَضَّلْنَاهُمْ عَلٰى كَث۪يرٍ مِمَّنْ خَلَقْنَا تَفْض۪يلاً۟', 'Biz, hakikaten insanoğlunu şan ve şeref sahibi kıldık. Onları, (çeşitli nakil vasıtaları ile) karada ve denizde taşıdık; kendilerine güzel güzel rızıklar verdik; yine onları, yarattıklarımızın birçoğundan cidden üstün kıldık.', 15, 288, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2100, 71, 17, 'İsra', 'يَوْمَ نَدْعُوا كُلَّ اُنَاسٍ بِاِمَامِهِمْۚ فَمَنْ اُو۫تِيَ كِتَابَهُ بِيَم۪ينِه۪ فَاُو۬لٰٓئِكَ يَقْرَؤُ۫نَ كِتَابَهُمْ وَلَا يُظْلَمُونَ فَت۪يلاً', 'Her insan topluluğunu önderleri ile birlikte çağıracağımız o günde kimlerin amel defteri sağından verilirse, onlar, en küçük bir haksızlığa uğramamış olarak amel defterlerini okuyacaklar.', 15, 288, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2101, 72, 17, 'İsra', 'وَمَنْ كَانَ ف۪ي هٰذِه۪ٓ اَعْمٰى فَهُوَ فِي الْاٰخِرَةِ اَعْمٰى وَاَضَلُّ سَب۪يلاً', 'Bu dünyada kör olan kimse ahirette de kördür; üstelik iyice yolunu şaşırmıştır.', 15, 288, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2102, 73, 17, 'İsra', 'وَاِنْ كَادُوا لَيَفْتِنُونَكَ عَنِ الَّـذ۪ٓي اَوْحَيْنَٓا اِلَيْكَ لِتَفْتَرِيَ عَلَيْنَا غَيْرَهُۗ وَاِذاً لَاتَّخَذُوكَ خَل۪يلاً', 'Müşrikler, sana vahyettiğimizden başka bir şeyi yalan yere bize isnat etmen için seni, nerdeyse, sana vahyettiğimizden saptıracaklar ve ancak o takdirde seni candan dost kabul edeceklerdi.', 15, 288, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2103, 74, 17, 'İsra', 'وَلَوْلَٓا اَنْ ثَبَّتْنَاكَ لَقَدْ كِدْتَ تَرْكَنُ اِلَيْهِمْ شَيْـٔاً قَل۪يلاًۗ', 'Eğer seni sebatkâr kılmasaydık, gerçekten, nerdeyse onlara birazcık meyledecektin.', 15, 288, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2104, 75, 17, 'İsra', 'اِذاً لَاَذَقْنَاكَ ضِعْفَ الْحَيٰوةِ وَضِعْفَ الْمَمَاتِ ثُمَّ لَا تَجِدُ لَكَ عَلَيْنَا نَص۪يراً', 'O zaman, hiç şüphesiz sana hayatın ve ölümün sıkıntılarını kat kat tattırırdık; sonra bize karşı kendin için bir yardımcı da bulamazdın.', 15, 288, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2105, 76, 17, 'İsra', 'وَاِنْ كَادُوا لَيَسْتَفِزُّونَكَ مِنَ الْاَرْضِ لِيُخْرِجُوكَ مِنْهَا وَاِذاً لَا يَلْبَثُونَ خِلَافَكَ اِلَّا قَل۪يلاً', 'Yine onlar, seni yurdundan çıkarmak için nerdeyse dünyayı başına dar getirecekler. O takdirde, senin ardından kendileri de fazla kalamazlar.', 15, 289, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2106, 77, 17, 'İsra', 'سُنَّةَ مَنْ قَدْ اَرْسَلْنَا قَبْلَكَ مِنْ رُسُلِنَا وَلَا تَجِدُ لِسُنَّتِنَا تَحْو۪يلاً۟', 'Senden önce gönderdiğimiz peygamberler hakkındaki kanun (da budur). Bizim kanunumuzda hiçbir değişiklik bulamazsın.', 15, 289, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2107, 78, 17, 'İsra', 'اَقِمِ الصَّلٰوةَ لِدُلُوكِ الشَّمْسِ اِلٰى غَسَقِ الَّيْلِ وَقُرْاٰنَ الْفَجْرِۜ اِنَّ قُرْاٰنَ الْفَجْرِ كَانَ مَشْهُوداً', 'Gündüzün güneş dönüp gecenin karanlığı bastırıncaya kadar (belli vakitlerde) namaz kıl; bir de sabah namazını. Çünkü sabah namazı şahitlidir.', 15, 289, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2108, 79, 17, 'İsra', 'وَمِنَ الَّيْلِ فَـتَهَجَّدْ بِه۪ نَافِلَةً لَكَۗ عَسٰٓى اَنْ يَبْعَثَكَ رَبُّكَ مَقَاماً مَحْمُوداً', 'Gecenin bir kısmında uyanarak, sana mahsus bir nafile olmak üzere namaz kıl. (Böylece) Rabbinin, seni, övgüye değer bir makama göndereceği umulur.', 15, 289, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2109, 80, 17, 'İsra', 'وَقُلْ رَبِّ اَدْخِلْن۪ي مُدْخَلَ صِدْقٍ وَاَخْرِجْن۪ي مُخْرَجَ صِدْقٍ وَاجْعَلْ ل۪ي مِنْ لَدُنْكَ سُلْطَاناً نَص۪يراً', 'Ve şöyle niyaz et: Rabbim! Gireceğim yere dürüstlükle girmemi sağla; çıkacağım yerden de dürüstlükle çıkmamı sağla. Bana tarafından, hakkıyla yardım edici bir kuvvet ver.', 15, 289, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2110, 81, 17, 'İsra', 'وَقُلْ جَٓاءَ الْحَقُّ وَزَهَقَ الْبَاطِلُۜ اِنَّ الْبَاطِلَ كَانَ زَهُوقاً', 'Yine de ki: Hak geldi; bâtıl yıkılıp gitti. Zaten bâtıl yıkılmaya mahkumdur.', 15, 289, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2111, 82, 17, 'İsra', 'وَنُنَزِّلُ مِنَ الْقُرْاٰنِ مَا هُوَ شِفَٓاءٌ وَرَحْمَةٌ لِلْمُؤْمِن۪ينَۙ وَلَا يَز۪يدُ الظَّالِم۪ينَ اِلَّا خَسَاراً', 'Biz, Kur´an´dan öyle bir şey indiriyoruz ki o, müminler için şifa ve rahmettir; zalimlerin ise yalnızca ziyanını artırır.', 15, 289, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2112, 83, 17, 'İsra', 'وَاِذَٓا اَنْعَمْنَا عَلَى الْاِنْسَانِ اَعْرَضَ وَنَاٰ بِجَانِبِه۪ۚ وَاِذَا مَسَّهُ الشَّرُّ كَانَ يَؤُ۫ساً', 'İnsana nimet verdiğimiz zaman (bizden) yüz çevirip yan çizer; ona bir de zarar ziyan dokunacak olsa iyice karamsarlığa düşer.', 15, 289, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2113, 84, 17, 'İsra', 'قُلْ كُلٌّ يَعْمَلُ عَلٰى شَاكِلَتِه۪ۜ فَرَبُّكُمْ اَعْلَمُ بِمَنْ هُوَ اَهْدٰى سَب۪يلاً۟', 'De ki: Herkes, kendi mizaç ve meşrebine göre iş yapar. Bu durumda kimin doğru bir yol tuttuğunu Rabbiniz en iyi bilendir.', 15, 289, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2114, 85, 17, 'İsra', 'وَيَسْـَٔلُونَكَ عَنِ الرُّوحِۜ قُلِ الرُّوحُ مِنْ اَمْرِ رَبّ۪ي وَمَٓا اُو۫ت۪يتُمْ مِنَ الْعِلْمِ اِلَّا قَل۪يلاً', 'Sana ruh hakkında soru sorarlar. De ki: Ruh, Rabbimin emrindendir. Size ancak az bir bilgi verilmiştir.', 15, 289, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2115, 86, 17, 'İsra', 'وَلَئِنْ شِئْنَا لَنَذْهَبَنَّ بِالَّـذ۪ٓي اَوْحَيْنَٓا اِلَيْكَ ثُمَّ لَا تَجِدُ لَكَ بِه۪ عَلَيْنَا وَك۪يلاًۙ', 'Hakikaten, biz dilersek sana vahyettiğimizi ortadan kaldırırız; sonra bu durumda sen de bize karşı hiçbir koruyucu bulamazsın.', 15, 289, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2116, 87, 17, 'İsra', 'اِلَّا رَحْمَةً مِنْ رَبِّكَۜ اِنَّ فَضْلَهُ كَانَ عَلَيْكَ كَب۪يراً', 'Ancak Rabbinin rahmeti (sayesinde Kur´an bâki kalmıştır). Çünkü O´nun sana lütufkârlığı çok büyüktür.', 15, 290, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2117, 88, 17, 'İsra', 'قُلْ لَئِنِ اجْتَمَعَتِ الْاِنْسُ وَالْجِنُّ عَلٰٓى اَنْ يَأْتُوا بِمِثْلِ هٰذَا الْقُرْاٰنِ لَا يَأْتُونَ بِمِثْلِه۪ وَلَوْ كَانَ بَعْضُهُمْ لِبَعْضٍ ظَه۪يراً', 'De ki: Andolsun, bu Kur´an´ın bir benzerini ortaya koymak üzere insü cin bir araya gelseler, birbirlerine destek de olsalar, onun benzerini ortaya getiremezler.', 15, 290, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2118, 89, 17, 'İsra', 'وَلَقَدْ صَرَّفْنَا لِلنَّاسِ ف۪ي هٰذَا الْقُرْاٰنِ مِنْ كُلِّ مَثَلٍۘ فَاَبٰٓى اَكْثَرُ النَّاسِ اِلَّا كُفُوراً', 'Muhakkak ki biz, bu Kur´an´da insanlara her türlü misali çeşitli şekillerde anlattık. Yine de insanların çoğu inkârcılıktan başkasını kabullenmediler.', 15, 290, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2119, 90, 17, 'İsra', 'وَقَالُوا لَنْ نُؤْمِنَ لَكَ حَتّٰى تَفْجُرَ لَنَا مِنَ الْاَرْضِ يَنْبُوعاًۙ', 'Onlar: «Sen, dediler, bizim için yerden bir kaynak fışkırtmadıkça sana asla inanmayacağız.»', 15, 290, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2120, 91, 17, 'İsra', 'اَوْ تَكُونَ لَكَ جَنَّةٌ مِنْ نَخ۪يلٍ وَعِنَبٍ فَتُفَجِّرَ الْاَنْهَارَ خِلَالَهَا تَفْج۪يراًۙ', '«Veya senin bir hurma bahçen ve üzüm bağın olmalı; öyle ki, içlerinden gürül gürül ırmaklar akıtmalısın.»', 15, 290, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2121, 92, 17, 'İsra', 'اَوْ تُسْقِطَ السَّمَٓاءَ كَمَا زَعَمْتَ عَلَيْنَا كِسَفاً اَوْ تَأْتِيَ بِاللّٰهِ وَالْمَلٰٓئِكَةِ قَب۪يلاًۙ', '«Yahut, iddia ettiğin gibi, üzerimize gökten parçalar yağdırmalısın veya Allah´ı ve melekleri gözümüzün önüne getirmelisin.»', 15, 290, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2122, 93, 17, 'İsra', 'اَوْ يَكُونَ لَكَ بَيْتٌ مِنْ زُخْرُفٍ اَوْ تَرْقٰى فِي السَّمَٓاءِۜ وَلَنْ نُؤْمِنَ لِرُقِيِّكَ حَتّٰى تُنَزِّلَ عَلَيْنَا كِتَاباً نَقْرَؤُ۬هُۜ قُلْ سُبْحَانَ رَبّ۪ي هَلْ كُنْتُ اِلَّا بَشَراً رَسُولاً۟', '«Yahut da altından bir evin olmalı, ya da göğe çıkmalısın. Bize, okuyacağımız bir kitap indirmediğin sürece (göğe) çıktığına da asla inanmayız.» De ki: Rabbimi tenzih ederim. Ben, sadece beşer bir elçiyim.', 15, 290, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2123, 94, 17, 'İsra', 'وَمَا مَنَعَ النَّاسَ اَنْ يُؤْمِنُٓوا اِذْ جَٓاءَهُمُ الْهُدٰٓى اِلَّٓا اَنْ قَالُٓوا اَبَعَثَ اللّٰهُ بَشَراً رَسُولاً', 'Zaten, kendilerine hidayet rehberi geldiğinde, insanların (buna) inanmalarını sırf, «Allah, peygamber olarak bir beşeri mi gönderdi?» demeleri engellemiştir.', 15, 290, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2124, 95, 17, 'İsra', 'قُلْ لَوْ كَانَ فِي الْاَرْضِ مَلٰٓئِكَةٌ يَمْشُونَ مُطْمَئِنّ۪ينَ لَنَزَّلْنَا عَلَيْهِمْ مِنَ السَّمَٓاءِ مَلَكاً رَسُولاً', 'Şunu söyle: Eğer yeryüzünde yerleşmiş gezip dolaşan melekler olsaydı, elbette onlara gökten, peygamber olarak bir melek gönderirdik.', 15, 290, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2125, 96, 17, 'İsra', 'قُلْ كَفٰى بِاللّٰهِ شَه۪يداً بَيْن۪ي وَبَيْنَكُمْۜ اِنَّهُ كَانَ بِعِبَادِه۪ خَب۪يراً بَص۪يراً', 'De ki: Benimle sizin aranızda gerçek şahit olarak Allah kâfidir. Zira O, kullarını hakikaten bilip görmektedir.', 15, 290, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2126, 97, 17, 'İsra', 'وَمَنْ يَهْدِ اللّٰهُ فَهُوَ الْمُهْتَدِۚ وَمَنْ يُضْلِلْ فَلَنْ تَجِدَ لَهُمْ اَوْلِيَٓاءَ مِنْ دُونِه۪ۜ وَنَحْشُرُهُمْ يَوْمَ الْقِيٰمَةِ عَلٰى وُجُوهِهِمْ عُمْياً وَبُكْماً وَصُماًّۜ مَأْوٰيهُمْ جَهَنَّمُۜ كُلَّمَا خَبَتْ زِدْنَاهُمْ سَع۪يراً', 'Allah kime hidayet verirse, işte doğru yolu bulan odur; kimi de hidayetten uzak tutarsa, artık onlara, Allah´tan başka dostlar bulamazsın. Kıyamet gününde onları kör, dilsiz ve sağır bir halde yüzükoyun haşrederiz. Onların varacağı ve kalacağı yer cehennemdir ki, ateşi yavaşladıkça onun alevini artırırız.', 15, 291, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2127, 98, 17, 'İsra', 'ذٰلِكَ جَزَٓاؤُ۬هُمْ بِاَنَّهُمْ كَفَرُوا بِاٰيَاتِنَا وَقَالُٓوا ءَاِذَا كُنَّا عِظَاماً وَرُفَاتاً ءَاِنَّا لَمَبْعُوثُونَ خَلْقاً جَد۪يداً', 'Cezaları işte budur! Çünkü onlar, âyetlerimizi inkâr etmişler ve: «Sahi bizler, bir kemik yığını ve kokuşmuş toprak olduktan sonra yeni bir yaratılışla diriltilmiş mi olacağız?» demişlerdir.', 15, 291, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2128, 99, 17, 'İsra', 'اَوَلَمْ يَرَوْا اَنَّ اللّٰهَ الَّذ۪ي خَلَقَ السَّمٰوَاتِ وَالْاَرْضَ قَادِرٌ عَلٰٓى اَنْ يَخْلُقَ مِثْلَهُمْ وَجَعَلَ لَهُمْ اَجَلاً لَا رَيْبَ ف۪يهِۜ فَاَبَى الظَّالِمُونَ اِلَّا كُفُوراً', 'Düşünmediler mi ki, gökleri ve yeri yaratmış olan Allah, kendilerinin benzerini yaratmaya da kadirdir! Allah, onlar için bir vâde takdir etti. Bunda şüphe yoktur. Ama zalimler, inkârcılıktan başkasını kabullenmediler.', 15, 291, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2129, 100, 17, 'İsra', 'قُلْ لَوْ اَنْتُمْ تَمْلِكُونَ خَزَٓائِنَ رَحْمَةِ رَبّ۪ٓي اِذاً لَاَمْسَكْتُمْ خَشْيَةَ الْاِنْفَاقِۜ وَكَانَ الْاِنْسَانُ قَتُوراً۟', 'De ki: Rabbimin rahmet hazinesine eğer siz sahip olsaydınız, harcanır korkusuyla kıstıkça kısardınız. İnsanoğlu da pek eli sıkıdır!', 15, 291, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2130, 101, 17, 'İsra', 'وَلَقَدْ اٰتَيْنَا مُوسٰى تِسْعَ اٰيَاتٍ بَيِّنَاتٍ فَسْـَٔلْ بَن۪ٓي اِسْرَٓائ۪لَ اِذْ جَٓاءَهُمْ فَقَالَ لَهُ فِرْعَوْنُ اِنّ۪ي لَاَظُنُّكَ يَا مُوسٰى مَسْحُوراً', 'Andolsun biz, Musa´ya açık açık dokuz âyet verdik. Haydi İsrailoğullarına sor. Musa onlara geldiğinde Firavun ona, «Ey Musa! dedi, senin büyülenmiş olduğunu sanıyorum!»', 15, 291, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2131, 102, 17, 'İsra', 'قَالَ لَقَدْ عَلِمْتَ مَٓا اَنْزَلَ هٰٓؤُ۬لَٓاءِ اِلَّا رَبُّ السَّمٰوَاتِ وَالْاَرْضِ بَصَٓائِرَۚ وَاِنّ۪ي لَاَظُنُّكَ يَا فِرْعَوْنُ مَثْبُوراً', '(Musa Firavun´a:) «Pek âlâ biliyorsun ki, dedi, bunları, birer ibret olmak üzere, ancak, göklerin ve yerin Rabbi indirdi. Ey Firavun! Ben de senin hakikaten mahvolduğunu sanıyorum!»', 15, 291, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2132, 103, 17, 'İsra', 'فَاَرَادَ اَنْ يَسْتَفِزَّهُمْ مِنَ الْاَرْضِ فَاَغْرَقْنَاهُ وَمَنْ مَعَهُ جَم۪يعاًۙ', 'Derken, Firavun onları ülkeden çıkarmak istedi. Bu yüzden biz onu ve maiyyetindekilerin hepsini (denizde) boğduk.', 15, 291, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2133, 104, 17, 'İsra', 'وَقُلْنَا مِنْ بَعْدِه۪ لِبَن۪ٓي اِسْرَٓائ۪لَ اسْكُنُوا الْاَرْضَ فَاِذَا جَٓاءَ وَعْدُ الْاٰخِرَةِ جِئْنَا بِكُمْ لَف۪يفاًۜ', 'Arkasından da İsrailoğullarına: «O topraklarda oturun! Ahiret vâdi tahakkuk edince, hepinizi toplayıp bir araya getireceğiz» dedik.', 15, 291, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2134, 105, 17, 'İsra', 'وَبِالْحَقِّ اَنْزَلْنَاهُ وَبِالْحَقِّ نَزَلَۜ وَمَٓا اَرْسَلْنَاكَ اِلَّا مُبَشِّراً وَنَذ۪يراًۢ', 'Biz Kur´an´ı hak olarak indirdik; o da hakkı getirdi. Seni de ancak müjdeleyici ve uyarıcı olarak gönderdik.', 15, 292, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2135, 106, 17, 'İsra', 'وَقُرْاٰناً فَرَقْنَاهُ لِتَقْرَاَهُ۫ عَلَى النَّاسِ عَلٰى مُكْثٍ وَنَزَّلْنَاهُ تَنْز۪يلاً', 'Biz onu, Kur´an olarak, insanlara dura dura okuyasın diye (âyet âyet, sûre sûre) ayırdık; ve onu peyderpey indirdik.', 15, 292, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2136, 107, 17, 'İsra', 'قُلْ اٰمِنُوا بِه۪ٓ اَوْ لَا تُؤْمِنُواۜ اِنَّ الَّذ۪ينَ اُو۫تُوا الْعِلْمَ مِنْ قَبْلِه۪ٓ اِذَا يُتْلٰى عَلَيْهِمْ يَخِرُّونَ لِلْاَذْقَانِ سُجَّداًۙ', 'De ki: Siz ona ister inanın, ister inanmayın; şu bir gerçek ki, bundan önce kendilerine ilim verilen kimselere o (Kur´an) okununca, derhal yüz üstü secdeye kapanırlar.', 15, 292, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2137, 108, 17, 'İsra', 'وَيَقُولُونَ سُبْحَانَ رَبِّنَٓا اِنْ كَانَ وَعْدُ رَبِّنَا لَمَفْعُولاً', 'Ve derlerdi ki: Rabbimizi tesbih ederiz. Rabbimizin vâdi mutlaka yerine getirilir.', 15, 292, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2138, 109, 17, 'İsra', 'وَيَخِرُّونَ لِلْاَذْقَانِ يَبْكُونَ وَيَز۪يدُهُمْ خُشُوعاً ۩', 'Ağlayarak yüz üstü yere kapanırlar. (Kur´an okumak) onların saygısını artırır.', 15, 292, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2139, 110, 17, 'İsra', 'قُلِ ادْعُوا اللّٰهَ اَوِ ادْعُوا الرَّحْمٰنَۜ اَياًّ مَا تَدْعُوا فَلَهُ الْاَسْمَٓاءُ الْحُسْنٰىۚ وَلَا تَجْهَرْ بِصَلَاتِكَ وَلَا تُخَافِتْ بِهَا وَابْتَغِ بَيْنَ ذٰلِكَ سَب۪يلاً', 'De ki: «İster Allah deyin, ister Rahman deyin. Hangisini deseniz olur. Çünkü en güzel isimler O´na hastır.» Namazında yüksek sesle okuma; onda sesini fazla da kısma; ikisinin arası bir yol tut.', 15, 292, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2140, 111, 17, 'İsra', 'وَقُلِ الْحَمْدُ لِلّٰهِ الَّذ۪ي لَمْ يَتَّخِذْ وَلَداً وَلَمْ يَكُنْ لَهُ شَر۪يكٌ فِي الْمُلْكِ وَلَمْ يَكُنْ لَهُ وَلِيٌّ مِنَ الذُّلِّ وَكَبِّرْهُ تَكْب۪يراً', '«Çocuk edinmeyen, hakimiyette ortağı bulunmayan, âcizlikten ötürü bir dosta da ihtiyacı olmayan Allah´a hamd olsun» de ve tekbir getirerek O´nun şanını yücelt!', 15, 292, 111, 6480, 'Geceleyin yürütmek', 'Mekke', 50),
(2141, 1, 18, 'Kehf', 'اَلْحَمْدُ لِلّٰهِ الَّـذ۪ٓي اَنْزَلَ عَلٰى عَبْدِهِ الْـكِتَابَ وَلَمْ يَجْعَلْ لَهُ عِـوَجا۔ًۜ', '(1-4) Hamd olsun Allah´a ki, O, (insanları) kendi tarafından çetin bir azap ile ikaz etmek, iyi iş ve davranışlarda bulunan müminlere, kendileri için, içinde ebedî kalacakları (cennette) güzel bir ecir bulunduğunu müjdelemek ve «Allah evlât edindi» diyenleri de uyarmak için kuluna (Muhammed´e), kendisinde hiçbir (tezat ve) eğrilik bulunmayan dosdoğru Kitab´ı indirdi.', 15, 292, 110, 6423, 'Mağara', 'Mekke', 69),
(2142, 2, 18, 'Kehf', 'قَيِّماً لِيُنْذِرَ بَأْساً شَد۪يداً مِنْ لَدُنْـهُ وَيُبَشِّرَ الْمُؤْمِن۪ينَ الَّذ۪ينَ يَعْمَلُونَ الصَّالِحَاتِ اَنَّ لَهُمْ اَجْراً حَسَناًۙ', '(1-4) Hamd olsun Allah´a ki, O, (insanları) kendi tarafından çetin bir azap ile ikaz etmek, iyi iş ve davranışlarda bulunan müminlere, kendileri için, içinde ebedî kalacakları (cennette) güzel bir ecir bulunduğunu müjdelemek ve «Allah evlât edindi» diyenleri de uyarmak için kuluna (Muhammed´e), kendisinde hiçbir (tezat ve) eğrilik bulunmayan dosdoğru Kitab´ı indirdi.', 15, 292, 110, 6423, 'Mağara', 'Mekke', 69),
(2143, 3, 18, 'Kehf', 'مَاكِث۪ينَ ف۪يهِ اَبَداًۙ', '(1-4) Hamd olsun Allah´a ki, O, (insanları) kendi tarafından çetin bir azap ile ikaz etmek, iyi iş ve davranışlarda bulunan müminlere, kendileri için, içinde ebedî kalacakları (cennette) güzel bir ecir bulunduğunu müjdelemek ve «Allah evlât edindi» diyenleri de uyarmak için kuluna (Muhammed´e), kendisinde hiçbir (tezat ve) eğrilik bulunmayan dosdoğru Kitab´ı indirdi.', 15, 292, 110, 6423, 'Mağara', 'Mekke', 69),
(2144, 4, 18, 'Kehf', 'وَيُنْذِرَ الَّذ۪ينَ قَالُوا اتَّخَذَ اللّٰهُ وَلَداًۗ', '(1-4) Hamd olsun Allah´a ki, O, (insanları) kendi tarafından çetin bir azap ile ikaz etmek, iyi iş ve davranışlarda bulunan müminlere, kendileri için, içinde ebedî kalacakları (cennette) güzel bir ecir bulunduğunu müjdelemek ve «Allah evlât edindi» diyenleri de uyarmak için kuluna (Muhammed´e), kendisinde hiçbir (tezat ve) eğrilik bulunmayan dosdoğru Kitab´ı indirdi.', 15, 292, 110, 6423, 'Mağara', 'Mekke', 69),
(2145, 5, 18, 'Kehf', 'مَا لَهُمْ بِه۪ مِنْ عِلْمٍ وَلَا لِاٰبَٓائِهِمْۜ كَبُرَتْ كَلِمَةً تَخْرُجُ مِنْ اَفْوَاهِهِمْۜ اِنْ يَقُولُونَ اِلَّا كَذِباً', 'Ne onların (Allah evlât edindi, diyenlerin), ne de atalarının bu konuda hiçbir bilgisi yoktur. Ağızlarından çıkan bu söz ne büyük oldu! Yalandan başka bir şey söylemiyorlar.', 15, 293, 110, 6423, 'Mağara', 'Mekke', 69),
(2146, 6, 18, 'Kehf', 'فَلَعَلَّكَ بَاخِـعٌ نَفْسَكَ عَلٰٓى اٰثَارِهِمْ اِنْ لَمْ يُؤْمِنُوا بِهٰذَا الْحَد۪يثِ اَسَفاً', 'Bu yeni Kitab´a inanmazlarsa (ve bu yüzden helâk olurlarsa) arkalarından üzüntüyle neredeyse kendini harap edeceksin.', 15, 293, 110, 6423, 'Mağara', 'Mekke', 69),
(2147, 7, 18, 'Kehf', 'اِنَّا جَعَلْنَا مَا عَلَى الْاَرْضِ ز۪ينَةً لَهَا لِنَبْلُوَهُمْ اَيُّهُمْ اَحْسَنُ عَمَلاً', 'Biz, insanların hangisinin daha güzel amel edeceğini deneyelim diye yeryüzündeki her şeyi dünyanın kendine mahsus bir zinet yaptık.', 15, 293, 110, 6423, 'Mağara', 'Mekke', 69),
(2148, 8, 18, 'Kehf', 'وَاِنَّا لَجَاعِلُونَ مَا عَلَيْهَا صَع۪يداً جُرُزاًۜ', '(Bununla beraber) biz mutlaka oradaki her şeyi kupkuru bir toprak yapacağız.', 15, 293, 110, 6423, 'Mağara', 'Mekke', 69),
(2149, 9, 18, 'Kehf', 'اَمْ حَسِبْتَ اَنَّ اَصْحَابَ الْكَهْفِ وَالرَّق۪يمِ كَانُوا مِنْ اٰيَاتِنَا عَجَباً', '(Resûlüm)! Yoksa sen, bizim âyetlerimizden Ashâb-ı Kehf ve Ashâb-ı Rakîm´in durumlarını şaşırtıcı mı buldun?', 15, 293, 110, 6423, 'Mağara', 'Mekke', 69),
(2150, 10, 18, 'Kehf', 'اِذْ اَوَى الْفِتْيَةُ اِلَى الْكَهْفِ فَقَالُوا رَبَّنَٓا اٰتِنَا مِنْ لَدُنْكَ رَحْمَةً وَهَيِّئْ لَنَا مِنْ اَمْرِنَا رَشَداً', 'O (yiğit) gençler mağaraya sığınmışlar ve: Rabbimiz! Bize tarafından rahmet ver ve bize, (şu) durumumuzdan bir kurtuluş yolu hazırla! demişlerdi.', 15, 293, 110, 6423, 'Mağara', 'Mekke', 69),
(2151, 11, 18, 'Kehf', 'فَضَرَبْنَا عَلٰٓى اٰذَانِهِمْ فِي الْكَهْفِ سِن۪ينَ عَدَداًۙ', 'Bunun üzerine biz de o mağarada onların kulaklarına nice yıllar perde koyduk (uykuya daldırdık.)', 15, 293, 110, 6423, 'Mağara', 'Mekke', 69),
(2152, 12, 18, 'Kehf', 'ثُمَّ بَعَثْنَاهُمْ لِنَعْلَمَ اَيُّ الْحِزْبَيْنِ اَحْصٰى لِمَا لَبِثُٓوا اَمَداً۟', 'Sonra da iki guruptan hangisinin kaldıkları müddeti daha iyi hesap edeceğini görelim diye onları uyandırdık.', 15, 293, 110, 6423, 'Mağara', 'Mekke', 69),
(2153, 13, 18, 'Kehf', 'نَحْنُ نَقُصُّ عَلَيْكَ نَبَاَهُمْ بِالْحَقِّۜ اِنَّهُمْ فِتْيَةٌ اٰمَنُوا بِرَبِّهِمْ وَزِدْنَاهُمْ هُدًىۗ', 'Biz sana onların başından geçenleri gerçek olarak anlatıyoruz. Hakikaten onlar, Rablerine inanmış gençlerdi. Biz de onların hidayetini arttırdık.', 15, 293, 110, 6423, 'Mağara', 'Mekke', 69),
(2154, 14, 18, 'Kehf', 'وَرَبَطْنَا عَلٰى قُلُوبِهِمْ اِذْ قَامُوا فَقَالُوا رَبُّنَا رَبُّ السَّمٰوَاتِ وَالْاَرْضِ لَنْ نَدْعُوَ۬ا مِنْ دُونِه۪ٓ اِلٰهاً لَقَدْ قُلْـنَٓا اِذاً شَطَطاً', 'Onların kalplerini metîn kıldık. O yiğitler (o yerin hükümdarı karşısında) ayağa kalkarak dediler ki: «Bizim Rabbimiz, göklerin ve yerin Rabbidir. Biz, O´ndan başkasına tanrı demeyiz. Yoksa saçma sapan konuşmuş oluruz.', 15, 293, 110, 6423, 'Mağara', 'Mekke', 69),
(2155, 15, 18, 'Kehf', 'هٰٓؤُ۬لَٓاءِ قَوْمُنَا اتَّخَذُوا مِنْ دُونِه۪ٓ اٰلِهَةًۜ لَوْلَا يَأْتُونَ عَلَيْهِمْ بِسُلْطَانٍ بَيِّنٍۜ فَمَنْ اَظْلَمُ مِمَّنِ افْتَرٰى عَلَى اللّٰهِ كَذِباًۜ', 'Şu bizim kavmimiz Allah´tan başka tanrılar edindiler. Bari bu tanrılar konusunda açık bir delil getirseler. (Ne mümkün!) Öyle ise Allah hakkında yalan uydurandan daha zalimi var mı?', 15, 293, 110, 6423, 'Mağara', 'Mekke', 69),
(2156, 16, 18, 'Kehf', 'وَاِذِ اعْتَزَلْتُمُوهُمْ وَمَا يَعْبُدُونَ اِلَّا اللّٰهَ فَأْوُٓ۫ا اِلَى الْكَهْفِ يَنْشُرْ لَكُمْ رَبُّكُمْ مِنْ رَحْمَتِه۪ وَيُهَيِّئْ لَكُمْ مِنْ اَمْرِكُمْ مِرْفَقاً', '(İçlerinden biri şöyle demişti:) «Madem ki siz onlardan ve onların Allah´ın dışında tapmakta oldukları varlıklardan uzaklaştınız, o halde mağaraya sığının ki, Rabbiniz size rahmetini yaysın ve işinizde sizin için fayda ve kolaylık sağlasın.»', 15, 294, 110, 6423, 'Mağara', 'Mekke', 69),
(2157, 17, 18, 'Kehf', 'وَتَرَى الشَّمْسَ اِذَا طَلَعَتْ تَزَاوَرُ عَنْ كَهْفِهِمْ ذَاتَ الْيَم۪ينِ وَاِذَا غَرَبَتْ تَقْرِضُهُمْ ذَاتَ الشِّمَالِ وَهُمْ ف۪ي فَجْوَةٍ مِنْهُۜ ذٰلِكَ مِنْ اٰيَاتِ اللّٰهِۜ مَنْ يَهْدِ اللّٰهُ فَهُوَ الْمُهْتَدِۚ وَمَنْ يُضْلِلْ فَلَنْ تَجِدَ لَهُ وَلِياًّ مُرْشِداً۟', '(Resûlüm! Orada bulunsaydın) güneşi görürdün: Doğduğu zaman mağaralarının sağına meyleder; batarken de sol taraftan onlara isabet etmeden geçerdi. (Böylece) onlar (güneş ışığından rahatsız olmaksızın) mağaranın bir köşesinde (uyurlardı). İşte bu, Allah´ın âyetlerindendir. Allah kime hidayet ederse, işte o, hakka ulaşmıştır, kimi de hidayetten mahrum ederse artık onu doğruya yöneltecek bir dost bulamazsın.', 15, 294, 110, 6423, 'Mağara', 'Mekke', 69),
(2158, 18, 18, 'Kehf', 'وَتَحْسَبُهُمْ اَيْقَاظاً وَهُمْ رُقُودٌۗ وَنُقَلِّبُهُمْ ذَاتَ الْيَم۪ينِ وَذَاتَ الشِّمَالِۗ وَكَلْبُهُمْ بَاسِطٌ ذِرَاعَيْهِ بِالْوَص۪يدِۜ لَوِ اطَّـلَعْتَ عَلَيْهِمْ لَوَلَّيْتَ مِنْهُمْ فِرَاراً وَلَمُلِئْتَ مِنْهُمْ رُعْباً', 'Kendileri uykuda oldukları halde sen onları uyanık sanırdın. Onları sağa sola çevirirdik. Köpekleri de mağaranın girişinde ön ayaklarını uzatmış yatmakta idi. Eğer onların durumlarına muttali olsa idin dönüp onlardan kaçardın ve gördüklerin yüzünden için korku ile dolardı.', 15, 294, 110, 6423, 'Mağara', 'Mekke', 69),
(2159, 19, 18, 'Kehf', 'وَكَذٰلِكَ بَعَثْنَاهُمْ لِيَتَسَٓاءَلُوا بَيْنَهُمْۜ قَالَ قَٓائِلٌ مِنْهُمْ كَمْ لَبِثْتُمْۜ قَالُوا لَبِثْنَا يَوْماً اَوْ بَعْضَ يَوْمٍۜ قَالُوا رَبُّكُمْ اَعْلَمُ بِمَا لَبِثْتُمْ فَابْعَثُٓوا اَحَدَكُمْ بِوَرِقِكُمْ هٰذِه۪ٓ اِلَى الْمَد۪ينَةِ فَلْيَنْظُرْ اَيُّهَٓا اَزْكٰى طَعَاماً فَلْيَأْتِكُمْ بِرِزْقٍ مِنْهُ وَلْيَتَلَطَّفْ وَلَا يُشْعِرَنَّ بِكُمْ اَحَداً', 'Böylece biz, aralarında birbirlerine sormaları için onları uyandırdık: İçlerinden biri: «Ne kadar kaldınız?» dedi. (Kimi) «Bir gün ya da günün bir parçası kadar kaldık» dediler; (kimi de) şöyle dediler: «Rabbiniz, kaldığınız müddeti daha iyi bilir. Şimdi siz, içinizden birini şu gümüş paranızla şehre gönderin de, baksın, (şehrin) hangi yiyeceği daha temiz ise size ondan erzak getirsin; ayrıca, nâzik davransın (gizli hareket etsin) ve sakın sizi kimseye sezdirmesin.»', 15, 294, 110, 6423, 'Mağara', 'Mekke', 69),
(2160, 20, 18, 'Kehf', 'اِنَّهُمْ اِنْ يَظْهَرُوا عَلَيْكُمْ يَرْجُمُوكُمْ اَوْ يُع۪يدُوكُمْ ف۪ي مِلَّتِهِمْ وَلَنْ تُفْلِحُٓوا اِذاً اَبَداً', '«Çünkü onlar eğer size muttali olurlarsa, ya sizi taşlayarak öldürürler veya kendi dinlerine çevirirler ki, o zaman ebediyyen iflah olmazsınız.»', 15, 294, 110, 6423, 'Mağara', 'Mekke', 69),
(2161, 21, 18, 'Kehf', 'وَكَذٰلِكَ اَعْثَرْنَا عَلَيْهِمْ لِيَعْلَمُٓوا اَنَّ وَعْدَ اللّٰهِ حَقٌّ وَاَنَّ السَّاعَةَ لَا رَيْبَ ف۪يهَاۚ اِذْ يَتَنَازَعُونَ بَيْنَهُمْ اَمْرَهُمْ فَقَالُوا ابْنُوا عَلَيْهِمْ بُنْيَاناًۜ رَبُّهُمْ اَعْلَمُ بِهِمْۜ قَالَ الَّذ۪ينَ غَلَبُوا عَلٰٓى اَمْرِهِمْ لَنَتَّخِذَنَّ عَلَيْهِمْ مَسْجِداً', 'Böylece (insanları) onlardan haberdar ettik ki, Allah´ın vâdinin hak olduğunu, kıyametin şüphe götürmez olduğunu bilsinler. Hani onlar aralarında Ashâb-ı Kehf´in durumunu tartışıyorlardı. Dediler ki: «Üzerlerine bir bina yapın. Rableri onları daha iyi bilir.» Onların durumuna vâkıf olanlar ise: «Bizler, kesinlikle onların yanıbaşlarına bir mescit yapacağız» dediler.', 15, 295, 110, 6423, 'Mağara', 'Mekke', 69),
(2162, 22, 18, 'Kehf', 'سَيَقُولُونَ ثَلٰثَةٌ رَابِعُهُمْ كَلْبُهُمْۚ وَيَقُولُونَ خَمْسَةٌ سَادِسُهُمْ كَلْبُهُمْ رَجْماً بِالْغَيْبِۚ وَيَقُولُونَ سَبْعَةٌ وَثَامِنُهُمْ كَلْبُهُمْۜ قُلْ رَبّ۪ٓي اَعْلَمُ بِعِدَّتِهِمْ مَا يَعْلَمُهُمْ اِلَّا قَل۪يلٌ۠ فَلَا تُمَارِ ف۪يهِمْ اِلَّا مِرَٓاءً ظَاهِراًۖ وَلَا تَسْتَفْتِ ف۪يهِمْ مِنْهُمْ اَحَداً۟', '(İnsanların kimi:) «Onlar üç kişidir; dördüncüleri de köpekleridir» diyecekler; yine: «Beş kişidir; altıncıları köpekleridir» diyecekler. (Bunlar) bilinmeyen hakkında tahmin yürütmektir. (Kimileri de:) «Onlar yedi kişidir; sekizincisi köpekleridir» derler. De ki: Onların sayılarını Rabbim daha iyi bilir. Onlar hakkında bilgisi olan çok azdır. Öyle ise Ashâb-ı Kehf hakkında, delillerin açık olması haricinde bir münakaşaya girişme ve onlar hakkında (ileri geri konuşan) kimselerin hiçbirinden malumat isteme.', 15, 295, 110, 6423, 'Mağara', 'Mekke', 69),
(2163, 23, 18, 'Kehf', 'وَلَا تَقُولَنَّ لِشَايْءٍ اِنّ۪ي فَاعِلٌ ذٰلِكَ غَداًۙ', '(23-24) Allah´ın dilemesine bağlamadıkça (inşâallah demedikçe) hiçbir şey için «Bunu yarın yapacağım» deme. Bunu unuttuğun takdirde Allah´ı an ve: «Umarım Rabbim beni, doğruya bundan daha yakın olan bir yola iletir» de.', 15, 295, 110, 6423, 'Mağara', 'Mekke', 69),
(2164, 24, 18, 'Kehf', 'اِلَّٓا اَنْ يَشَٓاءَ اللّٰهُۘ وَاذْكُرْ رَبَّكَ اِذَا نَس۪يتَ وَقُلْ عَسٰٓى اَنْ يَهْدِيَنِ رَبّ۪ي لِاَقْرَبَ مِنْ هٰذَا رَشَداً', '(23-24) Allah´ın dilemesine bağlamadıkça (inşâallah demedikçe) hiçbir şey için «Bunu yarın yapacağım» deme. Bunu unuttuğun takdirde Allah´ı an ve: «Umarım Rabbim beni, doğruya bundan daha yakın olan bir yola iletir» de.', 15, 295, 110, 6423, 'Mağara', 'Mekke', 69),
(2165, 25, 18, 'Kehf', 'وَلَبِثُوا ف۪ي كَـهْفِهِمْ ثَلٰثَ مِائَةٍ سِن۪ينَ وَازْدَادُوا تِسْعاً', 'Onlar mağaralarında üç yüzyıl ve buna ilaveten dokuz yıl kalmışlardır.', 15, 295, 110, 6423, 'Mağara', 'Mekke', 69),
(2166, 26, 18, 'Kehf', 'قُلِ اللّٰهُ اَعْلَمُ بِمَا لَبِثُواۚ لَهُ غَيْبُ السَّمٰوَاتِ وَالْاَرْضِۜ اَبْصِرْ بِه۪ وَاَسْمِـعْۜ مَا لَهُمْ مِنْ دُونِه۪ مِنْ وَلِيٍّۘ وَلَا يُشْرِكُ ف۪ي حُكْمِه۪ٓ اَحَداً', 'De ki: Ne kadar kaldıklarını Allah daha iyi bilir. Göklerin ve yerin gizli bilgisi O´na aittir. O´nun görmesi de, işitmesi de şâyanı hayrettir. Onların (göklerde ve yerde olanların), O´ndan başka bir yöneticisi yoktur. O, kendi hükümranlığına kimseyi ortak etmez.', 15, 295, 110, 6423, 'Mağara', 'Mekke', 69),
(2167, 27, 18, 'Kehf', 'وَاتْلُ مَٓا اُو۫حِيَ اِلَيْكَ مِنْ كِتَابِ رَبِّكَۚ لَا مُبَدِّلَ لِكَلِمَاتِه۪ وَلَنْ تَجِدَ مِنْ دُونِه۪ مُلْتَحَداً', 'Rabbinin Kitabı´ndan sana vahyedileni oku. Onun kelimelerini değiştirebilecek yoktur. O´ndan başka bir sığınak da bulamazsın.', 15, 295, 110, 6423, 'Mağara', 'Mekke', 69),
(2168, 28, 18, 'Kehf', 'وَاصْبِرْ نَفْسَكَ مَعَ الَّذ۪ينَ يَدْعُونَ رَبَّهُمْ بِالْغَدٰوةِ وَالْعَشِيِّ يُر۪يدُونَ وَجْهَهُ وَلَا تَعْدُ عَيْنَاكَ عَنْهُمْۚ تُر۪يدُ ز۪ينَةَ الْحَيٰوةِ الدُّنْيَا وَلَا تُطِـعْ مَنْ اَغْفَلْنَا قَلْبَهُ عَنْ ذِكْرِنَا وَاتَّبَعَ هَوٰيهُ وَكَانَ اَمْرُهُ فُرُطاً', 'Sabah akşam Rablerine, O´nun rızasını dileyerek dua edenlerle birlikte candan sebat et. Dünya hayatının süsünü isteyerek gözlerini onlardan çevirme. Kalbini bizi anmaktan gafil kıldığımız, kötü arzularına uymuş ve işi gücü aşırılık olan kimseye boyun eğme.', 15, 296, 110, 6423, 'Mağara', 'Mekke', 69),
(2169, 29, 18, 'Kehf', 'وَقُلِ الْحَقُّ مِنْ رَبِّكُمْ فَمَنْ شَٓاءَ فَلْيُؤْمِنْ وَمَنْ شَٓاءَ فَلْيَكْفُرْۙ اِنَّٓا اَعْتَدْنَا لِلظَّالِم۪ينَ نَاراًۙ اَحَاطَ بِهِمْ سُرَادِقُهَاۜ وَاِنْ يَسْتَغ۪يثُوا يُغَاثُوا بِمَٓاءٍ كَالْمُهْلِ يَشْوِي الْوُجُوهَۜ بِئْسَ الشَّرَابُۜ وَسَٓاءَتْ مُرْتَفَقاً', 'Ve de ki: Hak, Rabbinizdendir. Öyle ise dileyen iman etsin, dileyen inkâr etsin. Biz, zalimlere öyle bir cehennem hazırladık ki, onun duvarları kendilerini çepe çevre kuşatmıştır. (Susuzluktan) imdat dileyecek olsalar imdatlarına, erimiş maden gibi yüzleri haşlayan bir su ile cevap verilir. Ne fena bir içecek ve ne kötü bir kalma yeri!', 15, 296, 110, 6423, 'Mağara', 'Mekke', 69),
(2170, 30, 18, 'Kehf', 'اِنَّ الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ اِنَّا لَا نُض۪يعُ اَجْرَ مَنْ اَحْسَنَ عَمَلاًۚ', 'İman edip de güzel davranışlarda bulunanlar (bilmelidirler ki) biz, güzel işler yapanların ecrini zâyi etmeyiz.', 15, 296, 110, 6423, 'Mağara', 'Mekke', 69),
(2171, 31, 18, 'Kehf', 'اُو۬لٰٓئِكَ لَهُمْ جَنَّاتُ عَدْنٍ تَجْر۪ي مِنْ تَحْتِهِمُ الْاَنْهَارُ يُحَلَّوْنَ ف۪يهَا مِنْ اَسَاوِرَ مِنْ ذَهَبٍ وَيَلْبَسُونَ ثِيَاباً خُضْراً مِنْ سُنْدُسٍ وَاِسْتَبْرَقٍ مُتَّكِـ۪ٔينَ ف۪يهَا عَلَى الْاَرَٓائِكِۜ نِعْمَ الثَّوَابُۜ وَحَسُنَتْ مُرْتَفَقاً۟', 'İşte onlara, alt taraflarından ırmaklar akan Adn cennetleri vardır. Onlar Adn cennetlerinde tahtlar üzerine kurularak orada altın bileziklerle bezenecekler; ince ve kalın dîbâdan yeşil elbiseler giyecekler. Ne güzel karşılık ve ne güzel kalma yeri!', 15, 296, 110, 6423, 'Mağara', 'Mekke', 69),
(2172, 32, 18, 'Kehf', 'وَاضْرِبْ لَهُمْ مَثَلاً رَجُلَيْنِ جَعَلْنَا لِاَحَدِهِمَا جَنَّتَيْنِ مِنْ اَعْنَابٍ وَحَفَفْنَاهُمَا بِنَخْلٍ وَجَعَلْنَا بَيْنَهُمَا زَرْعاًۜ', 'Onlara, şu iki adamı misal olarak anlat: Bunlardan birine iki üzüm bağı vermiş, her ikisinin de etrafını hurmalarla donatmış, aralarında da ekinler bitirmiştik.', 15, 296, 110, 6423, 'Mağara', 'Mekke', 69),
(2173, 33, 18, 'Kehf', 'كِلْتَا الْجَنَّتَيْنِ اٰتَتْ اُكُلَهَا وَلَمْ تَظْلِمْ مِنْهُ شَيْـٔاًۙ وَفَجَّرْنَا خِلَالَهُمَا نَهَراًۙ', 'İki bağın ikisi de yemişlerini vermiş, hiçbirini eksik bırakmamıştı. İkisinin arasından bir de ırmak fışkırtmıştık.', 15, 296, 110, 6423, 'Mağara', 'Mekke', 69),
(2174, 34, 18, 'Kehf', 'وَكَانَ لَهُ ثَمَرٌۚ فَقَالَ لِصَاحِبِه۪ وَهُوَ يُحَاوِرُهُٓ اَنَا۬ اَكْثَرُ مِنْكَ مَالاً وَاَعَزُّ نَفَراً', 'Bu adamın başka geliri de vardı. Bu yüzden arkadaşıyla konuşurken ona şöyle dedi: «Ben, servetçe senden daha zenginim; insan sayısı bakımından da senden daha güçlüyüm.»', 15, 296, 110, 6423, 'Mağara', 'Mekke', 69),
(2175, 35, 18, 'Kehf', 'وَدَخَلَ جَنَّتَهُ وَهُوَ ظَالِمٌ لِنَفْسِه۪ۚ قَالَ مَٓا اَظُنُّ اَنْ تَب۪يدَ هٰذِه۪ٓ اَبَداًۙ', '(Böyle gurur ve kibirle) kendisine zulmederek bağına girdi. Şöyle dedi: «Bunun, hiçbir zaman yok olacağını sanmam.»', 15, 297, 110, 6423, 'Mağara', 'Mekke', 69),
(2176, 36, 18, 'Kehf', 'وَمَٓا اَظُنُّ السَّاعَةَ قَٓائِمَةًۙ وَلَئِنْ رُدِدْتُ اِلٰى رَبّ۪ي لَاَجِدَنَّ خَيْراً مِنْهَا مُنْقَلَباً', '«Kıyametin kopacağını da sanmıyorum. Şayet Rabbimin huzuruna götürülürsem, hiç şüphem yok ki, (orada) bundan daha hayırlı bir akıbet bulurum.»', 15, 297, 110, 6423, 'Mağara', 'Mekke', 69),
(2177, 37, 18, 'Kehf', 'قَالَ لَهُ صَاحِبُهُ وَهُوَ يُحَاوِرُهُٓ اَكَفَرْتَ بِالَّذ۪ي خَلَقَكَ مِنْ تُرَابٍ ثُمَّ مِنْ نُطْفَةٍ ثُمَّ سَوّٰيكَ رَجُلاًۜ', 'Karşılıklı konuşan arkadaşı ona hitaben: «Sen, dedi, seni topraktan, sonra nutfeden (spermadan) yaratan, daha sonra seni bir adam biçimine sokan Allah´ı inkâr mı ettin?»', 15, 297, 110, 6423, 'Mağara', 'Mekke', 69),
(2178, 38, 18, 'Kehf', 'لٰكِنَّا۬ هُوَ اللّٰهُ رَبّ۪ي وَلَٓا اُشْرِكُ بِرَبّ۪ٓي اَحَداً', '«Fakat O Allah benim Rabbimdir ve ben Rabbime hiçbir şeyi ortak koşmam.»', 15, 297, 110, 6423, 'Mağara', 'Mekke', 69),
(2179, 39, 18, 'Kehf', 'وَلَوْلَٓا اِذْ دَخَلْتَ جَنَّتَكَ قُلْتَ مَا شَٓاءَ اللّٰهُۙ لَا قُوَّةَ اِلَّا بِاللّٰهِۚ اِنْ تَرَنِ اَنَا۬ اَقَلَّ مِنْكَ مَالاً وَوَلَداًۚ', '«Bağına girdiğinde: Mâşâallah! Kuvvet yalnız Allah´ındır, deseydin ya! Eğer malca ve evlâtça beni kendinden güçsüz görüyorsan (şunu bil ki):»', 15, 297, 110, 6423, 'Mağara', 'Mekke', 69),
(2180, 40, 18, 'Kehf', 'فَعَسٰى رَبّ۪ٓي اَنْ يُؤْتِيَنِ خَيْراً مِنْ جَنَّتِكَ وَيُرْسِلَ عَلَيْهَا حُسْبَاناً مِنَ السَّمَٓاءِ فَتُصْبِحَ صَع۪يداً زَلَقاًۙ', '«Belki Rabbim bana, senin bağından daha iyisini verir; senin bağına ise gökten yıldırımlar gönderir de bağ kupkuru bir toprak haline gelir.»', 15, 297, 110, 6423, 'Mağara', 'Mekke', 69),
(2181, 41, 18, 'Kehf', 'اَوْ يُصْبِحَ مَٓاؤُ۬هَا غَوْراً فَلَنْ تَسْتَط۪يعَ لَهُ طَلَباً', '«Yahut, bağının suyu dibe çekilir de bir daha onu arayıp bulamazsın.»', 15, 297, 110, 6423, 'Mağara', 'Mekke', 69),
(2182, 42, 18, 'Kehf', 'وَاُح۪يطَ بِثَمَرِه۪ فَاَصْبَحَ يُقَلِّبُ كَفَّيْهِ عَلٰى مَٓا اَنْفَقَ ف۪يهَا وَهِيَ خَاوِيَةٌ عَلٰى عُرُوشِهَا وَيَقُولُ يَا لَيْتَن۪ي لَمْ اُشْرِكْ بِرَبّ۪ٓي اَحَداً', 'Derken onun serveti kuşatılıp yok edildi. Böylece, bağı uğruna yaptığı masraflardan ötürü ellerini oğuşturup kaldı. Bağın çardakları yere çökmüştü. «Ah, diyordu, keşke ben Rabbime hiçbir ortak koşmamış olsaydım!»', 15, 297, 110, 6423, 'Mağara', 'Mekke', 69),
(2183, 43, 18, 'Kehf', 'وَلَمْ تَكُنْ لَهُ فِئَةٌ يَنْصُرُونَهُ مِنْ دُونِ اللّٰهِ وَمَا كَانَ مُنْتَصِراًۜ', 'Kendisine Allah´tan başka yardım edecek destekçileri olmadığı gibi kendi kendini de kurtaracak güçte değildi.', 15, 297, 110, 6423, 'Mağara', 'Mekke', 69),
(2184, 44, 18, 'Kehf', 'هُنَالِكَ الْوَلَايَةُ لِلّٰهِ الْحَقِّۜ هُوَ خَيْرٌ ثَوَاباً وَخَيْرٌ عُقْباً۟', 'İşte burada yardım ve dostluk, Hak olan Allah´a mahsustur. Mükâfatı en iyi olan O, en güzel âkıbeti veren yine O´dur.', 15, 297, 110, 6423, 'Mağara', 'Mekke', 69),
(2185, 45, 18, 'Kehf', 'وَاضْرِبْ لَهُمْ مَثَلَ الْحَيٰوةِ الدُّنْيَا كَمَٓاءٍ اَنْزَلْنَاهُ مِنَ السَّمَٓاءِ فَاخْتَلَطَ بِه۪ نَبَاتُ الْاَرْضِ فَاَصْبَحَ هَش۪يماً تَذْرُوهُ الرِّيَاحُۜ وَكَانَ اللّٰهُ عَلٰى كُلِّ شَيْءٍ مُقْتَدِراً', 'Onlara şunu da misal göster: Dünya hayatı, gökten indirdiğimiz bir su gibidir ki, bu su sayesinde yeryüzünün bitkisi (önce gelişip) birbirine karışmış; arkasından rüzgârın savurduğu çerçöp haline gelmiştir. Allah, her şey üzerinde iktidar sahibidir.', 15, 297, 110, 6423, 'Mağara', 'Mekke', 69),
(2186, 46, 18, 'Kehf', 'اَلْمَالُ وَالْبَنُونَ ز۪ينَةُ الْحَيٰوةِ الدُّنْيَاۚ وَالْبَاقِيَاتُ الصَّالِحَاتُ خَيْرٌ عِنْدَ رَبِّكَ ثَوَاباً وَخَيْرٌ اَمَلاً', 'Servet ve oğullar, dünya hayatının süsüdür; ölümsüz olan iyi işler ise Rabbinin nezdinde hem sevapça daha hayırlı, hem de ümit bağlamaya daha lâyıktır.', 15, 298, 110, 6423, 'Mağara', 'Mekke', 69),
(2187, 47, 18, 'Kehf', 'وَيَوْمَ نُسَيِّرُ الْجِبَالَ وَتَرَى الْاَرْضَ بَارِزَةًۙ وَحَشَرْنَاهُمْ فَلَمْ نُغَادِرْ مِنْهُمْ اَحَداًۚ', '(Düşün) o günü ki, dağları yerinden götürürüz ve yeryüzünün çırılçıplak olduğunu görürsün. Hiçbirini bırakmaksızın onları (tüm ölüleri) mahşerde toplamış olacağız.', 15, 298, 110, 6423, 'Mağara', 'Mekke', 69),
(2188, 48, 18, 'Kehf', 'وَعُرِضُوا عَلٰى رَبِّكَ صَفاًّۜ لَقَدْ جِئْتُمُونَا كَمَا خَلَقْنَاكُمْ اَوَّلَ مَرَّةٍۘ بَلْ زَعَمْتُمْ اَلَّنْ نَجْعَلَ لَكُمْ مَوْعِداً', 'Ve hepsi sıra sıra Rabbinin huzuruna çıkarılmışlardır: Andolsun ki sizi ilk defasında yarattığımız şekilde bize geldiniz. Oysa size vâdedilenlerin tahakkuk edeceği bir zaman tayin etmediğimizi sanmıştınız, değil mi?', 15, 298, 110, 6423, 'Mağara', 'Mekke', 69),
(2189, 49, 18, 'Kehf', 'وَوُضِعَ الْكِتَابُ فَتَرَى الْمُجْرِم۪ينَ مُشْفِق۪ينَ مِمَّا ف۪يهِ وَيَقُولُونَ يَا وَيْلَتَنَا مَالِ هٰذَا الْكِتَابِ لَا يُغَادِرُ صَغ۪يرَةً وَلَا كَب۪يرَةً اِلَّٓا اَحْصٰيهَاۚ وَوَجَدُوا مَا عَمِلُوا حَاضِراًۜ وَلَا يَظْلِمُ رَبُّكَ اَحَداً۟', 'Kitap ortaya konmuştur: Suçluların, onda yazılı olanlardan korkmuş olduklarını görürsün. «Vay halimize! derler, bu nasıl kitapmış! Küçük büyük hiçbir şey bırakmaksızın (yaptıklarımızın) hepsini sayıp dökmüş!» Böylece yaptıklarını karşılarında bulmuşlardır. Senin Rabbin hiç kimseye zulmetmez.', 15, 298, 110, 6423, 'Mağara', 'Mekke', 69),
(2190, 50, 18, 'Kehf', 'وَاِذْ قُلْنَا لِلْمَلٰٓئِكَةِ اسْجُدُوا لِاٰدَمَ فَسَجَدُٓوا اِلَّٓا اِبْل۪يسَۜ كَانَ مِنَ الْجِنِّ فَفَسَقَ عَنْ اَمْرِ رَبِّه۪ۜ اَفَتَتَّخِذُونَهُ وَذُرِّيَّتَهُٓ اَوْلِيَٓاءَ مِنْ دُون۪ي وَهُمْ لَكُمْ عَدُوٌّۜ بِئْسَ لِلظَّالِم۪ينَ بَدَلاً', 'Hani biz meleklere: Âdem´e secde edin, demiştik; İblis hariç olmak üzere, onlar hemen secde ettiler. İblis cinlerdendi; Rabbinin emrinden dışarı çıktı. Şimdi siz, beni bırakıp da onu ve onun soyunu mu dost ediniyorsunuz? Oysa onlar sizin düşmanınızdır. Zalimler için bu ne fena bir değişmedir!', 15, 298, 110, 6423, 'Mağara', 'Mekke', 69),
(2191, 51, 18, 'Kehf', 'مَٓا اَشْهَدْتُهُمْ خَلْقَ السَّمٰوَاتِ وَالْاَرْضِ وَلَا خَلْقَ اَنْفُسِهِمْۖ وَمَا كُنْتُ مُتَّخِذَ الْمُضِلّ۪ينَ عَضُداً', 'Ben onları (İblis ve soyunu) ne göklerin ve yerin yaratılışına, ne de bizzat kendilerinin yaratılışına şahit tuttum. Ben yoldan çıkaranları yardımcı edinecek değilim.', 15, 298, 110, 6423, 'Mağara', 'Mekke', 69),
(2192, 52, 18, 'Kehf', 'وَيَوْمَ يَقُولُ نَادُوا شُرَكَٓاءِيَ الَّذ۪ينَ زَعَمْتُمْ فَدَعَوْهُمْ فَلَمْ يَسْتَج۪يبُوا لَهُمْ وَجَعَلْنَا بَيْنَهُمْ مَوْبِقاً', 'Yine o günü (düşünün ki, Allah, kâfirlere): Benim ortaklarım olduklarını ileri sürdüğünüz şeyleri çağırın! buyurur. Çağırmışlardır onları; fakat kendilerine cevap vermemişlerdir. Biz onların arasına tehlikeli bir uçurum koyduk.', 15, 298, 110, 6423, 'Mağara', 'Mekke', 69),
(2193, 53, 18, 'Kehf', 'وَرَاَ الْمُجْرِمُونَ النَّارَ فَظَنُّٓوا اَنَّهُمْ مُوَاقِعُوهَا وَلَمْ يَجِدُوا عَنْهَا مَصْرِفاً۟', 'Suçlular ateşi görür görmez, orayı boylayacaklarını iyice anladılar; ondan kurtuluş yolu da bulamadılar.', 15, 298, 110, 6423, 'Mağara', 'Mekke', 69),
(2194, 54, 18, 'Kehf', 'وَلَقَدْ صَرَّفْنَا ف۪ي هٰذَا الْقُرْاٰنِ لِلنَّاسِ مِنْ كُلِّ مَثَلٍۜ وَكَانَ الْاِنْسَانُ اَكْثَرَ شَيْءٍ جَدَلاً', 'Hakikaten biz bu Kur´an´da insanlar için her türlü misali sayıp dökmüşüzdür. Fakat tartışmaya en çok düşkün varlık insandır.', 15, 299, 110, 6423, 'Mağara', 'Mekke', 69),
(2195, 55, 18, 'Kehf', 'وَمَا مَنَعَ النَّاسَ اَنْ يُؤْمِنُٓوا اِذْ جَٓاءَهُمُ الْهُدٰى وَيَسْتَغْفِرُوا رَبَّهُمْ اِلَّٓا اَنْ تَأْتِيَهُمْ سُنَّةُ الْاَوَّل۪ينَ اَوْ يَأْتِيَهُمُ الْعَذَابُ قُبُلاً', 'Kendilerine hidayet geldiğinde insanları iman etmekten ve Rablerinden mağfiret talep etmekten alıkoyan şey, sadece, öncekilerinin başına gelenlerin kendi başlarına da gelmesini, yahut azabın göz göre göre kendilerine gelmesini beklemeleridir!', 15, 299, 110, 6423, 'Mağara', 'Mekke', 69),
(2196, 56, 18, 'Kehf', 'وَمَا نُرْسِلُ الْمُرْسَل۪ينَ اِلَّا مُبَشِّر۪ينَ وَمُنْذِر۪ينَۚ وَيُجَادِلُ الَّذ۪ينَ كَفَرُوا بِالْبَاطِلِ لِيُدْحِضُوا بِهِ الْحَقَّ وَاتَّخَذُٓوا اٰيَات۪ي وَمَٓا اُنْذِرُوا هُزُواً', 'Biz resulleri, sadece müjdeleyiciler ve uyarıcılar olarak göndeririz. Kâfir olanlar ise, hakkı bâtıla dayanarak ortadan kaldırmak için bâtıl yolla mücadele verirler. Onlar âyetlerimizi ve uyarıldıkları şeyleri alaya almışlardır.', 15, 299, 110, 6423, 'Mağara', 'Mekke', 69),
(2197, 57, 18, 'Kehf', 'وَمَنْ اَظْلَمُ مِمَّنْ ذُكِّرَ بِاٰيَاتِ رَبِّه۪ فَاَعْرَضَ عَنْهَا وَنَسِيَ مَا قَدَّمَتْ يَدَاهُۜ اِنَّا جَعَلْنَا عَلٰى قُلُوبِهِمْ اَكِنَّةً اَنْ يَفْقَهُوهُ وَف۪ٓي اٰذَانِهِمْ وَقْراًۜ وَاِنْ تَدْعُهُمْ اِلَى الْهُدٰى فَلَنْ يَهْتَدُٓوا اِذاً اَبَداً', 'Kendisine Rabbinin âyetleri hatırlatılıp da ona sırt çevirenden, kendi elleriyle yaptığını unutandan daha zalim kim vardır! Biz onların kalplerine, bunu anlamalarına engel olan bir ağırlık, kulaklarına da sağırlık verdik. Sen onları hidayete çağırsan da artık ebediyen hidayete eremeyeceklerdir.', 15, 299, 110, 6423, 'Mağara', 'Mekke', 69),
(2198, 58, 18, 'Kehf', 'وَرَبُّكَ الْغَفُورُ ذُوالرَّحْمَةِۜ لَوْ يُؤَاخِذُهُمْ بِمَا كَسَبُوا لَعَجَّلَ لَهُمُ الْعَذَابَۜ بَلْ لَهُمْ مَوْعِدٌ لَنْ يَجِدُوا مِنْ دُونِه۪ مَوْئِلاً', 'Senin, bağışı bol olan Rabbin merhamet sahibidir; şayet yaptıkları yüzünden onları (hemen) muaheze edecek olsaydı, onlara azabı çarçabuk verirdi. Fakat kendilerine tanınmış belli bir süre vardır ki, artık bundan kaçıp kurtulacakları bir sığınak bulamayacaklardır.', 15, 299, 110, 6423, 'Mağara', 'Mekke', 69),
(2199, 59, 18, 'Kehf', 'وَتِلْكَ الْقُرٰٓى اَهْلَكْنَاهُمْ لَمَّا ظَلَمُوا وَجَعَلْنَا لِمَهْلِكِهِمْ مَوْعِداً۟', 'İşte şu ülkeler; zulmettikleri zaman onları helâk ettik. Onları helâk etmek için de belli bir zaman tayin etmiştik.', 15, 299, 110, 6423, 'Mağara', 'Mekke', 69),
(2200, 60, 18, 'Kehf', 'وَاِذْ قَالَ مُوسٰى لِفَتٰيهُ لَٓا اَبْرَحُ حَتّٰٓى اَبْلُغَ مَجْمَعَ الْبَحْرَيْنِ اَوْ اَمْضِيَ حُقُباً', 'Bir vakit Musa genç adamına demişti ki: «Durup dinlenmeyeceğim; tâ iki denizin birleştiği yere kadar varacağım, yahut senelerce yürüyeceğim.»', 15, 299, 110, 6423, 'Mağara', 'Mekke', 69),
(2201, 61, 18, 'Kehf', 'فَلَمَّا بَلَغَا مَجْمَعَ بَيْنِهِمَا نَسِيَا حُوتَهُمَا فَاتَّخَذَ سَب۪يلَهُ فِي الْبَحْرِ سَرَباً', 'Her ikisi, iki denizin birleştiği yere varınca balıklarını unuttular. Balık, denizde bir yol tutup gitmişti.', 15, 299, 110, 6423, 'Mağara', 'Mekke', 69),
(2202, 62, 18, 'Kehf', 'فَلَمَّا جَاوَزَا قَالَ لِفَتٰيهُ اٰتِنَا غَدَٓاءَنَاۘ لَقَدْ لَق۪ينَا مِنْ سَفَرِنَا هٰذَا نَصَباً', '(Buluşma yerlerini) geçip gittiklerinde Musa genç adamına: Kuşluk yemeğimizi getir bize. Hakikaten şu yolculuğumuz yüzünden başımıza (epeyce) sıkıntı geldi, dedi.', 15, 300, 110, 6423, 'Mağara', 'Mekke', 69),
(2203, 63, 18, 'Kehf', 'قَالَ اَرَاَيْتَ اِذْ اَوَيْنَٓا اِلَى الصَّخْرَةِ فَاِنّ۪ي نَس۪يتُ الْحُوتَۘ وَمَٓا اَنْسَان۪يهُ اِلَّا الشَّيْطَانُ اَنْ اَذْكُرَهُۚ وَاتَّخَذَ سَب۪يلَهُ فِي الْبَحْرِۗ عَجَباً', '(Genç adam:) Gördün mü! dedi, kayaya sığındığımız sırada balığı unuttum. Onu hatırlamamı bana şeytandan başkası unutturmadı. O, şaşılacak bir şekilde denizde yolunu tutup gitmişti.', 15, 300, 110, 6423, 'Mağara', 'Mekke', 69),
(2204, 64, 18, 'Kehf', 'قَالَ ذٰلِكَ مَا كُنَّا نَبْغِۗ فَارْتَدَّا عَلٰٓى اٰثَارِهِمَا قَصَصاًۙ', 'Musa: İşte aradığımız o idi, dedi. Hemen izlerinin üzerine geri döndüler.', 15, 300, 110, 6423, 'Mağara', 'Mekke', 69),
(2205, 65, 18, 'Kehf', 'فَوَجَدَا عَبْداً مِنْ عِبَادِنَٓا اٰتَيْنَاهُ رَحْمَةً مِنْ عِنْدِنَا وَعَلَّمْنَاهُ مِنْ لَدُنَّا عِلْماً', 'Derken, kullarımızdan bir kul buldular ki, ona katımızdan bir rahmet (vahiy ve peygamberlik) vermiş, yine ona tarafımızdan bir ilim öğretmiştik.', 15, 300, 110, 6423, 'Mağara', 'Mekke', 69),
(2206, 66, 18, 'Kehf', 'قَالَ لَهُ مُوسٰى هَلْ اَتَّبِعُكَ عَلٰٓى اَنْ تُعَلِّمَنِ مِمَّا عُلِّمْتَ رُشْداً', 'Musa ona: Sana öğretilenden, bana, doğruyu bulmama yardım edecek bir bilgi öğretmen için sana tâbi olayım mı? dedi.', 15, 300, 110, 6423, 'Mağara', 'Mekke', 69),
(2207, 67, 18, 'Kehf', 'قَالَ اِنَّكَ لَنْ تَسْتَط۪يعَ مَعِيَ صَبْراً', 'Dedi ki: Doğrusu sen benimle beraberliğe sabredemezsin.', 15, 300, 110, 6423, 'Mağara', 'Mekke', 69),
(2208, 68, 18, 'Kehf', 'وَكَيْفَ تَصْبِرُ عَلٰى مَا لَمْ تُحِطْ بِه۪ خُبْراً', '(İç yüzünü) kavrayamadığın bir bilgiye nasıl sabredersin?', 15, 300, 110, 6423, 'Mağara', 'Mekke', 69),
(2209, 69, 18, 'Kehf', 'قَالَ سَتَجِدُن۪ٓي اِنْ شَٓاءَ اللّٰهُ صَابِراً وَلَٓا اَعْص۪ي لَكَ اَمْراً', 'Musa: İnşaallah, dedi, sen beni sabreder bulacaksın. Senin emrine de karşı gelmem.', 15, 300, 110, 6423, 'Mağara', 'Mekke', 69),
(2210, 70, 18, 'Kehf', 'قَالَ فَاِنِ اتَّبَعْتَن۪ي فَلَا تَسْـَٔلْن۪ي عَنْ شَيْءٍ حَتّٰٓى اُحْدِثَ لَكَ مِنْهُ ذِكْراً۟', '(O kul:) Eğer bana tâbi olursan, sana o konuda bilgi verinceye kadar hiçbir şey hakkında bana soru sorma! dedi.', 15, 300, 110, 6423, 'Mağara', 'Mekke', 69),
(2211, 71, 18, 'Kehf', 'فَانْطَلَقَا۠ حَتّٰٓى اِذَا رَكِبَا فِي السَّف۪ينَةِ خَرَقَهَاۜ قَالَ اَخَرَقْتَهَا لِتُغْرِقَ اَهْلَهَاۚ لَقَدْ جِئْتَ شَيْـٔاً اِمْراً', 'Bunun üzerine yürüdüler. Nihayet gemiye bindikleri zaman o (Hızır) gemiyi deldi. Musa: Halkını boğmak için mi onu deldin? Gerçekten sen (ziyanı) büyük bir iş yaptın! dedi.', 15, 300, 110, 6423, 'Mağara', 'Mekke', 69),
(2212, 72, 18, 'Kehf', 'قَالَ اَلَمْ اَقُلْ اِنَّكَ لَنْ تَسْتَط۪يعَ مَعِيَ صَبْراً', '(Hızır:) Ben sana, benimle beraberliğe sabredemezsin, demedim mi? dedi.', 15, 300, 110, 6423, 'Mağara', 'Mekke', 69),
(2213, 73, 18, 'Kehf', 'قَالَ لَا تُؤَاخِذْن۪ي بِمَا نَس۪يتُ وَلَا تُرْهِقْن۪ي مِنْ اَمْر۪ي عُسْراً', 'Musa: Unuttuğum şeyden dolayı beni muaheze etme; işimde bana güçlük çıkarma, dedi.', 15, 300, 110, 6423, 'Mağara', 'Mekke', 69),
(2214, 74, 18, 'Kehf', 'فَانْطَلَقَا۠ حَتّٰٓى اِذَا لَقِيَا غُلَاماً فَقَتَلَهُۙ قَالَ اَقَتَلْتَ نَفْساً زَكِيَّةً بِغَيْرِ نَفْسٍۜ لَقَدْ جِئْتَ شَيْـٔاً نُكْراً', 'Yine yürüdüler. Nihayet bir erkek çocuğa rastladıklarında (Hızır) hemen onu öldürdü. Musa dedi ki: Tertemiz bir canı, bir can karşılığı olmaksızın (kimseyi öldürmediği halde) katlettin ha! Gerçekten sen fena bir şey yaptın!', 15, 300, 110, 6423, 'Mağara', 'Mekke', 69),
(2215, 75, 18, 'Kehf', 'قَالَ اَلَمْ اَقُلْ لَكَ اِنَّكَ لَنْ تَسْتَط۪يعَ مَعِيَ صَبْراً', '(Hızır:) Ben sana, benimle beraber (olacaklara) sabredemezsin, demedim mi? dedi.', 16, 301, 110, 6423, 'Mağara', 'Mekke', 69),
(2216, 76, 18, 'Kehf', 'قَالَ اِنْ سَاَلْتُكَ عَنْ شَيْءٍ بَعْدَهَا فَلَا تُصَاحِبْن۪يۚ قَدْ بَلَغْتَ مِنْ لَدُنّ۪ي عُذْراً', 'Musa: Eğer, dedi, bundan sonra sana bir şey sorarsam artık bana arkadaşlık etme. Hakikaten benim tarafımdan (ileri sürebilecek) mazeretin sonuna ulaştın.', 16, 301, 110, 6423, 'Mağara', 'Mekke', 69),
(2217, 77, 18, 'Kehf', 'فَانْطَلَقَا۠ حَتّٰٓى اِذَٓا اَتَيَٓا اَهْلَ قَرْيَةٍۨ اسْتَطْعَمَٓا اَهْلَهَا فَاَبَوْا اَنْ يُضَيِّفُوهُمَا فَوَجَدَا ف۪يهَا جِدَاراً يُر۪يدُ اَنْ يَنْقَضَّ فَاَقَامَهُۜ قَالَ لَوْ شِئْتَ لَتَّخَذْتَ عَلَيْهِ اَجْراً', 'Yine yürüdüler. Nihayet bir köy halkına varıp onlardan yiyecek istediler. Ancak köy halkı onları misafir etmekten kaçındılar. Derken orada yıkılmak üzere bulunan bir duvarla karşılaştılar. (Hızır) hemen onu doğrulttu. Musa: Dileseydin, elbet buna karşı bir ücret alırdın, dedi.', 16, 301, 110, 6423, 'Mağara', 'Mekke', 69),
(2218, 78, 18, 'Kehf', 'قَالَ هٰذَا فِرَاقُ بَيْن۪ي وَبَيْنِكَۚ سَاُنَبِّئُكَ بِتَأْو۪يلِ مَا لَمْ تَسْتَطِـعْ عَلَيْهِ صَبْراً', '(Hızır) şöyle dedi: «İşte bu, benimle senin aramızın ayrılmasıdır. Şimdi sana, sabredemediğin şeylerin içyüzünü haber vereceğim.»', 16, 301, 110, 6423, 'Mağara', 'Mekke', 69),
(2219, 79, 18, 'Kehf', 'اَمَّا السَّف۪ينَةُ فَكَانَتْ لِمَسَاك۪ينَ يَعْمَلُونَ فِي الْبَحْرِ فَاَرَدْتُ اَنْ اَع۪يبَهَا وَكَانَ وَرَٓاءَهُمْ مَلِكٌ يَأْخُذُ كُلَّ سَف۪ينَةٍ غَصْباً', '«Gemi var ya, o, denizde çalışan yoksul kimselerindi. Onu kusurlu kılmak istedim. (Çünkü) onların arkasında, her (sağlam) gemiyi gasbetmekte olan bir kral vardı.»', 16, 301, 110, 6423, 'Mağara', 'Mekke', 69),
(2220, 80, 18, 'Kehf', 'وَاَمَّا الْغُلَامُ فَكَانَ اَبَوَاهُ مُؤْمِنَيْنِ فَخَش۪ينَٓا اَنْ يُرْهِقَهُمَا طُغْيَاناً وَكُفْراًۚ', '«Erkek çocuğa gelince, onun ana babası, mümin kimselerdi. Bunun için (çocuğun) onları azgınlık ve nankörlüğe boğmasından korktuk.»', 16, 301, 110, 6423, 'Mağara', 'Mekke', 69),
(2221, 81, 18, 'Kehf', 'فَاَرَدْنَٓا اَنْ يُبْدِلَهُمَا رَبُّهُمَا خَيْراً مِنْهُ زَكٰوةً وَاَقْرَبَ رُحْماً', '(Devam etti:) «Böylece istedik ki, Rableri onun yerine kendilerine, ondan daha temiz ve daha merhametlisini versin.»', 16, 301, 110, 6423, 'Mağara', 'Mekke', 69);
INSERT INTO `Ayetler` (`id`, `AyetNo`, `SureNo`, `SureAdi`, `Metin`, `Mana`, `Cuzu`, `Sayfasi`, `AyetSayisi`, `HarfSayisi`, `AdAnlami`, `IndigiYer`, `InisSirasi`) VALUES
(2222, 82, 18, 'Kehf', 'وَاَمَّا الْجِدَارُ فَكَانَ لِغُلَامَيْنِ يَت۪يمَيْنِ فِي الْمَد۪ينَةِ وَكَانَ تَحْتَهُ كَنْزٌ لَهُمَا وَكَانَ اَبُوهُمَا صَالِحاًۚ فَاَرَادَ رَبُّكَ اَنْ يَبْلُغَٓا اَشُدَّهُمَا وَيَسْتَخْرِجَا كَنْزَهُمَاۗ رَحْمَةً مِنْ رَبِّكَۚ وَمَا فَعَلْتُهُ عَنْ اَمْر۪يۜ ذٰلِكَ تَأْو۪يلُ مَا لَمْ تَسْطِـعْ عَلَيْهِ صَبْراًۜ۟', '«Duvara gelince, şehirde iki yetim çocuğun idi; altında da onlara ait bir hazine vardı; babaları ise iyi bir kimse idi. Rabbin istedi ki, o iki çocuk güçlü çağlarına erişsinler ve Rabbinden bir rahmet olarak hazinelerini çıkarsınlar. Ben bunu da kendiliğimden yapmadım. İşte, hakkında sabredemediğin şeylerin iç yüzü budur.»', 16, 301, 110, 6423, 'Mağara', 'Mekke', 69),
(2223, 83, 18, 'Kehf', 'وَيَسْـَٔلُونَكَ عَنْ ذِي الْقَرْنَيْنِۜ قُلْ سَاَتْلُوا عَلَيْكُمْ مِنْهُ ذِكْراًۜ', '(Resûlüm!) Sana Zülkarneyn hakkında soru sorarlar. De ki: Size ondan bir hatıra okuyacağım.', 16, 301, 110, 6423, 'Mağara', 'Mekke', 69),
(2224, 84, 18, 'Kehf', 'اِنَّا مَكَّنَّا لَهُ فِي الْاَرْضِ وَاٰتَيْنَاهُ مِنْ كُلِّ شَيْءٍ سَبَباًۙ', 'Gerçekten biz onu yeryüzünde iktidar ve kudret sahibi kıldık, ona (muhtaç olduğu) her şey için bir sebep (bir vasıta ve yol) verdik.', 16, 302, 110, 6423, 'Mağara', 'Mekke', 69),
(2225, 85, 18, 'Kehf', 'فَاَتْبَعَ سَبَباً', 'O da bir yol tutup gitti.', 16, 302, 110, 6423, 'Mağara', 'Mekke', 69),
(2226, 86, 18, 'Kehf', 'حَتّٰٓى اِذَا بَلَغَ مَغْرِبَ الشَّمْسِ وَجَدَهَا تَغْرُبُ ف۪ي عَيْنٍ حَمِئَةٍ وَوَجَدَ عِنْدَهَا قَوْماًۜ قُلْنَا يَا ذَا الْقَرْنَيْنِ اِمَّٓا اَنْ تُعَذِّبَ وَاِمَّٓا اَنْ تَتَّخِذَ ف۪يهِمْ حُسْناً', 'Nihayet güneşin battığı yere varınca, onu kara bir balçıkta batar buldu. Onun yanında (orada) bir kavme rastladı. Bunun üzerine biz: Ey Zülkarneyn! Onlara ya azap edecek veya haklarında iyilik etme yolunu seçeceksin, dedik.', 16, 302, 110, 6423, 'Mağara', 'Mekke', 69),
(2227, 87, 18, 'Kehf', 'قَالَ اَمَّا مَنْ ظَلَمَ فَسَوْفَ نُعَذِّبُهُ ثُمَّ يُرَدُّ اِلٰى رَبِّه۪ فَيُعَذِّبُهُ عَذَاباً نُكْراً', 'O, şöyle dedi: «Haksızlık edeni cezalandıracağız; sonra o, Rabbine gönderilecek; sonra Allah da ona korkunç bir azap uygulayacak.»', 16, 302, 110, 6423, 'Mağara', 'Mekke', 69),
(2228, 88, 18, 'Kehf', 'وَاَمَّا مَنْ اٰمَنَ وَعَمِلَ صَالِحاً فَلَهُ جَزَٓاءًۨ الْحُسْنٰىۚ وَسَنَقُولُ لَهُ مِنْ اَمْرِنَا يُسْراًۜ', '«İman edip de iyi davranan kimseye gelince, onun için de en güzel bir karşılık vardır. Ve buyruğumuzdan, ona kolay olanını söyleyeceğiz.»', 16, 302, 110, 6423, 'Mağara', 'Mekke', 69),
(2229, 89, 18, 'Kehf', 'ثُمَّ اَتْـبَعَ سَبَباً', 'Sonra yine bir yol tuttu.', 16, 302, 110, 6423, 'Mağara', 'Mekke', 69),
(2230, 90, 18, 'Kehf', 'حَتّٰٓى اِذَا بَلَغَ مَطْلِعَ الشَّمْسِ وَجَدَهَا تَطْلُعُ عَلٰى قَوْمٍ لَمْ نَجْعَلْ لَهُمْ مِنْ دُونِهَا سِتْراًۙ', 'Nihayet güneşin doğduğu yere ulaşınca, onu öyle bir kavim üzerine doğar buldu ki, onlar için güneşe karşı bir örtü yapmamıştık.', 16, 302, 110, 6423, 'Mağara', 'Mekke', 69),
(2231, 91, 18, 'Kehf', 'كَذٰلِكَۜ وَقَدْ اَحَطْنَا بِمَا لَدَيْهِ خُبْراً', 'İşte böylece onunla ilgili her şeyden haberdardık.', 16, 302, 110, 6423, 'Mağara', 'Mekke', 69),
(2232, 92, 18, 'Kehf', 'ثُمَّ اَتْبَعَ سَبَباً', 'Sonra yine bir yol tuttu.', 16, 302, 110, 6423, 'Mağara', 'Mekke', 69),
(2233, 93, 18, 'Kehf', 'حَتّٰٓى اِذَا بَلَغَ بَيْنَ السَّدَّيْنِ وَجَدَ مِنْ دُونِهِمَا قَوْماًۙ لَا يَكَادُونَ يَفْقَهُونَ قَوْلاً', 'Nihayet iki dağ arasına ulaştığında onların önünde, hemen hiçbir sözü anlamayan bir kavim buldu.', 16, 302, 110, 6423, 'Mağara', 'Mekke', 69),
(2234, 94, 18, 'Kehf', 'قَالُوا يَا ذَا الْقَرْنَيْنِ اِنَّ يَأْجُوجَ وَمَأْجُوجَ مُفْسِدُونَ فِي الْاَرْضِ فَهَلْ نَجْعَلُ لَكَ خَرْجاً عَلٰٓى اَنْ تَجْعَلَ بَيْنَنَا وَبَيْنَهُمْ سَداًّ', 'Dediler ki: Ey Zülkarneyn! Bu memlekette Ye´cûc ve Me´cûc bozgunculuk yapmaktadırlar. Bizimle onlar arasında bir sed yapman için sana bir vergi verelim mi?', 16, 302, 110, 6423, 'Mağara', 'Mekke', 69),
(2235, 95, 18, 'Kehf', 'قَالَ مَا مَكَّنّ۪ي ف۪يهِ رَبّ۪ي خَيْرٌ فَاَع۪ينُون۪ي بِقُوَّةٍ اَجْعَلْ بَيْنَكُمْ وَبَيْنَهُمْ رَدْماًۙ', 'Dedi ki: «Rabbimin beni içinde bulundurduğu nimet ve kudret daha hayırlıdır. Siz bana kuvvetinizle destek olun da, sizinle onlar arasına aşılmaz bir engel yapayım.»', 16, 302, 110, 6423, 'Mağara', 'Mekke', 69),
(2236, 96, 18, 'Kehf', 'اٰتُون۪ي زُبَرَ الْحَد۪يدِۜ حَتّٰٓى اِذَا سَاوٰى بَيْنَ الصَّدَفَيْنِ قَالَ انْفُخُواۜ حَتّٰٓى اِذَا جَعَلَهُ نَاراًۙ قَالَ اٰتُون۪ٓي اُفْرِغْ عَلَيْهِ قِطْراًۜ', '«Bana, demir kütleleri getirin.» Nihayet dağın iki yanı arasını aynı seviyeye getirince (vadiyi doldurunca): «Üfleyin (körükleyin)!» dedi. Artık onu kor haline sokunca: «Getirin bana, üzerine bir miktar erimiş bakır dökeyim» dedi.', 16, 302, 110, 6423, 'Mağara', 'Mekke', 69),
(2237, 97, 18, 'Kehf', 'فَمَا اسْطَاعُٓوا اَنْ يَظْهَرُوهُ وَمَا اسْتَطَاعُوا لَهُ نَقْباً', 'Bu sebeple onu ne aşmaya muktedir oldular ne de onu delebildiler.', 16, 302, 110, 6423, 'Mağara', 'Mekke', 69),
(2238, 98, 18, 'Kehf', 'قَالَ هٰذَا رَحْمَةٌ مِنْ رَبّ۪يۚ فَاِذَا جَٓاءَ وَعْدُ رَبّ۪ي جَعَلَهُ دَكَّٓاءَۚ وَكَانَ وَعْدُ رَبّ۪ي حَقاًّۜ', 'Zülkarneyn: Bu, Rabbimden bir rahmettir. Fakat Rabbimin vâdi gelince, O, bunu yerle bir eder. Rabbimin vâdi haktır, dedi.', 16, 303, 110, 6423, 'Mağara', 'Mekke', 69),
(2239, 99, 18, 'Kehf', 'وَتَرَكْنَا بَعْضَهُمْ يَوْمَئِذٍ يَمُوجُ ف۪ي بَعْضٍ وَنُفِخَ فِي الصُّورِ فَجَمَعْنَاهُمْ جَمْعاًۙ', 'O gün (kıyamet gününde bakarsın ki) biz onları, birbirine çarparak çalkalanır bir halde bırakmışızdır; Sûr´a da üfürülmüş, böylece onları bütünüyle bir araya getirmişizdir.', 16, 303, 110, 6423, 'Mağara', 'Mekke', 69),
(2240, 100, 18, 'Kehf', 'وَعَرَضْنَا جَهَنَّمَ يَوْمَئِذٍ لِلْكَافِر۪ينَ عَرْضاًۙ', '(100-101) Ve, gözleri beni görmeye kapalı bulunan, kulak vermeye de tahammül edemez olan kâfirleri o gün cehennemle yüz yüze getirmişizdir.', 16, 303, 110, 6423, 'Mağara', 'Mekke', 69),
(2241, 101, 18, 'Kehf', 'اَلَّذ۪ينَ كَانَتْ اَعْيُنُهُمْ ف۪ي غِطَٓاءٍ عَنْ ذِكْر۪ي وَكَانُوا لَا يَسْتَط۪يعُونَ سَمْعاً۟', '(100-101) Ve, gözleri beni görmeye kapalı bulunan, kulak vermeye de tahammül edemez olan kâfirleri o gün cehennemle yüz yüze getirmişizdir.', 16, 303, 110, 6423, 'Mağara', 'Mekke', 69),
(2242, 102, 18, 'Kehf', 'اَفَحَسِبَ الَّذ۪ينَ كَفَرُٓوا اَنْ يَتَّخِذُوا عِبَاد۪ي مِنْ دُون۪ٓي اَوْلِيَٓاءَۜ اِنَّٓا اَعْتَدْنَا جَهَنَّمَ لِلْكَافِر۪ينَ نُزُلاً', 'Kâfirler, beni bırakıp da kullarımı dostlar edineceklerini mi sandılar? Biz cehennemi kâfirlere bir konak olarak hazırladık.', 16, 303, 110, 6423, 'Mağara', 'Mekke', 69),
(2243, 103, 18, 'Kehf', 'قُلْ هَلْ نُنَبِّئُكُمْ بِالْاَخْسَر۪ينَ اَعْمَالاًۜ', 'De ki: Size, (yaptıkları) işler bakımından en çok ziyana uğrayanları bildirelim mi?', 16, 303, 110, 6423, 'Mağara', 'Mekke', 69),
(2244, 104, 18, 'Kehf', 'اَلَّذ۪ينَ ضَلَّ سَعْيُهُمْ فِي الْحَيٰوةِ الدُّنْيَا وَهُمْ يَحْسَبُونَ اَنَّهُمْ يُحْسِنُونَ صُنْعاً', '(Bunlar;) iyi işler yaptıklarını sandıkları halde, dünya hayatında çabaları boşa giden kimselerdir.', 16, 303, 110, 6423, 'Mağara', 'Mekke', 69),
(2245, 105, 18, 'Kehf', 'اُو۬لٰٓئِكَ الَّذ۪ينَ كَفَرُوا بِاٰيَاتِ رَبِّهِمْ وَلِقَٓائِه۪ فَحَبِطَتْ اَعْمَالُهُمْ فَلَا نُق۪يمُ لَهُمْ يَوْمَ الْقِيٰمَةِ وَزْناً', 'İşte onlar, Rablerinin âyetlerini ve O´na kavuşmayı inkâr eden, bu yüzden amelleri boşa giden kimselerdir ki, biz onlar için kıyamet gününde hiçbir ölçü tutmayacağız.', 16, 303, 110, 6423, 'Mağara', 'Mekke', 69),
(2246, 106, 18, 'Kehf', 'ذٰلِكَ جَزَٓاؤُ۬هُمْ جَهَنَّمُ بِمَا كَفَرُوا وَاتَّخَذُٓوا اٰيَات۪ي وَرُسُل۪ي هُزُواً', 'İşte, inkâr ettikleri, âyetlerimi ve resûllerimi alaya aldıkları için onların cezası cehennemdir.', 16, 303, 110, 6423, 'Mağara', 'Mekke', 69),
(2247, 107, 18, 'Kehf', 'اِنَّ الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ كَانَتْ لَهُمْ جَنَّاتُ الْفِرْدَوْسِ نُزُلاًۙ', 'İman edip iyi davranışlarda bulunanlara gelince, onlar için makam olarak Firdevs cennetleri vardır.', 16, 303, 110, 6423, 'Mağara', 'Mekke', 69),
(2248, 108, 18, 'Kehf', 'خَالِد۪ينَ ف۪يهَا لَا يَبْغُونَ عَنْهَا حِوَلاً', 'Orada ebedî kalacaklardır. Oradan hiç ayrılmak istemezler.', 16, 303, 110, 6423, 'Mağara', 'Mekke', 69),
(2249, 109, 18, 'Kehf', 'قُلْ لَوْ كَانَ الْبَحْرُ مِدَاداً لِكَلِمَاتِ رَبّ۪ي لَنَفِدَ الْبَحْرُ قَبْلَ اَنْ تَنْفَدَ كَلِمَاتُ رَبّ۪ي وَلَوْ جِئْنَا بِمِثْلِه۪ مَدَداً', 'De ki: Rabbimin sözleri için derya mürekkep olsa ve bir o kadar da ilâve getirsek dahi, Rabbimin sözleri bitmeden önce deniz tükenecektir.', 16, 303, 110, 6423, 'Mağara', 'Mekke', 69),
(2250, 110, 18, 'Kehf', 'قُلْ اِنَّـمَٓا اَنَا۬ بَشَرٌ مِثْلُكُمْ يُوحٰٓى اِلَيَّ اَنَّـمَٓا اِلٰهُكُمْ اِلٰهٌ وَاحِدٌۚ فَمَنْ كَانَ يَرْجُوا لِقَٓاءَ رَبِّه۪ فَلْيَعْمَلْ عَمَلاً صَالِحاً وَلَا يُشْرِكْ بِعِبَادَةِ رَبِّه۪ٓ اَحَداً', 'De ki: Ben, yalnızca sizin gibi bir beşerim. (Şu var ki) bana, İlâh´ınızın, sadece bir İlâh olduğu vahyolunuyor. Artık her kim Rabbine kavuşmayı umuyorsa, iyi iş yapsın ve Rabbine ibadette hiçbir şeyi ortak koşmasın.', 16, 303, 110, 6423, 'Mağara', 'Mekke', 69),
(2251, 1, 19, 'Meryem', 'كٓـهٰيٰعٓصٓۜ', 'Kâf. Hâ. Yâ. Ayn. Sâd.', 16, 304, 98, 3835, 'Meryem', 'Mekke', 44),
(2252, 2, 19, 'Meryem', 'ذِكْرُ رَحْمَتِ رَبِّكَ عَبْدَهُ زَكَرِيَّاۚ', '(Bu,) Rabbinin, Zekeriyya kuluna rahmetinin anılmasıdır.', 16, 304, 98, 3835, 'Meryem', 'Mekke', 44),
(2253, 3, 19, 'Meryem', 'اِذْ نَادٰى رَبَّهُ نِدَٓاءً خَفِياًّ', 'Hani o, gizli bir sesle Rabbine niyaz etmişti:', 16, 304, 98, 3835, 'Meryem', 'Mekke', 44),
(2254, 4, 19, 'Meryem', 'قَالَ رَبِّ اِنّ۪ي وَهَنَ الْعَظْمُ مِنّ۪ي وَاشْتَعَلَ الرَّأْسُ شَيْباً وَلَمْ اَكُنْ بِدُعَٓائِكَ رَبِّ شَقِياًّ', 'Rabbim! dedi, benden (vücudumdan), kemiklerim zayıfladı, saçım başım ağardı. Ve ben, Rabbim, sana (ettiğim) dua sayesinde hiç bedbaht olmadım.', 16, 304, 98, 3835, 'Meryem', 'Mekke', 44),
(2255, 5, 19, 'Meryem', 'وَاِنّ۪ي خِفْتُ الْمَوَالِيَ مِنْ وَرَٓاء۪ي وَكَانَتِ امْرَاَت۪ي عَاقِراً فَهَبْ ل۪ي مِنْ لَدُنْكَ وَلِياًّۚ', 'Doğrusu ben, arkamdan iş başına geçecek olan yakınlarımdan endişe ediyorum. Karım da kısırdır. Tarafından bana bir veli (oğul) ver.', 16, 304, 98, 3835, 'Meryem', 'Mekke', 44),
(2256, 6, 19, 'Meryem', 'يَرِثُن۪ي وَيَرِثُ مِنْ اٰلِ يَعْقُوبَۗ وَاجْعَلْهُ رَبِّ رَضِياًّ', 'Ki o bana vâris olsun; Ya´kub hanedanına da vâris olsun. Rabbim, onu rızana lâyık kıl!', 16, 304, 98, 3835, 'Meryem', 'Mekke', 44),
(2257, 7, 19, 'Meryem', 'يَا زَكَرِيَّٓا اِنَّـا نُـبَشِّرُكَ بِغُـلَامٍۨ اسْـمُهُ يَحْيٰىۙ لَمْ نَجْعَلْ لَهُ مِنْ قَبْلُ سَمِياًّ', '(Allah şöyle buyurdu:) Ey Zekeriyya! Biz sana bir oğul müjdeleriz ki, onun adı Yahya´dır. Daha önce ona kimseyi adaş yapmadık.', 16, 304, 98, 3835, 'Meryem', 'Mekke', 44),
(2258, 8, 19, 'Meryem', 'قَالَ رَبِّ اَنّٰى يَكُونُ ل۪ي غُلَامٌ وَكَانَتِ امْرَاَت۪ي عَاقِراً وَقَدْ بَلَغْتُ مِنَ الْكِبَرِ عِتِياًّ', 'Zekeriyya: Rabbim! dedi, karım kısır olduğu, ben de ihtiyarlığın son sınırına vardığım halde, benim nasıl oğlum olabilir?', 16, 304, 98, 3835, 'Meryem', 'Mekke', 44),
(2259, 9, 19, 'Meryem', 'قَالَ كَذٰلِكَۚ قَالَ رَبُّكَ هُوَ عَلَيَّ هَيِّنٌ وَقَدْ خَلَقْتُكَ مِنْ قَبْلُ وَلَمْ تَكُ شَيْـٔاً', 'Allah: Öyledir, dedi; Rabbin: O bana kolaydır. Daha önce, sen hiçbir şey değilken seni de yaratmıştım, buyurdu.', 16, 304, 98, 3835, 'Meryem', 'Mekke', 44),
(2260, 10, 19, 'Meryem', 'قَالَ رَبِّ اجْعَلْ ل۪ٓي اٰيَةًۜ قَالَ اٰيَتُكَ اَلَّا تُكَلِّمَ النَّاسَ ثَلٰثَ لَيَالٍ سَوِياًّ', 'O: Rabbim! dedi, (çocuğum olacağına dair) bana bir işaret ver. Allah: Sana işaret, sapasağlam olduğun halde (üç gün) üç gece insanlarla konuşamamandır, buyurdu.', 16, 304, 98, 3835, 'Meryem', 'Mekke', 44),
(2261, 11, 19, 'Meryem', 'فَخَرَجَ عَلٰى قَوْمِه۪ مِنَ الْمِحْرَابِ فَاَوْحٰٓى اِلَيْهِمْ اَنْ سَبِّحُوا بُكْرَةً وَعَشِياًّ', 'Bunun üzerine Zekeriyya, mâbetten kavminin karşısına çıkarak onlara: «Sabah akşam tesbihte bulunun» diye işaret verdi.', 16, 304, 98, 3835, 'Meryem', 'Mekke', 44),
(2262, 12, 19, 'Meryem', 'يَا يَحْيٰى خُذِ الْكِتَابَ بِقُوَّةٍۜ وَاٰتَيْنَاهُ الْحُكْمَ صَبِياًّۙ', '«Ey Yahya! Kitab´a (Tevrat´a) vargücünle sarıl!» (dedik) ve henüz sabi iken ona (ilim ve) hikmet verdik.', 16, 305, 98, 3835, 'Meryem', 'Mekke', 44),
(2263, 13, 19, 'Meryem', 'وَحَنَاناً مِنْ لَدُنَّا وَزَكٰوةًۜ وَكَانَ تَقِياًّۙ', 'Tarafımızdan ona kalp yumuşaklığı ve temizlik de (verdik). O, çok sakınan bir kimse idi.', 16, 305, 98, 3835, 'Meryem', 'Mekke', 44),
(2264, 14, 19, 'Meryem', 'وَبَراًّ بِوَالِدَيْهِ وَلَمْ يَكُنْ جَبَّاراً عَصِياًّ', 'Ana-babasına çok iyi davranırdı; o, isyankâr bir zorba değildi.', 16, 305, 98, 3835, 'Meryem', 'Mekke', 44),
(2265, 15, 19, 'Meryem', 'وَسَلَامٌ عَلَيْهِ يَوْمَ وُلِدَ وَيَوْمَ يَمُوتُ وَيَوْمَ يُبْعَثُ حَياًّ۟', 'Doğduğu gün, öleceği gün ve diri olarak kabirden kaldırılacağı gün ona selam olsun!', 16, 305, 98, 3835, 'Meryem', 'Mekke', 44),
(2266, 16, 19, 'Meryem', 'وَاذْكُرْ فِي الْكِتَابِ مَرْيَمَۢ اِذِ انْتَبَذَتْ مِنْ اَهْلِهَا مَكَاناً شَرْقِياًّۙ', '(Resûlüm!) Kitap´ta Meryem´i de an. Hani o, ailesinden ayrılarak doğu tarafında bir yere çekilmişti.', 16, 305, 98, 3835, 'Meryem', 'Mekke', 44),
(2267, 17, 19, 'Meryem', 'فَاتَّخَذَتْ مِنْ دُونِهِمْ حِجَاباً فَاَرْسَلْـنَٓا اِلَيْهَا رُوحَنَا فَتَمَثَّلَ لَهَا بَشَراً سَوِياًّ', 'Meryem, onlarla kendi arasına bir perde çekmişti. Derken, biz ona ruhumuzu gönderdik de o, kendisine tastamam bir insan şeklinde göründü.', 16, 305, 98, 3835, 'Meryem', 'Mekke', 44),
(2268, 18, 19, 'Meryem', 'قَالَتْ اِنّ۪ٓي اَعُوذُ بِالرَّحْمٰنِ مِنْكَ اِنْ كُنْتَ تَقِياًّ', 'Meryem dedi ki: Senden, çok esirgeyici olan Allah´a sığınırım! Eğer Allah´tan sakınan bir kimse isen (bana dokunma).', 16, 305, 98, 3835, 'Meryem', 'Mekke', 44),
(2269, 19, 19, 'Meryem', 'قَالَ اِنَّـمَٓا اَنَا۬ رَسُولُ رَبِّكِۗ لِاَهَبَ لَكِ غُلَاماً زَكِياًّ', 'Melek: Ben, yalnızca, sana tertemiz bir erkek çocuk bağışlamam için Rabbinin bir elçisiyim, dedi.', 16, 305, 98, 3835, 'Meryem', 'Mekke', 44),
(2270, 20, 19, 'Meryem', 'قَالَتْ اَنّٰى يَكُونُ ل۪ي غُلَامٌ وَلَمْ يَمْسَسْن۪ي بَشَرٌ وَلَمْ اَكُ بَغِياًّ', 'Meryem: Bana bir insan eli değmediği, iffetsiz de olmadığım halde benim nasıl çocuğum olabilir? dedi.', 16, 305, 98, 3835, 'Meryem', 'Mekke', 44),
(2271, 21, 19, 'Meryem', 'قَالَ كَذٰلِكِۚ قَالَ رَبُّكِ هُوَ عَلَيَّ هَيِّنٌۚ وَلِنَجْعَلَـهُٓ اٰيَةً لِلنَّاسِ وَرَحْمَةً مِنَّاۚ وَكَانَ اَمْراً مَقْضِياًّ', 'Melek: Öyledir, dedi; (zira) Rabbin buyurdu ki: Bu bana kolaydır. Çünkü biz, onu insanlara bir delil ve kendimizden bir rahmet kılacağız. Bu, hüküm ve karara bağlanmış (ezelde olup bitmiş) bir iş idi.', 16, 305, 98, 3835, 'Meryem', 'Mekke', 44),
(2272, 22, 19, 'Meryem', 'فَحَمَلَتْهُ فَانْتَبَذَتْ بِه۪ مَكَاناً قَصِياًّ', 'Meryem ona hamile kaldı. Bunun üzerine onunla (karnındaki çocukla) uzak bir yere çekildi.', 16, 305, 98, 3835, 'Meryem', 'Mekke', 44),
(2273, 23, 19, 'Meryem', 'فَاَجَٓاءَهَا الْمَخَاضُ اِلٰى جِذْعِ النَّخْلَةِۚ قَالَتْ يَا لَيْتَن۪ي مِتُّ قَبْلَ هٰذَا وَكُنْتُ نَسْياً مَنْسِياًّ', 'Doğum sancısı onu bir hurma ağacına (dayanmaya) sevketti. «Keşke, dedi, bundan önce ölseydim de unutulup gitseydim!»', 16, 305, 98, 3835, 'Meryem', 'Mekke', 44),
(2274, 24, 19, 'Meryem', 'فَنَادٰيهَا مِنْ تَحْتِهَٓا اَلَّا تَحْزَن۪ي قَدْ جَعَلَ رَبُّكِ تَحْتَكِ سَرِياًّ', 'Aşağısından (İsa yahut melek) ona şöyle seslendi: «Tasalanma! Rabbin senin alt yanında bir su arkı vücuda getirmiştir.»', 16, 305, 98, 3835, 'Meryem', 'Mekke', 44),
(2275, 25, 19, 'Meryem', 'وَهُزّ۪ٓي اِلَيْكِ بِجِذْعِ النَّخْلَةِ تُسَاقِطْ عَلَيْكِ رُطَباً جَنِياًّۘ', '«Hurma dalını kendine doğru silkele ki, üzerine taze, olgun hurma dökülsün.»', 16, 305, 98, 3835, 'Meryem', 'Mekke', 44),
(2276, 26, 19, 'Meryem', 'فَكُل۪ي وَاشْرَب۪ي وَقَرّ۪ي عَيْناًۚ فَاِمَّا تَرَيِنَّ مِنَ الْبَشَرِ اَحَداًۙ فَقُول۪ٓي اِنّ۪ي نَذَرْتُ لِلرَّحْمٰنِ صَوْماً فَلَنْ اُكَلِّمَ الْيَوْمَ اِنْسِياًّۚ', '«Ye, iç. Gözün aydın olsun! Eğer insanlardan birini görürsen de ki: Ben, çok merhametli olan Allah´a oruç adadım; artık bugün hiçbir insanla konuşmayacağım.»', 16, 306, 98, 3835, 'Meryem', 'Mekke', 44),
(2277, 27, 19, 'Meryem', 'فَاَتَتْ بِه۪ قَوْمَهَا تَحْمِلُهُۜ قَالُوا يَا مَرْيَمُ لَقَدْ جِئْتِ شَيْـٔاً فَرِياًّ', 'Nihayet onu (kucağında) taşıyarak kavmine getirdi. Dediler ki: Ey Meryem! Hakikaten sen iğrenç bir şey yaptın!', 16, 306, 98, 3835, 'Meryem', 'Mekke', 44),
(2278, 28, 19, 'Meryem', 'يَٓا اُخْتَ هٰرُونَ مَا كَانَ اَبُوكِ امْرَاَ سَوْءٍ وَمَا كَانَتْ اُمُّكِ بَغِياًّۚ', 'Ey Harun´un kız kardeşi! Senin baban kötü bir insan değildi; annen de iffetsiz değildi.', 16, 306, 98, 3835, 'Meryem', 'Mekke', 44),
(2279, 29, 19, 'Meryem', 'فَاَشَارَتْ اِلَيْهِ۠ قَالُوا كَيْفَ نُكَلِّمُ مَنْ كَانَ فِي الْمَهْدِ صَبِياًّ', 'Bunun üzerine Meryem çocuğu gösterdi. «Biz, dediler, beşikteki bir sabî ile nasıl konuşuruz?»', 16, 306, 98, 3835, 'Meryem', 'Mekke', 44),
(2280, 30, 19, 'Meryem', 'قَالَ اِنّ۪ي عَبْدُ اللّٰهِ۠ اٰتَانِيَ الْكِتَابَ وَجَعَلَن۪ي نَبِياًّۙ', 'Çocuk şöyle dedi: «Ben, Allah´ın kuluyum. O, bana Kitab´ı verdi ve beni peygamber yaptı.»', 16, 306, 98, 3835, 'Meryem', 'Mekke', 44),
(2281, 31, 19, 'Meryem', 'وَجَعَلَن۪ي مُبَارَكاً اَيْنَ مَا كُنْتُۖ وَاَوْصَان۪ي بِالصَّلٰوةِ وَالزَّكٰوةِ مَا دُمْتُ حَياًّۖ', '«Nerede olursam olayım, O beni mübarek kıldı; yaşadığım sürece bana namazı ve zekâtı emretti.»', 16, 306, 98, 3835, 'Meryem', 'Mekke', 44),
(2282, 32, 19, 'Meryem', 'وَبَراًّ بِوَالِدَت۪يۘ وَلَمْ يَجْعَلْن۪ي جَبَّاراً شَقِياًّ', '«Beni anneme saygılı kıldı; beni bedbaht bir zorba yapmadı.»', 16, 306, 98, 3835, 'Meryem', 'Mekke', 44),
(2283, 33, 19, 'Meryem', 'وَالسَّلَامُ عَلَيَّ يَوْمَ وُلِدْتُ وَيَوْمَ اَمُوتُ وَيَوْمَ اُبْعَثُ حَياًّ', '«Doğduğum gün, öleceğim gün ve diri olarak kabirden kaldırılacağım gün esenlik banadır.»', 16, 306, 98, 3835, 'Meryem', 'Mekke', 44),
(2284, 34, 19, 'Meryem', 'ذٰلِكَ ع۪يسَى ابْنُ مَرْيَمَۚ قَوْلَ الْحَقِّ الَّذ۪ي ف۪يهِ يَمْتَرُونَ', 'İşte, hakkında şüphe ettikleri Meryem oğlu İsa -hak söz olarak- budur.', 16, 306, 98, 3835, 'Meryem', 'Mekke', 44),
(2285, 35, 19, 'Meryem', 'مَا كَانَ لِلّٰهِ اَنْ يَتَّخِذَ مِنْ وَلَدٍۙ سُبْحَانَهُۜ اِذَا قَضٰٓى اَمْراً فَاِنَّمَا يَقُولُ لَهُ كُنْ فَيَكُونُۜ', 'Allah´ın bir evlât edinmesi, olacak şey değildir! O, bundan münezzehtir. Bir işe hükmettiği zaman, ona sadece «Ol!» der ve hemen olur.', 16, 306, 98, 3835, 'Meryem', 'Mekke', 44),
(2286, 36, 19, 'Meryem', 'وَاِنَّ اللّٰهَ رَبّ۪ي وَرَبُّكُمْ فَاعْبُدُوهُۜ هٰذَا صِرَاطٌ مُسْتَق۪يمٌ', '(İsa şunu da söyledi:) Muhakkak ki Allah, benim de Rabbim, sizin de Rabbinizdir. Öyle ise O´na kulluk ediniz. İşte doğru yol budur.', 16, 306, 98, 3835, 'Meryem', 'Mekke', 44),
(2287, 37, 19, 'Meryem', 'فَاخْتَلَفَ الْاَحْزَابُ مِنْ بَيْنِهِمْۚ فَوَيْلٌ لِلَّذ۪ينَ كَفَرُوا مِنْ مَشْهَدِ يَوْمٍ عَظ۪يمٍ', 'Sonra guruplar kendi aralarında ayrılığa düştüler. Büyük güne şahit olunduğu zamanda vay o kâfirlerin haline!', 16, 306, 98, 3835, 'Meryem', 'Mekke', 44),
(2288, 38, 19, 'Meryem', 'اَسْمِعْ بِهِمْ وَاَبْصِرْۙ يَوْمَ يَأْتُونَنَاۚ لٰكِنِ الظَّالِمُونَ الْيَوْمَ ف۪ي ضَلَالٍ مُب۪ينٍ', 'Onlar, bizim huzurumuza çıkacakları gün (başlarına gelecek olanları) ne iyi duyarlar ve ne iyi görürler (bir görsen)! Fakat o zalimler bugün açık bir sapıklık içindedirler.', 16, 306, 98, 3835, 'Meryem', 'Mekke', 44),
(2289, 39, 19, 'Meryem', 'وَاَنْذِرْهُمْ يَوْمَ الْحَسْرَةِ اِذْ قُضِيَ الْاَمْرُۚ وَهُمْ ف۪ي غَفْلَةٍ وَهُمْ لَا يُؤْمِنُونَ', '(Resûlüm!) Sen onları pişmanlık ve üzüntü günü hakkında uyar. Çünkü onlar bir gafletin içine dalmış oldukları halde ve henüz iman etmemişken (bakarsın) iş olup bitmiştir.', 16, 307, 98, 3835, 'Meryem', 'Mekke', 44),
(2290, 40, 19, 'Meryem', 'اِنَّا نَحْنُ نَرِثُ الْاَرْضَ وَمَنْ عَلَيْهَا وَاِلَيْنَا يُرْجَعُونَ۟', 'Yeryüzüne ve onun üzerindekilere ancak biz vâris oluruz (her şey gider, biz kalırız) ve onlar ancak bize döndürülürler.', 16, 307, 98, 3835, 'Meryem', 'Mekke', 44),
(2291, 41, 19, 'Meryem', 'وَاذْكُرْ فِي الْكِتَابِ اِبْرٰه۪يمَۜ اِنَّهُ كَانَ صِدّ۪يقاً نَبِياًّ', 'Kitap´ta İbrahim´i an. Zira o, sıdkı bütün bir peygamberdi.', 16, 307, 98, 3835, 'Meryem', 'Mekke', 44),
(2292, 42, 19, 'Meryem', 'اِذْ قَالَ لِاَب۪يهِ يَٓا اَبَتِ لِمَ تَعْبُدُ مَا لَا يَسْمَعُ وَلَا يُبْصِرُ وَلَا يُغْن۪ي عَنْكَ شَيْـٔاً', 'Bir zaman o babasına dedi ki: Babacığım! Duymayan, görmeyen ve sana hiçbir fayda sağlamayan bir şeye niçin taparsın?', 16, 307, 98, 3835, 'Meryem', 'Mekke', 44),
(2293, 43, 19, 'Meryem', 'يَٓا اَبَتِ اِنّ۪ي قَدْ جَٓاءَن۪ي مِنَ الْعِلْمِ مَا لَمْ يَأْتِكَ فَاتَّبِعْن۪ٓي اَهْدِكَ صِرَاطاً سَوِياًّ', 'Babacığım! Hakikaten sana gelmeyen bir ilim bana geldi. Öyle ise bana uy ki, seni düz yola çıkarayım.', 16, 307, 98, 3835, 'Meryem', 'Mekke', 44),
(2294, 44, 19, 'Meryem', 'يَٓا اَبَتِ لَا تَعْبُدِ الشَّيْطَانَۜ اِنَّ الشَّيْطَانَ كَانَ لِلرَّحْمٰنِ عَصِياًّ', 'Babacığım! Şeytana kulluk etme! Çünkü şeytan, çok merhametli olan Allah´a âsi oldu.', 16, 307, 98, 3835, 'Meryem', 'Mekke', 44),
(2295, 45, 19, 'Meryem', 'يَٓا اَبَتِ اِنّ۪ٓي اَخَافُ اَنْ يَمَسَّكَ عَذَابٌ مِنَ الرَّحْمٰنِ فَتَكُونَ لِلشَّيْطَانِ وَلِياًّ', 'Babacığım! Allah tarafından sana azap dokunup da şeytanın yakını olmandan korkuyorum.', 16, 307, 98, 3835, 'Meryem', 'Mekke', 44),
(2296, 46, 19, 'Meryem', 'قَالَ اَرَاغِبٌ اَنْتَ عَنْ اٰلِهَت۪ي يَٓا اِبْرٰه۪يمُۚ لَئِنْ لَمْ تَنْتَهِ۬ لَاَرْجُمَنَّكَ وَاهْجُرْن۪ي مَلِياًّ', '(Babası:) Ey İbrahim! dedi, sen benim tanrılarımdan yüz mü çeviriyorsun? Eğer vazgeçmezsen, andolsun seni taşlarım! Uzun bir zaman benden uzak dur!', 16, 307, 98, 3835, 'Meryem', 'Mekke', 44),
(2297, 47, 19, 'Meryem', 'قَالَ سَلَامٌ عَلَيْكَۚ سَاَسْتَغْفِرُ لَكَ رَبّ۪يۜ اِنَّهُ كَانَ ب۪ي حَفِياًّ', 'İbrahim: Selâm sana (esen kal) dedi, Rabbimden senin için mağfiret dileyeceğim. Çünkü O bana karşı çok lütufkârdır.', 16, 307, 98, 3835, 'Meryem', 'Mekke', 44),
(2298, 48, 19, 'Meryem', 'وَاَعْتَزِلُكُمْ وَمَا تَدْعُونَ مِنْ دُونِ اللّٰهِ وَاَدْعُوا رَبّ۪يۘ عَسٰٓى اَلَّٓا اَكُونَ بِدُعَٓاءِ رَبّ۪ي شَقِياًّ', 'Sizden de, Allah´ın dışında taptığınız şeylerden de uzaklaşıyor ve Rabbime yalvarıyorum. Umulur ki (senin için) Rabbime dua etmemle bedbaht (emeği boşa gitmiş) olmam.', 16, 307, 98, 3835, 'Meryem', 'Mekke', 44),
(2299, 49, 19, 'Meryem', 'فَلَمَّا اعْتَزَلَهُمْ وَمَا يَعْبُدُونَ مِنْ دُونِ اللّٰهِۙ وَهَبْنَا لَـهُٓ اِسْحٰقَ وَيَعْقُوبَۜ وَكُلاًّ جَعَلْنَا نَبِياًّ', 'Nihayet İbrahim onlardan ve Allah´tan başka taptıkları şeylerden uzaklaşıp bir tarafa çekildiği zaman biz ona İshak ve Yâ´kub´u bağışladık ve her birini peygamber yaptık.', 16, 307, 98, 3835, 'Meryem', 'Mekke', 44),
(2300, 50, 19, 'Meryem', 'وَوَهَبْنَا لَهُمْ مِنْ رَحْمَتِنَا وَجَعَلْنَا لَهُمْ لِسَانَ صِدْقٍ عَلِياًّ۟', 'Onlara rahmetimizden bağışta bulunduk; kendilerine haklı ve yüksek bir şöhret nasip ettik.', 16, 307, 98, 3835, 'Meryem', 'Mekke', 44),
(2301, 51, 19, 'Meryem', 'وَاذْكُرْ فِي الْكِتَابِ مُوسٰىۘ اِنَّهُ كَانَ مُخْلَصاً وَكَانَ رَسُولاً نَبِياًّ', '(Resûlüm!) Kitap´ta Musa´yı da an. Gerçekten o ihlâs sahibi idi ve hem resûl, hem de nebî idi.', 16, 307, 98, 3835, 'Meryem', 'Mekke', 44),
(2302, 52, 19, 'Meryem', 'وَنَادَيْنَاهُ مِنْ جَانِبِ الطُّورِ الْاَيْمَنِ وَقَرَّبْنَاهُ نَجِياًّ', 'Ona Tûr´un sağ tarafından seslendik ve onu, fısıldaşan kimse kadar (kendimize) yaklaştırdık.', 16, 308, 98, 3835, 'Meryem', 'Mekke', 44),
(2303, 53, 19, 'Meryem', 'وَوَهَبْنَا لَهُ مِنْ رَحْمَتِنَٓا اَخَاهُ هٰرُونَ نَبِياًّ', 'Rahmetimizin bir sonucu olarak ona kardeşi Harun´u bir peygamber olarak armağan ettik.', 16, 308, 98, 3835, 'Meryem', 'Mekke', 44),
(2304, 54, 19, 'Meryem', 'وَاذْكُرْ فِي الْكِتَابِ اِسْمٰع۪يلَۘ اِنَّهُ كَانَ صَادِقَ الْوَعْدِ وَكَانَ رَسُولاً نَبِياًّۚ', '(Resûlüm!) Kitap´ta İsmail´i de an. Gerçekten o, sözüne sâdıktı, resûl ve nebî idi.', 16, 308, 98, 3835, 'Meryem', 'Mekke', 44),
(2305, 55, 19, 'Meryem', 'وَكَانَ يَأْمُرُ اَهْلَهُ بِالصَّلٰوةِ وَالزَّكٰوةِۖ وَكَانَ عِنْدَ رَبِّه۪ مَرْضِياًّ', 'Halkına namazı ve zekâtı emrederdi; Rabbi nezdinde de hoşnutluk kazanmış bir kimse idi.', 16, 308, 98, 3835, 'Meryem', 'Mekke', 44),
(2306, 56, 19, 'Meryem', 'وَاذْكُرْ فِي الْكِتَابِ اِدْر۪يسَۘ اِنَّهُ كَانَ صِدّ۪يقاً نَبِياًّۗ', 'Kitapta İdris´i de an. Hakikaten o, pek doğru bir insan, bir peygamberdi.', 16, 308, 98, 3835, 'Meryem', 'Mekke', 44),
(2307, 57, 19, 'Meryem', 'وَرَفَعْنَاهُ مَكَاناً عَلِياًّ', 'Onu üstün bir makama yücelttik.', 16, 308, 98, 3835, 'Meryem', 'Mekke', 44),
(2308, 58, 19, 'Meryem', 'اُو۬لٰٓئِكَ الَّذ۪ينَ اَنْعَمَ اللّٰهُ عَلَيْهِمْ مِنَ النَّبِيّ۪نَ مِنْ ذُرِّيَّةِ اٰدَمَ وَمِمَّنْ حَمَلْنَا مَعَ نُوحٍۘ وَمِنْ ذُرِّيَّةِ اِبْرٰه۪يمَ وَاِسْرَٓائ۪لَ وَمِمَّنْ هَدَيْنَا وَاجْتَبَيْنَاۜ اِذَا تُتْلٰى عَلَيْهِمْ اٰيَاتُ الرَّحْمٰنِ خَرُّوا سُجَّداً وَبُكِياًّ ۩', 'İşte bunlar, Allah´ın kendilerine nimetler verdiği peygamberlerden, Âdem´in soyundan, Nuh ile birlikte (gemide) taşıdıklarımızdan, İbrahim ve İsrail (Ya´kub)´in soyundan, doğruya ulaştırdığımız ve seçkin kıldığımız kimselerdendir. Onlara, çok merhametli olan Allah´ın âyetleri okunduğunda ağlayarak secdeye kapanırlardı.', 16, 308, 98, 3835, 'Meryem', 'Mekke', 44),
(2309, 59, 19, 'Meryem', 'فَخَلَفَ مِنْ بَعْدِهِمْ خَلْفٌ اَضَاعُوا الصَّلٰوةَ وَاتَّبَعُوا الشَّهَوَاتِ فَسَوْفَ يَلْقَوْنَ غَياًّۙ', 'Nihayet onların peşinden öyle bir nesil geldi ki, bunlar namazı bıraktılar; nefislerinin arzularına uydular. Bu yüzden ileride sapıklıklarının cezasını çekecekler.', 16, 308, 98, 3835, 'Meryem', 'Mekke', 44),
(2310, 60, 19, 'Meryem', 'اِلَّا مَنْ تَابَ وَاٰمَنَ وَعَمِلَ صَالِحاً فَاُو۬لٰٓئِكَ يَدْخُلُونَ الْجَنَّةَ وَلَا يُظْلَمُونَ شَيْـٔاًۙ', '(60-61) Ancak tevbe eden, iman eden ve iyi davranışta bulunan kimseler hariçtir. Bunlar, hiçbir haksızlığa uğratılmaksızın cennete, çok merhametli olan Allah´ın, kullarına gıyaben vâdettiği Adn cennetlerine girecekler. Şüphesiz O´nun vâdi yerini bulacaktır.', 16, 308, 98, 3835, 'Meryem', 'Mekke', 44),
(2311, 61, 19, 'Meryem', 'جَنَّاتِ عَدْنٍۨ الَّت۪ي وَعَدَ الرَّحْمٰنُ عِبَادَهُ بِالْغَيْبِۜ اِنَّهُ كَانَ وَعْدُهُ مَأْتِياًّ', '(60-61) Ancak tevbe eden, iman eden ve iyi davranışta bulunan kimseler hariçtir. Bunlar, hiçbir haksızlığa uğratılmaksızın cennete, çok merhametli olan Allah´ın, kullarına gıyaben vâdettiği Adn cennetlerine girecekler. Şüphesiz O´nun vâdi yerini bulacaktır.', 16, 308, 98, 3835, 'Meryem', 'Mekke', 44),
(2312, 62, 19, 'Meryem', 'لَا يَسْمَعُونَ ف۪يهَا لَغْواً اِلَّا سَلَاماًۜ وَلَهُمْ رِزْقُهُمْ ف۪يهَا بُكْرَةً وَعَشِياًّ', 'Orada boş söz değil, hoş söz duyarlar. Ve orada, sabah akşam kendilerine ait rızıkları vardır.', 16, 308, 98, 3835, 'Meryem', 'Mekke', 44),
(2313, 63, 19, 'Meryem', 'تِلْكَ الْجَنَّةُ الَّت۪ي نُورِثُ مِنْ عِبَادِنَا مَنْ كَانَ تَقِياًّ', 'Kullarımızdan, takvâ sahibi kimselere vereceğimiz cennet işte budur.', 16, 308, 98, 3835, 'Meryem', 'Mekke', 44),
(2314, 64, 19, 'Meryem', 'وَمَا نَتَنَزَّلُ اِلَّا بِاَمْرِ رَبِّكَۚ لَهُ مَا بَيْنَ اَيْد۪ينَا وَمَا خَلْفَنَا وَمَا بَيْنَ ذٰلِكَۚ وَمَا كَانَ رَبُّكَ نَسِياًّۚ', 'Biz ancak Rabbinin emri ile ineriz. Önümüzde, arkamızda ve bunlar arasında olan her şey O´na aittir. Senin Rabbin unutkan değildir.', 16, 308, 98, 3835, 'Meryem', 'Mekke', 44),
(2315, 65, 19, 'Meryem', 'رَبُّ السَّمٰوَاتِ وَالْاَرْضِ وَمَا بَيْنَهُمَا فَاعْبُدْهُ وَاصْطَبِرْ لِعِبَادَتِه۪ۜ هَلْ تَعْلَمُ لَهُ سَمِياًّ۟', '(O) göklerin, yerin ve ikisi arasındaki şeylerin Rabbidir. Şu halde O´na kulluk et; O´na kulluk etmek için sabırlı ve metânetli ol. O´nun bir adaşı (benzeri) olduğunu biliyor musun? (Asla benzeri yoktur).', 16, 309, 98, 3835, 'Meryem', 'Mekke', 44),
(2316, 66, 19, 'Meryem', 'وَيَقُولُ الْاِنْسَانُ ءَاِذَا مَا مِتُّ لَسَوْفَ اُخْرَجُ حَياًّ', 'İnsan der ki: «Öldüğüm zaman sahi diri olarak (kabrimden) çıkarılacak mıyım?»', 16, 309, 98, 3835, 'Meryem', 'Mekke', 44),
(2317, 67, 19, 'Meryem', 'اَوَلَا يَذْكُرُ الْاِنْسَانُ اَنَّا خَلَقْنَاهُ مِنْ قَبْلُ وَلَمْ يَكُ شَيْـٔاً', 'İnsan düşünmez mi ki, daha önce o hiçbir şey olmadığı halde biz kendisini yaratmışızdır?', 16, 309, 98, 3835, 'Meryem', 'Mekke', 44),
(2318, 68, 19, 'Meryem', 'فَوَرَبِّكَ لَنَحْشُرَنَّهُمْ وَالشَّيَاط۪ينَ ثُمَّ لَنُحْضِرَنَّهُمْ حَوْلَ جَهَنَّمَ جِثِياًّۚ', 'Öyle ise, Rabbine andolsun ki, muhakkak surette onları şeytanlarla birlikte mahşerde toplayacağız; sonra onları diz üstü çökmüş vaziyette cehennemin çevresinde hazır bulunduracağız.', 16, 309, 98, 3835, 'Meryem', 'Mekke', 44),
(2319, 69, 19, 'Meryem', 'ثُمَّ لَنَنْزِعَنَّ مِنْ كُلِّ ش۪يعَةٍ اَيُّهُمْ اَشَدُّ عَلَى الرَّحْمٰنِ عِتِياًّۚ', 'Sonra her milletten, rahmân olan Allah´a en çok âsi olanlar hangileri ise çekip ayıracağız.', 16, 309, 98, 3835, 'Meryem', 'Mekke', 44),
(2320, 70, 19, 'Meryem', 'ثُمَّ لَنَحْنُ اَعْلَمُ بِالَّذ۪ينَ هُمْ اَوْلٰى بِهَا صِلِياًّ', 'Sonra, orayı boylamaya daha çok müstahak olanları elbette biz daha iyi biliriz.', 16, 309, 98, 3835, 'Meryem', 'Mekke', 44),
(2321, 71, 19, 'Meryem', 'وَاِنْ مِنْكُمْ اِلَّا وَارِدُهَاۚ كَانَ عَلٰى رَبِّكَ حَتْماً مَقْضِياًّۚ', 'İçinizden, oraya uğramayacak hiçbir kimse yoktur. Bu, Rabbin için kesinleşmiş bir hükümdür.', 16, 309, 98, 3835, 'Meryem', 'Mekke', 44),
(2322, 72, 19, 'Meryem', 'ثُمَّ نُنَجِّي الَّذ۪ينَ اتَّقَوْا وَنَذَرُ الظَّالِم۪ينَ ف۪يهَا جِثِياًّ', 'Sonra biz, Allah´tan sakınanları kurtarırız; zalimleri de diz üstü çökmüş olarak orada bırakırız.', 16, 309, 98, 3835, 'Meryem', 'Mekke', 44),
(2323, 73, 19, 'Meryem', 'وَاِذَا تُتْلٰى عَلَيْهِمْ اٰيَاتُنَا بَيِّنَاتٍ قَالَ الَّذ۪ينَ كَفَرُوا لِلَّذ۪ينَ اٰمَنُٓواۙ اَيُّ الْفَر۪يقَيْنِ خَيْرٌ مَقَاماً وَاَحْسَنُ نَدِياًّ', 'Kendilerine âyetlerimiz ayan beyan okunduğu zaman inkâr edenler, iman edenlere: İki topluluktan hangisinin (hangimizin) mevki ve makamı daha iyi, meclis ve topluluğu daha güzeldir? dediler.', 16, 309, 98, 3835, 'Meryem', 'Mekke', 44),
(2324, 74, 19, 'Meryem', 'وَكَمْ اَهْلَكْنَا قَبْلَهُمْ مِنْ قَرْنٍ هُمْ اَحْسَنُ اَثَاثاً وَرِءْياً', 'Onlardan önce de, eşya ve görünüş bakımından daha güzel olan nice nesiller helâk ettik.', 16, 309, 98, 3835, 'Meryem', 'Mekke', 44),
(2325, 75, 19, 'Meryem', 'قُلْ مَنْ كَانَ فِي الضَّلَالَةِ فَلْيَمْدُدْ لَهُ الرَّحْمٰنُ مَداًّۚ حَتّٰٓى اِذَا رَاَوْا مَا يُوعَدُونَ اِمَّا الْعَذَابَ وَاِمَّا السَّاعَةَۜ فَسَيَعْلَمُونَ مَنْ هُوَ شَرٌّ مَكَاناً وَاَضْعَفُ جُنْداً', 'De ki: Kim sapıklıkta ise, çok merhametli olan Allah ona mühlet versin! Nihayet kendilerine vâdolunan şeyi -ya azabı (müminler karşısında yenilgiyi), veya kıyameti- gördükleri zaman, mevki ve makamı daha kötü ve askeri daha zayıf olanın kim olduğunu öğreneceklerdir.', 16, 309, 98, 3835, 'Meryem', 'Mekke', 44),
(2326, 76, 19, 'Meryem', 'وَيَز۪يدُ اللّٰهُ الَّذ۪ينَ اهْتَدَوْا هُدًىۜ وَالْبَاقِيَاتُ الصَّالِحَاتُ خَيْرٌ عِنْدَ رَبِّكَ ثَوَاباً وَخَيْرٌ مَرَداًّ', 'Allah, doğru yola gidenlerin hidayetini artırır. Sürekli kalan iyi işler, Rabbinin nezdinde hem mükâfat bakımından daha hayırlı, hem de âkıbetçe daha iyidir.', 16, 309, 98, 3835, 'Meryem', 'Mekke', 44),
(2327, 77, 19, 'Meryem', 'اَفَرَاَيْتَ الَّذ۪ي كَفَرَ بِاٰيَاتِنَا وَقَالَ لَاُو۫تَيَنَّ مَالاً وَوَلَداًۜ', '(Resûlüm!) Âyetlerimizi inkâr eden ve «Muhakkak surette bana mal ve evlât verilecek» diyen adamı gördün mü?', 16, 310, 98, 3835, 'Meryem', 'Mekke', 44),
(2328, 78, 19, 'Meryem', 'اَطَّـلَعَ الْغَيْبَ اَمِ اتَّخَذَ عِنْدَ الرَّحْمٰنِ عَهْداًۙ', 'O, gaybı mı bildi, yoksa Allah´ın katından bir söz mü aldı?', 16, 310, 98, 3835, 'Meryem', 'Mekke', 44),
(2329, 79, 19, 'Meryem', 'كَلَّاۜ سَنَكْتُبُ مَا يَقُولُ وَنَمُدُّ لَهُ مِنَ الْعَذَابِ مَداًّۙ', 'Kesinlikle hayır! Biz onun söylediğini yazacağız ve azabını uzattıkça uzatacağız.', 16, 310, 98, 3835, 'Meryem', 'Mekke', 44),
(2330, 80, 19, 'Meryem', 'وَنَرِثُهُ مَا يَقُولُ وَيَأْت۪ينَا فَرْداً', 'Onun dediğine biz vâris oluruz, (malı ve evlâdı bize kalır); kendisi de bize yapayalnız gelir.', 16, 310, 98, 3835, 'Meryem', 'Mekke', 44),
(2331, 81, 19, 'Meryem', 'وَاتَّخَذُوا مِنْ دُونِ اللّٰهِ اٰلِهَةً لِيَكُونُوا لَهُمْ عِزاًّۙ', 'Onlar, kendilerine bir itibar ve kuvvet (vesilesi) olsun diye Allah´tan başka tanrılar edindiler.', 16, 310, 98, 3835, 'Meryem', 'Mekke', 44),
(2332, 82, 19, 'Meryem', 'كَلَّاۜ سَيَكْفُرُونَ بِعِبَادَتِهِمْ وَيَكُونُونَ عَلَيْهِمْ ضِداًّ۟', 'Hayır, hayır! (Taptıkları), onların ibadetlerini tanımayacaklar ve onlara hasım olacaklar.', 16, 310, 98, 3835, 'Meryem', 'Mekke', 44),
(2333, 83, 19, 'Meryem', 'اَلَمْ تَرَ اَنَّٓا اَرْسَلْنَا الشَّيَاط۪ينَ عَلَى الْكَافِر۪ينَ تَؤُزُّهُمْ اَزاًّۙ', '(Resûlüm!) Görmedin mi? Biz, kâfirlerin üzerine, kendilerini iyice (isyankârlığa) sevkeden şeytanları gönderdik.', 16, 310, 98, 3835, 'Meryem', 'Mekke', 44),
(2334, 84, 19, 'Meryem', 'فَلَا تَعْجَلْ عَلَيْهِمْۜ اِنَّمَا نَعُدُّ لَهُمْ عَداًّۚ', 'Öyle ise onlar hakkında acele etme. Biz onlar için (günlerini) teker teker sayıyoruz.', 16, 310, 98, 3835, 'Meryem', 'Mekke', 44),
(2335, 85, 19, 'Meryem', 'يَوْمَ نَحْشُرُ الْمُتَّق۪ينَ اِلَى الرَّحْمٰنِ وَفْداًۙ', '(85-87) Takvâ sahiplerini heyet halinde çok merhametli olan Allah´ın huzurunda topladığımız, günahkârları da susuz olarak cehenneme sürdüğümüz gün, Rahmân nezdinde söz ve izin alandan başkalarının şefâata güçleri yetmeyecektir.', 16, 310, 98, 3835, 'Meryem', 'Mekke', 44),
(2336, 86, 19, 'Meryem', 'وَنَسُوقُ الْمُجْرِم۪ينَ اِلٰى جَهَنَّمَ وِرْداًۢ', '(85-87) Takvâ sahiplerini heyet halinde çok merhametli olan Allah´ın huzurunda topladığımız, günahkârları da susuz olarak cehenneme sürdüğümüz gün, Rahmân nezdinde söz ve izin alandan başkalarının şefâata güçleri yetmeyecektir.', 16, 310, 98, 3835, 'Meryem', 'Mekke', 44),
(2337, 87, 19, 'Meryem', 'لَا يَمْلِكُونَ الشَّفَاعَةَ اِلَّا مَنِ اتَّخَذَ عِنْدَ الرَّحْمٰنِ عَهْداًۢ', '(85-87) Takvâ sahiplerini heyet halinde çok merhametli olan Allah´ın huzurunda topladığımız, günahkârları da susuz olarak cehenneme sürdüğümüz gün, Rahmân nezdinde söz ve izin alandan başkalarının şefâata güçleri yetmeyecektir.', 16, 310, 98, 3835, 'Meryem', 'Mekke', 44),
(2338, 88, 19, 'Meryem', 'وَقَالُوا اتَّخَذَ الرَّحْمٰنُ وَلَداًۜ', '«Rahmân çocuk edindi» dediler.', 16, 310, 98, 3835, 'Meryem', 'Mekke', 44),
(2339, 89, 19, 'Meryem', 'لَقَدْ جِئْتُمْ شَيْـٔاً اِداًّۙ', 'Hakikaten siz, pek çirkin bir şey ortaya attınız.', 16, 310, 98, 3835, 'Meryem', 'Mekke', 44),
(2340, 90, 19, 'Meryem', 'تَكَادُ السَّمٰوَاتُ يَتَفَطَّرْنَ مِنْهُ وَتَنْشَقُّ الْاَرْضُ وَتَخِرُّ الْجِبَالُ هَداًّۙ', 'Bundan dolayı, neredeyse gökler çatlayacak, yer yarılacak, dağlar yıkılıp düşecektir!', 16, 310, 98, 3835, 'Meryem', 'Mekke', 44),
(2341, 91, 19, 'Meryem', 'اَنْ دَعَوْا لِلرَّحْمٰنِ وَلَداًۚ', 'Rahmân´a çocuk isnadında bulunmaları yüzünden.', 16, 310, 98, 3835, 'Meryem', 'Mekke', 44),
(2342, 92, 19, 'Meryem', 'وَمَا يَنْبَغ۪ي لِلرَّحْمٰنِ اَنْ يَتَّخِذَ وَلَداًۜ', 'Halbuki çocuk edinmek Rahmân´ın şanına yakışmaz.', 16, 310, 98, 3835, 'Meryem', 'Mekke', 44),
(2343, 93, 19, 'Meryem', 'اِنْ كُلُّ مَنْ فِي السَّمٰوَاتِ وَالْاَرْضِ اِلَّٓا اٰتِي الرَّحْمٰنِ عَبْداًۜ', 'Göklerde ve yerde olan herkes istisnasız, kul olarak Rahmân´a gelecektir.', 16, 310, 98, 3835, 'Meryem', 'Mekke', 44),
(2344, 94, 19, 'Meryem', 'لَقَدْ اَحْصٰيهُمْ وَعَدَّهُمْ عَداًّۜ', 'O, bunların hepsini kuşatmış ve sayılarını tesbit etmiştir.', 16, 310, 98, 3835, 'Meryem', 'Mekke', 44),
(2345, 95, 19, 'Meryem', 'وَكُلُّهُمْ اٰت۪يهِ يَوْمَ الْقِيٰمَةِ فَرْداً', 'Bunların hepsi de kıyamet gününde O´nun huzuruna tek başına (yapayalnız) gelecektir.', 16, 310, 98, 3835, 'Meryem', 'Mekke', 44),
(2346, 96, 19, 'Meryem', 'اِنَّ الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ سَيَجْعَلُ لَهُمُ الرَّحْمٰنُ وُداًّ', 'İman edip de iyi davranışlarda bulunanlara gelince, onlar için çok merhametli olan Allah, (gönüllerde) bir sevgi yaratacaktır.', 16, 311, 98, 3835, 'Meryem', 'Mekke', 44),
(2347, 97, 19, 'Meryem', 'فَاِنَّمَا يَسَّرْنَاهُ بِلِسَانِكَ لِتُبَشِّرَ بِهِ الْمُتَّق۪ينَ وَتُنْذِرَ بِه۪ قَوْماً لُداًّ', '(Resûlüm!) Biz Kur´an´ı, sadece, onunla Allah´tan sakınanları müjdeleyesin ve şiddetle karşı çıkan bir topluluğu uyarasın diye senin dilinle (indirilip okutarak) kolaylaştırdık.', 16, 311, 98, 3835, 'Meryem', 'Mekke', 44),
(2348, 98, 19, 'Meryem', 'وَكَمْ اَهْلَكْنَا قَبْلَهُمْ مِنْ قَرْنٍۜ هَلْ تُحِسُّ مِنْهُمْ مِنْ اَحَدٍ اَوْ تَسْمَعُ لَهُمْ رِكْزاً', 'Biz, onlardan önce nice nesilleri helâk ettik. Sen, onlardan herhangi birinden (bir varlık emâresi) hissediyor veya onlara ait cılız bir ses işitiyor musun?', 16, 311, 98, 3835, 'Meryem', 'Mekke', 44),
(2349, 1, 20, 'Ta-Ha', 'طٰهٰۜ', 'Tâ. Hâ.', 16, 311, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2350, 2, 20, 'Ta-Ha', 'مَٓا اَنْزَلْنَا عَلَيْكَ الْقُرْاٰنَ لِتَشْقٰىۙ', '(2-3) Biz, Kur´an´ı sana, güçlük çekesin diye değil, ancak Allah´tan korkanlara bir öğüt olsun diye indirdik.', 16, 311, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2351, 3, 20, 'Ta-Ha', 'اِلَّا تَذْكِرَةً لِمَنْ يَخْشٰىۙ', '(2-3) Biz, Kur´an´ı sana, güçlük çekesin diye değil, ancak Allah´tan korkanlara bir öğüt olsun diye indirdik.', 16, 311, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2352, 4, 20, 'Ta-Ha', 'تَنْز۪يلاً مِمَّنْ خَلَقَ الْاَرْضَ وَالسَّمٰوَاتِ الْعُلٰىۜ', '(Kur´an) yeri ve yüce gökleri yaratan Allah tarafından peyderpey indirilmiştir.', 16, 311, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2353, 5, 20, 'Ta-Ha', 'اَلرَّحْمٰنُ عَلَى الْعَرْشِ اسْتَوٰى', 'Rahmân, Arş´a istivâ etmiştir.', 16, 311, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2354, 6, 20, 'Ta-Ha', 'لَهُ مَا فِي السَّمٰوَاتِ وَمَا فِي الْاَرْضِ وَمَا بَيْنَهُمَا وَمَا تَحْتَ الثَّرٰى', 'Göklerde, yerde ve ikisi arasında bulunan şeyler ile toprağın altında olanlar hep O´nundur.', 16, 311, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2355, 7, 20, 'Ta-Ha', 'وَاِنْ تَجْهَرْ بِالْقَوْلِ فَاِنَّهُ يَعْلَمُ السِّرَّ وَاَخْفٰى', 'Eğer sen, sözü açıktan söylersen, bilesin ki O, gizliyi de, gizlinin gizlisini de bilir.', 16, 311, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2356, 8, 20, 'Ta-Ha', 'اَللّٰهُ لَٓا اِلٰهَ اِلَّا هُوَۜ لَهُ الْاَسْمَٓاءُ الْحُسْنٰى', 'Allah, kendisinden başka ilâh olmayandır. En güzel isimler O´na mahsustur.', 16, 311, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2357, 9, 20, 'Ta-Ha', 'وَهَلْ اَتٰيكَ حَد۪يثُ مُوسٰىۢ', '(Resûlüm!) Musa (olayının) haberi sana ulaştı mı?', 16, 311, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2358, 10, 20, 'Ta-Ha', 'اِذْ رَاٰ نَاراً فَقَالَ لِاَهْلِهِ امْكُـثُٓوا اِنّ۪ٓي اٰنَسْتُ نَاراً لَعَلّ۪ٓي اٰت۪يكُمْ مِنْهَا بِقَبَسٍ اَوْ اَجِدُ عَلَى النَّارِ هُدًى', 'Hani o, bir ateş görmüş ve ailesine: Bekleyin! Eminim ki bir ateş gördüm. Belki ondan size bir meş´ale getiririm veya ateşin yanında bir rehber bulurum, demişti.', 16, 311, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2359, 11, 20, 'Ta-Ha', 'فَلَمَّٓا اَتٰيهَا نُودِيَ يَا مُوسٰى', 'Oraya vardığında kendisine (tarafımızdan): Ey Musa! diye seslenildi:', 16, 311, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2360, 12, 20, 'Ta-Ha', 'اِنّ۪ٓي اَنَا۬ رَبُّكَ فَاخْلَعْ نَعْلَيْكَۚ اِنَّكَ بِالْوَادِ الْمُقَدَّسِ طُوًىۜ', 'Muhakkak ki ben, evet ben senin Rabbinim! Hemen pabuçlarını çıkar! Çünkü sen kutsal vâdi Tuvâ´dasın!', 16, 311, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2361, 13, 20, 'Ta-Ha', 'وَاَنَا اخْتَرْتُكَ فَاسْتَمِـعْ لِمَا يُوحٰى', 'Ben seni seçtim. Şimdi vahyedilene kulak ver.', 16, 312, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2362, 14, 20, 'Ta-Ha', 'اِنَّـن۪ٓي اَنَا اللّٰهُ لَٓا اِلٰهَ اِلَّٓا اَنَا۬ فَاعْبُدْن۪يۙ وَاَقِمِ الصَّلٰوةَ لِذِكْر۪ي', 'Muhakkak ki ben, yalnızca ben Allah´ım. Benden başka ilâh yoktur. Bana kulluk et; beni anmak için namaz kıl.', 16, 312, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2363, 15, 20, 'Ta-Ha', 'اِنَّ السَّاعَةَ اٰتِيَةٌ اَكَادُ اُخْف۪يهَا لِتُجْزٰى كُلُّ نَفْسٍ بِمَا تَسْعٰى', 'Kıyamet günü mutlaka gelecektir. Herkes peşine koştuğu şeyin karşılığını bulsun diye neredeyse onu (kendimden) gizleyeceğim.', 16, 312, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2364, 16, 20, 'Ta-Ha', 'فَلَا يَصُدَّنَّكَ عَنْهَا مَنْ لَا يُؤْمِنُ بِهَا وَاتَّـبَعَ هَوٰيهُ فَتَرْدٰى', 'Ona inanmayan ve nefsinin arzularına uyan kimseler sakın seni ondan (kıyamete inanmaktan) alıkoymasın; sonra mahvolursun!', 16, 312, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2365, 17, 20, 'Ta-Ha', 'وَمَا تِلْكَ بِيَم۪ينِكَ يَا مُوسٰى', 'Şu sağ elindeki nedir, ey Musa?', 16, 312, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2366, 18, 20, 'Ta-Ha', 'قَالَ هِيَ عَصَايَۚ اَتَوَكَّـؤُ۬ا عَلَيْهَا وَاَهُشُّ بِهَا عَلٰى غَنَم۪ي وَلِيَ ف۪يهَا مَاٰرِبُ اُخْرٰى', 'O, benim asamdır, dedi, ona dayanırım, onunla davarlarıma yaprak silkelerim; benim ona başkaca ihtiyaçlarım da vardır.', 16, 312, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2367, 19, 20, 'Ta-Ha', 'قَالَ اَلْقِهَا يَا مُوسٰى', 'Allah: Yere at onu, ey Musa! dedi.', 16, 312, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2368, 20, 20, 'Ta-Ha', 'فَاَلْقٰيهَا فَاِذَا هِيَ حَيَّةٌ تَسْعٰى', 'Onu hemen yere attı. Bir de ne görsün, hızla sürünen bir yılan değil mi!', 16, 312, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2369, 21, 20, 'Ta-Ha', 'قَالَ خُذْهَا وَلَا تَخَفْ۠ سَنُع۪يدُهَا س۪يرَتَهَا الْاُو۫لٰى', 'Allah buyurdu: Al onu! Korkma! Biz onu şimdi ilk haline sokacağız.', 16, 312, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2370, 22, 20, 'Ta-Ha', 'وَاضْمُمْ يَدَكَ اِلٰى جَنَاحِكَ تَخْرُجْ بَيْضَٓاءَ مِنْ غَيْرِ سُٓوءٍ اٰيَةً اُخْرٰىۙ', 'Bir de elini koltuğunun altına sok ki, bir başka mucize olmak üzere o, kusursuz ve lekesiz beyazlıkta çıksın.', 16, 312, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2371, 23, 20, 'Ta-Ha', 'لِنُرِيَكَ مِنْ اٰيَاتِنَا الْكُبْرٰىۚ', 'Ta ki, sana, (böylece) en büyük âyetlerimizden bazılarını gösterelim.', 16, 312, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2372, 24, 20, 'Ta-Ha', 'اِذْهَبْ اِلٰى فِرْعَوْنَ اِنَّهُ طَغٰى۟', 'Firavun´a git. Çünkü o iyice azdı.', 16, 312, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2373, 25, 20, 'Ta-Ha', 'قَالَ رَبِّ اشْرَحْ ل۪ي صَدْر۪يۙ', 'Musa: Rabbim! dedi, yüreğime genişlik ver.', 16, 312, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2374, 26, 20, 'Ta-Ha', 'وَيَسِّرْ ل۪ٓي اَمْر۪يۙ', 'İşimi bana kolaylaştır.', 16, 312, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2375, 27, 20, 'Ta-Ha', 'وَاحْلُلْ عُقْدَةً مِنْ لِسَان۪يۙ', 'Dilimden (şu) bağı çöz.', 16, 312, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2376, 28, 20, 'Ta-Ha', 'يَفْقَهُوا قَوْل۪يۖ', 'Ki sözümü anlasınlar.', 16, 312, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2377, 29, 20, 'Ta-Ha', 'وَاجْعَلْ ل۪ي وَز۪يراً مِنْ اَهْل۪يۙ', 'Bana ailemden bir de vezir (yardımcı) ver,', 16, 312, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2378, 30, 20, 'Ta-Ha', 'هٰرُونَ اَخ۪يۚ', 'Kardeşim Harun´u.', 16, 312, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2379, 31, 20, 'Ta-Ha', 'اُشْدُدْ بِه۪ٓ اَزْر۪يۙ', 'Onun sayesinde arkamı kuvvetlendir.', 16, 312, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2380, 32, 20, 'Ta-Ha', 'وَاَشْرِكْهُ ف۪ٓي اَمْر۪يۙ', 'Ve onu işime ortak kıl.', 16, 312, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2381, 33, 20, 'Ta-Ha', 'كَيْ نُسَبِّحَكَ كَث۪يراًۙ', 'Böylece seni bol bol tesbih edelim.', 16, 312, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2382, 34, 20, 'Ta-Ha', 'وَنَذْكُرَكَ كَث۪يراًۜ', 'Ve çok çok analım seni.', 16, 312, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2383, 35, 20, 'Ta-Ha', 'اِنَّكَ كُنْتَ بِنَا بَص۪يراً', 'Şüphesiz sen bizi görmektesin.', 16, 312, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2384, 36, 20, 'Ta-Ha', 'قَالَ قَدْ اُو۫ت۪يتَ سُؤْلَكَ يَا مُوسٰى', 'Allah: Ey Musa! dedi, istediğin sana verildi.', 16, 312, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2385, 37, 20, 'Ta-Ha', 'وَلَقَدْ مَنَنَّا عَلَيْكَ مَرَّةً اُخْرٰىۙ', 'Andolsun biz sana bir defa daha lütufta bulunmuştuk.', 16, 312, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2386, 38, 20, 'Ta-Ha', 'اِذْ اَوْحَيْنَٓا اِلٰٓى اُمِّكَ مَا يُوحٰىۙ', 'Bir zaman, vahyedilecek şeyi annene (şöyle) vahyetmiştik:', 16, 313, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2387, 39, 20, 'Ta-Ha', 'اَنِ اقْذِف۪يهِ فِي التَّابُوتِ فَاقْذِف۪يهِ فِي الْيَمِّ فَلْيُلْقِهِ الْيَمُّ بِالسَّاحِلِ يَأْخُذْهُ عَدُوٌّ ل۪ي وَعَدُوٌّ لَهُۜ وَاَلْقَيْتُ عَلَيْكَ مَحَبَّةً مِنّ۪يۚ وَلِتُصْنَعَ عَلٰى عَيْن۪يۢ', 'Musa´yı sandığa koy; sonra onu denize (Nil´e) bırak; deniz onu kıyıya atsın da, benim düşmanım ve onun düşmanı olan biri onu alsın. (Ey Musa! Sevilmen) ve benim nezaretimde yetiştirilmen için sana kendimden sevgi verdim.', 16, 313, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2388, 40, 20, 'Ta-Ha', 'اِذْ تَمْش۪ٓي اُخْتُكَ فَتَقُولُ هَلْ اَدُلُّكُمْ عَلٰى مَنْ يَكْفُلُهُۜ فَرَجَعْنَاكَ اِلٰٓى اُمِّكَ كَيْ تَقَرَّ عَيْنُهَا وَلَا تَحْزَنَۜ وَقَتَلْتَ نَفْساً فَنَجَّيْنَاكَ مِنَ الْغَمِّ وَفَتَنَّاكَ فُتُوناً۠ فَلَبِثْتَ سِن۪ينَ ف۪ٓي اَهْلِ مَدْيَنَ ثُمَّ جِئْتَ عَلٰى قَدَرٍ يَا مُوسٰى', 'Hani, kız kardeşin gidip «Ona bakacak birini size bulayım mı?» diyordu. Böylece seni, gözü gönlü mutluluk dolsun ve üzülmesin diye annene geri verdik. Ve sen, birini öldürdün de seni endişeden kurtardık. Seni iyiden iyiye denemeden geçirdik. Bunun için yıllarca Medyen halkı arasında kaldın. Sonra takdire göre (bu makama) geldin ey Musa!', 16, 313, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2389, 41, 20, 'Ta-Ha', 'وَاصْطَنَعْتُكَ لِنَفْس۪يۚ', 'Seni, kendim için elçi seçtim.', 16, 313, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2390, 42, 20, 'Ta-Ha', 'اِذْهَبْ اَنْتَ وَاَخُوكَ بِاٰيَات۪ي وَلَا تَنِيَا ف۪ي ذِكْر۪يۚ', 'Sen ve kardeşin birlikte âyetlerimi götürün. Beni anmayı ihmal etmeyin.', 16, 313, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2391, 43, 20, 'Ta-Ha', 'اِذْهَبَٓا اِلٰى فِرْعَوْنَ اِنَّهُ طَغٰىۚ', 'Firavun´a gidin. Çünkü o, iyiden iyiye azdı.', 16, 313, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2392, 44, 20, 'Ta-Ha', 'فَقُولَا لَهُ قَوْلاً لَيِّناً لَعَلَّهُ يَتَذَكَّرُ اَوْ يَخْشٰى', 'Ona yumuşak söz söyleyin. Belki o, aklını başına alır veya korkar.', 16, 313, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2393, 45, 20, 'Ta-Ha', 'قَالَا رَبَّـنَٓا اِنَّـنَا نَخَافُ اَنْ يَفْرُطَ عَلَيْنَٓا اَوْ اَنْ يَطْغٰى', 'Dediler ki: Rabbimiz! Doğrusu biz, onun bize aşırı derecede kötü davranmasından yahut iyice azmasından endişe ediyoruz.', 16, 313, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2394, 46, 20, 'Ta-Ha', 'قَالَ لَا تَخَافَٓا اِنَّن۪ي مَعَكُمَٓا اَسْمَعُ وَاَرٰى', 'Buyurdu ki: Korkmayın, çünkü ben sizinle beraberim; işitir ve görürüm.', 16, 313, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2395, 47, 20, 'Ta-Ha', 'فَأْتِيَاهُ فَقُولَٓا اِنَّا رَسُولَا رَبِّكَ فَاَرْسِلْ مَعَنَا بَن۪ٓي اِسْرَٓائ۪لَ وَلَا تُعَذِّبْهُمْۜ قَدْ جِئْنَاكَ بِاٰيَةٍ مِنْ رَبِّكَۜ وَالسَّلَامُ عَلٰى مَنِ اتَّـبَعَ الْهُدٰى', 'Haydi, ona gidin de deyin ki: Biz, senin Rabbinin elçileriyiz. İsrailoğullarını hemen bizimle birlikte gönder; onlara eziyet etme! Biz, senin Rabbinden bir âyet getirdik. Kurtuluş, hidayete uyanlarındır.', 16, 313, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2396, 48, 20, 'Ta-Ha', 'اِنَّا قَدْ اُو۫حِيَ اِلَيْنَٓا اَنَّ الْعَذَابَ عَلٰى مَنْ كَذَّبَ وَتَوَلّٰى', 'Hakikaten bize vahyolundu ki: (Peygamberleri) yalanlayan ve yüz çevirenlere azap edilecektir.', 16, 313, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2397, 49, 20, 'Ta-Ha', 'قَالَ فَمَنْ رَبُّكُمَا يَا مُوسٰى', 'Firavun: Rabbiniz de kimmiş, ey Musa? dedi.', 16, 313, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2398, 50, 20, 'Ta-Ha', 'قَالَ رَبُّنَا الَّـذ۪ٓي اَعْطٰى كُلَّ شَيْءٍ خَلْقَهُ ثُمَّ هَدٰى', 'O da: Bizim Rabbimiz, her şeye hılkatini (varlık ve özelliğini) veren, sonra da doğru yolu gösterendir, dedi.', 16, 313, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2399, 51, 20, 'Ta-Ha', 'قَالَ فَمَا بَالُ الْقُرُونِ الْاُو۫لٰى', 'Firavun: Öyle ise, önceki milletlerin hali ne olacak? dedi.', 16, 313, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2400, 52, 20, 'Ta-Ha', 'قَالَ عِلْمُهَا عِنْدَ رَبّ۪ي ف۪ي كِتَابٍۚ لَا يَضِلُّ رَبّ۪ي وَلَا يَنْسٰىۘ', 'Musa: Onlar hakkındaki bilgi, Rabbimin yanında bir kitapta bulunur. Rabbim, ne yanılır ne de unutur, dedi.', 16, 314, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2401, 53, 20, 'Ta-Ha', 'اَلَّذ۪ي جَعَلَ لَكُمُ الْاَرْضَ مَهْداً وَسَلَكَ لَكُمْ ف۪يهَا سُبُلاً وَاَنْزَلَ مِنَ السَّمَٓاءِ مَٓاءًۜ فَاَخْرَجْنَا بِه۪ٓ اَزْوَاجاً مِنْ نَبَاتٍ شَتّٰى', 'O, yeri size beşik yapan ve onda size yollar açan, gökten de su indirendir. Onunla biz çeşitli bitkilerden çiftler çıkardık.', 16, 314, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2402, 54, 20, 'Ta-Ha', 'كُلُوا وَارْعَوْا اَنْعَامَكُمْۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَاتٍ لِاُو۬لِي النُّهٰى۟', 'Yeyiniz; hayvanlarınızı otlatınız. Şüphesiz bunda akıl sahipleri için (Allah´ın kudretine) işaretler vardır.', 16, 314, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2403, 55, 20, 'Ta-Ha', 'مِنْهَا خَلَقْنَاكُمْ وَف۪يهَا نُع۪يدُكُمْ وَمِنْهَا نُخْرِجُكُمْ تَارَةً اُخْرٰى', 'Sizi ondan (topraktan) yarattık; yine sizi oraya döndüreceğiz ve bir kez daha sizi ondan çıkaracağız.', 16, 314, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2404, 56, 20, 'Ta-Ha', 'وَلَقَدْ اَرَيْنَاهُ اٰيَاتِنَا كُلَّهَا فَكَذَّبَ وَاَبٰى', 'Andolsun biz ona (Firavun´a) bütün (bu) delillerimizi gösterdik; yine de yalanladı ve diretti.', 16, 314, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2405, 57, 20, 'Ta-Ha', 'قَالَ اَجِئْتَنَا لِتُخْرِجَنَا مِنْ اَرْضِنَا بِسِحْرِكَ يَا مُوسٰى', 'Dedi ki: Bizi, yaptığın büyü ile yurdumuzdan çıkarasın diye mi geldin, ey Musa?', 16, 314, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2406, 58, 20, 'Ta-Ha', 'فَلَنَأْتِيَنَّكَ بِسِحْرٍ مِثْلِه۪ فَاجْعَلْ بَيْنَنَا وَبَيْنَكَ مَوْعِداً لَا نُخْلِفُهُ نَحْنُ وَلَٓا اَنْتَ مَكَاناً سُوًى', 'Öyle ise, muhakkak surette biz de sana, aynen onun gibi bir büyü getireceğiz. Şimdi sen, seninle bizim aramızda, ne senin, ne de bizim muhalefet etmeyeceğimiz uygun bir yerde buluşma zamanı ayarla.', 16, 314, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2407, 59, 20, 'Ta-Ha', 'قَالَ مَوْعِدُكُمْ يَوْمُ الزّ۪ينَةِ وَاَنْ يُحْشَرَ النَّاسُ ضُحًى', 'Musa: Buluşma zamanınız, bayram günü, kuşluk vaktinde insanların toplanma zamanı olsun, dedi.', 16, 314, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2408, 60, 20, 'Ta-Ha', 'فَتَوَلّٰى فِرْعَوْنُ فَجَمَعَ كَيْدَهُ ثُمَّ اَتٰى', 'Bunun üzerine Firavun dönüp gitti. Hilesini (sihirbazlarını) topladı; sonra geri geldi.', 16, 314, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2409, 61, 20, 'Ta-Ha', 'قَالَ لَهُمْ مُوسٰى وَيْلَكُمْ لَا تَفْتَرُوا عَلَى اللّٰهِ كَذِباً فَيُسْحِتَكُمْ بِعَذَابٍۚ وَقَدْ خَابَ مَنِ افْتَرٰى', 'Musa onlara: Yazık size! dedi, Allah hakkında yalan uydurmayın! Sonra O, bir azap ile kökünüzü keser! İftira eden, muhakkak perişan olur.', 16, 314, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2410, 62, 20, 'Ta-Ha', 'فَتَنَازَعُٓوا اَمْرَهُمْ بَيْنَهُمْ وَاَسَرُّوا النَّجْوٰى', 'Bunun üzerine onlar, durumlarını aralarında tartıştılar; gizli gizli fısıldaştılar.', 16, 314, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2411, 63, 20, 'Ta-Ha', 'قَالُٓوا اِنْ هٰذَانِ لَسَاحِرَانِ يُر۪يدَانِ اَنْ يُخْرِجَاكُمْ مِنْ اَرْضِكُمْ بِسِحْرِهِمَا وَيَذْهَبَا بِطَر۪يقَتِكُمُ الْمُثْلٰى', 'Şöyle dediler: «Bu ikisi, muhakkak ki, sihirleriyle sizi yurdunuzdan çıkarmak ve sizin örnek yolunuzu ortadan kaldırmak isteyen iki sihirbazdırlar sadece.»', 16, 314, 135, 5233, 'Ta-Ha', 'Mekke', 45);
INSERT INTO `Ayetler` (`id`, `AyetNo`, `SureNo`, `SureAdi`, `Metin`, `Mana`, `Cuzu`, `Sayfasi`, `AyetSayisi`, `HarfSayisi`, `AdAnlami`, `IndigiYer`, `InisSirasi`) VALUES
(2412, 64, 20, 'Ta-Ha', 'فَاَجْمِعُوا كَيْدَكُمْ ثُمَّ ائْتُوا صَفاًّۚ وَقَدْ اَفْلَحَ الْيَوْمَ مَنِ اسْتَعْلٰى', '«Öyle ise hilenizi kurun; sonra sıra halinde gelin! Muhakkak ki bugün, üstün gelen kazanmıştır.»', 16, 314, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2413, 65, 20, 'Ta-Ha', 'قَالُوا يَا مُوسٰٓى اِمَّٓا اَنْ تُلْقِيَ وَاِمَّٓا اَنْ نَكُونَ اَوَّلَ مَنْ اَلْقٰى', 'Dediler ki: Ey Musa! Ya sen at veya önce atan biz olalım.', 16, 315, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2414, 66, 20, 'Ta-Ha', 'قَالَ بَلْ اَلْقُواۚ فَاِذَا حِبَالُهُمْ وَعِصِيُّهُمْ يُخَيَّلُ اِلَيْهِ مِنْ سِحْرِهِمْ اَنَّهَا تَسْعٰى', 'Hayır, siz atın, dedi. Bir de baktı ki, büyüleri sayesinde ipleri ve sopaları, kendisine gerçekten koşuyor gibi görünüyor.', 16, 315, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2415, 67, 20, 'Ta-Ha', 'فَاَوْجَسَ ف۪ي نَفْسِه۪ خ۪يفَةً مُوسٰى', 'Musa, birden içinde bir korku duydu.', 16, 315, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2416, 68, 20, 'Ta-Ha', 'قُلْنَا لَا تَخَفْ اِنَّكَ اَنْتَ الْاَعْلٰى', '«Korkma! dedik, üstün gelecek olan kesinlikle sensin.»', 16, 315, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2417, 69, 20, 'Ta-Ha', 'وَاَلْقِ مَا ف۪ي يَم۪ينِكَ تَلْقَفْ مَا صَنَعُواۜ اِنَّمَا صَنَعُوا كَيْدُ سَاحِرٍۜ وَلَا يُفْلِحُ السَّاحِرُ حَيْثُ اَتٰى', '«Sağ elindekini at da, onların yaptıklarını yutsun. Yaptıkları, sadece bir büyücü hilesidir. Büyücü ise, nereye varsa (ne yapsa) iflah olmaz.»', 16, 315, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2418, 70, 20, 'Ta-Ha', 'فَاُلْقِيَ السَّحَرَةُ سُجَّداً قَالُٓوا اٰمَنَّا بِرَبِّ هٰرُونَ وَمُوسٰى', 'Bunun üzerine sihirbazlar secdeye kapandılar; «Harun´un ve Musa´nın Rabbine iman ettik» dediler.', 16, 315, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2419, 71, 20, 'Ta-Ha', 'قَالَ اٰمَنْتُمْ لَهُ قَبْلَ اَنْ اٰذَنَ لَكُمْۜ اِنَّهُ لَكَب۪يرُكُمُ الَّذ۪ي عَلَّمَكُمُ السِّحْرَۚ فَلَاُقَطِّعَنَّ اَيْدِيَكُمْ وَاَرْجُلَكُمْ مِنْ خِلَافٍ وَلَاُصَلِّبَنَّكُمْ ف۪ي جُذُوعِ النَّخْلِۘ وَلَتَعْلَمُنَّ اَيُّـنَٓا اَشَدُّ عَذَاباً وَاَبْقٰى', '(Firavun) Şöyle dedi: Ben size izin vermeden önce ona inandınız öyle mi! Hakikat şu ki o, size büyü öğreten ulunuzdur. Şimdi elleriniz ile ayaklarınızı tereddüt etmeden çaprazlama keseceğim ve sizi hurma dallarına asacağım! Böylece, hangimizin azabının daha şiddetli ve sürekli olduğunu iyice anlayacaksınız.', 16, 315, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2420, 72, 20, 'Ta-Ha', 'قَالُوا لَنْ نُؤْثِرَكَ عَلٰى مَا جَٓاءَنَا مِنَ الْبَيِّنَاتِ وَالَّذ۪ي فَطَرَنَا فَاقْضِ مَٓا اَنْتَ قَاضٍۜ اِنَّمَا تَقْض۪ي هٰذِهِ الْحَيٰوةَ الدُّنْيَاۜ', 'Dediler ki: «Seni, bize gelen açık açık mucizelere ve bizi yaratana tercih edemeyiz. Öyle ise yapacağını yap! Sen, ancak bu dünya hayatında hükmünü geçirebilirsin.»', 16, 315, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2421, 73, 20, 'Ta-Ha', 'اِنَّٓا اٰمَنَّا بِرَبِّنَا لِيَغْفِرَ لَنَا خَطَايَانَا وَمَٓا اَكْرَهْتَنَا عَلَيْهِ مِنَ السِّحْرِۜ وَاللّٰهُ خَيْرٌ وَاَبْقٰى', '«Bize, hatalarımızı ve senin bize zorla yaptırdığın büyüyü bağışlaması için Rabbimize iman ettik. Allah hem daha hayırlı hem daha bâkidir.»', 16, 315, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2422, 74, 20, 'Ta-Ha', 'اِنَّهُ مَنْ يَأْتِ رَبَّهُ مُجْرِماً فَاِنَّ لَهُ جَهَنَّمَۜ لَا يَمُوتُ ف۪يهَا وَلَا يَحْيٰى', 'Şurası muhakkak ki, kim Rabbine günahkâr olarak varırsa, cehennem sırf onun içindir. O ise orada ne ölür ne de yaşar!', 16, 315, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2423, 75, 20, 'Ta-Ha', 'وَمَنْ يَأْتِه۪ مُؤْمِناً قَدْ عَمِلَ الصَّالِحَاتِ فَاُو۬لٰٓئِكَ لَهُمُ الدَّرَجَاتُ الْعُلٰىۙ', 'Kim de iyi davranışlarda bulunmuş bir mümin olarak O´na varırsa, üstün dereceler işte sırf bunlar içindir.', 16, 315, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2424, 76, 20, 'Ta-Ha', 'جَنَّاتُ عَدْنٍ تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُ خَالِد۪ينَ ف۪يهَاۜ وَذٰلِكَ جَزٰٓؤُ۬ا مَنْ تَزَكّٰى۟', 'İçinde ebedî kalacakları, zemininden ırmaklar akan Adn cennetleri! İşte arınanların mükâfatı budur.', 16, 315, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2425, 77, 20, 'Ta-Ha', 'وَلَقَدْ اَوْحَيْنَٓا اِلٰى مُوسٰٓى اَنْ اَسْرِ بِعِبَاد۪ي فَاضْرِبْ لَهُمْ طَر۪يقاً فِي الْبَحْرِ يَبَساًۚ لَا تَخَافُ دَرَكاً وَلَا تَخْشٰى', 'Andolsun ki biz Musa´ya: Kullarımla birlikte geceleyin yola çık da (size) yetişilmesinden korkmaksızın ve (boğulmaktan) endişe etmeksizin onlara denizde kuru bir yol aç, diye vahyetmiştik.', 16, 316, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2426, 78, 20, 'Ta-Ha', 'فَاَتْبَعَهُمْ فِرْعَوْنُ بِجُنُودِه۪ فَغَشِيَهُمْ مِنَ الْيَمِّ مَا غَشِيَهُمْۜ', 'Bunun üzerine Firavun, askerleri ile birlikte onların peşine düştü. Deniz onları gömüp boğuverdi.', 16, 316, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2427, 79, 20, 'Ta-Ha', 'وَاَضَلَّ فِرْعَوْنُ قَوْمَهُ وَمَا هَدٰى', 'Firavun, kavmini saptırdı, doğru yola sevketmedi.', 16, 316, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2428, 80, 20, 'Ta-Ha', 'يَا بَن۪ٓي اِسْرَٓائ۪لَ قَدْ اَنْجَيْنَاكُمْ مِنْ عَدُوِّكُمْ وَوٰعَدْنَاكُمْ جَانِبَ الطُّورِ الْاَيْمَنَ وَنَزَّلْنَا عَلَيْكُمُ الْمَنَّ وَالسَّلْوٰى', 'Ey İsrailoğulları! Sizi düşmanınızdan kurtardık; Tûr´un sağ tarafına (gelmeniz için) size vâde tanıdık ve size kudret helvası ile bıldırcın eti lütfettik.', 16, 316, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2429, 81, 20, 'Ta-Ha', 'كُلُوا مِنْ طَيِّبَاتِ مَا رَزَقْنَاكُمْ وَلَا تَطْغَوْا ف۪يهِ فَيَحِلَّ عَلَيْكُمْ غَضَب۪يۚ وَمَنْ يَحْلِلْ عَلَيْهِ غَضَب۪ي فَقَدْ هَوٰى', 'Size rızık olarak verdiklerimizin temiz olanlarından yeyiniz, bu hususta taşkınlık ve nankörlük de etmeyiniz; sonra sizi gazabım çarpar. Her kim ki kendisini gazabım çarparsa, hakikaten o, yıkılıp gitmiştir.', 16, 316, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2430, 82, 20, 'Ta-Ha', 'وَاِنّ۪ي لَغَفَّارٌ لِمَنْ تَابَ وَاٰمَنَ وَعَمِلَ صَالِحاً ثُمَّ اهْتَدٰى', 'Şu da muhakkak ki ben, tevbe eden, inanan ve yararlı iş yapan, sonra (böylece) doğru yolda giden kimseyi bağışlarım.', 16, 316, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2431, 83, 20, 'Ta-Ha', 'وَمَٓا اَعْجَلَكَ عَنْ قَوْمِكَ يَا مُوسٰى', 'Seni acele ile kavminden ayrılmaya sevkeden nedir, ey Musa!', 16, 316, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2432, 84, 20, 'Ta-Ha', 'قَالَ هُمْ اُو۬لَٓاءِ عَلٰٓى اَثَر۪ي وَعَجِلْتُ اِلَيْكَ رَبِّ لِتَرْضٰى', 'Musa: İşte, dedi, onlar da benim peşimdeler. Ben, memnun olasın diye sana acele ile geldim Rabbim.', 16, 316, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2433, 85, 20, 'Ta-Ha', 'قَالَ فَاِنَّا قَدْ فَتَنَّا قَوْمَكَ مِنْ بَعْدِكَ وَاَضَلَّهُمُ السَّامِرِيُّ', 'Allah buyurdu: Senden sonra biz, kavmini (Harun ile kalan İsrailoğullarını) imtihan ettik ve Sâmirî onları yoldan çıkardı.', 16, 316, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2434, 86, 20, 'Ta-Ha', 'فَرَجَعَ مُوسٰٓى اِلٰى قَوْمِه۪ غَضْبَانَ اَسِفاًۚ قَالَ يَا قَوْمِ اَلَمْ يَعِدْكُمْ رَبُّكُمْ وَعْداً حَسَناًۜ اَفَطَالَ عَلَيْكُمُ الْعَهْدُ اَمْ اَرَدْتُمْ اَنْ يَحِلَّ عَلَيْكُمْ غَضَبٌ مِنْ رَبِّكُمْ فَاَخْلَفْتُمْ مَوْعِد۪ي', 'Bunun üzerine Musa, öfkeli ve üzüntülü olarak kavmine döndü. Ey kavmim! dedi, Rabbiniz size güzel bir vaadde bulunmamış mıydı? Şu halde size zaman mı çok uzun geldi, yoksa üstünüze Rabbinizin gazabının inmesini mi istediniz ki, bana olan vâdinizden döndünüz?', 16, 316, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2435, 87, 20, 'Ta-Ha', 'قَالُوا مَٓا اَخْلَفْنَا مَوْعِدَكَ بِمَلْكِنَا وَلٰكِنَّا حُمِّلْـنَٓا اَوْزَاراً مِنْ ز۪ينَةِ الْقَوْمِ فَقَذَفْنَاهَا فَكَذٰلِكَ اَلْقَى السَّامِرِيُّۙ', 'Dediler ki: Biz sana olan vâdimizden, kendi kudret ve irademizle dönmedik. Fakat biz, o kavmin (Mısır´lıların) zinet eşyasından bir takım ağırlıklar yüklenmiş, sonra da onları atmıştık; aynı şekilde Sâmirî de atmıştı.', 16, 316, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2436, 88, 20, 'Ta-Ha', 'فَاَخْرَجَ لَهُمْ عِجْلاً جَسَداً لَهُ خُوَارٌ فَقَالُوا هٰذَٓا اِلٰهُكُمْ وَاِلٰهُ مُوسٰى فَنَسِيَۜ', 'Bu adam, onlar için, böğürebilen bir buzağı heykeli icat etti. Bunun üzerine: İşte, dediler, bu, sizin de, Musa´nın da tanrısıdır. Fakat onu unuttu.', 16, 317, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2437, 89, 20, 'Ta-Ha', 'اَفَلَا يَرَوْنَ اَلَّا يَرْجِعُ اِلَيْهِمْ قَوْلاًۙ وَلَا يَمْلِكُ لَهُمْ ضَراًّ وَلَا نَفْعاً۟', 'O şeyin, kendilerine hiçbir sözle mukabele edemeyeceğini, kendilerine ne bir zarar ne de bir fayda vermek gücünde olmadığını görmezler mi?', 16, 317, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2438, 90, 20, 'Ta-Ha', 'وَلَقَدْ قَالَ لَهُمْ هٰرُونُ مِنْ قَبْلُ يَا قَوْمِ اِنَّمَا فُتِنْتُمْ بِه۪ۚ وَاِنَّ رَبَّكُمُ الرَّحْمٰنُ فَاتَّبِعُون۪ي وَاَط۪يعُٓوا اَمْر۪ي', 'Hakikaten Harun, onlara daha önce: Ey kavmim! demişti, siz bunun yüzünden sadece fitneye uğradınız. Sizin Rabbiniz şüphesiz çok merhametli olan Allah´tır. Şu halde bana uyunuz ve emrime itaat ediniz.', 16, 317, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2439, 91, 20, 'Ta-Ha', 'قَالُوا لَنْ نَبْرَحَ عَلَيْهِ عَاكِف۪ينَ حَتّٰى يَرْجِعَ اِلَيْنَا مُوسٰى', 'Onlar: Biz, dediler, Musa aramıza dönünceye kadar buna tapmaktan asla vazgeçmeyeceğiz!', 16, 317, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2440, 92, 20, 'Ta-Ha', 'قَالَ يَا هٰرُونُ مَا مَنَعَكَ اِذْ رَاَيْتَهُمْ ضَلُّواۙ', '(92-93) (Musa, döndüğünde:) Ey Harun! dedi, sana ne engel oldu da, bunların dalâlete düştüklerini gördüğün vakit peşimden gelmedin? Emrime âsi mi oldun?', 16, 317, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2441, 93, 20, 'Ta-Ha', 'اَلَّا تَتَّبِعَنِۜ اَفَعَصَيْتَ اَمْر۪ي', '(92-93) (Musa, döndüğünde:) Ey Harun! dedi, sana ne engel oldu da, bunların dalâlete düştüklerini gördüğün vakit peşimden gelmedin? Emrime âsi mi oldun?', 16, 317, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2442, 94, 20, 'Ta-Ha', 'قَالَ يَبْنَؤُ۬مَّ لَا تَأْخُذْ بِلِحْيَت۪ي وَلَا بِرَأْس۪يۚ اِنّ۪ي خَش۪يتُ اَنْ تَقُولَ فَرَّقْتَ بَيْنَ بَن۪ٓي اِسْرَٓائ۪لَ وَلَمْ تَرْقُبْ قَوْل۪ي', '(Harun:) Ey annemin oğlu! dedi, saçımı sakalımı, yolma! Ben, senin: «İsrailoğullarının arasına ayrılık düşürdün; sözümü tutmadın!» demenden korktum.', 16, 317, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2443, 95, 20, 'Ta-Ha', 'قَالَ فَمَا خَطْبُكَ يَا سَامِرِيُّ', 'Musa: Ya senin zorun nedir, ey Sâmirî? dedi.', 16, 317, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2444, 96, 20, 'Ta-Ha', 'قَالَ بَصُرْتُ بِمَا لَمْ يَبْصُرُوا بِه۪ فَقَبَضْتُ قَبْضَةً مِنْ اَثَرِ الرَّسُولِ فَنَبَذْتُهَا وَكَذٰلِكَ سَوَّلَتْ ل۪ي نَفْس۪ي', 'O da: Ben, onların görmediklerini gördüm. Zira, o elçinin izinden bir avuç (toprak) alıp onu (erimiş mücevheratın içine) attım. Bunu böyle nefsim bana hoş gösterdi, dedi.', 16, 317, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2445, 97, 20, 'Ta-Ha', 'قَالَ فَاذْهَبْ فَاِنَّ لَكَ فِي الْحَيٰوةِ اَنْ تَقُولَ لَا مِسَاسَۖ وَاِنَّ لَكَ مَوْعِداً لَنْ تُخْلَفَهُۚ وَانْظُرْ اِلٰٓى اِلٰهِكَ الَّذ۪ي ظَلْتَ عَلَيْهِ عَاكِفاًۜ لَنُحَرِّقَنَّهُ ثُمَّ لَنَنْسِفَنَّهُ فِي الْيَمِّ نَسْفاً', 'Musa: Defol! dedi, artık hayatın boyunca sen: «Bana dokunmayın!» diyeceksin. Ayrıca senin için, kurtulamayacağın bir ceza günü var. Tapmakta olduğun tanrına da bak! Yemin ederim, biz onu yakacağız; sonra da onu parça parça edip denize savuracağız!', 16, 317, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2446, 98, 20, 'Ta-Ha', 'اِنَّـمَٓا اِلٰهُكُمُ اللّٰهُ الَّذ۪ي لَٓا اِلٰهَ اِلَّا هُوَۜ وَسِعَ كُلَّ شَيْءٍ عِلْماً', 'Sizin ilâhınız, yalnızca, kendisinden başka ilâh olmayan Allah´tır. O´nun ilmi her şeyi kuşatmıştır.', 16, 317, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2447, 99, 20, 'Ta-Ha', 'كَذٰلِكَ نَقُصُّ عَلَيْكَ مِنْ اَنْـبَٓاءِ مَا قَدْ سَبَقَۚ وَقَدْ اٰتَيْنَاكَ مِنْ لَدُنَّا ذِكْراًۚ', '(Resûlüm!) İşte böylece geçmiştekilerin haberlerinden bir kısmını sana anlatıyoruz. Şüphesiz ki, tarafımızdan sana bir zikir verdik.', 16, 318, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2448, 100, 20, 'Ta-Ha', 'مَنْ اَعْرَضَ عَنْهُ فَاِنَّهُ يَحْمِلُ يَوْمَ الْقِيٰمَةِ وِزْراًۙ', 'Kim ondan yüz çevirirse, şüphesiz ki kıyamet gününde o, ağır bir günah yükünü yüklenecektir.', 16, 318, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2449, 101, 20, 'Ta-Ha', 'خَالِد۪ينَ ف۪يهِۜ وَسَٓاءَ لَهُمْ يَوْمَ الْقِيٰمَةِ حِمْلاًۙ', 'Bu kimseler, onda (o günah yükünün altında) ebedî kalırlar. Onlar için kıyamet gününde bu ne kötü bir yüktür!', 16, 318, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2450, 102, 20, 'Ta-Ha', 'يَوْمَ يُنْفَخُ فِي الصُّورِ وَنَحْشُرُ الْمُجْرِم۪ينَ يَوْمَئِذٍ زُرْقاًۚ', 'O günde Sûr´a üflenir ve biz o zaman günahkârları, gözleri (korkudan) gömgök bir halde mahşerde toplarız.', 16, 318, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2451, 103, 20, 'Ta-Ha', 'يَتَخَافَتُونَ بَيْنَهُمْ اِنْ لَبِثْتُمْ اِلَّا عَشْراً', 'Aralarında birbirlerine gizli gizli şöyle derler: «Dünyada sadece on gün kaldınız.»', 16, 318, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2452, 104, 20, 'Ta-Ha', 'نَحْنُ اَعْلَمُ بِمَا يَقُولُونَ اِذْ يَقُولُ اَمْثَلُهُمْ طَر۪يقَةً اِنْ لَبِثْتُمْ اِلَّا يَوْماً۟', 'Aralarında konuştukları konuyu biz daha iyi biliriz. Onların en olgun ve akıllı olanı o zaman: «Bir günden fazla kalmadınız» der.', 16, 318, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2453, 105, 20, 'Ta-Ha', 'وَيَسْـَٔلُونَكَ عَنِ الْجِبَالِ فَقُلْ يَنْسِفُهَا رَبّ۪ي نَسْفاًۙ', '(Resûlüm!) Sana dağlar hakkında sorarlar. De ki: Rabbim onları ufalayıp savuracak.', 16, 318, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2454, 106, 20, 'Ta-Ha', 'فَيَذَرُهَا قَاعاً صَفْصَفاًۙ', 'Böylece yerlerini dümdüz, bomboş bırakacaktır.', 16, 318, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2455, 107, 20, 'Ta-Ha', 'لَا تَرٰى ف۪يهَا عِوَجاً وَلَٓا اَمْتاً', 'Orada ne bir iniş, ne de bir yokuş görebileceksin.', 16, 318, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2456, 108, 20, 'Ta-Ha', 'يَوْمَئِذٍ يَتَّبِعُونَ الدَّاعِيَ لَا عِوَجَ لَهُۚ وَخَشَعَتِ الْاَصْوَاتُ لِلرَّحْمٰنِ فَلَا تَسْمَعُ اِلَّا هَمْساً', 'O gün insanlar, dâvetçiye (İsrafil´e) uyacaklar. Ona karşı yan çizmek yoktur. Artık, çok esirgeyici Allah hürmetine sesler kısılmıştır. Bu yüzden, fısıltıdan başka bir ses işitemezsin.', 16, 318, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2457, 109, 20, 'Ta-Ha', 'يَوْمَئِذٍ لَا تَنْفَعُ الشَّفَاعَةُ اِلَّا مَنْ اَذِنَ لَهُ الرَّحْمٰنُ وَرَضِيَ لَهُ قَوْلاً', 'O gün, Rahmân´ın izin verdiği ve sözünden hoşlandığından başkasının şefaati fayda vermez.', 16, 318, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2458, 110, 20, 'Ta-Ha', 'يَعْلَمُ مَا بَيْنَ اَيْد۪يهِمْ وَمَا خَلْفَهُمْ وَلَا يُح۪يطُونَ بِه۪ عِلْماً', 'O, insanların geleceklerini de geçmişlerini de bilir. Onların ilmi ise bunu kapsayamaz.', 16, 318, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2459, 111, 20, 'Ta-Ha', 'وَعَنَتِ الْوُجُوهُ لِلْحَيِّ الْقَيُّومِۜ وَقَدْ خَابَ مَنْ حَمَلَ ظُلْماً', 'Bütün yüzler (insanlar), diri ve her şeye hakim olan Allah için eğilip boyun bükmüştür. Zulüm yüklenen ise, gerçekten perişan olmuştur.', 16, 318, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2460, 112, 20, 'Ta-Ha', 'وَمَنْ يَعْمَلْ مِنَ الصَّالِحَاتِ وَهُوَ مُؤْمِنٌ فَلَا يَخَافُ ظُلْماً وَلَا هَضْماً', 'Her kim, mümin olarak iyi olan işlerden yaparsa, artık o, ne zulümden ne de hakkının çiğnenmesinden korkar.', 16, 318, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2461, 113, 20, 'Ta-Ha', 'وَكَذٰلِكَ اَنْزَلْنَاهُ قُرْاٰناً عَرَبِياًّ وَصَرَّفْنَا ف۪يهِ مِنَ الْوَع۪يدِ لَعَلَّهُمْ يَتَّقُونَ اَوْ يُحْدِثُ لَهُمْ ذِكْراً', '(Resûlüm!) Biz onu böylece Arapça bir Kur´an olarak indirdik ve onda ikazları tekrar tekrar açıkladık. Umulur ki onlar (bu sayede günahtan) korunurlar; yahut da o (Kur´an) kendileri için bir ibret ortaya koyar.', 16, 318, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2462, 114, 20, 'Ta-Ha', 'فَتَعَالَى اللّٰهُ الْمَلِكُ الْحَقُّۚ وَلَا تَعْجَلْ بِالْقُرْاٰنِ مِنْ قَبْلِ اَنْ يُقْضٰٓى اِلَيْكَ وَحْيُهُۘ وَقُلْ رَبِّ زِدْن۪ي عِلْماً', 'Gerçek hükümdar olan Allah, yücedir. Sana O´nun vahyi tamamlanmazdan önce Kur´an´ı (okumakta) acele etme ve «Rabbim, benim ilmimi artır» de.', 16, 319, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2463, 115, 20, 'Ta-Ha', 'وَلَقَدْ عَهِدْنَٓا اِلٰٓى اٰدَمَ مِنْ قَبْلُ فَنَسِيَ وَلَمْ نَجِدْ لَهُ عَزْماً۟', 'Andolsun biz, daha önce de Âdem´e ahit (emir ve vahiy) vermiştik. Ne var ki o, (ahdi) unuttu. Onda azim de bulmadık.', 16, 319, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2464, 116, 20, 'Ta-Ha', 'وَاِذْ قُلْنَا لِلْمَلٰٓئِكَةِ اسْجُدُوا لِاٰدَمَ فَسَجَدُٓوا اِلَّٓا اِبْل۪يسَۜ اَبٰى', 'Bir zaman biz meleklere: Âdem´e secde edin! demiştik. Onlar hemen secde ettiler; yalnız İblis hariç. O, diretti.', 16, 319, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2465, 117, 20, 'Ta-Ha', 'فَقُلْنَا يَٓا اٰدَمُ اِنَّ هٰذَا عَدُوٌّ لَكَ وَلِزَوْجِكَ فَلَا يُخْرِجَنَّكُمَا مِنَ الْجَنَّةِ فَتَشْقٰى', 'Bunun üzerine: Ey Âdem! dedik, bu, hem senin için hem de eşin için büyük bir düşmandır. Sakın sizi cennetten çıkarmasın; sonra yorulur, sıkıntı çekersin!', 16, 319, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2466, 118, 20, 'Ta-Ha', 'اِنَّ لَكَ اَلَّا تَجُوعَ ف۪يهَا وَلَا تَعْرٰىۙ', 'Şimdi burada senin için ne acıkmak vardır, ne de çıplak kalmak.', 16, 319, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2467, 119, 20, 'Ta-Ha', 'وَاَنَّكَ لَا تَظْمَؤُ۬ا ف۪يهَا وَلَا تَضْحٰى', 'Yine burada sen, susuzluk çekmeyecek, sıcaktan da bunalmayacaksın.', 16, 319, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2468, 120, 20, 'Ta-Ha', 'فَوَسْوَسَ اِلَيْهِ الشَّيْطَانُ قَالَ يَٓا اٰدَمُ هَلْ اَدُلُّكَ عَلٰى شَجَرَةِ الْخُلْدِ وَمُلْكٍ لَا يَبْلٰى', 'Derken şeytan onun aklını karıştırıp «Ey Âdem! dedi, sana ebedîlik ağacını ve sonu gelmez bir saltanatı göstereyim mi?»', 16, 319, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2469, 121, 20, 'Ta-Ha', 'فَاَكَلَا مِنْهَا فَبَدَتْ لَهُمَا سَوْاٰتُهُمَا وَطَفِقَا يَخْصِفَانِ عَلَيْهِمَا مِنْ وَرَقِ الْجَنَّةِۘ وَعَصٰٓى اٰدَمُ رَبَّهُ فَغَوٰىۖ', 'Nihayet ondan yediler. Bunun üzerine kendilerine ayıp yerleri göründü. Üstlerini cennet yaprağı ile örtmeye çalıştılar. (Bu suretle) Âdem Rabbine âsi olup yolunu şaşırdı.', 16, 319, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2470, 122, 20, 'Ta-Ha', 'ثُمَّ اجْتَبٰيهُ رَبُّهُ فَتَابَ عَلَيْهِ وَهَدٰى', 'Sonra Rabbi onu seçkin kıldı; tevbesini kabul etti ve doğru yola yöneltti.', 16, 319, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2471, 123, 20, 'Ta-Ha', 'قَالَ اهْبِطَا مِنْهَا جَم۪يعاً بَعْضُكُمْ لِبَعْضٍ عَدُوٌّۚ فَاِمَّا يَأْتِيَنَّكُمْ مِنّ۪ي هُدًى فَمَنِ اتَّـبَعَ هُدَايَ فَلَا يَضِلُّ وَلَا يَشْقٰى', 'Dedi ki: Birbirinize düşman olarak hepiniz oradan (cennetten) inin! Artık benden size hidayet geldiğinde, kim benim hidayetime uyarsa o sapmaz ve bedbaht olmaz.', 16, 319, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2472, 124, 20, 'Ta-Ha', 'وَمَنْ اَعْرَضَ عَنْ ذِكْر۪ي فَاِنَّ لَهُ مَع۪يشَةً ضَنْكاً وَنَحْشُرُهُ يَوْمَ الْقِيٰمَةِ اَعْمٰى', 'Kim de beni anmaktan yüz çevirirse şüphesiz onun sıkıntılı bir hayatı olacak ve biz onu, kıyamet günü kör olarak haşredeceğiz.', 16, 319, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2473, 125, 20, 'Ta-Ha', 'قَالَ رَبِّ لِمَ حَشَرْتَـن۪ٓي اَعْمٰى وَقَدْ كُنْتُ بَص۪يراً', 'O: Rabbim! Beni niçin kör olarak haşrettin? Oysa ben, hakikaten görür idim!, der.', 16, 319, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2474, 126, 20, 'Ta-Ha', 'قَالَ كَذٰلِكَ اَتَتْكَ اٰيَاتُنَا فَـنَس۪يتَهَاۚ وَكَذٰلِكَ الْيَوْمَ تُنْسٰى', '(Allah) buyurur ki: İşte böyle. Çünkü sana âyetlerimiz geldi; ama sen onları unuttun. Bugün de aynı şekilde sen unutuluyorsun!', 16, 320, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2475, 127, 20, 'Ta-Ha', 'وَكَذٰلِكَ نَجْز۪ي مَنْ اَسْرَفَ وَلَمْ يُؤْمِنْ بِاٰيَاتِ رَبِّه۪ۜ وَلَعَذَابُ الْاٰخِرَةِ اَشَدُّ وَاَبْقٰى', 'Doğru yoldan sapanı ve Rabbinin âyetlerine inanmayanı işte böyle cezalandırırız. Ahiret azabı, elbette daha şiddetli ve daha süreklidir.', 16, 320, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2476, 128, 20, 'Ta-Ha', 'اَفَلَمْ يَهْدِ لَهُمْ كَمْ اَهْلَكْنَا قَبْلَهُمْ مِنَ الْقُرُونِ يَمْشُونَ ف۪ي مَسَاكِنِهِمْۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَاتٍ لِاُو۬لِي النُّهٰى۟', 'Bizim, onlardan önce nice nesilleri helâk etmiş olmamız kendilerini yola getirmedi mi? Halbuki onların yurtlarında gezip dolaşırlar. Bunda, elbette ki akıl sahipleri için nice ibretler vardır.', 16, 320, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2477, 129, 20, 'Ta-Ha', 'وَلَوْلَا كَلِمَةٌ سَبَقَتْ مِنْ رَبِّكَ لَكَانَ لِزَاماً وَاَجَلٌ مُسَمًّىۜ', 'Eğer Rabbinden, daha önce sâdır olmuş bir söz ve tayin edilmiş bir vâde olmasaydı, (ceza onlar için de dünyada) kaçınılmaz olurdu.', 16, 320, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2478, 130, 20, 'Ta-Ha', 'فَاصْبِرْ عَلٰى مَا يَقُولُونَ وَسَبِّحْ بِحَمْدِ رَبِّكَ قَبْلَ طُلُوعِ الشَّمْسِ وَقَبْلَ غُرُوبِهَاۚ وَمِنْ اٰنَٓائِ الَّيْلِ فَسَبِّـحْ وَاَطْرَافَ النَّهَارِ لَعَلَّكَ تَرْضٰى', '(Resûlüm!) Sen, onların söylediklerine sabret. Güneşin doğmasından önce de batmasından önce de Rabbini övgü ile tesbih et; gecenin bir kısım saatleri ile gündüzün etrafında (iki ucunda) da tesbih et ki, hoşnutluğa eresin.', 16, 320, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2479, 131, 20, 'Ta-Ha', 'وَلَا تَمُدَّنَّ عَيْنَيْكَ اِلٰى مَا مَتَّعْنَا بِه۪ٓ اَزْوَاجاً مِنْهُمْ زَهْرَةَ الْحَيٰوةِ الدُّنْيَا لِنَفْتِنَهُمْ ف۪يهِۜ وَرِزْقُ رَبِّكَ خَيْرٌ وَاَبْقٰى', 'Sakın, kendilerini denemek için onlardan bir kesimi faydalandırdığımız dünya hayatının çekiciliğine gözlerini dikme! Rabbinin nimeti hem daha hayırlı, hem de daha süreklidir.', 16, 320, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2480, 132, 20, 'Ta-Ha', 'وَأْمُرْ اَهْلَكَ بِالصَّلٰوةِ وَاصْطَبِرْ عَلَيْهَاۜ لَا نَسْـَٔلُكَ رِزْقاًۜ نَحْنُ نَرْزُقُكَۜ وَالْعَاقِبَةُ لِلتَّقْوٰى', 'Ailene namazı emret; kendin de ona sabırla devam et. Senden rızık istemiyoruz; (aksine) biz seni rızıklandırıyoruz. Güzel sonuç, takvâ iledir.', 16, 320, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2481, 133, 20, 'Ta-Ha', 'وَقَالُوا لَوْلَا يَأْت۪ينَا بِاٰيَةٍ مِنْ رَبِّه۪ۜ اَوَلَمْ تَأْتِهِمْ بَيِّنَةُ مَا فِي الصُّحُفِ الْاُو۫لٰى', 'Onlar: (Muhammed) bize Rabbinden bir mucize getirmeli değil miydi? dediler. Önce gelen kitaplardakinin apaçık delili (Kur´an) onlara gelmedi mi?', 16, 320, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2482, 134, 20, 'Ta-Ha', 'وَلَوْ اَنَّٓا اَهْلَكْنَاهُمْ بِعَذَابٍ مِنْ قَبْلِه۪ لَقَالُوا رَبَّنَا لَوْلَٓا اَرْسَلْتَ اِلَيْنَا رَسُولاً فَنَتَّبِعَ اٰيَاتِكَ مِنْ قَبْلِ اَنْ نَذِلَّ وَنَخْزٰى', 'Eğer biz, bundan (Kur´an´dan) önce onları bir azapla helâk etseydik, muhakkak ki şöyle diyeceklerdi: Ya Rabbi! Ne olurdu, bize bir elçi gönderseydin de, şu aşağılığa ve rüsvaylığa düşmeden önce âyetlerine uysaydık!', 16, 320, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2483, 135, 20, 'Ta-Ha', 'قُلْ كُلٌّ مُتَرَبِّصٌ فَتَرَبَّصُواۚ فَسَتَعْلَمُونَ مَنْ اَصْحَابُ الصِّرَاطِ السَّوِيِّ وَمَنِ اهْتَدٰى', 'De ki: Herkes beklemektedir: Öyle ise siz de bekleyin. Yakında anlayacaksınız; doğru düzgün yolun yolcuları kimmiş ve hidayette olan kimmiş!', 16, 320, 135, 5233, 'Ta-Ha', 'Mekke', 45),
(2484, 1, 21, 'Enbiya', 'اِقْتَرَبَ لِلنَّاسِ حِسَابُهُمْ وَهُمْ ف۪ي غَفْلَةٍ مُعْرِضُونَۚ', 'İnsanların hesaba çekilecekleri (gün) yaklaştı. Hal böyle iken onlar, gaflet içinde yüz çevirdiler.', 17, 321, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2485, 2, 21, 'Enbiya', 'مَا يَأْت۪يهِمْ مِنْ ذِكْرٍ مِنْ رَبِّهِمْ مُحْدَثٍ اِلَّا اسْتَمَعُوهُ وَهُمْ يَلْعَبُونَۙ', '(2-3) Rablerinden kendilerine ne zaman yeni bir ihtar gelse, onlar bunu, hep alaya alarak, kalpleri oyuna, eğlenceye dalarak dinlemişlerdir. O zalimler şöyle fısıldaştılar: Bu (Muhammed), sizin gibi bir beşer olmaktan başka nedir ki! Siz şimdi gözünüz göre göre büyüye mi kapılıyorsunuz?', 17, 321, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2486, 3, 21, 'Enbiya', 'لَاهِيَةً قُلُوبُهُمْۜ وَاَسَرُّوا النَّجْوٰىۗ اَلَّذ۪ينَ ظَلَمُواۗ هَلْ هٰذَٓا اِلَّا بَشَرٌ مِثْلُكُمْۚ اَفَتَأْتُونَ السِّحْرَ وَاَنْتُمْ تُبْصِرُونَ', '(2-3) Rablerinden kendilerine ne zaman yeni bir ihtar gelse, onlar bunu, hep alaya alarak, kalpleri oyuna, eğlenceye dalarak dinlemişlerdir. O zalimler şöyle fısıldaştılar: Bu (Muhammed), sizin gibi bir beşer olmaktan başka nedir ki! Siz şimdi gözünüz göre göre büyüye mi kapılıyorsunuz?', 17, 321, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2487, 4, 21, 'Enbiya', 'قَالَ رَبّ۪ي يَعْلَمُ الْقَوْلَ فِي السَّمَٓاءِ وَالْاَرْضِۘ وَهُوَ السَّم۪يعُ الْعَل۪يمُ', '(Peygamber) dedi ki: Rabbim, yerde ve gökte (söylenmiş) her sözü bilir. O, hakkıyla işiten ve bilendir.', 17, 321, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2488, 5, 21, 'Enbiya', 'بَلْ قَالُٓوا اَضْغَاثُ اَحْلَامٍ بَلِ افْتَرٰيهُ بَلْ هُوَ شَاعِرٌۚ فَلْيَأْتِنَا بِاٰيَةٍ كَمَٓا اُرْسِلَ الْاَوَّلُونَ', '«Hayır, dediler, (bunlar) saçma sapan rüyalardır; bilakis onu kendisi uydurmuştur; belki de o, şairdir. (Eğer öyle değilse) bize hemen, öncekilere gönderilenin benzeri bir âyet getirsin.»', 17, 321, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2489, 6, 21, 'Enbiya', 'مَٓا اٰمَنَتْ قَبْلَهُمْ مِنْ قَرْيَةٍ اَهْلَكْنَاهَاۚ اَفَهُمْ يُؤْمِنُونَ', 'Bunlardan önce helâk ettiğimiz hiçbir belde iman etmemişti; şimdi bunlar mı iman edecekler?', 17, 321, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2490, 7, 21, 'Enbiya', 'وَمَٓا اَرْسَلْنَا قَبْلَكَ اِلَّا رِجَالاً نُوح۪ٓي اِلَيْهِمْ فَسْـَٔلُٓوا اَهْلَ الذِّكْرِ اِنْ كُنْتُمْ لَا تَعْلَمُونَ', 'Biz, senden önce de, kendilerine vahiy verdiğimiz kişilerden başkasını peygamber olarak göndermedik. Eğer bilmiyorsanız bilenlerden sorunuz.', 17, 321, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2491, 8, 21, 'Enbiya', 'وَمَا جَعَلْنَاهُمْ جَسَداً لَا يَأْكُلُونَ الطَّعَامَ وَمَا كَانُوا خَالِد۪ينَ', 'Biz onları (peygamberleri), yemek yemez birer (cansız) ceset olarak yaratmadık. Onlar (bu dünyada) ebedî de değillerdir.', 17, 321, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2492, 9, 21, 'Enbiya', 'ثُمَّ صَدَقْنَاهُمُ الْوَعْدَ فَاَنْجَيْنَاهُمْ وَمَنْ نَشَٓاءُ وَاَهْلَكْنَا الْمُسْرِف۪ينَ', 'Sonra onlara (verdiğimiz) sözü yerine getirdik; böylece, hem onları hem de dilediğimiz (başka) kimseleri kurtuluşa erdirdik; müsrifleri de helâk ettik.', 17, 321, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2493, 10, 21, 'Enbiya', 'لَقَدْ اَنْزَلْـنَٓا اِلَيْكُمْ كِتَاباً ف۪يهِ ذِكْرُكُمْۜ اَفَلَا تَعْقِلُونَ۟', 'Andolsun, size içinde sizin için öğüt bulunan bir kitap indirdik. Hâla akıllanmaz mısınız?', 17, 321, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2494, 11, 21, 'Enbiya', 'وَكَمْ قَصَمْنَا مِنْ قَرْيَةٍ كَانَتْ ظَالِمَةً وَاَنْشَأْنَا بَعْدَهَا قَوْماً اٰخَر۪ينَ', 'Zalim olan nice beldeyi kırıp geçirdik; arkasından da nice başka topluluklar vücuda getirdik.', 17, 322, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2495, 12, 21, 'Enbiya', 'فَلَمَّٓا اَحَسُّوا بَأْسَنَٓا اِذَا هُمْ مِنْهَا يَرْكُضُونَۜ', 'Azabımızı hissettiklerinde bir de bakarsın ki oralardan (azap bölgesinden) kaçıyorlar!', 17, 322, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2496, 13, 21, 'Enbiya', 'لَا تَرْكُضُوا وَارْجِعُٓوا اِلٰى مَٓا اُتْرِفْتُمْ ف۪يهِ وَمَسَاكِنِكُمْ لَعَلَّكُمْ تُسْـَٔلُونَ', '«Kaçmayın! İçinde bulunduğunuz refaha ve yurtlarınıza dönün! Çünkü size sorular sorulacak!»', 17, 322, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2497, 14, 21, 'Enbiya', 'قَالُوا يَا وَيْلَنَٓا اِنَّا كُنَّا ظَالِم۪ينَ', '«Vay başımıza gelenlere! dediler; gerçekten biz zalim insanlarmışız.»', 17, 322, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2498, 15, 21, 'Enbiya', 'فَمَا زَالَتْ تِلْكَ دَعْوٰيهُمْ حَتّٰى جَعَلْنَاهُمْ حَص۪يداً خَامِد۪ينَ', 'Biz kendilerini, kuruyup biçilmiş ekine, sönmüş ateşe çevirinceye kadar bu feryatları sürüp gider.', 17, 322, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2499, 16, 21, 'Enbiya', 'وَمَا خَلَقْنَا السَّمَٓاءَ وَالْاَرْضَ وَمَا بَيْنَهُمَا لَاعِب۪ينَ', 'Biz, göğü, yeri ve bunlar arasındakileri, oyuncular (işi, eğlencesi) olarak yaratmadık.', 17, 322, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2500, 17, 21, 'Enbiya', 'لَوْ اَرَدْنَٓا اَنْ نَتَّخِذَ لَهْواً لَاتَّخَذْنَاهُ مِنْ لَدُنَّاۗ اِنْ كُنَّا فَاعِل۪ينَ', 'Eğer bir eğlence edinmek isteseydik, onu kendi tarafımızdan edinirdik. (Bu irademizin eseri olurdu. Ama) biz (bunu) yapanlardan değiliz.', 17, 322, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2501, 18, 21, 'Enbiya', 'بَلْ نَقْذِفُ بِالْحَقِّ عَلَى الْبَاطِلِ فَيَدْمَغُهُ فَاِذَا هُوَ زَاهِقٌۜ وَلَكُمُ الْوَيْلُ مِمَّا تَصِفُونَ', 'Bilakis biz, hakkı bâtılın tepesine bindiririz de o, bâtılın işini bitirir. Bir de bakarsınız ki, bâtıl yok olup gitmiştir. (Allah´a) yakıştırdığınız sıfatlardan dolayı yazıklar olsun size!', 17, 322, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2502, 19, 21, 'Enbiya', 'وَلَهُ مَنْ فِي السَّمٰوَاتِ وَالْاَرْضِۜ وَمَنْ عِنْدَهُ لَا يَسْتَكْبِرُونَ عَنْ عِبَادَتِه۪ وَلَا يَسْتَحْسِرُونَۚ', 'Göklerde ve yerde kimler varsa O´na aittir. O´nun huzurunda bulunanlar, O´na ibadet hususunda kibirlenmezler ve yorulmazlar.', 17, 322, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2503, 20, 21, 'Enbiya', 'يُسَبِّحُونَ الَّيْلَ وَالنَّهَارَ لَا يَفْتُرُونَ', 'Onlar, bıkıp usanmaksızın gece gündüz (Allah´ı) tesbih ederler.', 17, 322, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2504, 21, 21, 'Enbiya', 'اَمِ اتَّخَذُٓوا اٰلِهَةً مِنَ الْاَرْضِ هُمْ يُنْشِرُونَ', 'Yoksa (o müşrikler), yerden birtakım tanrılar edindiler de, (ölüleri) onlar mı diriltecekler?', 17, 322, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2505, 22, 21, 'Enbiya', 'لَوْ كَانَ ف۪يهِمَٓا اٰلِهَةٌ اِلَّا اللّٰهُ لَفَسَدَتَاۚ فَسُبْحَانَ اللّٰهِ رَبِّ الْعَرْشِ عَمَّا يَصِفُونَ', 'Eğer yerde ve gökte Allah´tan başka tanrılar bulunsaydı, yer ve gök, (bunların nizamı) kesinlikle bozulup gitmişti. Demek ki Arş´ın Rabbi olan Allah, onların yakıştırdıkları sıfatlardan münezzehtir.', 17, 322, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2506, 23, 21, 'Enbiya', 'لَا يُسْـَٔلُ عَمَّا يَفْعَلُ وَهُمْ يُسْـَٔلُونَ', 'Allah, yaptığından sorumlu tutulamaz; onlar ise sorguya çekileceklerdir.', 17, 322, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2507, 24, 21, 'Enbiya', 'اَمِ اتَّخَذُوا مِنْ دُونِه۪ٓ اٰلِهَةًۜ قُلْ هَاتُوا بُرْهَانَكُمْۚ هٰذَا ذِكْرُ مَنْ مَعِيَ وَذِكْرُ مَنْ قَبْل۪يۜ بَلْ اَكْثَرُهُمْ لَا يَعْلَمُونَۙ الْحَقَّ فَهُمْ مُعْرِضُونَ', 'Yoksa O´ndan başka birtakım tanrılar mı edindiler? De ki: Haydi delillerinizi getirin! İşte benimle beraber olanların Kitab´ı ve benden öncekilerin Kitab´ı. Hayır, onların çoğu hakkı bilmezler; bu yüzden de yüz çevirirler.', 17, 322, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2508, 25, 21, 'Enbiya', 'وَمَٓا اَرْسَلْنَا مِنْ قَبْلِكَ مِنْ رَسُولٍ اِلَّا نُوح۪ٓي اِلَيْهِ اَنَّهُ لَٓا اِلٰهَ اِلَّٓا اَنَا۬ فَاعْبُدُونِ', 'Senden önce hiçbir resûl göndermedik ki ona: «Benden başka İlâh yoktur; şu halde bana kulluk edin» diye vahyetmiş olmayalım.', 17, 323, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2509, 26, 21, 'Enbiya', 'وَقَالُوا اتَّخَذَ الرَّحْمٰنُ وَلَداً سُبْحَانَهُۜ بَلْ عِبَادٌ مُكْرَمُونَۙ', 'Rahmân (olan Allah, melekleri) evlât edindi, dediler. Hâşâ! O, bundan münezzehtir. Bilakis (melekler), lütuf ve ihsana mazhar olmuş kullardır.', 17, 323, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2510, 27, 21, 'Enbiya', 'لَا يَسْبِقُونَهُ بِالْقَوْلِ وَهُمْ بِاَمْرِه۪ يَعْمَلُونَ', 'O´ndan (emir almazdan) önce konuşmazlar; onlar, sadece O´nun emri ile hareket ederler.', 17, 323, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2511, 28, 21, 'Enbiya', 'يَعْلَمُ مَا بَيْنَ اَيْد۪يهِمْ وَمَا خَلْفَهُمْ وَلَا يَشْفَعُونَۙ اِلَّا لِمَنِ ارْتَضٰى وَهُمْ مِنْ خَشْيَتِه۪ مُشْفِقُونَ', 'Allah, onların önlerindekini de, arkalarındakini de (yaptıklarını da, yapacaklarını da) bilir. Allah rızasına ulaşmış olanlardan başkasına şefaat etmezler. Onlar, Allah korkusundan titrerler!', 17, 323, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2512, 29, 21, 'Enbiya', 'وَمَنْ يَقُلْ مِنْهُمْ اِنّ۪ٓي اِلٰهٌ مِنْ دُونِه۪ فَذٰلِكَ نَجْز۪يهِ جَهَنَّمَۜ كَذٰلِكَ نَجْزِي الظَّالِم۪ينَ۟', 'Onlardan her kim: «Tanrı O değil, benim!» derse, biz onu cehennemle cezalandırırız. İşte biz, zalimlere böyle ceza veririz!', 17, 323, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2513, 30, 21, 'Enbiya', 'اَوَلَمْ يَرَ الَّذ۪ينَ كَفَرُٓوا اَنَّ السَّمٰوَاتِ وَالْاَرْضَ كَانَتَا رَتْقاً فَفَتَقْنَاهُمَاۜ وَجَعَلْنَا مِنَ الْمَٓاءِ كُلَّ شَيْءٍ حَيٍّۜ اَفَلَا يُؤْمِنُونَ', 'İnkâr edenler, göklerle yer bitişik bir halde iken bizim, onları birbirinden kopardığımızı ve her canlı şeyi sudan yarattığımızı görüp düşünmediler mi? Yine de inanmazlar mı?', 17, 323, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2514, 31, 21, 'Enbiya', 'وَجَعَلْنَا فِي الْاَرْضِ رَوَاسِيَ اَنْ تَم۪يدَ بِهِمْ وَجَعَلْنَا ف۪يهَا فِجَاجاً سُبُلاً لَعَلَّهُمْ يَهْتَدُونَ', 'Onları sarsmasın diye yeryüzünde bir takım dağlar diktik. Orada geniş geniş yollar açtık; ta ki maksatlarına ulaşsınlar.', 17, 323, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2515, 32, 21, 'Enbiya', 'وَجَعَلْنَا السَّمَٓاءَ سَقْفاً مَحْفُوظاًۚ وَهُمْ عَنْ اٰيَاتِهَا مُعْرِضُونَ', 'Biz, gökyüzünü korunmuş bir tavan gibi yaptık. Onlar ise, gökyüzünün âyetlerinden yüz çevirirler.', 17, 323, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2516, 33, 21, 'Enbiya', 'وَهُوَ الَّذ۪ي خَلَقَ الَّيْلَ وَالنَّهَارَ وَالشَّمْسَ وَالْقَمَرَۜ كُلٌّ ف۪ي فَلَكٍ يَسْبَحُونَ', 'O, geceyi, gündüzü, güneşi, ayı... yaratandır. Her biri bir yörüngede yüzmektedirler.', 17, 323, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2517, 34, 21, 'Enbiya', 'وَمَا جَعَلْنَا لِبَشَرٍ مِنْ قَبْلِكَ الْخُلْدَۜ اَفَا۬ئِنْ مِتَّ فَهُمُ الْخَالِدُونَ', 'Biz, senden önce de hiçbir beşere ebedîlik vermedik. Şimdi sen ölürsen, sanki onlar ebedî mi kalacaklar?', 17, 323, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2518, 35, 21, 'Enbiya', 'كُلُّ نَفْسٍ ذَٓائِقَةُ الْمَوْتِۜ وَنَبْلُوكُمْ بِالشَّرِّ وَالْخَيْرِ فِتْنَةًۜ وَاِلَيْنَا تُرْجَعُونَ', 'Her canlı, ölümü tadar. Bir deneme olarak sizi hayırla da, şerle de imtihan ederiz. Ve siz, ancak bize döndürüleceksiniz.', 17, 323, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2519, 36, 21, 'Enbiya', 'وَاِذَا رَاٰكَ الَّذ۪ينَ كَفَرُٓوا اِنْ يَتَّخِذُونَكَ اِلَّا هُزُواًۜ اَهٰذَا الَّذ۪ي يَذْكُرُ اٰلِهَتَكُمْۚ وَهُمْ بِذِكْرِ الرَّحْمٰنِ هُمْ كَافِرُونَ', '(Resûlüm!) Kâfirler seni gördükleri zaman: «Sizin ilâhlarınızı diline dolayan bu mu?» diyerek seni hep alaya alırlar. Halbuki onlar, çok esirgeyici Allah´ın Kitabını inkâr edenlerin ta kendileridir.', 17, 324, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2520, 37, 21, 'Enbiya', 'خُلِقَ الْاِنْسَانُ مِنْ عَجَلٍۜ سَاُر۪يكُمْ اٰيَات۪ي فَلَا تَسْتَعْجِلُونِ', 'İnsan, aceleci (bir tabiatta) yaratılmıştır. Size âyetlerimi göstereceğim; benden acele istemeyin.', 17, 324, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2521, 38, 21, 'Enbiya', 'وَيَقُولُونَ مَتٰى هٰذَا الْوَعْدُ اِنْ كُنْتُمْ صَادِق۪ينَ', '«Eğer, diyorlar, doğru iseniz, ne zaman (gerçekleşecek) bu tehdit?»', 17, 324, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2522, 39, 21, 'Enbiya', 'لَوْ يَعْلَمُ الَّذ۪ينَ كَفَرُوا ح۪ينَ لَا يَكُفُّونَ عَنْ وُجُوهِهِمُ النَّارَ وَلَا عَنْ ظُهُورِهِمْ وَلَا هُمْ يُنْصَرُونَ', 'İnkâr edenler, yüzlerinden ve sırtlarından (saran) ateşi savamayacakları, kendilerine yardım dahi edilmeyeceği zamanı bir bilselerdi!', 17, 324, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2523, 40, 21, 'Enbiya', 'بَلْ تَأْت۪يهِمْ بَغْتَةً فَتَبْهَتُهُمْ فَلَا يَسْتَط۪يعُونَ رَدَّهَا وَلَا هُمْ يُنْظَرُونَ', 'Bilâkis kendilerine o (kıyamet) öyle âni gelir ki, onları şaşırtır. Artık, ne reddedebilirler onu, ne de kendilerine mühlet verilir.', 17, 324, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2524, 41, 21, 'Enbiya', 'وَلَقَدِ اسْتُهْزِئَ بِرُسُلٍ مِنْ قَبْلِكَ فَحَاقَ بِالَّذ۪ينَ سَخِرُوا مِنْهُمْ مَا كَانُوا بِه۪ يَسْتَهْزِؤُ۫نَ۟', 'Andolsun, senden önceki peygamberlerle de alay edildi; ama onları alaya alanları, o alay konusu ettikleri şey kuşatıverdi.', 17, 324, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2525, 42, 21, 'Enbiya', 'قُلْ مَنْ يَكْلَؤُ۬كُمْ بِالَّيْلِ وَالنَّهَارِ مِنَ الرَّحْمٰنِۜ بَلْ هُمْ عَنْ ذِكْرِ رَبِّهِمْ مُعْرِضُونَ', 'De ki: Allah´a karşı sizi gece gündüz kim koruyacak? Buna rağmen onlar Rablerini anmaktan yüz çevirirler.', 17, 324, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2526, 43, 21, 'Enbiya', 'اَمْ لَهُمْ اٰلِهَةٌ تَمْنَعُهُمْ مِنْ دُونِنَاۜ لَا يَسْتَط۪يعُونَ نَصْرَ اَنْفُسِهِمْ وَلَا هُمْ مِنَّا يُصْحَبُونَ', 'Yoksa kendilerini bize karşı savunacak birtakım ilâhları mı var? (O ilâh dedikleri şeyler) kendilerine bile yardım edecek güçte değildirler. Onlar bizden de alâka ve destek görmezler.', 17, 324, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2527, 44, 21, 'Enbiya', 'بَلْ مَتَّعْنَا هٰٓؤُ۬لَٓاءِ وَاٰبَٓاءَهُمْ حَتّٰى طَالَ عَلَيْهِمُ الْعُمُرُۜ اَفَلَا يَرَوْنَ اَنَّا نَأْتِي الْاَرْضَ نَنْقُصُهَا مِنْ اَطْرَافِهَاۜ اَفَهُمُ الْغَالِبُونَ', 'Evet, onları da, atalarını da barındırdık. Nihayet ömür kendilerine (hiç bitmeyecek gibi) uzun geldi. Oysa onlar, bizim gelip (kâfirlere ait) araziyi çevresinden eksilteceğimizi görmezler mi? Şu halde, üstün gelen onlar mı?', 17, 324, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2528, 45, 21, 'Enbiya', 'قُلْ اِنَّـمَٓا اُنْذِرُكُمْ بِالْوَحْيِۘ وَلَا يَسْمَعُ الصُّمُّ الدُّعَٓاءَ اِذَا مَا يُنْذَرُونَ', 'De ki: Ben, sadece, vahiy ile sizi ikaz ediyorum. Fakat, sağır olanlar, ikaz edildikleri zaman bu çağrıyı duymazlar.', 17, 325, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2529, 46, 21, 'Enbiya', 'وَلَئِنْ مَسَّتْهُمْ نَفْحَةٌ مِنْ عَذَابِ رَبِّكَ لَيَقُولُنَّ يَا وَيْلَنَٓا اِنَّا كُنَّا ظَالِم۪ينَ', 'Andolsun, onlara Rabbinin azabından ufak bir esinti dokunsa, hiç şüphesiz, «Vah bize! Hakikaten biz zalim kimselermişiz!» derler.', 17, 325, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2530, 47, 21, 'Enbiya', 'وَنَضَعُ الْمَوَاز۪ينَ الْقِسْطَ لِيَوْمِ الْقِيٰمَةِ فَلَا تُظْلَمُ نَفْسٌ شَيْـٔاًۜ وَاِنْ كَانَ مِثْقَالَ حَبَّةٍ مِنْ خَرْدَلٍ اَتَيْنَا بِهَاۜ وَكَفٰى بِنَا حَاسِب۪ينَ', 'Biz, kıyamet günü için adalet terazileri kurarız. Artık kimseye, hiçbir şekilde haksızlık edilmez. (Yapılan iş,) bir hardal tanesi kadar dahi olsa, onu (adalet terazisine) getiririz. Hesap gören olarak biz (herkese) yeteriz.', 17, 325, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2531, 48, 21, 'Enbiya', 'وَلَقَدْ اٰتَيْنَا مُوسٰى وَهٰرُونَ الْفُرْقَانَ وَضِيَٓاءً وَذِكْراً لِلْمُتَّق۪ينَۙ', 'Andolsun biz, Musa ve Harun´a, takvâ sahipleri için bir ışık, bir öğüt ve Furkan´ı verdik.', 17, 325, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2532, 49, 21, 'Enbiya', 'اَلَّذ۪ينَ يَخْشَوْنَ رَبَّهُمْ بِالْغَيْبِ وَهُمْ مِنَ السَّاعَةِ مُشْفِقُونَ', '(O takvâ sahipleri ki) onlar, görmedikleri halde Rablerine candan saygı gösterirler. Yine onlar, kıyametten korkan kimselerdir.', 17, 325, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2533, 50, 21, 'Enbiya', 'وَهٰذَا ذِكْرٌ مُبَارَكٌ اَنْزَلْنَاهُۜ اَفَاَنْتُمْ لَهُ مُنْكِرُونَ۟', 'İşte bu (Kur´an) da, bizim indirdiğimiz hayırlı ve faydalı bir öğüttür. Şimdi onu inkâr mı ediyorsunuz?', 17, 325, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2534, 51, 21, 'Enbiya', 'وَلَقَدْ اٰتَيْنَٓا اِبْرٰه۪يمَ رُشْدَهُ مِنْ قَبْلُ وَكُنَّا بِه۪ عَالِم۪ينَۚ', 'Andolsun biz İbrahim´e daha önce rüşdünü vermiştik. Biz onu iyi tanırdık.', 17, 325, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2535, 52, 21, 'Enbiya', 'اِذْ قَالَ لِاَب۪يهِ وَقَوْمِه۪ مَا هٰذِهِ التَّمَاث۪يلُ الَّت۪ٓي اَنْتُمْ لَهَا عَاكِفُونَ', 'O, babasına ve kavmine: Şu karşısına geçip tapmakta olduğunuz heykeller de ne oluyor? demişti.', 17, 325, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2536, 53, 21, 'Enbiya', 'قَالُوا وَجَدْنَٓا اٰبَٓاءَنَا لَهَا عَابِد۪ينَ', 'Dediler ki: Biz, babalarımızı bunlara tapar kimseler bulduk.', 17, 325, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2537, 54, 21, 'Enbiya', 'قَالَ لَقَدْ كُنْتُمْ اَنْتُمْ وَاٰبَٓاؤُ۬كُمْ ف۪ي ضَلَالٍ مُب۪ينٍ', 'Doğrusu, siz de, babalarınız da açık bir sapıklık içindesiniz, dedi.', 17, 325, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2538, 55, 21, 'Enbiya', 'قَالُٓوا اَجِئْتَنَا بِالْحَقِّ اَمْ اَنْتَ مِنَ اللَّاعِب۪ينَ', 'Dediler ki: Bize gerçeği mi getirdin, yoksa sen oyunbazlardan biri misin?', 17, 325, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2539, 56, 21, 'Enbiya', 'قَالَ بَلْ رَبُّكُمْ رَبُّ السَّمٰوَاتِ وَالْاَرْضِ الَّذ۪ي فَطَرَهُنَّۘ وَاَنَا۬ عَلٰى ذٰلِكُمْ مِنَ الشَّاهِد۪ينَ', 'Hayır, dedi, sizin Rabbiniz, yarattığı göklerin ve yerin de Rabbidir ve ben buna şahitlik edenlerdenim.', 17, 325, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2540, 57, 21, 'Enbiya', 'وَتَاللّٰهِ لَاَك۪يدَنَّ اَصْنَامَكُمْ بَعْدَ اَنْ تُوَلُّوا مُدْبِر۪ينَ', 'Allah´a yemin ederim ki, siz ayrılıp gittikten sonra putlarınıza bir oyun oynayacağım!', 17, 325, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2541, 58, 21, 'Enbiya', 'فَجَعَلَهُمْ جُذَاذاً اِلَّا كَب۪يراً لَهُمْ لَعَلَّهُمْ اِلَيْهِ يَرْجِعُونَ', 'Sonunda İbrahim onları paramparça etti. Yalnız onların büyüğünü bıraktı; belki ona müracaat ederler diye.', 17, 326, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2542, 59, 21, 'Enbiya', 'قَالُوا مَنْ فَعَلَ هٰذَا بِاٰلِهَتِنَٓا اِنَّهُ لَمِنَ الظَّالِم۪ينَ', 'Bunu tanrılarımıza kim yaptı? Muhakkak o, zalimlerden biridir, dediler.', 17, 326, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2543, 60, 21, 'Enbiya', 'قَالُوا سَمِعْنَا فَتًى يَذْكُرُهُمْ يُقَالُ لَـهُٓ اِبْرٰه۪يمُۜ', '(Bir kısmı:) Bunları diline dolayan bir genç duyduk; kendisine İbrahim denilirmiş, dediler.', 17, 326, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2544, 61, 21, 'Enbiya', 'قَالُوا فَأْتُوا بِه۪ عَلٰٓى اَعْيُنِ النَّاسِ لَعَلَّهُمْ يَشْهَدُونَ', 'O halde, dediler, onu hemen insanların gözü önüne getirin. Belki şahitlik ederler.', 17, 326, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2545, 62, 21, 'Enbiya', 'قَالُٓوا ءَاَنْتَ فَعَلْتَ هٰذَا بِاٰلِهَتِنَا يَٓا اِبْرٰه۪يمُۜ', 'Bunu ilâhlarımıza sen mi yaptın ey İbrahim? dediler.', 17, 326, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2546, 63, 21, 'Enbiya', 'قَالَ بَلْ فَعَلَهُۗ كَب۪يرُهُمْ هٰذَا فَسْـَٔلُوهُمْ اِنْ كَانُوا يَنْطِقُونَ', 'Belki de bu işi şu büyükleri yapmıştır. Hadi onlara sorun; eğer konuşuyorlarsa! dedi.', 17, 326, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2547, 64, 21, 'Enbiya', 'فَرَجَعُٓوا اِلٰٓى اَنْفُسِهِمْ فَقَالُٓوا اِنَّكُمْ اَنْتُمُ الظَّالِمُونَۙ', 'Bunun üzerine, kendi vicdanlarına dönüp (kendi kendilerine) «Zalimler sizlersiniz, sizler!» dediler.', 17, 326, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2548, 65, 21, 'Enbiya', 'ثُمَّ نُكِسُوا عَلٰى رُؤُ۫سِهِمْۚ لَقَدْ عَلِمْتَ مَا هٰٓؤُ۬لَٓاءِ يَنْطِقُونَ', 'Sonra tekrar eski inanç ve tartışmalarına döndüler: Sen bunların konuşmadığını pek âlâ biliyorsun, dediler.', 17, 326, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2549, 66, 21, 'Enbiya', 'قَالَ اَفَتَعْبُدُونَ مِنْ دُونِ اللّٰهِ مَا لَا يَنْفَعُكُمْ شَيْـٔاً وَلَا يَضُرُّكُمْۜ', 'İbrahim: Öyleyse, dedi, Allah´ı bırakıp da, size hiçbir fayda ve zarar vermeyen bir şeye hâla tapacak mısınız?', 17, 326, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2550, 67, 21, 'Enbiya', 'اُفٍّ لَكُمْ وَلِمَا تَعْبُدُونَ مِنْ دُونِ اللّٰهِۜ اَفَلَا تَعْقِلُونَ', 'Size de, Allah´ı bırakıp tapmakta olduğunuz şeylere de yuh olsun! Siz akıllanmaz mısınız?', 17, 326, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2551, 68, 21, 'Enbiya', 'قَالُوا حَرِّقُوهُ وَانْصُرُٓوا اٰلِهَتَكُمْ اِنْ كُنْتُمْ فَاعِل۪ينَ', '(Bir kısmı:) Eğer iş yapacaksanız, yakın onu da tanrılarınıza yardım edin! dediler.', 17, 326, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2552, 69, 21, 'Enbiya', 'قُلْنَا يَا نَارُ كُون۪ي بَرْداً وَسَلَاماً عَلٰٓى اِبْرٰه۪يمَۙ', '«Ey ateş! İbrahim için serinlik ve esenlik ol!» dedik.', 17, 326, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2553, 70, 21, 'Enbiya', 'وَاَرَادُوا بِه۪ كَيْداً فَجَعَلْنَاهُمُ الْاَخْسَر۪ينَۚ', 'Böylece ona bir tuzak kurmak istediler; fakat biz onları, daha çok hüsrana uğrayanlar durumuna soktuk.', 17, 326, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2554, 71, 21, 'Enbiya', 'وَنَجَّيْنَاهُ وَلُوطاً اِلَى الْاَرْضِ الَّت۪ي بَارَكْنَا ف۪يهَا لِلْعَالَم۪ينَ', 'Biz, onu ve Lût´u kurtararak, içinde cümle âleme bereketler verdiğimiz ülkeye ulaştırdık.', 17, 326, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2555, 72, 21, 'Enbiya', 'وَوَهَبْنَا لَـهُٓ اِسْحٰقَۜ وَيَعْقُوبَ نَافِلَةًۜ وَكُلاًّ جَعَلْنَا صَالِح۪ينَ', 'Ona (İbrahim´e), İshak´ı ve fazladan bir bağış olmak üzere Ya´kub´u lütfettik; herbirini sâlih insanlar yaptık.', 17, 326, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2556, 73, 21, 'Enbiya', 'وَجَعَلْنَاهُمْ اَئِمَّةً يَهْدُونَ بِاَمْرِنَا وَاَوْحَيْنَٓا اِلَيْهِمْ فِعْلَ الْخَيْرَاتِ وَاِقَامَ الصَّلٰوةِ وَا۪يتَٓاءَ الزَّكٰوةِۚ وَكَانُوا لَنَا عَابِد۪ينَۙ', 'Onları, emrimiz uyarınca doğru yolu gösteren önderler yaptık ve kendilerine hayırlı işler yapmayı, namaz kılmayı, zekât vermeyi vahyettik. Onlar, daima bize ibadet eden kimselerdi.', 17, 327, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2557, 74, 21, 'Enbiya', 'وَلُوطاً اٰتَيْنَاهُ حُكْماً وَعِلْماً وَنَجَّيْنَاهُ مِنَ الْقَرْيَةِ الَّت۪ي كَانَتْ تَعْمَلُ الْخَبَٓائِثَۜ اِنَّهُمْ كَانُوا قَوْمَ سَوْءٍ فَاسِق۪ينَۙ', 'Lût´a gelince, ona da hüküm (hakimlik, peygamberlik, hükümdarlık) ve ilim verdik; onu, çirkin işler yapmakta olan memleketten kurtardık. Zira onlar (o memleketin halkı), gerçekten fena işler yapan kötü bir kavimdi.', 17, 327, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2558, 75, 21, 'Enbiya', 'وَاَدْخَلْنَاهُ ف۪ي رَحْمَتِنَاۜ اِنَّهُ مِنَ الصَّالِح۪ينَ۟', 'Onu (Lût´u) rahmetimize kabul ettik; çünkü o, sâlihlerden idi.', 17, 327, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2559, 76, 21, 'Enbiya', 'وَنُوحاً اِذْ نَادٰى مِنْ قَبْلُ فَاسْتَجَبْنَا لَهُ فَنَجَّيْنَاهُ وَاَهْلَهُ مِنَ الْكَرْبِ الْعَظ۪يمِۚ', 'Nuh´u da (hatırla). Hani o dua etmiş, biz onun duasını kabul etmiştik. Böylece, kendisini ve (iman eden) yakınlarını büyük sıkıntıdan kurtarmıştık.', 17, 327, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2560, 77, 21, 'Enbiya', 'وَنَصَرْنَاهُ مِنَ الْقَوْمِ الَّذ۪ينَ كَذَّبُوا بِاٰيَاتِنَاۜ اِنَّهُمْ كَانُوا قَوْمَ سَوْءٍ فَاَغْرَقْنَاهُمْ اَجْمَع۪ينَ', 'Onu, âyetlerimizi inkâr eden kavimden koruduk. Gerçekten onlar, fena bir kavim idi; bu yüzden topunu birden (suya) gömdük.', 17, 327, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2561, 78, 21, 'Enbiya', 'وَدَاوُ۫دَ وَسُلَيْمٰنَ اِذْ يَحْكُمَانِ فِي الْحَرْثِ اِذْ نَفَشَتْ ف۪يهِ غَنَمُ الْقَوْمِۚ وَكُنَّا لِحُكْمِهِمْ شَاهِد۪ينَۙ', 'Davud ve Süleyman´ı da (an). Bir zaman, bir ekin konusunda hüküm veriyorlardı: bir gurup insanın koyun sürüsü, geceleyin başıboş bir vaziyette bu ekinin içine dağılıp ziyan vermişti. Biz onların hükmünü görüp bilmekte idik.', 17, 327, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2562, 79, 21, 'Enbiya', 'فَفَهَّمْنَاهَا سُلَيْمٰنَۚ وَكُلاًّ اٰتَيْنَا حُكْماً وَعِلْماًۘ وَسَخَّرْنَا مَعَ دَاوُ۫دَ الْجِبَالَ يُسَبِّحْنَ وَالطَّيْرَۜ وَكُنَّا فَاعِل۪ينَ', 'Böylece bunu (bu fetvayı) Süleyman´a biz anlatmıştık. Biz, onların her birine hüküm (hükümdarlık, peygamberlik) ve ilim verdik. Kuşları ve tesbih eden dağları da Davud´a boyun eğdirdik. (Bunları) biz yapmaktayız.', 17, 327, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2563, 80, 21, 'Enbiya', 'وَعَلَّمْنَاهُ صَنْعَةَ لَبُوسٍ لَكُمْ لِتُحْصِنَكُمْ مِنْ بَأْسِكُمْۚ فَهَلْ اَنْتُمْ شَاكِرُونَ', 'Ona, savaş sıkıntılarınızdan sizi koruması için zırh yapmayı öğrettik. Artık şükredecek misiniz?', 17, 327, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2564, 81, 21, 'Enbiya', 'وَلِسُلَيْمٰنَ الرّ۪يحَ عَاصِفَةً تَجْر۪ي بِاَمْرِه۪ٓ اِلَى الْاَرْضِ الَّت۪ي بَارَكْنَا ف۪يهَاۜ وَكُنَّا بِكُلِّ شَيْءٍ عَالِم۪ينَ', 'Süleyman´ın emrine de kasırga (gibi esen) rüzgârı verdik; onun emriyle içinde bereketler yarattığımız yere doğru eserdi. Biz herşeyi biliriz.', 17, 327, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2565, 82, 21, 'Enbiya', 'وَمِنَ الشَّيَاط۪ينِ مَنْ يَغُوصُونَ لَهُ وَيَعْمَلُونَ عَمَلاً دُونَ ذٰلِكَۚ وَكُنَّا لَهُمْ حَافِظ۪ينَۙ', 'Şeytanlar arasından da, onun için dalgıçlık eden (ve inciler çıkaran) ve bundan başka işler görenler vardı. Biz onları gözetim altında tutuyorduk.', 17, 328, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2566, 83, 21, 'Enbiya', 'وَاَيُّوبَ اِذْ نَادٰى رَبَّهُٓ اَنّ۪ي مَسَّنِيَ الضُّرُّ وَاَنْتَ اَرْحَمُ الرَّاحِم۪ينَۚ', 'Eyyub´u da (an). Hani Rabbine: «Başıma bu dert geldi. Sen, merhametlilerin en merhametlisisin» diye niyaz etmişti.', 17, 328, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2567, 84, 21, 'Enbiya', 'فَاسْتَجَبْنَا لَهُ فَكَشَفْنَا مَا بِه۪ مِنْ ضُرٍّ وَاٰتَيْنَاهُ اَهْلَهُ وَمِثْلَهُمْ مَعَهُمْ رَحْمَةً مِنْ عِنْدِنَا وَذِكْرٰى لِلْعَابِد۪ينَ', 'Bunun üzerine biz, tarafımızdan bir rahmet ve kulluk edenler için bir hatıra olmak üzere onun duasını kabul ettik; kendisinde dert ve sıkıntı olarak ne varsa giderdik ve ona aile efradını, ayrıca bunlarla birlikte bir mislini daha verdik.', 17, 328, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2568, 85, 21, 'Enbiya', 'وَاِسْمٰع۪يلَ وَاِدْر۪يسَ وَذَا الْكِفْلِۜ كُلٌّ مِنَ الصَّابِر۪ينَۚ', 'İsmail´i, İdris´i ve Zülkifl´i de (yâdet). Hepsi de sabreden kimselerdendi.', 17, 328, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2569, 86, 21, 'Enbiya', 'وَاَدْخَلْنَاهُمْ ف۪ي رَحْمَتِنَاۜ اِنَّهُمْ مِنَ الصَّالِح۪ينَ', 'Onları rahmetimize kabul ettik. Onlar hakikaten iyi kimselerdendi.', 17, 328, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2570, 87, 21, 'Enbiya', 'وَذَا النُّونِ اِذْ ذَهَبَ مُغَاضِباً فَظَنَّ اَنْ لَنْ نَقْدِرَ عَلَيْهِ فَنَادٰى فِي الظُّلُمَاتِ اَنْ لَٓا اِلٰهَ اِلَّٓا اَنْتَ سُبْحَانَكَۗ اِنّ۪ي كُنْتُ مِنَ الظَّالِم۪ينَۚ', 'Zünnûn´u da (Yunus´u da zikret). O öfkeli bir halde geçip gitmişti; bizim kendisini asla sıkıştırmayacağımızı zannetmişti. Nihayet karanlıklar içinde: «Senden başka hiçbir tanrı yoktur. Seni tenzih ederim. Gerçekten ben zalimlerden oldum!» diye niyaz etti.', 17, 328, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2571, 88, 21, 'Enbiya', 'فَاسْتَجَبْنَا لَهُۙ وَنَجَّيْنَاهُ مِنَ الْغَمِّۜ وَكَذٰلِكَ نُنْجِي الْمُؤْمِن۪ينَ', 'Bunun üzerine onun duasını kabul ettik ve onu kederden kurtardık. İşte biz müminleri böyle kurtarırız.', 17, 328, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2572, 89, 21, 'Enbiya', 'وَزَكَرِيَّٓا اِذْ نَادٰى رَبَّهُ رَبِّ لَا تَذَرْن۪ي فَرْداً وَاَنْتَ خَيْرُ الْوَارِث۪ينَۚ', 'Zekeriyya´yı da (an). Hani o, Rabbine şöyle niyaz etmişti: Rabbim! Beni yalnız bırakma! Sen, vârislerin en hayırlısısın, (her şey sonunda senindir).', 17, 328, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2573, 90, 21, 'Enbiya', 'فَاسْتَجَبْنَا لَهُۘ وَوَهَبْنَا لَهُ يَحْيٰى وَاَصْلَحْنَا لَهُ زَوْجَهُۜ اِنَّهُمْ كَانُوا يُسَارِعُونَ فِي الْخَيْرَاتِ وَيَدْعُونَنَا رَغَباً وَرَهَباًۜ وَكَانُوا لَنَا خَاشِع۪ينَ', 'Biz onun da duasını kabul ettik ve ona Yahya´yı verdik; eşini de kendisi için (çocuk doğurmaya) elverişli kıldık. Onlar (bütün bu peygamberler), hayır işlerinde koşuşurlar, umarak ve korkarak bize yalvarırlardı; onlar, bize karşı derin saygı içindeydiler.', 17, 328, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2574, 91, 21, 'Enbiya', 'وَالَّت۪ٓي اَحْصَنَتْ فَرْجَهَا فَنَفَخْنَا ف۪يهَا مِنْ رُوحِنَا وَجَعَلْنَاهَا وَابْنَهَٓا اٰيَةً لِلْعَالَم۪ينَ', 'Irzını iffetle korumuş olanı (Meryem´i de an.) Biz ona ruhumuzdan üfledik; onu ve oğlunu cümle âlem için bir ibret kıldık.', 17, 329, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2575, 92, 21, 'Enbiya', 'اِنَّ هٰذِه۪ٓ اُمَّتُكُمْ اُمَّةً وَاحِدَةًۘ وَاَنَا۬ رَبُّكُمْ فَاعْبُدُونِ', 'Hakikaten bu (bütün peygamberler ve onlara iman edenler) bir tek ümmet olarak sizin ümmetinizdir. Ben de sizin Rabbinizim. Öyle ise bana kulluk edin.', 17, 329, 112, 4925, 'Peygamberler', 'Mekke', 73);
INSERT INTO `Ayetler` (`id`, `AyetNo`, `SureNo`, `SureAdi`, `Metin`, `Mana`, `Cuzu`, `Sayfasi`, `AyetSayisi`, `HarfSayisi`, `AdAnlami`, `IndigiYer`, `InisSirasi`) VALUES
(2576, 93, 21, 'Enbiya', 'وَتَقَطَّعُٓوا اَمْرَهُمْ بَيْنَهُمْۜ كُلٌّ اِلَيْنَا رَاجِعُونَ۟', '(İnsanlar) kendi aralarında (din ve devlet) işlerinin birliğini bozdular. Halbuki hepsi bize döneceklerdir.', 17, 329, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2577, 94, 21, 'Enbiya', 'فَمَنْ يَعْمَلْ مِنَ الصَّالِحَاتِ وَهُوَ مُؤْمِنٌ فَلَا كُفْرَانَ لِسَعْيِه۪ۚ وَاِنَّا لَهُ كَاتِبُونَ', 'Bu durumda her kim mümin olarak iyi davranışlar yaparsa onun çabasını görmezlikten gelmek olmaz. Zira biz onu yazmaktayız.', 17, 329, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2578, 95, 21, 'Enbiya', 'وَحَرَامٌ عَلٰى قَرْيَةٍ اَهْلَكْنَاهَٓا اَنَّهُمْ لَا يَرْجِعُونَ', 'Helâk ettiğimiz bir belde için artık (yeniden mâmur olmak) imkânsızdır; çünkü onlar geri dönemeyeceklerdir.', 17, 329, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2579, 96, 21, 'Enbiya', 'حَتّٰٓى اِذَا فُتِحَتْ يَأْجُوجُ وَمَأْجُوجُ وَهُمْ مِنْ كُلِّ حَدَبٍ يَنْسِلُونَ', 'Nihayet Ye´cûc ve Me´cûc (sedleri) açıldığı ve onlar her tepeden akın ettiği zaman;', 17, 329, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2580, 97, 21, 'Enbiya', 'وَاقْتَرَبَ الْوَعْدُ الْحَقُّ فَاِذَا هِيَ شَاخِصَةٌ اَبْصَارُ الَّذ۪ينَ كَفَرُواۜ يَا وَيْلَنَا قَدْ كُنَّا ف۪ي غَفْلَةٍ مِنْ هٰذَا بَلْ كُنَّا ظَالِم۪ينَ', 'Ve gerçek vaad (ölüm, kıyamet) yaklaşınca, birden, inkâr edenlerin gözleri donakalır! «Yazıklar olsun bize! (derler), gerçekten biz, bu durumdan habersizmişiz; hatta biz zalim kimselermişiz.»', 17, 329, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2581, 98, 21, 'Enbiya', 'اِنَّكُمْ وَمَا تَعْبُدُونَ مِنْ دُونِ اللّٰهِ حَصَبُ جَهَنَّمَۜ اَنْتُمْ لَهَا وَارِدُونَ', 'Siz ve Allah´ın dışında taptığınız şeyler cehennem yakıtısınız. Siz oraya gireceksiniz.', 17, 329, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2582, 99, 21, 'Enbiya', 'لَوْ كَانَ هٰٓؤُ۬لَٓاءِ اٰلِهَةً مَا وَرَدُوهَاۜ وَكُلٌّ ف۪يهَا خَالِدُونَ', 'Eğer onlar birer tanrı olsalardı oraya (cehenneme) girmezlerdi. Halbuki hepsi (tapanlar da tapılanlar da) orada ebedî kalacaklardır.', 17, 329, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2583, 100, 21, 'Enbiya', 'لَهُمْ ف۪يهَا زَف۪يرٌ وَهُمْ ف۪يهَا لَا يَسْمَعُونَ', 'Orada onlara inim inim inlemek düşer. Yine onlar orada (hiçbir iyi haber) duymazlar.', 17, 329, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2584, 101, 21, 'Enbiya', 'اِنَّ الَّذ۪ينَ سَبَقَتْ لَهُمْ مِنَّا الْحُسْنٰٓىۙ اُو۬لٰٓئِكَ عَنْهَا مُبْعَدُونَۙ', 'Tarafımızdan kendilerine güzel âkıbet takdir edilmiş olanlara gelince, işte bunlar cehennemden uzak tutulurlar.', 17, 329, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2585, 102, 21, 'Enbiya', 'لَا يَسْمَعُونَ حَس۪يسَهَاۚ وَهُمْ ف۪ي مَا اشْتَهَتْ اَنْفُسُهُمْ خَالِدُونَۚ', 'Bunlar onun uğultusunu duymazlar; gönüllerinin dilediği nimetler içinde ebedî kalırlar.', 17, 330, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2586, 103, 21, 'Enbiya', 'لَا يَحْزُنُهُمُ الْفَزَعُ الْاَكْبَرُ وَتَتَلَقّٰيهُمُ الْمَلٰٓئِكَةُۜ هٰذَا يَوْمُكُمُ الَّذ۪ي كُنْتُمْ تُوعَدُونَ', 'En büyük dehşet dahi onları tasalandırmaz. Melekler kendilerini şöyle karşılar: İşte bu size vâdedilmiş olan (mutlu) gününüzdür.', 17, 330, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2587, 104, 21, 'Enbiya', 'يَوْمَ نَطْوِي السَّمَٓاءَ كَطَيِّ السِّجِلِّ لِلْكُتُبِۜ كَمَا بَدَأْنَٓا اَوَّلَ خَلْقٍ نُع۪يدُهُۜ وَعْداً عَلَيْنَاۜ اِنَّا كُنَّا فَاعِل۪ينَ', '(Düşün o) günü ki, yazılı kâğıtların tomarını dürer gibi göğü toplayıp düreriz. Tıpkı ilk yaratmaya başladığımız gibi onu tekrar o hale getiririz. (Bu,) üzerimize aldığımız bir vaad oldu. Biz, (vâdettiğimizi) yaparız.', 17, 330, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2588, 105, 21, 'Enbiya', 'وَلَقَدْ كَتَبْنَا فِي الزَّبُورِ مِنْ بَعْدِ الذِّكْرِ اَنَّ الْاَرْضَ يَرِثُهَا عِبَادِيَ الصَّالِحُونَ', 'Andolsun Zikir´den sonra Zebur´da da: «Yeryüzüne iyi kullarım vâris olacaktır» diye yazmıştık.', 17, 330, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2589, 106, 21, 'Enbiya', 'اِنَّ ف۪ي هٰذَا لَبَلَاغاً لِقَوْمٍ عَابِد۪ينَۜ', 'İşte bunda, (bize) kulluk eden bir kavim için bir mesaj vardır.', 17, 330, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2590, 107, 21, 'Enbiya', 'وَمَٓا اَرْسَلْنَاكَ اِلَّا رَحْمَةً لِلْعَالَم۪ينَ', '(Resûlüm!) Biz seni âlemlere ancak rahmet olarak gönderdik.', 17, 330, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2591, 108, 21, 'Enbiya', 'قُلْ اِنَّمَا يُوحٰٓى اِلَيَّ اَنَّـمَٓا اِلٰهُكُمْ اِلٰهٌ وَاحِدٌۚ فَهَلْ اَنْتُمْ مُسْلِمُونَ', 'De ki: Bana sadece, sizin ilâhınızın ancak bir tek Allah olduğu vahyedildi. Hâla müslüman olmayacak mısınız?', 17, 330, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2592, 109, 21, 'Enbiya', 'فَاِنْ تَوَلَّوْا فَقُلْ اٰذَنْتُكُمْ عَلٰى سَوَٓاءٍۜ وَاِنْ اَدْر۪ٓي اَقَر۪يبٌ اَمْ بَع۪يدٌ مَا تُوعَدُونَ', 'Eğer yüz çevirirlerse de ki: (Bana emrolunanı) hepinize açıkladım. Artık size vâdolunan şey (mahşerde toplanma zamanınız) yakın mı uzak mı, bilmiyorum.', 17, 330, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2593, 110, 21, 'Enbiya', 'اِنَّهُ يَعْلَمُ الْجَهْرَ مِنَ الْقَوْلِ وَيَعْلَمُ مَا تَكْتُمُونَ', 'Şüphesiz Allah sözün açığını da bilir, gizli tuttuklarınızı da bilir.', 17, 330, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2594, 111, 21, 'Enbiya', 'وَاِنْ اَدْر۪ي لَعَلَّهُ فِتْنَةٌ لَكُمْ وَمَتَاعٌ اِلٰى ح۪ينٍ', 'Bilmiyorum, belki de o (azabın ertelenmesi), sizi denemek ve bir zamana kadar sizi (imkânlardan) faydalandırmak içindir.', 17, 330, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2595, 112, 21, 'Enbiya', 'قَالَ رَبِّ احْكُمْ بِالْحَقِّۜ وَرَبُّنَا الرَّحْمٰنُ الْمُسْتَعَانُ عَلٰى مَا تَصِفُونَ', '(Muhammed:) Rabbim! (Onlar hakkında) adaletinle hükmünü ver. Bizim Rabbimiz Rahmân´dır. Sizin anlattıklarınıza karşı yardımı umulandır, dedi.', 17, 330, 112, 4925, 'Peygamberler', 'Mekke', 73),
(2596, 1, 22, 'Hac', 'يَٓا اَيُّهَا النَّاسُ اتَّقُوا رَبَّكُمْۚ اِنَّ زَلْزَلَةَ السَّاعَةِ شَيْءٌ عَظ۪يمٌ', 'Ey insanlar! Rabbinizden korkun! Çünkü kıyamet vaktinin depremi müthiş bir şeydir!', 17, 331, 78, 5196, 'Hac', 'Medine', 103),
(2597, 2, 22, 'Hac', 'يَوْمَ تَرَوْنَهَا تَذْهَلُ كُلُّ مُرْضِعَةٍ عَمَّٓا اَرْضَعَتْ وَتَضَعُ كُلُّ ذَاتِ حَمْلٍ حَمْلَهَا وَتَرَى النَّاسَ سُكَارٰى وَمَا هُمْ بِسُكَارٰى وَلٰكِنَّ عَذَابَ اللّٰهِ شَد۪يدٌ', 'Onu gördüğünüz gün, her emzikli kadın emzirdiği çocuğu unutur, her gebe kadın çocuğunu düşürür. İnsanları da sarhoş bir halde görürsün. Oysa onlar sarhoş değillerdir; fakat Allah´ın azabı çok dehşetlidir!', 17, 331, 78, 5196, 'Hac', 'Medine', 103),
(2598, 3, 22, 'Hac', 'وَمِنَ النَّاسِ مَنْ يُجَادِلُ فِي اللّٰهِ بِغَيْرِ عِلْمٍ وَيَتَّبِعُ كُلَّ شَيْطَانٍ مَر۪يدٍۙ', 'İnsanlardan, bilgisi olmaksızın Allah hakkında tartışmaya giren ve her inatçı şeytana uyan birtakım kimseler vardır.', 17, 331, 78, 5196, 'Hac', 'Medine', 103),
(2599, 4, 22, 'Hac', 'كُتِبَ عَلَيْهِ اَنَّهُ مَنْ تَوَلَّاهُ فَاَنَّهُ يُضِلُّهُ وَيَهْد۪يهِ اِلٰى عَذَابِ السَّع۪يرِ', 'Onun (şeytan) hakkında şöyle yazılmıştır: Kim onu yoldaş edinirse bilsin ki (şeytan) kendisini saptıracak ve alevli ateşin azabına sürükleyecektir.', 17, 331, 78, 5196, 'Hac', 'Medine', 103),
(2600, 5, 22, 'Hac', 'يَٓا اَيُّهَا النَّاسُ اِنْ كُنْتُمْ ف۪ي رَيْبٍ مِنَ الْبَعْثِ فَاِنَّا خَلَقْنَاكُمْ مِنْ تُرَابٍ ثُمَّ مِنْ نُطْفَةٍ ثُمَّ مِنْ عَلَقَةٍ ثُمَّ مِنْ مُضْغَةٍ مُخَلَّقَةٍ وَغَيْرِ مُخَلَّقَةٍ لِنُبَيِّنَ لَكُمْۜ وَنُقِرُّ فِي الْاَرْحَامِ مَا نَشَٓاءُ اِلٰٓى اَجَلٍ مُسَمًّى ثُمَّ نُخْرِجُكُمْ طِفْلاً ثُمَّ لِتَبْلُغُٓوا اَشُدَّكُمْۚ وَمِنْكُمْ مَنْ يُتَوَفّٰى وَمِنْكُمْ مَنْ يُرَدُّ اِلٰٓى اَرْذَلِ الْعُمُرِ لِكَيْلَا يَعْلَمَ مِنْ بَعْدِ عِلْمٍ شَيْـٔاًۜ وَتَرَى الْاَرْضَ هَامِدَةً فَاِذَٓا اَنْزَلْنَا عَلَيْهَا الْمَٓاءَ اهْتَزَّتْ وَرَبَتْ وَاَنْبَتَتْ مِنْ كُلِّ زَوْجٍ بَه۪يجٍ', 'Ey insanlar! Eğer yeniden dirilmekten şüphede iseniz, şunu bilin ki, biz sizi topraktan, sonra nutfeden, sonra alakadan (aşılanmış yumurtadan), sonra uzuvları (önce) belirsiz, (sonra) belirlenmiş canlı et parçasından (uzuvları zamanla oluşan ceninden) yarattık ki size (kudretimizi) gösterelim. Ve dilediğimizi, belirlenmiş bir süreye kadar rahimlerde bekletiriz; sonra sizi bir bebek olarak dışarı çıkarırız. Sonra güçlü çağınıza ulaşmanız için (sizi büyütürüz). İçinizden kimi vefat eder; yine içinizden kimi de ömrün en verimsiz çağına kadar götürülür; ta ki bilen bir kimse olduktan sonra bir şey bilmez hale gelsin. Sen, yeryüzünü de kupkuru ve ölü bir halde görürsün; fakat biz, üzerine yağmur indirdiğimizde o, kıpırdanır, kabarır ve her çeşitten (veya çiftten) iç açıcı bitkiler verir.', 17, 331, 78, 5196, 'Hac', 'Medine', 103),
(2601, 6, 22, 'Hac', 'ذٰلِكَ بِاَنَّ اللّٰهَ هُوَ الْحَقُّ وَاَنَّهُ يُحْـيِ الْمَوْتٰى وَاَنَّهُ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌۙ', 'Çünkü Allah hakkın ta kendisidir; O, ölüleri diriltir; yine O, her şeye hakkıyla kadirdir.', 17, 332, 78, 5196, 'Hac', 'Medine', 103),
(2602, 7, 22, 'Hac', 'وَاَنَّ السَّاعَةَ اٰتِيَةٌ لَا رَيْبَ ف۪يهَاۙ وَاَنَّ اللّٰهَ يَبْعَثُ مَنْ فِي الْقُبُورِ', 'Kıyamet vakti de gelecektir; bunda şüphe yoktur. Ve Allah kabirlerdeki kimseleri diriltip kaldıracaktır.', 17, 332, 78, 5196, 'Hac', 'Medine', 103),
(2603, 8, 22, 'Hac', 'وَمِنَ النَّاسِ مَنْ يُجَادِلُ فِي اللّٰهِ بِغَيْرِ عِلْمٍ وَلَا هُدًى وَلَا كِتَابٍ مُن۪يرٍۙ', '(8-9) İnsanlardan bazısı, bir bilgisi, bir rehberi ve (vahye dayanan) aydınlatıcı bir kitabı olmadığı halde, sırf Allah yolundan saptırmak için yanını eğip bükerek (kibir ve azamet içinde) Allah hakkında tartışmaya kalkar. Onun için dünyada bir rezillik vardır; kıyamet gününde ise ona yakıcı azabı tattıracağız.', 17, 332, 78, 5196, 'Hac', 'Medine', 103),
(2604, 9, 22, 'Hac', 'ثَانِيَ عِطْفِه۪ لِيُضِلَّ عَنْ سَب۪يلِ اللّٰهِۜ لَهُ فِي الدُّنْيَا خِزْيٌ وَنُذ۪يقُهُ يَوْمَ الْقِيٰمَةِ عَذَابَ الْحَر۪يقِ', '(8-9) İnsanlardan bazısı, bir bilgisi, bir rehberi ve (vahye dayanan) aydınlatıcı bir kitabı olmadığı halde, sırf Allah yolundan saptırmak için yanını eğip bükerek (kibir ve azamet içinde) Allah hakkında tartışmaya kalkar. Onun için dünyada bir rezillik vardır; kıyamet gününde ise ona yakıcı azabı tattıracağız.', 17, 332, 78, 5196, 'Hac', 'Medine', 103),
(2605, 10, 22, 'Hac', 'ذٰلِكَ بِمَا قَدَّمَتْ يَدَاكَ وَاَنَّ اللّٰهَ لَيْسَ بِظَلَّامٍ لِلْعَب۪يدِ۟', 'İşte bu, önceden yapıp ettiklerin yüzündendir (denilir). Elbette Allah kullarına haksızlık edici değildir.', 17, 332, 78, 5196, 'Hac', 'Medine', 103),
(2606, 11, 22, 'Hac', 'وَمِنَ النَّاسِ مَنْ يَعْبُدُ اللّٰهَ عَلٰى حَرْفٍۚ فَاِنْ اَصَابَهُ خَيْرٌۨ اطْمَاَنَّ بِه۪ۚ وَاِنْ اَصَابَتْهُ فِتْنَةٌۨ انْقَلَبَ عَلٰى وَجْهِه۪۠ خَسِرَ الدُّنْيَا وَالْاٰخِرَةَۜ ذٰلِكَ هُوَ الْخُسْرَانُ الْمُب۪ينُ', 'İnsanlardan kimi Allah´a yalnız bir yönden kulluk eder. Şöyle ki: Kendisine bir iyilik dokunursa buna pek memnun olur, bir de musibete uğrarsa çehresi değişir (dinden yüz çevirir). O, dünyasını da, ahiretini de kaybetmiştir. İşte bu, apaçık ziyanın ta kendisidir.', 17, 332, 78, 5196, 'Hac', 'Medine', 103),
(2607, 12, 22, 'Hac', 'يَدْعُوا مِنْ دُونِ اللّٰهِ مَا لَا يَضُرُّهُ وَمَا لَا يَنْفَعُهُۜ ذٰلِكَ هُوَ الضَّلَالُ الْبَع۪يدُ', 'O, Allah´ı bırakıp, kendisine ne faydası, ne de zararı dokunacak olan şeylere yalvarır. Bu, (haktan) büsbütün uzak olan sapıklığın ta kendisidir.', 17, 332, 78, 5196, 'Hac', 'Medine', 103),
(2608, 13, 22, 'Hac', 'يَدْعُوا لَمَنْ ضَرُّهُٓ اَقْرَبُ مِنْ نَفْعِه۪ۜ لَبِئْسَ الْمَوْلٰى وَلَبِئْسَ الْعَش۪يرُ', 'O, zararı faydasından daha (akla) yakın olan bir varlığa yalvarır. O (yalvardığı), ne kötü bir yardımcı, ne kötü bir dosttur!', 17, 332, 78, 5196, 'Hac', 'Medine', 103),
(2609, 14, 22, 'Hac', 'اِنَّ اللّٰهَ يُدْخِلُ الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ جَنَّاتٍ تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُۜ اِنَّ اللّٰهَ يَفْعَلُ مَا يُر۪يدُ', 'Muhakkak ki Allah, iman edip iyi davranışlarda bulunan kimseleri, zemininden ırmaklar akan cennetlere kabul eder. Şüphesiz Allah dilediği şeyi yapar.', 17, 332, 78, 5196, 'Hac', 'Medine', 103),
(2610, 15, 22, 'Hac', 'مَنْ كَانَ يَظُنُّ اَنْ لَنْ يَنْصُرَهُ اللّٰهُ فِي الدُّنْيَا وَالْاٰخِرَةِ فَلْيَمْدُدْ بِسَبَبٍ اِلَى السَّمَٓاءِ ثُمَّ لْيَقْطَعْ فَلْيَنْظُرْ هَلْ يُذْهِبَنَّ كَيْدُهُ مَا يَغ۪يظُ', 'Her kim, Allah´ın, dünya ve ahirette ona (Resûlüne) asla yardım etmeyeceğini zannetmekte ise, (Allah ona yardım ettiğine göre) artık o kimse tavana bir ip atsın; (boğazına geçirsin); sonra da (ayağını yerden) kessin! Şimdi bu kimse baksın! Acaba, hilesi (bu yaptığı), öfke duyduğu şeyi (Allah´ın Peygamber´e yardımını) gerçekten engelleyecek mi?', 17, 332, 78, 5196, 'Hac', 'Medine', 103),
(2611, 16, 22, 'Hac', 'وَكَذٰلِكَ اَنْزَلْنَاهُ اٰيَاتٍ بَيِّنَاتٍۙ وَاَنَّ اللّٰهَ يَهْد۪ي مَنْ يُر۪يدُ', 'İşte böylece biz o Kur´an´ı açık seçik âyetler halinde indirdik. Gerçek şu ki Allah dilediği kimseyi doğru yola sevkeder.', 17, 333, 78, 5196, 'Hac', 'Medine', 103),
(2612, 17, 22, 'Hac', 'اِنَّ الَّذ۪ينَ اٰمَنُوا وَالَّذ۪ينَ هَادُوا وَالصَّابِـ۪ٔينَ وَالنَّصَارٰى وَالْمَجُوسَ وَالَّذ۪ينَ اَشْرَكُواۗ اِنَّ اللّٰهَ يَفْصِلُ بَيْنَهُمْ يَوْمَ الْقِيٰمَةِۜ اِنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ شَه۪يدٌ', 'Mümin olanlar, yahudi olanlar, sâbiîler, hıristiyanlar, mecûsîler ve müşrik olanlara gelince, muhakkak ki Allah, bunlar arasında kıyamet gününde (ayrı ayrı) hükmünü verir. Çünkü Allah her şeyi hakkıyla bilendir.', 17, 333, 78, 5196, 'Hac', 'Medine', 103),
(2613, 18, 22, 'Hac', 'اَلَمْ تَرَ اَنَّ اللّٰهَ يَسْجُدُ لَهُ مَنْ فِي السَّمٰوَاتِ وَمَنْ فِي الْاَرْضِ وَالشَّمْسُ وَالْقَمَرُ وَالنُّجُومُ وَالْجِبَالُ وَالشَّجَرُ وَالدَّوَٓابُّ وَكَث۪يرٌ مِنَ النَّاسِۜ وَكَث۪يرٌ حَقَّ عَلَيْهِ الْعَذَابُۜ وَمَنْ يُهِنِ اللّٰهُ فَمَا لَهُ مِنْ مُكْرِمٍۜ اِنَّ اللّٰهَ يَفْعَلُ مَا يَشَٓاءُ ۩', 'Görmez misin ki, göklerde olanlar ve yerde olanlar, güneş, ay, yıldızlar, dağlar, ağaçlar, hayvanlar ve insanların birçoğu Allah´a secde ediyor; birçoğunun üzerine de azap hak olmuştur. Allah kimi hor ve hakir kılarsa, artık onu değerli kılacak bir kimse yoktur. Şüphesiz Allah dilediğini yapar.', 17, 333, 78, 5196, 'Hac', 'Medine', 103),
(2614, 19, 22, 'Hac', 'هٰذَانِ خَصْمَانِ اخْتَصَمُوا ف۪ي رَبِّهِمْۘ فَالَّذ۪ينَ كَفَرُوا قُطِّعَتْ لَهُمْ ثِيَابٌ مِنْ نَارٍۜ يُصَبُّ مِنْ فَوْقِ رُؤُ۫سِهِمُ الْحَم۪يمُۚ', 'Şu iki gurup, Rableri hakkında çekişen iki hasımdır: İmdi, inkâr edenler için ateşten bir elbise biçilmiştir. Onların başlarının üstünden kaynar su dökülecektir!', 17, 333, 78, 5196, 'Hac', 'Medine', 103),
(2615, 20, 22, 'Hac', 'يُصْهَرُ بِه۪ مَا ف۪ي بُطُونِهِمْ وَالْجُلُودُۜ', 'Bununla, karınlarının içindeki (organlar) ve derileri eritilecektir!', 17, 333, 78, 5196, 'Hac', 'Medine', 103),
(2616, 21, 22, 'Hac', 'وَلَهُمْ مَقَامِعُ مِنْ حَد۪يدٍ', 'Bir de onlar için demir kamçılar vardır!', 17, 333, 78, 5196, 'Hac', 'Medine', 103),
(2617, 22, 22, 'Hac', 'كُلَّمَٓا اَرَادُٓوا اَنْ يَخْرُجُوا مِنْهَا مِنْ غَمٍّ اُع۪يدُوا ف۪يهَا وَذُوقُوا عَذَابَ الْحَر۪يقِ۟', 'Izdıraptan dolayı oradan her çıkmak istediklerinde, oraya geri döndürülürler ve: «Tadın bu yakıcı azabı!» (denilir).', 17, 333, 78, 5196, 'Hac', 'Medine', 103),
(2618, 23, 22, 'Hac', 'اِنَّ اللّٰهَ يُدْخِلُ الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ جَنَّاتٍ تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُ يُحَلَّوْنَ ف۪يهَا مِنْ اَسَاوِرَ مِنْ ذَهَبٍ وَلُؤْلُؤً۬اۜ وَلِبَاسُهُمْ ف۪يهَا حَر۪يرٌ', 'Muhakkak ki Allah, iman edip iyi davranışlarda bulunanları, zemininden ırmaklar akan cennetlere kabul eder. Bunlar orada altın bileziklerle ve incilerle bezenirler. Orada giyecekleri ise ipektir.', 17, 333, 78, 5196, 'Hac', 'Medine', 103),
(2619, 24, 22, 'Hac', 'وَهُدُٓوا اِلَى الطَّيِّبِ مِنَ الْقَوْلِۗ وَهُدُٓوا اِلٰى صِرَاطِ الْحَم۪يدِ', 'Ve onlar, sözün en güzeline yöneltilmişler, övgüye lâyık olan Allah´ın yoluna iletilmişlerdir.', 17, 334, 78, 5196, 'Hac', 'Medine', 103),
(2620, 25, 22, 'Hac', 'اِنَّ الَّذ۪ينَ كَفَرُوا وَيَصُدُّونَ عَنْ سَب۪يلِ اللّٰهِ وَالْمَسْجِدِ الْحَرَامِ الَّذ۪ي جَعَلْنَاهُ لِلنَّاسِ سَوَٓاءًۨ الْعَاكِفُ ف۪يهِ وَالْبَادِۜ وَمَنْ يُرِدْ ف۪يهِ بِـاِلْحَادٍ بِظُـلْمٍ نُذِقْهُ مِنْ عَذَابٍ اَل۪يمٍ۟', 'İnkâr edenler, Allah´ın yolundan ve -yerli, taşralı- bütün insanlara eşit (kıble veya mâbed) kıldığımız Mescid-i Harâm´dan (insanları) alıkoymaya kalkanlar (şunu bilmeliler ki) kim orada (böyle) zulüm ile haktan sapmak isterse ona acı azaptan tattırırız.', 17, 334, 78, 5196, 'Hac', 'Medine', 103),
(2621, 26, 22, 'Hac', 'وَاِذْ بَوَّأْنَا لِاِبْرٰه۪يمَ مَكَانَ الْبَيْتِ اَنْ لَا تُشْرِكْ ب۪ي شَيْـٔاً وَطَهِّرْ بَيْتِيَ لِلطَّٓائِف۪ينَ وَالْقَٓائِم۪ينَ وَالرُّكَّعِ السُّجُودِ', 'Bir zamanlar İbrahim´e Beytullah´ın yerini hazırlamış ve (ona şöyle demiştik): Bana hiçbir şeyi eş tutma; tavaf edenler, ayakta ibadet edenler, rükû ve secdeye varanlar için evimi temiz tut.', 17, 334, 78, 5196, 'Hac', 'Medine', 103),
(2622, 27, 22, 'Hac', 'وَاَذِّنْ فِي النَّاسِ بِالْحَجِّ يَأْتُوكَ رِجَالاً وَعَلٰى كُلِّ ضَامِرٍ يَأْت۪ينَ مِنْ كُلِّ فَجٍّ عَم۪يقٍۙ', '(27-28) İnsanlar arasında haccı ilân et ki, gerek yaya olarak, gerekse nice uzak yoldan gelen yorgun argın develer üzerinde, kendilerine ait bir takım yararları yakînen görmeleri, Allah´ın kendilerine rızık olarak verdiği kurbanlık hayvanlar üzerine belli günlerde Allah´ın ismini anmaları (kurban kesmeleri için) sana (Kâbe´ye) gelsinler. Artık ondan hem kendiniz yeyin, hem de yoksula, fakire yedirin.', 17, 334, 78, 5196, 'Hac', 'Medine', 103),
(2623, 28, 22, 'Hac', 'لِيَشْهَدُوا مَنَافِـعَ لَهُمْ وَيَذْكُرُوا اسْمَ اللّٰهِ ف۪ٓي اَيَّامٍ مَعْلُومَاتٍ عَلٰى مَا رَزَقَهُمْ مِنْ بَه۪يمَةِ الْاَنْعَامِۚ فَكُلُوا مِنْهَا وَاَطْعِمُوا الْـبَٓائِسَ الْفَق۪يرَۘ', '(27-28) İnsanlar arasında haccı ilân et ki, gerek yaya olarak, gerekse nice uzak yoldan gelen yorgun argın develer üzerinde, kendilerine ait bir takım yararları yakînen görmeleri, Allah´ın kendilerine rızık olarak verdiği kurbanlık hayvanlar üzerine belli günlerde Allah´ın ismini anmaları (kurban kesmeleri için) sana (Kâbe´ye) gelsinler. Artık ondan hem kendiniz yeyin, hem de yoksula, fakire yedirin.', 17, 334, 78, 5196, 'Hac', 'Medine', 103),
(2624, 29, 22, 'Hac', 'ثُمَّ لْيَقْضُوا تَفَثَهُمْ وَلْيُوفُوا نُذُورَهُمْ وَلْيَطَّوَّفُوا بِالْبَيْتِ الْعَت۪يقِ', 'Sonra kirlerini gidersinler; adaklarını yerine getirsinler ve o Eski Ev´i (Kâbe´yi) tavaf etsinler.', 17, 334, 78, 5196, 'Hac', 'Medine', 103),
(2625, 30, 22, 'Hac', 'ذٰلِكَۗ وَمَنْ يُعَظِّمْ حُرُمَاتِ اللّٰهِ فَهُوَ خَيْرٌ لَهُ عِنْدَ رَبِّه۪ۜ وَاُحِلَّتْ لَكُمُ الْاَنْعَامُ اِلَّا مَا يُتْلٰى عَلَيْكُمْ فَاجْتَنِبُوا الرِّجْسَ مِنَ الْاَوْثَانِ وَاجْتَنِبُوا قَوْلَ الزُّورِۙ', 'Durum böyle. Her kim, Allah´ın emir ve yasaklarına saygı gösterirse, bu, Rabbinin katında kendisi için daha hayırlıdır. (Haram olduğu) size okunanların dışında kalan hayvanlar size helâl kılındı. O halde, pislikten, putlardan sakının; yalan sözden sakının.', 17, 334, 78, 5196, 'Hac', 'Medine', 103),
(2626, 31, 22, 'Hac', 'حُنَفَٓاءَ لِلّٰهِ غَيْرَ مُشْرِك۪ينَ بِه۪ۜ وَمَنْ يُشْرِكْ بِاللّٰهِ فَكَاَنَّمَا خَرَّ مِنَ السَّمَٓاءِ فَتَخْطَفُهُ الطَّيْرُ اَوْ تَهْو۪ي بِهِ الرّ۪يحُ ف۪ي مَكَانٍ سَح۪يقٍ', 'Kendisine ortak koşmaksızın Allah´ın hanifleri (O´nun birliğini tanıyan müminler olun). Kim Allah´a ortak koşarsa sanki o, gökten düşüp parçalanmış da kendisini kuşlar kapmış, yahut rüzgâr onu uzak bir yere sürüklemiş (bir nesne) gibidir.', 17, 335, 78, 5196, 'Hac', 'Medine', 103),
(2627, 32, 22, 'Hac', 'ذٰلِكَۗ وَمَنْ يُعَظِّمْ شَعَٓائِرَ اللّٰهِ فَاِنَّهَا مِنْ تَقْوَى الْقُلُوبِ', 'Durum öyledir. Her kim Allah´ın hükümlerine saygı gösterirse, şüphesiz bu, kalplerin takvâsındandır.', 17, 335, 78, 5196, 'Hac', 'Medine', 103),
(2628, 33, 22, 'Hac', 'لَكُمْ ف۪يهَا مَنَافِـعُ اِلٰٓى اَجَلٍ مُسَمًّى ثُمَّ مَحِلُّـهَٓا اِلَى الْبَيْتِ الْعَت۪يقِ۟', 'Onlarda (kurbanlık hayvanlarda veya hac fiillerinde) sizin için belli bir süreye kadar birtakım yararlar vardır. Sonra bunların varacakları (biteceği) yer, Eski Ev´e (Kâbe´ye) kadardır.', 17, 335, 78, 5196, 'Hac', 'Medine', 103),
(2629, 34, 22, 'Hac', 'وَلِكُلِّ اُمَّةٍ جَعَلْنَا مَنْسَكاً لِيَذْكُرُوا اسْمَ اللّٰهِ عَلٰى مَا رَزَقَهُمْ مِنْ بَه۪يمَةِ الْاَنْعَامِۜ فَاِلٰهُكُمْ اِلٰهٌ وَاحِدٌ فَلَـهُٓ اَسْلِمُواۜ وَبَشِّرِ الْمُخْبِت۪ينَۙ', 'Biz, her ümmete -(Kurban kesmeye uygun) hayvan cinsinden kendilerine rızık olarak verdiklerimiz üzerine Allah´ın adını ansınlar diye- kurban kesmeyi gerekli kıldık. İmdi, İlâhınız, bir tek İlâh´tır. Öyle ise, O´na teslim olun. (Ey Muhammed!) O ihlâslı ve mütevazi insanları müjdele!', 17, 335, 78, 5196, 'Hac', 'Medine', 103),
(2630, 35, 22, 'Hac', 'اَلَّذ۪ينَ اِذَا ذُكِرَ اللّٰهُ وَجِلَتْ قُلُوبُهُمْ وَالصَّابِر۪ينَ عَلٰى مَٓا اَصَابَهُمْ وَالْمُق۪يمِي الصَّلٰوةِۙ وَمِمَّا رَزَقْنَاهُمْ يُنْفِقُونَ', 'Onlar öyle kimseler ki, Allah anıldığı zaman kalpleri titrer; başlarına gelene sabrederler, namaz kılarlar ve kendilerine rızık olarak verdiğimiz şeylerden (Allah için) harcarlar.', 17, 335, 78, 5196, 'Hac', 'Medine', 103),
(2631, 36, 22, 'Hac', 'وَالْبُدْنَ جَعَلْنَاهَا لَكُمْ مِنْ شَعَٓائِرِ اللّٰهِ لَكُمْ ف۪يهَا خَيْرٌۗ فَاذْكُرُوا اسْمَ اللّٰهِ عَلَيْهَا صَوَٓافَّۚ فَاِذَا وَجَبَتْ جُنُوبُهَا فَكُلُوا مِنْهَا وَاَطْعِمُوا الْقَانِـعَ وَالْمُعْتَرَّۜ كَذٰلِكَ سَخَّرْنَاهَا لَكُمْ لَعَلَّكُمْ تَشْكُرُونَ', 'Biz, büyük baş hayvanları da sizin için Allah´ın (dininin) işaretlerinden (kurban) kıldık. Onlarda sizin için hayır vardır. Şu halde onlar, ayakları üzerine dururken üzerlerine Allah´ın ismini anınız (ve kurban ediniz). Yan üstü yere düştüklerinde ise, artık (canı çıktığında) onlardan hem kendiniz yeyin, hem de ihtiyacını gizleyen-gizlemeyen fakirlere yedirin. İşte bu hayvanları biz, şükredesiniz diye sizin istifadenize verdik.', 17, 335, 78, 5196, 'Hac', 'Medine', 103),
(2632, 37, 22, 'Hac', 'لَنْ يَنَالَ اللّٰهَ لُحُومُهَا وَلَا دِمَٓاؤُ۬هَا وَلٰكِنْ يَنَالُهُ التَّقْوٰى مِنْكُمْۜ كَذٰلِكَ سَخَّرَهَا لَكُمْ لِتُكَبِّرُوا اللّٰهَ عَلٰى مَا هَدٰيكُمْۜ وَبَشِّرِ الْمُحْسِن۪ينَ', 'Onların ne etleri ne de kanları Allah´a ulaşır; fakat O´na sadece sizin takvânız ulaşır. Sizi hidayete erdirdiğinden dolayı Allah´ı büyük tanıyasınız diye O, bu hayvanları böylece sizin istifadenize verdi. (Ey Muhammed!) Güzel davrananları müjdele!', 17, 335, 78, 5196, 'Hac', 'Medine', 103),
(2633, 38, 22, 'Hac', 'اِنَّ اللّٰهَ يُدَافِـعُ عَنِ الَّذ۪ينَ اٰمَنُواۜ اِنَّ اللّٰهَ لَا يُحِبُّ كُلَّ خَوَّانٍ كَفُورٍ۟', 'Allah, iman edenleri korur. Şu da muhakkak ki Allah, hain ve nankör olan herkesi sevgisinden mahrum eder.', 17, 335, 78, 5196, 'Hac', 'Medine', 103),
(2634, 39, 22, 'Hac', 'اُذِنَ لِلَّذ۪ينَ يُقَاتَلُونَ بِاَنَّهُمْ ظُلِمُواۜ وَاِنَّ اللّٰهَ عَلٰى نَصْرِهِمْ لَقَد۪يرٌۙ', 'Kendileriyle savaşılanlara (müminlere), zulme uğramış olmaları sebebiyle, (savaş konusunda) izin verildi. Şüphe yok ki Allah, onlara yardıma mutlak surette kadirdir.', 17, 336, 78, 5196, 'Hac', 'Medine', 103),
(2635, 40, 22, 'Hac', 'اَلَّذ۪ينَ اُخْرِجُوا مِنْ دِيَارِهِمْ بِغَيْرِ حَقٍّ اِلَّٓا اَنْ يَقُولُوا رَبُّنَا اللّٰهُۜ وَلَوْلَا دَفْعُ اللّٰهِ النَّاسَ بَعْضَهُمْ بِبَعْضٍ لَهُدِّمَتْ صَوَامِــعُ وَبِيَعٌ وَصَلَوَاتٌ وَمَسَاجِدُ يُذْكَرُ ف۪يهَا اسْمُ اللّٰهِ كَث۪يراًۜ وَلَيَنْصُرَنَّ اللّٰهُ مَنْ يَنْصُرُهُۜ اِنَّ اللّٰهَ لَقَوِيٌّ عَز۪يزٌ', 'Onlar, başka değil, sırf «Rabbimiz Allah´tır» dedikleri için haksız yere yurtlarından çıkarılmış kimselerdir. Eğer Allah, bir kısım insanları (kötülüklerini) diğer bir kısmı ile defedip önlemeseydi, mutlak surette, içlerinde Allah´ın ismi bol bol anılan manastırlar, kiliseler, havralar ve mescidler yıkılır giderdi. Allah, kendisine (kendi dinine) yardım edenlere muhakkak surette yardım eder. Hiç şüphesiz Allah, güçlüdür, galiptir.', 17, 336, 78, 5196, 'Hac', 'Medine', 103),
(2636, 41, 22, 'Hac', 'اَلَّذ۪ينَ اِنْ مَكَّنَّاهُمْ فِي الْاَرْضِ اَقَامُوا الصَّلٰوةَ وَاٰتَوُا الزَّكٰوةَ وَاَمَرُوا بِالْمَعْرُوفِ وَنَهَوْا عَنِ الْمُنْكَرِۜ وَلِلّٰهِ عَاقِبَةُ الْاُمُورِ', 'Onlar (o müminler) ki, eğer kendilerine yeryüzünde iktidar verirsek namazı kılar, zekâtı verirler, iyiliği emreder ve kötülükten nehyederler. İşlerin sonu Allah´a varır.', 17, 336, 78, 5196, 'Hac', 'Medine', 103),
(2637, 42, 22, 'Hac', 'وَاِنْ يُكَذِّبُوكَ فَقَدْ كَذَّبَتْ قَبْلَهُمْ قَوْمُ نُوحٍ وَعَادٌ وَثَمُودُۙ', '(42-44) (Resûlüm!) Eğer onlar (inkârcılar) seni yalanlıyorlarsa, (şunu bil ki) onlardan önce Nuh´un kavmi, Âd, Semûd, İbrahim´in kavmi, Lût´un kavmi ve Medyen halkı da (peygamberlerini) yalanladılar. Musa da yalanlanmıştı. İşte ben o kâfirlere süre tanıdım, sonra onları yakaladım. Nasıl oldu benim onları reddim (cezalandırmam)!', 17, 336, 78, 5196, 'Hac', 'Medine', 103),
(2638, 43, 22, 'Hac', 'وَقَوْمُ اِبْرٰه۪يمَ وَقَوْمُ لُوطٍۙ', '(42-44) (Resûlüm!) Eğer onlar (inkârcılar) seni yalanlıyorlarsa, (şunu bil ki) onlardan önce Nuh´un kavmi, Âd, Semûd, İbrahim´in kavmi, Lût´un kavmi ve Medyen halkı da (peygamberlerini) yalanladılar. Musa da yalanlanmıştı. İşte ben o kâfirlere süre tanıdım, sonra onları yakaladım. Nasıl oldu benim onları reddim (cezalandırmam)!', 17, 336, 78, 5196, 'Hac', 'Medine', 103),
(2639, 44, 22, 'Hac', 'وَاَصْحَابُ مَدْيَنَۚ وَكُذِّبَ مُوسٰى فَاَمْلَيْتُ لِلْكَافِر۪ينَ ثُمَّ اَخَذْتُهُمْۚ فَكَيْفَ كَانَ نَك۪يرِ', '(42-44) (Resûlüm!) Eğer onlar (inkârcılar) seni yalanlıyorlarsa, (şunu bil ki) onlardan önce Nuh´un kavmi, Âd, Semûd, İbrahim´in kavmi, Lût´un kavmi ve Medyen halkı da (peygamberlerini) yalanladılar. Musa da yalanlanmıştı. İşte ben o kâfirlere süre tanıdım, sonra onları yakaladım. Nasıl oldu benim onları reddim (cezalandırmam)!', 17, 336, 78, 5196, 'Hac', 'Medine', 103),
(2640, 45, 22, 'Hac', 'فَكَاَيِّنْ مِنْ قَرْيَةٍ اَهْلَكْنَاهَا وَهِيَ ظَالِمَةٌ فَهِيَ خَاوِيَةٌ عَلٰى عُرُوشِهَا وَبِئْرٍ مُعَطَّـلَةٍ وَقَصْرٍ مَش۪يدٍ', 'Nitekim, birçok memleket vardı ki, o memleket (halkı) zulmetmekte iken, biz onları helâk ettik. Şimdi o ülkelerde duvarlar, (çökmüş) tavanların üzerine yıkılmıştır. Nice kullanılmaz hale gelmiş kuyular ve (ıssız kalmış) ulu saraylar vardır.', 17, 336, 78, 5196, 'Hac', 'Medine', 103),
(2641, 46, 22, 'Hac', 'اَفَلَمْ يَس۪يرُوا فِي الْاَرْضِ فَتَكُونَ لَهُمْ قُلُوبٌ يَعْقِلُونَ بِهَٓا اَوْ اٰذَانٌ يَسْمَعُونَ بِهَاۚ فَاِنَّهَا لَا تَعْمَى الْاَبْصَارُ وَلٰكِنْ تَعْمَى الْقُلُوبُ الَّت۪ي فِي الصُّدُورِ', '(Seni yalanlayanlar) hiç yeryüzünde dolaşmadılar mı? Zira dolaşsalardı elbette düşünecek kalpleri ve işitecek kulakları olurdu. Ama gerçek şu ki, gözler kör olmaz; lâkin göğüsler içindeki kalpler kör olur.', 17, 336, 78, 5196, 'Hac', 'Medine', 103),
(2642, 47, 22, 'Hac', 'وَيَسْتَعْجِلُونَكَ بِالْعَذَابِ وَلَنْ يُخْلِفَ اللّٰهُ وَعْدَهُۜ وَاِنَّ يَوْماً عِنْدَ رَبِّكَ كَاَلْفِ سَنَةٍ مِمَّا تَعُدُّونَ', '(Resûlüm!) Onlar senden azabın çabuk gelmesini istiyorlar. Allah vâdinden asla dönmez. Muhakkak ki, Rabbinin nezdinde bir gün sizin saymakta olduklarınızdan bin yıl gibidir.', 17, 337, 78, 5196, 'Hac', 'Medine', 103),
(2643, 48, 22, 'Hac', 'وَكَاَيِّنْ مِنْ قَرْيَةٍ اَمْلَيْتُ لَهَا وَهِيَ ظَالِمَةٌ ثُمَّ اَخَذْتُهَاۚ وَاِلَيَّ الْمَص۪يرُ۟', 'Nice ülkeler var ki, zulmedip dururlarken onlara mühlet verdim. Sonunda onları yakaladım. Dönüş yalnız banadır.', 17, 337, 78, 5196, 'Hac', 'Medine', 103),
(2644, 49, 22, 'Hac', 'قُلْ يَٓا اَيُّهَا النَّاسُ اِنَّـمَٓا اَنَا۬ لَكُمْ نَذ۪يرٌ مُب۪ينٌۚ', 'De ki: Ey insanlar! Ben ancak sizin için apaçık bir uyarıcıyım.', 17, 337, 78, 5196, 'Hac', 'Medine', 103),
(2645, 50, 22, 'Hac', 'فَالَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ لَهُمْ مَغْفِرَةٌ وَرِزْقٌ كَر۪يمٌ', 'İman edip sâlih ameller işleyen kimseler için mağfiret ve bol rızık vardır.', 17, 337, 78, 5196, 'Hac', 'Medine', 103),
(2646, 51, 22, 'Hac', 'وَالَّذ۪ينَ سَعَوْا ف۪ٓي اٰيَاتِنَا مُعَاجِز۪ينَ اُو۬لٰٓئِكَ اَصْحَابُ الْجَح۪يمِ', 'Âyetlerimiz hakkında (onları tesirsiz kılmak için) birbirlerini geri bırakırcasına yarışanlara gelince, işte bunlar, cehennemliklerdir.', 17, 337, 78, 5196, 'Hac', 'Medine', 103),
(2647, 52, 22, 'Hac', 'وَمَٓا اَرْسَلْنَا مِنْ قَبْلِكَ مِنْ رَسُولٍ وَلَا نَبِيٍّ اِلَّٓا اِذَا تَمَنّٰٓى اَلْقَى الشَّيْطَانُ ف۪ٓي اُمْنِيَّتِه۪ۚ فَيَنْسَخُ اللّٰهُ مَا يُلْقِي الشَّيْطَانُ ثُمَّ يُحْكِمُ اللّٰهُ اٰيَاتِه۪ۜ وَاللّٰهُ عَل۪يمٌ حَك۪يمٌۙ', '(Ey Muhammed!) Biz, senden önce hiçbir resûl ve nebî göndermedik ki, o, bir temennide bulunduğunda, şeytan onun dileğine ille de (beşerî arzular) katmaya kalkışmasın. Ne var ki Allah, şeytanın katacağı şeyi iptal eder. Sonra Allah, kendi âyetlerini (lafız ve mana bakımından) sağlam olarak yerleştirir. Allah, hakkıyla bilendir, hüküm ve hikmet sahibidir.', 17, 337, 78, 5196, 'Hac', 'Medine', 103),
(2648, 53, 22, 'Hac', 'لِيَجْعَلَ مَا يُلْقِي الشَّيْطَانُ فِتْنَةً لِلَّذ۪ينَ ف۪ي قُلُوبِهِمْ مَرَضٌ وَالْقَاسِيَةِ قُلُوبُهُمْۜ وَاِنَّ الظَّالِم۪ينَ لَف۪ي شِقَاقٍ بَع۪يدٍۙ', '(Allah, şeytanın böyle yapmasına müsaade eder ki) kalplerinde hastalık olanlar ve kalpleri katılaşanlar için, şeytanın kattığı şeyi bir deneme (vesilesi) yapsın. Zalimler, gerçekten (haktan) oldukça uzak bir ayrılık içindedirler.', 17, 337, 78, 5196, 'Hac', 'Medine', 103),
(2649, 54, 22, 'Hac', 'وَلِيَعْلَمَ الَّذ۪ينَ اُو۫تُوا الْعِلْمَ اَنَّهُ الْحَقُّ مِنْ رَبِّكَ فَيُؤْمِنُوا بِه۪ فَتُخْبِتَ لَهُ قُلُوبُهُمْۜ وَاِنَّ اللّٰهَ لَهَادِ الَّذ۪ينَ اٰمَنُٓوا اِلٰى صِرَاطٍ مُسْتَق۪يمٍ', 'Bir de, kendilerine ilim verilenler, onun (Kur´an´ın) hakikaten Rabbin tarafından gelmiş bir gerçek olduğunu bilsinler de ona inansınlar, bu sayede kalpleri huzur ve tatmine kavuşsun. Şüphesiz ki Allah, iman edenleri, kesinlikle dosdoğru bir yola yöneltir.', 17, 337, 78, 5196, 'Hac', 'Medine', 103),
(2650, 55, 22, 'Hac', 'وَلَا يَزَالُ الَّذ۪ينَ كَفَرُوا ف۪ي مِرْيَةٍ مِنْهُ حَتّٰى تَأْتِيَهُمُ السَّاعَةُ بَغْتَةً اَوْ يَأْتِيَهُمْ عَذَابُ يَوْمٍ عَق۪يمٍ', 'İnkâr edenler, kendilerine o saat ansızın gelinceye, yahut da (kendileri için hayır yönünden) kısır bir günün azabı gelinceye kadar onun (Kur´an) hakkında hep şüphe içindedirler.', 17, 337, 78, 5196, 'Hac', 'Medine', 103),
(2651, 56, 22, 'Hac', 'اَلْمُلْكُ يَوْمَئِذٍ لِلّٰهِۜ يَحْكُمُ بَيْنَهُمْۜ فَالَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ ف۪ي جَنَّاتِ النَّع۪يمِ', 'O gün, mülk Allah´ındır. İnsanlar arasında hüküm verir. (Bu hüküm gereği) iman edip iyi davranışlarda bulunanlar Naîm cennetlerinin içindedirler.', 17, 338, 78, 5196, 'Hac', 'Medine', 103),
(2652, 57, 22, 'Hac', 'وَالَّذ۪ينَ كَفَرُوا وَكَذَّبُوا بِاٰيَاتِنَا فَاُو۬لٰٓئِكَ لَهُمْ عَذَابٌ مُه۪ينٌ', 'İnkâr edip âyetlerimizi yalanlayanlara gelince, işte onlar için alçaltıcı bir azap vardır.', 17, 338, 78, 5196, 'Hac', 'Medine', 103),
(2653, 58, 22, 'Hac', 'وَالَّذ۪ينَ هَاجَرُوا ف۪ي سَب۪يلِ اللّٰهِ ثُمَّ قُتِلُٓوا اَوْ مَاتُوا لَيَرْزُقَنَّهُمُ اللّٰهُ رِزْقاً حَسَناًۜ وَاِنَّ اللّٰهَ لَهُوَ خَيْرُ الرَّازِق۪ينَ', 'Allah yolunda hicret edip sonra öldürülen yahut ölenleri hiç şüphesiz Allah güzel bir rızıkla rızıklandıracaktır. Şüphesiz Allah, evet O, rızık verenlerin en hayırlısıdır.', 17, 338, 78, 5196, 'Hac', 'Medine', 103),
(2654, 59, 22, 'Hac', 'لَيُدْخِلَنَّهُمْ مُدْخَلاً يَرْضَوْنَهُۜ وَاِنَّ اللّٰهَ لَعَل۪يمٌ حَل۪يمٌ', 'Allah onları, herhalde memnun kalacakları bir girilecek yere sokacaktır. Allah, kesinlikle tam bir bilgi sahibidir, halîmdir.', 17, 338, 78, 5196, 'Hac', 'Medine', 103),
(2655, 60, 22, 'Hac', 'ذٰلِكَۚ وَمَنْ عَاقَبَ بِمِثْلِ مَا عُوقِبَ بِه۪ ثُمَّ بُغِيَ عَلَيْهِ لَيَنْصُرَنَّهُ اللّٰهُۜ اِنَّ اللّٰهَ لَعَفُوٌّ غَفُورٌ', 'İşte böyle. Her kim, kendisine verilen eziyetin dengi ile karşılık verir de, bundan sonra kendisine yine bir tecavüz ve zulüm vaki olursa, emin olmalıdır ki, Allah ona mutlaka yardım edecektir. Hakikaten Allah çok bağışlayıcı ve mağfiret edicidir.', 17, 338, 78, 5196, 'Hac', 'Medine', 103),
(2656, 61, 22, 'Hac', 'ذٰلِكَ بِاَنَّ اللّٰهَ يُولِجُ الَّيْلَ فِي النَّهَارِ وَيُولِجُ النَّهَارَ فِي الَّيْلِ وَاَنَّ اللّٰهَ سَم۪يعٌ بَص۪يرٌ', 'Böylece (Allah, haksızlığa uğrayana yardım edecektir ve buna kadirdir). Çünkü Allah, geceyi gündüze katar, gündüzü geceye katar. Şu da muhakkak ki Allah, hakkıyla işiten ve görendir.', 17, 338, 78, 5196, 'Hac', 'Medine', 103),
(2657, 62, 22, 'Hac', 'ذٰلِكَ بِاَنَّ اللّٰهَ هُوَ الْحَقُّ وَاَنَّ مَا يَدْعُونَ مِنْ دُونِه۪ هُوَ الْبَاطِلُ وَاَنَّ اللّٰهَ هُوَ الْعَلِيُّ الْكَب۪يرُ', 'Böyledir. Çünkü Allah, hakkın ta kendisidir. O´nun dışındaki taptıkları ise bâtılın ta kendisidir. Gerçek şu ki Allah, evet O, uludur, büyüktür.', 17, 338, 78, 5196, 'Hac', 'Medine', 103),
(2658, 63, 22, 'Hac', 'اَلَمْ تَرَ اَنَّ اللّٰهَ اَنْزَلَ مِنَ السَّمَٓاءِ مَٓاءًۘ فَتُصْبِـحُ الْاَرْضُ مُخْضَرَّةًۜ اِنَّ اللّٰهَ لَط۪يفٌ خَب۪يرٌۚ', 'Görmedin mi, Allah, gökten yağmur indirdi de bu sayede yeryüzü yeşeriyor. Gerçekten Allah çok lütufkârdır, (her şeyden) haberdardır.', 17, 338, 78, 5196, 'Hac', 'Medine', 103),
(2659, 64, 22, 'Hac', 'لَهُ مَا فِي السَّمٰوَاتِ وَمَا فِي الْاَرْضِۜ وَاِنَّ اللّٰهَ لَهُوَ الْغَنِيُّ الْحَم۪يدُ۟', 'Göklerde ve yerde ne varsa O´nundur. Hakikaten Allah, yalnız O zengindir, övgüye değerdir.', 17, 338, 78, 5196, 'Hac', 'Medine', 103),
(2660, 65, 22, 'Hac', 'اَلَمْ تَرَ اَنَّ اللّٰهَ سَخَّرَ لَكُمْ مَا فِي الْاَرْضِ وَالْفُلْكَ تَجْر۪ي فِي الْبَحْرِ بِاَمْرِه۪ۜ وَيُمْسِكُ السَّمَٓاءَ اَنْ تَقَعَ عَلَى الْاَرْضِ اِلَّا بِاِذْنِه۪ۜ اِنَّ اللّٰهَ بِالنَّاسِ لَرَؤُ۫فٌ رَح۪يمٌ', 'Görmedin mi, Allah, yerdeki eşyayı ve emri uyarınca denizde yüzen gemileri sizin hizmetinize verdi. Göğü de, kendi izni olmadıkça yer üzerine düşmekten korur. Çünkü Allah, insanlara çok şefkatli ve çok merhametlidir.', 17, 339, 78, 5196, 'Hac', 'Medine', 103),
(2661, 66, 22, 'Hac', 'وَهُوَ الَّـذ۪ٓي اَحْيَاكُمْۘ ثُمَّ يُم۪يتُكُمْ ثُمَّ يُحْي۪يكُمْۜ اِنَّ الْاِنْسَانَ لَكَفُورٌ', 'O, (önce) size hayat veren, sonra sizi öldürecek, sonra yine diriltecek olandır. Gerçekten insan, çok nankördür.', 17, 339, 78, 5196, 'Hac', 'Medine', 103),
(2662, 67, 22, 'Hac', 'لِكُلِّ اُمَّةٍ جَعَلْنَا مَنْسَكاً هُمْ نَاسِكُوهُ فَلَا يُنَازِعُنَّكَ فِي الْاَمْرِ وَادْعُ اِلٰى رَبِّكَۜ اِنَّكَ لَعَلٰى هُدًى مُسْتَق۪يمٍ', 'Biz, her ümmete, uygulamakta oldukları bir ibadet tarzı gösterdik. Öyle ise onlar (ehl-i kitap) bu işte seninle çekişmesinler. Sen, Rabbine davet et. Zira sen, hakikaten dosdoğru bir yoldasın.', 17, 339, 78, 5196, 'Hac', 'Medine', 103),
(2663, 68, 22, 'Hac', 'وَاِنْ جَادَلُوكَ فَقُلِ اللّٰهُ اَعْلَمُ بِمَا تَعْمَلُونَ', 'Eğer seninle münakaşa ve mücâdeleye girişirlerse: «Allah yaptığınızı çok iyi bilmektedir» de.', 17, 339, 78, 5196, 'Hac', 'Medine', 103),
(2664, 69, 22, 'Hac', 'اَللّٰهُ يَحْكُمُ بَيْنَكُمْ يَوْمَ الْقِيٰمَةِ ف۪يمَا كُنْتُمْ ف۪يهِ تَخْتَلِفُونَ', 'Allah kıyamet gününde, ihtilâf etmekte olduğunuz konulara dair aranızda hüküm verecektir.', 17, 339, 78, 5196, 'Hac', 'Medine', 103),
(2665, 70, 22, 'Hac', 'اَلَمْ تَعْلَمْ اَنَّ اللّٰهَ يَعْلَمُ مَا فِي السَّمَٓاءِ وَالْاَرْضِۜ اِنَّ ذٰلِكَ ف۪ي كِتَابٍۜ اِنَّ ذٰلِكَ عَلَى اللّٰهِ يَس۪يرٌ', 'Bilmez misin ki, Allah, yerde ve gökte ne varsa bilir? Bu, bir kitapta (levh-i mahfuzda) mevcuttur. Bu (eşya ve olayların bilgisine sahip olmak), Allah için çok kolaydır.', 17, 339, 78, 5196, 'Hac', 'Medine', 103),
(2666, 71, 22, 'Hac', 'وَيَعْبُدُونَ مِنْ دُونِ اللّٰهِ مَا لَمْ يُنَزِّلْ بِه۪ سُلْطَاناً وَمَا لَيْسَ لَهُمْ بِه۪ عِلْمٌۜ وَمَا لِلظَّالِم۪ينَ مِنْ نَص۪يرٍ', 'Onlar, Allah´ı bırakıp, Allah´ın kendisine hiçbir delil indirmediği, kendilerinin dahi hakkında bilgi sahibi olmadıkları şeylere tapıyorlar. Zalimlerin hiç yardımcısı yoktur.', 17, 339, 78, 5196, 'Hac', 'Medine', 103),
(2667, 72, 22, 'Hac', 'وَاِذَا تُتْلٰى عَلَيْهِمْ اٰيَاتُنَا بَيِّنَاتٍ تَعْرِفُ ف۪ي وُجُوهِ الَّذ۪ينَ كَفَرُوا الْمُنْكَرَۜ يَكَادُونَ يَسْطُونَ بِالَّذ۪ينَ يَتْلُونَ عَلَيْهِمْ اٰيَاتِنَاۜ قُلْ اَفَاُنَبِّئُكُمْ بِشَرٍّ مِنْ ذٰلِكُمْۜ اَلنَّارُۜ وَعَدَهَا اللّٰهُ الَّذ۪ينَ كَفَرُواۜ وَبِئْسَ الْمَص۪يرُ۟', 'Âyetlerimiz açık açık kendilerine okunduğunda, kâfirlerin suratlarında hoşnutsuzluk sezersin. Onlar, kendilerine âyetlerimizi okuyanların neredeyse üzerlerine saldırırlar. De ki: Size bundan (bu öfke ve huzursuzluğunuzdan) daha kötüsünü bildireyim mi? Cehennem! Allah, onu kâfirlere (ceza olarak) bildirdi. O, ne kötü sondur!', 17, 339, 78, 5196, 'Hac', 'Medine', 103),
(2668, 73, 22, 'Hac', 'يَٓا اَيُّهَا النَّاسُ ضُرِبَ مَثَلٌ فَاسْتَمِعُوا لَهُۜ اِنَّ الَّذ۪ينَ تَدْعُونَ مِنْ دُونِ اللّٰهِ لَنْ يَخْلُقُوا ذُبَاباً وَلَوِ اجْتَمَعُوا لَهُۜ وَاِنْ يَسْلُبْهُمُ الذُّبَابُ شَيْـٔاً لَا يَسْتَنْقِذُوهُ مِنْهُۜ ضَعُفَ الطَّالِبُ وَالْمَطْلُوبُ', 'Ey insanlar! (Size) bir misal verildi; şimdi onu dinleyin: Allah´ı bırakıp da yalvardıklarınız (taptıklarınız) bunun için bir araya gelseler bile bir sineği dahi yaratamazlar. Sinek onlardan bir şey kapsa, bunu ondan geri de alamazlar. İsteyen de âciz, kendinden istenen de!', 17, 340, 78, 5196, 'Hac', 'Medine', 103),
(2669, 74, 22, 'Hac', 'مَا قَدَرُوا اللّٰهَ حَقَّ قَدْرِه۪ۜ اِنَّ اللّٰهَ لَقَوِيٌّ عَز۪يزٌ', 'Onlar, (Bu âciz putları Allah´a ortak koşmak suretiyle) Allah´ın kadrini hakkıyla bilemediler. Hiç şüphesiz Allah, çok kuvvetlidir, çok üstündür.', 17, 340, 78, 5196, 'Hac', 'Medine', 103),
(2670, 75, 22, 'Hac', 'اَللّٰهُ يَصْطَف۪ي مِنَ الْمَلٰٓئِكَةِ رُسُلاً وَمِنَ النَّاسِۜ اِنَّ اللّٰهَ سَم۪يعٌ بَص۪يرٌۚ', 'Allah meleklerden de elçiler seçer, insanlardan da. Şüphesiz Allah işitendir, görendir.', 17, 340, 78, 5196, 'Hac', 'Medine', 103),
(2671, 76, 22, 'Hac', 'يَعْلَمُ مَا بَيْنَ اَيْد۪يهِمْ وَمَا خَلْفَهُمْۜ وَاِلَى اللّٰهِ تُرْجَعُ الْاُمُورُ', 'Onların önlerindekini de, arkalarındakini de (yaptıklarını da, yapacaklarını da) bilir. Bütün işler Allah´a döndürülür.', 17, 340, 78, 5196, 'Hac', 'Medine', 103),
(2672, 77, 22, 'Hac', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا ارْكَعُوا وَاسْجُدُوا وَاعْبُدُوا رَبَّكُمْ وَافْعَلُوا الْخَيْرَ لَعَلَّكُمْ تُفْلِحُونَۚ', 'Ey iman edenler! Rükû edin; secdeye kapanın; Rabbinize ibadet edin; hayır işleyin ki kurtuluşa eresiniz.', 17, 340, 78, 5196, 'Hac', 'Medine', 103),
(2673, 78, 22, 'Hac', 'وَجَاهِدُوا فِي اللّٰهِ حَقَّ جِهَادِه۪ۜ هُوَ اجْتَبٰيكُمْ وَمَا جَعَلَ عَلَيْكُمْ فِي الدّ۪ينِ مِنْ حَرَجٍۜ مِلَّةَ اَب۪يكُمْ اِبْرٰه۪يمَۜ هُوَ سَمّٰيكُمُ الْمُسْلِم۪ينَ مِنْ قَبْلُ وَف۪ي هٰذَا لِيَكُونَ الرَّسُولُ شَه۪يداً عَلَيْكُمْ وَتَكُونُوا شُهَدَٓاءَ عَلَى النَّاسِۚ فَاَق۪يمُوا الصَّلٰوةَ وَاٰتُوا الزَّكٰوةَ وَاعْتَصِمُوا بِاللّٰهِۜ هُوَ مَوْلٰيكُمْۚ فَنِعْمَ الْمَوْلٰى وَنِعْمَ النَّص۪يرُ', 'Allah uğrunda, hakkını vererek cihad edin. O, sizi seçti; din hususunda üzerinize hiçbir zorluk yüklemedi; babanız İbrahim´in dininde (de böyleydi). Peygamberin size şahit olması, sizin de insanlara şahit olmanız için, O, gerek daha önce (gelmiş kitaplarda), gerekse bunda (Kur´an´da) size «müslümanlar» adını verdi. Öyle ise namazı kılın; zekâtı verin ve Allah´a sımsıkı sarılın. O, sizin mevlânızdır. Ne güzel mevlâdır, ne güzel yardımcıdır!', 17, 340, 78, 5196, 'Hac', 'Medine', 103),
(2674, 1, 23, 'Mü\'minun', 'قَدْ اَفْلَحَ الْمُؤْمِنُونَۙ', 'Gerçekten müminler kurtuluşa ermiştir;', 18, 341, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2675, 2, 23, 'Mü\'minun', 'اَلَّذ۪ينَ هُمْ ف۪ي صَلَاتِهِمْ خَاشِعُونَۙ', 'Onlar ki, namazlarında huşû içindedirler;', 18, 341, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2676, 3, 23, 'Mü\'minun', 'وَالَّذ۪ينَ هُمْ عَنِ اللَّغْوِ مُعْرِضُونَۙ', 'Onlar ki, boş ve yararsız şeylerden yüz çevirirler;', 18, 341, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2677, 4, 23, 'Mü\'minun', 'وَالَّذ۪ينَ هُمْ لِلزَّكٰوةِ فَاعِلُونَۙ', 'Onlar ki, zekâtı verirler;', 18, 341, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2678, 5, 23, 'Mü\'minun', 'وَالَّذ۪ينَ هُمْ لِفُرُوجِهِمْ حَافِظُونَۙ', 'Ve onlar ki, iffetlerini korurlar;', 18, 341, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2679, 6, 23, 'Mü\'minun', 'اِلَّا عَلٰٓى اَزْوَاجِهِمْ اَوْ مَا مَلَكَتْ اَيْمَانُهُمْ فَاِنَّهُمْ غَيْرُ مَلُوم۪ينَۚ', 'Ancak eşleri ve ellerinin sahip olduğu (câriyeleri) hariç. (Bunlarla ilişkilerden dolayı) kınanmış değillerdir.', 18, 341, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2680, 7, 23, 'Mü\'minun', 'فَمَنِ ابْتَغٰى وَرَٓاءَ ذٰلِكَ فَاُو۬لٰٓئِكَ هُمُ الْعَادُونَۚ', 'Şu halde, kim bunun ötesine gitmek isterse, işte bunlar, haddi aşan kimselerdir.', 18, 341, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2681, 8, 23, 'Mü\'minun', 'وَالَّذ۪ينَ هُمْ لِاَمَانَاتِهِمْ وَعَهْدِهِمْ رَاعُونَۙ', 'Yine onlar (o müminler) ki, emanetlerine ve ahidlerine riayet ederler;', 18, 341, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2682, 9, 23, 'Mü\'minun', 'وَالَّذ۪ينَ هُمْ عَلٰى صَلَوَاتِهِمْ يُحَافِظُونَۢ', 'Ve onlar ki, namazlarına devam ederler.', 18, 341, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2683, 10, 23, 'Mü\'minun', 'اُو۬لٰٓئِكَ هُمُ الْوَارِثُونَۙ', 'İşte, asıl bunlar vâris olacaklardır;', 18, 341, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2684, 11, 23, 'Mü\'minun', 'اَلَّذ۪ينَ يَرِثُونَ الْفِرْدَوْسَۜ هُمْ ف۪يهَا خَالِدُونَ', '(Evet) Firdevs´e vâris olan bu kimseler, orada ebedî kalıcıdırlar.', 18, 341, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2685, 12, 23, 'Mü\'minun', 'وَلَقَدْ خَلَقْنَا الْاِنْسَانَ مِنْ سُلَالَةٍ مِنْ ط۪ينٍۚ', 'Andolsun biz insanı, çamurdan (süzülüp çıkarılmış) bir özden yarattık.', 18, 341, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2686, 13, 23, 'Mü\'minun', 'ثُمَّ جَعَلْنَاهُ نُطْفَةً ف۪ي قَرَارٍ مَك۪ينٍۖ', 'Sonra onu sağlam bir karargâhta nutfe haline getirdik.', 18, 341, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2687, 14, 23, 'Mü\'minun', 'ثُمَّ خَلَقْنَا النُّطْفَةَ عَلَقَةً فَخَلَقْنَا الْعَلَقَةَ مُضْغَةً فَخَلَقْنَا الْمُضْغَةَ عِظَاماً فَكَسَوْنَا الْعِظَامَ لَحْماًۗ ثُمَّ اَنْشَأْنَاهُ خَلْقاً اٰخَرَۜ فَتَبَارَكَ اللّٰهُ اَحْسَنُ الْخَالِق۪ينَۜ', 'Sonra nutfeyi alaka (aşılanmış yumurta) yaptık. Peşinden, alakayı, bir parçacık et haline soktuk; bu bir parçacık eti kemiklere (iskelete) çevirdik; bu kemikleri etle kapladık. Sonra onu başka bir yaratışla insan haline getirdik. Yapıp-yaratanların en güzeli olan Allah pek yücedir.', 18, 341, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2688, 15, 23, 'Mü\'minun', 'ثُمَّ اِنَّكُمْ بَعْدَ ذٰلِكَ لَمَيِّتُونَۜ', 'Sonra, muhakkak ki siz, bunun ardından elbet öleceksiniz.', 18, 341, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2689, 16, 23, 'Mü\'minun', 'ثُمَّ اِنَّكُمْ يَوْمَ الْقِيٰمَةِ تُبْعَثُونَ', 'Sonra da şüphesiz, sizler kıyamet gününde tekrar diriltileceksiniz.', 18, 341, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2690, 17, 23, 'Mü\'minun', 'وَلَقَدْ خَلَقْنَا فَوْقَكُمْ سَبْعَ طَرَٓائِقَۗ وَمَا كُنَّا عَنِ الْخَلْقِ غَافِل۪ينَ', 'Andolsun biz, sizin üstünüzde yedi yol yarattık. Biz yaratmaktan habersiz değiliz.', 18, 341, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2691, 18, 23, 'Mü\'minun', 'وَاَنْزَلْنَا مِنَ السَّمَٓاءِ مَٓاءً بِقَدَرٍ فَاَسْكَنَّاهُ فِي الْاَرْضِۗ وَاِنَّا عَلٰى ذَهَابٍ بِه۪ لَقَادِرُونَۚ', 'Gökten uygun bir ölçüde yağmur indirip onu arzda durdurduk. Bizim onu gidermeye de elbet gücümüz yeter.', 18, 342, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2692, 19, 23, 'Mü\'minun', 'فَاَنْشَأْنَا لَكُمْ بِه۪ جَنَّاتٍ مِنْ نَخ۪يلٍ وَاَعْنَابٍۢ لَكُمْ ف۪يهَا فَوَاكِهُ۬ كَث۪يرَةٌ وَمِنْهَا تَأْكُلُونَۙ', 'Böylece onun (yağmurun) sayesinde sizin yararınıza hurma bahçeleri ve üzüm bağları meydana getirdik. Bunlarda sizin için birçok meyveler vardır ve siz onlardan yersiniz.', 18, 342, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2693, 20, 23, 'Mü\'minun', 'وَشَجَرَةً تَخْرُجُ مِنْ طُورِ سَيْنَٓاءَ تَنْبُتُ بِالدُّهْنِ وَصِبْغٍ لِلْاٰكِل۪ينَ', 'Tûr-i Sînâ´da da yetişen bir ağaç daha meydana getirdik ki, bu ağaç hem yağ hem de yiyenlerin ekmeğine katık edecekleri (zeytin) verir.', 18, 342, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2694, 21, 23, 'Mü\'minun', 'وَاِنَّ لَكُمْ فِي الْاَنْعَامِ لَعِبْرَةًۜ نُسْق۪يكُمْ مِمَّا ف۪ي بُطُونِهَا وَلَكُمْ ف۪يهَا مَنَافِعُ كَث۪يرَةٌ وَمِنْهَا تَأْكُلُونَۙ', 'Hayvanlarda sizin için elbette ibretler vardır. Onların karınlarındakinden (sütlerinden) size içiririz. Onlarda sizin için birçok faydalar daha vardır; etlerinden de yersiniz.', 18, 342, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2695, 22, 23, 'Mü\'minun', 'وَعَلَيْهَا وَعَلَى الْفُلْكِ تُحْمَلُونَ۟', 'Onların üzerinde ve gemilerde taşınırsınız.', 18, 342, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2696, 23, 23, 'Mü\'minun', 'وَلَقَدْ اَرْسَلْنَا نُوحاً اِلٰى قَوْمِه۪ فَقَالَ يَا قَوْمِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِنْ اِلٰهٍ غَيْرُهُۜ اَفَلَا تَتَّقُونَ', 'Andolsun ki, Nuh´u kavmine gönderdik ve o: Ey kavmim! Allah´a kulluk edin. Sizin için O´ndan başka bir tanrı yoktur. Hâla sakınmaz mısınız? dedi.', 18, 342, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2697, 24, 23, 'Mü\'minun', 'فَقَالَ الْمَلَؤُا الَّذ۪ينَ كَفَرُوا مِنْ قَوْمِه۪ مَا هٰذَٓا اِلَّا بَشَرٌ مِثْلُكُمْۙ يُر۪يدُ اَنْ يَتَفَضَّلَ عَلَيْكُمْۜ وَلَوْ شَٓاءَ اللّٰهُ لَاَنْزَلَ مَلٰٓئِكَةًۚ مَا سَمِعْنَا بِهٰذَا ف۪ٓي اٰبَٓائِنَا الْاَوَّل۪ينَۚ', 'Bunun üzerine, kavminin inkarcı ileri gelenleri şöyle dediler: «Bu, sadece sizin gibi bir beşerdir. Size üstün ve hâkim olmak istiyor. Eğer Allah (peygamber göndermek) isteseydi, muhakkak ki melekler gönderirdi. Biz geçmişteki atalarımızdan böyle bir şey duymadık.»', 18, 342, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2698, 25, 23, 'Mü\'minun', 'اِنْ هُوَ اِلَّا رَجُلٌ بِه۪ جِنَّةٌ فَتَرَبَّصُوا بِه۪ حَتّٰى ح۪ينٍ', '«Bu, yalnızca kendisinde delilik bulunan bir kimsedir. Öyle ise, bir süreye kadar ona katlanıp bekleyin bakalım.»', 18, 342, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2699, 26, 23, 'Mü\'minun', 'قَالَ رَبِّ انْصُرْن۪ي بِمَا كَذَّبُونِ', '(Nuh), Rabbim! dedi, beni yalanlamalarına karşı bana yardım et!', 18, 342, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2700, 27, 23, 'Mü\'minun', 'فَاَوْحَيْنَٓا اِلَيْهِ اَنِ اصْنَعِ الْفُلْكَ بِاَعْيُنِنَا وَوَحْيِنَا فَاِذَا جَٓاءَ اَمْرُنَا وَفَارَ التَّنُّورُۙ فَاسْلُكْ ف۪يهَا مِنْ كُلٍّ زَوْجَيْنِ اثْنَيْنِ وَاَهْلَكَ اِلَّا مَنْ سَبَقَ عَلَيْهِ الْقَوْلُ مِنْهُمْۚ وَلَا تُخَاطِبْن۪ي فِي الَّذ۪ينَ ظَلَمُواۚ اِنَّهُمْ مُغْرَقُونَ', 'Bunun üzerine ona şöyle vahyettik: Gözlerimizin önünde (muhafazamız altında) ve bildirdiğimiz şekilde gemiyi yap. Bizim emrimiz gelip de sular coşup yükselmeye başlayınca her cinsten birer çift ile, daha önce kendisi aleyhinde hüküm verilmiş olanların dışındaki aileni gemiye al. Zulmetmiş olanlar konusunda bana hiç yalvarma! Zira onlar kesinlikle boğulacaklardır.', 18, 342, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2701, 28, 23, 'Mü\'minun', 'فَاِذَا اسْتَوَيْتَ اَنْتَ وَمَنْ مَعَكَ عَلَى الْفُلْكِ فَقُلِ الْحَمْدُ لِلّٰهِ الَّذ۪ي نَجّٰينَا مِنَ الْقَوْمِ الظَّالِم۪ينَ', 'Sen, yanındakilerle birlikte gemiye yerleştiğinde: «Bizi zalimler topluluğundan kurtaran Allah´a hamdolsun» de.', 18, 343, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2702, 29, 23, 'Mü\'minun', 'وَقُلْ رَبِّ اَنْزِلْن۪ي مُنْزَلاً مُبَارَكاً وَاَنْتَ خَيْرُ الْمُنْزِل۪ينَ', 'Ve de ki: Rabbim! Beni bereketli bir yere indir. Sen, iskân edenlerin en hayırlısısın.', 18, 343, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2703, 30, 23, 'Mü\'minun', 'اِنَّ ف۪ي ذٰلِكَ لَاٰيَاتٍ وَاِنْ كُنَّا لَمُبْتَل۪ينَ', 'Şüphesiz bunda (Nuh ve kavminin başından geçenlerde) birtakım ibretler vardır. Hakikaten biz (kullarımızı böyle) deneriz.', 18, 343, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2704, 31, 23, 'Mü\'minun', 'ثُمَّ اَنْشَأْنَا مِنْ بَعْدِهِمْ قَرْناً اٰخَر۪ينَۚ', 'Sonra onların ardından bir başka nesil meydana getirdik.', 18, 343, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2705, 32, 23, 'Mü\'minun', 'فَاَرْسَلْنَا ف۪يهِمْ رَسُولاً مِنْهُمْ اَنِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِنْ اِلٰهٍ غَيْرُهُۜ اَفَلَا تَتَّقُونَ۟', 'Onlar arasından kendilerine: «Allah´a kulluk edin. Sizin O´ndan başka bir tanrınız yoktur. Hâla Allah´tan korkmaz mısınız?» (mesajını ileten) bir peygamber gönderdik.', 18, 343, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2706, 33, 23, 'Mü\'minun', 'وَقَالَ الْمَلَأُ مِنْ قَوْمِهِ الَّذ۪ينَ كَفَرُوا وَكَذَّبُوا بِلِقَٓاءِ الْاٰخِرَةِ وَاَتْرَفْنَاهُمْ فِي الْحَيٰوةِ الدُّنْيَاۙ مَا هٰذَٓا اِلَّا بَشَرٌ مِثْلُكُمْۙ يَأْكُلُ مِمَّا تَأْكُلُونَ مِنْهُ وَيَشْرَبُ مِمَّا تَشْرَبُونَ', 'Onun kavminden, kâfir olup ahirete ulaşmayı inkâr eden ve dünya hayatında kendilerine refah verdiğimiz varlıklı kişiler: «Bu, dediler, sadece sizin gibi bir insandır; sizin yediğinizden yer, sizin içtiğinizden içer.»', 18, 343, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2707, 34, 23, 'Mü\'minun', 'وَلَئِنْ اَطَعْتُمْ بَشَراً مِثْلَكُمْ اِنَّكُمْ اِذاً لَخَاسِرُونَ', '«Gerçekten, sizin gibi bir beşere itaat ederseniz, herhalde ziyan edersiniz.»', 18, 343, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2708, 35, 23, 'Mü\'minun', 'اَيَعِدُكُمْ اَنَّكُمْ اِذَا مِتُّمْ وَكُنْتُمْ تُرَاباً وَعِظَاماً اَنَّكُمْ مُخْرَجُونَۖ', '«Size, öldüğünüz, toprak ve kemik yığını haline geldiğinizde, mutlak surette sizin (kabirden) çıkarılacağınızı mı vâdediyor?»', 18, 343, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2709, 36, 23, 'Mü\'minun', 'هَيْهَاتَ هَيْهَاتَ لِمَا تُوعَدُونَۖ', '«Bu size vaâdedilen (öldükten sonra yeniden dirilmek, gerçek olmaktan) çok uzak!»', 18, 343, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2710, 37, 23, 'Mü\'minun', 'اِنْ هِيَ اِلَّا حَيَاتُنَا الدُّنْيَا نَمُوتُ وَنَحْيَا وَمَا نَحْنُ بِمَبْعُوث۪ينَۖ', '«Hayat, şu dünya hayatımızdan ibarettir. (Kimimiz) ölürüz, (kimimiz) yaşarız; bir daha diriltilecek de değiliz.»', 18, 343, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2711, 38, 23, 'Mü\'minun', 'اِنْ هُوَ اِلَّا رَجُلٌۨ افْـتَرٰى عَلَى اللّٰهِ كَذِباً وَمَا نَحْنُ لَهُ بِمُؤْمِن۪ينَ', '«O, Allah hakkında yalnızca yalan uyduran bir adamdır; biz ona inanmıyoruz.»', 18, 343, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2712, 39, 23, 'Mü\'minun', 'قَالَ رَبِّ انْصُرْن۪ي بِمَا كَذَّبُونِ', 'O peygamber: Rabbim! dedi, beni yalanlamalarına karşılık bana yardımcı ol!', 18, 343, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2713, 40, 23, 'Mü\'minun', 'قَالَ عَمَّا قَل۪يلٍ لَيُصْبِحُنَّ نَادِم۪ينَۚ', 'Allah şöyle buyurdu: Pek yakında onlar mutlaka pişman olacaklar!', 18, 343, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2714, 41, 23, 'Mü\'minun', 'فَاَخَذَتْهُمُ الصَّيْحَةُ بِالْحَقِّ فَجَعَلْنَاهُمْ غُـثَٓاءًۚ فَبُعْداً لِلْقَوْمِ الظَّالِم۪ينَ', 'Nitekim, vukuu kaçınılmaz olan korkunç bir ses yakalayıverdi onları! Kendilerini hemen sel süprüntüsüne çevirdik. Zalimler topluluğunun canı cehenneme!', 18, 343, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2715, 42, 23, 'Mü\'minun', 'ثُمَّ اَنْشَأْنَا مِنْ بَعْدِهِمْ قُرُوناً اٰخَر۪ينَۜ', 'Sonra onların ardından başka nesiller getirdik.', 18, 343, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2716, 43, 23, 'Mü\'minun', 'مَا تَسْبِقُ مِنْ اُمَّةٍ اَجَلَهَا وَمَا يَسْتَأْخِرُونَۜ', 'Hiçbir ümmet, ecelini ne öne alabilir, ne de erteleyebilir.', 18, 344, 118, 4354, 'Mü\'minler', 'Mekke', 74);
INSERT INTO `Ayetler` (`id`, `AyetNo`, `SureNo`, `SureAdi`, `Metin`, `Mana`, `Cuzu`, `Sayfasi`, `AyetSayisi`, `HarfSayisi`, `AdAnlami`, `IndigiYer`, `InisSirasi`) VALUES
(2717, 44, 23, 'Mü\'minun', 'ثُمَّ اَرْسَلْنَا رُسُلَنَا تَتْرَاۜ كُلَّمَا جَٓاءَ اُمَّةً رَسُولُهَا كَذَّبُوهُ فَاَتْبَعْنَا بَعْضَهُمْ بَعْضاً وَجَعَلْنَاهُمْ اَحَاد۪يثَۚ فَبُعْداً لِقَوْمٍ لَا يُؤْمِنُونَ', 'Sonra biz peyderpey peygamberlerimizi gönderdik. Herhangi bir ümmete peygamberlerinin geldiği her defasında, onlar bu peygamberi yalanladılar; biz de onları birbiri ardından yok ettik ve onları ibret hikâyelerine dönüştürdük. Artık iman etmeyen kavmin canı cehenneme!', 18, 344, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2718, 45, 23, 'Mü\'minun', 'ثُمَّ اَرْسَلْنَا مُوسٰى وَاَخَاهُ هٰرُونَ بِاٰيَاتِنَا وَسُلْطَانٍ مُب۪ينٍۙ', '(45-46) Sonra âyetlerimizle ve apaçık bir fermanla Musa ve kardeşi Harun´u Firavun´a ve ileri gelenlerine gönderdik. Onlar ise kibire kapıldılar ve ululuk taslayan bir kavim oldular.', 18, 344, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2719, 46, 23, 'Mü\'minun', 'اِلٰى فِرْعَوْنَ وَمَلَا۬ئِه۪ فَاسْتَكْـبَرُوا وَكَانُوا قَوْماً عَال۪ينَۚ', '(45-46) Sonra âyetlerimizle ve apaçık bir fermanla Musa ve kardeşi Harun´u Firavun´a ve ileri gelenlerine gönderdik. Onlar ise kibire kapıldılar ve ululuk taslayan bir kavim oldular.', 18, 344, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2720, 47, 23, 'Mü\'minun', 'فَقَالُٓوا اَنُؤْمِنُ لِبَشَرَيْنِ مِثْلِنَا وَقَوْمُهُمَا لَنَا عَابِدُونَۚ', 'Bu yüzden dediler ki: Kavimleri bize kölelik ederken, bizim gibi olan bu iki adama inanır mıyız?', 18, 344, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2721, 48, 23, 'Mü\'minun', 'فَكَذَّبُوهُمَا فَكَانُوا مِنَ الْمُهْلَك۪ينَ', 'Böylece onları yalanladılar ve bu sebeple helâk edilenlerden oldular.', 18, 344, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2722, 49, 23, 'Mü\'minun', 'وَلَقَدْ اٰتَيْنَا مُوسَى الْكِتَابَ لَعَلَّهُمْ يَهْتَدُونَ', 'Andolsun biz Musa´ya, belki onlar yola gelirler diye, Kitab´ı verdik.', 18, 344, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2723, 50, 23, 'Mü\'minun', 'وَجَعَلْنَا ابْنَ مَرْيَمَ وَاُمَّهُٓ اٰيَةً وَاٰوَيْنَاهُمَٓا اِلٰى رَبْوَةٍ ذَاتِ قَرَارٍ وَمَع۪ينٍ۟', 'Meryem oğlunu ve annesini de (kudretimize) bir alâmet kıldık; onları, yerleşmeye elverişli, suyu bulunan bir tepeye yerleştirdik.', 18, 344, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2724, 51, 23, 'Mü\'minun', 'يَٓا اَيُّهَا الرُّسُلُ كُلُوا مِنَ الطَّيِّبَاتِ وَاعْمَلُوا صَالِحاًۜ اِنّ۪ي بِمَا تَعْمَلُونَ عَل۪يمٌۜ', '«Ey Peygamberler! Temiz olan şeylerden yeyin; güzel işler yapın. Ben sizin yaptıklarınızı hakkıyle bilmekteyim.»', 18, 344, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2725, 52, 23, 'Mü\'minun', 'وَاِنَّ هٰذِه۪ٓ اُمَّتُكُمْ اُمَّةً وَاحِدَةً وَاَنَا۬ رَبُّكُمْ فَاتَّقُونِ', '«Şüphesiz bu (insanlar) bir tek ümmet olarak sizin ümmetinizdir; ben de sizin Rabbinizim. Öyle ise benden sakının» (denildi).', 18, 344, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2726, 53, 23, 'Mü\'minun', 'فَـتَقَطَّـعُٓوا اَمْرَهُمْ بَيْنَهُمْ زُبُراًۜ كُلُّ حِزْبٍ بِمَا لَدَيْهِمْ فَرِحُونَ', 'Ne var ki insanlar kendi aralarındaki işlerini parça parça böldüler. Her gurup kendilerinde bulunan (fikir ve davranış) ile sevinip böbürlenmektedirler.', 18, 344, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2727, 54, 23, 'Mü\'minun', 'فَذَرْهُمْ ف۪ي غَمْرَتِهِمْ حَتّٰى ح۪ينٍ', 'Şimdi sen onları bir zamana kadar gaflet ve sapıklıkları ile başbaşa bırak!', 18, 344, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2728, 55, 23, 'Mü\'minun', 'اَيَحْسَبُونَ اَنَّمَا نُمِدُّهُمْ بِه۪ مِنْ مَالٍ وَبَن۪ينَۙ', '(55-56) Sanıyorlar mı ki, onlara verdiğimiz servet ve oğullar ile kendilerine faydalar sağlamak için can atıyoruz? Hayır, onlar işin farkına varamıyorlar.', 18, 344, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2729, 56, 23, 'Mü\'minun', 'نُسَارِعُ لَهُمْ فِي الْخَيْرَاتِۜ بَلْ لَا يَشْعُرُونَ', '(55-56) Sanıyorlar mı ki, onlara verdiğimiz servet ve oğullar ile kendilerine faydalar sağlamak için can atıyoruz? Hayır, onlar işin farkına varamıyorlar.', 18, 344, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2730, 57, 23, 'Mü\'minun', 'اِنَّ الَّذ۪ينَ هُمْ مِنْ خَشْيَةِ رَبِّهِمْ مُشْفِقُونَۙ', 'Rablerine olan saygıdan dolayı kötülükten sakınanlar;', 18, 344, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2731, 58, 23, 'Mü\'minun', 'وَالَّذ۪ينَ هُمْ بِاٰيَاتِ رَبِّهِمْ يُؤْمِنُونَۙ', 'Rablerinin âyetlerine inananlar;', 18, 344, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2732, 59, 23, 'Mü\'minun', 'وَالَّذ۪ينَ هُمْ بِرَبِّهِمْ لَا يُشْرِكُونَۙ', 'Rablerine ortak tanımayanlar;', 18, 344, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2733, 60, 23, 'Mü\'minun', 'وَالَّذ۪ينَ يُؤْتُونَ مَٓا اٰتَوْا وَقُلُوبُهُمْ وَجِلَةٌ اَنَّهُمْ اِلٰى رَبِّهِمْ رَاجِعُونَۙ', 'Ve Rablerine dönecekleri için yapmakta oldukları işleri kalpleri çarparak yapanlar;', 18, 345, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2734, 61, 23, 'Mü\'minun', 'اُو۬لٰٓئِكَ يُسَارِعُونَ فِي الْخَيْرَاتِ وَهُمْ لَهَا سَابِقُونَ', 'İşte onlar, iyiliklere koşuşurlar ve iyilik için yarışırlar.', 18, 345, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2735, 62, 23, 'Mü\'minun', 'وَلَا نُكَلِّفُ نَفْساً اِلَّا وُسْعَهَا وَلَدَيْنَا كِتَابٌ يَنْطِقُ بِالْحَقِّ وَهُمْ لَا يُظْلَمُونَ', 'Biz hiç kimseyi gücünün yettiğinden başkası ile yükümlü kılmayız. Nezdimizde hakkı söyleyen bir kitap vardır ve onlar haksızlığa uğratılmazlar.', 18, 345, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2736, 63, 23, 'Mü\'minun', 'بَلْ قُلُوبُهُمْ ف۪ي غَمْرَةٍ مِنْ هٰذَا وَلَهُمْ اَعْمَالٌ مِنْ دُونِ ذٰلِكَ هُمْ لَهَا عَامِلُونَ', 'Hayır, onların (o inkârcıların) kalpleri bu hususta cehâlet içindedir. Ayrıca onların bundan (bu şirk ve inkârcılıklarından) öte birtakım (kötü) işleri vardır ki, onlar bu işleri yapar dururlar.', 18, 345, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2737, 64, 23, 'Mü\'minun', 'حَتّٰٓى اِذَٓا اَخَذْنَا مُتْرَف۪يهِمْ بِالْعَذَابِ اِذَا هُمْ يَجْـَٔرُونَۜ', 'En nihayet, refah ve bolluk içinde olanlarını sıkıntıya (veya azaba) uğrattığımızda, bakarsın ki onlar feryadı basarlar.', 18, 345, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2738, 65, 23, 'Mü\'minun', 'لَا تَجْـَٔرُوا الْيَوْمَ اِنَّكُمْ مِنَّا لَا تُنْصَرُونَ', 'Boşuna sızlanmayın bugün! Zira bizden yardım göremeyeceksiniz!', 18, 345, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2739, 66, 23, 'Mü\'minun', 'قَدْ كَانَتْ اٰيَات۪ي تُتْلٰى عَلَيْكُمْ فَكُنْتُمْ عَلٰٓى اَعْقَابِكُمْ تَنْكِصُونَۙ', '(66-67) Çünkü âyetlerim size okunurdu da, siz, buna karşı kibirlenerek arkanızı döner, geceleyin (Kâbe´nin etrafında toplanarak) hezeyanlar savururdunuz.', 18, 345, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2740, 67, 23, 'Mü\'minun', 'مُسْتَكْبِر۪ينَ بِه۪ۗ سَامِراً تَهْجُرُونَ', '(66-67) Çünkü âyetlerim size okunurdu da, siz, buna karşı kibirlenerek arkanızı döner, geceleyin (Kâbe´nin etrafında toplanarak) hezeyanlar savururdunuz.', 18, 345, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2741, 68, 23, 'Mü\'minun', 'اَفَلَمْ يَدَّبَّرُوا الْقَوْلَ اَمْ جَٓاءَهُمْ مَا لَمْ يَأْتِ اٰبَٓاءَهُمُ الْاَوَّل۪ينَۘ', 'Onlar bu sözü (Kur´an´ı) hiç düşünmediler mi? Yoksa kendilerine, daha önce geçmişteki atalarına gelmeyen bir şey mi geldi?', 18, 345, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2742, 69, 23, 'Mü\'minun', 'اَمْ لَمْ يَعْرِفُوا رَسُولَهُمْ فَهُمْ لَهُ مُنْكِرُونَۘ', 'Yoksa Peygamberlerini henüz tanımadılar da bu yüzden mi onu inkâr ediyorlar?', 18, 345, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2743, 70, 23, 'Mü\'minun', 'اَمْ يَقُولُونَ بِه۪ جِنَّةٌۜ بَلْ جَٓاءَهُمْ بِالْحَقِّ وَاَكْثَرُهُمْ لِلْحَقِّ كَارِهُونَ', 'Yoksa onda bir cinnet olduğunu mu söylüyorlar? Hayır; o, kendilerine hakkı getirmiştir. Onların çoğu ise haktan hoşlanmamaktadırlar.', 18, 345, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2744, 71, 23, 'Mü\'minun', 'وَلَوِ اتَّبَعَ الْحَقُّ اَهْوَٓاءَهُمْ لَفَسَدَتِ السَّمٰوَاتُ وَالْاَرْضُ وَمَنْ ف۪يهِنَّۜ بَلْ اَتَيْنَاهُمْ بِذِكْرِهِمْ فَهُمْ عَنْ ذِكْرِهِمْ مُعْرِضُونَۜ', 'Eğer hak, onların kötü arzu ve isteklerine uysaydı, mutlaka gökler ve yer ile bunlarda bulunanlar bozulur giderdi. Hayır, biz onlara şan ve şereflerini getirdik; fakat onlar kendi şereflerine sırt çevirdiler.', 18, 345, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2745, 72, 23, 'Mü\'minun', 'اَمْ تَسْـَٔلُهُمْ خَرْجاً فَخَرَاجُ رَبِّكَ خَيْرٌۗ وَهُوَ خَيْرُ الرَّازِق۪ينَ', '(Resûlüm!) Yoksa sen onlardan bir karşılık mı istiyorsun? Rabbinin karşılığı daha hayırlıdır. O, rızık verenlerin en hayırlısıdır.', 18, 345, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2746, 73, 23, 'Mü\'minun', 'وَاِنَّكَ لَتَدْعُوهُمْ اِلٰى صِرَاطٍ مُسْتَق۪يمٍ', 'Gerçek şu ki sen onları doğru bir yola çağırıyorsun.', 18, 345, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2747, 74, 23, 'Mü\'minun', 'وَاِنَّ الَّذ۪ينَ لَا يُؤْمِنُونَ بِالْاٰخِرَةِ عَنِ الصِّرَاطِ لَنَاكِبُونَ', 'Ahirete inanmayanlar ise, ısrarla yoldan çıkmaktadırlar.', 18, 345, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2748, 75, 23, 'Mü\'minun', 'وَلَوْ رَحِمْنَاهُمْ وَكَشَفْنَا مَا بِهِمْ مِنْ ضُرٍّ لَلَجُّوا ف۪ي طُغْيَانِهِمْ يَعْمَهُونَ', 'Eğer onlara acıyıp da içinde bulundukları sıkıntıyı giderseydik, iyice körleşerek azgınlıklarında direnirlerdi.', 18, 346, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2749, 76, 23, 'Mü\'minun', 'وَلَقَدْ اَخَذْنَاهُمْ بِالْعَذَابِ فَمَا اسْتَكَانُوا لِرَبِّهِمْ وَمَا يَتَضَرَّعُونَ', 'Andolsun, biz onları sıkıntıya düşürdük de yine Rablerine boyun eğmediler, tazarru ve niyazda da bulunmuyorlar.', 18, 346, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2750, 77, 23, 'Mü\'minun', 'حَتّٰٓى اِذَا فَتَحْنَا عَلَيْهِمْ بَاباً ذَا عَذَابٍ شَد۪يدٍ اِذَا هُمْ ف۪يهِ مُبْلِسُونَ۟', 'En nihayet üzerlerine, azabı çok şiddetli bir kapı açtığımız zaman, bir de bakarsın ki onlar orada şaşkın ve ümitsiz kalmışlardır!', 18, 346, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2751, 78, 23, 'Mü\'minun', 'وَهُوَ الَّـذ۪ٓي اَنْشَاَ لَكُمُ السَّمْعَ وَالْاَبْصَارَ وَالْاَفْـِٔدَةَۜ قَل۪يلاً مَا تَشْكُرُونَ', 'O, sizin için kulakları, gözleri ve gönülleri yaratandır. Ne de az şükrediyorsunuz!', 18, 346, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2752, 79, 23, 'Mü\'minun', 'وَهُوَ الَّذ۪ي ذَرَاَكُمْ فِي الْاَرْضِ وَاِلَيْهِ تُحْشَرُونَ', 'Ve O, sizi yeryüzünde yaratıp türetendir. Sırf O´nun huzurunda toplanacaksınız.', 18, 346, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2753, 80, 23, 'Mü\'minun', 'وَهُوَ الَّذ۪ي يُحْـي۪ وَيُم۪يتُ وَلَهُ اخْتِلَافُ الَّيْلِ وَالنَّهَارِۜ اَفَلَا تَعْقِلُونَ', 'Ve O, yaşatan ve öldürendir; gecenin ve gündüzün değişmesi O´nun eseridir. Hâla aklınızı kullanmaz mısınız!', 18, 346, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2754, 81, 23, 'Mü\'minun', 'بَلْ قَالُوا مِثْلَ مَا قَالَ الْاَوَّلُونَ', 'Buna rağmen onlar, öncekilerin dedikleri gibi dediler.', 18, 346, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2755, 82, 23, 'Mü\'minun', 'قَالُٓوا ءَاِذَا مِتْنَا وَكُنَّا تُرَاباً وَعِظَاماً ءَاِنَّا لَمَبْعُوثُونَ', 'Dediler ki: Sahi biz, ölüp de bir toprak ve kemik yığını haline gelmişken, mutlaka yeniden diriltileceğiz öyle mi?', 18, 346, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2756, 83, 23, 'Mü\'minun', 'لَقَدْ وُعِدْنَا نَحْنُ وَاٰبَٓاؤُ۬نَا هٰذَا مِنْ قَبْلُ اِنْ هٰذَٓا اِلَّٓا اَسَاط۪يرُ الْاَوَّل۪ينَ', 'Hakikaten, gerek bize, gerekse daha önce atalarımıza böyle bir vaadde bulunuldu; (fakat) bu geçmiştekilerin masallarından başka bir şey değildir!', 18, 346, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2757, 84, 23, 'Mü\'minun', 'قُلْ لِمَنِ الْاَرْضُ وَمَنْ ف۪يهَٓا اِنْ كُنْتُمْ تَعْلَمُونَ', '(Resûlüm!) de ki: Eğer biliyorsanız (söyleyin bakalım), bu dünya ve onda bulunanlar kime aittir?', 18, 346, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2758, 85, 23, 'Mü\'minun', 'سَيَقُولُونَ لِلّٰهِۜ قُلْ اَفَلَا تَذَكَّرُونَ', '«Allah´a aittir» diyecekler. Öyle ise siz hiç düşünüp taşınmaz mısınız! de.', 18, 346, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2759, 86, 23, 'Mü\'minun', 'قُلْ مَنْ رَبُّ السَّمٰوَاتِ السَّبْعِ وَرَبُّ الْعَرْشِ الْعَظ۪يمِ', 'Yedi kat göklerin Rabbi, azametli Arş´ın Rabbi kimdir? diye sor.', 18, 346, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2760, 87, 23, 'Mü\'minun', 'سَيَقُولُونَ لِلّٰهِۜ قُلْ اَفَلَا تَتَّقُونَ', '«(Bunlar da) Allah´ındır» diyecekler. Şu halde siz Allah´tan korkmaz mısınız! de.', 18, 346, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2761, 88, 23, 'Mü\'minun', 'قُلْ مَنْ بِيَدِه۪ مَلَكُوتُ كُلِّ شَيْءٍ وَهُوَ يُج۪يرُ وَلَا يُجَارُ عَلَيْهِ اِنْ كُنْتُمْ تَعْلَمُونَ', 'Eğer biliyorsanız (söyleyin), her şeyin melekûtu (mülkiyeti ve yönetimi) kendisinin elinde olan, kendisi her şeyi koruyup kollayan, fakat kendisi korunmayan (buna muhtaç olmayan) kimdir? diye sor.', 18, 346, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2762, 89, 23, 'Mü\'minun', 'سَيَقُولُونَ لِلّٰهِۜ قُلْ فَاَنّٰى تُسْحَرُونَ', '«(Bunların hepsi) Allah´ındır» diyecekler. Öyle ise nasıl olup da büyüye kapılıyorsunuz? de.', 18, 346, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2763, 90, 23, 'Mü\'minun', 'بَلْ اَتَيْنَاهُمْ بِالْحَقِّ وَاِنَّهُمْ لَكَاذِبُونَ', 'Doğrusu biz onlara gerçeği getirdik; onlar ise hakikaten yalancılardır.', 18, 347, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2764, 91, 23, 'Mü\'minun', 'مَا اتَّخَذَ اللّٰهُ مِنْ وَلَدٍ وَمَا كَانَ مَعَهُ مِنْ اِلٰهٍ اِذاً لَذَهَبَ كُلُّ اِلٰهٍ بِمَا خَلَقَ وَلَعَلَا بَعْضُهُمْ عَلٰى بَعْضٍۜ سُبْحَانَ اللّٰهِ عَمَّا يَصِفُونَۙ', 'Allah evlât edinmemiştir; O´nunla beraber hiçbir tanrı da yoktur. Aksi takdirde her tanrı kendi yarattığını sevk ve idare eder ve mutlaka onlardan biri diğerine galebe çalardı. Allah, onların (müşriklerin) yakıştırdıkları şeylerden münezzehtir.', 18, 347, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2765, 92, 23, 'Mü\'minun', 'عَالِمِ الْغَيْبِ وَالشَّهَادَةِ فَتَعَالٰى عَمَّا يُشْرِكُونَ۟', 'Allah, gaybı da şehâdeti de bilendir. O, müşriklerin ortak koştukları şeylerden çok yüce ve münezzehtir.', 18, 347, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2766, 93, 23, 'Mü\'minun', 'قُلْ رَبِّ اِمَّا تُرِيَنّ۪ي مَا يُوعَدُونَۙ', '(93-94) (Resûlüm!) De ki: «Rabbim! Eğer onlara yöneltilen tehdidi (dünyevî sıkıntıyı ve uhrevî azabı) mutlaka bana göstereceksen; bu durumda beni zalimler topluluğunun içinde bulundurma Rabbim!»', 18, 347, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2767, 94, 23, 'Mü\'minun', 'رَبِّ فَلَا تَجْعَلْن۪ي فِي الْقَوْمِ الظَّالِم۪ينَ', '(93-94) (Resûlüm!) De ki: «Rabbim! Eğer onlara yöneltilen tehdidi (dünyevî sıkıntıyı ve uhrevî azabı) mutlaka bana göstereceksen; bu durumda beni zalimler topluluğunun içinde bulundurma Rabbim!»', 18, 347, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2768, 95, 23, 'Mü\'minun', 'وَاِنَّا عَلٰٓى اَنْ نُرِيَكَ مَا نَعِدُهُمْ لَقَادِرُونَ', 'Biz, onlara yönelttiğimiz tehdidi sana göstermeye elbette ki kadiriz.', 18, 347, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2769, 96, 23, 'Mü\'minun', 'اِدْفَعْ بِالَّت۪ي هِيَ اَحْسَنُ السَّيِّئَةَۜ نَحْنُ اَعْلَمُ بِمَا يَصِفُونَ', 'Sen, kötülüğü en güzel bir tutumla sav. Biz onların yakıştırmakta oldukları şeyi çok iyi bilmekteyiz.', 18, 347, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2770, 97, 23, 'Mü\'minun', 'وَقُلْ رَبِّ اَعُوذُ بِكَ مِنْ هَمَزَاتِ الشَّيَاط۪ينِۙ', 'Ve de ki: Rabbim! Şeytanların kışkırtmalarından sana sığınırım!', 18, 347, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2771, 98, 23, 'Mü\'minun', 'وَاَعُوذُ بِكَ رَبِّ اَنْ يَحْضُرُونِ', 'Onların yanımda bulunmalarından da sana sığınırım, Rabbim!', 18, 347, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2772, 99, 23, 'Mü\'minun', 'حَتّٰٓى اِذَا جَٓاءَ اَحَدَهُمُ الْمَوْتُ قَالَ رَبِّ ارْجِعُونِۙ', 'Nihayet onlardan (müşriklerden) birine ölüm gelip çattığında: «Rabbim! der, beni geri gönder;»', 18, 347, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2773, 100, 23, 'Mü\'minun', 'لَعَلّ۪ٓي اَعْمَلُ صَالِحاً ف۪يمَا تَرَكْتُ كَلَّاۜ اِنَّهَا كَلِمَةٌ هُوَ قَٓائِلُهَاۜ وَمِنْ وَرَٓائِهِمْ بَرْزَخٌ اِلٰى يَوْمِ يُبْعَثُونَ', '«Ta ki boşa geçirdiğim dünyada iyi iş (ve hareketler) yapayım.» Hayır! Bu onun ağzından çıkan (boş) bir laftan ibarettir. Onların gerisinde ise, yeniden dirilecekleri güne kadar (süren) bir berzah vardır.', 18, 347, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2774, 101, 23, 'Mü\'minun', 'فَاِذَا نُفِـخَ فِي الصُّورِ فَلَٓا اَنْسَابَ بَيْنَهُمْ يَوْمَئِذٍ وَلَا يَتَسَٓاءَلُونَ', 'Sûra üflendiği zaman artık aralarında akrabalık bağları kalmamıştır; birbirlerini de arayıp sormazlar.', 18, 347, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2775, 102, 23, 'Mü\'minun', 'فَمَنْ ثَقُلَتْ مَوَاز۪ينُهُ فَاُو۬لٰٓئِكَ هُمُ الْمُفْلِحُونَ', 'Artık kimlerin (sevap) tartıları ağır basarsa, işte asıl bunlar kurtuluşa erenlerdir.', 18, 347, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2776, 103, 23, 'Mü\'minun', 'وَمَنْ خَفَّتْ مَوَاز۪ينُهُ فَاُو۬لٰٓئِكَ الَّذ۪ينَ خَسِرُٓوا اَنْفُسَهُمْ ف۪ي جَهَنَّمَ خَالِدُونَۚ', 'Kimlerin de tartıları hafif gelirse, artık bunlar da kendilerine yazık etmişlerdir; (çünkü onlar) ebedî cehennemdedirler.', 18, 347, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2777, 104, 23, 'Mü\'minun', 'تَلْفَحُ وُجُوهَهُمُ النَّارُ وَهُمْ ف۪يهَا كَالِحُونَ', 'Ateş yüzlerini yakar; orada suratları çirkin ve gülünç bir halde bulunurlar.', 18, 347, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2778, 105, 23, 'Mü\'minun', 'اَلَمْ تَكُنْ اٰيَات۪ي تُتْلٰى عَلَيْكُمْ فَكُنْتُمْ بِهَا تُكَذِّبُونَ', 'Size âyetlerim okunurdu da, siz onları yalanlardınız değil mi?', 18, 348, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2779, 106, 23, 'Mü\'minun', 'قَالُوا رَبَّـنَا غَلَبَتْ عَلَيْنَا شِقْوَتُنَا وَكُنَّا قَوْماً ضَٓالّ۪ينَ', 'Derler ki: Rabbimiz! Azgınlığımız bizi altetti; biz, bir sapıklar topluluğu idik.', 18, 348, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2780, 107, 23, 'Mü\'minun', 'رَبَّـنَٓا اَخْرِجْنَا مِنْهَا فَاِنْ عُدْنَا فَاِنَّا ظَالِمُونَ', 'Rabbimiz! Bizi buradan çıkar. Eğer bir daha (ettiklerimize) dönersek, artık belli ki biz zalim insanlarız.', 18, 348, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2781, 108, 23, 'Mü\'minun', 'قَالَ اخْسَؤُ۫ا ف۪يهَا وَلَا تُكَلِّمُونِ', 'Buyurur ki: Alçaldıkça alçalın orada! Bana karşı konuşmayın artık!', 18, 348, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2782, 109, 23, 'Mü\'minun', 'اِنَّهُ كَانَ فَر۪يقٌ مِنْ عِبَاد۪ي يَقُولُونَ رَبَّـنَٓا اٰمَنَّا فَاغْفِرْ لَنَا وَارْحَمْنَا وَاَنْتَ خَيْرُ الرَّاحِم۪ينَۚ', 'Zira kullarımdan bir zümre: Rabbimiz! Biz iman ettik; öyle ise bizi affet; bize acı! Sen, merhametlilerin en iyisisin, demişlerdi.', 18, 348, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2783, 110, 23, 'Mü\'minun', 'فَاتَّخَذْتُمُوهُمْ سِخْرِياًّ حَتّٰٓى اَنْسَوْكُمْ ذِكْر۪ي وَكُنْتُمْ مِنْهُمْ تَضْحَكُونَ', 'İşte siz onları alaya aldınız; sonunda onlar (ile alay etmeniz) size beni yâdetmeyi unutturdu, siz onlara gülüyordunuz.', 18, 348, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2784, 111, 23, 'Mü\'minun', 'اِنّ۪ي جَزَيْتُهُمُ الْيَوْمَ بِمَا صَبَرُٓواۙ اَنَّهُمْ هُمُ الْفَٓائِزُونَ', 'Bugün ben onlara, sabrettiklerinin karşılığını verdim; onlar, hakikaten muratlarına erenlerdir.', 18, 348, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2785, 112, 23, 'Mü\'minun', 'قَالَ كَمْ لَبِثْتُمْ فِي الْاَرْضِ عَدَدَ سِن۪ينَ', '(Allah inkârcılara) «Yeryüzünde kaç yıl kaldınız?» diye sorar.', 18, 348, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2786, 113, 23, 'Mü\'minun', 'قَالُوا لَبِثْنَا يَوْماً اَوْ بَعْضَ يَوْمٍ فَسْـَٔلِ الْعَٓادّ۪ينَ', '«Bir gün veya günün bir kısmı kadar kaldık. İşte sayanlara sor» derler.', 18, 348, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2787, 114, 23, 'Mü\'minun', 'قَالَ اِنْ لَبِثْتُمْ اِلَّا قَل۪يلاً لَوْ اَنَّكُمْ كُنْتُمْ تَعْلَمُونَ', 'Buyurur: Sadece az bir süre kaldınız; keşke siz (bunu) bilmiş olsaydınız!', 18, 348, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2788, 115, 23, 'Mü\'minun', 'اَفَحَسِبْتُمْ اَنَّمَا خَلَقْنَاكُمْ عَبَثاً وَاَنَّكُمْ اِلَيْنَا لَا تُرْجَعُونَ', 'Sizi sadece boş yere yarattığımızı ve sizin hakikaten huzurumuza geri getirilmeyeceğinizi mi sandınız?', 18, 348, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2789, 116, 23, 'Mü\'minun', 'فَتَعَالَى اللّٰهُ الْمَلِكُ الْحَقُّۚ لَٓا اِلٰهَ اِلَّا هُوَۚ رَبُّ الْعَرْشِ الْكَر۪يمِ', 'Mutlak hakim ve hak olan Allah, çok yücedir. O´ndan başka tanrı yoktur, O, yüce Arş´ın sahibidir.', 18, 348, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2790, 117, 23, 'Mü\'minun', 'وَمَنْ يَدْعُ مَعَ اللّٰهِ اِلٰهاً اٰخَرَۙ لَا بُرْهَانَ لَهُ بِه۪ۙ فَاِنَّمَا حِسَابُهُ عِنْدَ رَبِّه۪ۜ اِنَّهُ لَا يُفْلِحُ الْكَافِرُونَ', 'Her kim Allah ile birlikte diğer bir tanrıya taparsa, -ki bu hususla ilgili hiçbir delili yoktur- o kimsenin hesabı ancak Rabbinin nezdindedir. Şurası muhakkak ki kâfirler iflah olmaz.', 18, 348, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2791, 118, 23, 'Mü\'minun', 'وَقُلْ رَبِّ اغْفِرْ وَارْحَمْ وَاَنْتَ خَيْرُ الرَّاحِم۪ينَ', '(Resûlüm!) De ki: Bağışla ve merhamet et Rabbim! Sen merhametlilerin en iyisisin.', 18, 348, 118, 4354, 'Mü\'minler', 'Mekke', 74),
(2792, 1, 24, 'Nur', 'سُورَةٌ اَنْزَلْنَاهَا وَفَرَضْنَاهَا وَاَنْزَلْنَا ف۪يهَٓا اٰيَاتٍ بَيِّنَاتٍ لَعَلَّكُمْ تَذَكَّرُونَ', '(Bu) Bizim inzâl ettiğimiz ve (hükümlerini üzerinize) farz kıldığımız bir sûredir. Belki düşünüp öğüt alırsınız diye onda açık seçik âyetler indirdik.', 18, 349, 64, 3875, 'Işık', 'Medine', 102),
(2793, 2, 24, 'Nur', 'اَلزَّانِيَةُ وَالزَّان۪ي فَاجْلِدُوا كُلَّ وَاحِدٍ مِنْهُمَا مِائَةَ جَلْدَةٍۖ وَلَا تَأْخُذْكُمْ بِهِمَا رَأْفَةٌ ف۪ي د۪ينِ اللّٰهِ اِنْ كُنْتُمْ تُؤْمِنُونَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِۚ وَلْيَشْهَدْ عَذَابَهُمَا طَٓائِفَةٌ مِنَ الْمُؤْمِن۪ينَ', 'Zina eden kadın ve zina eden erkekten her birine yüz sopa vurun; Allah´a ve ahiret gününe inanıyorsanız, Allah´ın dininde (hükümlerini uygularken) onlara acıyacağınız tutmasın. Müminlerden bir gurup da onlara uygulanan cezaya şahit olsun.', 18, 349, 64, 3875, 'Işık', 'Medine', 102),
(2794, 3, 24, 'Nur', 'اَلزَّان۪ي لَا يَنْكِحُ اِلَّا زَانِيَةً اَوْ مُشْرِكَةًۘ وَالزَّانِيَةُ لَا يَنْكِحُهَٓا اِلَّا زَانٍ اَوْ مُشْرِكٌۚ وَحُرِّمَ ذٰلِكَ عَلَى الْمُؤْمِن۪ينَ', 'Zina eden erkek, zina eden veya müşrik olan bir kadından başkası ile evlenmez; zina eden kadınla da ancak zina eden veya müşrik olan erkek evlenir. Bu, müminlere haram kılınmıştır.', 18, 349, 64, 3875, 'Işık', 'Medine', 102),
(2795, 4, 24, 'Nur', 'وَالَّذ۪ينَ يَرْمُونَ الْمُحْصَنَاتِ ثُمَّ لَمْ يَأْتُوا بِاَرْبَعَةِ شُهَدَٓاءَ فَاجْلِدُوهُمْ ثَمَان۪ينَ جَلْدَةً وَلَا تَقْبَلُوا لَهُمْ شَهَادَةً اَبَداًۚ وَاُو۬لٰٓئِكَ هُمُ الْفَاسِقُونَۙ', 'Namuslu kadınlara zina isnadında bulunup, sonra (bunu isbat için) dört şahit getiremeyenlere seksener sopa vurun ve artık onların şahitliğini hiçbir zaman kabul etmeyin. Onlar tamamen günahkârdırlar.', 18, 349, 64, 3875, 'Işık', 'Medine', 102),
(2796, 5, 24, 'Nur', 'اِلَّا الَّذ۪ينَ تَابُوا مِنْ بَعْدِ ذٰلِكَ وَاَصْلَحُواۚ فَاِنَّ اللّٰهَ غَفُورٌ رَح۪يمٌ', 'Ancak bundan sonra tevbe edip ıslah olanlar müstesnadır. Allah çok bağışlayıcı ve merhametlidir.', 18, 349, 64, 3875, 'Işık', 'Medine', 102),
(2797, 6, 24, 'Nur', 'وَالَّذ۪ينَ يَرْمُونَ اَزْوَاجَهُمْ وَلَمْ يَكُنْ لَهُمْ شُهَدَٓاءُ اِلَّٓا اَنْفُسُهُمْ فَشَهَادَةُ اَحَدِهِمْ اَرْبَعُ شَهَادَاتٍ بِاللّٰهِۙ اِنَّهُ لَمِنَ الصَّادِق۪ينَ', '(6-7) Eşlerine zina isnadında bulunup da kendilerinden başka şahitleri olmayanlara gelince, onların her birinin şahitliği, kendisinin doğru söyleyenlerden olduğuna dair dört defa Allah adına yemin ederek şahitlik etmesi, beşinci defa da, eğer yalan söyleyenlerden ise Allah´ın lânetinin kendi üzerine olmasını dilemesidir.', 18, 349, 64, 3875, 'Işık', 'Medine', 102),
(2798, 7, 24, 'Nur', 'وَالْخَامِسَةُ اَنَّ لَعْنَتَ اللّٰهِ عَلَيْهِ اِنْ كَانَ مِنَ الْكَاذِب۪ينَ', '(6-7) Eşlerine zina isnadında bulunup da kendilerinden başka şahitleri olmayanlara gelince, onların her birinin şahitliği, kendisinin doğru söyleyenlerden olduğuna dair dört defa Allah adına yemin ederek şahitlik etmesi, beşinci defa da, eğer yalan söyleyenlerden ise Allah´ın lânetinin kendi üzerine olmasını dilemesidir.', 18, 349, 64, 3875, 'Işık', 'Medine', 102),
(2799, 8, 24, 'Nur', 'وَيَدْرَؤُ۬ا عَنْهَا الْعَذَابَ اَنْ تَشْهَدَ اَرْبَعَ شَهَادَاتٍ بِاللّٰهِۙ اِنَّهُ لَمِنَ الْكَاذِب۪ينَۙ', '(8-9) Kadının, kocasının yalan söyleyenlerden olduğuna dair dört defa Allah adına yemin ve şahitlik etmesi, beşinci defa da, eğer (kocası) doğru söyleyenlerden ise Allah´ın gazabının kendi üzerine olmasını dilemesi kendisinden cezayı kaldırır.', 18, 349, 64, 3875, 'Işık', 'Medine', 102),
(2800, 9, 24, 'Nur', 'وَالْخَامِسَةَ اَنَّ غَضَبَ اللّٰهِ عَلَيْهَٓا اِنْ كَانَ مِنَ الصَّادِق۪ينَ', '(8-9) Kadının, kocasının yalan söyleyenlerden olduğuna dair dört defa Allah adına yemin ve şahitlik etmesi, beşinci defa da, eğer (kocası) doğru söyleyenlerden ise Allah´ın gazabının kendi üzerine olmasını dilemesi kendisinden cezayı kaldırır.', 18, 349, 64, 3875, 'Işık', 'Medine', 102),
(2801, 10, 24, 'Nur', 'وَلَوْلَا فَضْلُ اللّٰهِ عَلَيْكُمْ وَرَحْمَتُهُ وَاَنَّ اللّٰهَ تَـوَّابٌ حَك۪يمٌ۟', 'Ya Allah´ın size bol lütfu ve merhameti bulunmasaydı ve Allah, tevbeleri kabul eden hüküm ve hikmet sahibi olmasaydı (haliniz nice olurdu)!', 18, 349, 64, 3875, 'Işık', 'Medine', 102),
(2802, 11, 24, 'Nur', 'اِنَّ الَّذ۪ينَ جَٓاؤُ۫ بِالْاِفْكِ عُصْبَةٌ مِنْكُمْۜ لَا تَحْسَبُوهُ شَراًّ لَكُمْۜ بَلْ هُوَ خَيْرٌ لَكُمْۜ لِكُلِّ امْرِئٍ مِنْهُمْ مَا اكْتَسَبَ مِنَ الْاِثْمِۚ وَالَّذ۪ي تَوَلّٰى كِبْرَهُ مِنْهُمْ لَهُ عَذَابٌ عَظ۪يمٌ', '(Peygamber´in eşine) bu ağır iftirayı uyduranlar şüphesiz sizin içinizden bir guruptur. Bunu kendiniz için bir kötülük sanmayın, aksine o, sizin için bir iyiliktir. Onlardan her bir kişiye, günah olarak ne işlemişse (onun karşılığı ceza) vardır. Onlardan (elebaşlık yapıp) bu günahın büyüklüğünü yüklenen kimse için de çok büyük bir azap vardır.', 18, 350, 64, 3875, 'Işık', 'Medine', 102),
(2803, 12, 24, 'Nur', 'لَوْلَٓا اِذْ سَمِعْتُمُوهُ ظَنَّ الْمُؤْمِنُونَ وَالْمُؤْمِنَاتُ بِاَنْفُسِهِمْ خَيْراًۙ وَقَالُوا هٰذَٓا اِفْكٌ مُب۪ينٌ', 'Bu iftirayı işittiğinizde erkek ve kadın müminlerin, kendi vicdanları ile hüsnüzanda bulunup da: «Bu, apaçık bir iftiradır» demeleri gerekmez miydi?', 18, 350, 64, 3875, 'Işık', 'Medine', 102),
(2804, 13, 24, 'Nur', 'لَوْلَا جَٓاؤُ۫ عَلَيْهِ بِاَرْبَعَةِ شُهَدَٓاءَۚ فَاِذْ لَمْ يَأْتُوا بِالشُّهَدَٓاءِ فَاُو۬لٰٓئِكَ عِنْدَ اللّٰهِ هُمُ الْكَاذِبُونَ', 'Onların (iftiracıların) da bu konuda dört şahit getirmeleri gerekmez miydi? Mademki şahitler getiremediler, öyle ise onlar Allah nezdinde yalancıların ta kendisidirler.', 18, 350, 64, 3875, 'Işık', 'Medine', 102),
(2805, 14, 24, 'Nur', 'وَلَوْلَا فَضْلُ اللّٰهِ عَلَيْكُمْ وَرَحْمَتُهُ فِي الدُّنْيَا وَالْاٰخِرَةِ لَمَسَّكُمْ ف۪ي مَٓا اَفَضْتُمْ ف۪يهِ عَذَابٌ عَظ۪يمٌۚ', 'Eğer dünyada ve ahirette Allah´ın lütuf ve merhameti üstünüzde olmasaydı, içine daldığınız bu iftiradan dolayı size mutlaka büyük bir azap isabet ederdi.', 18, 350, 64, 3875, 'Işık', 'Medine', 102),
(2806, 15, 24, 'Nur', 'اِذْ تَلَقَّوْنَهُ بِاَلْسِنَتِكُمْ وَتَقُولُونَ بِاَفْوَاهِكُمْ مَا لَيْسَ لَكُمْ بِه۪ عِلْمٌ وَتَحْسَبُونَهُ هَيِّناًۗ وَهُوَ عِنْدَ اللّٰهِ عَظ۪يمٌ', 'Çünkü siz bu iftirayı, dilden dile birbirinize aktarıyor, hakkında bilgi sahibi olmadığınız şeyi ağızlarınızda geveleyip duruyorsunuz. Bunun önemsiz olduğunu sanıyorsunuz. Halbuki bu, Allah katında çok büyük (bir suç) tur.', 18, 350, 64, 3875, 'Işık', 'Medine', 102),
(2807, 16, 24, 'Nur', 'وَلَوْلَٓا اِذْ سَمِعْتُمُوهُ قُلْتُمْ مَا يَكُونُ لَـنَٓا اَنْ نَتَكَلَّمَ بِهٰذَاۗ سُبْحَانَكَ هٰذَا بُهْتَانٌ عَظ۪يمٌ', 'Onu duyduğunuzda: «Bunu konuşup yaymamız bize yakışmaz. Hâşâ! Bu, çok büyük bir iftiradır» demeli değil miydiniz?', 18, 350, 64, 3875, 'Işık', 'Medine', 102),
(2808, 17, 24, 'Nur', 'يَعِظُكُمُ اللّٰهُ اَنْ تَعُودُوا لِمِثْلِه۪ٓ اَبَداً اِنْ كُنْتُمْ مُؤْمِن۪ينَۚ', 'Eğer inanmış insanlarsanız, Allah, bir daha buna benzer tutumu tekrarlamaktan sizi sakındırıp uyarır.', 18, 350, 64, 3875, 'Işık', 'Medine', 102),
(2809, 18, 24, 'Nur', 'وَيُبَيِّنُ اللّٰهُ لَكُمُ الْاٰيَاتِۜ وَاللّٰهُ عَل۪يمٌ حَك۪يمٌ', 'Ve Allah âyetleri size açıklıyor. Allah, (işin iç yüzünü) çok iyi bilir, hüküm ve hikmet sahibidir.', 18, 350, 64, 3875, 'Işık', 'Medine', 102),
(2810, 19, 24, 'Nur', 'اِنَّ الَّذ۪ينَ يُحِبُّونَ اَنْ تَش۪يعَ الْفَاحِشَةُ فِي الَّذ۪ينَ اٰمَنُوا لَهُمْ عَذَابٌ اَل۪يمٌۙ فِي الدُّنْيَا وَالْاٰخِرَةِۜ وَاللّٰهُ يَعْلَمُ وَاَنْتُمْ لَا تَعْلَمُونَ', 'İnananlar arasında çirkin şeylerin yayılmasını arzulayan kimseler için dünyada da ahirette de çetin bir ceza vardır. Allah bilir, siz bilmezsiniz.', 18, 350, 64, 3875, 'Işık', 'Medine', 102),
(2811, 20, 24, 'Nur', 'وَلَوْلَا فَضْلُ اللّٰهِ عَلَيْكُمْ وَرَحْمَتُهُ وَاَنَّ اللّٰهَ رَؤُ۫فٌ رَح۪يمٌ۟', 'Ya sizin üstünüze Allah´ın lütuf ve merhameti olmasaydı, Allah çok şefkatli ve merhametli olmasaydı (haliniz nice olurdu)!', 18, 350, 64, 3875, 'Işık', 'Medine', 102),
(2812, 21, 24, 'Nur', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا لَا تَتَّبِعُوا خُطُوَاتِ الشَّيْطَانِۜ وَمَنْ يَتَّبِـعْ خُطُوَاتِ الشَّيْطَانِ فَاِنَّهُ يَأْمُرُ بِالْفَحْشَٓاءِ وَالْمُنْكَرِۜ وَلَوْلَا فَضْلُ اللّٰهِ عَلَيْكُمْ وَرَحْمَتُهُ مَا زَكٰى مِنْكُمْ مِنْ اَحَدٍ اَبَداًۙ وَلٰكِنَّ اللّٰهَ يُزَكّ۪ي مَنْ يَشَٓاءُۜ وَاللّٰهُ سَم۪يعٌ عَل۪يمٌ', 'Ey iman edenler! Şeytanın adımlarını takip etmeyin. Kim şeytanın adımlarını takip ederse, muhakkak ki o, edepsizliği (yüzkızartıcı suçları) ve kötülüğü emreder. Eğer üstünüzde Allah´ın lütuf ve merhameti olmasaydı, içinizden hiçbir kimse asla temize çıkamazdı. Fakat Allah dilediğini arındırır. Allah işitir ve bilir.', 18, 351, 64, 3875, 'Işık', 'Medine', 102),
(2813, 22, 24, 'Nur', 'وَلَا يَأْتَلِ اُو۬لُوا الْفَضْلِ مِنْكُمْ وَالسَّعَةِ اَنْ يُؤْتُٓوا اُو۬لِي الْقُرْبٰى وَالْمَسَاك۪ينَ وَالْمُهَاجِر۪ينَ ف۪ي سَب۪يلِ اللّٰهِۖ وَلْيَعْفُوا وَلْيَصْفَحُواۜ اَلَا تُحِبُّونَ اَنْ يَغْفِرَ اللّٰهُ لَكُمْۜ وَاللّٰهُ غَفُورٌ رَح۪يمٌ', 'İçinizden faziletli ve servet sahibi kimseler akrabaya, yoksullara, Allah yolunda göç edenlere (mallarından) vermeyeceklerine yemin etmesinler; bağışlasınlar; feragat göstersinler. Allah´ın sizi bağışlamasını arzulamaz mısınız? Allah çok bağışlayandır, çok merhametlidir.', 18, 351, 64, 3875, 'Işık', 'Medine', 102),
(2814, 23, 24, 'Nur', 'اِنَّ الَّذ۪ينَ يَرْمُونَ الْمُحْصَنَاتِ الْغَافِلَاتِ الْمُؤْمِنَاتِ لُعِنُوا فِي الدُّنْيَا وَالْاٰخِرَةِۖ وَلَهُمْ عَذَابٌ عَظ۪يمٌۙ', '(23-24) Namuslu, kötülüklerden habersiz mümin kadınlara zina isnadında bulunanlar, dünya ve ahirette lânetlenmişlerdir. Yapmış olduklarına, dilleri, elleri ve ayaklarının, aleyhlerinde şahitlik edeceği gün onlar için çok büyük bir azap vardır.', 18, 351, 64, 3875, 'Işık', 'Medine', 102),
(2815, 24, 24, 'Nur', 'يَوْمَ تَشْهَدُ عَلَيْهِمْ اَلْسِنَتُهُمْ وَاَيْد۪يهِمْ وَاَرْجُلُهُمْ بِمَا كَانُوا يَعْمَلُونَ', '(23-24) Namuslu, kötülüklerden habersiz mümin kadınlara zina isnadında bulunanlar, dünya ve ahirette lânetlenmişlerdir. Yapmış olduklarına, dilleri, elleri ve ayaklarının, aleyhlerinde şahitlik edeceği gün onlar için çok büyük bir azap vardır.', 18, 351, 64, 3875, 'Işık', 'Medine', 102),
(2816, 25, 24, 'Nur', 'يَوْمَئِذٍ يُوَفّ۪يهِمُ اللّٰهُ د۪ينَهُمُ الْحَقَّ وَيَعْلَمُونَ اَنَّ اللّٰهَ هُوَ الْحَقُّ الْمُب۪ينُ', 'O gün Allah onlara gerçek cezalarını tastamam verecek ve onlar Allah´ın apaçık gerçek olduğunu anlayacaklardır.', 18, 351, 64, 3875, 'Işık', 'Medine', 102),
(2817, 26, 24, 'Nur', 'اَلْخَب۪يثَاتُ لِلْخَب۪يث۪ينَ وَالْخَب۪يثُونَ لِلْخَب۪يثَاتِۚ وَالطَّيِّبَاتُ لِلطَّيِّب۪ينَ وَالطَّيِّبُونَ لِلطَّيِّبَاتِۚ اُو۬لٰٓئِكَ مُبَرَّؤُ۫نَ مِمَّا يَقُولُونَۜ لَهُمْ مَغْفِرَةٌ وَرِزْقٌ كَر۪يمٌ۟', 'Kötü kadınlar kötü erkeklere, kötü erkekler ise kötü kadınlara; temiz kadınlar temiz erkeklere, temiz erkekler de temiz kadınlara yaraşır. Bu sonuncular, (iftiracıların) söylediklerinden çok uzaktırlar. Kendileri için bağışlanma ve güzel bir rızık vardır.', 18, 351, 64, 3875, 'Işık', 'Medine', 102),
(2818, 27, 24, 'Nur', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا لَا تَدْخُلُوا بُيُوتاً غَيْرَ بُيُوتِكُمْ حَتّٰى تَسْتَأْنِسُوا وَتُسَلِّمُوا عَلٰٓى اَهْلِهَاۜ ذٰلِكُمْ خَيْرٌ لَكُمْ لَعَلَّكُمْ تَذَكَّرُونَ', 'Ey iman edenler! Kendi evinizden başka evlere, geldiğinizi farkettirip (izin alıp) ev halkına selâm vermedikçe girmeyin. Bu sizin için daha iyidir; herhalde (bunu) düşünüp anlarsınız.', 18, 351, 64, 3875, 'Işık', 'Medine', 102),
(2819, 28, 24, 'Nur', 'فَاِنْ لَمْ تَجِدُوا ف۪يهَٓا اَحَداً فَلَا تَدْخُلُوهَا حَتّٰى يُؤْذَنَ لَكُمْۚ وَاِنْ ق۪يلَ لَكُمُ ارْجِعُوا فَارْجِعُوا هُوَ اَزْكٰى لَكُمْۜ وَاللّٰهُ بِمَا تَعْمَلُونَ عَل۪يمٌ', 'Orada hiçbir kimse bulamadınızsa, size izin verilinceye kadar oraya girmeyin. Eğer size, «Geri dönün!» denilirse, hemen dönün. Çünkü bu, sizin için daha nezih bir davranıştır. Allah, yaptığınızı bilir.', 18, 352, 64, 3875, 'Işık', 'Medine', 102),
(2820, 29, 24, 'Nur', 'لَيْسَ عَلَيْكُمْ جُنَاحٌ اَنْ تَدْخُلُوا بُيُوتاً غَيْرَ مَسْكُونَةٍ ف۪يهَا مَتَاعٌ لَكُمْۜ وَاللّٰهُ يَعْلَمُ مَا تُبْدُونَ وَمَا تَكْتُمُونَ', 'İçinde kendinize ait eşyanın bulunduğu oturulmayan evlere girmenizde herhangi bir sakınca yoktur. Allah, sizin açığa vurduklarınızı da, gizlediklerinizi de bilir.', 18, 352, 64, 3875, 'Işık', 'Medine', 102),
(2821, 30, 24, 'Nur', 'قُلْ لِلْمُؤْمِن۪ينَ يَغُضُّوا مِنْ اَبْصَارِهِمْ وَيَحْفَظُوا فُرُوجَهُمْۜ ذٰلِكَ اَزْكٰى لَهُمْۜ اِنَّ اللّٰهَ خَب۪يرٌ بِمَا يَصْنَعُونَ', '(Resûlüm!) Mümin erkeklere, gözlerini (harama) dikmemelerini, ırzlarını da korumalarını söyle. Çünkü bu, kendileri için daha temiz bir davranıştır. Şüphesiz Allah, onların yapmakta olduklarından haberdardır.', 18, 352, 64, 3875, 'Işık', 'Medine', 102),
(2822, 31, 24, 'Nur', 'وَقُلْ لِلْمُؤْمِنَاتِ يَغْضُضْنَ مِنْ اَبْصَارِهِنَّ وَيَحْفَظْنَ فُرُوجَهُنَّ وَلَا يُبْد۪ينَ ز۪ينَتَهُنَّ اِلَّا مَا ظَهَرَ مِنْهَا وَلْيَضْرِبْنَ بِخُمُرِهِنَّ عَلٰى جُيُوبِهِنَّۖ وَلَا يُبْد۪ينَ ز۪ينَتَهُنَّ اِلَّا لِبُعُولَتِهِنَّ اَوْ اٰبَٓائِهِنَّ اَوْ اٰبَٓاءِ بُعُولَتِهِنَّ اَوْ اَبْنَٓائِهِنَّ اَوْ اَبْنَٓاءِ بُعُولَتِهِنَّ اَوْ اِخْوَانِهِنَّ اَوْ بَن۪ٓي اِخْوَانِهِنَّ اَوْ بَن۪ٓي اَخَوَاتِهِنَّ اَوْ نِسَٓائِهِنَّ اَوْ مَا مَلَكَتْ اَيْمَانُهُنَّ اَوِ التَّابِع۪ينَ غَيْرِ اُو۬لِي الْاِرْبَةِ مِنَ الرِّجَالِ اَوِ الطِّفْلِ الَّذ۪ينَ لَمْ يَظْهَرُوا عَلٰى عَوْرَاتِ النِّسَٓاءِۖ وَلَا يَضْرِبْنَ بِاَرْجُلِهِنَّ لِيُعْلَمَ مَا يُخْف۪ينَ مِنْ ز۪ينَتِهِنَّۜ وَتُوبُٓوا اِلَى اللّٰهِ جَم۪يعاً اَيُّهَ الْمُؤْمِنُونَ لَعَلَّكُمْ تُفْلِحُونَ', 'Mümin kadınlara da söyle: Gözlerini (harama bakmaktan) korusunlar; namus ve iffetlerini esirgesinler. Görünen kısımları müstesna olmak üzere, zinetlerini teşhir etmesinler. Baş örtülerini, yakalarının üzerine (kadar) örtsünler. Kocaları, babaları, kocalarının babaları, kendi oğulları, kocalarının oğulları, erkek kardeşleri, erkek kardeşlerinin oğulları, kız kardeşlerinin oğulları, kendi kadınları (mümin kadınlar), ellerinin altında bulunanlar (köleleri), erkeklerden, ailenin kadınına şehvet duymayan hizmetçi vb. tâbi kimseler, yahut henüz kadınların gizli kadınlık hususiyetlerinin farkında olmayan çocuklardan başkasına zinetlerini göstermesinler. Gizlemekte oldukları zinetleri anlaşılsın diye ayaklarını yere vurmasınlar (Dikkatleri üzerine çekecek tarzda yürümesinler). Ey müminler! Hep birden Allah´a tevbe ediniz ki kurtuluşa eresiniz.', 18, 352, 64, 3875, 'Işık', 'Medine', 102),
(2823, 32, 24, 'Nur', 'وَاَنْكِحُوا الْاَيَامٰى مِنْكُمْ وَالصَّالِح۪ينَ مِنْ عِبَادِكُمْ وَاِمَٓائِكُمْۜ اِنْ يَكُونُوا فُقَـرَٓاءَ يُغْنِهِمُ اللّٰهُ مِنْ فَضْلِه۪ۜ وَاللّٰهُ وَاسِعٌ عَل۪يمٌ', 'Aranızdaki bekârları, kölelerinizden ve cariyelerinizden elverişli olanları evlendirin. Eğer bunlar fakir iseler, Allah kendi lütfu ile onları zenginleştirir. Allah, (lütfu) geniş olan ve (her şeyi) bilendir.', 18, 353, 64, 3875, 'Işık', 'Medine', 102),
(2824, 33, 24, 'Nur', 'وَلْيَسْتَعْفِفِ الَّذ۪ينَ لَا يَجِدُونَ نِكَاحاً حَتّٰى يُغْنِيَهُمُ اللّٰهُ مِنْ فَضْلِه۪ۜ وَالَّذ۪ينَ يَبْتَغُونَ الْكِتَابَ مِمَّا مَلَكَتْ اَيْمَانُكُمْ فَكَاتِبُوهُمْ اِنْ عَلِمْتُمْ ف۪يهِمْ خَيْراًۗ وَاٰتُوهُمْ مِنْ مَالِ اللّٰهِ الَّـذ۪ٓي اٰتٰيكُمْۜ وَلَا تُكْرِهُوا فَتَيَاتِكُمْ عَلَى الْبِغَٓاءِ اِنْ اَرَدْنَ تَحَصُّناً لِتَبْتَغُوا عَرَضَ الْحَيٰوةِ الدُّنْيَاۜ وَمَنْ يُكْرِهْهُنَّ فَاِنَّ اللّٰهَ مِنْ بَعْدِ اِكْرَاهِهِنَّ غَفُورٌ رَح۪يمٌ', 'Evlenme imkânını bulamayanlar ise, Allah, lütfu ile kendilerini varlıklı kılıncaya kadar iffetlerini korusunlar. Ellerinizin altında bulunanlardan (köleler ve câriyelerden) mükâtebe yapmak isteyenlerle, eğer kendilerinde bir hayır (kabiliyet ve güvenilirlik) görüyorsanız, hemen mükâtebe yapın. Allah´ın size vermiş olduğu malından siz de onlara verin. Dünya hayatının geçici menfaatlerini elde edeceksiniz diye, namuslu kalmak isteyen câriyelerinizi fuhşa zorlamayın. Kim onları zor altında bırakırsa, bilinmelidir ki zorlanmalarından sonra Allah (onlar için) çok bağışlayıcı ve merhametlidir.', 18, 353, 64, 3875, 'Işık', 'Medine', 102),
(2825, 34, 24, 'Nur', 'وَلَقَدْ اَنْزَلْـنَٓا اِلَيْكُمْ اٰيَاتٍ مُبَيِّنَاتٍ وَمَثَلاً مِنَ الَّذ۪ينَ خَلَوْا مِنْ قَبْلِكُمْ وَمَوْعِظَةً لِلْمُتَّق۪ينَ۟', 'Andolsun ki biz size (gerekeni) açık açık bildiren âyetler, sizden önce yaşayıp gitmiş olanlardan örnekler ve takvâya ulaşmış kimseler için öğütler indirdik.', 18, 353, 64, 3875, 'Işık', 'Medine', 102),
(2826, 35, 24, 'Nur', 'اَللّٰهُ نُورُ السَّمٰوَاتِ وَالْاَرْضِۜ مَثَلُ نُورِه۪ كَمِشْكٰوةٍ ف۪يهَا مِصْبَاحٌۜ اَلْمِصْبَاحُ ف۪ي زُجَاجَةٍۜ اَلزُّجَاجَةُ كَاَنَّهَا كَوْكَبٌ دُرِّيٌّ يُوقَدُ مِنْ شَجَرَةٍ مُبَارَكَةٍ زَيْتُونَةٍ لَا شَرْقِيَّةٍ وَلَا غَرْبِيَّةٍۙ يَكَادُ زَيْتُهَا يُض۪ٓيءُ وَلَوْ لَمْ تَمْسَسْهُ نَارٌۜ نُورٌ عَلٰى نُورٍۜ يَهْدِي اللّٰهُ لِنُورِه۪ مَنْ يَشَٓاءُۜ وَيَضْرِبُ اللّٰهُ الْاَمْثَالَ لِلنَّاسِۜ وَاللّٰهُ بِكُلِّ شَيْءٍ عَل۪يمٌۙ', 'Allah, göklerin ve yerin nûrudur. O´nun nûrunun temsili, içinde lamba bulunan bir kandillik gibidir. O lamba kristal bir fanus içindedir; o fanus da sanki inciye benzer bir yıldız gibidir ki, doğuya da, batıya da nisbet edilemeyen mübarek bir ağaçtan, yani zeytinden (çıkan yağdan) tutuşturulur. Onun yağı, neredeyse, kendisine ateş değmese dahi ışık verir. (Bu,) nûr üstüne nûrdur. Allah dilediği kimseyi nûruna eriştirir. Allah insanlara (işte böyle) temsiller getirir. Allah her şeyi bilir.', 18, 353, 64, 3875, 'Işık', 'Medine', 102),
(2827, 36, 24, 'Nur', 'ف۪ي بُيُوتٍ اَذِنَ اللّٰهُ اَنْ تُرْفَعَ وَيُذْكَرَ ف۪يهَا اسْمُهُۙ يُسَبِّحُ لَهُ ف۪يهَا بِالْغُدُوِّ وَالْاٰصَالِۙ', '(Bu kandil) birtakım evlerdedir ki, Allah (o evlerin) yücelmesine ve içlerinde isminin anılmasına izin vermiştir. Orada sabah akşam O´nu (öyle kimseler) tesbih eder ki;', 18, 353, 64, 3875, 'Işık', 'Medine', 102),
(2828, 37, 24, 'Nur', 'رِجَالٌۙ لَا تُلْه۪يهِمْ تِجَارَةٌ وَلَا بَيْعٌ عَنْ ذِكْرِ اللّٰهِ وَاِقَامِ الصَّلٰوةِ وَا۪يتَٓاءِ الزَّكٰوةِۙ يَخَافُونَ يَوْماً تَتَقَلَّبُ ف۪يهِ الْقُلُوبُ وَالْاَبْصَارُۙ', 'Onlar, ne ticaret ne de alış-verişin kendilerini Allah´ı anmaktan, namaz kılmaktan ve zekât vermekten alıkoyamadığı insanlardır. Onlar, kalplerin ve gözlerin allak bullak olduğu bir günden korkarlar.', 18, 354, 64, 3875, 'Işık', 'Medine', 102),
(2829, 38, 24, 'Nur', 'لِيَجْزِيَهُمُ اللّٰهُ اَحْسَنَ مَا عَمِلُوا وَيَز۪يدَهُمْ مِنْ فَضْلِه۪ۜ وَاللّٰهُ يَرْزُقُ مَنْ يَشَٓاءُ بِغَيْرِ حِسَابٍ', 'Çünkü (o günde) Allah, onları yaptıklarının en güzeli ile mükâfatlandıracak ve lütfundan onlara fazlasıyla verecektir. Allah, dilediğini hesapsız rızıklandırır.', 18, 354, 64, 3875, 'Işık', 'Medine', 102),
(2830, 39, 24, 'Nur', 'وَالَّذ۪ينَ كَفَرُٓوا اَعْمَالُهُمْ كَسَرَابٍ بِق۪يعَةٍ يَحْسَبُهُ الظَّمْاٰنُ مَٓاءًۜ حَتّٰٓى اِذَا جَٓاءَهُ لَمْ يَجِدْهُ شَيْـٔاً وَوَجَدَ اللّٰهَ عِنْدَهُ فَوَفّٰيهُ حِسَابَهُۜ وَاللّٰهُ سَر۪يعُ الْحِسَابِۙ', 'İnkâr edenlere gelince, onların amelleri, ıssız çöllerdeki serap gibidir ki susayan onu su zanneder; nihayet ona vardığında orada herhangi bir şey bulamamış, üstelik yanıbaşında da (inanmadığı, kendisinden sakınmadığı) Allah´ı bulmuştur; Allah ise, onun hesabını tastamam görmüştür. Allah hesabı çok çabuk görür.', 18, 354, 64, 3875, 'Işık', 'Medine', 102),
(2831, 40, 24, 'Nur', 'اَوْ كَظُلُمَاتٍ ف۪ي بَحْرٍ لُجِّيٍّ يَغْشٰيهُ مَوْجٌ مِنْ فَوْقِه۪ مَوْجٌ مِنْ فَوْقِه۪ سَحَابٌۜ ظُلُمَاتٌ بَعْضُهَا فَوْقَ بَعْضٍۜ اِذَٓا اَخْرَجَ يَدَهُ لَمْ يَكَدْ يَرٰيهَاۜ وَمَنْ لَمْ يَجْعَلِ اللّٰهُ لَهُ نُوراً فَمَا لَهُ مِنْ نُورٍ۟', 'Yahut (o kâfirlerin duygu, düşünce ve davranışları) engin bir denizdeki yoğun karanlıklar gibidir; (öyle bir deniz) ki, onu dalga üstüne dalga kaplıyor; üstünde de bulut... Birbiri üstüne karanlıklar... İnsan, elini çıkarıp uzatsa, neredeyse onu dahi göremez. Bir kimseye Allah nûr vermemişse, artık o kimsenin aydınlıktan nasibi yoktur.', 18, 354, 64, 3875, 'Işık', 'Medine', 102),
(2832, 41, 24, 'Nur', 'اَلَمْ تَرَ اَنَّ اللّٰهَ يُسَبِّـحُ لَهُ مَنْ فِي السَّمٰوَاتِ وَالْاَرْضِ وَالطَّيْرُ صَٓافَّاتٍۜ كُلٌّ قَدْ عَلِمَ صَلَاتَهُ وَتَسْب۪يحَهُۜ وَاللّٰهُ عَل۪يمٌ بِمَا يَفْعَلُونَ', 'Göklerde ve yerde bulunanlarla dizi dizi kuşların Allah´ı tesbih ettiklerini görmez misin? Her biri kendi duasını ve tesbihini (öğrenmiş) bilmiştir. Allah, onların yapmakta olduklarını hakkıyle bilir.', 18, 354, 64, 3875, 'Işık', 'Medine', 102),
(2833, 42, 24, 'Nur', 'وَلِلّٰهِ مُلْكُ السَّمٰوَاتِ وَالْاَرْضِۚ وَاِلَى اللّٰهِ الْمَص۪يرُ', 'Göklerin ve yerin mülkü Allah´ındır; dönüş de ancak O´nadır.', 18, 354, 64, 3875, 'Işık', 'Medine', 102),
(2834, 43, 24, 'Nur', 'اَلَمْ تَرَ اَنَّ اللّٰهَ يُزْج۪ي سَحَاباً ثُمَّ يُؤَلِّفُ بَيْنَهُ ثُمَّ يَجْعَلُهُ رُكَاماً فَتَرَى الْوَدْقَ يَخْرُجُ مِنْ خِلَالِه۪ۚ وَيُنَزِّلُ مِنَ السَّمَٓاءِ مِنْ جِبَالٍ ف۪يهَا مِنْ بَرَدٍ فَيُص۪يبُ بِه۪ مَنْ يَشَٓاءُ وَيَصْرِفُهُ عَنْ مَنْ يَشَٓاءُۜ يَكَادُ سَنَا بَرْقِه۪ يَذْهَبُ بِالْاَبْصَارِۜ', 'Görmez misin ki Allah bir takım bulutları (çıkarıp) sürüyor; sonra onları bir araya getirip üstüste yığıyor. İşte görüyorsun ki bunlar arasından yağmur çıkıyor. O, gökten, oradaki dağlardan (dağlar büyüklüğünde bulutlardan) dolu indirir. Artık onu dilediğine isabet ettirir; dilediğinden de onu uzak tutar; (bu bulutların) şimşeğinin parıltısı neredeyse gözleri alır!', 18, 354, 64, 3875, 'Işık', 'Medine', 102),
(2835, 44, 24, 'Nur', 'يُقَلِّبُ اللّٰهُ الَّيْلَ وَالنَّهَارَۜ اِنَّ ف۪ي ذٰلِكَ لَعِبْرَةً لِاُو۬لِي الْاَبْصَارِ', 'Allah, gece ile gündüzü birbirine çeviriyor. Şüphesiz bunda basiret sahipleri için mutlak bir ibret vardır.', 18, 355, 64, 3875, 'Işık', 'Medine', 102),
(2836, 45, 24, 'Nur', 'وَاللّٰهُ خَلَقَ كُلَّ دَٓابَّةٍ مِنْ مَٓاءٍۚ فَمِنْهُمْ مَنْ يَمْش۪ي عَلٰى بَطْنِه۪ۚ وَمِنْهُمْ مَنْ يَمْش۪ي عَلٰى رِجْلَيْنِۚ وَمِنْهُمْ مَنْ يَمْش۪ي عَلٰٓى اَرْبَعٍۜ يَخْلُقُ اللّٰهُ مَا يَشَٓاءُۜ اِنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌ', 'Allah, her canlıyı sudan yarattı. İşte bunlardan kimi karnı üstünde sürünür, kimi iki ayağı üstünde yürür, kimi dört ayağı üstünde yürür... Allah dilediğini yaratır; şüphesiz Allah her şeye kadirdir.', 18, 355, 64, 3875, 'Işık', 'Medine', 102),
(2837, 46, 24, 'Nur', 'لَقَدْ اَنْزَلْـنَٓا اٰيَاتٍ مُبَيِّنَاتٍۜ وَاللّٰهُ يَهْد۪ي مَنْ يَشَٓاءُ اِلٰى صِرَاطٍ مُسْتَق۪يمٍ', 'Andolsun biz (bilmediklerinizi size) açık seçik bildiren âyetler indirdik. Allah, dilediğini doğru yola iletir.', 18, 355, 64, 3875, 'Işık', 'Medine', 102),
(2838, 47, 24, 'Nur', 'وَيَقُولُونَ اٰمَنَّا بِاللّٰهِ وَبِالرَّسُولِ وَاَطَعْنَا ثُمَّ يَتَوَلّٰى فَر۪يقٌ مِنْهُمْ مِنْ بَعْدِ ذٰلِكَۜ وَمَٓا اُو۬لٰٓئِكَ بِالْمُؤْمِن۪ينَ', '(Bazı insanlar:) «Allah´a ve Peygamber´e inandık ve itaat ettik» diyorlar; ondan sonra da içlerinden bir gurup yüz çeviriyor. Bunlar inanmış değillerdir.', 18, 355, 64, 3875, 'Işık', 'Medine', 102),
(2839, 48, 24, 'Nur', 'وَاِذَا دُعُٓوا اِلَى اللّٰهِ وَرَسُولِه۪ لِيَحْكُمَ بَيْنَهُمْ اِذَا فَر۪يقٌ مِنْهُمْ مُعْرِضُونَ', 'Onlar, aralarında hüküm vermesi için Allah´a ve Peygamber´e çağırıldıklarında, bakarsın ki içlerinden bir kısmı yüz çevirip dönerler.', 18, 355, 64, 3875, 'Işık', 'Medine', 102),
(2840, 49, 24, 'Nur', 'وَاِنْ يَكُنْ لَهُمُ الْحَقُّ يَأْتُٓوا اِلَيْهِ مُذْعِن۪ينَۜ', 'Ama, eğer (Allah ve Resûlünün hükmettiği) hak kendi lehlerine ise, ona boyun eğip gelirler.', 18, 355, 64, 3875, 'Işık', 'Medine', 102),
(2841, 50, 24, 'Nur', 'اَف۪ي قُلُوبِهِمْ مَرَضٌ اَمِ ارْتَابُٓوا اَمْ يَخَافُونَ اَنْ يَح۪يفَ اللّٰهُ عَلَيْهِمْ وَرَسُولُهُۜ بَلْ اُو۬لٰٓئِكَ هُمُ الظَّالِمُونَ۟', 'Kalplerinde bir hastalık mı var; yoksa şüphe içinde midirler, yahut Allah ve Resûlünün kendilerine zulüm ve haksızlık edeceğinden mi korkuyorlar? Hayır, asıl zalimler kendileridir!', 18, 355, 64, 3875, 'Işık', 'Medine', 102),
(2842, 51, 24, 'Nur', 'اِنَّمَا كَانَ قَوْلَ الْمُؤْمِن۪ينَ اِذَا دُعُٓوا اِلَى اللّٰهِ وَرَسُولِه۪ لِيَحْكُمَ بَيْنَهُمْ اَنْ يَقُولُوا سَمِعْنَا وَاَطَعْنَاۜ وَاُو۬لٰٓئِكَ هُمُ الْمُفْلِحُونَ', 'Aralarında hüküm vermesi için Allah´a ve Resûlüne davet edildiklerinde, müminlerin sözü ancak «İşittik ve itaat ettik» demeleridir. İşte asıl bunlar kurtuluşa erenlerdir.', 18, 355, 64, 3875, 'Işık', 'Medine', 102),
(2843, 52, 24, 'Nur', 'وَمَنْ يُطِـعِ اللّٰهَ وَرَسُولَهُ وَيَخْشَ اللّٰهَ وَيَتَّقْهِ فَاُو۬لٰٓئِكَ هُمُ الْفَٓائِزُونَ', 'Her kim Allah´a ve Resûlüne itaat eder, Allah´a saygı duyar ve O´ndan sakınırsa, işte asıl bunlar mutluluğa erenlerdir.', 18, 355, 64, 3875, 'Işık', 'Medine', 102),
(2844, 53, 24, 'Nur', 'وَاَقْسَمُوا بِاللّٰهِ جَهْدَ اَيْمَانِهِمْ لَئِنْ اَمَرْتَهُمْ لَيَخْرُجُنَّۜ قُلْ لَا تُقْسِمُواۚ طَاعَةٌ مَعْرُوفَةٌۜ اِنَّ اللّٰهَ خَب۪يرٌ بِمَا تَعْمَلُونَ', '(Münafıklar), sen hakikaten kendilerine emrettiğin takdirde mutlaka (savaşa) çıkacaklarına dair, en ağır yeminleri ile Allah´a yemin ettiler. De ki: Yemin etmeyin. İtaatiniz malûmdur! Bilin ki Allah, yaptıklarınızdan haberdardır.', 18, 355, 64, 3875, 'Işık', 'Medine', 102),
(2845, 54, 24, 'Nur', 'قُلْ اَط۪يعُوا اللّٰهَ وَاَط۪يعُوا الرَّسُولَۚ فَاِنْ تَوَلَّوْا فَاِنَّمَا عَلَيْهِ مَا حُمِّلَ وَعَلَيْكُمْ مَا حُمِّلْتُمْۜ وَاِنْ تُط۪يعُوهُ تَهْتَدُواۜ وَمَا عَلَى الرَّسُولِ اِلَّا الْبَلَاغُ الْمُب۪ينُ', 'De ki: Allah´a itaat edin; Peygamber´e de itaat edin. Eğer yüz çevirirseniz şunu bilin ki, Peygamber´in sorumluluğu kendisine yüklenen (tebliğ görevini yapmak), sizin sorumluluğunuz da size yüklenen (görevleri yerine getirmeniz)dir. Eğer ona itaat ederseniz, doğru yolu bulmuş olursunuz. Peygamber´e düşen, sadece açık seçik duyurmaktır.', 18, 356, 64, 3875, 'Işık', 'Medine', 102),
(2846, 55, 24, 'Nur', 'وَعَدَ اللّٰهُ الَّذ۪ينَ اٰمَنُوا مِنْكُمْ وَعَمِلُوا الصَّالِحَاتِ لَيَسْتَخْلِفَنَّهُمْ فِي الْاَرْضِ كَمَا اسْتَخْلَفَ الَّذ۪ينَ مِنْ قَبْلِهِمْۖ وَلَيُمَكِّنَنَّ لَهُمْ د۪ينَهُمُ الَّذِي ارْتَضٰى لَهُمْ وَلَيُبَدِّلَنَّهُمْ مِنْ بَعْدِ خَوْفِهِمْ اَمْناًۜ يَعْبُدُونَن۪ي لَا يُشْرِكُونَ ب۪ي شَيْـٔاًۜ وَمَنْ كَفَرَ بَعْدَ ذٰلِكَ فَاُو۬لٰٓئِكَ هُمُ الْفَاسِقُونَ', 'Allah, sizlerden iman edip iyi davranışlarda bulunanlara, kendilerinden öncekileri sahip ve hakim kıldığı gibi onları da yeryüzüne sahip ve hakim kılacağını, onlar için beğenip seçtiği dini (İslâm´ı) onların iyiliğine yerleştirip koruyacağını ve (geçirdikleri) korku döneminden sonra, bunun yerine onlara güven sağlayacağını vâdetti. Çünkü onlar bana kulluk ederler; hiçbir şeyi bana eş tutmazlar. Artık bundan sonra kim inkâr ederse, işte bunlar asıl büyük günahkârlardır.', 18, 356, 64, 3875, 'Işık', 'Medine', 102),
(2847, 56, 24, 'Nur', 'وَاَق۪يمُوا الصَّلٰوةَ وَاٰتُوا الزَّكٰوةَ وَاَط۪يعُوا الرَّسُولَ لَعَلَّكُمْ تُرْحَمُونَ', 'Namazı kılın; zekâtı verin; Peygamber´e itaat edin ki merhamet göresiniz.', 18, 356, 64, 3875, 'Işık', 'Medine', 102),
(2848, 57, 24, 'Nur', 'لَا تَحْسَبَنَّ الَّذ۪ينَ كَفَرُوا مُعْجِز۪ينَ فِي الْاَرْضِۚ وَمَأْوٰيهُمُ النَّارُۜ وَلَبِئْسَ الْمَص۪يرُ۟', 'İnkâr edenlerin, yeryüzünde (Allah´ı) âciz bırakacaklarını sanmayasın! Onların varacağı yer cehennemdir. Ne kötü varış yeri!', 18, 356, 64, 3875, 'Işık', 'Medine', 102),
(2849, 58, 24, 'Nur', 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا لِيَسْتَأْذِنْكُمُ الَّذ۪ينَ مَلَكَتْ اَيْمَانُكُمْ وَالَّذ۪ينَ لَمْ يَبْلُغُوا الْحُلُمَ مِنْكُمْ ثَلٰثَ مَرَّاتٍۜ مِنْ قَبْلِ صَلٰوةِ الْفَجْرِ وَح۪ينَ تَضَعُونَ ثِيَابَكُمْ مِنَ الظَّه۪يرَةِ وَمِنْ بَعْدِ صَلٰوةِ الْعِشَٓاءِ۠ ثَلٰثُ عَوْرَاتٍ لَكُمْۜ لَيْسَ عَلَيْكُمْ وَلَا عَلَيْهِمْ جُنَاحٌ بَعْدَهُنَّۜ طَوَّافُونَ عَلَيْكُمْ بَعْضُكُمْ عَلٰى بَعْضٍۜ كَذٰلِكَ يُبَيِّنُ اللّٰهُ لَكُمُ الْاٰيَاتِۜ وَاللّٰهُ عَل۪يمٌ حَك۪يمٌ', 'Ey müminler! Ellerinizin altında bulunan (köle ve cariyeleriniz) ve içinizden henüz ergenlik çağına girmemiş olanlar, sabah namazından önce, öğleyin soyunduğunuz vakit ve yatsı namazından sonra (yanınıza gireceklerinde) sizden üç defa izin istesinler. Bunlar, mahrem (kapanmamış) halde bulunabileceğiniz üç vakittir. Bu vakitlerin dışında ne sizin için ne de onlar için bir mahzur yoktur. Birbirinizin yanına girip çıkabilirsiniz. İşte Allah âyetleri size böyle açıklar. Allah, (her şeyi) bilendir, hüküm ve hikmet sahibidir.', 18, 356, 64, 3875, 'Işık', 'Medine', 102),
(2850, 59, 24, 'Nur', 'وَاِذَا بَلَغَ الْاَطْفَالُ مِنْكُمُ الْحُلُمَ فَلْيَسْتَأْذِنُوا كَمَا اسْتَأْذَنَ الَّذ۪ينَ مِنْ قَبْلِهِمْۜ كَذٰلِكَ يُبَيِّنُ اللّٰهُ لَكُمْ اٰيَاتِه۪ۜ وَاللّٰهُ عَل۪يمٌ حَك۪يمٌ', 'Çocuklarınız ergenlik çağına girdiklerinde, kendilerinden öncekiler (büyükleri) izin istedikleri gibi onlar da izin istesinler. İşte Allah, âyetlerini size böyle açıklar. Allah alîmdir, hakîmdir.', 18, 357, 64, 3875, 'Işık', 'Medine', 102),
(2851, 60, 24, 'Nur', 'وَالْقَوَاعِدُ مِنَ النِّسَٓاءِ الّٰت۪ي لَا يَرْجُونَ نِكَاحاً فَلَيْسَ عَلَيْهِنَّ جُنَاحٌ اَنْ يَضَعْنَ ثِيَابَهُنَّ غَيْرَ مُتَبَرِّجَاتٍ بِز۪ينَةٍۜ وَاَنْ يَسْتَعْفِفْنَ خَيْرٌ لَهُنَّۜ وَاللّٰهُ سَم۪يعٌ عَل۪يمٌ', 'Bir nikâh ümidi beslemeyen, çocuktan kesilmiş yaşlı kadınların, zinetleri (yabancı erkeklere) teşhir etmeksizin (bazı) elbiselerini çıkarmalarında kendilerine bir vebal yoktur. İffetli davranmaları kendileri için daha hayırlıdır. Allah işitendir, bilendir.', 18, 357, 64, 3875, 'Işık', 'Medine', 102);
INSERT INTO `Ayetler` (`id`, `AyetNo`, `SureNo`, `SureAdi`, `Metin`, `Mana`, `Cuzu`, `Sayfasi`, `AyetSayisi`, `HarfSayisi`, `AdAnlami`, `IndigiYer`, `InisSirasi`) VALUES
(2852, 61, 24, 'Nur', 'لَيْسَ عَلَى الْاَعْمٰى حَرَجٌ وَلَا عَلَى الْاَعْرَجِ حَرَجٌ وَلَا عَلَى الْمَر۪يضِ حَرَجٌ وَلَا عَلٰٓى اَنْفُسِكُمْ اَنْ تَأْكُلُوا مِنْ بُيُوتِكُمْ اَوْ بُيُوتِ اٰبَٓائِكُمْ اَوْ بُيُوتِ اُمَّهَاتِكُمْ اَوْ بُيُوتِ اِخْوَانِكُمْ اَوْ بُيُوتِ اَخَوَاتِكُمْ اَوْ بُيُوتِ اَعْمَامِكُمْ اَوْ بُيُوتِ عَمَّاتِكُمْ اَوْ بُيُوتِ اَخْوَالِكُمْ اَوْ بُيُوتِ خَالَاتِكُمْ اَوْ مَا مَلَكْتُمْ مَفَاتِحَهُٓ اَوْ صَد۪يقِكُمْۜ لَيْسَ عَلَيْكُمْ جُنَاحٌ اَنْ تَأْكُلُوا جَم۪يعاً اَوْ اَشْتَاتاًۜ فَاِذَا دَخَلْتُمْ بُيُوتاً فَسَلِّمُوا عَلٰٓى اَنْفُسِكُمْ تَحِيَّةً مِنْ عِنْدِ اللّٰهِ مُبَارَكَةً طَيِّبَةًۜ كَذٰلِكَ يُبَيِّنُ اللّٰهُ لَكُمُ الْاٰيَاتِ لَعَلَّكُمْ تَعْقِلُونَ۟', 'Âmâya güçlük yoktur; topala güçlük yoktur; hastaya da güçlük yoktur. (Bunlara yapamayacakları görev yüklenmez; yapamadıklarından dolayı günahkâr olmazlar.) Sizin için de, gerek kendi evlerinizden, gerekse babalarınızın evlerinden, annelerinizin evlerinden, erkek kardeşlerinizin evlerinden, kız kardeşlerinizin evlerinden, amcalarınızın evlerinden, halalarınızın evlerinden, dayılarınızın evlerinden, teyzelerinizin evlerinden, veya anahtarlarını uhdenizde bulundurduğunuz yerlerden, yahut dostlarınızın evlerinden yemenizde bir sakınca yoktur. Toplu halde veya ayrı ayrı yemenizde de bir sakınca yoktur. Evlere girdiğiniz zaman, Allah tarafından mübarek ve pek güzel bir yaşama dileği olarak kendinize (birbirinize) selâm verin. İşte Allah, düşünüp anlayasınız diye size âyetleri böyle açıklar.', 18, 357, 64, 3875, 'Işık', 'Medine', 102),
(2853, 62, 24, 'Nur', 'اِنَّمَا الْمُؤْمِنُونَ الَّذ۪ينَ اٰمَنُوا بِاللّٰهِ وَرَسُولِه۪ وَاِذَا كَانُوا مَعَهُ عَلٰٓى اَمْرٍ جَامِعٍ لَمْ يَذْهَبُوا حَتّٰى يَسْتَأْذِنُوهُۜ اِنَّ الَّذ۪ينَ يَسْتَأْذِنُونَكَ اُو۬لٰٓئِكَ الَّذ۪ينَ يُؤْمِنُونَ بِاللّٰهِ وَرَسُولِه۪ۚ فَاِذَا اسْتَأْذَنُوكَ لِبَعْضِ شَأْنِهِمْ فَأْذَنْ لِمَنْ شِئْتَ مِنْهُمْ وَاسْتَغْفِرْ لَهُمُ اللّٰهَۜ اِنَّ اللّٰهَ غَفُورٌ رَح۪يمٌ', 'Müminler, ancak Allah´a ve Resûlüne gönülden inanmış kimselerdir. Onlar, o Peygamber ile ortak bir iş üzerindeyken ondan izin istemedikçe bırakıp gitmezler. (Resûlüm!) Şu senden izin isteyenler, hakikaten Allah´a ve Resûlüne iman etmiş kimselerdir. Öyle ise, bazı işleri için senden izin istediklerinde, sen de onlardan dilediğine izin ver; onlar için Allah´tan bağış dile; Allah mağfiret edicidir, merhametlidir.', 18, 358, 64, 3875, 'Işık', 'Medine', 102),
(2854, 63, 24, 'Nur', 'لَا تَجْعَلُوا دُعَٓاءَ الرَّسُولِ بَيْنَكُمْ كَدُعَٓاءِ بَعْضِكُمْ بَعْضاًۜ قَدْ يَعْلَمُ اللّٰهُ الَّذ۪ينَ يَتَسَلَّلُونَ مِنْكُمْ لِوَاذاًۚ فَلْيَحْذَرِ الَّذ۪ينَ يُخَالِفُونَ عَنْ اَمْرِه۪ٓ اَنْ تُص۪يبَهُمْ فِتْنَةٌ اَوْ يُص۪يبَهُمْ عَذَابٌ اَل۪يمٌ', '(Ey müminler!) Peygamber´i, kendi aranızda birbirinizi çağırır gibi çağırmayın. İçinizden, birini siper edinerek sıvışıp gidenleri muhakkak ki Allah bilmektedir. Bu sebeple, onun emrine aykırı davrananlar, başlarına bir belâ gelmesinden veya kendilerine çok elemli bir azap isabet etmesinden sakınsınlar.', 18, 358, 64, 3875, 'Işık', 'Medine', 102),
(2855, 64, 24, 'Nur', 'اَلَٓا اِنَّ لِلّٰهِ مَا فِي السَّمٰوَاتِ وَالْاَرْضِۜ قَدْ يَعْلَمُ مَٓا اَنْتُمْ عَلَيْهِۜ وَيَوْمَ يُرْجَعُونَ اِلَيْهِ فَيُنَبِّئُهُمْ بِمَا عَمِلُواۜ وَاللّٰهُ بِكُلِّ شَيْءٍ عَل۪يمٌ', 'Bilmiş olun ki, göklerde ve yerde ne varsa Allah´ındır. O, sizin ne yolda olduğunuzu iyi bilir. İnsanlar O´nun huzuruna döndürüldükleri gün yapmış olduklarını onlara hemen bildirir. Allah, her şeyi hakkıyla bilendir.', 18, 358, 64, 3875, 'Işık', 'Medine', 102),
(2856, 1, 25, 'Furkan', 'تَبَارَكَ الَّذ۪ي نَزَّلَ الْفُرْقَانَ عَلٰى عَبْدِه۪ لِيَكُونَ لِلْعَالَم۪ينَ نَذ۪يراًۙ', '(1-2) Âlemlere uyarıcı olsun diye kulu Muhammed´e Furkan´ı indiren, göklerin ve yerin hükümranlığı kendisine ait olan, hiç çocuk edinmeyen, mülkünde ortağı bulunmayan, her şeyi yaratıp ona bir nizam veren ve mukadderatını tayin eden Allah, yüceler yücesidir.', 18, 358, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2857, 2, 25, 'Furkan', 'اَلَّذ۪ي لَهُ مُلْكُ السَّمٰوَاتِ وَالْاَرْضِ وَلَمْ يَتَّخِذْ وَلَداً وَلَمْ يَكُنْ لَهُ شَر۪يكٌ فِي الْمُلْكِ وَخَلَقَ كُلَّ شَيْءٍ فَقَدَّرَهُ تَقْد۪يراً', '(1-2) Âlemlere uyarıcı olsun diye kulu Muhammed´e Furkan´ı indiren, göklerin ve yerin hükümranlığı kendisine ait olan, hiç çocuk edinmeyen, mülkünde ortağı bulunmayan, her şeyi yaratıp ona bir nizam veren ve mukadderatını tayin eden Allah, yüceler yücesidir.', 18, 358, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2858, 3, 25, 'Furkan', 'وَاتَّخَذُوا مِنْ دُونِه۪ٓ اٰلِهَةً لَا يَخْلُقُونَ شَيْـٔاً وَهُمْ يُخْلَقُونَ وَلَا يَمْلِكُونَ لِاَنْفُسِهِمْ ضَراًّ وَلَا نَفْعاً وَلَا يَمْلِكُونَ مَوْتاً وَلَا حَيٰوةً وَلَا نُشُوراً', '(Kâfirler) O´nu (Allah´ı) bırakıp, hiçbir şey yaratamayan, bilakis kendileri yaratılmış olan, kendilerine bile ne zarar ne de fayda verebilen, öldürmeye, hayat vermeye ve ölüleri yeniden diriltip kabirden çıkarmaya güçleri yetmeyen tanrılar edindiler.', 18, 359, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2859, 4, 25, 'Furkan', 'وَقَالَ الَّذ۪ينَ كَفَرُٓوا اِنْ هٰذَٓا اِلَّٓا اِفْكٌۨ افْتَرٰيهُ وَاَعَانَهُ عَلَيْهِ قَوْمٌ اٰخَرُونَۚۛ فَقَدْ جَٓاؤُ۫ ظُلْماً وَزُوراًۚۛ', 'İnkâr edenler: Bu (Kur´an), olsa olsa onun (Muhammed´in) uydurduğu bir yalandır. Başka bir zümre de bu hususta kendisine yardım etmiştir, dediler. Böylece onlar hiç şüphesiz haksızlığa ve iftiraya başvurmuşlardır.', 18, 359, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2860, 5, 25, 'Furkan', 'وَقَالُٓوا اَسَاط۪يرُ الْاَوَّل۪ينَ اكْتَتَبَهَا فَهِيَ تُمْلٰى عَلَيْهِ بُكْرَةً وَاَص۪يلاً', 'Yine onlar dediler ki: (Bu âyetler), onun, başkasına yazdırıp da kendisine sabah akşam okunmakta olan, öncekilere ait masallardır.', 18, 359, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2861, 6, 25, 'Furkan', 'قُلْ اَنْزَلَهُ الَّذ۪ي يَعْلَمُ السِّرَّ فِي السَّمٰوَاتِ وَالْاَرْضِۜ اِنَّهُ كَانَ غَفُوراً رَح۪يماً', '(Resûlüm!) De ki: Onu göklerde ve yerdeki gizlilikleri bilen Allah indirdi. Şüphesiz O, çok bağışlayıcıdır, engin merhamet sahibidir.', 18, 359, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2862, 7, 25, 'Furkan', 'وَقَالُوا مَالِ هٰذَا الرَّسُولِ يَأْكُلُ الطَّعَامَ وَيَمْش۪ي فِي الْاَسْوَاقِۜ لَوْلَٓا اُنْزِلَ اِلَيْهِ مَلَكٌ فَيَكُونَ مَعَهُ نَذ۪يراًۙ', 'Onlar (bir de) şöyle dediler: Bu ne biçim peygamber; (bizler gibi) yemek yiyor, çarşılarda dolaşıyor! Ona bir melek indirilmeli, kendisiyle birlikte o da uyarıcı olmalıydı!', 18, 359, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2863, 8, 25, 'Furkan', 'اَوْ يُلْقٰٓى اِلَيْهِ كَنْزٌ اَوْ تَكُونُ لَهُ جَنَّةٌ يَأْكُلُ مِنْهَاۜ وَقَالَ الظَّالِمُونَ اِنْ تَتَّبِعُونَ اِلَّا رَجُلاً مَسْحُوراً', 'Yahut kendisine bir hazine verilmeli veya içinden yeyip (meşakkatsizce geçimini sağlayacağı) bir bahçesi olmalıydı. (Ayrıca) o zalimler (müminlere): Siz, ancak büyüye tutulmuş bir adama uymaktasınız! dediler.', 18, 359, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2864, 9, 25, 'Furkan', 'اُنْظُرْ كَيْفَ ضَرَبُوا لَكَ الْاَمْثَالَ فَضَلُّوا فَلَا يَسْتَط۪يعُونَ سَب۪يلاً۟', '(Resûlüm!) Senin hakkında bak ne biçim temsiller getirdiler! Artık onlar sapmışlardır ve (hidayete) hiçbir yol da bulamazlar.', 18, 359, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2865, 10, 25, 'Furkan', 'تَبَارَكَ الَّـذ۪ٓي اِنْ شَٓاءَ جَعَلَ لَكَ خَيْراً مِنْ ذٰلِكَ جَنَّاتٍ تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُۙ وَيَجْعَلْ لَكَ قُصُوراً', 'Dilerse sana bunlardan daha iyisini, altlarından ırmaklar akan cennetleri verecek ve sana saraylar ihsan edecek olan Allah´ın şanı yücedir.', 18, 359, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2866, 11, 25, 'Furkan', 'بَلْ كَذَّبُوا بِالسَّاعَةِ وَاَعْتَدْنَا لِمَنْ كَذَّبَ بِالسَّاعَةِ سَع۪يراًۚ', 'Onlar üstelik kıyameti de yalan saydılar. Biz ise, kıyameti inkâr edenler için alevli bir ateş hazırladık.', 18, 359, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2867, 12, 25, 'Furkan', 'اِذَا رَاَتْهُمْ مِنْ مَكَانٍ بَع۪يدٍ سَمِعُوا لَهَا تَغَيُّظاً وَزَف۪يراً', 'Cehennem ateşi uzak bir mesafeden kendilerini görünce, onun öfkelenişini (müthiş kaynamasını) ve uğultusunu işitirler.', 18, 360, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2868, 13, 25, 'Furkan', 'وَاِذَٓا اُلْقُوا مِنْهَا مَكَاناً ضَيِّقاً مُقَرَّن۪ينَ دَعَوْا هُنَالِكَ ثُبُوراًۜ', 'Elleri boyunlarına bağlı olarak onun (cehennemin) dar bir yerine atıldıkları zaman, oracıkta yok oluvermeyi isterler.', 18, 360, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2869, 14, 25, 'Furkan', 'لَا تَدْعُوا الْيَوْمَ ثُبُوراً وَاحِداً وَادْعُوا ثُبُوراً كَث۪يراً', '(Onlara şöyle denir:) Bugün (yalnız) bir defa yok olmayı istemeyin; aksine birçok defalar yok olmayı isteyin!', 18, 360, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2870, 15, 25, 'Furkan', 'قُلْ اَذٰلِكَ خَيْرٌ اَمْ جَنَّةُ الْخُلْدِ الَّت۪ي وُعِدَ الْمُتَّقُونَۜ كَانَتْ لَهُمْ جَزَٓاءً وَمَص۪يراً', 'De ki: Bu mu daha iyi, yoksa takvâ sahiplerine vâdedilen ebedilik cenneti mi? Orası, onlar için bir mükâfat ve (huzura kavuşacakları) bir varış yeridir.', 18, 360, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2871, 16, 25, 'Furkan', 'لَهُمْ ف۪يهَا مَا يَشَٓاؤُ۫نَ خَالِد۪ينَۜ كَانَ عَلٰى رَبِّكَ وَعْداً مَسْؤُ۫لاً', 'Onlar için orada ebedî kalmak üzere diledikleri her şey vardır. İşte bu, Rabbinin üzerine (aldığı ve yerine getirilmesi) istenen bir vaaddir.', 18, 360, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2872, 17, 25, 'Furkan', 'وَيَوْمَ يَحْشُرُهُمْ وَمَا يَعْبُدُونَ مِنْ دُونِ اللّٰهِ فَيَقُولُ ءَاَنْتُمْ اَضْلَلْتُمْ عِبَاد۪ي هٰٓؤُ۬لَٓاءِ اَمْ هُمْ ضَلُّوا السَّب۪يلَۜ', 'O gün Rabbin onları ve Allah´tan başka taptıkları şeyleri toplar da, der ki: Şu kullarımı siz mi saptırdınız, yoksa kendileri mi yoldan çıktılar?', 18, 360, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2873, 18, 25, 'Furkan', 'قَالُوا سُبْحَانَكَ مَا كَانَ يَنْبَغ۪ي لَـنَٓا اَنْ نَتَّخِذَ مِنْ دُونِكَ مِنْ اَوْلِيَٓاءَ وَلٰكِنْ مَتَّعْتَهُمْ وَاٰبَٓاءَهُمْ حَتّٰى نَسُوا الذِّكْرَۚ وَكَانُوا قَوْماً بُوراً', 'Onlar: Seni tenzih ederiz. Seni bırakıp da başka dostlar edinmek bize yaraşmaz; fakat sen onlara ve atalarına o kadar bol nimet verdin ki, sonunda (seni) anmayı unuttular ve helâki hak eden bir kavim oldular, derler.', 18, 360, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2874, 19, 25, 'Furkan', 'فَقَدْ كَذَّبُوكُمْ بِمَا تَقُولُونَۙ فَمَا تَسْتَط۪يعُونَ صَرْفاً وَلَا نَصْراًۚ وَمَنْ يَظْلِمْ مِنْكُمْ نُذِقْهُ عَذَاباً كَب۪يراً', '(Bunun üzerine ötekilere hitaben şöyle denir:) İşte (taptıklarınız), söylediklerinizde sizi yalancı çıkardılar. Artık ne (azabınızı) geri çevirebilir, ne de bir yardım temin edebilirsiniz. İçinizden zulmedenlere büyük bir azap tattıracağız!', 18, 360, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2875, 20, 25, 'Furkan', 'وَمَٓا اَرْسَلْنَا قَبْلَكَ مِنَ الْمُرْسَل۪ينَ اِلَّٓا اِنَّهُمْ لَيَأْكُلُونَ الطَّعَامَ وَيَمْشُونَ فِي الْاَسْوَاقِۜ وَجَعَلْنَا بَعْضَكُمْ لِبَعْضٍ فِتْنَةًۜ اَتَصْبِرُونَۚ وَكَانَ رَبُّكَ بَص۪يراً۟', '(Resûlüm!) Senden önce gönderdiğimiz bütün peygamberler de hiç şüphesiz yemek yerler, çarşılarda dolaşırlardı. (Ey insanlar!) Sizin bir kısmınızı diğer bir kısmınıza imtihan (vesilesi) kıldık; (bakalım) sabredecek misiniz? Rabbin her şeyi hakkıyla görmektedir.', 18, 360, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2876, 21, 25, 'Furkan', 'وَقَالَ الَّذ۪ينَ لَا يَرْجُونَ لِقَٓاءَنَا لَوْلَٓا اُنْزِلَ عَلَيْنَا الْمَلٰٓئِكَةُ اَوْ نَرٰى رَبَّـنَاۜ لَقَدِ اسْتَكْبَرُوا ف۪ٓي اَنْفُسِهِمْ وَعَتَوْ عُتُواًّ كَب۪يراً', 'Bizimle karşılaşmayı (bir gün huzurumuza geleceklerini) ummayanlar: Bize ya melekler indirilmeliydi ya da Rabbimizi görmeliydik, dediler. Andolsun ki onlar kendileri hakkında kibire kapılmışlar ve azgınlıkta pek ileri gitmişlerdir.', 19, 361, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2877, 22, 25, 'Furkan', 'يَوْمَ يَرَوْنَ الْمَلٰٓئِكَةَ لَا بُشْرٰى يَوْمَئِذٍ لِلْمُجْرِم۪ينَ وَيَقُولُونَ حِجْراً مَحْجُوراً', '(Fakat) melekleri görecekleri gün, günahkârlara o gün hiçbir sevinç haberi yoktur ve: (Size, sevinmek) yasaktır, yasak! diyeceklerdir.', 19, 361, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2878, 23, 25, 'Furkan', 'وَقَدِمْنَٓا اِلٰى مَا عَمِلُوا مِنْ عَمَلٍ فَجَعَلْنَاهُ هَبَٓاءً مَنْثُوراً', 'Onların yaptıkları her bir (iyi) işi ele alırız, onu saçılmış zerreler haline getiririz (değersiz kılarız).', 19, 361, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2879, 24, 25, 'Furkan', 'اَصْحَابُ الْجَنَّةِ يَوْمَئِذٍ خَيْرٌ مُسْتَقَراًّ وَاَحْسَنُ مَق۪يلاً', 'O gün cennetliklerin kalacakları yer çok huzurlu ve dinlenecekleri yer pek güzeldir.', 19, 361, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2880, 25, 25, 'Furkan', 'وَيَوْمَ تَشَقَّقُ السَّمَٓاءُ بِالْغَمَامِ وَنُزِّلَ الْمَلٰٓئِكَةُ تَنْز۪يلاً', 'O gün gökyüzü beyaz bulutlar ile yarılacak ve melekler bölük bölük indirileceklerdir.', 19, 361, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2881, 26, 25, 'Furkan', 'اَلْمُلْكُ يَوْمَئِذٍۨ الْحَقُّ لِلرَّحْمٰنِۜ وَكَانَ يَوْماً عَلَى الْكَافِر۪ينَ عَس۪يراً', 'İşte o gün, gerçek mülk (hükümranlık) çok merhametli olan Allah´ındır. Kâfirler için de pek çetin bir gündür o.', 19, 361, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2882, 27, 25, 'Furkan', 'وَيَوْمَ يَعَضُّ الظَّالِمُ عَلٰى يَدَيْهِ يَقُولُ يَا لَيْتَنِي اتَّخَذْتُ مَعَ الرَّسُولِ سَب۪يلاً', 'O gün, zalim kimse (pişmanlıktan) ellerini ısırıp şöyle der: Keşke o peygamberle birlikte bir yol tutsaydım!', 19, 361, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2883, 28, 25, 'Furkan', 'يَا وَيْلَتٰى لَيْتَن۪ي لَمْ اَتَّخِذْ فُلَاناً خَل۪يلاً', 'Yazık bana! Keşke falancayı (bâtıl yolcusunu) dost edinmeseydim!', 19, 361, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2884, 29, 25, 'Furkan', 'لَقَدْ اَضَلَّن۪ي عَنِ الذِّكْرِ بَعْدَ اِذْ جَٓاءَن۪يۜ وَكَانَ الشَّيْطَانُ لِلْاِنْسَانِ خَذُولاً', 'Çünkü zikir (Kur´an) bana gelmişken o, hakikaten beni ondan saptırdı. Şeytan insanı (uçuruma sürükleyip sonra) yüzüstü bırakıp rezil rüsvay eder.', 19, 361, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2885, 30, 25, 'Furkan', 'وَقَالَ الرَّسُولُ يَا رَبِّ اِنَّ قَوْمِي اتَّخَذُوا هٰذَا الْقُرْاٰنَ مَهْجُوراً', 'Peygamber der ki: Ey Rabbim! Kavmim bu Kur´an´ı büsbütün terkettiler.', 19, 361, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2886, 31, 25, 'Furkan', 'وَكَذٰلِكَ جَعَلْنَا لِكُلِّ نَبِيٍّ عَدُواًّ مِنَ الْمُجْرِم۪ينَۜ وَكَفٰى بِرَبِّكَ هَادِياً وَنَص۪يراً', '(Resûlüm!) İşte biz böylece her peygamber için suçlulardan düşmanlar peydâ ettik. Hidayet verici ve yardımcı olarak Rabbin yeter.', 19, 361, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2887, 32, 25, 'Furkan', 'وَقَالَ الَّذ۪ينَ كَفَرُوا لَوْلَا نُزِّلَ عَلَيْهِ الْقُرْاٰنُ جُمْلَةً وَاحِدَةًۚ كَذٰلِكَ لِنُثَبِّتَ بِه۪ فُؤٰادَكَ وَرَتَّلْنَاهُ تَرْت۪يلاً', 'İnkâr edenler: Kur´an ona bir defada topluca indirilmeli değil miydi? dediler. Biz onu senin kalbine iyice yerleştirmek için böyle yaptık (parça parça indirdik) ve onu tane tane (ayırarak) okuduk.', 19, 361, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2888, 33, 25, 'Furkan', 'وَلَا يَأْتُونَكَ بِمَثَلٍ اِلَّا جِئْنَاكَ بِالْحَقِّ وَاَحْسَنَ تَفْس۪يراًۜ', 'Onların sana getirdikleri hiçbir temsil yoktur ki, (onun karşılığında) sana doğrusunu ve daha açığını getirmeyelim.', 19, 362, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2889, 34, 25, 'Furkan', 'اَلَّذ۪ينَ يُحْشَرُونَ عَلٰى وُجُوهِهِمْ اِلٰى جَهَنَّمَۙ اُو۬لٰٓئِكَ شَرٌّ مَكَاناً وَاَضَلُّ سَب۪يلاً۟', 'Yüzükoyun cehenneme (sürülüp) toplanacak olanlar; işte onlar, yerleri en kötü, yolları en sapık olanlardır.', 19, 362, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2890, 35, 25, 'Furkan', 'وَلَقَدْ اٰتَيْنَا مُوسَى الْكِتَابَ وَجَعَلْنَا مَعَهُٓ اَخَاهُ هٰرُونَ وَز۪يراًۚ', 'Andolsun biz Musa´ya Kitab´ı verdik, kardeşi Harun´u da ona yardımcı yaptık.', 19, 362, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2891, 36, 25, 'Furkan', 'فَقُلْنَا اذْهَبَٓا اِلَى الْقَوْمِ الَّذ۪ينَ كَذَّبُوا بِاٰيَاتِنَاۜ فَدَمَّرْنَاهُمْ تَدْم۪يراًۜ', '«Âyetlerimizi yalan sayan kavme gidin» dedik. Sonunda, (yola gelmediklerinden) onları yerle bir ediverdik.', 19, 362, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2892, 37, 25, 'Furkan', 'وَقَوْمَ نُوحٍ لَمَّا كَذَّبُوا الرُّسُلَ اَغْرَقْنَاهُمْ وَجَعَلْنَاهُمْ لِلنَّاسِ اٰيَةًۜ وَاَعْتَدْنَا لِلظَّالِم۪ينَ عَـذَاباً اَل۪يماًۚ', 'Nuh kavmine gelince, peygamberleri yalancılıkla itham ettiklerinde onları, suda boğduk ve kendilerini insanlar için bir ibret yaptık. Zalimler için acıklı bir azap hazırladık.', 19, 362, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2893, 38, 25, 'Furkan', 'وَعَـاداً وَثَمُودَا۬ وَاَصْحَابَ الرَّسِّ وَقُرُوناً بَيْنَ ذٰلِكَ كَث۪يراً', 'Âd´ı, Semûd´u, Ress halkını ve bunlar arasında daha birçok nesilleri de (inkârcılıklarından ötürü helâk ettik).', 19, 362, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2894, 39, 25, 'Furkan', 'وَكُلاًّ ضَرَبْنَا لَهُ الْاَمْثَالَۘ وَكُلاًّ تَبَّرْنَا تَتْب۪يراً', 'Onların her birine (uymaları için) misaller getirdik; (ama öğüt almadıkları için) hepsini kırdık geçirdik.', 19, 362, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2895, 40, 25, 'Furkan', 'وَلَقَدْ اَتَوْا عَلَى الْقَرْيَةِ الَّت۪ٓي اُمْطِرَتْ مَطَرَ السَّوْءِۜ اَفَلَمْ يَكُونُوا يَرَوْنَهَاۚ بَلْ كَانُوا لَا يَرْجُونَ نُشُوراً', '(Resûlüm!) Andolsun (bu Mekkeli putperestler), belâ ve felâket yağmuruna tutulmuş olan o beldeye uğramışlardır. Peki onu görmüyorlar mıydı? Hayır, onlar öldükten sonra dirilmeyi ummamaktadırlar.', 19, 362, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2896, 41, 25, 'Furkan', 'وَاِذَا رَاَوْكَ اِنْ يَتَّخِذُونَكَ اِلَّا هُزُواًۜ اَهٰذَا الَّذ۪ي بَعَثَ اللّٰهُ رَسُولاً', 'Seni gördükleri zaman: «Bu mu Allah´ın peygamber olarak gönderdiği!» diyerek hep seni alaya alıyorlar.', 19, 362, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2897, 42, 25, 'Furkan', 'اِنْ كَادَ لَيُضِلُّنَا عَنْ اٰلِهَتِنَا لَوْلَٓا اَنْ صَبَرْنَا عَلَيْهَاۜ وَسَوْفَ يَعْلَمُونَ ح۪ينَ يَرَوْنَ الْعَذَابَ مَنْ اَضَلُّ سَب۪يلاً', '«Şayet tanrılarımıza inanmakta sebat göstermeseydik, gerçekten bizi neredeyse tanrılarımızdan saptıracaktı» diyorlar. Azabı gördükleri zaman, asıl kimin yolunun sapık olduğunu bilecekler!', 19, 362, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2898, 43, 25, 'Furkan', 'اَرَاَيْتَ مَنِ اتَّخَذَ اِلٰهَهُ هَوٰيهُۜ اَفَاَنْتَ تَكُونُ عَلَيْهِ وَك۪يلاًۙ', 'Kötü duygularını kendisine tanrı edinen kimseyi gördün mü? Sen (Resûlüm!) ona koruyucu olabilir misin?', 19, 362, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2899, 44, 25, 'Furkan', 'اَمْ تَحْسَبُ اَنَّ اَكْثَرَهُمْ يَسْمَعُونَ اَوْ يَعْقِلُونَۜ اِنْ هُمْ اِلَّا كَالْاَنْـعَامِ بَلْ هُمْ اَضَلُّ سَب۪يلاً۟', 'Yoksa sen, onların çoğunun gerçekten (söz) dinleyeceğini yahut düşüneceğini mi sanıyorsun? Hayır, onlar hayvanlar gibidir, hatta onlar yolca daha da sapıktırlar.', 19, 363, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2900, 45, 25, 'Furkan', 'اَلَمْ تَرَ اِلٰى رَبِّكَ كَيْفَ مَدَّ الظِّلَّۚ وَلَوْ شَٓاءَ لَجَعَلَهُ سَاكِناًۚ ثُمَّ جَعَلْنَا الشَّمْسَ عَلَيْهِ دَل۪يلاًۙ', 'Rabbinin gölgeyi nasıl uzattığını görmedin mi? Eğer dileseydi, onu elbet hareketsiz kılardı. Sonra biz güneşi, ona delil kıldık.', 19, 363, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2901, 46, 25, 'Furkan', 'ثُمَّ قَبَضْنَاهُ اِلَيْنَا قَبْضاً يَس۪يراً', 'Sonra onu (uzayan gölgeyi) yavaş yavaş kendimize çektik (kısalttık).', 19, 363, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2902, 47, 25, 'Furkan', 'وَهُوَ الَّذ۪ي جَعَلَ لَكُمُ الَّيْلَ لِبَاساً وَالنَّوْمَ سُبَاتاً وَجَعَلَ النَّهَارَ نُشُوراً', 'Sizin için geceyi örtü, uykuyu istirahat kılan, gündüzü de dağılıp çalışma (zamanı) yapan, O´dur.', 19, 363, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2903, 48, 25, 'Furkan', 'وَهُوَ الَّـذ۪ٓي اَرْسَلَ الرِّيَاحَ بُشْراً بَيْنَ يَدَيْ رَحْمَتِه۪ۚ وَاَنْزَلْنَا مِنَ السَّمَٓاءِ مَٓاءً طَهُوراًۙ', '(48-49) Rüzgârları rahmetinin önünde müjdeci olarak gönderen O´dur. Biz, ölü toprağa can vermek, yarattığımız nice hayvanlara ve nice insanlara su vermek için gökten tertemiz su indirdik.', 19, 363, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2904, 49, 25, 'Furkan', 'لِنُحْيِيَ بِه۪ بَلْدَةً مَيْتاً وَنُسْقِيَهُ مِمَّا خَلَقْنَٓا اَنْعَاماً وَاَنَاسِيَّ كَث۪يراً', '(48-49) Rüzgârları rahmetinin önünde müjdeci olarak gönderen O´dur. Biz, ölü toprağa can vermek, yarattığımız nice hayvanlara ve nice insanlara su vermek için gökten tertemiz su indirdik.', 19, 363, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2905, 50, 25, 'Furkan', 'وَلَقَدْ صَرَّفْنَاهُ بَيْنَهُمْ لِيَذَّكَّرُواۘ فَاَبٰٓى اَكْثَرُ النَّاسِ اِلَّا كُفُوراً', 'Andolsun bunu, insanların öğüt almaları için, aralarında çeşitli şekillerde anlatmışızdır; ama insanların çoğu ille nankörlük edip diretmiştir.', 19, 363, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2906, 51, 25, 'Furkan', 'وَلَوْ شِئْنَا لَبَعَثْنَا ف۪ي كُلِّ قَرْيَةٍ نَذ۪يراًۘ', '(Resûlüm!) Şayet dileseydik, elbet her ülkeye bir uyarıcı (peygamber) gönderirdik.', 19, 363, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2907, 52, 25, 'Furkan', 'فَلَا تُطِعِ الْكَافِر۪ينَ وَجَاهِدْهُمْ بِه۪ جِهَاداً كَب۪يراً', '(Fakat evrensel uyarıcılık görevini sana verdik.) O halde, kâfirlere boyun eğme ve bununla (Kur´an ile) onlara karşı olanca gücünle büyük bir savaş ver!', 19, 363, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2908, 53, 25, 'Furkan', 'وَهُوَ الَّذ۪ي مَرَجَ الْبَحْرَيْنِ هٰذَا عَذْبٌ فُرَاتٌ وَهٰذَا مِلْحٌ اُجَاجٌۚ وَجَعَلَ بَيْنَهُمَا بَرْزَخاً وَحِجْراً مَحْجُوراً', 'Birinin suyu tatlı ve susuzluğu giderici, diğerininki tuzlu ve acı iki denizi salıveren ve aralarına bir engel, aşılmaz bir sınır koyan O´dur.', 19, 363, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2909, 54, 25, 'Furkan', 'وَهُوَ الَّذ۪ي خَلَقَ مِنَ الْمَٓاءِ بَشَراً فَجَعَلَهُ نَسَباً وَصِهْراًۜ وَكَانَ رَبُّكَ قَد۪يراً', 'Sudan (meniden) bir insan yaratıp onu nesep ve sıhriyet (kan ve evlilik bağından doğan) yakınlığa dönüştüren O´dur. Rabbinin her şeye gücü yeter.', 19, 363, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2910, 55, 25, 'Furkan', 'وَيَعْبُدُونَ مِنْ دُونِ اللّٰهِ مَا لَا يَنْفَعُهُمْ وَلَا يَضُرُّهُمْۜ وَكَانَ الْكَافِرُ عَلٰى رَبِّه۪ ظَه۪يراً', '(Böyle iken inkârcılar) Allah´ı bırakıp kendilerine ne fayda ne de zarar verebilen şeylere kulluk ediyorlar. İnkârcı da Rabbine karşı uğraşıp durmaktadır.', 19, 363, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2911, 56, 25, 'Furkan', 'وَمَٓا اَرْسَلْنَاكَ اِلَّا مُبَشِّراً وَنَذ۪يراً', '(Resûlüm!) Biz seni ancak müjdeleyici ve uyarıcı olarak gönderdik.', 19, 364, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2912, 57, 25, 'Furkan', 'قُلْ مَٓا اَسْـَٔلُكُمْ عَلَيْهِ مِنْ اَجْرٍ اِلَّا مَنْ شَٓاءَ اَنْ يَتَّخِذَ اِلٰى رَبِّه۪ سَب۪يلاً', 'De ki: Buna karşılık, sizden, Rabbine doğru bir yol tutmayı dileyen kimseler (olmanız) dışında herhangi bir ücret istemiyorum.', 19, 364, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2913, 58, 25, 'Furkan', 'وَتَوَكَّلْ عَلَى الْحَيِّ الَّذ۪ي لَا يَمُوتُ وَسَبِّـحْ بِحَمْدِه۪ۜ وَكَفٰى بِه۪ بِذُنُوبِ عِبَادِه۪ خَب۪يراًۚۛ', 'Ölümsüz ve daima diri olan Allah´a güvenip dayan. O´nu hamd ile tesbih et. Kullarının günahlarını O´nun bilmesi yeter.', 19, 364, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2914, 59, 25, 'Furkan', 'اَلَّذ۪ي خَلَقَ السَّمٰوَاتِ وَالْاَرْضَ وَمَا بَيْنَهُمَا ف۪ي سِتَّةِ اَيَّامٍ ثُمَّ اسْتَوٰى عَلَى الْعَرْشِۚۛ اَلرَّحْمٰنُ فَسْـَٔلْ بِه۪ خَب۪يراً', 'Gökleri, yeri ve ikisinin arasındakileri altı günde yaratan, sonra Arş´a istivâ eden (ona hükmeden) Rahmân´dır. Bunu bir bilene sor.', 19, 364, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2915, 60, 25, 'Furkan', 'وَاِذَا ق۪يلَ لَهُمُ اسْجُدُوا لِلرَّحْمٰنِ قَالُوا وَمَا الرَّحْمٰنُۗ اَنَسْجُدُ لِمَا تَأْمُرُنَا وَزَادَهُمْ نُفُوراً۟ ۩', 'Onlara: Rahmân´a secde edin! denildiği zaman: «Rahmân da neymiş! Bize emrettiğin şeye secde eder miyiz hiç!» derler ve bu emir onların nefretini arttırır.', 19, 364, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2916, 61, 25, 'Furkan', 'تَبَارَكَ الَّذ۪ي جَعَلَ فِي السَّمَٓاءِ بُرُوجاً وَجَعَلَ ف۪يهَا سِرَاجاً وَقَمَراً مُن۪يراً', 'Gökte burçları var eden, onların içinde bir çerağ (güneş) ve nurlu bir ay barındıran Allah, yüceler yücesidir.', 19, 364, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2917, 62, 25, 'Furkan', 'وَهُوَ الَّذ۪ي جَعَلَ الَّيْلَ وَالنَّهَارَ خِلْفَةً لِمَنْ اَرَادَ اَنْ يَذَّكَّرَ اَوْ اَرَادَ شُكُوراً', 'İbret almak veya şükretmek dileyen kimseler için gece ile gündüzü birbiri ardınca getiren de O´dur.', 19, 364, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2918, 63, 25, 'Furkan', 'وَعِبَادُ الرَّحْمٰنِ الَّذ۪ينَ يَمْشُونَ عَلَى الْاَرْضِ هَوْناً وَاِذَا خَاطَبَهُمُ الْجَاهِلُونَ قَالُوا سَلَاماً', 'Rahmân´ın (has) kulları onlardır ki, yeryüzünde tevazu ile yürürler ve kendini bilmez kimseler onlara laf attığında (incitmeksizin) «Selam!» derler (geçerler);', 19, 364, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2919, 64, 25, 'Furkan', 'وَالَّذ۪ينَ يَب۪يتُونَ لِرَبِّهِمْ سُجَّداً وَقِيَاماً', 'Gecelerini Rablerine secde ederek ve kıyam durarak geçirirler.', 19, 364, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2920, 65, 25, 'Furkan', 'وَالَّذ۪ينَ يَقُولُونَ رَبَّـنَا اصْرِفْ عَنَّا عَذَابَ جَهَنَّمَۗ اِنَّ عَذَابَهَا كَانَ غَرَاماًۗ', 'Ve şöyle derler: Rabbimiz! Cehennem azabını üzerimizden sav. Doğrusu onun azabı gelip geçici değil, devamlıdır.', 19, 364, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2921, 66, 25, 'Furkan', 'اِنَّهَا سَٓاءَتْ مُسْتَقَراًّ وَمُقَاماً', 'Orası cidden ne kötü bir yerleşme ve ikamet yeridir!', 19, 364, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2922, 67, 25, 'Furkan', 'وَالَّذ۪ينَ اِذَٓا اَنْفَقُوا لَمْ يُسْرِفُوا وَلَمْ يَقْتُرُوا وَكَانَ بَيْنَ ذٰلِكَ قَوَاماً', '(O kullar), harcadıklarında ne israf ne de cimrilik ederler; ikisi arasında orta bir yol tutarlar.', 19, 364, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2923, 68, 25, 'Furkan', 'وَالَّذ۪ينَ لَا يَدْعُونَ مَعَ اللّٰهِ اِلٰهاً اٰخَرَ وَلَا يَقْتُلُونَ النَّفْسَ الَّت۪ي حَرَّمَ اللّٰهُ اِلَّا بِالْحَقِّ وَلَا يَزْنُونَۚ وَمَنْ يَفْعَلْ ذٰلِكَ يَلْقَ اَثَاماًۙ', 'Yine onlar ki, Allah ile beraber (tuttukları) başka bir tanrıya yalvarmazlar, Allah´ın haram kıldığı cana haksız yere kıymazlar ve zina etmezler. Bunları yapan, günahı(nın cezasını) bulur;', 19, 365, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2924, 69, 25, 'Furkan', 'يُضَاعَفْ لَهُ الْعَذَابُ يَوْمَ الْقِيٰمَةِ وَيَخْلُدْ ف۪يه۪۫ مُهَاناًۗ', 'Kıyamet günü azabı kat kat arttırılır ve onda (azapta) alçaltılmış olarak devamlı kalır.', 19, 365, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2925, 70, 25, 'Furkan', 'اِلَّا مَنْ تَابَ وَاٰمَنَ وَعَمِلَ عَمَلاً صَالِحاً فَاُو۬لٰٓئِكَ يُبَدِّلُ اللّٰهُ سَيِّـَٔاتِهِمْ حَسَنَاتٍۜ وَكَانَ اللّٰهُ غَفُوراً رَح۪يماً', 'Ancak tevbe ve iman edip iyi davranışta bulunanlar başkadır; Allah onların kötülüklerini iyiliklere çevirir. Allah çok bağışlayıcıdır, engin merhamet sahibidir.', 19, 365, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2926, 71, 25, 'Furkan', 'وَمَنْ تَابَ وَعَمِلَ صَالِحاً فَاِنَّهُ يَتُوبُ اِلَى اللّٰهِ مَتَاباً', 'Kim tevbe edip iyi davranış gösterirse, şüphesiz o, tevbesi kabul edilmiş olarak Allah´a döner.', 19, 365, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2927, 72, 25, 'Furkan', 'وَالَّذ۪ينَ لَا يَشْهَدُونَ الزُّورَۙ وَاِذَا مَرُّوا بِاللَّغْوِ مَرُّوا كِرَاماً', '(O kullar), yalan yere şahitlik etmezler, boş sözlerle karşılaştıklarında vakar ile (oradan) geçip giderler.', 19, 365, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2928, 73, 25, 'Furkan', 'وَالَّذ۪ينَ اِذَا ذُكِّرُوا بِاٰيَاتِ رَبِّهِمْ لَمْ يَخِرُّوا عَلَـيْـهَا صُـماًّ وَعُمْيَـاناً', 'Kendilerine Rablerinin âyetleri hatırlatıldığında ise, onlara karşı sağır ve kör davranmazlar;', 19, 365, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2929, 74, 25, 'Furkan', 'وَالَّذ۪ينَ يَقُولُونَ رَبَّـنَا هَبْ لَنَا مِنْ اَزْوَاجِنَا وَذُرِّيَّاتِنَا قُرَّةَ اَعْيُنٍ وَاجْعَلْنَا لِلْمُتَّق۪ينَ اِمَاماً', '(Ve o kullar): Rabbimiz! Bize gözümüzü aydınlatacak eşler ve zürriyetler bağışla ve bizi takvâ sahiplerine önder kıl! derler.', 19, 365, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2930, 75, 25, 'Furkan', 'اُو۬لٰٓئِكَ يُجْزَوْنَ الْغُرْفَةَ بِمَا صَبَرُوا وَيُلَقَّوْنَ ف۪يهَا تَحِيَّةً وَسَلَاماًۙ', 'İşte onlara, sabretmelerine karşılık cennetin en yüksek makamı verilecek, orada hürmet ve selamla karşılanacaklardır.', 19, 365, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2931, 76, 25, 'Furkan', 'خَالِد۪ينَ ف۪يهَاۜ حَسُنَتْ مُسْتَقَراًّ وَمُقَاماً', 'Orada ebedî kalacaklardır. Orası ne güzel bir yerleşme ve ikamet yeridir.', 19, 365, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2932, 77, 25, 'Furkan', 'قُلْ مَا يَعْبَؤُ۬ا بِكُمْ رَبّ۪ي لَوْلَا دُعَٓاؤُ۬كُمْۚ فَقَدْ كَذَّبْتُمْ فَسَوْفَ يَكُونُ لِزَاماً', '(Resûlüm!) De ki: (Kulluk ve) yalvarmanız olmasa, Rabbim size ne diye değer versin? (Ey inkârcılar! Size Resûl´ün bildirdiklerini) kesinkes yalan saydınız; onun için azap yakanızı bırakmayacaktır!', 19, 365, 77, 4034, 'Hak ile batılı birbirinden ayı', 'Mekke', 42),
(2933, 1, 26, 'Şu\'ara', 'طٰسٓمٓۜ', 'Tâ. Sîn. Mîm.', 19, 366, 227, 5529, 'Şairler', 'Mekke', 47),
(2934, 2, 26, 'Şu\'ara', 'تِلْكَ اٰيَاتُ الْكِتَابِ الْمُب۪ينِ', 'Bunlar, apaçık Kitab´ın âyetleridir.', 19, 366, 227, 5529, 'Şairler', 'Mekke', 47),
(2935, 3, 26, 'Şu\'ara', 'لَعَلَّكَ بَاخِعٌ نَفْسَكَ اَلَّا يَكُونُوا مُؤْمِن۪ينَ', '(Resûlüm!) Onlar iman etmiyorlar diye neredeyse kendine kıyacaksın!', 19, 366, 227, 5529, 'Şairler', 'Mekke', 47),
(2936, 4, 26, 'Şu\'ara', 'اِنْ نَشَأْ نُنَزِّلْ عَلَيْهِمْ مِنَ السَّمَٓاءِ اٰيَةً فَظَلَّتْ اَعْنَاقُهُمْ لَهَا خَاضِع۪ينَ', 'Biz dilesek, onların üzerine gökten bir mucize indiririz de, ona boyunları eğilip kalır.', 19, 366, 227, 5529, 'Şairler', 'Mekke', 47),
(2937, 5, 26, 'Şu\'ara', 'وَمَا يَأْت۪يهِمْ مِنْ ذِكْرٍ مِنَ الرَّحْمٰنِ مُحْدَثٍ اِلَّا كَانُوا عَنْهُ مُعْرِض۪ينَ', 'Kendilerine, o çok esirgeyici Allah´tan hiçbir yeni öğüt gelmez ki, ondan yüz çevirmesinler.', 19, 366, 227, 5529, 'Şairler', 'Mekke', 47),
(2938, 6, 26, 'Şu\'ara', 'فَقَدْ كَذَّبُوا فَسَيَأْت۪يهِمْ اَنْبٰٓؤُ۬ا مَا كَانُوا بِه۪ يَسْتَهْزِؤُ۫نَ', 'Üstelik (ona) «yalandır» derler; fakat alay edip durdukları şeylerin haberleri yakında onlara gelecektir.', 19, 366, 227, 5529, 'Şairler', 'Mekke', 47),
(2939, 7, 26, 'Şu\'ara', 'اَوَلَمْ يَرَوْا اِلَى الْاَرْضِ كَمْ اَنْبَتْنَا ف۪يهَا مِنْ كُلِّ زَوْجٍ كَر۪يمٍ', 'Yeryüzüne bir bakmazlar mı! Orada her güzel çiftten nice bitkiler yetiştirdik.', 19, 366, 227, 5529, 'Şairler', 'Mekke', 47),
(2940, 8, 26, 'Şu\'ara', 'اِنَّ ف۪ي ذٰلِكَ لَاٰيَةًۜ وَمَا كَانَ اَكْثَرُهُمْ مُؤْمِن۪ينَ', 'Şüphesiz bunlarda (Allah´ın kudretine) bir nişâne vardır; ama çoğu iman etmezler.', 19, 366, 227, 5529, 'Şairler', 'Mekke', 47),
(2941, 9, 26, 'Şu\'ara', 'وَاِنَّ رَبَّكَ لَهُوَ الْعَز۪يزُ الرَّح۪يمُ۟', 'Şüphe yok ki Rabbin, mutlak galip ve engin merhamet sahibidir.', 19, 366, 227, 5529, 'Şairler', 'Mekke', 47),
(2942, 10, 26, 'Şu\'ara', 'وَاِذْ نَادٰى رَبُّكَ مُوسٰٓى اَنِ ائْتِ الْقَوْمَ الظَّالِم۪ينَۙ', '(10-11) Hani Rabbin Musa´ya: O zalimler güruhuna, Firavun´un kavmine git. Hâla (başlarına gelecekten) sakınmayacaklar mı onlar? diye seslenmişti.', 19, 366, 227, 5529, 'Şairler', 'Mekke', 47),
(2943, 11, 26, 'Şu\'ara', 'قَوْمَ فِرْعَوْنَۜ اَلَا يَتَّقُونَ', '(10-11) Hani Rabbin Musa´ya: O zalimler güruhuna, Firavun´un kavmine git. Hâla (başlarına gelecekten) sakınmayacaklar mı onlar? diye seslenmişti.', 19, 366, 227, 5529, 'Şairler', 'Mekke', 47),
(2944, 12, 26, 'Şu\'ara', 'قَالَ رَبِّ اِنّ۪ٓي اَخَافُ اَنْ يُكَذِّبُونِۜ', 'Musa şöyle dedi: Rabbim! Doğrusu, beni yalancılıkla suçlamalarından korkuyorum.', 19, 366, 227, 5529, 'Şairler', 'Mekke', 47),
(2945, 13, 26, 'Şu\'ara', 'وَيَض۪يقُ صَدْر۪ي وَلَا يَنْطَلِقُ لِسَان۪ي فَاَرْسِلْ اِلٰى هٰرُونَ', '(Bu durumda) içim daralır, dilim dönmez; onun için Harun´a da elçilik ver.', 19, 366, 227, 5529, 'Şairler', 'Mekke', 47),
(2946, 14, 26, 'Şu\'ara', 'وَلَهُمْ عَلَيَّ ذَنْبٌ فَاَخَافُ اَنْ يَقْتُلُونِۚ', 'Onların bana isnad ettikleri bir suç da var. Bundan ötürü beni öldürmelerinden korkuyorum.', 19, 366, 227, 5529, 'Şairler', 'Mekke', 47),
(2947, 15, 26, 'Şu\'ara', 'قَالَ كَلَّاۚ فَاذْهَبَا بِاٰيَاتِنَٓا اِنَّا مَعَكُمْ مُسْتَمِعُونَ', 'Allah buyurdu: Hayır (seni asla öldüremezler)! İkiniz mucizelerimizle gidin. Şüphesiz ki, biz sizinle beraberiz, (her şeyi) işitmekteyiz.', 19, 366, 227, 5529, 'Şairler', 'Mekke', 47),
(2948, 16, 26, 'Şu\'ara', 'فَأْتِيَا فِرْعَوْنَ فَقُولَٓا اِنَّا رَسُولُ رَبِّ الْعَالَم۪ينَۙ', 'Haydi Firavun´a gidip deyin ki: Gerçekten biz, âlemlerin Rabbi´nin elçisiyiz;', 19, 366, 227, 5529, 'Şairler', 'Mekke', 47),
(2949, 17, 26, 'Şu\'ara', 'اَنْ اَرْسِلْ مَعَنَا بَن۪ٓي اِسْرَٓائ۪لَۜ', 'İsrailoğullarını bizimle beraber gönder.', 19, 366, 227, 5529, 'Şairler', 'Mekke', 47),
(2950, 18, 26, 'Şu\'ara', 'قَالَ اَلَمْ نُرَبِّكَ ف۪ينَا وَل۪يداً وَلَبِثْتَ ف۪ينَا مِنْ عُمُرِكَ سِن۪ينَ', '(Kendisine Allah´ın emri tebliğ edilince Firavun) dedi ki: Biz seni çocukken himayemize alıp büyütmedik mi? Hayatının birçok yıllarını aramızda geçirmedin mi?', 19, 366, 227, 5529, 'Şairler', 'Mekke', 47),
(2951, 19, 26, 'Şu\'ara', 'وَفَعَلْتَ فَعْلَتَكَ الَّت۪ي فَعَلْتَ وَاَنْتَ مِنَ الْكَافِر۪ينَ', 'Sonunda o yaptığın (kötü) işi de yaptın. Sen nankörün birisin!', 19, 366, 227, 5529, 'Şairler', 'Mekke', 47),
(2952, 20, 26, 'Şu\'ara', 'قَالَ فَعَلْتُـهَٓا اِذاً وَاَنَا۬ مِنَ الضَّٓالّ۪ينَۜ', 'Musa: Ben, dedi, o işi o anda sonunun ne olacağını bilmeyerek yaptım.', 19, 367, 227, 5529, 'Şairler', 'Mekke', 47),
(2953, 21, 26, 'Şu\'ara', 'فَفَرَرْتُ مِنْكُمْ لَمَّا خِفْتُكُمْ فَوَهَبَ ل۪ي رَبّ۪ي حُكْماً وَجَعَلَن۪ي مِنَ الْمُرْسَل۪ينَ', 'Sizden korkunca da hemen aranızdan kaçtım. Sonra Rabbim bana hikmet bahşetti ve beni peygamberlerden kıldı.', 19, 367, 227, 5529, 'Şairler', 'Mekke', 47),
(2954, 22, 26, 'Şu\'ara', 'وَتِلْكَ نِعْمَةٌ تَمُنُّهَا عَلَيَّ اَنْ عَبَّدْتَ بَن۪ٓي اِسْرَٓائ۪لَۜ', 'O nimet diye başıma kaktığın ise, (aslında) İsrailoğullarını kendine kul köle etmendir.', 19, 367, 227, 5529, 'Şairler', 'Mekke', 47),
(2955, 23, 26, 'Şu\'ara', 'قَالَ فِرْعَوْنُ وَمَا رَبُّ الْعَالَم۪ينَ', 'Firavun şöyle dedi: Âlemlerin Rabbi dediğin de nedir?', 19, 367, 227, 5529, 'Şairler', 'Mekke', 47),
(2956, 24, 26, 'Şu\'ara', 'قَالَ رَبُّ السَّمٰوَاتِ وَالْاَرْضِ وَمَا بَيْنَهُمَاۜ اِنْ كُنْتُمْ مُوقِن۪ينَ', 'Musa cevap verdi: Eğer işin gerçeğini düşünüp anlayan kişiler olsanız, (itiraf edersiniz ki) O, göklerin, yerin ve ikisi arasında bulunan her şeyin Rabbidir.', 19, 367, 227, 5529, 'Şairler', 'Mekke', 47),
(2957, 25, 26, 'Şu\'ara', 'قَالَ لِمَنْ حَوْلَـهُٓ اَلَا تَسْتَمِعُونَ', '(Firavun) etrafında bulunanlara: İşitiyor musunuz? dedi.', 19, 367, 227, 5529, 'Şairler', 'Mekke', 47),
(2958, 26, 26, 'Şu\'ara', 'قَالَ رَبُّكُمْ وَرَبُّ اٰبَٓائِكُمُ الْاَوَّل۪ينَ', 'Musa dedi ki: O, sizin de Rabbiniz, daha önceki atalarınızın da Rabbidir.', 19, 367, 227, 5529, 'Şairler', 'Mekke', 47),
(2959, 27, 26, 'Şu\'ara', 'قَالَ اِنَّ رَسُولَكُمُ الَّـذ۪ٓي اُرْسِلَ اِلَيْكُمْ لَمَجْنُونٌ', 'Firavun: Size gönderilen bu elçiniz mutlaka delidir, dedi.', 19, 367, 227, 5529, 'Şairler', 'Mekke', 47),
(2960, 28, 26, 'Şu\'ara', 'قَالَ رَبُّ الْمَشْرِقِ وَالْمَغْرِبِ وَمَا بَيْنَهُمَاۜ اِنْ كُنْتُمْ تَعْقِلُونَ', 'Musa devamla şunu söyledi: Şayet aklınızı kullansanız (anlarsınız ki), O, doğunun, batının ve ikisinin arasında bulunanların Rabbidir.', 19, 367, 227, 5529, 'Şairler', 'Mekke', 47),
(2961, 29, 26, 'Şu\'ara', 'قَالَ لَئِنِ اتَّخَذْتَ اِلٰهاً غَيْر۪ي لَاَجْعَلَنَّكَ مِنَ الْمَسْجُون۪ينَ', 'Firavun: Benden başkasını tanrı edinirsen, andolsun ki seni zindanlıklardan ederim! dedi.', 19, 367, 227, 5529, 'Şairler', 'Mekke', 47),
(2962, 30, 26, 'Şu\'ara', 'قَالَ اَوَلَوْ جِئْتُكَ بِشَيْءٍ مُب۪ينٍ', 'Musa: Sana apaçık bir şey getirmiş olsam da mı? dedi.', 19, 367, 227, 5529, 'Şairler', 'Mekke', 47),
(2963, 31, 26, 'Şu\'ara', 'قَالَ فَأْتِ بِه۪ٓ اِنْ كُنْتَ مِنَ الصَّادِق۪ينَ', 'Firavun: Doğru söyleyenlerden isen, haydi getir onu! diye karşılık verdi.', 19, 367, 227, 5529, 'Şairler', 'Mekke', 47),
(2964, 32, 26, 'Şu\'ara', 'فَاَلْقٰى عَصَاهُ فَاِذَا هِيَ ثُعْبَانٌ مُب۪ينٌۚ', 'Bunun üzerine Musa asâsını atıverdi; bir de ne görsünler, asâ apaçık koca bir yılan (oluvermiş)!', 19, 367, 227, 5529, 'Şairler', 'Mekke', 47),
(2965, 33, 26, 'Şu\'ara', 'وَنَزَعَ يَدَهُ فَاِذَا هِيَ بَيْضَٓاءُ لِلنَّاظِر۪ينَ۟', 'Elini de (koynundan) çıkardı; o da seyredenlere bembeyaz görünen (nur saçan bir şey oluvermiş)!', 19, 367, 227, 5529, 'Şairler', 'Mekke', 47),
(2966, 34, 26, 'Şu\'ara', 'قَالَ لِلْمَلَأِ حَوْلَـهُٓ اِنَّ هٰذَا لَسَاحِرٌ عَل۪يمٌۙ', 'Firavun, çevresindeki ileri gelenlere: Bu, dedi, doğrusu çok bilgili bir sihirbaz!', 19, 367, 227, 5529, 'Şairler', 'Mekke', 47),
(2967, 35, 26, 'Şu\'ara', 'يُر۪يدُ اَنْ يُخْرِجَكُمْ مِنْ اَرْضِكُمْ بِسِحْرِه۪ۗ فَمَاذَا تَأْمُرُونَ', 'Sizi sihiriyle yurdunuzdan çıkarmak istiyor. Şimdi ne buyurursunuz?', 19, 367, 227, 5529, 'Şairler', 'Mekke', 47),
(2968, 36, 26, 'Şu\'ara', 'قَالُٓوا اَرْجِهْ وَاَخَاهُ وَابْعَثْ فِي الْمَدَٓائِنِ حَاشِر۪ينَۙ', 'Dediler ki: Onu ve kardeşini eğle ve şehirlere toplayıcı görevliler gönder;', 19, 367, 227, 5529, 'Şairler', 'Mekke', 47),
(2969, 37, 26, 'Şu\'ara', 'يَأْتُوكَ بِكُلِّ سَحَّارٍ عَل۪يمٍ', 'Ne kadar bilgisi derin sihirbaz varsa sana getirsinler.', 19, 367, 227, 5529, 'Şairler', 'Mekke', 47),
(2970, 38, 26, 'Şu\'ara', 'فَجُمِعَ السَّحَرَةُ لِم۪يقَاتِ يَوْمٍ مَعْلُومٍۙ', 'Böylece sihirbazlar belli bir günün tayin edilen vaktinde biraraya getirildi.', 19, 367, 227, 5529, 'Şairler', 'Mekke', 47),
(2971, 39, 26, 'Şu\'ara', 'وَق۪يلَ لِلنَّاسِ هَلْ اَنْتُمْ مُجْتَمِعُونَۙ', 'Halka: Siz de toplanıyor musunuz (haydi hemen toplanın), denildi.', 19, 367, 227, 5529, 'Şairler', 'Mekke', 47),
(2972, 40, 26, 'Şu\'ara', 'لَعَلَّنَا نَتَّبِعُ السَّحَرَةَ اِنْ كَانُوا هُمُ الْغَالِب۪ينَ', '(Firavun´un adamları:) Eğer üstün gelirlerse, herhalde sihirbazlara uyarız, dediler.', 19, 368, 227, 5529, 'Şairler', 'Mekke', 47),
(2973, 41, 26, 'Şu\'ara', 'فَلَمَّا جَٓاءَ السَّحَرَةُ قَالُوا لِفِرْعَوْنَ اَئِنَّ لَنَا لَاَجْراً اِنْ كُنَّا نَحْنُ الْغَالِب۪ينَ', 'Sihirbazlar geldiklerinde Firavun´a: Şayet biz üstün gelirsek, muhakkak bize bir ücret vardır değil mi? dediler.', 19, 368, 227, 5529, 'Şairler', 'Mekke', 47),
(2974, 42, 26, 'Şu\'ara', 'قَالَ نَعَمْ وَاِنَّكُمْ اِذاً لَمِنَ الْمُقَرَّب۪ينَ', 'Firavun cevap verdi: Evet, o takdirde hiç şüphe etmeyin, gözde kimselerden de olacaksınız.', 19, 368, 227, 5529, 'Şairler', 'Mekke', 47),
(2975, 43, 26, 'Şu\'ara', 'قَالَ لَهُمْ مُوسٰٓى اَلْقُوا مَٓا اَنْتُمْ مُلْقُونَ', 'Musa onlara: Ne atacaksanız atın! dedi.', 19, 368, 227, 5529, 'Şairler', 'Mekke', 47),
(2976, 44, 26, 'Şu\'ara', 'فَاَلْقَوْا حِبَالَهُمْ وَعِصِيَّهُمْ وَقَالُوا بِعِزَّةِ فِرْعَوْنَ اِنَّا لَنَحْنُ الْغَالِبُونَ', 'Bunun üzerine iplerini ve değneklerini attılar ve: Firavun´un kudreti hakkı için elbette bizler galip geleceğiz, dediler.', 19, 368, 227, 5529, 'Şairler', 'Mekke', 47),
(2977, 45, 26, 'Şu\'ara', 'فَاَلْقٰى مُوسٰى عَصَاهُ فَاِذَا هِيَ تَلْقَفُ مَا يَأْفِكُونَۚ', 'Sonra Musa asâsını attı; bir de ne görsünler, onların uydurduklarını yutuveriyor!', 19, 368, 227, 5529, 'Şairler', 'Mekke', 47),
(2978, 46, 26, 'Şu\'ara', 'فَاُلْقِيَ السَّحَرَةُ سَاجِد۪ينَۙ', '(Bunu görünce) sihirbazlar derhal secdeye kapandılar.', 19, 368, 227, 5529, 'Şairler', 'Mekke', 47),
(2979, 47, 26, 'Şu\'ara', 'قَالُٓوا اٰمَنَّا بِرَبِّ الْعَالَم۪ينَۙ', '(47-48) «Âlemlerin Rabbine, Musa ve Harun´un Rabbine iman ettik» dediler.', 19, 368, 227, 5529, 'Şairler', 'Mekke', 47),
(2980, 48, 26, 'Şu\'ara', 'رَبِّ مُوسٰى وَهٰرُونَ', '(47-48) «Âlemlerin Rabbine, Musa ve Harun´un Rabbine iman ettik» dediler.', 19, 368, 227, 5529, 'Şairler', 'Mekke', 47),
(2981, 49, 26, 'Şu\'ara', 'قَالَ اٰمَنْتُمْ لَهُ قَبْلَ اَنْ اٰذَنَ لَكُمْۚ اِنَّهُ لَكَب۪يرُكُمُ الَّذ۪ي عَلَّمَكُمُ السِّحْرَۚ فَلَسَوْفَ تَعْلَمُونَۜ لَاُقَطِّعَنَّ اَيْدِيَكُمْ وَاَرْجُلَكُمْ مِنْ خِلَافٍ وَلَاُصَلِّبَنَّكُمْ اَجْمَع۪ينَ', 'Firavun, (kızgınlık içinde) dedi ki: Ben size izin vermeden ona iman ettiniz ha! Demek ki size sihiri öğreten büyüğünüzmüş o! Ama şimdi (size yapacağımı görecek ve) bileceksiniz: Andolsun, ellerinizi ve ayaklarınızı çaprazlama kestireceğim, hepinizi astıracağım!', 19, 368, 227, 5529, 'Şairler', 'Mekke', 47),
(2982, 50, 26, 'Şu\'ara', 'قَالُوا لَا ضَيْرَۘ اِنَّٓا اِلٰى رَبِّنَا مُنْقَلِبُونَۚ', '«Zararı yok, dediler, (nasıl olsa) biz şüphesiz Rabbimize döneceğiz.»', 19, 368, 227, 5529, 'Şairler', 'Mekke', 47),
(2983, 51, 26, 'Şu\'ara', 'اِنَّا نَطْمَعُ اَنْ يَغْفِرَ لَنَا رَبُّنَا خَطَايَانَٓا اَنْ كُنَّٓا اَوَّلَ الْمُؤْمِن۪ينَۜ۟', '«Biz, ilk iman edenler olduğumuz için Rabbimizin hatalarımızı bağışlayacağını umarız.»', 19, 368, 227, 5529, 'Şairler', 'Mekke', 47),
(2984, 52, 26, 'Şu\'ara', 'وَاَوْحَيْنَٓا اِلٰى مُوسٰٓى اَنْ اَسْرِ بِعِبَاد۪ٓي اِنَّكُمْ مُتَّبَعُونَ', 'Musa´ya: Kullarımı geceleyin yola çıkar; çünkü takip edileceksiniz, diye vahyettik.', 19, 368, 227, 5529, 'Şairler', 'Mekke', 47),
(2985, 53, 26, 'Şu\'ara', 'فَاَرْسَلَ فِرْعَوْنُ فِي الْمَدَٓائِنِ حَاشِر۪ينَۚ', 'Firavun da şehirlere (asker) toplayıcılar gönderdi:', 19, 368, 227, 5529, 'Şairler', 'Mekke', 47),
(2986, 54, 26, 'Şu\'ara', 'اِنَّ هٰٓؤُ۬لَٓاءِ لَشِرْذِمَةٌ قَل۪يلُونَۙ', '«Esasen bunlar, sayıları az, bölük pörçük bir cemaattır.»', 19, 368, 227, 5529, 'Şairler', 'Mekke', 47),
(2987, 55, 26, 'Şu\'ara', 'وَاِنَّهُمْ لَنَا لَـغَٓائِظُونَۙ', '«(Böyle iken) kesinkes bizi öfkelendirmişlerdir.»', 19, 368, 227, 5529, 'Şairler', 'Mekke', 47),
(2988, 56, 26, 'Şu\'ara', 'وَاِنَّا لَجَم۪يعٌ حَاذِرُونَۜ', '«Biz ise, elbette uyanık (ve yekvücut) bir cemaatız.» (diyor ve dedirtiyordu).', 19, 368, 227, 5529, 'Şairler', 'Mekke', 47),
(2989, 57, 26, 'Şu\'ara', 'فَاَخْرَجْنَاهُمْ مِنْ جَنَّاتٍ وَعُيُونٍۙ', '(57-58) Ama (sonunda) biz onları (Firavun ve kavmini), bahçelerden, pınarlardan, hazinelerden ve değerli bir yerden çıkardık.', 19, 368, 227, 5529, 'Şairler', 'Mekke', 47),
(2990, 58, 26, 'Şu\'ara', 'وَكُنُوزٍ وَمَقَامٍ كَر۪يمٍۙ', '(57-58) Ama (sonunda) biz onları (Firavun ve kavmini), bahçelerden, pınarlardan, hazinelerden ve değerli bir yerden çıkardık.', 19, 368, 227, 5529, 'Şairler', 'Mekke', 47),
(2991, 59, 26, 'Şu\'ara', 'كَذٰلِكَۜ وَاَوْرَثْنَاهَا بَن۪ٓي اِسْرَٓائ۪لَۚ', 'Böylece, bunlara İsrailoğullarını mirasçı yaptık.', 19, 368, 227, 5529, 'Şairler', 'Mekke', 47),
(2992, 60, 26, 'Şu\'ara', 'فَاَتْبَعُوهُمْ مُشْرِق۪ينَ', 'Derken (Firavun ve adamları) gün doğumunda onların ardına düştüler.', 19, 368, 227, 5529, 'Şairler', 'Mekke', 47),
(2993, 61, 26, 'Şu\'ara', 'فَلَمَّا تَـرَٓاءَ الْجَمْعَانِ قَالَ اَصْحَابُ مُوسٰٓى اِنَّا لَمُدْرَكُونَۚ', 'İki topluluk birbirini görünce, Musa´nın adamları: İşte yakalandık! dediler.', 19, 369, 227, 5529, 'Şairler', 'Mekke', 47),
(2994, 62, 26, 'Şu\'ara', 'قَالَ كَلَّاۚ اِنَّ مَعِيَ رَبّ۪ي سَيَهْد۪ينِ', 'Musa: Asla! dedi, Rabbim şüphesiz benimledir, bana yol gösterecektir.', 19, 369, 227, 5529, 'Şairler', 'Mekke', 47),
(2995, 63, 26, 'Şu\'ara', 'فَاَوْحَيْنَٓا اِلٰى مُوسٰٓى اَنِ اضْرِبْ بِعَصَاكَ الْبَحْرَۜ فَانْفَلَقَ فَكَانَ كُلُّ فِرْقٍ كَالطَّوْدِ الْعَظ۪يمِۚ', 'Bunun üzerine Musa´ya: Asân ile denize vur! diye vahyettik. (Vurunca deniz) derhal yarıldı (on iki yol açıldı), her bölük koca bir dağ gibi oldu.', 19, 369, 227, 5529, 'Şairler', 'Mekke', 47),
(2996, 64, 26, 'Şu\'ara', 'وَاَزْلَفْنَا ثَمَّ الْاٰخَر۪ينَۚ', 'Ötekilerini de oraya yaklaştırdık.', 19, 369, 227, 5529, 'Şairler', 'Mekke', 47),
(2997, 65, 26, 'Şu\'ara', 'وَاَنْجَيْنَا مُوسٰى وَمَنْ مَعَهُٓ اَجْمَع۪ينَۚ', 'Musa ve beraberinde bulunanların hepsini kurtardık.', 19, 369, 227, 5529, 'Şairler', 'Mekke', 47),
(2998, 66, 26, 'Şu\'ara', 'ثُمَّ اَغْرَقْنَا الْاٰخَر۪ينَۜ', 'Sonra ötekilerini suda boğduk.', 19, 369, 227, 5529, 'Şairler', 'Mekke', 47),
(2999, 67, 26, 'Şu\'ara', 'اِنَّ ف۪ي ذٰلِكَ لَاٰيَةًۜ وَمَا كَانَ اَكْثَرُهُمْ مُؤْمِن۪ينَ', 'Şüphesiz bunda bir ibret vardır; ama çokları iman etmiş değillerdir.', 19, 369, 227, 5529, 'Şairler', 'Mekke', 47),
(3000, 68, 26, 'Şu\'ara', 'وَاِنَّ رَبَّكَ لَهُوَ الْعَز۪يزُ الرَّح۪يمُ۟', 'Şüphesiz Rabbin, işte O, mutlak galip ve engin merhamet sahibidir.', 19, 369, 227, 5529, 'Şairler', 'Mekke', 47),
(3001, 69, 26, 'Şu\'ara', 'وَاتْلُ عَلَيْهِمْ نَبَاَ اِبْرٰه۪يمَۢ', '(Resûlüm!) Onlara İbrahim´in haberini de naklet.', 19, 369, 227, 5529, 'Şairler', 'Mekke', 47),
(3002, 70, 26, 'Şu\'ara', 'اِذْ قَالَ لِاَب۪يهِ وَقَوْمِه۪ مَا تَعْبُدُونَ', 'Hani o, babasına ve kavmine: Neye tapıyorsunuz? demişti.', 19, 369, 227, 5529, 'Şairler', 'Mekke', 47),
(3003, 71, 26, 'Şu\'ara', 'قَالُوا نَعْبُدُ اَصْنَاماً فَنَظَلُّ لَهَا عَاكِف۪ينَ', '«Putlara tapıyoruz ve onlara tapmaya devam edeceğiz» diye cevap verdiler.', 19, 369, 227, 5529, 'Şairler', 'Mekke', 47),
(3004, 72, 26, 'Şu\'ara', 'قَالَ هَلْ يَسْمَعُونَكُمْ اِذْ تَدْعُونَۙ', 'İbrahim: Peki, dedi, yalvardığınızda onlar sizi işitiyorlar mı?', 19, 369, 227, 5529, 'Şairler', 'Mekke', 47),
(3005, 73, 26, 'Şu\'ara', 'اَوْ يَنْفَعُونَكُمْ اَوْ يَضُرُّونَ', 'Yahut size fayda ya da zarar verebiliyorlar mı?', 19, 369, 227, 5529, 'Şairler', 'Mekke', 47),
(3006, 74, 26, 'Şu\'ara', 'قَالُوا بَلْ وَجَدْنَٓا اٰبَٓاءَنَا كَذٰلِكَ يَفْعَلُونَ', 'Şöyle cevap verdiler: Hayır, ama biz babalarımızı böyle yapar bulduk.', 19, 369, 227, 5529, 'Şairler', 'Mekke', 47),
(3007, 75, 26, 'Şu\'ara', 'قَالَ اَفَرَاَيْتُمْ مَا كُنْتُمْ تَعْبُدُونَۙ', '(75-76) İbrahim dedi ki: İyi ama, ister sizin, ister önceki atalarınızın; neye taptığınızı (biraz olsun) düşündünüz mü?', 19, 369, 227, 5529, 'Şairler', 'Mekke', 47),
(3008, 76, 26, 'Şu\'ara', 'اَنْتُمْ وَاٰبَٓاؤُ۬كُمُ الْاَقْدَمُونَ', '(75-76) İbrahim dedi ki: İyi ama, ister sizin, ister önceki atalarınızın; neye taptığınızı (biraz olsun) düşündünüz mü?', 19, 369, 227, 5529, 'Şairler', 'Mekke', 47),
(3009, 77, 26, 'Şu\'ara', 'فَاِنَّهُمْ عَدُوٌّ ل۪ٓي اِلَّا رَبَّ الْعَالَم۪ينَۙ', 'İyi bilin ki onlar benim düşmanımdır; ancak âlemlerin Rabbi (benim dostumdur);', 19, 369, 227, 5529, 'Şairler', 'Mekke', 47),
(3010, 78, 26, 'Şu\'ara', 'اَلَّذ۪ي خَلَقَن۪ي فَهُوَ يَهْد۪ينِۙ', 'Beni yaratan ve bana doğru yolu gösteren O´dur.', 19, 369, 227, 5529, 'Şairler', 'Mekke', 47),
(3011, 79, 26, 'Şu\'ara', 'وَالَّذ۪ي هُوَ يُطْعِمُن۪ي وَيَسْق۪ينِۙ', 'Beni yediren, içiren O´dur.', 19, 369, 227, 5529, 'Şairler', 'Mekke', 47),
(3012, 80, 26, 'Şu\'ara', 'وَاِذَا مَرِضْتُ فَهُوَ يَشْف۪ينِۖ', 'Hastalandığım zaman bana şifa veren O´dur.', 19, 369, 227, 5529, 'Şairler', 'Mekke', 47),
(3013, 81, 26, 'Şu\'ara', 'وَالَّذ۪ي يُم۪يتُن۪ي ثُمَّ يُحْي۪ينِۙ', 'Benim canımı alacak, sonra beni diriltecek O´dur.', 19, 369, 227, 5529, 'Şairler', 'Mekke', 47),
(3014, 82, 26, 'Şu\'ara', 'وَالَّـذ۪ٓي اَطْمَعُ اَنْ يَغْفِرَ ل۪ي خَط۪ٓيـَٔت۪ي يَوْمَ الدّ۪ينِۜ', 'Ve hesap günü hatalarımı bağışlayacağını umduğum O´dur.', 19, 369, 227, 5529, 'Şairler', 'Mekke', 47),
(3015, 83, 26, 'Şu\'ara', 'رَبِّ هَبْ ل۪ي حُكْماً وَاَلْحِقْن۪ي بِالصَّالِح۪ينَۙ', 'Rabbim! Bana hikmet ver ve beni iyiler arasına kat.', 19, 369, 227, 5529, 'Şairler', 'Mekke', 47),
(3016, 84, 26, 'Şu\'ara', 'وَاجْعَلْ ل۪ي لِسَانَ صِدْقٍ فِي الْاٰخِر۪ينَۙ', 'Bana, sonra gelecekler içinde, iyilikle anılmak nasip eyle!', 19, 370, 227, 5529, 'Şairler', 'Mekke', 47),
(3017, 85, 26, 'Şu\'ara', 'وَاجْعَلْن۪ي مِنْ وَرَثَةِ جَنَّةِ النَّع۪يمِۙ', 'Beni, Naîm cennetinin vârislerinden kıl.', 19, 370, 227, 5529, 'Şairler', 'Mekke', 47),
(3018, 86, 26, 'Şu\'ara', 'وَاغْفِرْ لِاَب۪ٓي اِنَّهُ كَانَ مِنَ الضَّٓالّ۪ينَۙ', 'Babamı da bağışla (ona tevbe ve iman nasip et). Çünkü o sapıklardandır.', 19, 370, 227, 5529, 'Şairler', 'Mekke', 47),
(3019, 87, 26, 'Şu\'ara', 'وَلَا تُخْزِن۪ي يَوْمَ يُبْعَثُونَۙ', '(İnsanların) dirilecekleri gün, beni mahcup etme.', 19, 370, 227, 5529, 'Şairler', 'Mekke', 47),
(3020, 88, 26, 'Şu\'ara', 'يَوْمَ لَا يَنْفَعُ مَالٌ وَلَا بَنُونَۙ', 'O gün, ne mal fayda verir ne de evlât.', 19, 370, 227, 5529, 'Şairler', 'Mekke', 47),
(3021, 89, 26, 'Şu\'ara', 'اِلَّا مَنْ اَتَى اللّٰهَ بِقَلْبٍ سَل۪يمٍۜ', 'Ancak Allah´a kalb-i selîm (temiz bir kalp) ile gelenler (o günde fayda bulur).', 19, 370, 227, 5529, 'Şairler', 'Mekke', 47),
(3022, 90, 26, 'Şu\'ara', 'وَاُزْلِفَتِ الْجَنَّةُ لِلْمُتَّق۪ينَۙ', '(O gün) cennet, takvâ sahiplerine yaklaştırılır.', 19, 370, 227, 5529, 'Şairler', 'Mekke', 47),
(3023, 91, 26, 'Şu\'ara', 'وَبُرِّزَتِ الْجَح۪يمُ لِلْغَاو۪ينَۙ', 'Cehennem de azgınlara apaçık gösterilir.', 19, 370, 227, 5529, 'Şairler', 'Mekke', 47),
(3024, 92, 26, 'Şu\'ara', 'وَق۪يلَ لَهُمْ اَيْنَ مَا كُنْتُمْ تَعْبُدُونَۙ', '(92-93) Onlara: Allah´tan gayrı taptıklarınız hani nerede? Size yardım edebiliyorlar mı veya kendilerine (olsun) yardımları dokunuyor mu? denilir.', 19, 370, 227, 5529, 'Şairler', 'Mekke', 47),
(3025, 93, 26, 'Şu\'ara', 'مِنْ دُونِ اللّٰهِۜ هَلْ يَنْصُرُونَكُمْ اَوْ يَنْتَصِرُونَۜ', '(92-93) Onlara: Allah´tan gayrı taptıklarınız hani nerede? Size yardım edebiliyorlar mı veya kendilerine (olsun) yardımları dokunuyor mu? denilir.', 19, 370, 227, 5529, 'Şairler', 'Mekke', 47),
(3026, 94, 26, 'Şu\'ara', 'فَكُبْكِبُوا ف۪يهَا هُمْ وَالْغَاوُ۫نَۙ', '(94-95) Artık onlar, o azgınlar ve İblis orduları, toptan oraya tepetaklak (cehenneme) atılırlar.', 19, 370, 227, 5529, 'Şairler', 'Mekke', 47),
(3027, 95, 26, 'Şu\'ara', 'وَجُنُودُ اِبْل۪يسَ اَجْمَعُونَۜ', '(94-95) Artık onlar, o azgınlar ve İblis orduları, toptan oraya tepetaklak (cehenneme) atılırlar.', 19, 370, 227, 5529, 'Şairler', 'Mekke', 47),
(3028, 96, 26, 'Şu\'ara', 'قَالُوا وَهُمْ ف۪يهَا يَخْتَصِمُونَۙ', 'Orada birbirleriyle çekişerek şöyle derler:', 19, 370, 227, 5529, 'Şairler', 'Mekke', 47);
INSERT INTO `Ayetler` (`id`, `AyetNo`, `SureNo`, `SureAdi`, `Metin`, `Mana`, `Cuzu`, `Sayfasi`, `AyetSayisi`, `HarfSayisi`, `AdAnlami`, `IndigiYer`, `InisSirasi`) VALUES
(3029, 97, 26, 'Şu\'ara', 'تَاللّٰهِ اِنْ كُنَّا لَف۪ي ضَلَالٍ مُب۪ينٍۙ', 'Vallahi, biz gerçekten apaçık bir sapıklık içindeymişiz.', 19, 370, 227, 5529, 'Şairler', 'Mekke', 47),
(3030, 98, 26, 'Şu\'ara', 'اِذْ نُسَوّ۪يكُمْ بِرَبِّ الْعَالَم۪ينَ', 'Çünkü biz sizi âlemlerin Rabbi ile eşit tutuyorduk.', 19, 370, 227, 5529, 'Şairler', 'Mekke', 47),
(3031, 99, 26, 'Şu\'ara', 'وَمَٓا اَضَلَّـنَٓا اِلَّا الْمُجْرِمُونَ', 'Bizi ancak o günahkârlar saptırdı.', 19, 370, 227, 5529, 'Şairler', 'Mekke', 47),
(3032, 100, 26, 'Şu\'ara', 'فَمَا لَنَا مِنْ شَافِع۪ينَۙ', '(100-101) Şimdi artık bizim ne şefaatçilerimiz var, ne de yakın bir dostumuz.', 19, 370, 227, 5529, 'Şairler', 'Mekke', 47),
(3033, 101, 26, 'Şu\'ara', 'وَلَا صَد۪يقٍ حَم۪يمٍ', '(100-101) Şimdi artık bizim ne şefaatçilerimiz var, ne de yakın bir dostumuz.', 19, 370, 227, 5529, 'Şairler', 'Mekke', 47),
(3034, 102, 26, 'Şu\'ara', 'فَلَوْ اَنَّ لَنَا كَرَّةً فَنَكُونَ مِنَ الْمُؤْمِن۪ينَ', 'Ah keşke bizim için (dünyaya) bir dönüş daha olsa da, müminlerden olsak!', 19, 370, 227, 5529, 'Şairler', 'Mekke', 47),
(3035, 103, 26, 'Şu\'ara', 'اِنَّ ف۪ي ذٰلِكَ لَاٰيَةًۜ وَمَا كَانَ اَكْثَرُهُمْ مُؤْمِن۪ينَ', 'Bunda elbet (alınacak) büyük bir ders vardır; ama çokları iman etmezler.', 19, 370, 227, 5529, 'Şairler', 'Mekke', 47),
(3036, 104, 26, 'Şu\'ara', 'وَاِنَّ رَبَّكَ لَهُوَ الْعَز۪يزُ الرَّح۪يمُ۟', 'Şüphesiz Rabbin, işte O, mutlak galip ve engin merhamet sahibidir.', 19, 370, 227, 5529, 'Şairler', 'Mekke', 47),
(3037, 105, 26, 'Şu\'ara', 'كَذَّبَتْ قَوْمُ نُوحٍۨ الْمُرْسَل۪ينَۚ', 'Nuh kavmi de peygamberleri yalancılıkla suçladılar.', 19, 370, 227, 5529, 'Şairler', 'Mekke', 47),
(3038, 106, 26, 'Şu\'ara', 'اِذْ قَالَ لَهُمْ اَخُوهُمْ نُوحٌ اَلَا تَتَّقُونَۚ', 'Kardeşleri Nuh onlara şöyle demişti: (Allah´a karşı gelmekten) sakınmaz mısınız?', 19, 370, 227, 5529, 'Şairler', 'Mekke', 47),
(3039, 107, 26, 'Şu\'ara', 'اِنّ۪ي لَكُمْ رَسُولٌ اَم۪ينٌۙ', 'Bilin ki ben, size gönderilmiş güvenilir bir elçiyim.', 19, 370, 227, 5529, 'Şairler', 'Mekke', 47),
(3040, 108, 26, 'Şu\'ara', 'فَاتَّقُوا اللّٰهَ وَاَط۪يعُونِۚ', 'Artık Allah´a karşı gelmekten sakının ve bana itaat edin.', 19, 370, 227, 5529, 'Şairler', 'Mekke', 47),
(3041, 109, 26, 'Şu\'ara', 'وَمَٓا اَسْـَٔلُكُمْ عَلَيْهِ مِنْ اَجْرٍۚ اِنْ اَجْرِيَ اِلَّا عَلٰى رَبِّ الْعَالَم۪ينَۚ', 'Buna karşı sizden hiçbir ücret istemiyorum. Benim ecrimi verecek olan, ancak âlemlerin Rabbidir.', 19, 370, 227, 5529, 'Şairler', 'Mekke', 47),
(3042, 110, 26, 'Şu\'ara', 'فَاتَّقُوا اللّٰهَ وَاَط۪يعُونِۜ', 'Onun için, Allah´tan korkun ve bana itaat edin.', 19, 370, 227, 5529, 'Şairler', 'Mekke', 47),
(3043, 111, 26, 'Şu\'ara', 'قَالُٓوا اَنُؤْمِنُ لَكَ وَاتَّبَعَكَ الْاَرْذَلُونَۜ', 'Onlar şöyle cevap verdiler: Sana düşük seviyeli kimseler tâbi olup dururken, biz sana iman eder miyiz hiç!', 19, 370, 227, 5529, 'Şairler', 'Mekke', 47),
(3044, 112, 26, 'Şu\'ara', 'قَالَ وَمَا عِلْم۪ي بِمَا كَانُوا يَعْمَلُونَۚ', 'Nuh dedi ki: Onların yaptıkları hakkında bilgim yoktur.', 19, 371, 227, 5529, 'Şairler', 'Mekke', 47),
(3045, 113, 26, 'Şu\'ara', 'اِنْ حِسَابُهُمْ اِلَّا عَلٰى رَبّ۪ي لَوْ تَشْعُرُونَۚ', 'Onların hesabı ancak Rabbime aittir. Bir düşünseniz!', 19, 371, 227, 5529, 'Şairler', 'Mekke', 47),
(3046, 114, 26, 'Şu\'ara', 'وَمَٓا اَنَا۬ بِطَارِدِ الْمُؤْمِن۪ينَۚ', 'Ben iman eden kimseleri kovacak değilim.', 19, 371, 227, 5529, 'Şairler', 'Mekke', 47),
(3047, 115, 26, 'Şu\'ara', 'اِنْ اَنَا۬ اِلَّا نَذ۪يرٌ مُب۪ينٌۜ', 'Ben ancak apaçık bir uyarıcıyım.', 19, 371, 227, 5529, 'Şairler', 'Mekke', 47),
(3048, 116, 26, 'Şu\'ara', 'قَالُوا لَئِنْ لَمْ تَنْتَهِ۬ يَا نُوحُ لَتَكُونَنَّ مِنَ الْمَرْجُوم۪ينَۜ', 'Dediler ki: Ey Nuh! (Bu davadan) vazgeçmezsen, iyi bil ki, taşlanmışlardan olacaksın!', 19, 371, 227, 5529, 'Şairler', 'Mekke', 47),
(3049, 117, 26, 'Şu\'ara', 'قَالَ رَبِّ اِنَّ قَوْم۪ي كَذَّبُونِۚ', 'Nuh: Rabbim! dedi, kavmim beni yalancılıkla suçladı.', 19, 371, 227, 5529, 'Şairler', 'Mekke', 47),
(3050, 118, 26, 'Şu\'ara', 'فَافْتَحْ بَيْن۪ي وَبَيْنَهُمْ فَتْحاً وَنَجِّن۪ي وَمَنْ مَعِيَ مِنَ الْمُؤْمِن۪ينَ', 'Artık benimle onların arasında sen hükmünü ver. Beni ve beraberimdeki müminleri kurtar.', 19, 371, 227, 5529, 'Şairler', 'Mekke', 47),
(3051, 119, 26, 'Şu\'ara', 'فَاَنْجَيْنَاهُ وَمَنْ مَعَهُ فِي الْفُلْكِ الْمَشْحُونِۚ', 'Bunun üzerine biz onu ve beraberindekileri, o dolu geminin içinde (taşıyarak) kurtardık.', 19, 371, 227, 5529, 'Şairler', 'Mekke', 47),
(3052, 120, 26, 'Şu\'ara', 'ثُمَّ اَغْرَقْنَا بَعْدُ الْبَاق۪ينَۜ', 'Sonra da geri kalanları suda boğduk.', 19, 371, 227, 5529, 'Şairler', 'Mekke', 47),
(3053, 121, 26, 'Şu\'ara', 'اِنَّ ف۪ي ذٰلِكَ لَاٰيَةًۜ وَمَا كَانَ اَكْثَرُهُمْ مُؤْمِن۪ينَ', 'Doğrusu bunda büyük bir ders vardır; ama çokları iman etmezler.', 19, 371, 227, 5529, 'Şairler', 'Mekke', 47),
(3054, 122, 26, 'Şu\'ara', 'وَاِنَّ رَبَّكَ لَهُوَ الْعَز۪يزُ الرَّح۪يمُ۟', 'Şüphesiz Rabbin, işte O, mutlak galip ve engin merhamet sahibidir.', 19, 371, 227, 5529, 'Şairler', 'Mekke', 47),
(3055, 123, 26, 'Şu\'ara', 'كَذَّبَتْ عَادٌۨ الْمُرْسَل۪ينَۚ', 'Âd (kavmi) de peygamberleri yalancılıkla suçladı.', 19, 371, 227, 5529, 'Şairler', 'Mekke', 47),
(3056, 124, 26, 'Şu\'ara', 'اِذْ قَالَ لَهُمْ اَخُوهُمْ هُودٌ اَلَا تَتَّقُونَۚ', 'Kardeşleri Hûd onlara şöyle demişti: (Allah´a karşı gelmekten) sakınmaz mısınız?', 19, 371, 227, 5529, 'Şairler', 'Mekke', 47),
(3057, 125, 26, 'Şu\'ara', 'اِنّ۪ي لَكُمْ رَسُولٌ اَم۪ينٌۙ', 'Bilin ki, ben size gönderilmiş güvenilir bir elçiyim.', 19, 371, 227, 5529, 'Şairler', 'Mekke', 47),
(3058, 126, 26, 'Şu\'ara', 'فَاتَّقُوا اللّٰهَ وَاَط۪يعُونِۚ', 'Artık Allah´a karşı gelmekten sakının ve bana itaat edin.', 19, 371, 227, 5529, 'Şairler', 'Mekke', 47),
(3059, 127, 26, 'Şu\'ara', 'وَمَٓا اَسْـَٔلُكُمْ عَلَيْهِ مِنْ اَجْرٍۚ اِنْ اَجْرِيَ اِلَّا عَلٰى رَبِّ الْعَالَم۪ينَۜ', 'Buna karşı sizden hiçbir ücret istemiyorum. Benim ecrimi verecek olan, ancak âlemlerin Rabbidir.', 19, 371, 227, 5529, 'Şairler', 'Mekke', 47),
(3060, 128, 26, 'Şu\'ara', 'اَتَبْنُونَ بِكُلِّ ر۪يعٍ اٰيَةً تَعْبَثُونَۙ', 'Siz her yüksek yere bir alâmet dikerek eğleniyor musunuz?', 19, 371, 227, 5529, 'Şairler', 'Mekke', 47),
(3061, 129, 26, 'Şu\'ara', 'وَتَتَّخِذُونَ مَصَانِـعَ لَعَلَّكُمْ تَخْلُدُونَۚ', 'Temelli kalacağınızı umarak sağlam yapılar mı ediniyorsunuz?', 19, 371, 227, 5529, 'Şairler', 'Mekke', 47),
(3062, 130, 26, 'Şu\'ara', 'وَاِذَا بَطَشْتُمْ بَطَشْتُمْ جَبَّار۪ينَۚ', 'Yakaladığınız zaman, zorbalar gibi mi yakalıyorsunuz?', 19, 371, 227, 5529, 'Şairler', 'Mekke', 47),
(3063, 131, 26, 'Şu\'ara', 'فَاتَّقُوا اللّٰهَ وَاَط۪يعُونِۚ', 'Artık Allah´tan korkun ve bana itaat edin.', 19, 371, 227, 5529, 'Şairler', 'Mekke', 47),
(3064, 132, 26, 'Şu\'ara', 'وَاتَّقُوا الَّـذ۪ٓي اَمَدَّكُمْ بِمَا تَعْلَمُونَۚ', '(132-134) Bildiğiniz şeyleri size veren, size davarlar, oğullar, bağlar, pınarlar ihsan eden (Allah´a karşı gelmek)ten sakının.', 19, 371, 227, 5529, 'Şairler', 'Mekke', 47),
(3065, 133, 26, 'Şu\'ara', 'اَمَدَّكُمْ بِاَنْعَامٍ وَبَن۪ينَۙ', '(132-134) Bildiğiniz şeyleri size veren, size davarlar, oğullar, bağlar, pınarlar ihsan eden (Allah´a karşı gelmek)ten sakının.', 19, 371, 227, 5529, 'Şairler', 'Mekke', 47),
